(window.webpackJsonp=window.webpackJsonp||[]).push([[1103],{1126:function(p){p.exports={chapterTitle:null,title:"\u7b2c1100\u7ae0 \u8054\u901a\u7684\u5b9a\u5236\u7b56\u7565",content:"<article><h2>\u7b2c1100\u7ae0 \u8054\u901a\u7684\u5b9a\u5236\u7b56\u7565</h2><p>\u3000\u3000\u5317\u4eac\u5927\u96ea\uff0c\u96ea\u7c92\u7ed9\u51db\u70c8\u7684\u5bd2\u98ce\u631f\u88f9\u7740\uff0c\u649e\u51fb\u7740\u63a5\u673a\u5927\u5385\u9876\u4e0a\u7684\u901a\u98ce\u7a97\u53d1\u51fa\u4e1d\u4e1d\u7684\u5f02\u54cd\uff0c\u4eba\u867d\u7136\u8fd8\u7ad9\u5728\u6e29\u6696\u5982\u6625\u7684\u63a5\u673a\u5927\u5385\u91cc\uff0c\u5374\u5df2\u7ecf\u611f\u53d7\u5230\u5317\u4eac\u7684\u4e25\u5bd2\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0e\u8096\u745e\u6c11\u89c1\u8fc7\u9762\uff0c\u5c31\u8fc7\u53bb\u627e\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u671b\u7740\u665a\u6674\u5982\u4e00\u6cd3\u79cb\u6c34\u7684\u7738\u5b50\uff0c\u773c\u6ce2\u91cc\u4f3c\u8d77\u4e86\u7ec6\u7ec6\u7684\u6d9f\u6f2a\uff0c\u8d81\u7740\u5de5\u4f5c\u4eba\u5458\u90fd\u8d70\u51fa\u63a5\u673a\u5927\u5385\u7b49\u5206\u516c\u53f8\u7684\u8f66\u5f00\u5230\u53f0\u9636\u4e0a\u6765\uff0c\u4ed6\u7275\u8fc7\u665a\u6674\u6e29\u6da6\u7389\u7684\u624b\uff0c\u95ee\u9053\uff1a\u201c\u8fd8\u662f\u8ba9\u8096\u745e\u6c11\u8fc7\u6765\u89c1\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ba9\u4ed6\u8fc7\u6765\u89c1\u4f60\uff0c\u201d\u8c22\u665a\u6674\u58f0\u97f3\u5f88\u8f7b\uff0c\u4fc4\u5c14\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u7b11\u5bb9\u5ae3\u7136\u59a9\u5a9a\uff0c\u201c\u8fd8\u662f\u4f60\u53bb\u89c1\u4eba\u5bb6\u5427\uff0c\u6211\u7b49\u4f60\u3002\u201d</p><p>\u3000\u3000\u770b\u7740\u665a\u6674\u7738\u5b50\u91cc\u90fd\u6709\u4e9b\u6c34\u5149\u6a2a\u655b\uff0c\u5f20\u606a\u5c06\u624b\u6307\u809a\u5b50\u8f7b\u67d4\u7684\u8d34\u4e0a\u53bb\uff0c\u5728\u5979\u7684\u77b3\u5b54\u4e0a\u8f7b\u8f7b\u7684\u5370\u4e86\u4e00\u4e0b\uff0c\u771f\u6709\u4e9b\u6e7f\u75d5\uff0c\u5c06\u624b\u6307\u809a\u5b50\u644a\u5f00\u5979\u770b\uff1a\u201c\u4f60\u8fd9\u91cc\u90fd\u6709\u6c34\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u53cd\u624b\u6293\u4f4f\u5f20\u606a\u7684\u624b\u638c\u8f7b\u634f\u4e86\u4e00\u4e0b\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u5fc3\u60c5\u7ed9\u5f20\u606a\u770b\u5728\u773c\u5e95\u8fd8\u662f\u89c9\u5f97\u6709\u4e9b\u7f9e\u6da9\uff0c\u81ea\u5df1\u60f3\u60f3\u90fd\u89c9\u5f97\u597d\u7b11\uff0c\u89c1\u52a9\u7406\u8d70\u56de\u63a5\u673a\u5927\u5385\u901a\u77e5\u5979\u5750\u8f66\u53bb\uff0c\u5979\u677e\u5f00\u5f20\u606a\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5148\u53bb\u9999\u6797\uff0c\u968f\u4fbf\u4f60\u4ec0\u4e48\u65f6\u5019\u8fc7\u6765\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5728\u63a5\u673a\u5927\u5385\u91cc\u78e8\u8e6d\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8ddf\u8096\u745e\u6c11\u901a\u7535\u8bdd\uff0c\u544a\u8bc9\u4ed6\u81ea\u5df1\u521a\u5230\u5317\u4eac\uff0c\u98ce\u96ea\u8fd9\u4e48\u5927\uff0c\u4f1a\u5728\u9999\u6797\u5c0f\u9547\u7684\u522b\u5885\u4f4f\u4e0b\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u81ea\u7136\u4e0d\u5962\u671b\u5f20\u606a\u7d27\u5df4\u5df4\u7684\u8d76\u5230\u5e02\u533a\u53bb\u89c1\u4ed6\uff0c\u5fd9\u5e94\u53e3\u8bf4\u9a6c\u4e0a\u51fa\u57ce\u5230\u9999\u6797\u5c0f\u9547\u6765\u89c1\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u8155\u8868\uff0c\u5fc3\u60f3\u5916\u9762\u98ce\u96ea\u8fd9\u4e48\u5927\uff0c\u8096\u745e\u6c11\u600e\u4e48\u4e5f\u8981\u5927\u534a\u4e2a\u5c0f\u65f6\u624d\u80fd\u51fa\u57ce\uff0c\u4ed6\u94bb\u8fdb\u5206\u516c\u53f8\u6d3e\u6765\u7684\u8f7f\u8f66\u91cc\uff0c\u8ba9\u53f8\u673a\u76f4\u63a5\u9001\u4ed6\u53bb\u9999\u6797\u5c0f\u9547\u7684\u524d\u6e56\u68ee\u6797\u522b\u5885\uff0c\u90a3\u91cc\u662f\u665a\u6674\u5728\u5317\u4eac\u8d2d\u7f6e\u7684\u7269\u4e1a\uff0c\u521a\u521a\u5e03\u7f6e\u597d\uff0c\u4e5f\u7b97\u662f\u5728\u5317\u4eac\u4e00\u5904\u843d\u811a\u5730\uff0c\u4e0d\u8fc7\u5c31\u7b97\u4ece\u665a\u6674\u624b\u91cc\u62ff\u5230\u522b\u5885\u533a\u7684\u901a\u884c\u5361\u4e00\u6bb5\u65f6\u95f4\u4e86\uff0c\u5f20\u606a\u90fd\u8fd8\u6ca1\u6709\u65f6\u95f4\u4f4f\u8fdb\u53bb\u8fc7\u3002</p><p>\u3000\u3000\u516c\u8def\u4e24\u4fa7\u7684\u7530\u91ce\uff0c\u79ef\u96ea\u6709\u76f8\u5f53\u89c4\u6a21\u4e86\uff0c\u5df2\u7ecf\u770b\u4e0d\u89c1\u571f\u8272\uff0c\u6cbf\u7740\u673a\u573a\u4e8c\u7ebf\u516c\u8def\u884c\u9a76\u4e0d\u4e45\uff0c\u62d0\u4e0b\u6765\u5c31\u662f\u5317\u4eac\u987a\u4e49\u7684\u9ad8\u6863\u793e\u533a\u7fa4\u9999\u6797\u5c0f\u9547\uff0c\u793e\u533a\u8fb9\u4e0a\u6709\u56db\u661f\u7ea7\u7684\u56fd\u5bcc\u5927\u9152\u5e97\uff0c\u968f\u884c\u7684\u5de5\u4f5c\u4eba\u5458\u90fd\u5165\u4f4f\u56fd\u5bcc\u5927\u9152\u5e97\uff0c\u5f20\u606a\u5355\u72ec\u5f00\u8f66\u8fdb\u4e86\u9999\u6797\u5c0f\u9547\u5185\u4fa7\u7684\u5357\u6e56\u68ee\u6797\u522b\u5885\u533a\u3002</p><p>\u3000\u3000\u8fdb\u4e86\u522b\u5885\u533a\uff0c\u5faa\u7740\u538b\u8fc7\u79ef\u96ea\u7684\u8f66\u8f99\uff0c\u5f88\u5bb9\u6613\u5c31\u627e\u5230\u5730\u65b9\uff0c\u770b\u7740\u6240\u6709\u7684\u7a97\u6237\u90fd\u6253\u5f00\u6765\u5728\u6362\u6c14\uff0c\u665a\u6674\u542c\u7740\u6c7d\u8f66\u54cd\u58f0\uff0c\u4ece\u4e8c\u697c\u7684\u7a97\u6237\u91cc\u63a2\u51fa\u5934\u6765\uff0c\u8ba9\u5f20\u606a\u76f4\u63a5\u5c06\u901a\u884c\u5361\u9760\u8fd1\u95e8\u7981\u611f\u5e94\u5668\u4e0a\uff0c\u9662\u95e8\u5c31\u6253\u5f00\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8f66\u5b50\u91cc\u505c\u5728\u540e\u9662\u79ef\u4e86\u96ea\u7684\u8349\u576a\u4e0a\uff0c\u8d70\u8fdb\u5c4b\u91cc\uff0c\u98ce\u5f88\u5927\uff0c\u6709\u96ea\u7c92\u5439\u8d77\u6765\uff0c\u843d\u5728\u5ba2\u5385\u91cc\uff0c\u7a97\u6237\u6d1e\u5f00\uff0c\u98ce\u5728\u5ba4\u5185\u4e71\u7a9c\uff0c\u8981\u4e0d\u662f\u5341\u516b\u5ea6\u7684\u4e25\u5bd2\uff0c\u6e05\u65b0\u7684\u7a7a\u6c14\u5012\u662f\u8ba9\u4eba\u6109\u60a6\u3002</p><p>\u3000\u3000\u201c\u4f60\u5148\u5750\u4f1a\u513f\uff0c\u8981\u4e48\u5e2e\u6211\u70e7\u58f6\u6c34\u4e5f\u884c\uff0c\u8fd9\u91cc\u53ea\u6709\u901f\u6eb6\u5496\u5561\uff0c\u6ca1\u6709\u8336\uff01\u623f\u5b50\u6536\u62fe\u597d\u4e4b\u540e\u5c31\u6ca1\u6709\u4eba\u8fc7\u6765\u8fc7\uff0c\u8981\u6253\u5f00\u7a97\u5b50\u5439\u4e00\u5439\uff0c\u201d\u665a\u6674\u8fd8\u7a7f\u7740\u9ed1\u5462\u5b50\u7684\u77ed\u98ce\u8863\uff0c\u7ad9\u5728\u4e8c\u697c\u7684\u697c\u68af\u53e3\u8ddf\u5f20\u606a\u8bf4\u8bdd\uff0c\u5f20\u606a\u6545\u610f\u5728\u8def\u4e0a\u803d\u6401\u4e86\u4e00\u4f1a\u513f\uff0c\u6253\u5f00\u7a97\u6237\u900f\u6c14\u6709\u4e9b\u65f6\u95f4\u4e86\uff0c\u201c\u4f60\u7b49\u6211\u5c06\u7a97\u6237\u518d\u5173\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u53a8\u623f\u91cc\u5c06\u6c34\u5148\u70e7\u4e0a\uff0c\u7136\u540e\u8d70\u5230\u524d\u95e8\u7684\u5eca\u5385\u5f80\u5916\u770b\uff0c\u522b\u5885\u524d\u662f\u6761\u51b0\u5c01\u4f4f\u7684\u5c0f\u6cb3\uff0c\u6709\u6761\u5375\u77f3\u94fa\u6210\u7684\u5c0f\u5f84\u5c06\u522b\u5885\u524d\u7cbe\u81f4\u7684\u9662\u843d\u5ef6\u4f38\u5230\u6cb3\u8fb9\u677e\u6728\u642d\u6210\u7684\u6c34\u53f0\u4e0a\uff0c\u51ac\u5929\u7684\u6811\u7bf1\u6709\u4e9b\u7a00\u758f\u3002</p><p>\u3000\u3000\u201c\u5c06\u95e8\u5173\u4e0a\u5427\uff0c\u4e00\u4f1a\u513f\u5c31\u80fd\u6696\u8d77\u6765\uff0c\u201d\u665a\u6674\u8d70\u4e0b\u6765\uff0c\u5c06\u51b0\u51b7\u7684\u624b\u5728\u5f20\u606a\u7684\u8138\u4e0a\u8d34\u4e86\u4e00\u4e0b\uff0c\u7b11\u7740\u95ee\u5f20\u606a\uff0c\u201c\u51b7\u4e0d\u51b7\uff1f\u201d</p><p>\u3000\u3000\u665a\u6674\u672c\u6ca1\u6709\u591a\u5c11\u673a\u4f1a\u63a5\u89e6\u5230\u5ba4\u5916\u7684\u4e25\u5bd2\uff0c\u8863\u670d\u7a7f\u5f97\u5355\u8584\uff0c\u53ea\u662f\u522b\u5885\u91cc\u7684\u7a97\u6237\u8981\u6253\u5f00\u6765\u6362\u6c14\uff0c\u786e\u51ff\u6328\u4e86\u4e00\u4f1a\u513f\u51bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u6293\u4f4f\u5979\u51b0\u51b7\u7684\u5c0f\u624b\u4ece\u8863\u895f\u4e0b\u4f38\u5230\u81ea\u5df1\u814b\u4e0b\u6765\u53d6\u6696\uff0c\u62ff\u81ea\u5df1\u8fd8\u662f\u70ed\u4e4e\u7684\u624b\u6367\u7740\u5979\u51b0\u5f97\u6108\u53d1\u96ea\u767d\u7684\u5706\u6da6\u8138\u86cb\uff0c\u51dd\u671b\u7740\u5979\u660e\u7738\u7693\u9f7f\u3001\u6e05\u4e3d\u5a07\u5a9a\uff0c\u5979\u6210\u719f\u7684\u5bb9\u989c\u91cc\u900f\u7740\u64a9\u4eba\u7684\u98ce\u60c5\u3002</p><p>\u3000\u3000\u201c\u770b\u4ec0\u4e48\u770b\uff1f\u4eba\u90fd\u8001\u4e86\u3002\u201d\u665a\u6674\u4e0d\u597d\u610f\u601d\u7684\u522b\u8fc7\u8138\u3002</p><p>\u3000\u3000\u201c\u54ea\u91cc\u6709\uff1f\u201d\u5f20\u606a\u53cc\u624b\u6367\u7740\u665a\u6674\u7684\u8138\u86cb\u8f6c\u6b63\u8fc7\u6765\uff0c\u8fc7\u5e74\u665a\u6674\u5c31\u4e09\u5341\u4e94\u5c81\u4e86\uff0c\u96be\u602a\u5979\u4f1a\u6709\u8fd9\u6837\u7684\u611f\u6982\u3002\u8bb8\u591a\u4eba\u90fd\u8bf4\u5973\u4eba\u8fc7\u4e86\u4e8c\u5341\u4e94\u5c81\u5c31\u4f1a\u957f\u51fa\u7b2c\u4e00\u9053\u9c7c\u5c3e\u7eb9\uff0c\u4f46\u662f\u4f18\u8d8a\u7684\u751f\u6d3b\u4e0e\u7cbe\u5fc3\u7684\u62a4\u7406\u786e\u5b9e\u4f1a\u5c3d\u53ef\u80fd\u5c06\u5973\u4eba\u7684\u9752\u6625\u633d\u7559\u5f97\u66f4\u957f\u7684\u65f6\u95f4\uff0c\u665a\u6674\u773c\u89d2\u5507\u8fb9\u5e76\u6ca1\u6709\u7ec6\u7ec6\u6d45\u6d45\u7684\u5c81\u6708\u523b\u75d5\uff0c\u808c\u80a4\u4f9d\u65e7\u6709\u7740\u5e74\u8f7b\u5973\u5b50\u7684\u5a07\u5ae9\u4e0e\u5f39\u8f6f\uff0c\u53ea\u662f\u795e\u60c5\u95f4\u66f4\u6709\u6210\u719f\u5973\u5b50\u7684\u98ce\u97f5\u7f62\u4e86\u3002\u5f20\u606a\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u867d\u7136\u8fd8\u5f88\u5e74\u8f7b\u5a07\u5a9a\uff0c\u4e0d\u8fc7\u8fd8\u8981\u65f6\u65f6\u6d47\u704c\u4e00\u4e0b\u2026\u2026\u201d\u624b\u642d\u5728\u665a\u6674\u6e10\u8f6f\u7684\u80a9\u4e0a\uff0c\u8ba9\u5979\u4f9d\u504e\u5728\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u201c\u8fd9\u4e48\u5927\u7684\u98ce\u96ea\uff0c\u8096\u745e\u6c11\u51fa\u57ce\u6765\u8981\u597d\u4e00\u4f1a\u513f\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u5148\u8fdb\u5367\u5ba4\u53c2\u89c2\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u201c\u5510\u5a67\u4e0a\u5348\u76f4\u6253\u54c8\u6b20\uff0c\u4f60\u8fd8\u6709\u8fd9\u4e2a\u7cbe\u529b\uff1f\u201d\u665a\u6674\u5a07\u55d4\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5979\u5c31\u662f\u770b\u5230\u5510\u5a67\u5750\u8f66\u53bb\u673a\u573a\u65f6\u76f4\u6253\u54c8\u6b20\uff0c\u60f3\u5230\u5979\u4e0e\u5f20\u606a\u591c\u91cc\u6298\u817e\u5f97\u591f\u545b\u624d\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u7684\u60c5\u70ed\uff0c\u534a\u63a8\u534a\u5c31\u7684\u8ddf\u5f20\u606a\u62e5\u543b\u7740\u4e0a\u4e86\u697c\u68af\u5230\u4e8c\u697c\u7684\u4e3b\u5367\u5ba4\uff0c\u8fd9\u65f6\u5019\u5ba4\u5185\u7684\u6e29\u5ea6\u4e5f\u5347\u4e86\u4e0a\u6765\uff0c\u4e0d\u518d\u89c9\u5f97\u5bd2\u51b7\u3002</p><p>\u3000\u3000\u96d5\u82b1\u53cc\u4eba\u5927\u5e8a\uff0c\u5496\u5561\u8272\u7684\u5e8a\u5355\u94fa\u5c55\u5f00\uff0c\u5899\u58c1\u4e0a\u6302\u7740\u5c11\u5973\u6d74\u6cc9\u7684\u6cb9\u753b\uff0c\u5367\u5ba4\u4e1c\u5357\u662f\u4e2a\u7a7a\u95f4\u6781\u5927\u7684\u5f27\u89d2\uff0c\u6446\u7740\u6df1\u6a61\u6728\u8272\u7684\u4e66\u684c\uff0c\u5f20\u606a\u4e00\u8d70\u8fdb\u6765\uff0c\u5c31\u77e5\u9053\u665a\u6674\u662f\u6309\u7167\u5979\u7684\u559c\u597d\u6765\u5e03\u7f6e\u8fd9\u4e2a\u623f\u95f4\uff0c\u5c06\u665a\u6674\u63a8\u5012\u5728\u5e8a\u4e0a\uff0c\u5c06\u5979\u8003\u7a76\u7684\u98ce\u8863\u3001\u88d9\u88c5\u3001\u886c\u8863\u4ee5\u53ca\u4e73\u7f69\u3001\u5185\u88e4\u7edf\u7edf\u7684\u8131\u6389\uff0c\u53ea\u5269\u4e0b\u808c\u80a4\u96ea\u767d\u3001\u4e73\u623f\u4e30\u633a\u7684\u8d64\u88f8\u5a07\u8eaf\u2026\u2026</p><p>\u3000\u3000\u8096\u745e\u6c11\u6bd4\u60f3\u8c61\u4e2d\u8981\u63d0\u65e9\u8d76\u5230\u3002</p><p>\u3000\u3000\u665a\u6674\u7684\u7b2c\u4e00\u6b21\u9ad8\u6f6e\u6765\u5f97\u6025\u4fc3\u800c\u7545\u5feb\uff0c\u5979\u62e5\u88ab\u8eba\u5728\u5e8a\u5934\uff0c\u770b\u7740\u5f20\u606a\u624b\u5fd9\u811a\u4e71\u7684\u5728\u90a3\u91cc\u7a7f\u886c\u8863\uff0c\u4e0b\u9762\u7684\u4e1c\u897f\u8fd8\u6302\u8361\u5728\u90a3\u91cc\uff0c\u5a07\u5a9a\u7684\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u53ef\u8981\u5c06\u6211\u85cf\u597d\u4e86\uff0c\u6211\u5c31\u4e0d\u8d77\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7a7f\u597d\u8863\u670d\u4e0b\u4e86\u697c\uff0c\u665a\u6674\u806a\u660e\u5f97\u7d27\uff0c\u505a\u4e8b\u53c8\u5c0f\u5fc3\uff0c\u6ca1\u6709\u5728\u697c\u4e0b\u7559\u4e0b\u5979\u7684\u75d5\u8ff9\uff0c\u4ed6\u8dd1\u8fc7\u53bb\u5c06\u51b2\u4e86\u4e00\u676f\u901f\u6eb6\u5496\u5561\uff0c\u7aef\u8fc7\u6765\u653e\u5230\u5ba2\u5385\u7684\u8336\u51e0\u4e0a\uff0c\u9662\u95e8\u5916\u5c31\u6709\u6c7d\u8f66\u7684\u58f0\u97f3\u4f20\u6765\u3002</p><p>\u3000\u3000\u6253\u5f00\u95e8\uff0c\u8ba9\u8096\u745e\u6c11\u4e0e\u4ed6\u7684\u52a9\u624b\u8fdb\u6765\uff0c\u5f20\u606a\u5c06\u5496\u5561\u3001\u73bb\u7483\u676f\u3001\u6c34\u58f6\u62ff\u51fa\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u91cc\u53ea\u6709\u901f\u6eb6\u5496\u5561\uff0c\u4f60\u4eec\u5c31\u5c06\u7a76\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6bcf\u6b21\u5230\u5317\u4eac\u90fd\u662f\u524d\u62e5\u540e\u7c07\u7684\uff0c\u8fd9\u6b21\u72ec\u81ea\u5728\u522b\u5885\u91cc\u62db\u5f85\u4ed6\u4eec\uff0c\u5927\u6982\u662f\u6709\u4e9b\u4e8b\u60c5\u6216\u8005\u6709\u4e9b\u4eba\u4e0d\u60f3\u8ba9\u8eab\u8fb9\u7684\u5de5\u4f5c\u4eba\u5458\u770b\u5230\uff0c\u8096\u745e\u6c11\u60f3\u7740\u8c08\u5b8c\u4e8b\u60c5\u8fd8\u662f\u8d76\u7d27\u8d70\u4eba\uff0c\u7ad9\u8d77\u6765\u63a5\u8fc7\u676f\u5b50\uff0c\u770b\u7740\u5ba4\u5185\u7684\u5e03\u7f6e\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5230\u5317\u4eac\u6765\u4f4f\u9152\u5e97\u603b\u662f\u627e\u4e0d\u5230\u8fd9\u79cd\u4f4f\u5bb6\u7684\u611f\u89c9\uff0c\u606a\u5c11\u5bf9\u751f\u6d3b\u7684\u8981\u6c42\u8fd8\u662f\u5f88\u82db\u523b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f4f\u9152\u5e97\uff0c\u4eba\u7a0d\u95f2\u4e0b\u6765\u5c31\u611f\u89c9\u5e94\u8be5\u627e\u4e9b\u4e8b\u60c5\u505a\u505a\uff0c\u5b8c\u5168\u4e0d\u4f1a\u7aef\u7740\u5496\u5561\u5750\u5728\u6c99\u53d1\u4e0a\u53d1\u6123\u2026\u2026\u201d\u5f20\u606a\u5750\u4e0b\u6765\uff0c\u7aef\u7740\u5496\u5561\u676f\u4e0e\u8096\u745e\u6c11\u5bd2\u6684\u3002</p><p>\u3000\u3000\u201c\u4e5f\u7684\u786e\u8981\u6b47\u4e00\u6b47\uff0c\u201d\u8096\u745e\u6c11\u7b11\u7740\u8bf4\uff0c\u201c\u4e2d\u6676\u5fae\u82af\u7684\u91d1\u5c71\u5de5\u5382\u5dee\u4e0d\u591a\u5b9a\u4e0b\u8c03\u5b50\u4e86\u5427\uff1f\u53ea\u53ef\u60dc\u524d\u9762\u6709\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u4e0e\u65b0\u4ead\u94a2\u94c1\u57fa\u5730\u9879\u76ee\uff0c\u4e0d\u7136\u4e2d\u6676\u5fae\u82af\u7684\u91d1\u5c71\u5de5\u5382\u5c06\u6c5f\u5357\u7701\u6709\u53f2\u4ee5\u6765\u6295\u8d44\u89c4\u6a21\u6700\u5927\u7684\u9879\u76ee\u3002\u9526\u6e56\u7684\u8868\u73b0\u603b\u80fd\u8ba9\u611f\u53d7\u5230\u5cf0\u56de\u8def\u8f6c\u3001\u67f3\u6697\u82b1\u660e\u7684\u5999\u610f\uff0c\u597d\u4e9b\u4eba\u8fd9\u65f6\u5019\u90fd\u6ee1\u5730\u7684\u627e\u773c\u955c\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4ed6\u8bf4\u9053\uff0c\u201c\u6211\u4eca\u5929\u7279\u610f\u8c03\u51fa\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1ESS\u57fa\u9891\u677f\u7684\u91c7\u8d2d\u6570\u636e\uff0c\u611f\u89c9\u8054\u4fe1\u8fd9\u4e24\u4e2a\u6708\u7684ESS\u624b\u673a\u8d70\u91cf\u8fd8\u53ef\u4ee5\uff0c\u5c31\u662f\u4e0d\u6e05\u695a\u5b9e\u9645\u7684\u5e93\u5b58\u60c5\u51b5\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000ESS\u624b\u673a\u662f\u6307\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u751f\u4ea7\u51fa\u7684\u624b\u673a\uff0cESS\u624b\u673a\u7684\u57fa\u9891\u677f\u5b8c\u5168\u7531\u9526\u6e56\u4f9b\u5e94\uff0c\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u7684ESS\u624b\u673a\u4ea7\u91cf\u5f20\u606a\u81ea\u7136\u662f\u638c\u63e1\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u4f46\u662f\u5b9e\u9645\u7684\u9500\u91cf\u5374\u4e0d\u4e00\u5b9a\u51c6\u786e\u3002</p><p>\u3000\u3000\u201c\u7ecf\u9500\u5546\u624b\u91cc\u6216\u8bb8\u8fd8\u6709\u4e9b\u5e93\u5b58\uff0c\u201d\u8096\u745e\u6c11\u8bf4\u9053\uff0c\u201c\u8054\u4fe1\u8fd9\u8fb9\u53ea\u89c9\u5f97\u4ea7\u91cf\u4e25\u91cd\u4e0d\u8db3\uff0c\u8fd8\u5e0c\u671b\u9526\u6e56\u80fd\u653e\u5927\u57fa\u9891\u677f\u7684\u4f9b\u8d27\u91cf\u5462\u3002\u201d</p><p>\u3000\u3000\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u7684\u7b2c\u4e00\u4ee3\u624b\u673a\u867d\u7136\u5728\u529f\u80fd\u4e0a\u8fd8\u5f88\u5355\u4e00\uff0c\u624b\u673a\u9020\u578b\u4e5f\u8c08\u4e0d\u4e0a\u591a\u4e48\u7684\u7cbe\u81f4\u65f6\u5c1a\uff0c\u4e0d\u8fc7\u6027\u80fd\u7a33\u5b9a\uff0c\u53c8\u662f\u5e02\u573a\u552f\u4e00\u4e24\u6b3e\u552e\u4ef7\u4f4e\u4e8e\u4e00\u5343\u4e94\u767e\u5143\u7684\u4f4e\u4ef7\u624b\u673a\uff0c\u4ece\u4e24\u4e2a\u6708\u524d\u63a8\u51fa\u5e02\u573a\u4e4b\u540e\uff0c\u5c31\u9887\u53d7\u6b22\u8fce\uff0c\u53ea\u662f\u9526\u6e56\u4e25\u683c\u63a7\u5236\u57fa\u9891\u677f\u7684\u4f9b\u8d27\u91cf\uff0c\u65e0\u8bba\u662f\u8054\u4fe1\u8fd8\u662f\u79d1\u738b\u9ad8\u79d1\uff0c\u90fd\u65e0\u8bba\u901a\u8fc7\u6269\u5927\u9500\u91cf\u8fd8\u725f\u53d6\u66f4\u591a\u7684\u5229\u6da6\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u6025\u5df4\u5df4\u7684\u8dd1\u8fc7\u6765\u89c1\u5f20\u606a\uff0c\u5c31\u662f\u5e0c\u671b\u9526\u6e56\u80fd\u653e\u5f00\u57fa\u9891\u677f\u7684\u4f9b\u5e94\u3002</p><p>\u3000\u3000\u201c\u7b2c\u4e8c\u4ee3\u57fa\u5e26\u82af\u7247\u6280\u672f\u4f1a\u66f4\u6210\u719f\u4e00\u4e9b\uff0c\u54ea\u6015\u53ea\u80fd\u5f80\u524d\u8fc8\u4e00\u5c0f\u6b65\uff0c\u5bf9\u6211\u4eec\u6765\u8bf4\u4e5f\u662f\u610f\u4e49\u91cd\u5927\uff0c\u7b49\u5230\u6027\u80fd\u66f4\u7a33\u5b9a\u7684\u7b2c\u4e8c\u4ee3\u57fa\u5e26\u82af\u7247\u51fa\u6765\u518d\u6269\u5927\u4f9b\u8d27\u91cf\u4e5f\u8bb8\u66f4\u5408\u9002\u4e00\u4e9b\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u65f6\u95f4\u4e5f\u5feb\u4e86\uff0c\u6625\u8282\u540e\u5c31\u80fd\u5c06\u6837\u7247\u63d0\u4f9b\u7ed9\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u3002\u201d</p><p>\u3000\u3000\u201c\u8054\u901a\u4e89\u53d6\u8fd9\u4e24\u4e2a\u6708\u5c31\u5f7b\u5e95\u7684\u5c06GSM\u7f51\u7edc\u9057\u7559\u95ee\u9898\u7406\u987a\uff0c\u8fd9\u4e2a\u6d88\u606f\u606a\u5c11\u60f3\u5fc5\u65e9\u5c31\u77e5\u9053\u4e86\uff0c\u201d\u8096\u745e\u6c11\u8bf4\u9053\uff0c\u201c\u6709\u4e2a\u6d88\u606f\uff0c\u8fd8\u8981\u8ddf\u9526\u6e56\u77e5\u4f1a\u4e00\u58f0\uff0c\u4e5f\u5e0c\u671b\u9526\u6e56\u80fd\u652f\u6301\u6211\u4eec\u8054\u4fe1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5f20\u606a\u8f7b\u5e94\u4e86\u4e00\u58f0\uff0c\u7b49\u8096\u745e\u6c11\u8bf4\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u8054\u901a\u589e\u5f3a\u7ade\u4e89\u529b\uff0c\u6253\u7b97\u5b66\u6d77\u5916\u8fd0\u8425\u5546\u8d70\u624b\u673a\u5b9a\u5236\u7684\u8fd0\u8425\u7b56\u7565\uff0c\u8054\u4fe1\u521a\u62ff\u5230\u8054\u901a\u7b2c\u4e00\u7b14\u5b9a\u5236\u624b\u673a\u8ba2\u5355\u2026\u2026\u201d\u8096\u745e\u6c11\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bb0\u4e0d\u5f97\u524d\u5c18\u5f80\u4e16\u8bb0\u5fc6\u91cc\u7684\u8054\u901a\u7b2c\u4e00\u6279\u5b9a\u5236\u624b\u673a\u662fGSM\u624b\u673a\u8fd8\u662fCDMA\u624b\u673a\uff0c\u4e0d\u8fc7\u6b64\u65f6\u7684\u8054\u901a\u7b2c\u4e00\u6279\u5b9a\u5236\u624b\u673a\u5374\u662fGSM\u624b\u673a\uff0c\u8054\u901a\u7684CDMA\u7f51\u7edc\u6700\u65e9\u8fd8\u8981\u7b49\u5230\u660e\u5e74\u5e74\u4e2d\u624d\u80fd\u5b8c\u5168\u5efa\u6210\u3002</p><p>\u3000\u3000\u6240\u8c13\u7684\u5b9a\u5236\u7b56\u7565\uff0c\u5c31\u662f\u7528\u6237\u53ea\u8981\u4e0e\u79fb\u52a8\u901a\u8baf\u8fd0\u8425\u5546\u8054\u901a\u7b7e\u8ba2\u4e00\u5b9a\u671f\u9650\u7684\u5728\u7f51\u5408\u540c\uff0c\u5c31\u53ef\u4ee5\u4f4e\u4e8e\u6210\u672c\u4ef7\u4e70\u5230\u65b0\u624b\u673a\uff0c\u6b64\u4e3e\u65e0\u7591\u80fd\u8ba9\u8054\u901a\u66f4\u52a0\u8fc5\u901f\u7684\u53d1\u5c55\u7528\u6237\uff0c\u5b9a\u5236\u624b\u673a\u7684\u9500\u552e\u91cf\u4e5f\u5c06\u76f8\u5f53\u53ef\u89c2\uff0c\u624b\u673a\u5382\u5546\u7684\u5229\u6da6\u5219\u7531\u8fd0\u8425\u5546\u8054\u901a\u6765\u8865\u8d34\u3002</p><p>\u3000\u3000\u6309\u8bf4\u8054\u8baf\u4e0e\u4e1c\u5174\u8ddf\u8054\u901a\u7684\u5173\u7cfb\u8ddf\u4eb2\u8fd1\u4e00\u4e9b\uff0c\u6700\u65e9\u90fd\u662f\u5c5e\u4e8e\u7535\u5b50\u5de5\u4e1a\u90e8\u4e00\u7cfb\u7684\uff0c\u4f46\u662f\u8054\u8baf\u3001\u4e1c\u5174\u7684\u624b\u673a\u90fd\u662f\u4ece\u56fd\u5916\u9ad8\u4ef7\u8fdb\u53e3\u7ec4\u4ef6\u751f\u4ea7\u624b\u673a\uff0c\u5229\u6da6\u7a7a\u95f4\u672c\u8eab\u5c31\u6709\u9650\uff0c\u6ca1\u6709\u4f4e\u4ef7\u63d0\u4f9b\u5b9a\u5236\u624b\u673a\u7684\u6761\u4ef6\uff0c\u6027\u80fd\u4e0e\u8d28\u91cf\u4e5f\u6ca1\u6709\u7279\u522b\u5927\u7684\u4f18\u52bf\u3002\u9000\u800c\u6c42\u5176\u6b21\uff0c\u8054\u901a\u8bd5\u6c34\u5b9a\u5236\u7b56\u7565\u53ea\u80fd\u9009\u62e9\u8ddf\u8054\u4fe1\u5408\u4f5c\uff0c\u6bd5\u7adf\u8054\u4fe1\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u751f\u4ea7\u7684\u624b\u673a\u80fd\u7ed9\u5b9a\u5236\u7b56\u7565\u63d0\u4f9b\u76f8\u5f53\u53ef\u89c2\u7684\u64cd\u4f5c\u7a7a\u95f4\uff0c\u751a\u81f3\u53ef\u4ee5\u76f4\u63a5\u91c7\u53d6\u201c\u5145\u8bdd\u8d39\u9001\u624b\u673a\u201d\u7684\u7b56\u7565\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u79d1\u738b\u9ad8\u79d1\u4e5f\u5e94\u8be5\u662f\u8054\u901a\u7684\u9009\u62e9\u4e4b\u4e00\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u79cd\u597d\u4e8b\u6682\u65f6\u8fd8\u8f6e\u4e0d\u5230\u79d1\u738b\u9ad8\u79d1\u7684\u5934\u4e0a\u3002</p><p>\u3000\u3000\u8054\u901a\u4ece\u4e5d\u56db\u5e74\u6210\u7acb\u4ee5\u6765\u5c31\u4e3b\u8981\u4ee5\u53d1\u5c55\u79fb\u52a8\u901a\u8baf\u4e1a\u52a1\u4e3a\u4e3b\uff0c\u53ea\u662f\u4e00\u76f4\u7ed9\u7535\u4fe1\u96c6\u56e2\u6b7b\u6b7b\u7684\u538b\u5236\u7740\uff0c\u4e5f\u662f\u5f53\u521d\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0e\u90ae\u7535\u90e8\u77db\u76fe\u7684\u4e3b\u8981\u8868\u73b0\u4e4b\u4e00\uff0c\u56fd\u52a1\u9662\u673a\u6784\u6539\u9769\u4e4b\u540e\uff0c\u4fe1\u606f\u4ea7\u4e1a\u90e8\u6210\u7acb\uff0c\u8054\u901a\u4e0e\u7535\u4fe1\u96c6\u56e2\u90fd\u5212\u5f52\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7ba1\u8f96\uff0c\u4e24\u8005\u4e4b\u95f4\u7684\u77db\u76fe\u624d\u5f97\u4ee5\u7f13\u548c\uff0c\u8054\u901a\u7684\u79fb\u52a8\u901a\u8baf\u4e1a\u52a1\u624d\u5f97\u5230\u4e9b\u53d1\u5c55\uff0c\u53ea\u4e0d\u8fc7\u4e5f\u8fd8\u662f\u8fdc\u8fdc\u843d\u540e\u4e8e\u7535\u4fe1\u96c6\u56e2\u3002</p><p>\u3000\u3000\u8054\u901a\u518d\u5dee\u52b2\uff0c\u4e5f\u662f\u56fd\u5185\u552f\u4e00\u4e24\u5bb6\u79fb\u52a8\u8fd0\u8425\u5546\u4e4b\u4e00\uff0c\u867d\u7136\u4e4b\u524d\u7684\u4e1a\u7ee9\u6bd4\u8f83\u5dee\u5f3a\u4eba\u610f\uff0c\u4f46\u662f\u5f20\u606a\u5fc3\u91cc\u5374\u6e05\u695a\u8054\u901a\u4ece\u96f6\u96f6\u5e74\u5f80\u540e\u51e0\u5e74\u7684\u53d1\u5c55\u8fd8\u662f\u975e\u5e38\u7684\u8fc5\u901f\uff0c\u6b63\u662f\u8054\u901a\u7684\u5d1b\u8d77\u8feb\u4f7f\u540e\u6765\u72ec\u7acb\u51fa\u6765\u7684\u4e2d\u56fd\u79fb\u52a8\u5927\u5e45\u964d\u4f4e\u79fb\u52a8\u901a\u8baf\u8d44\u8d39\uff0c\u6700\u7ec8\u4f7f\u56fd\u5185\u7684\u624b\u673a\u5e02\u573a\u5f97\u5230\u5f7b\u5e95\u7684\u6269\u5bb9\u3002</p><p>\u3000\u3000\u65e0\u8bba\u4ece\u54ea\u65b9\u9762\u6765\u8bf4\uff0c\u5f20\u606a\u90fd\u662f\u4e50\u4e8e\u770b\u5230\u8054\u901a\u53d8\u5f97\u66f4\u5f3a\u5927\uff0c\u53d8\u5f97\u5bf9\u5c06\u6765\u7684\u4e2d\u56fd\u79fb\u52a8\u66f4\u6709\u5a01\u80c1\uff0c\u5f53\u7136\uff0c\u4ed6\u4e5f\u6e05\u695a\u8096\u745e\u6c11\u7684\u8bdd\u8bf4\u5f97\u4e0d\u5c3d\u4e0d\u5b9e\uff0c\u8054\u901a\u5b9e\u884c\u5b9a\u5236\u7b56\u7565\uff0c\u5c31\u7b97\u518d\u5c0f\u5bb6\u5b50\u6c14\uff0c\u7b2c\u4e00\u6279\u5b9a\u5355\u4e5f\u4e0d\u4f1a\u4f4e\u4e8e\u5341\u4e07\u53ea\uff0c\u8054\u4fe1\u6ca1\u6709\u9526\u6e56\u652f\u6301\uff0c\u662f\u65e0\u6cd5\u4e00\u4e0b\u5b50\u63d0\u4f9b\u5341\u4e07\u53ea\u5b9a\u5236\u624b\u673a\u7684\uff1b\u53e6\u5916\u3002\u5b9a\u5236\u624b\u673a\u7684\u6210\u672c\u4e8b\u5b9e\u4e0a\u662f\u7531\u9526\u6e56\u51b3\u5b9a\u7684\uff0c\u5728\u6ca1\u6709\u5f97\u5230\u9526\u6e56\u7684\u80af\u5b9a\u7b54\u590d\u4e4b\u524d\uff0c\u8054\u4fe1\u4e0e\u8054\u901a\u4e4b\u95f4\u7684\u5b9a\u5236\u5408\u540c\u5c31\u4e0d\u597d\u7b7e\u3002</p><p>\u3000\u3000\u5f20\u606a\u7aef\u7740\u5496\u5561\u676f\uff0c\u7ad9\u8d77\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u6211\u771f\u662f\u8981\u795d\u8d3a\u8054\u4fe1\u4e86\uff0c\u53ef\u60dc\u53ea\u80fd\u62ff\u5496\u5561\u4ee3\u9152\u2026\u2026\u5982\u6b64\u770b\u6765\uff0c\u7b2c\u4e8c\u4ee3ESS\u57fa\u5e26\u82af\u7247\u7684\u6700\u540e\u6d4b\u8bd5\u8981\u6293\u7d27\u4e00\u4e9b\u4e86\uff0c\u6216\u8bb8\u80fd\u8d76\u4e0a\u63d0\u4f9b\u7ed9\u8054\u4fe1\u751f\u4ea7\u5b9a\u5236\u624b\u673a\u7528\u3002\u201d\u5fc3\u91cc\u60f3\u7740\uff0c\u8fd9\u4e5f\u7b97\u662f\u8d70\u4e0a\u7206\u4ea7\u80fd\u7684\u7b2c\u4e00\u6b65\u4e86\uff0c\u6bd4\u8f83\u5927\u5f20\u65d7\u9f13\u7684\u5e7f\u544a\u653b\u52bf\uff0c\u6da6\u7269\u7ec6\u65e0\u58f0\u4e00\u4e9b\uff0c\u6216\u8bb8\u80fd\u8ba9\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u3001\u4e09\u661f\u8fd9\u4e9b\u5382\u5546\u5fc3\u7406\u597d\u53d7\u4e00\u4e9b\uff0c\u4e5f\u80fd\u7ed9\u9526\u6e56\u66f4\u591a\u4e00\u4e9b\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u8bdd\u91cc\u7684\u610f\u601d\u9526\u6e56\u80fd\u8d76\u5728\u6625\u8282\u4e4b\u524d\u62ff\u51fa\u7b2c\u4e8c\u4ee3ESS\u57fa\u5e26\u82af\u7247\uff0c\u8096\u745e\u6c11\u5c31\u653e\u5bbd\u4e86\u5fc3\u3002\u6682\u65f6\u8fd8\u4e0d\u9700\u8981\u5f00\u53d1\u65b0\u7684\u624b\u673a\uff0c\u53ef\u4ee5\u76f4\u63a5\u5347\u7ea7\u57fa\u5e26\u82af\u7247\uff0c\u9664\u4e86\u6027\u80fd\u66f4\u7a33\u5b9a\u4e4b\u5916\uff0c\u8fd8\u53ef\u4ee5\u6700\u5927\u5e45\u5ea6\u7684\u6446\u8131\u4ea7\u91cf\u4e0a\u7684\u9650\u5236\uff0c\u7684\u786e\u662f\u4e2a\u597d\u6d88\u606f\u3002</p></article>"}}}]);
