(window.webpackJsonp=window.webpackJsonp||[]).push([[1104],{1127:function(p){p.exports={chapterTitle:null,title:"\u7b2c902\u7ae0 \u5510\u5b66\u8c26\u7684\u4ed5\u9014\u65b0\u8d77\u70b9",content:"<article><h2>\u7b2c902\u7ae0 \u5510\u5b66\u8c26\u7684\u4ed5\u9014\u65b0\u8d77\u70b9</h2><p>\u3000\u3000\u201c\u5230\u5317\u4eac\u4e0d\u5230\u4e24\u4e2a\u6708\uff0c\u4eba\u90fd\u80d6\u4e86\u4e24\u5708\u2026\u2026\u201d\u5510\u5b66\u8c26\u62cd\u4e86\u62cd\u8170\u95f4\u5fae\u663e\u7684\u8d58\u8089\uff0c\u7b11\u7740\u8ddf\u5f20\u606a\u6253\u8da3\uff0c\u5bf9\u53f6\u5efa\u658c\uff0c\u4ed6\u5374\u662f\u5f88\u6b63\u5e38\u7684\u63e1\u624b\u62db\u547c\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u624d\u770b\u89c1\u8bb8\u745e\u5e73\u4ece\u6821\u5185\u8d70\u8fc7\u6765\uff0c\u4e0d\u6653\u5f97\u4ed6\u6709\u4ec0\u4e48\u4e8b\u60c5\u803d\u6401\uff0c\u8fd9\u624d\u6025\u51b2\u51b2\u7684\u8d76\u4e86\u51fa\u6765\uff0c\u5f20\u606a\u7b11\u7740\u95ee\u4ed6\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u4f60\u4e5f\u6765\u4e2d\u592e\u515a\u6821\u5b66\u4e60\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u7b11\u6211\u6ca1\u6709\u8d44\u683c\u554a\uff0c\u201d\u8bb8\u745e\u5e73\u7b11\u7740\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u6bcf\u6b21\u51fa\u6765\u90fd\u524d\u62e5\u540e\u547c\u7684\uff0c\u5176\u4ed6\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u90fd\u5728\u5916\u9762\u7b49\u7740\uff0c\u4eba\u592a\u591a\u8fdb\u6765\uff0c\u6709\u4e9b\u60f9\u773c\u4e86\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\u8bf4\u9053\u3002</p><p>\u3000\u3000\u8bb8\u745e\u5e73\u7ed9\u5510\u5b66\u8c26\u5f53\u79d8\u4e66\u6709\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u884c\u653f\u7ea7\u522b\u4e0a\u4e86\u526f\u5904\u3002\u65e0\u8bba\u662f\u5f90\u5b66\u5e73\uff0c\u8fd8\u662f\u5510\u5b66\u8c26\u53bb\u6c5f\u5357\u7701\u90fd\u6ca1\u6709\u4f53\u5df1\u7684\u4eba\uff0c\u5f90\u5b66\u5e73\u5c06\u6c5f\u5357\u7701\u5f53\u6210\u4ed6\u4ed5\u9014\u7684\u6700\u540e\u4e00\u7ad9\uff0c\u6ca1\u6709\u4ec0\u4e48\u987e\u8651\uff0c\u80fd\u591f\u653e\u5f00\u624b\u811a\u53bb\u6574\u987f\u6c5f\u5357\u7701\u7684\u9648\u89c4\u964b\u4fd7\uff0c\u4e0d\u8fc7\u5510\u5b66\u8c26\u5374\u662f\u5c06\u6c5f\u5357\u7701\u5f53\u6210\u81ea\u5df1\u4ed5\u9014\u65b0\u7684\u8d77\u70b9\uff0c\u8981\u662f\u4ee5\u4e3a\u62b1\u4e0a\u5f90\u5b66\u5e73\u7684\u7c97\u817f\u5c31\u53ef\u4ee5\u9ad8\u6795\u65e0\u5fe7\uff0c\u90a3\u5c31\u611a\u4e0d\u53ef\u53ca\u4e86\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u8981\u5e26\u4e00\u4e24\u4e2a\u4f53\u5df1\u7684\u4eba\u4e00\u8d77\u53bb\u6c5f\u5357\uff0c\u4e5f\u6ca1\u6709\u6bd4\u8bb8\u745e\u5e73\u66f4\u5408\u9002\u7684\u4eba\u4e86\u3002\u8bb8\u745e\u5e73\u8ddf\u7740\u5510\u5b66\u8c26\u53bb\u6c5f\u5357\u7701\uff0c\u8d77\u70b9\u8981\u6bd4\u6d77\u5dde\u8fd8\u8981\u597d\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u5bf9\u6d77\u5dde\u7b97\u662f\u653e\u624b\u4e86\uff0c\u8bb8\u745e\u5e73\u4e5f\u5c31\u6ca1\u6709\u7559\u5728\u6d77\u5dde\uff0c\u8fd9\u6b21\u5148\u8ddf\u7740\u5230\u5317\u4eac\u6765\uff0c\u5148\u6302\u540d\u5230\u6d77\u5dde\u5e02\u9a7b\u4eac\u529e\u4e8b\u5904\uff0c\u7b49\u5510\u5b66\u8c26\u7684\u804c\u52a1\u6b63\u5f0f\u8c03\u6574\u8fc7\u6765\uff0c\u4ed6\u53ef\u4ee5\u968f\u65f6\u5148\u6302\u5165\u6c5f\u5357\u7701\u529e\u4e8b\u5904\u7684\u540d\u4e0b\uff0c\u7136\u540e\u518d\u968f\u5510\u5b66\u8c26\u53bb\u6c5f\u5357\u3002</p><p>\u3000\u3000\u4e2d\u592e\u515a\u6821\u5185\u7684\u9053\u8def\u90fd\u662f\u6d53\u5bc6\u7684\u6797\u836b\u906e\u95ed\uff0c\u5915\u9633\u659c\u6656\u4ece\u6811\u6728\u95f4\u9699\u95f4\u5c04\u6765\uff0c\u4eff\u4f5b\u7ed9\u6c34\u6ce5\u8def\u62b9\u4e0a\u4e00\u5c42\u91d1\u8272\u7684\u5149\u6cfd\uff0c\u6797\u95f4\u7684\u8349\u4e1b\u8fd8\u662f\u78a7\u7eff\uff0c\u8fd8\u6ca1\u6709\u9732\u51fa\u79cb\u540e\u7684\u6e05\u51b7\u67af\u8272\u6765\u3002\u4ed6\u4eec\u9009\u7684\u9910\u5385\u770b\u4e0a\u53bb\u50cf\u662f\u4e94\u516d\u5341\u5e74\u4ee3\u5174\u5efa\u7684\u4f1a\u5802\u5f0f\u5efa\u7b51\uff0c\u8d70\u5c06\u8fdb\u53bb\uff0c\u91cc\u9762\u7684\u88c5\u6f62\u5012\u4e5f\u7b97\u5f97\u4e0a\u91d1\u78a7\u8f89\u714c\u3002\u4ed6\u4eec\u8fdb\u6765\u7528\u9910\u7b97\u662f\u65e9\u7684\uff0c\u9910\u5385\u91cc\u6ca1\u6709\u591a\u5c11\u4eba\u3002\u5f20\u606a\u5fc3\u60f3\uff1a\u5373\u4f7f\u5230\u4e86\u7528\u9910\u65f6\u95f4\uff0c\u5927\u6982\u4e5f\u4e0d\u4f1a\u6709\u591a\u5c11\u4eba\u5427\uff1f\u8fdb\u4e2d\u592e\u515a\u6821\u5b66\u4e60\u7684\u90fd\u662f\u4e2d\u9ad8\u7ea7\u5b98\u5458\u4e14\u5927\u591a\u6570\u90fd\u624b\u63e1\u5b9e\u6743\uff0c\u50cf\u5510\u5b66\u8c26\u60f3\u8fc7\u5f97\u8212\u670d\u4e00\u4e9b\uff0c\u751f\u6d3b\u5b8c\u5168\u53ef\u4ee5\u8ba9\u6d77\u5dde\u9a7b\u4eac\u529e\u4e8b\u5904\u7167\u987e\u5f97\u65e0\u5fae\u4e0d\u81f3\uff0c\u7279\u522b\u662f\u90a3\u4e9b\u65e0\u5b54\u4e0d\u5165\u7684\u5546\u4eba\uff0c\u5730\u65b9\u4e0a\u7684\u7236\u6bcd\u5b98\u8fdb\u4eac\u5b66\u4e60\uff0c\u4ed6\u4eec\u53c8\u600e\u4e48\u4f1a\u653e\u5f03\u5982\u6b64\u826f\u673a\u4e0d\u8d76\u8fc7\u6765\u8ddf\u7236\u6bcd\u5b98\u4eb2\u8fd1\u5462\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u5510\u5b66\u8c26\u5927\u4ecb\u7ecd\u8d77\u8fd9\u4e9b\u5929\u6765\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7684\u5b9e\u9645\u8fdb\u5c55\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u65b0\u4ead\u7684\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u5012\u662f\u6025\u7740\u6765\u89c1\u5510\u4f2f\u4f2f\u4f60\uff0c\u53ef\u60dc\u8fd9\u65f6\u5019\u8fd8\u5e08\u51fa\u65e0\u540d\u2014\u2014\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4e0e\u7aa6\u552f\u6d9b\u7684\u76f4\u63a5\u63a5\u89e6\u4e5f\u5c31\u4ec5\u9650\u4e8e\u4e0a\u56de\u5728\u9999\u6e2f\u56fd\u9645\u673a\u573a\u7ed9\u7aa6\u552f\u6d9b\u622a\u4f4f\u7684\u90a3\u6b21\uff0c\u8fd9\u4e9b\u5929\u4e5f\u5728\u7814\u7a76\u6c5f\u5357\u7701\u7684\u4e00\u4e9b\u6750\u6599\uff0c\u4ece\u7aa6\u552f\u6d9b\u4ee5\u5f80\u7684\u5c65\u5386\u4e0e\u4eba\u671b\u6765\u770b\uff0c\u77e5\u9053\u8fd9\u4eba\u8fd8\u662f\u76f8\u5f53\u6709\u4e9b\u653f\u6cbb\u62b1\u8d1f\u7684\uff0c\u4e5f\u6709\u80fd\u529b\u4e0e\u5e72\u4e8b\u7684\u9b44\u529b\u3002\u8fd9\u6b21\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u3001\u672a\u6765\u8fd8\u8981\u5927\u89c4\u6a21\u7684\u53d1\u5c55\u6e2f\u53e3\u7ecf\u6d4e\uff0c\u65b0\u4ead\u5e02\u53d7\u60e0\u5e45\u5ea6\u4e4b\u5927\u5c06\u8d85\u4e4e\u4e00\u822c\u4eba\u7684\u60f3\u8c61\uff0c\u53ef\u4ee5\u9884\u89c1\u65b0\u4ead\u7684\u7ecf\u6d4e\u53d1\u5c55\u901f\u5ea6\u5c06\u8fdc\u8fdc\u8d85\u8d8a\u6c5f\u5357\u7701\u7684\u5176\u4ed6\u5730\u533a\uff0c\u5373\u4f7f\u8d85\u8d8a\u91d1\u5c71\u4e5f\u4e0d\u662f\u592a\u96be\u60f3\u8c61\u7684\u4e8b\u60c5\uff0c\u90a3\u7aa6\u552f\u6d9b\u672a\u6765\u5728\u6c5f\u5357\u7701\u7684\u5730\u4f4d\u4e5f\u5c31\u4e0d\u5bb9\u5ffd\u89c6\u2014\u2014\u5510\u5b66\u8c26\u4e0d\u4f1a\u56e0\u4e3a\u5373\u5c06\u5347\u4efb\u526f\u7701\u90e8\u7ea7\u800c\u5bf9\u7aa6\u552f\u6d9b\u6709\u6240\u8f7b\u6162\uff0c\u518d\u8bf4\u4ed6\u5df2\u7ecf\u5c06\u6c5f\u5357\u7701\u5f53\u6210\u4ed6\u4ed5\u9014\u7684\u65b0\u8d77\u70b9\uff0c\u505a\u597d\u4e86\u5728\u6c5f\u5357\u624e\u6839\u7acb\u8db3\u5341\u5e74\u7684\u51c6\u5907\uff0c\u5728\u6c5f\u5357\u7701\u9700\u8981\u4e00\u6279\u653f\u6cbb\u4e0a\u7684\u76df\u53cb\u4e0e\u652f\u6301\u8005\uff0c\u800c\u4e0d\u662f\u7b80\u5355\u7684\u53bb\u4f9d\u8d56\u5f90\u5b66\u5e73\u3002\u5c31\u50cf\u9526\u6e56\u5728\u6c5f\u5357\u7701\u7684\u5229\u76ca\uff0c\u4e5f\u4e0d\u662f\u4e09\u56db\u4f4d\u8eab\u5c45\u9ad8\u4f4d\u7684\u5b98\u5458\u5c31\u80fd\u591f\u4fdd\u8bc1\u7684\uff0c\u5510\u5b66\u8c26\u770b\u7740\u5f20\u606a\uff0c\u867d\u8bf4\u4ece\u5c0f\u770b\u7740\u4ed6\u957f\u5927\uff0c\u4ed6\u8111\u5b50\u91cc\u8fd0\u8f6c\u7684\u4e1c\u897f\u8fd8\u771f\u662f\u8d85\u4e4e\u60f3\u8c61\u554a\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7b11\u7740\u8bf4\uff1a\u201c\u6709\u4ec0\u4e48\u5e08\u51fa\u65e0\u540d\u7684\uff0c\u7aa6\u552f\u6d9b\u5230\u5317\u4eac\u6765\uff0c\u4f5c\u4e3a\u670b\u53cb\uff0c\u4e5f\u662f\u53ef\u4ee5\u805a\u4e00\u805a\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u7aa6\u552f\u6d9b\u8fd1\u4e24\u5929\u4e5f\u662f\u8981\u6765\u4e00\u8d9f\u5317\u4eac\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u53f6\u54e5\u8fd9\u6bb5\u65f6\u95f4\u4f1a\u7559\u5728\u5317\u4eac\uff0c\u6211\u4e5f\u4f1a\u5728\u5317\u4eac\u591a\u7559\u4e24\u5929\uff0c\u6709\u673a\u4f1a\uff0c\u90a3\u5c31\u89c1\u4e00\u9762\u5427\u2026\u2026\u867d\u8bf4\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u4e00\u4e8b\u624d\u521a\u521a\u62ac\u51fa\u6c34\u9762\uff0c\u65b0\u4ead\u5e02\u91cc\u7684\u51c6\u5907\u5de5\u4f5c\u5374\u5f88\u5145\u5206\uff0c\u518d\u8bf4\u7aa6\u552f\u6d9b\u4e5f\u6709\u4e9b\u62c5\u5fe7\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u534e\u4eba\u8d44\u672c\u9003\u79bb\u5370\u5c3c\u4f1a\u560e\u7136\u800c\u6b62\uff0c\u6211\u60f3\u4ed6\u4e5f\u662f\u5e0c\u671b\u5c06\u4e00\u4e9b\u5de5\u4f5c\u62a2\u5728\u524d\u9762\u505a\u5b8c\uff0c\u4e0d\u7136\u7b49\u7701\u91cc\u3001\u90e8\u59d4\u5c06\u5de5\u4f5c\u7406\u987a\u5c11\u8bf4\u4e5f\u8981\u4e09\u4e94\u4e2a\u6708\u7684\u65f6\u95f4\u2014\u2014\u6b63\u5f0f\u52a8\u5de5\u5174\u5efa\u80af\u5b9a\u8981\u62d6\u5230\u660e\u5e74\u3002\u201d</p><p>\u3000\u3000\u201c\u5370\u5c3c\u653f\u5c40\u4e0d\u4f1a\u6c38\u8fdc\u52a8\u8361\u4e0b\u53bb\uff0c\u5370\u5c3c\u6709\u4eb2\u7f8e\u4e14\u5f3a\u52bf\u7684\u653f\u6cbb\u9886\u8896\u4e0a\u53f0\u6765\u7a33\u5b9a\u653f\u5c40\uff0c\u534e\u4eba\u8d44\u672c\u64a4\u79bb\u5370\u5c3c\u7684\u8d8b\u52bf\u5c31\u53ef\u80fd\u5f97\u5230\u904f\u6b62\uff0c\u201d\u5510\u5b66\u8c26\u95ee\u9053\uff0c\u201c\u4f60\u7ed9\u6211\u4ea4\u4e2a\u5e95\uff0c\u878d\u8d44\u7684\u4e8b\u60c5\u5230\u5e95\u6709\u591a\u5927\u7684\u628a\u63e1\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u4e5f\u6ca1\u6709\u5916\u4eba\uff0c\u5c06\u5e95\u8bf4\u7ed9\u5510\u4f2f\u4f2f\u4f60\u542c\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u5546\u4e8b\u3001\u5357\u6d0b\u6d77\u8fd0\u7b49\u76f8\u5173\u65b9\u65e9\u5c31\u5f00\u59cb\u7b79\u96c6\u5efa\u6e2f\u7684\u8d44\u91d1\uff0c\u52a0\u4e0a\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u3001\u6d0b\u6d66\u8239\u4e1a\u7684\u51fa\u8d44\uff0c\u73b0\u5728\u6211\u4eec\u6b63\u79ef\u6781\u8981\u5c06\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u62d6\u8fdb\u6765\uff0c\u5c31\u7b97\u534e\u4eba\u8d44\u672c\u4ece\u5370\u5c3c\u64a4\u79bb\u73b0\u5728\u5c31\u55c4\u7136\u800c\u6b62\uff0c\u5341\u591a\u4ebf\u3001\u5c0f\u4e8c\u5341\u4ebf\u7f8e\u5143\u7684\u878d\u8d44\u4e5f\u4e0d\u6210\u95ee\u9898\u2026\u2026\u5173\u952e\u8fd8\u662f\u56fd\u5185\u7684\u95ee\u9898\u2014\u2014\u4f46\u662f\u8fd9\u4e2a\u5e95\uff0c\u6211\u4e0d\u80fd\u968f\u4fbf\u4ea4\u5f85\u51fa\u6765\uff0c\u4e4b\u524d\u4e5f\u5c31\u544a\u8bc9\u5f90\u4f2f\u4f2f\u4e00\u58f0\uff0c\u5bf9\u5176\u4ed6\u4eba\u90fd\u5b88\u7740\u53e3\u98ce\u2014\u2014\u4e5f\u4e0d\u662f\u8981\u5201\u96be\u522b\u4eba\uff0c\u53ea\u4e0d\u8fc7\u6211\u4eec\u4e0d\u53bb\u5201\u96be\u522b\u4eba\uff0c\u5c31\u4f1a\u7ed9\u522b\u4eba\u5201\u96be\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7b11\u4e86\u7b11\uff0c\u6c5f\u5357\u90a3\u8fb9\u4e0d\u4f1a\u5b58\u5728\u4ec0\u4e48\u95ee\u9898\uff0c\u4f46\u662f\u8fd9\u4e2a\u9879\u76ee\u8981\u5728\u90e8\u59d4\u8fc7\u597d\u51e0\u9053\u5173\u5361\uff0c\u90e8\u59d4\u7684\u5173\u5361\u53ef\u4e0d\u662f\u90a3\u4e48\u597d\u8fc7\u7684\u3002</p><p>\u3000\u3000\u5b98\u573a\u5c31\u662f\u4e00\u5927\u67d3\u7f38\uff0c\u5730\u65b9\u4e0e\u90e8\u59d4\u90fd\u662f\u5927\u540c\u5c0f\u5f02\u7684\uff0c\u82e5\u662f\u878d\u8d44\u6ca1\u6709\u7740\u843d\u3001\u8fd8\u968f\u65f6\u6709\u53ef\u80fd\u4f1a\u9519\u8fc7\u8fd9\u6b21\u673a\u9047\uff0c\u90e8\u59d4\u7684\u5b98\u5458\u6015\u627f\u62c5\u62d6\u5ef6\u7684\u8d23\u4efb\uff0c\u4e8b\u60c5\u4f1a\u7d27\u7740\u529e\uff0c\u4e2d\u592e\u5927\u4f6c\u4e2d\u5bf9\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7559\u5fc3\u7684\u66fe\u5982\u5723\u4e5f\u4f1a\u7d27\u76ef\u7740\u4e0b\u9762\u7684\u90e8\u59d4\uff1b\u8981\u662f\u8ba9\u4ed6\u4eec\u77e5\u9053\u878d\u8d44\u5df2\u7ecf\u4e0d\u6210\u95ee\u9898\uff0c\u6ecb\u751f\u6020\u6162\u7684\u60c5\u7eea\u51e0\u4e4e\u662f\u80af\u5b9a\u7684\uff0c\u6307\u4e0d\u5b9a\u8fd8\u4f1a\u8df3\u51fa\u4e24\u4e09\u53ea\u62e6\u8def\u864e\u51fa\u6765\uff0c\u66fe\u5982\u5723\u90a3\u65f6\u5019\u53cd\u800c\u53c8\u4e0d\u4f1a\u7ad9\u51fa\u6765\u5e2e\u7740\u8bf4\u8bdd\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u90fd\u6ca1\u6709\u5c06\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7684\u4e8b\u60c5\u6258\u4eba\u5f80\u66fe\u5982\u5723\u526f\u603b\u7406\u8033\u6735\u91cc\u5439\u98ce\uff0c\u66fe\u5982\u5723\u526f\u603b\u7406\u516b\u6708\u95f4\u5230\u91d1\u5c71\u89c6\u5bdf\u707e\u60c5\u63d0\u5230\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u4e00\u4e8b\uff0c\u6709\u53ef\u80fd\u662f\u4ed6\u5fc3\u8840\u6765\u6f6e\u7684\u5173\u5fc3\u8d77\u5730\u65b9\u4e0a\u7684\u7ecf\u6d4e\u5efa\u8bbe\u2014\u2014\u6bd5\u7adf\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u4e00\u4e8b\u63d0\u8d77\u90fd\u6709\u5341\u5e74\u65f6\u95f4\u4e86\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u6709\u5176\u4ed6\u4eba\u5c06\u8fd9\u4e8b\u4f20\u5230\u4ed6\u8033\u6735\u91cc\u3002</p><p>\u3000\u3000\u53ea\u662f\u8fd9\u91cc\u9762\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u90fd\u6ca1\u6709\u63a2\u542c\u6e05\u695a\uff0c\u6000\u7591\u7aa6\u552f\u6d9b\u80cc\u540e\u7684\u5173\u7cfb\u4e0d\u6d45\uff0c\u53ea\u4e0d\u8fc7\u8fd9\u65f6\u5019\u8fd8\u4e0d\u4fbf\u76f4\u63a5\u53bb\u5957\u7aa6\u552f\u6d9b\u7684\u53e3\u98ce\uff0c\u603b\u4e4b\u968f\u7740\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u9879\u76ee\u9010\u6b65\u5f80\u524d\u63a8\u8fdb\uff0c\u80cc\u540e\u5185\u60c5\u90fd\u4f1a\u6c34\u843d\u77f3\u51fa\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u544a\u8bc9\u5510\u5b66\u8c26\uff1a\u201c\u540e\u5929\u662f\u987e\u957f\u5e9a\u8001\u7237\u5b50\u4e03\u5341\u4e5d\u5c81\u7684\u6563\u751f\u65e5\uff0c\u4f1a\u6446\u51e0\u5e2d\u79c1\u5bb4\uff0c\u4e0d\u4f1a\u4e13\u7a0b\u7684\u53d1\u8bf7\u5e16\uff0c\u5230\u65f6\u5019\u4f1a\u6253\u7535\u8bdd\u6765\u8bf7\u5510\u4f2f\u4f2f\u4e00\u8d77\u8fc7\u53bb\u805a\u4e00\u805a\u2014\u2014\u6211\u5148\u8ddf\u5510\u4f2f\u4f2f\u8bf4\u4e00\u58f0\uff1b\u53e6\u5916\uff0c\u53f6\u54e5\u660e\u5929\u8ba9\u4eba\u5c06\u53f6\u8001\u7237\u5b50\u4ece\u5efa\u90ba\u63a5\u6765\uff0c\u5510\u4f2f\u4f2f\u5012\u662f\u8981\u5c06\u660e\u540e\u5929\u7684\u65f6\u95f4\u7a7a\u51fa\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u884c\uff01\u201d\u5510\u5b66\u8c26\u723d\u5229\u7684\u7b54\u5e94\u4e0b\u6765\uff0c\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u4e0d\u7b54\u5e94\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7684\u8fd9\u6b21\u5347\u8fc1\u53ef\u4ee5\u8bf4\u662f\u4e00\u6b21\u8f83\u5076\u7136\u7684\u673a\u9047\uff0c\u5012\u4e5f\u662f\u770b\u5230\u5728\u4ed5\u9014\u4e0a\u66f4\u5e7f\u9614\u7684\u524d\u666f\uff0c\u4f46\u4ed6\u662f\u4ece\u5730\u65b9\u5347\u4e0a\u6765\u7684\uff0c\u5728\u4e2d\u592e\u6ca1\u6709\u4ec0\u4e48\u4eba\u8109\u8d44\u6e90\uff0c\u8fd9\u5c06\u662f\u5236\u7ea6\u4ed6\u65e5\u540e\u5728\u7701\u90e8\u7ea7\u804c\u4f4d\u4e0a\u53d1\u5c55\u7684\u6700\u5927\u74f6\u9888\u3002\u867d\u8bf4\u9526\u6e56\u53d1\u5c55\u8d70\u51fa\u6d77\u5dde\u4e4b\u540e\uff0c\u5c31\u4e0e\u53f6\u5bb6\u3001\u987e\u5bb6\u6709\u7740\u7d27\u5bc6\u7684\u8054\u7cfb\uff0c\u4f46\u662f\u4e4b\u524d\u7684\u5510\u5b66\u8c26\u4e00\u76f4\u90fd\u5c40\u9650\u5728\u6d77\u5dde\u4e00\u9685\u53d1\u5c55\uff0c\u5012\u4e5f\u6ca1\u6709\u591a\u5c11\u673a\u4f1a\u8ddf\u53f6\u5bb6\u3001\u987e\u5bb6\u7684\u6210\u5458\u7ed3\u8bc6\uff0c\u4e0e\u53f6\u5bb6\u7684\u53f6\u81fb\u6c11\u3001\u53f6\u795d\u6c11\uff0c\u987e\u5bb6\u7684\u987e\u5174\u79b9\u3001\u9648\u65b0\u6c11\u7b49\u4eba\u4e5f\u4ec5\u4ec5\u7b97\u662f\u8ba4\u8bc6\u800c\u5df2\uff0c\u5e76\u6ca1\u6709\u6df1\u4ea4\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u987e\u5bb6\u5e73\u65e5\u4e5f\u4e0d\u5f20\u626c\uff0c\u987e\u8001\u7237\u5b50\u5373\u4f7f\u8981\u8fc7\u6563\u751f\u65e5\u4e5f\u662f\u5bb6\u91cc\u4eba\u5c0f\u805a\u4e00\u4e0b\uff0c\u8fd9\u6b21\u6446\u51e0\u5e2d\u79c1\u5bb4\u53ef\u4ee5\u8bf4\u662f\u4e13\u95e8\u4e3a\u5510\u5b66\u8c26\u5b89\u6392\uff0c\u751a\u81f3\u8fd8\u5c06\u53f6\u5bb6\u8001\u7237\u5b50\u53f6\u7956\u8303\u4ece\u5efa\u90ba\u63a5\u8fc7\u6765\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5e74\u6765\uff0c\u5b98\u573a\u4e0a\u7684\u5347\u8fc1\u8c03\u4efb\u90fd\u5f62\u6210\u4e86\u56fa\u6709\u7684\u89c4\u5f8b\uff0c\u5373\u4f7f\u6709\u6df1\u539a\u7684\u4eba\u8109\u5173\u7cfb\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u5c31\u80fd\u51fa\u5934\uff1b\u5f53\u7136\u4e86\uff0c\u6ca1\u6709\u4eba\u8109\u5173\u7cfb\uff0c\u5510\u5b66\u8c26\u80fd\u5728\u4e94\u5341\u5c81\u4e4b\u524d\u8d70\u5230\u5e38\u59d4\u526f\u7701\u7684\u4f4d\u7f6e\uff0c\u53ef\u4ee5\u8bf4\u662f\u6781\u81f4\u4e86\u3002</p><p>\u3000\u3000\u653f\u6cbb\u8d44\u6e90\u662f\u6709\u9650\u7684\uff0c\u5373\u4f7f\u53f6\u5bb6\u3001\u987e\u5bb6\u5229\u7528\u5168\u90e8\u7684\u4eba\u8109\u5173\u7cfb\uff0c\u4e5f\u53ea\u80fd\u57f9\u517b\u4e00\u4e24\u4e2a\u6838\u5fc3\u4eba\u7269\u51fa\u6765\uff0c\u5510\u5b66\u8c26\u80fd\u4e0d\u80fd\u5206\u4eab\u53f6\u5bb6\u3001\u987e\u5bb6\u7684\u653f\u6cbb\u8d44\u6e90\u518d\u4e0a\u4e00\u5c42\uff0c\u9664\u4e86\u9526\u6e56\u4e0e\u53f6\u5bb6\u3001\u987e\u5bb6\u7684\u76df\u53cb\u5173\u7cfb\u4e4b\u5916\uff0c\u8fd8\u9700\u8981\u5510\u5b66\u8c26\u81ea\u5df1\u83b7\u5f97\u4ed6\u4eec\u7684\u80af\u5b9a\u624d\u884c\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8bf4\u9053\uff1a\u201c\u5510\u4f2f\u4f2f\u8fd9\u4e24\u4e2a\u6708\u5728\u5317\u4eac\u68cb\u827a\u5e94\u8be5\u63d0\u9ad8\u4e86\u4e0d\u5c11\u5427\uff1f\u591c\u91cc\u53f6\u54e5\u8fd8\u6709\u4eba\u8981\u89c1\uff0c\u6211\u53cd\u6b63\u65e0\u804a\uff0c\u7b49\u4f1a\u513f\u627e\u4e2a\u5730\u65b9\u966a\u5510\u4f2f\u4f2f\u4f60\u4e0b\u4e24\u76d8\u68cb\u3002\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5f53\u771f\u4ee5\u4e3a\u73b0\u5728\u8fd8\u80fd\u5728\u68cb\u76d8\u4e0a\u6b3a\u8d1f\u6211\u4e0d\u6210\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8981\u4e0b\u8fc7\u624d\u77e5\u9053\u3002\u201d\u5f20\u606a\u8146\u7740\u8138\u4e00\u7b11\uff0c\u4ed6\u4e0e\u5510\u5b66\u8c26\u8bf4\u8bdd\u603b\u662f\u968f\u610f\u7684\u3002</p><p>\u3000\u3000\u8fb9\u7528\u9910\u8fb9\u804a\u5929\uff0c\u9910\u5385\u91cc\u7684\u4eba\u59cb\u7ec8\u4e5f\u4e0d\u591a\uff0c\u4ed6\u4eec\u56db\u4eba\u5360\u4e86\u89d2\u843d\u4e34\u7a97\u7684\u5c0f\u65b9\u684c\uff0c\u5468\u56f4\u90fd\u7a7a\u843d\u843d\u7684\u2014\u2014\u5728\u8fd9\u91cc\uff0c\u5373\u4f7f\u6709\u4eba\u8fdb\u6765\u7528\u9910\uff0c\u4e5f\u4e0d\u4f1a\u9009\u62e9\u8ddf\u5176\u4ed6\u4e0d\u76f8\u5173\u7684\u5ba2\u4eba\u7d27\u6328\u7740\u5750\uff0c\u597d\u50cf\u90fd\u5728\u8bf4\u4e0d\u80fd\u8ba9\u65c1\u4eba\u542c\u5230\u7684\u79d8\u5bc6\u4f3c\u7684\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8d76\u5230\u65f6\u95f4\u70b9\u8981\u79bb\u5f00\uff0c\u5c31\u7ed3\u675f\u4e86\u7528\u9910\uff0c\u4e5f\u662f\u7a7a\u95f2\u7740\uff0c\u5c31\u9001\u53f6\u5efa\u658c\u5230\u6821\u95e8\u53e3\uff0c\u53f8\u673a\u5f00\u8f66\u5230\u6821\u95e8\u6765\u63a5\u4ed6\u3002\u8d70\u5230\u6821\u95e8\u53e3\uff0c\u5374\u53c8\u770b\u89c1\u9ec4\u660f\u65f6\u5206\u5728\u6821\u95e8\u53e3\u9047\u5230\u7684\u90a3\u8f86\u91d1\u8272\u51ef\u8fea\u62c9\u514b\u505c\u5728\u90a3\u91cc\uff0c\u5b8f\u4fe1\u7684\u4e25\u6587\u4ecb\u4e0e\u4e00\u4e2a\u4e2d\u5e74\u4eba\u7ad9\u5728\u8f66\u65c1\u8bf4\u8bdd\uff0c\u539f\u6765\u90a3\u51ef\u8fea\u62c9\u514b\u662f\u4e25\u6587\u4ecb\u5728\u5317\u4eac\u7684\u8f66\u5b50\u3002</p></article>"}}}]);
