(window.webpackJsonp=window.webpackJsonp||[]).push([[1104],{1127:function(p){p.exports={chapterTitle:null,title:"\u7b2c1101\u7ae0 \u6076\u4eba\u96be\u5f53",content:"<article><h2>\u7b2c1101\u7ae0 \u6076\u4eba\u96be\u5f53</h2><p>\u3000\u3000\u5929\u8272\u660f\u6697\uff0c\u98ce\u52bf\u96ea\u52bf\u53c8\u6e10\u72c2\u4e71\uff0c\u51ac\u5b63\u91cc\u7ed9\u5439\u5f97\u53d1\u9752\u5e72\u88c2\u7684\u6811\u679d\u522e\u843d\u5230\u524d\u5ead\u91cc\uff0c\u6253\u5f00\u7a97\u73bb\u7483\u4e0a\u567c\u54e9\u556a\u5566\u7684\u54cd\u3002</p><p>\u3000\u3000\u4e00\u5219\u5929\u6c14\u6076\u52a3\uff0c\u518d\u5219\u697c\u4e0a\u6216\u8bb8\u8fd8\u4e0d\u65b9\u4fbf\u4e0b\u697c\u7684\u4f73\u4eba\u5728\u7b49\u5019\uff0c\u8096\u745e\u6c11\u5c06\u5496\u5561\u559d\u5b8c\uff0c\u5c31\u5148\u544a\u8f9e\u4e86\u3002\u4e0e\u8096\u745e\u6c11\u5728\u9662\u95e8\u524d\u8a00\u522b\uff0c\u89c1\u8096\u745e\u6c11\u7684\u52a9\u624b\u62ac\u5934\u5f80\u4e8c\u697c\u770b\uff0c\u5f20\u606a\u8f6c\u8fc7\u8eab\u6765\uff0c\u62ac\u5934\u770b\u89c1\u665a\u6674\u7684\u8138\u8d34\u5728\u4e8c\u697c\u7684\u7a97\u73bb\u7483\u4e0a\uff0c\u96ea\u7c92\u6253\u5f00\u7a97\u73bb\u7483\u4e0a\uff0c\u7ed3\u7740\u51b0\u82b1\uff0c\u53ea\u80fd\u9690\u9690\u7ea6\u7ea6\u770b\u89c1\u4e00\u5f20\u5973\u4eba\u7684\u8138\uff0c\u8096\u745e\u6c11\u4ed6\u4eec\u6839\u672c\u770b\u4e0d\u6e05\u695a\u4e8c\u697c\u7684\u5973\u4eba\u5230\u5e95\u662f\u8c01\uff0c\u5f20\u606a\u4e5f\u61d2\u5f97\u8ddf\u8096\u745e\u6c11\u7ee7\u7eed\u5bd2\u6684\uff0c\u5c06\u4ed6\u4eec\u9001\u51fa\u9662\u95e8\uff0c\u5c31\u8fc5\u901f\u5173\u95e8\u95ed\u6237\u7684\u4e0a\u4e86\u697c\u3002</p><p>\u3000\u3000\u5ba4\u5916\u5929\u5bd2\u5730\u51bb\u7684\uff0c\u5ba4\u5185\u5374\u6e29\u6696\u5982\u6625\uff0c\u665a\u6674\u5c31\u7a7f\u7740\u4ef6\u84dd\u7d2b\u8272\u7684\u683c\u5b50\u539a\u68c9\u886c\u8863\u5c48\u817f\u8dea\u5750\u5728\u94fa\u7740\u6591\u6593\u76ae\u88d8\u7684\u7a97\u53f0\u4e0a\uff0c\u4fee\u957f\u6d51\u5706\u7684\u96ea\u767d\u5927\u817f\u4ece\u886b\u8863\u4e0b\u6446\u9732\u51fa\u6765\uff0c\u8fd8\u82e5\u9690\u82e5\u73b0\u7684\u9732\u51fa\u4e24\u817f\u4ea4\u53c9\u5904\u7eaf\u767d\u8272\u7684\u8584\u68c9\u5185\u88e4\uff0c\u5979\u95ee\u9053\uff1a\u201c\u8096\u745e\u6c11\u4e0e\u4f60\u8c08\u4e86\u4e9b\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u73b0\u5728\u8c08\u8fd9\u4e2a\u662f\u4e0d\u662f\u6709\u4e9b\u715e\u98ce\u666f\uff1f\u201d\u5f20\u606a\u7ad9\u5230\u7a97\u53f0\u8fb9\uff0c\u900f\u8fc7\u73bb\u7483\u4e0a\u79ef\u7740\u96ea\u7c92\u51b0\u82b1\u7684\u7a97\u5b50\u5f80\u5916\u770b\uff0c\u8096\u745e\u6c11\u4ed6\u4eec\u7684\u8f66\u5df2\u7ecf\u51fa\u4e86\u5c0f\u533a\uff0c\u5c0f\u533a\u4e5f\u6ca1\u6709\u4eba\u51fa\u6765\u8d70\u52a8\uff0c\u6a21\u6a21\u7cca\u7cca\u7684\u9ec4\u660f\uff0c\u98ce\u96ea\u4e0b\u6811\u7626\u8349\u67af\uff0c\u522b\u6709\u4e00\u756a\u6ecb\u5473\u3002</p><p>\u3000\u3000\u5f20\u606a\u6380\u8d77\u665a\u6674\u7684\u886b\u8863\u4e0b\u6446\uff0c\u9732\u51fa\u5979\u91cc\u9762\u7a7f\u7740\u7684\u5185\u88e4\uff0c\u95ee\u9053\uff1a\u201c\u4e0d\u662f\u8bf4\u4e0d\u4e0b\u5e8a\u5417\uff1f\u201d\u7eaf\u767d\u8272\u7684\u8584\u68c9\u5185\u88e4\u6709\u4e9b\u5c0f\uff0c\u6709\u5c0f\u7247\u7684\u96ea\u767d\u81c0\u74e3\u9732\u51fa\u6765\uff0c\u5185\u88e4\u7ee3\u7740\u4e00\u7ea2\u4e00\u7eff\u4e24\u53ea\u873b\u8713\uff0c\u5f20\u606a\u8ba9\u665a\u6674\u7fd8\u81c0\u8dea\u5728\u90a3\u91cc\uff0c\u4e24\u53ea\u873b\u8713\u5982\u751f\u5c55\u7fc5\u6b32\u98de\uff0c\u5f20\u606a\u5728\u5979\u6d51\u5706\u633a\u7fd8\u7684\u96ea\u81c0\u4e0a\u62cd\u4e86\u4e24\u4e0b\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u6761\u5185\u88e4\u5c31\u8d35\u5728\u8fd9\u4e24\u6761\u873b\u8713\u4e0a\u4e86\uff0c\u6211\u7684\u773c\u5149\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u7684\u773c\u5149\u5982\u4f55\uff0c\u9700\u8981\u95ee\u6211\u5417\uff1f\u201d\u665a\u6674\u7ffb\u5750\u8fc7\u6765\uff0c\u53cc\u81c2\u642d\u5728\u5f20\u606a\u7684\u8116\u5b50\uff0c\u5a9a\u773c\u5982\u4e1d\u7684\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u9001\u6211\u5185\u8863\uff0c\u6700\u540e\u8fd8\u4e0d\u662f\u4e3a\u4e86\u8ba8\u597d\u4f60\u81ea\u5df1\uff0c\u4f60\u559c\u6b22\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8ba9\u665a\u6674\u6402\u7d27\u81ea\u5df1\u7684\u8116\u5b50\uff0c\u53c8\u8ba9\u5979\u7684\u817f\u73af\u7740\u81ea\u5df1\u7684\u8170\uff0c\u624b\u6258\u7740\u5979\u4e30\u8174\u7684\u8089\u81c0\uff0c\u5c06\u5979\u62b1\u5230\u5e8a\u4e0a\uff0c\u5c06\u683c\u5b50\u68c9\u886c\u886b\u89e3\u5f00\uff0c\u9732\u51fa\u91cc\u9762\u7a7f\u7740\u7684\u5185\u8863\u6765\uff0c\u7eaf\u767d\u8272\u7684\u68c9\u8d28\u5185\u88e4\u5f88\u8584\uff0c\u8d34\u8eab\u7a7f\u7740\uff0c\u5c06\u665a\u6674\u6027\u611f\u7684\u8eab\u6750\u5b8c\u7f8e\u65e0\u6687\u7684\u5448\u73b0\u51fa\u6765\uff0c\u8fd8\u5fae\u5fae\u9732\u4e9b\u8089\u8272\uff0c\u8bf1\u4eba\u4e4b\u6781\uff0c\u8fd9\u624d\u95ee\u5979\uff1a\u201c\u6211\u4eec\u662f\u804a\u8096\u745e\u6c11\u521a\u624d\u8ddf\u6211\u8bf4\u8d77\u6765\u7684\u8bdd\u9898\uff0c\u8fd8\u662f\u7ee7\u7eed\u521a\u624d\u7ed9\u8096\u745e\u6c11\u6253\u65ad\u7684\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u53c8\u6ca1\u6709\u7ed9\u6253\u65ad\uff0c\u201d\u665a\u6674\u5ae3\u7136\u800c\u7b11\uff0c\u5e2e\u5f20\u606a\u5c06\u5916\u5957\u8131\u6389\uff0c\u4f9d\u504e\u5728\u4ed6\u7684\u6000\u91cc\uff0c\u6e29\u67d4\u7684\u629a\u6478\u4ed6\u5e26\u7740\u80e1\u5b50\u6e23\u5b50\u7684\u4e0b\u5df4\uff0c\u201c\u8be5\u9001\u4e00\u53ea\u5243\u987b\u5200\u7ed9\u4f60\u4e86\uff0c\u521a\u624d\u523a\u5f97\u4eba\u75bc\u6b7b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u53ea\u662f\u75d2\u5462\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u624b\u5f80\u665a\u6674\u4e24\u817f\u4e4b\u95f4\u6478\uff0c\u90a3\u91cc\u7684\u76ae\u80a4\u53c8\u6ed1\u53c8\u7ec6\uff0c\u89c1\u665a\u6674\u8170\u80a2\u626d\u52a8\u3001\u4e24\u817f\u60c5\u4e0d\u81ea\u7981\u7684\u5939\u7d27\u7740\uff0c\u95ee\u9053\uff0c\u201c\u73b0\u5728\u662f\u75d2\u662f\u75bc\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e24\u4eba\u5728\u6e29\u6696\u5982\u6625\u7684\u5367\u5ba4\u7f20\u7ef5\u5230\u5c0f\u533a\u91cc\u7684\u8def\u706f\u70b9\u4eae\uff0c\u8179\u4e2d\u9965\u80a0\u6f09\u6f09\uff0c\u522b\u5885\u91cc\u4ec0\u4e48\u5403\u98df\u90fd\u6ca1\u6709\uff0c\u60f3\u5230\u521a\u624d\u5f00\u8f66\u8fdb\u6765\uff0c\u522b\u5885\u533a\u5916\u7684\u9999\u6797\u5c0f\u9547\u4e0a\u6709\u51e0\u5bb6\u7cbe\u81f4\u7684\u9910\u5385\uff0c\u5f20\u606a\u4e0e\u665a\u6674\u7a7f\u597d\u8863\u670d\uff0c\u6253\u7b97\u51fa\u53bb\u7528\u9910\u3002</p><p>\u3000\u3000\u665a\u6674\u8863\u886b\u5355\u8584\uff0c\u867d\u7136\u53ea\u6709\u4e00\u5c0f\u6bb5\u8def\uff0c\u5f20\u606a\u8fd8\u662f\u62ff\u8d77\u8f66\u94a5\u5319\uff0c\u51c6\u5907\u5f00\u8f66\u51fa\u53bb\uff0c\u665a\u6674\u8bf4\u9053\uff1a\u201c\u4f60\u966a\u6211\u5230\u9694\u58c1\u6362\u4e00\u4ef6\u539a\u5916\u5957\u5c31\u884c\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u53d1\u6123\u7684\u8ddf\u7740\u665a\u6674\u8d70\u5230\u522b\u5885\u524d\u7684\u5ead\u9662\uff0c\u624d\u53d1\u73b0\u6811\u7bf1\u95f4\u6709\u4e00\u5ea7\u4e0d\u8d77\u773c\u7684\u94c1\u827a\u95e8\u8ddf\u9694\u58c1\u7684\u522b\u5885\u76f8\u8fde\uff0c\u539f\u6765\u665a\u6674\u5c06\u8fd9\u4e24\u680b\u5e76\u6392\u7684\u522b\u5885\u90fd\u4e70\u4e86\u4e0b\u6765\uff0c\u683c\u5c40\u8ddf\u6d77\u5dde\u65b0\u9526\u82d1\u91cc\u7684\u4e24\u5bb6\u6d0b\u697c\u4e00\u6837\uff0c\u5373\u4f7f\u7ed9\u522b\u4eba\u53d1\u73b0\uff0c\u4e5f\u53ef\u4ee5\u72e1\u8fa9\u8bf4\u5728\u5317\u4eac\u4e70\u623f\u4e5f\u53ea\u662f\u60f3\u505a\u90bb\u5c45\u800c\u5df2\u3002</p><p>\u3000\u3000\u201c\u5728\u5317\u4eac\u603b\u8981\u6709\u4e00\u5904\u843d\u811a\u7684\u5730\u65b9\uff0c\u201d\u665a\u6674\u5ae3\u7136\u7b11\u7740\u8bf4\uff0c\u201c\u6bcf\u6b21\u5e7d\u4f1a\u540e\u90fd\u8981\u5c06\u6c14\u5473\u4ec0\u4e48\u7684\u6253\u5305\u5e26\u8d70\uff0c\u591a\u5c11\u6709\u4e9b\u51c4\u51c9\u4e86\uff0c\u6211\u5c31\u60f3\u7740\u8fd9\u4e48\u5b89\u6392\u4f1a\u6bd4\u8f83\u597d\u4e00\u4e9b\uff0c\u4e24\u680b\u522b\u5885\u8d70\u51fa\u5c0f\u533a\u7684\u51fa\u53e3\u4e5f\u4e0d\u4e00\u6837\uff0c\u4e0d\u901a\u8fc7\u8fd9\u9053\u94c1\u95e8\uff0c\u751a\u81f3\u90fd\u4e0d\u53ef\u80fd\u5728\u9999\u6797\u5c0f\u9547\u91cc\u9047\u5230\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u539f\u6765\u8fd9\u6837\u554a\u2026\u2026\u201d\u5f20\u606a\u521a\u624d\u8fd8\u5947\u602a\u4e3a\u4ec0\u4e48\u521a\u624d\u522b\u5885\u91cc\u9664\u4e86\u665a\u6674\u9999\u8273\u7684\u4eba\u7ad9\u5728\u90a3\u91cc\u4e4b\u5916\uff0c\u5c31\u518d\u627e\u4e0d\u5230\u5979\u7684\u75d5\u8ff9\uff0c\u800c\u4e14\u522b\u5885\u91cc\u7684\u5e03\u7f6e\u90fd\u662f\u6309\u7167\u4ed6\u7684\u559c\u6b22\u6765\u8fdb\u884c\uff0c\u539f\u6765\u5979\u5728\u73a9\u72e1\u5154\u4e09\u7a9f\u7684\u6e38\u620f\u3002</p><p>\u3000\u3000\u201c\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u665a\u6674\u4f38\u624b\u634f\u4e86\u634f\u5f20\u606a\u7684\u9f3b\u5b50\uff0c\u7b11\u76c8\u76c8\u7684\u95ee\u4ed6\uff0c\u201c\u5728\u60f3\u540c\u4e00\u4e2a\u5730\u65b9\u8981\u4e70\u51e0\u680b\u522b\u5885\u624d\u591f\u5b89\u987f\u597d\u6240\u6709\u7684\u4eba\uff0c\u8fd8\u5728\u60f3\u90a3\u51e0\u680b\u522b\u5885\u662f\u6309\u6885\u82b1\u5f62\u5e03\u5c40\uff0c\u8fd8\u662f\u6309\u7167\u4e5d\u5bab\u5f62\u5e03\u5c40\uff1f\u201d</p><p>\u3000\u3000\u201c\u5012\u662f\u597d\u4e3b\u610f\u2026\u2026\u201d\u5f20\u606a\u563f\u7136\u800c\u7b11\uff0c\u63a8\u7740\u665a\u6674\u8d70\u8fdb\u9694\u58c1\u7684\u522b\u5885\u91cc\uff0c\u8fd9\u91cc\u624d\u662f\u6309\u7167\u665a\u6674\u5979\u81ea\u5df1\u7684\u559c\u6b22\u6765\u5e03\u7f6e\uff0c\u7b49\u5979\u6362\u4e0a\u539a\u5916\u5957\uff0c\u624d\u4e00\u8d77\u4ece\u4fa7\u95e8\u8d70\u51fa\u53bb\u3002</p><p>\u3000\u3000\u98ce\u96ea\u5f88\u5927\uff0c\u5ba4\u5916\u7684\u6e29\u5ea6\u964d\u5230\u96f6\u4e0b\u5341\u4e03\u516b\u5ea6\uff0c\u4eba\u8d70\u52a8\u8d77\u6765\uff0c\u5012\u4e0d\u89c9\u5f97\u7279\u522b\u7684\u5bd2\u51b7\uff0c\u8d70\u51fa\u522b\u5885\u533a\uff0c\u624d\u89c9\u5f97\u5929\u5bd2\u5730\u51b0\u7684\u5ba4\u5916\u8fd8\u6709\u4e9b\u4eba\u6c14\uff0c\u8857\u9053\u7684\u4e24\u4fa7\u90fd\u662f\u706f\u5149\u901a\u660e\u3001\u88c5\u4fee\u7cbe\u81f4\u7684\u5e97\u94fa\uff0c\u627e\u4e86\u4e00\u95f4\u897f\u9910\u5385\u8fdb\u53bb\u3002\u9910\u5385\u5df2\u6709\u4e03\u4e03\u516b\u516b\u7684\u5750\u4e86\u597d\u4e9b\u5bf9\u4eba\uff0c\u5f20\u606a\u7ad9\u5728\u90a3\u91cc\u5f80\u5927\u5385\u91cc\u626b\u4e86\u4e24\u773c\uff0c\u6709\u4e09\u56db\u5bf9\u662f\u5e74\u8f7b\u5a07\u5a9a\u7684\u5973\u5b69\u5b50\u5728\u966a\u540c\u4e2d\u8001\u5e74\u7537\u4eba\u5728\u7528\u9910\uff0c\u7b11\u7740\u8ddf\u665a\u6674\u8bf4\uff1a\u201c\u770b\u6765\u597d\u4e9b\u4eba\u90fd\u77e5\u9053\u5728\u8fd9\u91cc\u7b51\u5de2\u7684\u5999\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u6390\u4e86\u5f20\u606a\u4e00\u4e0b\u8170\uff0c\u4e0d\u8ba9\u4ed6\u80e1\u8bf4\u516b\u9053\uff0c\u5bfb\u4e86\u4e00\u5904\u4e34\u7a97\u7684\u684c\u5b50\u5750\u4e0b\uff0c\u70b9\u8fc7\u9910\uff0c\u5f20\u606a\u624d\u8ddf\u665a\u6674\u8bf4\u8d77\u8096\u745e\u6c11\u4e0b\u5348\u8fc7\u6765\u8c08\u5230\u7684\u4e8b\u60c5\uff1a\u201c\u6625\u8282\u8fc7\u540e\u79fb\u52a8\u901a\u4fe1\u5c40\u5c31\u4f1a\u4ece\u4e2d\u56fd\u7535\u4fe1\u96c6\u56e2\u72ec\u7acb\u51fa\u6765\u6210\u7acb\u72ec\u7acb\u7684\u96c6\u56e2\u516c\u53f8\uff0c\u7b2c\u4e00\u4e2a\u4e0e\u6700\u65b0\u5546\u4e1a\u6807\u51c6\u517c\u5bb9\u7684GPRS\u7f51\u7edc\u4e5f\u5c06\u5efa\u6210\uff0c\u8054\u901a\u9ad8\u5c42\u611f\u53d7\u5230\u5f88\u5927\u7684\u538b\u529b\u4e5f\u662f\u6b63\u5e38\u7684\uff0c\u8c01\u8ba9\u8054\u901a\u672c\u6765\u5c31\u662f\u540e\u5a18\u517b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u91d1\u5c71\u7684\u8ba1\u5212\uff0c\u8ba9\u4e0d\u8ba9\u8054\u4fe1\u53c2\u52a0\uff1f\u201d\u665a\u6674\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5012\u4e0d\u6025\uff0c\u91d1\u5c71\u63d0\u4f9b\u6807\u51c6\u5382\u623f\uff0c\u624b\u673a\u7ec4\u88c5\u5de5\u5382\u7b79\u5efa\u8d77\u6765\u4e5f\u8fc5\u901f\uff0c\u4e0d\u5fd9\u7740\u8ba9\u8054\u4fe1\u63ba\u5408\u8fdb\u6765\u6253\u8349\u60ca\u86c7\uff0c\u6211\u53ea\u662f\u5728\u8003\u8651\uff0c\u6bd4\u60f3\u79fb\u52a8\u901a\u4fe1\u5c40\u7684\u76db\u6c14\u51cc\u4eba\uff0c\u6b64\u65f6\u7684\u8054\u901a\u6216\u8bb8\u66f4\u597d\u8bf4\u8bdd\u4e00\u4e9b\uff0c\u8096\u745e\u6c11\u4eca\u5929\u6ca1\u6709\u5c06\u610f\u601d\u8bf4\u900f\uff0c\u6211\u5728\u5317\u4eac\u591a\u7559\u4e24\u5929\uff0c\u6216\u8bb8\u4f1a\u6709\u522b\u7684\u60ca\u559c\u4e5f\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u8bf4\u8054\u901a\u53ef\u80fd\u4e3b\u52a8\u627e\u9526\u6e56\u8fdb\u884c\u6df1\u5ea6\u7684\u6280\u672f\u5408\u4f5c\uff1f\u201d\u665a\u6674\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u5e0c\u671b\u5982\u6b64\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5927\u5bb6\u4e5f\u53d7\u591f\u79fb\u52a8\u901a\u4fe1\u5c40\u7684\u9e1f\u6c14\uff0c\u5c31\u7b97\u6211\u4eec\u60f3\u88c5\u5b59\u5b50\uff0c\u4e5f\u8981\u4eba\u5bb6\u8ba4\u6211\u4eec\u8fd9\u4e2a\u5b59\u5b50\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u665a\u6674\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u53ef\u6ca1\u6709\u89c1\u5230\u8fc7\u4f60\u88c5\u5b59\u5b50\u7684\u65f6\u5019\u2026\u2026\u201d</p><p>\u3000\u3000\u8bf4\u8d77\u79fb\u52a8\u901a\u4fe1\u5c40\uff08\u5373\u5c06\u6210\u7acb\u7684\u4e2d\u56fd\u79fb\u52a8\u96c6\u56e2\uff09\uff0c\u5f20\u606a\u8fd8\u662f\u6709\u6ee1\u8179\u610f\u89c1\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\u6625\u540e\uff0c\u9526\u6e56\u9996\u5148\u5f00\u53d1\u51fa\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff0c\u5e76\u5411\u5e02\u573a\u63a8\u51fa\u7b2c\u4e00\u53ea\u5168\u4e2d\u6587\u6570\u5b57\u624b\u673a\uff0c\u56fd\u5185\u79fb\u8fd0\u901a\u4fe1\u5e02\u573a\u5c31\u5b8c\u5168\u5177\u5907\u4e86\u5728\u56fd\u5185\u5927\u529b\u53d1\u5c55\u77ed\u4fe1\u589e\u503c\u4e1a\u52a1\u7684\u6280\u672f\u3001\u5e02\u573a\u6761\u4ef6\u3002</p><p>\u3000\u3000\u8981\u4e0d\u8981\u53d1\u5c55\u77ed\u4fe1\u4e1a\u52a1\uff0c\u51b3\u5b9a\u5b8c\u5168\u5728\u56fd\u5185\u4e24\u5bb6\u8fd0\u8425\u5546\u624b\u91cc\uff0c\u4e5d\u516b\u5e74\u65f6\u7684\u8054\u901a\u66f4\u52a0\u5f31\u4e0d\u7981\u98ce\uff0c\u8054\u901a\u7684GSM\u7f51\u90fd\u4e03\u96f6\u516b\u843d\u7684\uff0c\u9526\u6e56\u5f53\u65f6\u53ea\u80fd\u9009\u62e9\u8ddf\u7535\u4fe1\u96c6\u56e2\u4e0b\u5c5e\u7684\u79fb\u52a8\u901a\u4fe1\u5c40\u5408\u4f5c\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0e\u79fb\u52a8\u901a\u4fe1\u5c40\u524d\u524d\u540e\u540e\u8c08\u5224\u4e86\u6709\u8fd1\u4e00\u5e74\u65f6\u95f4\uff0c\u76f4\u5230\u4e5d\u516b\u5e74\u51ac\u5929\uff0c\u79fb\u52a8\u901a\u4fe1\u5c40\u624d\u52c9\u5f3a\u5728\u5168\u56fd\u4e3b\u8981\u57ce\u5e02\u5927\u529b\u63a8\u5e7f\u4e2d\u6587\u77ed\u4fe1\u4e1a\u52a1\uff1b\u90a3\u65f6\uff0c\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u7b49\u624b\u673a\u5382\u5546\u90fd\u5df2\u7ecf\u5b8c\u6210\u4e2d\u6587\u5f55\u5165\u7684\u6280\u672f\u51c6\u5907\uff0c\u4e5f\u76f8\u7ee7\u63a8\u52a8\u5168\u4e2d\u6587\u6570\u5b57\u624b\u673a\uff0c\u9526\u6e56\u5b9e\u9645\u4e0a\u5c31\u9519\u8fc7\u4e86\u4e00\u6b21\u5229\u7528\u4f18\u52bf\u6280\u672f\u6269\u5927\u56fd\u5185\u5e02\u573a\u4efd\u989d\u7684\u826f\u673a\u3002</p><p>\u3000\u3000\u79fb\u52a8\u901a\u4fe1\u8d44\u8d39\u9ad8\u6602\uff0c\u6536\u8d39\u4f4e\u5ec9\u7684\u4e2d\u6587\u77ed\u4fe1\u8fc5\u901f\u6210\u4e3a\u4e2d\u4f4e\u7aef\u79fb\u52a8\u7528\u6237\u7684\u6700\u4f73\u66ff\u4ee3\u9009\u62e9\uff0c\u6bd4\u8d77\u7172\u7535\u8bdd\u7ca5\uff0c\u53d1\u77ed\u4fe1\u66f4\u80fd\u6740\u65f6\u95f4\u3002\u51e0\u4e4e\u5c31\u5728\u5411\u5e02\u573a\u63a8\u5e7f\u7684\u540c\u65f6\uff0c\u77ed\u4fe1\u5c31\u98ce\u9761\u8d77\u6765\uff0c\u6210\u4e3a\u79fb\u52a8\u901a\u4fe1\u589e\u6da8\u6700\u8fc5\u901f\u7684\u589e\u503c\u670d\u52a1\uff0c\u4e5d\u4e5d\u5e74\u4e00\u5e74\u5c31\u4e3a\u56fd\u5185\u79fb\u52a8\u901a\u4fe1\u5e02\u573a\u521b\u6536\u8d85\u8fc7\u56db\u5341\u4ebf\uff0c\u4ece\u8fd9\u91cc\u5f20\u606a\u4e5f\u4e0d\u96be\u7406\u89e3\u5373\u65f6\u901a\u8baf\u5e02\u573a\u4e3a\u4ec0\u4e48\u4f1a\u90a3\u4e48\u5927\u3002</p><p>\u3000\u3000\u5982\u6b64\u7684\u826f\u673a\u9519\u5931\uff0c\u4ee4\u4eba\u500d\u611f\u60cb\u60dc\uff0c\u8981\u662f\u6293\u4f4f\u5176\u4ed6\u624b\u673a\u5382\u5546\u4e09\u5230\u516d\u4e2a\u6708\u7684\u6280\u672f\u7a7a\u767d\u671f\uff0c\u9526\u6e56\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u5360\u6709\u7387\u81f3\u5c11\u80fd\u63d0\u9ad85\u4e2a\u70b9\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u4f7f\u9526\u6e56\u770b\u6e05\u695a\u79fb\u52a8\u901a\u4fe1\u5c40\u5bf9\u9526\u6e56\u771f\u6b63\u7684\u6001\u5ea6\uff0c\u5373\u4f7f\u5728\u4e3b\u7ba1\u90e8\u59d4\u4fe1\u4ea7\u90e8\u5bf9\u9526\u6e56\u7684\u6001\u5ea6\u6709\u6240\u8f6c\u53d8\u4e4b\u9645\uff0c\u5728\u79fb\u52a8\u901a\u4fe1\u5c40\u7684\u773c\u91cc\uff0c\u9526\u6e56\u8fd8\u662f\u8981\u8c28\u5b88\u7ec8\u7aef\u8bbe\u5907\u5382\u5546\u7684\u672c\u4efd\uff0c\u63a8\u52a8\u901a\u4fe1\u6280\u672f\u53d1\u5c55\u4e0e\u6269\u5927\u4e1a\u52a1\u8303\u56f4\u7684\u4e8b\u60c5\uff0c\u4ed6\u4eec\u5e76\u4e0d\u5e0c\u671b\u9526\u6e56\u8fc7\u591a\u7684\u53c2\u4e0e\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u79fb\u52a8\u901a\u8baf\u5c40\u4ece\u4e5d\u516b\u5e74\u5e74\u5e95\u5f00\u59cb\u5728\u539f\u6709\u7684GSM\u7f51\u7edc\u57fa\u7840\u4e0a\u53d1\u5c55\u6570\u636e\u589e\u5f3a\u578bGPRS\u79fb\u52a8\u901a\u4fe1\u6280\u672f\uff0c\u8fd9\u662f\u4ece\u7b2c\u4e8c\u4ee3\u79fb\u52a8\u901a\u4fe1\u6280\u672f\uff082G\uff09\u5411\u7b2c\u4e09\u4ee3\u79fb\u52a8\u901a\u4fe1\u6280\u672f\uff083G\uff09\u8de8\u8d8a\u7684\u91cd\u8981\u4e2d\u95f4\u6b65\u9aa4\uff0c\u4fd7\u79f02.5G\uff0c\u7b2c\u4e00\u4e2a\u4e0e\u6700\u65b0\u5546\u4e1a\u6807\u51c6\u517c\u5bb9\u7684GPRS\u7f51\u7edc\u4e5f\u5373\u5c06\u5efa\u6210\u3002</p><p>\u3000\u3000\u5c31\u5f20\u606a\u6240\u77e5\uff0c\u4e00\u76f4\u5230\u96f6\u516b\u5e74\u56fd\u5185\u90fd\u6ca1\u6709\u771f\u6b63\u7684\u63a8\u5e7f\u7b2c\u4e09\u4ee3\u79fb\u52a8\u901a\u4fe1\u6280\u672f\uff0c\u4e5f\u5c31\u662f\u8bf4\u5728\u4eca\u540e\u51e0\u5e74\u65f6\u95f4\u91cc\uff0cGPRS\u624d\u662f\u5360\u636e\u56fd\u5185\u79fb\u52a8\u901a\u8baf\u5e02\u573a\u7684\u4e3b\u6d41\u3002</p><p>\u3000\u3000\u5f88\u53ef\u60dc\uff0c\u79fb\u52a8\u901a\u8baf\u5c40\u5347\u7ea7GPRS\u79fb\u52a8\u901a\u4fe1\u6280\u672f\u3001\u9009\u62e9\u7ec8\u7aef\u8bbe\u5907\u5382\u5546\u8fdb\u884c\u6280\u672f\u5408\u4f5c\u65f6\uff0c\u4ee5\u6280\u672f\u6761\u4ef6\u4e0d\u7b26\u5408\u8981\u6c42\u4e3a\u7531\u5c06\u9526\u6e56\u6392\u9664\u5728\u5916\uff0c\u8fd9\u6b21\u6253\u51fb\u7684\u75d5\u8ff9\u5c31\u66f4\u52a0\u660e\u663e\u4e86\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u53ca\u65f6\u8ffd\u8e2a\u6700\u5148\u8fdb\u7684\u79fb\u52a8\u901a\u8baf\u6280\u672f\uff0c\u9526\u6e56\u53ea\u5f97\u9009\u62e9\u4e0e\u65b0\u52a0\u5761\u7535\u4fe1\u8fdb\u884c\u6280\u672f\u5408\u4f5c\uff0c\u5148\u671f\u5728\u9a6c\u6765\u897f\u4e9a\u3001\u65b0\u52a0\u5761\u3001\u9999\u6e2f\u63a8\u51fa\u6700\u65b0\u7684GPRS\u624b\u673a\uff0c\u4e5f\u786e\u4fdd\u56fd\u5185\u5927\u4e3e\u63a8\u5e7fGPRS\u7f51\u7edc\u65f6\u9526\u6e56\u4e0d\u4f1a\u51fa\u73b0\u6280\u672f\u7a7a\u767d\u3002\u53ef\u4ee5\u9884\u89c1\u5c06\u6765\u8fdb\u884c3G\u624b\u673a\u5f00\u53d1\uff0c\u4e5f\u53ea\u80fd\u4f18\u5148\u9009\u62e9\u8ddf\u56fd\u5916\u8fd0\u8425\u5546\u5408\u4f5c\u3002</p><p>\u3000\u3000\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\uff0c\u9526\u6e56\u7684\u5e02\u573a\u4efd\u989d\u7d27\u8ddf\u5728\u6469\u6258\u7f57\u62c9\u4e0e\u8bfa\u57fa\u4e9a\u4e4b\u540e\uff0c\u8981\u662f\u6709\u534a\u5e74\u7684\u65f6\u95f4\u4e0d\u80fd\u63a8\u51fa\u65b0\u624b\u673a\uff0c\u53d7\u5230\u7684\u6253\u51fb\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u76f8\u5f53\u60e8\u70c8\u7684\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u56fd\u5185\u6b64\u65f6\u7684GSM\u7f51\u7edc\uff0cGPRS\u7684\u7528\u9014\u5341\u5206\u5e7f\u6cdb\uff0c\u9664\u4e86\u5305\u62ec\u97f3\u9891\u3001\u56fe\u7247\u3001\u89c6\u9891\u7b49\u591a\u5a92\u4f53\u589e\u503c\u5e94\u7528\u4e4b\u5916\uff0c\u7535\u5b50\u90ae\u4ef6\u4ee5\u4e92\u8054\u7f51\u6d4f\u89c8\u7b49\u529f\u80fd\u90fd\u80fd\u5b9e\u73b0\uff0c\u4f4d\u4e8e\u77ed\u4fe1\u4e4b\u540e\u56fd\u5185\u79fb\u52a8\u901a\u4fe1\u5e02\u573a\u7684\u7b2c\u4e8c\u5927\u589e\u503c\u4e1a\u52a1\uff0c\u624b\u673a\u5f69\u94c3\u529f\u80fd\u5728GPRS\u4e0a\u624d\u80fd\u66f4\u65b9\u4fbf\u7684\u5b9e\u73b0\u3002</p><p>\u3000\u3000\u79fb\u52a8\u901a\u4fe1\u5c40\u4eca\u5e74\u5c31\u5c06\u72ec\u7acb\u51fa\u6765\u6210\u4e3a\u526f\u90e8\u7ea7\u7684\u4e2d\u56fd\u79fb\u52a8\u96c6\u56e2\u516c\u53f8\uff0c\u6c14\u7130\u56a3\u5f20\u5c06\u66f4\u4e0d\u53ef\u4e00\u4e16\uff0c\u9526\u6e56\u4f3c\u4e4e\u4e5f\u5e94\u8be5\u679c\u65ad\u7684\u653e\u5f03\u725f\u6c42\u4e0e\u79fb\u52a8\u901a\u4fe1\u5c40\u8fdb\u884c\u6df1\u5ea6\u5408\u4f5c\u7684\u52aa\u529b\u2026\u2026</p><p>\u3000\u3000\u201c\u73b0\u5728\u7684\u8054\u901a\u8fd8\u5f88\u5f31\u5c0f\uff0c\u6b63\u56e0\u4e3a\u8054\u901a\u6bd4\u8f83\u5f31\u5c0f\uff0c\u6211\u4eec\u624d\u80fd\u5728\u5408\u4f5c\u4e2d\u83b7\u5f97\u6bd4\u8f83\u5e73\u7b49\u7684\u5730\u4f4d\uff0c\u201d\u5f20\u606a\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u4e0d\u8fc7\u4e5f\u96be\u8bf4\uff0c\u8054\u901a\u8fd9\u6b21\u540c\u65f6\u5728\u7ebd\u7ea6\u4e0e\u9999\u6e2f\u4e0a\u5e02\u53d1\u884c\u65b0\u80a1\u878d\u8d44\u7684\u8ba1\u5212\u975e\u5e38\u7684\u5b8f\u4f1f\uff0c\u8bf4\u4e0d\u5b9a\u8054\u901a\u9ad8\u5c42\u7684\u5fc3\u91cc\u8fd8\u662f\u5c06\u9526\u6e56\u5f53\u6210\u4e00\u679a\u5bf9\u6297\u7ade\u4e89\u5bf9\u624b\u7684\u68cb\u5b50\u5728\u7528\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u5374\u7b11\u5f97\u8f7b\u677e\uff0c\u5728\u5979\u770b\u6765\uff0c\u591a\u5c11\u4eba\u60f3\u5c06\u5f20\u606a\u5f53\u6210\u68cb\u5b50\u5229\u7528\u5230\u5934\u6765\u624d\u53d1\u73b0\u81ea\u5df1\u624d\u662f\u5f20\u606a\u624b\u91cc\u7684\u68cb\u5b50\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4f30\u8ba1\u8054\u901a\u8fd9\u6b21\u7684\u878d\u8d44\u89c4\u6a21\u4f1a\u6709\u591a\u5e9e\u5927\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ebd\u7ea6\u4e0e\u9999\u6e2f\u4e24\u8fb9\u52a0\u8d77\u6765\u4e00\u767e\u4ebf\u7f8e\u5143\u5de6\u53f3\uff0c\u771f\u662f\u8ba9\u4eba\u7fa1\u6155\u554a\uff01\u201d\u5f20\u606a\u5567\u5567\u6709\u58f0\u7684\u5482\u5634\uff0c\u60f3\u7740\u9526\u6e56\u4ec0\u4e48\u65f6\u5019\u80fd\u5728\u8d44\u672c\u5e02\u573a\u5957\u4e00\u767e\u4ebf\u7f8e\u5143\u6765\u7528\u7528\uff0c\u5c31\u4e0d\u7528\u518d\u6709\u8116\u5b50\u7ed9\u5957\u7ef3\u7d22\u7684\u611f\u89c9\u4e86\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u8054\u901a\u627e\u4e0a\u95e8\u6765\uff0c\u4f60\u6253\u7b97\u600e\u4e48\u8ddf\u8054\u901a\u8fdb\u884c\u6df1\u5ea6\u7684\u5408\u4f5c\uff1f\u201d\u665a\u6674\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5177\u4f53\u7684\u8fd8\u8981\u7b49\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u5230\u5317\u4eac\u6765\u8ba8\u8bba\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5728\u6211\u7684\u5fc3\u76ee\u4e2d\uff0c\u8054\u901a\u771f\u4e0d\u80fd\u7b97\u662f\u5f88\u597d\u7684\u5408\u4f5c\u8005\uff0c\u6bd4\u8d77\u79fb\u52a8\u5728GSM\u7f51\u7edc\u57fa\u7840\u4e0a\u4e13\u6ce8\u7684\u53d1\u5c55\u589e\u5f3a\u578b\u7684\u9ad8\u7aefGPRS\u901a\u4fe1\u6280\u672f\uff0c\u4e0d\u7528\u5efa\u65b0\u7684\u901a\u4fe1\u7f51\u7edc\u3002\u8054\u901a\u5374\u5728\u4fdd\u6301\u4f4e\u7aefGSM\u7f51\u7edc\u7684\u540c\u65f6\uff0c\u7adf\u7136\u60f3\u7740\u4e09\u5fc3\u4e8c\u610f\u7684\u53bb\u5efa\u8bbeCDMA\u7f51\u7edc\uff0c\u51e0\u4e4e\u662f\u4ee5\u8d4c\u5f92\u7684\u5fc3\u6001\u5c06\u9ad8\u7aef\u4e1a\u52a1\u7684\u7b79\u7801\u90fd\u538b\u5728CDMA\u4e0a\uff0c\u5f88\u5bb9\u6613\u9677\u5165\u8fdb\u9000\u4e24\u96be\u7684\u5883\u754c\u2026\u2026GPRS\u7684\u6280\u672f\u66f4\u5f00\u653e\u4e00\u4e9b\uff0cCDMA\u6280\u672f\u5374\u7ed9\u7f8e\u56fd\u9ad8\u901a\u4e00\u5bb6\u516c\u53f8\u628a\u6301\uff0c\u5f00\u653e\u6027\u592a\u5dee\uff0c\u5b9e\u5728\u4e0d\u80fd\u7b97\u662f\u4e00\u4ef6\u597d\u4e8b\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u6309\u4f60\u7684\u610f\u601d\uff0c\u8054\u901a\u5c31\u4e0d\u8be5\u5f15\u8fdbCDMA\u6280\u672f\uff1f\u201d\u665a\u6674\u7693\u767d\u96ea\u8155\u6258\u7740\u8ff7\u4eba\u7684\u4e0b\u5df4\uff0c\u51dd\u7738\u671b\u7740\u5f20\u606a\uff0c\u5979\u5bf9\u5f20\u606a\u5982\u6b64\u81ea\u4fe1\u7684\u6307\u70b9\u6c5f\u5c71\u6709\u79cd\u7279\u6b8a\u7684\u8ff7\u604b\uff0c\u8fd9\u80fd\u8ba9\u5979\u5b8c\u5168\u5ffd\u89c6\u4e24\u4eba\u4e4b\u95f4\u5e74\u9f84\u7684\u5dee\u8ddd\uff0c\u5728\u5f20\u606a\u9762\u524d\uff0c\u665a\u6674\u66f4\u611f\u89c9\u81ea\u5df1\u50cf\u4e2a\u5b66\u751f\u6216\u8005\u662f\u5d07\u62dc\u8005\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53eb\u8be5\u4e0d\u8be5\u554a\uff1f\u5927\u6982\u662f\u8fd9\u4e9b\u5e74\u6765\u7ed9\u90ae\u7535\u90e8\u4e0e\u7535\u4fe1\u96c6\u56e2\u6b3a\u8d1f\u60e8\u4e86\uff0c\u8054\u901a\u53d8\u5f97\u5f02\u5e38\u7684\u597d\u6597\uff0c\u7ba1\u7406\u5c42\u7684\u5fc3\u6001\u4e5f\u6709\u4e9b\u53d8\u5f97\u4e0d\u987e\u4e00\u5207\u3002\u4ed6\u4eec\u8981\u662f\u653e\u5f03\u5bf9CDMA\u7684\u5e7b\u60f3\uff0c\u5c06\u8fd9\u6b21\u878d\u8d44\u76ee\u6807\u6539\u4e3a\u5168\u9762\u66f4\u65b0\u3001\u5b8c\u5584\u73b0\u6709\u7684G\u7f51\uff0c\u6216\u8bb8\u624d\u80fd\u771f\u6b63\u7684\u7ed9\u7ade\u4e89\u5bf9\u624b\u4ee5\u5a01\u80c1\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u7684\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u7684\u8054\u7f51G\u7f51\u8986\u76d6\u4e0d\u597d\uff0c\u6709\u5927\u91cf\u7684\u76f2\u533a\uff0c\u901a\u8bdd\u8d28\u91cf\u5dee\uff0c\u6709\u5927\u91cf\u5355\u901a\u3001\u4e32\u97f3\u3001\u6389\u53f7\u7f3a\u9677\uff0c\u6025\u5f85\u7eed\u5efa\u548c\u7f51\u7edc\u4f18\u5316\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5bf9\u8054\u901a\u6765\u8bf4\uff0c\u6700\u6709\u4f18\u52bf\u7684\u4e00\u70b9\u5c31\u662f\u7ed9\u4e2d\u79fb\u52a8\u6b3a\u8d1f\u5f97\u518d\u60e8\uff0c\u4e5f\u662f\u4e2d\u56fd\u552f\u4e00\u4e24\u5bb6\u79fb\u52a8\u8fd0\u8425\u5546\u4e4b\u4e00\uff0c\u5883\u9047\u518d\u600e\u4e48\u51c4\u51c9\uff0c\u4e5f\u662f\u5929\u4e4b\u9a84\u5b50\u3001\u56fd\u4e4b\u5ba0\u513f\u2026\u2026\u201d\u665a\u6674\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u77e5\u9053\u8054\u901a\u5bf9\u9526\u6e56\u6765\u8bf4\u6700\u5927\u7684\u4f5c\u7528\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u4ec0\u4e48\uff1f\u201d\u665a\u6674\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8054\u901a\u7684G\u7f51\u8d28\u91cf\u5dee\uff0c\u4f46\u662f\u8054\u901a\u53c8\u6025\u4e8e\u589e\u52a0\u65b0\u7528\u6237\u6570\u91cf\uff0c\u5bf9\u4e8e\u4ed6\u4eec\u6765\u8bf4\uff0c\u552f\u4e00\u80fd\u8d70\u7684\u5e02\u573a\u7ecf\u8425\u7b56\u7565\u5c31\u662f\u2018\u5356\u4fbf\u5b9c\u2019\uff0c\u8054\u901a\u4e0e\u79fb\u52a8\u6253\u4ef7\u683c\u6218\u624d\u662f\u6211\u4eec\u6700\u4e50\u610f\u770b\u5230\u7684\u5c40\u9762\u2026\u2026\u201d\u5f20\u606a\u4e0d\u6000\u597d\u610f\u7684\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u5c31\u8bf4\u4f60\u809a\u5b50\u91cc\u6ca1\u6709\u4ec0\u4e48\u597d\u6c34\u3002\u201d\u665a\u6674\u7b11\u9053\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53eb\u6ca1\u6709\u4ec0\u4e48\u597d\u6c34\uff1f\u201d\u5f20\u606a\u95ee\u9053\uff0c\u201c\u8054\u901a\u9ad8\u5c42\u4e5f\u672a\u5fc5\u4e2a\u4e2a\u90fd\u662f\u7cca\u6d82\u86cb\uff0c\u80af\u5b9a\u6709\u4eba\u5fc3\u91cc\u662f\u6e05\u695a\u7684\uff0c\u53ea\u662f\u8fd9\u4e2a\u5c42\u6b21\u7684\u4eba\u5927\u591a\u751f\u6d3b\u5728\u8c0e\u8a00\u91cc\uff0c\u672a\u5fc5\u559c\u6b22\u542c\u771f\u8bdd\u554a\u3002\u8bf4\u53e5\u4e0d\u597d\u542c\u7684\u8bdd\uff0c\u8054\u901a\u7684G\u7f51\u8d28\u91cf\u5f88\u5dee\uff0c\u4f46\u662f\u4ed6\u4eec\u53c8\u6025\u4e8e\u6c42\u6210\uff0c\u53ea\u80fd\u53e6\u8f9f\u8e4a\u5f84\u8bb2\u2018\u65b0\u6545\u4e8b\u2019\u4e86\u3002\u81f3\u5c11\u73b0\u5728\u770b\u4e0a\u53bb\uff0c\u65b0\u6545\u4e8b\u66f4\u52a8\u542c\u4e00\u4e9b\uff0c\u8054\u901a\u516c\u53f8\u7ed9\u4fe1\u4ea7\u90e8\u3001\u56fd\u52a1\u9662\u7684\u62a5\u544a\u91cc\u63d0\u5230\u5f15\u8fdb\u9ad8\u901a\u7684CDMA\u6280\u672f\u4e0d\u4ec5\u80fd\u4f7f\u8054\u901a\u516c\u53f8\u53d7\u76ca\uff0c\u8fd8\u53ef\u4ee5\u5e26\u52a8\u56fd\u5185\u7535\u5b50\u5de5\u4e1a\u589e\u52a05000\u4ebf\u4ea7\u503c\u3002\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u8054\u901a\u8010\u5fc3\u5b8c\u5584\u3001\u4f18\u5316\u73b0\u6709\u7684G\u7f51\uff0c\u5c31\u4e0d\u80fd\u5e26\u52a8\u56fd\u5185\u7535\u5b50\u5de5\u4e1a\u589e\u52a0\u65b0\u7684\u4ea7\u503c\uff1f\u201d</p><p>\u3000\u3000\u201c\u5176\u4e2d\u4f18\u52a3\uff0c\u4f60\u90fd\u4e00\u76ee\u4e86\u7136\uff0c\u201d\u665a\u6674\u5fae\u5fae\u4e00\u53f9\uff0c\u95ee\u9053\uff0c\u201c\u8054\u901a\u5c06\u7b79\u7801\u90fd\u5728CDMA\u4e0a\uff0c\u5f88\u53ef\u80fd\u4f1a\u5bfc\u81f4\u56fd\u5bb6\u5927\u91cf\u6295\u8d44\u800c\u5f97\u4e0d\u5230\u5e94\u6709\u7684\u56de\u62a5\uff0c\u73b0\u5728\u963b\u6b62\u4e5f\u6765\u5f97\u53ca\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u867d\u7136\u6b63\u5f0f\u7684\u534f\u8bae\u8fd8\u6ca1\u6709\u7b7e\u7f72\uff0c\u6050\u6015\u4e5f\u6765\u4e0d\u53ca\u4e86\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u201c\u9996\u5148\u662f\u8c01\u51fa\u9762\u963b\u6b62\u7684\u95ee\u9898\uff1f\u8054\u901a\u7684\u65b0\u6545\u4e8b\u8ba9\u8d75\u6d4e\u4e1c\u603b\u7406\u542c\u4e86\u9887\u4e3a\u5fc3\u52a8\uff0c\u9526\u6e56\u8fd8\u4e0d\u591f\u8d44\u683c\uff0c\u6613\u4e91\u98de\u4e0d\u7ba1\u8fd9\u5757\uff0c\u4ed6\u662f\u526f\u90e8\u957f\uff0c\u7a81\u7136\u63d2\u624b\u8fc7\u6765\uff0c\u53ea\u4f1a\u9677\u5165\u5f7b\u5e95\u7684\u88ab\u52a8\uff0c\u4e5f\u4e0d\u4f1a\u53d6\u5230\u591a\u5c11\u4f5c\u7528\uff1b\u82e5\u8bf4\u8c01\u51fa\u9762\u963b\u6b62\u5408\u9002\u7684\u8bdd\uff0c\u53ea\u6015\u4e5f\u53ea\u6709\u803f\u91cd\u9633\u51fa\u9762\u6700\u5408\u9002\uff0c\u4f46\u662f\u803f\u91cd\u9633\u4ed6\u7684\u6001\u5ea6\u4e0d\u660e\uff0c\u516c\u5e73\u9732\u9762\u5f88\u5c11\u5bf9\u8054\u901a\u5f15\u8fdbCDMA\u9879\u76ee\u8868\u6001\uff0c\u4e5f\u4e0d\u6e05\u695a\u4ed6\u5fc3\u91cc\u771f\u5b9e\u7684\u60f3\u6cd5\u3002\u53e6\u5916\uff0c\u8fd9\u4e48\u5927\u7684\u9879\u76ee\u6210\u8d25\u53ef\u4ee5\u8bf4\u662f\u5f71\u54cd\u5230\u4e2d\u7f8e\u53cc\u8fb9\u5173\u7cfb\uff0c\u8fd1\u671f\u6765\u4e2d\u7f8e\u53cc\u8fb9\u5173\u7cfb\u5f97\u5230\u5f88\u5927\u7684\u7f13\u89e3\uff0c\u9526\u6e56\u4e0e\u5fb7\u4eea\u7684\u6280\u672f\u8f6c\u8ba9\u534f\u8bae\u80fd\u5f97\u5230\u901a\u8fc7\u4e5f\u662f\u5b9e\u9645\u53d7\u76ca\u8005\u2026\u2026\u4e8b\u5b9e\u4e0a\uff0c\u8054\u901a\u76f2\u76ee\u5efaCDMA\u7f51\u7edc\uff0c\u9526\u6e56\u4e5f\u662f\u53d7\u76ca\u8005\uff0c\u6211\u4eec\u76ee\u524d\u6240\u638c\u63e1\u7684ESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u5728GPRS\u79fb\u52a8\u901a\u4fe1\u7f51\u7edc\u5e94\u7528\u4e2d\u8fd8\u4e0d\u591f\u5b8c\u5584\uff0c\u4e00\u65e6\u4e2d\u79fb\u52a8\u7684G\u7f51\u5168\u9762\u5347\u7ea7\uff0cESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u5c31\u53ef\u80fd\u66b4\u9732\u51fa\u6280\u672f\u50a8\u5907\u7684\u4e0d\u8db3\uff1b\u7136\u800c\u8054\u901a\u5c06\u9ad8\u7aef\u4e1a\u52a1\u5bc4\u5e0c\u671b\u5728CDMA\u6280\u672f\uff0c\u8054\u901a\u7684G\u7f51\u8f83\u957f\u65f6\u95f4\u5185\u5c31\u4e0d\u53ef\u80fd\u5347\u7ea7\uff0c\u8fd9\u6070\u6070\u662f\u9526\u6e56ESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u524d\u671f\u7684\u5e02\u573a\u51fa\u53e3\u3002\u53e6\u5916\uff0c\u7531\u4e8e\u97e9\u56fd\u56fd\u5185\u7684\u79fb\u52a8\u901a\u8baf\u91c7\u7528CDMA\u6280\u672f\uff0c\u4e09\u661f\u5728CDMA\u624b\u673a\u5360\u636e\u6781\u5f3a\u7684\u6280\u672f\u4f18\u52bf\uff0c\u8054\u901a\u5927\u529b\u5728\u56fd\u5185\u5efaCDMA\u7f51\u7edc\uff0c\u867d\u7136\u4f1a\u7ed9\u4e09\u661f\u501f\u673a\u6269\u5927\u5728\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u4e0a\u7684\u4efd\u989d\uff0c\u4f46\u662f\u9526\u6e56\u6b64\u65f6\u4e0e\u8054\u901a\u8fdb\u884c\u6df1\u5ea6\u7684\u5408\u4f5c\uff0c\u5176\u5b9e\u8fd8\u6709\u6bd4\u8f83\u5145\u88d5\u7684\u65f6\u95f4\u53bb\u5f00\u53d1CDMA\u624b\u673a\uff0c\u5c06\u6709\u52a9\u6211\u4eec\u5e73\u8861\u4e0e\u4e09\u661f\u4e4b\u95f4\u7684\u624b\u673a\u6280\u672f\u5dee\u8ddd\uff0c\u6269\u5927\u6211\u4eec\u7684\u4f18\u52bf\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u9526\u6e56\u8fd8\u627e\u4e0d\u5230\u542f\u52a8\u5f00\u53d1CDMA\u624b\u673a\u7684\u6700\u4f73\u65f6\u673a\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u665a\u6674\u624b\u6258\u7740\u8138\u988a\u76ef\u7740\u5f20\u606a\u770b\uff0c\u201c\u4f60\u5fc3\u91cc\u672a\u5fc5\u60f3\u8981\u8fd9\u4e2a\u5e02\u573a\u7f3a\u53e3\u5427\uff0c\u4e5f\u672a\u5fc5\u60f3\u8981\u8fd9\u4e2a\u65f6\u673a\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u4f60\u60f3\u8c61\u7684\u90a3\u4e48\u98ce\u683c\u9ad8\u5c1a\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u4e3b\u8981\u7684\u8fd8\u662f\u6015\u5f97\u7f6a\u4eba\uff0c\u8054\u901a\u4e0a\u4e0b\u536f\u8db3\u4e86\u52b2\u8981\u5efaCDMA\u7f51\uff0c\u6211\u8dd1\u8fc7\u53bb\u641e\u7834\u574f\uff0c\u8fd8\u4e0d\u7ed9\u6253\u5f97\u6ee1\u5934\u9752\u5305\u56de\u6765\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8096\u745e\u6c11\u4e4b\u524d\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4eca\u5929\u6ca1\u6709\u7ec6\u8c08\u7684\u8010\u5fc3\u5f88\u5feb\u5c31\u5bf9\u8054\u4fe1\u4e0e\u8054\u901a\u8fdb\u884c\u5b9a\u5236\u624b\u673a\u5408\u4f5c\u4e00\u4e8b\u8868\u6001\u7ed9\u4e88\u652f\u6301\uff0c\u9999\u6797\u5c0f\u9547\u7684\u89c1\u9762\u7ed3\u675f\u540e\uff0c\u5192\u7740\u98ce\u96ea\u8fd4\u56de\u5e02\u5185\uff0c\u8096\u745e\u6c11\u624d\u53d1\u73b0\u65f6\u95f4\u8fd8\u65e9\uff0c\u56de\u5230\u529e\u516c\u5ba4\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u7ed9\u6768\u84c9\u5e73\u6253\u7535\u8bdd\u3002</p><p>\u3000\u3000\u8d76\u5de7\u6768\u84c9\u5e73\u4eba\u5728\u529e\u516c\u5ba4\u91cc\uff0c\u8096\u745e\u6c11\u5c31\u8d76\u5230\u8054\u901a\u4f4d\u4e8e\u897f\u5355\u7684\u603b\u90e8\u5927\u697c\u53bb\u89c1\u6768\u84c9\u5e73\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u662f\u539f\u90ae\u7535\u90e8\u51fa\u8eab\uff0c\u8054\u901a\u96c6\u56e2\u526f\u603b\u7ecf\u7406\u6768\u84c9\u5e73\u662f\u539f\u7535\u5b50\u5de5\u4e1a\u90e8\u51fa\u8eab\uff0c\u867d\u7136\u540e\u6765\u90fd\u5f52\u62e2\u5230\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7684\u5927\u65d7\u4e0b\uff0c\u4f46\u662f\u591a\u5e74\u6765\u7684\u77db\u76fe\u4e0d\u53ef\u80fd\u8f7b\u6613\u5c31\u6d88\u9664\u6389\uff0c\u7279\u522b\u662f\u6709\u4e9b\u53f8\u5c40\u7ea7\u7684\u5b98\u5458\u79c1\u4eba\u95f4\u7684\u77db\u76fe\u4e5f\u5f88\u6df1\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u957f\u671f\u8d1f\u8d23\u539f\u90ae\u7535\u90e8\u4e0b\u5c5e\u7814\u7a76\u9662\u7684\u5de5\u4f5c\uff0c\u5904\u4e8e\u77db\u76fe\u65cb\u6da1\u7684\u8fb9\u7f18\uff0c\u540e\u671f\u624d\u8d1f\u8d23\u90ae\u7535\u90e8\u7ba1\u7406\u4e0b\u5c5e\u7684\u4f01\u4e1a\uff0c\u4e0e\u6768\u84c9\u5e73\u8fd9\u4e9b\u7535\u5b50\u5de5\u4e1a\u90e8\u51fa\u8eab\u7684\u5b98\u5458\u81f3\u5c11\u6ca1\u6709\u7279\u522b\u6df1\u7684\u79c1\u4eba\u77db\u76fe\u3002</p><p>\u3000\u3000\u8054\u901a\u7684\u9ad8\u5c42\u4e00\u5411\u8ba4\u4e3a\u4ed6\u4eec\u8fd9\u4e9b\u5e74\u4e00\u76f4\u90fd\u5728\u53d7\u5230\u79c1\u751f\u5b50\u7684\u5c90\u89c6\uff0c\u751f\u6027\u90fd\u504f\u597d\u6597\u3002\u8fd9\u6bb5\u65f6\u95f4\u6765\uff0c\u534e\u590f\u7535\u5b50\u5185\u90e8\u4e5f\u95f9\u5f97\u5389\u5bb3\uff0c\u8054\u4fe1\u4e0e\u8096\u745e\u6c11\u90fd\u662f\u7ed9\u6392\u6324\u7684\u5bf9\u8c61\uff0c\u8054\u901a\u9ad8\u5c42\u770b\u5230\u8096\u745e\u6c11\u591a\u5c11\u6709\u4e9b\u540c\u75c5\u76f8\u601c\u7684\u611f\u89c9\uff0c\u800c\u4e14\u8096\u745e\u6c11\u610f\u56fe\u72ec\u7acb\u7684\u6c14\u8282\uff0c\u4e5f\u66f4\u80fd\u5f97\u5230\u8054\u901a\u9ad8\u5c42\u7684\u5171\u9e23\u3002</p><p>\u3000\u3000\u8054\u901a\u8fd9\u4e9b\u8bd5\u6c34\u5b9a\u5236\u624b\u673a\uff0c\u6ca1\u6709\u627e\u540c\u6839\u7cfb\u7684\u8054\u8baf\u3001\u4e1c\u5174\uff0c\u51fa\u4eba\u610f\u6599\u7684\u627e\u5230\u8054\u4fe1\uff0c\u4e5f\u6709\u8fd9\u79cd\u56e0\u7d20\u5728\u5185\u3002</p><p>\u3000\u3000\u4e0e\u8054\u901a\u7684\u5408\u4f5c\uff0c\u4e5f\u80fd\u589e\u52a0\u5728\u534e\u590f\u7535\u5b50\u5185\u90e8\u5bf9\u6297\u845b\u5efa\u5fb7\u7684\u7b79\u7801\uff0c\u8096\u745e\u6c11\u81ea\u7136\u6781\u4e3a\u91cd\u89c6\uff0c\u8d76\u5230\u6768\u84c9\u5e73\u7684\u529e\u516c\u5ba4\uff0c\u6ca1\u6709\u7ed5\u4ec0\u4e48\u5f2f\u5b50\uff0c\u5c31\u5c06\u4e0b\u5348\u4e0e\u5f20\u606a\u4f1a\u9762\u7684\u60c5\u51b5\u8bf4\u7ed9\u4ed6\u542c\uff1a\u201c\u5f20\u606a\u4eca\u665a\u4f4f\u5728\u987a\u4e49\u7684\u9999\u6797\u5c0f\u9547\uff0c\u4ed6\u5bf9\u8054\u901a\u5411\u6211\u4eec\u91c7\u8d2d\u5b9a\u5236\u624b\u673a\u4e00\u4e8b\u751a\u4e3a\u652f\u6301\uff0c\u8fd8\u540c\u610f\u6625\u8282\u8fc7\u540e\u5c31\u7ed9\u5b9a\u5236\u624b\u673a\u63d0\u4f9b\u7b2c\u4e8c\u4ee3ESS\u57fa\u5e26\u82af\u7247\u2026\u2026\u4ed6\u9887\u4e3a\u671f\u5f85\u4e0e\u8054\u901a\u8fdb\u884c\u66f4\u6df1\u5c42\u6b21\u7684\u5408\u4f5c\uff0c\u4e5f\u4e0d\u4ecb\u610f\u4e0e\u8054\u901a\u79c1\u4e0b\u63a5\u89e6\u4e00\u4e0b\uff0c\u8981\u662f\u65b9\u4fbf\u7684\u8bdd\uff0c\u6211\u6765\u5b89\u6392\uff1f\u201d</p></article>"}}}]);
