(window.webpackJsonp=window.webpackJsonp||[]).push([[1107],{1130:function(p){p.exports={chapterTitle:null,title:"\u7b2c905\u7ae0 \u5371\u9669\u7684\u56e0\u7d20\u8981\u94f2\u9664",content:"<article><h2>\u7b2c905\u7ae0 \u5371\u9669\u7684\u56e0\u7d20\u8981\u94f2\u9664</h2><p>\u3000\u3000\u8096\u660e\u5efa\u6447\u6446\u4e0d\u5b9a\u603b\u662f\u4e00\u4e2a\u76f8\u5f53\u5371\u9669\u7684\u56e0\u7d20\uff0c\u5f20\u606a\u60f3\u7740\u8fd9\u6bb5\u65f6\u95f4\u6765\u8096\u660e\u5efa\u6001\u5ea6\u7684\u53d8\u6765\u53d8\u53bb\uff0c\u7f57\u541b\u8fd8\u6709\u4e9b\u653f\u6cbb\u5bb6\u7684\u6c14\u5ea6\uff0c\u8096\u660e\u5efa\u5219\u5b8c\u5168\u662f\u4e2a\u653f\u5ba2\u4e86\uff0c\u8ba9\u4eba\u770b\u4e0d\u5230\u4ed6\u7684\u7acb\u573a\u5728\u54ea\u91cc\uff0c\u62ac\u5934\u95ee\u53f6\u5efa\u658c\uff1a\u201c\u59da\u6587\u76db\u660e\u5929\u80fd\u5230\u5317\u4eac\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u660e\u5929\u4e0d\u4e00\u5b9a\u5c31\u80fd\u8d70\u5f97\u6210\uff0c\u201d\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u6240\u8c13\u7684\u5730\u4ea7\u62db\u5546\u5927\u4f1a\uff0c\u6211\u770b\u773c\u4e0b\u8fd8\u662f\u4e25\u6587\u4ecb\u5728\u4eac\u57ce\u516c\u5b50\u5708\u5185\u9020\u52bf\uff0c\u5efa\u90ba\u90a3\u91cc\u5e94\u8be5\u6ca1\u6709\u6d88\u606f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8ba9\u738b\u7ef4\u5747\u8282\u5916\u751f\u679d\u5982\u4f55\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u8282\u5916\u751f\u679d\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u5730\u4ea7\u62db\u5546\uff0c\u770b\u4e2d\u7684\u4e5f\u662f\u6e56\u4e1c\u65b0\u57ce\u5927\u7247\u5f85\u5f00\u53d1\u571f\u5730\uff0c\u90fd\u662f\u9ad8\u65b0\u533a\u7684\u7ba1\u8f96\u8303\u56f4\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e00\u65b9\u9762\uff0c\u8ba9\u9ad8\u65b0\u533a\u4e0e\u5efa\u90ba\u5e02\u5c3d\u5feb\u63a8\u51fa\u56fd\u6709\u571f\u5730\u4f7f\u7528\u6743\u8f6c\u8ba9\u5fc5\u987b\u8d70\u2018\u62db\u6302\u62cd\u2019\u7a0b\u5e8f\u7684\u6210\u6587\u6cd5\u89c4\uff0c\u62ff\u6210\u6587\u7684\u5236\u5ea6\u6765\u5835\u4f4f\u67d0\u4e9b\u4eba\u8fc7\u5ea6\u8d2a\u5a6a\u7684\u80c3\u53e3\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u6240\u8c13\u7684\u5730\u4ea7\u62db\u5546\u5927\u4f1a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u53ef\u4ee5\u7531\u738b\u7ef4\u5747\u62a2\u5148\u63d0\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u6253\u7b97\u5f7b\u5e95\u653e\u5f03\u8096\u660e\u5efa\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u65e2\u7136\u4ed6\u4eec\u5904\u5904\u60f3\u9488\u5bf9\u9526\u6e56\uff0c\u4e0d\u53cd\u624b\u72e0\u72e0\u62bd\u4ed6\u4eec\u4e00\u5df4\u638c\uff0c\u8fd8\u771f\u4ee5\u4e3a\u9526\u6e56\u8f6f\u5f31\u53ef\u6b3a\u5462\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8096\u660e\u5efa\u4e0d\u662f\u9526\u6e56\u8981\u4e0d\u8981\u5f7b\u5e95\u653e\u5f03\u7684\u95ee\u9898\uff0c\u800c\u8096\u660e\u5efa\u6839\u672c\u4e0d\u53ef\u80fd\u8ddf\u9526\u6e56\u8d70\u5230\u4e00\u8d77\u6765\u2014\u2014\u4e5f\u8981\u8ba9\u4e25\u6587\u4ecb\u77e5\u9053\u8fd9\u52bf\u4e0d\u662f\u968f\u4fbf\u5c31\u80fd\u501f\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u5e94\u8be5\u8fd9\u6837\u554a\uff0c\u8fd9\u54ea\u91cc\u662f\u8282\u5916\u751f\u679d\uff1f\u5bf9\u8096\u660e\u5efa\u6765\u8bf4\u7b80\u503c\u5c31\u662f\u91dc\u5e95\u62bd\u85aa\u554a\uff0c\u201d\u53f6\u5efa\u658c\u795e\u8272\u632f\u594b\u8d77\u6765\uff0c\u4ed6\u8eab\u4e0a\u6d41\u8d9f\u7684\u8fd8\u662f\u597d\u6597\u7684\u8840\u6db2\uff0c\u8bf4\u8d77\u6765\u4ee5\u4ed6\u53f6\u5bb6\u7684\u6839\u57fa\u4e0e\u4eba\u8109\uff0c\u4eac\u57ce\u516c\u5b50\u5708\u91cc\u4e5f\u5c11\u6709\u8ba9\u4ed6\u754f\u60e7\u7684\u4eba\uff0c\u4e25\u6587\u4ecb\u8fd9\u89d2\u8272\u518d\u72e0\uff0c\u4e5f\u5b81\u591a\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u53ea\u4e0d\u8fc7\u8003\u8651\u5230\u9526\u6e56\u53d1\u5c55\u7684\u5927\u5c40\uff0c\u6709\u65f6\u5019\u8981\u97ec\u5149\u517b\u6666\uff0c\u201c\u4eac\u57ce\u516c\u5b50\u5708\u91cc\u4e2a\u4e2a\u90fd\u8981\u7b97\u8fc7\u6c5f\u9f99\uff0c\u4f46\u662f\u5728\u5efa\u90ba\uff0c\u6211\u4eec\u8981\u7b97\u5750\u5c71\u864e\uff0c\u8981\u8bf4\u53ea\u5c40\u9650\u5728\u5730\u4ea7\u884c\u4e1a\u5185\u7ade\u4e89\uff0c\u6211\u4eec\u5f53\u7136\u53ef\u4ee5\u5bf8\u6b65\u4e0d\u8ba9\uff1b\u53ea\u4e0d\u8fc7\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u7275\u6d89\u751a\u5e7f\uff0c\u4ea4\u901a\u90e8\u3001\u6d77\u5173\u603b\u7f72\u3001\u8ba1\u59d4\u3001\u7ecf\u8d38\u59d4\u3001\u5efa\u8bbe\u59d4\u3001\u5916\u7ecf\u8d38\u90e8\u7b49\u7b49\u90e8\u59d4\uff0c\u968f\u4fbf\u7ed9\u4eba\u5361\u4f4f\u4e00\u9053\u53e3\u5b50\u5c31\u4f1a\u8ba9\u4eba\u75db\u4e0d\u6b32\u751f\u554a\uff01\u8ba9\u738b\u7ef4\u5747\u62a2\u5148\u62ff\u5730\u4ea7\u62db\u5546\u5927\u4f1a\u6765\u62c9\u62e2\u4eac\u57ce\u7684\u516c\u5b50\u54e5\u4eec\uff0c\u603b\u6bd4\u8ba9\u4e25\u6587\u4ecb\u4e0e\u8096\u660e\u5efa\u5c06\u4eac\u57ce\u516c\u5b50\u5708\u5f15\u5bfc\u5230\u6211\u4eec\u7684\u5bf9\u7acb\u9762\u53bb\u5f3a\u5f97\u591a\u2026\u2026\u8096\u660e\u5efa\u5c31\u662f\u90a3\u79cd\u62ff\u6765\u7ed9\u727a\u7272\u6389\u7684\u8d27\u8272\u3002\u201d\u53f6\u5efa\u658c\u5bf9\u8096\u660e\u5efa\u7684\u53cd\u8986\u65e0\u5e38\u4e5f\u76f8\u5f53\u7684\u607c\u706b\u3002</p><p>\u3000\u3000\u4eba\u81f3\u6e05\u5219\u65e0\u5f92\uff0c\u6c34\u81f3\u6e05\u5219\u65e0\u9c7c\uff0c\u9526\u6e56\u60f3\u8981\u6c14\u52bf\u6c79\u6c79\u7684\u6321\u4f4f\u4e00\u5927\u7fa4\u4eba\u7684\u8d22\u5bcc\uff0c\u6ce8\u5b9a\u8981\u9c9c\u8840\u6dcb\u6f13\uff0c\u623f\u5730\u4ea7\u4e1a\u5bfc\u81f4\u793e\u4f1a\u8d22\u5bcc\u4e8c\u6b21\u5206\u914d\u3001\u5bfc\u81f4\u793e\u4f1a\u8d22\u5bcc\u518d\u6b21\u5927\u89c4\u6a21\u7684\u5411\u7279\u6743\u9636\u5c42\u96c6\u4e2d\u7684\u8d8b\u52bf\u5df2\u7ecf\u4e0d\u662f\u4e2a\u4eba\u7684\u52aa\u529b\u80fd\u907f\u514d\u7684\u3002</p><p>\u3000\u3000\u4ece\u56db\u6708\u5230\u4e5d\u6708\uff0c\u56fd\u52a1\u9662\u5185\u90e8\u5c31\u8981\u4e0d\u8981\u5c06\u623f\u5730\u4ea7\u4e1a\u653e\u5230\u56fd\u6c11\u7ecf\u6d4e\u652f\u67f1\u4ea7\u4e1a\u7684\u9ad8\u5ea6\u6765\u4fc3\u8fdb\u53d1\u5c55\u4ea7\u751f\u6781\u5927\u7684\u4e89\u8bae\u3002</p><p>\u3000\u3000\u4e94\u6708\u4e2d\u65ec\uff0c\u5728\u8d75\u6d4e\u4e1c\u603b\u7406\u7684\u652f\u6301\u4e0b\uff0c\u56fd\u52a1\u9662\u53d1\u6587\u51b3\u8bae\u53d6\u6d88\u201c\u5c06\u623f\u5730\u4ea7\u4e1a\u53d1\u5c55\u4e3a\u56fd\u6c11\u7ecf\u6d4e\u652f\u67f1\u4ea7\u4e1a\u201d\u7684\u63d0\u6cd5\uff0c\u7136\u800c\u5230\u516b\u6708\u4e0b\u65ec\uff0c\u98ce\u5411\u5c31\u9661\u7136\u8f6c\u53d8\uff0c\u4e94\u6708\u4e2d\u65ec\u7684\u53d1\u6587\u88ab\u53d6\u6d88\uff0c\u6b63\u5f0f\u786e\u5b9a\u4e86\u623f\u5730\u4ea7\u4e1a\u4e3a\u56fd\u6c11\u7ecf\u6d4e\u652f\u67f1\u4ea7\u4e1a\u7684\u5730\u4f4d\u2014\u2014\u6700\u76f4\u63a5\u7684\u540e\u679c\u5c31\u662f\u5bfc\u81f4\u56fd\u5bb6\u5404\u7c7b\u8d44\u6e90\u5411\u623f\u5730\u4ea7\u4e1a\u503e\u659c\u3002</p><p>\u3000\u3000\u8fd9\u5927\u6982\u662f\u56fd\u5185\u8d44\u672c\u529b\u91cf\u7b2c\u4e00\u6b21\u5f3a\u52bf\u7684\u9006\u8f6c\u56fd\u5bb6\u653f\u7b56\u8d70\u5411\uff0c\u4f46\u662f\u4e5d\u516b\u5e74\u623f\u5730\u4ea7\u8d44\u672c\u6d41\u6dcc\u7740\u592a\u591a\u7684\u7ea2\u8272\u8840\u6db2\u3002\u4e5d\u516b\u5e74\uff0c\u8c01\u90fd\u6ca1\u6709\u5f20\u606a\u90a3\u4e48\u6e05\u695a\u8fd9\u9879\u653f\u7b56\u5bf9\u672a\u6765\u6570\u5341\u5e74\u56fd\u6c11\u7ecf\u6d4e\u4e0e\u56fd\u6c11\u751f\u6d3b\u7684\u5f71\u54cd\u662f\u4f55\u7b49\u7684\u6df1\u8fdc\uff01\u63a5\u4e0b\u6765\uff0c\u5730\u65b9\u653f\u5e9c\u4e5f\u4f1a\u5f88\u5feb\u610f\u8bc6\u5230\u53d1\u5c55\u5927\u529b\u623f\u5730\u4ea7\u4e1a\u7684\u5b9e\u9645\u597d\u5904\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u95ee\u53ca\u53f6\u5efa\u658c\u665a\u4e0a\u4e0e\u4ea4\u901a\u90e8\u5b98\u5458\u7684\u4f1a\u9762\u7ed3\u679c\u5982\u4f55\u3002</p><p>\u3000\u3000\u201c\u6d88\u606f\u5728\u5317\u4eac\u4f20\u8d77\u6765\u4e5f\u771f\u662f\u90aa\u4e4e\u7684\u5feb\uff0c\u201d\u53f6\u5efa\u658c\u62b1\u5934\u9760\u7740\u6c99\u53d1\u6905\u80cc\uff0c\u611f\u6168\u7684\u8bf4\u4e86\u4e00\u58f0\uff0c\u201c\u5e78\u4e8f\u6211\u4eec\u538b\u5230\u73b0\u5728\u624d\u5c06\u6d88\u606f\u653e\u51fa\u6765\uff0c\u4e0d\u7136\u65e9\u5c31\u4e71\u4e86\u9635\u811a\u2014\u2014\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7684\u4e8b\u60c5\u624d\u6446\u5230\u53f0\u9762\u4e0a\uff0c\u6d88\u606f\u5c31\u5728\u5317\u4eac\u4f20\u5f00\u4e86\uff0c\u4ea4\u901a\u90e8\u8fd9\u4e00\u5173\u8fc7\u4e5f\u5bb9\u6613\uff0c\u8ba9\u4e2d\u8fdc\u53c2\u4e0e\u8fdb\u6765\u5c31\u884c\uff01\u201d</p><p>\u3000\u3000\u4e2d\u8fdc\u5373\u4e2d\u56fd\u8fdc\u6d0b\u8fd0\u8f93\u96c6\u56e2\uff0c\u662f\u4ea4\u901a\u90e8\u4e0b\u5c5e\u7684\u5927\u578b\u56fd\u6709\u4f01\u4e1a\uff0c\u56fd\u5185\u6d77\u8fd0\u9886\u57df\u7684\u5b98\u5458\u4e0e\u4f01\u4e1a\u7ba1\u7406\u8005\u90fd\u610f\u8bc6\u5230\u6d77\u8fd0\u4f01\u4e1a\u5411\u6e2f\u53e3\u4e1a\u6e17\u900f\u662f\u5927\u52bf\u6240\u8d8b\uff0c\u4ea4\u901a\u90e8\u7684\u8981\u6c42\u4e5f\u4e0d\u51fa\u4eba\u610f\u6599\uff0c\u5f20\u606a\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7b54\u5e94\u4ed6\u4eec\u5c31\u662f\uff0c\u65e2\u7136\u4ed6\u4eec\u60f3\u8981\u597d\u5904\uff0c\u8d23\u4efb\u4e5f\u8981\u4ed6\u4eec\u627f\u62c5\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u804a\u5230\u51cc\u6668\uff0c\u53f6\u5efa\u658c\u8981\u4f4f\u5230\u5e02\u91cc\u53bb\uff0c\u660e\u5929\u8d77\u65e9\u8fd8\u8981\u4e0e\u6d77\u5173\u603b\u7f72\u7684\u5b98\u5458\u89c1\u9762\uff0c\u7761\u5728\u7fe0\u56ed\u5c31\u6015\u660e\u5929\u65e9\u6668\u4f1a\u7ed9\u62e5\u585e\u7684\u4ea4\u901a\u5835\u5728\u57ce\u5916\u8fdb\u4e0d\u4e86\u57ce\uff1b\u8bb8\u745e\u5e73\u4e5f\u8ddf\u4ed6\u4e00\u8d77\u56de\u4e86\u5e02\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u7740\u54c8\u6b20\uff0c\u95ee\u7fdf\u4e39\u9752\u7fe0\u56ed\u6709\u4ec0\u4e48\u597d\u89e3\u4e4f\u7684\u73a9\u827a\u3002</p><p>\u3000\u3000\u201c\u91cc\u53a2\u6709\u5ea7\u5ba4\u5185\u6cf3\u6c60\uff0c\u662f\u4ece\u534a\u5c71\u8170\u5f15\u6765\u7684\u6e29\u6cc9\u6c34\uff0c\u201d\u7fdf\u4e39\u9752\u8bf4\u9053\uff0c\u201c\u7fe0\u56ed\u4f3c\u4e4e\u8fd8\u53ef\u4ee5\u627e\u4e9b\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u6765\u966a\u4f60\u6e38\u6cf3\uff0c\u60f3\u5fc5\u5f88\u89e3\u4e4f\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u60f3\u5230\u7fe0\u56ed\u8fd8\u6709\u8fd9\u79cd\u597d\u53bb\u5904\uff0c\u770b\u7740\u4e00\u7fa4\u7a7f\u6bd4\u57fa\u5c3c\u7684\u7f8e\u4eba\u9c7c\u5728\u6cf3\u6c60\u91cc\u6e38\u6765\u6e38\u53bb\uff0c\u90a3\u7684\u786e\u662f\u5f88\u89e3\u4e4f\u63d0\u795e\uff0c\u53ea\u662f\u8fd9\u79cd\u5fc3\u601d\u600e\u4e48\u80fd\u5728\u7fdf\u4e39\u9752\u9762\u524d\u6d41\u9732\u51fa\u6765\uff1f\u7528\u5f88\u5e73\u6de1\u7684\u8bed\u6c14\u8bf4\u9053\uff1a\u201c\u53bb\u6e29\u6cc9\u6c34\u91cc\u6ce1\u4e00\u6ce1\uff0c\u5012\u662f\u4e0d\u9519\u2026\u2026\u201d\u4f3c\u4e4e\u5f88\u4e0d\u5c51\u63d0\u966a\u6cf3\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u7a7f\u7740\u6cf3\u88e4\u8d70\u8fdb\u6cf3\u6c60\u91cc\uff0c\u5f20\u606a\u7684\u80a0\u5b50\u90fd\u6094\u9752\u4e86\uff0c\u7a7a\u8361\u8361\u7684\u901a\u957f\u623f\u95f4\u91cc\uff0c\u715e\u767d\u7684\u8282\u80fd\u706f\u5c06\u5149\u6d12\u5728\u78a7\u84dd\u7684\u6e29\u6cc9\u6c34\u4e0a\uff0c\u4ed6\u5962\u671b\u7740\u7fdf\u4e39\u9752\u4f1a\u7a7f\u7740\u6bd4\u57fa\u5c3c\u6cf3\u88c5\u6027\u611f\u800c\u5996\u5a06\u7684\u8d70\u8fdb\u6765\uff0c\u4ed6\u4e00\u4e2a\u4eba\u6ce1\u4e86\u534a\u5929\uff0c\u65e0\u804a\u5f97\u6ce1\u5728\u6e29\u6c34\u91cc\u8981\u6253\u54c8\u6b20\uff0c\u9b3c\u5f71\u5b50\u90fd\u6ca1\u6709\u770b\u5230\u534a\u4e2a\uff0c\u5fc3\u91cc\u60f3\u7740\u8fd9\u4e16\u95f4\u7684\u7537\u4eba\u5230\u5e95\u6709\u591a\u5c11\u533a\u522b\uff0c\u7537\u4eba\u7231\u4e0a\u5973\u4eba\u96be\u9053\u4e0d\u662f\u90fd\u4ece\u8d2a\u6155\u5973\u8272\u7684\u77ac\u95f4\u5f00\u59cb\u7684\u5417\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u738b\u7ef4\u5747\u7279\u610f\u51c6\u4e86\u59da\u6587\u76db\u7684\u5047\uff0c\u8ba9\u4ed6\u63d0\u524d\u4e00\u5929\u56de\u5317\u4eac\uff0c\u53f6\u7956\u8303\u4e5f\u662f\u63d0\u524d\u4e00\u5929\u5c31\u4f4f\u8fdb\u987e\u5b85\uff0c\u8fd9\u5929\u6b63\u597d\u4e5f\u662f\u5468\u672b\uff0c\u515a\u6821\u5b66\u4e60\u73ed\u653e\u5047\uff0c\u5510\u5b66\u8c26\u5f53\u7136\u4e0d\u4f1a\u6d6a\u8d39\u5982\u6b64\u826f\u673a\u56de\u6d77\u5dde\u53bb\uff0c\u8ddf\u7740\u5f20\u606a\u5230\u987e\u5b85\u62dc\u8bbf\u987e\u957f\u5e9a\u3001\u53f6\u7956\u8303\u4e24\u4f4d\u8001\u4eba\uff0c\u987e\u957f\u5e9a\u7684\u5973\u5a7f\u9648\u65b0\u6c11\u4e0e\u59bb\u5b50\u987e\u7545\u4e5f\u662f\u8fd9\u65e5\u62b5\u8fbe\u5317\u4eac\u3002</p><p>\u3000\u3000\u9648\u65b0\u6c11\u66fe\u5728\u6c5f\u5357\u7701\u5de5\u4f5c\u4e86\u8fd1\u5341\u5e74\uff0c\u4f4d\u53ca\u7701\u59d4\u526f\u4e66\u8bb0\u7684\u9ad8\u4f4d\uff0c\u4f46\u662f\u53bb\u5e74\u79cb\u540e\u6c5f\u5357\u7701\u5b98\u573a\u52a8\u8361\uff0c\u9648\u65b0\u6c11\u4e5f\u53d7\u5230\u6d89\u53ca\uff0c\u88ab\u8c03\u5230\u4e2d\u90e8\u67d0\u7701\u7ee7\u7eed\u62c5\u4efb\u7701\u59d4\u526f\u4e66\u8bb0\uff0c\u653f\u6cbb\u524d\u666f\u6709\u4e9b\u4e0d\u5927\u660e\u6717\u3002\u4e14\u4e0d\u53bb\u7ba1\u9648\u65b0\u6c11\u5728\u4e2d\u90e8\u5730\u533a\u7684\u524d\u7a0b\uff0c\u9648\u65b0\u6c11\u5728\u6c5f\u5357\u7701\u5de5\u4f5c\u8fd1\u5341\u5e74\uff0c\u624e\u4e0b\u5f88\u6df1\u7684\u6839\u57fa\uff0c\u8fd9\u90fd\u8981\u7b97\u662f\u6781\u597d\u7684\u653f\u6cbb\u8d44\u6e90\u3002\u8fd9\u4e9b\u653f\u6cbb\u8d44\u6e90\u65e0\u8bba\u7531\u5f90\u5b66\u5e73\u6216\u5510\u5b66\u8c26\u6765\u7ee7\u627f\uff0c\u5bf9\u987e\u5bb6\u90fd\u6ca1\u6709\u592a\u5927\u7684\u533a\u522b\uff0c\u603b\u662f\u8981\u7b97\u662f\u7eed\u4e86\u987e\u5bb6\u7684\u6e0a\u6e90\u4e0e\u4eba\u8109\u3002\u5bf9\u5f20\u606a\u4ed6\u4eec\u6765\u8bf4\uff0c\u5c31\u6709\u5f88\u5927\u7684\u533a\u522b\uff0c\u4e1c\u5357\u7701\u662f\u5f90\u5b66\u5e73\u4ed5\u9014\u7684\u6700\u540e\u4e00\u7ad9\u51e0\u6210\u5b9a\u5c40\uff0c\u8fd9\u4e9b\u653f\u6cbb\u8d44\u6e90\u7531\u5f90\u5b66\u5e73\u6765\u63a5\u6536\uff0c\u5f90\u5b66\u5e73\u9000\u4f4d\u540e\uff0c\u5c31\u4f1a\u5b58\u5728\u5510\u5b66\u8c26\u8be5\u5982\u4f55\u7ee7\u7eed\u63a5\u624b\u7684\u95ee\u9898\uff0c\u8fd8\u6d89\u53ca\u5230\u5510\u5b66\u8c26\u4e0e\u6881\u4f1f\u6cd5\u7684\u5173\u7cfb\u5982\u4f55\u5904\u7406\u7684\u95ee\u9898\uff0c\u6bd5\u7adf\u6881\u4f1f\u6cd5\u4e5f\u662f\u4eb2\u9526\u6e56\u7684\uff0c\u4e0d\u80fd\u8fc7\u5206\u5bd2\u4e86\u4ed6\u7684\u5fc3\u2014\u2014\u8fd8\u4e0d\u5982\u8fd9\u65f6\u5019\u5c31\u8ba9\u5510\u5b66\u8c26\u51fa\u9762\u63a5\u6536\u9648\u65b0\u6c11\u5728\u6c5f\u5357\u7684\u653f\u6cbb\u8d44\u6e90\uff0c\u76f4\u63a5\u5960\u5b9a\u5510\u5b66\u8c26\u5728\u6c5f\u5357\u7684\u6839\u57fa\uff1b\u5982\u6b64\u4e00\u6765\u4e5f\u80fd\u7ef4\u62a4\u6c5f\u5357\u7701\u9ad8\u5c42\u65e5\u540e\u5b89\u5b9a\u56e2\u7ed3\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u867d\u8bf4\u987e\u957f\u5e9a\u7684\u751f\u65e5\u662f26\u65e5\uff0c25\u65e5\u987e\u5b85\u5c31\u70ed\u95f9\u8d77\u6765\u4e86\u3002</p><p>\u3000\u3000\u987e\u5b85\u5728\u5317\u4eac\u57ce\u897f\u5317\u89d2\uff0c\u8ddd\u7fe0\u56ed\u4e0d\u8fdc\uff0c\u662f\u680b\u56db\u5408\u9662\u5f0f\u7684\u5efa\u7b51\uff0c\u5e73\u65e5\u91cc\u5c31\u987e\u957f\u5e9a\u4e0e\u8001\u4f34\u8fd8\u6709\u4fdd\u59c6\u3001\u53a8\u5e08\u3001\u8b66\u536b\u5458\u4f4f\u5728\u91cc\u9762\uff0c\u9662\u91cc\u79cd\u4e86\u4e00\u68f5\u94f6\u674f\u6811\uff0c\u987e\u957f\u5e9a\u7b11\u7740\u8bf4\uff1a\u201c\u4e4b\u524d\u6709\u4e24\u68f5\u94f6\u674f\uff0c\u516d\u4e03\u5e74\u67af\u6b7b\u4e86\u4e00\u68f5\uff0c\u90fd\u8bf4\u9662\u4e2d\u72ec\u6728\u4e3a\u2018\u56f0\u2019\uff0c\u53ea\u662f\u516d\u4e03\u5e74\u8c01\u6709\u80c6\u91cf\u518d\u79cd\u4e00\u682a\uff1f\u5c31\u8fd9\u68f5\u94f6\u674f\u72ec\u81ea\u5174\u65fa\u7684\u957f\u4e86\u8fd9\u4e48\u591a\u5e74\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u9910\u524d\uff0c\u4fe1\u606f\u4ea7\u4e1a\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u4e0e\u5973\u513f\u6613\u99a8\u4e5f\u8d76\u4e86\u8fc7\u6765\uff0c\u6613\u99a8\u5012\u6ca1\u6709\u4e11\u5ab3\u5987\u89c1\u516c\u5a46\u7684\u7f9e\u6da9\uff0c\u987e\u5bb6\u4e0a\u4e0b\u51e0\u4e4e\u90fd\u662f\u770b\u7740\u5979\u957f\u5927\u7684\u957f\u8f88\u3002\u8ba1\u5212\u53d1\u5c55\u59d4\u9ad8\u79d1\u6280\u53d1\u5c55\u53f8\u7684\u9646\u5065\u662f\u59da\u6587\u76db\u5728\u5317\u4eac\u65f6\u7684\u72d0\u670b\u72d7\u53cb\uff0c\u9646\u5bb6\u4e0e\u987e\u5bb6\u4e5f\u662f\u4e16\u4ea4\uff0c\u9646\u5bb6\u7684\u8001\u4eba\u5df2\u7ecf\u901d\u4e16\uff0c\u636e\u59da\u6587\u76db\u8bf4\uff0c\u9646\u5065\u7684\u7236\u4eb2\u9646\u65b0\u534e\u5e74\u8f7b\u65f6\u66fe\u8ffd\u6c42\u8fc7\u4ed6\u59d1\u59d1\u987e\u7545\u672a\u5f97\u6210\u529f\uff0c\u8fd9\u4e9b\u5e74\u8ddf\u987e\u5bb6\u7684\u5173\u7cfb\u5f88\u6de1\uff0c\u5012\u662f\u4e0d\u7981\u6b62\u9646\u5065\u8ddf\u8fd9\u8fb9\u4eb2\u8fd1\u3002\u9646\u5065\u542c\u5230\u59da\u6587\u76db\u56de\u5230\u5317\u4eac\uff0c\u4e5f\u8feb\u4e0d\u53ca\u5f85\u7684\u8d76\u8fc7\u6765\u8e6d\u665a\u996d\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u9510\u610f\u5728\u653f\u754c\u53d1\u5c55\uff0c\u6539\u5934\u6362\u9762\uff0c\u4e5f\u9042\u4e86\u4ed6\u5bb6\u91cc\u4eba\u7684\u5fc3\u610f\uff0c\u4e0e\u6613\u99a8\u6b63\u5f0f\u4ea4\u5f80\u8d77\u6765\uff0c\u8fd8\u786e\u5b9a\u7684\u5a5a\u671f\uff1b\u9646\u5065\u5012\u662f\u6ca1\u6709\u6536\u5fc3\uff0c\u73a9\u6027\u5f88\u91cd\uff0c\u4ea4\u4e86\u8ba1\u59d4\u673a\u5173\u5e7c\u513f\u56ed\u7684\u4e00\u540d\u6559\u5e08\u5973\u53cb\uff0c\u53ef\u662f\u5f53\u4e86\u5f20\u606a\u4ed6\u4eec\u9762\u63a5\u4e86\u4e24\u4e2a\u5973\u5b69\u5b50\u7684\u7535\u8bdd\uff0c\u90fd\u53ef\u4ee5\u80af\u5b9a\u4e0d\u662f\u5728\u5e7c\u513f\u56ed\u5de5\u4f5c\u7684\uff0c\u7fdf\u4e39\u9752\u8fd8\u627e\u4e86\u673a\u4f1a\u6084\u58f0\u8bbd\u523a\u5f20\u606a\u8bf4\uff1a\u201c\u7537\u4eba\u5012\u662f\u5bb9\u6613\u627e\u5230\u81ed\u5473\u76f8\u6295\u7684\u3002\u201d</p><p>\u3000\u3000\u665a\u4e0a\u5510\u5b66\u8c26\u4e0e\u6613\u4e91\u98de\u3001\u9648\u65b0\u6c11\u966a\u987e\u957f\u5e9a\u3001\u53f6\u7956\u8303\u4e24\u4e2a\u8001\u4eba\u8bf4\u8bdd\uff0c\u4e5f\u662f\u8981\u5728\u4e24\u4f4d\u8001\u4eba\u9762\u524d\u663e\u9732\u624d\u5b66\u3002\u5510\u5b66\u8c26\u662f\u771f\u6b63\u7684\u5b66\u8005\u51fa\u8eab\uff0c\u5bf9\u7ecf\u6d4e\u7814\u7a76\u6781\u6df1\uff0c\u5728\u5730\u65b9\u4e0a\u8fd1\u5341\u5e74\u7684\u4ece\u653f\u7ecf\u9a8c\u4e5f\u7b97\u4e30\u5bcc\uff0c\u53c8\u6709\u6d77\u5dde\u5e02\u56db\u5e74\u7ecf\u6d4e\u817e\u98de\u7684\u5b9e\u7ee9\uff0c\u7b97\u662f\u6709\u4e00\u4efd\u5b8c\u7f8e\u7684\u5c65\u5386\uff0c\u7136\u800c\u53f6\u3001\u987e\u4e24\u5bb6\u613f\u610f\u5c06\u591a\u5c11\u7684\u653f\u6cbb\u8d44\u6e90\u5411\u5510\u5b66\u8c26\u503e\u659c\uff0c\u8fd8\u662f\u8981\u7531\u987e\u957f\u5e9a\u3001\u53f6\u7956\u8303\u4e24\u4f4d\u8001\u4eba\u8bf4\u4e86\u7b97\u3002</p><p>\u3000\u3000\u653f\u6cbb\u5c40\u5c31\u4e09\u5341\u4e2a\u4e0d\u5230\u7684\u4f4d\u5b50\uff0c\u6709\u5b9e\u529b\u9010\u9e7f\u8fd9\u4e9b\u4f4d\u5b50\u7684\u4eba\u6ca1\u6709\u4e24\u767e\u4e5f\u6709\u4e00\u767e\u516b\uff0c\u4f55\u51b5\u5510\u5b66\u8c26\u8fd9\u65f6\u5019\u8fde\u4e2d\u592e\u5019\u8865\u59d4\u5458\u7684\u4f4d\u5b50\u8fd8\u5dee\u4e00\u622a\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ee5\u53ca\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u3001\u9646\u5065\u3001\u8bb8\u745e\u5e73\u4ed6\u4eec\u8fd9\u4e9b\u7b97\u665a\u8f88\u7684\u90fd\u4e0d\u51d1\u8fd9\u4e2a\u70ed\u95f9\uff0c\u4e00\u8d77\u56de\u7fe0\u56ed\u6ce1\u6e29\u6cc9\u6cf3\u6c60\u3002</p></article>"}}}]);
