(window.webpackJsonp=window.webpackJsonp||[]).push([[111],{134:function(p){p.exports={chapterTitle:null,title:"\u7b2c1095\u7ae0 \u4e0d\u80fd\u505a\u60c5\u4eba",content:"<article><h2>\u7b2c1095\u7ae0 \u4e0d\u80fd\u505a\u60c5\u4eba</h2><p>\u3000\u3000\u4e00\u6708\u516b\u65e5\uff0c\u6d77\u7c9f\u79d1\u6280\u518d\u5ea6\u505c\u724c\u53d1\u5e03\u516c\u544a\uff0c\u5c06\u5411\u5b8f\u4fe1\u6295\u8d44\u3001\u7cbe\u5178\u5730\u4ea7\u7b49\u5927\u80a1\u4e1c\u5b9a\u5411\u589e\u53d1\u65b0\u80a1\u7b79\u96c6\u4e94\u4ebf\u5143\u8d44\u91d1\uff0c\u6b64\u6b21\u5b9a\u5411\u52df\u8d44\uff0c\u5176\u4e2d\u56db\u4ebf\u5c06\u7528\u4e8e\u5bf9\u7535\u5b50\u5546\u52a1\u5e73\u53f08818\u7684\u6ce8\u8d44\uff0c\u4f7f\u5f97\u6d77\u7c9f\u79d1\u6280\u5bf98818\u7684\u6301\u80a1\u6bd4\u4f8b\u7531\u5f53\u524d\u768430\uff05\u63d0\u9ad8\u5230\u8d85\u8fc7\u534a\u6570\uff0c\u5176\u4f59\u8d44\u91d1\u5c06\u7528\u4e8e\u53d1\u5c55\u521b\u57df\u7f51\u5427\u4ea7\u4e1a\u3001\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5efa\u8bbe\u4ee5\u53ca\u5bf9\u4e09\u661f\u5b8f\u82af\u7684\u6ce8\u8d44\u3002</p><p>\u3000\u3000\u6bd4\u8f83\u8d77\u4e94\u6708\u4e2d\u65ec\u501f\u58f3\u91cd\u7ec4\u4e0a\u5e02\u4e4b\u524d\u7684\u80a1\u4ef7\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u4ef7\u683c\u5b9e\u9645\u589e\u6da8\u4e86\u4e8c\u5341\u500d\uff0c\u8282\u540e\u51e0\u5929\u65f6\u95f4\u91cc\uff0c\u603b\u5e02\u503c\u4ece\u516d\u5341\u516b\u4ebf\u8dc3\u5347\u5230\u516b\u5341\u4e8c\u4ebf\uff0c\u5e02\u503c\u7a81\u7834\u767e\u4ebf\u4f3c\u4e4e\u4e5f\u6307\u65e5\u53ef\u5f85\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5728\u8fd9\u4e00\u62e8\u4e92\u8054\u7f51\u6d6a\u6f6e\u91cc\uff0c\u6d77\u7c9f\u79d1\u6280\u5e76\u975e\u72ec\u81ea\u5728\u72c2\u821e\uff0c\u4ece\u4e94\u6708\u4e2d\u65ec\u65b0\u79d1\u6280\u884c\u60c5\u731b\u70c8\u62c9\u5347\u4ee5\u6765\uff0c\u6caa\u6df1\u4e24\u5e02\u5171\u6709\u516d\u652f\u80a1\u4ef7\u6da8\u5e45\u5728\u534a\u5e74\u7a0d\u591a\u4e00\u4e9b\u7684\u65f6\u95f4\u91cc\u8d85\u8fc72000\uff05\u3002</p><p>\u3000\u3000\u76f8\u6bd4\u8f83\u5176\u4ed6\u4e94\u652f\u5996\u80a1\uff0c\u6d77\u7c9f\u79d1\u6280\u4f3c\u4e4e\u66f4\u6709\u589e\u6da8\u7684\u7406\u7531\uff0c\u501f\u58f3\u91cd\u7ec4\u3001\u7535\u5b50\u5546\u52a1\u5e73\u53f0\u3001\u5784\u65ad\u6027\u8d28\u7684\u7f51\u5427\u4ea7\u4e1a\u3001\u53c2\u4e0e\u7535\u5b50\u4ea7\u4e1a\u57fa\u77f3\u9879\u76ee\u6676\u5706\u5382\u7684\u5efa\u8bbe\uff0c\u65e0\u8bba\u54ea\u4e00\u4e2a\u9898\u6750\u62ff\u5230\u8bc1\u5238\u5e02\u573a\u4e0a\u90fd\u4f1a\u7ed9\u70ed\u7092\uff0c\u6b64\u65f6\u6d77\u7c9f\u79d1\u6280\u5927\u80a1\u4e1c\u53c8\u5728\u8fd9\u6837\u7684\u9ad8\u4ef7\u4f4d\u5927\u89c4\u6a21\u53c2\u52a0\u5b9a\u5411\u589e\u53d1\uff0c\u81ea\u7136\u8fdb\u4e00\u6b65\u52a0\u5f3a\u4e86\u4e2d\u5c0f\u6563\u6237\u53ca\u6295\u8d44\u673a\u6784\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u65b0\u52a0\u5761\u7ec8\u5e74\u90fd\u662f\u590f\u5b63\uff0c\u6ca1\u6709\u6625\u82b1\u79cb\u6708\uff0c\u4e5f\u6ca1\u6709\u51ac\u5b63\u7684\u98ce\u96ea\uff0c\u4ece\u5bd2\u6f6e\u9891\u53d1\u7684\u5efa\u90ba\u62b5\u8fbe\u9177\u6691\u708e\u70ed\u7684\u72ee\u57ce\u65b0\u52a0\u5761\uff0c\u5f20\u606a\u591a\u5c11\u6709\u4e9b\u4e0d\u9002\u5e94\uff0c\u89c9\u5f97\u65b0\u52a0\u5761\u7684\u5929\u6c14\u6c89\u95f7\u3002\u8981\u4e0d\u662f\u5c0f\u59ae\u5b50\u5728\u65b0\u52a0\u5761\u8ba9\u4eba\u671f\u5f85\uff0c\u597d\u4e0d\u5bb9\u6613\u627e\u5230\u501f\u53e3\u5230\u65b0\u52a0\u5761\u6765\uff0c\u4ed6\u5bf9\u65b0\u52a0\u5761\u8fd9\u4e2a\u534e\u4eba\u56fd\u5bb6\u4e0e\u57ce\u5e02\uff0c\u5b9e\u8d28\u4e0a\u7684\u597d\u611f\u4e5f\u6709\u9650\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u662f\u534e\u4eba\u5728\u6d77\u5916\u6210\u7acb\u7684\u552f\u4e00\u653f\u6743\uff0c\u63d0\u8d77\u65b0\u52a0\u5761\u6765\uff0c\u56fd\u5185\u90fd\u4f1a\u6709\u7740\u8840\u7f18\u4e0a\u4eb2\u8fd1\u7684\u597d\u611f\uff0c\u518d\u52a0\u4e0a\u8fd1\u51e0\u5e74\u65b0\u52a0\u5761\u4e0e\u5185\u5730\u7684\u7ecf\u6d4e\u5408\u4f5c\u5bc6\u5207\uff0c\u8fd9\u79cd\u4eb2\u8fd1\u611f\u8d8a\u53d1\u7684\u660e\u663e\u3002</p><p>\u3000\u3000\u4eba\u5230\u65b0\u52a0\u5761\u4e4b\u540e\uff0c\u5c31\u4f1a\u89c9\u5f97\u5b9e\u9645\u7684\u60c5\u51b5\u4e0e\u6211\u4eec\u4e00\u53a2\u60c5\u613f\u6240\u8bbe\u60f3\u7684\u60c5\u51b5\u8fd8\u6709\u4e9b\u533a\u522b\u3002\u65b0\u52a0\u5761\u4eba\u53e3\u867d\u7136\u4ee5\u534e\u4eba\u4e3a\u4e3b\uff0c\u4f46\u662f\u5728\u653f\u6cbb\u3001\u6587\u5316\u4e0a\u6781\u529b\u6cdb\u897f\u5316\uff0c\u672c\u5e94\u8be5\u5728\u65b0\u52a0\u5761\u53d7\u5230\u91cd\u89c6\u7684\u534e\u8bed\u6559\u80b2\u5374\u5728\u9010\u5e74\u7684\u6ca1\u843d\u3002</p><p>\u3000\u3000\u9ec4\u660f\u65f6\u5206\uff0c\u65b0\u52a0\u5761\u7684\u5929\u7a7a\u4e0b\u7740\u5fae\u96e8\uff0c\u521a\u4e0b\u98de\u673a\u7684\u5f20\u606a\u627e\u4e86\u4e00\u5bb6\u4f4d\u4e8e\u5546\u4e1a\u533a\u7684\u9152\u5427\u5750\u4e0b\uff0c\u5728\u7b49\u5f85\u5b59\u9759\u6aac\u8d76\u6765\uff0c\u4ed6\u65e0\u804a\u7684\u62ff\u8d77\u7ecf\u7814\u4e2d\u5fc3\u5173\u7cfb\u6d77\u7c9f\u79d1\u6280\u7684\u62a5\u544a\u770b\u8d77\u6765\u3002</p><p>\u3000\u3000\u8ba4\u771f\u8ba1\u7b97\u4e0b\u6765\uff0c\u5b8f\u4fe1\u6295\u8d44\u4e0e\u7cbe\u5178\u5730\u4ea7\u5f80\u6d77\u7c9f\u79d1\u6280\u91cc\u5b9e\u9645\u6ce8\u5165\u7684\u8d44\u91d1\u5dee\u4e0d\u591a\u6709\u5341\u4e09\u56db\u4ebf\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u4ed6\u4eec\u7684\u8d26\u9762\u76c8\u5229\u76f8\u5f53\u9ad8\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u5e02\u503c\u90fd\u7a81\u7834\u516b\u5341\u4ebf\u4e86\u3002</p><p>\u3000\u3000\u5b8f\u4fe1\u6295\u8d44\u4e0e\u7cbe\u5178\u5730\u4ea7\u4e24\u6b21\u53c2\u4e0e\u5b9a\u5411\u589e\u53d1\uff0c\u5171\u589e\u6301\u6d77\u7c9f\u79d1\u6280\u7ea620\uff05\u7684\u80a1\u6743\uff0c\u8fd9\u90e8\u5206\u80a1\u7968\u5e02\u503c\u8d85\u8fc7\u5341\u516d\u4ebf\uff0c\u7ecf\u8fc7\u5341\u4e8c\u4e2a\u6708\u7684\u9501\u5b9a\u671f\u4e4b\u540e\uff0c\u8fd9\u90e8\u5206\u80a1\u7968\u53ef\u4ee5\u5411\u516c\u5f00\u5e02\u573a\u51cf\u6301\u5957\u73b0\u7684\u3002\u53e6\u5916\uff0c\u867d\u7136\u65e0\u6cd5\u4f30\u7b97\u89c4\u6a21\uff0c\u4f46\u662f\u53ef\u4ee5\u80af\u5b9a\u5b8f\u4fe1\u6295\u8d44\u52a8\u7528\u5927\u91cf\u7684\u573a\u5916\u8d44\u91d1\u64cd\u7eb5\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\uff0c\u6d77\u7c9f\u79d1\u6280\u80a1\u4ef7\u5728\u534a\u5e74\u591a\u65f6\u95f4\u91cc\u589e\u6da8\u8fd1\u4e8c\u5341\u500d\uff0c\u573a\u5916\u8d44\u91d1\u7684\u83b7\u5229\u4e5f\u662f\u76f8\u5f53\u60ca\u4eba\u7684\u3002</p><p>\u3000\u3000\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u5c31\u7b97\u4e0d\u8ba1\u7b97\u65e0\u6cd5\u6d41\u901a\u7684\u6cd5\u4eba\u80a1\u4e0a\u79ef\u7d2f\u7684\u76c8\u5229\uff0c\u5373\u4f7f\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u5728\u4e0b\u4e00\u523b\u8170\u65a9\uff0c\u5b8f\u4fe1\u4e0e\u7cbe\u5178\u5730\u4ea7\u524d\u671f\u79ef\u7d2f\u7684\u76c8\u5229\u4e5f\u4e0d\u4f1a\u90fd\u5410\u7a7a\u3002</p><p>\u3000\u3000\u5927\u6982\u5c31\u662f\u8fd9\u70b9\uff0c\u624d\u8ba9\u4ed6\u4eec\u66f4\u52a0\u7684\u6709\u6043\u65e0\u6050\u5427\uff1f</p><p>\u3000\u3000\u53ea\u8981\u6491\u5230\u4e94\u6708\u80fd\u591f\u516c\u5f00\u589e\u53d1\uff0c\u5c31\u80fd\u4ece\u6d77\u7c9f\u79d1\u6280\u8eab\u4e0a\u5957\u53d6\u4e03\u516b\u5341\u4ebf\u751a\u81f3\u4e0a\u767e\u4ebf\u7684\u8d44\u91d1\uff0c\u5982\u6b64\u5de8\u5927\u7684\u8bf1\u60d1\u5927\u6982\u80fd\u8ba9\u4e25\u6587\u4ecb\u4ed6\u4eec\u4e0b\u5927\u51b3\u5fc3\u72e0\u72e0\u7684\u8d4c\u4e00\u628a\u5427\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u62ff\u7740\u91d1\u5c5e\u5319\u6405\u52a8\u676f\u4e2d\u7684\u5496\u5561\uff0c\u9152\u5427\u91cc\u5f25\u6f2b\u7740\u4f73\u917f\u7684\u9187\u9999\u4e0e\u5496\u5561\u7684\u9999\u6c14\uff0c\u4ed6\u51fa\u6765\u65e9\u4e86\u4e9b\uff0c\u9759\u6aac\u8981\u4e0a\u5b8c\u8bfe\u624d\u80fd\u8d76\u8fc7\u6765\uff0c\u4ed6\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u671b\u7740\u7a97\u5916\u7684\u65b0\u666f\u5927\u9053\u4e0a\u901a\u884c\u7684\u8f66\u8f86\u5f88\u5c11\uff0c\u9ec4\u660f\u7684\u4e1d\u96e8\u6709\u4e9b\u6c89\u5bc2\u4e86\u3002</p><p>\u3000\u3000\u201c\u54ce\uff01\u201d\u542c\u5f97\u4e00\u58f0\u5a07\u67d4\u751c\u6da6\u7684\u547c\u5524\uff0c\u5f20\u606a\u8f6c\u8fc7\u5934\u6765\uff0c\u9759\u6aac\u5df2\u7ecf\u4f38\u8fc7\u624b\u5728\u4ed6\u7684\u773c\u775b\u524d\u62db\u6447\uff0c\u201c\u662f\u4e0d\u662f\u5fc3\u9b42\u90fd\u7ed9\u65b0\u52a0\u5761\u7684\u706b\u8fa3\u5973\u90ce\u52fe\u8fc7\u53bb\u4e86\uff1f\u6211\u53ef\u8981\u63d0\u9192\u4f60\u4e00\u58f0\uff0c\u65b0\u52a0\u5761\u6709\u516c\u5f00\u7ecf\u8425\u7684\u5993\u9662\uff0c\u4f46\u662f\u5f53\u8857\u8c03\u620f\u5987\u5973\u88ab\u902e\u4f4f\u7684\u8bdd\uff0c\u53ef\u4e0d\u7ba1\u4f60\u606a\u5c11\u5728\u5185\u5730\u6709\u591a\u5927\u5a01\u98ce\uff0c\u90fd\u94c1\u5b9a\u8981\u6328\u51e0\u97ad\u5b50\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u5fc5\u8981\u521a\u4e0b\u98de\u673a\u5c31\u7ed9\u6211\u4e0b\u9a6c\u5a01\u554a\uff0c\u6211\u6765\u65b0\u52a0\u5761\u5c31\u662f\u60f3\u8c03\u620f\u5987\u5973\uff0c\u4e5f\u53ea\u4f1a\u60f3\u7740\u8c03\u620f\u4f60\u554a\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u63a5\u8fc7\u9759\u6aac\u624b\u91cc\u7cbe\u81f4\u7684\u624b\u888b\uff0c\u8bf7\u5979\u5750\u4e0b\u6765\uff0c\u65b0\u52a0\u5761\u82db\u523b\u7684\u6cd5\u5236\u8ba9\u8fd9\u5ea7\u57ce\u5e02\u4e00\u5207\u90fd\u770b\u4e0a\u53bb\u4e95\u4e95\u6709\u6761\uff0c\u8d70\u5728\u65b0\u52a0\u5761\u7684\u8857\u5934\uff0c\u4e0d\u7528\u592a\u62c5\u5fc3\u6cbb\u5b89\u95ee\u9898\uff0c\u65b0\u52a0\u5761\u4eba\u4f3c\u4e4e\u90fd\u5df2\u7ecf\u4e60\u60ef\u4e86\u5faa\u89c4\u8e48\u77e9\uff0c\u4e5f\u8ba9\u8fd9\u5ea7\u57ce\u5e02\u663e\u5f97\u6709\u4e9b\u6c89\u95f7\uff0c\u6709\u4eba\u4f1a\u7279\u522b\u5e0c\u671b\u5c45\u4f4f\u5728\u65b0\u52a0\u5761\uff0c\u6709\u4eba\u5c31\u4f1a\u89c9\u5f97\u65b0\u52a0\u5761\u662f\u6587\u5316\u7684\u6c99\u6f20\uff0c\u6c89\u95f7\u800c\u7565\u663e\u67af\u71e5\u3002\u5b59\u9759\u6aac\u4ece\u5c0f\u5c31\u5728\u9999\u6e2f\u9002\u5e94\u4e86\u82f1\u5f0f\u6559\u80b2\uff0c\u751f\u6d3b\u5728\u65b0\u52a0\u5761\u4e0d\u4f1a\u6709\u4ec0\u4e48\u4e0d\u4e60\u60ef\uff0c\u5979\u5230\u65b0\u52a0\u5761\u6765\u5b66\u4e60\u7684\u662f\u97f3\u4e50\uff0c\u5bf9\u4ece\u5c0f\u4f9d\u604b\u5979\u59d0\u59d0\u5b59\u9759\u9999\u7684\u5979\u6765\u8bf4\uff0c\u4f3c\u4e4e\u4e5f\u89c9\u5f97\u5c06\u6765\u8be5\u4e3a\u4e16\u7eaa\u534e\u97f3\u505a\u4e9b\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u7a7f\u7740\u70df\u7070\u8272\u7684\u8d85\u957fT\u6064\u886b\uff0c\u5728\u5f2f\u8170\u8fdb\u5ea7\u4f4d\u65f6\uff0c\u624d\u9732\u51fa\u91cc\u9762\u7a7f\u7740\u7684\u77ed\u88e4\u7684\u4e00\u8fb9\uff0c\u957fT\u6064\u886b\u4e0b\u4e24\u6761\u7f8e\u817f\u6dcb\u6f13\u5c3d\u81f4\u7684\u5c55\u793a\u7740\uff0c\u867d\u7136\u906e\u4f4f\u5979\u7ea4\u8170\u3001\u5c0f\u81c0\u3001\u7fd8\u4e73\u7684\u66f2\u7ebf\uff0c\u4f46\u662f\u770b\u7740\u5ae9\u6ed1\u7ea4\u957f\u7684\u7f8e\u817f\u4e0e\u6e05\u7eaf\u7cbe\u81f4\u7684\u8138\u86cb\uff0c\u5c31\u80fd\u611f\u53d7\u5230\u9752\u6625\u5a07\u8273\u7684\u8bf1\u4eba\u6ecb\u5473\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4e0b\u8f66\u5230\u9152\u5427\u6765\uff0c\u5934\u53d1\u6dcb\u4e86\u4e9b\u96e8\uff0c\u8fd8\u6709\u51e0\u7f15\u6e7f\u53d1\u8d34\u5728\u5149\u6d01\u660e\u4eae\u7684\u989d\u5934\u4e0a\uff0c\u5979\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8d70\u8fdb\u5ea7\u4f4d\u91cc\uff0c\u6ca1\u6709\u6025\u7740\u5750\u4e0b\uff0c\u51dd\u7738\u770b\u7740\u5f20\u606a\u7684\u773c\u775b\uff0c\u95ee\u9053\uff1a\u201c\u5728\u5efa\u90ba\u6709\u6ca1\u6709\u5076\u5c14\u7684\u60f3\u8d77\u8fc7\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u4e0d\u7136\u6211\u4f55\u5fc5\u8f9b\u8f9b\u82e6\u82e6\u7684\u8d76\u8fd9\u8d9f\u6d51\u6c34\u554a\uff1f\u201d\u5f20\u606a\u770b\u7740\u5979\u50cf\u7cbe\u7075\u4e00\u6837\u7684\u773c\u775b\uff0c\u4e0d\u77e5\u9053\u662f\u8be5\u9677\u8fdb\u53bb\u8fd8\u662f\u8981\u6323\u624e\u51fa\u6765\uff0c\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u53eb\u4f8d\u8005\u62ff\u9152\u6c34\u5355\u4e0a\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u5750\u5728\u65b0\u52a0\u5761\u7684\u9152\u5427\u91cc\uff0c\u603b\u627e\u4e0d\u52301978\u7684\u90a3\u79cd\u5473\u9053\uff0c\u7ecf\u8425\u9152\u5427\uff0c\u4e5f\u771f\u662f\u8981\u770b\u4eba\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u7684\u8001\u677f\u53c8\u4e0d\u50cf\u4f60\u8ddf\u6211\u8fd9\u6837\u7684\u8d25\u5bb6\u5b50\uff0c\u4eba\u5bb6\u8981\u9760\u8fd9\u4e2a\u517b\u6d3b\u4e00\u5bb6\u51e0\u53e3\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u517b\u4e09\u4e94\u4e2a\u60c5\u4eba\u2014\u2014\u65b0\u52a0\u5761\u627e\u60c5\u4eba\u4e5f\u4e0d\u8fdd\u6cd5\u3002\u201d\u5b59\u9759\u6aac\u5ae3\u7136\u4e00\u7b11\uff0c\u5979\u4e5f\u77e5\u9053\u81ea\u5df1\u8d25\u5bb6\uff0c\u53c8\u72e1\u9ee0\u7684\u8ddf\u5f20\u606a\u6324\u4e86\u6324\u773c\u775b\uff0c\u8bf4\u9053\uff0c\u201c\u5343\u79a7\u591c\u7684\u70df\u82b1\u8868\u6f14\uff0c\u6211\u770b\u5230\u5f55\u50cf\u5e26\u4e86\uff0c\u771f\u662f\u9057\u61be\u6ca1\u6709\u56de\u5efa\u90ba\uff0c\u5f53\u65f6\u5fc3\u91cc\u5c31\u7ea0\u7ed3\u6765\u7740\uff0c\u6700\u7ec8\u8fd8\u662f\u7ed9\u6211\u5988\u558a\u56de\u9999\u6e2f\u4e86\uff0c\u53bb\u4e86\u5efa\u90ba\uff0c\u4e0d\u5355\u80fd\u770b\u70df\u82b1\u62ff\u73ab\u7470\uff0c\u8fd8\u80fd\u770b\u4f60\u5982\u4f55\u5de6\u53f3\u9022\u6e90\u5e94\u4ed8\u8bb8\u601d\u4e0e\u5510\u5a67\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54b3\uff0c\u54b3\uff0c\u201d\u5f20\u606a\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u6478\u4e86\u6478\u9f3b\u5934\uff0c\u6709\u90a3\u4e48\u4e00\u79d2\u7684\u5c34\u5c2c\uff0c\u5f88\u5feb\u5c31\u7ed9\u63a9\u9970\u8fc7\u53bb\u4e86\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u65e9\u77e5\u9053\u4e0d\u8ddf\u4f60\u8bc9\u82e6\u4e86\uff0c\u5230\u65b0\u52a0\u5761\u6765\u8fd8\u8981\u53d7\u4f60\u7684\u5632\u7b11\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b59\u9759\u6aac\u6b6a\u7740\u8111\u888b\u4fcf\u76ae\u7684\u76ef\u7740\u5f20\u606a\u7684\u773c\u775b\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u4e0d\u4fe1\u4ed6\u7684\u8138\u76ae\u4f1a\u8fd9\u4e48\u8584\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4e0d\u8981\u5728\u8fd9\u91cc\u67af\u5750\uff0c\u966a\u6211\u53bb\u901b\u8857\u597d\u4e0d\u597d\u3002\u4e00\u4e2a\u4eba\u5b64\u96f6\u96f6\u7684\u5728\u65b0\u52a0\u5761\uff0c\u96be\u5f97\u4f60\u8fc7\u6765\u4e00\u8d9f\uff0c\u603b\u8981\u88c5\u4f5c\u6709\u604b\u4eba\u7684\u6837\u5b50\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u88c5\u4f5c\u6709\u5417\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u554a\uff01\u201d\u5b59\u9759\u6aac\u55c5\u4e86\u55c5\u5934\uff0c\u9a84\u50b2\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u79bb\u5b59\u5c1a\u4e49\u7684\u98de\u673a\u62b5\u8fbe\u65b0\u52a0\u5761\u8fd8\u6709\u4e9b\u65f6\u95f4\uff0c\u8981\u7b49\u5b59\u5c1a\u4e49\u5230\u65b0\u52a0\u5761\u4e4b\u540e\u4e00\u8d77\u7528\u9910\uff0c\u603b\u6709\u627e\u5730\u65b9\u6253\u53d1\u65f6\u95f4\u624d\u884c\uff0c\u5f20\u606a\u5c06\u676f\u4e2d\u7684\u5496\u5561\u559d\u5c3d\uff0c\u4e0e\u5b59\u9759\u6aac\u4e24\u4eba\u5e76\u80a9\u8d70\u51fa\u9152\u5427\uff0c\u8d70\u5230\u70df\u96e8\u6726\u80e7\u7684\u5927\u9053\u4e0a\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5728\u65b0\u52a0\u5761\u5f00\u4e00\u8f86\u7ebf\u6761\u786c\u6717\u7684\u94f6\u8272\u5954\u9a70\uff0c\u5f00\u8f66\u5230\u9644\u8fd1\u7684\u5546\u4e1a\u8857\u3002\u5546\u4e1a\u8857\u4e0a\u65b9\u6709\u592a\u9633\u677f\u5236\u6210\u7684\u7a79\u9876\u906e\u4f4f\uff0c\u5916\u9762\u70df\u96e8\u6726\u80e7\u3001\u96e8\u52bf\u6e10\u5927\uff0c\u5546\u4e1a\u8857\u5185\u8863\u9999\u9b13\u5f71\uff0c\u4eba\u4eec\u901b\u8857\u7684\u5174\u81f4\u4e1d\u6beb\u6ca1\u6709\u53d7\u96e8\u5929\u7684\u5e72\u6270\u3002</p><p>\u3000\u3000\u5085\u4fca\u4e0e\u968f\u884c\u7684\u5de5\u4f5c\u4eba\u5458\u627e\u4e86\u4e00\u5bb6\u4e34\u8857\u7684\u5496\u5561\u5e97\u5750\u4e0b\uff0c\u5f20\u606a\u966a\u540c\u5b59\u9759\u6aac\u5728\u4e00\u5bb6\u5bb6\u7cbe\u81f4\u3001\u6807\u683c\u6602\u8d35\u7684\u7cbe\u54c1\u5e97\u91cc\u7a7f\u68ad\uff0c\u4ece\u7b2c\u4e8c\u5e97\u8d70\u51fa\u6765\uff0c\u5b59\u9759\u6aac\u633d\u7740\u4ed6\u7684\u80f3\u818a\uff0c\u7d27\u7d27\u7684\u4f9d\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u4ed6\u80fd\u611f\u89c9\u5230\u5979\u7684\u5fc3\u8df3\u58f0\u4ee5\u53ca\u9694\u7740\u8584\u8584\u8863\u886b\u900f\u8fc7\u6765\u6e29\u70ed\u7684\u4f53\u6e29\u4e0e\u6de1\u6de1\u7684\u5c11\u5973\u4f53\u9999\uff0c\u77ac\u95f4\u53c8\u6062\u590d\u5230\u5728\u5efa\u90ba\u65f6\u4e24\u4eba\u76f8\u5904\u7684\u6e29\u99a8\u4e0e\u4eb2\u5bc6\u3002</p><p>\u3000\u3000\u8857\u5934\u6709\u827a\u4eba\u652f\u8d77\u7535\u5b50\u7434\u5728\u90a3\u91cc\u5f39\u5531\uff0c\u56f4\u7740\u4e00\u7fa4\u4eba\uff0c\u5b59\u9759\u6aac\u7ad9\u5728\u524d\u9762\u4e9b\uff0c\u5934\u5fae\u4ef0\u7740\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\u8ddf\u6211\u59d0\u53d7\u4e0d\u4e86\u5bb6\u91cc\u7684\u6c14\uff0c\u8fd8\u60f3\u7740\u9003\u51fa\u6765\u5230\u8857\u5934\u5356\u5531\uff0c\u53ea\u662f\u521a\u8d70\u51fa\u5bb6\u95e8\u6ca1\u6709\u591a\u4e45\uff0c\u5c31\u8ba9\u6211\u7238\u7ed9\u902e\u4e86\u56de\u53bb\u2026\u2026\u201d\u8bf4\u8bdd\u65f6\uff0c\u534a\u4e2a\u8eab\u5b50\u5c31\u4f9d\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u5f20\u606a\u8f7b\u6402\u7740\u5979\u7684\u7ec6\u8170\uff0c\u7ad9\u5728\u90a3\u91cc\u542c\u7740\u8857\u5934\u827a\u4eba\u5728\u90a3\u91cc\u4e00\u66f2\u66f2\u7684\u5531\u7740\u60c5\u6b4c\u3002</p><p>\u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u5c31\u5c06\u5165\u591c\uff0c\u5b59\u9759\u6aac\u542c\u7740\u624b\u888b\u91cc\u7684\u624b\u673a\u5728\u54cd\uff0c\u638f\u51fa\u6765\u770b\u4e86\u770b\uff0c\u7591\u60d1\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u65f6\u5019\uff0c\u6211\u7238\u4ed6\u4eba\u4e0d\u5e94\u8be5\u5728\u98de\u673a\u4e0a\u5417\uff0c\u600e\u4e48\u4f1a\u7ed9\u6211\u6253\u7535\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u97f3\u4e50\u58f0\u5608\u6742\uff0c\u5f20\u606a\u4e5f\u542c\u4e0d\u6e05\u5b59\u5c1a\u4e49\u5728\u7535\u8bdd\u91cc\u8bf4\u4ec0\u4e48\uff0c\u542c\u7740\u5b59\u9759\u6aac\u7684\u7b54\u8bdd\uff0c\u4f3c\u4e4e\u9999\u6e2f\u56fd\u9645\u673a\u573a\u53d1\u751f\u4e00\u4e9b\u5c0f\u610f\u5916\uff0c\u6709\u4e2a\u7537\u5b50\u88ab\u5973\u53cb\u7529\u4e86\uff0c\u5728\u5973\u53cb\u79bb\u5f00\u9999\u6e2f\u4e4b\u9645\u51b2\u8fdb\u8dd1\u9053\u8981\u6c42\u5973\u53cb\u518d\u7ed9\u4ed6\u4e00\u6b21\u673a\u4f1a\u2026\u2026\u4e5f\u4e0d\u77e5\u9053\u7537\u5b50\u4e3a\u4f55\u80fd\u7ed5\u8fc7\u91cd\u91cd\u5b89\u4fdd\u51b2\u8fdb\u8dd1\u9053\uff0c\u6574\u4e2a\u822a\u7ad9\u697c\u56e0\u4e3a\u8fd9\u6b21\u610f\u5916\u6682\u65f6\u7ed9\u5c01\u95ed\u8d77\u6765\uff0c\u4e9a\u6d32\u822a\u73ed\u5168\u7ebf\u5ef6\u8bef\u3002</p><p>\u3000\u3000\u201c\u6211\u7238\u4ed6\u4eec\u7ed9\u56f0\u5728\u9999\u6e2f\u673a\u573a\u4e86\uff0c\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u79bb\u5f00\u9999\u6e2f\uff0c\u6211\u7238\u8981\u6211\u6765\u62db\u5f85\u4f60\uff0c\u600e\u4e48\u8bf4\uff1f\u201d\u5b59\u9759\u6aac\u6b6a\u7740\u8111\u888b\uff0c\u773c\u775b\u4eae\u6676\u6676\u7684\u770b\u7740\u5f20\u606a\uff0c\u201c\u662f\u627e\u4e00\u5bb6\u8d85\u8c6a\u534e\u7684\u9910\u5385\uff0c\u8fd8\u662f\u968f\u4fbf\u4e70\u4e9b\u5564\u9152\uff1f\u6211\u4f4f\u7684\u5730\u65b9\u8fd8\u5b58\u7740\u4e9b\u901f\u51b0\u6c34\u997a\uff0c\u5e94\u8be5\u591f\u4e24\u4e2a\u4eba\u5403\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u521a\u8bfb\u521d\u4e2d\u65f6\uff0c\u6211\u5988\u6bcf\u4e2a\u661f\u671f\u53ea\u7ed9\u6211\u4e00\u5757\u94b1\u96f6\u82b1\u94b1\uff0c\u6211\u8981\u6512\u4e09\u5143\u624d\u80fd\u5403\u7897\u6c34\u997a\uff0c\u6709\u65f6\u5019\u5b9e\u5728\u5634\u998b\uff0c\u8fd8\u4f1a\u5fcd\u4e0d\u4f4f\u53bb\u9a97\u5510\u5a67\u7684\u96f6\u82b1\u94b1\u3002\u90a3\u65f6\u5019\u6211\u5c31\u8bb8\u4e0b\u4eba\u751f\u5b8f\u613f\uff0c\u4ee5\u540e\u8981\u662f\u80fd\u6bcf\u987f\u90fd\u5403\u4e0a\u9c9c\u8089\u997a\u5b50\u5c31\u4e0d\u6789\u6b64\u751f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u77e5\u9053\u8d2b\u5634\uff01\u201d\u5b59\u9759\u6aac\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u542c\u5f20\u606a\u515c\u91cc\u7684\u624b\u673a\u4e5f\u54cd\u4e86\u8d77\u6765\uff0c\u77e5\u9053\u662f\u5979\u7238\u7238\u7684\u7535\u8bdd\u6253\u4e86\u8fc7\u6765\uff0c\u8ba9\u4ed6\u5148\u63a5\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u968f\u5373\u63a5\u5230\u5b59\u5c1a\u4e49\u4ece\u9999\u6e2f\u6253\u6765\u7684\u7535\u8bdd\uff0c\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5c31\u5c06\u7535\u8bdd\u6302\u6389\uff0c\u53c8\u7535\u8bdd\u901a\u77e5\u5085\u4fca\u4e0e\u5de5\u4f5c\u4eba\u5458\u5148\u56de\u9152\u5e97\u7528\u9910\uff0c\u8fd8\u4e0d\u6653\u5f97\u9999\u6e2f\u7684\u822a\u7ad9\u697c\u8981\u5c01\u95ed\u51e0\u4e2a\u5c0f\u65f6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f00\u8f66\u56de\u5230\u5b59\u9759\u6aac\u5728\u65b0\u52a0\u5761\u4f4f\u7684\u516c\u5bd3\u697c\uff0c\u79bb\u5f20\u606a\u4eca\u665a\u8981\u4e0b\u69bb\u7684\u9152\u5e97\u5f88\u8fd1\uff0c\u5c31\u5728\u516c\u5bd3\u697c\u4e0b\u4e70\u4e86\u5f88\u591a\u5564\u9152\u4e0e\u4e00\u4e9b\u719f\u98df\u4e0a\u4e86\u697c\uff0c\u5728\u7535\u68af\u91cc\uff0c\u5b59\u9759\u6aac\u5174\u594b\u4e0d\u5df2\uff0c\u4f3c\u4e4e\u6ca1\u6709\u5f15\u72fc\u5165\u5ba4\u7684\u62c5\u5fe7\uff0c\u767d\u7699\u800c\u7cbe\u81f4\u7684\u8138\u86cb\u900f\u7740\u4e9b\u5fae\u7684\u7eef\u7ea2\uff0c\u5f20\u606a\u7684\u80f3\u818a\u7ed9\u5979\u633d\u5728\u6000\u91cc\uff0c\u80fd\u611f\u89c9\u5230\u5979\u547c\u5438\u7684\u8d77\u4f0f\uff0c\u6bd4\u8f83\u534a\u5e74\u524d\uff0c\u80f8\u812f\u4f3c\u4e4e\u53c8\u53d1\u80b2\u4e86\u4e9b\u3002</p><p>\u3000\u3000\u201c\u6253\u7b97\u5c06\u4e00\u95f4\u5367\u5ba4\u79df\u7ed9\u522b\u4eba\uff0c\u627e\u4e2a\u5ba4\u53cb\uff0c\u6211\u59d0\u5230\u65b0\u52a0\u5761\u6765\u4f4f\u8fc7\u4e00\u6bb5\u65f6\u95f4\uff0c\u5c06\u4e00\u5806\u4e1c\u897f\u90fd\u4e22\u5728\u8fd9\u91cc\uff0c\u4e5f\u5c31\u4f5c\u7f62\u4e86\u2026\u2026\u201d\u5b59\u9759\u6aac\u6253\u5f00\u623f\u95e8\u8ba9\u5f20\u606a\u53c2\u89c2\u5979\u7684\u516c\u5bd3\u3002</p><p>\u3000\u3000\u867d\u7136\u516c\u5bd3\u697c\u4f4d\u4e8e\u65b0\u52a0\u5761\u7684\u7e41\u534e\u5730\u6bb5\uff0c\u516c\u5bd3\u697c\u7684\u7ba1\u7406\u770b\u4e0a\u53bb\u4e5f\u76f8\u5f53\u7684\u9ad8\u7ea7\uff0c\u53ea\u662f\u5b59\u9759\u6aac\u4f4f\u7684\u516c\u5bd3\u7b97\u4e0d\u4e0a\u5962\u534e\uff0c\u6bd4\u8f83\u8d77\u5b59\u5bb6\u7684\u8c6a\u5bcc\uff0c\u4e24\u95f4\u5367\u5ba4\u3001\u4e00\u95f4\u4e66\u623f\uff0c\u53ea\u80fd\u8bf4\u592a\u7b80\u6734\u4e86\uff0c\u5145\u6ee1\u7740\u5973\u6027\u7684\u6c14\u606f\uff0c\u5c4b\u91cc\u6ca1\u6709\u6253\u7a7a\u8c03\uff0c\u663e\u5f97\u6709\u4e9b\u95f7\u70ed\u3002</p><p>\u3000\u3000\u5f20\u606a\u63a2\u5934\u770b\u8fc7\u53bb\uff0c\u5ba2\u5385\u7684\u9633\u53f0\u4e0a\u667e\u6652\u7740\u5185\u8863\u88e4\u4e0e\u4e73\u7f69\u2026\u2026</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6253\u5f00\u51b0\u7bb1\u5c06\u901f\u51b0\u6c34\u997a\u62ff\u51fa\u6765\uff0c\u9012\u7ed9\u5f20\u606a\uff0c\u95ee\u9053\uff1a\u201c\u6211\u53bb\u6d17\u4e2a\u6fa1\uff0c\u4f60\u6765\u716e\u6c34\u997a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5f80\u9505\u5b50\u91cc\u52a0\u4e86\u6c34\u7b49\u70e7\u5f00\uff0c\u62ff\u4e86\u4e00\u7f50\u5564\u9152\u5750\u5230\u5ba2\u5385\u7684\u6c99\u53d1\u4e0a\u7ffb\u770b\u77ee\u51e0\u4e0a\u7684\u4e66\u672c\uff0c\u90fd\u662f\u4e00\u4e9b\u4e50\u7406\u3001\u821e\u8e48\u65b9\u9762\u7684\u5185\u5bb9\uff0c\u4ed6\u5c06\u4e00\u7f50\u5564\u9152\u559d\u5b8c\uff0c\u6c34\u997a\u4e5f\u716e\u597d\u7528\u76d8\u5b50\u88c5\u597d\u7aef\u4e86\u51fa\u6765\uff0c\u5b59\u9759\u6aac\u4e5f\u6d17\u597d\u6fa1\u7a7f\u7740\u539f\u6765\u7684\u8863\u670d\u51fa\u6765\uff0cT\u6064\u886b\u7684\u4e0b\u6446\u8fd8\u6709\u4e9b\u5496\u5561\u6e0d\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u4e0d\u6362\u4e00\u8eab\u8863\u670d\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u665a\u4e0a\u4e00\u76f4\u90fd\u5728\u5916\u9762\u901b\u8857\uff0c\u54ea\u6709\u673a\u4f1a\u6362\u8863\u670d\u554a\uff1f\u201d\u5b59\u9759\u6aac\u5658\u5634\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u62ff\u8d77\u4e00\u7f50\u5564\u9152\u6253\u5f00\u704c\u4e86\u4e00\u5927\u53e3\uff0c\u624d\u7a0d\u89e3\u53e3\u6e34\uff0c\u201c\u597d\u70ed\u554a\uff0c\u6211\u90fd\u5feb\u997f\u574f\u4e86\u3002\u201d\u6328\u7740\u5f20\u606a\u5750\u5728\u5730\u677f\u4e0a\uff0c\u62ff\u8d77\u7b77\u5b50\u5939\u8d77\u76d8\u5b50\u91cc\u7684\u997a\u6c34\u5c31\u8981\u5f80\u5634\u91cc\u585e\uff0c\u78b0\u4e86\u4e00\u4e0b\u5634\u5507\uff0c\u53c8\u55f7\u55f7\u76f4\u53eb\u70eb\uff0c\u8fde\u5fd9\u5439\u6c14\u6563\u70ed\u6025\u4e0d\u53ef\u8010\u7684\u5c06\u4e00\u53ea\u997a\u5b50\u5403\u4e0b\u53bb\u4f3c\u4e4e\u53ea\u6709\u8fd9\u6837\u624d\u80fd\u8ba9\u809a\u5b50\u91cc\u7684\u997f\u866b\u4e0d\u518d\u6323\u624e\u3002</p><p>\u3000\u3000\u9759\u6aac\u5728\u5bb6\u91cc\u5c31\u6ca1\u6709\u518d\u7a7f\u77ed\u88e4\uff0c\u5979\u5750\u4e0b\u6765\uff0cT\u6064\u886b\u4e00\u89d2\u5377\u8d77\u6765\uff0c\u96ea\u5ae9\u7684\u5927\u817f\u8fb9\u7f18\u9732\u7c89\u7eff\u8272\u7684\u5185\u88e4\u4e00\u89d2\uff0cT\u6064\u886b\u91cc\u4e5f\u6ca1\u6709\u6234\u4e73\u7f69\uff0c\u5979\u6328\u7740\u5f20\u606a\u7684\u5c0f\u817f\u9760\u6c99\u53d1\u5750\u5728\u5730\u677f\u4e0a\uff0c\u5f20\u606a\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u4ece\u5fae\u8361\u5f00\u7684\u9886\u53e3\u80fd\u591f\u77a5\u89c1\u767d\u5ae9\u633a\u7acb\u7684\u4e73\u5cf0\u3002</p><p>\u3000\u3000\u5b8c\u5168\u662f\u9752\u5e74\u516c\u5bd3\u91cc\u4e24\u4eba\u72ec\u5904\u65f6\u6beb\u65e0\u62d8\u675f\u7684\u7a7f\u7740\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u65b0\u52a0\u5761\u4e5f\u8fd9\u4e48\u7a7f\u5417\uff1f\u201d\u5f20\u606a\u4ece\u9759\u6aac\u7684\u80f8\u90e8\u79fb\u5f00\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u54ea\u80fd\u5728\u4f60\u8fd9\u4e2a\u8272\u72fc\u9762\u524d\u7a7f\u90a3\u4e48\u66b4\u9732\uff1f\u201d\u9759\u6aac\u53c8\u559d\u4e86\u4e00\u5927\u53e3\u5564\u9152\uff0c\u8138\u988a\u5fae\u7ea2\uff0c\u5c06\u6613\u62c9\u7f50\u653e\u4e0b\uff0c\u8dd1\u8fc7\u53bb\u5c06\u9633\u53f0\u4e0a\u7684\u7a97\u5e18\u62c9\u8d77\u6765\uff0c\u7ad9\u5728\u9633\u53f0\u8f6c\u8fc7\u8eab\u95ee\u9053\uff1a\u201c\u60f3\u77e5\u9053\u6211\u5728\u65b0\u52a0\u5761\u4f4f\u5904\u600e\u4e48\u7a7f\u8863\u670d\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6452\u606f\u9759\u6c14\u7684\u76ef\u7740\u5979\u5996\u5a9a\u7684\u7738\u5b50\uff0c\u5728\u706f\u5149\u4e0b\uff0c\u5979\u7565\u6709\u4e9b\u6f6e\u7ea2\u7684\u8138\u86cb\u7cbe\u81f4\u800c\u59a9\u5a9a\uff0c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u603b\u4e0d\u4f1a\u4e00\u4ef6\u8863\u670d\u90fd\u4e0d\u7a7f\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u624d\u7f8e\u5f97\u4f60\uff0c\u201d\u5b59\u9759\u6aac\u8d70\u8fc7\u6765\uff0c\u6293\u4f4f\u5f20\u606a\u7684\u53cc\u624b\u8ba9\u4ed6\u7684\u53cc\u624b\u522b\u5230\u8eab\u540e\uff0c\u8bf4\u9053\uff0c\u201c\u7ed9\u4f60\u770b\u53ef\u4ee5\uff0c\u4e0d\u8fc7\u4f60\u8981\u7b54\u5e94\u6211\u4e24\u4ef6\u4e8b\uff1a\u53ea\u8bb8\u770b\uff0c\u4e0d\u8bb8\u78b0\uff1b\u7b49\u79bb\u5f00\u8fd9\u95f4\u516c\u5bd3\u5c31\u8981\u628a\u8fd9\u4ef6\u4e8b\u7ed9\u5fd8\u6389\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u4f60\u63d0\u4ec0\u4e48\u8981\u6c42\uff0c\u6211\u90fd\u7b54\u5e94\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u597d\u2026\u2026\u201d\u5b59\u9759\u7ad9\u76f4\u8eab\u4f53\uff0c\u53cc\u81c2\u4ea4\u53c9\u5c06T\u6064\u4ece\u5934\u8131\u4e0b\u6765\uff0c\u8038\u7fd8\u3001\u6d51\u5706\u7684\u96ea\u5ae9\u767d\u4e73\u7ed9T\u6064\u886b\u4e0b\u6446\u515c\u4f4f\u53c8\u4e00\u4e0b\u5b50\u5f39\u8131\u51fa\u6765\uff0c\u5f39\u8361\u4e0d\u4f11\u7684\u96ea\u767d\u5ae9\u4e73\u5c16\u4e0a\u5ae3\u7ea2\uff0c\u5c0f\u8179\u575a\u5b9e\u5e73\u5766\uff0c\u7f8e\u817f\u4fee\u957f\uff0c\u4e09\u89d2\u533a\u57df\u7ed9\u7c89\u7eff\u8272\u5c0f\u5185\u88e4\u906e\u7740\uff0c\u7a0d\u6709\u4e9b\u900f\u660e\uff0c\u5728\u706f\u5149\u4e0b\uff0c\u900f\u7740\u9ed1\u832c\u832c\u7684\u4e00\u7247\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u76f4\u89c9\u5fc3\u810f\u7ed9\u9759\u6aac\u4e00\u53ea\u65e0\u5f62\u7684\u5c0f\u624b\u6525\u7d27\u7740\uff0c\u60f3\u8bf4\u4e00\u4e24\u53e5\u8bdd\u7f13\u89e3\u4e00\u4e0b\u5fc3\u5934\u7684\u538b\u529b\uff0c\u53ea\u662f\u5589\u5499\u773c\u5e72\u6da9\u5f97\u5f88\u3002</p><p>\u3000\u3000\u201c\u6ca1\u6709\u60f3\u5230\u4eca\u5929\u8981\u628a\u4f60\u9886\u56de\u6765\uff0c\u4eca\u5929\u5c31\u8fd9\u6837\u597d\u4e0d\u597d\uff1f\u201d\u5b59\u9759\u6aac\u4fef\u8eab\u8d34\u8fc7\u6765\uff0c\u6367\u7740\u5f20\u606a\u7684\u8138\u988a\uff0c\u770b\u7740\u4ed6\u7684\u773c\u775b\u8ba4\u771f\u7684\u8bf4\uff0c\u201c\u4f60\u77e5\u9053\uff0c\u6211\u73b0\u5728\u8fd8\u4e0d\u80fd\u505a\u4f60\u7684\u60c5\u4eba\uff0c\u6211\u8ddf\u6211\u59d0\u4e4b\u95f4\uff0c\u603b\u8981\u6709\u4e00\u4e2a\u4eba\u7ed9\u6211\u7238\u5988\u4e00\u4e9b\u5e0c\u671b\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u662f\u4e0d\u662f\u8ba9\u6211\u5148\u53bb\u51b2\u4e00\u4e0b\u51c9\u6c34\u6fa1\u518d\u51fa\u6765\u559d\u5564\u9152\u5403\u6c34\u997a\uff01\u201d\u5f20\u606a\u5fc3\u60f3\u81ea\u5df1\u5b9a\u6027\u518d\u5f3a\uff0c\u4e5f\u53d7\u4e0d\u4e86\u8fd9\u6837\u7684\u6311\u9017\u554a\uff0c\u8fd8\u662f\u5148\u53bb\u51b2\u4e00\u4e0b\u51c9\u6c34\u6fa1\u53bb\u4e00\u53bb\u706b\u4fdd\u9669\u4e9b\uff0c\u8fd9\u65f6\u5019\u5df2\u7ecf\u5fcd\u4e0d\u4f4f\u60f3\u5c06\u8fd9\u5177\u7f8e\u5999\u52a8\u4eba\u7684\u5a07\u8eaf\u6402\u5728\u6000\u91cc\u597d\u597d\u7684\u8e42\u8e8f\u4e00\u756a\u4e86\u3002</p><p>\u3000\u3000\u201c\u6ca1\u5173\u7cfb\uff0c\u53ea\u8981\u4e0d\u505a\u90a3\u4e2a\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d\u5b59\u9759\u6aac\u7ea2\u5507\u8f7b\u8f7b\u7684\u5728\u5f20\u606a\u5507\u4e0a\u5370\u4e86\u4e00\u4e0b\uff0c\u770b\u7740\u5f20\u606a\u88e4\u88c6\u91cc\u7684\u9686\u8d77\uff0c\u8f7b\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u597d\u4e11\uff0c\u5343\u4e07\u4e0d\u8981\u9732\u51fa\u6765\u89c1\u4eba\u3002\u201d</p><p>\u3000\u3000\u542c\u5230\u5b59\u9759\u6aac\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u6402\u8fc7\u5979\u7684\u8170\uff0c\u611f\u89e6\u7740\u5979\u8170\u8089\u7684\u7ea4\u5ae9\u3001\u7ec6\u817b\u4e0e\u60ca\u4eba\u7684\u5f39\u8f6f\uff0c\u4ec5\u8fd9\u4e00\u89e6\u5c31\u8ba9\u4eba\u60c5\u4e0d\u81ea\u7981\u7684\u8840\u6d41\u52a0\u901f\u3001\u5fc3\u8df3\u52a0\u5feb\uff0c\u771f\u6000\u7591\u80fd\u63a7\u5236\u4f4f\u81ea\u5df1\uff0c\u6284\u7740\u5979\u575a\u5b9e\u5f39\u8f6f\u7684\u81c0\uff0c\u8ba9\u5979\u5750\u5230\u81ea\u5df1\u7684\u5927\u817f\u4e0a\u6765\uff0c\u6258\u4f4f\u5979\u7684\u540e\u8111\u52fa\uff0c\u5634\u5411\u5979\u8bf1\u4eba\u7684\u7ea2\u5507\u543b\u8fc7\u53bb\uff0c\u77ac\u95f4\uff0c\u6e7f\u6da6\u3001\u4e30\u6ee1\u3001\u67d4\u8f6f\u7684\u89e6\u89c9\u4ee5\u53ca\u5c11\u5973\u6d74\u540e\u82ac\u82b3\u7684\u6c14\u606f\u3001\u706b\u70ed\u53d1\u8361\u7684\u808c\u80a4\u51e0\u4e4e\u8ba9\u4ed6\u7684\u611f\u89c9\u795e\u7ecf\u9ebb\u75f9\u6389\u3002</p><p>\u3000\u3000\u867d\u8bf4\u8fd9\u67d4\u8f6f\u4e30\u5ae9\u7684\u6a31\u5507\u4e0d\u662f\u7b2c\u4e00\u6b21\u54c1\u5c1d\uff0c\u53ea\u662f\u518d\u6b21\u89e6\u78b0\u65f6\uff0c\u603b\u6709\u79cd\u8ba9\u4eba\u96be\u4ee5\u81ea\u6291\u7684\u75af\u72c2\u8bf1\u60d1\uff0c\u5438\u7740\u4e01\u9999\u4f3c\u7684\u820c\u5c16\uff0c\u7ef5\u8f6f\u9999\u6ed1\u4f3c\u4e4e\u662f\u8fd9\u4e16\u95f4\u6781\u81f4\u9c9c\u5ae9\u7684\u8089\u4f53\uff0c\u820c\u5c16\u5206\u6ccc\u7684\u6d25\u6db2\u4e5f\u6709\u7740\u96be\u8a00\u7684\u82ac\u82b3\uff0c\u5f20\u606a\u4e00\u624b\u629a\u5f04\u7740\u9759\u6aac\u7684\u79c0\u53d1\uff0c\u4e00\u624b\u629a\u5f04\u5979\u80f8\u524d\u7684\u90a3\u5bf9\u5c0f\u767d\u5154\uff0c\u8fd8\u60f3\u518d\u751f\u51fa\u4e00\u53ea\u624b\u6765\u53bb\u629a\u5f04\u5979\u5149\u6ed1\u96ea\u5ae9\u7684\u80cc\u808c\u3001\u518d\u751f\u51fa\u4e00\u53ea\u624b\u6765\u53bb\u629a\u5f04\u5979\u6d51\u5706\u4e30\u8174\u7684\u5c0f\u81c0\u3001\u518d\u751f\u51fa\u4e00\u53ea\u624b\u6765\u53bb\u629a\u5f04\u5979\u4fee\u957f\u7ed3\u5b9e\u7684\u5927\u817f\u2026\u2026</p><p>\u3000\u3000\u5b59\u9759\u6aac\u51e0\u4e4e\u762b\u8f6f\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u6025\u5267\u7684\u5598\u7740\u70ed\u6c14\uff0c\u4eab\u53d7\u7740\u604b\u4eba\u5e26\u6765\u7684\u610f\u4e71\u60c5\u8ff7\uff0c\u53ea\u6709\u5f20\u606a\u7684\u624b\u5411\u5979\u7684\u4e24\u817f\u4e4b\u95f4\u6478\u53bb\u65f6\u624d\u4f38\u624b\u6321\u4e00\u6321\u3002</p><p>\u3000\u3000\u684c\u4e0a\u7684\u624b\u673a\u54cd\u8d77\u6765\uff0c\u4e24\u4eba\u7ed9\u60c5\u6b32\u5305\u88f9\u7740\u7684\u7537\u5973\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u5b59\u9759\u6aac\u4ece\u5730\u677f\u4e0a\u5750\u8d77\u6765\uff0c\u5c06\u624b\u673a\u62ff\u8fc7\u6765\uff0c\u539f\u6765\u662f\u9999\u6e2f\u90a3\u8fb9\u7684\u5c0f\u610f\u5916\u5df2\u7ecf\u89e3\u51b3\u597d\uff0c\u4e9a\u6d32\u822a\u7ebf\u6062\u590d\uff0c\u4ed6\u7238\u5df2\u7ecf\u767b\u4e0a\u98de\u673a\u6b63\u51c6\u5907\u8d77\u98de\u3002</p><p>\u3000\u3000\u5fc3\u865a\u7684\u5728\u7535\u8bdd\u91cc\u80e1\u626f\u4e86\u51e0\u53e5\uff0c\u5b59\u9759\u6aac\u53c8\u8dea\u7740\u722c\u8fc7\u6765\u5c06\u624b\u673a\u4e22\u5728\u684c\u4e0a\uff0c\u5979\u81c0\u90e8\u5c0f\u5de7\u7ed3\u5b9e\uff0c\u8dea\u5728\u90a3\u91cc\u653e\u624b\u673a\u65f6\uff0c\u6d51\u5706\u800c\u4f18\u7f8e\uff0c\u7c89\u7eff\u8272\u7a84\u5c0f\u7684\u5185\u88e4\u6839\u672c\u5c31\u5305\u4e0d\u4e0b\u6574\u4e2a\u81c0\u90e8\uff0c\u5927\u7247\u96ea\u767d\u7684\u81c0\u74e3\u9732\u5728\u5916\u9762\uff0c\u88c6\u90e8\u90fd\u7ed9\u6e17\u51fa\u6765\u7684\u6d25\u6db2\u6d78\u900f\uff0c\u5448\u534a\u900f\u660e\u72b6\uff0c\u6e7f\u6f09\u6f09\u7684\u9732\u51fa\u79c1\u5904\u5b8c\u7f8e\u7684\u5f62\u72b6\u6765\u2026\u2026</p><p>\u3000\u3000\u770b\u7740\u5b59\u9759\u6aac\u4f38\u624b\u906e\u5728\u81c0\u540e\uff0c\u6361\u8d77\u5730\u677f\u4e0a\u7684T\u6064\u886b\u8981\u7a7f\uff0c\u5f20\u606a\u5fcd\u4e0d\u4f4f\u8981\u5e72\u568e\u4e00\u58f0\u6765\u53d1\u6cc4\u5fc3\u4e2d\u7684\u90c1\u82e6\uff0c\u8fd9\u59ae\u5b50\u8eab\u4f53\u7684\u7279\u8d28\u8ddf\u5176\u4ed6\u4eba\u6709\u4e9b\u4e0d\u540c\uff0c\u5979\u4f3c\u4e4e\u53ea\u8981\u629a\u6478\u80f8\u90e8\u5c31\u80fd\u4eab\u53d7\u5230\u6781\u81f4\u7684\u5feb\u611f\uff0c\u5bf9\u4e8e\u771f\u6b63\u7684\u7537\u5973\u6027\u4e8b\u5c31\u4e0d\u662f\u5f88\u5411\u5f80\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6ca1\u6709\u5c06T\u6064\u7a7f\u4e0a\uff0c\u57ab\u5230\u5f20\u606a\u8eab\u8fb9\uff0c\u4fcf\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u7528\u624b\u5e2e\u4f60\u597d\u4e0d\u597d\uff1f\u201d\u6321\u7740\u4e0d\u8ba9\u5f20\u606a\u5750\u8d77\u6765\uff0c\u4f38\u624b\u8981\u53bb\u89e3\u5f20\u606a\u8170\u95f4\u7684\u76ae\u5e26\uff0c\u53ea\u662f\u76ae\u5e26\u6263\u592a\u590d\u6742\uff0c\u89e3\u4e86\u534a\u5929\u5374\u6ca1\u6709\u89e3\u5f00\u6765\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9999\u6e2f\u98de\u5f80\u65b0\u52a0\u5761\u9700\u8981\u4e09\u4e2a\u591a\u5c0f\u65f6\uff0c\u5f20\u606a\u5728\u5b59\u9759\u6aac\u4f4f\u6240\u6d17\u8fc7\u6fa1\uff0c\u4e00\u8d77\u5c06\u4e00\u5927\u76d8\u997a\u5b50\u4e0e\u5341\u51e0\u542c\u5564\u9152\u559d\u5b8c\uff0c\u624d\u79bb\u5f00\u516c\u5bd3\u697c\u3002\u65b0\u52a0\u5761\u5bf9\u9152\u540e\u9a7e\u8f66\u7684\u5904\u7f5a\u5f88\u91cd\uff0c\u897f\u5386\u65b0\u5e74\u524d\u540e\uff0c\u65b0\u52a0\u5761\u8b66\u65b9\u5bf9\u9152\u540e\u9a7e\u8f66\u7684\u67e5\u5904\u4e5f\u5f88\u4e25\u683c\uff0c\u5b59\u9759\u6aac\u4e5f\u4e0d\u6562\u5728\u559d\u4e86\u56db\u4e94\u542c\u5564\u9152\u4e4b\u540e\u518d\u5f00\u8f66\uff0c\u8981\u662f\u7ed9\u6293\u4f4f\uff0c\u4ee5\u540e\u90fd\u4e0d\u7528\u60f3\u5728\u65b0\u52a0\u5761\u5f00\u8f66\u4e86\u3002</p><p>\u3000\u3000\u5750\u7684\u58eb\u5230\u5f20\u606a\u4e0b\u69bb\u7684\u9152\u5e97\uff0c\u4e0e\u5085\u4fca\u4ed6\u4eec\u6c47\u5408\u4e4b\u540e\uff0c\u518d\u53bb\u673a\u573a\u8fce\u63a5\u5b59\u5c1a\u4e49\u7b49\u4eba\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u6536\u8d2d\u7279\u8bb8\u534a\u5bfc\u4f53\u65d7\u4e0b0.25\u5fae\u7c73\u6676\u5706\u5de5\u5382\u3001\u7279\u8bb8\u534a\u5bfc\u4f53\u5bf9\u4e2d\u6676\u5fae\u82af\u7247\u8fdb\u884c\u6301\u80a1\u4e00\u4e8b\u5728\u591a\u65b9\u7ecf\u8fc7\u8fd9\u6bb5\u65f6\u95f4\u7684\u79d8\u5bc6\u8c08\u5224\uff0c\u591a\u65b9\u610f\u89c1\u57fa\u672c\u8fbe\u6210\u4e00\u81f4\uff0c\u6bd5\u7adf\u8fd9\u4e48\u5927\u7684\u4e00\u5757\uff0c\u9526\u6e56\u4e5f\u6ca1\u6709\u60f3\u7740\u8981\u4e00\u53e3\u541e\u4e0b\u53bb\uff0c\u5728\u5f53\u524d\u7684\u7ecf\u6d4e\u683c\u5c40\u4e2d\uff0c\u591a\u8d62\u7684\u5c40\u9762\u5e76\u4e0d\u662f\u591a\u4e48\u96be\u4ee5\u60f3\u8c61\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u5230\u65b0\u52a0\u5761\u6765\uff0c\u4e3b\u8981\u662f\u6765\u53c2\u52a0\u6700\u540e\u9636\u6bb5\u7684\u8c08\u5224\uff0c\u8981\u6ca1\u6709\u65b0\u751f\u51fa\u6765\u7684\u95ee\u9898\uff0c\u8fd9\u6b21\u5c31\u80fd\u7b7e\u7f72\u6b63\u5f0f\u7684\u534f\u8bae\u5e76\u516c\u5e03\u4e8e\u4f17\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u7684\u4ee3\u8868\u3001\u5fb7\u4eea\u7684\u4ee3\u8868\u4ee5\u53ca\u4e91\u6e90\u96c6\u56e2\u7684\u4ee3\u8868\u660e\u5929\u624d\u4f1a\u5230\u65b0\u52a0\u5761\u6765\uff0c\u5f20\u606a\u501f\u53e3\u660e\u5929\u4e0a\u5348\u8981\u53c2\u89c2\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\uff0c\u63d0\u524d\u4e00\u5929\u5230\u4e86\u65b0\u52a0\u5761\uff0c\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u662f\u4e1c\u5c71\u6e2f\u5efa\u8bbe\u7684\u91cd\u8981\u529b\u91cf\uff0c\u4e00\u76f4\u4ee5\u6765\u90fd\u662f\u5b59\u5c1a\u4e49\u8054\u7edc\u7684\uff0c\u5b59\u5c1a\u4e49\u81ea\u7136\u4e5f\u8981\u63d0\u524d\u4e00\u591c\u8d76\u5230\u65b0\u52a0\u5761\u6765\u53c2\u52a0\u660e\u5929\u4e0a\u5348\u7684\u6d3b\u52a8\u3002</p><p>\u3000\u3000\u4ece\u673a\u573a\u5c06\u5b59\u5c1a\u4e49\u4e00\u884c\u63a5\u56de\u5230\u9152\u5e97\uff0c\u5feb\u5230\u5b50\u591c\u4e86\uff0c\u4e00\u8d77\u5403\u8fc7\u591c\u5bb5\uff0c\u5b59\u9759\u6aac\u81ea\u7136\u4e5f\u7559\u5bbf\u5728\u9152\u5e97\u91cc\u3002</p></article>"}}}]);
