(window.webpackJsonp=window.webpackJsonp||[]).push([[111],{133:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e09\u8282 \u7565\u6709\u6240\u609f",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e09\u8282 \u7565\u6709\u6240\u609f</h2><p>\u8fd9\u600e\u4e48\u641e\u7684\uff1f\u8001\u5bb6\u4f19\u660e\u660e\u8bf4\u8fd9\u91c7\u7389\u8bc0\u662f\u7528\u6765\u5438\u5185\u529b\u7684\uff0c\u4f46\u90a3\u4e2a\u6740\u624b\u96be\u4e0d\u6210\u4e5f\u7ec3\u8fc7\u5185\u529f\uff1f\u518d\u8bf4\u8fd9\u9634\u5bd2\u51b0\u51b7\u7684\u611f\u89c9\uff0c\u53c8\u54ea\u91cc\u50cf\u4ec0\u4e48\u5185\u529f\u4e86\uff0c\u50cf\u4e2d\u6bd2\u4e86\u8fd8\u5dee\u4e0d\u591a\u2026\u2026</p><p>\u867d\u7136\u4e0d\u660e\u767d\u5176\u4e2d\u7f18\u7531\uff0c\u4f46\u77e5\u9053\u6765\u5386\u7f18\u7531\uff0c\u603b\u7b97\u662f\u5fc3\u4e2d\u7a0d\u7a0d\u5b89\u5b9a\u4e0b\u6765\u3002\u65e2\u7136\u662f\u91c7\u7389\u8bc0\u5438\u6765\u7684\uff0c\u4e0d\u7ba1\u662f\u4e0d\u662f\u5185\u529b\uff0c\u53cd\u6b63\u6b7b\u9a6c\u5f53\u6d3b\u9a6c\u533b\uff0c\u8bd5\u8bd5\u4fbf\u662f\u3002</p><p>\u5f53\u65f6\u7530\u4f2f\u5149\u4f20\u6388\u91c7\u7389\u8bc0\uff0c\u6700\u6839\u672c\u7684\u76ee\u7684\u5c31\u662f\u8981\u5438\u7eb3\u5973\u6027\u9ad8\u624b\u7684\u5185\u529b\u2014\u2014\u524d\u63d0\u662f\u5979\u6709\u5185\u529b\u3002\u6240\u4ee5\u5982\u4f55\u201c\u5438\u8fc7\u6765\u201d\uff0c\u8fd9\u53ea\u662f\u524d\u534a\u90e8\u5206\u7684\u5185\u5bb9\uff1b\u540e\u9762\u8fd8\u6709\u4e00\u534a\uff0c\u8bb2\u7684\u5c31\u662f\u5982\u4f55\u5316\u89e3\u5185\u529b\uff0c\u4e3a\u6211\u6240\u7528\uff0c\u5426\u5219\u5438\u6765\u505a\u4ec0\u4e48\uff0c\u96be\u9053\u5b58\u5728\u8eab\u4f53\u91cc\u50a8\u5b58\u7740\uff0c\u5f53\u5b9a\u65f6\u70b8\u5f39\u4e48\u3002</p><p>\u6b64\u65f6\u4ed6\u9ed8\u9ed8\u56de\u5fc6\u90a3\u540e\u534a\u90e8\u5206\u7684\u53e3\u8bc0\uff0c\u6697\u81ea\u8fd0\u8f6c\u5185\u606f\uff0c\u4eff\u4f5b\u4e00\u6247\u7f13\u7f13\u79fb\u8f6c\u7684\u65e0\u5f62\u78e8\u76d8\uff0c\u4e00\u70b9\u70b9\u5730\u5c06\u90a3\u80a1\u9690\u9690\u6f5c\u4f0f\u7684\u9634\u5bd2\u611f\u78e8\u53bb\u3002\u91c7\u7389\u8bc0\u7684\u57fa\u672c\u601d\u8def\uff0c\u7c97\u7565\u5f62\u5bb9\u7684\u8bdd\uff0c\u5c31\u662f\u5148\u5c06\u5bf9\u65b9\u7684\u5185\u529b\u5438\u6765\uff0c\u5b58\u5728\u4f53\u5185\uff0c\u56e0\u4e3a\u548c\u81ea\u8eab\u5185\u529b\u5b9a\u7136\u4e0d\u5408\uff0c\u6240\u4ee5\u65e0\u6cd5\u76f4\u63a5\u5bb9\u7eb3\uff0c\u4eff\u4f5b\u5c31\u662f\u4e00\u5927\u56e2\u5f02\u8d28\u3002\u7136\u540e\u7528\u81ea\u8eab\u7684\u5185\u529b\u505a\u57fa\u7840\uff0c\u4eff\u4f5b\u78e8\u76d8\u6216\u8005\u9509\u5200\u4e00\u822c\uff0c\u4e00\u70b9\u70b9\u5c06\u5438\u6765\u7684\u5185\u529b\u6d88\u89e3\u3001\u878d\u5316\uff0c\u9010\u6e10\u541e\u566c\u58ee\u5927\u3002\u8fd9\u4e2a\u8fc7\u7a0b\u7684\u957f\u77ed\uff0c\u4e3b\u8981\u53d6\u51b3\u4e8e\u81ea\u8eab\u5185\u529b\u7684\u5f3a\u5f31\u3001\u6839\u57fa\u7684\u624e\u5b9e\u7a0b\u5ea6\u548c\u5438\u6765\u5185\u529b\u7684\u591a\u5c11\u3002\u743c\u6069\u4e5f\u7b97\u4fee\u70bc\u5185\u529f\u591a\u5e74\uff0c\u867d\u7136\u63d0\u5347\u4e0d\u4e0a\u53bb\uff0c\u57fa\u7840\u603b\u8fd8\u7b97\u6253\u5f97\u7262\u9760\uff0c\u4f55\u51b5\u8fd9\u6b21\u53ef\u4ee5\u7b97\u662f\u201c\u6d45\u5c1d\u8f84\u6b62\u201d\uff0c\u5176\u5b9e\u4e5f\u53ea\u5438\u4e86\u4e00\u70b9\u70b9\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u603b\u7b97\u5c06\u80f8\u53e3\u90a3\u80a1\u5bd2\u6c14\u6d88\u53bb\uff0c\u6682\u65f6\u4eff\u4f5b\u662f\u65e0\u788d\u4e86\u3002</p><p>\u4e0d\u8fc7\u4ed6\u5fc3\u91cc\u4e5f\u5f88\u6e05\u695a\uff0c\u53ea\u662f\u6682\u65f6\u65e0\u788d\u7f62\u4e86\u3002\u81ea\u5df1\u7684\u5185\u529b\u592a\u5dee\uff0c\u6240\u4ee5\u6d88\u89e3\u3001\u878d\u5316\u7684\u901f\u5ea6\u4e5f\u5c31\u6162\u5f97\u51fa\u5947\u3002\u867d\u7136\u5982\u6b64\uff0c\u743c\u6069\u5fc3\u4e2d\u8fd8\u662f\u4e07\u5206\u9ad8\u5174\uff0c\u56e0\u4e3a\u8fd9\u8bf4\u660e\u4e00\u76f4\u88ab\u89c6\u4e3a\u662f\u5783\u573e\u6280\u80fd\u7684\u91c7\u7389\u8bc0\uff0c\u539f\u6765\u5728\u8fd9\u4e2a\u4e16\u754c\u4e5f\u8fd8\u662f\u6709\u7528\u5904\u7684\u3002</p><p>\u4ed6\u540e\u6094\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u65e9\u70b9\u8bd5\u7528\uff0c\u4e5f\u4e0d\u7528\u7b49\u5230\u4eca\u5929\u624d\u53d1\u89c9\u3002\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u8fd9\u4e5f\u662f\u6ca1\u529e\u6cd5\uff0c\u9634\u9b42\u57ce\u90a3\u79cd\u9b3c\u5730\u65b9\uff0c\u519b\u4e8b\u5316\u7ba1\u7406\uff0c\u4ed6\u4e00\u4e2a\u5e73\u6c11\u5b50\u5f1f\uff0c\u54ea\u91cc\u6562\u53bb\u52fe\u642d\u5973\u4eba\uff0c\u5728\u5deb\u5e08\u5b66\u6821\u91cc\u4e5f\u53ea\u6709\u4e00\u4e2a\u8299\u857e\u72c4\uff0c\u53c8\u592a\u8fc7\u5a07\u5f31\uff0c\u743c\u6069\u6015\u4f24\u4e86\u5979\uff0c\u4e5f\u4e0d\u6562\u5728\u5979\u8eab\u4e0a\u8bd5\u7528\u8fd9\u79cd\u9738\u9053\u7684\u91c7\u8865\u529f\u592b\u3002\u597d\u4e0d\u5bb9\u6613\u6bd5\u4e1a\u4e86\uff0c\u773c\u770b\u81ea\u7531\u4e86\uff0c\u7ed3\u679c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u4e8c\u8bdd\u4e0d\u8bf4\u628a\u4ed6\u6253\u53d1\u51fa\u6765\u2026\u2026\u53ea\u80fd\u8bf4\uff0c\u8fd0\u6c14\u5b9e\u5728\u662f\u4e0d\u4f73\u3002</p><p>\u7b97\u4e86\uff0c\u53cd\u6b63\u73b0\u5728\u53d1\u89c9\u4e5f\u4e0d\u8fdf\u3002</p><p>\u4f46\u6709\u4e00\u4e2a\u5173\u952e\u95ee\u9898\u8fd8\u6ca1\u6709\u5f97\u5230\u89e3\u51b3\uff0c\u5c31\u662f\u8fd9\u91c7\u7389\u8bc0\u4e3a\u4ec0\u4e48\u80fd\u8d77\u4f5c\u7528\u3002</p><p>\u6309\u7167\u7530\u4f2f\u5149\u7684\u8bf4\u6cd5\uff0c\u8fd9\u662f\u4e13\u95e8\u7528\u6765\u5bf9\u4ed8\u5730\u7403\u4e0a\u90a3\u4e9b\u8eab\u5177\u5185\u529f\u7684\u5973\u4fa0\u7684\u2014\u2014\u96be\u9053\u8bf4\u8fd9\u4e2a\u5973\u6740\u624b\u4e5f\u7ec3\u6709\u5185\u529f\uff1f\u8fd9\u79cd\u9634\u5bd2\u611f\u89c9\uff0c\u5176\u5b9e\u5c31\u662f\u67d0\u79cd\u90aa\u95e8\u5185\u529f\uff0c\u6bd4\u5982\u4ec0\u4e48\u7384\u51a5\u771f\u6c14\u4e4b\u7c7b\u7684\u4e1c\u897f\uff1f</p><p>\u7530\u4f2f\u5149\u8fd8\u66fe\u7ecf\u731c\u6d4b\u8bf4\u8fd9\u91c7\u7389\u8bc0\u4f1a\u4e0d\u4f1a\u5bf9\u5deb\u5e08\u6709\u7528\uff0c\u80fd\u50cf\u5438\u5185\u529b\u4e00\u6837\u5438\u9b54\u6cd5\u2014\u2014\u8fd9\u79cd\u8bf4\u6cd5\u9996\u5148\u4ece\u9b54\u6cd5\u539f\u7406\u4e0a\u5c31\u4e0d\u592a\u8bf4\u5f97\u901a\uff1b\u5176\u6b21\u5c31\u7b97\u662f\u771f\uff0c\u96be\u9053\u90a3\u4e2a\u5973\u6740\u624b\u4e5f\u4f1a\u9b54\u6cd5\uff0c\u540c\u65f6\u4e5f\u662f\u4e2a\u5deb\u5e08\uff1f\u8fd9\u53ef\u80fd\u6027\u592a\u4f4e\u4e86\uff0c\u5979\u7a7f\u7740\u5982\u6b64\u66b4\u9732\uff0c\u52a8\u4e0d\u52a8\u8fd8\u5168\u88f8\u51fa\u573a\uff0c\u5982\u679c\u662f\u5deb\u5e08\uff0c\u90a3\u5979\u7684\u65bd\u6cd5\u6750\u6599\u90fd\u653e\u5728\u54ea\u91cc\uff1f\u743c\u6069\u77e5\u9053\uff0c\u9020\u8be3\u9ad8\u6df1\u7684\u5deb\u5e08\uff0c\u786e\u5b9e\u662f\u53ef\u4ee5\u5728\u65bd\u6cd5\u65f6\u7701\u7565\u4e00\u4e9b\u6cd5\u672f\u6750\u6599\u7684\uff0c\u4f46\u4e5f\u53ea\u80fd\u7701\u7565\u5f88\u5c11\u7684\u4e00\u90e8\u5206\u7f62\u4e86\u3002</p><p>\u8fd9\u4e2a\u6839\u672c\u539f\u7406\u7684\u95ee\u9898\u641e\u4e0d\u6e05\u695a\uff0c\u91c7\u7389\u8bc0\u5c31\u8fd8\u4e0d\u80fd\u7b97\u662f\u771f\u6b63\u6709\u4ef7\u503c\u3002\u5426\u5219\u5230\u5904\u4e71\u5438\uff0c\u51fa\u4ec0\u4e48\u5c94\u5b50\u5c31\u9ebb\u70e6\u4e86\u3002\u6700\u597d\u7684\u65b9\u6cd5\uff0c\u81ea\u7136\u662f\u628a\u90a3\u4e2a\u5973\u6740\u624b\u6293\u8fc7\u6765\u597d\u597d\u7814\u7a76\uff0c\u5982\u679c\u5979\u771f\u7684\u7ec3\u6709\u5185\u529f\uff0c\u90a3\u8fd9\u4e00\u5207\u90fd\u597d\u89e3\u91ca\uff0c\u53ea\u4e0d\u8fc7\u662f\u91c7\u7389\u8bc0\u5728\u6b63\u5e38\u53d1\u6325\u4f5c\u7528\uff08\u81f3\u4e8e\u5979\u4e3a\u4ec0\u4e48\u4f1a\u5185\u529f\uff0c\u90a3\u662f\u53e6\u5916\u4e00\u4e2a\u95ee\u9898\u4e86\uff09\uff1b\u4f46\u5982\u679c\u5979\u5176\u5b9e\u6ca1\u6709\u5185\u529f\uff0c\u90a3\u8fd9\u4e2d\u95f4\u95ee\u9898\u5c31\u5927\u4e86\uff0c\u9700\u8981\u597d\u597d\u5206\u6790\u7422\u78e8\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u53d1\u73b0\u4ec0\u4e48\u5927\u540d\u5802\u3002</p><p>\u53ef\u60dc\u5982\u4eca\u5973\u6740\u624b\u838e\u73de\u514b\u5df2\u7ecf\u88ab\u5c01\u5728\u5730\u4e0b\uff0c\u548c\u4e00\u53ea\u574f\u813e\u6c14\u7684\u94f6\u9f99\u5e7d\u7075\u4f5c\u4f34\u4e86\u3002</p><p>\u7b97\u4e86\u5427\uff0c\u743c\u6069\u601d\u5fd6\u7740\uff0c\u53cd\u6b63\u4ee5\u540e\u8fd8\u6709\u673a\u4f1a\uff0c\u627e\u51e0\u4e2a\u5973\u4eba\u6765\u8bd5\u9a8c\u8bd5\u9a8c\uff0c\u672a\u5fc5\u4e0d\u80fd\u5206\u6790\u51fa\u70b9\u4e1c\u897f\u6765\u3002\u70db\u5821\u8fd9\u91cc\u81ea\u7136\u662f\u6ca1\u6709\u4e86\uff0c\u535a\u5f97\u4e4b\u95e8\u65e2\u7136\u662f\u5546\u8d38\u5927\u57ce\u5e02\uff0c\u5993\u9662\u6216\u8005\u7c7b\u4f3c\u7684\u98ce\u6708\u573a\u6240\u603b\u8be5\u662f\u6709\u7684\uff0c\u53ea\u662f\u521a\u906d\u761f\u75ab\uff0c\u53ea\u6015\u4e5f\u65e9\u5173\u95e8\u6b47\u4e1a\u4e86\u3002\u53ef\u60dc\u4e86\uff0c\u8eab\u8fb9\u5c31\u6709\u4e00\u4e2a\u4e00\u6d41\u7684\u7f8e\u4eba\uff0c\u5374\u662f\u53ea\u80fd\u4eab\u53d7\u5c0f\u5634\uff0c\u5b9e\u5728\u6d6a\u8d39\u2026\u2026</p><p>\u60f3\u5230\u6885\u83f2\u65af\uff0c\u4e0d\u7531\u5f97\u773c\u89d2\u4f59\u5149\u77a5\u53bb\uff0c\u89c1\u5c11\u5973\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u53f3\u624b\u66f2\u8098\u6491\u5728\u6276\u624b\u4e0a\uff0c\u624b\u638c\u6258\u7740\u816e\uff0c\u6b63\u6709\u4e9b\u660f\u660f\u6b32\u7761\u7684\u6837\u5b50\uff0c\u8138\u8272\u4f3c\u4e4e\u4e0d\u592a\u597d\u3002\u5979\u5728\u5730\u4e0b\u9f99\u7a9f\u91cc\uff0c\u88ab\u94f6\u9f99\u5e7d\u7075\u643a\u5e26\u7684\u8d1f\u80fd\u91cf\u4f24\u5bb3\uff0c\u540e\u6765\u53c8\u906d\u9047\u5973\u6740\u624b\u5077\u88ad\uff0c\u672c\u5c31\u5df2\u7ecf\u7cbe\u75b2\u529b\u7aed\uff0c\u90a3\u53ea\u53eb\u745e\u6069\u65af\u5766\u7684\u8679\u5f69\u9f99\u867d\u7136\u4e0d\u77e5\u7528\u4e86\u4ec0\u4e48\u6cd5\u672f\u5e2e\u5979\u6cbb\u7597\u4e86\u4e00\u4e0b\uff0c\u4f46\u663e\u7136\u4e5f\u6ca1\u6709\u5b8c\u5168\u6062\u590d\u8fc7\u6765\u3002\u5426\u5219\u6362\u4e86\u5e73\u65f6\uff0c\u5979\u662f\u4e0d\u53ef\u80fd\u5927\u767d\u5929\u6253\u778c\u7761\u7684\uff0c\u53ea\u80fd\u8bf4\u786e\u5b9e\u662f\u7d2f\u4e86\u3002</p><p>\u743c\u6069\u89c1\u5979\u8138\u8272\u4e0d\u4f73\uff0c\u5fc3\u5934\u9690\u9690\u8d77\u4e86\u4e9b\u601c\u60dc\u4e4b\u610f\uff0c\u8d77\u8eab\u4e0b\u5e8a\uff0c\u5c06\u5979\u4ece\u6905\u5b50\u4e0a\u62b1\u4e86\u8d77\u6765\u3002\u6885\u83f2\u65af\u867d\u7136\u8eab\u6750\u9ad8\u6311\uff0c\u4f46\u9aa8\u8089\u5300\u79f0\uff0c\u5176\u5b9e\u5012\u5e76\u4e0d\u5f88\u91cd\uff0c\u743c\u6069\u529b\u6c14\u4e0d\u7b97\u5927\uff0c\u4e5f\u80fd\u8f7b\u677e\u62b1\u8d77\u3002\u5c11\u5973\u4f3c\u4e4e\u786e\u5b9e\u56f0\u5026\u5f97\u5389\u5bb3\u4e86\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u5514\u4e86\u4e00\u58f0\uff0c\u5374\u6ca1\u6709\u52a8\u5f39\uff0c\u4efb\u7531\u743c\u6069\u628a\u5979\u62b1\u4e0a\u5e8a\uff0c\u8f7b\u8f7b\u76d6\u4e0a\u88ab\u5b50\u3002</p><p>\u201c\u5982\u679c\u628a\u5185\u529f\u6559\u7ed9\u5979\u600e\u4e48\u6837\uff1f\u201d\u4e00\u4e2a\u5ff5\u5934\u7a81\u7136\u4ece\u8111\u4e2d\u5192\u51fa\u6765\u3002</p><p>\u5176\u5b9e\u8fd9\u4e2a\u60f3\u6cd5\u743c\u6069\u5f88\u4e45\u4ee5\u524d\u5c31\u6709\u3002\u5f53\u4ed6\u786e\u8ba4\u81ea\u5df1\u662f\u4e2a\u6b66\u5b66\u5e9f\u67f4\u4e4b\u540e\uff0c\u4ed6\u5c31\u8003\u8651\u8fc7\u8981\u4e0d\u8981\u628a\u90a3\u4e9b\u5185\u529f\u5fc3\u6cd5\u6697\u4e2d\u6559\u7ed9\u73ca\u5609\uff0c\u8ba9\u59d0\u59d0\u7ec3\u6765\u8bd5\u8bd5\uff0c\u8bf4\u4e0d\u5b9a\u6bd4\u81ea\u5df1\u6210\u6548\u66f4\u4f73\uff1b\u540e\u6765\u5b66\u4f1a\u4e86\u91c7\u7389\u8bc0\uff0c\u66f4\u662f\u5347\u8d77\u4e00\u4e2a\u60f3\u6cd5\uff1a\u201c\u6211\u81ea\u5df1\u7ec3\u5185\u529f\uff0c\u662f\u57fa\u672c\u6ca1\u5e0c\u671b\u80fd\u7ec3\u6210\u9ad8\u624b\u4e86\u3002\u91c7\u7389\u8bc0\u867d\u7136\u88ab\u8001\u5bb6\u4f19\u5439\u5f97\u795e\u4e4e\u5176\u795e\uff0c\u4f46\u5728\u8fd9\u4e2a\u4e16\u754c\u91cc\u662f\u538b\u6839\u6ca1\u7528\u2014\u2014\u90a3\u80fd\u4e0d\u80fd\u627e\u4e00\u4e9b\u8d44\u8d28\u597d\u7684\u5973\u4eba\uff0c\u628a\u5185\u529f\u5fc3\u6cd5\u6559\u7ed9\u5979\u4eec\uff0c\u7136\u540e\u6211\u518d\u6765\u5438\uff1f\u201d</p><p>\u8fd9\u4e3b\u610f\u9887\u4e3a\u6deb\u90aa\u800c\u4e14\u6076\u6bd2\uff0c\u4f46\u542c\u8d77\u6765\u5012\u4e5f\u4f3c\u4e4e\u53ef\u884c\u3002\u4f46\u743c\u6069\u72b9\u8c6b\u518d\u4e09\uff0c\u8fd8\u662f\u6682\u65f6\u653e\u5f03\u4e86\u8fd9\u79cd\u4e0d\u5207\u5b9e\u9645\u7684\u5ff5\u5934\u3002\u9996\u5148\u662f\u56e0\u4e3a\u6b64\u524d\u4ed6\u4e00\u76f4\u5446\u5728\u9634\u9b42\u57ce\u91cc\uff0c\u8fd9\u79cd\u519b\u4e8b\u5316\u4e25\u683c\u7ba1\u7406\u7684\u5730\u65b9\uff0c\u641e\u5c0f\u52a8\u4f5c\u662f\u7eaf\u5c5e\u627e\u6b7b\u3002\u5176\u6b21\uff0c\u7530\u4f2f\u5149\u548c\u81ea\u5df1\u4e00\u6837\u662f\u4e2a\u7a7f\u8d8a\u8005\uff0c\u4ed6\u4f20\u6388\u7684\u90a3\u4e9b\u5185\u529f\u5fc3\u6cd5\uff0c\u5168\u90fd\u662f\u8fd9\u4e2a\u4e16\u754c\u6240\u6ca1\u6709\u7684\u4e1c\u897f\uff0c\u4e00\u65e6\u6d41\u4f20\u900f\u9732\u51fa\u53bb\uff0c\u5f88\u5bb9\u6613\u5c31\u4f1a\u6cc4\u9732\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u62db\u6765\u9ebb\u70e6\u3002\u66f4\u4f55\u51b5\uff0c\u743c\u6069\u81ea\u5df1\u76ee\u524d\u4e5f\u5c31\u8fd9\u70b9\u672c\u4e8b\uff0c\u5c31\u7b97\u771f\u80fd\u627e\u5230\u8d44\u8d28\u51fa\u8272\u7684\u5973\u6027\uff0c\u4f20\u6388\u5185\u529f\uff0c\u800c\u4e14\u771f\u7684\u7ec3\u6210\u4e86\u2014\u2014\u5bf9\u65b9\u96be\u9053\u5c31\u4f1a\u4e56\u4e56\u5730\u4efb\u7531\u81ea\u5df1\u5438\u5185\u529f\uff1f\u7109\u77e5\u5979\u4e0d\u4f1a\u53cd\u8fc7\u6765\u4e00\u638c\u628a\u743c\u6069\u62cd\u6b7b\u3002</p><p>\u5c31\u7b97\u662f\u59d0\u59d0\u73ca\u5609\uff0c\u81f3\u4eb2\u81f3\u8fd1\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u7684\u79d8\u5bc6\u3002</p><p>\u4f46\u5982\u4eca\u5df2\u7ecf\u51fa\u4e86\u9634\u9b42\u57ce\uff0c\u5929\u5730\u5e7f\u9614\uff0c\u5c11\u4e86\u7ea6\u675f\uff0c\u53c8\u8d76\u4e0a\u8fd9\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u4e8b\u60c5\u63d0\u9192\uff0c\u5012\u662f\u8fd9\u4e2a\u5f88\u4e45\u4ee5\u524d\u7684\u5ff5\u5934\u6084\u7136\u6d6e\u73b0\u51fa\u6765\u3002</p><p>\u5982\u4eca\u6885\u83f2\u65af\u5df2\u7ecf\u7b97\u662f\u4ed6\u7684\u4fdd\u9556\uff0c\u800c\u4e14\u6309\u7167\u534f\u8bae\uff0c\u662f\u4f1a\u4e00\u76f4\u8ddf\u968f\u4ed6\u7684\u3002\u90a3\u4e48\uff0c\u662f\u4e0d\u662f\u8981\u6559\u5979\u5185\u529f\uff0c\u8bd5\u8bd5\u6548\u679c\u5462\uff0c\u63d0\u5347\u5979\u7684\u5b9e\u529b\uff0c\u4e5f\u7b97\u662f\u589e\u52a0\u81ea\u5df1\u7684\u5b89\u5168\u7cfb\u6570\u3002\u867d\u7136\u6ca1\u53bb\u6ce8\u610f\u8fc7\u6885\u83f2\u65af\u7684\u540e\u8111\u6795\u9aa8\u591f\u4e0d\u591f\u9ad8\uff0c\u4f46\u731c\u6d4b\u8d77\u6765\uff0c\u5979\u5e94\u8be5\u6bd4\u743c\u6069\u66f4\u50cf\u4e2a\u6b66\u5b66\u5929\u624d\u5427\u3002\u5c31\u7b97\u662f\u4e5f\u7ec3\u4e0d\u6210\uff0c\u81f3\u5c11\u6253\u70b9\u5185\u529f\u57fa\u7840\uff0c\u589e\u5f3a\u6062\u590d\u80fd\u529b\u4e5f\u597d\u3002</p><p>\u4f46\u771f\u8981\u8fd9\u4e48\u505a\u4e48\uff1f\u6885\u83f2\u65af\u5df2\u7ecf\u5f88\u5f3a\u608d\u4e86\uff0c\u65e0\u8bba\u6218\u529b\u3001\u5934\u8111\u8fd8\u662f\u7ecf\u9a8c\uff0c\u90fd\u662f\u4e0a\u7b49\u4e4b\u9009\uff0c\u8981\u4e0d\u662f\u4e8b\u5148\u6709\u534f\u8bae\uff0c\u5979\u53c8\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u90a3\u5341\u4e2a\u743c\u6069\u4e5f\u88ab\u5979\u780d\u4e86\u3002\u5979\u7684\u5b9e\u529b\u518d\u63d0\u5347\uff0c\u56fa\u7136\u662f\u80fd\u66f4\u597d\u5730\u4fdd\u62a4\u743c\u6069\uff0c\u4f46\u4e07\u4e00\u5979\u54ea\u4e00\u5929\u7ffb\u8138\uff0c\u90a3\u743c\u6069\u4e5f\u53ea\u4f1a\u6b7b\u5f97\u66f4\u5feb\u5427\u3002</p><p>\u867d\u7136\u8fd9\u6bb5\u65f6\u95f4\u6765\u7684\u63a5\u89e6\uff0c\u743c\u6069\u89c9\u5f97\u53ef\u4ee5\u4fe1\u4efb\u8fd9\u4e2a\u5c11\u5973\u2014\u2014\u4f46\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u5f53\u65f6\u5728\u9634\u9b42\u57ce\uff0c\u4ed6\u53c8\u4f55\u5c1d\u4e0d\u4fe1\u4efb\u8299\u857e\u72c4\uff0c\u7ed3\u679c\u5374\u5982\u4f55\uff1f</p><p>\u5728\u81ea\u5df1\u7684\u529b\u91cf\u4e0d\u80fd\u5b8c\u5168\u538b\u5236\u3001\u638c\u63e1\u5bf9\u65b9\u4e4b\u524d\uff0c\u5962\u8c08\u4ec0\u4e48\u4fe1\u4efb\u2026\u2026\u8fd9\u662f\u5728\u5f00\u73a9\u7b11\u5427\u3002</p><p>\u7b97\u4e86\u3002</p><p>\u6452\u5f03\u8fd9\u4e2a\u65e0\u804a\u7684\u5ff5\u5934\uff0c\u4ed6\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u770b\u7740\u6c89\u7761\u4e2d\u7684\u6885\u83f2\u65af\u3002\u623f\u95f4\u91cc\u5f88\u5b89\u9759\uff0c\u9690\u7ea6\u8fd8\u80fd\u542c\u89c1\u5c11\u5973\u5747\u5300\u7684\u547c\u5438\u58f0\uff0c\u770b\u7740\u5979\u719f\u7761\u4e2d\u5b89\u8be6\u7684\u8138\u5e9e\uff0c\u743c\u6069\u4e0d\u7531\u5f97\u53c8\u60f3\u8d77\u81ea\u5df1\u7b2c\u4e00\u6b21\u89c1\u5230\u5979\u7684\u60c5\u5f62\u3002\u90a3\u65f6\u5019\uff0c\u5979\u6b63\u4e00\u5251\u51fb\u6740\u4e86\u5de8\u5927\u7684\u9aa8\u866b\uff0c\u4ece\u78a7\u7eff\u706b\u6d77\u4e2d\u4ece\u5bb9\u6b65\u51fa\uff0c\u53d6\u4e0b\u5934\u76d4\uff0c\u62c4\u7740\u94f6\u5251\uff0c\u80cc\u540e\u662f\u5915\u9633\u80dc\u8840\uff0c\u6b8b\u7ea2\u6f2b\u5929\uff0c\u90a3\u662f\u4f55\u7b49\u7684\u82f1\u59ff\u98d2\u723d\uff0c\u771f\u5982\u5973\u6b66\u795e\u4e00\u822c\u3002</p><p>\u8c01\u4e5f\u4e0d\u66fe\u6599\u5230\uff0c\u4e24\u4eba\u4f1a\u5f04\u6210\u4eca\u5929\u8fd9\u79cd\u5173\u7cfb\u5427\u3002</p><p>\u4e16\u4e8b\u65e0\u5e38\uff0c\u8c01\u4e5f\u9884\u6599\u4e0d\u5230\u3002</p><p>\u5bf9\u4e86\uff0c\u5947\u602a\u554a\uff0c\u5979\u662f\u5723\u6b66\u58eb\uff0c\u6309\u9053\u7406\u8bf4\uff0c\u5723\u6b66\u58eb\u867d\u7136\u4e0d\u5982\u7267\u5e08\u7cbe\u64c5\u795e\u672f\uff0c\u66f4\u591a\u7684\u529b\u91cf\u5728\u4e8e\u5200\u5251\uff0c\u4f46\u81f3\u5c11\u4e5f\u662f\u4f1a\u4e00\u4e9b\u7b80\u5355\u7684\u6cbb\u7597\u795e\u672f\u7684\u3002\u4e00\u8def\u540c\u884c\u8fd9\u4e48\u591a\u5929\uff0c\u65e0\u8bba\u5728\u4ec0\u4e48\u60c5\u51b5\u4e0b\uff0c\u743c\u6069\u90fd\u4e0d\u66fe\u89c1\u5979\u4f7f\u7528\u795e\u672f\u6cbb\u7597\u81ea\u5df1\uff0c\u8fd9\u5012\u662f\u6709\u4e9b\u53e4\u602a\u3002</p><p>\u4ed6\u6c89\u601d\u7740\uff0c\u7a81\u7136\u542c\u89c1\u5916\u9762\u8d70\u5eca\u91cc\u4f20\u6765\u8f7b\u8f7b\u7684\u811a\u6b65\u58f0\u3002\u743c\u6069\u672c\u80fd\u5730\u8b66\u89c9\u8d77\u6765\uff0c\u539f\u672c\u5728\u70db\u5821\u8fd9\u79cd\u5730\u65b9\uff0c\u4ed6\u8fd8\u662f\u6bd4\u8f83\u653e\u677e\u7684\uff0c\u4f46\u56e0\u4e3a\u6628\u5929\u5973\u6740\u624b\u7684\u4e8b\u60c5\uff0c\u4ed6\u5df2\u7ecf\u6709\u4e9b\u8349\u6728\u7686\u5175\u4e86\u3002\u4ed4\u7ec6\u503e\u542c\u7740\u811a\u6b65\u58f0\uff0c\u8d8a\u6765\u8d8a\u8fd1\uff0c\u8d8a\u6765\u8d8a\u6e05\u6670\uff0c\u6b63\u662f\u671d\u8fd9\u4e2a\u623f\u95f4\u8d70\u8fc7\u6765\u7684\uff0c\u743c\u6069\u4f38\u624b\u6084\u6084\u63a2\u5165\u6000\u4e2d\uff0c\u53d6\u4e86\u4e00\u64ae\u9ed1\u8272\u7c89\u672b\uff0c\u9632\u5907\u7740\u3002\u80cc\u540e\u6709\u8f7b\u5fae\u7684\u52a8\u9759\uff0c\u6885\u83f2\u65af\u4e5f\u4ece\u5e8a\u4e0a\u5750\u8d77\u8eab\u6765\uff0c\u5979\u5f88\u8b66\u9192\u3002</p><p>\u8fd9\u4e48\u770b\u6765\uff0c\u743c\u6069\u521a\u624d\u62b1\u5979\u4e0a\u5e8a\u7684\u65f6\u5019\uff0c\u5979\u5176\u5b9e\u4e5f\u662f\u6e05\u9192\u77e5\u9053\u7684\u5427\uff0c\u53ea\u662f\u4e0d\u60f3\u52a8\u5f39\uff0c\u88c5\u7761\u7f62\u4e86\u3002</p><p>\u811a\u6b65\u58f0\u8d8a\u6765\u8d8a\u6e05\u695a\uff0c\u5df2\u7ecf\u8d70\u5230\u95e8\u8fb9\uff0c\u201c\u7b03\uff01\u7b03\uff01\u201d\u6765\u4eba\u6572\u4e86\u4e24\u4e0b\u95e8\uff0c\u4e0d\u7b49\u91cc\u9762\u4eba\u7b54\u8bdd\uff0c\u5f84\u81ea\u63a8\u95e8\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u201c\u6253\u6270\u4e86\uff0c\u4e24\u4f4d\uff0c\u201d\u91d1\u53d1\u9752\u5e74\u745e\u6069\u65af\u5766\u4f9d\u65e7\u7b11\u5bb9\u6ee1\u9762\uff0c\u4ed6\u4eba\u4e00\u8d70\u8fdb\u6765\uff0c\u8eab\u4e0a\u4fbf\u5982\u9633\u5149\u707f\u70c2\u822c\uff0c\u6574\u4e2a\u623f\u95f4\u90fd\u660e\u4eae\u4e86\u51e0\u5206\uff0c\u201c\u4e0b\u5348\u597d\u3002\u201d</p><p>\u201c\u4e0b\u5348\uff1f\u201d\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u4ed6\u548c\u6885\u83f2\u65af\u56de\u623f\u95f4\u7684\u65f6\u5019\uff0c\u5e94\u8be5\u8fd8\u4e0d\u5230\u4e0a\u5348\u4e5d\u70b9\u949f\u3002\u7136\u540e\u4e5f\u6ca1\u505a\u4ec0\u4e48\uff0c\u53ea\u662f\u8fd0\u529f\u8c03\u606f\u4e00\u4f1a\uff0c\u600e\u4e48\u5c31\u5230\u4e0b\u5348\u4e86\uff1f</p><p>\u6885\u83f2\u65af\u7684\u623f\u95f4\u91cc\uff0c\u4e00\u5929\u5230\u665a\u62c9\u7740\u539a\u539a\u7684\u7a97\u5e18\uff0c\u4e5f\u770b\u4e0d\u5230\u5916\u9762\u7684\u5929\u8272\u3002\u4e0d\u8fc7\u8fd9\u662f\u5c0f\u4e8b\uff0c\u743c\u6069\u867d\u7136\u5fc3\u5934\u7591\u60d1\uff0c\u6682\u65f6\u4e5f\u61d2\u4e8e\u8ba1\u8f83\uff0c\u4ed6\u7684\u76ee\u5149\u843d\u5230\u745e\u6069\u65af\u5766\u7684\u624b\u4e0a\u3002</p><p>\u8679\u5f69\u9f99\u7684\u624b\u4e0a\uff0c\u6b63\u63d0\u7740\u4ed6\u90a3\u9762\u9f99\u9cde\u5c0f\u76fe\u3002</p></article>"}}}]);
