(window.webpackJsonp=window.webpackJsonp||[]).push([[1110],{1133:function(p){p.exports={chapterTitle:null,title:"\u7b2c1107\u7ae0 \u51fa\u4eba\u610f\u6599\u7684\u9053\u6b49",content:"<article><h2>\u7b2c1107\u7ae0 \u51fa\u4eba\u610f\u6599\u7684\u9053\u6b49</h2><p>\u3000\u3000\u8f66\u7a97\u5916\u719f\u6089\u7684\u5929\u7a7a\uff0c\u7070\u6697\u9634\u6c89\uff0c\u6ca1\u6709\u4e00\u70b9\u751f\u673a\uff1b\u6781\u76ee\u8fdc\u773a\uff0c\u90fd\u662f\u79ef\u96ea\uff0c\u88ab\u98ce\u5439\u5e72\u7684\u79ef\u96ea\uff0c\u5e72\u5df4\u5df4\u7684\uff0c\u6ca1\u6709\u6e7f\u6da6\u7684\u5149\u5ea6\uff0c\u770b\u4e0a\u53bb\u4e5f\u6ca1\u6709\u4ec0\u4e48\u751f\u673a\u3002</p><p>\u3000\u3000\u9999\u6797\u5c0f\u9547\u5f88\u5feb\u5c31\u51fa\u73b0\u5728\u773c\u524d\uff0c\u5b8b\u5a49\u4f69\u66fe\u7ecf\u4e5f\u60f3\u5728\u9999\u6797\u5c0f\u9547\u4e1c\u7aef\u7684\u5357\u6e56\u68ee\u6797\u522b\u5885\u5c0f\u533a\u91cc\u4e70\u680b\u522b\u5885\uff0c\u73b0\u5728\u5374\u89c9\u5f97\u8fd9\u91cc\u5c24\u5176\u7684\u4ee4\u4eba\u89c9\u5f97\u618e\u6076\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u7693\u767d\u5982\u96ea\u7684\u7d20\u8155\u6258\u7740\u4e0b\u5df4\uff0c\u4e24\u773c\u5931\u795e\u7684\u671b\u7740\u7a97\u5916\uff0c\u5b8b\u7956\u8a89\u5c06\u4ed6\u7684\u5954\u9a70\u6d3e\u6765\u63a5\u5979\uff0c\u5979\u53ef\u6ca1\u6709\u89c9\u5f97\u6709\u4e1d\u6beb\u7684\u8363\u5e78\uff0c\u5f85\u8f66\u62d0\u4e0a\u8fdb\u9999\u6797\u5c0f\u9547\u7684\u6c34\u6ce5\u752c\u9053\uff0c\u5b8b\u5a49\u4f69\u6293\u8d77\u5ea7\u4f4d\u4e0a\u7684\u4e0a\u5957\uff0c\u8981\u8ba9\u53f8\u673a\u5728\u524d\u9762\u5c06\u8f66\u505c\u4e0b\u6765\uff1a\u201c\u5269\u4e0b\u4e00\u70b9\u8def\uff0c\u6211\u8d70\u8fc7\u53bb\u5c31\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5929\u5f88\u51b7\uff0c\u8fd8\u662f\u5750\u8f66\u5427\u2026\u2026\u201d\u4e00\u76f4\u4e0d\u542d\u58f0\u7684\u7ecf\u7eaa\u4eba\u7a81\u7136\u8bf4\u4e86\u4e00\u53e5\uff0c\u8981\u963b\u6b62\u5b8b\u5a49\u4f69\u4e0b\u8f66\uff0c\u89c1\u5979\u674f\u773c\u9732\u51fa\u6012\u8272\uff0c\u7ecf\u7eaa\u4eba\u8f6c\u8fc7\u8138\u53bb\u770b\u7740\u7a97\u5916\u8bf4\u9053\uff0c\u201c\u5b8b\u603b\u8fd9\u4e48\u5429\u5490\u7684\uff0c\u4f60\u8981\u5b9e\u5728\u4e0d\u613f\u610f\uff0c\u6495\u6bc1\u4e0e\u516c\u53f8\u7684\u5408\u7ea6\u5c31\u662f\uff0c\u6211\u4eec\u6362\u5bb6\u516c\u53f8\u4e00\u6837\u5403\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u9893\u7136\u5750\u5728\u5ea7\u4f4d\uff0c\u5979\u521a\u51fa\u9053\u65f6\uff0c\u4ec0\u4e48\u90fd\u4e0d\u61c2\uff0c\u8ddf\u516c\u53f8\u7b7e\u4e86\u5341\u4e94\u5e74\u7684\u957f\u671f\u5408\u540c\uff1b\u7b49\u5979\u89c1\u8bc6\u5230\u4eba\u5fc3\u7684\u9669\u6076\uff0c\u624d\u77e5\u9053\u90a3\u4efd\u5341\u4e94\u5e74\u7684\u5408\u7ea6\u6839\u672c\u5c31\u662f\u4e00\u7eb8\u5356\u8eab\u6954\u3002\u4e0d\u8981\u770b\u8fd9\u4e24\u5e74\u5979\u661f\u9014\u5766\u8361\uff0c\u540d\u6c14\u5927\u589e\uff0c\u5e7f\u544a\u5546\u3001\u7247\u7ea6\u7ed9\u4e88\u7684\u5408\u7ea6\u62a5\u916c\u5c61\u521b\u65b0\u9ad8\uff0c\u4f46\u662f\u7531\u4e8e\u5341\u5e74\u5356\u8eab\u6954\u7684\u5b58\u5728\uff0c\u7edd\u5927\u90e8\u5206\u6536\u5165\u90fd\u6d41\u5165\u516c\u53f8\u624b\u91cc\uff0c\u5979\u4e2a\u4eba\u5206\u5f97\u7684\u5341\u5206\u6709\u9650\uff0c\u9664\u975e\u5979\u5356\u4e86\u81ea\u5df1\uff0c\u4e0d\u7136\u5979\u652f\u4ed8\u4e0d\u8d77\u6495\u6bc1\u7ecf\u7eaa\u5408\u540c\u7684\u8d54\u507f\u91d1\u3002</p><p>\u3000\u3000\u8fd9\u5927\u6982\u662f\u63a5\u4e0b\u6765\u6700\u6f2b\u957f\u7684\u4e00\u6bb5\u8f66\u7a0b\uff0c\u53f8\u673a\u4e5f\u6709\u610f\u7684\u653e\u7f13\u8f66\u901f\uff0c\u5b8b\u5a49\u4f69\u5373\u4f7f\u8fd8\u62b1\u7740\u4e00\u4e1d\u9976\u5e78\u7684\u5962\u671b\uff0c\u5fc3\u4e5f\u60c5\u4e0d\u81ea\u7981\u7684\u51b0\u51b7\u5f97\u8ddf\u7ed3\u51b0\u7684\u6e56\u6c34\u4e00\u6837\uff0c\u51b7\u5f97\u8ba9\u4eba\u5fcd\u4e0d\u4f4f\u8981\u5fae\u5fae\u98a4\u6296\u52a8\u3002</p><p>\u3000\u3000\u8f66\u5b50\u9a76\u8fdb\u522b\u5885\u56ed\uff0c\u8bfa\u5927\u7684\u5e84\u56ed\u5f0f\u522b\u5885\u56ed\u533a\u5373\u4f7f\u8986\u76d6\u539a\u539a\u7684\u4e00\u5c42\u79ef\u96ea\uff0c\u4ecd\u80fd\u770b\u51fa\u5185\u4e2d\u7684\u5962\u534e\u6765\uff0c\u6765\u5230\u4e34\u8fd1\u5357\u6e56\u5cb8\u8fb9\u7684\u90a3\u680b\u522b\u5885\uff0c\u5b8b\u5a49\u4f69\u62ff\u7740\u5916\u5957\u4e0b\u4e86\u8f66\uff0c\u968f\u5373\u8f66\u95e8\u5c31\u7ed9\u4eba\u4ece\u91cc\u9762\u62c9\u4e0a\uff0c\u8f66\u7f13\u7f13\u9a76\u5f00\uff0c\u5b8b\u5a49\u4f69\u77e5\u9053\u8981\u7b49\u5979\u771f\u6b63\u7684\u8d70\u8fdb\u53bb\uff0c\u53f8\u673a\u624d\u4f1a\u5c06\u8f66\u5f00\u8d70\u3002</p><p>\u3000\u3000\u65e9\u8be5\u6709\u8fd9\u6837\u7684\u89c9\u609f\u4e86\uff0c\u5b8b\u5a49\u4f69\u6df1\u5438\u4e86\u4e00\u53e3\u51b0\u51b7\u7684\u7a7a\u6c14\uff0c\u53ea\u89c9\u5f97\u80c3\u5b50\u91cc\u90fd\u51c9\u900f\u4e86\uff0c\u8fd8\u662f\u8d70\u4e0a\u53bb\u6309\u95e8\u94c3\uff0c\u5f88\u5feb\u7ed9\u6f06\u6210\u80e1\u6843\u7070\u9ed1\u7684\u94c1\u95e8\u4ece\u91cc\u9762\u7ed9\u4eba\u6253\u5f00\u3002\u6709\u4e2a\u7a7f\u6df1\u84dd\u8272\u68c9\u5927\u8863\u7684\u9752\u5e74\u4ece\u91cc\u9762\u9732\u51fa\u8138\u6765\uff0c\u770b\u4e86\u5b8b\u5a49\u4f69\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u5b8b\u5c0f\u59d0\u8fc7\u6765\uff0c\u5f20\u5148\u751f\u5728\u5ba2\u5385\u91cc\u6b63\u7b49\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u4e0b\u610f\u8bc6\u7684\u8981\u5f80\u540e\u9000\uff0c\u4fee\u9970\u7cbe\u81f4\u7684\u9662\u5b50\u90a3\u5934\uff0c\u662f\u6247\u900f\u660e\u7684\u73bb\u7483\u95e8\uff0c\u5f20\u606a\u5c31\u7ad9\u5728\u73bb\u7483\u95e8\u540e\u773c\u775b\u76ef\u7740\u8fd9\u8fb9\u5728\u6253\u7535\u8bdd\u3002\u4e0d\u77e5\u9053\u4ed6\u5728\u7ed9\u8c01\u6253\u7535\u8bdd\uff0c\u5b8b\u5a49\u4f69\u5374\u77e5\u9053\u81ea\u5df1\u65e0\u8def\u53ef\u9000\uff0c\u5c31\u80fd\u4fa5\u5e78\u7684\u5e0c\u671b\u8054\u901a\u90a3\u5973\u7684\u6628\u5929\u591c\u91cc\u90a3\u4e48\u8bf4\u4e0d\u7eaf\u7cb9\u53ea\u662f\u5b89\u6170\u5979\u3002</p><p>\u3000\u3000\u770b\u5230\u5f20\u606a\u5728\u73bb\u7483\u95e8\u540e\u7ed9\u81ea\u5df1\u62db\u624b\uff0c\u5b8b\u5a49\u4f69\u5fc3\u91cc\u53c8\u6709\u8131\u8352\u800c\u9003\u7684\u5ff5\u5934\uff0c\u5374\u5f3a\u8feb\u81ea\u5df1\u5f80\u524d\u8d70\u3002</p><p>\u3000\u3000\u201c\u6211\u8fd8\u4ee5\u4e3a\u5b8b\u5c0f\u59d0\u4f1a\u665a\u4e9b\u65f6\u95f4\u8fc7\u6765\u5462\uff0c\u201d\u5f20\u606a\u5c06\u624b\u673a\u5939\u5728\u9888\u8116\u5b50\u91cc\uff0c\u4f38\u624b\u8981\u5e2e\u5b8b\u5a49\u4f69\u5c06\u5916\u5957\u63a5\u8fc7\u53bb\uff0c\u63a5\u7740\u4ed6\u4f3c\u4e4e\u53c8\u662f\u5728\u8ddf\u522b\u4eba\u8bb2\u7535\u8bdd\uff0c\u201c\u55ef\uff0c\u6ca1\u9519\uff0c\u5979\u6bd4\u6211\u9884\u6599\u7684\u8981\u65e9\u5230\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u5dee\u70b9\u5c31\u63a7\u5236\u4e0d\u4f4f\u53d1\u4f5c\u8d77\u6765\uff1a\u96be\u9053\u81ea\u5df1\u5728\u4ed6\u773c\u91cc\u5c31\u5f88\u72af\u8d31\uff0c\u9001\u4e0a\u95e8\u6765\u8fd8\u8feb\u4e0d\u53ca\u5f85\uff1f\u8fd8\u662f\u5c06\u70df\u7070\u8272\u7684\u5462\u5b50\u5916\u5957\u4ea4\u7ed9\u5f20\u606a\u6302\u5230\u7384\u5173\u7684\u955c\u67b6\u4e0a\uff0c\u8fd9\u624d\u6ce8\u610f\u5230\u521a\u624d\u7ed9\u5979\u5f00\u95e8\u7684\u90a3\u4e2a\u9752\u5e74\u6ca1\u6709\u8ddf\u7740\u8d70\u8fdb\u6765\uff0c\u901a\u8fc7\u73bb\u7483\u95e8\u53ef\u4ee5\u770b\u89c1\u4ed6\u4ece\u53f0\u9636\u53f3\u4fa7\u7ed5\u5230\u522b\u5885\u7684\u4fa7\u9762\u53bb\uff0c\u77e5\u9053\u522b\u5885\u91cc\u8fd8\u5e26\u6709\u72ec\u7acb\u7684\u4fdd\u9556\u5ba4\u6216\u8005\u8bf4\u662f\u5de5\u4eba\u623f\uff0c\u5ba2\u5385\u91cc\u5c31\u5979\u4e0e\u5f20\u606a\u4e24\u4e2a\u4eba\uff0c\u53ea\u6015\u6574\u680b\u697c\u91cc\u771f\u6ca1\u6709\u5176\u4ed6\u4eba\u5728\u4e86\u3002</p><p>\u3000\u3000\u201c\u90a3\u6211\u5148\u6302\u7535\u8bdd\u4e86\u2026\u2026\u201d\u5f20\u606a\u7ed3\u675f\u901a\u8bdd\uff0c\u5c06\u624b\u673a\u62ff\u5728\u624b\u91cc\uff0c\u8ddf\u5b8b\u5a49\u4f69\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u672c\u6765\u662f\u6d3e\u4eba\u8fc7\u53bb\u63a5\u5b8b\u5c0f\u59d0\u7684\uff0c\u624d\u53d1\u73b0\u6628\u5929\u5fd8\u4e86\u95ee\u5b8b\u5c0f\u59d0\u5728\u5317\u4eac\u7684\u4f4f\u5740\u4e86\u2026\u2026\u201d\u8bf7\u5b8b\u5a49\u4f69\u5f80\u91cc\u8d70\uff0c\u89c1\u5979\u8131\u6389\u5462\u5b50\u98ce\u8863\uff0c\u91cc\u9762\u8fd8\u7a7f\u7740\u5ae9\u7d2b\u8272\u7684\u5c0f\u5939\u514b\u897f\u88c5\uff0c\u7ffb\u5f00\u7684\u9886\u895f\u91cc\u9732\u51fa\u70df\u7070\u8272\u7684\u7ed2\u7ebf\u8863\u6765\uff0c\u5979\u4e0b\u8eab\u7a7f\u7740\u770b\u4e0a\u53bb\u5f88\u7ed3\u5b9e\u7684\u725b\u4ed4\u88e4\uff0c\u5fc3\u60f3\u6380\u5f00\u5979\u7684\u4e0a\u8863\u4e0b\u6446\u5927\u6982\u53ef\u4ee5\u770b\u5230\u5979\u8170\u4e0a\u624e\u5f97\u7ed3\u7ed3\u5b9e\u5b9e\u7684\u76ae\u5e26\u3002</p><p>\u3000\u3000\u7ed9\u5f20\u606a\u6253\u91cf\u7740\uff0c\u5b8b\u5a49\u4f69\u611f\u89c9\u7ed9\u8292\u523a\u8f7b\u624e\u5fc3\u5934\u4e00\u6837\u96be\u53d7\uff0c\u4e5f\u6839\u672c\u6ca1\u6709\u88c5\u7b11\u7684\u5fc3\u60c5\uff0c\u53ea\u671f\u5f85\u5669\u68a6\u80fd\u8d76\u7d27\u9192\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u5b8b\u5c0f\u59d0\u5e94\u8be5\u997f\u4e86\u5427\uff0c\u6211\u6ca1\u6709\u60f3\u5230\u5b8b\u5c0f\u59d0\u4f1a\u8fd9\u4e48\u65e9\u5230\uff0c\u8fd8\u8981\u8fc7\u534a\u4e2a\u5c0f\u65f6\uff0c\u624d\u80fd\u7528\u4e0a\u5348\u9910\uff0c\u5e0c\u671b\u5b8b\u5c0f\u59d0\u4e0d\u8981\u89c9\u5f97\u7ed9\u6020\u6162\u4e86\uff0c\u8981\u4e0d\u8981\u6211\u966a\u5b8b\u5c0f\u59d0\u5148\u53c2\u89c2\u4e00\u4e0b\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u7684\u76ee\u5149\u843d\u5728\u4e0a\u697c\u7684\u697c\u68af\u4e0a\uff0c\u5b8b\u5a49\u4f69\u6253\u4e86\u4e00\u4e2a\u6fc0\u7075\uff0c\u54ea\u91cc\u6562\u8ddf\u4ed6\u4e0a\u697c\u53bb\uff1f\u6324\u51fa\u8ddf\u82e6\u4e00\u6837\u7684\u7b11\u5bb9\u8bf4\u9053\uff1a\u201c\u6628\u5929\u6211\u6709\u773c\u4e0d\u8bc6\u6cf0\u5c71\uff0c\u8bef\u4f1a\u5f20\u5148\u751f\u4e86\uff0c\u5e0c\u671b\u5f20\u5148\u751f\u5927\u4eba\u5927\u91cf\uff0c\u9999\u6797\u5c0f\u9547\u5916\u56fd\u90fd\u5927\u9152\u5e97\u7684\u6cd5\u56fd\u9910\u5385\u4e13\u7a0b\u4ece\u6cd5\u56fd\u8bf7\u6765\u5927\u53a8\uff0c\u5e0c\u671b\u5f20\u5148\u751f\u80fd\u7ed9\u6211\u4e00\u4e2a\u8c22\u7f6a\u7684\u673a\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u662f\u5de7\u4e86\uff0c\u56fd\u90fd\u5927\u9152\u5e97\u7684\u6cd5\u56fd\u5927\u53a8\u521a\u7ed9\u6211\u8bf7\u8fc7\u6765\u51c6\u5907\u4e2d\u9910\uff0c\u770b\u6765\u6211\u8ba8\u597d\u5b8b\u5c0f\u59d0\u7684\u5fc3\u601d\u771f\u662f\u6ca1\u6709\u767d\u8d39\u5de5\u592b\uff0c\u8fd9\u4f4d\u5927\u53a8\u8fd8\u662f\u4e2a\u91d1\u53d1\u78a7\u773c\u7684\u7f8e\u5973\u3002\u90fd\u8bf4\u53a8\u827a\u5230\u8fbe\u4e00\u5b9a\u7684\u5883\u754c\uff0c\u4e5f\u5f88\u5177\u6b23\u8d4f\u6027\uff0c\u5b8b\u5c0f\u59d0\u4e0d\u4ecb\u610f\u966a\u6211\u53bb\u53c2\u89c2\u4e00\u4e0b\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u7684\u8868\u6f14\u5427\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u5fc3\u91cc\u60f3\u9003\u79bb\u4e0e\u5f20\u606a\u5355\u72ec\u76f8\u5904\u7684\u5ba2\u5385\uff0c\u6068\u4e0d\u5f97\u62e8\u817f\u5c31\u671d\u53a8\u623f\u8d70\u53bb\uff0c\u8f6c\u8fc7\u4e00\u9053\u8349\u4e66\u6a80\u6728\u96d5\u523b\u7684\u5899\uff0c\u5c31\u662f\u5962\u534e\u4e4b\u6781\u7684\u9910\u5385\uff0c\u53a8\u623f\u5c31\u5728\u9910\u5385\u7684\u53e6\u4e00\u5934\uff0c\u6070\u53ef\u4ee5\u770b\u89c1\u7a7f\u767d\u8272\u53a8\u5e08\u670d\u7684\u6cd5\u56fd\u91d1\u53d1\u5973\u5927\u53a8\u6b63\u5fae\u4f4e\u7740\u5934\u51c6\u5907\u83dc\u80b4\uff0c\u8fd8\u6709\u4e00\u540d\u52a9\u624b\u5728\u91cc\u9762\u5728\u7ed9\u7c7b\u4f3c\u571f\u8c46\u6ce5\u7684\u4e00\u5c0f\u76d8\u7cca\u7cca\u505a\u9020\u578b\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u54ea\u6709\u5fc3\u601d\u53bb\u6b23\u8d4f\u4ec0\u4e48\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u7684\u624b\u827a\uff0c\u53ea\u662f\u89c9\u5f97\u6709\u65c1\u4eba\u5728\u573a\u5c31\u591a\u4e86\u4e00\u5206\u5b89\u5168\u611f\uff0c\u4f6f\u88c5\u60ca\u559c\u7684\u8d70\u8fc7\u53bb\uff0c\u5dee\u70b9\u8981\u8ddf\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u8d34\u5728\u4e00\u8d77\u3002\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u592a\u4e13\u5fc3\uff0c\u7b49\u5b8b\u5a49\u4f69\u8d70\u5230\u8eab\u8fb9\uff0c\u624d\u5413\u4e86\u4e00\u8df3\u7684\u770b\u5230\u5979\uff0c\u5374\u795e\u8272\u8be7\u5f02\u7684\u95ee\u4e86\u5f20\u606a\u4e00\u53e5\u8bdd\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u6123\u662f\u4e00\u4e2a\u8bcd\u90fd\u6ca1\u6709\u542c\u61c2\uff0c\u5fc3\u60f3\u5979\u8bf4\u7684\u662f\u6cd5\u8bed\u5427\uff1f\u89c1\u90a3\u4e2a\u7a7f\u53a8\u5e08\u8863\u670d\u7684\u52a9\u624b\u8fd8\u5728\u4f4e\u5934\u7ed9\u571f\u8c46\u6ce5\u505a\u9020\u578b\uff0c\u4f3c\u4e4e\u6ca1\u6709\u62ac\u5934\u5e2e\u5fd9\u7ffb\u8bd1\u7684\u610f\u601d\uff0c\u5fc3\u91cc\u6697\u60f3\uff1a\u65e0\u6cd5\u8ddf\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u4ea4\u6d41\uff0c\u600e\u6837\u624d\u80fd\u8ba9\u5979\u5e2e\u81ea\u5df1\u6e21\u8fc7\u773c\u524d\u7684\u96be\u5173\uff1f\u5b8b\u5a49\u4f69\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u63a5\u4e0b\u6765\u8ddf\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u4e5f\u8bf4\u4e86\u4e00\u53e5\u5979\u5b8c\u5168\u542c\u4e0d\u61c2\u7684\u8bdd\uff0c\u771f\u662f\u6ca1\u6709\u60f3\u5230\u8fd9\u773c\u6328\u5343\u5200\u7684\u6d51\u7403\u7adf\u7136\u4f1a\u8bf4\u6cd5\u8bed\uff0c\u770b\u4ed6\u4eec\u5728\u90a3\u91cc\u7709\u6765\u773c\u53bb\u7684\u76f8\u804a\u751a\u6b22\uff0c\u4e24\u4eba\u7684\u773c\u795e\u8fd8\u65f6\u4e0d\u65f6\u7684\u671b\u81ea\u5df1\u8eab\u4e0a\u98d8\uff0c\u5b8b\u5a49\u4f69\u53ea\u89c9\u5f97\u8d70\u8fdb\u8fd9\u680b\u522b\u5885\u91cc\u8fd8\u671f\u5f85\u6709\u6551\u661f\uff0c\u7b80\u76f4\u5c31\u662f\u5c0f\u5973\u5b69\u624d\u4f1a\u6709\u5929\u771f\uff01</p><p>\u3000\u3000\u201c\u6211\u8ddf\u51ef\u7433\u5a1c\u8bf4\u4f60\u5f88\u5d07\u62dc\u5979\u505a\u6cd5\u56fd\u83dc\u7684\u624b\u827a\uff0c\u4e3a\u6b64\u8fd8\u6492\u4e86\u4e00\u4e2a\u5c0f\u8c0e\uff0c\u8bf4\u662f\u5e94\u4f60\u7684\u8981\u6c42\u6211\u624d\u7279\u610f\u5c06\u5979\u8bf7\u6765\u51c6\u5907\u8fd9\u987f\u6cd5\u5f0f\u4e2d\u9910\uff1b\u8fd8\u8bf4\u4f60\u662f\u56fd\u5185\u5f88\u8457\u540d\u7684\u5f71\u89c6\u6b4c\u4e09\u6816\u660e\u661f\uff0c\u6709\u673a\u4f1a\u8fd8\u8981\u5c06\u5979\u5b66\u4e60\u505a\u83dc\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u6210\u4e3a\u6cd5\u56fd\u83dc\u80b4\u9876\u7ea7\u5927\u53a8\u7684\u4e8b\u8ff9\u62cd\u6210\u4e2d\u56fd\u7535\u5f71\u3002\u5979\u4e3a\u6b64\u975e\u5e38\u9ad8\u5174\uff0c\u8fd8\u8bf4\u8981\u5728\u5348\u9910\u540e\u8bf7\u4f60\u5e2e\u5979\u5408\u5f71\u7559\u5ff5\uff0c\u597d\u8ba9\u5979\u7684\u6cd5\u56fd\u670b\u53cb\u77e5\u9053\u5979\u5728\u4e2d\u56fd\u4ea4\u4e86\u4e00\u4e2a\u5927\u660e\u661f\u670b\u53cb\u2026\u2026\u201d\u5f20\u606a\u4e0e\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u804a\u5b8c\u5929\uff0c\u968f\u4fbf\u7ed9\u5b8b\u5a49\u4f69\u7b80\u5355\u7684\u8bf4\u4e86\u4e00\u904d\u4ed6\u4eec\u8c08\u8bdd\u7684\u5185\u5bb9\u3002</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u770b\u7740\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u770b\u5411\u81ea\u5df1\u7684\u773c\u795e\u91cc\u6ee1\u542b\u548c\u5584\uff0c\u771f\u4e0d\u5fcd\u5fc3\u544a\u8bc9\u5979\u5b9e\u60c5\uff0c\u4e24\u4eba\u8bed\u8a00\u4e0d\u901a\uff0c\u4e5f\u65e0\u6cd5\u544a\u8bc9\u5979\u5b9e\u60c5\u2026\u2026</p><p>\u3000\u3000\u201c\u597d\u4e86\uff0c\u6211\u4eec\u4e0d\u6253\u6270\u5927\u53a8\u5de5\u4f5c\u4e86\uff0c\u6211\u4eec\u5c31\u7b49\u7740\u54c1\u5c1d\u5927\u53a8\u7684\u624b\u827a\u5427\uff0c\u201d\u5f20\u606a\u4f38\u624b\u9080\u5b8b\u5a49\u4f69\u8d70\u51fa\u7565\u663e\u5f97\u6709\u4e9b\u72ed\u5c0f\u7684\u53a8\u623f\uff0c\u624b\u6307\u8fd8\u6ca1\u6709\u6cbe\u4e0a\u5b8b\u5a49\u4f69\u7684\u80a9\u5934\uff0c\u5c31\u89c1\u5979\u50cf\u662f\u8981\u7ed9\u9488\u624e\u5230\u4f3c\u7684\u5f80\u524d\u95ea\u3002\u8fd9\u65f6\u5019\u5916\u9762\u8d70\u8fdb\u4e00\u4e2a\u4eba\uff0c\u5b8b\u5a49\u4f69\u5413\u4e86\u4e00\u8df3\u731b\u7684\u5f80\u65c1\u8fb9\u8eb2\uff0c\u624b\u8098\u4e0d\u5728\u610f\u7684\u5f80\u540e\u62d0\u4e86\u4e00\u4e0b\uff0c\u610f\u8bc6\u5230\u649e\u5230\u5f20\u606a\u4e86\uff0c\u8f6c\u8eab\u770b\u89c1\u5f20\u606a\u624b\u6342\u7740\u80f8\u53e3\u5728\u90a3\u91cc\u82e6\u7b11\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u5b8b\u5c0f\u59d0\u4e0d\u662f\u6765\u9053\u6b49\u7684\uff0c\u662f\u62a5\u4ec7\u6765\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u4e00\u70b9\u90fd\u4e0d\u89c9\u5f97\u5f20\u606a\u7684\u8bdd\u6709\u4ec0\u4e48\u597d\u7b11\u7684\uff0c\u53ea\u5f53\u4ed6\u5728\u8fd9\u91cc\u5c31\u8981\u5f00\u53e3\u63d0\u4ed6\u65e0\u803b\u7684\u8981\u6c42\uff0c\u53c8\u9661\u7136\u60f6\u7136\u8d77\u6765\uff0c\u53ea\u770b\u89c1\u8d70\u8fdb\u6765\u7684\u4eba\u5728\u5f20\u606a\u8033\u7554\u8f7b\u8bed\u4e86\u51e0\u53e5\u8bdd\uff0c\u597d\u50cf\u8033\u6735\u5931\u806a\u4f3c\u7684\uff0c\u542c\u4e0d\u89c1\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff0c\u53ea\u770b\u89c1\u5f20\u606a\u5634\u89d2\u8fb9\u9732\u51fa\u663e\u5f97\u90aa\u6c14\u7684\u7b11\u5bb9\u3002</p><p>\u3000\u3000\u201c\u597d\u4e86\uff0c\u5ba2\u4eba\u90fd\u5230\u9f50\u4e86\uff0c\u53ef\u4ee5\u6b63\u5f0f\u54c1\u5c1d\u6cd5\u56fd\u7f8e\u5973\u5927\u53a8\u7684\u624b\u827a\u4e86\u2026\u2026\u201d\u5f20\u606a\u6413\u624b\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5b8b\u5a49\u4f69\u8ddf\u4e27\u5c38\u4f3c\u7684\u5e94\u4e86\u4e00\u58f0\uff0c\u51e0\u4e4e\u5728\u540c\u65f6\u53c8\u6253\u4e86\u4e00\u4e2a\u6fc0\u7075\u7684\u60ca\u9192\u8fc7\u6765\uff0c\u201c\u4ec0\u4e48\uff0c\u8fd8\u6709\u522b\u7684\u5ba2\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u672c\u6253\u7b97\u5355\u72ec\u5bb4\u8bf7\u5b8b\u5c0f\u59d0\u8d54\u793c\u9053\u6b49\u7684\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u53ea\u662f\u5f88\u4e0d\u5de7\uff0c\u521a\u597d\u6709\u670b\u53cb\u5230\u5317\u4eac\u6765\uff0c\u6211\u4e5f\u4e0d\u80fd\u4e0d\u62db\u5f85\uff0c\u5b8b\u5c0f\u59d0\u4e0d\u4f1a\u5c31\u6b64\u89c9\u5f97\u6211\u7684\u8bda\u610f\u4e0d\u8db3\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b8b\u5a49\u4f69\u8fd9\u65f6\u5019\u5b8c\u5168\u4e0d\u77e5\u9053\u8981\u8bf4\u4ec0\u4e48\u597d\uff0c\u4f9d\u7740\u6728\u96d5\u5899\uff0c\u53ea\u60f3\u7740\u6062\u590d\u4e9b\u529b\u6c14\u80fd\u8d70\u8def\u518d\u8bf4\uff0c\u542c\u89c1\u6237\u5916\u6709\u6c7d\u8f66\u58f0\u4f20\u8fdb\u6765\uff0c\u5fc3\u60f3\u771f\u662f\u6709\u5176\u4ed6\u5ba2\u4eba\u8fc7\u6765\uff0c\u5979\u8fd9\u65f6\u624d\u6ce8\u610f\u5230\u6cd5\u56fd\u7f8e\u56fd\u5927\u53a8\u5728\u53a8\u623f\u91cc\u51c6\u5907\u7684\u591a\u4efd\u83dc\u80b4\u4e5f\u5b8c\u5168\u4e0d\u662f\u4e24\u4e2a\u4eba\u80fd\u6d88\u706d\u6389\u4e86\u7684\uff0c\u53ea\u89c9\u5f97\u8eab\u5b50\u8f6f\u8f6f\u7684\u8981\u762b\u5012\u4e0b\u6765\uff0c\u4e00\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u770b\u7740\u5f20\u606a\u5634\u89d2\u6302\u7740\u7684\u90aa\u6c14\u7b11\u5bb9\uff0c\u5fc3\u91cc\u7a81\u7136\u53c8\u6068\u5f97\u8981\u547d\uff0c\u4e4b\u524d\u4e5f\u662f\u6068\uff0c\u73b0\u5728\u4e5f\u662f\u6068\uff0c\u8fd9\u4e24\u79cd\u6068\u4e4b\u95f4\u6216\u8bb8\u6709\u7a0d\u7a0d\u7684\u4e0d\u540c\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u201c\u5b8b\u5c0f\u59d0\u8fd9\u65f6\u5019\u5e94\u8be5\u76f8\u4fe1\u6211\u6628\u5929\u771f\u4e0d\u662f\u6545\u610f\u7684\u5427\uff1f\u201d\u5f20\u606a\u5ffd\u7684\u51d1\u5230\u5b8b\u5a49\u4f69\u7684\u8033\u8fb9\u8f7b\u58f0\u8a00\u8bed\u4e86\u4e00\u53e5\uff0c\u5c31\u5f80\u5916\u8d70\u53bb\uff0c\u8fd8\u4e0d\u5fd8\u5429\u5490\u5de5\u4f5c\u4eba\u5458\uff0c\u201c\u5e26\u5b8b\u5c0f\u59d0\u53bb\u5316\u5986\u95f4\uff0c\u6216\u8bb8\u5b8b\u5c0f\u59d0\u9700\u8981\u8865\u4e00\u4e0b\u5986\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u5a49\u4f69\u51fa\u95e8\u65f6\u6068\u4e0d\u5f97\u6bc1\u4e86\u81ea\u5df1\u7684\u5bb9\uff0c\u54ea\u91cc\u4f1a\u6536\u62fe\u81ea\u5df1\u554a\uff1f\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5c31\u89c9\u5f97\u6628\u5929\u7ed9\u4ed6\u624b\u8098\u649e\u7684\u80f8\u90e8\u8fd8\u9690\u9690\u7ea6\u7ea6\u7684\u6709\u4e9b\u75db\u3002</p></article>"}}}]);
