(window.webpackJsonp=window.webpackJsonp||[]).push([[1112],{1135:function(p){p.exports={chapterTitle:null,title:"\u7b2c909\u7ae0 \u624d\u662f\u7b2c\u4e00\u6b21",content:"<article><h2>\u7b2c909\u7ae0 \u624d\u662f\u7b2c\u4e00\u6b21</h2><p>\u3000\u3000\u534e\u590f\u7535\u5b50\u4e0e\u65e5\u672cNEC\u7535\u5b50\u7684\u8054\u5408\u6ce8\u8d44\u652f\u6301\u79d1\u738b\u53d1\u5c55CTR\u5f69\u7535\u5236\u9020\uff0c\u5f20\u606a\u89c6\u4e4b\u4e3a\u79d1\u738b\u5782\u6b7b\u6323\u624e\u524d\u6293\u5230\u7684\u6551\u547d\u7a3b\u8349\uff0c\u8c22\u5251\u5357\u5374\u4e0d\u8fd9\u4e48\u8ba4\u4e3a\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u56fd\u5185\u5f69\u7535\u5236\u9020\u4e1a\u4ea7\u503c\u8d85\u8fc7\u516b\u767e\u4ebf\uff0c\u4e5d\u516b\u5e74\u4e9a\u6d32\u7ecf\u6d4e\u6447\u6447\u6b32\u5760\uff0c\u4f46\u662f\u56fd\u5185\u7684\u5f69\u7535\u5236\u9020\u4e1a\u4ea7\u503c\u6709\u671b\u7a81\u7834\u5343\u4ebf\uff0c\u6838\u5fc3\u5f69\u7535\u4f01\u4e1a\u7684\u76c8\u5229\u80fd\u529b\u4e00\u76f4\u4e0d\u9519\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e3a\u8c03\u6574\u79d1\u738b\u7684\u4ea7\u4e1a\u7ed3\u6784\uff0c\u5e74\u521d\u5e76\u8d2d\u6d77\u5dde\u5e02\u4e09\u5143\u7535\u89c6\u673a\u5382\u8fdb\u884c\u7ba1\u7406\u6574\u987f\uff0c\u6062\u590d\u4e0d\u9519\u7684\u8d62\u5229\uff0c\u82e5\u80fd\u5728\u51e0\u9879\u5173\u952e\u6280\u672f\u4e0a\u83b7\u5f97NEC\u7684\u652f\u6301\uff0c\u4ed6\u76f8\u4fe1\u8d62\u5229\u7a7a\u95f4\u4f1a\u66f4\u5927\u3002</p><p>\u3000\u3000\u8fd9\u5012\u4e5f\u4e0d\u80fd\u6028\u8c22\u5251\u5357\u76ee\u5149\u77ed\u6d45\uff0c\u800c\u662f\u6574\u4e2a\u65f6\u4ee3\u7684\u5c40\u9650\u6027\u5c31\u50cf\u4e00\u56e2\u8ff7\u96fe\u906e\u5728\u773c\u524d\uff0c\u56fd\u5185\u7edd\u5927\u591a\u6570\u4f01\u4e1a\u7ba1\u7406\u8005\u7684\u8111\u5b50\u91cc\u5bf9\u5e02\u573a\u7ade\u4e89\u7684\u7406\u89e3\u8fd8\u505c\u7559\u5728\u4e0d\u60dc\u8840\u672c\u7684\u964d\u4ef7\u7ade\u4e89\u4e0e\u94fa\u5929\u76d6\u5730\u7684\u5e7f\u544a\u7ade\u4e89\u3002</p><p>\u3000\u3000\u6700\u4e3a\u5173\u952e\u7684\uff0c\u8c22\u5251\u5357\u8fd9\u65f6\u5019\u8fd8\u6709\u5176\u4ed6\u7684\u9009\u62e9\u5417\uff1f</p><p>\u3000\u3000\u91d1\u5c71\u7535\u5b50\u56ed\u7684\u5de5\u4eba\u6b63\u5728\u6d41\u5931\uff0c\u518d\u62d6\u4e0a\uff0c\u65b0\u62db\u52df\u91cd\u65b0\u652f\u4ed8\u57f9\u8bad\u8d39\u5c31\u5c06\u518d\u635f\u5931\u4e00\u5927\u7b14\u94b1\uff0c\u62d6\u5230\u90a3\u65f6\u5019\uff0c\u91d1\u5c71\u7535\u5b50\u56ed\u53ef\u4ee5\u5c31\u771f\u7684\u6ca1\u6551\u4e86\u3002</p><p>\u3000\u300029\u65e5\uff0c\u8c22\u5251\u5357\u5728\u6d77\u5dde\u8fce\u6765\u4e09\u4e95\u7269\u4ea7\u9a7b\u534e\u603b\u4ee3\u8868\u6c60\u4f50\u79c0\u85cf\u3001\u4e09\u4e95\u7269\u4ea7\u9a7b\u534e\u603b\u987e\u95ee\u5927\u91ce\u4fe1\u884c\u4e0e\u65e5\u672cNEC\u7535\u5b50\u4e2d\u56fd\u533a\u7684\u603b\u8f7d\u4e2d\u6751\u88d5\u4e4b\u4ee5\u53ca\u534e\u590f\u7535\u5b50\u603b\u88c1\u5ba4\u526f\u7ecf\u7406\u5468\u5174\u4e1c\u4e00\u884c\u4eba\u53c2\u89c2\u79d1\u738b\u5728\u6d77\u5dde\u9526\u6e56\u5de5\u4e1a\u533a\u7684\u5236\u9020\u57fa\u5730\u3002</p><p>\u3000\u3000\u5728\u79d1\u738b\u5de5\u5382\u4e1c\u8fb9\u662f\u9526\u6e56\u66f4\u52a0\u5e7f\u9614\u7684\u5de5\u5382\u533a\uff0c\u6c14\u6d3e\u975e\u51e1\uff0c\u5c31\u7b97\u968f\u884c\u7684\u65e5\u672c\u5b98\u5458\u770b\u5230\u90a3\u91cc\u90fd\u5728\u4ea4\u5934\u63a5\u8033\u7684\u8ba8\u8bba\u3002\u5468\u5174\u4e1c\u60f3\u8d77\u524d\u4e9b\u5929\u5728\u5317\u4eac\u7684\u4e0d\u6109\u5feb\u7ecf\u5386\uff0c\u5fc3\u91cc\u5c31\u6697\u6697\u4e2d\u7684\u53d1\u6068\u3002\u90a3\u5929\u591c\u91cc\u7070\u6e9c\u6e9c\u7684\u4ece\u7fe0\u56ed\u79bb\u5f00\u540e\uff0c\u4ed6\u8fd8\u5728\u6697\u6697\u7684\u61ca\u607c\uff1a\u6709\u5fc5\u8981\u5bf9\u90a3\u5bb6\u4f19\u8fd9\u4e48\u754f\u60e7\uff1f\u771f\u662f\u4e22\u5149\u4e86\u8138\u9762\u3002</p><p>\u3000\u3000\u8054\u4fe1\u7684\u5357\u65b9\u516c\u53f8\u6bd5\u7adf\u662f\u534e\u590f\u65d7\u4e0b\u7684\u4e09\u7ea7\u516c\u53f8\uff0c\u8096\u745e\u6c11\u6574\u987f\u8054\u4fe1\u7684\u8425\u9500\u4f53\u7cfb\u4e4b\u540e\uff0c\u5728\u5357\u65b9\u516c\u53f8\u5c31\u635e\u4e0d\u5230\u591a\u5c11\u6cb9\u6c34\uff0c\u5468\u5174\u4e1c\u6316\u7a7a\u5fc3\u601d\u6324\u8fdb\u534e\u590f\u7535\u5b50\u603b\u88c1\u5ba4\u62c5\u4efb\u526f\u7ecf\u7406\uff0c\u5b9e\u9645\u4e0a\u8fd8\u662f\u5347\u4e86\u534a\u7ea7\u300226\u65e5\u53bb\u7fe0\u56ed\uff0c\u662f\u526f\u5e02\u957f\u8d75\u6dfb\u7684\u516c\u5b50\u8d75\u666e\u8bda\u4e3a\u4ed6\u8c03\u56de\u5317\u4eac\u5e86\u795d\uff0c\u6ca1\u60f3\u5230\u4f1a\u9047\u4e0a\u90a3\u715e\u661f\u3002\u7559\u5728\u603b\u88c1\u5ba4\u4e00\u822c\u8bf4\u6765\u6ca1\u6709\u4ec0\u4e48\u6cb9\u6c34\u53ef\u635e\uff0c\u4f46\u662f\u603b\u88c1\u5ba4\u6210\u5458\u4f5c\u4e3a\u534e\u590f\u7535\u5b50\u7ba1\u7406\u5c42\u7684\u540e\u5907\u68af\u961f\uff0c\u5bf9\u5185\u3001\u5bf9\u5916\u6709\u4ec0\u4e48\u65b0\u7684\u72ec\u7acb\u9879\u76ee\uff0c\u603b\u662f\u4f18\u5148\u8003\u8651\u4ece\u603b\u88c1\u5ba4\u62bd\u8c03\u4eba\u624b\uff0c\u5468\u5174\u4e1c\u4fbf\u662f\u7785\u51c6\u603b\u88c1\u5ba4\u7684\u673a\u4f1a\u591a\u624d\u60f3\u6cd5\u8c03\u8fdb\u53bb\u7684\u3002</p><p>\u3000\u3000\u8c01\u90fd\u77e5\u9053\u73b0\u5728\u7684\u6d77\u5dde\u7ed9\u9526\u6e56\u4e00\u624b\u906e\u5929\uff0c\u65e0\u8bba\u662f\u8c22\u5251\u5357\u8fd8\u662f\u6c60\u4f50\u79c0\u85cf\uff0c\u8fd8\u662f\u534e\u590f\u7535\u5b50\u7684\u845b\u5efa\u5fb7\u4ee5\u53ca\u4e2d\u95f4\u7684\u6413\u548c\u4eba\u6797\u96ea\u90fd\u4e0d\u5e0c\u671b\u592a\u65e9\u7ed9\u5f20\u606a\u90a3\u5bb6\u4f19\u77e5\u9053\u8fd9\u6b21\u5408\u4f5c\u7684\u4e8b\u60c5\u2014\u2014\u9526\u6e56\u4e0e\u987e\u5bb6\u4ee5\u53ca\u4e0e\u6613\u4e91\u98de\u7684\u5173\u7cfb\u8fd8\u6ca1\u6709\u9732\u51fa\u6c34\u9762\u2014\u2014\u6c60\u4f50\u79c0\u85cf\u53c2\u89c2\u79d1\u738b\u4e00\u884c\u6ca1\u6709\u60ca\u52a8\u6d77\u5dde\u5e02\u91cc\uff0c\u751a\u81f3\u90fd\u6ca1\u6709\u76f4\u63a5\u4ece\u5317\u4eac\u4e58\u98de\u4eac\u62b5\u8fbe\u6d77\u5dde\uff0c\u800c\u662f\u5148\u62b5\u8fbe\u5efa\u90ba\u518d\u8f6c\u8f66\u5230\u6d77\u5dde\u7684\u3002</p><p>\u3000\u3000\u7b2c\u4e00\u5929\u7684\u53c2\u89c2\u5f88\u5feb\u5c31\u8fc7\u53bb\u4e86\uff0c\u665a\u5bb4\u540e\uff0c\u6c60\u4f50\u79c0\u85cf\u3001\u5927\u91ce\u4fe1\u884c\u3001\u4e2d\u6751\u88d5\u4e4b\u6ca1\u6709\u53c2\u52a0\u63a5\u4e0b\u6765\u5b89\u6392\u7684\u6d3b\u52a8\u5c31\u76f4\u63a5\u56de\u4e86\u5bbe\u9986\uff0c\u897f\u57ce\u5927\u9152\u5e97\u91cc\u8bbe\u6709\u65e5\u5f0f\u7684\u8336\u5ba4\uff0c\u4ed6\u4eec\u4e09\u4eba\u5c06\u968f\u884c\u7684\u79d8\u4e66\u6452\u9000\u6389\uff0c\u5728\u8336\u5ba4\u91cc\u79d8\u8bae\u3002</p><p>\u3000\u3000\u201c\u4e2d\u6751\u541b\uff0c\u4f60\u5bf9\u4e0e\u79d1\u738b\u7684\u5408\u4f5c\u6709\u4f55\u8bc4\u4ef7\uff1fNEC\u82e5\u89c9\u5f97\u7684\u56f0\u96be\uff0c\u6211\u53ef\u4ee5\u53bb\u62dc\u6258\u590f\u666e\u7684\u677e\u5c9b\u541b\u2026\u2026\u201d\u6c60\u4f50\u79c0\u85cf\u7565\u77e5\u8336\u827a\uff0c\u76d8\u817f\u968f\u610f\u7684\u5750\u7740\u5e2e\u5927\u91ce\u4fe1\u884c\u3001\u4e2d\u6751\u88d5\u4e4b\u6c8f\u8336\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u9009\u62e9\u8fd9\u5bb6\u4f01\u4e1a\uff1f\u201d\u4e2d\u6751\u88d5\u4e4b\u5750\u76f4\u8eab\u5b50\uff0c\u624b\u8f7b\u63a9\u8336\u676f\u63a5\u8336\uff0c\u201c\u6211\u89c9\u5f97\u65e0\u8bba\u662fNEC\u8fd8\u662f\u590f\u666e\u5728\u4e2d\u56fd\u5e94\u8be5\u90fd\u6709\u66f4\u591a\u7684\u9009\u62e9\uff0c\u8fd9\u5bb6\u4f01\u4e1a\u5f53\u524d\u7684\u56f0\u5883\uff0c\u5f88\u96be\u8ba9\u4ed6\u4eec\u518d\u83b7\u5f97\u5730\u65b9\u653f\u5e9c\u7684\u652f\u6301\u3002\u201d</p><p>\u3000\u3000\u201c\u534e\u590f\u7535\u5b50\u7684\u53c2\u80a1\uff0c\u5c06\u8ba9\u79d1\u738b\u7684\u4f01\u4e1a\u6027\u8d28\u6709\u6240\u53d8\u5316\uff0c\u5c31\u53ef\u4ee5\u7406\u76f4\u6c14\u58ee\u7684\u8981\u6c42\u5730\u65b9\u653f\u5e9c\u7ed9\u4e88\u8d37\u6b3e\u4e0a\u7684\u652f\u63f4\uff0c\u201d\u6c60\u4f50\u79c0\u85cf\u8bf4\u9053\uff0c\u201c\u53e6\u5916\uff0c\u534e\u590f\u7535\u5b50\u53ef\u4ee5\u76f4\u63a5\u7ed9\u79d1\u738b\u63d0\u4f9b\u4fe1\u7528\u62c5\u4fdd\uff0c\u878d\u8d44\u7684\u95ee\u9898\u4e0d\u7528\u592a\u62c5\u5fc3\uff0c\u81f3\u4e8e\u653f\u7b56\u4e0a\u7684\u652f\u6301\uff0c\u65e0\u8bba\u662f\u8fd9\u91cc\u7684\u5236\u9020\u57fa\u5730\uff0c\u8fd8\u662f\u6211\u4eec\u770b\u4e2d\u7684\u91d1\u5c71\u5de5\u5382\uff0c\u90fd\u5728\u7279\u5b9a\u5212\u51fa\u7684\u5de5\u4e1a\u533a\u5185\uff0c\u5176\u5e94\u4eab\u53d7\u3001\u80fd\u4eab\u53d7\u7684\u653f\u7b56\u652f\u6301\u90fd\u5df2\u7ecf\u4eab\u53d7\u5230\u4e86\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4eec\u9009\u62e9\u7684\u5408\u4f5c\u65b9\u4f9d\u65e7\u662f\u534e\u590f\u7535\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5bf9\u7ec8\u7aef\u4ea7\u54c1\u7684\u5408\u8d44\uff0c\u6211\u80fd\u591f\u7406\u89e3\uff0c\u4f46\u662f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u5408\u8d44\u6210\u7acb\u4e00\u7cfb\u5217\u7684\u96f6\u90e8\u4ef6\u5de5\u5382\uff1f\u201d\u4e2d\u6751\u88d5\u4e4b\u4e0d\u89e3\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e09\u661f\u60f3\u51ed\u501f\u81ea\u8eab\u7684\u4ea7\u54c1\u76f4\u63a5\u6253\u8fdb\u4e2d\u56fd\u5e02\u573a\u7684\u7b56\u7565\uff0c\u6211\u6709\u4e0d\u540c\u7684\u89c1\u89e3\uff1a\u9009\u62e9\u4e0e\u4e2d\u8d44\u54c1\u724c\u5408\u4f5c\uff0c\u5f62\u6210\u4e00\u4e2a\u4e0a\u6e38\u5728\u65e5\u672c\u3001\u4e2d\u4e0b\u6e38\u5728\u4e2d\u56fd\u3001\u4ece\u4e0a\u6e38\u5230\u4e2d\u6e38\u3001\u5230\u4e0b\u6e38\u6211\u65b9\u6301\u80a1\u6bd4\u4f8b\u9010\u6e10\u964d\u4f4e\u7684\u4ea7\u4e1a\u94fe\u96c6\u7fa4\u3002\u8fd9\u6837\uff0c\u6211\u4eec\u5c31\u80fd\u8ba9\u65e5\u672c\u7535\u5b50\u4ea7\u54c1\u5316\u6574\u4e3a\u96f6\u7684\u6e17\u900f\u5230\u4e2d\u56fd\u7684\u5e02\u573a\uff0c\u8fd8\u53ef\u4ee5\u6700\u5927\u9650\u5236\u7684\u5c06\u5229\u6da6\u5411\u6211\u4eec\u503e\u659c\uff0c\u5982\u679c\u6240\u6709\u7684\u96f6\u90e8\u4ef6\u90fd\u7531\u65e5\u672c\u7684\u5de5\u5382\u8f93\u9001\uff0c\u5bf9\u79d1\u738b\u6765\u8bf4\u6210\u672c\u592a\u9ad8\uff0c\u4f1a\u8ba9\u4ed6\u4eec\u6025\u4e8e\u6446\u8131\u6211\u4eec\u7684\u63a7\u5236\u800c\u5728\u4e2d\u56fd\u5883\u5185\u9009\u62e9\u96f6\u90e8\u4ef6\u4f9b\u5e94\u5546\u2014\u2014\u8ba9\u4ed6\u4eec\u6709\u9650\u5ea6\u7684\u4eab\u53d7\u96f6\u90e8\u4ef6\u5de5\u5382\u7684\u5229\u6da6\uff0c\u5c31\u80fd\u5c06\u4ed6\u4eec\u59cb\u7ec8\u7ed1\u6210\u6211\u4eec\u7684\u9493\u94a9\u4e0a\u2014\u2014\u53e6\u4e00\u65b9\u9762\uff0c\u7ec8\u7aef\u4ea7\u54c1\u662f\u4e2d\u8d44\u54c1\u724c\uff0c\u5c31\u80fd\u6d88\u5f31\u4e2d\u56fd\u6c11\u4f17\u7684\u62b5\u89e6\u60c5\u7eea\u52a0\u5927\u5e02\u573a\u7684\u6e17\u900f\u529b\u5ea6\u3002\u5f53\u7136\uff0c\u8fd9\u53ea\u662f\u9488\u5bf9\u4e2d\u4f4e\u7aef\u4ea7\u54c1\u7684\u7b56\u7565\uff0c\u6211\u4eec\u81ea\u5df1\u5c31\u53ef\u4ee5\u4e13\u5fc3\u653b\u514b\u4e2d\u56fd\u7684\u4e2d\u9ad8\u7aef\u5e02\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u8bf4\u5b8c\u8fd9\u756a\u8bdd\u5c31\u7b49\u7740\u4e2d\u6751\u88d5\u4e4b\u7684\u56de\u5e94\uff0c\u4e2d\u6751\u88d5\u4e4b\u4ed6\u8fd8\u662f\u96be\u4ee5\u7406\u89e3\u6c60\u4f50\u79c0\u85cf\u4e3a\u4ec0\u4e48\u8981\u9009\u62e9\u79d1\u738b\uff0c\u4e0d\u8fc7\u6c60\u4f50\u79c0\u85cf\u7684\u6e17\u900f\u7b56\u7565\u4ed6\u662f\u8d5e\u540c\u7684\uff0c\u601d\u91cf\u4e86\u8bb8\u4e45\uff0c\u624d\u7f13\u7f13\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u8868\u793a\u7ed9\u6c60\u4f50\u79c0\u85cf\u8bf4\u670d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u672a\u6765\u662f\u7b49\u79bb\u5b50\u4f53\u8fd8\u662f\u6db2\u6676\u7684\u5929\u4e0b\uff0cCRT\u7535\u89c6\u7ec8\u7a76\u4f1a\u8ddf\u4e0d\u4e0a\u65f6\u4ee3\uff0c\u6211\u60f3NEC\u65e5\u672c\u603b\u90e8\u4e5f\u662f\u8ba4\u771f\u7684\u601d\u8003\u6c60\u4f50\u541b\u7684\u63d0\u8bae\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u966a\u540c\u5468\u5174\u4e1c\u5728\u6d77\u5dde\u9752\u5e74\u8def\u4e00\u5bb6\u65e5\u5f0f\u7684\u4ff1\u4e50\u90e8\u91cc\u559d\u9152\uff0c\u65c1\u8fb9\u4f9d\u5750\u7740\u4e24\u540d\u8138\u86cb\u751c\u7f8e\u7684\u5f85\u9152\u5c0f\u59d0\uff0c\u6c60\u4f50\u79c0\u85cf\u7b49\u4eba\u7684\u63d0\u524d\u79bb\u573a\uff0c\u4f7f\u5f97\u8fd9\u8fb9\u7684\u5b89\u6392\u6d3e\u4e0d\u4e0a\u7528\u573a\uff0c\u4f46\u662f\u4ed6\u5bf9\u5468\u5174\u4e1c\u4e5f\u4e0d\u6562\u9a6c\u864e\uff0c\u8fd9\u79cd\u4eba\u4f60\u4e0d\u80fd\u6307\u671b\u4ed6\u5e2e\u4e0a\u4ec0\u4e48\u5fd9\uff0c\u4f46\u662f\u4e5f\u8981\u9632\u6b62\u4ed6\u5e2e\u5012\u5fd9\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5728\u7b49\u6c60\u4f50\u79c0\u85cf\u7684\u7535\u8bdd\uff0c\u4ed6\u4e4b\u524d\u4e0e\u6c60\u4f50\u79c0\u85cf\u7684\u4f1a\u9762\u5df2\u7ecf\u79d8\u5bc6\u8c08\u59a5\u5408\u4f5c\u7684\u534f\u8bae\u2014\u2014\u867d\u8bf4\u4e09\u4e95\u7269\u4e1a\u5bf9\u4e09\u4e95\u8d22\u56e2\u4f53\u7cfb\u5185\u7684\u4f01\u4e1a\u6709\u6781\u5927\u7684\u5f71\u54cd\u529b\u4e0e\u63a7\u5236\u529b\uff0c\u4f46\u662f\u8fd9\u79cd\u5f71\u54cd\u529b\u4e0e\u63a7\u5236\u529b\u5e76\u4e0d\u662f\u76f4\u63a5\u65bd\u52a0\u7684\uff0c\u6c60\u4f50\u79c0\u85cf\u8bf4\u670dNEC\u4e2d\u56fd\u533a\u603b\u88c1\uff0c\u624d\u80fd\u4f7f\u4e8b\u60c5\u5f80\u524d\u518d\u63a8\u4e00\u6b65\u8fdb\u5165\u5b9e\u9645\u64cd\u4f5c\u9636\u6bb5\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u8fd9\u65f6\u5019\u4e5f\u5728\u72b9\u8c6b\u9009\u62e9\u8fd9\u4e48\u505a\u5230\u5e95\u6b63\u4e0d\u6b63\u786e\u3002</p><p>\u3000\u3000\u80c1\u8feb\u5609\u4fe1\u7535\u5b50\u63a5\u53d7\u91d1\u5c71\u5de5\u5382\u8499\u53d7\u5de8\u989d\u635f\u5931\u7684\u4e8b\u5b9e\uff0c\u5c06\u91d1\u5c71\u5de5\u5382\u4ee5\u4f4e\u51c0\u503c\u5e76\u5165\u79d1\u738b\uff0c\u7136\u540e\u63a5\u53d7\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u8054\u5408\u6ce8\u8d44\uff0c\u5c06\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4efd\u538b\u7f29\u5230\u4e00\u4e2a\u8f83\u5c0f\u7684\u6bd4\u4f8b\uff0c\u4fdd\u8bc1\u534e\u590f\u7535\u5b50\u4e0e\u8c22\u5bb6\u4e3a\u5927\u80a1\u4e1c\uff0c\u5728\u96f6\u914d\u4ef6\u5de5\u5382\u7684\u5408\u8d44\u4e4b\u4e2d\uff0c\u5219\u5c06\u5609\u4fe1\u7535\u5b50\u5b8c\u5168\u629b\u5f03\u5f00\u3002</p><p>\u3000\u3000\u5609\u4fe1\u7535\u5b50\u662f\u5229\u76ca\u727a\u7272\u8005\uff0c\u5bf9\u91d1\u5c71\u5de5\u5382\u9020\u6210\u635f\u5931\u8981\u8fdb\u884c\u5927\u989d\u8ba1\u63d0\uff0c\u5bf9\u79d1\u738b\u7684\u6301\u80a1\u4e5f\u5c06\u7ed9\u538b\u5230\u4e00\u4e2a\u5f88\u5c0f\u7684\u6bd4\u4f8b\uff0c\u5728\u79d1\u738b\u4e27\u5931\u8bed\u8bdd\u6743\uff0c\u8fd8\u65e0\u6cd5\u53c2\u4e0e\u4e0a\u6e38\u96f6\u914d\u4ef6\u5229\u76ca\u7684\u5206\u914d\u3002</p><p>\u3000\u3000\u79d1\u738b\u6ce8\u8d44\u65b9\u6848\u53ea\u8981NEC\u63a5\u53d7\u4e86\uff0c\u8c22\u5251\u5357\u81ea\u7136\u6709\u624b\u6bb5\u8ba9\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u63a5\u53d7\uff0c\u4f46\u662f\u4e4b\u524d\u865a\u5047\u7684\u8109\u8109\u6e29\u60c5\u5c31\u4e0d\u590d\u5b58\u5728\u4e86\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5012\u662f\u5f88\u6000\u5ff5\u4e0e\u845b\u836b\u5747\u5728\u6d77\u5916\u4e00\u8d77\u6e21\u8fc7\u7684\u8352\u6deb\u7684\u7559\u5b66\u751f\u6daf\uff0c\u6bd4\u8d77\u53cb\u8c0a\uff0c\u773c\u4e0b\u7684\u751f\u5b58\u66f4\u4e3a\u91cd\u8981\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u8c22\u5251\u5357\u4e0e\u6797\u96ea\u8fd8\u7ea6\u5b9a\u5171\u540c\u538b\u7f29\u5609\u4fe1\u5728\u9526\u57ce\u5730\u4ea7\u4e0a\u7684\u6743\u76ca\uff0c\u5609\u4fe1\u88ab\u538b\u7f29\u7684\u6743\u76ca\uff0c\u8c22\u5251\u5357\u4e0e\u6797\u96ea\u90fd\u672a\u66fe\u6709\u8fc7\u4eab\u53d7\u7684\u5fc3\u601d\uff0c\u90fd\u8981\u62ff\u53bb\u586b\u9971\u5b8f\u4fe1\u5efa\u8bbe\u8fd9\u5934\u80c3\u53e3\u5947\u597d\u7684\u5de8\u9cc4\uff0c\u4ee5\u51cf\u5c11\u4ed6\u4eec\u4e0e\u5b8f\u4fe1\u5efa\u8bbe\u4ea4\u6613\u65f6\u7684\u635f\u5931\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u4e5f\u9887\u4e3a\u5927\u5ea6\uff0c\u540c\u610f\u8c22\u5251\u5357\u3001\u6797\u96ea\u5148\u62ff\u9526\u57ce\u5730\u4ea7\u7684\u80a1\u6743\u5230\u4fe1\u901a\u94f6\u884c\u8fdb\u884c\u62b5\u62bc\u8d37\u6b3e\u4ee5\u89e3\u71c3\u7709\u4e4b\u6025\uff0c\u5f53\u7136\u8fd9\u6b21\u878d\u8d44\u9644\u52a0\u7684\u79d8\u5bc6\u6761\u6b3e\u662f\u65b9\u4fbf\u5b8f\u4fe1\u5efa\u8bbe\u6536\u8d2d\u4ed6\u4eec\u624b\u91cc\u5bf9\u9526\u57ce\u5730\u4ea7\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u8fd9\u624d\u662f\u771f\u6b63\u7684\u5546\u754c\uff01</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u83b7\u5f97\u5728\u7edf\u6218\u90e8\u62c5\u4efb\u90e8\u957f\u52a9\u7406\u7684\u7236\u4eb2\u7684\u540c\u610f\uff0c\u9646\u5065\u8feb\u4e0d\u53ca\u5f85\u7684\u5468\u4e00\u5c31\u5728\u5355\u4f4d\u529e\u4e86\u505c\u85aa\u7559\u804c\u624b\u7eed\uff0c\u76f4\u63a5\u5230\u5434\u542f\u7acb\u5728\u5317\u4eac\u8bbe\u7acb\u7684\u5609\u91cc\u5730\u4ea7\u5f00\u53d1\u6709\u9650\u516c\u53f8\u62c5\u4efb\u526f\u603b\u7ecf\u7406\u3002</p><p>\u3000\u3000\u5609\u91cc\u5730\u4ea7\u633a\u5efa\u90ba\u697c\u5e02\u4e5f\u65e0\u9700\u6025\u4e8e\u4e00\u65f6\uff0c\u9646\u5065\u6682\u65f6\u7559\u5728\u5317\u4eac\u6b63\u5f0f\u4ee5\u5730\u4ea7\u5546\u7684\u8eab\u4efd\u4e3a\u738b\u7ef4\u5747\u3001\u59da\u6587\u76db\u5230\u4eac\u57ce\u5730\u4ea7\u5708\u6765\u62db\u5546\u505a\u4e9b\u7b79\u5907\u5de5\u4f5c\uff0c\u5609\u91cc\u5730\u4ea7\u4e5f\u5c06\u987a\u7406\u6210\u7ae0\u7684\u901a\u8fc7\u6b63\u5f0f\u7684\u62db\u5546\u6e20\u9053\u633a\u8fdb\u5efa\u90ba\u697c\u5e02\u53d1\u5c55\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u6682\u65f6\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4e9b\uff0c\u4f46\u662f\u4eba\u5728\u5317\u4eac\u5bb6\u91cc\u7684\u4e25\u6587\u4ecb\u5750\u5728\u591c\u5e55\u7b3c\u7f69\u4e0b\u7684\u9633\u53f0\u4e0a\uff0c\u8fdc\u79bb\u5e02\u4e2d\u5fc3\u7684\u90ca\u533a\u522b\u5885\uff0c\u623f\u5b50\u5468\u8fb9\u7684\u8def\u706f\u90fd\u5f88\u6697\uff0c\u4e25\u6587\u4ecb\u5750\u5728\u9634\u5f71\u91cc\uff0c\u4ed6\u611f\u89c9\u4f3c\u4e4e\u6709\u4e9b\u4f4e\u4f30\u90a3\u4e2a\u5bb6\u4f19\u4e86\u3002</p><p>\u3000\u3000\u4ed6\u6628\u5929\u63a5\u5230\u8096\u660e\u5efa\u7684\u7535\u8bdd\u4e4b\u540e\u5c31\u77e5\u9053\u6d88\u606f\u4e0d\u53ef\u907f\u514d\u7684\u4f20\u5230\u90a3\u4e2a\u5bb6\u4f19\u7684\u8033\u6735\u91cc\uff0c\u8ba1\u7b97\u90a3\u5bb6\u4f19\u62b5\u4eac\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u53cd\u5e94\u4e5f\u771f\u591f\u8fc5\u901f\u7684\u2014\u2014\u738b\u7ef4\u5747\u7684\u5148\u53d1\u5236\u4eba\u7684\u786e\u8ba9\u4eba\u63aa\u624b\u4e0d\u53ca\uff0c\u800c\u738b\u7ef4\u5747\u5e76\u975e\u5728\u5efa\u90ba\u53eb\u56a3\u4e24\u58f0\uff0c\u7f57\u541b\u4e5f\u5e0c\u671b\u7c4d\u6b64\u8ddf\u4eac\u57ce\u516c\u5b50\u5708\u91cc\u7684\u4eba\u7269\u641e\u597d\u5173\u7cfb\uff0c\u5317\u4eac\u4e5f\u7684\u786e\u6709\u4eba\u7ad9\u51fa\u6765\u4e3a\u4ed6\u4eec\u9020\u58f0\u52bf\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4ec5\u624d\u662f\u7b2c\u4e00\u6b21\uff01\u201d\u4e25\u6587\u4ecb\u81ea\u8a00\u81ea\u8bed\u7684\u8bf4\u4e86\u4e00\u58f0\uff0c\u5634\u89d2\u7275\u52a8\u4e86\u4e00\u4e0b\u3002</p></article>"}}}]);
