(window.webpackJsonp=window.webpackJsonp||[]).push([[1114],{1137:function(p){p.exports={chapterTitle:null,title:"\u7b2c911\u7ae0 \u4f73\u4eba\u76f8\u4f34\u6e21\u5047",content:"<article><h2>\u7b2c911\u7ae0 \u4f73\u4eba\u76f8\u4f34\u6e21\u5047</h2><p>\u3000\u3000\u9999\u6e2f\u4e0d\u53d7\u56fd\u5185\u9ec4\u91d1\u5468\u5047\u671f\u8c03\u6574\u7684\u5f71\u54cd\uff0c\u56fd\u5e86\u8282\u53ea\u6709\u4e00\u5929\u7684\u516c\u5047\uff0c\u52a0\u4e0a\u4e2d\u79cb\u4e0e\u53cc\u4f11\u65e5\uff0c\u5510\u5a67\u4e5f\u6709\u56db\u5929\u7684\u5047\u671f\uff0c\u5979\u76f4\u98de\u5317\u4eac\u4e0e\u5979\u7238\u5988\u6c47\u5408\uff0c\u8fd8\u8981\u5317\u4eac\u505c\u7559\u4e00\u5929\u624d\u80fd\u5230\u5efa\u90ba\u6765\u3002\u5f20\u606a\u5728\u5efa\u90ba\u4e5f\u4e0d\u5bc2\u5bde\uff0c\u966a\u5b59\u9759\u6aac\u901b\u4e86\u4e00\u5929\u8857\uff0c10\u67082\u65e5\uff0c\u4e0d\u6653\u5f97\u76db\u590f\u54ea\u6839\u795e\u7ecf\u642d\u9519\u4e86\uff0c\u8981\u62c9\u7740\u675c\u98de\u53bb\u653e\u98ce\u7b5d\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u4e5f\u7ed9\u62c9\u4e86\u8fc7\u53bb\u73a9\u4e86\u4e00\u5929\u3002</p><p>\u3000\u3000\u5510\u5a67\u9ec4\u660f\u65f6\u5206\u4e58\u98de\u673a\u62b5\u8fbe\u5efa\u90ba\u7684\uff0c\u5728\u5efa\u90ba\u56fd\u9645\u673a\u573a\u6ca1\u6709\u803d\u6401\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u52a0\u4e0a\u9648\u5983\u84c9\u3001\u675c\u98de\u3001\u76db\u590f\u4e94\u4e2a\u4eba\u8fde\u591c\u8d76\u53bb\u65b0\u829c\u3002</p><p>\u3000\u3000\u65b0\u829c\u4e0e\u5efa\u90ba\u4e4b\u95f4\u7684\u9ad8\u901f\u516c\u8def\u7ae3\u5de5\u901a\u8def\u4e4b\u540e\uff0c\u4ece\u5efa\u90ba\u57ce\u897f\u5357\u7684\u673a\u573a\u4e00\u8def\u9ad8\u901f\u516c\u8def\u8d76\u5230\u65b0\u829c\u5e02\u91cc\u90fd\u4e0d\u8981\u4e24\u4e2a\u5c0f\u65f6\uff0c\u884c\u7a0b\u6bd4\u5148\u524d\u7f29\u77ed\u4e86\u4e00\u534a\u90fd\u4e0d\u6b62\u3002</p><p>\u3000\u3000\u5230\u65b0\u829c\u5e02\u91cc\u5403\u8fc7\u665a\u996d\uff0c\u4e5f\u6ca1\u6709\u4f4f\u5728\u65b0\u829c\u5bb6\u91cc\uff0c\u800c\u662f\u8fde\u591c\u4e0a\u4e86\u5929\u4e91\u5c71\u3002</p><p>\u3000\u3000\u4e0e\u53bb\u5e74\u7684\u51b7\u6e05\u76f8\u6bd4\uff0c\u6e29\u6cc9\u6e56\u5cb8\u8fb9\u7684\u6e21\u5047\u6728\u5c4b\u90fd\u5df2\u7ecf\u5ba2\u6ee1\u4e3a\u60a3\u4e86\uff0c\u501f\u7740\u8425\u706b\u7684\u4eae\u5149\uff0c\u80fd\u591f\u770b\u89c1\u6e56\u7554\u7684\u89c2\u666f\u5e7f\u573a\u8fd8\u642d\u8bbe\u4e86\u4e00\u4e9b\u9732\u8425\u7684\u5e10\u7bf7\u2014\u2014\u524d\u9762\u7684\u6e29\u6cc9\u5bbe\u9986\u4e5f\u65e9\u5df2\u4eba\u6ee1\u4e3a\u60a3\u2014\u2014\u5230\u6e56\u7554\u89c2\u666f\u5e7f\u573a\u642d\u8bbe\u5e10\u7bf7\u4e5f\u662f\u8981\u6536\u8d39\u7684\u3002</p><p>\u3000\u3000\u675c\u98de\u4e0e\u76db\u590f\u4e24\u4eba\u4e4b\u524d\u5c31\u9884\u7559\u4e86\u4e00\u5ea7\u6e56\u7554\u6e21\u5047\u6728\u5c4b\uff0c\u89c1\u6709\u4eba\u642d\u8bbe\u5e10\u7bf7\uff0c\u5012\u89c9\u5f97\u7761\u5e10\u7bf7\u6709\u8da3\uff0c\u4e0d\u8fc7\u6765\u4e0d\u53ca\u51c6\u5907\uff0c\u6253\u4e2a\u54c8\u6b20\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u8fdb\u4e86\u6e21\u5047\u6728\u5c4b\uff0c\u8ba9\u9648\u5983\u84c9\u8ddf\u7740\u5f20\u606a\u3001\u5510\u5a67\u4f4f\u5230\u6e56\u5fc3\u5c9b\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u4e58\u8239\u8d9f\u8fc7\u6708\u5149\u4e0b\u7684\u6e29\u6cc9\u6e56\u6c34\uff0c\u4e0a\u4e86\u6e56\u5fc3\u5c9b\u7684\u6728\u5c4b\uff0c\u5cb8\u8fb9\u4e0e\u5012\u6620\u5728\u6e56\u6c34\u91cc\u7684\u8425\u706b\u4ee5\u53ca\u6c89\u5728\u6e56\u5e95\u7684\u94f6\u8272\u95ea\u5149\u661f\u8fb0\uff0c\u5c71\u4e0b\u7684\u4e16\u754c\u5df2\u7ecf\u662f\u6e05\u79cb\u4e86\uff0c\u6e29\u6cc9\u6e56\u6e29\u70ed\u7684\u6e56\u6c34\u5728\u6708\u5149\u4e0b\u8fd8\u84b8\u817e\u7740\u6c32\u6c24\u6c34\u6c7d\uff0c\u5730\u70ed\u4f7f\u5f97\u6e56\u5fc3\u5c9b\u7684\u6c14\u6e29\u4e5f\u8981\u9ad8\u8fc7\u5c71\u4e0b\u8bb8\u591a\u3002\u6362\u4e0a\u6cf3\u88c5\u7684\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u5c31\u50cf\u4e24\u6761\u6708\u5149\u4e0b\u8ff7\u4eba\u7684\u7f8e\u4eba\u9c7c\uff0c\u53ef\u60dc\u6cf3\u88c5\u592a\u4fdd\u5b88\u4e86\u4e9b\uff0c\u5f20\u606a\u7a7f\u7740\u77ed\u88e4\u8d64\u8eab\u8eba\u5728\u9732\u53f0\u7684\u6728\u5730\u677f\u4e0a\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u671b\u7740\u5929\u7a7a\u4e4b\u4e0a\u7684\u661f\u8fb0\u3002</p><p>\u3000\u3000\u505a\u4e86\u4e2a\u68a6\uff0c\u68a6\u89c1\u4e0d\u4ec5\u4ec5\u5510\u5a67\u3001\u9648\u5983\u84c9\u966a\u81ea\u5df1\u5230\u6e56\u5fc3\u5c9b\u6728\u5c4b\u6765\uff0c\u665a\u6674\u3001\u8bb8\u601d\u8fd8\u662f\u7fdf\u4e39\u9752\u4e5f\u4e00\u8d77\u4f4f\u8fdb\u6728\u5c4b\u6765\uff0c\u5927\u5bb6\u5176\u4e50\u6eb6\u6eb6\uff0c\u5973\u4eba\u4eec\u4e00\u4eba\u4e00\u95f4\u5367\u5ba4\uff0c\u72ec\u4ed6\u6ca1\u6709\uff0c\u5f20\u606a\u5728\u68a6\u91cc\u8fd8\u662f\u601d\u91cf\u662f\u4e0d\u662f\u591c\u91cc\u53ef\u4ee5\u968f\u4fbf\u8fdb\u54ea\u95f4\u5367\u5ba4\u4f11\u606f\uff0c\u6ca1\u7b49\u4ed6\u597d\u68a6\u505a\u5b8c\uff0c\u5374\u611f\u89c9\u8eab\u4f53\u7a81\u7136\u95f4\u60ac\u7a7a\u8d77\u6765\uff0c\u7741\u5f00\u773c\u775b\uff0c\u9f3b\u5c16\u5df2\u7ecf\u6d78\u5230\u6e56\u6c34\u91cc\u4e86\uff0c\u6323\u624e\u7740\u559d\u4e86\u4e24\u53e3\u6c34\u5728\u6e56\u6c34\u91cc\u624d\u7a33\u4f4f\u8eab\u5b50\uff0c\u63a2\u51fa\u5934\uff0c\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u8e72\u5728\u9732\u53f0\u8fb9\u4e0a\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u60f3\u8d77\u8bb8\u601d\u4e0e\u8bb8\u7ef4\u56de\u6d77\u5dde\u966a\u7236\u6bcd\u8fc7\u4e2d\u79cb\u4e86\uff0c\u665a\u6674\u9886\u7740\u82b7\u5f64\u5728\u91d1\u5c71\u4e0e\u5f90\u5b66\u5e73\u3001\u5468\u6dd1\u60e0\u56e2\u5706\uff0c\u7fdf\u4e39\u9752\u8bf4\u662f\u8fd9\u4e9b\u5e74\u6765\u7b2c\u4e00\u6b21\u56de\u5bb6\u966a\u7236\u6bcd\u8fc7\u4e2d\u79cb\u8282\uff0c\u5f20\u606a\u4e0d\u6653\u5f97\u5979\u662f\u4e0d\u662f\u5728\u8eb2\u81ea\u5df1\u3002</p><p>\u3000\u3000\u5510\u5a67\u624b\u8f7b\u8f7b\u7684\u6367\u4f4f\u5f20\u606a\u7684\u8138\u988a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4e24\u4e2a\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u5c11\u5973\u966a\u4f60\u591c\u6cf3\u5462\uff0c\u4f60\u7adf\u7136\u80fd\u7761\u7740\uff1f\u201d</p><p>\u3000\u3000\u4e4d\u843d\u5230\u6c34\u91cc\uff0c\u8fd8\u662f\u89c9\u5f97\u6709\u4e9b\u51b7\uff0c\u6e29\u6cc9\u6e56\u7684\u6c34\u57df\u8fd9\u4e48\u5e7f\uff0c\u6c34\u6e29\u4e5f\u53ea\u662f\u7565\u9ad8\u4e24\u4e09\u5ea6\u800c\u5df2\uff0c\u4e0d\u8fc7\u6574\u5929\u79cb\u5929\u7684\u6c34\u6e29\u90fd\u5c06\u7ef4\u6301\u8fd9\u6c34\u5e73\uff0c\u7a0d\u5fae\u8010\u51b7\u7684\u4eba\u53ef\u4ee5\u6e38\u6cf3\u4e00\u76f4\u5230\u5c71\u5317\u4e0b\u7b2c\u4e00\u573a\u96ea\uff0c\u5c71\u5357\u7684\u6c14\u6e29\u5373\u4f7f\u5230\u51ac\u5b63\u4e5f\u662f\u6e29\u6696\u5982\u6625\u7684\u3002\u4e5f\u4e0d\u6653\u5f97\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u4e24\u4eba\u4ec0\u4e48\u65f6\u5019\u5077\u5077\u7684\u4e0a\u5cb8\u51b7\u4e0d\u4e01\u7684\u5c06\u4ed6\u63a8\u4e0b\u6c34\uff0c\u5f20\u606a\u6293\u4f4f\u5510\u5a67\u7684\u5c0f\u624b\uff0c\u5c06\u5979\u4e5f\u62d6\u4e0b\u6c34\u6765\u3002</p><p>\u3000\u3000\u5510\u5a67\u8ba9\u9648\u5983\u84c9\u4e5f\u4e0b\u6c34\u6765\uff0c\u9648\u5983\u84c9\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u5cb8\u4e0a\u6b47\u4e00\u4f1a\u513f\u2026\u2026\u201d\u5750\u5728\u9732\u53f0\u5730\u677f\u4e0a\uff0c\u62ff\u5e72\u6bdb\u5dfe\u64e6\u6e7f\u6f09\u6f09\u7684\u957f\u53d1\uff0c\u88f8\u9732\u7684\u4fee\u957f\u53cc\u817f\u5728\u6708\u5149\u5c31\u50cf\u7389\u767d\u96d5\u523b\u7684\u827a\u672f\u54c1\u3002</p><p>\u3000\u3000\u9002\u5e94\u4e86\u6c34\u6e29\uff0c\u5f20\u606a\u5728\u6c34\u4e0b\u6402\u7740\u5510\u5a67\u5a07\u8f6f\u52a8\u4eba\u7684\u8eab\u4f53\u6709\u4e9b\u60c5\u70ed\uff0c\u5b09\u6c34\u65f6\u633a\u76f4\u7684\u4e0b\u4f53\u65f6\u4e0d\u65f6\u7ed9\u9876\u649e\u5230\u5510\u5a67\u7684\u81c0\u8179\u3002\u5510\u5a67\u4f38\u624b\u5728\u6c34\u4e0b\u6293\u4e86\u90a3\u73a9\u827a\u4e00\u628a\uff0c\u4fcf\u76ae\u7684\u7b11\u4e86\u7b11\uff0c\u53c8\u8fc5\u901f\u7684\u653e\u5f00\uff0c\u9644\u5230\u5f20\u606a\u7684\u8033\u8fb9\u95ee\u9053\uff1a\u201c\u4f60\u8111\u5b50\u91cc\u600e\u4e48\u7adf\u662f\u8fd9\u4e9b\u4e1c\u897f\uff1f\u5983\u84c9\u5728\u8fd9\u91cc\uff0c\u4f60\u53ef\u8981\u7ed9\u6211\u8001\u5b9e\u70b9\u3002\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u5e26\u5979\u4e00\u8d77\u8fc7\u6765\u554a\uff1f\u5e94\u8be5\u5c06\u5979\u4e22\u5cb8\u4e0a\u3002\u201d\u5f20\u606a\u6324\u51fa\u4e00\u8138\u7684\u75db\u82e6\uff0c\u8fd9\u6e56\u5fc3\u5c9b\u4e0a\u591c\u6df1\u4eba\u9759\uff0c\u6728\u5c4b\u7684\u9694\u97f3\u6548\u679c\u53c8\u4e0d\u662f\u5341\u5206\u7684\u597d\uff0c\u9648\u5983\u84c9\u4e5f\u7761\u5728\u8fd9\u91cc\uff0c\u5510\u5a67\u591c\u91cc\u80fd\u8ba9\u81ea\u5df1\u6cbe\u5979\u7684\u8eab\u5b50\u624d\u53eb\u9047\u5230\u9b3c\u5462\uff01</p><p>\u3000\u3000\u201c\u6211\u60f3\u8ddf\u4f60\u5728\u4e00\u8d77\uff0c\u4e5f\u60f3\u591a\u8ddf\u5983\u84c9\u5728\u4e00\u8d77\u554a\uff0c\u8c01\u53eb\u6211\u53ea\u6709\u56db\u5929\u7684\u5047\u671f\u5462\uff1f\u201d\u5510\u5a67\u8bf4\u9053\uff0c\u5634\u89d2\u8fb9\u9732\u51fa\u574f\u574f\u7684\u7b11\u5bb9\u3002</p><p>\u3000\u3000\u5510\u5a67\u5bf9\u6027\u4e8b\u4e0d\u662f\u5341\u5206\u7684\u70ed\u8877\uff0c\u5f20\u606a\u4e5f\u65e0\u53ef\u5948\u4f55\uff0c\u5728\u6c34\u91cc\u6402\u8fc7\u5979\u7684\u5a07\u8eaf\uff0c\u6084\u58f0\u8bf4\u9053\uff1a\u201c\u591c\u91cc\u7b49\u9648\u5983\u84c9\u7761\u4e86\uff0c\u4f60\u966a\u6211\u5230\u9662\u5b50\u540e\u6ce1\u6fa1\u2026\u2026\u201d\u6728\u5c4b\u80cc\u540e\u6709\u6728\u7bf1\u56f4\u8d77\u6765\u4e00\u5ea7\u7cbe\u81f4\u7684\u9662\u5b50\uff0c\u9732\u5929\u7684\u6d74\u6c60\u6709\u76f4\u63a5\u5f15\u63a5\u8fdb\u6765\u7684\u6e29\u6cc9\u70ed\u6c34\u53ef\u4ee5\u6ce1\u6fa1\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5728\u804a\u4ec0\u4e48\uff1f\u201d\u9648\u5983\u84c9\u8eab\u5b50\u503e\u8fc7\u6765\uff0c\u89c1\u5f20\u606a\u4e0e\u5510\u5a67\u624b\u6293\u7740\u9732\u53f0\u7684\u5730\u677f\u8fb9\u6cbf\u6084\u58f0\u8bf4\u8bdd\uff0c\u597d\u5947\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8bf4\u591c\u91cc\u600e\u4e48\u8d81\u7761\u89c9\u628a\u4f60\u7ed9\u5356\u6389\uff0c\u201d\u5510\u5a67\u7b11\u8d77\u7740\uff0c\u201c\u6211\u4eec\u53bb\u540e\u9662\u6ce1\u6fa1\u5427\u2026\u2026\u201d\u624b\u81c2\u6491\u7740\u9732\u53f0\u8fb9\u6cbf\u8981\u722c\u4e0a\u5cb8\uff0c\u5c0f\u811a\u4e0d\u7ecf\u610f\u95f4\u8e39\u5230\u5f20\u606a\u50f5\u76f4\u7684\u4e0b\u4f53\uff0c\u5f20\u606a\u75db\u5f97\u5dee\u70b9\u53c8\u6c89\u5230\u6e56\u6c34\u91cc\u53bb\u3002\u5510\u5a67\u529b\u6c14\u5c0f\uff0c\u722c\u4e86\u4e24\u4e0b\u53c8\u6ed1\u5230\u6c34\u91cc\uff0c\u5f20\u606a\u6258\u7740\u5979\u5706\u7fd8\u7684\u5c0f\u81c0\u8ba9\u5979\u4e0a\u4e86\u5cb8\uff0c\u8fd8\u5728\u5979\u7684\u5c0f\u81c0\u4e0a\u634f\u4e86\u4e24\u628a\uff0c\u5510\u5a67\u54af\u54af\u7b11\u7740\u62ac\u811a\u8981\u6765\u8e22\u4ed6\uff0c\u5f20\u606a\u6e38\u5230\u9732\u53f0\u7684\u53e6\u4e00\u4fa7\u4e0a\u4e86\u5cb8\uff0c\u8ddf\u5510\u5a67\u3001\u9648\u5983\u84c9\u4e00\u8d77\u53bb\u540e\u9662\u7684\u6e29\u6cc9\u6d74\u6c60\u91cc\u6ce1\u6fa1\u2014\u2014\u552f\u4e00\u53ef\u60dc\u662f\u5927\u5bb6\u90fd\u7a7f\u7740\u6cf3\u8863\u6ce1\u6fa1\uff0c\u611f\u89c9\u4e0d\u591f\u8212\u7545\u3002</p><p>\u3000\u3000\u5510\u5a67\u7f20\u7740\u8981\u5f20\u606a\u5230\u524d\u9762\uff0c\u597d\u8ba9\u5979\u4e0e\u9648\u5983\u84c9\u80fd\u8131\u6389\u675f\u7f1a\u6ce1\u6fa1\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u8fb9\u4e5f\u6ca1\u6709\u5916\u4eba\uff0c\u8131\u5c31\u8131\u5427\uff0c\u5927\u4e0d\u4e86\u6211\u966a\u4f60\u4eec\u4e00\u8d77\u8131\u2026\u2026\u201d\u5510\u5a67\u54af\u54af\u7b11\u7740\u8981\u6765\u6495\u5f20\u606a\u7684\u5634\uff0c\u5f20\u606a\u6b7b\u6d3b\u8d56\u7740\u5c31\u4e0d\u80af\u8d70\u3002</p><p>\u3000\u3000\u8fd9\u662f\u7528\u5375\u77f3\u4e0e\u6e56\u77f3\u780c\u6210\u7684\u504f\u4e09\u89d2\u5f27\u5f62\u9732\u5929\u6d74\u6c60\uff0c\u4e09\u4eba\u5404\u636e\u4e00\u89d2\u8eba\uff0c\u817f\u4f38\u76f4\uff0c\u516d\u53ea\u767d\u751f\u751f\u7684\u811a\u521a\u521a\u62b5\u5230\u6d74\u6c60\u4e2d\u5fc3\uff0c\u5f20\u606a\u62ac\u5934\u770b\u7740\u5f53\u7a7a\u7684\u6708\u8272\uff0c\u8eab\u5fc3\u90fd\u6c89\u6d78\u5728\u8fd9\u4e0e\u81ea\u7136\u76f8\u8c10\u7684\u6c14\u6c1b\u4e4b\u4e2d\uff0c\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u804a\u7740\u7410\u788e\u7684\u8bdd\u9898\uff0c\u8fd8\u811a\u62b5\u5728\u6c60\u5b50\u4e2d\u5fc3\u8e22\u6c34\u73a9\uff0c\u9648\u5983\u84c9\u7684\u811a\u5076\u5c14\u4e5f\u4f1a\u78b0\u5230\u5f20\u606a\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8981\u523b\u610f\u7684\u8eb2\u5f00\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u80fd\u5728\u8fd9\u91cc\u6ce1\u4e0a\u4e00\u8f88\u5b50\u5c31\u597d\u4e86\uff01\u201d\u5510\u5a67\u611f\u6168\u8bf4\u4e86\u4e00\u53e5\uff0c\u5728\u8fd9\u91cc\u6709\u7740\u65e0\u62d8\u65e0\u675f\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u53ea\u662f\u6ce1\u6fa1\u5417\uff1f\u201d\u5f20\u606a\u62ac\u8d77\u5934\u6765\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u90a3\u5c31\u7b97\u4e86\uff0c\u8fd8\u771f\u6ca1\u6709\u5438\u5f15\u529b\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u60f3\u505a\u4ec0\u4e48\uff1f\u201d\u5510\u5a67\u624b\u6cfc\u7740\u6c34\u6076\u5f62\u6076\u8272\u7684\u77aa\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6211\u60f3\u5c3f\u5c3f\uff0c\u662f\u4e0d\u662f\u53ef\u4ee5\u5c31\u8fd9\u4e48\u5c3f\uff1f\u201d\u5f20\u606a\u8146\u7740\u8138\u95ee\uff0c\u770b\u7740\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u5c16\u53eb\u7740\u8df3\u51fa\u6d74\u6c60\uff0c\u4ed6\u624d\u610f\u6001\u72b9\u8db3\u7684\u722c\u51fa\u6d74\u6c60\u3001\u4e0d\u987e\u4e24\u4e2a\u5973\u5b69\u5b50\u7684\u8ffd\u6253\u8fdb\u5c4b\u65b9\u4fbf\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u300010\u67084\u65e5\uff0c\u5f20\u77e5\u884c\u4e0e\u59bb\u5b50\u6881\u683c\u73cd\u4e5f\u5230\u5c71\u4e0a\u6765\u6e21\u5047\uff0c\u5e02\u59d4\u4e66\u8bb0\u6c5f\u4e0a\u5143\u4e0e\u59bb\u5b50\u5e26\u7740\u624d\u4e94\u5c81\u7684\u5b59\u5b50\u4e5f\u4e00\u540c\u4f4f\u8fdb\u6e29\u6cc9\u5bbe\u9986\u91cc\u3002</p><p>\u3000\u3000\u5929\u4e91\u5c71\u5404\u666f\u533a\u4eba\u6e38\u5982\u7ec7\uff0c\u4f46\u5bf9\u4ed6\u4eec\u662f\u6ca1\u6709\u591a\u5927\u5f71\u54cd\u4e86\uff0c\u4ed6\u4eec\u4f4f\u8fdb\u6e56\u7554\u7684\u6e21\u5047\u6728\u5c4b\u91cc\uff0c\u8fd8\u6709\u4fbf\u8863\u503c\u52e4\uff0c\u5012\u662f\u5b89\u5fc3\u3002</p><p>\u3000\u3000\u665a\u9910\u5c31\u662f\u5728\u6e56\u7554\u6e21\u5047\u6728\u5c4b\u91cc\u4eab\u53d7\uff0c\u52a0\u4e0a\u658c\u8001\u5927\uff0c\u6b63\u597d\u51d1\u6ee1\u4e00\u5706\u684c\uff0c\u7528\u8fc7\u9910\u5c31\u5750\u5728\u9732\u53f0\u4e0a\u804a\u5929\uff0c\u7b49\u4f1a\u513f\u6e56\u5bf9\u5cb8\u4e0a\u7684\u7a7a\u5730\u4e0a\u4f1a\u6709\u70df\u82b1\u8868\u6f14\uff0c\u6e56\u7554\u7684\u89c2\u666f\u5e7f\u573a\u91cc\u5750\u6ee1\u4e86\u6e38\u4eba\uff0c\u66f4\u591a\u7684\u6e38\u4eba\u662f\u8d2d\u4e70\u7ad9\u7968\u5165\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u4e5f\u89c4\u89c4\u77e9\u77e9\u7684\u5750\u5728\u90a3\u91cc\u966a\u7236\u4eb2\u4ee5\u53ca\u65b0\u829c\u5e02\u59d4\u4e66\u8bb0\u6c5f\u4e0a\u5143\u804a\u5929\uff0c\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u76db\u590f\u4e09\u4e2a\u5973\u5b69\u5b50\u5219\u966a\u6c5f\u4e0a\u5143\u7684\u5b59\u5b50\u5728\u6e56\u8fb9\u73a9\u800d\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u773c\u524d\u591c\u8272\u4e0b\u5f00\u9614\u7684\u6e29\u6cc9\u6e56\u5c71\u6c34\u98ce\u60c5\uff0c\u5fc3\u91cc\u60f3\u7740\u4e5d\u516b\u5e74\u6842\u6797\u9633\u6714\u5927\u6982\u4e5f\u624d\u521a\u521a\u6709\u7b79\u5212\u5c71\u6c34\u5b9e\u666f\u620f\u300a\u5370\u8c61\u5218\u4e09\u59d0\u300b\u7684\u5ff5\u5934\uff0c\u603b\u4e4b\u5faa\u7740\u5386\u53f2\u7684\u8f68\u8ff9\u53bb\u53d1\u5c55\uff0c\u8981\u7b49\u5230\u96f6\u4e09\u5e74\u624d\u4f1a\u6b63\u5f0f\u7684\u9762\u4e16\u3002\u56fd\u5916\u7684\u5b9e\u666f\u6f14\u51fa\u8fd9\u65f6\u5019\u5df2\u7ecf\u6709\u5f88\u591a\u4e86\uff0c\u6240\u8c13\u7684\u521b\u610f\u4e5f\u65e0\u6240\u8c13\u6284\u4e0d\u6284\u88ad\uff0c\u65b0\u829c\u7684\u5386\u53f2\u3001\u6c11\u95f4\u4f20\u8bf4\u4e5f\u6709\u5f88\u591a\u53ef\u4ee5\u6316\u6398\u7684\u5730\u65b9\uff0c\u5173\u952e\u8fd8\u662f\u8981\u627e\u5230\u6709\u540d\u6c14\u3001\u6709\u5b9e\u529b\u7684\u5bfc\u6f14\u6765\u7b79\u5212\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e5f\u6ca1\u6709\u5f3a\u8c03\u8fd9\u91cc\u53ef\u4ee5\u641e\u5c71\u6c34\u5b9e\u666f\u6f14\u51fa\uff0c\u603b\u4e4b\u5929\u4e91\u5c71\u666f\u533a\u8fd8\u8981\u6301\u7eed\u7684\u5f00\u53d1\uff0c\u65c5\u6e38\u8d44\u6e90\u5e94\u8be5\u66f4\u4e30\u5bcc\u591a\u5f69\uff0c\u8ba9\u6e38\u4eba\u5728\u4e0d\u540c\u7684\u5b63\u8282\u6709\u66f4\u591a\u7684\u9009\u62e9\uff0c\u66f4\u4e0d\u5e94\u8be5\u5c40\u9650\u5728\u89c2\u666f\u4e0a\uff0c\u5f88\u591a\u4eba\u51fa\u6765\u662f\u6e38\u73a9\u7684\uff0c\u8981\u63d0\u4f9b\u4e30\u5bcc\u591a\u5f69\u7684\u5a31\u4e50\u9879\u76ee\u3001\u6f14\u51fa\u7b49\u7b49\u2014\u2014\u5728\u57fa\u7840\u8bbe\u65bd\u9010\u6e10\u5b8c\u5584\u4e4b\u540e\uff0c\u63a5\u4e0b\u6765\u7684\u65c5\u6e38\u8d44\u6e90\u5f00\u53d1\u5c31\u53ef\u4ee5\u5f15\u5165\u673a\u6784\u4e0e\u4e2a\u4eba\u7684\u6295\u8d44\u3002</p><p>\u3000\u3000\u968f\u610f\u7684\u8bf4\u7740\u8bdd\uff0c\u70df\u82b1\u8868\u6f14\u51c6\u5907\u5f00\u59cb\uff0c\u7480\u74a8\u7684\u70df\u82b1\u6216\u5982\u6ee1\u5929\u7e41\u661f\u3001\u6216\u5982\u7e41\u82b1\u76db\u5f00\uff0c\u5c06\u8fd9\u5c71\u3001\u8fd9\u6c34\u63a9\u6620\u5f97\u82e5\u9690\u82e5\u73b0\u3001\u58ee\u7f8e\u79c0\u4e3d\u3002</p><p>\u3000\u3000\u8981\u8bf41\u53f7\u90a3\u5929\u591c\u91cc\u77e5\u9053\u5929\u4e91\u5c71\u666f\u533a\u7684\u6e38\u4eba\u6570\u636e\u4e4b\u540e\u8fd8\u4e0d\u6562\u592a\u5fd7\u5f97\u610f\u6ee1\uff0c\u4eca\u5929\u5df2\u7ecf\u662f\u7b2c\u56db\u5929\u4e86\uff0c\u8fdb\u5165\u9ec4\u91d1\u5468\u5047\u671f\u7684\u6700\u9ad8\u5cf0\u671f\uff0c\u5e02\u5047\u65e5\u529e\u5bf9\u5168\u5e02\u666f\u533a\u4ee5\u53ca\u65c5\u6e38\u4f4f\u5bbf\u5730\u70b9\u7684\u524d\u671f\u521d\u6b65\u7edf\u8ba1\u5df2\u7ecf\u51fa\u6765\u4e86\uff0c\u5b8c\u5168\u53ef\u4ee5\u5f88\u80af\u5b9a\u7684\u8bf4\u65b0\u829c\u5e02\u53d1\u5c55\u65c5\u6e38\u7ecf\u6d4e\u6253\u4e86\u4e2a\u6f02\u4eae\u7684\u7ffb\u8eab\u4ed7\u3002</p><p>\u3000\u3000\u524d\u4e09\u65e5\uff0c\u5929\u4e91\u5c71\u7efc\u5408\u666f\u533a\u5171\u63a5\u5f85\u6e38\u5ba2\u5341\u516d\u4e07\u4eba\u6b21\uff0c\u6574\u4e2a\u9ec4\u91d1\u5468\u9884\u8ba1\u63a5\u5f85\u65c5\u5ba2\u8d85\u8fc7\u4e09\u5341\u4e07\uff0c\u5404\u7c7b\u95e8\u7968\u6536\u5165\u8d85\u8fc7\u4e00\u5343\u516d\u767e\u4e07\uff0c\u4e4b\u524d\u600e\u4e48\u80fd\u60f3\u5230\u5929\u4e91\u5c71\u91cc\u5728\u5165\u591c\u4e4b\u540e\u8fd8\u4f1a\u6709\u8d85\u8fc7\u516b\u5343\u7684\u6e38\u5ba2\u5728\u5c71\u4e0a\u6d88\u8d39\uff0c\u53ef\u60dc\u5c71\u4e0a\u7684\u4f4f\u5bbf\u6761\u4ef6\u6709\u9650\uff0c\u53ea\u80fd\u4f9b\u4e0d\u5230\u5341\u5206\u4e4b\u4e00\u7684\u5e8a\u4f4d\uff0c\u5927\u90e8\u5206\u7684\u65c5\u5ba2\u8981\u5728\u6df1\u591c\u4e4b\u524d\u8fd4\u56de\u5230\u5c71\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u4e5f\u5c31\u662f\u8bf4\uff0c\u6839\u636e\u56fd\u52a1\u9662\u7684\u5b89\u6392\uff0c\u4e00\u5e74\u4e2d\u52b3\u52a8\u8282\u3001\u6625\u8282\u3001\u56fd\u5e86\u8282\u4e09\u4e2a\u9ec4\u91d1\u5468\uff0c\u5929\u4e91\u5c71\u7efc\u5408\u666f\u533a\u4ec5\u4e8c\u5341\u4e00\u5929\u7684\u95e8\u7968\u6536\u5165\u5c31\u5c06\u7a81\u7834\u56db\u5343\u4e07\uff0c\u8fd9\u8fd8\u4e0d\u8ba1\u7b97\u666f\u533a\u5185\u7684\u5a31\u4e50\u3001\u9910\u996e\u3001\u5546\u4e1a\u3001\u4ea4\u901a\u7b49\u65b9\u9762\u7684\u6536\u5165\uff0c\u8fd9\u65f6\u5019\u8c01\u8fd8\u4f1a\u8df3\u51fa\u6765\u8bf4\u5f20\u77e5\u884c\u5f80\u5929\u4e91\u5c71\u7efc\u5408\u666f\u533a\u5185\u6295\u5165\u7684\u56db\u4e2a\u4ebf\u662f\u4e71\u82b1\u94b1\uff1f</p></article>"}}}]);
