(window.webpackJsonp=window.webpackJsonp||[]).push([[1123],{1146:function(p){p.exports={chapterTitle:null,title:"\u7b2c919\u7ae0 \u4e09\u4e2a\u4eba\u7684\u4e16\u754c",content:"<article><h2>\u7b2c919\u7ae0 \u4e09\u4e2a\u4eba\u7684\u4e16\u754c</h2><p>\u3000\u3000\u665a\u6674\u5f88\u5feb\u5c31\u7ed9\u63a8\u4e0a\u4e91\u7aef\uff0c\u4f3c\u4e4e\u5468\u8eab\u6bcf\u4e00\u5904\u7ec6\u80de\u90fd\u5f97\u5230\u6ecb\u6da6\uff0c\u5982\u4e91\u79c0\u53d1\u51cc\u4e71\u7684\u906e\u4f4f\u6f6e\u7ea2\u5982\u671d\u971e\u7684\u79c0\u8138\uff0c\u5a07\u5598\u672a\u6b62\uff0c\u9ad8\u8038\u7684\u80f8\u90e8\u8d77\u4f0f\u7740\uff0c\u8fd9\u65f6\u5019\u624d\u89c9\u5f97\u6709\u4e9b\u96be\u582a\uff0c\u6ca1\u6709\u52c7\u6c14\u5c06\u906e\u4f4f\u8138\u7684\u79c0\u53d1\u64a9\u5f00\uff0c\u5f20\u606a\u8fd9\u5bb6\u4f19\u624b\u8fd8\u5728\u5979\u9ad8\u8038\u9971\u6ee1\u7684\u4e73\u4e0a\u63c9\u6413\u7740\uff0c\u8212\u670d\u662f\u5341\u5206\u7684\u8212\u670d\uff0c\u4e0d\u8fc7\u6062\u590d\u601d\u8003\u80fd\u529b\u4e4b\u540e\u5c31\u5728\u60f3\u521a\u624d\u600e\u4e48\u5c31\u8fd9\u4e48\u8ddf\u8bb8\u601d\u63d0\u51fa\u8fd9\u4e48\u5927\u80c6\u7684\u8981\u6c42\uff1f\u5173\u952e\u8fd9\u5bb6\u4f19\u8fd8\u6ca1\u6709\u5f97\u5230\u6ee1\u8db3\uff0c\u575a\u786c\u633a\u76f4\u7684\u6233\u5728\u7a74\u53e3\u91cc\uff0c\u6da8\u5f97\u5389\u5bb3\uff0c\u53c8\u4e0d\u80fd\u5c06\u8fd9\u5bb6\u4f19\u63a8\u5f00\uff0c\u542c\u89c1\u6d74\u5ba4\u95e8\u6253\u5f00\u7684\u58f0\u97f3\uff0c\u7f9e\u4e0d\u80dc\u7f9e\uff0c\u6293\u4f4f\u6795\u5934\u76d6\u4f4f\u8138\uff0c\u4e0b\u8eab\u53c8\u6781\u81f4\u7684\u654f\u611f\uff0c\u5982\u6f6e\u6c34\u4f3c\u7684\u60c5\u6b32\u53c8\u63a8\u6d8c\u51fa\u6765\uff0c\u76f4\u4e4e\u5c06\u90a3\u6e05\u9192\u7684\u610f\u8bc6\u6df9\u6ca1\u6389\u3002</p><p>\u3000\u3000\u6d74\u5ba4\u7684\u95e8\u662f\u7ed9\u98ce\u5439\u5f00\u7684\uff0c\u542c\u7740\u7ec6\u7ec6\u5982\u98ce\u58f0\u7684\u547b\u541f\u58f0\u4f20\u6765\uff0c\u8bb8\u601d\u5012\u662f\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u81ea\u5df1\u9677\u5165\u90a3\u5947\u5999\u7684\u5feb\u611f\u4e4b\u4e2d\uff0c\u4e5f\u662f\u8fd9\u822c\u7684\u547b\u541f\u5417\uff1f\u5fc3\u95f4\u6ca1\u6709\u4ec0\u4e48\u5f3a\u70c8\u7684\u9178\u610f\uff0c\u4f3c\u4e4e\u8fd8\u6709\u5f02\u6837\u7684\u523a\u6fc0\u5728\u5fc3\u95f4\u6d41\u8d9f\uff0c\u60f3\u5077\u5077\u7684\u770b\u4e00\u773c\uff0c\u603b\u662f\u6ca1\u6709\u52c7\u6c14\u7ad9\u8d77\u6765\uff0c\u53c8\u60f3\u665a\u6674\u521a\u624d\u7ad9\u5728\u95e8\u53e3\u770b\u5979\u4e0e\u5f20\u606a\u6b22\u7231\u65f6\u4e5f\u662f\u8fd9\u79cd\u611f\u53d7\u5417\uff1f</p><p>\u3000\u3000\u542c\u89c1\u5f20\u606a\u55d3\u7ba1\u91cc\u53d1\u51fa\u6765\u4e00\u58f0\u538b\u6291\u4f4e\u543c\uff0c\u8bb8\u601d\u60ac\u7740\u7684\u5fc3\u9661\u7136\u8ddf\u7740\u4e00\u677e\uff0c\u624d\u89c9\u5f97\u4e24\u817f\u4e4b\u95f4\u53c8\u6d41\u51fa\u4e86\u4e9b\u6c34\u8ff9\uff0c\u5fc3\u91cc\u9a82\u4e86\u81ea\u5df1\u4e00\u58f0\uff0c\u72b9\u8c6b\u7740\u4ec0\u4e48\u65f6\u5019\u8d70\u51fa\u53bb\u5408\u9002\uff0c\u5374\u89c1\u665a\u6674\u4e00\u624b\u906e\u7740\u4e30\u4e73\u3001\u4e00\u624b\u6342\u5728\u4e24\u817f\u4e4b\u95f4\u5fae\u8eac\u7740\u8eab\u5b50\u8d70\u8fdb\u6765\uff0c\u8fd8\u6709\u767d\u8272\u6db2\u4f53\u987a\u7740\u5927\u817f\u5f80\u4e0b\u6ef4\u3002</p><p>\u3000\u3000\u665a\u6674\u770b\u5230\u6709\u6db2\u4f53\u987a\u7740\u5927\u817f\u5f80\u4e0b\u6ef4\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u62ac\u5934\u770b\u4e86\u5750\u5728\u6d74\u7f38\u8fb9\u6cbf\u4e0a\u7684\u8bb8\u601d\u4e00\u773c\uff0c\u53c8\u76f8\u89c6\u7b11\u4e86\u8d77\u6765\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u9163\u7545\u6dcb\u6f13\u7684\u8eba\u5728\u5e8a\u4e0a\uff0c\u542c\u7740\u6d74\u5ba4\u91cc\u7684\u52a8\u9759\uff0c\u7247\u523b\u4e4b\u95f4\u542c\u89c1\u82b1\u6d12\u55b7\u5934\u653e\u6c34\u7684\u58f0\u97f3\uff0c\u542c\u89c1\u8bb8\u601d\u4e0e\u665a\u6674\u5728\u6d74\u5ba4\u91cc\u4f4e\u58f0\u7ec6\u8bed\uff0c\u53ea\u662f\u542c\u4e0d\u6e05\u695a\u5979\u4eec\u5728\u8bf4\u8bdd\uff0c\u4ed6\u7ad9\u8d77\u6765\u63a8\u95e8\u8d70\u8fdb\u6d74\u5ba4\uff0c\u53c8\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u653e\u6c34\u58f0\u6ca1\u6709\u505c\uff0c\u4e5f\u6ca1\u89c1\u8bb8\u601d\u4e0e\u665a\u6674\u8d70\u51fa\u6765\uff0c\u5f20\u606a\u4ece\u5e8a\u4e0a\u722c\u8d77\u6765\uff0c\u63a8\u5f00\u6d74\u5ba4\u91cc\u7684\u95e8\uff0c\u89c1\u8bb8\u601d\u4e0e\u665a\u6674\u90fd\u5750\u5728\u6d74\u7f38\u91cc\u51c6\u5907\u6d17\u6fa1\uff0c\u6c34\u624d\u653e\u5230\u4e00\u534a\uff0c\u80f8\u90e8\u9732\u51fa\u6765\uff0c\u8fd8\u6ca1\u6709\u7b49\u5f20\u606a\u8ba4\u771f\u7684\u6bd4\u8f83\u4e24\u4e2a\u5973\u4eba\u8eab\u6750\u7684\u5f02\u540c\u5904\u6765\uff0c\u8bb8\u601d\u4e0e\u665a\u6674\u90fd\u968f\u624b\u6284\u8d77\u80fd\u62ff\u5230\u4e1c\u897f\u671d\u4ed6\u4e22\u8fc7\u6765\uff0c\u5c16\u53eb\u7740\uff1a\u201c\u4f60\u4e2a\u6d51\u7403\uff0c\u4e0d\u8981\u8fc7\u6765\uff01\u201d\u5f20\u606a\u80cc\u7740\u8eab\u5b50\u4e0d\u987e\u4e1c\u897f\u4e71\u4e22\u8fc7\u6765\u671d\u6d74\u7f38\u9000\u53bb\uff0c\u4e00\u5c41\u80a1\u5750\u5230\u6d74\u7f38\u7684\u8fb9\u7f18\u4e0a\uff0c\u8eab\u5b50\u671d\u6c34\u91cc\u5012\u53bb\uff1a\u201c\u521a\u624d\u6211\u51fa\u90a3\u4e48\u591a\u6c57\uff0c\u4e5f\u4e0d\u8bf4\u8ba9\u6211\u5148\u6d17\u3002\u201d\u6b7b\u6d3b\u8d56\u5728\u6d74\u7f38\u91cc\u4e0d\u80af\u8d77\u6765\u3002\u665a\u6674\u3001\u8bb8\u601d\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u62ff\u4ed6\u6ca1\u6709\u529e\u6cd5\uff0c\u4e5f\u66f4\u62c5\u5fc3\u4ed6\u5c0f\u817f\u7684\u4f24\u53e3\u6d78\u4e86\u6c34\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u817f\u8df7\u5728\u6d74\u7f38\u8fb9\u4e0a\u53d1\u5f00\u7eb1\u5e03\u770b\u4e86\u770b\uff0c\u4f24\u53e3\u7ed3\u4e86\u75a4\uff0c\u6ca1\u6709\u4ec0\u4e48\u5927\u788d\u4e86\uff0c\u53ea\u662f\u989d\u5934\u4e0a\u8fd8\u4f1a\u7559\u4e0b\u4e00\u9053\u75a4\u4e00\u65f6\u6d88\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u5e74\u8f7b\u7684\u8eab\u4f53\u60c5\u6b32\u4e5f\u662f\u65fa\u76db\u7684\uff0c\u53ef\u60dc\u8bb8\u601d\u4e0e\u665a\u6674\u5306\u5ffd\u6d17\u8fc7\u6fa1\u5c31\u62ab\u7740\u6d74\u5dfe\u9003\u4e86\u51fa\u53bb\uff0c\u4e0d\u6562\u518d\u642d\u7406\u5f20\u606a\uff0c\u5f20\u606a\u53ea\u80fd\u7d22\u7136\u65e0\u5473\u7684\u5728\u6d74\u7f38\u91cc\u6ce1\u4e86\u534a\u4e2a\u5c0f\u65f6\u624d\u4e0b\u4e86\u697c\uff0c\u65e9\u9910\u51c6\u5907\u597d\u5728\u9910\u684c\u4e0a\uff0c\u53ea\u662f\u4e24\u4e2a\u5973\u4eba\u90fd\u4e0d\u6653\u5f97\u53bb\u4e86\u54ea\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u62e8\u4e86\u8bb8\u601d\u7684\u624b\u673a\uff0c\u542c\u89c1\u5979\u7684\u624b\u673a\u5728\u4e66\u623f\u91cc\u54cd\uff0c\u53c8\u62e8\u4e86\u665a\u6674\u7684\u624b\u673a\uff0c\u624d\u77e5\u9053\u4e24\u4e2a\u5973\u4eba\u4e00\u8d77\u53bb\u901b\u8857\u4e86\u3002</p><p>\u3000\u3000\u60f3\u7740\u65e9\u6668\u7684\u8352\u5510\u4e8b\uff0c\u8bb8\u601d\u4e0e\u665a\u6674\u867d\u8bf4\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u96be\u582a\uff0c\u8fd8\u662f\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u8bf4\u6765\u4e5f\u5947\u602a\uff0c\u4e24\u4e2a\u5973\u4eba\u6ca1\u6709\u60f3\u7740\u76f8\u4e92\u8eb2\u5f00\uff0c\u5374\u662f\u5728\u4e0b\u610f\u8bc6\u91cc\u8981\u8eb2\u5f00\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u5348\u5728\u4e66\u623f\u91cc\u5904\u7406\u4e86\u4e00\u4e9b\u90ae\u4ef6\uff0c\u5230\u4e2d\u5348\u611f\u89c9\u5230\u809a\u5b50\u6709\u4e9b\u997f\u4e86\uff0c\u6253\u7535\u8bdd\u7ed9\u665a\u6674\u95ee\u5979\u4eec\u4eba\u5728\u54ea\u91cc\uff0c\u6253\u7b97\u627e\u4e2a\u5730\u65b9\u4e00\u8d77\u5403\u4e2d\u996d\u3002\u665a\u6674\u4e0e\u8bb8\u601d\u8fd9\u65f6\u5019\u66f4\u662f\u4e0d\u80af\u8ddf\u5f20\u606a\u4e00\u8d77\u5728\u5916\u9762\u7684\u9910\u9986\u91cc\u5403\u996d\uff0c\u8ba9\u5f20\u606a\u5446\u5728\u5b85\u5b50\u91cc\u4e0d\u8981\u51fa\u6765\uff0c\u5979\u4eec\u4e70\u5feb\u9910\u5e26\u56de\u6765\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4e0e\u665a\u6674\u56de\u6765\u65f6\uff0c\u5f20\u606a\u63a5\u5230\u7fdf\u4e39\u9752\u4ece\u5efa\u90ba\u6253\u8fc7\u6765\u7684\u7535\u8bdd\u3002\u4eca\u5929\u662f\u56fd\u5e86\u957f\u5047\u7684\u6700\u540e\u4e00\u5929\uff0c\u7fdf\u4e39\u9752\u4e0a\u5348\u56de\u5230\u5efa\u90ba\uff0c\u6253\u7535\u8bdd\u901a\u77e5\u5f20\u606a\u4e00\u58f0\uff0c\u53c8\u95ee\u5f20\u606a\u51e0\u65f6\u56de\u5efa\u90ba\u3002</p><p>\u3000\u3000\u201c\u662f\u4e39\u9752\u5417\uff1f\u201d\u665a\u6674\u542c\u7740\u7535\u8bdd\u4f20\u6765\u7684\u58f0\u97f3\u50cf\u662f\u7fdf\u4e39\u9752\uff0c\u95ee\u4e86\u4e24\u811a\u8df7\u5728\u4e66\u684c\u4e0a\u7684\u5f20\u606a\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u5979\u4e0a\u5348\u521a\u56de\u5efa\u90ba\uff0c\u8fd9\u624d\u6253\u7535\u8bdd\u8fc7\u6765\u3002\u201d\u5f20\u606a\u70b9\u4e86\u70b9\u5934\uff0c\u8ddf\u665a\u6674\u8bf4\u8bdd\u65f6\uff0c\u4e5f\u6ca1\u6709\u523b\u610f\u7684\u6342\u4f4f\u8bdd\u7b52\u4e0d\u8ba9\u7535\u8bdd\u90a3\u5934\u7684\u7fdf\u4e39\u9752\u542c\u89c1\u3002\u665a\u6674\u70b9\u70b9\u5934\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u5f20\u606a\u53c8\u8ddf\u7535\u8bdd\u90a3\u5934\u7684\u7fdf\u4e39\u9752\u8bf4\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7d27\u8981\u7684\u4e8b\u60c5\uff0c\u90a3\u6211\u540e\u5929\u56de\u5efa\u90ba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54a6\uff0c\u5979\u600e\u4e48\u4e0d\u5230\u6d77\u5dde\u6765\uff1f\u201d\u8bb8\u601d\u95ee\u4e86\u4e00\u58f0\uff0c\u7591\u60d1\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u7fdf\u4e39\u9752\u505a\u8d3c\u5fc3\u865a\uff0c\u5979\u4f5c\u4e3a\u5f20\u606a\u7684\u52a9\u7406\uff0c\u7ed3\u675f\u4f11\u5047\u5f53\u7136\u662f\u7b2c\u4e00\u65f6\u95f4\u8d76\u5230\u6d77\u5dde\u6765\u8ddf\u5f20\u606a\u6c47\u5408\uff0c\u665a\u6674\u4e5f\u62ac\u5934\u7591\u60d1\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u624b\u5934\u7684\u4e8b\u60c5\u662f\u8d8a\u6765\u8d8a\u7e41\u7410\uff0c\u6211\u6253\u7b97\u518d\u627e\u51e0\u4e2a\u4eba\u5e2e\u7fdf\u4e39\u9752\u5904\u7406\u2014\u2014\u5efa\u90ba\u8fd8\u6709\u4e9b\u4e8b\u60c5\u8981\u7fdf\u4e39\u9752\u7559\u5728\u90a3\u91cc\u5904\u7406\uff0c\u201d\u5f20\u606a\u627e\u501f\u53e3\u968f\u624b\u62c8\u6765\uff0c\u201c\u6709\u4e86\u80fd\u5e72\u7684\u4e0b\u5c5e\uff0c\u8001\u677f\u624d\u80fd\u5fd9\u91cc\u5077\u95f2\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5728\u7535\u8bdd\u90a3\u5934\u542c\u5230\u665a\u6674\u4e0e\u8bb8\u601d\u7684\u58f0\u97f3\uff0c\u77e5\u9053\u5f20\u606a\u56de\u6d77\u5dde\u8fd9\u4e24\u5929\u90fd\u8981\u8ddf\u5979\u4eec\u4fe9\u5728\u4e00\u8d77\uff0c\u6ca1\u6709\u8ddf\u5f20\u606a\u5728\u7535\u8bdd\u91cc\u591a\u8bf4\u4ec0\u4e48\uff0c\u5c31\u6302\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u4e0b\u5348\uff0c\u5468\u6dd1\u60e0\u6d3e\u8f66\u9001\u82b7\u4e39\u56de\u6d77\u5dde\uff0c\u957f\u5047\u7ed3\u675f\uff0c\u82b7\u5f64\u660e\u5929\u8fd8\u8981\u4e0a\u5b66\u3002\u5f20\u606a\u60f3\u4e0e\u665a\u6674\u3001\u8bb8\u601d\u518d\u4e00\u6b21\u4e09\u4eba\u540c\u5e8a\u5171\u6795\u7684\u7f8e\u597d\u613f\u671b\u5c31\u843d\u5728\u7a7a\u5904\u3001\u65e0\u9650\u671f\u7684\u63a8\u540e\u4e86\u3002\u665a\u4e0a\u4e00\u8d77\u5403\u8fc7\u996d\uff0c\u665a\u6674\u5e26\u7740\u82b7\u4e39\u4f4f\u8fdb\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u91cc\uff0c\u5f20\u606a\u4e0e\u8bb8\u601d\u56de\u5230\u4e39\u4e95\u5df7\u7684\u5b85\u5b50\u91cc\uff0c\u5728\u4e8c\u4eba\u4e16\u754c\u91cc\u9ed1\u5929\u660f\u5730\u7684\u98a0\u9e3e\u5012\u51e4\u3001\u6781\u5c3d\u6b22\u6109\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56fd\u5e86\u957f\u5047\u521a\u8fc7\uff0c\u8ba1\u5212\u53d1\u5c55\u59d4\u8fd0\u884c\u8425\u526f\u5c40\u957f\u5218\u95fb\u6d9b\uff08\u6b63\u5c40\u7ea7\uff09\u5c06\u5230\u6d77\u5dde\u62c5\u4efb\u5e02\u59d4\u4e66\u8bb0\u7684\u6d88\u606f\u5c31\u4f20\u4e86\u51fa\u6765\u3002\u867d\u8bf4\u5218\u95fb\u6d9b\u5c65\u5386\u4e2d\u6709\u591a\u6b21\u5230\u5730\u65b9\u6302\u804c\u953b\u70bc\u7684\u7ecf\u5386\uff0c\u4f46\u662f\u7a81\u7136\u95f4\u80fd\u7a7a\u964d\u5230\u8fd1\u5e74\u6765\u7ecf\u6d4e\u975e\u5e38\u6d3b\u8dc3\u7684\u6d77\u5dde\u62c5\u4efb\u4e00\u628a\u624b\uff0c\u4e1c\u6d77\u7701\u5185\u90e8\u5bf9\u6b64\u8fd8\u662f\u8ba8\u8bba\u7eb7\u7eb7\uff0c\u89c9\u5f97\u6709\u4e9b\u96be\u4ee5\u7406\u89e3\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u4e1c\u6d77\u7701\uff0c\u4e0e\u5218\u95fb\u6d9b\u540c\u4e00\u6279\u8fd8\u6709\u591a\u540d\u4e2d\u592e\u90e8\u59d4\u53f8\u5c40\u7ea7\u5b98\u5458\u5c06\u88ab\u6d3e\u9063\u5230\u5730\u65b9\u62c5\u4efb\u515a\u653f\u9886\u5bfc\uff0c\u53ef\u4ee5\u8bf4\u662f\u4e2d\u592e\u9010\u6b65\u52a0\u5f3a\u5bf9\u5730\u65b9\u7684\u63a7\u5236\u529b\uff0c\u7279\u522b\u662f\u7ecf\u6d4e\u57fa\u7840\u597d\u3001\u53d1\u5c55\u8fc5\u901f\u7684\u5730\u533a\u3002\u5bf9\u5730\u65b9\u6765\u8bf4\uff0c\u5f80\u597d\u5904\u60f3\uff0c\u4e5f\u53ef\u4ee5\u501f\u673a\u52a0\u5f3a\u4e0e\u4e2d\u592e\u90e8\u59d4\u7684\u8054\u7cfb\uff0c\u5f53\u7136\u4e5f\u6709\u4e0d\u4fbf\u5229\u7684\u5730\u65b9\uff0c\u5730\u65b9\u4e0a\u7684\u653f\u6cbb\u5747\u8861\u5f88\u5bb9\u6613\u7ed9\u7a7a\u964d\u519b\u56e2\u7834\u574f\u6389\u3002</p><p>\u3000\u3000\u56fd\u5e86\u957f\u5047\u8fc7\u540e\uff0c\u5f20\u606a\u53bb\u4e86\u4e00\u8d9f\u9999\u6e2f\u3002</p><p>\u3000\u3000\u79d8\u5bc6\u8bbe\u7acb\u7684\u5bf9\u51b2\u57fa\u91d1\u5728\u5e74\u521d\u9646\u7eed\u6d17\u51fa\u4e00\u4ebf\u591a\u7f8e\u5143\u7684\u5229\u6da6\u4e4b\u540e\uff0c\u4fdd\u7559\u5728\u5bf9\u51b2\u57fa\u91d1\u8d26\u6237\u91cc\u7684\u8d44\u91d1\u4ecd\u9ad8\u8fbe\u4e24\u4ebf\u7f8e\u5143\uff0c\u4e94\u6708\u8d77\u53c8\u9646\u7eed\u51fa\u51fb\u5370\u5c3c\u3001\u97e9\u56fd\u3001\u4fc4\u7f57\u65af\u7b49\u56fd\u91d1\u878d\u7a7a\u5934\u5e02\u573a\uff0c\u672c\u91d1\u52a0\u76c8\u5229\u53c8\u8fc5\u901f\u589e\u52a0\u5230\u56db\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u770b\u4e0a\u53bb\u4f3c\u4e4e\u5230\u4e86\u6700\u6076\u52a3\u7684\u5173\u5934\uff0c\u4f46\u4e5f\u4ee3\u8868\u80fd\u8086\u610f\u6e14\u5229\u7684\u7a7a\u5934\u5e02\u573a\u5df2\u7ecf\u7ec8\u7ed3\uff0c\u63a5\u4e0b\u6765\u5219\u662f\u65f6\u95f4\u66f4\u6f2b\u957f\u7684\u6062\u590d\u671f\uff0c\u6295\u673a\u5e02\u573a\u7684\u76c8\u5229\u673a\u4f1a\u867d\u7136\u8fd8\u6709\uff0c\u4f46\u98ce\u9669\u4e5f\u968f\u4e4b\u52a0\u5267\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u53bb\u9999\u6e2f\u5c31\u662f\u4e00\u8d77\u8ba8\u8bba\u5bf9\u51b2\u57fa\u91d1\u65e5\u540e\u7684\u53d1\u5c55\u95ee\u9898\uff0c\u6295\u673a\u5e02\u573a\u76c8\u5229\u673a\u4f1a\u5de8\u5927\uff0c\u4f46\u662f\u65e0\u8bba\u662f\u5f20\u606a\u3001\u53f6\u5efa\u658c\uff0c\u8fd8\u662f\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u90fd\u5bf9\u91d1\u878d\u6295\u673a\u6ca1\u6709\u7279\u522b\u5927\u7684\u5174\u8da3\u3002</p><p>\u3000\u3000\u5bf9\u51b2\u57fa\u91d1\u77ed\u77ed\u4e00\u5e74\u534a\u7684\u65f6\u95f4\u91cc\u83b7\u5229\u8d85\u8fc7\u4e09\u4ebf\u7f8e\u5143\uff0c\u540c\u4e00\u65f6\u671f\u9526\u6e56\u7684\u51c0\u76c8\u5229\u4e5f\u53ea\u6709\u6b64\u6570\uff0c\u4f46\u662f\u5bf9\u51b2\u57fa\u91d1\u83b7\u5f97\u7684\u53ea\u662f\u9700\u8981\u82b1\u8d39\u5927\u91cf\u65f6\u95f4\u6d17\u51fa\u6765\u7684\u4e00\u7b14\u5de8\u989d\u8d44\u91d1\u800c\u5df2\uff0c\u800c\u9526\u6e56\u5728\u8fc7\u53bb\u4e00\u5e74\u534a\u7684\u65f6\u95f4\u91cc\uff0c\u4ece\u6d77\u5dde\u4e00\u9685\u8d70\u51fa\u6765\uff0c\u5df2\u7ecf\u53d1\u5c55\u6210\u5728\u884c\u4e1a\u5185\u62e5\u6709\u4e3e\u8db3\u8f7b\u91cd\u5730\u4f4d\u7684\u5e9e\u5927\u4f53\u7cfb\uff0c\u5176\u4ef7\u503c\u8981\u8fdc\u8fdc\u8d85\u8fc7\u4e09\u4ebf\u7f8e\u5143\uff0c\u56e0\u6b64\u800c\u83b7\u5f97\u7684\u793e\u4f1a\u5f71\u54cd\u529b\u4e5f\u662f\u5929\u5dee\u5730\u522b\u7684\u3002</p><p>\u3000\u3000\u91d1\u878d\u6295\u673a\u4e0e\u53d1\u5c55\u5b9e\u4e1a\u7684\u4f18\u52a3\u53ef\u4ee5\u8bf4\u662f\u4e00\u76ee\u4e86\u7136\uff0c\u518d\u8bf4\u9526\u6e56\u5546\u4e8b\u8981\u505a\u5f3a\u505a\u5927\uff0c\u4e5f\u9700\u8981\u6ce8\u5165\u5de8\u91cf\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u534e\u4eba\u8d44\u672c\u9003\u79bb\u5370\u5c3c\u4e00\u76f4\u5230\u5341\u6708\u4e2d\u65ec\u90fd\u6ca1\u6709\u505c\u606f\uff0c\u9526\u6e56\u5546\u4e8b\u4ece\u4e2d\u83b7\u5f97\u5de8\u91cf\u7684\u53d1\u5c55\u8d44\u91d1\u5728\u5185\u5730\u5e02\u573a\u8fdb\u884c\u80a1\u6743\u3001\u503a\u6743\u6295\u8d44\u3002</p><p>\u3000\u3000\u80a1\u6743\u3001\u503a\u6743\u6295\u8d44\u901a\u5e38\u90fd\u662f\u957f\u671f\u6295\u8d44\uff0c\u5f88\u96be\u5728\u77ed\u65f6\u95f4\u91cc\u89c1\u6548\uff0c\u4e5f\u5f88\u96be\u77ed\u65f6\u95f4\u91cc\u64a4\u79bb\u51fa\u6765\uff0c\u4f46\u9526\u6e56\u5546\u4e8b\u4ece\u5370\u5c3c\u534e\u5546\u90a3\u91cc\u83b7\u5f97\u7684\u8d44\u91d1\u5374\u4e0d\u662f\u5341\u5206\u7684\u7a33\u5b9a\uff0c\u6709\u53ef\u80fd\u5728\u77ed\u65f6\u95f4\u5185\u906d\u5230\u5bc6\u96c6\u7684\u64a4\u79bb\u3002\u4e00\u65e6\u53d1\u751f\u8fd9\u79cd\u60c5\u5f62\uff0c\u5bf9\u6839\u57fa\u5c1a\u4e0d\u6df1\u7684\u9526\u6e56\u5546\u4e8b\u7edd\u5bf9\u662f\u4e2a\u4e25\u5cfb\u7684\u8003\u9a8c\uff0c\u8d44\u91d1\u94fe\u4e00\u65e6\u8131\u8282\uff0c\u9526\u6e56\u5546\u4e8b\u6574\u4f53\u5d29\u6e83\u4e5f\u4e0d\u662f\u5f88\u96be\u60f3\u8c61\u7684\u4e8b\u60c5\u2014\u2014\u5f88\u591a\u6839\u57fa\u4e0d\u6df1\u53c8\u6025\u4e8e\u53d1\u5c55\u6269\u5f20\u7684\u5e9e\u7136\u5927\u7269\u90fd\u56e0\u4e3a\u8d44\u91d1\u94fe\u4e0a\u5f88\u5c0f\u7684\u4e00\u73af\u51fa\u73b0\u95ee\u9898\u5bfc\u81f4\u8fde\u9501\u53cd\u5e94\u800c\u8f70\u7136\u5d29\u6e83\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u90fd\u5e0c\u671b\u80fd\u5c06\u5bf9\u51b2\u57fa\u91d1\u8d26\u6237\u91cc\u7684\u5de8\u989d\u8d44\u91d1\u6d17\u5230\u9526\u6e56\u5546\u4e8b\u65d7\u4e0b\uff0c\u5728\u8fdb\u4e00\u6b65\u63d0\u9ad8\u9526\u6e56\u5546\u4e8b\u8d44\u91d1\u5b9e\u529b\u7684\u540c\u65f6\uff0c\u4e5f\u52a0\u5f3a\u9526\u6e56\u5546\u4e8b\u8d22\u52a1\u7ed3\u6784\u7684\u5b89\u5168\u6027\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u80fd\u6709\u4e00\u4e2a\u79d8\u5bc6\u8bbe\u7acb\u7684\u5bf9\u51b2\u57fa\u91d1\u638c\u63e1\u5728\u624b\u91cc\uff0c\u6709\u65f6\u5019\u4e5f\u80fd\u591f\u63d0\u4f9b\u4e00\u4e9b\u7279\u6b8a\u7684\u65b9\u4fbf\uff0c\u65ad\u7136\u5c06\u5bf9\u51b2\u57fa\u91d1\u53d6\u6d88\u6389\u4e5f\u4e0d\u5408\u9002\u3002</p><p>\u3000\u3000\u6700\u7ec8\u8ba8\u8bba\u51fa\u6765\u7684\u65b9\u6848\uff0c\u5c31\u662f\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\uff08\u4ee3\u8868\u53f6\u5bb6\uff09\u5728\u5bf9\u51b2\u57fa\u91d1\u8d26\u6237\u91cc\u4fdd\u7559\u4e00\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u4e0e\u5c3c\u514b\u91cc\u68ee\u6240\u9886\u5bfc\u56e2\u961f\u7684\u516b\u5343\u4e07\u7f8e\u5143\u5de8\u8d44\uff08\u8fd9\u662f\u5c3c\u514b\u91cc\u68ee\u6240\u9886\u5bfc\u56e2\u961f\u5728\u8fc7\u53bb\u4e00\u5e74\u591a\u534a\u91cc\u7684\u83b7\u5229\uff09\u653e\u5728\u4e00\u8d77\u5bf9\u5168\u7403\u91d1\u878d\u5e02\u573a\u8fdb\u884c\u6295\u673a\u8fd0\u4f5c\uff0c\u5f20\u606a\u4ed6\u4eec\u5219\u63d0\u53d6\u4e24\u4ebf\u591a\u7f8e\u5143\u7684\u5229\u76ca\u501f\u7740\u534e\u4eba\u8d44\u672c\u64a4\u79bb\u5370\u5c3c\u7684\u6df7\u4e71\u6d17\u5165\u9526\u6e56\u5546\u4e8b\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u9526\u6e56\u5546\u4e8b\u624b\u91cc\u80fd\u591f\u8fd0\u7528\u7684\u8d44\u91d1\u603b\u91cf\u8fbe\u5230\u60ca\u4eba\u7684\u516b\u4ebf\u7f8e\u5143\u4e4b\u5de8\uff0c\u8fd9\u8fd8\u4e0d\u8ba1\u7b97\u4e4b\u524d\u5411\u5357\u6d0b\u6d77\u8fd0\u8fdb\u884c\u80a1\u6743\u6295\u8d44\u7684\u4e24\u4ebf\u7f8e\u5143\u5de8\u8d44\u3002</p><p>\u3000\u300018\u65e5\uff0c\u5f20\u606a\u4ece\u9999\u6e2f\u8fd4\u56de\u5efa\u90ba\uff0c\u4eba\u8fd8\u521a\u4e0b\u98de\u673a\u5c31\u63a5\u5230\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u79d8\u4e66\u67f3\u5fd7\u519b\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u9676\u664b\u4e66\u8bb0\u60f3\u4e0e\u4ed6\u89c1\u4e00\u9762\u3002</p></article>"}}}]);
