(window.webpackJsonp=window.webpackJsonp||[]).push([[1124],{1147:function(p){p.exports={chapterTitle:null,title:"\u7b2c1121\u7ae0 \u8c22\u5251\u5357\u7684\u51b3\u5fc3",content:"<article><h2>\u7b2c1121\u7ae0 \u8c22\u5251\u5357\u7684\u51b3\u5fc3</h2><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u4e0e\u65b0\u5143\u7684\u624b\u673a\u5de5\u5382\u9009\u5740\u5c31\u5728\u79d1\u738b\u91d1\u5c71\u5de5\u5382\u7684\u897f\u4fa7\uff0c\u4e3a\u5960\u57fa\u5178\u793c\u524d\u4e9b\u5929\u5c31\u5728\u90a3\u7247\u8349\u576a\u5730\u4e34\u8fd1\u516c\u8def\u7684\u4e00\u4fa7\u5f00\u8f9f\u4e86\u4e00\u7247\u7a7a\u5730\uff0c\u8fd8\u642d\u4e86\u53f0\u5b50\uff0c\u6628\u5929\u591c\u91cc\u53c8\u94fa\u4e0a\u8165\u7ea2\u7684\u5730\u6bef\uff0c\u53ea\u662f\u4e00\u76f4\u6ca1\u6709\u4ec0\u4e48\u6d88\u606f\u516c\u5e03\u51fa\u6765\uff0c\u4eca\u5929\u5f00\u8f66\u7ecf\u8fc7\u90a3\u91cc\uff0c\u770b\u7740\u8def\u8fb9\u7ad9\u5728\u4e00\u7fa4\u4eba\uff0c\u8c22\u5251\u5357\u77e5\u9053\u4ed6\u4eec\u5728\u8fd9\u8fb9\u7684\u52a8\u4f5c\u7b97\u662f\u6b63\u5f0f\u542f\u52a8\u4e86\u3002</p><p>\u3000\u3000\u4ece\u516c\u5f00\u7684\u8d44\u6599\u6765\u770b\uff0c\u79d1\u738b\u9ad8\u79d1\u4ee5\u53ca\u65b0\u5143\u7535\u5668\u90fd\u5c06\u5728\u91d1\u5c71\u5efa\u9020\u603b\u5e74\u4ea7\u80fd\u4e3a\u4e00\u5343\u4e07\u4ef6\u624b\u673a\u7684\u7ec4\u88c5\u5de5\u5382\uff0c\u4e0d\u8fc7\u4ece\u4e00\u671f\u7528\u5730\u89c4\u6a21\u4ee5\u53ca\u91d1\u5c71\u5e02\u653f\u5e9c\u4e3a\u5176\u9884\u7559\u7684\u4e8c\u671f\u5efa\u8bbe\u7528\u5730\u89c4\u6a21\u6765\u770b\uff0c\u79d1\u738b\u9ad8\u79d1\u4e0e\u65b0\u5143\u7535\u5668\u7684\u5b9e\u9645\u91ce\u5fc3\u53ea\u6015\u8981\u6bd4\u4ed6\u4eec\u5bf9\u5916\u516c\u5e03\u7684\u8981\u5e9e\u5927\u8bb8\u591a\u3002</p><p>\u3000\u3000\u7ec4\u88c5\u5de5\u5382\u7684\u5efa\u8bbe\u901f\u5ea6\u76f8\u5f53\u5feb\uff0c\u5728\u5de5\u5382\u5efa\u8bbe\u7684\u540c\u65f6\uff0c\u5458\u5de5\u62db\u8058\u4e0e\u57f9\u8bad\u5de5\u4f5c\u4e5f\u540c\u65f6\u542f\u52a8\uff0c\u4ece\u79d1\u738b\u9ad8\u79d1\u4ee5\u53ca\u65b0\u5143\u7535\u5668\u5728\u91d1\u5c71\u5e02\u7684\u5148\u671f\u62db\u8058\u89c4\u6a21\u6765\u770b\uff0c\u53ea\u6015\u4e00\u5e74\u4e4b\u540e\uff0c\u4e24\u5bb6\u4f01\u4e1a\u5728\u91d1\u5c71\u7684\u4ea7\u80fd\u89c4\u6a21\u5c31\u8981\u7a81\u7834\u4e00\u5343\u4e07\u4ef6\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7ad9\u5728\u529e\u516c\u697c\u897f\u4fa7\u7684\u4f1a\u8bae\u5ba4\u7a97\u524d\uff0c\u770b\u7740\u897f\u8fb9\u5960\u57fa\u5178\u793c\u7684\u73b0\u573a\u3002</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u53bb\u5e74\u5728\u83b7\u5f97\u624b\u673a\u724c\u7167\u4e4b\u540e\uff0c\u51ed\u501f\u5b9d\u77f3\u7cfb\u5217\u624b\u673a\uff0c\u624b\u673a\u5e74\u9500\u91cf\u7a81\u7834\u4e00\u767e\u4e07\u4ef6\uff0c\u9500\u552e\u989d\u8fbe\u5230\u4e09\u5341\u4ebf\uff0c\u76c8\u5229\u63a5\u8fd1\u4e94\u4e2a\u4ebf\uff0c\u7b97\u662f\u53bb\u5e74\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u5f02\u519b\u7a81\u8d77\u7684\u4e00\u5339\u9ed1\u9a6c\uff0c\u4e5f\u5c06\u51ed\u501f\u6b64\u4f18\u5f02\u7684\u4e1a\u7ee9\u6324\u8fdb\u7535\u5b50\u5de5\u4e1a\u767e\u5f3a\u6392\u540d\u524d\u4e94\u5341\u5f3a\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u79d1\u738b\u9ad8\u79d1\u5728\u5916\u754c\u770b\u6765\uff0c\u5efa\u9020\u5e74\u4ea7\u80fd\u4e00\u5343\u4e07\u4ef6\u624b\u673a\u7684\u7ec4\u88c5\u5de5\u5382\u5df2\u7ecf\u76f8\u5f53\u6709\u8fdc\u89c1\u4e86\u3002</p><p>\u3000\u3000\u65b0\u5143\u7535\u5668\u672a\u80fd\u83b7\u5f97\u624b\u673a\u724c\u7167\uff0c\u4f46\u662f\u4ecb\u5165\u624b\u673a\u7ec4\u914d\u4ef6\u751f\u4ea7\u4e5f\u6709\u4e24\u5e74\u65f6\u95f4\uff0c\u7231\u8fbe\u53bb\u5e74\u4ea7\u80fd\u6709\u6240\u4e0d\u8db3\u4e4b\u540e\uff0c\u6ca1\u6709\u6025\u4e8e\u6269\u5f20\u4e0b\u6e38\u7ec4\u88c5\u5de5\u5382\u7684\u89c4\u6a21\uff0c\u800c\u662f\u5c06\u90e8\u5206\u8ba2\u5355\u4ea4\u7ed9\u65b0\u5143\u7535\u5668\uff0c\u65b0\u5143\u7535\u5668\u7531\u6b64\u8fdb\u5165\u624b\u673a\u4ee3\u5de5\u4f01\u4e1a\u7684\u884c\u5217\u3002\u7531\u4e8e\u65b0\u5143\u7535\u5668\u80fd\u591f\u4ece\u9526\u6e56\u83b7\u5f97\u4e00\u5b9a\u91cf\u7684\u624b\u673a\u82af\u7247\u53ca\u6db2\u6676\u5c4f\u7b49\u6838\u5fc3\u7ec4\u4ef6\uff0c\u81ea\u8eab\u4e5f\u6709\u7ec4\u4ef6\u751f\u4ea7\u80fd\u529b\uff0c\u4ece\u53bb\u5e74\u5e74\u5e95\u8d77\uff0c\u56fd\u5185\u4e00\u4e9b\u53d7\u5143\u5668\u4ef6\u4f9b\u5e94\u7d27\u7f3a\u72b6\u51b5\u56f0\u6270\u7684\u624b\u673a\u5382\u5546\u4e5f\u5c06\u4e00\u90e8\u5206\u751f\u4ea7\u8ba2\u5355\u4ea4\u7ed9\u65b0\u5143\u7535\u5668\u6765\u5b8c\u6210\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u65b0\u5143\u7535\u5668\u7684\u624b\u673a\u751f\u4ea7\u90e8\u95e8\u89c4\u6a21\u4f9d\u65e7\u6709\u9650\u5f97\u5f88\uff0c\u5728\u91d1\u5c71\u5efa\u9020\u5e74\u4ea7\u80fd\u4e00\u5343\u4e07\u4ef6\u624b\u673a\u7684\u7ec4\u88c5\u5de5\u5382\uff0c\u5145\u5206\u66b4\u9732\u51fa\u5176\u91ce\u5fc3\u6765\u3002</p><p>\u3000\u3000\u5f88\u53ef\u60dc\uff0c\u5916\u754c\u770b\u4e0d\u5230\u79d1\u738b\u9ad8\u79d1\u3001\u65b0\u5143\u7535\u5668\u5728\u5bf9\u5916\u516c\u5f00\u7684\u4ea7\u80fd\u6269\u5f20\u8ba1\u5212\u80cc\u540e\u85cf\u7740\u66f4\u5927\u7684\u91ce\u5fc3\uff0c\u8fd9\u80cc\u540e\u5c31\u662f\u9526\u6e56\u7684\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u5230\u4e2d\u5348\u65f6\uff0c\u8c22\u5251\u5357\u8fd8\u662f\u4ece\u7535\u89c6\u65b0\u95fb\u91cc\u5f97\u77e5\u5c31\u5728\u4e0a\u5348\u7684\u5960\u57fa\u4eea\u5f0f\u4e0a\uff0c\u8054\u901a\u4e0b\u5c5e\u4e13\u95e8\u8d1f\u8d23\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u7684\u516c\u53f8\u8054\u65d7\u7535\u8baf\u4e0e\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u7b7e\u7f72\u603b\u91cf\u8fbe\u4e09\u767e\u4e07\u4ef6\u624b\u673a\u7684\u5b9a\u5236\u91c7\u8d2d\u5408\u7ea6\uff0c\u8054\u4fe1\u4e0e\u65b0\u5143\u7535\u5668\u7b7e\u7f72\u603b\u91cf\u8fbe\u4e00\u767e\u4e07\u4ef6\u624b\u673a\u7684\u4ee3\u5de5\u751f\u4ea7\u534f\u8bae\u3002</p><p>\u3000\u3000\u65b0\u5143\u7535\u5668\u8463\u4e8b\u957f\u4e01\u6587\u7965\u5728\u7535\u89c6\u91c7\u8bbf\u4e2d\u5ba3\u5e03\u4e3a\u4e86\u4e13\u5fc3\u81f4\u5fd7\u7684\u505a\u597d\u624b\u673a\u4ee3\u5de5\u4e1a\u52a1\uff0c\u65b0\u5143\u7535\u5668\u5728\u4eca\u540e\u51e0\u5e74\u65f6\u95f4\u91cc\u5c06\u653e\u5f03\u76f4\u63a5\u9500\u552e\u81ea\u6709\u54c1\u724c\u624b\u673a\u7684\u52aa\u529b\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u5e74\u65f6\u95f4\u91cc\uff0c\u65b0\u5143\u7535\u5668\u7684\u53d1\u5c55\u8981\u6bd4\u79d1\u738b\u987a\u5229\u591a\u4e86\uff0c\u65e9\u5728\u4e5d\u516b\u5e74\u5c31\u6324\u5165\u7535\u5b50\u4ea7\u4e1a\u767e\u5f3a\u699c\u4e4b\u5217\u3002\u65b0\u5143\u7535\u5668\u81ea\u77e5\u5728\u6280\u672f\u4e0a\u7684\u5f31\u52bf\uff0c\u6ca1\u6709\u5b9e\u529b\u590d\u5236\u9526\u6e56\u7684\u6280\u672f\u53d1\u5c55\u9053\u8def\uff0c\u5c31\u4e13\u5fc3\u81f4\u5fd7\u7684\u58ee\u5927\u4e2d\u4e0b\u6e38\u7ec4\u4ef6\u3001\u7ec4\u88c5\u5de5\u5382\u7684\u89c4\u6a21\u3002</p><p>\u3000\u3000\u8fd9\u4e24\u5e74\u6765\uff0c\u7231\u8fbe\u7535\u5b50\u6bcf\u5e74\u7684\u789f\u673a\u9500\u552e\u91cf\u90fd\u5728\u516b\u767e\u4e07\u53f0\u4ee5\u4e0a\uff0c\u81ea\u6709\u5de5\u5382\u5b9e\u9645\u53ea\u627f\u62c5\u4e0d\u5230\u4e09\u5206\u4e4b\u4e00\u7684\u751f\u4ea7\u4efb\u52a1\uff0c\u5176\u4f59\u7684\u90fd\u4ea4\u7ed9\u65b0\u5143\u4ee3\u5de5\uff1b\u5c31\u8fde\u65af\u9ad8\u67cf\u5728\u6b27\u7f8e\u5730\u533a\u9500\u552e\u7684\u789f\u673a\uff0c\u4e5f\u90fd\u4ea4\u7ed9\u65b0\u5143\u4ee3\u5de5\u3002\u65b0\u5143\u7535\u5668\u9664\u4e86\u4f20\u7edf\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e4b\u5916\uff0c\u53c8\u662f\u56fd\u5185\u552f\u4e00\u4e00\u5bb6\u83b7\u5f97\u9526\u6e56iplayer\u4e0e\u95ea\u5b58\u76d8\u4ea7\u54c1\u6280\u672f\u6388\u6743\u7684\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u65b0\u5143\u5728\u6d77\u5dde\u7684\u5de5\u5382\u89c4\u6a21\u751a\u81f3\u6bd4\u7231\u8fbe\u96c6\u56e2\u65d7\u4e0b\u7684\u5de5\u5382\u8fd8\u8981\u5e9e\u5927\uff0c\u8fd9\u6b21\u9526\u6e56\u53c8\u62c9\u4e0a\u65b0\u5143\u7535\u5668\u4e00\u8d77\u4e0a\u9635\uff0c\u7531\u4e8e\u56fd\u5185\u624b\u673a\u5382\u5546\u5728\u6280\u672f\u5f00\u53d1\u3001\u7ec4\u4ef6\u751f\u4ea7\u80fd\u529b\u4e0a\u7684\u6ede\u540e\uff0c\u83b7\u5f97\u9526\u6e56\u652f\u6301\u7684\u65b0\u5143\u7535\u5668\u672a\u5fc5\u6ca1\u6709\u5728\u624b\u673a\u4ee3\u5de5\u4e1a\u52a1\u4e0a\u5927\u653e\u5149\u5f69\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u7ec8\u4e8e\u63ed\u5f00\u5e8f\u5e55\u4e86\u554a\uff01</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fae\u5fae\u4e00\u53f9\uff0c\u4ece\u7535\u89c6\u65b0\u95fb\u955c\u5934\u91cc\u770b\u5230\u9526\u6e56\u65b9\u9762\u53ea\u6709\u5143\u5668\u4ef6\u90e8\u603b\u88c1\u5f20\u4e9a\u5e73\u5217\u5e2d\uff0c\u5012\u662f\u80fd\u770b\u51fa\u4e9b\u7aef\u502a\u6765\uff1a\u9526\u6e56\u4f9b\u5e94\u4e3b\u8981\u5143\u5668\u4ef6\uff0c\u7531\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u3001\u65b0\u5143\u7535\u5668\u5728\u524d\u9762\u51b2\u950b\u9677\u9635\u3002</p><p>\u3000\u3000\u64ad\u8fc7\u5960\u57fa\u5178\u793c\u7684\u65b0\u95fb\uff0c\u7535\u89c6\u955c\u5934\u4e00\u8f6c\uff0c\u5374\u662f\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u9080\u8bf7\u53c2\u52a0\u5178\u793c\u5609\u5bbe\u53c2\u89c2\u996e\u9a6c\u6cb3\u897f\u5cb8\u6668\u66e6\u793e\u533a\u7684\u65b0\u95fb\uff0c\u65b0\u5149\u7eb8\u4e1a\u5de5\u5382\u4e5f\u51fa\u73b0\u5728\u955c\u5934\u91cc\uff0c\u8c22\u5251\u5357\u4ece\u4eba\u7fa4\u91cc\u770b\u5230\u5f20\u606a\u7684\u8eab\u5f71\uff0c\u539f\u6765\u4ed6\u4eba\u4e5f\u5728\u91d1\u5c71\uff0c\u6ca1\u6709\u53c2\u52a0\u8fd9\u8fb9\u7684\u5960\u57fa\u5178\u793c\uff0c\u5374\u8dd1\u8fc7\u53bb\u53c2\u89c2\u6668\u66e6\u793e\u533a\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7a81\u7136\u6293\u8d77\u62ff\u8d77\u684c\u4e0a\u7684\u624b\u673a\uff0c\u7ffb\u627e\u51fa\u5f20\u606a\u7684\u53f7\u7801\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u53f7\u7801\u4ed6\u4ece\u6765\u90fd\u6ca1\u6709\u62e8\u6253\u8fc7\uff0c\u4f46\u662f\u4ed6\u77e5\u9053\u901a\u8fc7\u8fd9\u4e2a\u53f7\u7801\u80fd\u591f\u76f4\u63a5\u8054\u7edc\u5230\u5f20\u606a\uff0c\u773c\u775b\u76ef\u7740\u53f7\u7801\u770b\u4e86\u6709\u4e24\u79d2\u949f\uff0c\u4ed6\u54ac\u4e86\u54ac\u7259\u62e8\u4e86\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u60f3\u5f20\u606a\u53ef\u80fd\u4f1a\u7f6e\u4e4b\u4e0d\u7406\uff0c\u53ef\u80fd\u4f1a\u5c06\u624b\u673a\u4ea4\u7ed9\u8eab\u8fb9\u7684\u4eba\u5904\u7406\uff0c\u66f4\u53ef\u80fd\u4ed6\u5b8c\u5168\u4e0d\u77e5\u9053\u662f\u8c01\u6253\u7684\u8fd9\u901a\u7535\u8bdd\u3002</p><p>\u3000\u3000\u8f6c\u77ac\u4e4b\u95f4\uff0c\u8c22\u5251\u5357\u8111\u5b50\u91cc\u60f3\u8fc7\u8bb8\u591a\u5ff5\u5934\uff0c\u76f4\u5230\u5f20\u606a\u90a3\u719f\u6089\u53c8\u7565\u6709\u4e9b\u6c99\u6c89\u7684\u58f0\u97f3\u4f20\u8fc7\u6765\uff1a\u201c\u6211\u662f\u5f20\u606a\uff0c\u8c22\u603b\u627e\u6211\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8c22\u5251\u5357\u7a81\u7136\u89c9\u5f97\u63d0\u89c1\u9762\u7684\u8981\u6c42\u6709\u4e9b\u52c9\u5f3a\u4e86\uff0c\u72b9\u8c6b\u4e86\u51e0\u79d2\u949f\uff0c\u786e\u5b9a\u5f20\u606a\u5e76\u6ca1\u6709\u4ec0\u4e48\u4e0d\u8010\u70e6\uff0c\u624d\u95ee\u9053\uff0c\u201c\u521a\u770b\u7535\u89c6\u65b0\u95fb\u77e5\u9053\u4f60\u4eca\u5929\u5230\u91d1\u5c71\u6765\uff0c\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u89c1\u4f60\u4e00\u9762\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u7eb8\u5382\u8fd9\u8fb9\u5403\u4e2d\u996d\uff0c\u5c31\u5feb\u8981\u5403\u5b8c\u4e86\uff0c\u5f88\u62b1\u6b49\u4e0d\u80fd\u8bf7\u4f60\u5403\u996d\uff0c\u4f60\u8981\u5728\u65b9\u4fbf\uff0c\u6211\u5728\u8fd9\u91cc\u7b49\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u65b0\u95fb\u4e0a\u5348\u5341\u70b9\u8bb8\u5e02\u59d4\u4e66\u8bb0\u8d75\u6709\u4f26\u966a\u540c\u8054\u901a\u9ad8\u5c42\u4ee5\u53ca\u79d1\u738b\u9ad8\u79d1\u7684\u90c1\u840d\u3001\u65b0\u5143\u7535\u5668\u4e01\u6587\u7965\u7b49\u4eba\u53c2\u89c2\u4e0e\u6668\u66e6\u793e\u533a\u4e4b\u540e\u5c31\u8fd4\u56de\u5e02\u5185\u7528\u9910\uff0c\u8c22\u5251\u5357\u6ca1\u60f3\u5230\u5f20\u606a\u8fd8\u7559\u5728\u7eb8\u5382\uff0c\u4e5f\u4e0d\u62d2\u7edd\u4ed6\u89c1\u9762\u7684\u8bf7\u6c42\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7acb\u5373\u6293\u8d77\u5916\u5957\uff0c\u901a\u77e5\u53f8\u673a\u5f00\u8f66\u9001\u4ed6\u5230\u996e\u9a6c\u6cb3\u5bf9\u5cb8\u53bb\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u91d1\u5c71\u5de5\u5382\u7684\u5382\u533a\u9664\u4e86\u5927\u800c\u6574\u6d01\u4e4b\u5916\uff0c\u5e76\u6ca1\u6709\u7279\u522b\u60f9\u773c\u7684\u5730\u65b9\uff0c\u5382\u533a\u91cc\u80fd\u770b\u5230\u7684\u4eba\u5f88\u5c11\uff0c\u90fd\u5728\u8f66\u95f4\u91cc\u3002\u9a76\u8fc7\u5382\u533a\uff0c\u524d\u9762\u5c31\u662f\u88ab\u6c5f\u5357\u7701\u4f5c\u4e3a\u707e\u540e\u91cd\u5efa\u6837\u7248\u793e\u533a\u7684\u6668\u66e6\u793e\u533a\uff0c\u5e02\u91cc\u90fd\u627e\u4e0d\u5230\u51e0\u5904\u6bd4\u8fd9\u91cc\u89c4\u683c\u8fd8\u8981\u9ad8\u7684\u793e\u533a\u3002</p><p>\u3000\u3000\u4ece\u7eb8\u5382\u8857\u7ed5\u8fc7\uff0c\u5f00\u5f80\u4e0e\u5f20\u606a\u7ea6\u597d\u89c1\u9762\u7684\u6668\u66e6\u5e7f\u573a\uff0c\u770b\u89c1\u5f20\u606a\u8896\u624b\u7ad9\u5728\u5706\u5f62\u5e7f\u573a\u7684\u4e2d\u5fc3\uff0c\u6b63\u772f\u8d77\u773c\u775b\u770b\u7740\u5317\u9762\u4ec0\u4e48\u5efa\u7b51\uff0c\u8fd9\u91cc\u4e5f\u662f\u6668\u66e6\u793e\u533a\u7684\u5546\u4e1a\u4e2d\u5fc3\u533a\u3002</p><p>\u3000\u3000\u7531\u4e8e\u65b0\u533a\u90a3\u91cc\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u8c22\u5251\u5357\u4e5f\u5076\u5c14\u4f1a\u5230\u8fd9\u91cc\u5bb4\u8bf7\u5ba2\u4eba\uff0c\u4ed6\u4e0d\u6e05\u695a\u5f20\u606a\u7ad9\u5728\u90a3\u91cc\u505a\u4ec0\u4e48\uff0c\u603b\u4e0d\u53ef\u80fd\u4e13\u7a0b\u5230\u5916\u9762\u63a5\u4ed6\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u5230\u5f20\u606a\u6ce8\u610f\u5230\u4ed6\u5750\u8f66\u8fc7\u6765\uff0c\u53ea\u662f\u5fae\u5fae\u9894\u9996\uff0c\u5e76\u6ca1\u6709\u51fa\u5e7f\u573a\u8fce\u63a5\u7684\u610f\u601d\uff0c\u8c22\u5251\u5357\u5f53\u7136\u4e5f\u6ca1\u6709\u8fd9\u4e9b\u5962\u671b\uff0c\u4ed6\u8ba9\u53f8\u673a\u5c06\u8f66\u505c\u5728\u8def\u8fb9\uff0c\u4e0b\u8f66\u8d70\u8fc7\u53bb\u3002\u8d70\u5230\u5e7f\u573a\u8fb9\u7f18\uff0c\u8c22\u5251\u5357\u624d\u6ce8\u610f\u5230\u5085\u4fca\u4e0e\u51e0\u540d\u4fdd\u9556\u90fd\u7ad9\u5728\u5e7f\u573a\u5317\u4fa7\u7684\u8fb9\u7f18\uff0c\u8fd8\u6709\u51e0\u90e8\u8f66\u505c\u5728\u90a3\u91cc\uff0c\u770b\u6837\u5b50\u5f20\u606a\u5c31\u7b49\u7740\u79bb\u5f00\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5faa\u7740\u5f20\u606a\u7684\u89c6\u7ebf\u5f80\u5317\u9762\u770b\u8fc7\u53bb\uff0c\u90a3\u91cc\u662f\u6668\u66e6\u793e\u533a\u91cc\u65b0\u5f00\u4e1a\u4e0d\u4e45\u7684\u4e00\u5ea7\u5927\u578b\u8d85\u5e02\u3002\u7531\u4e8e\u6668\u66e6\u793e\u533a\u81ea\u5efa\u6210\u4e4b\u65e5\u5c31\u5f62\u6210\u76f8\u5f53\u7684\u5c45\u4f4f\u89c4\u6a21\uff0c\u8fd9\u4e48\u4e00\u5ea7\u5360\u5730\u4e09\u56db\u5343\u5e73\u65b9\u7c73\u3001\u5373\u4f7f\u653e\u5728\u91d1\u5c71\u5e02\u4e2d\u5fc3\u90fd\u4e0d\u7b97\u5c0f\u89c4\u6a21\u7684\u5927\u578b\u8d85\u5e02\u5012\u4e5f\u4e0d\u51b7\u6e05\u3002</p><p>\u3000\u3000\u8d85\u5e02\u5f00\u5728\u4e8c\u697c\uff0c\u5e95\u697c\u4e34\u8857\u662f\u80af\u5fb7\u9e21\u3001\u5305\u798f\u8bb0\u4e24\u5bb6\u5e97\u9762\u6574\u6d01\u7684\u5feb\u9910\u5e97\u4e0e\u4e00\u5bb6\u88c5\u4fee\u7cbe\u81f4\u7684\u5973\u6027\u7f8e\u5bb9\u5e97\uff0c\u6709\u4e24\u9053\u95e8\u5ead\u5f00\u9614\u7684\u901a\u9053\u53ef\u4ee5\u8d70\u8fdb\u53bb\uff0c\u8c22\u5251\u5357\u8fd9\u91cc\u8d70\u8fc7\u4e24\u4e09\u56de\uff0c\u77e5\u9053\u91cc\u9762\u7684\u5e03\u5c40\uff0c\u4ece\u901a\u9053\u8fdb\u53bb\uff0c\u6709\u4e00\u5bb6\u89c4\u6a21\u4e0d\u5c11\u7684\u4e66\u5e97\u4e0e\u97f3\u50cf\u5e97\uff0c\u8d70\u8fdb\u5185\u90e8\u5927\u5385\u91cc\uff0c\u6709\u7535\u68af\u76f4\u901a\u4e8c\u697c\u7684\u8d85\u5e02\uff0c\u7ed5\u8fc7\u7535\u68af\uff0c\u80cc\u540e\u5374\u662f\u4e00\u6761\u76f4\u901a\u5230\u793e\u533a\u5185\u90e8\u7684\u6b65\u884c\u5546\u5e97\u8857\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0d\u77e5\u9053\u5f20\u606a\u7ad9\u5728\u8fd9\u91cc\u89c2\u5bdf\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff0c\u770b\u4ed6\u7684\u795e\u60c5\u4f3c\u4e4e\u770b\u5f97\u5f88\u4ed4\u7ec6\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u5728\u5efa\u90ba\u4e0e\u9676\u664b\u4e66\u8bb0\u89c1\u9762\uff0c\u4ed6\u8ddf\u6211\u8c08\u8d77\u5efa\u90ba\u623f\u4ef7\u4e0a\u6da8\u5f97\u5389\u5bb3\u2026\u2026\u201d\u5f20\u606a\u77e5\u9053\u6709\u4e9b\u7591\u95ee\u8c22\u5251\u5357\u4e0d\u4f1a\u76f4\u63a5\u5f00\u53e3\u95ee\uff0c\u4ed6\u7d22\u6027\u81ea\u52a8\u7ed9\u4ed6\u89e3\u91ca\u81ea\u5df1\u72ec\u81ea\u7ad9\u5728\u5e7f\u573a\u4e0a\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u201c\u9700\u6c42\u592a\u591a\uff0c\u5e02\u573a\u5316\u8fdb\u7a0b\u53c8\u4e00\u4e0b\u5b50\u653e\u5f00\uff0c\u623f\u4ef7\u4e0a\u6da8\u7684\u52bf\u529b\u53ea\u6015\u4e00\u65f6\u904f\u5236\u4e0d\u4f4f\u3002\u201d\u8c22\u5251\u5357\u8bf4\u8d77\u4ed6\u7684\u89c1\u89e3\uff0c\u6700\u7ec8\u867d\u7136\u5c06\u9526\u57ce\u5730\u4ea7\u7684\u6743\u76ca\u90fd\u8f6c\u8ba9\u7ed9\u4e25\u5bb6\u7684\u5b8f\u4fe1\u6295\u8d44\uff0c\u5e76\u4e0d\u610f\u5473\u7740\u4ed6\u5bf9\u5730\u4ea7\u5e02\u573a\u4e0d\u518d\u5173\u6ce8\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u6211\u4e5f\u662f\u8fd9\u4e48\u56de\u7b54\u9676\u664b\u4e66\u8bb0\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u5317\u4eac\u3001\u4e0a\u6d77\u662f\u56fd\u5185\u623f\u5730\u4ea7\u5e02\u573a\u6700\u5148\u653e\u5f00\u7684\u57ce\u5e02\uff0c\u62ff\u5f53\u524d\u5a92\u4f53\u7684\u89c2\u70b9\u6765\u8bf4\uff0c\u65e9\u5c31\u8d85\u8fc7\u5e02\u6c11\u80fd\u591f\u627f\u53d7\u7684\u8303\u56f4\uff0c\u7279\u522b\u662f\u90a3\u4e9b\u4e2d\u4f4e\u6536\u5165\u5bb6\u5ead\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5404\u5730\u533a\u4e0d\u90fd\u5728\u641e\u7ecf\u6d4e\u9002\u7528\u623f\u5417\uff1f\u57ce\u5e02\u804c\u5de5\u4f4f\u623f\u798f\u5229\u5236\u5ea6\u6539\u9769\u7684\u540c\u65f6\uff0c\u4e2d\u592e\u56fd\u52a1\u9662\u51e0\u4e4e\u662f\u540c\u65f6\u51fa\u53f0\u7ecf\u6d4e\u9002\u7528\u623f\u653f\u7b56\uff0c\u5c31\u662f\u7c4d\u5e0c\u671b\u80fd\u89e3\u51b3\u4e00\u90e8\u5206\u4e2d\u4f4e\u6536\u5165\u5bb6\u5ead\uff0c\u6216\u8bb8\u4f1a\u6709\u4e9b\u6548\u679c\u2026\u2026\u201d\u8c22\u5251\u5357\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u653f\u5e9c\u641e\u7ecf\u6d4e\u9002\u7528\u623f\u6709\u4e9b\u4ef7\u683c\u53cc\u8f68\u5236\u7684\u5473\u9053\u5728\u91cc\u9762\uff0c\u5728\u56fd\u5185\u4f53\u5236\u8fd8\u76f8\u5bf9\u6df7\u4e71\u4e4b\u65f6\uff0c\u623f\u5730\u4ea7\u5e02\u573a\u641e\u4ef7\u683c\u53cc\u8f68\u5236\u53ea\u662f\u7ed9\u67d0\u4e00\u5c0f\u64ae\u4eba\u63d0\u4f9b\u66f4\u591a\u7684\u6743\u529b\u5bfb\u79df\u7a7a\u95f4\u7f62\u4e86\u3002\u4e5d\u4e5d\u5e74\u662f\u4e2d\u592e\u63a8\u884c\u7ecf\u6d4e\u9002\u7528\u623f\u653f\u7b56\u7684\u7b2c\u4e00\u5e74\uff0c\u4e5f\u662f\u56fd\u5185\u623f\u5730\u4ea7\u5e02\u573a\u6df1\u5165\u6539\u9769\u7684\u7b2c\u4e00\u5e74\uff0c\u7ecf\u6d4e\u9002\u7528\u623f\u5efa\u8bbe\u91cf\u5360\u6240\u6709\u4f4f\u5b85\u5efa\u8bbe\u768415\uff05\uff0c\u53ef\u4ee5\u8bf4\u662f\u6700\u8f89\u714c\u7684\u4e00\u5e74\uff0c\u4f46\u662f\u5e76\u6ca1\u6709\u7f13\u89e3\u4e2d\u4f4e\u6536\u5165\u5bb6\u5ead\u4f4f\u623f\u96be\u7684\u95ee\u9898\u3002\u4e4b\u540e\u51e0\u5e74\uff0c\u7ecf\u6d4e\u9002\u7528\u623f\u7684\u5efa\u8bbe\u89c4\u6a21\u6240\u5360\u6bd4\u4f8b\u8d8a\u6765\u8d8a\u5c0f\uff0c\u5bf9\u89e3\u51b3\u4e2d\u4f4e\u6536\u5165\u5bb6\u5ead\u7684\u4f4f\u623f\u95ee\u9898\u3001\u5e73\u6291\u623f\u4ef7\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4ea7\u751f\u8fc7\u592a\u5927\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u201c\u4f60\u627e\u6211\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u5f20\u606a\u95ee\u9053\uff0c\u6ca1\u6709\u5fc5\u8981\u8ddf\u8c22\u5251\u5357\u5c31\u623f\u5730\u4ea7\u7684\u95ee\u9898\u7ee7\u7eed\u6df1\u5165\u7684\u8ba8\u8bba\u4e0b\u53bb\uff0c\u95ee\u8d77\u4ed6\u7a81\u7136\u8ddf\u81ea\u5df1\u89c1\u9762\u7684\u7f18\u7531\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u7684\u51b3\u5fc3\u5f88\u5927\u554a\uff0c\u6216\u8bb8\u4e0d\u7528\u4e24\u5e74\uff0c\u9526\u6e56\u5c31\u80fd\u5728\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u79f0\u96c4\u4e86\u5427\uff1f\u201d\u8c22\u5251\u5357\u614e\u91cd\u7684\u9009\u62e9\u5408\u9002\u7684\u5b57\u773c\u3001\u5408\u9002\u7684\u8bed\u6c14\u5c06\u4ed6\u8981\u8868\u8fbe\u7684\u610f\u601d\u8868\u8fbe\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u597d\u4e9b\u4e8b\u60c5\u662f\u5149\u6709\u51b3\u5fc3\u4e0d\u591f\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u5fc3\u60f3\u969c\u773c\u6cd5\u603b\u4e0d\u53ef\u80fd\u8ff7\u60d1\u4f4f\u6240\u6709\u7684\u4eba\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4e0e\u8c22\u5bb6\u7684\u6069\u6028\u7ea0\u7f20\u4e5f\u6df1\uff0c\u8981\u8bf4\u5916\u4eba\u8c01\u6700\u4e86\u89e3\u9526\u6e56\uff0c\u53ea\u6015\u4e5f\u53ea\u6709\u79d1\u738b\u7684\u4eba\u4e86\uff0c\u4ed6\u770b\u4e86\u8c22\u5251\u5357\u4e00\u773c\uff0c\u8bf4\u9053\uff0c\u201c\u8c01\u80fd\u77e5\u9053\u4e24\u5e74\u540e\u624b\u673a\u5e02\u573a\u7684\u683c\u5c40\u4f1a\u53d1\u751f\u600e\u6837\u7684\u53d8\u5316\uff1f\u4e24\u5e74\u540e\uff0c\u4e2d\u79fb\u52a8\u7684\u901a\u8baf\u7f51\u7edc\u5c06\u5168\u9762\u5347\u7ea7\u5230GPRS\u65f6\u4ee3\uff0c\u5373\u4f7f\u662f\u4e2d\u4f4e\u7aef\u624b\u673a\uff0c\u4e5f\u5c06\u5168\u9762\u91c7\u7528GPRS\u57fa\u9891\u82af\u7247\u7ec4\uff0c\u6211\u4eec\u73b0\u5728\u4e5f\u6ca1\u6709\u628a\u63e1\u80fd\u5728\u4e24\u5e74\u65f6\u95f4\u91cc\u5f00\u53d1\u51fa\u6765GPRS\u57fa\u9891\u82af\u7247\u7ec4\uff0c\u8fd9\u65f6\u5019\u8bf4\u8fd9\u4e9b\u8fd8\u5acc\u65e9\u4e86\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0d\u6e05\u695a\u5f20\u606a\u8ddf\u4ed6\u8bf4\u7684\u8bdd\u6709\u51e0\u5206\u662f\u771f\u7684\uff0c\u9526\u6e56\u65e9\u5c31\u91c7\u7528\u5fb7\u4eea\u7684\u624b\u673a\u82af\u7247\u7ec4\u5411\u65b0\u52a0\u5761\u3001\u9999\u6e2f\u7b49\u5730\u7684\u5e02\u573a\u63a8\u51faGPRS\u624b\u673a\uff0c\u4ee5\u9526\u6e56\u7684\u91ce\u5fc3\uff0c\u5e94\u8be5\u5728GPRS\u57fa\u9891\u82af\u7247\u7ec4\u7684\u5f00\u53d1\u6709\u6240\u51c6\u5907\uff0c\u4ed6\u53ea\u80fd\u987a\u7740\u5f20\u606a\u7684\u8bed\u6c14\u8bf4\u4e0b\u53bb\uff1a\u201c\u5728\u4e2d\u79fb\u52a8\u5168\u9762\u5347\u7ea7\u901a\u8baf\u7f51\u7edc\u4e4b\u540e\uff0c\u8054\u901a\u516c\u53f8\u8fd8\u5c06\u7ee7\u7eed\u957f\u65f6\u95f4\u7684\u8fd0\u8425GSM\u7f51\u7edc\uff0c\u5728\u4fdd\u6301GSM\u7f51\u7edc\u7684\u540c\u65f6\u53e6\u5916\u5efa\u8bbeCDMA\u7f51\u7edc\u6765\u5f00\u53d1\u4e2d\u9ad8\u7aef\u5ba2\u6237\u3002\u9664\u4e86\u8054\u901a\u4e4b\u5916\uff0c\u4e1c\u5357\u4e9a\u3001\u5357\u4e9a\u4ee5\u53ca\u5176\u4ed6\u7ecf\u6d4e\u4e0d\u591f\u53d1\u8fbe\u7684\u65b0\u5174\u56fd\u5bb6\u4e0e\u5730\u65b9\u8fd8\u6709\u8bb8\u591a\u5bfa\u65b9\u5c06\u957f\u671f\u4f7f\u7528GSM\u7f51\u7edc\uff0c\u8fd9\u4e9b\u5730\u65b9\u4e5f\u5c06\u662f\u9526\u6e56\u5f00\u62d3\u4e2d\u4f4e\u7aef\u5e02\u573a\u7684\u533a\u57df\uff0c\u5728\u8fd9\u4e9b\u533a\u57df\uff0c\u79d1\u738b\u9ad8\u79d1\u7684\u6e20\u9053\u8d44\u6e90\u8fd8\u7565\u6210\u89c4\u6a21\u2026\u2026\u201d</p><p>\u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u8c22\u5251\u5357\u773c\u775b\u7d27\u76ef\u7740\u5f20\u606a\uff0c\u5373\u4f7f\u4ece\u5f20\u606a\u5634\u91cc\u7acb\u5373\u542c\u5230\u62d2\u7edd\u7684\u8bdd\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u6709\u591a\u5927\u610f\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u62bf\u7740\u5634\uff0c\u4ed6\u6e05\u695a\u8c22\u5251\u5357\u7a81\u7136\u627e\u4e0a\u95e8\u6765\u7684\u610f\u56fe\u3002</p><p>\u3000\u3000\u79d1\u738b\u5f53\u521d\u5f00\u53d1\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u5bf9\u8fd9\u4e00\u5757\u5e02\u573a\u7684\u5f00\u53d1\uff0c\u53ef\u4ee5\u8bf4\u662f\u538b\u5012\u845b\u660e\u4fe1\u7236\u5b50\u7684\u6700\u540e\u4e00\u6839\u7a3b\u8349\uff0c\u4e1c\u5357\u4e9a\u91d1\u878d\u5371\u673a\u4e2d\u6240\u53d7\u5230\u7684\u6301\u7eed\u635f\u5931\u6700\u7ec8\u7531\u845b\u660e\u4fe1\u7236\u5b50\u79c1\u4eba\u627f\u62c5\uff0c\u4f46\u662f\u6e20\u9053\u8d44\u6e90\u51ff\u51ff\u5b9e\u5b9e\u7684\u7559\u7ed9\u79d1\u738b\u4e86\u3002\u79d1\u738b\u7684\u789f\u673a\u3001\u7535\u89c6\u673a\u3001\u97f3\u54cd\u7b49\u7535\u5b50\u4ea7\u54c1\u6b64\u65f6\u5df2\u7ecf\u987a\u5229\u7684\u5411\u8fd9\u4e9b\u56fd\u5bb6\u4e0e\u5730\u65b9\u8f93\u9001\uff0c\u8c22\u5251\u5357\u8fd8\u662f\u60f3\u4ecb\u5165\u624b\u673a\u4e1a\u52a1\u3002\u5373\u4f7f\u6682\u65f6\u65e0\u6cd5\u83b7\u5f97\u624b\u673a\u724c\u7167\uff0c\u6682\u65f6\u65e0\u6cd5\u8fdb\u5165\u56fd\u5185\u624b\u673a\u5e02\u573a\uff0c\u4f46\u662f\u79d1\u738b\u4f9d\u65e7\u53ef\u4ee5\u5229\u7528\u73b0\u6709\u7684\u6e20\u9053\u8d44\u6e90\u5c06\u624b\u673a\u9500\u552e\u5230\u4e1c\u5357\u4e9a\u751a\u81f3\u5357\u4e9a\u3001\u975e\u6d32\u56fd\u5bb6\u4e0e\u5730\u533a\u53bb\uff0c\u6765\u5171\u540c\u53c2\u4e0e\u5230\u6570\u5b57\u624b\u673a\u7684\u76db\u5bb4\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5728\u56fd\u5185\uff0c\u8981\u662f\u56fd\u6709\u4f01\u4e1a\u76f4\u63a5\u4ece\u4fe1\u4ea7\u90e8\u83b7\u5f97\u624b\u673a\u724c\u7167\u8fd8\u597d\u8bf4\uff0c\u79d1\u738b\u8981\u4ecb\u5165\u624b\u673a\u7684\u751f\u4ea7\u4e0e\u51fa\u53e3\uff0c\u9996\u5148\u8981\u6210\u4e3a\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6210\u5458\u4f01\u4e1a\u624d\u80fd\u591f\u83b7\u5f97\u5927\u91cf\u7684\u6280\u672f\u8d44\u6e90\uff0c\u66f4\u9700\u6c42\u4ece\u9526\u6e56\u53ca\u5173\u8054\u4f01\u4e1a\u83b7\u5f97\u751f\u4ea7\u6240\u9700\u624b\u673a\u7684\u5143\u914d\u4ef6\u3001\u7ec4\u4ef6\uff0c\u9526\u6e56\u662f\u79d1\u738b\u5fc5\u987b\u8981\u8de8\u8fc7\u53bb\u7684\u4e00\u9053\u5173\u5361\u3002\u6ca1\u6709\u9526\u6e56\u7684\u5e94\u5141\uff0c\u79d1\u738b\u5373\u4f7f\u5f3a\u884c\u8fdb\u5165\u624b\u673a\u4ea7\u4e1a\uff0c\u90fd\u4e0d\u53ef\u80fd\u6709\u591a\u5927\u7684\u4f5c\u4e3a\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u5e76\u6ca1\u6709\u56e0\u4e3a\u8c22\u5251\u5357\u6c42\u4e0a\u95e8\u6765\u5c31\u7279\u522b\u7684\u795e\u6e05\u6c14\u723d\uff0c\u6b64\u65f6\u7684\u8c22\u5251\u5357\u7ed9\u4eba\u7684\u611f\u89c9\u8981\u6c89\u655b\u5f97\u591a\uff0c\u950b\u8292\u4e5f\u6536\u8d77\u6765\uff0c\u4e5f\u6709\u80fd\u5c48\u80fd\u4f38\u7684\u6c14\u5ea6\uff0c\u4ed6\u7a0d\u7a0d\u505c\u987f\u4e86\u4e00\u4f1a\u513f\u8bf4\u9053\uff0c\u201c\u597d\u5427\uff0c\u6211\u8981\u6c42\u9526\u6e56\u5bf9\u5185\u5bf9\u5916\u8981\u5c3d\u53ef\u80fd\u505a\u5230\u4e00\u81f4\uff0c\u5bf9\u5916\u4e0d\u540c\u7684\u5408\u4f5c\u5355\u4f4d\u4e5f\u8981\u5c3d\u53ef\u80fd\u7684\u505a\u5230\u4e00\u81f4\uff0c\u4e5f\u4e0d\u4f1a\u5355\u72ec\u7ed9\u79d1\u738b\u4ec0\u4e48\u7279\u522b\u7684\u4f18\u60e0\u653f\u7b56\uff0c\u5982\u679c\u79d1\u738b\u6253\u7b97\u8ddf\u6211\u4eec\u5728\u624b\u673a\u4e1a\u52a1\u65b9\u9762\u6709\u6240\u5408\u4f5c\u7684\u8bdd\uff0c\u5e0c\u671b\u4f60\u80fd\u7406\u89e3\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u5f53\u7136\u4e0d\u5962\u671b\u5f97\u5230\u9526\u6e56\u4f18\u60e0\u7684\u5bf9\u5f85\uff0c\u4ed6\u53ea\u662f\u62c5\u5fc3\u53d7\u5230\u7279\u522b\u7684\u6b67\u89c6\u7f62\u4e86\uff0c\u5f20\u606a\u59cb\u7ec8\u4fdd\u6301\u516c\u4e8b\u516c\u529e\u7684\u6001\u5ea6\uff0c\u4ed6\u5012\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u62c5\u5fc3\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8d8a\u5357\u3001\u6cf0\u56fd\u7684\u7ecf\u6d4e\u590d\u82cf\u6bd4\u8f83\u5982\u4eba\u610f\uff0c\u79fb\u52a8\u901a\u8baf\u6295\u8d44\u6570\u989d\u4e5f\u8f83\u5927\uff0c\u8c22\u5251\u5357\u8003\u8651\u79d1\u738b\u8981\u4ecb\u5165\u624b\u673a\u4e1a\u52a1\uff0c\u77ed\u65f6\u95f4\u91cc\u4e0d\u53ef\u80fd\u5728\u56fd\u5185\u62ff\u5230\u724c\u7167\uff0c\u6d77\u5916\u5e02\u573a\u5148\u4ece\u8d8a\u5357\u3001\u6cf0\u56fd\u4e24\u5730\u505a\u8d77\uff0c\u5012\u4e5f\u662f\u4e0d\u9519\u7684\u9009\u62e9\uff1b\u6216\u8bb8\u8fd8\u53ef\u4ee5\u5c06\u5370\u5ea6\u7684\u5e02\u573a\u8003\u8651\u5728\u5185\u3002</p><p>\u3000\u3000\u610f\u5916\u7684\u4e0e\u8c22\u5251\u5357\u5728\u6668\u66e6\u793e\u533a\u7684\u4e2d\u5fc3\u5e7f\u573a\u77ed\u6682\u7684\u4f1a\u9762\u4e4b\u540e\uff0c\u5f20\u606a\u5c31\u76f4\u63a5\u4e58\u8f66\u4ece\u91d1\u5c71\u9a76\u5165\u65b0\u4ead\uff0c\u4e00\u8def\u4e0a\uff0c\u4ed6\u5012\u6ca1\u6709\u7ec6\u601d\u79d1\u738b\u8981\u5148\u4ece\u6d77\u5916\u5730\u533a\u53d1\u5c55\u624b\u673a\u4e1a\u52a1\u7684\u51b3\u5b9a\uff0c\u4ed6\u5fc3\u91cc\u8fd8\u662f\u5728\u7422\u78e8\u623f\u5730\u4ea7\u4e0e\u7ecf\u6d4e\u9002\u7528\u623f\u8fd9\u4e9b\u4e8b\u3002</p><p>\u3000\u3000\u5b8f\u8fdc\u4e5d\u4e94\u5e74\u8d77\u5c31\u6709\u5927\u89c4\u6a21\u4f4f\u5b85\u793e\u533a\u5efa\u8bbe\u7684\u7ecf\u9a8c\uff0c\u4e3a\u5b89\u7f6e\u6c99\u7530\u65e7\u57ce\u6539\u9020\u4e2d\u642c\u8fc1\u51fa\u6765\u7684\u5bb6\u5ead\uff0c\u56db\u5e74\u65f6\u95f4\u5185\u5728\u5e02\u4e1c\u90ca\u5efa\u8bbe\u4e86\u603b\u5efa\u7b51\u9762\u79ef\u8fbe\u4e00\u767e\u4e8c\u5341\u4e07\u5e73\u65b9\u7c73\u7684\u5927\u578b\u4f4f\u5b85\u793e\u533a\uff0c\u5bb9\u7eb3\u8fd1\u516b\u4e07\u5c45\u6c11\u5165\u4f4f\uff0c\u5dee\u4e0d\u591a\u62b5\u5f97\u4e0a\u4e1c\u90e8\u5730\u533a\u4e24\u5ea7\u4e61\u9547\u7684\u4eba\u53e3\u89c4\u6a21\uff0c\u6210\u4e3a\u6d77\u5dde\u5e02\u6700\u5927\u89c4\u6a21\u7684\u793e\u533a\u7fa4\uff0c\u57ce\u5357\u533a\u751a\u81f3\u4e13\u95e8\u8bbe\u7acb\u4e86\u8857\u9053\u4e00\u7ea7\u884c\u653f\u673a\u6784\u3002</p><p>\u3000\u3000\u7531\u4e8e\u662f\u65e7\u57ce\u6539\u9020\u5b89\u7f6e\u623f\u7684\u6027\u8d28\uff0c\u8fd8\u6350\u5efa\u4e86\u5305\u62ec\u4e24\u5ea7\u5e7c\u513f\u56ed\u3001\u4e00\u6240\u5c0f\u5b66\u4ee5\u53ca\u793e\u533a\u536b\u751f\u9662\u3001\u793e\u533a\u516c\u56ed\u3001\u5783\u573e\u7ad9\u3001\u884c\u653f\u53ca\u6d3e\u51fa\u6240\u529e\u516c\u697c\u7b49\u591a\u680b\u516c\u5171\u8bbe\u65bd\uff0c\u867d\u7136\u603b\u5f00\u53d1\u9ad8\u8fbe\u4e00\u767e\u591a\u4e07\u5e73\u65b9\u7c73\uff0c\u5efa\u7b51\u603b\u6210\u672c\u8d85\u8fc7\u5341\u4ebf\u5143\uff0c\u7531\u4e8e\u9500\u552e\u4ef7\u683c\u53d7\u5230\u4e25\u683c\u7684\u9650\u5236\uff0c\u5b8f\u8fdc\u6700\u7ec8\u83b7\u5f97\u7684\u76f4\u63a5\u6536\u76ca\u76f8\u5f53\u6709\u9650\uff0c\u4e0d\u8fc7\u5339\u5339\u4e24\u4e09\u5343\u4e07\u800c\u5df2\uff0c\u5229\u6da6\u7387\u90fd\u4e0d\u8db33\uff05\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e5d\u516b\u5e74\u4e4b\u524d\u56fd\u5185\u4f4f\u5b85\u5e02\u573a\u8fdc\u672a\u542f\u52a8\uff0c\u623f\u5730\u4ea7\u5f00\u53d1\u5546\u8fd8\u6ca1\u6709\u4e0e\u5efa\u7b51\u5546\u8fdb\u884c\u89d2\u8272\u5206\u79bb\uff0c\u884c\u4e1a\u5229\u6da6\u6c34\u5e73\u4e5f\u8fdc\u4e0d\u6b623\uff05\u3002</p><p>\u3000\u3000\u5b8f\u8fdc\u5efa\u8bbe\u771f\u6b63\u6536\u76ca\u7684\u662f\u5728\u516d\u4e07\u5e73\u65b9\u7c73\u7684\u9644\u5c5e\u5546\u4e1a\u5b9e\u65bd\u4e0a\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u6210\u7acb\u540e\uff0c\u8fd9\u90e8\u5206\u8d44\u4ea7\u5c31\u6298\u4ef7\u4e00\u4ebf\u4e8c\u5343\u4e07\u5143\u8ba1\u5165\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\uff0c\u6b64\u65f6\u6bcf\u5e74\u7684\u79df\u91d1\u6536\u76ca\u5df2\u7ecf\u8fbe\u5230\u4e24\u5343\u591a\u4e07\u5143\u3002\u968f\u7740\u57ce\u5e02\u5efa\u8bbe\u7684\u53d1\u5c55\u3001\u5c45\u6c11\u6536\u5165\u6c34\u5e73\u7684\u63d0\u9ad8\uff0c\u9644\u5c5e\u5546\u4e1a\u8bbe\u65bd\u7684\u79df\u91d1\u6536\u76ca\u4e5f\u5c06\u5927\u5e45\u5ea6\u63d0\u9ad8\u3002</p><p>\u3000\u3000\u7531\u4e8e\u516d\u4e07\u5e73\u65b9\u7c73\u7684\u9644\u5c5e\u5546\u4e1a\u8bbe\u65bd\u8fd8\u4e0d\u80fd\u6ee1\u8db3\u6574\u4e2a\u793e\u533a\u7684\u9700\u6c42\uff0c\u4e16\u7eaa\u9526\u6e56\u8fd8\u5c06\u5bf9\u793e\u533a\u7684\u5546\u4e1a\u8bbe\u65bd\u8fdb\u884c\u6539\u6269\u5efa\uff0c\u5c06\u589e\u52a0\u4e09\u56db\u4e07\u5e73\u65b9\u7c73\u7684\u9762\u79ef\u6765\u3002</p><p>\u3000\u3000\u8981\u8bf4\u6709\u9057\u61be\uff0c\u90a3\u5c31\u662f\u793e\u533a\u5185\u7684\u5c45\u6c11\u6ca1\u6709\u7f34\u7eb3\u7269\u4e1a\u8d39\u7684\u4e60\u60ef\uff0c\u4ed6\u4eec\u751a\u81f3\u8ba4\u4e3a\u793e\u533a\u5185\u7684\u5546\u4e1a\u8bbe\u65bd\u5c5e\u4e8e\u516c\u6709\u8d22\u4ea7\uff0c\u66f4\u662f\u7406\u76f4\u6c14\u58ee\u7684\u62d2\u4ea4\u7269\u4e1a\u8d39\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u6bcf\u5e74\u4e0d\u5f97\u4e0d\u989d\u5916\u7ed9\u6574\u4e2a\u793e\u533a\u8865\u8d34\u4e24\u767e\u591a\u4e07\u7684\u7269\u4e1a\u8d39\u3002</p><p>\u3000\u3000\u5f53\u521d\u5728\u5f88\u591a\u4eba\u7684\u610f\u8bc6\u91cc\uff0c\u5c31\u662f\u5728\u9526\u6e56\u5185\u90e8\u4eba\u5458\u7684\u610f\u8bc6\u65f6\uff0c\u5b8f\u8fdc\u6700\u521d\u627f\u5efa\u5b89\u7f6e\u5c0f\u533a\uff0c\u4e5f\u89c9\u5f97\u4e3b\u8981\u662f\u4e3a\u5510\u5b66\u8c26\u5728\u5f53\u65f6\u6d77\u6d32\u5e02\u5185\u7684\u653f\u6cbb\u4e89\u6597\u4e2d\u589e\u52a0\u653f\u6cbb\u5206\uff0c\u6ca1\u6709\u5962\u671b\u8fc7\u80fd\u83b7\u5f97\u591a\u5927\u7684\u76f4\u63a5\u6536\u76ca\uff0c\u6ca1\u6709\u60f3\u5230\u4f1a\u6709\u73b0\u5728\u65e0\u5fc3\u63d2\u67f3\u67f3\u6210\u836b\u7684\u7ed3\u679c\u3002</p></article>"}}}]);
