(window.webpackJsonp=window.webpackJsonp||[]).push([[1125],{1148:function(p){p.exports={chapterTitle:null,title:"\u7b2c921\u7ae0 \u5730\u5934\u86c7\u7684\u95ee\u5019",content:"<article><h2>\u7b2c921\u7ae0 \u5730\u5934\u86c7\u7684\u95ee\u5019</h2><p>\u3000\u3000\u591c\u91cc\uff0c\u56fe\u4e66\u9605\u89c8\u5ba4\u5c06\u8fd1\u5173\u95e8\u7684\u65f6\u5019\uff0c\u5f20\u606a\u8fd8\u6253\u7b97\u5148\u9001\u9648\u5983\u84c9\u56de\u5bbf\u820d\uff0c\u63a5\u5230\u59da\u6587\u76db\u6253\u6765\u7684\u7535\u8bdd\u3002\u59da\u6587\u76db\u6b63\u966a\u738b\u7ef4\u5747\u7ecf\u8fc7\u9ad8\u65b0\u533a\u65b0\u884c\u653f\u4e2d\u5fc3\u5927\u697c\u7684\u5de5\u5730\uff0c\u95ee\u5f20\u606a\u6709\u6ca1\u6709\u5174\u8da3\u4e00\u8d77\u8fc7\u53bb\u770b\u770b\u6e56\u4e1c\u5546\u5708\u7684\u5efa\u8bbe\u8fdb\u5ea6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u9001\u9648\u5983\u84c9\u56de\u5bbf\u820d\uff0c\u7136\u540e\u8ba9\u5085\u4fca\u5f00\u8f66\u5230\u4e1c\u5927\u5357\u95e8\u7b49\u4ed6\u3002</p><p>\u3000\u3000\u51fa\u4e1c\u5927\u6821\u95e8\u5c31\u80fd\u770b\u5230\u65b0\u884c\u653f\u4e2d\u5fc3\u5927\u697c\u77d7\u7acb\u5728\u6e56\u7554\u9ed1\u9ee2\u9ee2\u7684\u5f71\u5b50\uff0c\u6709\u4e34\u65f6\u4f9b\u7535\u7684\u767d\u70bd\u706f\u6ce1\u52fe\u52d2\u51fa\u5927\u697c\u5728\u591c\u7a7a\u4e0b\u7684\u8f6e\u5ed3\uff0c\u4f46\u662f\u5f00\u8f66\u8fc7\u53bb\u8fd8\u662f\u8981\u5148\u7ecf\u8fc7\u4e16\u7eaa\u9526\u6e56\u7684\u5de5\u5730\u3002\u7ecf\u8fc7\u4e16\u7eaa\u9526\u6e56\u7684\u5de5\u5730\u65f6\uff0c\u5f20\u606a\u8fdc\u8fdc\u770b\u89c1\u524d\u9762\u7684\u8def\u53e3\u505c\u7740\u51e0\u8f86\u8f7f\u8f66\uff0c\u597d\u4e9b\u4eba\u7ad9\u5728\u8def\u706f\u7684\u8def\u706f\u4e0b\u6307\u7740\u4e16\u7eaa\u9526\u6e56\u5728\u6e56\u4e1c\u5cb8\u5f00\u53d1\u7684\u3001\u5df2\u7ecf\u7ed3\u6784\u5c01\u9876\u7684\u9ad8\u5c42\u516c\u5bd3\u697c\u6307\u624b\u6307\u811a\u7684\u3002\u8f66\u5f00\u5230\u8fd1\u5904\uff0c\u5f20\u606a\u624d\u770b\u5230\u90a3\u4e9b\u4eba\u91cc\u6709\u4e9b\u662f\u719f\u9762\u5b54\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5728\u71d5\u5f52\u6e56\u4e1c\u5cb8\u62ff\u4e0b\u8fd1\u4e24\u4e07\u9877\u9762\u79ef\u7684\u571f\u5730\u5efa\u8bbe\u6e56\u4e1c\u5546\u5708\u6700\u6838\u5fc3\u7684\u90e8\u5206\uff0c\u5e74\u521d\u5148\u671f\u542f\u52a8\u7684\u662f\u4e34\u8fd1\u6e56\u7554\u7684\u4f4f\u5b85\u9879\u76ee\u3002\u6839\u636e\u71d5\u5f52\u6e56\u6cbf\u5cb8\u7684\u5efa\u7b51\u89c4\u5212\uff0c\u6cbf\u6e56\u5730\u533a\u7684\u5efa\u7b51\u5c42\u9ad8\u53d7\u5230\u4e25\u683c\u7684\u63a7\u5236\uff0c\u6700\u9760\u8fd1\u6e56\u7684\u5730\u65b9\u662f\u71d5\u5f52\u6e56\u4e1c\u5317\u9685\u4e24\u767e\u4ea9\u7684\u522b\u5885\u5efa\u8bbe\u9879\u76ee\uff0c\u8fd9\u4e5f\u662f\u71d5\u5f52\u6e56\u6cbf\u5cb8\u552f\u4e00\u7684\u522b\u5885\u9879\u76ee\uff0c\u5efa\u6210\u4e4b\u540e\u5c06\u7531\u9526\u6e56\u6574\u4f53\u56de\u8d2d\uff0c\u7528\u6765\u5e2e\u52a9\u201c\u767e\u4eba\u8ba1\u5212\u201d\u62db\u52df\u5230\u7684\u6d77\u5916\u534e\u88d4\u5de5\u7a0b\u5e08\u4e0e\u79d1\u5b66\u5bb6\u5728\u5efa\u90ba\u5b89\u5bb6\u843d\u6237\uff0c\u4ece\u73af\u6e56\u4e1c\u8def\u8fc7\u53bb\u4e00\u76f4\u5230\u56ed\u6797\u8def\u4e4b\u95f4\u662f\u5341\u4e8c\u680b\u9ad8\u5c42\u516c\u5bd3\u697c\uff0c\u6709\u6761\u5929\u7136\u7684\u7a84\u6cb3\u7a7f\u8fc7\u516c\u5bd3\u697c\u533a\uff0c\u4e16\u7eaa\u9526\u6e56\u5c06\u5efa\u4e00\u5ea7\u5c0f\u578b\u7684\u6cbf\u6cb3\u5546\u4e1a\u8857\u6765\u4e30\u5bcc\u793e\u533a\u7684\u5546\u4e1a\u73af\u5883\uff0c\u4e5f\u5c06\u662f\u672a\u6765\u6e56\u4e1c\u5546\u5708\u5546\u4e1a\u5730\u4ea7\u4e00\u4e2a\u5c0f\u90e8\u5206\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u9886\u5bfc\u4e0b\u7684\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u51e0\u4e4e\u5c06\u6240\u6709\u7684\u7cbe\u529b\u90fd\u6295\u5165\u5230\u6e56\u4e1c\u5546\u5708\u7684\u5efa\u8bbe\u4e4b\u4e2d\uff0c\u8d22\u529b\u4e5f\u76f8\u5f53\u5bbd\u88d5\uff0c\u5efa\u8bbe\u7684\u901f\u5ea6\u5c31\u975e\u5e38\u5feb\uff0c\u522b\u5885\u9879\u76ee\u5df2\u7ecf\u5f00\u59cb\u6536\u5c3e\u3002\u5341\u4e8c\u680b\u9ad8\u5c42\u516c\u5bd3\u697c\u4ee5\u56db\u680b\u4e3a\u4e00\u5355\u5143\u5c0f\u533a\u5206\u4e09\u671f\u5efa\u8bbe\uff0c\u4e00\u671f\u56db\u680b\u697c\u53ea\u7528\u4e86\u534a\u5e74\u65f6\u95f4\u516b\u6708\u4e0b\u65ec\u5c31\u7ed3\u6784\u5c01\u9876\uff1b\u5230\u4eca\u5929\uff0c\u516c\u5bd3\u697c\u9879\u76ee\u4e8c\u671f\u7684\u56db\u680b\u697c\u4e5f\u5efa\u5230\u4e09\u5206\u4e4b\u4e8c\u7684\u9ad8\u5ea6\uff0c\u4e09\u671f\u7684\u56db\u680b\u697c\u6869\u57fa\u5de5\u7a0b\u4e5f\u5df2\u7ecf\u7ed3\u675f\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5bf9\u5f20\u606a\u3001\u5bf9\u738b\u7ef4\u5747\u7b49\u4eba\u7684\u53e3\u98ce\u8fd8\u662f\u5f88\u7d27\uff0c\u4e0d\u968f\u4fbf\u5938\u6d77\u53e3\uff0c\u4e0d\u8fc7\u5728\u4ed6\u7684\u5de5\u4f5c\u5b89\u6392\u4e2d\uff0c\u5230\u6625\u8282\uff0c\u516c\u5bd3\u697c\u4e00\u671f\u56db\u680b\u697c\u5c31\u8981\u4ea4\u4ed8\u4f7f\u7528\uff0c\u4e09\u671f\u56db\u680b\u697c\u4e5f\u8981\u5728\u6625\u8282\u524d\u5c01\u9876\uff0c\u6b64\u65f6\u5230\u6625\u8282\u8fd8\u6709\u4e09\u4e2a\u6708\u591a\u4e00\u4e9b\u7684\u65f6\u95f4\uff0c\u5230\u660e\u5e74\u5e74\u4e2d\uff0c\u4f4f\u5b85\u9879\u76ee\u90e8\u5206\u5c06\u5168\u90e8\u7ed3\u675f\u3002</p><p>\u3000\u3000\u56ed\u6797\u8def\u5f80\u4e1c\u4e00\u76f4\u5230\u65b0\u6d66\u5927\u9053\u90fd\u662f\u5199\u5b57\u697c\u4e0e\u5546\u4e1a\u5730\u4ea7\u9879\u76ee\uff0c\u4e16\u7eaa\u9526\u6e56\u5c06\u5206\u6210\u4e1c\u7247\u4e0e\u897f\u7247\u4e24\u4e2a\u533a\u57df\u6765\u5f00\u53d1\uff0c\u4e2d\u95f4\u4f1a\u5efa\u8bbe\u4e00\u5ea7\u5927\u578b\u7684\u57ce\u5e02\u5e7f\u573a\uff0c\u5546\u4e1a\u533a\u897f\u7247\u90bb\u8fd1\u4f4f\u5b85\u533a\u7684\u90e8\u5206\u5df2\u7ecf\u5f00\u59cb\u505a\u6869\u57fa\u5de5\u7a0b\u4e86\uff0c\u800c\u4e14\u662f\u540c\u65f6\u5efa\u9020\u516d\u680b\u9ad8\u5c42\u5199\u5b57\u697c\u4e0e\u4e00\u680b\u4e94\u661f\u7ea7\u9152\u5e97\u5efa\u7b51\u3002</p><p>\u3000\u3000\u8096\u660e\u5efa\u3001\u5218\u95fb\u6d9b\u3001\u4e25\u6587\u4ecb\u3001\u8c22\u5251\u5357\u3001\u5468\u5174\u4e1c\u3001\u6797\u96ea\u7b49\u4eba\u5c31\u7ad9\u5728\u56ed\u6797\u8def\u4e0a\u5f80\u7740\u591c\u91cc\u8fd8\u5728\u7d27\u5f20\u65bd\u5de5\u4e2d\u7684\u4f4f\u5b85\u533a\u5de5\u5730\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u7684\u8d44\u672c\u8fd8\u771f\u662f\u96c4\u539a\u554a\uff01\u201d\u4e25\u6587\u4ecb\u8f7b\u58f0\u611f\u6168\u9053\uff0c\u4ed6\u770b\u7740\u8096\u660e\u5efa\u4e00\u773c\uff0c\u201c\u6309\u7167\u8fd9\u4e2a\u901f\u5ea6\uff0c\u6e56\u4e1c\u5546\u5708\u5f88\u5feb\u5c31\u80fd\u5efa\u6210\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u539f\u8ba1\u5212\u662f\u56db\u5230\u516d\u5e74\uff0c\u89c4\u5b9a\u9650\u671f\u9650\u672a\u80fd\u5b8c\u5de5\uff0c\u8fd8\u53ef\u4ee5\u518d\u653e\u5bbd\u5230\u4e24\u5e74\uff0c\u6bd5\u7adf\u4e16\u7eaa\u9526\u6e56\u5728\u521a\u627f\u63fd\u9879\u76ee\u65f6\uff0c\u6ce8\u518c\u8d44\u672c\u624d\u662f\u4e09\u4ebf\u2014\u2014\u201d\u8096\u660e\u5efa\u8eab\u4e3a\u5efa\u90ba\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u957f\uff0c\u5bf9\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u7684\u60c5\u51b5\u8fd8\u662f\u76f8\u5f53\u4e86\u89e3\u7684\uff0c\u201c\u4e94\u6708\u4e0b\u65ec\uff0c\u4ed6\u4eec\u4ece\u5370\u5c3c\u534e\u4eba\u624b\u91cc\u83b7\u5f97\u5de8\u989d\u878d\u8d44\uff0c\u53e6\u5916\uff0c\u9526\u6e56\u4e5f\u76f4\u63a5\u5c06\u4e1c\u5317\u89d2\u4e0a\u7684\u522b\u5885\u533a\u6574\u7247\u7684\u8d2d\u4e0b\uff0c\u4e16\u7eaa\u9526\u6e56\u7684\u5efa\u8bbe\u8fdb\u5ea6\u5c31\u7a81\u7136\u52a0\u5feb\u4e86\u3002\u201d</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u4e0d\u6e05\u695a\u534e\u4eba\u8d44\u672c\u9003\u79bb\u5370\u5c3c\u7684\u7ec6\u8282\uff0c\u4e5f\u65e0\u4ece\u5224\u65ad\u4e16\u7eaa\u9526\u6e56\u4ece\u4e2d\u83b7\u5f97\u5230\u591a\u5c11\u878d\u8d44\uff0c\u552f\u4e00\u80fd\u80af\u5b9a\u7684\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u96c6\u56e2\u80cc\u540e\u83b7\u5f97\u9526\u6e56\u5f3a\u6709\u529b\u7684\u652f\u6491\u2014\u2014\u4e0d\u7ba1\u662f\u95f4\u63a5\u7684\u3001\u8fd8\u662f\u76f4\u63a5\u7684\uff0c\u9526\u6e56\u80fd\u8c03\u52a8\u7684\u8d44\u672c\u8d85\u4e4e\u4e00\u822c\u4eba\u7684\u60f3\u8c61\u3002\u5c31\u773c\u524d\u7684\u5efa\u8bbe\u89c4\u6a21\u6765\u770b\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5728\u56fd\u5185\u7edd\u5bf9\u8981\u7b97\u5927\u5730\u4ea7\u5546\u4e86\uff0c\u8981\u8ba1\u7b97\u5f00\u53d1\u89c4\u6a21\uff0c\u80cc\u540e\u6709\u4fe1\u901a\u94f6\u884c\u652f\u6491\u7684\u5b8f\u4fe1\u5efa\u8bbe\u5728\u4eac\u6d25\u5730\u533a\u7684\u5730\u4ea7\u5f00\u53d1\u4e5f\u4e0d\u8fc7\u5982\u6b64\u89c4\u6a21\u3002</p><p>\u3000\u3000\u201c\u623f\u5730\u4ea7\u5f00\u53d1\u7b97\u4e0d\u4e0a\u6280\u672f\u6d3b\uff0c\u662f\u8d44\u91d1\u5bc6\u96c6\u578b\u4ea7\u4e1a\uff0c\u201d\u5468\u5174\u4e1c\u4e0d\u77e5\u8f7b\u6d45\u7684\u63d2\u4e86\u4e00\u53e5\u8bdd\uff0c\u201c\u5317\u4eac\u6700\u5feb\u7684\u9020\u697c\u4e09\u5929\u4e00\u5c42\uff0c\u4e00\u680b\u767e\u7c73\u9ad8\u7684\u5927\u53a6\uff0c\u4e0d\u9700\u8981\u4e00\u767e\u5929\u5c31\u80fd\u7ed3\u6784\u5c01\u9876\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u660e\u5efa\u8138\u4e0a\u6709\u4e9b\u4e0d\u8c6b\uff0c\u5728\u591c\u8272\u63a9\u9970\u4e0b\uff0c\u4ed6\u60f3\u8d77\u738b\u7ef4\u5747\u6377\u8db3\u5148\u767b\u63d0\u51fa\u7684\u9488\u5bf9\u5317\u4eac\u5730\u4ea7\u5708\u7684\u62db\u5546\u5f15\u8d44\u8ba1\u5212\u4f3c\u4e4e\u6267\u884c\u5f97\u76f8\u5f53\u987a\u5229\uff0c\u4e5f\u662f\u5341\u6708\u521d\u56fd\u5e86\u671f\u95f4\u738b\u7ef4\u5747\u3001\u59da\u6587\u76db\u9080\u8bf7\u5317\u4eac\u5730\u4ea7\u5708\u91cc\u7684\u4e00\u4e9b\u4eba\u58eb\u5230\u5efa\u90ba\u6765\u53c2\u52a0\u4e00\u4e2a\u975e\u6b63\u5f0f\u7684\u5730\u4ea7\u8bba\u575b\uff0c\u8ba9\u4ed6\u4eec\u770b\u5230\u4e16\u7eaa\u9526\u6e56\u5728\u9ad8\u65b0\u533a\u7684\u5efa\u9020\u89c4\u6a21\u4ee5\u53ca\u9ad8\u65b0\u533a\u8fd9\u4e24\u5e74\u6765\u7684\u5de8\u5927\u53d8\u5316\uff0c\u81f3\u4e8e\u56fd\u5bb6\u4e0e\u5730\u65b9\u5bf9\u9ad8\u65b0\u533a\u7684\u9ad8\u989d\u6295\u5165\u4e0e\u4f18\u60e0\u653f\u7b56\uff0c\u4e4b\u524d\u4e25\u6587\u4ecb\u5df2\u7ecf\u66ff\u4ed6\u4eec\u514d\u8d39\u5ba3\u4f20\u8fc7\u4e86\uff0c\u53ea\u4e0d\u8fc7\u6843\u5b50\u7ed9\u738b\u7ef4\u5747\u5148\u624b\u6458\u8d70\u7f62\u4e86\u3002</p><p>\u3000\u3000\u5218\u95fb\u6d9b\u7709\u5934\u5fae\u5fae\u8e59\u7740\uff0c\u5f88\u5c11\u8bf4\u8bdd\uff0c\u4ed6\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\uff0c\u5f20\u606a\u4e58\u5750\u7684\u5954\u9a70\u8f66\u884c\u9a76\u5230\u8ddf\u524d\uff0c\u4ed6\u90fd\u6ca1\u6709\u7559\u610f\u5230\uff0c\u76f4\u5230\u5c31\u7b97\u5728\u5efa\u90ba\u4e5f\u4e0d\u591a\u89c1\u7684\u884c\u653f\u7ea7\u5954\u9a70\u8f7f\u8f66\u5728\u4ed6\u4eec\u9762\u524d\u505c\u4e0b\u6765\uff0c\u4ed6\u624d\u56de\u8fc7\u795e\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u4e86\u8f66\uff0c\u73af\u987e\u7740\u7ad9\u5728\u8def\u65c1\u7684\u4f17\u4eba\uff0c\u8ddf\u8096\u660e\u5efa\u70b9\u5934\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8096\u5e02\u957f\u8fd9\u4e48\u665a\u90fd\u6765\u89c6\u5bdf\u5de5\u5730\u554a\uff0c\u600e\u4e48\u6ca1\u89c1\u4e16\u7eaa\u9526\u6e56\u7684\u4eba\u966a\u540c\uff1f\u201d\u53c8\u4fa7\u5934\u5429\u5490\u5085\u4fca\uff0c\u201c\u4f60\u7ed9\u738b\u4e66\u8bb0\u6253\u4e2a\u7535\u8bdd\uff0c\u8bf4\u6211\u5728\u8fd9\u91cc\u78b0\u5230\u8096\u5e02\u957f\u4e86\uff0c\u6211\u5728\u8fd9\u91cc\u7b49\u4ed6\u3002\u201d</p><p>\u3000\u3000\u8096\u660e\u5efa\u6577\u884d\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u79d1\u738b\u4e0e\u534e\u590f\u7535\u5b50\u3001NEC\u5728\u5efa\u90ba\u5408\u8d44\u6210\u7acb\u4e86\u7814\u7a76\u9662\uff0c\u6211\u4ee3\u8868\u5e02\u91cc\u51fa\u5e2d\u4e86\u4ed6\u4eec\u62db\u5f85\u7684\u9152\u4f1a\uff0c\u9152\u4f1a\u521a\u6563\uff0c\u4fbf\u966a\u4e9b\u5ba2\u4eba\u53c2\u89c2\u5efa\u90ba\u7684\u5efa\u8bbe\u65b0\u8c8c\uff0c\u968f\u5174\u8d70\u5230\u8fd9\u91cc\uff0c\u4e0d\u7528\u523b\u610f\u60ca\u52a8\u592a\u591a\u7684\u4eba\u2014\u2014\u8fd9\u4e9b\u5ba2\u4eba\u8981\u4e0d\u8981\u6211\u5e2e\u4f60\u4ecb\u7ecd\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u201c\u90fd\u662f\u4e9b\u8001\u670b\u53cb\u4e86\uff0c\u4e0d\u7528\u9ebb\u70e6\u8096\u5e02\u957f\u5e2e\u6211\u4ecb\u7ecd\uff0c\u201d\u5f20\u606a\u53c8\u77a5\u773c\u5728\u8c22\u5251\u5357\u3001\u5468\u5174\u4e1c\u7b49\u4eba\u7684\u8138\u4e0a\u626b\u8fc7\uff0c\u770b\u4e25\u6587\u4ecb\u65f6\u53ea\u662f\u6de1\u6de1\u7684\u4e00\u7b11\uff0c\u53c8\u770b\u4e86\u5218\u95fb\u6d9b\u4e00\u773c\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u4e0d\u662f\u5510\u4f2f\u4f2f\u5728\u4e2d\u592e\u515a\u6821\u7684\u6821\u53cb\u5417\uff0c\u5218\u5c40\u957f\u4e5f\u5230\u5efa\u90ba\u6765\u505a\u5ba2\uff1f\u201d</p><p>\u3000\u3000\u9ec4\u660f\u65f6\u9676\u664b\u89c1\u5f20\u606a\u7684\u6d88\u606f\u6ca1\u90a3\u4e48\u5bb9\u6613\u4f20\u6765\uff0c\u4f46\u662f\u5728\u573a\u53c8\u6709\u591a\u5c11\u4eba\u770b\u4e0d\u51fa\u5f20\u606a\u662f\u5728\u88c5\u75f4\u5356\u50bb\uff1f\u8c22\u5251\u5357\u89c1\u5218\u95fb\u6d9b\u8138\u4e0a\u6709\u63a9\u9970\u4e0d\u4e86\u7684\u5c34\u5c2c\uff0c\u4ed6\u5fc3\u77e5\u81ea\u5df1\u4e5f\u4e0d\u8ba8\u5f20\u606a\u559c\u6b22\uff0c\u4f46\u662f\u5728\u573a\u4e5f\u53ea\u6709\u4ed6\u80fd\u66ff\u5218\u95fb\u6d9b\u786c\u8f6c\u8fd9\u4e2a\u5f2f\uff1a\u201c\u5218\u5c40\u957f\u5df2\u4e0d\u518d\u662f\u5218\u5c40\u957f\u4e86\uff0c\u518d\u8fc7\u51e0\u5929\uff0c\u5218\u5c40\u957f\u5c31\u8981\u53bb\u6d77\u5dde\u62c5\u4efb\u5e02\u59d4\u4e66\u8bb0\u2026\u2026\u60f3\u5fc5\u9526\u6e56\u5f88\u5feb\u5c31\u4f1a\u63a5\u5230\u901a\u77e5\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u5218\u4e66\u8bb0\u2026\u2026\u771f\u662f\u60ca\u559c\u4e86\uff0c\u4e0a\u56de\u89c1\u9762\u90fd\u5b8c\u5168\u6ca1\u89c9\u5f97\u5218\u4e66\u8bb0\u4f1a\u5230\u6d77\u5dde\u5de5\u4f5c\uff0c\u5230\u6d77\u5dde\u4e4b\u540e\u4e00\u5b9a\u8981\u8ba9\u6211\u5c3d\u4e00\u4e0b\u5730\u4e3b\u4e4b\u8c0a\u554a\u3002\u201d\u5f20\u606a\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u53cc\u624b\u5408\u62e2\u653e\u5728\u8eab\u524d\uff0c\u8eab\u5b50\u5fae\u5fae\u6b20\u7740\u8bf4\u8bdd\uff0c\u7b97\u662f\u8868\u793a\u606d\u656c\uff0c\u4f46\u662f\u4ed6\u7684\u8bed\u6c14\u5374\u8ba9\u5218\u95fb\u6d9b\u8138\u8272\u5f88\u96be\u770b\u3002</p><p>\u3000\u3000\u8fd9\u662f\u4ec0\u4e48\u8bdd\uff0c\u6446\u660e\u8bf4\u4ed6\u81ea\u5df1\u662f\u6d77\u5dde\u7684\u5730\u5934\u86c7\uff1f\u8c22\u5251\u5357\u4e0d\u77e5\u9053\u5218\u95fb\u6d9b\u5728\u4e5d\u6708\u4e0b\u65ec\u4e0e\u5f20\u606a\u5076\u7136\u7684\u90a3\u6b21\u76f8\u9047\uff0c\u5f20\u606a\u7684\u5484\u5484\u903c\u4eba\u8ba9\u4ed6\u5fc3\u5934\u4e5f\u611f\u53d7\u5230\u4e00\u5206\u538b\u529b\u3002</p><p>\u3000\u3000\u865a\u4f2a\u7684\u5bd2\u6684\u7247\u523b\uff0c\u738b\u7ef4\u5747\u4e0e\u59da\u6587\u76db\u5c31\u8d76\u8fc7\u6765\u4e86\uff0c\u90b5\u81f3\u521a\u966a\u540c\u4ed6\u4eec\u5728\u4e00\u8d77\uff0c\u8fd9\u66f4\u663e\u51fa\u8096\u660e\u5efa\u7684\u5bd2\u9178\u6765\u4e86\uff0c\u8868\u9762\u4e0a\u548c\u6c14\u4e00\u56e2\uff0c\u53ea\u662f\u8fd9\u6837\u7684\u5bd2\u6684\uff0c\u4e0d\u8bba\u662f\u5f20\u606a\u8fd9\u8fb9\uff0c\u8fd8\u662f\u8096\u660e\u5efa\u4ed6\u4eec\u90a3\u4e00\u8fb9\uff0c\u90fd\u89c9\u5f97\u96be\u4ee5\u5fcd\u53d7\uff0c\u8096\u660e\u5efa\u7b49\u4eba\u5f88\u5feb\u5c31\u627e\u501f\u53e3\u544a\u8f9e\u4e86\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8fd8\u662f\u5ff5\u5ff5\u4e0d\u5fd8\u554a\uff01\u201d\u770b\u7740\u8096\u660e\u5efa\u7b49\u4eba\u4e58\u5750\u7684\u51e0\u8f86\u8f7f\u8f66\u79bb\u53bb\uff0c\u738b\u7ef4\u5747\u611f\u6168\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u571f\u5730\u751f\u957f\u51fa\u6765\u7684\u8d22\u5bcc\u4f1a\u8ba9\u5f88\u591a\u4eba\u90fd\u5927\u5403\u4e00\u60ca\uff0c\u201d\u5f20\u606a\u8f7b\u8f7b\u7684\u8bf4\u9053\uff0c\u53c8\u5f00\u73a9\u7b11\u7684\u8ddf\u738b\u7ef4\u5747\u8bf4\uff0c\u201c\u4f60\u8fd9\u4e48\u665a\u8fd8\u62c9\u6211\u51fa\u6765\uff0c\u8fd8\u4e0d\u5c31\u662f\u770b\u5230\u8fd9\u4e00\u70b9\u5417\uff1f\u201d</p><p>\u3000\u3000\u738b\u7ef4\u5747\u7b11\u4e86\u7b11\uff0c\u53c8\u95ee\u9053\uff1a\u201c\u4e0e\u6d77\u5dde\u7684\u65b0\u5e02\u59d4\u4e66\u8bb0\u89c1\u8fc7\u9762\u4e86\uff0c\u611f\u89c9\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u4e0d\u662f\u4e00\u4f19\u7684\uff0c\u95fb\u7740\u5473\u5c31\u77e5\u9053\u4e86\uff0c\u201d\u5f20\u606a\u6447\u5934\u800c\u7b11\uff0c\u201c\u4e0d\u7136\u600e\u4e48\u8bf4\u81ed\u5473\u76f8\u6295\u5462\uff1f\u5f53\u7136\uff0c\u6211\u4e0d\u662f\u8bf4\u6211\u81ea\u5df1\uff0c\u6211\u505a\u4eba\u6ca1\u6709\u90a3\u4e48\u6311\u5254\uff0c\u662f\u4ed6\u4eec\u6311\u5254\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u79d1\u738b\u4e0e\u534e\u590f\u7535\u5b50\u3001\u65e5\u672cNEC\u7684\u5408\u4f5c\u8fdb\u7a0b\u975e\u5e38\u8fc5\u901f\uff0c18\u65e5\u5728\u5efa\u90ba\u5408\u8d44\u6210\u7acb\u4e86\u7814\u7a76\u9662\u5f00\u53d1\u89c6\u542c\u5f71\u50cf\u7535\u5b50\u4ea7\u54c1\uff0c28\u65e5\uff0c\u5408\u8d44\u4f01\u4e1a\u65b0\u7684\u79d1\u738b\u7535\u5668\u96c6\u56e2\u5c31\u6b63\u5f0f\u5728\u6d77\u5dde\u6302\u724c\u6210\u7acb\u4e86\uff0c\u65b0\u79d1\u738b\u4e0d\u4ec5\u83b7\u5f97NEC\u8f6c\u79fb\u5230\u56fd\u5185\u7684\u751f\u4ea7\u6280\u672f\u4e0e\u8bbe\u5907\uff0c\u8fd8\u83b7\u5f97\u534e\u590f\u7535\u5b50\u3001\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u7b49\u5171\u56db\u4e2a\u4ebf\u7684\u6ce8\u8d44\uff0c\u8fd8\u5c06\u91d1\u5c71\u7535\u5b50\u56ed\u7684\u5de5\u5382\u3001\u8bbe\u5907\u3001\u4eba\u5458\u4ee5\u4f4e\u5ec9\u7684\u4ef7\u683c\u517c\u8d2d\u8fc7\u6765\u4f5c\u4e3a\u65d7\u4e0b\u7684\u751f\u4ea7\u57fa\u5730\uff0c\u53ef\u4ee5\u8bf4\u662f\u6682\u65f6\u5c06\u4e00\u56e2\u6b7b\u68cb\u7ed9\u76d8\u6d3b\u4e86\u2014\u2014\u8fd9\u4e5f\u610f\u5473\u7740\u79d1\u738b\u539f\u5148\u7684\u7b2c\u4e8c\u5927\u80a1\u4e1c\u5609\u4fe1\u7535\u5b50\u6ca1\u6709\u505a\u4ec0\u4e48\u53cd\u6297\u2014\u2014\u5609\u4fe1\u7535\u5b50\u5bf9\u76f8\u5e94\u635f\u5931\u505a\u51fa\u5de8\u989d\u8ba1\u63d0\uff0c\u81f4\u4f7f\u5609\u4fe1\u7535\u5b50\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u7684\u80a1\u7968\u4ef7\u683c\u66f4\u52a0\u7684\u98ce\u96e8\u98d8\u6447\u3002</p><p>\u3000\u3000\u5230\u6d77\u5dde\u8d74\u4efb\u624d\u4e24\u5929\u7684\u65b0\u4efb\u5e02\u59d4\u4e66\u8bb0\u5218\u95fb\u6d9b28\u9ad8\u8c03\u7684\u51fa\u5e2d\u7684\u79d1\u738b\u7535\u5668\u96c6\u56e2\u7684\u5408\u8d44\u6302\u724c\u5178\u793c\u3002\u534e\u590f\u7535\u5b50\u3001\u65e5\u672cNEC\u3001\u79d1\u738b\u4e09\u5bb6\u4f01\u4e1a\u5728\u56fd\u5185\u90fd\u6709\u76f8\u5f53\u9ad8\u7684\u5730\u4f4d\uff0c\u56fd\u5185\u7684\u5a92\u4f53\u518d\u4e91\u96c6\u6d77\u5dde\uff0c\u5218\u95fb\u6d9b\u8138\u4e0a\u56e0\u6b64\u4e5f\u589e\u5149\u4e0d\u5c11\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u6000\u7591\u65b0\u738b\u6302\u724c\u6210\u7acb\u7684\u65f6\u673a\u9009\u62e9\u4f3c\u4e4e\u662f\u4e13\u95e8\u7ed9\u5218\u95fb\u6d9b\u51c6\u5907\u4e86\u4e00\u4e2a\u9ad8\u8c03\u4eae\u76f8\u7684\u673a\u4f1a\u3002\u8fd9\u4e00\u5929\uff0c\u4ed6\u4eba\u4e5f\u5728\u6d77\u5dde\uff0c\u4e0e\u53f6\u5efa\u658c\u5728\u90b5\u5fc3\u6587\u3001\u9648\u4fe1\u751f\u7b49\u4eba\u7684\u966a\u540c\u4e0b\u89c6\u5bdf\u9526\u6e56\u5728\u5de5\u4e1a\u65b0\u533a\u7684\u5de5\u5382\uff0c\u751a\u81f3\u80fd\u542c\u89c1\u4ece\u9694\u58c1\u5382\u533a\u9ad8\u97f3\u5587\u53ed\u91cc\u4f20\u51fa\u6765\u7684\u5218\u95fb\u6d9b\u7684\u8bb2\u8bdd\u3002</p><p>\u3000\u3000\u201c\u79d1\u738b\u5c31\u662f\u4e00\u8258\u4fee\u4fee\u8865\u8865\u8fd8\u4e00\u76f4\u5728\u6f0f\u7684\u8239\uff0c\u5012\u662f\u6709\u4eba\u524d\u8d74\u540e\u7ee7\u7684\u5f80\u8fd9\u8258\u8239\u4e0a\u8df3\uff0c\u201d\u5f20\u606a\u8f7b\u677e\u81ea\u82e5\u7684\u8ddf\u9648\u4fe1\u751f\u3001\u90b5\u5fc3\u6587\u5f00\u73a9\u7b11\uff0c\u201c\u5468\u5bcc\u660e\u5982\u6b64\u3001\u82cf\u9038\u7fa4\u5982\u6b64\uff0c\u6362\u4e86\u5218\u95fb\u6d9b\u4e5f\u6ca1\u89c1\u53d8\u806a\u660e\u4e9b\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u90b5\u5fc3\u6587\u542c\u4e86\u54c8\u54c8\u4e00\u7b11\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8bf4\u9053\uff1a\u201c\u5218\u95fb\u6d9b\u6216\u8bb8\u4ece\u65b0\u7684\u79d1\u738b\u8eab\u4e0a\u770b\u5230\u4e00\u4e9b\u5e0c\u671b\u4e86\u2026\u2026\u65b0\u79d1\u738b\u6bd5\u7adf\u6df7\u5165\u4e86\u65e5\u8d44\u4e0e\u56fd\u8d44\u7684\u8840\u7edf\u2026\u2026\u5218\u95fb\u6d9b\u867d\u8bf4\u4e0d\u80fd\u62ff\u6211\u4eec\u600e\u4e48\u6837\uff0c\u5012\u662f\u6709\u53ef\u80fd\u504f\u6267\u7684\u53bb\u652f\u6301\u79d1\u738b\u3002\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u5427\uff0c\u201d\u5f20\u606a\u62ac\u5934\u770b\u4e86\u770b\u98d8\u6e3a\u7684\u5929\u7a7a\uff0c\u8bf4\u9053\uff0c\u201c\u644a\u5b50\u7684\u672c\u8d28\u6ca1\u6709\u53d8\u5316\uff0c\u518d\u5927\u7684\u652f\u6301\uff0c\u4e5f\u4e0d\u8fc7\u662f\u8ba9\u4e00\u5806\u5c0f\u7684\u70c2\u644a\u5b50\u53d8\u6210\u4e00\u5806\u66f4\u5927\u7684\u70c2\u644a\u5b50\u800c\u5df2\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u6253\u4e0d\u6253\u7b97\u505a\u9ed1\u7535\uff1f\u201d\u90b5\u5fc3\u6587\u95ee\u9053\uff0c\u201c\u7231\u8fbe\u4e0d\u505a\uff0c\u9999\u96ea\u6d77\u4e5f\u4e0d\u505a\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd1\u4e24\u5e74\u6ca1\u6709\u6253\u7b97\uff0c\u591a\u5143\u5316\u7ecf\u8425\u867d\u8bf4\u80fd\u8ba9\u54c1\u724c\u7684\u4ef7\u503c\u5f97\u5230\u66f4\u5145\u5206\u7684\u4f53\u73b0\uff0c\u4f46\u662f\u592a\u4ed3\u4fc3\u884c\u4e8b\u4e0d\u597d\uff0c\u54c1\u724c\u7684\u4ef7\u503c\u4e5f\u4e0d\u80fd\u65e0\u9650\u5236\u7ed9\u7a00\u91ca\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u201c\u8fc7\u4e24\u5e74\u518d\u8003\u8651\u4e5f\u4e0d\u8fdf\uff0c\u5f69\u8272\u663e\u50cf\u7ba1\u7535\u89c6\uff0c\u6709\u5fc5\u8981\u5230\u65f6\u5019\u76f4\u63a5\u6536\u8d2d\u56fd\u5185\u7684\u5de5\u5382\u5c31\u884c\u4e86\uff0c\u6211\u4f30\u8ba1\u7740\u5f69\u8272\u663e\u50cf\u7ba1\u7535\u89c6\u5e02\u573a\u6ce8\u5b9a\u6709\u573a\u6076\u6218\u2014\u2014\u4e0d\u6653\u5f97\u5728\u6076\u6218\u6765\u4e34\u4e4b\u524d\uff0c\u79d1\u738b\u6709\u6ca1\u6709\u8db3\u591f\u7684\u51c6\u5907\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u73b0\u5728\u5012\u662f\u66ff\u79d1\u738b\u62c5\u5fe7\u8d77\u6765\u4e86\u554a\uff1f\u201d\u53f6\u5efa\u658c\u620f\u8c11\u7684\u6253\u8da3\u3002</p><p>\u3000\u3000\u201c\u6211\u7684\u5fc3\u80a0\u5f88\u8f6f\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u53c8\u54c8\u54c8\u4e00\u7b11\uff0c\u201c\u514d\u5f97\u7ed9\u4f60\u4eec\u8bf4\u6210\u5e78\u707e\u4e50\u7978\uff0c\u8fd8\u662f\u4e0d\u8981\u8c08\u79d1\u738b\u4e86\u2014\u2014\u6211\u4e00\u76f4\u5728\u8003\u8651\u9526\u6e56\u8981\u8fdb\u5165\u9ed1\u7535\u5e02\u573a\uff0c\u672a\u6765\u8981\u594b\u6597\u7684\u76ee\u6807\u8fd8\u662f\u9ad8\u6e05\u7535\u89c6\uff0c\u6db2\u6676\u7535\u89c6\u624d\u662f\u5927\u52bf\u6240\u8d8b\uff01\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u3001\u90b5\u5fc3\u6587\u5012\u6ca1\u6709\u6000\u7591\u5f20\u606a\u4e3a\u4ec0\u4e48\u5728\u7b49\u79bb\u5b50\u663e\u793a\u6280\u672f\u4e0e\u6db2\u6676\u663e\u793a\u6280\u672f\u4e4b\u95f4\u76f4\u63a5\u9009\u62e9\u6db2\u6676\u663e\u793a\u6280\u672f\u4f5c\u4e3a\u9526\u6e56\u672a\u6765\u7684\u53d1\u5c55\u76ee\u6807\u3002</p><p>\u3000\u3000\u9526\u6e56\u5df2\u7ecf\u638c\u63e1SN\u6db2\u6676\u663e\u793a\u6280\u672f\uff0c\u4e0e\u6db2\u6676\u7535\u89c6\u6240\u9700\u8981\u7684\u8584\u819c\u6db2\u6676\u663e\u793a\u6280\u672f\u6709\u4e00\u5b9a\u7684\u5173\u8054\u6027\uff1b\u518d\u8bf4\u9526\u6e56\u5343\u65b9\u767e\u8ba1\u7684\u4ecb\u5165\u56fd\u5185\u7684\u7a00\u6563\u91d1\u5c5e\u9886\u57df\uff0c\u8054\u5408\u4e2d\u592e\u4e0e\u5730\u65b9\u673a\u6784\u7ec4\u5efa\u534e\u7a00\u77ff\u4e1a\u96c6\u56e2\uff0c\u6700\u5148\u505a\u7684\u4e8b\u5c31\u662f\u63a7\u5236\u56fd\u5185\u7684\u94df\u51fa\u53e3\u8d38\u6613\uff0c\u800c\u94df\u91d1\u5c5e\u662f\u6db2\u6676\u5c4f\u751f\u4ea7\u7684\u5173\u952e\u6027\u539f\u6599\uff0c\u867d\u8bf4\u672a\u6765\u7684\u9ad8\u6e05\u7535\u89c6\u9010\u6e10\u5206\u5316\u6210\u4e24\u5927\u9635\u8425\uff0c\u6b64\u65f6\u7684\u9526\u6e56\u8fd8\u6ca1\u6709\u5b9e\u529b\u4e00\u811a\u8e0f\u4e24\u811a\u540c\u65f6\u53d1\u5c55\u4e24\u79cd\u6280\u672f\uff0c\u9009\u62e9\u6db2\u6676\u9ad8\u6e05\u7535\u89c6\u6280\u672f\u4f5c\u4e3a\u672a\u6765\u7684\u53d1\u5c55\u662f\u7406\u6240\u5f53\u7136\u7684\uff0c\u5c31\u7b97\u662f\u6db2\u6676\u9ad8\u6e05\u663e\u793a\u6280\u672f\u60f3\u8981\u5f97\u5230\u4e5f\u662f\u6781\u56f0\u96be\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u90b5\u5fc3\u6587\u8fd9\u6b21\u62c9\u53f6\u5efa\u658c\u966a\u4ed6\u5230\u6d77\u5dde\u6765\uff0c\u8fd8\u5c06\u4e00\u76f4\u7b79\u5212\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u4e8b\u52a1\u7684\u53f6\u5efa\u658c\u4ece\u767e\u5fd9\u4e4b\u4e2d\u626f\u5230\u6d77\u5dde\u6765\uff0c\u5012\u4e0d\u662f\u8981\u8ba8\u8bba\u672a\u6765\u9ad8\u6e05\u7535\u89c6\u53d1\u5c55\u65b9\u5411\u7684\uff0c\u76db\u946b\u73af\u7403\u7535\u5668\u65d7\u4e0b\u7684\u5bb6\u7535\u8fde\u9501\u4ea7\u4e1a\u53d1\u5c55\u5230\u73b0\u5728\uff0c\u65d7\u4e0b\u5728\u5168\u56fd\u5404\u5730\u5df2\u7ecf\u6709\u8d85\u8fc7\u516b\u5341\u5bb6\u5bb6\u7535\u5356\u573a\uff0c\u518d\u53d1\u5c55\u4e0b\u53bb\uff0c\u53d7\u5230\u7684\u5236\u7ea6\u5df2\u7ecf\u4e0d\u4ec5\u4ec5\u662f\u8d44\u91d1\u7684\u5236\u7ea6\u4e86\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8dd1\u53bb\u627f\u62c5\u8d77\u9526\u6e56\u5546\u4e8b\u7684\u8d23\u4efb\u8d77\u6765\uff0c\u5c06\u76db\u946b\u73af\u7403\u7535\u5668\u7684\u644a\u5b50\u90fd\u4e22\u5230\u90b5\u5fc3\u6587\u7684\u5934\u4e0a\uff0c\u76db\u946b\u73af\u7403\u7535\u5668\u4e0b\u4e00\u6b65\u8981\u600e\u4e48\u53d1\u5c55\uff0c\u90b5\u5fc3\u6587\u8fd8\u662f\u60f3\u542c\u542c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u7684\u610f\u89c1\u3002</p></article>"}}}]);
