(window.webpackJsonp=window.webpackJsonp||[]).push([[1126],{1149:function(p){p.exports={chapterTitle:null,title:"\u7b2c922\u7ae0 \u8fd8\u4ee5\u989c\u8272",content:"<article><h2>\u7b2c922\u7ae0 \u8fd8\u4ee5\u989c\u8272</h2><p>\u3000\u3000\u76db\u946b\u73af\u7403\u7535\u5668\u9ad8\u901f\u53d1\u5c55\u7684\u8fd9\u4e24\u5e74\uff0c\u5404\u5730\u7684\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u4e5f\u5f97\u5230\u5145\u5206\u7684\u53d1\u5c55\uff0c\u5c31\u50cf\u6d77\u6cf0\u7535\u5668\uff0c\u867d\u8bf4\u53d1\u5c55\u53d7\u5230\u76db\u946b\u7684\u5236\u7ea6\uff0c\u4f46\u662f\u5728\u4e1c\u6d77\u7701\u4ee5\u53ca\u90bb\u8fd1\u7701\u5e02\u4e5f\u53d1\u5c55\u4e86\u5341\u516b\u5bb6\u95e8\u5e97\u3002\u53d1\u5c55\u5230\u73b0\u5728\uff0c\u8fd9\u4e9b\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u4e0e\u76db\u946b\u73af\u7403\u7535\u5668\u5171\u540c\u5c06\u56fd\u5185\u7684\u5e02\u573a\u7c97\u653e\u5f0f\u7684\u74dc\u5206\u5b8c\u6bd5\uff0c\u63a5\u4e0b\u6765\u7684\u6269\u5f20\u5c31\u8981\u8003\u8651\u6765\u81ea\u5176\u4ed6\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u7684\u76f4\u63a5\u7ade\u4e89\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e24\u5e74\u76db\u946b\u5728\u5e9e\u5927\u8d44\u672c\u7684\u652f\u6301\u4e0b\u9ad8\u901f\u6269\u5f20\uff0c\u5bb6\u7535\u5356\u573a\u3001\u95e8\u5e97\u53c8\u5206\u5e03\u5728\u5168\u56fd\u5404\u5730\u7684\u5927\u4e2d\u57ce\u5e02\uff0c\u90b5\u5fc3\u6587\u4e5f\u5f88\u6e05\u9192\u7684\u8ba4\u8bc6\u5230\u9ad8\u901f\u6269\u5f20\u4f1a\u63a9\u76d6\u7ba1\u7406\u4e0a\u5b58\u5728\u7684\u4e00\u4e9b\u4e25\u91cd\u7f3a\u9677\uff0c\u4ed6\u5df2\u7ecf\u91c7\u63aa\u4e00\u4e9b\u63aa\u65bd\u8fdb\u884c\u7ba1\u7406\u4e0a\u7684\u8c03\u6574\uff0c\u4f46\u662f\u8fd9\u9700\u8981\u65f6\u95f4\u3002\u4ed6\u65e2\u4e0d\u60f3\u5728\u4e0e\u5176\u4ed6\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u6b8b\u9177\u7ade\u4e89\u65f6\u66b4\u9732\u4e00\u4e9b\u81f4\u547d\u7684\u7f3a\u9677\uff0c\u4e5f\u4e0d\u60f3\u5728\u6b64\u65f6\u7f13\u4e0b\u6269\u5f20\u7684\u6b65\u4f10\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u5efa\u8bae\u5f88\u76f4\u63a5\uff1a\u201c\u6682\u65f6\u8981\u907f\u514d\u4e0e\u5176\u4ed6\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u7684\u76f4\u63a5\u7ade\u4e89\uff0c\u4f46\u53c8\u4e0d\u505c\u4e0b\u6269\u5f20\u7684\u6b65\u4f10\uff0c\u90a3\u5c31\u5230\u76db\u946b\u8fd8\u6ca1\u6709\u6269\u5f20\u5230\u7684\u57ce\u5e02\u5f00\u8bbe\u624b\u673a\u4e13\u8425\u5e97\u5427\u2014\u2014\u56fd\u5185\u5927\u591a\u6570\u5bb6\u7535\u8fde\u9501\u4f01\u4e1a\u8fd8\u4e0d\u591f\u91cd\u89c6\u624b\u673a\u7684\u9500\u552e\uff0c\u505a\u4e13\u8425\u624b\u673a\u7684\u5356\u573a\u53ef\u4ee5\u907f\u514d\u76f4\u63a5\u7ade\u4e89\uff0c\u53c8\u80fd\u8ba9\u76db\u946b\u7684\u89e6\u89d2\u7ee7\u7eed\u5ef6\u4f38\uff0c\u800c\u4e14\u624b\u673a\u4e13\u8425\u5e97\u7684\u89c4\u6a21\u76f8\u5bf9\u8f83\u5c0f\uff0c\u65b9\u4fbf\u64cd\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u51fa\u4e3b\u610f\u5012\u4e5f\u662f\u4e0d\u5fd8\u8fd9\u8fb9\u7684\u597d\u5904\u2026\u2026\u201d\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u624b\u673a\u662f\u7231\u8fbe\u5f53\u524d\u5728\u56fd\u5185\u7684\u4e3b\u8425\u4e1a\u52a1\uff0c\u5728\u5f20\u606a\u7684\u5efa\u8bae\u4e0b\uff0c\u76db\u946b\u53bb\u5e74\u5c31\u5728\u65d7\u4e0b\u7684\u5bb6\u7535\u5356\u573a\u91cc\u8bbe\u7f6e\u4e86\u624b\u673a\u9500\u552e\u4e13\u67dc\uff0c\u5982\u6b64\u518d\u7ee7\u7eed\u5f00\u62d3\u624b\u673a\u76f4\u8425\u5e97\u4e1a\u52a1\uff0c\u6700\u5148\u5728\u5168\u56fd\u8303\u56f4\u5185\u5f62\u6210\u5b8c\u6574\u7684\u624b\u673a\u8425\u9500\u4f53\u7cfb\uff0c\u5bf9\u7231\u8fbe\u7684\u624b\u673a\u4e1a\u52a1\u4e5f\u6709\u76f4\u63a5\u7684\u4fc3\u8fdb\u4f5c\u7528\u3002</p><p>\u3000\u3000\u201c\u6211\u5c31\u7b97\u6709\u4e9b\u79c1\u5fc3\uff0c\u4f60\u80fd\u5426\u8ba4\u6211\u7684\u4e3b\u610f\u5176\u5b9e\u8fd8\u4e0d\u8d56\uff1f\u201d\u5f20\u606a\u53cd\u95ee\u53f6\u5efa\u658c\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u644a\u644a\u624b\uff0c\u5bf9\u5f20\u606a\u8fd9\u4e48\u8146\u7740\u8138\u7684\u53cd\u95ee\uff0c\u4ed6\u8fd8\u771f\u662f\u6ca1\u6709\u597d\u7684\u5e94\u5bf9\u4e4b\u8bcd\u3002</p><p>\u3000\u3000\u53bb\u5e74\u5341\u6708\uff0c\u56fd\u5185\u79fb\u52a8\u901a\u8baf\u7528\u6237\u603b\u6570\u7ecf\u8fc7\u8fd1\u5341\u5e74\u7684\u79ef\u7d2f\u7ec8\u4e8e\u7a81\u7834\u4e00\u5343\u4e07\u5927\u5173\uff0c\u7136\u800c\u5728\u8fc7\u53bb\u4e00\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u56fd\u5185\u7684\u79fb\u52a8\u901a\u8baf\u7528\u6237\u65b0\u589e\u6570\u91cf\u5c31\u8fbe\u5230\u4e00\u5343\u4e07\uff0c\u4ece\u8fd9\u91cc\u9762\u5c31\u53ef\u4ee5\u770b\u51fa\u56fd\u5185\u6570\u5b57\u624b\u673a\u5e02\u573a\u7684\u7206\u70b8\u6027\u53d1\u5c55\u3002</p><p>\u3000\u3000\u773c\u4e0b\u6b63\u6b65\u5165\u624b\u673a\u4ea7\u4e1a\u7684\u9ec4\u91d1\u65f6\u671f\uff0c\u76db\u946b\u6682\u65f6\u4e0d\u5b9c\u7ee7\u7eed\u518d\u7c97\u653e\u5f0f\u7684\u5f00\u8bbe\u5bb6\u7535\u5356\u573a\uff0c\u7ba1\u7406\u4e0a\u7684\u6574\u987f\u4e0e\u53cd\u601d\u662f\u5fc5\u9700\u7684\uff0c\u90a3\u5728\u76db\u946b\u4f9d\u8d56\u65e2\u6709\u7684\u8d44\u6e90\u5728\u8fd8\u6ca1\u6709\u6d89\u8db3\u5230\u7684\u57ce\u5e02\u5f00\u62d3\u624b\u673a\u4e13\u8425\u4e1a\u52a1\u65e0\u7591\u662f\u4e2a\u597d\u9009\u62e9\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u53f6\u5efa\u658c\u3001\u90b5\u5fc3\u6587\u90fd\u76b1\u7740\u7709\u5934\u601d\u91cf\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5f97\u4e86\u5427\uff0c\u6211\u624d\u4e0d\u76f8\u4fe1\u4f60\u4eec\u6ca1\u6709\u8003\u8651\u8fd9\u4e2a\u95ee\u9898\u3002\u5728\u8fc7\u53bb\u4e00\u5e74\u65f6\u95f4\u91cc\uff0c\u76db\u946b\u5bb6\u7535\u5356\u573a\u7684\u624b\u673a\u9500\u552e\u4e13\u67dc\u5171\u9500\u552e\u624b\u673a\u516b\u5341\u591a\u4e07\u90e8\uff0c\u4e3a\u76db\u946b\u8d21\u732e\u4e86\u63a5\u8fd1\u4e09\u4ebf\u7684\u5229\u6da6\uff0c\u624b\u673a\u4e0a\u7684\u5229\u6da6\u662f\u54ea\u4e00\u7c7b\u5bb6\u7535\u9500\u552e\u80fd\u591f\u6bd4\u7684\uff1f\u8981\u6ca1\u6709\u8003\u8651\u624b\u673a\u4e13\u8425\u5e97\u7684\u95ee\u9898\uff0c\u8bf4\u660e\u4f60\u4eec\u5728\u4f01\u4e1a\u6218\u7565\u7ba1\u7406\u4e0a\u662f\u4e0d\u5408\u683c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u5fc3\u6587\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u4f60\u9762\u524d\u5012\u662f\u5f88\u96be\u88c5\u6a21\u4f5c\u6837\u2026\u2026\u201d\u624b\u673a\u4e13\u8425\u5e97\u7684\u6a21\u5f0f\uff0c\u5f20\u606a\u5728\u53bb\u5e74\u5c31\u63d0\u51fa\u6765\u4e86\uff0c\u800c\u4e14\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u505a\u5927\u4e4b\u540e\uff0c\u9646\u7eed\u6709\u4e9b\u57ce\u5e02\u51fa\u73b0\u4e86\u624b\u673a\u4e13\u8425\u5356\u573a\u7684\u9500\u552e\u6a21\u5f0f\uff0c\u53ea\u4e0d\u8fc7\u90fd\u5904\u4e8e\u53d1\u5c55\u7684\u521d\u671f\u7f62\u4e86\u3002</p><p>\u3000\u3000\u201c\u8bf4\u8bf4\u4f60\u7684\u6838\u5fc3\u95ee\u9898\uff0c\u54ea\u6709\u8fd9\u4e48\u591a\u65f6\u95f4\u8ddf\u4f60\u5728\u8fd9\u91cc\u515c\u5708\u5b50\uff1f\u4eba\u5bb6\u5218\u4e66\u8bb0\u4e00\u901a\u6f14\u8bb2\u8fdb\u884c\u8fd9\u91cc\u4e5f\u624d\u4e09\u5341\u5206\u949f\u800c\u5df2\u2026\u2026\u201d\u5f20\u606a\u6320\u4e86\u6320\u8033\u6735\uff0c\u62ac\u5934\u770b\u4e86\u770b\u659c\u524d\u65b9\u56f4\u5899\u706f\u67f1\u4e0a\u7684\u9ad8\u55fd\u53ed\uff0c\u5728\u79d1\u738b\u5382\u533a\u5185\uff0c\u4e0e\u7231\u8fbe\u7684\u5382\u533a\u5c31\u9694\u7740\u4e00\u6761\u4e24\u8f66\u9053\u7684\u752c\u9053\uff0c\u9ad8\u97f3\u5587\u53ed\u91cc\u8fd8\u5728\u5e7f\u64ad\u5218\u95fb\u6d9b\u7684\u5373\u5174\u6f14\u8bb2\uff0c\u4ed6\u7591\u60d1\u7684\u95ee\u53f6\u5efa\u658c\uff0c\u201c\u4f1a\u4e0d\u4f1a\u662f\u523b\u610f\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u523b\u610f\u7684\uff1f\u201d\u53f6\u5efa\u658c\u6709\u4e9b\u7591\u60d1\u4e0d\u89e3\u3002</p><p>\u3000\u3000\u201c\u9ad8\u97f3\u5587\u53ed\u554a\uff0c\u4f1a\u4e0d\u4f1a\u662f\u60f3\u5bf9\u9526\u6e56\u7684\u5458\u5de5\u6d17\u8111\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u80fd\u5427\uff0c\u4e5f\u6216\u8bb8\u77e5\u9053\u6211\u4eec\u4eca\u5929\u5728\u6d77\u5dde\u3002\u201d</p><p>\u3000\u3000\u201c\u4e5f\u7b97\u662f\u7ed9\u6211\u4eec\u70b9\u989c\u8272\u770b\u770b\uff1f\u201d\u5f20\u606a\u54d1\u7136\u5931\u7b11\uff0c\u201c\u90a3\u76db\u946b\u8fd9\u6b21\u7684\u52a8\u4f5c\u5e45\u5ea6\u5927\u4e00\u4e9b\uff0c\u7b97\u662f\u8fd8\u4ee5\u989c\u8272\uff0c\u76db\u946b\u672a\u6269\u5f20\u5230\u7684\u4e8c\u7c7b\u4ee5\u4e0a\u7684\u57ce\u5e02\u8fd8\u5269\u591a\u5c11\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53eb\u8fd8\u5269\u591a\u5c11\uff1f\u201d\u90b5\u5fc3\u6587\u8bf4\u9053\uff0c\u201c\u9664\u4e86\u534e\u4e1c\u5730\u533a\uff0c\u5176\u4ed6\u5730\u533a\uff0c\u9664\u4e86\u4e2d\u5fc3\u57ce\u5e02\u53d1\u5c55\u4e86\u65d7\u8230\u5e97\u4e4b\u5916\uff0c\u5176\u4ed6\u57ce\u5e02\u51e0\u4e4e\u90fd\u6ca1\u6709\u8986\u76d6\u5230\uff0c\u53ea\u7edf\u8ba1\u4e8c\u7c7b\u4ee5\u4e0a\u7684\u57ce\u5e02\uff0c\u5dee\u4e0d\u591a\u6709\u4e00\u767e\u5ea7\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u662f\u4f9d\u6258\u4e2d\u5fc3\u57ce\u5e02\u7684\u65d7\u8230\u5e97\u8d44\u6e90\uff0c\u5411\u5468\u8fb9\u4e8c\u7c7b\u4ee5\u4e0a\u57ce\u5e02\u53d1\u5c55\u624b\u673a\u4e13\u8425\u5e97\uff0c\u62df\u5b9a\u4e2a\u767e\u57ce\u767e\u5e97\u8ba1\u5212\uff0c\u201d\u5f20\u606a\u5174\u594b\u7684\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u4e3a\u4eca\u5929\u7684\u6302\u724c\u4eea\u5f0f\u8bf7\u4e86\u516d\u4e03\u5341\u5bb6\u5a92\u4f53\u8bb0\u8005\u8fc7\u6765\uff0c\u6211\u770b\u4f60\u4eec\u5c31\u5728\u8fd9\u91cc\u5ba3\u5e03\u8fd9\u4e2a\u8ba1\u5212\uff0c\u573a\u5730\u6211\u501f\u7ed9\u4f60\u4eec\uff0c\u518d\u8ba9\u5e02\u573a\u90e8\u7684\u5458\u5de5\u5c06\u5728\u79d1\u738b\u7684\u5a92\u4f53\u8bb0\u8005\u90fd\u62c9\u8fc7\u6765\uff0c\u6740\u6740\u4ed6\u4eec\u7684\u5fd7\u6c14\uff01\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\u2026\u2026\u201d\u9648\u4fe1\u751f\u7b11\u4e86\u7b11\uff0c\u79d1\u738b\u5c31\u7b97\u83b7\u5f97NEC\u3001\u534e\u590f\u7535\u5b50\u7684\u6ce8\u8d44\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5984\u60f3\u7740\u8981\u64bc\u52a8\u9526\u6e56\u7684\u5730\u4f4d\uff0c\u5f20\u606a\u8fd9\u79cd\u6363\u86cb\u5f0f\u7684\u4e3e\u52a8\uff0c\u4e0d\u8fc7\u662f\u8981\u5632\u8bbd\u4e00\u4e0b\u8fd9\u6b21\u7ad9\u5728\u79d1\u738b\u80cc\u540e\u7684\u5bb6\u4f19\u4eec\uff0c\u5f53\u7136\u4e5f\u662f\u5bf9\u5218\u95fb\u6d9b\u521d\u5230\u6d77\u5dde\u5c31\u5982\u6b64\u9ad8\u8c03\u7684\u4e0e\u79d1\u738b\u6405\u548c\u5728\u4e00\u8d77\u8868\u793a\u4e00\u4e0b\u4e0d\u6ee1\u3002</p><p>\u3000\u3000\u201c\u73b0\u5728\u5c31\u53e3\u5934\u4e0a\u8ba8\u8bba\u4e00\u4e0b\uff0c\u8fd8\u6ca1\u6709\u6210\u6587\u7684\u65b9\u6848\uff0c\u5a92\u4f53\u8bb0\u8005\u8981\u662f\u8ffd\u95ee\u8d77\u6765\uff0c\u90a3\u600e\u4e48\u529e\uff1f\u201d\u90b5\u5fc3\u6587\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u5c31\u5728\u53f0\u4e0a\u80e1\u63b0\u5457\uff0c\u53cd\u6b63\u662f\u5a92\u4f53\u5ba3\u4f20\uff0c\u767e\u57ce\u5343\u5e97\u7684\u6d77\u53e3\u4f60\u4e5f\u4e00\u6837\u53ef\u4ee5\u5938\uff0c\u5ba3\u4f20\u4e0e\u843d\u5b9e\u603b\u662f\u6709\u5dee\u8ddd\uff0c\u8fd9\u662f\u56fd\u60c5\u3002\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u53f6\u5efa\u658c\u3001\u9648\u4fe1\u751f\u3001\u90b5\u5fc3\u6587\u90fd\u7b11\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u662f\u5b58\u5fc3\u7ed9\u9694\u58c1\u7684\u90a3\u4e9b\u4eba\u5fc3\u91cc\u6dfb\u5835\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u624b\u6307\u53e9\u4e86\u53e9\u8111\u95e8\uff0c\u8bf4\u9053\uff1b\u201c\u5bf9\u5a92\u4f53\u6765\u8bf4\uff0c\u8fd8\u662f\u7231\u8fbe\u6709\u5438\u5f15\u529b\uff0c\u8981\u662f\u8fd9\u9879\u8ba1\u5212\u8ddf\u7231\u8fbe\u6ca1\u6709\u4e00\u70b9\u5173\u7cfb\uff0c\u501f\u8fd9\u91cc\u53ec\u5f00\u8bb0\u8005\u4f1a\u4e0d\u4f1a\u592a\u602a\u5f02\u4e86\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u6307\u7740\u53f6\u5efa\u658c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u72d0\u72f8\u5c3e\u5df4\u9732\u51fa\u6765\u4e86\u5427\uff01\u515c\u6765\u515c\u53bb\uff0c\u8fd8\u4e0d\u662f\u8981\u5c06\u4e3b\u610f\u6253\u5230\u8fd9\u8fb9\u5934\u4e0a\u6765\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u644a\u644a\u624b\uff0c\u4e00\u4ed8\u51a4\u5c48\u7684\u6a21\u6837\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u7ed9\u5fc3\u6587\u62d6\u8fc7\u6765\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7684\u786e\u6709\u8003\u8651\u8fc7\u624b\u673a\u4e13\u8425\u5e97\u7684\u95ee\u9898\uff0c\u53ea\u662f\u4e5f\u6ca1\u6709\u8003\u8651\u5230\u8981\u540c\u65f6\u5728\u6240\u6709\u4e8c\u7c7b\u57ce\u5e02\u90fd\u542f\u52a8\uff0c\u6211\u521d\u6b65\u8003\u8651\u7684\u8ba1\u5212\u89c4\u6a21\u8981\u5c11\u5f97\u591a\uff0c\u52a8\u7528\u7684\u8d44\u91d1\u4e5f\u53ea\u6709\u4e00\u4e2a\u4ebf\uff0c\u52c9\u5f3a\u4e0d\u4f1a\u5bf9\u76db\u946b\u7684\u8d44\u91d1\u94fe\u9020\u6210\u5927\u7684\u538b\u529b\uff0c\u201d\u90b5\u5fc3\u6587\u8001\u5b9e\u7684\u5c06\u53f6\u5efa\u658c\u51fa\u5356\u4e86\uff0c\u201c\u5efa\u658c\u770b\u5230\u6211\u7684\u8ba1\u5212\u4e66\uff0c\u8bf4\u89c4\u6a21\u592a\u5c0f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u6ca1\u6709\u5938\u5f20\u5230\u8981\u540c\u65f6\u5360\u9886\u6240\u6709\u4e8c\u7ebf\u57ce\u5e02\uff0c\u201d\u53f6\u5efa\u658c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u767e\u57ce\u767e\u5e97\uff0c\u8fd9\u4e2a\u540d\u5b57\u542c\u8d77\u6765\u5012\u662f\u86ee\u723d\u5229\u7684\uff0c\u5c11\u8bf4\u8981\u4e94\u4e2a\u4ebf\uff0c\u76db\u946b\u6b64\u65f6\u6324\u51fa\u4e24\u4e2a\u4ebf\u5df2\u7ecf\u662f\u6781\u9650\u4e86\uff0c\u6211\u53ef\u662f\u8ba9\u5fc3\u6587\u7167\u4e24\u4e2a\u4ebf\u7684\u6807\u51c6\u6539\u8ba1\u5212\uff0c\u6ca1\u60f3\u5230\u4f60\u7684\u4fe1\u5fc3\u8fd8\u8981\u5f3a\u70c8\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5dee\u4e09\u4e2a\u4ebf\u554a\uff0c\u201d\u5f20\u606a\u6320\u4e86\u6320\u8111\u888b\uff0c\u95ee\u9648\u4fe1\u751f\uff0c\u201c\u7231\u8fbe\u80fd\u6324\u51fa\u8fd9\u7b14\u94b1\u5417\uff1f\u201d\u9526\u6e56\u5546\u4e8b\u5012\u662f\u4e0d\u7f3a\u94b1\uff0c\u4f46\u662f\u9526\u6e56\u5546\u4e8b\u81ea\u6210\u7acb\u4e4b\u521d\u5c31\u5b9a\u4e0b\u6295\u8d44\u7684\u51c6\u5219\uff0c\u9526\u6e56\u539f\u6709\u4f53\u7cfb\u4e0b\u7684\u5b9e\u4e1a\u8981\u5c3d\u53ef\u80fd\u7684\u5c11\u5360\u7528\u9526\u6e56\u5546\u4e8b\u7684\u8d44\u91d1\u3001\u907f\u514d\u7ed9\u9526\u6e56\u5546\u4e8b\u7684\u8d44\u91d1\u88e2\u9020\u6210\u989d\u5916\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u4e09\u4e2a\u4ebf\uff0c\u95ee\u9898\u4e0d\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u6b64\u65f6\u4e00\u4e2a\u6708\u7684\u51c0\u5229\u5c31\u8d85\u8fc7\u4e09\u4e2a\u4ebf\uff0c\u867d\u8bf4\u5bf9\u7814\u7a76\u4f53\u7cfb\u7684\u6295\u5165\u5de8\u5927\uff0c\u4f46\u4e5f\u6709\u4e2a\u6301\u7eed\u6295\u5165\u7684\u8fc7\u7a0b\uff0c\u77ed\u671f\u5185\u989d\u5916\u652f\u51fa\u4e09\u4ebf\uff0c\u4e0d\u4f1a\u7ed9\u7231\u8fbe\u96c6\u56e2\u9020\u6210\u989d\u5916\u7684\u8d44\u91d1\u538b\u529b\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6413\u4e86\u6413\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u5c31\u6210\u4e86\u76db\u946b\u4e0e\u7231\u8fbe\u7684\u5408\u4f5c\u8ba1\u5212\uff0c\u5a92\u4f53\u624d\u6709\u7092\u4f5c\u7684\u70ed\u70b9\uff0c\u8fd9\u6837\u5fc3\u6587\u62df\u7684\u65b9\u6848\u53ef\u4ee5\u5148\u968f\u4fbf\u6539\u52a8\u4e00\u4e0b\u5e94\u4ed8\u5a92\u4f53\uff0c\u5148\u5c06\u8bb0\u8005\u4f1a\u7cca\u5f04\u8fc7\u53bb\u518d\u8bf4\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u95ee\u9053\uff1a\u201c\u771f\u8981\u5f00\u8bb0\u8005\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u201d\u53f6\u5efa\u658c\u5b09\u7b11\u7740\u8bf4\uff0c\u201c\u603b\u5f52\u8981\u8ba9\u4ed6\u4eec\u5fc3\u5934\u8ddf\u585e\u4e86\u4e00\u56e2\u8305\u8349\u4f3c\u7684\u522b\u626d\u7740\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u7b11\u4e86\u7b11\uff0c\u6253\u7535\u8bdd\u5c06\u6768\u4e91\u4e0e\u8d1f\u8d23\u7231\u8fbe\u96c6\u56e2\u8d1f\u8d23\u624b\u673a\u4e1a\u52a1\u7684\u9c81\u5e86\u751f\u627e\u51fa\u6765\uff0c\u5c31\u5f53\u662f\u6f14\u620f\uff0c\u4e5f\u8981\u50cf\u6a21\u50cf\u6837\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4ec5\u8ba1\u7b97\u789f\u673a\u4e1a\u52a1\u7684\u5e7f\u544a\u6295\u653e\u91cf\uff0c\u9526\u6e56\u65d7\u4e0b\u7684\u7231\u8fbe\u96c6\u56e2\u4e0e\u79d1\u738b\u76f8\u5f53\uff0c\u4f46\u662f\u7231\u8fbe\u4eca\u5e74\u63a8\u51fa\u4e24\u6279\u516d\u6b3e\u65b0\u624b\u673a\uff0c\u6bcf\u6b21\u90fd\u4f1a\u5bf9\u4e3b\u529b\u6b3e\u578b\u90fd\u8fdb\u884c\u91cd\u70b9\u5ba3\u4f20\uff0c\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u7684\u5e7f\u544a\u6295\u653e\u91cf\u8981\u8fdc\u8fdc\u8d85\u8fc7\u789f\u673a\uff0c\u518d\u52a0\u5176\u4ed6\u4e00\u4e9b\u652f\u7ebf\u4ea7\u54c1\u7684\u5e7f\u544a\u6295\u653e\uff0c\u7231\u8fbe\u5728\u4ea7\u54c1\u5ba3\u4f20\u4e0e\u54c1\u724c\u5ba3\u4f20\u4e0a\u7684\u5e7f\u544a\u6295\u5165\u8981\u8fdc\u8fdc\u8d85\u8fc7\u79d1\u738b\uff0c\u8fd9\u8fd8\u6ca1\u6709\u8ba1\u7b97\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u9999\u96ea\u6d77\u3001\u76db\u946b\u3001\u65b0\u5149\u7b49\u4f01\u4e1a\u7684\u5e7f\u544a\u6295\u53d1\u3002\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u7684\u4f01\u4e1a\u5bf9\u54c1\u724c\u7684\u5851\u9020\u8fd8\u4e0d\u662f\u5341\u5206\u7684\u91cd\u89c6\uff0c\u867d\u8bf4\u65d7\u4e0b\u4ea7\u54c1\u4e30\u5bcc\u3001\u54c1\u724c\u4f17\u591a\uff0c\u603b\u91cf\u8fd8\u662f\u8fdc\u4e0d\u53ca\u9526\u6e56\uff0cNEC\u7684\u4ea7\u54c1\u8fd8\u6ca1\u6709\u5927\u4e3e\u7684\u8fdb\u5165\u4e2d\u56fd\u7684\u6d88\u8d39\u5e02\u573a\uff0c\u5728\u4e2d\u56fd\u7684\u5e7f\u544a\u6295\u53d1\u5f53\u7136\u5c31\u66f4\u52a0\u4e0d\u53ca\u3002</p><p>\u3000\u3000\u4f01\u4e1a\u4e0e\u5a92\u4f53\u7684\u5173\u7cfb\u4eb2\u5bc6\u5ea6\u662f\u76f4\u63a5\u53d7\u5e7f\u544a\u6295\u653e\u91cf\u5f71\u54cd\u7684\uff0c\u4e00\u5bb6\u5a92\u4f53\u6bcf\u5e74\u4ece\u9526\u6e56\u65d7\u4e0b\u7684\u4f01\u4e1a\u62ff\u8d70\u6570\u767e\u4e07\u3001\u4e0a\u5343\u4e07\u7684\u5e7f\u544a\u8d39\uff0c\u7231\u8fbe\u5e02\u573a\u90e8\u901a\u77e5\u4ed6\u4eec\u8bf4\u6709\u4e2a\u91cd\u8981\u6d3b\u52a8\u53ec\u5f00\u8bb0\u8005\u4f1a\u9700\u8981\u4ed6\u4eec\u914d\u5408\u4e00\u4e0b\uff0c\u4ed6\u4eec\u7edd\u4e0d\u4f1a\u8f7b\u6613\u62d2\u7edd\u7684\u2014\u2014\u518d\u8bf4\u8fd1\u5e74\u6765\u7231\u8fbe\u6bcf\u4e00\u6b21\u5927\u7684\u52a8\u4f5c\u90fd\u610f\u5473\u7740\u4e00\u5219\u91cd\u8981\u7684\u8d22\u7ecf\u65b0\u95fb\u51fa\u6765\u3002</p><p>\u3000\u3000\u5728\u65b0\u79d1\u738b\u5408\u8d44\u63ed\u724c\u4eea\u5f0f\u4f1a\u573a\u7684\u4e00\u4e9b\u5173\u7cfb\u5a92\u4f53\u7684\u8bb0\u8005\u63a5\u5230\u7231\u8fbe\u5e02\u573a\u90e8\u5458\u5de5\u7684\u901a\u77e5\u8bf4\u7231\u8fbe\u4f1a\u5728\u79d1\u738b\u8fd9\u8fb9\u7684\u5178\u793c\u7ed3\u675f\u4e4b\u540e\u53ec\u5f00\u8bb0\u8005\u4f1a\uff0c\u867d\u8bf4\u7231\u8fbe\u7684\u8bb0\u8005\u4f1a\u4e0e\u8fd9\u8fb9\u4e0d\u51b2\u7a81\uff0c\u4f46\u6709\u8bb0\u8005\u5c31\u6709\u4e9b\u6c89\u4e0d\u5728\u6c14\u4e86\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u51fa\u5e2d\u65b0\u79d1\u738b\u63ed\u724c\u4eea\u5f0f\u7684\u9664\u4e86\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u5218\u95fb\u6d9b\u4e4b\u5916\uff0c\u534e\u590f\u7535\u5b50\u603b\u88c1\u845b\u5efa\u5fb7\u3001\u4e09\u4e95\u7269\u4ea7\u4e2d\u56fd\u4ee3\u8868\u6c60\u4f50\u79c0\u85cf\u3001NEC\u4e2d\u56fd\u533a\u603b\u88c1\u4e2d\u6751\u88d5\u4e4b\u3001\u4fe1\u901a\u94f6\u884c\u8463\u4e8b\u957f\u517c\u884c\u957f\u4efb\u4f2f\u540c\u3001\u4e1c\u6d77\u7701\u7ecf\u8d38\u59d4\u526f\u4e3b\u4efb\u5218\u5927\u5e74\u7b49\u4eba\u90fd\u662f\u4e00\u4e9b\u6709\u8eab\u4efd\u6709\u5730\u4f4d\u7684\u4eba\uff0c\u8bb0\u8005\u4eec\u8fd8\u662f\u80fd\u8010\u4e0b\u5fc3\u7b49\u8fd9\u8fb9\u8bb0\u8005\u4f1a\u53ec\u5f00\u5b8c\u518d\u79bb\u5f00\u7684\uff1b\u4e0d\u8fc7\u8138\u4e0a\u7684\u4e0d\u8010\u70e6\u6e10\u6e10\u660e\u663e\u4e86\u4e9b\u3002</p><p>\u3000\u3000\u6709\u4e9b\u8bb0\u8005\u5c31\u76f4\u63a5\u5728\u53f0\u4e0b\u5f00\u8d77\u5c0f\u5dee\uff0c\u788e\u8bed\u804a\u5f00\u6765\uff1a\u201c\u79d1\u738b\u83b7\u5f97\u534e\u590f\u7535\u5b50\u3001NEC\u6ce8\u8d44\u540e\uff0c\u6ce8\u518c\u8d44\u672c\u7a81\u7834\u5341\u4ebf\uff0c\u8fd9\u5728\u56fd\u5185\u4e5f\u6ca1\u6709\u591a\u5c11\u5bb6\uff0c\u518d\u8bf4\u845b\u5efa\u5fb7\u3001\u6c60\u4f50\u79c0\u85cf\u7684\u8eab\u4efd\u6446\u5728\u8fd9\u91cc\uff0c\u4eca\u5929\u7684\u4eea\u5f0f\uff0c\u5373\u4f7f\u7701\u957f\u674e\u8fdc\u6e56\u6ca1\u6709\u7a7a\u53c2\u52a0\uff0c\u526f\u7701\u957f\u603b\u8981\u6765\u4e00\u4e2a\u7684\uff0c\u4e3a\u4ec0\u4e48\u7701\u91cc\u53ea\u6709\u7ecf\u8d38\u59d4\u7684\u526f\u4e3b\u4efb\u4e0b\u6765\uff0c\u770b\u90a3\u4e2a\u526f\u4e3b\u4efb\u7684\u9762\uff0c\u4f3c\u4e4e\u4e5f\u8001\u5927\u7684\u4e0d\u60c5\u613f\uff1f\u6d77\u5dde\u8fd9\u8fb9\u6709\u5e02\u59d4\u4e66\u8bb0\u51fa\u5e2d\uff0c\u867d\u8bf4\u5206\u91cf\u662f\u591f\u4e86\uff0c\u4f46\u662f\u5176\u4ed6\u5b98\u5458\u5462\uff0c\u96be\u9053\u6d77\u5dde\u8fd8\u6709\u5dee\u4e0d\u591a\u91cd\u8981\u6216\u8005\u66f4\u91cd\u8981\u7684\u7ecf\u6d4e\u6d3b\u52a8\u8981\u53c2\u52a0\uff1f\u201d</p><p>\u3000\u3000\u201c\u5144\u5f1f\u662f\u521a\u521a\u5165\u884c\u8fd8\u662f\u521a\u521a\u8ddf\u8d22\u653f\u7ebf\u7684\u65b0\u95fb\uff1f\u201d\u65c1\u8fb9\u6709\u4e2a\u8bb0\u8005\u5c31\u542c\u4e0d\u8fc7\u53bb\u4e86\uff0c\u201c\u7231\u8fbe\u4e0e\u79d1\u738b\u7684\u6069\u6028\u597d\u50cf\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u884c\u4e1a\u5185\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\uff0c\u6d77\u5dde\u5e02\u7684\u8fd9\u4e2a\u65b0\u4e66\u8bb0\u53ef\u80fd\u4e0d\u6e05\u695a\u6c34\u5e95\u2026\u2026\u201d</p><p>\u3000\u3000\u4e00\u5f85\u8fd9\u8fb9\u5ba3\u5e03\u8fdb\u5165\u81ea\u7531\u7b54\u95ee\u9636\u6bb5\uff0c\u5c31\u6709\u4e9b\u8bb0\u8005\u9646\u7eed\u79bb\u573a\u4e86\uff0c\u5750\u5728\u4e3b\u5e2d\u53f0\u4e0a\u7684\u4f17\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u8c22\u5251\u5357\u5bf9\u6b64\u6709\u7ecf\u9a8c\uff0c\u4e5d\u516d\u5e74\u79d1\u738b\u83b7\u5f97\u592e\u89c6\u6807\u738b\u4e4b\u540e\u7684\u9996\u5c4a\u7ecf\u9500\u5546\u5927\u4f1a\u4e0a\uff0c\u5927\u91cf\u7684\u5a92\u4f53\u8bb0\u8005\u5c31\u5728\u4f1a\u573a\u7ed9\u7231\u8fbe\u62c9\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bb0\u7740\u7701\u59d4\u4e66\u8bb0\u7684\u529d\u544a\u8981\u7ef4\u6301\u6d77\u5dde\u56e2\u7ed3\u7a33\u5b9a\u7684\u5c40\u9762\uff0c\u7279\u610f\u8ba9\u6768\u4e91\u8d76\u8fc7\u6765\u8bf7\u65b0\u5e02\u59d4\u4e66\u8bb0\u5218\u95fb\u6d9b\u5230\u7231\u8fbe\u53bb\u5217\u5e2d\u8bb0\u8005\u4f1a\uff0c\u81f3\u5c11\u8981\u5728\u5a92\u4f53\u8bb0\u8005\u524d\u7ef4\u6301\u6d77\u5dde\u56e2\u7ed3\u7a33\u5b9a\u7684\u5c40\u9762\uff1b\u987a\u4fbf\u4e5f\u5c06\u845b\u5efa\u5fb7\u4e0e\u7701\u7ecf\u8d38\u59d4\u526f\u4e3b\u4efb\u5218\u5927\u5e74\u4ee5\u53ca\u4fe1\u901a\u94f6\u884c\u8463\u4e8b\u957f\u517c\u884c\u957f\u4efb\u4f2f\u540c\u4e5f\u8bf7\u8fc7\u53bb\uff0c\u8868\u9762\u7684\u6577\u884d\u4e5f\u662f\u9700\u8981\u7684\uff1b\u5bf9\u6c60\u4f50\u79c0\u85cf\u3001\u4e2d\u6751\u88d5\u4e4b\u5219\u7406\u90fd\u672a\u7406\uff0c\u8c22\u5251\u5357\u81ea\u7136\u4e5f\u6ca1\u6709\u8ddf\u8fc7\u53bb\u770b\u70ed\u95f9\u7684\u5fc3\u601d\uff0c\u5176\u4ed6\u50cf\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u3001\u5468\u5174\u4e1c\u7b49\u89d2\u8272\u5219\u76f4\u63a5\u7ed9\u5ffd\u7565\u6389\u4e86\uff0c\u4ed6\u4eec\u8981\u8ddf\u7740\u8fc7\u53bb\uff0c\u4e5f\u4e0d\u4f1a\u88ab\u62d2\u7edd\u5c31\u662f\u4e86\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u3001\u5218\u95fb\u6d9b\u90fd\u80fd\u60f3\u5230\u9526\u6e56\u7a81\u7136\u53ec\u5f00\u8bb0\u8005\u4f1a\u5c31\u662f\u4e3a\u4e86\u62c6\u8fd9\u91cc\u8fb9\u8fb9\u7684\u53f0\uff0c\u538b\u7740\u5fc3\u5934\u7684\u6012\u6c14\uff0c\u4ed6\u4eec\u4eca\u5929\u5927\u5f20\u65d7\u9f13\uff0c\u4e5f\u662f\u8981\u7ed9\u9526\u6e56\u597d\u770b\u2014\u2014\u8fd9\u5e74\u5934\u6c38\u8fdc\u90fd\u5f62\u52bf\u6bd4\u4eba\u5f3a\uff0c\u845b\u5efa\u5fb7\u3001\u5218\u95fb\u6d9b\u4e5f\u60f3\u770b\u770b\u9526\u6e56\u8fd9\u6b21\u80fd\u73a9\u51fa\u4ec0\u4e48\u82b1\u6837\u6765\uff0c\u4e5f\u5c31\u6ca1\u6709\u62d2\u7edd\u53bb\u53c2\u52a0\u7231\u8fbe\u7684\u8bb0\u8005\u4f1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u53f6\u5efa\u658c\u61d2\u5f97\u4e0e\u845b\u5efa\u5fb7\u3001\u5218\u95fb\u6d9b\u7b49\u4eba\u865a\u4e0e\u59d4\u86c7\uff0c\u65e9\u4e00\u6b65\u79bb\u5f00\u4e86\u5de5\u5382\uff0c\u845b\u5efa\u5fb7\u3001\u5218\u95fb\u6d9b\u8ddf\u7740\u8bb0\u8005\u5230\u7231\u8fbe\u7684\u5382\u533a\uff0c\u53ea\u6709\u9648\u4fe1\u751f\u3001\u90b5\u5fc3\u6587\u7b49\u4eba\u51fa\u9762\u4e3b\u6301\u8bb0\u8005\u4f1a\u516c\u5e03\u4e34\u65f6\u8ba8\u8bba\u51fa\u6765\u7684\u201c\u9884\u8ba1\u8017\u8d44\u5341\u4ebf\u7684\u767e\u57ce\u767e\u5e97\u65b9\u6848\u201d\u3002</p><p>\u3000\u3000\u7531\u4e8e\u90b5\u5fc3\u6587\u5bf9\u624b\u673a\u4e13\u8425\u95ee\u9898\u8fdb\u884c\u8fc7\u6df1\u5165\u7684\u601d\u8003\uff0c\u4f7f\u5f97\u8fd9\u573a\u8bb0\u8005\u4f1a\u4e0d\u4f1a\u592a\u7c97\u7cd9\uff0c\u4f46\u662f\u6240\u8c13\u7684\u201c\u8017\u8d44\u5341\u4ebf\u201d\u9488\u5bf9\u6027\u5c31\u592a\u660e\u663e\u4e86\uff0c\u5218\u95fb\u6d9b\u5728\u542c\u7231\u8fbe\u5e02\u573a\u90e8\u603b\u88c1\u6768\u4e91\u8bfb\u65b0\u95fb\u7a3f\uff0c\u5fcd\u4e0d\u4f4f\u8981\u51b7\u54fc\u4e86\uff0c\u4f46\u662f\u845b\u5efa\u5fb7\u5374\u660e\u663e\u611f\u5230\u8fd9\u4e2a\u8ba1\u5212\u7ed9\u4ed6\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u4e09\u5bb6\u56fd\u4ea7\u624b\u673a\u4f01\u4e1a\u52a0\u4e0a\u79d1\u738b\u9ad8\u79d1\u8fd9\u5bb6\u8d34\u8054\u4fe1\u54c1\u724c\u7684\u4f01\u4e1a\uff0c\u501f\u7740\u539f\u90ae\u7535\u7cfb\u7edf\u65d7\u4e0b\u7684\u9500\u552e\u7f51\u7edc\uff0c\u624b\u673a\u9500\u552e\u603b\u91cf\u8fd8\u53ea\u6bd4\u9526\u6e56\u7565\u9ad8\u4e00\u4e9b\uff0c\u4eca\u5e74\u56fd\u52a1\u9662\u5728\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7ec4\u5efa\u7684\u57fa\u7840\u4e0a\u8fdb\u4e00\u6b65\u8981\u6c42\u7535\u4fe1\u884c\u4e1a\u201c\u6253\u7834\u5784\u65ad\u3001\u5f15\u8fdb\u7ade\u4e89\u201d\uff0c\u800c\u9526\u6e56\u4e0e\u76db\u946b\u8054\u624b\u7684\u8fd9\u4e2a\u65b9\u6848\u53c8\u5c06\u663e\u8457\u52a0\u5f3a\u7231\u8fbe\u624b\u673a\u5728\u7ec8\u7aef\u5e02\u573a\u7684\u9500\u552e\u80fd\u529b\uff0c\u8981\u662f\u56db\u6253\u4e00\u90fd\u7ed9\u9526\u6e56\u8fdc\u8fdc\u7684\u629b\u5f00\uff0c\u534e\u590f\u7535\u5b50\u7684\u8138\u5c31\u66f4\u96be\u770b\u4e86\u3002</p></article>"}}}]);
