(window.webpackJsonp=window.webpackJsonp||[]).push([[1130],{1153:function(p){p.exports={chapterTitle:null,title:"\u7b2c1127\u7ae0 \u6295\u77f3\u95ee\u8def",content:"<article><h2>\u7b2c1127\u7ae0 \u6295\u77f3\u95ee\u8def</h2><p>\u3000\u3000\u7ad9\u5728\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u6307\u519b\u90e8\u5927\u697c\u7684\u697c\u9876\u773a\u671b\u8fdc\u5904\uff0c\u8fdc\u5904\u7684\u6d77\u6c34\u5c31\u50cf\u84dd\u9ed1\u8272\u7684\u7ed2\u7f0e\u5b50\uff0c\u4e1c\u5c71\u5c9b\u90a3\u8fb9\u7684\u5de5\u5730\u4e5f\u662f\u663c\u591c\u4e0d\u606f\uff0c\u706f\u706b\u901a\u660e\uff0c\u5c31\u50cf\u5d4c\u5728\u84dd\u9ed1\u7f0e\u5b50\u4e0a\u7684\u4e00\u9897\u660e\u73e0\uff0c\u9ad8\u8038\u7684\u540a\u5854\u4e5f\u9690\u7ea6\u53ef\u89c1\u3002</p><p>\u3000\u3000\u5317\u9762\u6e2f\u53e3\u5de5\u4e1a\u56ed\u91cc\u7684\u5de5\u5730\u4e5f\u662f\u663c\u591c\u4e0d\u606f\uff0c\u6c5f\u9759\u6db5\u624b\u522b\u5728\u8170\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u65e9\u524d\u7684\u89c4\u5212\u53ef\u65e0\u6cd5\u60f3\u8c61\u6b64\u65f6\u7684\u6c14\u8c61\uff0c\u8fd9\u7247\u571f\u5730\u7b97\u662f\u8dd1\u6b65\u8fdb\u5165\u5de5\u4e1a\u5316\u4e86\uff0c\u6709\u4e86\u8fd9\u67b6\u52a8\u529b\u5f3a\u52b2\u7684\u53d1\u52a8\u673a\uff0c\u6c5f\u5357\u7701\u7684\u7ecf\u6d4e\u817e\u98de\u6307\u65e5\u53ef\u5f85\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u4e1c\u5c71\u6e2f\u53ca\u6e2f\u53e3\u5de5\u4e1a\u533a\u4e00\u671f\u5de5\u7a0b\u8ba1\u5212\u5728\u96f6\u4e8c\u5e74\u4e4b\u524d\u5168\u90e8\u5efa\u6210\uff0c\u6295\u8d44\u603b\u989d\u5c31\u5c06\u8d85\u8fc7\u4e00\u767e\u4ebf\u7f8e\u5143\u3002\u5728\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u542f\u52a8\u4e4b\u524d\uff0c\u65b0\u4ead\u5e02\u56fd\u6c11\u751f\u4ea7\u603b\u503c\u624d\u516b\u5341\u4ebf\uff0c\u4e5d\u4e5d\u5e74\u9aa4\u589e\u5230\u4e00\u767e\u4e5d\u5341\u4ebf\uff0c\u542b\u571f\u5730\u51fa\u5165\u6536\u76ca\u5728\u5185\u7684\u5730\u65b9\u8d22\u653f\u6536\u5165\u4e5f\u4ece\u4e0d\u8db3\u5341\u4ebf\u9aa4\u589e\u5230\u4e8c\u5341\u4e8c\u4ebf\u3002</p><p>\u3000\u3000\u65b0\u4ead\u5e02\u7684\u7ecf\u6d4e\u4e4b\u524d\u5728\u6c5f\u5357\u7701\u90fd\u5c5e\u4e8e\u4e2d\u7b49\u504f\u4e0b\u6c34\u5e73\uff0c\u77ed\u77ed\u4e00\u5e74\u65f6\u95f4\uff0c\u5c31\u8dc3\u5c45\u7b2c\u4e8c\uff0c\u4ec5\u6b21\u4e8e\u91d1\u5c71\u5e02\u3002\u7b49\u4e1c\u5c71\u6e2f\u53ca\u6e2f\u53e3\u5de5\u4e1a\u533a\u4e00\u671f\u5de5\u7a0b\u5efa\u6210\uff0c\u65b0\u4ead\u5e02\u56fd\u6c11\u751f\u4ea7\u603b\u503c\u5c06\u8f7b\u677e\u677e\u7684\u7a81\u7834\u4e94\u767e\u4ebf\uff0c\u5730\u65b9\u8d22\u653f\u6536\u5165\u4e5f\u5c06\u4e0e\u5317\u9762\u7684\u6d77\u5dde\u5e02\u8ffd\u9f50\uff0c\u91d1\u5c71\u5e02\u8981\u662f\u4e0d\u52aa\u529b\u4e00\u628a\uff0c\u5f88\u53ef\u80fd\u5728\u4e24\u4e09\u5e74\u540e\u5c31\u4f1a\u7ed9\u65b0\u4ead\u5e02\u8d85\u8d8a\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u4e5d\u4e5d\u5e74\u56fd\u6c11\u751f\u4ea7\u603b\u503c\u624d\u4e00\u5343\u516b\u767e\u4ebf\u7684\u6c5f\u5357\u7701\u6765\uff0c\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u5bf9\u5168\u7701\u7ecf\u6d4e\u7684\u76f4\u63a5\u62c9\u52a8\u5c31\u5230\u8d85\u8fc75\uff05\uff0c\u7684\u786e\u53ef\u4ee5\u7b97\u5f97\u4e0a\u4e00\u67b6\u52a8\u529b\u5f3a\u52b2\u7684\u53d1\u52a8\u673a\u3002</p><p>\u3000\u3000\u9762\u5bf9\u6fc0\u52a8\u4eba\u5fc3\u7684\u7ecf\u6d4e\u84dd\u56fe\uff0c\u5f20\u606a\u5fc3\u91cc\u5374\u7422\u78e8\u56fd\u5bb6\u7ecf\u8d38\u59d4\u526f\u4e3b\u4efb\u6c5f\u654f\u4e4b\u5c06\u51fa\u4efb\u4e1c\u6d77\u7701\u7701\u957f\u8fd9\u4e8b\u6765\u3002</p><p>\u3000\u3000\u6bd4\u8f83\u8d77\u653f\u6cbb\u7ecf\u6d4e\u7406\u5ff5\u4e0d\u540c\u7684\u5b98\u5458\uff0c\u8d2a\u5b98\u6c61\u540f\u8981\u5bb9\u6613\u5e94\u4ed8\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5e74\u6765\uff0c\u5c06\u6765\u76f8\u5f53\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\u5c06\u4e5f\u5982\u6b64\uff0c\u4e2d\u592e\u7684\u7ecf\u6d4e\u653f\u7b56\u5728\u4fdd\u6301\u6574\u4f53\u65b9\u5411\u4e0d\u53d8\u7684\u540c\u65f6\uff0c\u4e5f\u6709\u76f8\u5f53\u7a0b\u5ea6\u7684\u6ce2\u52a8\u3002</p><p>\u3000\u3000\u5bf9\u975e\u516c\u7ecf\u6d4e\u4f53\u5728\u56fd\u5185\u7684\u53d1\u5c55\u653f\u7b56\u95ee\u9898\uff0c\u4e2d\u592e\u65e2\u6709\u66f4\u4e3a\u6fc0\u8fdb\u7684\u62e5\u62a4\u6d3e\uff0c\u4e5f\u6709\u76f8\u5bf9\u4fdd\u5b88\u7684\u62b5\u5236\u6d3e\u3002\u6c5f\u654f\u4e4b\u5c31\u662f\u4e2d\u592e\u90e8\u59d4\u91cc\u4e3b\u5f20\u5f3a\u5316\u56fd\u6709\u8d44\u672c\u5730\u4f4d\u3001\u62b5\u5236\u975e\u516c\u7ecf\u6d4e\u4f53\u8fc7\u5ea6\u6269\u5f20\u7684\u4ee3\u8868\u4eba\u7269\u4e4b\u4e00\uff0c\u7279\u522b\u662f\u57fa\u7840\u4ea7\u4e1a\u9886\u57df\uff0c\u90fd\u76f8\u5f53\u575a\u51b3\u7684\u62b5\u5236\u5411\u6c11\u8425\u8d44\u672c\u653e\u5f00\u3002</p><p>\u3000\u3000\u6839\u636e\u7ecf\u6d4e\u5f62\u52bf\u7684\u4e0d\u540c\uff0c\u4e3b\u5bfc\u4e2d\u592e\u7ecf\u6d4e\u653f\u7b56\u8d70\u5411\u7684\u601d\u8def\u603b\u4f1a\u9002\u65f6\u7684\u51fa\u73b0\u4e00\u4e9b\u5fae\u5999\u7684\u8c03\u6574\uff0c\u4f46\u662f\u8fd9\u79cd\u5fae\u5999\u7684\u8c03\u6574\u4f20\u5bfc\u5230\u5730\u65b9\u4e0a\uff0c\u6ce2\u52a8\u4f1a\u53d8\u5f97\u66f4\u5927\uff0c\u6bd5\u7adf\u5f53\u524d\u5730\u65b9\u4e0a\u7684\u6267\u653f\u65bd\u653f\u5e26\u6709\u66f4\u591a\u9c9c\u660e\u7684\u4e2a\u4eba\u98ce\u683c\u4e0e\u70d9\u5370\uff0c\u524d\u526f\u603b\u7406\u6c5f\u9759\u6db5\u7684\u8fd9\u4e00\u5242\u6295\u77f3\u95ee\u8def\u5f0f\u7684\u9884\u9632\u9488\u8fd8\u771f\u662f\u8ba9\u4eba\u6709\u4e9b\u62c5\u5fe7\u554a\u3002</p><p>\u3000\u3000\u5fc3\u91cc\u867d\u7136\u7422\u78e8\u7740\u4e8b\u60c5\uff0c\u8ddf\u6c5f\u9759\u6db5\u7545\u8c08\u65b0\u4ead\u53d1\u5c55\u5f62\u52bf\u4e4b\u65f6\uff0c\u5374\u4e5f\u8c08\u7b11\u98ce\u751f\uff0c\u4f3c\u4e4e\u538b\u6839\u513f\u5c31\u6ca1\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u53ea\u6709\u7740\u4e3a\u524d\u526f\u603b\u7406\u591c\u8bbf\u5de5\u5730\u7684\u610f\u5916\u6b23\u559c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u961a\u552f\u6d9b\u7b49\u65b0\u4ead\u5b98\u5458\u5c06\u524d\u526f\u603b\u7406\u6c5f\u9759\u6db5\u9001\u5230\u6c5f\u5bb6\u5728\u897f\u4e61\u9547\uff0c\u6ca1\u6709\u803d\u6401\uff0c\u5c31\u544a\u8f9e\u79bb\u53bb\u3002</p><p>\u3000\u3000\u5e94\u6c5f\u9759\u6db5\u8bf8\u4e8b\u4e0d\u60ca\u52a8\u5730\u65b9\u7684\u8bf7\u6c42\uff0c\u5e02\u91cc\u660e\u5929\u4e0d\u4f1a\u6d3e\u8b66\u8f66\u62a4\u9001\u4ed6\u4eec\u79bb\u5883\uff1b\u8bdd\u867d\u7136\u8fd9\u4e48\u8bf4\uff0c\u961a\u552f\u6d9b\u8fd8\u662f\u53ee\u5631\u5e02\u516c\u5b89\u5c40\u5f80\u897f\u4e61\u589e\u6d3e\u6cbb\u5b89\u6c11\u8b66\uff0c\u786e\u4fdd\u524d\u526f\u603b\u7406\u53ca\u5bb6\u4eba\u7edd\u5bf9\u4e0d\u4f1a\u5728\u897f\u4e61\u6709\u4ec0\u4e48\u4e0d\u6109\u5feb\u7684\u4f53\u9a8c\u3002</p><p>\u3000\u3000\u6c5f\u5bb6\u8001\u5b85\u4f4d\u4e8e\u897f\u4e61\u9547\u4e1c\u7684\u519c\u6751\uff0c\u5176\u4ed6\u5730\u533a\u8fde\u4e61\u9547\u516c\u8def\u90fd\u6ca1\u6709\u5b8c\u5168\u786c\u5316\uff0c\u897f\u4e61\u9547\u65e9\u5c31\u6751\u6751\u901a\u4e86\u67cf\u6cb9\u8def\uff0c\u6cbf\u8def\u8fd8\u90fd\u7ad6\u8d77\u8def\u706f\uff0c\u5f7b\u591c\u706f\u706b\u901a\u660e\u3002</p><p>\u3000\u3000\u6c5f\u9759\u6db5\u4e5f\u77e5\u9053\u6709\u4e9b\u4e8b\u60c5\u662f\u5730\u65b9\u4e0a\u505a\u7ed9\u4ed6\u770b\u7684\uff0c\u6240\u4ee5\u8fd9\u4e9b\u5e74\u6765\u6781\u5c11\u56de\u8001\u5bb6\u6765\uff0c\u8fd9\u6b21\u662f\u5bb6\u65cf\u91cc\u4e00\u4e2a\u8840\u7f18\u8f83\u8fd1\u7684\u957f\u8f88\u901d\u4e16\uff0c\u65e9\u5e74\u53d7\u8fc7\u4eba\u5bb6\u6069\u60e0\uff0c\u8fd9\u624d\u56de\u65b0\u4ead\u6765\u3002</p><p>\u3000\u3000\u8f66\u5230\u6751\u53e3\uff0c\u6c5f\u9759\u6db5\u8ba9\u5b59\u5973\u6c5f\u6e44\u966a\u4ed6\u8d70\u7740\u56de\u8001\u5b85\u3002</p><p>\u3000\u3000\u8001\u5b85\u4f4d\u4e8e\u4e00\u5ea7\u6625\u6c34\u79c0\u7f8e\u7684\u6c34\u586c\u5b50\u91cc\uff0c\u65e9\u5c31\u7531\u9547\u653f\u5e9c\u51fa\u8d44\u7ffb\u4fee\u8fc7\uff0c\u5f53\u5e74\u7684\u571f\u576f\u8986\u74e6\u7684\u9662\u5b50\uff0c\u6b64\u65f6\u7cbe\u81f4\u6574\u9970\u5982\u56ed\u6797\uff0c\u6c34\u7554\u758f\u67f3\u6276\u659c\uff0c\u4ece\u516c\u8def\u659c\u51fa\u4e00\u6761\u5c94\u53e3\u901a\u8fc7\u586c\u5b50\u91cc\u3002</p><p>\u3000\u3000\u8def\u53e3\u6709\u4e00\u4e2a\u9ad8\u5927\u7684\u80cc\u666f\u7ad9\u5728\u90a3\u91cc\u62bd\u70df\uff0c\u7ad9\u5728\u8def\u706f\u4e0b\u62c9\u5f00\u4e00\u9053\u7626\u957f\u7684\u5f71\u5b50\uff0c\u770b\u89c1\u6c5f\u9759\u6db5\u4e0e\u6c5f\u6e44\u56de\u6765\uff0c\u4ed6\u5c06\u70df\u5c41\u80a1\u4e22\u5230\u8def\u65c1\u7684\u5730\u57c2\u91cc\uff0c\u95ee\u9053\uff1a\u201c\u8bf4\u662f\u53bb\u770b\u4e00\u773c\uff0c\u600e\u4e48\u53bb\u4e86\u8fd9\u4e48\u4e45\uff1f\u201d</p><p>\u3000\u3000\u201c\u7238\uff0c\u4f60\u731c\u6211\u4eec\u9047\u89c1\u8c01\u4e86\uff1f\u201d\u6c5f\u6e44\u6545\u4f5c\u795e\u79d8\u7684\u95ee\u9053\uff0c\u53c8\u8f6c\u5934\u8ddf\u6c5f\u9759\u6db5\u8bf4\u9053\uff0c\u201c\u4ed6\u4e5f\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u7279\u522b\u7684\u554a\uff0c\u4e00\u53ea\u9f3b\u5b50\u4e24\u53ea\u773c\u775b\uff0c\u6bd4\u522b\u4eba\u4e0d\u591a\uff0c\u6bd4\u522b\u4eba\u4e0d\u5c11\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u90a3\u771f\u662f\u5de7\u4e86\uff0c\u5728\u56fd\u5185\u4e0a\u5c42\uff0c\u4ed6\u8981\u6bd4\u4e2d\u592e\u59d4\u5458\u66f4\u53d7\u5230\u5173\u6ce8\uff0c\u201d\u6c5f\u654f\u4e4b\u77e5\u9053\u5f20\u606a\u4eca\u5929\u4eba\u5728\u65b0\u4ead\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u5973\u513f\u966a\u7740\u8001\u7236\u4eb2\u5230\u4e1c\u5c71\u6e2f\u5de5\u5730\u8d70\u4e00\u8d70\uff0c\u9047\u4e0a\u4ed6\u4eec\u4ee4\u4eba\u4e00\u70b9\u90fd\u4e0d\u89c9\u5f97\u610f\u5916\uff0c\u4ed6\u53c8\u4ece\u515c\u91cc\u62ff\u51fa\u9999\u70df\u6765\u70b9\u4e0a\uff0c\u201c\u9526\u6e56\u5728\u4e1c\u6d77\u7b97\u662f\u6839\u57fa\u6df1\u539a\uff0c\u5bfb\u5e38\u4eba\u7b49\u90fd\u62db\u60f9\u4e0d\u8d77\uff0c\u5373\u4f7f\u6211\u89c9\u5f97\u4e1c\u6d77\u73b0\u6709\u7684\u7ecf\u6d4e\u4ea7\u4e1a\u653f\u7b56\u4e2d\u6709\u8c03\u6574\u7684\u5fc5\u8981\uff0c\u4e5f\u4e0d\u4f1a\u523b\u610f\u7684\u9488\u5bf9\u9526\u6e56\uff0c\u6211\u505a\u4e8b\u53ea\u6c42\u95ee\u5fc3\u65e0\u6127\u2026\u2026\u201d</p><p>\u3000\u3000\u5341\u5e74\u6d69\u52ab\u671f\u95f4\uff0c\u6c5f\u9759\u6db5\u53d7\u5230\u4e0d\u516c\u6b63\u7684\u6279\u6597\uff0c\u6c5f\u654f\u4e4b\u4e0e\u5f53\u65f6\u8fd8\u6ca1\u6709\u6210\u5e74\u7684\u5f1f\u5f1f\u3001\u59b9\u59b9\u90fd\u56de\u5230\u5bb6\u4e61\uff0c\u90a3\u65f6\u5728\u65b0\u4ead\u7684\u5904\u5883\u4e5f\u5f88\u8270\u96be\uff0c\u53d7\u5230\u5bb6\u91cc\u7684\u8001\u4eba\u5e87\u62a4\u624d\u71ac\u8fc7\u5341\u5e74\u6d69\u52ab\u3002\u8fd9\u6b21\u662f\u90a3\u4f4d\u8001\u4eba\u8fc7\u4e16\uff0c\u6c5f\u654f\u4e4b\u624d\u5728\u767e\u5fd9\u4e4b\u4e2d\u966a\u8001\u7236\u4eb2\u56de\u8001\u5bb6\u6765\u3002</p><p>\u3000\u3000\u201c\u8bdd\u662f\u8fd9\u4e48\u8bf4\uff0c\u4eba\u5bb6\u672a\u5fc5\u4f1a\u8fd9\u4e48\u60f3\u554a\uff0c\u6211\u4e4b\u524d\u662f\u6709\u8fd9\u6837\u7684\u62c5\u5fe7\uff0c\u4e5f\u89c9\u5f97\u6211\u8fd9\u4e2a\u8001\u5bb6\u4f19\u8fd8\u6709\u5f20\u8001\u8138\u53ef\u4ee5\u5356\u4e9b\u9762\u5b50\u51fa\u53bb\uff0c\u201d\u6c5f\u9759\u6db5\u5fae\u5fae\u4e00\u53f9\uff0c\u8896\u624b\u800c\u7acb\uff0c\u4ed6\u8863\u886b\u5355\u8584\uff0c\u5728\u6e05\u5bd2\u7684\u6625\u591c\u91cc\uff0c\u767d\u53d1\u82cd\u82cd\uff0c\u7626\u9aa8\u5d99\u5ccb\uff0c\u4eba\u5374\u5341\u5206\u7684\u7cbe\u795e\uff0c\u201c\u4eba\u6211\u89c1\u5230\u4e86\uff0c\u5c31\u89c9\u5f97\u6709\u4e9b\u4e1c\u897f\u6ca1\u6709\u5fc5\u8981\u70b9\u900f\u2026\u2026\u505a\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f60\u53ea\u8981\u95ee\u5fc3\u65e0\u6127\u4e5f\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u505a\uff0c\u6211\u6709\u5206\u5bf8\u7684\u2026\u2026\u201d\u6c5f\u654f\u4e4b\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5e74\u8f7b\u4e00\u4ee3\u4eba\u91cc\uff0c\u777f\u667a\u660e\u9510\u4e0d\u89c1\u6709\u6bd4\u5f97\u4e0a\u4ed6\u7684\u3002\u672a\u89c1\u4e4b\u524d\uff0c\u89c9\u5f97\u9526\u6e56\u80fd\u6709\u773c\u4e0b\u4f1f\u4e1a\uff0c\u771f\u80fd\u8bf4\u662f\u5947\u8ff9\uff1b\u89c1\u8fc7\u4e4b\u540e\uff0c\u5c31\u89c9\u5f97\u603b\u5e94\u8be5\u6709\u4eba\u80fd\u505a\u51fa\u8fd9\u756a\u6210\u5c31\u51fa\u6765\uff0c\u6070\u597d\u662f\u4ed6\u3002\u201d\u6c5f\u9759\u6db5\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u7237\u7237\uff0c\u5f88\u5c11\u542c\u4f60\u8fd9\u4e48\u5938\u4eba\u5462\uff0c\u201d\u6c5f\u6e44\u633d\u7740\u6c5f\u9759\u6db5\u7684\u80f3\u818a\uff0c\u5934\u63a2\u5230\u524d\u9762\u626d\u8fc7\u6765\u770b\u7740\u5979\u7237\u7237\u7684\u773c\u775b\uff0c\u201c\u6211\u600e\u4e48\u5c31\u89c9\u5f97\u4ed6\u7a00\u758f\u5e73\u5e38\u5f97\u5f88\uff0c\u5c31\u957f\u7740\u4e00\u5f20\u5c0f\u767d\u8138\uff0c\u4e3a\u5c0f\u660e\u661f\u4e89\u98ce\u5403\u918b\u5012\u771f\u50cf\u4ed6\u8be5\u505a\u7684\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u770b\u4eba\u5149\u7528\u773c\u775b\u770b\u8fd8\u4e0d\u591f\uff0c\u201d\u6c5f\u9759\u6db5\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u8fd9\u8f88\u5b50\u8ba4\u8bc6\u65e0\u6570\u7684\u4eba\uff0c\u89c1\u8fc7\u65e0\u6570\u5f20\u9762\u5b54\uff0c\u770b\u4eba\u8bc6\u4eba\u8fd8\u662f\u6709\u4e9b\u81ea\u4fe1\u7684\u3002\u4f60\u554a\uff0c\u73b0\u5728\u8fd8\u592a\u5e74\u8f7b\u4e86\uff0c\u201d\u53c8\u8ddf\u513f\u5b50\u6c5f\u654f\u4e4b\u8bf4\u9053\uff0c\u201c\u4f60\u65e5\u540e\u4e0e\u4ed6\u89c1\u9762\uff0c\u5c31\u77e5\u9053\u6211\u8bf4\u7684\u662f\u4e0d\u662f\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u80fd\u4e0d\u76f8\u4fe1\u4f60\u7684\u773c\u5149\uff1f\u201d\u6c5f\u654f\u4e4b\u7b11\u9053\uff0c\u201c\u5916\u9762\u662f\u4e00\u4e9b\u5e72\u6270\u89c6\u7ebf\u7684\u4f20\u95fb\uff0c\u770b\u4eba\uff0c\u542c\u5176\u8a00\u770b\u5176\u884c\uff0c\u4f20\u95fb\u7ec8\u5f52\u53ea\u662f\u4f20\u95fb\u800c\u5df2\uff0c\u6211\u77e5\u9053\u53d6\u820d\u7684\u2026\u2026\u201d\u8fb9\u8d70\u8fb9\u804a\u56de\u5230\u8001\u5b85\uff0c\u6c5f\u654f\u4e4b\u53c8\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u65e9\u4e9b\u7761\u5427\uff0c\u660e\u5929\u8fd8\u8981\u8d77\u65e9\u8d76\u5230\u91d1\u5c71\u4e58\u98de\u673a\u56de\u5317\u4eac\u5462\uff1f\u201d\u89c1\u5973\u513f\u6c5f\u6e44\u540e\u9762\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\u7684\u4f3c\u4e4e\u60f3\u7740\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u95ee\u5979\uff0c\u201c\u524d\u9762\u8bdd\u90a3\u4e48\u591a\uff0c\u540e\u9762\u600e\u4e48\u53c8\u4e0d\u542d\u58f0\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u53c8\u4e0d\u8d76\u65f6\u95f4\uff0c\u7237\u7237\uff0c\u6211\u966a\u4f60\u5750\u706b\u8f66\u56de\u5317\u4eac\u53bb\u884c\u4e0d\u884c\uff1f\u201d\u6c5f\u6e44\u633d\u7740\u6c5f\u9759\u6db5\u7684\u80f3\u818a\uff0c\u8bf4\u9053\uff0c\u201c\u5c0f\u65f6\u5019\u5750\u4f60\u7684\u4e13\u673a\u98de\u6765\u98de\u53bb\uff0c\u5374\u6ca1\u6709\u4ec0\u4e48\u673a\u4f1a\u5750\u706b\u8f66\uff0c\u660e\u5929\u4e5f\u4e0d\u7528\u8ddf\u6211\u7238\u4ed6\u8d77\u5927\u65e9\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u7528\u4e0a\u5b66\uff0c\u4f46\u662f\u9700\u8981\u5b9e\u4e60\uff0c\u4f60\u8bf4\u4f60\u8fd9\u662f\u5728\u5b9e\u4e60\uff1f\u201d\u6c5f\u654f\u4e4b\u7709\u5934\u5fae\u76b1\uff0c\u89c9\u5f97\u5728\u8001\u7236\u4eb2\u9762\u524d\u8bad\u5973\u513f\u4e5f\u4e0d\u5408\u9002\uff0c\u76b1\u8d77\u6765\u7684\u7709\u5934\u53c8\u8212\u7f13\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u8d70\u4e07\u91cc\u8def\u8bfb\u4e07\u5377\u8def\uff0c\u5750\u98de\u673a\u8fde\u4e00\u672c\u4e66\u90fd\u8bfb\u4e0d\u5b8c\uff0c\u6211\u7ed9\u7237\u7237\u5f53\u79d8\u4e66\uff0c\u4e5f\u7b97\u662f\u5b9e\u4e60\u554a\u3002\u201d\u6c5f\u6e44\u9876\u5634\u9053\u3002</p><p>\u3000\u3000\u6c5f\u654f\u4e4b\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8ddf\u6211\u4e00\u9053\u8d70\u5c31\u4e0d\u8ddf\u6211\u4e00\u9053\u8d70\uff0c\u4f60\u8981\u7167\u987e\u597d\u7237\u7237\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u60f3\u4e0d\u5230\u662f\u6c5f\u654f\u4e4b\u554a\uff0c\u201d\u961a\u552f\u6d9b\u4e0e\u9676\u4e66\u827a\u90fd\u4f4f\u5728\u65b0\u4ead\u5e02\u91cc\uff0c\u5f20\u606a\u4e0e\u90b5\u81f3\u521a\u591c\u91cc\u4f4f\u5317\u5c71\u5bbe\u9986\uff0c\u5728\u5415\u6d0b\u6e2f\u51fa\u6765\u7684\u8def\u53e3\u8ddf\u961a\u552f\u6d9b\u4ed6\u4eec\u5206\u5f00\uff0c\u90b5\u81f3\u521a\u5750\u5230\u5f20\u606a\u7684\u8f66\u91cc\uff0c\u611f\u6168\u7684\u8bf4\u9053\uff0c\u201c\u6c5f\u8001\u4eca\u665a\u6709\u4e9b\u5b88\u682a\u5f85\u5154\u7684\u610f\u5473\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u662f\u90a3\u53ea\u5154\u5b50\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u80fd\u7ed9\u524d\u526f\u603b\u7406\u5b88\u682a\u6295\u77f3\u95ee\u8def\uff0c\u6211\u4e5f\u4e50\u610f\u5f53\u90a3\u53ea\u5154\u5b50\uff0c\u201d\u90b5\u81f3\u521a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u770b\u6765\u6c5f\u654f\u4e4b\u5bf9\u4e1c\u6d77\u7701\u7684\u7ecf\u6d4e\u4ea7\u4e1a\u53d1\u5c55\u6709\u4ed6\u81ea\u5df1\u7684\u4e00\u5957\u89c1\u89e3\u554a\uff0c\u4f60\u600e\u4e48\u770b\u8fd9\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u59d1\u4e14\u770b\u4e4b\u5427\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u7684\u8bf4\u9053\uff0c\u201c\u6c5f\u9759\u6db5\u6ca1\u6709\u70b9\u900f\uff0c\u603b\u662f\u6709\u7f13\u548c\u7684\u610f\u601d\u5728\u91cc\u9762\uff0c\u59d1\u4e14\u770b\u4e4b\uff0c\u96be\u9053\u8fd8\u80fd\u63ba\u548c\u8fdb\u53bb\uff1f\u4ed6\u4eec\u662f\u62c5\u5fc3\u6211\u5e74\u8f7b\u6c14\u76db\u5427\uff01\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u90b5\u81f3\u521a\u7b11\u4e86\u7b11\uff0c\u5fc3\u60f3\u5f20\u606a\u5012\u662f\u5e38\u60f9\u4e8b\u751f\u975e\uff0c\u8981\u662f\u8c01\u8ba4\u4e3a\u4f1a\u5e74\u8f7b\u6c14\u76db\uff0c\u90a3\u771f\u662f\u5bf9\u4ed6\u6ca1\u6709\u4e00\u70b9\u7684\u4e86\u89e3\u554a\uff0c\u8bf4\u9053\uff0c\u201c\u6c5f\u8001\u4f1a\u6709\u8fd9\u6837\u7684\u62c5\u5fe7\u4e5f\u5c5e\u6b63\u5e38\uff0c\u4ed6\u4e4b\u524d\u53ef\u6ca1\u6709\u8ddf\u4f60\u63a5\u89e6\u8fc7\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u62c5\u5fc3\u6c5f\u654f\u4e4b\u4e0a\u4efb\u4e4b\u540e\u4f1a\u8ddf\u9526\u6e56\u53d1\u751f\u76f4\u63a5\u7684\u51b2\u7a81\u3002</p><p>\u3000\u3000\u6c5f\u654f\u4e4b\u4e0a\u4efb\u5373\u4f7f\u8981\u8c03\u6574\u4e1c\u6d77\u7701\u7684\u4ea7\u4e1a\u7ecf\u6d4e\u653f\u7b56\uff0c\u4e5f\u4e0d\u4f1a\u6709\u7ffb\u5929\u8986\u5730\u7684\u53d8\u5316\uff0c\u674e\u8fdc\u6e56\u9996\u5148\u4e0d\u4f1a\u7b54\u5e94\u4ed6\uff0c\u4e2d\u592e\u4e5f\u4e0d\u4f1a\u5e0c\u671b\u770b\u5230\u7ecf\u6d4e\u5f62\u52bf\u4e00\u7247\u5927\u597d\u7684\u4e1c\u6d77\u7701\u77db\u76fe\u5c16\u9510\u5316\u3002\u6c5f\u654f\u4e4b\u8981\u5148\u5904\u7406\u597d\u4e0e\u674e\u8fdc\u6e56\u53ca\u7701\u59d4\u5176\u4ed6\u73ed\u5b50\u6210\u5458\u7684\u5173\u7cfb\uff0c\u63a5\u4e0b\u6765\u624d\u8f6e\u5230\u9526\u6e56\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u6c5f\u9759\u6db5\u4eca\u5929\u665a\u4e0a\u6ca1\u6709\u70b9\u900f\u3001\u9690\u85cf\u5728\u80cc\u540e\u7684\u610f\u601d\uff1a\u6c5f\u654f\u4e4b\u5728\u4e2d\u592e\u90e8\u59d4\u7684\u6267\u653f\u98ce\u683c\u5c31\u5341\u5206\u7684\u9c9c\u660e\uff0c\u4ed6\u5230\u4e1c\u6d77\u6765\u8981\u63a8\u884c\u4ed6\u7684\u90a3\u4e00\u5957\u7406\u5ff5\uff0c\u5373\u4f7f\u4e0d\u4f1a\u523b\u610f\u7684\u9488\u5bf9\u9526\u6e56\uff0c\u4f46\u662f\u5bf9\u4e1c\u6d77\u7701\u7684\u4ea7\u4e1a\u7ecf\u6d4e\u653f\u7b56\u8fdb\u884c\u8c03\u6574\uff0c\u4e5f\u4f1a\u4f7f\u9526\u6e56\u5728\u4e1c\u6d77\u4e4b\u524d\u6240\u83b7\u5f97\u7684\u4f18\u52bf\u53d8\u5f31\uff0c\u6c5f\u9759\u6db5\u662f\u62c5\u5fe7\u9526\u6e56\u5728\u6c5f\u654f\u4e4b\u5230\u4efb\u4e4b\u524d\u5c31\u79d8\u5bc6\u7b56\u5212\u8054\u5408\u5c06\u4ed6\u67b6\u7a7a\u6389\u2026\u2026</p></article>"}}}]);
