(window.webpackJsonp=window.webpackJsonp||[]).push([[1131],{1154:function(p){p.exports={chapterTitle:null,title:"\u7b2c1128\u7ae0 \u62d2\u7edd\u8bf1\u60d1",content:"<article><h2>\u7b2c1128\u7ae0 \u62d2\u7edd\u8bf1\u60d1</h2><p>\u3000\u3000\u521d\u6625\u65b0\u96e8\uff0c\u7eff\u80a5\u5982\u6cb9\uff0c\u65b0\u4ead\u5230\u6df1\u591c\u53c8\u4e0b\u8d77\u96e8\u6765\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u4e3a\u6293\u5de5\u671f\u53ef\u4ee5\u8bf4\u663c\u591c\u65e0\u4f11\uff0c\u4e0b\u96e8\u5929\u5927\u6982\u662f\u6700\u4e0d\u53d7\u6b22\u8fce\u7684\u5929\u6c14\u4e86\uff0c\u5f20\u606a\u5fc3\u91cc\u5374\u60f3\u7f13\u4e00\u7f13\u4e5f\u65e0\u59a8\uff0c\u867d\u7136\u65f6\u65f6\u90fd\u6df1\u5f7b\u5165\u9aa8\u7684\u5371\u673a\u611f\uff0c\u795e\u7ecf\u4e00\u76f4\u7ef7\u5f97\u90a3\u4e48\u7d27\u4e5f\u4e0d\u662f\u4ec0\u4e48\u597d\u4e8b\u3002</p><p>\u3000\u3000\u7a97\u6237\u655e\u5f00\u7740\uff0c\u5165\u6237\u7684\u5fae\u98ce\u62c2\u52a8\u7eb1\u5e18\uff0c\u6709\u7740\u96e8\u4e1d\u4e0e\u8302\u5bc6\u679d\u53f6\u76f8\u7ec7\u7684\u7ec6\u5fae\u54cd\u58f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u7aef\u7740\u5496\u5561\u5750\u5728\u7a97\u53f0\u4e0a\uff0c\u51dd\u671b\u7740\u7a97\u5916\u7684\u96e8\u4e1d\u3002\u7a97\u5916\u5730\u576a\u706f\u660e\u4eae\uff0c\u591c\u8272\u7ed9\u7a00\u91ca\u5f97\u6ca1\u6709\u6a21\u6837\uff0c\u5212\u8fc7\u773c\u524d\u7684\u96e8\u4e1d\u53cd\u7740\u5149\uff0c\u50cf\u94f6\u8272\u7684\u4e1d\u7ebf\u3002</p><p>\u3000\u3000\u5728\u7535\u8bdd\u91cc\uff0c\u5f20\u606a\u8ddf\u5f90\u5b66\u5e73\u3001\u5510\u5b66\u8c26\u3001\u8bb8\u9e3f\u4f2f\u3001\u53f6\u5efa\u658c\u7b49\u4eba\u6c9f\u901a\u8fc7\u524d\u526f\u603b\u7406\u6c5f\u9759\u6db5\u4e4b\u5b50\u6c5f\u654f\u4e4b\u53ef\u80fd\u51fa\u4efb\u4e1c\u6d77\u7701\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u7684\u4e8b\u60c5\uff0c\u5927\u5bb6\u7684\u610f\u89c1\u90fd\u662f\u201c\u59d1\u4e14\u770b\u4e4b\u201d\u3002</p><p>\u3000\u3000\u5f20\u606a\u6000\u7591\u6c5f\u654f\u4e4b\u4eba\u5c31\u5728\u65b0\u4ead\uff0c\u7701\u90e8\u7ea7\u5b98\u5458\u7684\u884c\u52a8\u6ca1\u6709\u90a3\u4e48\u81ea\u7531\uff0c\u8981\u6253\u542c\u5230\u6c5f\u654f\u4e4b\u7684\u884c\u8e2a\u5f88\u4e5f\u5f88\u65b9\u4fbf\uff0c\u53ea\u662f\u773c\u4e0b\u771f\u4e0d\u662f\u89c1\u9762\u7684\u826f\u673a\uff0c\u6c5f\u654f\u4e4b\u5728\u4e0d\u5728\u65b0\u4ead\u90fd\u4e0d\u91cd\u8981\u3002</p><p>\u3000\u3000\u6e05\u6668\u9192\u6765\uff0c\u7ef5\u7ef5\u7ec6\u96e8\u65e9\u5c31\u4e0d\u89c1\u5f71\u8e2a\uff0c\u5929\u5df2\u7ecf\u6536\u6674\uff0c\u671d\u971e\u6a2a\u57a3\u5728\u8fdc\u65b9\u7684\u6d77\u5929\u4e4b\u95f4\uff0c\u8fd9\u65f6\u5019\u53bb\u6d77\u8fb9\u8d70\u4e00\u8d70\uff0c\u5439\u4e00\u5439\u62c2\u9762\u4e0d\u5bd2\u7684\u6d77\u98ce\uff0c\u90a3\u662f\u5f88\u597d\u7684\u4eab\u53d7\uff0c\u53ea\u4e0d\u8fc7\u4e1c\u5c71\u9547\u4e1c\u9762\u7684\u6ee9\u6d82\u90fd\u5df2\u7ecf\u53d8\u6210\u8fbd\u9614\u800c\u7e41\u5fd9\u7684\u5de5\u5730\uff0c\u8981\u9759\u9759\u7684\u770b\u6d77\uff0c\u5c31\u8981\u5230\u5357\u9762\u7684\u5cac\u5c71\uff0c\u5f20\u606a\u60f3\u7740\u672a\u5fc5\u6709\u8fd9\u4e2a\u95f2\u5de5\u592b\uff0c\u5c31\u6b64\u4f5c\u7f62\u3002</p><p>\u3000\u3000\u542c\u5230\u8499\u5b66\u5e86\u4e0e\u5085\u4fca\u5728\u697c\u4e0b\u7684\u8bf4\u8bdd\u58f0\uff0c\u60f3\u7740\u8981\u4e0e\u8499\u5b66\u5e86\u4e00\u8d77\u7528\u8fc7\u65e9\u9910\u518d\u79bb\u5f00\u65b0\u4ead\uff0c\u6d17\u6f31\u8fc7\u4e0b\u697c\u6765\uff0c\u6ca1\u60f3\u5230\u5f20\u6885\u8ddf\u4e2a\u4e0d\u901f\u4e4b\u5ba2\u5728\u697c\u4e0b\u7684\u8d77\u5c45\u5ba4\u91cc\u7b49\u5019\u3002</p><p>\u3000\u3000\u6c5f\u6e44\u9976\u6709\u5174\u8da3\u7684\u770b\u7740\u8d77\u5c45\u5ba4\u6c99\u53d1\u80cc\u540e\u5899\u58c1\u4e0a\u60ac\u6302\u7684\u9759\u7269\u753b\uff0c\u753b\u5e03\u4e0a\u6709\u4e00\u53ea\u5c06\u503e\u672a\u503e\u7684\u7ec6\u9888\u957f\u74f6\uff0c\u74f6\u4e2d\u6ce8\u6ee1\u7684\u6e05\u6c34\u5c31\u5c06\u6ea2\u51fa\uff0c\u5374\u4f3c\u4e4e\u66f4\u8ba9\u4eba\u62c5\u4efb\u74f6\u5b50\u968f\u65f6\u4f1a\u5012\u788e\u2014\u2014\u771f\u662f\u4e0d\u6e05\u695a\u5bbe\u9986\u91cc\u4e3a\u4ec0\u4e48\u4f1a\u9009\u62e9\u8fd9\u4e48\u4e00\u5e45\u9759\u7269\u753b\u88c5\u70b9\u8d77\u5c45\u5ba4\uff0c\u6c5f\u6e44\u542c\u5230\u5f20\u606a\u4e0b\u697c\u68af\u7684\u58f0\u97f3\uff0c\u4f3c\u4e4e\u66f4\u9976\u6709\u5174\u81f4\u7684\u770b\u7740\u5f20\u606a\u8138\u4e0a\u9732\u51fa\u7684\u8bb6\u7136\u7684\u795e\u8272\uff0c\u5979\u7b49\u7740\u5f20\u6885\u66ff\u81ea\u5df1\u89e3\u91ca\u8fc7\u6765\u7684\u7f18\u7531\u3002</p><p>\u3000\u3000\u201c\u6c5f\u8001\u4e34\u65f6\u8981\u53bb\u6587\u821f\u8bbf\u53cb\uff0c\u6c5f\u5c0f\u59d0\u4e0a\u5348\u8981\u53bb\u91d1\u5c71\u4e58\u56de\u5317\u4eac\u7684\u98de\u673a\uff0c\u4e0d\u60f3\u9ebb\u70e6\u5e02\u91cc\u6d3e\u8f66\u9001\u5979\uff0c\u6628\u5929\u542c\u5f20\u5148\u751f\u8bf4\u4eca\u5929\u4e0a\u5348\u8981\u56de\u91d1\u5c71\u53bb\uff0c\u201d\u5f20\u6885\u89e3\u91ca\u5979\u9886\u6c5f\u6e44\u8fc7\u6765\u7684\u539f\u56e0\uff0c\u5979\u5f81\u8be2\u7684\u770b\u7740\u5f20\u606a\uff0c\u201c\u60f3\u642d\u5f20\u5148\u751f\u7684\u8f66\u4e00\u8d77\u53bb\u91d1\u5c71\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u77e5\u9053\u65b9\u4e0d\u65b9\u4fbf\uff1f\u201d\u6c5f\u6e44\u63a5\u8fc7\u5f20\u6885\u7684\u8bdd\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u5c31\u60f3\u95ee\u6709\u6ca1\u6709\u8fd9\u4e2a\u8363\u5e78\uff0c\u53c8\u6015\u592a\u5192\u6627\u4e86\u3002\u201d\u770b\u7740\u6c99\u53d1\u811a\u4e0b\u7684\u7ea2\u8272\u65c5\u884c\u62c9\u6746\u7bb1\uff0c\u5f20\u606a\u5fc3\u60f3\u5979\u90fd\u5c06\u65c5\u884c\u62c9\u6746\u7bb1\u62d6\u8fdb\u6765\u4e86\uff0c\u8fd8\u80fd\u5c06\u5979\u8d76\u51fa\u53bb\uff1f</p><p>\u3000\u3000\u6628\u5929\u7684\u6c5f\u6e44\u7a7f\u7740\u73ab\u7ea2\u8272\u7684\u9f50\u88d9\u98ce\u8863\u4e0e\u91cc\u9762\u7a7f\u7740\u5496\u5561\u8272\u7684\u7ed2\u7ebf\u8863\u4e0e\u77ed\u88d9\uff0c\u77ed\u88d9\u7ed9\u98ce\u8863\u4e0b\u6446\u906e\u4f4f\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u4e00\u4e2a\u660e\u8273\u7684\u90fd\u5e02\u5973\u90ce\uff0c\u4eca\u5929\u7684\u6c5f\u6e44\u6362\u4e0a\u4f4e\u8170\u611f\u7684\u7d27\u8eab\u725b\u4ed4\u88e4\uff0c\u51f8\u663e\u51fa\u7fd8\u81c2\u4e0e\u4fee\u957f\u53cc\u817f\u7684\u7f8e\u4e3d\u66f2\u7ebf\uff0c\u957f\u53d1\u98d8\u9038\uff0c\u9752\u6625\u6c14\u606f\u76ce\u7136\u903c\u4eba\u3002</p><p>\u3000\u3000\u56fd\u5185\u516c\u4f17\u6781\u5c11\u77e5\u9053\u56fd\u5185\u4ee5\u5851\u9020\u5973\u5730\u4e0b\u515a\u5458\u5f62\u8c61\u800c\u95fb\u540d\u7684\u7535\u5f71\u6f14\u5458\u6797\u747e\u662f\u6c5f\u654f\u4e4b\u7684\u59bb\u5b50\uff0c\u6c5f\u6e44\u5012\u662f\u7ee7\u627f\u4e86\u5979\u6bcd\u4eb2\u7684\u7f8e\u8c8c\uff0c\u6628\u5929\u591c\u91cc\u6ce8\u610f\u529b\u653e\u5728\u524d\u526f\u603b\u7406\u6c5f\u9759\u6db5\u7684\u8eab\u4e0a\uff0c\u8fd9\u65f6\u5019\u624d\u611f\u89c9\u5230\u6c5f\u6e44\u8eab\u4e0a\u6709\u4e00\u79cd\u538b\u8feb\u4eba\u5fc3\u7684\u7f8e\u3002</p><p>\u3000\u3000\u6f02\u4eae\u662f\u4e00\u7801\u4e8b\uff0c\u8981\u662f\u5c06\u6c5f\u6e44\u6f02\u4eae\u7684\u8138\u86cb\u906e\u4f4f\uff0c\u770b\u4e0a\u53bb\u66f4\u50cf\u4e00\u5806\u8ba9\u4eba\u5934\u75bc\u7684\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u5f20\u606a\u9080\u8bf7\u6c5f\u6e44\u4e00\u8d77\u7528\u65e9\u9910\uff0c\u4e0e\u8d76\u8fc7\u6765\u76f8\u89c1\u7684\u9676\u4e66\u827a\u5306\u5306\u89c1\u8fc7\u4e00\u9762\uff0c\u5c31\u51c6\u5907\u5750\u8f66\u524d\u5f80\u4e1c\u5c71\uff0c\u521a\u51fa\u4e1c\u5c71\u9547\uff0c\u5f20\u606a\u63a5\u8fc7\u4e00\u901a\u7535\u8bdd\u5c31\u5f88\u62b1\u6b49\u7684\u8ddf\u6c5f\u6e44\u8bf4\u9053\uff1a\u201c\u771f\u662f\u5bf9\u4e0d\u8d77\uff0c\u4e0d\u80fd\u4eb2\u81ea\u9001\u5230\u6c5f\u5c0f\u59d0\u53bb\u91d1\u5c71\u673a\u573a\uff0c\u4e34\u65f6\u6709\u4e8b\u8981\u56de\u6d77\u5dde\u5904\u7406\u4e00\u4e0b\uff0c\u6211\u4f1a\u8ba9\u5de5\u4f5c\u4eba\u5458\u9001\u6c5f\u5c0f\u59d0\u5230\u673a\u573a\u5b89\u68c0\u53e3\u7684\uff0c\u6c5f\u5c0f\u59d0\u8981\u662f\u6709\u4ec0\u4e48\u8981\u6c42\uff0c\u4e5f\u5c3d\u7ba1\u5429\u5490\u4ed6\u4eec\u53bb\u505a\u5c31\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5f\u4e0d\u7ba1\u6c5f\u6e44\u7ed9\u4e22\u5728\u8f66\u91cc\u4f1a\u4e0d\u4f1a\u6c14\u5f97\u8dfa\u811a\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u5750\u5230\u540e\u9762\u7684\u5546\u52a1\u8f66\u6389\u5934\u5f80\u6e21\u53e3\u65b9\u5411\u800c\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u56de\u5230\u6d77\u5dde\uff0c\u5148\u53bb\u627e\u665a\u6674\uff0c\u53c8\u62ff\u665a\u6674\u7684\u624b\u673a\u7ed9\u5979\u53d1\u77ed\u4fe1\uff0c\u9a97\u5979\u5230\u4e39\u4e95\u5df7\u7684\u5b85\u5b50\u91cc\u4e00\u8d77\u5403\u4e2d\u996d\uff0c\u4e09\u4e2a\u4eba\u5728\u4e8c\u697c\u9732\u53f0\u4e0a\u5c31\u7740\u7ea2\u9152\u5403\u7740\u81ea\u5236\u7684\u70e4\u8089\uff0c\u8bf4\u8d77\u5728\u65b0\u4ead\u53d1\u751f\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u6c5f\u6e44\u771f\u8ddf\u6797\u747e\u5e74\u8f7b\u65f6\u7ffb\u6a21\u4e00\u6837\uff0c\u90a3\u8fd8\u771f\u662f\u6f02\u4eae\uff0c\u6211\u7238\u90fd\u662f\u6797\u747e\u7684\u5f71\u8ff7\u3002\u201d\u8bb8\u601d\u5355\u624b\u6258\u7740\u4e0b\u5df4\u542b\u7b11\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4eba\u5bb6\u6c5f\u5c0f\u59d0\u8fd9\u4e48\u4e3b\u52a8\uff0c\u4f60\u5c31\u8fd9\u6837\u968f\u4fbf\u7684\u5c06\u5979\u4e22\u5230\u524d\u5f80\u91d1\u5c71\u7684\u8f66\u4e0a\uff0c\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u53ef\u60dc\u4e86\uff1f\u201d\u665a\u6674\u6253\u8da3\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u53ef\u60dc\u554a\uff0c\u4f60\u5c31\u4e0d\u80fd\u5c06\u6211\u60f3\u5f97\u5927\u4e49\u51db\u7136\u4e00\u4e9b\uff1f\u201d\u5f20\u606a\u4f38\u624b\u8f7b\u8f7b\u7684\u6572\u665a\u6674\u5149\u6d01\u5982\u7389\u7684\u989d\u5934\uff0c\u201c\u96be\u9053\u975e\u8981\u6ee1\u8db3\u4e00\u4e2a\u5c11\u5973\u7684\u597d\u5947\u5fc3\uff0c\u5c31\u8981\u618b\u5c48\u7684\u966a\u5230\u4e00\u8def\u540c\u884c\u5230\u91d1\u5c71\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u7528\u523b\u610f\u6539\u53d8\u884c\u7a0b\u554a\uff0c\u201d\u5728\u8bb8\u601d\u9762\u524d\u7ed9\u5f20\u606a\u6572\u4e86\u4e00\u4e0b\u989d\u5916\uff0c\u665a\u6674\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u5979\u603b\u60f3\u8868\u73b0\u5f97\u66f4\u6301\u91cd\u4e00\u4e9b\uff0c\u8ddf\u5f20\u606a\u5077\u60c5\u5df2\u7ecf\u5f88\u96be\u582a\u4e86\uff0c\u8fd8\u8981\u7ed9\u4ed6\u5f53\u6210\u5c0f\u5973\u5b69\u4e00\u6837\u201c\u6b3a\u8d1f\u201d\uff0c\u5c06\u8bdd\u9898\u53c8\u8f6c\u5230\u6b63\u4e8b\u4e0a\uff0c\u201c\u4f60\u4e0d\u662f\u8981\u53bb\u91d1\u5c71\u8bf7\u4e00\u4e9b\u4eba\u5403\u4e2d\u996d\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u66f4\u4e0d\u884c\u4e86\uff0c\u90fd\u7535\u8bdd\u8054\u7cfb\u8fc7\u4e86\uff0c\u6539\u4e86\u65e5\u7a0b\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e0e\u6c5f\u9759\u6db5\u7684\u89c1\u9762\u8fd8\u80fd\u7b97\u662f\u4e0d\u671f\u800c\u9047\uff0c\u60f3\u56de\u907f\u4e5f\u56de\u907f\u4e0d\u4e86\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u56de\u907f\uff0c\u4f46\u6682\u65f6\u8fd8\u662f\u6ca1\u6709\u5fc5\u8981\u8ddf\u6c5f\u654f\u4e4b\u6216\u8005\u8bf4\u6c5f\u5bb6\u4eba\u6709\u8fdb\u4e00\u6b65\u7684\u79c1\u4eba\u63a5\u89e6\uff0c\u8fd9\u4e16\u4e0a\u5c31\u6ca1\u6709\u4e0d\u900f\u98ce\u7684\u5899\uff0c\u4e0d\u7136\u4e8b\u60c5\u4f20\u5230\u674e\u8fdc\u6e56\u8033\u6735\u91cc\uff0c\u4ed6\u5fc3\u91cc\u4f1a\u600e\u4e48\u60f3\uff1f\u201d</p><p>\u3000\u3000\u201c\u91d1\u5c71\u6bcf\u5929\u53ea\u6709\u4e24\u73ed\u98de\u5f80\u5317\u4eac\u7684\u822a\u73ed\uff0c\u8981\u4e48\u662f\u65e9\u6668\uff0c\u8981\u4e48\u662f\u9ec4\u660f\uff0c\u4f30\u8ba1\u8d76\u4e0d\u4e0a\u65e9\u6668\u7684\u822a\u73ed\uff0c\u5f20\u606a\u771f\u8981\u8ddf\u6c5f\u5927\u5c0f\u59d0\u540c\u884c\uff0c\u4e5f\u4e0d\u80fd\u5230\u91d1\u5c71\u540e\u5c31\u5c06\u4eba\u4e22\u5728\u673a\u573a\u91cc\uff0c\u201d\u8bb8\u601d\u4e0d\u7406\u4f1a\u5f20\u606a\uff0c\u8ddf\u665a\u6674\u5728\u90a3\u91cc\u5206\u6790\uff0c\u201c\u6211\u770b\u5f20\u606a\u662f\u62c5\u5fc3\u522b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u55ef\uff0c\u6211\u4e5f\u8fd9\u4e48\u60f3\uff0c\u8fd9\u5c0f\u5b50\u5fc3\u601d\u603b\u8981\u6211\u4eec\u63e3\u6d4b\u7684\u8fd8\u8981\u590d\u6742\u4e00\u767e\u500d\u2026\u2026\u201d\u665a\u6674\u9644\u548c\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u5c06\u5f20\u606a\u201c\u51b7\u5632\u70ed\u8bbd\u201d\u7684\u6349\u5f04\u4e86\u4e00\u901a\uff0c\u624d\u6b63\u7ecf\u7684\u8c08\u6b63\u4e8b\uff0c\u95ee\u5f20\u606a\uff0c\u201c\u96be\u9053\u6c5f\u654f\u4e4b\u5230\u4e1c\u6d77\u5c65\u4efb\u4e4b\u540e\uff0c\u9526\u6e56\u8981\u907f\u7740\u4ed6\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u60f9\u4e0d\u8d77\u6211\u8fd8\u8eb2\u5f97\u8d77\u554a\uff0c\u201d\u5f20\u606a\u6253\u4e86\u54c8\u6b20\uff0c\u8bf4\u9053\uff0c\u201c\u6628\u5929\u901a\u7535\u8bdd\u4e00\u76f4\u5230\u51cc\u6668\u4e24\u70b9\u624d\u7761\u89c9\u2026\u2026\u4e8b\u60c5\u8981\u6bd4\u60f3\u8c61\u7684\u590d\u6742\uff0c\u5c31\u7b97\u65b0\u5a5a\u592b\u5987\u8fc7\u65e5\u5b50\u8fd8\u8981\u6709\u6bb5\u78e8\u5408\u671f\u5462\uff0c\u9526\u6e56\u6682\u65f6\u5148\u5b89\u5206\u5b88\u5df1\u8d77\u6765\uff0c\u4e5f\u4e0d\u662f\u90a3\u4e48\u8ba9\u4eba\u96be\u4ee5\u5fcd\u53d7\u3002\u201d</p><p>\u3000\u3000\u201c\u95ee\u9898\u6ca1\u6709\u8fd9\u4e48\u4e25\u91cd\u5427\uff1f\u4f60\u5c31\u662f\u4e0d\u77e5\u9053\u7231\u60dc\u81ea\u5df1\u7684\u7fbd\u6bdb\uff0c\u624d\u591a\u4e45\u7684\u4e8b\u60c5\uff0c\u4f60\u8fd8\u5728\u5317\u4eac\u8ddf\u90a3\u4e9b\u516c\u5b50\u54e5\u4e3a\u5973\u660e\u661f\u4e89\u98ce\u5403\u918b\u3002\u6211\u4eec\u77e5\u9053\u4f60\u5728\u5916\u9762\u60f9\u4e8b\u751f\u975e\u90fd\u662f\u6781\u6709\u5206\u5bf8\u7684\uff0c\u4f46\u662f\u8fd9\u4e9b\u4e8b\u60c5\u4f20\u5230\u5916\u4eba\u7684\u8033\u6735\u91cc\uff0c\u81ea\u7136\u662f\u53e6\u5916\u4e00\u56de\u4e8b\u3002\u6c5f\u9759\u6db5\u4e0d\u5c31\u662f\u6015\u4f60\u5e74\u8f7b\u6c14\u76db\u4f1a\u4f7f\u77db\u76fe\u6fc0\u5316\u800c\u4e0d\u53ef\u6536\u62fe\u624d\u8dd1\u51fa\u6765\u6253\u9884\u9632\u9488\u5417\uff1f\u65e2\u7136\u80fd\u6709\u4e9b\u9ed8\u5951\uff0c\u4ee5\u540e\u5c31\u5e94\u8be5\u6ca1\u6709\u591a\u5927\u95ee\u9898\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u96be\u8bf4\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u201c\u5927\u4eba\u7269\u5929\u751f\u5c31\u6709\u4e00\u79cd\u60f3\u638c\u63a7\u4e00\u5207\u7684\u6b32\u671b\uff0c\u8bf4\u7684\u597d\u542c\u4e00\u4e9b\uff0c\u90a3\u662f\u6743\u529b\u8fdb\u53d6\u7684\u9510\u6c14\uff0c\u6c5f\u654f\u4e4b\u4e5f\u4e0d\u4f1a\u4f8b\u5916\uff0c\u4ed6\u8981\u60f3\u5728\u4e1c\u6d77\u624e\u7a33\u6839\uff0c\u5c31\u8981\u6405\u548c\u8d77\u4e8b\u60c5\u51fa\u6765\uff0c\u4f1a\u4e0d\u4f1a\u5c06\u9526\u6e56\u5377\u8fdb\u53bb\uff0c\u8fd9\u65f6\u5019\u8fd8\u771f\u4e0d\u597d\u5224\u65ad\u2014\u2014\u6211\u5b81\u53ef\u5148\u60b2\u89c2\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u5427\uff0c\u7537\u4eba\u56f4\u7ed5\u6743\u529b\u4f1a\u4ea7\u751f\u600e\u6837\u7684\u5fc3\u601d\uff0c\u6709\u65f6\u5019\uff0c\u6211\u4eec\u662f\u600e\u4e48\u731c\u90fd\u731c\u4e0d\u900f\u7684\u3002\u201d\u665a\u6674\u8bf4\u9053\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u4ece\u5c0f\u5728\u90a3\u6837\u7684\u73af\u5883\u957f\u5927\uff0c\u53c8\u505a\u4e86\u5f90\u5bb6\u7684\u513f\u5ab3\uff0c\u5b98\u573a\u4e0a\u7684\u6f5c\u89c4\u5219\u8fd8\u662f\u76f8\u5f53\u7684\u4e86\u89e3\u7684\u3002\u7a7a\u964d\u5175\u4e0e\u5730\u65b9\u52bf\u529b\u603b\u4e0d\u4f1a\u90a3\u4e48\u8f7b\u677e\u5c31\u548c\u8c10\u76f8\u5904\uff0c\u6c5f\u654f\u4e4b\u5230\u4e1c\u6d77\u662f\u62c5\u4efb\u4e8c\u628a\u624b\uff0c\u5c5e\u4e8e\u5939\u5fc3\u5c42\uff0c\u5e73\u5fc3\u5c42\u8981\u662f\u5728\u53bf\u5e02\u91cc\uff0c\u591a\u534a\u5c5e\u4e8e\u88ab\u67b6\u7a7a\u7684\u90a3\u4e00\u7c7b\uff0c\u4f46\u662f\u5728\u7701\u91cc\uff0c\u504f\u504f\u5982\u6b64\uff0c\u5373\u5c06\u51fa\u4efb\u7701\u59d4\u4e66\u8bb0\u7684\u674e\u8fdc\u6e56\u81f3\u5c11\u5728\u660e\u9762\u4e0a\u4f1a\u66f4\u6536\u655b\u4e00\u4e9b\uff0c\u8ba9\u6c5f\u654f\u4e4b\u6709\u66f4\u5927\u7684\u6743\u529b\u4e0e\u53d1\u6325\u7a7a\u95f4\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u6c5f\u654f\u4e4b\u4e5f\u4e0d\u4f1a\u662f\u4ec0\u4e48\u7b80\u5355\u89d2\u8272\uff0c\u8c01\u53c8\u80fd\u77e5\u9053\u4ed6\u5fc3\u91cc\u5bf9\u9526\u6e56\u7684\u771f\u5b9e\u60f3\u6cd5\u662f\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u201c\u6709\u4e9b\u65f6\u5019\uff0c\u4e0d\u7ec6\u60f3\u4e0d\u884c\uff0c\u60f3\u592a\u591a\u4e5f\u4e0d\u884c\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6628\u5929\u7761\u5f97\u5f88\u5c11\uff0c\u6211\u90fd\u60f3\u7761\u4e00\u89c9\u624d\u53bb\u8003\u8651\u8fd9\u4e9b\u5934\u75bc\u7684\u4e8b\u60c5\uff0c\u4f60\u4eec\u8981\u4e0d\u8981\u4e5f\u7761\u4e2a\u5348\u89c9\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u56de\u5b66\u6821\u8fd8\u6709\u4e8b\u60c5\u8981\u5904\u7406\uff0c\u201d\u665a\u6674\u9003\u4e4b\u592d\u592d\u8fd8\u6765\u4e0d\u53ca\uff0c\u54ea\u91cc\u4f1a\u518d\u4e0a\u5f20\u606a\u7684\u5f53\uff0c\u8bf4\u9053\uff0c\u201c\u8bb8\u601d\u4e0b\u5348\u6ca1\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8ba9\u5979\u966a\u4f60\u2026\u2026\u201d\u62ff\u8d77\u7535\u8bdd\u5c31\u901a\u77e5\u52a9\u624b\u5f00\u8f66\u5230\u4e39\u4e95\u5df7\u6765\u63a5\u5979\u3002</p><p>\u3000\u3000\u8bb8\u601d\u60f3\u9003\u6ca1\u9003\u6389\uff0c\u534a\u63a8\u534a\u5c31\u7684\u7ed9\u5f20\u606a\u62fd\u8fdb\u5367\u5ba4\u91cc\u4e00\u8d77\u7761\u5348\u89c9\uff0c\u8eba\u5728\u5f20\u606a\u7684\u81c2\u5f2f\u91cc\uff0c\u5934\u5fae\u5fae\u626c\u7740\uff0c\u770b\u7740\u5f20\u606a\u6df1\u9083\u7684\u773c\u775b\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u6837\u7684\u5973\u4eba\u624d\u4f1a\u5bf9\u4f60\u6709\u5438\u5f15\u529b\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u4e9b\u5973\u4eba\u5373\u4f7f\u5f88\u7f8e\uff0c\u5374\u4e0d\u8ff7\u4eba\uff01\u201d\u5f20\u606a\u4fa7\u8f6c\u8fc7\u8eab\u6765\uff0c\u624b\u8f7b\u8f7b\u7684\u6402\u8fc7\u8bb8\u601d\u67d4\u8f6f\u7684\u8170\u80a2\uff0c\u8ba9\u5979\u6e29\u8f6f\u7684\u8eab\u5b50\u8d34\u7d27\u81ea\u5df1\uff0c\u611f\u53d7\u7740\u7279\u6709\u7684\u4e30\u6ed1\u7ec6\u817b\uff0c\u6df1\u60c5\u7684\u770b\u7740\u5979\u7684\u7738\u5b50\uff0c\u5979\u7684\u7f8e\u6709\u4e00\u79cd\u6781\u81f4\u7684\u6e05\u6f88\uff0c\u4ee4\u4eba\u8ff7\u9189\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u4e0d\u77e5\u9053\u4f60\u6709\u591a\u8ff7\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5728\u6d77\u5dde\u4f4f\u4e86\u4e09\u5929\u624d\u56de\u5efa\u90ba\uff0c\u4ed6\u5012\u4e0d\u62c5\u5fc3\u6c5f\u6e44\u4f1a\u53bb\u5efa\u90ba\u5835\u4ed6\uff0c\u5728\u8fd9\u4e09\u5929\u65f6\u95f4\u91cc\uff0c\u4e2d\u592e\u4e24\u4f1a\u4e5f\u987a\u5229\u5728\u5317\u4eac\u53ec\u5f00\u3002\u5728\u5168\u56fd\u4eba\u6c11\u4ee3\u8868\u5927\u4f1a\u4e0a\uff0c\u9676\u664b\u9009\u4e3e\u62c5\u4efb\u5168\u56fd\u4eba\u5927\u59d4\u5458\u4f1a\u526f\u59d4\u5458\u957f\uff0c\u4f46\u662f\u4ed6\u6682\u65f6\u5c06\u7ee7\u7eed\u517c\u4efb\u4e1c\u6d77\u7701\u59d4\u4e66\u3001\u7701\u4eba\u5927\u4e3b\u4efb\u4e24\u804c\uff0c\u76f4\u5230\u987a\u5229\u5b8c\u6210\u6743\u529b\u8fc7\u6e21\u4e4b\u540e\u624d\u4f1a\u5f7b\u5e95\u7684\u79bb\u5f00\u4e1c\u6d77\u7701\u3002</p></article>"}}}]);
