(window.webpackJsonp=window.webpackJsonp||[]).push([[1137],{1160:function(p){p.exports={chapterTitle:null,title:"\u7b2c932\u7ae0 \u8272\u60c5\u7247",content:"<article><h2>\u7b2c932\u7ae0 \u8272\u60c5\u7247</h2><p>\u3000\u3000\u4ece\u4e8c\u6708\u4e2d\u65ec\u6d45\u6c34\u6e7e\u5927\u9053\u53d1\u751f\u7684\u90a3\u6869\u8f66\u7978\u8d77\uff0c\u5f20\u606a\u5c31\u4e00\u76f4\u4e0d\u5fd8\u6253\u51fb\u845b\u6c0f\u7236\u5b50\uff0c\u56db\u4e94\u6708\u4efd\uff0c\u52a8\u7528\u5de8\u8d44\u589e\u6301\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u7684\u80a1\u7968\uff0c\u8feb\u4f7f\u845b\u6c0f\u7236\u5b50\u4e3a\u4fdd\u4f4f\u5bf9\u8fd9\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u7684\u63a7\u5236\u6743\u800c\u88ab\u52a8\u7684\u505a\u51fa\u6765\u589e\u6301\u7684\u51b3\u5b9a\uff0c\u4ee5\u6b64\u52a0\u901f\u5609\u4fe1\u96c6\u56e2\u73b0\u91d1\u6d41\u7684\u6d88\u8017\uff0c\u4f7f\u5176\u5728\u968f\u540e\u5230\u6765\u7684\u66f4\u52a0\u4e25\u91cd\u7684\u7ecf\u6d4e\u5371\u673a\u4f1a\u7f3a\u4e4f\u8db3\u591f\u7684\u8d44\u91d1\u652f\u6491\u800c\u9677\u5165\u8d8a\u53d1\u8270\u96be\u7684\u56f0\u5883\u3002</p><p>\u3000\u3000\u82e5\u6709\u53ef\u80fd\uff0c\u5f20\u606a\u7edd\u4e0d\u613f\u610f\u9762\u5bf9\u91d1\u5c71\u6e56\u6e83\u5824\u8fd9\u4e00\u51fa\u917f\u6210\u6b7b\u4f24\u4e00\u5343\u591a\u4eba\u7684\u60e8\u5267\uff0c\u5374\u4e0d\u5f97\u4e0d\u627f\u8ba4\u91d1\u5c71\u6e56\u6e83\u5824\u7ed9\u5609\u4fe1\u7535\u5b50\u81f4\u547d\u7684\u6253\u51fb\uff0c\u540c\u65f6\u534e\u8363\u7b49\u53f0\u6e7e\u7535\u5b50\u4ee3\u5de5\u4e1a\u7684\u5de8\u5934\u8d81\u7ecf\u6d4e\u5371\u673a\u5728\u5927\u9646\u5927\u8086\u6269\u5f20\uff0c\u6253\u538b\u7ade\u4e89\u5bf9\u624b\uff0c\u5728\u5df2\u7ecf\u840e\u7f29\u7684\u7535\u5b50\u4ee3\u5de5\u5e02\u573a\u56f4\u903c\u5609\u4fe1\u7535\u5b50\uff0c\u8fdb\u4e00\u6b65\u52d2\u7d27\u5609\u4fe1\u7535\u5b50\u7684\u54bd\u5589\uff0c\u4ece\u53bb\u5e74\u79cb\u5b63\u4ee5\u6765\uff0c\u5609\u4fe1\u7535\u5b50\u4e3b\u8425\u7684\u7535\u5b50\u4ee3\u5de5\u4e1a\u52a1\u6301\u7eed\u56db\u4e2a\u5b63\u5ea6\u7684\u5de8\u989d\u4e8f\u635f\uff0c\u4e5f\u4f7f\u5609\u4fe1\u7535\u5b50\u6301\u7eed\u5931\u8840\uff0c\u7f3a\u4e4f\u81ea\u6551\u7684\u80fd\u529b\u3002</p><p>\u3000\u3000\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4ef7\uff0c\u4ece\u516d\u6708\u4e0b\u65ec\u4ee5\u6765\u5c31\u9677\u5165\u65e0\u6cd5\u81ea\u62e8\u7684\u9634\u8dcc\u4e4b\u4e2d\uff0c\u9526\u6e56\u4e4b\u524d\u4ee5\u8d8a\u79c0\u63a7\u80a1\u7684\u540d\u4e49\u62c6\u5de8\u8d44\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u589e\u6301\uff0c\u81ea\u7136\u8499\u53d7\u5de8\u989d\u635f\u5931\uff0c\u6240\u4ee5\u770b\u5230\u7fdf\u4e39\u9752\u62ff\u8fc7\u6765\u7684\u5f53\u5929\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u7684\u6536\u76d8\u62a5\u544a\uff0c\u5f20\u606a\u624d\u8bf4\u201c\u6740\u654c\u4e00\u4e07\u3001\u81ea\u635f\u4e09\u5343\u201d\u7684\u8bdd\uff1a\u8d8a\u79c0\u63a7\u80a1\u3001\u7231\u8fbe\u7535\u5b50\u624b\u91cc\u8fd8\u6301\u6709\u7684\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u7968\u53ef\u4ee5\u8bf4\u5df2\u7ecf\u53d8\u6210\u4e0d\u503c\u51e0\u6587\u7684\u5783\u573e\u80a1\u4e86\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u9664\u4e86\u5f20\u606a\u4e4b\u5916\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u662f\u66f4\u613f\u610f\u5bf9\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u65bd\u52a0\u575a\u51b3\u7684\u6253\u51fb\uff0c\u8fd9\u70b9\u635f\u5931\uff0c\u8fd8\u4e0d\u80fd\u8ba9\u62e5\u6709\u4e0a\u767e\u4ebf\u4f18\u8d28\u8d44\u4ea7\u7684\u8d8a\u79c0\u63a7\u80a1\u4f24\u7b4b\u75db\u9aa8\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8584\u8584\u7684\u51e0\u9875\u62a5\u544a\u5377\u8d77\u6765\u5939\u5728\u814b\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u9759\u6aac\u4e00\u5b9a\u4f1a\u4e50\u610f\u770b\u5230\u8fd9\u4e2a\u6d88\u606f\u7684\uff0c\u966a\u6211\u4e00\u8d77\u53bb\u9152\u5427\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u772f\u773c\u770b\u4e86\u770b\u6797\u68a2\u4e4b\u4e0a\u7684\u5915\u9633\uff0c\u7a0d\u72b9\u8c6b\u4e86\u7247\u523b\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u597d\u5427\uff0c\u597d\u4e45\u6ca1\u6709\u53bb1978\u5750\u4e00\u5750\u4e86\uff0c\u867d\u8bf4\u8fd9\u65f6\u5019\u6709\u4e9b\u65e9\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u9152\u5427\u5e94\u8be5\u5c5e\u4e8e\u591c\u751f\u6d3b\u7684\u4e16\u754c\uff0c\u4e0d\u8fc7\u5b66\u5e9c\u5df7\u7684\u9152\u5427\u591a\u662f\u9762\u5411\u9644\u8fd1\u9ad8\u6821\u5b66\u751f\u7684\uff0c\u4e0b\u5348\u5c31\u4f1a\u9646\u7eed\u7684\u5f00\u95e8\u8425\u4e1a\uff0c1978\u66f4\u53e6\u7c7b\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u8d70\u8fdb1978\uff0c\u5b59\u9759\u6aac\u8fd8\u6ca1\u6709\u4ece\u97f3\u4e50\u5b66\u9662\u8fc7\u6765\uff0c\u6b63\u503c\u7528\u9910\u65f6\u95f4\uff0c\u5e97\u91cc\u6ca1\u6709\u51e0\u4e2a\u4eba\uff0c\u674e\u99a8\u4e88\u5750\u5728\u5427\u53f0\u524d\uff0c\u4e00\u672c\u6b63\u7ecf\u7684\u8ddf\u7ad9\u5728\u67dc\u53f0\u540e\u7684\u4f55\u5f26\u7ec3\u4e60\u6c49\u8bed\uff0c\u4f55\u5f26\u5219\u501f\u673a\u8ddf\u674e\u99a8\u4e88\u5b66\u4e60\u97e9\u8bed\uff0c\u536b\u5170\u4e0e\u5b59\u9759\u6aac\u540c\u65f6\u8d70\u8fdb\u6765\uff0c\u5012\u4e0d\u6653\u5f97\u5979\u4eec\u600e\u4e48\u4f1a\u9047\u5230\u4e00\u8d77\uff0c\u536b\u5170\u5df2\u7ecf\u9002\u5e94\u4e86\u5927\u5b66\u6821\u56ed\u91cc\u7684\u751f\u6d3b\uff0c\u519b\u8bad\u65f6\u526a\u77ed\u4e86\u5934\u53d1\uff0c\u76f4\u53d1\u521a\u521a\u906e\u4f4f\u8033\u6735\uff0c\u6d45\u7eff\u8272\u50cf\u8774\u8776\u4f3c\u7684\u7cbe\u81f4\u53d1\u5939\u522b\u5728\u5934\u53d1\u4e0a\uff0c\u5bb9\u8c8c\u6e05\u7eaf\u52a8\u4eba\uff0c\u5f20\u606a\u770b\u7740\u536b\u5170\u8d70\u8fdb\u6765\u7b11\u7740\u8bf4\uff1a\u201c\u6362\u4e0a\u5973\u9ad8\u4e2d\u751f\u7684\u77ed\u88d9\u6821\u670d\u5c31\u66f4\u5b8c\u7f8e\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5efa\u90ba\u6709\u54ea\u6240\u4e2d\u5b66\u7684\u5973\u751f\u53ef\u4ee5\u7a7f\u77ed\u88d9\u7684\uff0c\u4f60\u8be5\u4e0d\u4f1a\u53c8\u5077\u5077\u7684\u5728\u770b\u65e5\u672c\u8272\u60c5\u7247\u4e86\u5427\uff1f\u201d\u5b59\u9759\u6aac\u5fae\u6b6a\u7740\u5934\u76ef\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5973\u5b69\u5b50\u4eec\u8086\u65e0\u5fcc\u60ee\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u53d1\u73b0\u81ea\u5df1\u5728\u8fd9\u91cc\u624d\u662f\u5f31\u52bf\u7fa4\u4f53\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5c0f\u6cfd\u5706\u4e5d\u4e03\u5e74\u5c31\u62cd\u51fa\u7b2c\u4e00\u672c\u5927\u80c6\u9732\u70b9\u7684\u5199\u771f\u96c6\uff0c\u4f46\u662f\u76f4\u5230\u4eca\u5e74\u624d\u6709\u5f55\u50cf\u5e26\u95ee\u4e16\uff0c\u5b59\u9759\u6aac\u662f\u94c1\u5b9a\u65e0\u6cd5\u7406\u89e3\u5f20\u606a\u5c06\u5c0f\u6cfd\u5706\u7684\u5f71\u7247\u5b58\u5728\u7b14\u8bb0\u672c\u7535\u8111\u91cc\u66f4\u591a\u7684\u53ea\u662f\u5e26\u7740\u5bf9\u524d\u5c18\u5f80\u4e8b\u7684\u9752\u6625\u56de\u5fc6\uff0c\u5979\u65e0\u610f\u95f4\u770b\u5230\u5f20\u606a\u7b14\u8bb0\u672c\u7535\u8111\u91cc\u5b58\u6709\u8272\u60c5\u7247\uff0c\u90a3\u60ca\u8bb6\u4e0e\u9119\u5937\u7684\u795e\u60c5\u7b80\u76f4\u8981\u590d\u6742\u5230\u9876\u70b9\uff0c\u5f53\u7136\u4e5f\u6ca1\u6709\u66ff\u5f20\u606a\u4e25\u5b88\u79d8\u5bc6\u7684\u6253\u7b97\uff0c\u5f88\u5feb\u536b\u5170\u3001\u7fdf\u4e39\u9752\u90fd\u77e5\u9053\u4e86\uff0c\u8fd9\u65f6\u5019\u53c8\u4e00\u4ed8\u751f\u6015\u674e\u99a8\u4e88\u3001\u4f55\u5f26\u4e0d\u77e5\u9053\u7684\u6a21\u6837\u2014\u2014\u770b\u4f55\u5f26\u62bf\u7b11\u800c\u7b11\u7684\u6837\u5b50\uff0c\u5e94\u8be5\u662f\u77e5\u9053\u8fd9\u4e8b\u4e86\uff0c\u5f20\u606a\u5c06\u51e0\u9875\u62a5\u544a\u5f80\u5b59\u9759\u6aac\u624b\u91cc\u4e00\u585e\uff0c\u7b2c\u4e00\u4e2a\u5ff5\u5934\u5c31\u662f\u60f3\u627e\u501f\u53e3\u9003\u79bb\u8fd9\u4e2a\u5bb9\u6613\u7ed9\u7fa4\u5632\u7684\u5730\u65b9\uff0c\u53c8\u60f3\u8fd9\u6837\u9003\u8d70\u672a\u514d\u592a\u793a\u5f31\u4e8e\u4eba\uff0c\u89c1\u9152\u5427\u91cc\u4e5f\u6ca1\u6709\u65c1\u4eba\uff0c\u80e1\u626f\u9053\uff1a\u201c\u8981\u8bf4\u516c\u4f17\u4eba\u7269\u91cc\u6709\u4ec0\u4e48\u5973\u6027\u503c\u5f97\u6211\u8d5e\u8d4f\uff0c\u5c0f\u6cfd\u5706\u80af\u5b9a\u8981\u6392\u5728\u7b2c\u4e00\u4f4d\uff0c\u6211\u770b\u5979\u5f71\u7247\u65f6\u7684\u5fc3\u60c5\uff0c\u53ef\u8ddf\u4f60\u4eec\u60f3\u7684\u5b8c\u5168\u4e0d\u4e00\u6837\u2014\u2014\u4f60\u4eec\u77e5\u9053\u5c0f\u6cfd\u5706\u7684\u8eab\u4e16\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u80e1\u63b0\u5427\uff0c\u770b\u8272\u60c5\u7247\u8fd8\u80fd\u63d0\u9ad8\u5230\u600e\u6837\u7684\u9ad8\u5ea6\uff1f\u201d\u5b59\u9759\u6aac\u7f8e\u7738\u772f\u7b11\u7740\u6709\u5982\u8ff7\u4eba\u7684\u661f\u6708\uff0c\u5979\u8d70\u5230\u5427\u53f0\u91cc\uff0c\u624b\u6258\u7740\u4e0b\u5df4\u770b\u7740\u5f20\u606a\uff0c\u4e00\u526f\u6d17\u8033\u606d\u542c\u7684\u6a21\u6837\uff0c\u4e0d\u5c51\u7684\u8bf4\u9053\uff0c\u201c\u770b\u4f60\u80fd\u600e\u4e48\u63b0\uff01\u201d</p><p>\u3000\u3000\u201c\u5728\u4e00\u822c\u4eba\u7684\u5370\u8c61\u91cc\uff0c\u6295\u8eabAV\u754c\u7684\u5973\u661f\u90fd\u5e94\u8be5\u8feb\u4e8e\u751f\u8ba1\uff0c\u5c0f\u6cfd\u5706\u5374\u662f\u4e00\u4f4d\u751f\u6d3b\u65e0\u5fe7\u7684\u5bcc\u5bb6\u5343\u91d1\uff0c\u76f8\u8c8c\u6e05\u7eaf\u751c\u7f8e\u81ea\u4e0d\u7528\u8bf4\uff0c\u751f\u6d3b\u4e2d\u5979\u8fd8\u64c5\u957f\u6f14\u594f\u94a2\u7434\uff0c\u70ed\u7231\u6392\u7403\u3001\u7f51\u7403\u8fd0\u52a8\uff0c\u53ea\u4e0d\u8fc7\u538c\u5026\u4e86\u7ed9\u5e7f\u544a\u5546\u5f53\u6cf3\u88c5\u7f8e\u5973\uff0c\u5e0c\u671b\u5c1d\u8bd5\u4e00\u4e9b\u65b0\u5947\u7684\u4f53\u9a8c\uff0c\u624d\u6295\u8eabAV\u754c\uff0c\u5979\u7684\u601d\u8003\u53ca\u559c\u597d\u5f02\u4e8e\u5e38\u4eba\uff0c\u884c\u4e8b\u98ce\u683c\u7279\u7acb\u72ec\u884c\uff0c\u601d\u60f3\u65b0\u6f6e\u524d\u536b\uff0c\u4f60\u4e5f\u77e5\u9053\u4e1c\u4e9a\u9752\u5c11\u5e74\u7684\u5fc3\u7406\u901a\u5e38\u90fd\u5f02\u5e38\u82e6\u95f7\uff0c\u5c0f\u6cfd\u5706\u7684\u884c\u4e3a\u4e0d\u4ec5\u4ece\u751f\u7406\u4e0a\u8fd8\u662f\u5fc3\u7406\u4e0a\uff0c\u90fd\u8db3\u4ee5\u6210\u4e3a\u4e1c\u4e9a\u9752\u5c11\u5e74\u7684\u7cbe\u795e\u5bfc\u5e08\uff0c\u5982\u6b64\u884c\u4e3a\u7adf\u8ba9\u4f60\u770b\u5f97\u5982\u6b64\u4e0d\u582a\u2026\u2026\u201d\u5f20\u606a\u5567\u5567\u7684\u5482\u4e86\u4e24\u58f0\uff0c\u9887\u4e3a\u4e0d\u5c51\u7684\u659c\u89c6\u7740\u5b59\u9759\u6447\u4e86\u4e24\u4e0b\u5934\uff0c\u624d\u4e3a\u81ea\u5df1\u8fa9\u89e3\uff0c\u201c\u5982\u6b64\u7684\u5973\u6027\u96be\u9053\u4e0d\u503c\u5f97\u5c0a\u656c\uff0c\u96be\u9053\u4e0d\u503c\u5f97\u5c06\u5979\u7684\u5f71\u7247\u630e\u8fc7\u6765\u597d\u597d\u7684\u6b23\u8d4f\u4e00\u756a\uff1f\u6211\u751a\u81f3\u8fd8\u8003\u8651\u7231\u8fbe\u5728\u65e5\u672c\u7684\u5f62\u8c61\u4ee3\u8a00\u4eba\u662f\u4e0d\u662f\u5c06\u677e\u9686\u5b50\u6362\u6210\u5c0f\u6cfd\u5706\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b59\u9759\u6aac\u773c\u775b\u77aa\u4e86\u6e9c\u5706\uff0c\u201c\u4f60\u8fd9\u4e2a\u8c0e\u8a00\u90fd\u80fd\u6709\u52c7\u6c14\u63b0\u51fa\u6765\uff0c\u4f60\u8981\u771f\u5c06\u7231\u8fbe\u5728\u65e5\u672c\u7684\u5f62\u8c61\u4eba\u771f\u6362\u6210\u5c0f\u6cfd\u5706\uff0c\u6211\u5c31\u4fe1\u4e86\u4f60\u8fd9\u9b3c\u8bdd\uff01\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u4e5f\u4e0d\u662f\u6211\u4e00\u4e2a\u4eba\u80fd\u505a\u4e3b\u7684\u2026\u2026\u518d\u8bf4\u516c\u53f8\u4e0e\u677e\u9686\u5b50\u7684\u5408\u7ea6\u8981\u5230\u96f6\u96f6\u5e74\u624d\u7ed3\u675f\uff0c\u8fd9\u65f6\u5019\u4e5f\u6ca1\u6cd5\u8bc1\u660e\u7ed9\u4f60\u770b\u3002\u201d\u5f20\u606a\u65e0\u8d56\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u65e5\u672c\u5206\u516c\u53f8\u63a8\u8350\u6765\u7684\u804c\u5458\u8096\u96c1\u5b81\u542c\u8bf4\u4e0e\u4f60\u5e74\u524d\u5728\u4e1c\u4eac\u8ba4\u8bc6\u7684\uff0c\u201d\u7fdf\u4e39\u9752\u534a\u5929\u6ca1\u6709\u8bf4\u8bdd\uff0c\u7a81\u7136\u4fa7\u8fc7\u8eab\u63d2\u4e86\u4e00\u53e5\u8bdd\uff0c\u201c\u6211\u770b\u5979\u7684\u8138\u5f62\u8ddf\u4f60\u8bf4\u7684\u5c0f\u6cfd\u5706\u6709\u51e0\u5206\u76f8\u50cf\u2014\u2014\u5979\u5f97\u5230\u63a8\u8350\uff0c\u8be5\u4e0d\u4f1a\u662f\u4f60\u7ed9\u65e5\u672c\u516c\u53f8\u4ec0\u4e48\u6697\u793a\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u5f20\u606a\u6123\u4e86\u4e24\u79d2\u949f\uff0c\u624d\u60f3\u8d77\u6765\u8981\u95ee\u7fdf\u4e39\u9752\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\u5c0f\u6cfd\u5706\u957f\u4ec0\u4e48\u6837\u5b50\uff1f\u201d\u4ed6\u4ece\u7fdf\u4e39\u9752\u7684\u8138\u4e0a\u6c38\u8fdc\u90fd\u770b\u4e0d\u51fa\u4ec0\u4e48\u7834\u7efd\u6765\uff0c\u4ed6\u5c31\u8f6c\u8fc7\u5934\u770b\u536b\u5170\u3002\u536b\u5170\u8138\u5ae9\uff0c\u7ed9\u5f20\u606a\u4e00\u770b\u5c31\u4fcf\u8138\u7c89\u7ea2\uff0c\u8fd8\u5fc3\u865a\u7684\u5148\u6cc4\u4e86\u5e95\uff1a\u201c\u6211\u672c\u4e0d\u60f3\u770b\u7684\uff0c\u9759\u6aac\u62c9\u7740\u6211\u62ff\u4f60\u7684\u7535\u8111\u627e\u7fdf\u59d0\u8f93\u5bc6\u7801\u2014\u2014\u5c31\u770b\u4e86\u4e24\u773c\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u7535\u8111\u9664\u4e86\u4ed6\u672c\u4eba\u4e4b\u5916\uff0c\u53ea\u6709\u7fdf\u4e39\u9752\u80fd\u6253\u5f00\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8fd9\u65f6\u5019\u624d\u5fc3\u865a\u7684\u66ff\u81ea\u5df1\u8fa9\u89e3\u4e00\u53e5\uff1a\u201c\u6211\u4e5f\u662f\u60f3\u77e5\u9053\u4f60\u4eec\u7537\u4eba\u5230\u5e95\u5728\u60f3\u4ec0\u4e48\uff0c\u600e\u4e48\u4f1a\u5bf9\u8fd9\u79cd\u4e1c\u897f\u8fd9\u4e48\u611f\u5174\u8da3\u2014\u2014\u5c0f\u6cfd\u5706\u5012\u662f\u86ee\u6f02\u4eae\u7684\u2026\u2026\u201d\u8f6c\u8eab\u51fa\u4e86\u5427\u53f0\u5f80\u9152\u5427\u5185\u4fa7\u7684\u5c0f\u821e\u6c60\u8d70\u53bb\uff0c\u536b\u5170\u4e5f\u7ea2\u7740\u8138\u6e9c\u8d70\u4e86\uff0c\u53ea\u6709\u7fdf\u4e39\u9752\u9762\u4e0d\u6539\u8272\u7684\u5750\u5728\u5f20\u606a\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u4e86\u5927\u611f\u540e\u6094\uff0c\u8981\u662f\u80fd\u4eb2\u81ea\u6349\u4f4f\u4e09\u4e2a\u5973\u4eba\u8eb2\u5728\u623f\u95f4\u91cc\u770b\u8272\u60c5\u7247\u7684\u573a\u666f\u8be5\u662f\u4f55\u7b49\u7684\u6fc0\u52a8\u4eba\u5fc3\u554a\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u8fd9\u65f6\u5019\u4fa7\u8fc7\u5934\u6765\u95ee\u5f20\u606a\uff1a\u201c\u8096\u662f\u8bf4\u4e1c\u4eac\u8ba4\u8bc6\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u5979\u4e5f\u5230\u5efa\u90ba\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u5979\u63d0\u524d\u7ed3\u675f\u4e86\u5728\u5343\u53f6\u7684\u5b66\u4e1a\uff0c\u5728\u4e1c\u4eac\u901a\u8fc7\u9526\u6e56\u7684\u5de5\u4f5c\u9762\u8bd5\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u5728\u5efa\u90ba\u8ba4\u8bc6\u7684\u4eba\u4f1a\u8d8a\u6765\u8d8a\u591a\u7684\uff0c\u4e0d\u59a8\u5c06\u8fd9\u91cc\u5f53\u6210\u4f60\u7684\u7b2c\u4e8c\u6545\u4e61\u3002\u201d</p><p>\u3000\u3000\u8096\u96c1\u5b81\u662fiplayer\u5728\u4e1c\u4eac\u4e0a\u5e02\u63a8\u5e7f\u65f6\u5f20\u606a\u524d\u5f80\u4e1c\u4eac\u8ba4\u8bc6\u7684\u9152\u5427\u5973\u90ce\uff0c\u8fd8\u662f\u56e0\u4e3a\u8096\u96c1\u5b81\u7684\u7f18\u6545\uff0c\u5f20\u606a\u4e0e\u674e\u99a8\u4e88\u5728\u4e1c\u4eac\u7684\u5076\u5c14\u9082\u9005\u5ef6\u957f\u6210\u4e00\u6bb5\u4ee4\u4eba\u5fc3\u9189\u7684\u5f02\u4e61\u4e4b\u65c5\uff0c\u8096\u96c1\u5b81\u867d\u7136\u6ca1\u6709\u771f\u6b63\u7684\u6cbe\u6c34\uff0c\u603b\u662f\u6709\u6bb5\u4e0d\u582a\u7684\u8fc7\u53bb\uff0c\u5728\u65e5\u672c\u751f\u6d3b\u4e5f\u89c9\u5f97\u8eab\u5fc3\u75b2\u60eb\uff0c\u63d0\u524d\u7ed3\u675f\u5b66\u4e1a\u56de\u56fd\u3002\u9526\u6e56\u4e00\u76f4\u4ee5\u6765\u90fd\u901a\u8fc7\u6d77\u5916\u673a\u6784\u62db\u8058\u6d77\u5916\u7559\u5b66\u4eba\u5458\u56de\u56fd\u5c31\u4e1a\uff0c\u8096\u96c1\u5b81\u4e5f\u501f\u8fd9\u4e2a\u6e20\u9053\u8fdb\u5165\u9526\u6e56\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u6240\u6846\u67b6\u4e0b\u6210\u7acb\u7684\u72ec\u7acb\u529e\u516c\u5ba4\uff0c\u662f\u9526\u6e56\u6700\u9ad8\u51b3\u7b56\u673a\u5236\u7684\u79d8\u4e66\u673a\u6784\uff0c\u9700\u8981\u9ad8\u5c42\u6b21\u77e5\u8bc6\u80cc\u666f\u7684\u9ad8\u7d20\u8d28\u5458\u5de5\uff0c\u4e5d\u540d\u5458\u5de5\uff0c\u56db\u4eba\u6709\u6d77\u5916\u7559\u5b66\u80cc\u666f\uff0c\u8096\u96c1\u5b81\u4e0d\u7b97\u9526\u6e56\u7684\u8001\u5458\u5de5\uff0c\u4f46\u662f\u7531\u4e8e\u4e0e\u5f20\u606a\u7b97\u662f\u201c\u65e7\u8bc6\u201d\uff0c\u4e5f\u7834\u4f8b\u7ed9\u63a8\u8350\u8fdb\u6765\u5de5\u4f5c\u2014\u2014\u4e0d\u8fc7\u8096\u96c1\u5b81\u5728\u65e5\u672c\u4ea4\u4e86\u7537\u670b\u53cb\uff0c\u662f\u9526\u6e56\u5728\u65e5\u672c\u5206\u516c\u53f8\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u80fd\u5305\u5bb9\u5979\u201c\u4e0d\u582a\u201d\u7684\u8fc7\u53bb\uff0c\u7fdf\u4e39\u9752\u63d0\u5979\u4e0d\u8fc7\u662f\u8981\u5632\u8bbd\u4e00\u4e0b\u5f20\u606a\uff0c\u6ca1\u60f3\u5230\u4e00\u4e0d\u7559\u795e\u7ed9\u5f20\u606a\u53cd\u6293\u4f4f\u628a\u67c4\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u770b\u4e86\u770b\u8155\u8868\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u5df2\u7ecf\u5230\u4e86\u53ef\u4ee5\u5403\u665a\u996d\u7684\u65f6\u95f4\uff0c\u6211\u5c31\u5148\u544a\u8f9e\u4e86\uff0c\u201d\u53c8\u4fcf\u76ae\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u4e0d\u59a8\u788d\u4f60\u4eec\u8ba8\u8bba\u5982\u6b64\u4e25\u8083\u7684\u827a\u672f\u8bdd\u9898\u2026\u2026\u201d\u6367\u8d77\u5427\u53f0\u4e0a\u7684\u4e66\u672c\uff0c\u5c31\u5f80\u5916\u8d70\u53bb\u5374\u7ed9\u51fa\u73b0\u5728\u9152\u5427\u95e8\u5916\u7684\u4ee4\u5c0f\u71d5\u6321\u4e86\u6321\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u6253\u6b7b\u6211\u90fd\u4e0d\u8fdb\u53bb\u4e86\uff01\u201d\u4ee4\u5c0f\u71d5\u51fa\u73b0\u9152\u5427\u95e8\u53e3\u770b\u7740\u9152\u5427\u91cc\u7684\u5973\u5b69\u4eec\uff0c\u6c14\u52bf\u6c79\u6c79\u7684\u8bf4\u9053\uff1a\u201c\u770b\u5230\u4f60\u4eec\u5728\u4e00\u8d77\uff0c\u603b\u6709\u79cd\u8ba9\u8981\u6293\u72c2\u7684\u51b2\u52a8\u554a\uff01\u9648\u5983\u84c9\u90a3\u59ae\u5b50\u600e\u4e48\u6ca1\u6709\u8fc7\u6765\uff0c\u7d22\u6027\u5f7b\u5e95\u7684\u8ba9\u6211\u5d29\u6e83\u5f97\u4e86\u3002\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u3001\u4f55\u5f26\u3001\u5b59\u9759\u6aac\u3001\u7fdf\u4e39\u9752\u3001\u536b\u5170\u3001\u9648\u5983\u84c9\uff0c\u968f\u4fbf\u54ea\u4e00\u4e2a\u7ad9\u51fa\u6765\u4e0d\u662f\u5a07\u5a9a\u8981\u8001\u547d\u7684\u4e3d\u5bb9\uff1f\u666e\u901a\u5973\u6027\u770b\u4e86\u90fd\u8981\u627f\u53d7\u6781\u5927\u7684\u5fc3\u7406\u538b\u529b\uff0c\u4ee4\u5c0f\u71d5\u5012\u662f\u53d7\u6253\u51fb\u60ef\u4e86\uff0c\u5f53\u5e74\u5979\u4e0e\u6c5f\u9edb\u513f\u4e5f\u662f\u505a\u966a\u886c\u7684\u547d\uff0c\u5634\u91cc\u867d\u7136\u8fd9\u4e48\u53eb\u56a3\u7740\uff0c\u811a\u4e0b\u8fd8\u662f\u6162\u60a0\u60a0\u7684\u8d70\u5c06\u8fdb\u6765\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u62bf\u5634\u7b11\u7740\u3001\u5c06\u4e66\u672c\u6367\u5728\u80f8\u524d\u79bb\u5f00\u3002</p><p>\u3000\u3000\u9664\u4e86\u4f55\u5f26\u5faa\u7740\u5386\u53f2\u7684\u8f68\u8ff9\u4e00\u76f4\u505c\u7559\u5728\u8fd9\u91cc\u5916\uff0c\u5b59\u9759\u6aac\u3001\u7fdf\u4e39\u9752\u3001\u536b\u5170\u3001\u9648\u5983\u84c9\u3001\u674e\u99a8\u4e88\u90fd\u662f\u6216\u591a\u6216\u5c11\u56e0\u4e3a\u5f20\u606a\u7684\u539f\u56e0\u624d\u805a\u96c6\u5230\u8fd9\u91cc\uff0c\u4e5f\u4ee4\u5efa\u90ba\u7684\u71d5\u5f52\u6e56\u9ad8\u6821\u7fa4\u5728\u8fd9\u4e24\u5e74\u4ee4\u4eba\u683c\u5916\u7684\u6000\u5ff5\u2014\u2014\u867d\u8bf4\u6328\u7740\u97f3\u4e50\u5b66\u9662\u3001\u5e08\u8303\u5927\u5b66\u8fd9\u4e24\u6240\u76db\u4ea7\u7f8e\u5973\u7684\u9ad8\u6821\uff0c\u4f46\u662f\u5996\u5b7d\u7ea7\u7684\u7978\u6c34\u7f8e\u5973\u5374\u662f\u53ef\u9047\u4e0d\u53ef\u6c42\u7684\u3002\u8981\u662f\u5404\u4eba\u90fd\u5faa\u7740\u5386\u53f2\u7684\u8f68\u8ff9\u53bb\u8d70\uff0c\u5b59\u9759\u6aac\u5927\u5b66\u4e8c\u5e74\u7ea7\u5c31\u4f1a\u53bb\u56fd\u5916\u7559\u5b66\uff0c\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u8fd8\u4f1a\u5728\u90a3\u79cd\u7279\u6b8a\u7684\u73af\u5883\u91cc\u627f\u53d7\u547d\u8fd0\u51cb\u96f6\u7684\u6298\u78e8\uff0c\u9648\u5983\u84c9\u9ad8\u4e2d\u5c31\u662f\u8f8d\u5b66\uff0c\u674e\u99a8\u4e88\u6700\u7ec8\u4e5f\u4f1a\u5728\u4e1c\u4eac\u516c\u5bd3\u51b0\u51b7\u7684\u51b0\u7f38\u91cc\u7ed3\u675f\u5979\u5e74\u8f7b\u7684\u751f\u547d\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u6781\u559c\u6b22\u6b64\u65f6\u76841978\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8d70\u5230\u5427\u53f0\u91cc\uff0c\u7ed9\u81ea\u5df1\u5012\u4e86\u676f\u77ff\u6cc9\u6c34\uff0c\u5f00\u59cb\u770b\u5f20\u606a\u7ed9\u5979\u7684\u90a3\u51e0\u9875\u62a5\u544a\u7eb8\uff0c\u5c06\u62a5\u544a\u644a\u5728\u5427\u53f0\u4e0a\uff0c\u671d\u5427\u53f0\u5916\u4fa7\u7684\u5f20\u606a\u62db\u4e86\u62db\u624b\u3002\u5f20\u606a\u8eab\u5b50\u503e\u8fc7\u53bb\uff0c\u4f46\u662f\u5427\u53f0\u5f88\u5bbd\uff0c\u5b59\u9759\u6aac\u60f3\u4e0e\u5f20\u606a\u8033\u8bed\u8fd8\u662f\u4e0d\u65b9\u4fbf\uff0c\u5979\u633a\u62d4\u7ea4\u7ec6\u7684\u8eab\u5b50\u8db4\u5728\u5427\u53f0\u4e0a\uff0c\u4f38\u624b\u591f\u8fc7\u53bb\u62c9\u4f4f\u5f20\u606a\u7684\u8033\u6735\uff0c\u53c8\u8ba9\u5f20\u606a\u7684\u8111\u888b\u5f80\u5979\u8eab\u8fb9\u9760\u4e86\u9760\uff0c\u8ddf\u4ed6\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u9ebb\u6728\u4e0d\u4ec1\u7684\u4eba\uff0c\u8981\u80fd\u8ba9\u4ed6\u4eec\u5c1d\u5230\u4e00\u65e0\u6240\u6709\u7684\u6ecb\u5473\u624d\u53eb\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7f8e\u5973\u5fc3\u80a0\u591a\u6076\u6bd2\uff0c\u201d\u5f20\u606a\u65e0\u5948\u7684\u82e6\u7b11\uff0c\u201c\u4f60\u845b\u5bb6\u7684\u8d44\u4ea7\u597d\u6b79\u5728\u9999\u6e2f\u534e\u5546\u5708\u5185\u66fe\u5360\u8fc7\u8001\u56db\u7684\u4f4d\u5b50\uff0c\u4f60\u5916\u516c\u88ab\u6e2f\u4eba\u79f0\u4e3a\u56db\u53d4\uff0c\u90fd\u8bf4\u7626\u6b7b\u7684\u9a86\u9a7c\u6bd4\u9a6c\u5927\uff0c\u6b64\u65f6\u7684\u9526\u6e56\u6bd4\u8d77\u5609\u4fe1\u96c6\u56e2\u66fe\u7ecf\u63a7\u5236\u5728\u624b\u91cc\u7684\u5e9e\u5927\u8d44\u4ea7\u8fd8\u8981\u5dee\u4e00\u622a\uff0c\u8981\u4e0d\u662f\u4f60\u4eec\u845b\u5bb6\u5185\u90e8\u5927\u4e71\uff0c\u7ecf\u6d4e\u5371\u673a\u53c8\u6301\u7eed\u8fd9\u4e48\u957f\u7684\u65f6\u95f4\uff0c\u6211\u4eec\u8fd8\u771f\u4e0d\u80fd\u5948\u4ed6\u4eec\u4f55\u2014\u2014\u60f3\u8981\u8ba9\u4ed6\u4eec\u5931\u53bb\u4e00\u5207\u3001\u4e00\u65e0\u6240\u6709\uff0c\u8fd9\u4e2a\u8981\u6c42\u7684\u96be\u5ea6\u8fd8\u771f\u662f\u6709\u70b9\u9ad8\u2026\u2026\u8981\u662f\u8fd9\u6b21\u9999\u6e2f\u8bc1\u76d1\u90e8\u95e8\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u8c03\u67e5\u8fd8\u80fd\u67e5\u51fa\u522b\u7684\u5b9e\u8d28\u6027\u95ee\u9898\u6765\uff0c\u4e8b\u60c5\u5012\u5bb9\u6613\u529e\u4e9b\u2014\u2014\u867d\u8bf4\u56f0\u517d\u72b9\u6597\uff0c\u4e0d\u8fc7\u4e5f\u66f4\u5bb9\u6613\u94e4\u800c\u8d70\u9669\u7684\u53bb\u8e29\u9677\u9631\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5fae\u62bf\u7740\u5634\uff0c\u5fae\u6b6a\u7740\u5934\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u4f1a\u505a\u7684\u5bf9\u4e0d\u5bf9\uff1f\u201d\u4e24\u4eba\u8138\u6328\u7740\uff0c\u5b59\u9759\u6aac\u5634\u5507\u7c89\u6da6\u5149\u4eae\u3001\u5507\u5f62\u6781\u7f8e\u8bf1\u4eba\uff0c\u8981\u4e0d\u662f\u65c1\u8fb9\u6709\u4eba\uff0c\u5f20\u606a\u4f1a\u5fcd\u4e0d\u4f4f\u51d1\u8fc7\u5438\u4e00\u53e3\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5c06\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u7684\u4eb2\u5bc6\u6a21\u6837\u770b\u5728\u773c\u91cc\u4e5f\u4e0d\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u591c\u91cc\uff0c\u82cf\u6d25\u4e1c\u7ecf\u8fc7\u5efa\u90ba\uff0c\u7ed9\u5f20\u606a\u9001\u6765\u51e0\u53ea\u521a\u4ece\u751f\u4ea7\u7ebf\u4e0b\u7ebf\u7684iplayer\u3002</p><p>\u3000\u3000\u5012\u4e0d\u8bf4\u662f\u4ec0\u4e48\u6700\u65b0\u6280\u672f\uff0c\u4e0e\u4ee5\u5f80\u7684\u4ea7\u54c1\u4e0d\u540c\uff0c\u8fd9\u51e0\u6b3eiplayer\u5305\u62ec\u5fae\u5904\u7406\u5668\u3001\u97f3\u9891\u89e3\u7801\u82af\u7247\u3001\u50a8\u5b58\u82af\u7247\u4ee5\u53ca\u63a7\u5236\u82af\u7247\u5728\u5185\u7684\u6838\u5fc3\u5143\u4ef6\u90fd\u662f\u9526\u6e56\u901a\u8fc7\u81ea\u8eab\u7814\u7a76\u4e0e\u6280\u672f\u4ea4\u6362\u7b49\u5404\u79cd\u624b\u6bb5\u6240\u83b7\u5f97\u7684\u81ea\u6709\u6280\u672f\uff0c\u82af\u7247\u7684\u751f\u4ea7\u4e5f\u662f\u7531\u4e2d\u6676\u5fae\u82af\u4e0e\u7231\u8fbe\u5fae\u7535\u5b50\u65d7\u4e0b\u7684\u5de5\u5382\u8054\u5408\u5b8c\u6210\uff0c\u6db2\u6676\u5c4f\u4e5f\u662f\u7531\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u65d7\u4e0b\u7684\u5de5\u5382\u751f\u4ea7\uff0c\u5176\u4ed6\u4e3b\u8981\u914d\u4ef6\u4e5f\u662f\u7531\u6d77\u5dde\u6216\u5efa\u90ba\u7684\u7535\u5b50\u5382\u5546\u63d0\u4f9b\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5728\u9526\u6e56\u65e5\u76ca\u4e30\u5bcc\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e2d\uff0c\u8fd9\u51e0\u6b3eiplayer\u53ea\u80fd\u7b97\u4e2d\u4f4e\u7aef\u4ea7\u54c1\uff0c\u63a5\u9a73\u5230\u7acb\u4f53\u97f3\u7bb1\u4e0a\uff0c\u58f0\u97f3\u62e7\u5927\u65f6\uff0c\u6709\u4e9b\u5fae\u6ecb\u6ecb\u7684\u7535\u6d41\u6742\u97f3\uff0c\u8fd9\u8868\u660e\u4ea7\u54c1\u7684\u54c1\u8d28\u8fd8\u6709\u8bb8\u591a\u9700\u8981\u63d0\u9ad8\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u97f3\u7bb1\u91cc\u4f20\u51fa\u6765\u7684\u6c5f\u9edb\u513f\u7684\u6b4c\u5589\u5374\u8ba9\u5f20\u606a\u542c\u5f97\u5982\u75f4\u5982\u9189\uff0c\u5f85iplayer\u91cc\u50a8\u5b58\u7684\u5341\u4e8c\u9996\u6b4c\u64ad\u653e\u5b8c\u5c06\u8981\u5faa\u73af\u64ad\u653e\u65f6\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\u5c06\u97f3\u7bb1\u7684\u58f0\u97f3\u62e7\u5c0f\uff0c\u5bf9\u82cf\u6d25\u4e1c\u8bf4\u9053\uff1a\u201c\u522b\u4eba\u610f\u8bc6\u4e0d\u5230\u8fd9\u5bf9\u9526\u6e56\u610f\u5473\u7740\u4ec0\u4e48\uff0c\u56db\u5e74\u524d\uff0c\u4f60\u5230\u6d77\u5dde\u6765\u89c1\u6211\uff0c\u6709\u6ca1\u6709\u60f3\u8fc7\u6211\u4eec\u80fd\u505a\u5230\u8fd9\u4e00\u6b65\uff1f\u201d</p><p>\u3000\u3000\u201c\u505a\u60f3\u4e5f\u6ca1\u6709\u60f3\u8fc7\uff0c\u201d\u82cf\u6d25\u4e1c\u7b11\u7740\u6447\u4e86\u6447\u5934\uff0c\u201c\u6211\u662f\u5f88\u73b0\u5b9e\u7684\u4e00\u4e2a\u4eba\uff0c\u5373\u4f7f\u505a\u68a6\u4e5f\u4e0d\u4f1a\u6709\u4e0d\u5207\u5b9e\u9645\u7684\u5e7b\u60f3\uff0c\u8981\u662f\u56db\u5e74\u524d\u6709\u4eba\u544a\u8bc9\u6211\u4e2d\u56fd\u7684\u7535\u5b50\u4ea7\u4e1a\u80fd\u505a\u5230\u8fd9\u4e00\u6b65\uff0c\u6211\u4e00\u5b9a\u4f1a\u8ba4\u4e3a\u8fd9\u662f\u75f4\u4eba\u505a\u68a6\uff01\u201d</p><p>\u3000\u3000\u7684\u786e\uff0c\u4e5d\u56db\u5e74\u7684\u65f6\u5019\uff0c\u8c01\u4f1a\u76f8\u4fe1\u56fd\u5185\u7684\u7535\u5b50\u4ea7\u4e1a\u57fa\u7840\u80fd\u652f\u6301\u4e00\u679a\u5168\u65b0\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u7684\u751f\u4ea7\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5c06\u684c\u4e0a\u7684\u4e00\u679aiplayer\u4e3e\u5230\u706f\u5149\u4e0b\u7ec6\u770b\uff0c\u867d\u7136\u4e0d\u662f\u9526\u6e56\u6700\u9ad8\u7aef\u7684\u4ea7\u54c1\uff0c\u4ed6\u5374\u6709\u7740\u6b23\u8d4f\u827a\u672f\u54c1\u7684\u6b23\u559c\u795e\u60c5\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8981\u5c06\u7231\u8fbe\u4e0b\u9762\u7684\u7ecf\u7406\u4eec\u53ec\u96c6\u8d77\u6765\u5f00\u4e2a\u5e86\u529f\u5bb4\uff1f\u201d\u82cf\u6d25\u4e1c\u7b11\u76c8\u76c8\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8981\u4f4e\u8c03\u4e00\u4e9b\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u8981\u4f4e\u8c03\u4e00\u4e9b\uff0c\u4e0d\u8981\u8ba9\u4e0b\u9762\u4eba\u8bef\u4ee5\u4e3a\u6211\u4eec\u5bf9\u6b64\u5c31\u5df2\u7ecf\u6ee1\u8db3\u4e86\u2014\u2014\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u65e0\u5948\u7684\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u770b\u5f20\u606a\u5634\u89d2\u8fb9\u7684\u6d45\u7b11\uff0c\u54ea\u91cc\u6709\u4e0d\u6ee1\u8db3\u7684\u6a21\u6837\uff1f\u80fd\u5728\u4e2d\u4f4e\u7aef\u7684iplayer\u4ea7\u54c1\u4e0a\u51e0\u4e4e\u5b8c\u5168\u7684\u5b9e\u73b0\u56fd\u4ea7\u5316\uff0c\u7edd\u5bf9\u662f\u4e00\u4e2a\u4ee4\u4eba\u6fc0\u52a8\u7684\u6807\u5fd7\u6027\u6210\u529f\u3002</p><p>\u3000\u3000\u5728\u6b64\u4e4b\u524d\uff0c\u9526\u6e56\u7684\u4e2d\u4f4e\u7aef\u4ea7\u54c1\uff0c32M\u50a8\u5b58\u5bb9\u91cf\u7684iplayer\u5728\u56fd\u9645\u5e02\u573a\u4e0a\u552e\u4ef7\u7ea6\u4e09\u767e\u7f8e\u5143\uff0c\u5b9a\u4ef7\u5982\u6b64\u4e4b\u9ad8\u7684\u6210\u672c\u538b\u529b\u4e3b\u8981\u6765\u81ea\u95ea\u5b58\u82af\u7247\uff0c\u8fd9\u4e5f\u963b\u788d\u4e86iplayer\u7684\u524d\u671f\u5e02\u573a\u6269\u5f20\u3002</p><p>\u3000\u3000\u4e1c\u829d\u4ece\u516b\u5341\u5e74\u4ee3\u521d\u671f\u5c31\u5f00\u59cb\u5f00\u53d1NAND\u578b\u95ea\u5b58\uff0c\u76f4\u5230\u9526\u6e56\u5f00\u53d1\u51faDMP\u6570\u5b57\u97f3\u9891\u64ad\u653e\u5668\u4ea7\u54c1\u6280\u672f\uff0cNAND\u578b\u95ea\u5b58\u624d\u9996\u6b21\u83b7\u5f97\u5927\u89c4\u6a21\u5546\u4e1a\u5e94\u7528\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u4e1c\u829d\u8981\u5c06\u5341\u591a\u5e74\u6765\u5728NAND\u7814\u53d1\u4e0a\u7684\u5de8\u989d\u6295\u5165\u5728\u77ed\u671f\u5185\u6536\u56de\uff0c\u8fd8\u8981\u83b7\u5f97\u8db3\u591f\u7684\u8d44\u91d1\u6295\u5165\u5230\u95ea\u5b58\u6280\u672f\u7684\u6301\u7eed\u7814\u53d1\u4e4b\u4e2d\uff0c\u5e02\u573a\u65e9\u671f\u7684\u95ea\u5b58\u82af\u7247\u7684\u5b9a\u4ef7\u4e4b\u9ad8\uff0c\u81ea\u7136\u4e0d\u96be\u60f3\u8c61\u2014\u2014\u4e09\u661f\u4ece\u516b\u5341\u5e74\u4ee3\u672b\u671f\u4ece\u4e1c\u829d\u83b7\u5f97NAND\u578b\u95ea\u5b58\u6280\u672f\uff0c\u6295\u5165\u7684\u7814\u53d1\u8d44\u91d1\u7a0d\u4f4e\uff0c\u6280\u672f\u5012\u662f\u843d\u540e\u4e8e\u4e1c\u829d\uff0c\u76f4\u5230\u770b\u5230\u95ea\u5b58\u6280\u672f\u6709\u5927\u89c4\u6a21\u5546\u4e1a\u5e94\u7528\u7684\u53ef\u80fd\u4e86\uff0c\u53c8\u5f00\u59cb\u6295\u5165\u5927\u91cf\u7684\u8d44\u91d1\u52a0\u5f3a\u7814\u7a76\uff0c\u5176\u63d0\u4f9b\u7ed9\u5e02\u573a\u7684NAND\u578b\u95ea\u5b58\u82af\u7247\u6027\u4ef7\u6bd4\u751a\u81f3\u6bd4\u4e1c\u829d\u8fd8\u5dee\u3002</p><p>\u3000\u3000\u9526\u6e56\u4eca\u5e74\u4e94\u6708\u4ece\u4e1c\u829d\u624b\u91cc\u4ea4\u6362\u83b7\u5f97\u7684\u95ea\u5b58\u6280\u672f\u662f\u6700\u65b0\u7684\u5b8c\u6574\u6280\u672f\uff0c\u4ea7\u54c1\u5b9a\u4ef7\u4e0a\u65e0\u9700\u644a\u8584\u524d\u671f\u7684\u7814\u7a76\u6210\u672c\uff0c\u5373\u4f7f\u8003\u8651\u5230\u4e2d\u6676\u5fae\u82af\u7684\u826f\u54c1\u7387\u8fd8\u4e0d\u80fd\u8ba9\u4eba\u6ee1\u610f\uff0c\u4e00\u679a\u4e1c\u829d\u552e\u4ef7\u4e00\u767e\u7f8e\u5143\u768432M\u95ea\u5b58\u82af\u7247\uff0c\u9526\u6e56\u7684\u6210\u672c\u4e5f\u53ea\u6709\u5341\u7f8e\u5143\u5de6\u53f3\uff0c\u52a0\u4e0a\u5fae\u5904\u7406\u5668\u3001\u97f3\u9891\u89e3\u7801\u5668\u3001\u63a7\u5236\u5143\u4ef6\u7b49\u6838\u5fc3\u82af\u7247\u7684\u81ea\u4ea7\u5316\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u4e00\u679a32M\u7684iplayer\u5728\u56fd\u9645\u5e02\u573a\u4e0a\u7684\u552e\u4ef7\u80fd\u591f\u4ece\u4e09\u767e\u7f8e\u5143\u76f4\u964d\u5230\u4e8c\u767e\u7f8e\u5143\u4ee5\u5185\u9526\u6e56\u8fd8\u80fd\u591f\u4fdd\u7559\u8db3\u591f\u7684\u5229\u6da6\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u9526\u6e56\u5bf9\u65d7\u4e0b\u7684iplayer\u4ea7\u54c1\u5728\u5168\u7403\u5e02\u573a\u964d\u4ef7\u9500\u552e\uff0c\u4e1c\u829d\u4e0e\u4e09\u661f\u4e5f\u4f1a\u5bf9\u95ea\u5b58\u82af\u7247\u8fdb\u884c\u964d\u4ef7\u4f7f\u5f97\u6240\u6709iplayer\u5382\u5546\u7684\u6210\u672c\u964d\u4f4e\u2014\u2014\u5bf9\u4e1c\u829d\u4e0e\u4e09\u661f\u6765\u8bf4\u53ea\u662f\u5c06\u524d\u671f\u7814\u53d1\u6295\u8d44\u7684\u56de\u6536\u671f\u5ef6\u957f\u4e00\u6bb5\u65f6\u95f4\u800c\u5df2\uff0c\u8fd9\u4e0d\u662fiplayer\u5382\u5546\u95f4\u5927\u6253\u4ef7\u683c\u6218\u8fdb\u884c\u6076\u6027\u7ade\u4e89\uff0c\u53ea\u662f\u610f\u5473\u7740\u5c06\u76f4\u63a5\u83b7\u5f97\u5bf9MD\u64ad\u653e\u5668\u7b49\u97f3\u54cd\u4ea7\u54c1\u7684\u4ef7\u683c\u4f18\u52bf\u3002</p><p>\u3000\u3000\u5728\u5168\u7403\u97f3\u54cd\u5e02\u573a\uff0ciplayer\u65f6\u4ee3\u5373\u5c06\u5230\u6765\u3002</p></article>"}}}]);
