(window.webpackJsonp=window.webpackJsonp||[]).push([[1143],{1166:function(p){p.exports={chapterTitle:null,title:"\u7b2c1140\u7ae0 \u5371\u673a\u4e0e\u6295\u673a",content:"<article><h2>\u7b2c1140\u7ae0 \u5371\u673a\u4e0e\u6295\u673a</h2><p>\u3000\u3000\u56fd\u5185\u6c11\u4f17\u4eab\u53d7\u52b3\u52a8\u8282\u9ec4\u91d1\u5468\u7684\u60a0\u957f\u5047\u671f\uff0c\u4f01\u4e8b\u4e1a\u673a\u5173\u5355\u4f4d\u653e\u5047\uff0c\u6caa\u6df1\u4e24\u5e02\u4e5f\u4f11\u5e02\u505c\u6b62\u4ea4\u6613\uff0c\u592a\u5e73\u6d0b\u5f7c\u5cb8\u7684\u8bc1\u5238\u5e02\u573a\u4f9d\u65e7\u70ed\u706b\u671d\u5929\uff0c\u5438\u5f15\u5168\u7403\u5927\u591a\u6570\u6295\u8d44\u8005\u7684\u76ee\u5149\u2014\u2014\u7f8e\u56fd\u6c11\u4f17\u8981\u7b49\u5230\u4e5d\u6708\u7684\u7b2c\u4e00\u4e2a\u661f\u671f\u4e00\u624d\u80fd\u4eab\u53d7\u52b3\u52a8\u8282\u7684\u516c\u5047\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u4e09\u5929\u96ea\u5d29\u4f3c\u7684\u4e0b\u632b\uff0c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5728\u7ecf\u8fc7\u53cc\u4f11\u65e5\u77ed\u6682\u7684\u505c\u987f\u4e4b\u540e\uff0c\u4e94\u6708\u7684\u7b2c\u4e09\u5929\u5f00\u59cb\u62c9\u952f\u5f0f\u7684\u632f\u8361\uff1b\u867d\u7136\u6709\u4e00\u4e9b\u53cd\u5f39\u7684\u82d7\u5934\uff0c\u7ec8\u662f\u4e0d\u6210\u4ec0\u4e48\u89c4\u6a21\u2014\u2014\u7eb3\u65af\u8fbe\u514b\u4e09\u5929\u96ea\u5d29\u5f0f\u7684\u4e0b\u632b\uff0c\u8ba9\u666e\u901a\u6295\u8d44\u8005\u90fd\u660e\u767d\u4e86\u534e\u5c14\u8857\u6295\u8d44\u5bb6\u9f13\u5439\u7684\u65b0\u7ecf\u6d4e\u7e41\u8363\u6d6a\u6f6e\u662f\u591a\u4e48\u7684\u8106\u5f31\u3002</p><p>\u3000\u3000\u56fd\u5185\u9ec4\u91d1\u5468\u957f\u671f\u95f4\uff0c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u4e00\u76f4\u90fd\u9634\u8dcc\u4e0d\u4f11\uff0c\u6ca1\u6709\u50cf\u6a21\u50cf\u6837\u7684\u53cd\u5f39\u2014\u2014\u5f20\u606a\u4ee5\u53ca\u8bb8\u591a\u4eba\u90fd\u76f8\u4fe1\u90a3\u4e9b\u8131\u8eab\u8f83\u65e9\u7684\u6295\u8d44\u8005\u4f1a\u6709\u76f8\u5f53\u90e8\u5206\u8f6c\u8eab\u52a0\u5165\u6cbd\u7a7a\u7684\u9635\u8425\uff0c\u589e\u52a0\u6307\u6570\u5411\u4e0b\u884c\u8d70\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u4e94\u6708\u521d\u7684\u91d1\u5c71\u591c\u51c9\u5982\u6c34\uff0c\u5510\u5a67\u5149\u7740\u811a\u4e2b\u5b50\u8e29\u5728\u5730\u677f\u4e0a\uff0c\u5979\u7a7f\u7740\u5fae\u5fae\u6709\u4e9b\u900f\u5149\u7684\u8584\u5370\u82b1\u5e03\u7761\u8863\uff0c\u4ece\u80cc\u540e\u6402\u4f4f\u5f20\u606a\uff0c\u4e0b\u5df4\u78d5\u7740\u4ed6\u7684\u5934\u9876\u4e0a\uff0c\u67d4\u817b\u5ae9\u6ed1\u7684\u4e0b\u5df4\u7ed9\u4ed6\u77ed\u5934\u53d1\u523a\u5f97\u53d1\u75d2\uff0c\u76ef\u7740\u7b14\u8bb0\u672c\u5c4f\u5e55\u4e0a\u663e\u793a\u7684\u90ae\u4ef6\uff0c\u95ee\u9053\uff1a\u201c\u5c3c\u514b\u91cc\u68ee\u6bcf\u5929\u90fd\u8981\u53d1\u8fd9\u4e48\u957f\u5e45\u5ea6\u7684\u62a5\u544a\u6765\u5417\uff1f\u201d</p><p>\u3000\u3000\u9999\u6e2f\u5927\u5b66\u52b3\u52a8\u8282\u53ea\u6709\u4e00\u5929\u7684\u516c\u5047\uff0c\u52a0\u4e0a\u5468\u672b\uff0c\u5510\u5a67\u53ea\u80fd\u56de\u5185\u5730\u5446\u4e0a\u4e09\u5929\uff0c\u7b2c\u4e00\u591c\u5728\u5efa\u90ba\u5ea6\u8fc7\uff0c\u7b2c\u4e8c\u591c\u81ea\u7136\u5c31\u56de\u91d1\u5c71\u966a\u5979\u7238\u5988\u4e86\u3002\u5f20\u606a\u966a\u5728\u5510\u5a67\u8eab\u8fb9\u7684\u65f6\u95f4\u6709\u9650\uff0c\u81ea\u7136\u4e5f\u8981\u966a\u5979\u5230\u91d1\u5c71\u6765\u591a\u73a9\u4e00\u5929\uff0c\u597d\u5728\u5510\u5b66\u8c26\u3001\u987e\u5efa\u840d\u5df2\u7ecf\u8ba4\u4e86\u4ed6\u8fd9\u4e2a\u51c6\u5973\u5a7f\uff0c\u5230\u91d1\u5c71\u540e\u4e0e\u5510\u5a67\u4e5f\u4e0d\u9700\u8981\u5047\u6a21\u5047\u6837\u7684\u5206\u623f\u7761\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u591c\u91cc\u6709\u5546\u52a1\u6d3b\u52a8\u8981\u53c2\u52a0\uff0c\u5f20\u606a\u4ed6\u4eec\u7b80\u5355\u5403\u8fc7\u665a\u996d\uff0c\u5c31\u56de\u623f\u6d17\u6fa1\u4f11\u606f\u3002\u8d81\u5510\u5a67\u5728\u6d74\u5ba4\u6d17\u6fa1\u65f6\uff0c\u5f20\u606a\u7a7f\u7740\u7761\u8863\u5750\u5728\u529e\u516c\u684c\u524d\u6293\u7d27\u65f6\u95f4\u5c06\u90ae\u4ef6\u770b\u5b8c\uff0c\u8fd9\u5df2\u7ecf\u662f\u5c3c\u514b\u674e\u68ee\u8fde\u7eed\u4e00\u5468\u65f6\u95f4\u4ee5\u6765\u6bcf\u5929\u53d1\u4e00\u5c01\u90ae\u4ef6\u7ed9\u4ed6\u3002</p><p>\u3000\u3000\u201c\u54ea\u4f1a\u5929\u5929\u6709\uff1f\u5c31\u8fd9\u51e0\u5929\u591a\u4e9b\uff0c\u8981\u662f\u5929\u5929\u6709\uff0c\u6211\u5bb0\u4e86\u4ed6\u7684\u5fc3\u90fd\u6709\uff01\u201d\u5f20\u606a\u53cd\u624b\u6293\u4f4f\u5510\u5a67\u7684\u5c0f\u624b\uff0c\u5c06\u5979\u6402\u5165\u6000\uff0c\u8ba9\u5979\u5750\u5230\u81ea\u5df1\u7684\u5927\u817f\u4e0a\u6765\uff0c\u624b\u638c\u4e60\u60ef\u6027\u7684\u653e\u5728\u5979\u7ea4\u7ec6\u5f39\u8f6f\u7684\u8170\u4e0a\uff0c\u611f\u89c9\u90a3\u91cc\u662f\u90a3\u6837\u7684\u5706\u6da6\uff0c\u53c8\u662f\u90a3\u6837\u7684\u5145\u6ee1\u5f39\u529b\u3002</p><p>\u3000\u3000\u5510\u5a67\u5750\u5728\u5f20\u606a\u7684\u5927\u817f\u4e0a\uff0c\u8eab\u5b50\u4e0d\u5b89\u5206\u7684\u4e71\u626d\uff0c\u6309\u7740\u9f20\u6807\u5c06\u90ae\u4ef6\u5f80\u4e0b\u62d6\uff0c\u76ef\u7740\u5197\u957f\u800c\u4e4f\u5473\u7684\u62a5\u544a\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u8981\u6253\u54c8\u6b20\uff0c\u4e5f\u4e0d\u987e\u5f20\u606a\u7684\u53cc\u624b\u4e0d\u8001\u5b9e\u7684\u6293\u7740\u5979\u80f8\u524d\u7684\u90a3\u5bf9\u6602\u9996\u633a\u7acb\u7684\u4e73\u9e3d\uff0c\u8f6c\u56de\u5934\u6402\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u201c\u65e2\u7136\u80fd\u5224\u65ad\u7eb3\u6307\u5728\u4e00\u6bb5\u8f83\u957f\u65f6\u95f4\u91cc\u5904\u4e8e\u4e0b\u884c\u901a\u9053\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u591a\u8c03\u4e9b\u8d44\u91d1\u6cbd\u7a7a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u632f\u8361\u5f97\u5389\u5bb3\uff0c\u5373\u4f7f\u80fd\u5224\u65ad\u5c40\u52bf\u4f1a\u6301\u7eed\u6076\u5316\u4e0b\u53bb\uff0c\u4f46\u662f\u6574\u4e2a\u8fc7\u7a0b\u4e5f\u8981\u6bd4\u4e00\u822c\u4eba\u60f3\u8c61\u7684\u590d\u6742\u3001\u9669\u6076\u2026\u2026\u201d\u5f20\u606a\u8010\u5fc3\u7684\u8ddf\u5510\u5a67\u89e3\u91ca\uff0c\u201c\u4e09\u4e95\u3001\u4e09\u661f\u8fd9\u4e9b\u65e5\u97e9\u8d22\u56e2\u90fd\u79d8\u5bc6\u63a7\u5236\u7740\u6570\u91cf\u4e0d\u7b49\u3001\u89c4\u6a21\u5927\u5c0f\u4e0d\u4e00\u7684\u5bf9\u51b2\u57fa\u91d1\uff0c\u4f46\u662f\u5bf9\u51b2\u57fa\u91d1\u7684\u6295\u673a\u884c\u4e3a\u4ece\u6765\u90fd\u4e0d\u662f\u8fd9\u4e9b\u8d22\u56e2\u7684\u4e3b\u4e1a\uff0c\u4e00\u65e6\u4ea7\u4e1a\u8d44\u672c\u7ed9\u6253\u4e0a\u6295\u673a\u8005\u7684\u6807\u7b7e\uff0c\u52bf\u5fc5\u4f1a\u964d\u4f4e\u5e02\u573a\u4ee5\u53ca\u6d88\u8d39\u8005\u5bf9\u4ea7\u4e1a\u8d44\u672c\u7684\u4fe1\u4efb\u5ea6\u2026\u2026\u4e0d\u4f1a\u989d\u5916\u8c03\u96c6\u8d44\u91d1\uff0c\u5bf9\u51b2\u57fa\u91d1\u65d7\u4e0b\u7684\u8d44\u91d1\u4e5f\u4e0d\u4f1a\u96c6\u4e2d\u5230\u4e00\u4e2a\u5e02\u573a\u4e0a\u725f\u5229\uff0c\u9664\u4e86\u5206\u6563\u98ce\u9669\u4e4b\u5916\uff0c\u4e5f\u8981\u5206\u6563\u5176\u4ed6\u52bf\u529b\u7684\u6ce8\u610f\u529b\u3002\u201d</p><p>\u3000\u3000\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u671f\u95f4\uff0c\u9526\u6e56\u5728\u6d77\u5916\u79d8\u5bc6\u8bbe\u7acb\u7684\u5bf9\u51b2\u57fa\u91d1\u5728\u56fd\u9645\u5916\u6c47\u5e02\u573a\u6295\u8d44\u6cf0\u94e2\u3001\u97e9\u5143\u3001\u5370\u5c3c\u76fe\u7b49\u8d27\u5e01\u79cd\u7c7b\u8fdb\u884c\u505a\u7a7a\u4ea4\u6613\uff0c\u6240\u83b7\u9887\u4e30\u3002\u4e4b\u540e\u5dee\u4e0d\u591a\u6709\u4e09\u4ebf\u7f8e\u5143\u7684\u8d62\u5229\u4ee5\u516c\u53f8\u503a\u6743\u7684\u540d\u4e49\u5217\u5165\u9526\u6e56\u5546\u4e8b\u7684\u8d26\u4e0b\u3002</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u4f5c\u4e3a\u9526\u6e56\u8fdb\u884c\u4ea7\u4e1a\u8d44\u672c\u8fd0\u4f5c\u7684\u6838\u5fc3\uff0c\u8d1f\u503a\u89c4\u6a21\u9ad8\u8fbe\u4e8c\u5341\u4e94\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u5f53\u521d\u505a\u7a7a\u4e1c\u5357\u4e9a\u8d27\u5e01\u8d62\u5229\u7684\u4e09\u4ebf\u7f8e\u5143\u8f6c\u5230\u9526\u6e56\u5546\u4e8b\u5e10\u4e0b\uff0c\u5b9e\u9645\u4e0a\u5e76\u6ca1\u6709\u8d77\u5230\u591a\u5927\u7684\u4f5c\u7528\uff0c\u4e5f\u8bb8\u53ea\u80fd\u8ba9\u9526\u6e56\u9ad8\u5c42\u5fc3\u7406\u4e0a\u7a0d\u5f97\u5230\u4e9b\u5b89\u6170\uff1a\u9526\u6e56\u5546\u4e8b\u7684\u5b9e\u9645\u8d1f\u503a\u89c4\u6a21\u4e0d\u662f\u8d26\u9762\u4e0a\u7684\u4e8c\u5341\u4e94\u4ebf\u7f8e\u5143\uff0c\u800c\u662f\u4e8c\u5341\u4e8c\u4ebf\u7f8e\u5143\u3002\u4f46\u662f\u53c8\u6709\u4ec0\u4e48\u533a\u522b\u5462\uff1f\u4ee5\u9526\u6e56\u5546\u4e8b\u7684\u878d\u8d44\u80fd\u529b\uff0c\u80fd\u591f\u5c06\u8d1f\u503a\u89c4\u6a21\u63a7\u5236\u4e8c\u5341\u4e94\u4ebf\u7f8e\u5143\u5de6\u53f3\uff0c\u5373\u4f7f\u5f53\u521d\u6ca1\u6709\u4ece\u5bf9\u51b2\u57fa\u91d1\u91cc\u8f6c\u8fdb\u6765\u7684\u4e09\u4ebf\u7f8e\u5143\uff0c\u9526\u6e56\u5546\u4e8b\u4e5f\u80fd\u4ece\u5176\u4ed6\u91d1\u878d\u6295\u8d44\u673a\u6784\u878d\u5f97\u8fd9\u7b14\u8d44\u91d1\u3002</p><p>\u3000\u3000\u5f53\u521d\u8f6c\u51fa\u4e09\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\u51fa\u6765\u4e4b\u540e\uff0c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7b49\u4eba\u4ecd\u7136\u4ee5\u79c1\u4eba\u7684\u540d\u4e49\u5728\u5c3c\u514b\u91cc\u68ee\u6240\u7ba1\u7406\u7684\u5bf9\u51b2\u57fa\u91d1\u8d26\u6237\u4e0a\u7559\u6709\u8fd1\u4e00\u4ebf\u7f8e\u5143\u7684\u8d44\u672c\u91d1\u3002\u5bf9\u51b2\u57fa\u91d1\u8d26\u6237\u91cc\u7684\u8fd9\u90e8\u5206\u8d44\u91d1\u8fd9\u4e24\u5e74\u5728\u6d77\u5916\u6295\u8d44\u5e02\u573a\u9887\u6709\u65a9\u83b7\uff0c\u8d44\u672c\u91d1\u589e\u76ca\u6709\u516d\u5343\u4e07\u7f8e\u91d1\u5de6\u53f3\u3002\u8fd9\u5df2\u7ecf\u662f\u76f8\u5f53\u4e0d\u9519\u7684\u6210\u7ee9\uff0c\u4f46\u76f8\u6bd4\u8f83\u9526\u6e56\u65d7\u4e0b\u4ea7\u4e1a\u7684\u53d1\u5c55\uff0c\u672a\u514d\u6709\u4e9b\u4e0d\u591f\u770b\u2014\u2014\u4e5f\u6b63\u662f\u5982\u6b64\uff0c\u9526\u6e56\u4e0a\u4e0b\u5bf9\u5728\u91d1\u878d\u5e02\u573a\u7684\u6295\u673a\u884c\u4e3a\u90fd\u7f3a\u4e4f\u8db3\u591f\u7684\u5174\u8da3\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5c3c\u514b\u91cc\u68ee\u6240\u7ba1\u7406\u7684\u5bf9\u51b2\u57fa\u91d1\u89c4\u6a21\u4e5f\u8d85\u8fc7\u4e09\u4ebf\u7f8e\u5143\uff0c\u5168\u7403\u65b0\u7ecf\u6d4e\u6d6a\u6f6e\u6ce1\u6cab\u7834\u706d\u3001\u91d1\u878d\u98ce\u66b4\u518d\u5ea6\u6c79\u6d8c\uff0c\u4e5f\u6070\u6070\u662f\u4ed6\u4eec\u51fa\u6765\u5174\u98ce\u4f5c\u6d6a\u7684\u65f6\u523b\uff0c\u5c3c\u514b\u91cc\u68ee\u8fd9\u6bb5\u65f6\u95f4\u51e0\u4e4e\u6bcf\u5929\u90fd\u8981\u8ddf\u5f20\u606a\u5c31\u5c40\u52bf\u53d1\u5c55\u8fdb\u884c\u4ea4\u6d41\u3002</p><p>\u3000\u3000\u65b0\u7ecf\u6d4e\u6d6a\u6f6e\u6ce1\u6cab\u7684\u7834\u706d\u5e76\u975e\u5076\u7136\u4e8b\u4ef6\uff0c\u5373\u4f7f\u4e0d\u662f\u5fae\u8f6f\u53cd\u5784\u65ad\u6848\u6210\u4e3a\u5bfc\u706b\u7d22\uff0c\u4e5f\u4f1a\u6709\u5176\u4ed6\u4e8b\u60c5\u6210\u4e3a\u4fc3\u8fdb\u6ce1\u6cab\u7834\u706d\u7684\u6700\u540e\u4e00\u6839\u7a3b\u8349\uff1b\u6ce1\u6cab\u7834\u706d\u6240\u5377\u8d77\u7684\u91d1\u878d\u98ce\u66b4\u4e5f\u6ce8\u5b9a\u5c06\u5411\u5b9e\u4f53\u4ea7\u4e1a\u6ce2\u53ca\u3002\u8fd9\u5f53\u4e2d\u4e5f\u8bb8\u4f1a\u6709\u6ce2\u6298\uff0c\u4f46\u662f\u5c40\u52bf\u7684\u957f\u671f\u53d1\u5c55\u5374\u662f\u4e00\u76ee\u4e86\u7136\u3002\u5f20\u606a\u5373\u4f7f\u6709\u7740\u524d\u5c18\u5f80\u4e16\u7684\u8bb0\u5fc6\uff0c\u5bf9\u5c40\u52bf\u7684\u5224\u65ad\u4e5f\u4e0d\u89c1\u5f97\u5c31\u6bd4\u7ad9\u5728\u534e\u5c14\u8857\u91d1\u5b57\u5854\u9876\u5c16\u7684\u4eba\u7269\u66f4\u654f\u9510\uff1b\u5bf9\u7eb3\u6307\u6cbd\u7a7a\u6216\u53cd\u5f39\u65f6\u673a\u7684\u5224\u65ad\uff0c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u6bd4\u5c3c\u514b\u91cc\u68ee\u66f4\u51c6\u786e\u3002</p><p>\u3000\u3000\u5c3c\u514b\u91cc\u68ee\u5728\u90ae\u4ef6\u91cc\u63d0\u5230\u516d\u6708\u4e2d\u65ec\u7f8e\u56fd\u8054\u90a6\u6700\u9ad8\u6cd5\u9662\u5bf9\u5fae\u8f6f\u53cd\u5784\u65ad\u6848\u7684\u4e0a\u8bc9\u88c1\u51b3\u5f88\u53ef\u80fd\u6210\u4e3a\u4e0b\u4e00\u4e2a\u5173\u952e\u7684\u65f6\u95f4\u8282\u70b9\u3002</p><p>\u3000\u3000\u82e5\u662f\u5fae\u8f6f\u7684\u4e0a\u8bc9\u88ab\u8054\u90a6\u6700\u9ad8\u6cd5\u9662\u9a73\u56de\uff0c\u5fae\u8f6f\u88ab\u5206\u62c6\u7684\u547d\u8fd0\u5c06\u65e0\u6cd5\u66f4\u6539\uff1b\u4f46\u662f\u5fae\u8f6f\u53cd\u5784\u65ad\u6848\u76f4\u63a5\u5f15\u8d77\u7eb3\u65af\u8fbe\u514b\u4ee5\u53ca\u5168\u7403\u65b0\u7ecf\u6d4e\u8d44\u672c\u5e02\u573a\u6709\u5982\u91d1\u878d\u6d77\u5578\u4e00\u822c\u7684\u632f\u8361\uff0c\u5176\u5f71\u54cd\u529b\u4e4b\u6df1\u8fdc\u3001\u6076\u52a3\u8fdc\u8fdc\u8d85\u8fc7\u666e\u901a\u4eba\u7684\u9884\u6599\uff0c\u5fae\u8f6f\u7ed9\u5206\u62c6\u7684\u53ef\u80fd\u53cd\u800c\u6781\u5927\u7684\u964d\u4f4e\u4e86\u2014\u2014\u516d\u6708\u4e2d\u65ec\u7f8e\u56fd\u8054\u90a6\u6cd5\u9662\u6781\u53ef\u80fd\u505a\u51fa\u5bf9\u5fae\u8f6f\u6709\u5229\u7684\u4e0a\u8bc9\u88c1\u51b3\uff0c\u5c4a\u65f6\u4e5f\u6781\u6709\u53ef\u80fd\u4f1a\u8bf1\u53d1\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5f3a\u52bf\u7684\u53cd\u5f39\u3002</p><p>\u3000\u3000\u8981\u662f\u534e\u5c14\u8857\u7684\u6295\u673a\u5ba2\u90fd\u6709\u7c7b\u4f3c\u7684\u89c2\u70b9\uff0c\u7f8e\u56fd\u8054\u90a6\u6700\u9ad8\u6cd5\u9662\u5bf9\u5fae\u8f6f\u53cd\u5784\u65ad\u6848\u7684\u4e0a\u8bc9\u88c1\u51b3\u4e4b\u524d\u7684\u8dcc\u5e45\u4ee5\u53ca\u4e4b\u540e\u7684\u53cd\u5f39\u5f88\u53ef\u80fd\u90fd\u5c06\u8fdc\u8fdc\u8d85\u8fc7\u5e38\u4eba\u7684\u9884\u671f\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u53ea\u80fd\u9644\u548c\u5c3c\u514b\u91cc\u68ee\u7684\u5224\u65ad\uff0c\u8fd9\u4e9b\u5e74\u6765\u7684\u5b9e\u7ee9\u4e5f\u8bc1\u5b9e\u5c3c\u514b\u91cc\u68ee\u662f\u6781\u4e3a\u4f18\u79c0\u3001\u89e6\u611f\u654f\u9510\u7684\u6295\u8d44\u5bb6\uff0c\u7279\u522b\u662f\u5bf9\u91d1\u878d\u5371\u673a\u4e2d\u8574\u85cf\u7684\u673a\u9047\u89e6\u89c9\u5341\u5206\u7684\u654f\u9510\u3002</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5f20\u606a\u867d\u7136\u4ecd\u5bf9\u57fa\u91d1\u6709\u7740\u5f88\u6df1\u7684\u5f71\u54cd\uff0c\u4f46\u4e0d\u518d\u76f4\u63a5\u63a7\u5236\uff0c\u5bf9\u51b2\u57fa\u91d1\u5df2\u7ecf\u53d8\u6210\u5c3c\u514b\u91cc\u68ee\u4e3b\u5bfc\u7684\u79c1\u52df\u57fa\u91d1\uff0c\u5f20\u606a\u4e0e\u5b59\u5c1a\u4e49\u4ed6\u4eec\u4e5f\u7eaf\u7cb9\u7684\u4ee5\u79c1\u4eba\u6295\u8d44\u8005\u7684\u8eab\u4efd\u53c2\u4e0e\u6295\u8d44\uff0c\u8981\u662f\u4ece\u9526\u6e56\u6210\u5458\u4f01\u4e1a\u8d26\u6237\u91cc\u8c03\u96c6\u8d44\u91d1\u7684\u8bdd\uff0c\u53c8\u4f1a\u5c06\u597d\u4e0d\u5bb9\u6613\u68b3\u7406\u7684\u5173\u7cfb\u590d\u6742\u5316\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5510\u5a67\u5728\u91d1\u5c71\u4f4f\u4e86\u4e00\u591c\uff0c\u7b2c\u4e8c\u5929\u4e0a\u5348\u5c31\u4e58\u98de\u673a\u76f4\u63a5\u4ece\u91d1\u5c71\u98de\u5f80\u9999\u6e2f\uff0c\u5f20\u606a\u9001\u5510\u5a67\u4e0a\u98de\u673a\u4e4b\u540e\u518d\u79bb\u5f00\u673a\u573a\u524d\u5f80\u4e2d\u6676\u5fae\u82af\u91d1\u5c71\u5de5\u5382\u7684\u5efa\u8bbe\u5de5\u5730\u3002\u5510\u5b66\u8c26\u516c\u52a1\u7e41\u5fd9\uff0c\u6ca1\u6709\u65f6\u95f4\u53bb\u9001\u673a\uff0c\u4e5f\u662f\u7ea6\u597d\u65f6\u95f4\u8ddf\u5f20\u606a\u5728\u5de5\u5730\u6c47\u5408\u3002</p><p>\u3000\u3000\u91d1\u5c71\u5de5\u5382\u5efa\u9020\u542f\u52a8\u624d\u4e24\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u6f06\u6210\u5496\u5561\u8272\u7684\u94c1\u827a\u6805\u680f\u56f4\u51fa\u4e00\u5927\u7247\u5730\uff0c\u8fdc\u5904\u6709\u4e00\u7247\u6c34\u6749\u6797\u3002\u5f20\u606a\u4e0e\u5510\u5b66\u8c26\u5c31\u5c06\u8f66\u505c\u5728\u6c34\u6749\u6797\u5916\uff0c\u8d70\u8fdb\u6c34\u6749\u6797\u91cc\uff0c\u5728\u6c34\u6749\u6797\u7684\u53e6\u4e00\u4fa7\uff0c\u662f\u4e00\u5ea7\u6e05\u6f88\u7684\u6cb3\u6d41\uff0c\u5341\u4e03\u516b\u7c73\u5bbd\uff0c\u6cb3\u6ee9\u4e0a\u6742\u8349\u4e1b\uff0c\u5c31\u77e5\u9053\u8fd9\u662f\u4e00\u6761\u5e73\u65f6\u4e0d\u600e\u4e48\u53d7\u6ce8\u610f\u7684\u6c34\u6cb3\u3002\u5f20\u606a\u5012\u6ca1\u6709\u60f3\u5230\u91d1\u57ce\u65b0\u57ce\u8303\u56f4\u4e4b\u5185\u4f1a\u6709\u8fd9\u4e48\u6e05\u6f88\u7684\u6d41\u6c34\u3002</p><p>\u3000\u3000\u201c\u65b0\u7ecf\u6d4e\u6ce1\u6cab\u7834\u706d\u5f15\u53d1\u7684\u91d1\u878d\u6d77\u5578\u5bf9\u5b9e\u4f53\u7684\u6d89\u53ca\u4f1a\u6709\u591a\u6df1\uff1f\u201d\u5510\u5b66\u8c26\u8fd8\u662f\u5173\u7cfb\u4f01\u4e1a\u53d1\u5c55\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u5f88\u96be\u8bf4\uff0c\u201d\u5f20\u606a\u4e5f\u4e0d\u80fd\u76f4\u63a5\u8bf4\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5f88\u53ef\u80fd\u5c06\u6df1\u8dcc\u5230\u6700\u9ad8\u70b9\u65f6\u7684\u56db\u5206\u4e4b\u4e00\u5de6\u53f3\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u5192\u5145\u4ec0\u4e48\u9884\u8a00\u5bb6\uff0c\u4ed6\u8fd8\u662f\u62ff\u9526\u6e56\u7ecf\u7814\u4e2d\u5fc3\u4ee5\u53ca\u5c3c\u514b\u91cc\u68ee\u63d0\u4ea4\u7684\u62a5\u9053\u6765\u8ddf\u5510\u5b66\u8c26\u89e3\u91ca\uff0c\u201c\u5411\u5b9e\u4f53\u6269\u6563\u662f\u5fc5\u7136\u8d8b\u52bf\u2026\u2026\u80a1\u5e02\u5982\u6b64\u6df1\u8dcc\uff0c\u52bf\u5fc5\u662f\u8981\u5c06\u6574\u4e2a\u65b0\u7ecf\u6d4e\u90fd\u8981\u62d6\u4e0b\u6df1\u6e0a\uff0c\u8bc1\u5238\u8d44\u672c\u5e02\u573a\u4ee5\u53ca\u98ce\u6295\u57fa\u91d1\u5373\u4f7f\u4e0d\u5bf9\u7f51\u7edc\u516c\u53f8\u5173\u4e0a\u5927\u95e8\uff0c\u7559\u4e0b\u7684\u90a3\u9053\u95e8\u950b\u4e5f\u5e94\u8be5\u6781\u4e3a\u6709\u9650\uff0c\u5927\u591a\u6570\u6ca1\u6709\u98ce\u9669\u8d44\u672c\u652f\u6491\u7684\u4e2d\u5c0f\u7f51\u7edc\u516c\u53f8\u90fd\u5f88\u96be\u518d\u751f\u5b58\u4e0b\u53bb\u2014\u2014\u7f51\u7edc\u516c\u53f8\u7684\u5927\u91cf\u5012\u95ed\uff0c\u9664\u4e86\u5149\u7ea4\u7b49\u901a\u4fe1\u57fa\u7840\u8bbe\u5907\u4ea7\u4e1a\u5e02\u573a\u9700\u6c42\u4f1a\u5927\u91cf\u964d\u4f4e\u4e4b\u5916\uff0c\u50a8\u5b58\u82af\u7247\u7b49\u7535\u5b50\u5143\u4ef6\u4ee5\u53ca\u8f6f\u4ef6\u9700\u6c42\u4e5f\u4f1a\u5927\u5e45\u4e0b\u964d\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u900f\u8fc7\u79c0\u76f4\u7684\u6c34\u6749\u6797\u6728\uff0c\u770b\u7740\u6811\u6797\u5bf9\u9762\u7684\u5de5\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u4e9a\u6d32\u8fd9\u4e24\u5e74\u6676\u5706\u5236\u9020\u4ea7\u4e1a\u5e76\u6ca1\u6709\u4ec0\u4e48\u8d77\u8272\uff0c\u8fd9\u6b21\u53c8\u8981\u53d7\u5f71\u54cd\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u8981\u662f\u6676\u5706\u5e02\u573a\u9700\u6c42\u8ddf\u6211\u5224\u65ad\u7684\u90a3\u6837\u4f1a\u5927\u5e45\u5ea6\u6536\u7f29\u7684\u8bdd\uff0c\u5168\u7403\u6676\u5706\u4ea7\u4e1a\uff0c\u7279\u522b\u662f\u4ee3\u5de5\u9886\u57df\uff0c\u9664\u4e86\u5c11\u6570\u51e0\u5bb6\u6676\u5706\u4f01\u4e1a\u80fd\u591f\u8d62\u5229\u4e4b\u5916\uff0c\u5927\u591a\u6570\u6676\u5706\u4f01\u4e1a\u90fd\u8981\u9677\u5165\u4e8f\u635f\u7684\u6ce5\u6dd6\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u534e\u590fNEC\u6301\u7eed\u4e8f\u635f\u662f\u65e9\u5c31\u80fd\u9884\u6d4b\u5230\u7684\u7ed3\u679c\uff0c\u5f20\u606a\u8fd8\u770b\u5230\u4e09\u661f\u6709\u53ef\u80fd\u5ef6\u8fdf\u5b8f\u82af\u7535\u5b50\u7684\u5efa\u8bbe\u8ba1\u5212\u2014\u2014\u4e0e\u4e09\u661f\u7535\u5b50\u5171\u540c\u6295\u8d44\u6676\u5706\u5382\u672c\u662f\u6d77\u7c9f\u79d1\u6280\u5e02\u573a\u7092\u4f5c\u7684\u4e00\u4e2a\u70ed\u70b9\uff0c\u4e09\u661f\u5ef6\u8fdf\u5b8f\u82af\u7535\u5b50\u7684\u5efa\u8bbe\uff0c\u5bf9\u6d77\u7c9f\u79d1\u6280\u5e94\u8be5\u7b97\u662f\u4e00\u6b21\u4e0d\u5c11\u7684\u6253\u51fb\uff0c\u53ea\u662f\u4e0d\u6e05\u695a\u4f55\u65f6\u4f1a\u53d1\u751f\u3002</p></article>"}}}]);
