(window.webpackJsonp=window.webpackJsonp||[]).push([[1144],{1167:function(p){p.exports={chapterTitle:null,title:"\u7b2c938\u7ae0 \u60c5\u6295\u610f\u5408",content:"<article><h2>\u7b2c938\u7ae0 \u60c5\u6295\u610f\u5408</h2><p>\u3000\u3000\u201c\u2026\u2026\u201d\u7fdf\u4e39\u9752\u7741\u773c\u6123\u770b\u4e86\u5510\u5a67\u7247\u523b\uff0c\u4efb\u662f\u5979\u8001\u7ec3\u4e16\u6545\uff0c\u4e4d\u542c\u5230\u5510\u5a67\u95ee\u81ea\u5df1\u559c\u6b22\u5f20\u606a\u4ec0\u4e48\u4e5f\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u5f85\u770b\u5230\u5510\u5a67\u7684\u76ee\u5149\u4f3c\u7c98\u5728\u5f20\u606a\u8eab\u4e0a\u7684\u6ca1\u6709\u6ce8\u610f\u7684\u81ea\u5df1\u7684\u5931\u6001\uff0c\u624d\u610f\u8bc6\u5230\u8fd9\u53ea\u662f\u5979\u8ddf\u4eba\u8bf4\u8bdd\u7684\u65b9\u5f0f\u800c\u5df2\uff0c\u5927\u6982\u65c1\u8fb9\u5750\u4e2a\u7537\u7684\uff0c\u5979\u4e5f\u4f1a\u95ee\u4ed6\u559c\u6b22\u5f20\u606a\u4ec0\u4e48\u7684\uff0c\u51c6\u786e\u7684\u8bf4\uff0c\u5e94\u8be5\u662f\u95ee\u4ed6\u4eec\u8fd9\u4e9b\u65e0\u8bba\u5230\u4ec0\u4e48\u5730\u65b9\u90fd\u582a\u79f0\u7cbe\u82f1\u7684\u4eba\u4e3a\u4ec0\u4e48\u5fc3\u7518\u60c5\u613f\u7684\u805a\u96c6\u5728\u9526\u6e56\u7684\u65d7\u4e0b\u3002</p><p>\u3000\u3000\u201c\u6211\u80fd\u559c\u6b22\u4ed6\u4ec0\u4e48\uff1f\u201d\u7fdf\u4e39\u9752\u63a9\u9970\u6389\u5185\u5fc3\u7684\u6ce2\u6f9c\uff0c\u8f7b\u7b11\u9053\uff0c\u201c\u8fd9\u5e74\u5934\u627e\u4e00\u4e2a\u7ed9\u6211\u5f00\u8fd9\u4e48\u9ad8\u5de5\u8d44\u7684\u8001\u677f\u53ef\u4e0d\u5bb9\u6613\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u542c\u7fdf\u4e39\u9752\u8bf4\uff0c\u4fa7\u8fc7\u5934\u6765\u563b\u563b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u80a5\u5f97\u5192\u6cb9\uff0c\u770b\u4ed6\u6574\u5929\u6e38\u624b\u597d\u95f2\u7684\uff0c\u4e8b\u60c5\u90fd\u8ba9\u4f60\u4eec\u5e2e\u7740\u505a\u6389\u4e86\uff0c\u53ef\u4e0d\u80fd\u90fd\u4fbf\u5b9c\u4e86\u4ed6\u4e00\u4e2a\u4eba\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5fc3\u91cc\u5374\u5728\u60f3\u5510\u5a67\u5bf9\u5f20\u606a\u7684\u60c5\u611f\u8fd8\u662f\u5e26\u7740\u4e9b\u5d07\u62dc\u7684\u56e0\u7d20\uff0c\u5728\u4e24\u4eba\u7684\u5173\u7cfb\u4e0a\u5c31\u4e0b\u610f\u8bc6\u7684\u964d\u4f4e\u4e86\u81ea\u5df1\u7684\u5730\u4f4d\uff0c\u60f3\u60f3\u665a\u6674\u3001\u8bb8\u601d\uff0c\u5927\u6982\u90fd\u662f\u4e0d\u6562\u5962\u671b\u62e5\u6709\u8fd9\u5bb6\u4f19\u5b8c\u6574\u60c5\u611f\u7684\uff0c\u5979\u53c8\u5728\u60f3\uff1a\u5510\u5a67\u77e5\u4e0d\u77e5\u9053\u665a\u6674\u7684\u5b58\u5728\uff1f\u4e0d\u8fc7\u5979\u5fc3\u91cc\u4e5f\u662f\u6709\u4e9b\u5fc3\u865a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u7ed9\u5f20\u606a\u7684\u4e00\u756a\u8bdd\u8bf4\u5f97\u5fc3\u7eea\u6fc0\u8361\uff0c\u4e00\u65f6\u7406\u4e0d\u51fa\u4ec0\u4e48\u5934\u7eea\u6765\u56de\u5e94\u5f20\u606a\u7684\u95ee\u9898\uff0c\u505a\u4e00\u756a\u4e8b\u4e1a\u662f\u9700\u8981\u6fc0\u60c5\u7684\uff0c\u4e0d\u7136\u5982\u4f55\u80fd\u5728\u804c\u4e1a\u751f\u6daf\u6709\u66f4\u5927\u7684\u7a81\u7834\uff1f</p><p>\u3000\u3000\u5bf9\u4e00\u76f4\u6765\u90fd\u662f\u9526\u6e56\u7684\u8001\u670b\u53cb\uff0c\u5f20\u606a\u4e5f\u65e0\u9700\u8ddf\u5f20\u4e9a\u5e73\u7ec6\u8bf4\u9526\u6e56\u7684\u73b0\u72b6\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u8ddf\u4ed6\u515c\u591a\u5927\u7684\u5708\u5b50\u518d\u8f6c\u5230\u6b63\u9898\u4e0a\uff0c\u5f84\u76f4\u7684\u8bf4\u9053\uff1a\u201c\u5728\u8fc7\u53bb\u7684\u4e00\u5e74\u591a\u65f6\u95f4\u91cc\uff0c\u7231\u8fbe\u4e0b\u9762\u5e94\u8be5\u8bf4\u662f\u5404\u4e2a\u90e8\u95e8\u7684\u6210\u957f\u90fd\u5f88\u8fc5\u901f\uff0c\u6211\u4eec\u5df2\u7ecf\u5728\u4f4e\u4e2d\u7aefiplayer\u4ea7\u54c1\u4e0a\u5b9e\u73b0\u5143\u5668\u4ef6\u81ea\u4ea7\u5316\u7387\u8d85\u8fc780\uff05\uff0c\u8fd9\u4e2a\u76ee\u6807\u540c\u6837\u4f1a\u5f88\u5feb\u5728\u624b\u673a\u53ca\u5176\u4ed6\u4ea7\u54c1\u4e0a\u4e1a\u52a1\u4e0a\u5b9e\u73b0\uff0c\u5728\u5438\u6536\u3001\u6d88\u5316\u53ca\u521b\u65b0\u6280\u672f\u65b9\u9762\u90fd\u53d6\u5f97\u5f88\u5927\u7684\u8fdb\u6b65\uff0c\u6211\u5bf9\u7231\u8fbe\u4e0a\u4e0b\u7684\u5de5\u4f5c\u6ca1\u6709\u4e1d\u6beb\u7684\u4e0d\u6ee1\uff0c\u5373\u4f7f\u6709\u4e0d\u8db3\uff0c\u90a3\u4e5f\u662f\u56e0\u4e3a\u4e0d\u80fd\u4e00\u53e3\u5403\u6210\u80d6\u5b50\u2014\u2014\u4e2d\u6676\u5fae\u82af\u4e5f\u53ea\u7528\u4e0d\u5230\u4e00\u5e74\u7684\u65f6\u95f4\u5c31\u6210\u529f\u6295\u4ea7\u4e86\uff0c\u5927\u5bb6\u90fd\u5bf9\u826f\u54c1\u7387\u611f\u5230\u4e0d\u751a\u6ee1\u610f\uff0c\u521a\u6295\u4ea7\u826f\u54c1\u7387\u5c31\u80fd\u8fbe\u523027\uff05\uff0c\u6211\u672c\u4eba\u5f88\u6ee1\u610f\u4e86\u3002\u56fd\u5185\u7684\u57fa\u7840\u5f88\u5f31\uff0c\u9700\u8981\u7ed9\u4e2d\u6676\u5fae\u82af\u4e00\u4e2a\u63d0\u9ad8\u7684\u8fc7\u7a0b\u3002\u7231\u8fbe\u8fd9\u8fb9\uff0c\u751f\u4ea7\u6280\u672f\u4e0e\u6761\u4ef6\uff0c\u6211\u4eec\u8981\u5dee\u4e00\u4e9b\uff0c\u62db\u8058\u7684\u5de5\u4eba\u8981\u6210\u4e3a\u719f\u624b\u4e5f\u9700\u8981\u4e00\u5b9a\u7684\u65f6\u95f4\uff0c\u5de5\u5382\u53ea\u6709\u82db\u523b\u7684\u63a7\u5236\u51fa\u5382\u4ea7\u54c1\u7684\u8d28\u91cf\u624d\u80fd\u7ef4\u62a4\u7231\u8fbe\u54c1\u724c\u7684\u5f62\u8c61\uff0c\u56e0\u6b64\u826f\u54c1\u7387\u76f8\u6bd4\u8f83\u6d77\u5916\u4f01\u4e1a\u4e5f\u7a0d\u5dee\u4e00\u4e9b\uff0c\u751f\u4ea7\u6548\u7387\u4e5f\u8981\u5dee\u4e00\u4e9b\uff0c\u6211\u672c\u4eba\u6ca1\u6709\u4ec0\u4e48\u4e0d\u6ee1\u610f\u7684\u5730\u65b9\uff0c\u5012\u662f\u8001\u9648\u3001\u8001\u82cf\u4ed6\u4eec\u5e38\u5e38\u81ea\u6211\u68c0\u8ba8\u8bf4\u8981\u5728\u7cbe\u7ec6\u5316\u751f\u4ea7\u4e0a\u4e0b\u66f4\u5927\u7684\u5de5\u592b\uff0c\u8981\u6700\u5927\u9650\u5ea6\u7684\u964d\u4f4e\u8d28\u91cf\u4e8b\u6545\u3001\u63d0\u9ad8\u751f\u4ea7\u6548\u7387\uff0c\u4e3a\u6b64\u63d0\u51fa\u2018\u7cbe\u51c6\u5236\u9020\u2019\u8ba1\u5212\uff0c\u8981\u7ec4\u7ec7\u4e00\u4e2a\u56e2\u961f\u5b9e\u65bd\u8fd9\u4e2a\u8ba1\u5212\uff0c\u5bf9\u7231\u8fbe\u73b0\u6709\u7684\u6240\u6709\u5de5\u5e8f\u90fd\u8fdb\u884c\u7cbe\u7ec6\u5316\u89c4\u8303\u2014\u2014\u4ed6\u4eec\u90fd\u79f0\u8d5e\u4f60\u662f\u4e2d\u56fd\u7535\u5b50\u5236\u9020\u7ba1\u7406\u7684\u7b2c\u4e00\u4eba\uff0c\u8bf4\u4f60\u662f\u7ec4\u7ec7\u5e76\u9886\u5bfc\u8fd9\u4e2a\u56e2\u961f\u7684\u6700\u5408\u9002\u4eba\u9009\uff1b\u53e6\u5916\uff0c\u7231\u8fbe\u8fd8\u5c06\u8ba1\u5212\u7ec4\u5efa\u5143\u5668\u4ef6\u90e8\u95e8\u4ee5\u52a0\u5f3a\u5bf9\u65d7\u4e0b\u5143\u5668\u4ef6\u751f\u4ea7\u3001\u9500\u552e\u4e1a\u52a1\u7684\u7ba1\u7406\uff0c\u6211\u4e5f\u5e0c\u671b\u4f60\u80fd\u591f\u8fc7\u6765\u4e00\u5e76\u9886\u5bfc\u8fd9\u4e2a\u90e8\u95e8\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u4e0e\u98de\u5229\u6d66\u7684\u5408\u7ea6\u5373\u5c06\u5230\u671f\u4e86\uff0c\u98de\u5229\u6d66\u4e5f\u5e0c\u671b\u4ed6\u80fd\u591f\u7ee7\u7eed\u7559\u4efb\u5728\u98de\u5229\u6d66\uff0c\u9664\u4e86\u5143\u5668\u4ef6\u90e8\u5927\u4e2d\u534e\u533a\u603b\u88c1\u7684\u804c\u4f4d\u4e4b\u5916\uff0c\u8fd8\u52a0\u4e86\u5143\u5668\u4ef6\u90e8\u9ad8\u7ea7\u526f\u603b\u88c1\u7684\u7b79\u7801\uff0c\u6bd5\u7adf\u5f20\u4e9a\u5e73\u4e3a\u98de\u5229\u6d66\u5728\u5927\u4e2d\u534e\u533a\u7684\u4e1a\u52a1\u53d1\u5c55\u7acb\u4e0b\u6c57\u9a6c\u529f\u52b3\uff0c\u800c\u4ed6\u672c\u4eba\u4e5f\u6b63\u503c\u5e74\u5bcc\u529b\u5f3a\uff1b\u56fd\u5185\u4e5f\u6709\u591a\u5bb6\u5927\u578b\u7535\u5b50\u4f01\u4e1a\u7f34\u8bf7\u4ed6\u62c5\u4efb\u603b\u88c1\u6216\u9996\u5e2d\u6267\u884c\u5b98\uff0c\u4f46\u662f\u8fd9\u51e0\u5e74\u6765\u56fd\u5185\u7535\u5b50\u4e1a\u552f\u6709\u9526\u6e56\u7684\u4e8b\u4e1a\u6700\u8ba9\u4eba\u5fc3\u6fc0\u626c\uff0c\u800c\u4e14\u957f\u671f\u4ee5\u6765\u7684\u63a5\u89e6\uff0c\u5f20\u4e9a\u5e73\u5bf9\u9526\u6e56\u6709\u5f88\u6df1\u523b\u7684\u4e86\u89e3\uff0c\u77e5\u9053\u9526\u6e56\u7684\u4f18\u52bf\u4e0e\u9700\u8981\u6539\u8fdb\u7684\u5730\u65b9\uff0c\u4e0e\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u9c81\u5e86\u751f\u7b49\u4eba\u7684\u79c1\u8c0a\u4e5f\u76f8\u5f53\u4e0d\u9519\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u5185\u5fc3\u6df1\u5904\u662f\u6e34\u671b\u52a0\u5165\u9526\u6e56\u7684\uff0c\u4f46\u662f\u8fd9\u9700\u8981\u9526\u6e56\u63d0\u51fa\u6765\uff0c\u5e76\u5b89\u6392\u51fa\u4ed6\u80fd\u591f\u65bd\u5c55\u62f3\u811a\u3001\u53d1\u6325\u624d\u80fd\u7684\u7a7a\u95f4\u6765\u3002</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u5e74\u5e95\u5230\u4e5d\u516d\u5e74\u5e74\u521d\uff0c\u56fd\u5185\u789f\u673a\u4e1a\u52a1\u53d7\u5230\u673a\u82af\u4f9b\u5e94\u91cf\u7684\u5236\u7ea6\uff0c\u624b\u63e1\u98de\u5229\u6d66\u4e0a\u6d77\u5de5\u5382\u5927\u6743\u7684\u5f20\u4e9a\u5e73\u53ef\u4ee5\u8bf4\u76f4\u63a5\u51b3\u5b9a\u4e86\u5f53\u65f6\u789f\u673a\u4f01\u4e1a\u5728\u5e02\u573a\u4e0a\u80fd\u5360\u5230\u7684\u4efd\u989d\uff0c\u5f53\u65f6\u4ed6\u505a\u51b3\u7b56\u7684\u4f9d\u636e\uff0c\u5c31\u662f\u770b\u54ea\u5bb6\u789f\u673a\u4f01\u4e1a\u7684\u7ba1\u7406\u66f4\u5b8c\u5584\u3001\u66f4\u6709\u53d1\u5c55\u6f5c\u529b\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u6cbb\u7406\u5de5\u5382\u3001\u7ba1\u7406\u751f\u4ea7\u7684\u6c34\u5e73\u6781\u9ad8\uff0c\u5f53\u65f6\u7684\u7231\u8fbe\u4e5f\u56e0\u6b64\u83b7\u5f97\u5f20\u4e9a\u5e73\u7684\u9752\u7750\uff0c\u4e00\u4e0b\u5b50\u62ff\u5230\u98de\u5229\u6d66\u8fd1\u534a\u6570\u7684\u4f9b\u5e94\u91cf\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5e74\u6765\uff0c\u82cf\u6d25\u4e1c\u3001\u9c81\u5e86\u751f\u7b49\u4eba\u5bf9\u5de5\u5382\u7ba1\u7406\u7684\u8981\u6c42\u4e00\u5411\u90fd\u5f88\u82db\u523b\uff0c\u5728\u56fd\u5185\u540c\u4e1a\u91cc\u5f88\u7a81\u51fa\uff0c\u4f46\u662f\u82cf\u6d25\u4e1c\u3001\u9c81\u5e86\u751f\u7b49\u4eba\u81ea\u8eab\u4e5f\u6709\u7ecf\u9a8c\u4e0a\u7684\u4e0d\u8db3\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u7231\u8fbe\u751f\u4ea7\u90e8\u95e8\u7684\u6269\u5f20\u53c8\u662f\u5f02\u5e38\u7684\u5feb\u901f\uff0c\u4f7f\u5f97\u7231\u8fbe\u65d7\u4e0b\u5de5\u5382\u4e0e\u6d77\u5916\u7535\u5b50\u5de8\u5934\u7684\u751f\u4ea7\u7ba1\u7406\u6c34\u5e73\u76f8\u6bd4\uff0c\u8fd8\u662f\u5b58\u5728\u8f83\u5927\u7684\u5dee\u8ddd\u3002</p><p>\u3000\u3000\u7231\u8fbe\u76ee\u524d\u7684\u786e\u5b58\u5728\u719f\u7ec3\u5de5\u4eba\u4e0d\u8db3\uff0c\u6709\u7ecf\u9a8c\u7684\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u504f\u5c11\u3001\u751f\u4ea7\u4e0d\u591f\u7cbe\u7ec6\u3001\u5bf9\u51fa\u5382\u4ea7\u54c1\u54c1\u8d28\u8981\u6c42\u82db\u523b\u5bfc\u81f4\u826f\u54c1\u7387\u4e0b\u964d\u3001\u751f\u4ea7\u6548\u7387\u504f\u4f4e\u3001\u7269\u6599\u635f\u8017\u504f\u9ad8\u7b49\u8bf8\u591a\u95ee\u9898\uff0c\u8fd9\u4e9b\u4e5f\u90fd\u5236\u7ea6\u7740\u7231\u8fbe\u7684\u4ea7\u80fd\u65e0\u6cd5\u518d\u8fc5\u901f\u7684\u63d0\u9ad8\uff0c\u751f\u4ea7\u6210\u672c\u7684\u504f\u9ad8\u53c8\u62b5\u6d88\u56fd\u5185\u4eba\u529b\u8d44\u6e90\u6210\u672c\u7684\u4f18\u52bf\u3002</p><p>\u3000\u3000\u5e78\u597d\uff0c\u7231\u8fbe\u5728\u56fd\u5185\u5e02\u573a\u4e3b\u8981\u5f00\u5c55\u7684\u53ef\u4ee5\u8bf4\u662f\u6b63\u5904\u4e8e\u66b4\u5229\u671f\u7684\u624b\u673a\u4e1a\u52a1\uff0ciplayer\u4e0e\u95ea\u5b58\u76d8\u4f5c\u4e3a\u65b0\u5f00\u53d1\u51fa\u6765\u7684\u4ea7\u54c1\uff0c\u786e\u5b9a\u5e02\u573a\u552e\u4ef7\u65f6\u662f\u9884\u7559\u4e86\u8db3\u591f\u5229\u6da6\u7a7a\u95f4\u7684\uff0c\u9526\u6e56\u56e0\u6b64\u80fd\u591f\u5f62\u6210\u5de8\u5927\u7684\u76c8\u5229\u89c4\u6a21\uff0c\u8fd9\u4e9b\u4e5f\u8db3\u4ee5\u6682\u65f6\u7684\u906e\u63a9\u5176\u4ed6\u7684\u4e0d\u8db3\u4e4b\u5904\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u5f20\u606a\u4ee5\u53ca\u7231\u8fbe\u7684\u7ba1\u7406\u5c42\u662f\u6e05\u9192\u7684\uff0c\u6570\u5b57\u624b\u673a\u7684\u66b4\u5229\u671f\u4f1a\u8fc7\u53bb\uff0ciplayer\u3001\u95ea\u5b58\u76d8\u4e5f\u4e0d\u4f1a\u6c38\u8fdc\u90fd\u6ca1\u6709\u7ade\u4e89\u8005\uff0c\u90a3\u4e4b\u524d\u7684\u4e0d\u8db3\u4e4b\u5904\u5c31\u4f1a\u66b4\u9732\u51fa\u6765\u2014\u2014\u5f53\u7136\uff0c\u773c\u4e0b\u6700\u5934\u75bc\u7684\u95ee\u9898\u662f\u4ea7\u80fd\u65e0\u6cd5\u5feb\u901f\u7684\u63d0\u9ad8\u3002</p><p>\u3000\u3000\u91c7\u53d6\u7c97\u653e\u6a21\u5f0f\u7684\u6269\u5f20\u4ea7\u80fd\uff0c\u6700\u7ec8\u635f\u574f\u7684\u8fd8\u662f\u54c1\u724c\u81ea\u8eab\u7684\u5229\u76ca\uff0c\u5b81\u53ef\u8ba9\u5e02\u573a\u7f3a\u8d27\uff0c\u4e5f\u8981\u5c3d\u6700\u5927\u53ef\u80fd\u7684\u63a7\u5236\u4e0d\u8ba9\u4e0d\u826f\u54c1\u6d41\u5411\u5e02\u573a\uff0c\u8fd9\u662f\u7231\u8fbe\u6b64\u65f6\u91c7\u53d6\u7684\u7ecf\u8425\u5e95\u9650\u3002</p><p>\u3000\u3000\u5bf9\u5236\u7a0b\u5de5\u5e8f\u8fdb\u884c\u7cbe\u7ec6\u5316\u89c4\u8303\u7ba1\u7406\uff0c\u63d0\u9ad8\u751f\u4ea7\u6548\u7387\u3001\u6d88\u9664\u8d28\u91cf\u4e8b\u6545\u3001\u63d0\u9ad8\u826f\u54c1\u7387\uff0c\u65e0\u7591\u5c31\u80fd\u5728\u7ef4\u6301\u73b0\u6709\u7684\u5de5\u5382\u89c4\u6a21\u4e0b\u6700\u5927\u9650\u5ea6\u7684\u63d0\u9ad8\u4ea7\u91cf\u3002</p><p>\u3000\u3000\u5c31\u50cf\u4e2d\u6676\u5fae\u82af\u7684\u8bbe\u8ba1\u6708\u4ea7\u80fd\u662f\u4e24\u4e07\u7247\uff08\u826f\u54c1\u7387\u8fbe\u523060\uff05\uff09\uff0c\u6b64\u65f6\u521a\u6295\u4ea7\u7684\u4e2d\u6676\u5fae\u82af\u826f\u54c1\u7387\u8fd8\u4e0d\u523030\uff05\uff0c\u63d0\u9ad8\u826f\u54c1\u7387\uff0c\u63d0\u9ad8\u751f\u4ea7\u6548\u7387\uff0c\u5c06\u6708\u4ea7\u91cf\u63d0\u9ad8\u5230\u8bbe\u8ba1\u4ea7\u80fd\u4e00\u500d\u8fbe\u5230\u56db\u4e07\u7247\uff0c\u4e5f\u4e0d\u662f\u5929\u65b9\u591c\u8c08\u3002</p><p>\u3000\u3000\u4e00\u65e6\u4e0d\u5b58\u5728\u4ec0\u4e48\u66b4\u5229\uff0c\u751a\u81f3\u9677\u5165\u7ecf\u6d4e\u5371\u673a\u4e4b\u4e2d\uff0c\u7535\u5b50\u4f01\u4e1a\u8981\u5728\u6b8b\u9177\u7684\u7ade\u4e89\u4e2d\u751f\u5b58\u4e0b\u6765\uff0c\u4e3b\u8981\u7684\u8fd8\u662f\u8981\u9760\u8fd9\u65b9\u9762\u7684\u80fd\u529b\u3002</p><p>\u3000\u3000\u4f46\u662f\u8fd9\u65b9\u9762\u7684\u5de5\u4f5c\u9700\u8981\u4e00\u4e2a\u5f3a\u6709\u529b\u7684\u7ba1\u7406\u8005\u6765\u9886\u5bfc\uff0c\u53ef\u4ee5\u8bf4\u5f20\u4e9a\u5e73\u4e00\u76f4\u90fd\u5728\u5f20\u606a\u3001\u9648\u4fe1\u751f\u7b49\u4eba\u7684\u89c6\u91ce\u4e4b\u5185\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u672c\u4eba\u7684\u80fd\u529b\u3001\u8d44\u5386\u90fd\u5df2\u7ecf\u8db3\u591f\u4e86\uff0c\u800c\u4e14\u4ee5\u4ed6\u5728\u4e1a\u5185\u7684\u4eba\u8109\uff0c\u80fd\u5f88\u5feb\u7684\u7ec4\u7ec7\u8d77\u4e00\u4e2a\u6709\u7ecf\u9a8c\u7684\u56e2\u961f\u6765\u5f00\u5c55\u76f8\u5173\u5de5\u4f5c\uff0c\u4e0e\u9526\u6e56\u957f\u4e45\u4ee5\u6765\u7684\u5bc6\u5207\u5173\u7cfb\uff0c\u4f7f\u4ed6\u521d\u81f3\u9526\u6e56\u5c31\u62c5\u4efb\u7231\u8fbe\u96c6\u56e2\u526f\u603b\u88c1\u3001\u65b0\u7ec4\u5efa\u7684\u5143\u5668\u4ef6\u90e8\u95e8\u603b\u88c1\u8fd9\u6837\u7684\u8981\u804c\u4e5f\u4e0d\u4f1a\u5728\u9526\u6e56\u5185\u90e8\u5f15\u8d77\u592a\u5927\u7684\u6469\u64e6\u2014\u2014\u6700\u4e3a\u5173\u952e\u7684\uff0c\u8981\u5bf9\u5f20\u606a\u4f5c\u4e3a\u9526\u6e56\u7684\u6700\u9ad8\u9886\u5bfc\u8005\u6709\u8ba4\u540c\u611f\u3002</p><p>\u3000\u3000\u5bf9\u9526\u6e56\u3001\u5bf9\u5f20\u606a\u4e0d\u751a\u719f\u6089\u7684\u4eba\uff0c\u6ca1\u6709\u5171\u540c\u6210\u957f\u7684\u7ecf\u5386\uff0c\u8981\u5fc3\u7518\u60c5\u613f\u7684\u5728\u4e00\u4e2a\u521a\u4e8c\u5341\u5c81\u51fa\u5934\u7684\u9752\u5e74\u9886\u5bfc\u4e0b\u5c3d\u5fc3\u5c3d\u529b\u7684\u7ba1\u7406\u4e00\u4e2a\u5e9e\u5927\u7684\u90e8\u95e8\uff0c\u662f\u5f88\u96be\u60f3\u8c61\u7684\u4e8b\u60c5\u2014\u2014\u5f20\u4e9a\u5e73\u8fd9\u79cd\u5c42\u6b21\u7684\u4eba\u624d\uff0c\u9ad8\u85aa\u8d44\u5df2\u7ecf\u4e0d\u518d\u662f\u4ed6\u4eec\u4e3b\u8981\u8003\u8651\u7684\u95ee\u9898\uff0c\u4e8b\u4e1a\u7684\u8bf1\u60d1\u4e0e\u9886\u5bfc\u8005\u7684\u4eba\u683c\u9b44\u529b\u624d\u6210\u4e3a\u51b3\u5b9a\u4ed6\u4eec\u53bb\u7559\u7684\u5173\u952e\u6027\u56e0\u7d20\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0e\u5f20\u4e9a\u5e73\u4e4b\u95f4\u53ef\u4ee5\u62ff\u6765\u201c\u60c5\u6295\u610f\u5408\u201d\u6765\u5f62\u5bb9\uff0c\u8fd9\u5e72\u67f4\u53ea\u5dee\u70c8\u706b\u6765\u70b9\u71c3\uff0c\u518d\u600e\u4e48\u7740\u4e5f\u8981\u7b49\u5f20\u4e9a\u5e73\u5728\u98de\u5229\u6d66\u7684\u4efb\u671f\u7ed3\u675f\u3002</p><p>\u3000\u3000\u5bf9\u5f20\u606a\u6765\u8bf4\uff0c\u501f\u7535\u5b50\u5c55\u7684\u673a\u4f1a\uff0c\u4e0e\u5f20\u4e9a\u5e73\u89c1\u9762\uff0c\u624d\u662f\u4ed6\u8fd9\u6b21\u5230\u9999\u6e2f\u6765\u7684\u4e3b\u8981\u76ee\u7684\uff0c\u4e0d\u7136\u53cc\u65b9\u7684\u884c\u7a0b\u5f88\u96be\u51d1\u5230\u4e00\u8d77\u6765\u3002\u534a\u5bb5\u7545\u8c08\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5df2\u5230\u51cc\u6668\uff0c\u9001\u5f20\u4e9a\u5e73\u51fa\u95e8\u65f6\uff0c\u5f20\u4e9a\u5e73\u8fdb\u9526\u6e56\u7684\u4e8b\u60c5\u5c31\u8fd9\u6837\u786e\u5b9a\u4e0b\u6765\u4e86\u3002</p><p>\u3000\u3000\u5bf9\u66fe\u7ecf\u7ed9\u4e8e\u9526\u6e56\u5de8\u5927\u52a9\u529b\u7684\u5f20\u4e9a\u5e73\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u541d\u556c\uff0c\u4ed6\u8fdb\u9526\u6e56\uff0c\u5730\u4f4d\u8fd8\u8981\u5728\u9c81\u5e86\u751f\u3001\u6768\u4e91\u7b49\u4eba\u4e4b\u4e0a\uff0c\u4e0e\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u5730\u4f4d\u76f8\u5f53\uff0c\u76f4\u63a5\u8fdb\u5165\u51b3\u7b56\u5c42\uff0c\u5f85\u9047\u81ea\u7136\u4e5f\u662f\u5411\u9648\u4fe1\u751f\u3001\u8096\u664b\u6210\u3001\u6210\u6c49\u748b\u7b49\u4eba\u770b\u9f50\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u662f\u51cc\u6668\uff0c\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u90fd\u544a\u8f9e\u56de\u623f\u95f4\uff0c\u7fdf\u4e39\u9752\u5c06\u4e9b\u6750\u6599\u3001\u6587\u4ef6\u6536\u62fe\u597d\u624d\u544a\u8f9e\u79bb\u5f00\uff0c\u51fa\u95e8\u65f6\u8ddf\u5510\u5a67\u610f\u5473\u6a21\u7cca\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u53ef\u8981\u65e9\u4e9b\u7761\u554a\uff0c\u5c0f\u5fc3\u660e\u5929\u8d77\u5e8a\u540e\u6709\u9ed1\u773c\u5708\u8ba9\u4eba\u770b\u4e86\u7b11\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u80e1\u8bf4\u4ec0\u4e48\u554a\uff1f\u201d\u5510\u5a67\u5a07\u7f9e\u4e0d\u582a\u7684\u55d4\u6028\u4e86\u4e00\u53e5\uff0c\u5979\u8fc5\u901f\u7684\u51b2\u8fdb\u5367\u5ba4\uff0c\u6367\u7740\u6362\u6d17\u7684\u8863\u670d\uff0c\u5c31\u8eb2\u5230\u7fdf\u4e39\u9752\u8eab\u540e\u8ddf\u5f20\u606a\u8bf4\uff0c\u201c\u4f60\u4e5f\u8981\u65e9\u4e9b\u7761\u89c9\u554a\uff0c\u6211\u7761\u7fdf\u59d0\u623f\u91cc\u53bb\u2026\u2026\u201d\u8bdd\u6ca1\u6709\u8bf4\u5b8c\uff0c\u4eba\u5c31\u5148\u6e9c\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u4e2b\u5934\u600e\u4e48\u8fd9\u4e48\u5bb9\u6613\u4e0a\u5f53\u53d7\u9a97\u554a\uff1f\u5f20\u606a\u5fc3\u91cc\u5728\u6012\u543c\uff0c\u4f46\u662f\u7ed9\u4ed6\u4e24\u4e2a\u80c6\u5b50\u4e5f\u4e0d\u6562\u5728\u7fdf\u4e39\u9752\u9762\u524d\u6d41\u9732\u51fa\u6765\uff0c\u53ea\u662f\u671d\u7fdf\u4e39\u9752\u644a\u644a\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e5f\u65e9\u4e9b\u7761\u5427\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5f97\u610f\u7684\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8fd9\u4e00\u773c\u5a9a\u6001\u6a2a\u98de\uff0c\u8ba9\u5f20\u606a\u7684\u9aa8\u5934\u90fd\u9165\u4e86\u4e09\u5206\uff0c\u4f46\u662f\u7fdf\u4e39\u9752\u9ec4\u51fa\u4e86\u623f\u95f4\u5c31\u6709\u4e9b\u540e\u6094\u4e86\uff1a\u81ea\u5df1\u5230\u5e95\u662f\u5728\u7ea0\u7ed3\u4ec0\u4e48\u554a\uff1f\u4e0d\u662f\u4e0b\u5b9a\u51b3\u5fc3\u4e0d\u8ba9\u8fd9\u5bb6\u4f19\u518d\u6cbe\u4fbf\u5b9c\u4e86\u5417\uff0c\u8fd9\u65f6\u5019\u8fd8\u8ba1\u8f83\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f</p></article>"}}}]);
