(window.webpackJsonp=window.webpackJsonp||[]).push([[1146],{1169:function(p){p.exports={chapterTitle:null,title:"\u7b2c1143\u7ae0 \u9526\u6e56\u7684\u4e92\u8054\u7f51\u8d44\u6e90",content:"<article><h2>\u7b2c1143\u7ae0 \u9526\u6e56\u7684\u4e92\u8054\u7f51\u8d44\u6e90</h2><p>\u3000\u3000\u5165\u591c\uff0c\u4e00\u8f6e\u660e\u6708\u60ac\u6302\u5728\u57ce\u5e02\u7684\u4e0a\u7a7a\uff0c\u71d5\u5f52\u6e56\u5bf9\u5cb8\u7684\u5efa\u7b51\u7fa4\u5c31\u50cf\u526a\u7eb8\u4e00\u6837\u5355\u8584\u7684\u8d34\u5728\u753b\u6846\u5185\uff0c\u6e56\u9762\u4e0a\u63a0\u8fc7\u7684\u5c42\u5c42\u6d9f\u6f2a\uff0c\u6563\u5c04\u51fa\u94f6\u8272\u7684\u7cbc\u7cbc\u6ce2\u5149\u3002</p><p>\u3000\u3000\u4e8c\u697c\u7684\u9732\u53f0\u4e2d\u95f4\u6446\u7740\u4e00\u5f20\u9ed1\u8272\u9542\u82b1\u7684\u94a2\u5316\u73bb\u7483\u957f\u684c\uff0c\u5f20\u606a\u624b\u91cc\u62ff\u7740\u9152\u676f\u80cc\u9760\u7740\u680f\u6746\u7ad9\u5728\u90a3\u91cc\uff0c\u770b\u7740\u675c\u98de\u3001\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u3001\u65f6\u5b66\u658c\u3001\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u4eba\u5750\u5728\u957f\u684c\u65c1\u56f4\u7740\u8c22\u5b50\u5609\u5f7c\u6b64\u4ea4\u6d41\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u770b\u6cd5\uff0c\u6c14\u6c1b\u975e\u5e38\u7684\u70ed\u70c8\uff0c\u4e1d\u6beb\u6ca1\u6709\u89c9\u5bdf\u5230\u6b64\u65f6\u5df2\u7ecf\u662f\u51cc\u6668\u4e86\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u8981\u7167\u987e\u8fd8\u4e0d\u6ee1\u534a\u5468\u5c81\u5927\u7684\u5e7c\u513f\uff0c\u65e9\u65e9\u5c31\u56de\u53bb\u4e86\uff1b\u5f20\u606a\u672c\u6765\u6697\u793a\u9648\u9759\u8ddf\u4ed6\u5148\u8d70\uff0c\u53ea\u662f\u8c22\u5b50\u5609\u6b7b\u7f20\u70c2\u6253\u7684\u8ba9\u9648\u9759\u5728\u8fd9\u91cc\u966a\u5979\uff0c\u5f20\u606a\u4e5f\u53ea\u80fd\u5047\u88c5\u5bf9\u4ed6\u4eec\u7684\u8bdd\u9898\u5f88\u611f\u5174\u8da3\uff0c\u7559\u4e86\u4e0b\u6765\u3002\u6a61\u6811\u56ed\u521b\u6295\u662f\u9526\u6e56\u65d7\u4e0b\u9664\u4e1c\u5927\u521b\u57df\u5916\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u6d89\u8db3\u6700\u6df1\u7684\u90e8\u95e8\uff0c\u8d75\u6893\u7433\u5f53\u7136\u4e5f\u8981\u7559\u4e0b\u6765\u966a\u7740\u71ac\u591c\uff0c\u5979\u7231\u4eba\u4e0e\u5973\u513f\u5148\u56de\u53bb\u4f11\u606f\u4e86\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u4eba\u5728\u4e5d\u4e94\u5e74\u5e95\u67b6\u8bbe\u540e\u6765\u53d1\u5c55\u6210\u4e92\u8054\u7f51\u4e2d\u6587\u7b2c\u4e00\u95e8\u6237\u5e73\u53f0\u7684\u4e2a\u4eba\u7f51\u7ad9\uff1b\u65bd\u65b0\u98de\u3001\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u7b49\u4eba\u7a0d\u665a\u4e00\u4e9b\u5728\u4e1c\u5927\u6210\u7acb\u866b\u4ff1\u4e50\u90e8\uff0c\u4e3a\u4e1c\u5927\u67b6\u8bbeBBS\u3001\u5f00\u53d1\u6587\u5b57\u6e38\u620f\uff1b\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u4eba\u4e5d\u4e03\u5e74\u5c31\u5c1d\u8bd5\u5728\u4e2a\u4eba\u7f51\u7ad9\u4e0a\u9500\u552e\u56fe\u4e66\u3001\u97f3\u50cf\u8d44\u6599\u2026\u2026\u4ed6\u4eec\u90fd\u53ef\u4ee5\u8bf4\u662f\u56fd\u5185\u6700\u65e9\u4e00\u6279\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u53c2\u4e0e\u8005\uff0c\u4ed6\u4eec\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u6709\u7740\u6df1\u523b\u7684\u7406\u89e3\uff0c\u4ed6\u4eec\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u6709\u7740\u65e0\u6bd4\u7684\u70ed\u5ff1\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u5f20\u606a\u53cd\u800c\u89c9\u5f97\u81ea\u5df1\u662f\u65c1\u89c2\u8005\uff0c\u81f3\u5c11\u5728\u4e92\u8054\u7f51\u9886\u57df\u91cc\uff0c\u9664\u4e86\u63d0\u4f9b\u4e00\u4e9b\u5fc5\u9700\u7684\u652f\u6301\u5916\uff0c\u4ed6\u5c31\u5e94\u8be5\u95ed\u4e0a\u5634\uff0c\u770b\u7740\u4ed6\u4eec\u5728\u4e92\u8054\u7f51\u6d6a\u6f6e\u91cc\u640f\u98ce\u51fb\u6d6a\u2014\u2014\u5f20\u606a\u4e5f\u7684\u786e\u662f\u5076\u5c14\u4f1a\u5fc3\u8840\u6765\u6d8c\u7684\u53c2\u4e0e\u8fdb\u53bb\uff0c\u5927\u591a\u6570\u65f6\u90fd\u662f\u8896\u624b\u65c1\u89c2\u3002</p><p>\u3000\u3000\u6b64\u65f6\uff0c\u817e\u8baf\u3001\u963f\u91cc\u5df4\u5df4\u3001\u767e\u5ea6\u7b49\u65e5\u540e\u5728\u4e92\u8054\u7f51\u9886\u57df\u5c06\u8d6b\u8d6b\u6709\u540d\u7684\u516c\u53f8\u90fd\u76f8\u7ee7\u6210\u7acb\u4e86\u3002</p><p>\u3000\u3000\u4e1c\u5927\u521b\u57df\u76f4\u63a5\u53c2\u4e0e\u4e86\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u5353\u57df\u7f51\u7684\u6295\u8d44\uff0c\u4e0e\u6b64\u65f6\u76848818\u3001\u963f\u91cc\u5df4\u5df4\u7b49\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u662f\u7ade\u4e89\u5bf9\u624b\uff0c\u6b63\u60f3\u65b9\u8bbe\u6cd5\u7684\u5438\u53d6\u5176\u6210\u529f\u7684\u7ecf\u9a8c\u3001\u538b\u5236\u4ed6\u4eec\u7684\u53d1\u5c55\uff0c\u4e0d\u8fc7\u8fd9\u65f6\u5019\u5927\u5bb6\u89c4\u6a21\u90fd\u6bd4\u8f83\u5c0f\uff0c\u5e02\u573a\u5b9a\u4f4d\u4e0a\u4e5f\u6709\u7a0d\u8bb8\u7684\u4e0d\u540c\uff0c\u65e0\u8bba\u662f\u5728\u4e1a\u52a1\u4e0a\uff0c\u8fd8\u662f\u5e02\u573a\u4e0a\uff0c\u90fd\u6ca1\u6709\u4ec0\u4e48\u592a\u591a\u91cd\u53e0\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u767e\u5ea6\u7684\u6210\u7acb\u6bd4\u66fe\u7ecf\u53d1\u751f\u7684\u5386\u53f2\u7565\u65e9\u4e00\u4e9b\uff0c\u4eca\u5e74\u4e8c\u6708\u4efd\u5c31\u5728\u56fd\u5185\u6b63\u5f0f\u63a8\u51fa\u6700\u65b0\u7684\u4e2d\u6587\u641c\u7d22\u5f15\u64ce\u6280\u672f\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u867d\u7136\u5728\u4e5d\u516b\u5e74\u5c31\u63a8\u51fa\u81ea\u5df1\u7684\u4e2d\u6587\u641c\u7d22\u5f15\u64ce\uff0c\u4f46\u662f\u5728\u6280\u672f\u4e0a\u5e76\u6ca1\u6709\u8fc7\u4eba\u4e4b\u5904\uff0c\u5728\u56db\u6708\u4efd\u65f6\uff0c\u4e0e\u767e\u5ea6\u516c\u53f8\u8fbe\u6210\u534f\u8bae\uff0c\u5728\u7f51\u7ad9\u4e2d\u5d4c\u5165\u767e\u5ea6\u641c\u7d22\u5f15\u64ce\uff0c\u7531\u767e\u5ea6\u516c\u53f8\u63d0\u4f9b\u641c\u7d22\u670d\u52a1\u3002\u4f5c\u4e3a\u5408\u4f5c\u7684\u9644\u52a0\u6761\u4ef6\uff0c\u6606\u817e\u5728\u7ebf\u4e0e\u4e1c\u5927\u521b\u57df\u4ee5\u53ca\u6a61\u6811\u56ed\u521b\u6295\u4e09\u5bb6\u5408\u8d44\u6210\u7acb\u7684\u4e92\u8054\u7f51\u6280\u672f\u6295\u8d44\u57fa\u91d1\u5411\u767e\u5ea6\u516c\u53f8\u6ce8\u8d44\u4e00\u767e\u4e07\u7f8e\u5143\u589e\u6301\u4e00\u767e\u4e94\u5341\u4e07\u80a1\u7684\u666e\u901a\u80a1\uff0c\u6301\u80a1\u6bd4\u4f8b\u4e3a8\uff05\u2014\u2014\u51e0\u5e74\u540e\u6216\u8bb8\u4f1a\u6210\u4e3a\u6a61\u6811\u56ed\u521b\u6295\u7684\u53e6\u4e00\u4e2a\u6295\u8d44\u7ecf\u5178\uff0c\u4f46\u662f\u4e8b\u60c5\u4f1a\u4e0d\u4f1a\u504f\u79bb\u5386\u53f2\u800c\u53d1\u5c55\uff0c\u8c01\u4e5f\u8bf4\u4e0d\u597d\uff0c\u6b64\u65f6\u7684\u767e\u5ea6\u4e5f\u770b\u4e0d\u51fa\u6709\u6210\u4e3a\u4e2d\u6587\u641c\u7d22\u5f15\u64ce\u738b\u8005\u7684\u6c14\u8c61\uff0c\u5728\u4e92\u8054\u7f51\u9886\u57df\u60f3\u8981\u6210\u529f\uff0c\u5076\u7136\u6027\u7684\u56e0\u7d20\u592a\u591a\u4e86\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\uff0c\u56fd\u5185\u5927\u91cf\u4e92\u8054\u7f51\u4f01\u4e1a\u6a21\u4effICQ\u5f00\u53d1\u4e2d\u6587\u7f51\u7edc\u5373\u65f6\u901a\u8baf\u8f6f\u4ef6\uff0c\u817e\u8baf\u53ea\u662f\u5176\u4e2d\u4e0d\u5927\u663e\u773c\u7684\u4e00\u5458\u3002</p><p>\u3000\u3000\u817e\u8baf\u53bb\u5e74\u4e94\u6708\u751a\u81f3\u8981\u5c06QICQ\u51fa\u552e\u7ed9\u4e1c\u5927\u521b\u57df\uff0c\u53ea\u662f\u6606\u817e\u5728\u7ebf\u7684\u6280\u672f\u90e8\u95e8\u90a3\u5df2\u7ecf\u5728\u5f00\u53d1\u81ea\u5df1\u7684\u4e2d\u6587\u7f51\u7edc\u5373\u65f6\u901a\u8baf\u8f6f\u4ef6MICQ\uff0c\u800c\u4e1c\u5927\u521b\u57df\u7684\u6280\u672f\u529b\u91cf\u4e5f\u8db3\u591f\u5f3a\uff0c\u817e\u8baf\u56db\u5341\u4e07\u7f8e\u5143\u7684\u62a5\u4ef7\uff0c\u663e\u7136\u8d85\u8fc7\u675c\u98de\u4ed6\u4eec\u7684\u627f\u53d7\u5e95\u9650\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5373\u4f7f\u5c06QICQ\u8f6f\u4ef6\u5356\u65ad\uff0c\u5c06\u817e\u8baf\u6390\u6b7b\u5728\u6447\u7bee\u91cc\uff0c\u4e5f\u8fdc\u8fdc\u4e0d\u80fd\u4fdd\u8bc1\u6606\u817e\u5728\u7ebf\u672a\u6765\u80fd\u6210\u4e3a\u4e2d\u6587\u5373\u65f6\u901a\u8baf\u5e02\u573a\u7684\u738b\u8005\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u5c31\u8ba9\u5fae\u8f6f\u7684\u4e2d\u6587MSN\u4ea7\u54c1\u5728\u4e2d\u56fd\u5e02\u573a\u5c11\u4e86\u4e00\u4e2a\u5f3a\u52b2\u7684\u7ade\u4e89\u5bf9\u624b\u800c\u79f0\u738b\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u5373\u4f7f\u540e\u6765\u817e\u8baf\u516c\u53f8\u4e0b\u5b9a\u51b3\u5fc3\u81ea\u5df1\u5728\u7ebf\u8fd0\u8425QICQ\u4ea7\u54c1\u56db\u5904\u7b79\u94b1\u65f6\uff0c\u6606\u817e\u5728\u7ebf\u65d7\u4e0b\u62e5\u6709\u4e2d\u6587\u5373\u65f6\u901a\u8baf\u4ea7\u54c1\uff0c\u65e0\u8bba\u662f\u4e1c\u5927\u521b\u57df\u8fd8\u662f\u6a61\u6811\u56ed\u521b\u6295\u90fd\u6ca1\u6709\u7406\u7531\u5411\u817e\u8baf\u63d0\u4f9b\u878d\u8d44\u652f\u6301\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u817e\u8baf\u6700\u6101\u94b1\u65f6\u66fe\u5411\u6d77\u7c9f\u79d1\u6280\u4f38\u51fa\u6c42\u63f4\u4e4b\u624b\uff0c\u5f53\u65f6\u817e\u8baf\u5df2\u7ecf\u53d1\u5c55\u4e86\u8fd1\u767e\u4e07\u7684QICQ\u6ce8\u518c\u7528\u6237\uff0c\u53c8\u6b63\u503c\u4e92\u8054\u7f51\u6d6a\u6f6e\u7ffb\u817e\u5f97\u6b63\u6c79\u6d8c\uff0c\u817e\u8baf\u7684\u5f00\u4ef7\u4e0d\u83f2\uff0c\u53c8\u575a\u51b3\u4e0d\u80af\u653e\u5f03\u5bf9\u516c\u53f8\u7684\u63a7\u5236\u6743\u2014\u2014\u7ed3\u679c\u81ea\u7136\u4e5f\u662f\u53ef\u60f3\u800c\u77e5\uff0c\u6d77\u7c9f\u79d1\u6280\u6709\u53ef\u80fd\u653e\u5f03\u4e86\u767b\u4e0a\u4e00\u6761\u575a\u5b9e\u5927\u8239\u7684\u552f\u4e00\u673a\u4f1a\u3002</p><p>\u3000\u3000\u73b0\u5728\u7684\u4e2d\u6587\u7f51\u7edc\u5373\u65f6\u901a\u8baf\u5e02\u573a\uff0c\u817e\u8baf\u7684QICQ\u3001\u5fae\u8f6f\u7684\u4e2d\u6587MSN\u5360\u636e\u8d85\u8fc760\uff05\u7684\u5e02\u573a\u4efd\u989d\uff0c\u6606\u817e\u5728\u7ebf\u7684\u5373\u65f6\u901a\u8baf\u4ea7\u54c1MICQ\u4f9d\u9760\u95e8\u6237\u7f51\u7ad9\u7684\u7528\u6237\u4f18\u52bf\uff0c\u5e02\u573a\u4efd\u989d\u6392\u540d\u5904\u4e8e\u7b2c\u56db\u7684\u4f4d\u7f6e\u3002</p><p>\u3000\u3000\u5728\u4e5d\u5341\u5e74\u4e2d\u540e\u671f\uff0c\u6e38\u620f\u4ea7\u4e1a\u90fd\u7ed9\u5355\u673a\u6e38\u620f\u6240\u7edf\u6cbb\u3002\u4e2d\u56fd\u4eba\u5bf9\u7f51\u7edc\u6e38\u620f\u7684\u552f\u4e00\u5c1d\u8bd5\u5927\u6982\u4e5f\u5c31\u662f\u4ece\u4e5d\u516d\u3001\u4e5d\u4e03\u5e74\u5f00\u59cb\u76db\u884c\u7684\u6587\u5b57MUD\u4e86\uff0c\u56fe\u5f62\u7f51\u7edc\u6e38\u620f\u5728\u4e16\u754c\u8303\u56f4\u5185\u4e5f\u4ec5\u6709\u4e00\u6b3e\u300a\u7f51\u7edc\u521b\u4e16\u7eaa\u300b\u5728\u8270\u96be\u7684\u8fd0\u8425\u3002\u5728\u8fd9\u671f\u95f4\uff0c\u6587\u5b57MUD\u300a\u4e07\u738b\u4e4b\u738b\u300b\u4e5f\u5b8c\u6210\u4ece\u6587\u5b57\u5230\u56fe\u5f62\u7684\u8f6c\u5316\uff0c\u4e5d\u4e5d\u5e74\u79cb\u5b63\u5728\u5927\u9646\u5730\u533a\u8fd0\u8425\uff0c\u6b63\u5f0f\u5f00\u542f\u4e86\u666e\u901a\u7f51\u6c11\u6240\u516c\u8ba4\u7684\u7f51\u6e38\u65f6\u4ee3\u3002</p><p>\u3000\u3000\u5f53\u521d\u65bd\u65b0\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u6210\u7acb\u866b\u4ff1\u4e50\u90e8\u5c31\u662f\u4e3a\u4e86\u67b6\u8bbe\u4e1c\u5927BBS\u4ee5\u53ca\u5f00\u53d1\u6587\u5b57MUD\uff0c\u4ed6\u4eec\u65e9\u5728\u4e5d\u4e03\u5e74\u65f6\u5c31\u63d0\u51fa\u5c06\u6587\u5b57MUD\u56fe\u5f62\u5316\u7684\u6784\u60f3\uff0c\u8fd8\u5177\u4f53\u62df\u51fa\u300a\u4e09\u56fd\u70c8\u8c6a\u4f20\u300b\u7684\u6e38\u620f\u5f00\u53d1\u8349\u6848\u3002\u540e\u6765\uff0c\u866b\u4ff1\u4e50\u90e8\u7ed9\u675c\u98de\u3001\u8499\u4e50\u6536\u7f16\u5230\u4e1c\u5927\u521b\u57df\u65d7\u4e0b\uff0c\u7531\u4e8e\u5f53\u65f6\u8fd8\u4e0d\u662f\u5f00\u53d1\u56fe\u5f62\u6e38\u620f\u7684\u65f6\u673a\uff0c\u8ba1\u5212\u7ed9\u6401\u7f6e\u8d77\u6765\u3002</p><p>\u3000\u3000\u8fde\u9501\u7f51\u7edc\u4e1a\u52a1\u7ed9\u6d77\u7c9f\u79d1\u6280\u6536\u8d2d\u4e4b\u540e\uff0c\u4e1c\u5927\u521b\u57df\u7684\u6280\u672f\u529b\u91cf\u76f8\u6bd4\u4ee5\u524d\u975e\u4f46\u6ca1\u6709\u524a\u5f31\uff0c\u8fd8\u8fdb\u4e00\u6b65\u5f97\u5230\u52a0\u5f3a\uff1b\u867d\u7136\u56fd\u5185\u7684\u5bb6\u5ead\u5bbd\u5e26\u7f51\u7edc\u8fd8\u521a\u521a\u5f97\u5230\u53d1\u5c55\uff0c\u4f46\u662f\u7531\u4e8e\u57ce\u5e02\u7f51\u5427\u7684\u666e\u53ca\u4ee5\u53ca\u4e92\u8054\u7f51\u7528\u6237\u7684\u6301\u7eed\u6fc0\u589e\uff0c\u675c\u98de\u7b49\u4eba\u5f88\u81ea\u7136\u5c31\u80fd\u770b\u5230\u56fe\u5f62\u7f51\u7edc\u6e38\u620f\u53d1\u5c55\u7684\u5e02\u573a\u524d\u666f\uff0c\u518d\u52a0\u4e0a\u4e1c\u5927\u521b\u57df\u624b\u4e2d\u7684\u8d44\u91d1\u5145\u88d5\uff0c\u624d\u6b63\u5f0f\u542f\u52a8\u300a\u4e09\u56fd\u70c8\u8c6a\u4f20\u300b\u56fe\u5f62\u6e38\u620f\u7684\u5f00\u53d1\u8ba1\u5212\u2014\u2014\u4e0d\u8fc7\u5728\u6b64\u4e4b\u524d\uff0c\u65bd\u65b0\u98de\u7b49\u866b\u5b50\u5929\u7a7a\u5de5\u4f5c\u5ba4\u6210\u5458\u5df2\u7ecf\u505a\u4e86\u5927\u91cf\u7684\u524d\u671f\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\u65f6\uff0c\u56fd\u5185\u6e38\u620f\u6280\u672f\u4ece\u6587\u5b57\u5230\u56fe\u5f62\u5b58\u5728\u7740\u51e0\u4e4e\u65e0\u6cd5\u8de8\u8d8a\u7684\u6280\u672f\u9e3f\u6c9f\uff0c\u56fd\u5185\u6ca1\u6709\u4eba\u5bf9\u56fe\u5f62\u5316\u7684\u7f51\u7edc\u6e38\u620f\u6709\u5f00\u53d1\u7ecf\u9a8c\uff0c\u800c\u65bd\u65b0\u98de\u4ed6\u4eec\u7684\u91ce\u5fc3\u53c8\u9887\u5927\uff0c\u60f3\u5f00\u53d1\u6574\u4e2a\u53ef\u7f16\u8f91\u7684\u6e38\u620f\u7cfb\u7edf\uff0c\u5305\u62ec\u4e00\u4e9b\u4e92\u4ea4\u5f0f\u5b9e\u65f6\u56fe\u50cf\u5e94\u7528\u7a0b\u5e8f\u7684\u6838\u5fc3\u7ec4\u4ef6\uff0c\u96be\u5ea6\u6781\u5927\u3002\u5728\u5f00\u53d1\u9047\u5230\u963b\u529b\u540e\uff0c\u4e1c\u5927\u521b\u57df\u4e0d\u5f97\u4e0d\u51fa\u5927\u4ef7\u94b1\u4ece\u6b27\u6d32\u6e38\u620f\u5f00\u53d1\u5546\u4ee5\u53ca\u56fe\u5f62\u56fe\u50cf\u5e94\u7528\u8f6f\u4ef6\u5546\u90a3\u91cc\u5f15\u8fdb\u4e00\u4e9b\u6280\u672f\uff0c\u8d44\u91d1\u6295\u5165\u4e0d\u5c11\uff0c\u5f00\u53d1\u8fdb\u5ea6\u8fd8\u53ea\u5b8c\u6210\u4e0d\u5230\u4e8c\u5206\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u82e5\u662f\u4ee5\u7eaf\u5546\u4e1a\u7684\u89c6\u89d2\u6765\u770b\uff0c\u5f53\u524d\u5728\u56fd\u5185\u5f00\u53d1\u56fe\u5f62\u7f51\u7edc\u6e38\u620f\u7684\u5546\u4e1a\u98ce\u9669\u592a\u5927\uff0c\u5931\u8d25\u7684\u53ef\u80fd\u6027\u76f8\u5f53\u9ad8\uff0c\u4ee3\u7406\u56fd\u5916\u8fd0\u8425\u8f83\u4e3a\u6210\u719f\u7684\u7f51\u7edc\u6e38\u620f\u66f4\u7b26\u5408\u5546\u4e1a\u4ef7\u503c\u4e0a\u7684\u53d6\u820d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4f1a\u76f4\u63a5\u5e72\u6d89\u4e1c\u5927\u521b\u57df\u7684\u8fd0\u8425\u65b9\u9488\uff0c\u4ed6\u751a\u81f3\u8ba4\u540c\u4e1c\u5927\u521b\u57df\u81ea\u884c\u4ece\u65e0\u5230\u6709\u5f00\u53d1\u56fe\u5f62\u6e38\u620f\u7684\u52aa\u529b\u3002\u4e00\u65b9\u9762\uff0c\u5f53\u521d\u5c06\u866b\u4ff1\u4e50\u90e8\u6536\u7f16\u5230\u521b\u57df\u65d7\u4e0b\uff0c\u9ed8\u8ba4\u8fc7\u65e5\u540e\u8981\u51fa\u8d44\u542f\u52a8\u300a\u4e09\u56fd\u70c8\u8c6a\u4f20\u300b\u56fe\u5f62\u6e38\u620f\u7684\u5f00\u53d1\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u7f51\u6e38\u65f6\u4ee3\u7684\u5230\u6765\u5df2\u7ecf\u65e0\u6cd5\u9006\u8f6c\uff0c\u4e1c\u5927\u521b\u57df\u7740\u624b\u505a\u6e38\u620f\u5f15\u64ce\u65b9\u9762\u7684\u57fa\u7840\u6280\u672f\u5f00\u53d1\u5de5\u4f5c\uff1b\u4e5f\u80fd\u8ba9\u4e2d\u534e\u6587\u5316\u5e95\u8574\u66f4\u65e9\u7684\u5728\u7f51\u6e38\u4f53\u73b0\u51fa\u6765\uff0c\u8ba9\u90a3\u4e9b\u516b\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\u4ee5\u540e\u51fa\u751f\u7684\u5c0f\u5b69\u4e5f\u77e5\u9053\u4ec0\u4e48\u53eb\u4e2d\u534e\u6587\u5316\u7684\u535a\u5927\u7cbe\u6df1\uff1b\u518d\u8bf4\u4e86\uff0c\u9526\u6e56\u5728\u5458\u5de5\u57f9\u8bad\u4e0a\u7684\u82b1\u8d39\u76f8\u5f53\u7684\u6177\u6168\uff0c\u5f00\u53d1\u5931\u8d25\u5c31\u7b97\u662f\u638f\u51fa\u51e0\u5343\u4e07\u57f9\u517b\u4e86\u4e00\u6279\u5728\u56fe\u5f62\u56fe\u50cf\u5e94\u7528\u7a0b\u5e8f\u5f00\u53d1\u9886\u57df\u7684\u4e13\u4e1a\u4eba\u624d\uff0c\u8fd9\u6070\u6070\u4e5f\u662f\u9526\u6e56\u4ee5\u540e\u505a\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u5e94\u7528\u7684\u65b9\u5411\u4e4b\u4e00\u2014\u2014\u8fd9\u4e48\u60f3\uff0c\u5fc3\u7406\u5c31\u4f1a\u5e73\u8861\u591a\u4e86\u3002</p><p>\u3000\u3000\u5728\u5f00\u53d1\u56fe\u5f62\u6e38\u620f\u300a\u4e09\u56fd\u70c8\u8c6a\u4f20\u300b\u7684\u8fc7\u7a0b\u5f53\u4e2d\uff0c\u4e1c\u5927\u521b\u57df\u5374\u662f\u5c06\u4f11\u95f2\u6e38\u620f\u5e73\u53f0\u5148\u6210\u529f\u8fd0\u8425\u8d77\u6765\u4e86\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\u521d\u5f00\u53d1\u540d\u4e3a\u521b\u57df\u6e38\u620f\u7684\u8fd9\u4e2a\u4f11\u95f2\u6e38\u620f\u5e73\u53f0\u65f6\uff0c\u8fd8\u662f\u4e3a\u4e86\u7ed9\u65d7\u4e0b\u7684\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u63d0\u4f9b\u66f4\u591a\u7684\u589e\u503c\u670d\u52a1\u3002\u7531\u6700\u521d\u7684\u4e94\u6b3e\u68cb\u724c\u6e38\u620f\u9010\u6e10\u589e\u52a0\u6570\u5341\u6b3e\u8986\u76d6\u4e2d\u56fd\u6587\u5316\u5e95\u8574\u4e0e\u5730\u65b9\u7279\u8272\u7684\u68cb\u724c\u7c7b\u6e38\u620f\uff0c\u53c8\u5c06\u7ade\u6280\u5bf9\u6218\u6e38\u620f\u6574\u5408\u5230\u5e73\u53f0\u91cc\uff0c\u5f62\u6210\u7efc\u5408\u6027\u8d28\u7684\u4f11\u95f2\u6e38\u620f\u5e73\u53f0\u3002</p><p>\u3000\u3000\u6700\u521d\u67b6\u8bbe\u7684\u670d\u52a1\u5668\u5f88\u6709\u9650\uff0c\u53ea\u5728\u521b\u57df\u65d7\u4e0b\u7684\u7f51\u5427\u91cc\u63a8\u5e7f\u5e94\u7528\uff0c\u540e\u6765\u5728\u6e38\u620f\u5e73\u53f0\u91cc\u5d4c\u5165\u5e7f\u544a\uff0c\u624d\u5bf9\u5916\u5f00\u653e\u3002\u53ea\u4e0d\u8fc7\u4ec5\u9760\u5e7f\u544a\u690d\u5165\uff0c\u6e38\u620f\u5e73\u53f0\u8fd8\u8fdc\u8fdc\u65e0\u6cd5\u5b9e\u73b0\u76c8\u5229\uff0c\u5f53\u65f6\u5e76\u6ca1\u6709\u8fdb\u884c\u5927\u89c4\u6a21\u7684\u63a8\u5e7f\u3002\u5c31\u7b97\u5982\u6b64\uff0c\u4f9d\u9760\u521b\u57df\u65d7\u4e0b\u7684\u7f51\u5427\u63a8\u5e7f\u4ee5\u53ca\u4e92\u8054\u7f51\u4e0a\u7684\u53e3\u7891\u76f8\u4f20\uff0c\u53bb\u5e74\u5c06\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u51fa\u552e\u7ed9\u6d77\u7c9f\u79d1\u6280\u65f6\uff0c\u521b\u57df\u6e38\u620f\u7684\u6ce8\u518c\u7528\u6237\u4e5f\u8fbe\u5230\u4e09\u5341\u4e07\u4e4b\u591a\u3002</p><p>\u3000\u3000\u51fa\u552e\u7f51\u5427\u4e1a\u52a1\u65f6\uff0c\u6e38\u620f\u5e73\u53f0\u7ed9\u7559\u4e86\u4e0b\u6765\u3002\u5f53\u65f6\u4e1c\u5927\u521b\u57df\u7684\u6838\u5fc3\u4e1a\u52a1\u4e5f\u5c11\uff0c\u8d44\u91d1\u4e5f\u8f83\u5bbd\u88d5\uff0c\u5c31\u6b63\u5f0f\u6ce8\u518c\u4e86\u516c\u53f8\uff0c\u5c06\u6e38\u620f\u5e73\u53f0\u7f6e\u5165\u5176\u4e2d\uff0c\u6295\u5165\u4e24\u5343\u4e07\u7684\u8d44\u91d1\uff0c\u6b63\u5f0f\u8fd0\u8425\u8d77\u6765\u3002\u6b64\u65f6\u5df2\u7ecf\u53d1\u5c55\u5230\u8d85\u8fc7\u4e24\u767e\u4e07\u7684\u6ce8\u518c\u7528\u6237\uff0c\u6ce8\u518c\u7528\u6237\u6570\u91cf\u4e5f\u4ec5\u6b21\u4e8e\u534e\u7535\u63a7\u80a1\u65d7\u4e0b\u7684\u6e38\u620f\u5e73\u53f0\u8054\u950b\u6e38\u620f\u4e16\u754c\u3002</p><p>\u3000\u3000\u9664\u4e86\u7535\u5b50\u5546\u52a1\u3001\u6e38\u620f\u5e73\u53f0\u4e4b\u5916\uff0c\u4e1c\u5927\u521b\u57df\u5728\u4e92\u8054\u7f51\u9886\u57df\u53e6\u4e00\u9879\u5927\u7684\u6295\u8d44\u5c31\u662f\u5927\u578b\u793e\u533a\u7f51\u7ad9\u4e1c\u793e\u6c5f\u6e56\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u73b0\u5728\u6709\u4e86\u7a33\u5b9a\u7684\u5e7f\u544a\u6536\u5165\uff0c\u4f46\u662f\u4e5f\u53ea\u80fd\u52c9\u5f3a\u7ef4\u6301\u95e8\u6237\u7f51\u7ad9\u7684\u6b63\u5e38\u8fd0\u8425\uff0c\u65d7\u4e0b\u7684\u7535\u90ae\u3001MICQ\uff0c\u4ee5\u53ca\u5353\u57df\u7684\u7f51\u4e0a\u5546\u57ce\u3001\u4e1c\u5927\u521b\u57df\u7684\u6e38\u620f\u5e73\u53f0\u3001\u793e\u533a\u7f51\u7ad9\u7b49\u7b49\u4ea7\u54c1\u53ca\u4e1a\u52a1\u90fd\u5904\u4e8e\u4e25\u91cd\u5165\u4e0d\u6577\u51fa\u7684\u9636\u6bb5\u3002</p><p>\u3000\u3000\u57284\u670826\u65e5\uff0c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u96ea\u5d29\u4e4b\u524d\uff0c\u4e1a\u5185\u90fd\u5c06\u89c6\u7ebf\u76ef\u5728\u7eb3\u65af\u8fbe\u514b\u7b49\u6d77\u5916\u8bc1\u5238\u5e02\u573a\u4e0a\u5e02\u4e0a\uff0c\u5e76\u6ca1\u6709\u8ba4\u771f\u8003\u8651\u8fc7\u53d1\u5c55\u6a21\u5f0f\u4e0e\u76c8\u5229\u80fd\u529b\u8fd9\u4e9b\u95ee\u9898\uff0c\u7f51\u7ad9\u53d1\u5c55\u53ea\u8ffd\u6c42\u70b9\u51fb\u7387\u3001\u6ce8\u518c\u7528\u6237\u6570\u91cf\u3002\u4e1c\u5927\u521b\u57df\u3001\u6606\u817e\u5728\u7ebf\u4ee5\u53ca\u5353\u57df\u5728\u5f20\u606a\u7684\u5f71\u54cd\u4e0b\uff0c\u5df2\u7ecf\u76f8\u5f53\u52a1\u5b9e\u4e86\uff0c\u4f46\u662f\u4ecd\u7136\u514d\u4e0d\u4e86\u88ab\u5927\u73af\u5883\u6240\u5f71\u54cd\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u524d\u540e\u83b7\u5f97\u591a\u6b21\u98ce\u9669\u6295\u8d44\u7684\u6ce8\u8d44\uff0c\u53c8\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u878d\u8d44\u6210\u529f\uff0c\u8d44\u91d1\u6700\u96c4\u539a\uff0c\u8d44\u91d1\u4f59\u989d\u4e00\u5ea6\u8d85\u8fc7\u4e00\u4ebf\u7f8e\u5143\uff1b\u5373\u65f6\u4ee5\u76ee\u524d\u7684\u6269\u5f20\u6001\u52bf\uff0c\u4ecd\u7136\u80fd\u652f\u6491\u5230\u4e09\u5e74\u4e4b\u540e\u3002\u4e1c\u5927\u521b\u57df\u7a0d\u597d\u4e00\u4e9b\uff0c\u4e0d\u8fc7\u4e5f\u67b6\u4e0d\u4f4f\u6e38\u620f\u5f00\u53d1\u3001\u6e38\u620f\u5e73\u53f0\u3001\u793e\u533a\u7f51\u7ad9\u4e09\u5927\u65e0\u5e95\u6d1e\u5438\u91d1\u3002\u5353\u8d8a\u7684\u60c5\u51b5\u6700\u7cdf\u7cd5\uff0c\u7ef4\u6301\u773c\u4e0b\u7684\u53d1\u5c55\u6001\u5ea6\uff0c\u8d44\u91d1\u53ea\u80fd\u652f\u6491\u5230\u4eca\u5e74\u5e74\u5e95\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u3001\u738b\u5f69\u73b2\u5230\u5efa\u90ba\u6765\uff0c\u675c\u98de\u53c8\u5c06\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u3001\u65f6\u5b66\u658c\u7b49\u4eba\u53ec\u96c6\u8d77\u6765\uff0c\u5c31\u662f\u60f3\u7740\u80fd\u4e0d\u80fd\u627e\u5230\u4e00\u6761\u6377\u5f84\u626d\u8f6c\u5f53\u524d\u53ea\u6295\u5165\u6ca1\u4ea7\u51fa\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u867d\u7136\u4e5f\u62c5\u5fc3\u4ed6\u4eec\u4f1a\u77eb\u6789\u8fc7\u6b63\uff0c\u53ea\u662f\u770b\u8c22\u5b50\u5609\u5982\u6b64\u70ed\u5fc3\u7684\u53c2\u4e0e\u8fdb\u6765\uff0c\u4e5f\u5c31\u51b3\u5b9a\u5148\u8896\u624b\u65c1\u89c2\uff0c\u8ba9\u4ed6\u4eec\u5148\u5c06\u4e92\u8054\u7f51\u8d44\u6e90\u6574\u5408\u7684\u8349\u6848\u8ba8\u8bba\u51fa\u6765\uff0c\u4ed6\u518d\u89c6\u60c5\u51b5\u8981\u4e0d\u8981\u4ece\u65c1\u63a8\u52a8\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u71ac\u5230\u51cc\u6668\u4e24\u70b9\uff0c\u89c1\u8c22\u5b50\u5609\u3001\u675c\u98de\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u8c08\u5174\u6b63\u6d53\uff0c\u6ca1\u6709\u4e1d\u6beb\u56f0\u987f\u8981\u4f11\u606f\u7684\u610f\u601d\uff0c\u5f20\u606a\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u4f38\u624b\u667e\u51fa\u8155\u8868\u7ed9\u4ed6\u4eec\uff1a\u201c\u90fd\u4e24\u70b9\u949f\u4e86\uff0c\u6211\u660e\u65e9\u8fd8\u6709\u4f1a\u8bae\u8981\u53c2\u52a0\uff0c\u6211\u5148\u56de\u53bb\u7761\u89c9\u4e86\uff0c\u5c31\u4e0d\u966a\u4f60\u4eec\u5728\u8fd9\u91cc\u5e72\u8017\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4ece\u71d5\u56ed\u5357\u95e8\u51fa\u53bb\uff0c\u8d70\u4e24\u6b65\u8def\u5c31\u5230\u6e56\u7554\u6728\u5c4b\uff0c\u5012\u8fd8\u6709\u51e0\u5bf9\u9752\u5e74\u7537\u5973\u604b\u4eba\u5728\u6ee8\u6e56\u7eff\u5730\u7684\u957f\u6905\u4e0a\u76f8\u62e5\u800c\u5750\uff0c\u4e0d\u80af\u56de\u53bb\u4f11\u606f\u3002\u5f20\u606a\u5728\u4e00\u5f20\u7a7a\u6905\u4e0a\u5750\u4e0b\u6765\u62bd\u4e86\u4e00\u652f\u70df\uff0c\u9648\u9759\u624d\u9b3c\u9b3c\u795f\u795f\u7684\u63a2\u5934\u5f80\u8fd9\u91cc\u770b\u3002</p><p>\u3000\u3000\u201c\u53c8\u4e0d\u662f\u505a\u8d3c\u2026\u2026\u201d\u5f20\u606a\u7ad9\u8d77\u6765\u62db\u624b\u8ba9\u5979\u8fc7\u6765\uff0c\u63fd\u7740\u5979\u7684\u80a9\u5f80\u6e56\u7554\u6728\u5c4b\u65b9\u5411\u3002</p><p>\u3000\u3000\u591c\u8fd9\u4e48\u6df1\u4e86\uff0c\u9648\u9759\u8fd8\u662f\u62c5\u5fc3\u7ed9\u719f\u4eba\u78b0\u5230\uff0c\u7ed9\u5f20\u606a\u63fd\u7740\u80a9\uff0c\u751c\u871c\u4e4b\u4f59\u8fd8\u662f\u6709\u62c5\u5fc3\uff0c\u8d70\u5230\u6e56\u7554\u6728\u5c4b\u9662\u95e8\u5916\uff0c\u624d\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u5b50\u5609\u5bf9\u4e92\u8054\u7f51\u7684\u5174\u81f4\u5f88\u9ad8\uff0c\u5f53\u521d\u7b56\u5212\u5b9d\u77f3\u7cfb\u5217\u624b\u673a\u6848\u65f6\uff0c\u4e5f\u672a\u89c1\u5979\u8fd9\u4e48\u6295\u5165\u8fc7\u2026\u2026\u4f60\u4f1a\u540c\u610f\u5979\u53c2\u4e0e\u4e1c\u5927\u521b\u57df\u90a3\u644a\u5b50\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u5979\u53ef\u4e0d\u8981\u8bf4\u670d\u6211\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u675c\u98de\u4ed6\u4eec\u4e5f\u5e72\u5f97\u76f8\u5f53\u4e0d\u9519\uff0c\u6211\u89c9\u5f97\u6211\u5728\u65c1\u8fb9\u6307\u624b\u5212\u811a\u4e0d\u597d\uff0c\u8c22\u5b50\u5609\u80fd\u8bf4\u670d\u4ed6\u4eec\u5c31\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5728\u65c1\u8fb9\u542c\u4e86\u8fd9\u4e48\u4e45\u90fd\u4e00\u58f0\u4e0d\u542d\uff0c\u5c31\u662f\u4e0d\u60f3\u6307\u624b\u5212\u811a\uff1f\u201d\u9648\u9759\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u7684\u89c2\u70b9\u4e0d\u4e00\u5b9a\u5c31\u662f\u6700\u51c6\u786e\u65e0\u8bef\u7684\uff1b\u7ed9\u4ed6\u4eec\u6307\u51fa\u4e00\u6761\u6700\u51c6\u786e\u65e0\u8bef\u7684\u9053\u8def\uff0c\u5e76\u6ca1\u6709\u591a\u5927\u7684\u76ca\u5904\u2014\u2014\u6709\u597d\u7684\u60f3\u6cd5\u662f\u4e00\u56de\u4e8b\uff0c\u5173\u952e\u8fd8\u662f\u6267\u884c\u529b\uff0c\u5373\u4f7f\u4e00\u5f00\u59cb\u6ca1\u6709\u8d70\u5728\u6700\u51c6\u786e\u65e0\u8bef\u7684\u9053\u8def\u4e0a\uff0c\u4e5f\u7b97\u662f\u4e00\u79cd\u78e8\u52b1\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u524d\u5c18\u5f80\u4e16\u91cc\u4ed6\u7eb5\u89c2\u4e86\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u5341\u591a\u5e74\u7684\u574e\u5777\u53d1\u5c55\u5386\u53f2\uff0c\u5728\u77ed\u77ed\u5341\u591a\u5e74\u65f6\u95f4\u91cc\uff0c\u4e92\u8054\u7f51\u4ea7\u4e1a\u5c31\u7ecf\u5386\u4e86\u4e09\u4ee3\u53d8\u8fc1\uff0c\u54ea\u4e9b\u4e92\u8054\u7f51\u6a21\u5f0f\u4f1a\u88ab\u6dd8\u6c70\uff0c\u54ea\u4e9b\u4e92\u8054\u7f51\u6a21\u5f0f\u4f1a\u751f\u5b58\u4e0b\u6765\u7ee7\u7eed\u63a5\u53d7\u5e02\u573a\u7684\u8003\u9a8c\uff0c\u4ed6\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u5e95\uff0c\u4e1c\u5927\u521b\u57df\u4ee5\u53ca\u6606\u817e\u5728\u7ebf\u8fd9\u4e00\u5757\u7684\u53d1\u5c55\u8fd8\u662f\u653e\u624b\u7ed9\u675c\u98de\u3001\u9a6c\u5411\u4e1c\u4ed6\u4eec\u53d1\u6325\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u5728\u6e56\u7554\u6728\u5c4b\u7559\u5bbf\uff0c\u6b22\u7231\u540e\u6c89\u6c89\u5165\u7761\uff0c\u9192\u6765\u65f6\u5df2\u662f\u6b21\u65e5\u4e0a\u5348\u5341\u70b9\uff0c\u5f20\u606a\u65e9\u5c31\u8d77\u5e8a\u53bb\u53c2\u52a0\u4e0a\u5348\u7684\u4f1a\u8bae\uff0c\u9648\u9759\u6d17\u6f31\u8fc7\uff0c\u8fd8\u7279\u5730\u6362\u4e86\u4e00\u8eab\u8fd0\u52a8\u886b\u8fd4\u56de\u71d5\u56ed\u3002\u5373\u4f7f\u7ed9\u5b50\u5609\u649e\u4e0a\uff0c\u8fd8\u53ef\u4ee5\u8bf4\u662f\u4e00\u65e9\u53bb\u8fd0\u52a8\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u56de\u5230\u71d5\u56ed\u65f6\uff0c\u5b50\u5609\u4e00\u8138\u56f0\u987f\u7684\u4e5f\u662f\u521a\u56de\u6765\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u4e1d\u6beb\u6ca1\u6709\u610f\u8bc6\u5230\u9648\u9759\u4e5f\u662f\u4e00\u591c\u672a\u5f52\uff0c\u6320\u7740\u9f3b\u5c16\uff0c\u8bed\u6c14\u91cc\u7a0d\u5e26\u6709\u4e9b\u5fff\u6028\u7684\u8bf4\u9053\uff1a\u201c\u90a3\u4e9b\u5927\u8001\u7237\u4eec\u5c31\u662f\u76ee\u5149\u77ed\u6d45\uff0c\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u53d1\u5c55\u5230\u73b0\u5728\uff0c\u8be5\u6709\u7684\u4e1a\u52a1\u6a21\u5f0f\u5dee\u4e0d\u591a\u90fd\u6709\u4e86\uff0c\u4e5f\u5f62\u6210\u6839\u6df1\u8482\u56fa\u7684\u514d\u8d39\u4f20\u7edf\uff0c\u8c01\u5148\u6539\u53d8\u8fd9\u4e2a\u4f20\u7edf\uff0c\u8c01\u5c31\u4f1a\u5148\u53d7\u5230\u5927\u7684\u51b2\u51fb\u2014\u2014\u7eb3\u6307\u7f51\u7edc\u80a1\u96ea\u5d29\u4f1a\u9020\u6210\u5f88\u4e25\u91cd\u7684\u540e\u679c\uff0c\u4f46\u8fd8\u662f\u6539\u53d8\u4e0d\u4e86\u4e00\u4e2a\u6700\u57fa\u672c\u7684\u4e8b\u5b9e\uff0c\u6d3b\u8dc3\u7528\u6237\u4e0e\u514d\u8d39\u662f\u4e2d\u56fd\u4e92\u8054\u7f51\u4f01\u4e1a\u80fd\u591f\u53d1\u5c55\u7684\u6839\u57fa\uff0c\u9526\u6e56\u8d44\u91d1\u5b9e\u529b\u8fd9\u4e48\u96c4\u539a\uff0c\u5b8c\u5168\u53ef\u4ee5\u591a\u652f\u6491\u4e24\u5e74\u2026\u2026\u201d\u53c8\u7591\u60d1\u7684\u8bf4\u9053\uff0c\u201c\u5f20\u606a\u90a3\u5bb6\u4f19\u6628\u5929\u6ca1\u6709\u53c2\u4e0e\u8ba8\u8bba\uff0c\u662f\u4e0d\u662f\u9526\u6e56\u4e0d\u4f1a\u518d\u5bf9\u4e1c\u5927\u521b\u57df\u3001\u5353\u57df\u63d0\u4f9b\u8d44\u91d1\u652f\u6301\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u9648\u9759\u63a9\u9970\u9053\uff0c\u201c\u4e0d\u7ba1\u4ec0\u4e48\u8bf4\uff0c\u4f01\u4e1a\u603b\u662f\u8981\u4ee5\u8d62\u5229\u4e3a\u76ee\u7684\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u7b97\u8981\u8d62\u5229\uff0c\u4e5f\u4e0d\u5e94\u8be5\u6025\u4e8e\u63a8\u884c\u666e\u904d\u7684\u4f1a\u5458\u6536\u8d39\u5236\u5ea6\uff0c\u201d\u8c22\u5b50\u5609\u8bf4\u8bdd\u6fc0\u52a8\u65f6\uff0c\u5c0f\u52a8\u4f5c\u9887\u591a\uff0c\u5934\u53d1\u7ed9\u5979\u81ea\u5df1\u6320\u5f97\u84ec\u677e\u8d77\u6765\uff0c\u201c\u666e\u904d\u7684\u514d\u8d39\u4f1a\u5458\u5236\u5ea6\u4e0b\uff0c\u4ee5\u6536\u8d39\u7684\u589e\u503c\u670d\u52a1\u53d1\u5c55\u9ad8\u7aef\u7528\u6237\uff0c\u4f46\u662f\u7edd\u4e0d\u610f\u5473\u7740\u53ef\u4ee5\u9650\u5236\u666e\u901a\u4f1a\u5458\u5728\u7f51\u7ad9\u4e0a\u7684\u6743\u76ca\u2014\u2014\u5e94\u8be5\u5728\u73b0\u6709\u7684\u4ea7\u54c1\u57fa\u7840\u4e0a\u5f00\u53d1\u65b0\u7684\u9ad8\u4f53\u9a8c\u5ea6\u7684\u589e\u503c\u4ea7\u54c1\uff0c\u6606\u817e\u5728\u7ebf\u3001\u5353\u57df\u3001\u4e1c\u5927\u521b\u57df\u4e4b\u95f4\u7684\u4e92\u8054\u7f51\u8d44\u6e90\u6574\u5408\uff0c\u4e5f\u5e94\u8be5\u4ee5\u8fd9\u4e2a\u4e3a\u524d\u63d0\u3002\u5728\u5f53\u524d\u56fd\u5185\u4e92\u8054\u7f51\u5e94\u7528\u73af\u5883\u4e2d\uff0c\u6606\u817e\u5728\u7ebf\u7684MICQ\u5c31\u5e94\u8be5\u4ea4\u7ed9\u4e1c\u5927\u521b\u57df\u4e0e\u6e38\u620f\u5e73\u53f0\u4e0e\u793e\u533a\u7f51\u7ad9\u3001\u97f3\u4e50\u7f51\u7ad9\u8054\u5408\u8d77\u6765\u8fd0\u8425\uff0c\u624d\u4f1a\u6709\u66f4\u597d\u7684\u589e\u503c\u6548\u7528\u2014\u2014\u6216\u8bb8\u76f4\u63a5\u5c06\u6e38\u620f\u5e73\u53f0\u3001\u793e\u533a\u7f51\u7ad9\u3001\u5728\u7ebf\u97f3\u4e50\u3001\u7535\u5b50\u90ae\u7bb1\u7b49\u7b49\u4ea7\u54c1\u6574\u5408\u5230MICQ\u4e2d\u53bb\uff0c\u5f62\u6210\u4e00\u4e2a\u7efc\u5408\u6027\u7684\u5927\u5e73\u53f0\uff0c\u751a\u81f3\u53ef\u4ee5\u5c06\u5353\u57df\u7f51\u7684\u4ea4\u6613\u754c\u9762\u90fd\u6574\u5408\u5230MICQ\u4e2d\u53bb\uff0c\u53e6\u5916\u5353\u57df\u7684\u53d1\u5c55\u6a21\u5f0f\u4f3c\u4e4e\u4e5f\u6bd4\u8f83\u5371\u9669\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u633a\u597d\u7684\u601d\u8def\uff0c\u6211\u4e4b\u524d\u90fd\u6ca1\u6709\u60f3\u5230\u4f60\u5bf9\u8fd9\u4e9b\u7814\u7a76\u6709\u8fd9\u4e48\u6df1\u5165\uff0c\u96be\u9053\u4ed6\u4eec\u90fd\u987d\u56fa\u4e0d\u5316\uff0c\u542c\u4e0d\u8fdb\u4f60\u7684\u8bdd\uff1f\u201d\u9648\u9759\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u90a3\u8fb9\u6f0f\u6d1e\u5c11\uff0c\u7422\u78e8\u8d77\u6765\u6ca1\u6709\u610f\u601d\uff0c\u4e1c\u5927\u521b\u57df\u8fd9\u8fb9\uff0c\u563f\u563f\u563f\u2026\u2026\u201d\u8c22\u5b50\u5609\u5938\u5f20\u7684\u7b11\u4e86\u4e24\u58f0\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u8bf4\u5185\u90e8\u8fd8\u8981\u7ee7\u7eed\u8ba8\u8bba\uff0c\u5c31\u5c06\u6211\u8d76\u4e86\u51fa\u6765\uff0c\u4f60\u8bf4\u6709\u4ed6\u4eec\u8fd9\u6837\u8fc7\u6cb3\u62c6\u6865\u7684\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u6ca1\u6709\u8bf4\u4ec0\u4e48\u523a\u6fc0\u4ed6\u4eec\u7684\u8bdd\uff1f\u201d\u9648\u9759\u5bf9\u5b50\u5609\u7684\u6027\u5b50\u8fd8\u662f\u5f88\u4e86\u89e3\u7684\uff0c\u6628\u5929\u591c\u91cc\u6709\u5979\u8ddf\u5f20\u606a\u5728\u573a\uff0c\u5b50\u5609\u8fd8\u80fd\u6291\u7740\u6027\u5b50\u4e0d\u5f20\u626c\uff0c\u8c01\u77e5\u9053\u5979\u4e0e\u5f20\u606a\u79bb\u5f00\u4e4b\u540e\u5b50\u5609\u6709\u6ca1\u6709\u66b4\u9732\u672c\u6027\u51b7\u5632\u70ed\u8bbd\u51e0\u53e5\uff1f</p><p>\u3000\u3000\u201c\u5e94\u8be5\u6ca1\u6709\u5427\uff1f\u201d\u8c22\u5b50\u5609\u4e5f\u4e0d\u5927\u786e\u5b9a\uff0c\u5f20\u606a\u5728\u573a\uff0c\u5979\u8fd8\u987e\u5fcc\u7740\u8bf4\u51fa\u6ca1\u6c34\u51c6\u7684\u8bdd\u4f1a\u7ed9\u5f20\u606a\u770b\u8f7b\u4e86\uff0c\u9648\u9759\u4e0e\u5f20\u606a\u4e00\u8d70\uff0c\u5979\u5c31\u6ca1\u6709\u4ec0\u4e48\u538b\u529b\uff0c\u6ca1\u6709\u538b\u529b\uff0c\u5979\u5c31\u559c\u6b22\u4e71\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u522b\u7ba1\u90a3\u4e9b\u4e86\uff0c\u4f60\u5148\u6d17\u7740\u6fa1\u8865\u4e00\u89c9\u5427\uff0c\u770b\u4f60\u71ac\u591c\u773c\u888b\u90fd\u51fa\u6765\u4e86\uff0c\u4e0d\u8981\u4ee5\u4e3a\u4ed7\u7740\u5e74\u8f7b\uff0c\u7b49\u518d\u8fc7\u5341\u5e74\uff0c\u6709\u4f60\u54ed\u7684\u65f6\u5019\u3002\u201d\u9648\u9759\u63a8\u7740\u5b50\u5609\u8fdb\u5c4b\uff0c\u8ba9\u5979\u5148\u53bb\u6d74\u5ba4\u6d17\u6fa1\u3002</p><p>\u3000\u3000\u9648\u9759\u56de\u5367\u5ba4\u6362\u4e86\u8eab\u8863\u670d\u5c31\u53bb\u529e\u516c\u5ba4\u4e86\u3002\u9ec4\u660f\u65f6\uff0c\u5f20\u606a\u5c06\u9526\u6e56\u7ecf\u7814\u4e2d\u5fc3\u6700\u65b0\u7814\u7a768818\u7535\u5b50\u5546\u52a1\u4e1a\u52a1\u6a21\u5f0f\u7684\u62a5\u544a\u53d1\u5230\u5979\u7684\u90ae\u7bb1\u91cc\uff0c\u9648\u9759\u5c06\u62a5\u544a\u6253\u5370\u4e0b\u6765\u5c31\u56de\u5230\u71d5\u56ed\u3002\u8fd9\u65f6\u5019\u5b50\u5609\u624d\u521a\u521a\u8d77\u5e8a\uff0c\u7a7f\u7740\u7761\u888d\uff0c\u63c9\u7740\u60fa\u677e\u7684\u7761\u773c\uff0c\u770b\u7740\u9648\u9759\u8fdb\u5c4b\u65f6\u5c06\u6587\u4ef6\u888b\u653e\u978b\u67dc\u4e0a\uff0c\u5979\u987a\u624b\u5c31\u62ff\u8fc7\u6765\u5750\u5230\u8737\u817f\u5750\u5230\u6c99\u53d1\u4e0a\u770b\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u54a6\uff0c\u4f60\u624b\u91cc\u600e\u4e48\u4f1a\u6709\u9526\u6e56\u7ecf\u7814\u4e2d\u5fc3\u6700\u65b0\u7684\u62a5\u544a\uff1f\u201d\u8c22\u5b50\u5609\u7ffb\u770b\u4e86\u51e0\u5929\uff0c\u62a5\u544a\u4e0a\u6ca1\u6709\u6807\u660e\u65e5\u671f\uff0c\u4f46\u662f\u91cc\u9762\u91c7\u7528\u7684\u6570\u636e\u622a\u6b62\u5230\u56db\u6708\u5e95\uff0c\u4e94\u6708\u7684\u5934\u4e03\u5929\u662f\u9ec4\u91d1\u5468\u957f\u5047\uff0c\u8fd9\u4efd\u62a5\u544a\u6700\u65b0\u4e5f\u662f\u6628\u5929\u521a\u51fa\u7089\u7684\u3002</p><p>\u3000\u3000\u201c\u8bb8\u4f60\u5c06\u522b\u4eba\u8d2c\u5f97\u4e00\u6587\u4e0d\u503c\uff0c\u5c31\u4e0d\u8bb8\u522b\u4eba\u8ba9\u4f60\u770b\u770b\u4ed6\u4eec\u80cc\u540e\u7684\u667a\u56ca\u56e2\u6709\u591a\u5f3a\u5927\uff1f\u8fd9\u4efd\u62a5\u544a\u662f\u7ed9\u4f60\u770b\u7684\uff0c\u201d\u9648\u9759\u6362\u597d\u978b\uff0c\u76f4\u8d77\u8170\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u7ecf\u7814\u4e2d\u5fc3\u6709\u4e00\u4e2a\u9635\u5bb9\u8c6a\u534e\u7684\u6570\u636e\u5206\u6790\u4e2d\u5fc3\u2014\u2014\u4f60\u6628\u5929\u7684\u5efa\u8bae\uff0c\u8981\u662f\u80fd\u5f97\u5230\u4e2d\u5fc3\u7684\u6570\u636e\u652f\u6301\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u7ed9\u4ed6\u4eec\u91c7\u7eb3\u3002\u5728\u7f51\u7edc\u5373\u65f6\u901a\u8baf\u8f6f\u4ef6\u4e0a\u96c6\u6210\u5176\u4ed6\u5e94\u7528\uff0c\u4e5f\u4e0d\u662f\u6ca1\u6709\u7f51\u7ad9\u5b9e\u9a8c\u8fc7\uff0c\u4f46\u662f\u8fd9\u4e2a\u7f51\u7ad9\u7684\u6210\u7ee9\u5e76\u4e0d\u662f\u5341\u5206\u7684\u7406\u60f3\u3002\u6211\u770b\u4ed6\u4eec\u73b0\u5728\u4e5f\u6015\u77eb\u6789\u8fc7\u6b63\uff0c\u6709\u65f6\u5019\u5b81\u53ef\u4e0d\u8ffd\u6c42\u4e00\u6b65\u5230\u4f4d\uff0c\u4e5f\u8981\u907f\u514d\u72af\u66f4\u5927\u7684\u9519\u8bef\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5658\u7740\u5634\uff0c\u8737\u817f\u5750\u5728\u6c99\u53d1\u770b\u62a5\u544a\uff0c\u770b\u5230\u4e00\u534a\u65f6\uff0c\u5567\u5567\u6709\u58f0\u7684\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u53ef\u771f\u662f\u5e78\u707e\u4e50\u7978\uff0c\u770b\u77408818\u671b\u6c34\u5751\u91cc\u8df3\u2014\u2014\u6709\u8fd9\u4efd\u62a5\u544a\u505a\u5e95\uff0c\u5353\u57df\u7684\u7ecf\u8425\u6a21\u5f0f\u4e5f\u8bb8\u5c31\u8981\u8c03\u6574\u4e00\u4e0b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u6ca1\u6765\u5f97\u53ca\u770b\uff0c\u62a5\u544a\u4e0a\u600e\u4e48\u8bf4\u7684\uff1f\u201d\u9648\u9759\u8d70\u8fdb\u53a8\u623f\u91cc\u7ed9\u81ea\u5df1\u5012\u4e86\u4e00\u676f\u767d\u5f00\u6c34\u7aef\u5728\u624b\u91cc\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c8818\u8fc7\u4e8e\u6fc0\u8fdb\u7684\u53d1\u5c55\u7ebf\u4e0b\u904d\u53ca\u5168\u56fd\u5927\u4e2d\u578b\u57ce\u5e02\u7684\u7269\u6d41\u914d\u9001\u53ca\u4ed3\u50a8\u4e1a\u52a1\uff0c\u53c8\u8fc7\u4e8e\u6fc0\u8fdb\u7684\u8ffd\u6c42\u7528\u6237\u6570\u91cf\uff0c\u5168\u9762\u7684\u589e\u52a0\u5728\u7ebf\u9500\u552e\u7269\u54c1\u7684\u79cd\u7c7b\uff0c\u8d44\u91d1\u6d88\u8017\u8fc7\u5feb\uff0c\u6d77\u7c9f\u79d1\u6280\u5e74\u524d\u6ce8\u5165\u7684\u56db\u4ebf\u8d44\u91d1\u5df2\u7ecf\u6d88\u8017\u4e86\u4e09\u5206\u4e4b\u4e00\u3002\u53e6\u4e00\u65b9\u9762\uff0c8818\u867d\u7136\u4ee5\u5982\u6b64\u6fc0\u8fdb\u7684\u53d1\u5c55\u65b9\u5f0f\u5728\u56fd\u5185\u7535\u5b50\u5546\u52a1\u5e02\u573a\u5360\u636e\u8fd160\uff05\u7684\u4efd\u989d\uff0c\u4f46\u662f\u4eca\u5e74\u7684\u9500\u552e\u989d\u52c9\u5f3a\u80fd\u7a81\u7834\u4e24\u4ebf\uff0c\u800c8818\u9500\u552e\u989d\u8981\u8fbe\u523020\u4ebf\u624d\u80fd\u7ef4\u6301\u4f4f\u5df2\u7ecf\u5efa\u6210\u7684\u7269\u6d41\u914d\u9001\u4e0e\u4ed3\u50a8\u4f53\u7cfb\uff0c\u8fd9\u5dee\u4e0d\u591a\u662f8818\u96f6\u4e09\u5e74\u4e4b\u540e\u624d\u80fd\u8fbe\u5230\u76ee\u6807\uff0c\u5728\u6b64\u671f\u95f4\uff0c8818\u8fd8\u9700\u8981\u5404\u80a1\u4e1c\u7ee7\u7eed\u8f93\u51656\u52308\u4ebf\u5143\u7684\u8d44\u672c\u91d1\u2026\u2026\u201d\u8c22\u5b50\u5609\u5927\u58f0\u8ddf\u9648\u9759\u590d\u8ff0\u62a5\u544a\u91cc\u7684\u5185\u5bb9\uff0c\u95ee\u9053\uff0c\u201c\u4f60\u8bf4\u9526\u6e56\u662f\u4e0d\u662f\u5728\u7b49\u7740\u770b\u5b8f\u4fe1\u7684\u597d\u620f\uff1f\u201d</p><p>\u3000\u3000\u201c\u5353\u57df\u7684\u53d1\u5c55\u6a21\u5f0f\u8ddf8818\u7c7b\u4f3c\uff0c\u9526\u6e56\u54ea\u91cc\u4f1a\u8fd9\u4e48\u65e0\u804a\uff1f\u8fd9\u7bc7\u62a5\u544a\u5bf9\u5353\u57df\u5c06\u6765\u7684\u53d1\u5c55\u8def\u7ebf\u9009\u62e9\u5f88\u6709\u4ef7\u503c\uff0c\u4f60\u6628\u5929\u4e0d\u662f\u4e5f\u8bf4\u5353\u57df\u7684\u53d1\u5c55\u6a21\u5f0f\u6709\u95ee\u9898\u5417\uff1f\u201d\u9648\u9759\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e4b\u524d\u53ea\u662f\u63a8\u6d4b\uff0c\u73b0\u5728\u9526\u6e56\u81ea\u5df1\u63d0\u4f9b\u6570\u636e\u652f\u6301\u4e86\uff0c\u201d\u8c22\u5b50\u5609\u63a5\u8fc7\u9648\u9759\u7684\u6c34\u676f\u559d\u4e86\u4e00\u53e3\u6c34\uff0c\u201c\u5353\u57df\u8981\u662f\u6309\u7167\u65e2\u5b9a\u7684\u6a21\u5f0f\u53d1\u5c55\uff0c\u4e5f\u662f\u590d\u52368818\u8d70\u8fc7\u7684\u9053\u8def\uff0c\u5373\u4f7f\u9526\u6e56\u54ac\u54ac\u7259\u5f80\u5353\u57df\u6295\u5165\u5341\u4ebf\u8d44\u91d1\uff0c\u5353\u57df\u5230\u96f6\u4e09\u5e74\u540e\u7684\u9500\u552e\u989d\u4e5f\u5c31\u4e8c\u5341\u4ebf\u5de6\u53f3\u7684\u6837\u5b50\u3002\u4e2d\u56fd\u7535\u5b50\u5546\u52a1\u5e02\u573a\u6c1b\u56f4\uff0c\u6bd5\u7adf\u8ddf\u6b27\u7f8e\u5dee\u592a\u591a\u4e86\uff0c\u4e9a\u9a6c\u900a\u6a21\u5f0f\u80fd\u5728\u7f8e\u56fd\u6210\u529f\uff0c\u5374\u4e0d\u4e00\u5b9a\u80fd\u9002\u5408\u4e2d\u56fd\u7684\u6c34\u571f\uff0c\u81ea\u5df1\u5efa\u7acb\u5b8c\u5584\u7684\u7269\u6d41\u914d\u9001\u4e0e\u4ed3\u50a8\u4f53\u7cfb\uff0c\u672c\u5c31\u662f\u5403\u529b\u4e0d\u8ba8\u597d\u7684\u6d3b\u2014\u2014\u5353\u57df\u8981\u51fb\u8d258818\u4e0d\u662f\u4ec0\u4e48\u96be\u4e8b\uff0c\u6bd5\u7adf\u76db\u946b\u3001\u9999\u96ea\u6d77\u4ee5\u53ca\u7231\u8fbe\u7684\u7269\u6d41\u4f53\u7cfb\u6574\u5408\u4e4b\u540e\u5dee\u4e0d\u591a\u5df2\u7ecf\u8986\u76d6\u5168\u56fd\u4e86\uff0c\u5353\u57df\u4f9d\u9760\u73b0\u6709\u7684\u57fa\u7840\uff0c\u5efa\u7acb\u7269\u6d41\u914d\u9001\u4f53\u7cfb\uff0c\u8981\u8282\u7ea6\u5927\u91cf\u7684\u6210\u672c\uff0c\u4f46\u662f\u4ee5\u51fb\u8d258818\u4e3a\u76ee\u6807\u53c8\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u51e0\u5934\u7b28\u725b\u6324\u5728\u4e00\u6761\u7a84\u5df7\u5b50\u91cc\u8d5b\u8dd1\u7f62\u4e86\u2026\u2026\u201d\u53c8\u62c5\u5fe7\u7684\u95ee\u9053\uff0c\u201c\u4ed6\u4eec\u4e0d\u4f1a\u771f\u8fc7\u6cb3\u62c6\u6865\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u9648\u9759\u644a\u4e86\u644a\u624b\uff0c\u201c\u4f60\u96be\u9053\u60f3\u53c2\u4e0e\u5230\u4ed6\u4eec\u4e2d\u53bb\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u90a3\u5a46\u5a18\u8bf4\u8bdd\u771f\u662f\u7280\u5229\uff0c\u4f60\u6628\u5929\u5728\u65f6\uff0c\u5979\u8bf4\u8bdd\u8fd8\u6ce8\u610f\u5206\u5bf8\uff0c\u4f60\u8ddf\u9648\u9759\u4e00\u8d70\uff0c\u5979\u7684\u59ff\u6001\u5c31\u5f00\u59cb\u5f80\u4e0a\u8d70\u4e86\u2026\u2026\u201d\u675c\u98de\u4e0b\u5348\u624d\u4f11\u606f\u4e09\u56db\u4e2a\u5c0f\u65f6\uff0c\u7cbe\u529b\u4f9d\u65e7\u5341\u5206\u7684\u5145\u6c9b\uff0c\u8ddf\u5f20\u606a\u63cf\u8ff0\u8c22\u5b50\u5609\u5728\u4ed6\u8ddf\u9648\u9759\u8d70\u4e4b\u540e\u7684\u8868\u73b0\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u8bc9\u82e6\u9053\uff1a\u201c\u6211\u4eec\u4e00\u7fa4\u5927\u8001\u7237\u4eec\u90fd\u7ed9\u5979\u8bf4\u5f97\u4e00\u65e0\u662f\u5904\u554a\uff0c\u5c31\u6068\u4f60\u4e3a\u4ec0\u4e48\u63d0\u524d\u8d70\u4eba\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u5f97\uff0c\u4f60\u4eec\u7684\u8138\u76ae\u6ca1\u90a3\u4e48\u8584\uff01\u8fd8\u662f\u5148\u8c08\u8c08\u4f60\u4eec\u5bf9\u8c22\u5b50\u5609\u5979\u7684\u5efa\u8bae\u6709\u4ec0\u4e48\u770b\u6cd5\u5427\uff0c\u5979\u7684\u6001\u5ea6\u95ee\u9898\uff0c\u6211\u4eec\u4ee5\u540e\u518d\u8ba8\u8bba\u3002\u201d\u5f20\u606a\u7b11\u7740\u6253\u65ad\u9a6c\u5411\u4e1c\u7684\u8bdd\uff0c\u8fd9\u65f6\u5019\u53ea\u6709\u675c\u98de\u3001\u9a6c\u5411\u4e1c\u8fd8\u6709\u5c0f\u8868\u54e5\u6881\u6587\u6c5f\u8fc7\u6765\u627e\u4ed6\uff0c\u5176\u4ed6\u4eba\u597d\u4e9b\u90fd\u5728\u7761\u89c9\uff0c\u8c22\u5b50\u5609\u6628\u5929\u53c2\u4e0e\u8ba8\u8bba\u65f6\u6ca1\u6709\u505a\u8bb0\u5f55\uff0c\u4e0d\u8fc7\u5728\u8c22\u5b50\u5609\u79bb\u5f00\u4e4b\u540e\uff0c\u4ed6\u4eec\u5185\u90e8\u8ba8\u8bba\u65f6\u6709\u5b8c\u6574\u7684\u4f1a\u8bae\u7eaa\u8981\uff0c\u5f20\u606a\u7ffb\u770b\u4f1a\u8bae\u7eaa\u8981\uff0c\u5c31\u80fd\u5927\u6982\u7684\u638c\u63e1\u5230\u4ed6\u4eec\u7ecf\u8fc7\u6628\u591c\u601d\u8def\u4e0a\u6709\u600e\u6837\u7684\u8f6c\u53d8\u3002</p><p>\u3000\u3000\u201c\u5f88\u96be\u60f3\u8c61\u5979\u4e4b\u524d\u6ca1\u6709\u63a5\u89e6\u8fc7\u4e92\u8054\u7f51\u4ea7\u4e1a\uff0c\u6709\u4e9b\u4eba\u5929\u8d4b\u662f\u4e0e\u751f\u4ff1\u6765\u7684\uff0c\u6211\u73b0\u5728\u76f8\u4fe1\u8fd9\u8bdd\u3002\u201d\u9a6c\u5411\u4e1c\u8bf4\u9053\uff0c\u201c\u5979\u7684\u5efa\u8bae\u81f3\u5c11\u5e94\u8be5\u662f\u6211\u4eec\u8981\u52aa\u529b\u7684\u4e00\u4e2a\u65b9\u5411\uff0c\u4e0d\u8fc7\u8981\u7167\u5979\u5efa\u8bae\u7684\u65b9\u5411\u53d1\u5c55\uff0c\u6211\u4eec\u7684MICQ\u4ea7\u54c1\u8981\u4ea4\u7ed9\u4e1c\u5927\u521b\u57df\u6765\u8fdb\u884c\u6574\u5408\u8fd0\u4f5c\u624d\u5408\u9002\uff0c\u6606\u817e\u4e2d\u6587\u7efc\u5408\u793e\u533a\u4e0e\u4e1c\u793e\u6c5f\u6e56\u8fd8\u662f\u8981\u8d70\u4e0d\u540c\u7684\u65b9\u5411\u53d1\u5c55\uff0c\u514d\u8d39\u5e94\u7528\u662f\u4e00\u4e2a\u5927\u7684\u524d\u63d0\u2026\u2026\u5353\u57df\u7684\u53d1\u5c55\u95ee\u9898\u4e0a\uff0c\u6768\u4f1f\u6bc5\u8ddf\u5979\u53cd\u9a73\u4e86\u4e24\u53e5\uff0c\u5979\u5c31\u7231\u8bf4\u4e0d\u8bf4\uff0c\u6211\u60f3\u5979\u5927\u6982\u4e5f\u662f\u770b\u52308818\u590d\u5236\u4e9a\u9a6c\u900a\u6a21\u5f0f\u5728\u4e2d\u56fd\u7684\u53d1\u5c55\u5c40\u9650\u201d\u4ed6\u4eec\u624b\u91cc\u4e5f\u521a\u521a\u62ff\u5230\u9526\u6e56\u7ecf\u7814\u4e2d\u5fc3\u521a\u51fa\u7089\u7684\u5206\u6790\u62a5\u544a\u3002</p><p>\u3000\u3000\u201c\u5353\u57df\u7684\u8d44\u91d1\u4ee5\u73b0\u5728\u7684\u52bf\u6001\u7ee7\u7eed\u53d1\u5c55\u4e0b\u53bb\uff0c\u5e74\u5e95\u5c31\u8981\u8017\u5c3d\u3002\u5373\u4f7f\u5438\u53d68818\u7684\u4e00\u4e9b\u6559\u8bad\uff0c\u4e13\u6ce8\u8d70\u7cbe\u54c1\u8def\u7ebf\uff0c\u4e5f\u8bb8\u80fd\u63d0\u524d\u5b9e\u73b0\u76c8\u5229\uff0c\u4f46\u662f\u53d1\u5c55\u524d\u666f\u5c31\u4f1a\u53d7\u5230\u5f88\u5927\u7684\u9650\u5236\uff0c\u201d\u675c\u98de\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u5728\u8003\u8651\u5353\u57df\u662f\u4e0d\u662f\u8981\u8f6c\u53d8\u4e00\u4e0b\u601d\u8def\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f20\u7edf\u7684\u96f6\u552e\u6a21\u5f0f\uff0c\u4e3b\u8981\u6709\u4e09\u7c7b\uff0c\u4e00\u662f\u767e\u8d27\u5546\u573a\u6216\u8d85\u7ea7\u5546\u573a\u6a21\u5f0f\uff0c\u4e00\u662f\u5546\u5e97\u8857\u6a21\u5f0f\uff0c\u4e00\u662f\u8df3\u86a4\u5e02\u573a\u6216\u8005\u8bf4\u662f\u96c6\u8d38\u5e02\u573a\u6a21\u5f0f\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c8818\u3001\u5353\u57df\u4e8b\u5b9e\u4e0a\u5c06\u767e\u8d27\u5546\u5e97\u6216\u8d85\u5e02\u642c\u5230\u4e92\u8054\u7f51\u4e0a\uff0c\u8fd9\u9700\u89818818\u3001\u5353\u57df\u5efa\u7acb\u5e9e\u5927\u7684\u7ebf\u4e0b\u7269\u6d41\u914d\u9001\u53ca\u4ed3\u50a8\u4f53\u7cfb\uff0c\u6295\u8d44\u5de8\u5927\u4e0d\u8bf4\uff0c\u4eca\u540e\u7684\u8fd0\u8425\u53ca\u7ba1\u7406\u6210\u672c\u4e5f\u76f8\u5f53\u60ca\u4eba\u3002\u4e0e\u4f20\u7edf\u7684\u767e\u8d27\u5546\u573a\u4e0e\u8d85\u5e02\u76f8\u6bd4\uff0c8818\u4e5f\u8bb8\u8282\u7ea6\u5b9e\u4f53\u5546\u573a\u7684\u79df\u7528\u6210\u672c\uff0c\u4f46\u662f\u4e5f\u589e\u52a0\u4e86\u7269\u6d41\u914d\u9001\u6210\u672c\u4ee5\u53ca\u6d88\u8d39\u8005\u8fdd\u7ea6\u6210\u672c\uff0c\u540c\u65f6\u8fd8\u53d7\u9650\u56fd\u5185\u4e0d\u5b8c\u5584\u7684\u652f\u4ed8\u4f53\u7cfb\u2014\u2014\u6bd4\u8d77\u4e9a\u9a6c\u900a\u6a21\u5f0f\uff0c\u6211\u66f4\u503e\u5411\u8ba4\u540cEBAY\u7684\u8df3\u86a4\u5e02\u573a\u6a21\u5f0f\u5728\u56fd\u5185\u6709\u66f4\u5927\u7684\u53d1\u5c55\u7a7a\u95f4\uff0c\u4e92\u8054\u7f51\u5c31\u5e94\u8be5\u7a81\u663e\u51fa\u5e73\u53f0\u529f\u80fd\u6765\u3002\u4f46\u662fEBAY\u5bf9\u5356\u5bb6\u758f\u4e8e\u7ba1\u7406\uff0c\u800c\u56fd\u5185\u53c8\u4fe1\u5949\u65e0\u5546\u4e0d\u5978\u7684\u51c6\u5219\uff0c\u76f8\u4e92\u7684\u8bda\u4fe1\u4f53\u7cfb\u7684\u5efa\u7acb\u9887\u4e3a\u56f0\u96be\uff0c\u5353\u57df\u6700\u7ec8\u6216\u8bb8\u53ef\u4ee5\u9009\u62e9\u8d70\u4e9a\u9a6c\u900a\u4e0eEBAY\u7684\u4e2d\u95f4\u8def\u7ebf\u3002\u6bd4\u7167\u4f20\u7edf\u7684\u96f6\u552e\u4e1a\uff0c\u79f0\u4e4b\u4e3a\u5546\u5e97\u8857\u6a21\u5f0f\u4e5f\u7b97\u52c9\u5f3a\u80fd\u884c\uff0c\u5c31\u662f\u8981\u52a0\u5f3a\u5bf9\u5356\u5bb6\u7684\u7ba1\u7406\uff0c\u4f18\u5148\u63a8\u52a8\u4ea7\u54c1\u7684\u4ee3\u7406\u5546\u3001\u7ecf\u9500\u5546\u5229\u7528\u5353\u57df\u7684\u5e73\u53f0\u8fdb\u884c\u96f6\u552e\u8d38\u6613\u3002\u5f53\u7136\uff0c\u5353\u57df\u539f\u5148\u7684\u5546\u57ce\u6a21\u5f0f\u4e5f\u53ef\u4ee5\u4e88\u4ee5\u4fdd\u7559\uff0c\u6682\u65f6\u4e13\u6ce8\u4e8e\u8d70\u7cbe\u54c1\u8def\u7ebf\u3002\u5373\u4f7f\u5728\u4f20\u7edf\u7684\u767e\u8d27\u5546\u5e97\u91cc\uff0c\u4e5f\u6709\u5382\u5546\u6216\u4ee3\u7406\u5546\u8bbe\u7acb\u7684\u9500\u552e\u4e13\u67dc\u5b58\u5728\uff0c\u5353\u57df\u5546\u57ce\u6700\u7ec8\u7684\u53d1\u5c55\u65b9\u5411\u4e5f\u8bb8\u662f\u8fd9\u4e2a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u675c\u98de\u8bf4\u9053\uff0c\u201c\u603b\u4e4b\u8fd9\u65f6\u5019\u4e0d\u662f\u5927\u89c4\u6a21\u5efa\u7acb\u7269\u6d41\u914d\u7528\u4f53\u7cfb\u4e0e\u4ed3\u50a8\u4f53\u7cfb\u7684\u65f6\u673a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u6628\u5929\u62c9\u90b5\u5fc3\u6587\u8fc7\u53bb\uff0c\u5c31\u662f\u90b5\u5fc3\u6587\u60f3\u5c06\u76db\u946b\u7535\u8baf\u4e0e\u5353\u57df\u7684\u5e73\u53f0\u8854\u63a5\uff0c\u7ed9\u8c22\u5b50\u5609\u95ef\u8fdb\u6765\u6253\u5c94\uff0c\u8fd9\u4e8b\u5012\u6ca1\u6709\u8c08\u6210\uff0c\u4f60\u4eec\u6709\u673a\u4f1a\u5c31\u76f4\u63a5\u53bb\u627e\u90b5\u5fc3\u6587\u2026\u2026\u201d</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u53c8\u95ee\u675c\u98de\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u7684\u8ba8\u8bba\u8fd8\u8981\u4e0d\u8981\u8bf7\u8c22\u5b50\u5609\u51fa\u5e2d\uff1f\u201d</p><p>\u3000\u3000\u201c\u867d\u7136\u7ed9\u4e00\u4e2a\u5973\u5a03\u770b\u4e0d\u8d77\u6709\u4e9b\u4e22\u8138\uff0c\u4f46\u662f\u6211\u4eec\u4e5f\u4e0d\u80fd\u5e72\u8fc7\u6cb3\u62c6\u6865\u7684\u4e8b\u60c5\uff0c\u201d\u675c\u98de\u53c8\u8ddf\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8c22\u5bb6\u5973\u9b54\u5934\u8fd9\u4e48\u79ef\u6781\uff0c\u5176\u76ee\u7684\u53ea\u6015\u4e5f\u662f\u60f3\u63ba\u4e00\u817f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u51b3\u5b9a\u5c31\u597d\uff0c\u53c2\u80a1\u53c8\u662f\u4e0d\u662f\u63a7\u80a1\uff0c\u9526\u6e56\u65d7\u4e0b\u7684\u4f01\u4e1a\u8981\u66f4\u5f00\u53d1\u4e00\u4e9b\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u884c\uff0c\u5206\u5bf8\u6211\u4eec\u5c31\u5148\u81ea\u884c\u638c\u63e1\u4e86\uff0c\u201d\u675c\u98de\u8bf4\u9053\uff0c\u201c\u603b\u4e4b\u65b9\u6848\u6700\u540e\u4e5f\u662f\u8981\u62ff\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u4e0a\u8ba8\u8bba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u4e0d\u8981\u803d\u6401\u65f6\u95f4\u4e86\uff0c\u6211\u8ddf\u6881\u6587\u6c5f\u660e\u5929\u4e0b\u5348\u8fd8\u8981\u56de\u5317\u4eac\u5462\u3002\u201d\u9a6c\u5411\u4e1c\u8feb\u4e0d\u53ca\u5f85\u7684\u7ad9\u8d77\u6765\uff0c\u8981\u675c\u98de\u3001\u6881\u6587\u6c5f\u4e5f\u7ad9\u8d77\u6765\u8ddf\u4ed6\u4e00\u8d77\u51fa\u53bb\uff0c\u518d\u5c06\u5927\u5bb6\u63ea\u5230\u4e00\u8d77\u6765\u7ee7\u7eed\u8ba8\u8bba\uff0c\u8fd9\u65f6\u5019\u8ba8\u8bba\u5f97\u8d8a\u8be6\u7ec6\uff0c\u4ed6\u4eec\u56de\u5317\u4eac\u540e\u7684\u5de5\u4f5c\u4e5f\u8d8a\u597d\u505a\u3002</p></article>"}}}]);
