(window.webpackJsonp=window.webpackJsonp||[]).push([[1146],{1169:function(p){p.exports={chapterTitle:null,title:"\u7b2c940\u7ae0 \u6ca1\u843d",content:"<article><h2>\u7b2c940\u7ae0 \u6ca1\u843d</h2><p>\u3000\u3000\u4e0b\u5348\u5728\u53f6\u5efa\u658c\u4e0e\u5357\u6d0b\u6d77\u8fd0\u8463\u4e8b\u5c40\u4e3b\u5e2d\u9a6c\u6587\u6e20\u7684\u5f15\u8350\u4e0b\uff0c\u5f20\u606a\u5728\u534a\u5c9b\u9152\u5e97\u4e0e\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u7684\u526f\u603b\u88c1\u674e\u8000\u534e\u89c1\u4e86\u4e00\u9762\u3002</p><p>\u3000\u3000\u5357\u6d0b\u6d77\u8fd0\u8fd9\u4e9b\u5e74\u6765\u5728\u4e9a\u6d32\u5404\u5730\u79ef\u6781\u6295\u8d44\u53c2\u4e0e\u6d77\u6e2f\u7801\u5934\u7684\u5efa\u8bbe\uff0c\u4f46\u662f\u5c1a\u7f3a\u4e4f\u72ec\u7acb\u627f\u63a5\u7efc\u5408\u6027\u8d85\u5927\u578b\u6df1\u6c34\u6d77\u6e2f\u5efa\u8bbe\u7684\u7ecf\u9a8c\uff0c\u66f4\u4f55\u51b5\u8fd8\u5305\u62ec\u4e34\u6e2f\u65b0\u57ce\u4e0e\u6e2f\u53e3\u4ea7\u4e1a\u533a\u7684\u89c4\u5212\u5efa\u8bbe\u91cd\u4efb\u3002\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u5728\u8fd9\u65b9\u9762\u7684\u7ecf\u9a8c\u5c31\u8981\u4e30\u5bcc\u5f97\u591a\uff0c\u800c\u4e14\u5728\u4e9a\u6d32\u5730\u533a\uff0c\u65b0\u52a0\u5761\u7684\u6e2f\u53e3\u7ba1\u7406\u6c34\u5e73\u4e5f\u662f\u8d85\u4e00\u6d41\u7684\uff0c\u5c06\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u5f15\u8fdb\u5230\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u4e2d\u6765\uff0c\u53ef\u4e0d\u662f\u4e3a\u4e86\u4ec5\u4ec5\u5206\u62c5\u5efa\u8bbe\u878d\u8d44\u4e0a\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u867d\u8bf4\u5728\u8fd9\u6b21\u7684\u91d1\u878d\u98ce\u66b4\u4e2d\u652f\u6491\u4f4f\u4e86\uff0c\u7ecf\u6d4e\u6ca1\u6709\u906d\u53d7\u91cd\u632b\uff0c\u4f46\u662f\u7834\u574f\u529b\u5982\u6b64\u4e4b\u5927\u7684\u91d1\u878d\u98ce\u66b4\u4e5f\u8ba9\u65b0\u52a0\u5761\u5404\u9636\u5c42\u90fd\u5fc3\u6709\u4f59\u60b8\uff0c\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u5bf9\u672a\u6765\u7684\u53d1\u5c55\u65b9\u5411\u81ea\u7136\u4e5f\u6709\u65b0\u7684\u601d\u8003\u4e0e\u8c03\u6574\u3002</p><p>\u3000\u3000\u7531\u5357\u6d0b\u6d77\u8fd0\u4e0e\u9526\u6e56\u5546\u4e8b\u8054\u5408\u51fa\u9762\u4e0e\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u7684\u8c08\u5224\uff0c\u4e5f\u5feb\u6709\u4e09\u4e2a\u6708\u4e86\u3002\u5728\u8fc7\u53bb\u7684\u4e24\u4e2a\u6708\u91cc\uff0c\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u524d\u540e\u4e09\u6b21\u6d3e\u51fa\u8003\u5bdf\u56e2\u5230\u65b0\u4ead\u8003\u5bdf\uff0c\u5e76\u5355\u72ec\u59d4\u6258\u65b0\u52a0\u5761\u822a\u9053\u8bbe\u8ba1\u9662\u5bf9\u65b0\u4ead\u5e02\u6e2f\u52a1\u5c40\u8bbe\u8ba1\u822a\u9053\u8fdb\u884c\u5168\u7a0b\u6d77\u6d4b\u626b\u63cf\uff0c\u5e76\u4ece\u6587\u821f\u6e2f\u8c03\u52a8\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u65d7\u4e0b\u4e00\u8f6e\u5403\u6c3417\u7c73\u6df1\u7684\u8d85\u5927\u578b\u6563\u88c5\u8d27\u8f6e\u5bf9\u822a\u9053\u8fdb\u884c\u8bd5\u822a\uff0c\u8bc1\u5b9e\u4e86\u4e1c\u5c71\u5c9b\u822a\u9053\u662f\u6587\u821f\u6e2f\u4ee5\u5317\u5730\u76f4\u5230\u6e24\u6d77\u6e7e\u4ee5\u5357\u6700\u4f18\u8d28\u7684\u6df1\u6c34\u6d77\u6e2f\u8d44\u6e90\uff0c\u8003\u5bdf\u5de5\u4f5c\u5b8c\u6210\u4e4b\u540e\uff0c\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u5185\u90e8\u5f88\u5feb\u5c31\u505a\u51fa\u51b3\u5b9a\uff0c\u8fbe\u6210\u5171\u540c\u51fa\u8d44\u5efa\u8bbe\u5e76\u7ba1\u7406\u4e1c\u5c71\u5c9b\u7684\u5408\u4f5c\u610f\u5411\uff0c\u6210\u4e3a\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u7684\u5171\u540c\u53d1\u8d77\u4eba\u3002</p><p>\u3000\u3000\u674e\u8000\u534e\u6b64\u884c\u662f\u524d\u5f80\u65b0\u4ead\u4ee3\u8868\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u7b7e\u7f72\u6e2f\u53e3\u5f00\u53d1\u5408\u4f5c\u610f\u5411\u6027\u534f\u8bae\uff0c\u4ece\u65b0\u52a0\u5761\u6ca1\u6709\u76f4\u98de\u91d1\u5c71\u7684\u822a\u73ed\uff0c\u53ea\u80fd\u5728\u9999\u6e2f\u4e2d\u8f6c\uff0c\u4e0d\u8fc7\u8981\u7b49\u5230\u660e\u5929\u65e9\u6668\u624d\u6709\u98de\u5f80\u91d1\u5c71\u7684\u98de\u673a\u3002</p><p>\u3000\u3000\u5230\u73b0\u5728\uff0c\u4ee5\u5357\u6d0b\u6d77\u8fd0\u3001\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u3001\u9526\u6e56\u5546\u4e8b\u3001\u65b0\u4ead\u5e02\u6e2f\u52a1\u5c40\u3001\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u3001\u6d0b\u6d66\u9020\u8239\u4e1a\u96c6\u56e2\u4ee5\u53ca\u4e2d\u56fd\u8fdc\u6d0b\u8fd0\u8f93\u96c6\u56e2\u5728\u5185\u7684\u4e03\u5bb6\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u53d1\u8d77\u4eba\u5171\u540c\u7ec4\u5efa\u4e1c\u5c71\u6e2f\u52a1\u96c6\u56e2\u6709\u9650\u516c\u53f8\u5f00\u53d1\u4e1c\u5c71\u5c9b\u7684\u5408\u4f5c\u6846\u67b6\u5df2\u7ecf\u521d\u6b65\u786e\u5b9a\u4e0b\u6765\uff0c\u7b49\u7740\u56fd\u52a1\u9662\u5404\u90e8\u59d4\u7684\u5ba1\u6279\u7a0b\u5e8f\u8d70\u5b8c\uff0c\u5c31\u4f1a\u5373\u65f6\u7684\u6b63\u5f0f\u542f\u52a8\u3002</p><p>\u3000\u3000\u6839\u636e\u6e2f\u53e3\u6295\u8d44\u81ea\u6709\u8d44\u672c\u4e0d\u4f4e\u4e8e40\uff05\u7684\u89c4\u5b9a\uff0c\u4e00\u671f\u89c4\u5212\u603b\u6295\u8d44\u8fbe25\u4ebf\u7f8e\u5143\u7684\u4e1c\u5c71\u6e2f\u5f00\u53d1\u96c6\u56e2\u6709\u9650\u516c\u53f8\u53d1\u8d77\u8d44\u672c\u5c31\u5c06\u9ad8\u8fbe10\u4ebf\u7f8e\u5143\uff0c\u9526\u6e56\u5546\u4e8b\u5c06\u6ce8\u5165\u8d44\u672c1\u4ebf6\u5343\u4e07\u7f8e\u5143\uff0c\u6301\u80a116\uff05\uff0c\u5357\u6d0b\u6d77\u8fd0\u6ce8\u5165\u8d44\u672c1\u4ebf8\u5343\u4e07\u7f8e\u5143\uff0c\u6301\u80a118\uff05\uff0c\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u6ce8\u5165\u8d44\u672c1\u4ebf5\u5343\u4e07\u7f8e\u5143\uff0c\u6301\u80a115\uff05\uff0c\u4e09\u5bb6\u6d77\u5916\u516c\u53f8\u603b\u6301\u80a1\u6743\u4e3a49\uff05\uff0c\u65b0\u4ead\u5e02\u653f\u5e9c\u4ee5\u4e1c\u5c71\u5c9b\u571f\u5730\u8d44\u6e90\u4ee5\u53ca60\u5e73\u65b9\u516c\u91cc\u6d77\u57df\u8d44\u6e90\u4f5c\u4ef7\u4e00\u4ebf\u7f8e\u5143\u5165\u8d44\uff0c\u8054\u5408\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u3001\u6d0b\u6d66\u8239\u4e1a\u3001\u4e2d\u56fd\u8fdc\u6d0b\u8fd0\u8f93\u96c6\u56e2\u5171\u540c\u6301\u6709\u4f59\u4e0b51\uff05\u7684\u80a1\u6743\uff0c\u8fd9\u4e48\u5b89\u6392\u4e5f\u662f\u907f\u514d\u5ba1\u6279\u7a0b\u5e8f\u4f1a\u51fa\u95ee\u9898\u7ed9\u5361\u8116\u5b50\uff0c\u81f3\u5c11\u4fdd\u8bc1\u4e86\u56fd\u5bb6\u5bf9\u672a\u6765\u4e1c\u5c71\u5c9b\u7684\u63a7\u80a1\u6743\u3002</p><p>\u3000\u3000\u9664\u4e86\u76f4\u63a5\u6ce8\u8d44\u7684\u4e5d\u4ebf\u7f8e\u5143\u8d44\u672c\u4e4b\u5916\uff0c\u6e2f\u53e3\u4e00\u671f\u5de5\u7a0b\u8fd8\u6709\u5341\u516d\u4ebf\u7f8e\u5143\u7684\u5efa\u8bbe\u8d44\u91d1\u7f3a\u53e3\uff0c\u5c06\u7531\u5404\u5bb6\u53d1\u8d77\u4eba\u4ee5\u6301\u80a1\u6bd4\u4f8b\u5206\u644a\u4ee5\u503a\u6743\u878d\u8d44\u7684\u5f62\u5f0f\u6ce8\u5165\uff0c\u9526\u6e56\u5546\u4e8b\u4e0e\u5357\u6d0b\u6d77\u8fd0\u8fd8\u5c06\u627f\u62c55.44\u7f8e\u5143\uff0c\u5c06\u603b\u5171\u638f\u51fa8\u4ebf5\u5343\u4e07\u7f8e\u5143\u7684\u771f\u91d1\u5b9e\u94f6\u6765\u3002\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u7684\u8d44\u91d1\u5b9e\u529b\u81ea\u7136\u4e0d\u7528\u8d28\u7591\uff0c\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u4e0e\u6d0b\u6d66\u8239\u4e1a\u96c6\u56e2\u7684\u51fa\u8d44\u90e8\u5206\u6709\u4e1c\u6d77\u7701\u653f\u5e9c\u652f\u6301\u5e76\u62c5\u4fdd\uff0c\u4e5f\u4e0d\u6210\u4ec0\u4e48\u95ee\u9898\uff0c\u4e2d\u56fd\u8fdc\u6d0b\u8fd0\u8f93\u96c6\u56e2\u4f5c\u4e3a\u56fd\u5185\u6700\u5927\u7684\u56fd\u6709\u6d77\u6d0b\u8fd0\u8f93\u96c6\u56e2\uff0c\u5373\u4f7f\u81ea\u6709\u8d44\u91d1\u5b9e\u529b\u6709\u4e9b\u4e0d\u8db3\uff0c\u4f46\u662f\u94f6\u884c\u8ddf\u4ed6\u81ea\u5bb6\u5f00\u7684\u4f3c\u7684\uff0c\u51fa\u8d44\u90e8\u5206\u4e5f\u4e0d\u4f1a\u6210\u4ec0\u4e48\u95ee\u9898\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u6e2f\u5f00\u53d1\u96c6\u56e2\u9664\u4e86\u6e2f\u53e3\u5efa\u8bbe\u4e4b\u5916\uff0c\u8fd8\u5c06\u627f\u63a5\u4f9b\u6c34\u3001\u4f9b\u7535\u7b49\u914d\u5957\u5de5\u7a0b\u7684\u5efa\u8bbe\uff0c\u4ec5\u7531\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u65d7\u4e0b\u5fb7\u5229\u96c6\u56e2\u627f\u5efa\u7684\u70ed\u7535\u5382\u4e00\u671f\u6295\u8d44\u5c31\u9ad8\u8fbe\u4e94\u4ebf\u7f8e\u5143\uff0c\u5b9e\u9645\u7528\u4e8e\u6e2f\u53e3\u4e00\u671f\u57fa\u5efa\u7684\u8d39\u7528\u8fd8\u4e0d\u8db3\u4e8c\u5341\u4ebf\u7f8e\u5143\uff0c\u5176\u4e2d\u8fd8\u5305\u62ec\u4e00\u5ea7\u8fde\u63a5\u4e1c\u5c71\u5c9b\u4e0e\u6d77\u5cb8\u7684\u76f4\u63a5\u8de8\u5ea6\u8fbe\u4e94\u516c\u91cc\u7684\u8de8\u6d77\u5927\u6865\u7684\u6295\u8d44\u3002</p><p>\u3000\u3000\u6c5f\u5357\u7701\u4e0e\u65b0\u4ead\u5e02\u8c8c\u4f3c\u4e0d\u7528\u76f4\u63a5\u51fa\u8d44\uff0c\u4f46\u662f\u9664\u4f9b\u7535\u3001\u4f9b\u6c34\u914d\u5957\u5de5\u7a0b\u4e4b\u5916\uff0c\u901a\u8baf\u3001\u516c\u8def\u3001\u94c1\u8def\u4ea4\u901a\u8854\u63a5\u7b49\u914d\u5957\u5de5\u7a0b\u7684\u6295\u8d44\u5c06\u4e0d\u4f1a\u4f4e\u4e8e\u4e8c\u5341\u4ebf\uff0c\u8fd9\u8fd8\u4e0d\u5305\u62ec\u91d1\u5c71\u3001\u60e0\u5c71\u5230\u65b0\u4ead\u9ad8\u901f\u516c\u8def\u7f51\u4ee5\u53ca\u65b0\u4ead\u5230\u6e2f\u53e3\u7684\u9ad8\u901f\u516c\u8def\u6bb5\u7684\u5efa\u8bbe\u8d39\u7528\u3002</p><p>\u3000\u3000\u6e2f\u53e3\u5efa\u8bbe\u9700\u8981\u6709\u4ea7\u4e1a\u6765\u652f\u6491\u624d\u80fd\u51f8\u663e\u51fa\u5de8\u5927\u7684\u4ef7\u503c\u6765\uff0c\u4f9d\u8d56\u4e1c\u5c71\u6e2f\u7684\u4e34\u6e2f\u65b0\u57ce\u4e0e\u6e2f\u53e3\u4ea7\u4e1a\u56ed\u533a\u624d\u662f\u672a\u6765\u6295\u8d44\u7684\u5de8\u5934\u3002</p><p>\u3000\u3000\u9664\u4e86\u94a2\u94c1\u4ea7\u4e1a\u57fa\u5730\u4e0e\u9020\u8239\u57fa\u5730\u5df2\u7ecf\u786e\u5b9a\u6295\u8d44\u610f\u5411\u3001\u6b63\u5728\u505a\u89c4\u5212\u4e4b\u5916\uff0c\u65b0\u4ead\u5e02\u653f\u5e9c\u6b63\u4e0e\u65b0\u52a0\u5761\u653f\u5e9c\u78cb\u5546\uff0c\u5e0c\u671b\u80fd\u8054\u5408\u5f00\u53d1\u6e2f\u53e3\u4ea7\u4e1a\u56ed\u533a\u7684\u5269\u4f59\u90e8\u5206\u4ee5\u53ca\u4e34\u6e2f\u65b0\u57ce\uff0c\u8ba1\u5212\u5728\u4e1c\u5c71\u9547\u4ee5\u4e1c\u3001\u4e1c\u5c71\u5c9b\uff08\u4e1c\u5c71\u6e2f\uff09\u4ee5\u897f\u4e4b\u95f4\u7684\u6d77\u6ee8\u6ee9\u6d82\u5730\u4e0a\u56f4\u5830\u586b\u6d77\u9020\u51fa\u4e24\u767e\u5e73\u65b9\u516c\u91cc\u7684\u5efa\u8bbe\u7528\u5730\u6765\u3002\u4ec5\u94a2\u94c1\u4ea7\u4e1a\u57fa\u5730\u4e00\u671f\u5de5\u7a0b\u4e00\u4e07\u4e94\u5343\u4ea9\u7528\u5730\u7684\u586b\u6d77\u521d\u6b65\u6295\u8d44\u9884\u7b97\u5c31\u5c06\u9ad8\u8fbe6\u4ebf\uff0c\u82e5\u662f\u4e0e\u65b0\u52a0\u5761\u653f\u5e9c\u8fbe\u6210\u534f\u8bae\uff0c\u6574\u4e2a\u56f4\u5830\u586b\u6d77\u5de5\u7a0b\u603b\u6295\u8d44\u5c06\u8d85\u8fc7\u4e00\u767e\u4ebf\u3002\u56f4\u6d77\u5de5\u7a0b\u5b8c\u6210\u4e4b\u540e\uff0c\u6e2f\u53e3\u4ea7\u4e1a\u56ed\u5c06\u5bb9\u7eb3\u6570\u4ee5\u5343\u4ebf\u8ba1\u7684\u6295\u8d44\u3002</p><p>\u3000\u3000\u8fd9\u5728\u4e5d\u516b\u5e74\u7684\u4e2d\u56fd\uff0c\u7edd\u5bf9\u8981\u7b97\u4e00\u4e2a\u4ee4\u4eba\u53f9\u4e3a\u89c2\u6b62\u7684\u9879\u76ee\uff0c\u5373\u4f7f\u653e\u773c\u5168\u7403\uff0c\u4e5f\u4e0d\u591a\u89c1\u3002</p><p>\u3000\u3000\u5f20\u606a\u660e\u5929\u4e0b\u5348\u7684\u98de\u673a\u56de\u5efa\u90ba\uff0c\u56de\u5230\u5efa\u90ba\u4e5f\u6ca1\u6709\u8ba1\u5212\u53bb\u91d1\u5c71\u53c2\u52a0\u53d1\u8d77\u4eba\u5408\u4f5c\u610f\u5411\u7684\u7b7e\u5b57\u4eea\u5f0f\uff0c\u4e00\u5207\u90fd\u8fd8\u8981\u56fd\u52a1\u9662\u7684\u6b63\u5f0f\u6279\u6587\u4e0b\u6765\uff0c\u53ef\u80fd\u8fd8\u9700\u8981\u4e00\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\u624d\u6709\u7709\u76ee\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u665a\u5bb4\u7531\u53f6\u5efa\u658c\u3001\u9a6c\u6587\u6e20\u966a\u540c\u674e\u8000\u534e\uff0c\u5f20\u606a\u8fd4\u56de\u4e07\u4e3d\u6d77\u666f\u9152\u5e97\uff0c\u665a\u4e0a\u8fd8\u8981\u4e0e\u5fb7\u4eea\u7684\u9ad8\u7ea7\u526f\u603b\u88c1\u5fb7\u5c14\u6cd5\u897f\u5171\u9910\u3002</p><p>\u3000\u3000\u5230\u9152\u5e97\u540e\uff0c\u65f6\u95f4\u5c1a\u65e9\uff0c\u4fbf\u966a\u5510\u5a67\u518d\u5230\u5c55\u9986\u8d70\u4e86\u4e00\u5708\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\uff0c\u56fd\u5185\u4f01\u4e1a\u4e5f\u7ec4\u56e2\u53c2\u52a0\u9999\u6e2f\u7535\u5b50\u5c55\uff0c\u53ea\u4e0d\u8fc7\u7ba1\u7406\u90e8\u95e8\u5212\u7ed9\u56fd\u5185\u4f01\u4e1a\u7684\u5185\u5730\u9986\u533a\u662f\u5730\u5e95\u8f66\u5e93\u4e34\u65f6\u6539\u5efa\u7684\uff0c\u4f4d\u5b50\u8f83\u504f\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u7279\u5730\u7ed5\u8fc7\u53bb\u770b\u4e86\u4e00\u4e0b\uff0c\u5f88\u591a\u4eba\u5728\u4e3b\u5c55\u9986\u90fd\u4e0d\u4f1a\u6ce8\u610f\u5230\u62d0\u8fc7\u6765\u7684\u72ed\u5c0f\u901a\u9053\u2014\u2014\u901a\u9053\u5c31\u5728\u4e00\u5c42\u5927\u5385\u5395\u6240\u89d2\u843d\u91cc\uff0c\u9ed1\u9ee2\u9ee2\u7684\uff0c\u8981\u4e0d\u662f\u7fdf\u4e39\u9752\u5e26\u8def\uff0c\u5f20\u606a\u80fd\u6478\u5230\u624d\u53eb\u6709\u9b3c\u5462\u3002</p><p>\u3000\u3000\u201c\u56fd\u5185\u7684\u4f01\u4e1a\u5bf9\u6b64\u6028\u58f0\u8f7d\u9053\uff0c\u201d\u7fdf\u4e39\u9752\u8bf4\u8d77\u53c2\u5c55\u56fd\u5185\u4f01\u4e1a\u7684\u60c5\u51b5\uff0c\u201c\u8fd8\u8ba1\u5212\u7740\u8054\u5408\u8d77\u6765\u627e\u7ba1\u7406\u90e8\u95e8\u8981\u8bf4\u6cd5\u2026\u2026\u201d</p><p>\u3000\u3000\u5c55\u533a\u5149\u7ebf\u4e5f\u4e0d\u5145\u8db3\uff0c\u4eba\u6d41\u91cf\u6781\u5c11\uff0c\u7ed9\u4e22\u5230\u8fd9\u4e2a\u89d2\u843d\u91cc\uff0c\u7684\u786e\u5f88\u4e0d\u516c\u5e73\uff0c\u867d\u8bf4\u9999\u6e2f\u5df2\u7ecf\u56de\u5f52\u6709\u4e00\u5e74\u591a\u65f6\u95f4\u4e86\uff0c\u79cd\u79cd\u7684\u504f\u89c1\uff0c\u8fd8\u662f\u5b58\u5728\u7684\u3002\u5f20\u606a\u8ddf\u7fdf\u4e39\u9752\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u9526\u6e56\u8d1f\u8d23\u5c55\u4f1a\u7684\u8c01\u6253\u4e2a\u7535\u8bdd\uff0c\u5c3d\u91cf\u7684\u63d0\u4f9b\u4e9b\u5e2e\u52a9\u3002\u201d</p><p>\u3000\u3000\u845b\u836b\u5747\u8fce\u9762\u8d70\u6765\uff0c\u4ed6\u5148\u770b\u5230\u5f20\u606a\u4ed6\u4eec\uff0c\u811a\u4e0b\u72b9\u8c6b\u7740\u522b\u8fc7\u5934\u53bb\u4e0e\u5f20\u606a\u4ed6\u4eec\u9519\u8eab\u800c\u8fc7\uff0c\u8fde\u4e0e\u5f20\u606a\u5bf9\u89c6\u7684\u52c7\u6c14\u90fd\u6ca1\u6709\u3002</p><p>\u3000\u3000\u9999\u6e2f\u79cb\u5b63\u7535\u5b50\u5c55\u66f4\u50cf\u662f\u8d38\u6613\u5c55\uff0c\u845b\u836b\u5747\u51fa\u73b0\u5728\u5185\u5730\u9986\u533a\u4e5f\u4e0d\u5947\u602a\uff0c\u4ed6\u8eb2\u7740\u8fd9\u8fb9\u8d70\u5f00\uff0c\u66f4\u4e0d\u5947\u602a\u4e86\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u56de\u5934\u770b\u4e86\u845b\u836b\u5747\u4e00\u773c\uff0c\u89c1\u4ed6\u52a0\u5feb\u6b65\u4f10\u4e0e\u968f\u884c\u4eba\u5458\u79bb\u5f00\u8fd9\u91cc\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u9999\u6e2f\u8bc1\u76d1\u90e8\u95e8\u6b63\u5f0f\u4ecb\u5165\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u8c03\u67e5\u4e5f\u6709\u51e0\u5929\u4e86\uff0c\u4f60\u4f30\u8ba1\u4f1a\u6709\u4e00\u4e2a\u4ec0\u4e48\u7ed3\u679c\uff1f\u201d</p><p>\u3000\u3000\u201c\u7f34\u7eb3\u7f5a\u91d1\u7b7e\u7f72\u548c\u89e3\u534f\u8bae\u6765\u7ec8\u6b62\u8bc1\u76d1\u90e8\u95e8\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u8c03\u67e5\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u7559\u7ed9\u4ed6\u4eec\u7684\u9009\u62e9\u4e5f\u4e0d\u591a\u554a\u2026\u2026\u4ed6\u4eec\u53bb\u5e74\u5e95\u5c06\u5b9e\u9645\u4e8f\u635f\u4e24\u4e2a\u4ebf\u7684\u79d1\u738b\u6d77\u5916\u4e1a\u52a1\u8f6c\u79fb\u7ed9\u4e0a\u5e02\u516c\u53f8\uff0c\u8be5\u4e1a\u52a1\u4eca\u5e74\u53c8\u6301\u7eed\u4e8f\u635f\u8d85\u8fc7\u4e00\u4ebf\uff0c\u5012\u662f\u4fdd\u8bc1\u4e86\u79d1\u738b\u4ece\u6d77\u5916\u4e1a\u52a1\u4e2d\u8d62\u5229\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u79c1\u4eba\u8981\u7f34\u7eb3\u7f5a\u91d1\u5927\u6982\u4e0d\u4f1a\u4f4e\u4e8e\u6b64\u6570\u7684\u4e24\u500d\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u7236\u4eb2\u4fe9\u5bb6\u5e95\u6709\u6ca1\u6709\u8fd9\u4e48\u539a\u5b9e\u2026\u2026\u201d</p><p>\u3000\u3000\u4e0d\u8bf4\u845b\u5bb6\u66fe\u7ecf\u7684\u8f89\u714c\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u540d\u4e0b\u7684\u8d44\u4ea7\u6700\u5dc5\u5cf0\u65f6\u8d85\u8fc7\u4e00\u767e\u4e94\u5341\u4ebf\uff0c\u5c31\u7b97\u6b64\u65f6\uff0c\u540d\u4e0b\u7684\u8d44\u4ea7\u4e5f\u4e0d\u4f1a\u4f4e\u4e8e\u56db\u5341\u4ebf\uff0c\u4f46\u662f\u8fd9\u56db\u5341\u4ebf\u8d44\u4ea7\u4e3b\u8981\u662f\u4ee5\u5609\u4fe1\u96c6\u56e2\u80a1\u6743\u8d44\u4ea7\u7684\u5f62\u5f0f\u5b58\u5728\u7684\uff0c\u5f20\u606a\u76f8\u4fe1\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u79c1\u4eba\u540d\u4e0b\u8fd8\u62e5\u6709\u7684\u8d44\u4ea7\u5e94\u8be5\u6781\u4e3a\u6709\u9650\u4e86\u3002\u5f20\u606a\u5fc3\u60f3\u81ea\u5df1\u4e5f\u7b97\u662f\u6709\u94b1\u4eba\u4e86\u2014\u2014\u56fd\u5185\u80fd\u6bd4\u4ed6\u8fd8\u8981\u6709\u94b1\u7684\u9690\u5f62\u5bcc\u8c6a\u4e00\u53ea\u624b\u7684\u624b\u6307\u7edd\u5bf9\u6570\u5f97\u8fc7\u6765\uff0c\u4f46\u662f\u4ed6\u540d\u4e0b\u7684\u8d44\u4ea7\u4e5f\u4e3b\u8981\u662f\u9526\u6e56\u7684\u80a1\u6743\u8d44\u4ea7\uff0c\u5c31\u7b97\u4ed6\u81ea\u5df1\u5f88\u559c\u6b22\u8f66\uff0c\u4e3a\u4e86\u907f\u7a0e\u4ee5\u53ca\u65b9\u4fbf\u7ba1\u7406\u7684\u7f18\u6545\uff0c\u6bcf\u5e74\u7ed9\u81ea\u5df1\u914d\u7684\u8c6a\u8f66\u4e5f\u90fd\u662f\u4ee5\u516c\u53f8\u7684\u540d\u4e49\u91c7\u8d2d\uff0c\u5728\u9526\u6e56\u4e4b\u5916\u7684\u79c1\u4eba\u8d44\u4ea7\uff0c\u62e2\u5171\u52a0\u8d77\u6765\uff0c\u53ef\u80fd\u4e5f\u5c31\u4e00\u4e24\u5343\u4e07\u800c\u5df2\u3002\u5f20\u606a\u4e5f\u7ecf\u5e38\u8ddf\u675c\u98de\u5f00\u73a9\u7b11\u4e0d\u8981\u627e\u4ed6\u76f4\u63a5\u501f\u94b1\uff0c\u56e0\u4e3a\u4ed6\u5f88\u591a\u65f6\u95f4\u76ae\u5939\u91cc\u90fd\u6ca1\u6709\u4e00\u5206\u94b1\u3002</p><p>\u3000\u3000\u79d1\u738b\u6d77\u5916\u516c\u53f8\u662f\u845b\u836b\u5747\u79c1\u4eba\u6ce8\u518c\u7684\u516c\u53f8\uff0c\u8981\u7f34\u7eb3\u7f5a\u91d1\uff0c\u4e5f\u53ea\u80fd\u662f\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u79c1\u4eba\u7f34\u7eb3\uff0c\u5982\u6b64\u5de8\u989d\u7684\u7f5a\u91d1\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u4e5f\u53ea\u6709\u51fa\u552e\u5609\u4fe1\u96c6\u56e2\u7684\u80a1\u6743\u624d\u80fd\u7b79\u96c6\uff0c\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u6700\u4e3b\u8981\u7684\u4e24\u5bb6\u4e0a\u5e02\u516c\u53f8\u4e5f\u662f\u5609\u4fe1\u96c6\u56e2\u7684\u6838\u5fc3\u8d44\u4ea7\u6b63\u503c\u98ce\u96e8\u98d8\u6447\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u6b64\u65f6\u60f3\u5957\u73b0\uff0c\u60f3\u8981\u5356\u4e2a\u597d\u4ef7\u4f4d\uff0c\u90a3\u771f\u662f\u96be\u4e8e\u4e0a\u9752\u5929\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6307\u6572\u4e86\u6572\u989d\u5934\uff0c\u5bf9\u7fdf\u4e39\u9752\u8bf4\u9053\uff1a\u201c\u60f3\u5fc5\u9999\u6e2f\u5e94\u8be5\u6709\u4e0d\u5c11\u4eba\u5bf9\u5609\u4fe1\u96c6\u56e2\u5b58\u6709\u89ca\u89ce\u4e4b\u5fc3\uff0c\u63a5\u4e0b\u6765\uff0c\u8be5\u662f\u4ed6\u4eec\u8868\u6f14\u7684\u65f6\u5019\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5e94\u8be5\u6709\u7684\u2026\u2026\u201d\u7fdf\u4e39\u9752\u7b11\u4e86\u7b11\uff0c\u5fc3\u60f3\uff1a\u5899\u5012\u4f17\u4eba\u63a8\uff0c\u845b\u5bb6\u7b97\u662f\u8981\u5f7b\u5e95\u7684\u6ca1\u843d\u4e86\uff0c\u53c8\u60f3\u5230\u8fd9\u4e48\u8bf4\u4e5f\u4e0d\u6070\u5f53\uff0c\u845b\u660e\u5fb7\u3001\u5b59\u5c1a\u4e49\u5e94\u8be5\u7b97\u662f\u845b\u5bb6\u7684\u65c1\u652f\uff0c\u5b59\u5c1a\u4e49\u660e\u91cc\u3001\u6697\u91cc\u7684\u8d44\u4ea7\u4e5f\u6709\u4e09\u56db\u5341\u4ebf\u4e86\u3002</p></article>"}}}]);
