(window.webpackJsonp=window.webpackJsonp||[]).push([[1147],{1170:function(p){p.exports={chapterTitle:null,title:"\u7b2c941\u7ae0 \u5fc3\u6025\u8981\u5403\u70ed\u8c46\u8150",content:"<article><h2>\u7b2c941\u7ae0 \u5fc3\u6025\u8981\u5403\u70ed\u8c46\u8150</h2><p>\u3000\u3000\u665a\u4e0a\u4e0e\u5fb7\u5c14\u6cd5\u897f\u7684\u665a\u9910\uff0c\u4e2d\u6676\u5fae\u82af\u7684\u603b\u88c1\u67f3\u5fd7\u6210\u4e5f\u4ece\u5efa\u90ba\u4e58\u98de\u673a\u4e13\u7a0b\u98de\u9999\u6e2f\u6765\u53c2\u52a0\uff0c\u518d\u52a0\u4e0a\u5fb7\u4eea\u524d\u4e2d\u56fd\u533a\u603b\u88c1\u9648\u4fe1\u751f\uff0c\u8fd8\u6709\u5c31\u662f\u5fb7\u4eea\u4e2d\u56fd\u533a\u73b0\u4efb\u603b\u88c1\u5468\u6b63\u9752\u3002\u5305\u53a2\u91cc\u5c31\u4e94\u4e2a\u4eba\uff0c\u6e05\u9759\u96c5\u81f4\uff0c\u9002\u4e8e\u4ea4\u8c08\u3002\u5510\u5a67\u4e0e\u7fdf\u4e39\u9752\u8fd8\u6709\u76db\u590f\u53bb\u901b\u9999\u6e2f\u591c\u665a\u7684\u8857\u5e02\u4e86\u3002</p><p>\u3000\u3000\u201c\u7b2c\u4e00\u676f\u9152\u8981\u4e3a\u4e2d\u6676\u5fae\u82af\u7684\u6210\u529f\u4e3e\u676f\u2026\u2026\u201d\u5fb7\u5c14\u6cd5\u897f\u4e0e\u9648\u4fe1\u751f\u76f8\u8bc6\u5341\u591a\u5e74\uff0c\u4e0e\u67f3\u5fd7\u6210\u4e5f\u65e9\u5c31\u8ba4\u8bc6\uff0c\u4e0e\u5f20\u606a\u4e5f\u4e0d\u751f\u5206\uff0c\u5728\u5305\u53a2\u91cc\uff0c\u5012\u6ca1\u6709\u5728\u6709\u4eba\u5728\u573a\u65f6\u7684\u795e\u60c5\u4e25\u8083\u3002</p><p>\u3000\u3000\u5fb7\u4e50\u6cd5\u897f\u7684\u4e2d\u6587\u5f88\u51fa\u8272\uff0c\u5728\u9910\u684c\u4e0a\u5927\u5bb6\u90fd\u9042\u4ed6\u7684\u610f\u62ff\u4e2d\u6587\u804a\u8c08\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u76840.35\u5fae\u7c73\u5236\u7a0b\u8bd5\u4ea7\u6210\u529f\uff0c\u5bf9\u56fd\u5185\u7684\u7535\u5b50\u4ea7\u4e1a\u6765\u8bf4\uff0c\u5177\u6709\u91cc\u7a0b\u7891\u5f0f\u7684\u610f\u4e49\uff0c\u8bd5\u751f\u4ea7\u7684\u82af\u7247\u826f\u54c1\u7387\u4e0e\u56fd\u9645\u65b0\u5efa\u6676\u5706\u5382\u76f8\u6bd4\uff0c\u5e76\u4e0d\u5dee\u591a\u5c11\uff0c\u8981\u8bf4\u4eca\u665a\u7684\u7b2c\u4e00\u676f\u9152\uff0c\u7684\u786e\u8981\u8fd9\u4e3e\u8d77\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u4e00\u773c\u67f3\u5fd7\u6210\uff0c\u9526\u6e56\u4e0e\u5fb7\u4eea\u540c\u4e3a\u4e2d\u6676\u5fae\u82af\u7684\u4e3b\u8981\u80a1\u4e1c\uff0c\u53ea\u4e0d\u662f\u9526\u6e56\u95f4\u63a5\u63a7\u80a1\u4e86\u4e2d\u6676\u5fae\u82af\u7f62\u4e86\uff0c\u5fb7\u5c14\u6cd5\u897f\u8fd9\u8bdd\u5012\u662f\u8981\u67f3\u5fd7\u6210\u6765\u5e94\u3002</p><p>\u3000\u3000\u201c\u6211\u521a\u5230\u9999\u6e2f\u5c31\u542c\u8bf4\u8fd9\u6b21\u7535\u5b50\u5c55\u4e0a\uff0c\u5fb7\u4eea\u5c55\u793a\u4e860.13\u5fae\u7c73\u5236\u7a0b\u5de5\u827a\u7684\u6210\u719f\u4ea7\u54c1\uff0c\u201d\u67f3\u5fd7\u6210\u95ee\u9053\uff0c\u201c\u90a3\u5fb7\u4eea\u5e94\u8be5\u5f88\u5feb\u5c31\u5c06\u4f1a\u8be5\u5236\u7a0b\u5de5\u827a\u7528\u4e8e\u751f\u4ea7\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ee5\u4e2d\u56fd\u7684\u6625\u8282\u8ba1\u7b97\uff0c\u5e94\u8be5\u662f\u5b89\u6392\u5728\u5e74\u540e\u2026\u2026\u201d\u5fb7\u5c14\u6cd5\u897f\u636e\u5b9e\u76f8\u544a\uff0c\u660e\u5929\u7684\u65b0\u95fb\u4f1a\u4e0a\u8981\u662f\u6709\u8bb0\u8005\u63d0\u53ca\u8fd9\u4e2a\u95ee\u9898\uff0c\u4ed6\u4e5f\u4f1a\u636e\u5b9e\u56de\u7b54\u3002</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u542c\u8bf4\u5fb7\u4eea\u6709\u65b0\u589e\u5de5\u5382\u7684\u8ba1\u5212\uff0c\u65b0\u6280\u672f\u4ea7\u54c1\u7684\u8ba2\u5355\u589e\u52a0\uff0c\u4e0d\u518d\u6269\u5f20\u5de5\u5382\u89c4\u6a21\u7684\u5fb7\u4eea\u662f\u5426\u8fd8\u4f1a\u4f9d\u60ef\u4f8b\u5c06\u4f4e\u6280\u672f\u6c34\u5e73\u7684\u4ea7\u54c1\u8ba2\u5355\u4ea4\u7ed9\u5176\u4ed6\u5382\u5546\u4ee3\u5de5\uff1f\u201d\u67f3\u5fd7\u6210\u95ee\u9053\u3002\u5fb7\u4eea\u7684\u4f4e\u6280\u672f\u6c34\u6765\uff0c\u5bf9\u4e2d\u6676\u5fae\u82af\u6765\u8bf4\uff0c\u5219\u662f\u66f4\u5148\u8fdb\u7684\u6280\u672f\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u8fd8\u771f\u96be\u4ee5\u56de\u7b54\uff0c\u6bd5\u7adf\u5f53\u524d\u91c7\u53d6\u65b0\u5de5\u827a\u7684\u82af\u7247\u4ea7\u54c1\u4e0d\u591a\u2026\u2026\u201d\u5fb7\u5c14\u6cd5\u897f\u6253\u4e86\u9a6c\u864e\u773c\u5c06\u95ee\u9898\u6643\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u65e9\u5c31\u90e8\u5206\u6dd8\u6c70\u4e860.35\u5fae\u7c73\u5236\u7a0b\u82af\u7247\u4ea7\u54c1\u7684\u751f\u4ea7\uff0c\u957f\u671f\u4ee5\u6765\uff0c\u53f0\u79ef\u7535\u3001\u4e16\u8054\u7535\u5b50\u90fd\u662f\u5fb7\u4eea\u76f8\u5173\u4ea7\u54c1\u7684\u4ee3\u5de5\u5382\u5546\u3002\u9648\u4fe1\u751f\u957f\u671f\u62c5\u4efb\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\uff0c\u4e5f\u5f88\u6e05\u695a\u5fb7\u4eea\u7684\u4f20\u7edf\uff1a\u63a5\u4e0b\u6765\u5fb7\u4eea\u5c31\u4f1a\u9010\u6e10\u7684\u5c060.25\u5fae\u7c73\u4ea7\u54c1\u4ea4\u7ed9\u522b\u7684\u5de5\u5382\u4ee3\u5de5\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5bf9\u5fb7\u4eea\u4e0b\u5c5e\u7684\u5de5\u5382\u6765\u8bf4\uff0c\u5c06\u4e0d\u7528\u518d\u8003\u8651\u57280.25\u5fae\u7c73\u5236\u7a0b\u5de5\u827a\u6c34\u5e73\u4e0a\u7684\u7ade\u4e89\u4e86\u2014\u2014\u4e2d\u6676\u5fae\u82af\u4ece\u5fb7\u4eea\u83b7\u5f970.25\u5fae\u7c73\u5236\u7a0b\u6280\u672f\u4e5f\u5c31\u6709\u4e86\u4e9b\u53ef\u80fd\uff0c\u518d\u8bf4\u5fb7\u4eea\u8fd8\u662f\u4e2d\u6676\u5fae\u82af\u7684\u4e3b\u8981\u80a1\u4e1c\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u521a\u8bd5\u4ea7\u6210\u529f\uff0c\u5c31\u8981\u76ef\u4e0a\u66f4\u5148\u8fdb\u7684\u5236\u7a0b\u6280\u672f\uff0c\u5728\u522b\u4eba\u773c\u91cc\uff0c\u4e5f\u8bb8\u4f1a\u89c9\u5f97\u6709\u4e9b\u6025\u8e81\u4e86\uff0c\u67f3\u5fd7\u6210\u5fc3\u91cc\u5374\u65e0\u6cd5\u5fd8\u8bb0\u8fd9\u6837\u7684\u4e8b\u5b9e\uff1a\u4ece0.35\u5fae\u7c73\u52300.13\u5fae\u7c73\uff0c\u4e2d\u95f4\u8fd8\u9694\u4e86\u6574\u6574\u4e24\u4ee3\u7684\u6280\u672f\u9e3f\u6c9f\u3001\u6574\u6574\u5341\u5e74\u7684\u5dee\u8ddd\uff0c\u54ea\u91cc\u5bb9\u5f97\u4ed6\u6162\u6162\u7684\u53d1\u5c55\uff1f\u9664\u4e86\u5fb7\u4eea\u4e4b\u5916\uff0c\u67f3\u5fd7\u6210\u8fd8\u5c06\u89c6\u91ce\u4e0e\u5e0c\u671b\u653e\u5728\u65b0\u52a0\u5761\u7684\u4e16\u65b0\u7535\u5b50\u8eab\u4e0a\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u53ef\u4ee5\u76f4\u63a5\u5c06\u5bf9\u4e2d\u6676\u5fae\u82af\u6765\u8bf4\u66f4\u5148\u8fdb\u7684\u5236\u7a0b\u5de5\u827a\u6388\u6743\u4e2d\u6676\u5fae\u82af\u4f7f\u7528\uff0c\u4e5f\u53ef\u80fd\u63a9\u8033\u76d7\u94c3\u5f0f\u7684\u4e0e\u4e2d\u6676\u5fae\u82af\u6210\u7acb\u5408\u8d44\u79d1\u7814\u516c\u53f8\u5f00\u53d1\u5bf9\u4e2d\u6676\u5fae\u82af\u6765\u8bf4\u66f4\u5148\u8fdb\u7684\u5236\u7a0b\u5de5\u827a\uff0c\u5fb7\u4eea\u4e5f\u53ef\u4ee5\u5c06\u90e8\u5206\u8ba2\u5355\u4ea4\u7ed9\u4e2d\u6676\u5fae\u82af\u7247\u6765\u5b8c\u6210\u3002</p><p>\u3000\u3000\u5fb7\u5c14\u6cd5\u897f\u53c8\u89c9\u5f97\u521a\u624d\u7684\u56de\u7b54\u8fc7\u4e8e\u76f4\u63a5\u4e86\uff0c\u6362\u4e86\u4e2a\u8bdd\u9898\uff0c\u4fa7\u8fc7\u5934\u8ddf\u5f20\u606a\u804a\u8d77\u6765\uff1a\u201c\u5fb7\u4eea\u7684\u7814\u53d1\u90e8\u95e8\u5458\u5de5\u5bf9\u9526\u6e56\u53ea\u7528\u4e0d\u5230\u534a\u5e74\u7684\u65f6\u95f4\u5c31\u6d88\u5316\u4e86\u4ece\u4e1c\u829d\u4ea4\u6362\u6765\u7684\u95ea\u5b58\u6280\u672f\u90fd\u76f8\u5f53\u7684\u60ca\u8bb6\uff0c\u4e0d\u7792\u4f60\u4eec\u8bf4\uff0c\u6211\u4e4b\u524d\u4e5f\u9884\u6599\u9526\u6e56\u4f1a\u665a\u5f88\u4e45\u2026\u2026\u201d\u518d\u4e00\u6b21\u8868\u8fbe\u4e86\u5bf9\u9526\u6e56\u6280\u672f\u529b\u91cf\u7684\u60ca\u8bb6\uff0c\u8fd9\u4e9b\u5e74\u53d7\u5230\u7684\u60ca\u8bb6\u5df2\u7ecf\u662f\u591f\u591a\u7684\u4e86\uff0c\u5c31\u7b97\u5173\u7cfb\u518d\u4eb2\u5bc6\uff0c\u5fb7\u5c14\u6cd5\u897f\u4e5f\u4e0d\u662f\u7279\u522b\u7684\u5e0c\u671b\u770b\u5230\u66f4\u591a\u7684\u60ca\u559c\u3002\u5c31\u50cf\u666e\u901a\u4eba\u5bf9\u5f85\u670b\u53cb\u7684\u6001\u5ea6\uff0c\u670b\u53cb\u7684\u5883\u51b5\u8981\u662f\u6bd4\u81ea\u5df1\u5dee\u5f88\u8fdc\uff0c\u5173\u7cfb\u5012\u662f\u80fd\u4eb2\u5bc6\u4e0b\u53bb\uff0c\u8981\u662f\u670b\u53cb\u7a81\u7136\u53d1\u4e86\u6a2a\u8d22\uff0c\u5f80\u5f80\u662f\u4ed6\u5fc3\u91cc\u6700\u522b\u626d\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u8fd9\u5b9e\u5728\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u6ca1\u6709\u66f4\u5148\u8fdb\u6676\u5706\u5236\u7a0b\u6280\u672f\u4e0a\u7684\u7ecf\u9a8c\uff0c\u6211\u4eec\u60f3\u5728\u95ea\u5b58\u6280\u672f\u7ee7\u7eed\u8fdb\u6b65\uff0c\u5219\u8981\u591a\u4e00\u4e9b\u8346\u68d8\u2014\u2014\u597d\u4e9b\u4eba\u90fd\u8dd1\u8fc7\u544a\u8bc9\u6211\uff0c\u8fd9\u4e9b\u90fd\u662f\u8981\u7b97\u878d\u4f1a\u8d2f\u901a\u7684\u827a\u672f\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u4f01\u4e1a\u4e0e\u4f01\u4e1a\u4e4b\u95f4\u662f\u6ca1\u6709\u53cb\u8c0a\u7684\uff0c\u4e0d\u8fc7\u5408\u4f5c\u4e60\u60ef\u4e86\uff0c\u4f1a\u6709\u4e00\u79cd\u60ef\u6027\u7684\u529b\u91cf\uff0c\u5c06\u5927\u5bb6\u90fd\u7275\u6d89\u5728\u4e00\u8d77\uff0c\u66f4\u4f55\u51b5\u5fb7\u4eea\u5728\u4e2d\u6676\u5fae\u82af\u4e5f\u6709\u4e00\u5b9a\u7684\u6743\u76ca\uff0c\u5f53\u521d\u5f15\u8fdb\u5fb7\u4eea\u6210\u4e3a\u4e2d\u6676\u5fae\u82af\u7684\u6218\u7565\u5408\u4f5c\u4f19\u4f34\u3001\u4e3b\u8981\u80a1\u4e1c\uff0c\u5c31\u662f\u770b\u4e2d\u5fb7\u4eea\u5728\u6676\u5706\u5236\u9020\u65b9\u9762\u7684\u6280\u672f\u5b9e\u529b\u3002</p><p>\u3000\u3000\u201c\u8fd9\u8fd8\u4e0d\u7b97\u4ec0\u4e48\u554a\uff1f\u201d\u5fb7\u5c14\u6cd5\u897f\u7b11\u7740\u8bf4\uff0c\u201c\u5468\u6b63\u9752\u90fd\u5728\u8ddf\u6211\u62b1\u6028\uff0c\u8bf4\u4e2d\u56fd\u5404\u5927\u9ad8\u6821\u7684\u6bd5\u4e1a\u751f\u90fd\u8981\u7ed9\u9526\u6e56\u62db\u8d70\u4e86\uff0c\u7279\u522b\u662f\u7855\u58eb\u751f\u3001\u535a\u58eb\u751f\u2026\u2026\u542c\u8bf4\u4f60\u4eec\u8fd8\u5c06\u89e6\u624b\u4f38\u5230\u9999\u6e2f\u7684\u51e0\u6240\u5927\u5b66\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u90a3\u4e48\u5938\u5f20\uff0c\u201d\u9648\u4fe1\u751f\u7b11\u7740\u8bf4\uff0c\u201c\u4e0e\u5916\u8d44\u4f01\u4e1a\u76f8\u6bd4\uff0c\u6211\u4eec\u9526\u6e56\u62db\u65b0\u5e76\u4e0d\u5360\u4f18\u52bf\u2026\u2026\u9999\u6e2f\u8fd9\u8fb9\uff0c\u4e5f\u662f\u53d7\u5230\u8bf7\u6258\u53bb\u8003\u5bdf\u51e0\u540d\u535a\u58eb\u751f\u7684\u80fd\u529b\uff0c\u6211\u4eba\u5728\u9999\u6e2f\uff0c\u5c31\u987a\u5229\u53c2\u52a0\u4e86\u8003\u5bdf\u5de5\u4f5c\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5fb7\u5c14\u6cd5\u897f\u7b11\u4e86\u7b11\uff0c\u80fd\u8ba9\u9648\u4fe1\u751f\u4eb2\u81ea\u51fa\u9762\u9762\u8bd5\uff0c\u8fd9\u51e0\u540d\u535a\u58eb\u751f\u8fd8\u662f\u5f88\u53d7\u91cd\u89c6\u7684\uff0c\u8bf4\u9053\uff0c\u201c\u81f3\u4e8e\u4e2d\u6676\u5fae\u82af\u5bf90.25\u5fae\u7c73\u7684\u5236\u7a0b\u6280\u672f\u611f\u5174\u8da3\uff0c\u6211\u4f1a\u5411\u5b89\u5409\u4f2f\u8c28\u614e\u63a8\u8350\u7684\u3002\u201d</p><p>\u3000\u3000\u4eca\u5929\u665a\u4e0a\u5c31\u7b49\u5fb7\u5c14\u6cd5\u897f\u8fd9\u53e5\u8bdd\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u6b27\u7f8e\uff0c\u7535\u5b50\u884c\u4e1a\u91cc\u4f18\u79c0\u7684\u79d1\u5b66\u5bb6\u3001\u5de5\u7a0b\u5e08\uff0c\u534e\u4eba\u5dee\u4e0d\u591a\u5360\u4e86\u56db\u5206\u4e4b\u4e00\uff0c\u9526\u6e56\u9664\u6b64\u8fd1\u51e0\u5e74\u6765\u5728\u96c6\u6210\u7535\u8def\u8bbe\u8ba1\u4e0a\u7d2f\u79ef\u4e86\u4e00\u4e9b\u5e95\u5b50\u4e4b\u5916\uff0c\u5728\u4e00\u5e74\u534a\u7684\u65f6\u95f4\u91cc\u6a61\u6811\u56ed\u7684\u201c\u767e\u4eba\u8ba1\u5212\u201d\u5b9e\u9645\u62db\u52df\u4e86\u8fd1\u4e09\u767e\u540d\u534e\u4eba\u79d1\u5b66\u5bb6\u3001\u4e2d\u9ad8\u7ea7\u5de5\u7a0b\u5e08\u5230\u56fd\u5185\u6765\u53c2\u52a0\u7814\u53d1\u5de5\u4f5c\uff0c\u8fd9\u8fd8\u4e0d\u5305\u62ec\u4e2d\u6676\u5fae\u82af\u65d7\u4e0b\u7531\u67f3\u5fd7\u6210\u76f4\u63a5\u4ece\u53f0\u79ef\u6781\u62c9\u51fa\u6765\u7684\u4e00\u767e\u591a\u4eba\u7684\u6280\u672f\u56e2\u961f\uff0c\u4e5f\u4e0d\u5305\u62ec\u65e5\u672c\u7b51\u6ce2\u5e02\u4e0e\u7f8e\u56fd\u52a0\u5229\u798f\u5c3c\u4e9a\u5ddeESS\u4e24\u5904\u5b9e\u9a8c\u57fa\u5730\u5438\u7eb3\u7684\u534e\u4eba\u79d1\u5b66\u5bb6\u4eec\uff0c\u4e5f\u4e0d\u5305\u62ec\u4e0e\u5fb7\u4eea\u3001\u4e0e\u65af\u9ad8\u67cf\u7b49\u516c\u53f8\u8054\u5408\u6210\u7acb\u7684\u6280\u672f\u7814\u7a76\u673a\u6784\u3002</p><p>\u3000\u3000\u5efa\u90ba\u7684\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u5df2\u7ecf\u5f62\u6210\u8d85\u8fc7\u5343\u4eba\u89c4\u6a21\u7684\u7814\u53d1\u56e2\u961f\uff0c\u52a0\u4e0a\u53cc\u5bfc\u5e08\u5236\u6784\u67b6\u4e0b\u8fdb\u5165\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u5b9e\u4e60\u3001\u5b9e\u9645\u53c2\u52a0\u7814\u53d1\u5de5\u4f5c\u7684\u5efa\u90ba\u5404\u9ad8\u6821\u7855\u58eb\u3001\u535a\u58eb\u751f\u89c4\u6a21\u4e5f\u63a5\u8fd1\u5343\u4eba\u3002</p><p>\u3000\u3000\u8fd9\u4fbf\u662f\u8ba9\u5fb7\u5c14\u6cd5\u897f\u4e5f\u8981\u60ca\u53f9\u4e00\u4e0b\u7684\u9526\u6e56\u6280\u672f\u56e2\u961f\u3002</p><p>\u3000\u3000\u5982\u6b64\u89c4\u6a21\u7684\u7814\u53d1\u9635\u8425\u5728\u4e5d\u516b\u5e74\u56fd\u5185\u5df2\u7ecf\u53ef\u4ee5\u8bf4\u662f\u5962\u534e\u4e86\uff0c\u5c31\u7b97\u4e2d\u79d1\u9662\u51e0\u5bb6\u5fae\u7535\u5b50\u6240\u4e13\u5bb6\u5b66\u8005\u805a\u96c6\u8d77\u6765\u90fd\u6ca1\u6709\u9526\u6e56\u7684\u6280\u672f\u9635\u8425\u5e9e\u5927\uff0c\u4f46\u6bd4\u8d77\u6d77\u5916\u7535\u5b50\u5de8\u5934\u52a8\u8f84\u4e0a\u4e07\u4eba\u7684\u7814\u53d1\u961f\u4f0d\uff0c\u6a61\u6811\u56ed\u8fd8\u771f\u6709\u4e9b\u4e0d\u591f\u770b\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e5d\u4e5d\u5e74\u5ea6\u7684\u4eba\u4e8b\u62db\u65b0\u8ba1\u5212\u5df2\u7ecf\u6b63\u5f0f\u542f\u52a8\u4e86\uff0c\u4ec5\u7231\u8fbe\u5c31\u8ba1\u5212\u62db\u8058\u56db\u5343\u540d\u5e94\u5c4a\u672c\u79d1\u3001\u7814\u7a76\u751f\u6bd5\u4e1a\u751f\uff0c\u8865\u5145\u751f\u4ea7\u90e8\u95e8\u3001\u884c\u653f\u7ba1\u7406\u90e8\u95e8\u7684\u65b0\u5458\u5de5\u4e0d\u8db3\u534a\u6570\uff0c\u5305\u62ec\u91d1\u5c71\u5206\u56ed\u3001\u676d\u5dde\u5206\u56ed\u7b49\u5206\u56ed\u5728\u5185\u7684\u6a61\u6811\u56ed\u7814\u53d1\u4f53\u7cfb\u5c06\u5438\u7eb3\u8d85\u8fc7\u4e24\u5343\u540d\u7684\u65b0\u7814\u53d1\u4eba\u5458\u3002\u53e6\u5916\u4e2d\u6676\u5fae\u82af\u7684\u7814\u53d1\u3001\u751f\u4ea7\u90e8\u95e8\u5f53\u524d\u7684\u5458\u5de5\u57fa\u6570\u4e4b\u4e0a\u8fd8\u8981\u62db\u52df\u516b\u767e\u540d\u65b0\u5458\u5de5\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\u56fd\u5185\u4e92\u8054\u7f51\u70ed\u6f6e\u4e5f\u6e10\u6e10\u5174\u8d77\uff0c\u5404\u5730\u90fd\u5728\u53d1\u5c55\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\uff0c\u4fe1\u606f\u7535\u5b50\u3001\u8ba1\u7b97\u673a\u7b49\u76f8\u5173\u4e13\u4e1a\u7684\u4eba\u624d\u5c24\u5176\u7684\u7d27\u7f3a\uff0c\u6708\u85aa\u4f4e\u4e8e\u4e00\u4e07\u7684\u5de5\u4f5c\u5bf9\u6e05\u534e\u7684\u8ba1\u7b97\u673a\u7cfb\u672c\u79d1\u6bd5\u4e1a\u751f\u5df2\u7ecf\u6ca1\u6709\u591a\u5c11\u5438\u5f15\u529b\u4e86\uff0c\u9526\u6e56\u7684\u4eba\u4e8b\u90e8\u95e8\u4e5f\u662f\u65e9\u65e9\u7684\u5c31\u63d0\u524d\u5230\u5317\u4eac\u3001\u6b66\u6c49\u3001\u676d\u5dde\u7b49\u91cd\u70b9\u9ad8\u6821\u53bb\u6492\u7f51\u62db\u65b0\u4e86\u3002\u4e0d\u8981\u8bf4\u77e5\u540d\u9662\u6821\u4ee5\u53ca\u5404\u5730\u7684\u90ae\u7535\u5b66\u9662\u7684\u5b66\u751f\uff0c\u4e5d\u516b\u5e74\u53ea\u8981\u662f\u8ddf\u4fe1\u606f\u7535\u5b50\u3001\u8ba1\u7b97\u673a\u6cbe\u8fb9\u7684\u4e13\u4e1a\uff0c\u54ea\u6015\u662f\u4e09\u6d41\u5927\u5b66\u3001\u5927\u5b66\u671f\u95f4\u662f\u7cca\u6df7\u6e21\u8fc7\u7684\u5b66\u751f\uff0c\u53ea\u8981\u80fd\u5bf9\u8ba1\u7b97\u673a\u672f\u8bed\u8fd8\u80fd\u80e1\u63b0\u51e0\u53e5\uff0c\u90fd\u80fd\u8f7b\u6613\u627e\u5230\u4e09\u56db\u5343\u4ee5\u4e0a\u6708\u85aa\u7684\u5de5\u4f5c\uff0c\u800c\u4e1c\u90e8\u6cbf\u6d77\u5730\u533a\uff0c\u5728\u5de5\u8d44\u7ecf\u8fc7\u8fd1\u5e74\u6765\u4e24\u6b21\u5927\u5e45\u5ea6\u4e0a\u8c03\u4e4b\u540e\uff0c\u5e73\u5747\u5de5\u8d44\u624d\u521a\u4e0a\u5343\u5143\u7684\u6c34\u5e73\u3002</p><p>\u3000\u3000\u5341\u5e74\u4e4b\u540e\u7684\u4fe1\u606f\u3001\u7535\u5b50\u7b49\u4e13\u4e1a\u7684\u5927\u5b66\u6bd5\u4e1a\u751f\u4eec\u662f\u5f88\u65e9\u60f3\u8c61\u4ed6\u4eec\u7684\u524d\u8f88\u662f\u4f55\u7b49\u7684\u98ce\u5149\uff0c\u800c\u4ed6\u4eec\u5374\u8fde\u4efd\u6bd4\u6c11\u5de5\u5de5\u8d44\u7a0d\u9ad8\u4e9b\u7684\u5de5\u4f5c\u90fd\u5f88\u56f0\u96be\u3002</p><p>\u3000\u3000\u5012\u662f\u5728\u5efa\u90ba\uff0c\u5728\u6a61\u6811\u56ed\u7814\u53d1\u57fa\u5730\u7684\u5927\u672c\u8425\u91cc\uff0c\u7531\u4e8e\u53bb\u5e74\u5c31\u4e0e\u5404\u9ad8\u6821\u76f8\u5173\u4e13\u4e1a\u8bd5\u884c\u4e86\u53cc\u5bfc\u5e08\u5236\uff0c\u80fd\u591f\u4fdd\u8bc1\u6700\u65e9\u4e00\u6279\u5728\u53cc\u5bfc\u5e08\u5236\u57f9\u8bad\u4e0b\u7684\u5e94\u5c4a\u6bd5\u4e1a\u751f\u91cc\u7edd\u5927\u591a\u6570\u4eba\u4f1a\u7559\u5728\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\uff0c\u800c\u4e14\u53cc\u5bfc\u5e08\u5236\u57f9\u517b\u7684\u4eba\u624d\u5728\u5b9e\u9645\u7684\u7814\u53d1\u5de5\u4f5c\u4e2d\u5df2\u7ecf\u79ef\u6781\u4e86\u4e00\u5b9a\u7684\u7ecf\u9a8c\uff0c\u800c\u4e14\u6a61\u6811\u56ed\u8fd9\u8fb9\u7ed9\u8fd9\u4e9b\u5b66\u751f\u914d\u5907\u7684\u5bfc\u5e08\u90fd\u662f\u80fd\u529b\u3001\u7ecf\u9a8c\u3001\u5b66\u8bc6\u90fd\u6781\u4e30\u5bcc\u7684\u79d1\u5b66\u5bb6\u3001\u4e2d\u9ad8\u7ea7\u5de5\u7a0b\u5e08\uff0c\u6bd4\u56fd\u5185\u5176\u4ed6\u9ad8\u6821\u5728\u5f53\u524d\u6559\u80b2\u4f53\u5236\u4e0b\u57f9\u517b\u7684\u5e94\u5c4a\u6bd5\u4e1a\u751f\u7814\u53d1\u6c34\u51c6\u8981\u9ad8\u51fa\u8bb8\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u9999\u6e2f\u56de\u5efa\u90ba\uff0c\u5341\u4e00\u6708\u5341\u4e8c\u65e5\u6b63\u662f\u5efa\u90ba\u4e3a\u5404\u9ad8\u6821\u5e94\u5c4a\u6bd5\u4e1a\u751f\u5c31\u4e1a\u4e3e\u529e\u5927\u578b\u62db\u8058\u4f1a\u2014\u2014\u4eca\u5e74\u5404\u5927\u9ad8\u6821\u90fd\u5b9e\u73b0\u5b8c\u5168\u5e76\u8f6e\uff0c\u9664\u4e86\u5e08\u8303\u3001\u519c\u3001\u6797\u3001\u7267\u7b49\u6709\u9650\u7684\u4e13\u4e1a\u4e4b\u5916\uff0c\u7edd\u5927\u591a\u6570\u7684\u6bd5\u4e1a\u751f\u90fd\u7ed9\u8e22\u5230\u4eba\u624d\u5e02\u573a\u81ea\u5df1\u627e\u5de5\u4f5c\u53bb\uff0c\u5e94\u5c4a\u751f\u5927\u578b\u62db\u8058\u4f1a\u5728\u5efa\u90ba\u8fd8\u662f\u9996\u6b21\u4e3e\u529e\uff0c\u624d\u521d\u6b65\u6269\u62db\u7684\u5927\u5b66\u751f\u4eec\u90fd\u8fd8\u6ca1\u6709\u6bd5\u4e1a\uff0c\u8fd8\u8fdc\u8fdc\u6bd4\u4e0d\u4e0a\u5341\u5e74\u4e4b\u540e\u4e8c\u4e09\u5341\u4e07\u4eba\u6324\u7206\u62db\u8058\u4f1a\u73b0\u573a\u94c1\u95e8\u7684\u70ed\u95f9\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u53cc\u5bfc\u5e08\u5236\u53ea\u9650\u4e8e\u76f8\u5173\u4e13\u4e1a\u7684\u7855\u58eb\u3001\u535a\u58eb\u7814\u7a76\u751f\uff0c\u5efa\u90ba\u5404\u9ad8\u6821\u672c\u79d1\u9636\u6bb5\u7684\u8ba1\u7b97\u673a\u3001\u4fe1\u606f\u7535\u5b50\u7b49\u4e13\u4e1a\u7684\u6bd5\u4e1a\u751f\u8fd8\u662f\u5404\u5927\u7535\u5b50\u4f01\u4e1a\u4e89\u593a\u7684\u5bf9\u8c61\uff0c\u5f20\u606a\u56de\u5230\u5efa\u90ba\u540e\uff0c\u4e0d\u4f1a\u7279\u610f\u53bb\u51d1\u62db\u8058\u5927\u4f1a\u7684\u70ed\u95f9\uff0c\u8d76\u7740\u7b2c\u4e8c\u5929\u5468\u65e5\uff0c\u8bb8\u591a\u4f01\u4e8b\u4e1a\u5355\u4f4d\u5b9a\u70b9\u5230\u4e1c\u5927\u6821\u5185\u4e3e\u529e\u4e00\u4e2a\u4e2d\u7b49\u89c4\u6a21\u7684\u62db\u8058\u4f1a\uff0c\u5730\u70b9\u662f\u5728\u5408\u6b22\u5c71\u897f\u8fb9\u7684\u6821\u4f53\u80b2\u9986\u5185\uff0c\u5f20\u606a\u5341\u4e09\u65e5\u4e2d\u5348\u65f6\u5206\u4ece\u56fe\u4e66\u9986\u9605\u89c8\u5ba4\u51fa\u6765\uff0c\u5012\u662f\u9047\u5230\u4e00\u4e2a\u610f\u60f3\u4e0d\u5230\u7684\u4eba\u4e5f\u53c2\u52a0\u4e1c\u5927\u7684\u62db\u8058\u4f1a\u627e\u5de5\u4f5c\u3002</p></article>"}}}]);
