(window.webpackJsonp=window.webpackJsonp||[]).push([[1148],{1171:function(p){p.exports={chapterTitle:null,title:"\u7b2c1145\u7ae0 \u821e\u66f2\u9189\u4eba\u5fc3",content:"<article><h2>\u7b2c1145\u7ae0 \u821e\u66f2\u9189\u4eba\u5fc3</h2><p>\u3000\u3000\u5c06\u675c\u98de\u3001\u76db\u590f\u9001\u56de\u71d5\u56ed\u522b\u5885\uff0c\u5f20\u606a\u4e0e\u674e\u99a8\u4e88\u5728\u4e1c\u5927\u5357\u95e8\u4e0b\u4e86\u51fa\u79df\u8f66\uff0c\u6253\u7b97\u7a7f\u8fc7\u6821\u56ed\u8d70\u56de\u5230\u9752\u5e74\u516c\u5bd3\u53bb\u3002</p><p>\u3000\u3000\u6b63\u503c\u96e8\u5b63\uff0c\u5c06\u665a\u65f6\u8fd8\u4f59\u6656\u707f\u70c2\uff0c\u8fd9\u4f1a\u513f\u5929\u7a7a\u5374\u7b3c\u7f69\u7740\u8584\u4e91\u3002\u62ac\u5934\u671b\u5929\u900f\u7740\u5fae\u660e\uff0c\u80fd\u611f\u89c9\u5230\u4e91\u5c42\u5f88\u8584\uff0c\u8fd8\u4ee5\u4e3a\u8fd9\u4e48\u8584\u7684\u4e91\u5c42\u4e0d\u4f1a\u6709\u4ec0\u4e48\u96e8\uff0c\u6cbf\u7740\u4e24\u4fa7\u90fd\u662f\u9ad8\u5927\u6749\u6728\u7684\u6c34\u6ce5\u752c\u9053\u5f80\u524d\u8d70\u3001\u5c06\u8fd1\u6559\u804c\u5de5\u6d3b\u52a8\u4e2d\u5fc3\u65f6\uff0c\u96e8\u70b9\u5c31\u7838\u843d\u4e0b\u6765\u3002</p><p>\u3000\u3000\u8d77\u521d\u53ea\u662f\u51e0\u70b9\uff0c\u8f6c\u773c\u95f4\u96e8\u52bf\u5c31\u6025\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u4e0e\u674e\u99a8\u4e88\u6e9c\u5230\u6559\u804c\u5de5\u6d3b\u52a8\u4e2d\u5fc3\u7684\u8d70\u5eca\u91cc\uff0c\u96e8\u6a90\u4e0a\u90fd\u6302\u51fa\u6c34\u5e18\u6765\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u7a7f\u7740\u7d20\u8272\u6d45\u683c\u7eb9\u7684\u4e2d\u8896\u886c\u886b\uff0c\u6dcb\u4e86\u96e8\uff0c\u5728\u660e\u4eae\u7684\u5eca\u706f\u6709\u4e9b\u6e7f\u900f\uff0c\u663e\u9732\u51fa\u5979\u8ff7\u4eba\u7684\u66f2\u7ebf\u6765\uff0c\u5979\u634b\u8d77\u6e7f\u8d34\u5728\u989d\u5934\u7684\u4e4c\u9ed1\uff0c\u64a9\u5230\u8033\u540e\uff0c\u9732\u51fa\u6e05\u7eaf\u7cbe\u81f4\u7684\u9762\u5bb9\u6765\uff0c\u5eca\u706f\u7167\u5728\u5979\u7684\u8138\uff0c\u6d01\u767d\u5254\u900f\u5f97\u50cf\u4e00\u6367\u96ea\uff0c\u4e94\u5b98\u8ff7\u4eba\u800c\u751f\u52a8\u3002\u5979\u8e2e\u7740\u811a\u4fa7\u51fa\u8eab\u5b50\u62ac\u5934\u770b\u4e86\u770b\u5929\uff0c\u8584\u4e91\u8fd8\u662f\u900f\u7740\u4e9b\u5fae\u660e\uff0c\u4f3c\u4e4e\u661f\u6708\u5c31\u85cf\u5728\u8584\u4e91\u4e4b\u540e\uff0c\u4e5f\u4e0d\u6653\u5f97\u8fd9\u96e8\u8981\u957f\u591a\u4e45\uff0c\u4e0e\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u65e9\u77e5\u9053\u76f4\u63a5\u5750\u8f66\u5230\u516c\u5bd3\u697c\u524d\u4e86\uff0c\u4f60\u770b\u770b\uff0c\u8fd9\u4e0b\u5b50\u7ed9\u56f0\u5728\u8fd9\u91cc\u4e86\u5427\u3002\u201d\u5979\u5728\u5efa\u90ba\u751f\u6d3b\u4e86\u8fd9\u4e48\u4e45\uff0c\u666e\u901a\u8bed\u8bf4\u5f97\u8154\u5706\u5b57\u6b63\uff0c\u8fd8\u6709\u4e00\u79cd\u5a07\u5a9a\u7684\u5706\u6da6\uff0c\u542c\u7740\u4eba\u9aa8\u5b50\u53d1\u9165\uff0c\u5979\u867d\u7136\u8fd9\u4e48\u8bf4\u7740\uff0c\u770b\u5979\u7684\u795e\u8272\uff0c\u5bf9\u8fd9\u573a\u96e8\u7684\u5230\u6765\u4f3c\u4e4e\u9887\u4e3a\u5174\u594b\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u6211\u4eec\u5728\u96e8\u91cc\u6e9c\u56de\u53bb\uff1f\u201d\u5f20\u606a\u7b11\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u624d\u4e0d\u8981\uff0c\u8fd9\u4e48\u5927\u7684\u96e8\uff0c\u8863\u886b\u4f1a\u6e7f\u900f\u4e86\u3002\u201d\u674e\u99a8\u4e88\u8f7b\u58f0\u8bf4\u9053\u3002</p><p>\u3000\u3000\u597d\u51e0\u5bf9\u60c5\u4fa3\u501f\u7740\u591c\u8272\u7684\u63a9\u9970\u5728\u9644\u8fd1\u8c08\u604b\u7231\uff0c\u8fd9\u4f1a\u513f\u90fd\u5230\u8d70\u5eca\u4e0a\u907f\u96e8\u3002\u867d\u8bf4\u6709\u5fc3\u4e0a\u4eba\u5728\u8eab\u8fb9\uff0c\u90a3\u4e9b\u7537\u5b69\u5b50\u7684\u773c\u775b\u8fd8\u662f\u60c5\u4e0d\u81ea\u7981\u7684\u5f80\u5929\u59ff\u5a07\u5a9a\u7684\u674e\u99a8\u4e88\u8eab\u4e0a\u7785\uff0c\u6068\u4e0d\u5f97\u5c06\u8eab\u8fb9\u4eba\u4e00\u9524\u5b50\u7838\u6210\u8089\u6cab\u4e22\u4e0b\u6c34\u9053\u51b2\u6389\uff0c\u5fc3\u60f3\u5929\u4e0b\u771f\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u770b\u7740\u674e\u99a8\u4e88\u65c1\u8fb9\u7684\u5c0f\u767d\u8138\u5f20\u606a\uff0c\u8863\u7740\u4e0d\u51e1\u3001\u76f8\u8c8c\u82f1\u4fca\u5012\u662f\u5176\u6b21\uff0c\u90a3\u4efd\u795e\u95f2\u6c14\u5b9a\u7684\u6c14\u5ea6\u8ba9\u65c1\u4eba\u81ea\u60ed\u5f62\u79fd\uff1a\u81ea\u5df1\u771f\u662f\u4e0d\u914d\u505a\u8fd9\u5c0f\u767d\u8138\u7684\u60c5\u654c\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u53ea\u662f\u8138\u671d\u5411\u5f20\u606a\uff0c\u4e0d\u7406\u4f1a\u90a3\u4e9b\u60ca\u7fa1\u6155\u8273\u7684\u773c\u795e\uff0c\u5979\u89c1\u5f20\u606a\u7684\u76ee\u5149\u6709\u4e9b\u5f02\u6837\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u8fd9\u624d\u53d1\u73b0\u4e00\u7c92\u886c\u886b\u6263\u5b50\u521a\u624d\u8dd1\u52a8\u65f6\u4e0d\u6ce8\u610f\u7ef7\u65ad\u4e86\uff0c\u4e24\u4eba\u6328\u5f97\u8fd1\uff0c\u5f20\u606a\u53c8\u6bd4\u5979\u7a0d\u9ad8\u4e00\u4e9b\uff0c\u5979\u81ea\u5df1\u80fd\u770b\u5230\u90a3\u96ea\u767d\u4f3c\u7389\u7684\u4e73\u6c9f\u6df1\u58d1\uff0c\u5f20\u606a\u7684\u76ee\u5149\u81ea\u7136\u80fd\u4e5f\u80fd\u94bb\u66f4\u6df1\u2026\u2026\u4e0b\u610f\u8bc6\u7684\u6293\u4f4f\u886c\u8863\u9886\u5b50\u5c06\u90a3\u5904\u7edd\u5999\u65e0\u6bd4\u7684\u98ce\u5149\u906e\u4f4f\uff0c\u8eab\u4e0a\u4e5f\u662f\u7f9e\u5f97\u53d1\u70eb\uff0c\u81ea\u5df1\u90fd\u80fd\u611f\u89c9\u8eab\u4e0a\u7684\u808c\u80a4\u90fd\u7f9e\u7ea2\u4e86\uff0c\u58ee\u4e2a\u80c6\u5b50\u773c\u5e18\u64a9\u8d77\u6765\u7684\u770b\u5f20\u606a\u4e00\u773c\uff1a\u5f20\u606a\u6b63\u6536\u655b\u773c\u795e\u770b\u7740\u8d70\u5eca\u5916\u7684\u96e8\u6ef4\u3002</p><p>\u3000\u3000\u5c31\u7b97\u7ef7\u65ad\u6389\u4e00\u7c92\u6263\u5b50\uff0c\u4e5f\u624d\u89e3\u5f00\u4e24\u7c92\u6263\u5b50\uff0c\u53ea\u662f\u80f8\u90e8\u9ad8\u8038\uff0c\u5374\u5c24\u5176\u7684\u663e\u5f97\u6027\u611f\u88f8\u9732\uff0c\u674e\u99a8\u4e88\u514b\u5236\u4f4f\u5fc3\u91cc\u7684\u7f9e\u6da9\uff0c\u653e\u4e0b\u624b\u6765\uff0c\u9732\u51fa\u90a3\u8ff7\u4eba\u7684\u4e73\u6c9f\uff0c\u5c0f\u9e1f\u4f9d\u4eba\u7684\u7ad9\u5728\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u770b\u5230\u5f20\u606a\u53c8\u5077\u5077\u7684\u6765\u7785\u4e00\u773c\uff0c\u5fc3\u91cc\u867d\u7136\u7f9e\u6da9\uff0c\u5374\u4e5f\u6709\u6697\u6697\u7684\u5174\u594b\u4e0e\u5f97\u610f\u3002</p><p>\u3000\u3000\u6559\u804c\u5de5\u6d3b\u52a8\u4e2d\u5fc3\u662f\u680b\u4eff\u53e4\u98ce\u683c\u7684\u5ead\u9662\u5efa\u7b51\uff0c\u56de\u5eca\u66f2\u6298\uff0c\u5ead\u9662\u7cbe\u81f4\uff0c\u91cc\u9762\u8fd8\u6709\u97f3\u4e50\u4f20\u51fa\u6765\uff0c\u4e00\u8d77\u907f\u96e8\u7684\u51e0\u5bf9\u60c5\u4fa3\u89c1\u96e8\u4e00\u65f6\u505c\u4e0d\u4e86\uff0c\u4e5f\u4e0d\u7126\u6025\uff0c\u53cd\u800c\u7275\u624b\u5f80\u91cc\u9762\u8d70\u53bb\uff0c\u674e\u99a8\u4e88\u7591\u60d1\u7684\u770b\u7740\u5f20\u606a\uff0c\u5979\u5bf9\u4e1c\u5927\u4e0d\u751a\u719f\u6089\uff0c\u751a\u81f3\u4e0d\u77e5\u9053\u8fd9\u5904\u5730\u65b9\u53eb\u4ec0\u4e48\u540d\u5b57\uff1a\u201c\u91cc\u9762\u4ec0\u4e48\u5730\u65b9\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e00\u65f6\u534a\u4f1a\u513f\u4e5f\u8d70\u4e0d\u4e86\uff0c\u8bf7\u4f60\u8df3\u821e\u5427\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u8fd9\u91cc\u9762\u7adf\u7136\u662f\u821e\u5385\uff1f\u201d\u674e\u99a8\u4e88\u5fae\u5e26\u7740\u60ca\u8bb6\u7684\u95ee\u9053\uff0c\u8fd8\u4e0d\u597d\u610f\u601d\u76f4\u63a5\u633d\u5f20\u606a\u7684\u80f3\u818a\uff0c\u8f7b\u8f7b\u62fd\u7740\u4ed6\u7684\u8863\u8896\uff0c\u8dc3\u8dc3\u6b32\u8bd5\u7684\u8ddf\u7740\u5f20\u606a\u5f80\u91cc\u8d70\u53bb\u3002</p><p>\u3000\u3000\u867d\u8bf4\u662f\u6559\u804c\u5de5\u6d3b\u52a8\u4e2d\u5fc3\uff0c\u5374\u4e0d\u77e5\u4f55\u65f6\u7ed9\u627f\u5305\u4e86\u6210\u4e3a\u5b66\u751f\u821e\u5385\uff0c\u4e24\u4eba\u7684\u95e8\u7968\u4e5f\u53ea\u9700\u8981\u516d\u5143\u94b1\u800c\u5df2\u3002</p><p>\u3000\u3000\u91cc\u9762\u591a\u662f\u5b66\u751f\u7537\u5973\uff0c\u4e5f\u4fdd\u4e0d\u5b9a\u6709\u5916\u9762\u6df7\u8fdb\u6765\u9a97\u5b66\u751f\u59b9\u7684\u9752\u5e74\uff0c\u65f6\u5b66\u658c\u4ed6\u4eec\u6bd5\u4e1a\u540e\u867d\u7136\u4e00\u6b65\u8de8\u8fdb\u91d1\u9886\u9636\u5c42\u4e5f\u559c\u6b22\u5f80\u8fd9\u91cc\u6e9c\u3002\u4e1c\u5927\u53f7\u79f0\u5e05\u54e5\u591a\uff0c\u5e08\u5927\u3001\u97f3\u4e50\u5b66\u9662\u4ee5\u53ca\u9644\u8fd1\u9ad8\u4e2d\u91cc\u6027\u5b50\u91ce\u7684\u5973\u5b69\u5b50\u4e5f\u559c\u6b22\u6e9c\u8fdb\u6765\u73a9\u3002\u7531\u4e8e\u5728\u5b66\u6821\u91cc\uff0c\u4e5f\u4e0d\u6709\u793e\u4f1a\u821e\u5385\u90a3\u4e48\u591a\u4e4c\u4e03\u516b\u7cdf\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5149\u7ebf\u5e7d\u6697\uff0c\u7a0d\u8fdc\u4e00\u4e9b\u5c31\u770b\u4e0d\u6e05\u4eba\u7684\u8138\uff0c\u8fd9\u65f6\u5019\u821e\u6c60\u91cc\u4e00\u5927\u7fa4\u4eba\u6b63\u5728\u8df3\u4e24\u5343\u5e74\u6b63\u6d41\u884c\u7684\u5154\u5b50\u821e\uff0c\u4e00\u4e2a\u63a5\u4e00\u4e2a\u642d\u80a9\u4e32\u5728\u4e00\u8d77\uff0c\u8e66\u8e66\u8df3\u8df3\u7684\u751a\u662f\u70ed\u95f9\u3002\u97f3\u54cd\u4e5f\u8bf4\u4e0d\u4e0a\u597d\uff0c\u821e\u5385\u4e2d\u592e\u7684\u65cb\u8f6c\u706f\u4e5f\u4e0d\u662f\u7279\u522b\u7684\u534e\u4e3d\u3002\u5f20\u606a\u4e0e\u674e\u99a8\u4e88\u521a\u8d70\u5230\u91cc\u4fa7\u7684\u957f\u51f3\u4e0a\u5750\u4e0b\u6765\uff0c\u5154\u5b50\u821e\u7684\u821e\u66f2\u5c31\u7ed3\u675f\u4e86\uff0c\u674e\u99a8\u4e88\u7ad9\u8d77\u6765\uff0c\u663e\u5f97\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u6216\u8bb8\u662f\u5728\u6697\u5904\uff0c\u5979\u5bb9\u6613\u7f9e\u6da9\u7684\u6027\u5b50\u5f97\u5230\u63a9\u9970\uff0c\u53d8\u5f97\u66f4\u79ef\u6781\u4e3b\u52a8\u8d77\u6765\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u534e\u5c14\u5179\u7684\u821e\u66f2\u54cd\u8d77\u6765\uff0c\u5f20\u606a\u62ac\u8d77\u5934\u770b\u7740\u5979\u5728\u6d6e\u5e7d\u4e2d\u50cf\u661f\u8fb0\u7684\u7738\u5b50\uff0c\u7275\u8fc7\u5979\u6ed1\u82e5\u51dd\u8102\u7684\u5c0f\u624b\u7ad9\u8d77\u6765\uff1a\u201c\u6211\u4eec\u6765\u8df3\u821e\uff01\u201d\u6258\u8d77\u5979\u89e6\u624b\u8f6f\u5f39\u7684\u7ec6\u8170\uff0c\u60a0\u7136\u6ed1\u8fdb\u821e\u6c60\u7fe9\u7fe9\u8d77\u821e\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u821e\u59ff\u8f7b\u76c8\u5a34\u719f\uff0c\u4f46\u662f\u5b66\u751f\u821e\u5385\u91cc\u7684\u65b0\u624b\u592a\u591a\uff0c\u6ca1\u8df3\u4e24\u6b65\uff0c\u5c31\u8ddf\u4eba\u78b0\u649e\u4e86\u4e00\u4e0b\u3002\u78b0\u649e\u4e5f\u6ca1\u6709\u591a\u91cd\uff0c\u53ea\u662f\u5979\u521a\u4e0a\u573a\u65e2\u5174\u594b\u4e5f\u6709\u4e9b\u62d8\u8c28\uff0c\u78b0\u4e86\u4e00\u4e0b\uff0c\u53c8\u8e29\u540e\u9762\u4eba\u7684\u811a\uff0c\u4e0b\u610f\u8bc6\u8eab\u5b50\u5f80\u524d\u95ea\uff0c\u6574\u4e2a\u6251\u5230\u5f20\u606a\u7684\u6000\u91cc\u3002</p><p>\u3000\u3000\u867d\u7136\u662f\u8eab\u4f53\u7a0d\u63a5\u89e6\u5c31\u5206\u5f00\uff0c\u674e\u99a8\u4e88\u5374\u77e5\u9053\u81ea\u5df1\u9ad8\u8038\u7684\u80f8\u90e8\u76f4\u63a5\u4e0e\u5f20\u606a\u649e\u5728\u4e00\u8d77\uff0c\u81ea\u5df1\u90fd\u80fd\u611f\u89c9\u5230\u76f8\u649e\u65f6\u7684\u7f13\u51b2\u5f39\u529b\uff0c\u5206\u5f00\u540e\u90a3\u91cc\u8fd8\u6709\u4e9b\u5fae\u5fae\u7684\u9165\u9ebb\uff0c\u5c0f\u624b\u7ed9\u5f20\u606a\u7684\u5de6\u624b\u63e1\u7740\uff0c\u8170\u80a2\u4e0a\u8fd8\u642d\u7740\u5f20\u606a\u7684\u5bbd\u539a\u6e29\u70ed\u7684\u624b\u638c\uff0c\u5979\u7684\u5fc3\u810f\u4e0d\u4e89\u6c14\u7684\u4e71\u8df3\u8d77\u6765\uff0c\u4e5f\u6ca1\u6709\u52c7\u6c14\u62ac\u5934\u770b\u5f20\u606a\u4ec0\u4e48\u8868\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u662f\u6068\u4e0d\u5f97\u518d\u7ed9\u649e\u4e00\u5343\u4e0b\u3001\u4e00\u4e07\u4e0b\uff0c\u90a3\u649e\u4e0a\u6765\u65f6\u7684\u7f13\u51b2\u5f39\u529b\uff0c\u8ba9\u4eba\u5fc3\u9b42\u6da4\u8361\uff0c\u624b\u91cc\u6402\u7740\u5979\u89e6\u624b\u5f39\u8f6f\u7684\u7ec6\u8170\uff0c\u6709\u7740\u60f3\u5c06\u5979\u62c9\u6000\u91cc\u62d6\u7684\u51b2\u52a8\uff0c\u773c\u5e18\u4f4e\u5782\uff0c\u770b\u7740\u5979\u7f8e\u4e3d\u52a8\u4eba\u7684\u8138\u5e9e\uff0c\u957f\u957f\u6311\u8d77\u7684\u776b\u6bdb\u4e0e\u79c0\u76f4\u7684\u9f3b\u6881\u4ee5\u53ca\u5fae\u655e\u5f00\u9886\u53e3\u91cc\u9732\u51fa\u7684\u4e73\u6c9f\uff0c\u5ae9\u817b\u5982\u7389\uff0c\u6709\u7740\u8bf4\u4e0d\u51fa\u7684\u8bf1\u4eba\u6ecb\u5473\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u821e\u59ff\u76f8\u5f53\u4e0d\u9519\uff0c\u53ea\u662f\u7ed9\u78b0\u4e86\u4e00\u4e0b\uff0c\u80c6\u5b50\u53d8\u5c0f\u4e86\uff0c\u53ea\u8f7b\u8f7b\u7684\u8ddf\u7740\u821e\u66f2\u8f7b\u52a8\uff0c\u4e5f\u4e0d\u4f1a\u5c06\u53f3\u624b\u6309\u7167\u6807\u51c6\u821e\u59ff\u4f38\u51fa\u6765\uff0c\u677e\u4e0b\u6765\u653e\u5728\u8eab\u4fa7\uff0c\u4e24\u53ea\u624b\u63e1\u5728\u4e00\u8d77\uff0c\u5c31\u50cf\u5728\u821e\u6c60\u91cc\u8f7b\u76c8\u6f2b\u6b65\u7684\u60c5\u4fa3\u3002</p><p>\u3000\u3000\u4e00\u66f2\u7ec8\u4e86\uff0c\u674e\u99a8\u4e88\u624d\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u5c06\u5c0f\u624b\u62bd\u56de\u6765\uff0c\u5728\u821e\u6c60\u4e2d\u592e\u7b49\u4e0b\u4e00\u9996\u821e\u66f2\u54cd\u8d77\u3002</p><p>\u3000\u3000\u201c\u4f60\u8df3\u821e\u771f\u4e0d\u9519\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u201d\u674e\u99a8\u4e88\u7738\u5149\u7075\u52a8\uff0c\u5076\u5c14\u6709\u706f\u5149\u65cb\u8f6c\u800c\u8fc7\u626b\u5728\u5979\u5a07\u5a9a\u6e05\u7eaf\u7684\u8138\u4e0a\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5c0f\u65f6\u5019\u5b66\u8fc7\u821e\u8e0f\uff0c\u4e0d\u8fc7\u4ea4\u9645\u821e\u8fd8\u662f\u8ddf\u6211\u7236\u4eb2\u5b66\u7684\uff0c\u6bcf\u6b21\u5bb6\u91cc\u5bb4\u4f1a\u8fc7\u540e\u8981\u6709\u821e\u4f1a\uff0c\u7236\u4eb2\u90fd\u8981\u6559\u6211\u8df3\u821e\u2026\u2026\u4f60\u8df3\u821e\u4e5f\u5f88\u597d\u554a\u3002\u201d</p><p>\u3000\u3000\u97f3\u4e50\u58f0\u53c8\u54cd\u8d77\u6765\uff0c\u662f\u6162\u6b65\u821e\u66f2\uff0c\u53ea\u662f\u521d\u65f6\u6ca1\u6709\u542c\u5230\u8282\u594f\u70b9\uff0c\u65c1\u8fb9\u6709\u5bf9\u7537\u5973\uff0c\u5973\u7684\u53cc\u81c2\u73af\u7ed5\u7740\u7537\u7684\u9888\u90e8\uff0c\u7537\u7684\u6402\u7740\u5973\u7684\u8170\u80a2\uff0c\u4e24\u4eba\u7684\u8138\u8d34\u5f97\u5f88\u8fd1\u3002\u5f20\u606a\u542c\u51fa\u6765\u662f\u56db\u6b65\u821e\u66f2\uff0c\u5de6\u624b\u61d2\u5f97\u6258\u8d77\u674e\u99a8\u4e88\u7684\u624b\u4f38\u51fa\u53bb\uff0c\u6ca1\u6709\u63e1\u5979\u7684\u53f3\u624b\uff0c\u5c31\u53f3\u624b\u63fd\u7740\u5979\u7684\u7ec6\u8170\u4fcf\u76ae\u7684\u6446\u52a8\u4e86\u4e24\u4e0b\u8eab\u4f53\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u4e48\u8df3\u4e5f\u4e0d\u9519\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u4f3c\u4e4e\u4f1a\u9519\u4e86\u610f\uff0c\u7738\u5b50\u7075\u52a8\u7684\u95ea\u671b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u90a3\u7738\u5149\u5145\u6ee1\u7740\u5f02\u6837\u7684\u9b45\u60d1\uff0c\u5979\u4e5f\u5b66\u65c1\u8fb9\u90a3\u5973\u5b69\u5b50\uff0c\u5c06\u53cc\u624b\u90fd\u642d\u5728\u5f20\u606a\u7684\u80a9\u4e0a\uff0c\u53ea\u662f\u6ca1\u6709\u6562\u5927\u80c6\u7684\u73af\u6402\u4f4f\u4ed6\u7684\u8116\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u53ea\u7a7f\u7740\u4e00\u4ef6\u886c\u886b\uff0c\u80a9\u7a9d\u7a9d\u7ed9\u674e\u99a8\u4e88\u7684\u5c0f\u624b\u8d34\u7740\uff0c\u80fd\u611f\u89c9\u5230\u6e29\u70ed\u7684\u4f53\u6e29\uff0c\u95fb\u7740\u5979\u8eab\u4e0a\u8ff7\u4eba\u7684\u9999\u5473\uff0c\u5fc3\u601d\u4f3c\u6c89\u6d78\u5728\u865a\u5e7b\u98d8\u7f08\u7684\u68a6\u91cc\uff0c\u53cc\u624b\u63fd\u8d77\u5979\u7684\u7ec6\u8170\uff0c\u4f34\u7740\u97f3\u4e50\u7684\u8282\u594f\u800c\u821e\u3002</p><p>\u3000\u3000\u201c\u8ddf\u4f60\u8df3\u821e\uff0c\u5c31\u50cf\u8ddf\u7236\u4eb2\u5728\u4e00\u8d77\u90a3\u6837\u5b89\u5fc3\uff0c\u53ea\u662f\u7237\u7237\u6b7b\u540e\uff0c\u7236\u4eb2\u53d8\u5f97\u66f4\u52a0\u5fd9\u788c\uff0c\u4ed6\u5bf9\u6211\u7684\u8981\u6c42\u4e5f\u53d8\u5f97\u66f4\u52a0\u4e25\u5389\uff0c\u542c\u7740\u4f60\u7684\u5fc3\u8df3\u58f0\uff0c\u80fd\u60f3\u8d77\u5c0f\u65f6\u5019\u7ed9\u7236\u4eb2\u62b1\u5728\u6000\u91cc\u8ddf\u4ed6\u4e00\u8d77\u8df3\u821e\u7684\u611f\u89c9\u2026\u2026\u201d</p><p>\u3000\u3000\u4e0d\u7ba1\u674e\u5065\u7199\u591a\u4e48\u8d39\u5c3d\u5fc3\u673a\u60f3\u5c06\u5973\u513f\u57f9\u517b\u6210\u4e09\u661f\u7684\u63a5\u73ed\u4eba\uff0c\u674e\u99a8\u4e88\u4e5f\u53ea\u662f\u8106\u5f31\u7684\u5e26\u7740\u4e9b\u4e0d\u5207\u5b9e\u9645\u68a6\u60f3\u7684\u5c0f\u5973\u5b69\u5b50\uff0c\u53ef\u80fd\u5979\u7684\u5c0f\u5973\u5b69\u5b50\u5fc3\u6001\u4e5f\u5f88\u5c0f\u7684\u65f6\u5019\u5c31\u4e0d\u5f97\u4e0d\u56e0\u4e3a\u5bb6\u65cf\u8d23\u4efb\u800c\u6291\u5236\u4e0b\u6765\u2014\u2014\u674e\u5065\u7199\u4e4b\u7236\u901d\u4e16\u65f6\uff0c\u674e\u99a8\u4e88\u624d\u4e5d\u5c81\u3002</p><p>\u3000\u3000\u201c\u8ba9\u6211\u542c\u542c\u4f60\u7684\u5fc3\u8df3\u58f0\u884c\u4e0d\u884c\uff1f\u201d\u674e\u99a8\u4e88\u62ac\u8d77\u6765\u8138\u6765\u4e4c\u6e9c\u6e9c\u7684\u7738\u5b50\u5b9a\u775b\u770b\u7740\u5f20\u606a\uff0c\u773c\u7738\u91cc\u85cf\u7740\u8ff7\u79bb\u82ac\u82b3\u7684\u8bf1\u60d1\uff0c\u4fcf\u76ae\u7684\u4e00\u7b11\uff0c\u642d\u7740\u5f20\u606a\u80a9\u8180\u7684\u4e00\u53ea\u624b\u5c31\u653e\u4e0b\u6765\uff0c\u4ece\u5f20\u606a\u7684\u814b\u4e0b\u7a7f\u8fc7\u53bb\u6402\u4f4f\u4ed6\u7684\u8170\uff0c\u5934\u8d34\u7740\u4ed6\u7684\u80a9\u8180\u6ed1\u5230\u5fc3\u810f\u7684\u4f4d\u7f6e\uff0c\u8ba4\u771f\u7684\u542c\u4e86\u4e00\u4f1a\u513f\u5fc3\u8df3\u58f0\uff0c\u53c8\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u7684\u5fc3\u8df3\u597d\u5927\u58f0\u554a\uff0c\u53c8\u5feb\u4e86\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u7ed9\u674e\u99a8\u4e88\u8fd9\u4e2a\u59ff\u6001\u6402\u7740\uff0c\u808b\u4e0b\u81ea\u7136\u7ed9\u5979\u4e30\u6ee1\u7684\u80f8\u90e8\u8d34\u4e86\u4e2a\u4e25\u5b9e\uff0c\u5f20\u606a\u5b9a\u6027\u518d\u597d\uff0c\u7f8e\u4eba\u5165\u6000\uff0c\u4e5f\u60c5\u4e0d\u81ea\u7981\u7684\u5fc3\u6d6e\u610f\u4e71\u8d77\u6765\uff0c\u66f4\u4f55\u51b5\u624b\u8fd8\u6402\u7740\u5979\u67d4\u8f6f\u7684\u7ec6\u8170\u3002\u4e0d\u5f97\u4e0d\u627f\u8ba4\u6000\u91cc\u7684\u4f73\u4eba\u6709\u79cd\u8ba9\u4eba\u60c5\u4e0d\u81ea\u7981\u6df1\u9677\u8fdb\u53bb\u7684\u8ff7\u9189\uff0c\u5f20\u606a\u5fae\u5fae\u5438\u4e86\u53e3\u6c14\uff0c\u8ba9\u81ea\u5df1\u7684\u5fc3\u7eea\u7a0d\u5e73\u9759\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u4e00\u4f1a\u513f\u7ad9\u597d\u8eab\u5b50\uff0c\u4e0d\u597d\u610f\u601d\u7684\u770b\u7740\u5f20\u606a\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u8981\u8c22\u8c22\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\uff0c\u8981\u4e0d\u6211\u4eec\u51fa\u53bb\u900f\u53e3\u6c14\uff1f\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e0d\u7136\u6211\u7684\u5fc3\u810f\u518d\u8981\u53d7\u4e0d\u4e86\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u597d\u554a\u3002\u201d\u674e\u99a8\u4e88\u4fcf\u76ae\u7684\u7b11\u4e86\u7b11\uff0c\u7ed9\u5f20\u606a\u7275\u7740\u7684\u624b\u6ca1\u6709\u62bd\u56de\u6765\uff0c\u8ddf\u4ed6\u5f80\u821e\u6c60\u5916\u8d70\u53bb\uff0c\u65cb\u8f6c\u706f\u67f1\u5c06\u7eda\u4e3d\u591a\u5f69\u7684\u706f\u5149\u6253\u5230\u5979\u7684\u8138\uff0c\u867d\u7136\u53ea\u662f\u4e00\u77ac\uff0c\u5374\u8ba9\u8bb8\u591a\u4eba\u770b\u5230\u5979\u60ca\u8273\u8ff7\u4eba\u7684\u5bb9\u989c\uff0c\u4e5f\u5305\u62ec\u89d2\u843d\u91cc\u7684\u674e\u91d1\u5357\u52c7\u8ddf\u53e6\u5916\u4e24\u4e2a\u97e9\u56fd\u9752\u5e74\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e0d\u662f\u4f1a\u957f\u5343\u91d1\u5417\uff0c\u5979\u5728\u4e2d\u56fd\u6709\u604b\u4eba\u4e86\u554a\uff0c\u90a3\u4e2a\u9752\u5e74\u662f\u8c01\uff1f\u201d\u4e00\u540d\u97e9\u56fd\u9752\u5e74\u8be7\u5f02\u7684\u95ee\u91d1\u5357\u52c7\u3002</p></article>"}}}]);
