(window.webpackJsonp=window.webpackJsonp||[]).push([[1149],{1172:function(p){p.exports={chapterTitle:null,title:"\u7b2c943\u7ae0 \u6709\u4e9b\u610f\u5916\u65e0\u6cd5\u9884\u9632",content:"<article><h2>\u7b2c943\u7ae0 \u6709\u4e9b\u610f\u5916\u65e0\u6cd5\u9884\u9632</h2><p>\u3000\u3000\u5f20\u606a\u90fd\u5fcd\u4e0d\u4f4f\u60f3\u63d0\u9192\u5510\u5fe0\u4e00\u53e5\uff0c\u4ed6\u8fd9\u53ea\u624b\u673a\u6309\u4e00\u4e2a\u952e\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u60f3\u8ddf\u4f60\u4e3a\u654c\uff0c\u4e5f\u4e0d\u60f3\u5bf9\u4f60\u505a\u4ec0\u4e48\uff0c\u201d\u5510\u5fe0\u773c\u775b\u6076\u72e0\u72e0\u7684\u76ef\u7740\u5f20\u606a\uff0c\u4ed6\u7684\u58f0\u97f3\u5374\u662f\u5e72\u5df4\u5df4\u7684\uff0c\u8fd8\u6709\u4e9b\u5fae\u98a4\uff0c\u201c\u4f46\u662f\u6211\u8981\u544a\u8bc9\u4f60\uff0c\u8981\u662f\u6211\u7238\u8ddf\u6211\u53d4\u6b7b\u4e86\uff0c\u6211\u5c31\u4ec0\u4e48\u90fd\u6ca1\u6709\u4e86\uff0c\u4f60\u5e94\u8be5\u77e5\u9053\u4ec0\u4e48\u90fd\u6ca1\u6709\u7684\u4eba\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u5e72\u5f97\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u4e24\u4e2a\u9752\u5e74\u62ff\u5f39\u7c27\u5200\u5c06\u5f20\u606a\u903c\u5728\u56f4\u680f\u4e00\u89d2\uff0c\u53e6\u4e24\u4e2a\u9752\u5e74\u7ad9\u5f97\u7a0d\u8fdc\u4e00\u4e9b\uff0c\u9632\u6b62\u53ef\u80fd\u7ecf\u8fc7\u7684\u8def\u4eba\u6253\u6270\u4e86\u4ed6\u4eec\u7684\u884c\u52a8\u3002</p><p>\u3000\u3000\u5f20\u606a\u771f\u60f3\u95ee\u95ee\u5510\u5fe0\u8fd9\u6837\u7684\u5bf9\u8bdd\u662f\u4ece\u54ea\u91cc\u5b66\u8fc7\u6765\u7684\uff0c\u8ddf\u7535\u5f71\u91cc\u5b66\u7684\uff1f\u4ed6\u51b7\u9759\u7684\u770b\u7740\u5510\u5fe0\u7684\u8138\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u4ece\u54ea\u91cc\u77e5\u9053\u80cc\u540e\u6240\u53d1\u751f\u7684\u4e00\u5207\uff0c\u6309\u7167\u9053\u7406\u6765\u8bf4\uff0c\u4e0d\u5e94\u8be5\u6709\u4eba\u5c06\u9526\u6e56\u7684\u5e95\u7ec6\u544a\u8bc9\u5510\u5fe0\u2014\u2014\u5510\u5fe0\u53ea\u662f\u4e27\u5bb6\u4e4b\u72ac\u7f62\u4e86\uff0c\u53c8\u6ca1\u6709\u4ea1\u547d\u4e4b\u5f92\u7684\u6c14\u6168\uff0c\u771f\u662f\u6ca1\u6709\u4e00\u70b9\u5229\u7528\u7684\u4ef7\u503c\u3002\u96be\u9053\u5e26\u7740\u56db\u4e2a\u4eba\u62ff\u7740\u5f39\u7c27\u5200\u5c06\u81ea\u5df1\u903c\u5728\u8fd9\u91cc\u5c31\u80fd\u6551\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u4e00\u547d\uff1f\u771f\u662f\u592a\u5355\u7eaf\u4e86\uff0c\u5f20\u606a\u90fd\u61d2\u5f97\u8ddf\u4ed6\u8d39\u4ec0\u4e48\u53e3\u820c\uff0c\u4e0d\u8fc7\u5012\u662f\u80fd\u7406\u89e3\u5510\u5fe0\u7684\u52c7\u6c14\u4ece\u4f55\u800c\u6765\uff0c\u6216\u8bb8\u8fd9\u4e9b\u5929\u53c2\u52a0\u62db\u8058\u4f1a\u53d7\u5230\u4e86\u4ec0\u4e48\u523a\u6fc0\u5427\uff0c\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u4e24\u5144\u5f1f\u4e8c\u5ba1\u5224\u6b7b\u7f13\u4e0e\u6539\u5224\u6b7b\u5211\u7acb\u5373\u6267\u884c\uff0c\u5bf9\u5510\u5fe0\u6765\u8bf4\u662f\u7edd\u5bf9\u4e0d\u540c\u7684\u2014\u2014\u5224\u6b7b\u7f13\u7684\u4eba\uff0c\u5510\u6c0f\u5144\u5f1f\u5728\u7ae0\u5dde\u7684\u6b8b\u4f59\u52bf\u529b\u6216\u8005\u8bf4\u662f\u6b8b\u4f59\u5f71\u54cd\u8fd8\u80fd\u7ed9\u5510\u5fe0\u4ee5\u836b\u5e87\u3002\u5f20\u606a\u7528\u773c\u89d2\u4f59\u5149\u6253\u91cf\u5c06\u81ea\u5df1\u903c\u5728\u89d2\u843d\u91cc\u7684\u56db\u4e2a\u9752\u5e74\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u591f\u4e13\u4e1a\uff0c\u4ed6\u4f4e\u5934\u770b\u4e86\u770b\u978b\u5c16\uff0c\u5728\u5085\u4fca\u4ed6\u4eec\u8d76\u6765\u4e4b\u524d\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u767d\u75f4\u5230\u505a\u4ec0\u4e48\u6311\u8845\u7684\u4e3e\u52a8\uff0c\u53ea\u6709\u62d6\u4e09\u4e94\u5206\u949f\u5c31\u8db3\u591f\u4e86\uff0c\u5e73\u9759\u7684\u8bf4\u9053\uff1a\u201c\u6211\u80fd\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5982\u4f55\u8fd9\u6837\u505a\u6709\u6548\uff0c\u4f60\u4eec\u4f3c\u4e4e\u66f4\u5e94\u8be5\u53bb\u627e\u8d1f\u8d23\u4e8c\u5ba1\u7684\u6cd5\u5b98\u2026\u2026\u201d\u4ed6\u5728\u9ed8\u6570\u65f6\u95f4\uff0c\u5fc3\u91cc\u5728\u60f3\u8981\u662f\u8d85\u8fc7\u4e09\u5206\u949f\uff0c\u662f\u4e0d\u662f\u5c06\u90a3\u4e9b\u5b89\u5168\u4e8b\u52a1\u52a9\u7406\u90fd\u53ef\u4ee5\u8f9e\u9000\uff0c\u5085\u4fca\u4e0e\u9a6c\u6d77\u9f99\u4eca\u5e74\u7684\u80a1\u6743\u6fc0\u52b1\u4e5f\u8981\u53d6\u6d88\u6389\uff1f</p><p>\u3000\u3000\u201c\u6211\u4ec0\u4e48\u4e8b\u60c5\u90fd\u53ef\u4ee5\u5e72\u5f97\u51fa\u6765\uff0c\u4f60\u4e0d\u8981\u903c\u6211\uff01\u201d\u5510\u5fe0\u4e00\u7bad\u6b65\u4e0a\u53bb\u62fd\u4f4f\u5f20\u606a\u9886\u53e3\uff0c\u6076\u72e0\u72e0\u7684\u76ef\u7740\u4ed6\uff0c\u5634\u91cc\u7684\u53e3\u6c14\u55b7\u51fa\u6765\u6709\u4e9b\u8165\u81ed\u3002</p><p>\u3000\u3000\u5f20\u606a\u522b\u8fc7\u5934\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6211\u7b54\u5e94\u4f60\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u7559\u6211\u7238\u8ddf\u6211\u53d4\u4e00\u6761\u547d\uff0c\u5f53\u4eca\u5929\u4ec0\u4e48\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\u8fc7\u3002\u201d\u5510\u5fe0\u5929\u771f\u7684\u8bf4\u9053\uff0c\u201c\u4e0d\u7136\u6211\u505a\u9b3c\u90fd\u4e0d\u4f1a\u653e\u8fc7\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u4eba\u6765\u4e86\u2026\u2026\u201d\u5728\u7a0d\u8fdc\u5904\u653e\u54e8\u7684\u6c49\u5b50\u538b\u7740\u58f0\u97f3\u544a\u8bc9\u8fd9\u8fb9\u3002</p><p>\u3000\u3000\u5f20\u606a\u521a\u8981\u770b\u8fc7\u53bb\uff0c\u4e0b\u5df4\u5374\u7ed9\u8eab\u5de6\u8fb9\u7684\u90a3\u4e2a\u9752\u5e74\u634f\u4f4f\u65e0\u6cd5\u770b\u8c01\u8d70\u8fc7\u6765\uff0c\u8170\u95f4\u8fd8\u7ed9\u62b5\u4e86\u523a\u51b7\u7684\u5f39\u7c27\u5200\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8981\u558a\uff0c\u558a\u5c31\u6345\u6b7b\u4f60\uff01\u201d\u53f3\u8fb9\u90a3\u9752\u5e74\u51b7\u51b7\u7684\u5a01\u80c1\u5f20\u606a\u3002</p><p>\u3000\u3000\u542c\u7740\u719f\u6089\u7684\u9ad8\u8ddf\u978b\u51fb\u5730\u7684\u58f0\u97f3\u4ece\u6821\u95e8\u65b9\u5411\u4f20\u6765\uff0c\u5f20\u606a\u542c\u58f0\u97f3\u5c31\u77e5\u9053\u662f\u7fdf\u4e39\u9752\uff0c\u60f3\u6765\u662f\u7fdf\u4e39\u9752\u521a\u4ece\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u56de\u6765\u3002</p><p>\u3000\u3000\u4ece\u4e1c\u5927\u6821\u56ed\u91cc\u7a7f\u8fc7\u6b65\u884c\u56de\u516c\u5bd3\u53ea\u9700\u8981\u5341\u591a\u5206\u949f\u7684\u8def\u7a0b\uff0c\u7fdf\u4e39\u9752\u4e5f\u559c\u6b22\u4e1c\u5927\u6821\u56ed\u591c\u665a\u7684\u6c1b\u56f4\uff0c\u6216\u8bb8\u4e5f\u662f\u4e0b\u610f\u8bc6\u7684\u671f\u5f85\u8fd9\u65f6\u5019\u7ecf\u8fbe\u6821\u56ed\u65f6\u80fd\u4e0e\u5f20\u606a\u4e0d\u7ecf\u610f\u7684\u76f8\u9047\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u539f\u672c\u5fc3\u60f3\u7fdf\u4e39\u9752\u5e94\u8be5\u4f1a\u77e5\u9053\u5982\u4f55\u5904\u7f6e\u773c\u524d\u7684\u5c40\u9762\uff0c\u4ed6\u5374\u6ca1\u6709\u770b\u5230\u7fdf\u4e39\u9752\u7684\u773c\u795e\u5728\u77a5\u89c1\u62b5\u5728\u5f20\u606a\u8170\u95f4\u7684\u5f39\u7c27\u5200\u65f6\u5df2\u7ecf\u614c\u4e71\u4e86\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u811a\u4e0b\u4e00\u62d0\u505c\u4e86\u4e0b\u6765\uff0c\u5510\u5fe0\u6076\u72e0\u72e0\u7684\u8d70\u8fc7\u6765\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u60f9\u4e8b\uff0c\u6eda\u5f00\uff01\u201d</p><p>\u3000\u3000\u201c\u6211\u811a\u62d0\u4e86\uff0c\u4f60\u53eb\u6211\u600e\u4e48\u8d70\uff1f\u201d\u7fdf\u4e39\u9752\u5f3a\u8feb\u7740\u81ea\u5df1\u4e0d\u53bb\u770b\u5f20\u606a\uff0c\u975e\u4f46\u6ca1\u6709\u8d70\uff0c\u8fd8\u4e00\u7638\u4e00\u62d0\u5f80\u8def\u4fa7\u8d70\u4e86\u4e24\u6b65\uff0c\u76b1\u7709\u5fcd\u75db\u4f3c\u7684\u4e00\u5c41\u80a1\u5750\u5230\u8def\u7259\u4e0a\uff0c\u5b8c\u5168\u4e0d\u987e\u5979\u7edd\u8272\u7f8e\u5973\u7684\u4eea\u6001\uff0c\u629a\u7740\u4f3c\u4e4e\u771f\u7ed9\u62d0\u75db\u4e86\u7684\u811a\u8e1d\uff0c\u5fc3\u614c\u614c\u7684\u76f4\u8981\u53d1\u6296\uff0c\u5374\u5f3a\u5fcd\u4f4f\u5185\u5fc3\u6df1\u5904\u6d8c\u51fa\u6765\u7684\u6050\u614c\uff0c\u4f3c\u55d4\u4f3c\u6028\u7684\u6a2a\u4e86\u5510\u5fe0\u4e00\u773c\uff0c\u5c06\u88e4\u7ba1\u5f80\u4e0a\u4e00\u634b\uff0c\u9732\u51fa\u5728\u6697\u5149\u91cc\u6d01\u767d\u53d1\u4eae\u7684\u79c0\u8db3\uff0c\u201c\u4f60\u53eb\u6211\u600e\u4e48\u8d70\uff1f\u4f60\u53eb\u6211\u600e\u4e48\u8d70\uff1f\u201d\u53c8\u56de\u5934\u770b\u4e86\u5f20\u606a\u4ed6\u4eec\u4e00\u773c\uff0c\u5929\u771f\u4f3c\u7684\u95ee\u9053\uff0c\u201c\u4f60\u4eec\u5728\u505a\u4ec0\u4e48\uff0c\u6253\u67b6\u5417\uff1f\u8fd9\u6709\u4ec0\u4e48\u4e0d\u80fd\u770b\u7684\uff1f\u201d</p><p>\u3000\u3000\u62ff\u5200\u903c\u4f4f\u5f20\u606a\u7684\u9752\u5e74\u7ed9\u8273\u5149\u56db\u5c04\u7684\u7fdf\u4e39\u9752\u5982\u6b64\u4e00\u95ee\uff0c\u4e0b\u610f\u8bc6\u7684\u60f3\u8981\u5c06\u5f39\u7c27\u5200\u6536\u8d77\u6765\uff0c\u4e0d\u8fc7\u4ed6\u6ca1\u6709\u8fd9\u4e48\u50bb\uff0c\u53ea\u662f\u5c06\u5200\u7a0d\u6536\u4e86\u4e00\u4e9b\uff0c\u8eab\u4f53\u5c06\u5200\u6321\u4f4f\uff0c\u4e0d\u8ba9\u7fdf\u4e39\u9752\u80fd\u76f4\u63a5\u770b\u5230\u3002\u5939\u5cd9\u5f20\u606a\u7684\u53e6\u4e00\u4e2a\u9752\u5e74\u4f3c\u4e4e\u610f\u8bc6\u4e0d\u5230\u60c5\u51b5\u6709\u591a\u4e25\u5cfb\uff0c\u4ece\u4ed6\u7684\u89d2\u5ea6\u521a\u597d\u80fd\u4ece\u7fdf\u4e39\u9752\u7684\u8863\u9886\u91cc\u770b\u5230\u4e73\u6c9f\u7684\u4e00\u89d2\uff0c\u5fcd\u4e0d\u4f4f\u8d70\u51fa\u4e00\u6b65\u60f3\u770b\u66f4\u4ed4\u7ec6\u4e9b\u3002\u5510\u5fe0\u4e5f\u4e0d\u8ba4\u8bc6\u7fdf\u4e39\u9752\uff0c\u7fdf\u4e39\u9752\u8fd9\u6bb5\u65f6\u5019\u867d\u7136\u6e34\u671b\u4e0e\u5f20\u606a\u80fd\u4e0d\u7ecf\u610f\u7684\u76f8\u9047\uff0c\u5374\u53c8\u514b\u5236\u7740\u4e0d\u4e0e\u5f20\u606a\u7c98\u5728\u4e00\u8d77\uff0c\u771f\u662f\u77db\u76fe\u4e4b\u6781\u7684\u5fc3\u60c5\u3002\u5510\u5fe0\u53ef\u6ca1\u6709\u5fc3\u601d\u6b23\u8d4f\u4ec0\u4e48\u7f8e\u8272\uff0c\u4ed6\u62ac\u811a\u8e22\u4e86\u8e22\u7fdf\u4e39\u9752\uff1a\u201c\u5988B\u7684\uff0c\u6eda\u5f00\uff0c\u4e0d\u8981\u81ea\u8ba8\u6ca1\u8da3\u3002\u201d</p><p>\u3000\u3000\u201c\u5988B\u517b\u7684\uff0c\u4f60\u6562\u8e22\u8001\u5a18\u2026\u2026\u4f60\u6562\u8e22\u8001\u5a18\uff0c\u8001\u5a18\u53eb\u4f60\u540e\u6094\u4ece\u4f60\u5988B\u91cc\u94bb\u51fa\u6765\u3002\u201d\u7fdf\u4e39\u9752\u7a81\u7136\u66b4\u51fa\u4ee4\u4eba\u77a0\u76ee\u7ed3\u820c\u7684\u7c97\u53e3\uff0c\u7a81\u7136\u53d1\u766b\u4f3c\u7684\u4fa7\u8eab\u62b1\u4f4f\u5510\u5fe0\u7684\u53cc\u817f\uff0c\u5934\u9876\u7740\u4ed6\u7684\u5c0f\u809a\u5b50\uff0c\u53c8\u4f38\u624b\u62fd\u7740\u4ed6\u7684\u8863\u6446\u8d34\u8eab\u7ad9\u8d77\u6765\uff0c\u671d\u4ed6\u7684\u8138\u4e71\u6293\u53bb\uff0c\u5634\u91cc\u5c16\u53eb\u7740\u8fd8\u8981\u671d\u4ed6\u7684\u8138\u5543\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7684\u7a81\u7136\u53d1\u4f5c\u5b8c\u5168\u51fa\u4e4e\u5510\u5fe0\u4ed6\u4eec\u7684\u610f\u6599\uff0c\u5510\u5fe0\u8138\u4e0a\u987f\u65f6\u7ed9\u6293\u7834\u4e86\u51e0\u9053\u4e5f\u6323\u624e\u4e0d\u51fa\u7fdf\u4e39\u9752\u53d1\u75af\u4f3c\u7684\u6495\u54ac\uff0c\u521a\u624d\u8fd8\u60f3\u5077\u7aa5\u7fdf\u4e39\u9752\u6625\u8272\u7684\u9752\u5e74\u81ea\u7136\u5c31\u53bb\u7ed9\u5510\u5fe0\u89e3\u56f4\uff0c\u7fdf\u4e39\u9752\u7ed9\u62fd\u4f4f\u5934\u53d1\u8fde\u9000\u4e86\u4e24\u6b65\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u60f3\u5230\u7fdf\u4e39\u9752\u4f1a\u6709\u5982\u6b64\u53cd\u5e94\uff0c\u5bf9\u5979\u7684\u53cd\u5e94\u4e5f\u6709\u4e9b\u63aa\u624b\u4e0d\u53ca\u3002\u5f53\u7fdf\u4e39\u9752\u7ed9\u62fd\u7740\u5934\u53d1\u8fde\u9000\u4e24\u6b65\uff0c\u5374\u7a81\u7136\u53d1\u529b\u6323\u624e\u5f00\u8f6c\u8eab\u7f20\u62b1\u4f4f\u62ff\u5200\u62b5\u4f4f\u5f20\u606a\u7684\u90a3\u540d\u9752\u5e74\u7684\u8eab\u4f53\uff0c\u58f0\u5636\u529b\u7aed\u7684\u558a\u9053\uff1a\u201c\u4f60\u5feb\u8dd1\uff01\u201d\u5f20\u606a\u624d\u77ac\u95f4\u660e\u767d\u5979\u8981\u505a\u4ec0\u4e48\uff0c\u5192\u9669\u4e00\u640f\u5374\u662f\u4e3a\u4e86\u7ed9\u81ea\u5df1\u9003\u547d\u7684\u673a\u4f1a\uff0c\u5fc3\u91cc\u60b8\u52a8\u7740\uff1a\u8fd9\u4e2a\u50bb\u5973\u4eba\uff01</p><p>\u3000\u3000\u5f20\u606a\u600e\u4e48\u53ef\u80fd\u6487\u4e0b\u7fdf\u4e39\u9752\u81ea\u5df1\u9003\u547d\uff0c\u7ed9\u4e0b\u610f\u8bc6\u671d\u7fdf\u4e39\u9752\u6251\u8fc7\u53bb\u7684\u5510\u5fe0\u88c6\u4e0b\u4e00\u811a\uff0c\u5c06\u4ed6\u8e39\u4e0b\u8def\u7259\uff0c\u4f38\u624b\u6293\u4f4f\u7fdf\u4e39\u9752\u62b1\u4f4f\u90a3\u9752\u5e74\u7684\u5934\u53d1\uff0c\u731b\u7136\u626f\u4f4f\u5f80\u94c1\u6805\u680f\u731b\u649e\u8fc7\u53bb\u3002\u4e24\u540d\u4fdd\u536b\u5df2\u7ecf\u4ece\u5c0f\u533a\u91cc\u5148\u8d34\u8fd1\u8fc7\u6765\uff0c\u770b\u5230\u8fd9\u91cc\u7684\u52a8\u9759\uff0c\u4e5f\u4e0d\u987e\u9690\u85cf\u6551\u4eba\u7684\u610f\u56fe\uff0c\u5927\u558a\u9053\uff1a\u201c\u4f4f\u624b\uff01\u201d\u6492\u811a\u5c31\u51b2\u4e86\u8fc7\u6765\uff0c\u79bb\u4ed6\u4eec\u6700\u8fd1\u7684\u90a3\u4e2a\u6c49\u5b50\u60f3\u8981\u6321\u4e00\u6321\uff0c\u7ed9\u62ac\u811a\u5c31\u8e39\u7ffb\u5728\u5730\u3002</p><p>\u3000\u3000\u5510\u5fe0\u6bd5\u7adf\u6ca1\u6709\u73a9\u547d\u4e4b\u5f92\u7684\u6c14\u6168\uff0c\u770b\u89c1\u7ed9\u4eba\u53d1\u73b0\uff0c\u7b2c\u4e00\u4e2a\u5ff5\u5934\u5c31\u662f\u6492\u624b\u9003\u8dd1\uff0c\u5f20\u606a\u54ea\u91cc\u5bb9\u4ed6\u9003\u8dd1\uff0c\u4e00\u7bad\u6b65\u8fc7\u53bb\u62fd\u4f4f\u4ed6\u7684\u8863\u9886\u5f80\u540e\u52d2\uff0c\u62f3\u5934\u76f4\u671d\u4ed6\u7684\u540e\u8111\u52fa\u7838\u8fc7\u53bb\uff0c\u4e5f\u4e0d\u987e\u624b\u75bc\uff0c\u8fde\u7838\u4e24\u62f3\u6253\u5f97\u5510\u5fe0\u773c\u5192\u91d1\u661f\u6ca1\u6709\u53cd\u51fb\u4e4b\u529b\uff0c\u5f20\u606a\u5374\u542c\u89c1\u8eab\u540e\u7fdf\u4e39\u9752\u53d1\u51fa\u4e00\u58f0\u5c16\u9510\u5230\u6781\u70b9\u7684\u60e8\u53eb\uff0c\u62e7\u5934\u770b\u5374\uff0c\u90a3\u4e2a\u5077\u770b\u7fdf\u4e39\u9752\u4e73\u6c9f\u7684\u9752\u5e74\u6301\u7740\u5f39\u7c27\u5200\u671d\u81ea\u5df1\u523a\u6765\uff0c\u7fdf\u4e39\u9752\u5c16\u53eb\u7740\u8eab\u5b50\u6321\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u770b\u7740\u5f39\u7c27\u5200\u523a\u8fdb\u7fdf\u4e39\u9752\u7684\u80f8\u53e3\uff0c\u4e00\u80a1\u4ee4\u4eba\u98a4\u7c9f\u7684\u4e25\u5bd2\u5c06\u5f20\u606a\u6574\u4e2a\u4eba\u90fd\u51bb\u4f4f\uff0c\u4ed6\u53ea\u662f\u4e0b\u610f\u8bc6\u7684\u62b1\u4f4f\u7fdf\u4e39\u9752\u5012\u4e0b\u7684\u8eab\u4f53\uff0c\u89c1\u90a3\u4eba\u62bd\u51fa\u5200\u8fd8\u8981\u523a\uff0c\u4e5f\u662f\u4e0b\u610f\u8bc6\u7684\u4f38\u624b\u6321\u4e86\u4e00\u4e0b\u2026\u2026</p><p>\u3000\u3000\u90a3\u4eba\u8fd8\u8981\u523a\u6765\uff0c\u6700\u5148\u51b2\u8fc7\u6765\u7684\u4fdd\u536b\u53d1\u72e0\u4e00\u811a\u671d\u90a3\u4eba\u8170\u773c\u8e22\u53bb\uff0c\u8fde\u4eba\u5e26\u5200\u7ed9\u72e0\u72e0\u8e22\u649e\u5728\u94c1\u6805\u680f\u4e0a\uff0c\u4eba\u6b6a\u6b6a\u7684\u5012\u4e0b\uff0c\u4e5f\u4e0d\u77e5\u6b7b\u6d3b\u3002</p><p>\u3000\u3000\u5f20\u606a\u6d51\u8eab\u98a4\u7c9f\uff0c\u4e5f\u4e0d\u89c9\u5f97\u7ed9\u523a\u4e2d\u7684\u624b\u81c2\u75bc\u75db\uff0c\u53ea\u611f\u89c9\u6e29\u70ed\u7684\u9c9c\u8840\u4ece\u7fdf\u4e39\u9752\u80f8\u53e3\u6d8c\u51fa\u6765\uff0c\u6309\u90fd\u6309\u4e0d\u505c\uff0c\u4e5f\u65e0\u529b\u53bb\u7ba1\u522b\u7684\u4e8b\u60c5\uff0c\u6d51\u8eab\u53d1\u5bd2\uff0c\u53ea\u662f\u5c06\u7fdf\u4e39\u9752\u6402\u5728\u6000\u91cc\uff0c\u624b\u6309\u4f4f\u5979\u7ed9\u523a\u4e2d\u7684\u4f24\u53e3\uff0c\u5085\u4fca\u4e0e\u53e6\u4e24\u540d\u4fdd\u536b\u5f88\u5feb\u5c31\u8d76\u5230\uff0c\u6551\u62a4\u8f66\u5927\u6982\u5728\u4e94\u5206\u949f\u540e\u8d76\u5230\uff0c\u5f20\u606a\u53ea\u89c9\u5f97\u8fc7\u4e86\u597d\u51e0\u4e2a\u5c0f\u65f6\uff0c\u770b\u7740\u6551\u62a4\u8f66\u7684\u53f8\u673a\u4e0b\u8f66\u6765\u7834\u53e3\u5927\u9a82\uff1a\u201c\u4f60\u4eec\u5979\u5988\u7684\u5e72\u4ec0\u4e48\u5403\u7684\uff01\u201d\u62ac\u811a\u8981\u8e22\u4eba\uff0c\u8fd8\u662f\u7ed9\u5085\u4fca\u4ed6\u4eec\u62e6\u4f4f\uff0c\u6709\u4eba\u6765\u7ed9\u4ed6\u5305\u624e\u4f24\u53e3\uff0c\u4ed6\u90fd\u4e00\u628a\u63a8\u5f00\uff0c\u53ea\u662f\u81ea\u5df1\u624b\u6309\u7740\u624b\u81c2\u4e0a\u7684\u4f24\u53e3\uff0c\u8ddf\u7740\u62c5\u67b6\u4e00\u8d77\u4e0a\u4e86\u6551\u62a4\u8f66\uff0c\u4e5f\u4e0d\u7ba1\u5176\u4ed6\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5230\u4e86\u533b\u9662\uff0c\u770b\u7740\u751f\u6b7b\u4e0d\u77e5\u7684\u7fdf\u4e39\u9752\u7ed9\u63a8\u8fdb\u6025\u6551\u5ba4\uff0c\u5f20\u606a\u90a3\u7ed9\u4e25\u5bd2\u50f5\u4f4f\u7684\u8111\u5b50\u624d\u7a0d\u7a0d\u6062\u590d\u8fc7\u6765\uff0c\u5750\u5728\u6025\u6551\u5ba4\u5916\uff0c\u773c\u795e\u51b7\u5cfb\u7684\u76ef\u7740\u5085\u4fca\uff0c\u4f3c\u4e4e\u8981\u5c06\u4ed6\u7ed9\u5403\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u8010\u70e6\u533b\u5e08\u5728\u4e00\u65c1\u7ed9\u4ed6\u62ff\u9488\u7f1d\u51e0\u4e4e\u7ed9\u523a\u7a7f\u7684\u624b\u81c2\uff0c\u4e5f\u4e0d\u89c9\u5f97\u6709\u591a\u75bc\u75db\u3002</p><p>\u3000\u3000\u5085\u4fca\u4e5f\u662f\u5934\u75bc\uff0c\u5f20\u606a\u76db\u6012\u4e4b\u65f6\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u4e3a\u81ea\u5df1\u89e3\u91ca\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u4f20\u51fa\u4fe1\u53f7\u4e4b\u540e\uff0c\u5b89\u4fdd\u8fd9\u8fb9\u5c31\u7acb\u5373\u52a8\u4e86\u8d77\u6765\uff0c\u4fdd\u536b\u5ba4\u7684\u4e24\u540d\u4fdd\u536b\u6700\u5148\u5230\u73b0\u573a\uff0c\u4f46\u662f\u5f20\u606a\u7ed9\u631f\u6301\u4f4f\uff0c\u4e24\u540d\u4fdd\u536b\u4e0d\u6562\u8d38\u7136\u91c7\u53d6\u884c\u52a8\uff0c\u751a\u81f3\u4e5f\u4e0d\u80fd\u592a\u8d34\u8fd1\u60ca\u52a8\u5510\u5fe0\u4ed6\u4eec\uff0c\u5085\u4fca\u79bb\u73b0\u573a\u7a0d\u8fdc\uff0c\u4ed6\u4e0e\u4e24\u540d\u4fdd\u536b\u4ee5\u6700\u5feb\u901f\u5ea6\u5f80\u73b0\u573a\u8d76\u6765\u3002\u672c\u6765\u5c40\u52bf\u80fd\u63a7\u5236\u4f4f\uff0c\u4f46\u662f\u8c01\u4e5f\u6ca1\u6709\u9884\u6599\u5230\u7fdf\u4e39\u9752\u7684\u53cd\u5e94\u3002\u7fdf\u4e39\u9752\u4e3a\u4e86\u5f20\u606a\u80fd\u8131\u56f0\u7adf\u7136\u72af\u9669\u4e00\u640f\uff0c\u8fd9\u5b8c\u5168\u4e5f\u8ba9\u5085\u4fca\u4ed6\u4eec\u4e71\u4e86\u624b\u811a\uff0c\u8fd9\u65f6\u5019\u4e5f\u4e0d\u7528\u53bb\u60f3\u7fdf\u4e39\u9752\u4e3a\u4ec0\u4e48\u4f1a\u5931\u53bb\u51b7\u9759\u7684\u5934\u8111\uff0c\u4e5f\u4e0d\u7528\u53bb\u60f3\u7fdf\u4e39\u9752\u4e3a\u4ec0\u4e48\u4f1a\u594b\u4e0d\u987e\u8eab\u7684\u66ff\u5f20\u606a\u6321\u4e86\u4e00\u5200\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u80f8\u53e3\u7ed9\u5f39\u7c27\u5200\u523a\u4e2d\u4e86\uff0c\u8fd9\u65f6\u5019\u751f\u6b7b\u4e0d\u77e5\uff0c\u800c\u4e14\u6700\u5148\u8d76\u5230\u73b0\u573a\u7684\u4e24\u540d\u4fdd\u536b\u8981\u7559\u4e00\u4eba\u5b88\u7740\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u53eb\u6551\u62a4\u8f66\uff0c\u53ea\u6709\u4e00\u4eba\u53bb\u8ffd\u884c\u51f6\u7684\u4eba\uff0c\u5085\u4fca\u4ed6\u4eec\u7a0d\u8fdf\u4e86\u4e00\u5206\u949f\u8d76\u5230\u73b0\u573a\uff0c\u6700\u7ec8\u53ea\u6293\u4f4f\u5176\u4e2d\u7684\u56db\u4eba\uff0c\u8fd8\u662f\u8ba9\u4e00\u4e2a\u4eba\u9003\u8131\u4e86\u3002</p><p>\u3000\u3000\u7ed9\u5f20\u606a\u51b7\u5cfb\u7684\u773c\u795e\u76ef\u7740\uff0c\u5085\u4fca\u90fd\u89c9\u5f97\u81ea\u5df1\u72af\u4e86\u5927\u9519\uff0c\u5fc3\u5934\u53d1\u865a\uff0c\u8270\u96be\u7684\u8bf4\u9053\uff1a\u201c\u6293\u4f4f\u56db\u4e2a\u4eba\uff0c\u5176\u4e2d\u5e9f\u4e86\u4e00\u4e2a\uff0c\u6709\u4e00\u4e2a\u4eba\u7ed9\u9003\u8131\u4e86\uff0c\u73b0\u5728\u5df2\u7ecf\u4ea4\u7ed9\u8b66\u65b9\u5904\u7406\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u538b\u7740\u5fc3\u5934\u7684\u6012\u706b\uff0c\u544a\u8beb\u81ea\u5df1\u8981\u51b7\u9759\uff0c\u4e0d\u53ef\u4ee5\u8fc1\u6012\u4ed6\u4eba\uff0c\u5934\u8f6c\u5411\u4e00\u8fb9\uff0c\u5bf9\u5085\u4fca\u7684\u6c47\u62a5\u4e5f\u4e0d\u7f6e\u53ef\u5426\u3002\u675c\u98de\u3001\u5b59\u9759\u6aac\u4ed6\u4eec\u5f88\u5feb\u90fd\u8d76\u4e86\u8fc7\u6765\uff0c\u5bf9\u4ed6\u4eec\u8d76\u8fc7\u6765\uff0c\u5f20\u606a\u5750\u5728\u90a3\u91cc\u795e\u8272\u51b7\u5cfb\u7684\u4e5f\u4e00\u58f0\u4e0d\u542d\uff0c\u53ea\u76ef\u7740\u6025\u6551\u5ba4\u7d27\u95ed\u7740\u7684\u5927\u95e8\uff0c\u76ef\u7740\u6025\u6551\u5ba4\u5927\u95e8\u4e0a\u7684\u706f\u7bb1\u724c\u3002</p></article>"}}}]);
