(window.webpackJsonp=window.webpackJsonp||[]).push([[1150],{1173:function(p){p.exports={chapterTitle:null,title:"\u7b2c944\u7ae0 \u5927\u80f8\u812f\u4e0d\u662f\u6beb\u65e0\u7528\u5904",content:"<article><h2>\u7b2c944\u7ae0 \u5927\u80f8\u812f\u4e0d\u662f\u6beb\u65e0\u7528\u5904</h2><p>\u3000\u3000\u201c\u5200\u53e3\u523a\u53ca\u80f8\u9aa8\u540e\u5e76\u4e0d\u6df1\uff0c\u9669\u4e9b\u6ca1\u6709\u4f24\u53ca\u5fc3\u810f\uff0c\u5931\u8840\u6709\u4e9b\u591a\uff0c\u95ee\u9898\u4e5f\u4e0d\u662f\u7279\u522b\u7684\u4e25\u91cd\uff0c\u8fd8\u8981\u8fc7\u4e9b\u65f6\u95f4\u4f24\u8005\u624d\u4f1a\u9192\u8fc7\u6765\uff0c\u4f60\u4eec\u73b0\u5728\u53ef\u4ee5\u8fdb\u53bb\u770b\u4e00\u4e0b\uff0c\u8fc7\u4e00\u4f1a\u513f\u62a4\u536b\u4f1a\u5e2e\u7740\u8f6c\u5230\u75c5\u623f\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u533b\u5e08\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u7ef7\u7d27\u7684\u795e\u7ecf\u624d\u7f13\u4e0b\u6765\uff0c\u7ef7\u7d27\u7684\u795e\u7ecf\u4e00\u7f13\u4e0b\u6765\uff0c\u5c31\u89c9\u5f97\u53f3\u624b\u81c2\u75db\u5f97\u5389\u5bb3\u3002\u521a\u624d\u4ed6\u4eba\u5c31\u5728\u6025\u6551\u5ba4\u5916\u7ed9\u533b\u751f\u7f1d\u5408\u53f3\u624b\u81c2\u51e0\u4e4e\u7ed9\u523a\u7a7f\u7684\u4f24\u53e3\uff0c\u53ea\u6253\u4e86\u5c0f\u5242\u91cf\u7684\u9ebb\u9189\uff0c\u8fd9\u4f1a\u513f\u9ebb\u9189\u65e9\u5c31\u8fc7\u4e86\uff0c\u4e4b\u524d\u62c5\u5fc3\u4e39\u9752\u7684\u4f24\u60c5\uff0c\u4e5f\u6ca1\u6709\u89c9\u5f97\u624b\u81c2\u75bc\u75db\uff0c\u8fd9\u65f6\u5019\u5012\u662f\u611f\u89c9\u5230\u4e86\uff0c\u4e0d\u8fc7\u8fd8\u80fd\u5fcd\u53d7\u3002</p><p>\u3000\u3000\u53f3\u624b\u540a\u5728\u80f8\u524d\uff0c\u5f20\u606a\u5de6\u624b\u8f7b\u8f7b\u7684\u62cd\u4e86\u62cd\u536b\u5170\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u6ca1\u4e8b\u4e86\uff0c\u6211\u4eec\u8fdb\u53bb\u770b\u770b\u5427\u3002\u201d\u536b\u5170\u54ed\u5f97\u68a8\u82b1\u5e26\u96e8\u3001\u60b2\u6006\u6b32\u7edd\uff0c\u65c1\u4eba\u4e0d\u77e5\u9053\u536b\u5170\u7684\u4eba\u751f\u7ecf\u5386\uff0c\u5c31\u65e0\u6cd5\u7406\u89e3\u536b\u5170\u5bf9\u7fdf\u4e39\u9752\u7684\u611f\u60c5\uff0c\u5728\u4e00\u5b9a\u7684\u7a0b\u5ea6\u4e0a\uff0c\u7fdf\u4e39\u9752\u53ef\u4ee5\u8bf4\u662f\u536b\u5170\u7684\u7cbe\u795e\u652f\u67f1\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\uff0c\u533b\u9662\u91cc\u95fb\u8baf\u805a\u96c6\u8fc7\u6765\u8bb8\u591a\u4eba\uff0c\u4e0d\u60f3\u592a\u591a\u7684\u6253\u6405\u5230\u8fd8\u5728\u660f\u8ff7\u4e4b\u4e2d\u7684\u7fdf\u4e39\u9752\uff0c\u5f20\u606a\u5c31\u4e0e\u536b\u5170\u3001\u9759\u6aac\u8fdb\u4e86\u6025\u6551\u5ba4\u3002\u9ebb\u9189\u6ca1\u6709\u8fc7\uff0c\u7fdf\u4e39\u9752\u8fd8\u9677\u5165\u660f\u8ff7\u4e4b\u4e2d\uff0c\u7f8e\u4e3d\u7684\u8138\u86cb\u56e0\u5931\u8840\u800c\u5f02\u5e38\u7684\u82cd\u767d\uff0c\u5c31\u8ddf\u51ac\u5929\u7684\u521d\u96ea\u4e00\u822c\uff0c\u795e\u8272\u5012\u662f\u9759\u8c27\u3002\u5f20\u606a\u6293\u4f4f\u7fdf\u4e39\u9752\u5fae\u51c9\u7684\u624b\u63e1\u4e86\u63e1\uff0c\u611f\u89c9\u5230\u4f53\u6e29\uff0c\u5012\u662f\u5fc3\u5b89\u4e00\u4e9b\uff0c\u53c8\u5c06\u5979\u7684\u624b\u6396\u5230\u96ea\u767d\u88ab\u5355\u4e0b\uff0c\u8f7b\u8f7b\u7684\u8bf4\u9053\uff1a\u201c\u771f\u662f\u50bb\u554a\u2026\u2026\u6211\u54ea\u91cc\u503c\u5f97\u4f60\u4e3a\u6211\u6321\u8fd9\u4e00\u5200\uff1f\u201d</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u4e0e\u62a4\u58eb\u4e00\u540c\u5e2e\u7740\u5c06\u7fdf\u4e39\u9752\u79fb\u5230\u7279\u62a4\u75c5\u623f\uff0c\u5728\u7279\u62a4\u75c5\u91cc\u966a\u4e86\u4e00\u4f1a\u513f\uff0c\u7fdf\u4e39\u9752\u867d\u7136\u8fd8\u6ca1\u6709\u9192\u8fc7\u6765\uff0c\u4e0d\u8fc7\u770b\u4e0a\u53bb\u8ba9\u4eba\u5b89\u5fc3\u4e86\uff0c\u5085\u4fca\u8fd9\u65f6\u5019\u8d70\u8fdb\u6765\u544a\u8bc9\u4ed6\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u9646\u6587\u592b\u5df2\u7ecf\u5728\u5916\u9762\u7b49\u5f88\u4e45\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u9646\u6587\u592b\u4e4b\u5916\uff0c\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u7684\u79d8\u4e66\u67f3\u5fd7\u519b\u4ee5\u53ca\u738b\u7ef4\u5747\u3001\u59da\u6587\u76db\u90fd\u8d76\u5230\u533b\u9662\u91cc\u6765\u4e86\u3002\u5176\u5b9e\u674e\u8fdc\u6e56\u4e0e\u7f57\u541b\u4e4d\u542c\u5230\u6d88\u606f\u540e\u90fd\u51c6\u5907\u5f80\u533b\u9662\u8fd9\u91cc\u8d76\u6765\uff0c\u5728\u8fdb\u4e00\u6b65\u542c\u5230\u66f4\u8be6\u7ec6\u7684\u60c5\u51b5\uff0c\u77e5\u9053\u5f20\u606a\u53ea\u662f\u7ed9\u523a\u4f24\uff0c\u6ca1\u6709\u591a\u5927\u7684\u5371\u9669\uff0c\u5c31\u6ca1\u6709\u4eb2\u81ea\u8fde\u591c\u8d76\u8fc7\u6765\uff0c\u800c\u662f\u8ba9\u9646\u6587\u592b\u4e0e\u738b\u7ef4\u5747\u4ee3\u8868\u4ed6\u4eec\u5148\u5230\u533b\u9662\u6765\u63a2\u671b\uff0c\u9676\u664b\u5012\u662f\u76f4\u63a5\u5c31\u8ba9\u67f3\u5fd7\u519b\u5c31\u8fc7\u6765\u63a2\u671b\u4e86\u3002\u5f20\u606a\u867d\u8bf4\u6ca1\u6709\u4ec0\u4e48\u5927\u788d\uff0c\u4f46\u662f\u5173\u5fc3\u7fdf\u4e39\u9752\u80fd\u4e0d\u80fd\u633a\u8fc7\u8fd9\u4e00\u5173\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4fbf\u5fd9\u7740\u8fc7\u6765\u6253\u6270\u4ed6\uff0c\u90fd\u8010\u5fc3\u5728\u533b\u9662\u7684\u4f11\u606f\u533a\u91cc\u7b49\u5f85\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\uff0c\u786e\u5b9a\u7fdf\u4e39\u9752\u4e0d\u4f1a\u6709\u751f\u547d\u5371\u9669\uff0c\u5085\u4fca\u81ea\u7136\u8981\u8fdb\u6765\u63d0\u9192\u5f20\u606a\u4e00\u58f0\uff0c\u9664\u4e86\u4eb2\u81ea\u8d76\u5230\u533b\u9662\u7684\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u65e0\u6570\u7684\u7535\u8bdd\u6253\u8fdb\u6765\uff0c\u6709\u4e9b\u7535\u8bdd\uff0c\u5085\u4fca\u76f4\u63a5\u5e2e\u5f20\u606a\u56de\u4e86\uff0c\u8bf4\u660e\u60c5\u51b5\u5c31\u53ef\u4ee5\u4e86\uff0c\u6709\u4e9b\u7535\u8bdd\u5374\u662f\u9700\u8981\u5f20\u606a\u4eb2\u81ea\u56de\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u7ed9\u4ed6\u5988\u6302\u7535\u8bdd\uff0c\u8fd9\u624d\u77e5\u9053\u4ed6\u7238\u8ddf\u4ed6\u5988\u5df2\u7ecf\u5728\u8d76\u5f80\u5efa\u90ba\u7684\u8def\u4e0a\uff0c\u542c\u7535\u8bdd\u91cc\u54fd\u54bd\u7684\u58f0\u97f3\uff0c\u60f3\u5fc5\u4e5f\u662f\u62c5\u5fc3\u4e0d\u5f97\u4e86\uff0c\u4e0d\u8fc7\u5728\u8def\u4e0a\u5df2\u7ecf\u77e5\u9053\u4e8b\u60c5\u7684\u8be6\u60c5\uff0c\u653e\u5bbd\u4e86\u4e9b\u5fc3\uff0c\u8fd9\u65f6\u5019\u4e5f\u5df2\u7ecf\u53e6\u5916\u518d\u6d3e\u8f66\u53bb\u63a5\u7fdf\u4e39\u9752\u7684\u7236\u6bcd\u3002\u63a5\u7740\u53c8\u7ed9\u665a\u6674\u6302\u4e86\u7535\u8bdd\uff0c\u8bb8\u601d\u4e5f\u5728\u665a\u6674\u90a3\u91cc\uff0c\u5f3a\u5fcd\u4f4f\u62c5\u5fe7\u7684\u5fc3\u60c5\uff0c\u6ca1\u6709\u8fde\u591c\u8d76\u8fc7\u6765\u3002</p><p>\u3000\u3000\u4eba\u6ca1\u6709\u4e8b\uff0c\u5f20\u606a\u7684\u6001\u5ea6\u5c31\u7f13\u548c\u8fc7\u6765\uff0c\u6ca1\u6709\u521a\u624d\u4e25\u5cfb\u4e0e\u51b0\u51b7\uff0c\u8ddf\u9759\u6aac\u3001\u536b\u5170\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4fe9\u5728\u8fd9\u91cc\u5b88\u7740\u4e86\u2026\u2026\u4e39\u9752\u9192\u8fc7\u6765\u5c31\u6765\u53eb\u6211\u3002\u201d\u4e0e\u5085\u4fca\u8d70\u51fa\u75c5\u623f\uff0c\u7ecf\u8fc7\u8fc7\u9053\u62d0\u89d2\u65f6\uff0c\u5374\u542c\u89c1\u6709\u4e24\u540d\u503c\u591c\u7684\u62a4\u58eb\u5728\u89d2\u843d\u91cc\u8bae\u8bba\uff1a\u201c\u90a3\u4e2a\u5973\u4eba\u771f\u662f\u6f02\u4eae\uff0c\u80f8\u4e5f\u5f88\u5927\uff0c\u4e5f\u5e78\u4e8f\u80f8\u5927\uff0c\u5200\u4ece\u4e73\u623f\u5916\u4fa7\u659c\u523a\u7a7f\u8fdb\u53bb\uff0c\u529b\u91cf\u7ed9\u7f13\u51b2\u4e86\uff0c\u624d\u523a\u7a7f\u80f8\u808b\u9aa8\u4e00\u70b9\u70b9\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5085\u4fca\u8d70\u8fc7\u53bb\uff0c\u8eb2\u5728\u89d2\u843d\u91cc\u8bae\u8bba\u7684\u4e24\u540d\u5e74\u7eaa\u8f7b\u8f7b\u7684\u62a4\u58eb\u5413\u4e86\u4e00\u8df3\uff0c\u5410\u4e86\u5410\u820c\u5934\u81ea\u77e5\u95ef\u7978\u7684\u8d76\u7d27\u8d70\u5f00\u4e86\u3002\u542c\u5230\u8fd9\u6837\u7684\u8bae\u8bba\u8fd8\u771f\u662f\u8ba9\u4eba\u54ed\u7b11\u4e0d\u5f97\uff0c\u5f20\u606a\u53ea\u8981\u7fdf\u4e39\u9752\u6ca1\u6709\u4ec0\u4e48\u751f\u547d\u5371\u9669\uff0c\u542c\u5230\u8fd9\u6837\u7684\u8bae\u8bba\uff0c\u4ed6\u53ea\u662f\u7b11\u7b11\u4e86\u4e8b\uff0c\u867d\u8bf4\u8fd9\u6837\u7684\u8bae\u8bba\u633a\u4e0d\u5c0a\u91cd\u4eba\u7684\uff0c\u4e0d\u8fc7\u54ea\u91cc\u4f1a\u8ddf\u8fd9\u4e9b\u62a4\u58eb\u8ba1\u8f83\u8fd9\u4e9b\uff1f</p><p>\u3000\u3000\u738b\u7ef4\u5747\u3001\u9646\u6587\u592b\u4ed6\u4eec\u90fd\u5728\u533b\u9662\u6025\u8bca\u697c\u5927\u91cc\u7684\u4f11\u606f\u533a\u7b49\u7740\uff0c\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8ba9\u4f60\u4eec\u62c5\u5fc3\u4e86\uff0c\u8fd9\u6df1\u66f4\u534a\u591c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4eba\u6ca1\u4e8b\u6211\u4eec\u4e5f\u80fd\u5bbd\u5fc3\uff0c\u201d\u9646\u6587\u592b\u6307\u4e86\u6307\u5f20\u606a\u540a\u5728\u80f8\u524d\u7684\u624b\u81c2\uff0c\u8bf4\u9053\uff0c\u201c\u600e\u4e48\u4e0d\u4f4f\u8fdb\u75c5\u623f\u91cc\uff1f\u4f60\u8fd9\u6837\u4e5f\u4e0d\u8981\u968f\u4fbf\u4e71\u8d70\uff0c\u603b\u8981\u5728\u533b\u9662\u91cc\u517b\u597d\u4f24\u518d\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u624d\u902e\u4f4f\u56db\u4e2a\u4eba\u5417\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u53f3\u624b\u81c2\u7684\u5f62\u8c61\u8fd8\u771f\u662f\u51c4\u60e8\uff0c\u5916\u5957\u8fde\u540c\u886c\u886b\u7684\u8863\u8896\u9f50\u80f3\u818a\u8098\u7ed9\u526a\u6389\uff0c\u7f20\u4e0a\u7ef7\u5e26\u540a\u6302\u5728\u80f8\u524d\uff0c\u201c\u6211\u60f3\u7740\u662f\u4e0d\u662f\u8981\u53bb\u516c\u5b89\u5c40\u7b49\u6d88\u606f\uff0c\u201d\u8fd9\u65f6\u5019\u624d\u60f3\u8d77\u8981\u95ee\u5085\u4fca\u4e00\u4e9b\u4e8b\u60c5\u5904\u7406\u7684\u7ec6\u8282\uff0c\u201c\u4e8b\u60c5\u662f\u4ea4\u7ed9\u5e02\u5c40\u8fd8\u662f\u533a\u5206\u5c40\u5904\u7406\uff1f\u201d</p><p>\u3000\u3000\u738b\u7ef4\u5747\u77e5\u9053\u5f20\u606a\u5bf9\u6682\u65f6\u9003\u8131\u7684\u90a3\u4e2a\u4eba\u4e5f\u6068\u4e4b\u5165\u9aa8\uff0c\u8bf4\u9053\uff1a\u201c\u7f57\u4e66\u8bb0\u77e5\u9053\u540e\u7ed9\u5e02\u5c40\u6253\u4e86\u547d\u4ee4\uff0c\u8981\u6c42\u5e02\u5c40\u8fde\u591c\u8c03\u52a8\u4e00\u5207\u80fd\u8c03\u52a8\u7684\u8b66\u529b\u6293\u6355\u9003\u8131\u7684\u90a3\u4e2a\u884c\u51f6\u5acc\u7591\u4eba\u2026\u2026\u91d1\u5385\u957f\u4e5f\u8ba9\u7701\u5385\u4e5f\u6d3e\u4e86\u4e13\u5bb6\u914d\u5408\u5e02\u5c40\u7684\u6293\u6355\u5de5\u4f5c\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u5c31\u53bb\u5e02\u5c40\u7b49\u6d88\u606f\u3002\u201d\u5f20\u606a\u795e\u60c5\u4e00\u70b9\u90fd\u4e0d\u4e25\u5cfb\uff0c\u53e3\u6c14\u4e5f\u8f7b\u677e\u7684\u8bf4\u7740\u8bdd\uff0c\u671d\u738b\u7ef4\u5747\u3001\u9646\u6587\u592b\u8bf4\u9053\uff0c\u201c\u5927\u534a\u591c\u60ca\u52a8\u4f60\u4eec\uff0c\u771f\u8fc7\u610f\u4e0d\u53bb\uff0c\u73b0\u5728\u6ca1\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff0c\u6539\u5929\u518d\u8ddf\u4f60\u4eec\u8c22\u7f6a\u554a\u3002\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u4e0d\u77e5\u9053\u4ed6\u4eec\u4e00\u7fa4\u4eba\u966a\u540c\u5f20\u606a\u8d76\u5230\u5e02\u5c40\u53bb\u7b49\u6d88\u606f\u3001\u5e02\u5c40\u7684\u5934\u5934\u8111\u8111\u4eec\u4f1a\u6709\u4ec0\u4e48\u5fc3\u60c5\u3001\u4f1a\u611f\u53d7\u5230\u600e\u6837\u7684\u538b\u529b\uff0c\u4f46\u662f\u5f20\u606a\u575a\u6301\u8981\u53bb\u5e02\u5c40\u5750\u7b49\u6d88\u606f\uff0c\u4ed6\u4e0e\u738b\u7ef4\u5747\u5f53\u7136\u4e0d\u4f1a\u771f\u5c31\u56de\u5bb6\u7761\u89c9\u53bb\uff0c\u4e5f\u53ea\u80fd\u8ddf\u7740\u4e00\u8d77\u53bb\u5e02\u5c40\uff0c\u5f20\u606a\u7684\u5fc3\u60c5\u5012\u662f\u80fd\u591f\u7406\u89e3\uff0c\u8c01\u7684\u751f\u547d\u53d7\u5230\u5a01\u80c1\uff0c\u7adf\u7136\u8fd8\u8981\u4e0b\u5c5e\u594b\u4e0d\u987e\u8eab\u7684\u6321\u4e00\u5200\uff0c\u95f9\u4e9b\u6027\u5b50\u90fd\u662f\u5e94\u8be5\u7684\uff0c\u4e0d\u7136\u4e5f\u592a\u6ca1\u6709\u813e\u6c14\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u9646\u6587\u592b\u3001\u738b\u7ef4\u5747\u7b49\u4eba\u5916\uff0c\u9526\u6e56\u4f53\u7cfb\u91cc\u5728\u5efa\u90ba\u7684\u9ad8\u5c42\u542c\u5230\u6d88\u606f\u4e5f\u90fd\u8d76\u5230\u533b\u9662\u6765\uff0c\u5f20\u606a\u8ba9\u4ed6\u4eec\u5148\u56de\u53bb\u4f11\u606f\uff0c\u4e5f\u771f\u662f\u5e0c\u671b\u4ed6\u4eec\u5148\u56de\u53bb\u4f11\u606f\u3002</p><p>\u3000\u3000\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u542c\u5230\u6d88\u606f\u540e\u8d76\u8fc7\u6765\uff0c\u8fd9\u65f6\u5019\u90fd\u7559\u5728\u533b\u9662\u91cc\uff0c\u67f3\u5fd7\u6210\u3001\u90b5\u5fc3\u6587\u3001\u5468\u4e00\u5e73\u7b49\u4eba\u5f97\u77e5\u7fdf\u4e39\u9752\u4e00\u65f6\u534a\u4f1a\u9ebb\u9189\u8fd8\u9000\u4e0d\u4e86\uff0c\u5c31\u5148\u79bb\u5f00\u4e86\uff0c\u56fd\u5546\u9662\u9662\u957f\u517c\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u4e3b\u4efb\u5d14\u56fd\u6052\u4e0e\u56fd\u5546\u4e5d\u4e03\u7ea7\u5e74\u7ea7\u4e3b\u4efb\u5d14\u90c1\u66fc\u542c\u5230\u6d88\u606f\u4e5f\u90fd\u8d76\u5230\u533b\u9662\u6765\uff0c\u8fd9\u65f6\u5019\u4e5f\u5148\u79bb\u5f00\u533b\u9662\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u9646\u6587\u592b\u3001\u738b\u7ef4\u5747\u7b49\u4eba\u7684\u966a\u540c\u4e0b\uff0c\u76f4\u63a5\u524d\u5f80\u5e02\u516c\u5b89\u5c40\uff0c\u76ef\u7740\u5e02\u516c\u5b89\u5c40\u8fde\u591c\u6293\u6355\u9003\u8131\u7684\u5acc\u7591\u72af\u3002</p><p>\u3000\u3000\u5f20\u606a\u575a\u6301\u8981\u5230\u5e02\u5c40\u5750\u7b49\u6d88\u606f\uff0c\u5e02\u653f\u6cd5\u59d4\u4e66\u8bb0\u4f59\u5e86\u6797\u81ea\u7136\u4e5f\u4e0d\u80fd\u5728\u5bb6\u91cc\u5750\u7b49\u6d88\u606f\uff0c\u5728\u63a5\u5230\u7535\u8bdd\u901a\u77e5\u540e\uff0c\u4e5f\u8d76\u7d27\u5750\u8f66\u8d76\u5230\u5e02\u516c\u5b89\u5c40\u3002</p><p>\u3000\u3000\u867d\u8bf4\u7701\u957f\u674e\u8fdc\u6e56\u3001\u7701\u59d4\u5e38\u59d4\u3001\u7701\u516c\u5b89\u5385\u5385\u957f\u91d1\u56fd\u6d77\u3001\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u6ca1\u6709\u8dd1\u5230\u5c40\u91cc\u6765\uff0c\u4f46\u662f\u8eab\u4e3a\u5e02\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u516c\u5b89\u5c40\u5c40\u957f\u7684\u9648\u79c9\u5fb7\u5fc3\u91cc\u6e05\u695a\uff0c\u4ed6\u4eec\u8fd9\u65f6\u5019\u90fd\u4e0d\u4f1a\u5b89\u5fc3\u7684\u7761\u89c9\uff0c\u6709\u5c0a\u5927\u795e\u5728\u5c40\u91cc\u76ef\u7740\u5462\u3002\u9646\u6587\u592b\u662f\u674e\u8fdc\u6e56\u7684\u4ee3\u8868\uff0c\u738b\u7ef4\u5747\u672c\u8eab\u5c31\u662f\u5e02\u59d4\u5e38\u59d4\uff0c\u53c8\u4ee3\u8868\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\uff0c\u91d1\u56fd\u6d77\u76f4\u63a5\u6d3e\u7701\u5385\u7684\u4e13\u5bb6\u8fc7\u6765\u534f\u52a9\u6293\u6355\u3001\u5ba1\u8baf\uff0c\u96be\u9053\u8981\u91d1\u56fd\u6d77\u5c06\u4e8b\u60c5\u505a\u5f97\u66f4\u660e\u663e\u3001\u4eb2\u81ea\u8dd1\u5230\u5e02\u5c40\u6765\u7763\u529e\u5417\uff1f</p><p>\u3000\u3000\u8981\u662f\u7834\u6848\u7684\u65f6\u95f4\u62d6\u592a\u4e45\uff0c\u9648\u79c9\u5fb7\u77e5\u9053\u81ea\u5df1\u94c1\u5b9a\u9003\u4e0d\u4e86\u4e00\u4e2a\u201c\u65e0\u80fd\u201d\u7684\u8bc4\u4ef7\u3002</p><p>\u3000\u3000\u9664\u4e86\u90e8\u7f72\u8b66\u529b\u5b9e\u65bd\u6293\u6355\u9003\u8131\u4e00\u4eba\u4e4b\u5916\uff0c\u5e02\u516c\u5b89\u5c40\u8fd8\u8981\u7ec4\u7ec7\u4eba\u624b\u8fde\u591c\u5ba1\u8baf\u7ed9\u6293\u4f4f\u7684\u4e09\u4eba\uff0c\u8981\u641e\u6e05\u695a\u4ed6\u4eec\u9488\u5bf9\u5f20\u606a\u7684\u80c1\u6301\u5e55\u540e\u6709\u6ca1\u6709\u66f4\u5927\u7684\u9634\u8c0b\u2014\u2014\u8fd8\u6709\u4e00\u4eba\uff0c\u5c31\u662f\u90a3\u4e2a\u7a0d\u6709\u4e9b\u73a9\u547d\u6c14\u6168\u3001\u4e00\u5200\u523a\u5165\u7fdf\u4e39\u9752\u80f8\u53e3\u7684\u9752\u5e74\u5728\u4ed6\u518d\u5ea6\u5c06\u5f39\u7c27\u5200\u523a\u5411\u5f20\u606a\u4e4b\u9645\u7ed9\u53ca\u65f6\u8d76\u5230\u7684\u4fdd\u536b\u4e00\u811a\u8e22\u4e2d\u8170\u773c\u8fd9\u4e00\u8981\u5bb3\u90e8\u4f4d\uff0c\u73b0\u5728\u9001\u8fdb\u53e6\u5916\u4e00\u6240\u533b\u9662\u6025\u6551\uff0c\u6551\u6d3b\u7684\u5e0c\u671b\u4e0d\u5927\u3002\u5e02\u5c40\u4e5f\u8fde\u591c\u901a\u77e5\u6c5f\u5357\u7701\u8b66\u65b9\uff0c\u884c\u51f6\u5df2\u7ecf\u7ed9\u6293\u83b7\u5f97\u56db\u4eba\uff0c\u90fd\u662f\u6c5f\u5357\u7ae0\u5dde\u4eba\uff0c\u9664\u4e86\u5e08\u8303\u5927\u5b66\u5927\u56db\u5b66\u751f\u5510\u5fe0\u4e4b\u5916\uff0c\u5176\u4ed6\u4e09\u4e2a\u4eba\u4e24\u5929\u524d\u4ece\u7ae0\u5dde\u5230\u5efa\u90ba\u7684\uff0c\u8fd8\u6709\u4e00\u4eba\u5c06\u4e24\u5929\u524d\u7684\u8f66\u7968\u7559\u5728\u76ae\u5939\u91cc\u2014\u2014\u8fd9\u4e9b\u4eba\u5e94\u8be5\u662f\u5510\u6c0f\u5144\u5f1f\u5728\u7ae0\u5dde\u7684\u6b8b\u4f59\u52bf\u529b\u3002\u867d\u8bf4\u518d\u7f1c\u5bc6\u7684\u8c03\u67e5\u90fd\u4e0d\u53ef\u80fd\u5c06\u72af\u7f6a\u4efd\u5b50\u4e00\u7f51\u6253\u5c3d\uff0c\u4f46\u662f\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u8981\u8ffd\u8d77\u8d23\u4efb\u6765\uff0c\u6c5f\u5357\u7701\u8b66\u65b9\u662f\u63a8\u8131\u4e0d\u5e72\u51c0\u7684\uff0c\u5f20\u606a\u4ed6\u4eec\u5230\u5e02\u5c40\u540e\u4e0d\u4e45\uff0c\u6c5f\u5357\u7701\u8b66\u65b9\u7531\u5e38\u52a1\u526f\u5385\u957f\u5e26\u961f\u7684\u534f\u8c03\u5c0f\u7ec4\u5e26\u7740\u7ae0\u5dde\u6848\u7684\u4fa6\u67e5\u5377\u5b97\u5c31\u8d76\u5230\u5efa\u90ba\u4e86\uff0c\u5e0c\u671b\u80fd\u5bf9\u6293\u6355\u4e0e\u5ba1\u8baf\u5de5\u4f5c\u6709\u6240\u5e2e\u52a9\u3002</p><p>\u3000\u3000\u9664\u4e86\u6293\u6355\u9003\u8131\u7684\u4e00\u4eba\u4e4b\u5916\uff0c\u5f20\u606a\u8fd8\u5173\u5fc3\u5510\u5fe0\u662f\u4ece\u54ea\u91cc\u77e5\u9053\u9526\u6e56\u4e3b\u5bfc\u4e86\u5bf9\u7ae0\u5dde\u7a00\u571f\u77ff\u4e1a\u7684\u6e05\u67e5\u5de5\u4f5c\u4ee5\u53ca\u81ea\u5df1\u7684\u771f\u5b9e\u8eab\u4efd\uff0c\u5f20\u606a\u8981\u77e5\u9053\u81ea\u5df1\u7684\u654c\u4eba\u5230\u5e95\u662f\u8c01\uff0c\u8fd9\u6b21\u7ed9\u5c04\u4e86\u4e00\u51fb\u51b7\u7bad\uff0c\u4ed6\u53ef\u4e0d\u60f3\u4e00\u76f4\u7ed9\u5c04\u51b7\u7bad\uff0c\u7fdf\u4e39\u9752\u8fd9\u6b21\u662f\u547d\u597d\uff0c\u6ca1\u6709\u5927\u788d\uff0c\u6362\u4f5c\u4e0b\u4e00\u6b21\u5462\uff1f\u8fd9\u4e2a\u50bb\u5973\u4eba\u554a\uff0c\u5f20\u606a\u5fc3\u91cc\u8f7b\u8f7b\u7684\u53f9\u9053\uff1a\u4f60\u8981\u6709\u4e09\u957f\u4e24\u77ed\uff0c\u6211\u8fd9\u8f88\u5b50\u5982\u4f55\u80fd\u5b89\u5fc3\uff1f</p><p>\u3000\u3000\u884c\u51f6\u5acc\u7591\u4eba\u501f\u7740\u591c\u8272\u9003\u8131\uff0c\u60f3\u8981\u7acb\u65f6\u6293\u6355\u5f52\u6848\u7684\u96be\u5ea6\u5f88\u5927\uff0c\u4eba\u5bb6\u5728\u54ea\u4e2a\u65ee\u65ef\u91cc\u732b\u7740\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6d3e\u51fa\u6240\u6709\u7684\u8b66\u529b\u5c06\u71d5\u5f52\u6e56\u5730\u533a\u5f7b\u5e95\u5c01\u9501\u641c\u7d22\u6bcf\u4e00\u5bf8\u7684\u571f\u5730\u3002\u6293\u6355\u884c\u4e3a\u80fd\u4e0d\u80fd\u5728\u6700\u5feb\u65f6\u95f4\u91cc\u5b8c\u6210\uff0c\u5e02\u516c\u5b89\u5c40\u8fd9\u8fb9\u6beb\u65e0\u628a\u63e1\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0e\u6881\u683c\u73cd\u5df2\u7ecf\u8d76\u5230\u5efa\u90ba\u4e86\uff0c\u4ed6\u4eec\u6ca1\u5230\u5e02\u5c40\u6765\uff0c\u800c\u662f\u76f4\u63a5\u53bb\u4e86\u533b\u9662\uff0c\u4eba\u5230\u533b\u9662\u540e\u624d\u6253\u7535\u8bdd\u7ed9\u5f20\u606a\u3002\u5f20\u606a\u4e5f\u4e0d\u80fd\u6b7b\u6d3b\u8d56\u5728\u5e02\u516c\u5b89\u5c40\u4e0d\u8d70\uff0c\u4ed6\u95ee\u966a\u540c\u4ed6\u4e00\u8d77\u5750\u7b49\u6d88\u606f\u7684\u653f\u6cd5\u59d4\u4e66\u8bb0\u4f59\u5e86\u6797\uff1a\u201c\u6211\u8981\u8ddf\u90a3\u4e2a\u5510\u5fe0\u89c1\u4e00\u9762\u2026\u2026\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u907f\u8bb3\u7684\u5730\u65b9\uff1f\u201d</p><p>\u3000\u3000\u5373\u4f7f\u6709\u907f\u8bb3\u7684\u5730\u65b9\uff0c\u4f59\u5e86\u6797\u6b64\u65f6\u4e5f\u4e0d\u4f1a\u62d2\u7edd\u5f20\u606a\u7684\u8981\u6c42\uff0c\u627e\u6765\u5e02\u5c40\u5c40\u957f\u9648\u79c9\u5fb7\u5b89\u6392\u5f20\u606a\u4e0e\u7ed9\u6293\u6355\u5f52\u6848\u7684\u5510\u5fe0\u501f\u5ba1\u8baf\u5ba4\u7684\u5730\u76d8\u8c08\u4e00\u8c08\u3002</p></article>"}}}]);
