(window.webpackJsonp=window.webpackJsonp||[]).push([[1155],{1178:function(p){p.exports={chapterTitle:null,title:"\u7b2c948\u7ae0 \u9ec4\u660f\u96e8\u4e2d\u7684\u5c11\u5973\u4e0e\u5927\u8272\u72fc",content:"<article><h2>\u7b2c948\u7ae0 \u9ec4\u660f\u96e8\u4e2d\u7684\u5c11\u5973\u4e0e\u5927\u8272\u72fc</h2><p>\u3000\u3000\u5c31\u7b97\u73b0\u5728\uff0c\u5916\u754c\u53ea\u77e5\u9053\u9526\u6e56\u5546\u4e8b\u5728\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u6574\u5408\u4e2d\u5360\u4e86\u4fbf\u5b9c\uff0c\u6301\u6709\u65b0\u534e\u7a00\u77ff\u4e1a12\uff05\u7684\u80a1\u6743\uff0c\u9526\u6e56\u5546\u4e8b\u4e4b\u6240\u4ee5\u80fd\u5360\u8fd9\u4e2a\u4fbf\u5b9c\u7684\u80cc\u540e\u539f\u56e0\u5374\u751a\u5c11\u6709\u4eba\u77e5\u9053\u3002\u5728\u5ea7\u7684\u4e09\u4eba\u9664\u4e86\u9648\u99a5\u6770\u56e0\u4e3a\u6c5f\u5357\u7701\u8b66\u65b9\u5168\u9762\u4ecb\u5165\u7ae0\u5dde\u6848\u800c\u77e5\u6089\u4e8b\u60c5\u7684\u7ec6\u8282\u5916\uff0c\u4f59\u5e86\u6797\u3001\u9648\u79c9\u5fb7\u90fd\u4e0d\u6e05\u695a\u4e8b\u60c5\u7684\u5168\u8c8c\uff0c\u8c01\u4f1a\u5c06\u80cc\u540e\u7684\u5b9e\u60c5\u544a\u8bc9\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\uff0c\u4ee5\u81f4\u4ed6\u4eec\u5728\u5782\u6b7b\u6323\u624e\u4e4b\u9645\u7adf\u7136\u60f3\u5230\u8981\u5c06\u631f\u6301\u5f20\u606a\u5f53\u6210\u6700\u540e\u4e00\u6839\u7a3b\u8349\uff1f</p><p>\u3000\u3000\u867d\u8bf4\u77e5\u9053\u4e8b\u60c5\u5168\u8c8c\u7684\u4eba\u6781\u5c11\uff0c\u5374\u4e5f\u4e0d\u662f\u6ca1\u6709\u3002\u50cf\u8c22\u5bb6\u5c31\u7b97\u5468\u747e\u73ba\u6210\u4e86\u690d\u7269\u4eba\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u96be\u63a8\u6d4b\u51fa\u9526\u6e56\u5728\u7ae0\u5dde\u6848\u80cc\u540e\u53d1\u6325\u7684\u4f5c\u7528\uff1b\u4e09\u4e95\u7269\u4ea7\u5728\u6b64\u6b21\u4e8b\u4ef6\u53d7\u5230\u6253\u51fb\u6700\u5927\uff0c\u4ece\u4e2d\u56fd\u8fdb\u53e3\u7a00\u571f\u7cbe\u77ff\u7684\u8d38\u6613\u94fe\u7ed9\u4e00\u4e0b\u5b50\u7aef\u6389\uff0c\u4ed6\u4eec\u4e5f\u5b8c\u5168\u6709\u80fd\u529b\u987a\u85e4\u6478\u74dc\u6478\u5230\u9526\u6e56\u5934\u4e0a\u6765\uff1b\u53e6\u5916\uff0c\u6709\u6ca1\u6709\u53ef\u80fd\u662f\u4e25\u6587\u4ecb\u5462\uff1f\u5b8f\u4fe1\u5efa\u8bbe\u7684\u89e6\u624b\u4f38\u5230\u4f53\u7cfb\u5185\u5982\u6b64\u4e4b\u6df1\uff0c\u8981\u4e86\u89e3\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u6574\u5408\u80cc\u540e\u7684\u6839\u6e90\u4e5f\u4e0d\u662f\u96be\u4e8b\u3002</p><p>\u3000\u3000\u65e0\u8bba\u662f\u5468\u747e\u745c\u3001\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u4e00\u5bb6\u5b50\uff0c\u8fd8\u662f\u4e09\u4e95\u7269\u4ea7\uff0c\u6291\u6216\u662f\u5b8f\u4fe1\u5efa\u8bbe\uff0c\u53ea\u8981\u5bf9\u9526\u6e56\u4e0d\u5229\uff0c\u4efb\u4f55\u63a8\u6ce2\u52a9\u6f9c\u7684\u4e8b\u60c5\u4ed6\u4eec\u90fd\u4f1a\u4e50\u610f\u505a\u7684\u2014\u2014\u53cd\u8fc7\u6765\u5f20\u606a\u4e5f\u7edd\u4e0d\u4f1a\u5fc3\u6148\u624b\u8f6f\u7684\u3002</p><p>\u3000\u3000\u542c\u4f59\u5e86\u6797\u3001\u5b59\u99a5\u6770\u3001\u9648\u79c9\u5fb7\u6c47\u62a5\u8fc7\u6848\u60c5\u8c03\u67e5\u8fdb\u5c55\uff0c\u5f20\u606a\u6ca1\u6709\u505a\u4ec0\u4e48\u8bc4\u8bba\uff0c\u5f88\u516c\u5f0f\u5316\u7684\u8bf4\u9053\uff1a\u201c\u8fd8\u8981\u9ebb\u70e6\u4e24\u5730\u7684\u8b66\u65b9\u8d39\u5fc3\u8c03\u67e5\u2026\u2026\u201d\u6ca1\u6709\u7559\u4ed6\u4eec\u4e0b\u6765\u7528\u9910\u7684\u610f\u601d\uff0c\u533b\u9662\u91cc\u7528\u9910\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u4e0e\u7236\u4eb2\u3001\u4e0e\u5b59\u5c1a\u4e49\u4e00\u8d77\u9001\u4ed6\u4eec\u8d70\u8fdb\u7535\u68af\u4e3a\u6b62\u3002</p><p>\u3000\u3000\u201c\u5c31\u7b97\u80fd\u67e5\u51fa\u4e9b\u86db\u4e1d\u9a6c\u8ff9\uff0c\u4f46\u60f3\u8981\u6293\u4f4f\u4ec0\u4e48\u628a\u67c4\u4f3c\u4e4e\u4e5f\u662f\u5962\u60f3\uff0c\u201d\u5f20\u606a\u6298\u8eab\u5f80\u75c5\u623f\u8d70\uff0c\u8ddf\u4ed6\u7236\u4eb2\u4ee5\u53ca\u5b59\u5c1a\u4e49\u8bf4\u9053\uff0c\u201c\u8981\u662f\u80fd\u5c06\u7ae0\u5dde\u6848\u7684\u4e3b\u8981\u6848\u72af\u9001\u4e0a\u5211\u573a\uff0c\u95f9\u8fd9\u4e48\u4e00\u573a\uff0c\u4e5f\u662f\u503c\u5f97\u3002\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u70b9\u70b9\u5934\uff0c\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5e78\u597d\u53ea\u662f\u865a\u60ca\u4e00\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8bf4\u9053\uff1a\u201c\u63a8\u6ce2\u52a9\u6f9c\u7684\u4e8b\u60c5\u6216\u8bb8\u662f\u6709\u7684\uff0c\u4f46\u5e94\u8be5\u4e0d\u80fd\u7b97\u662f\u523b\u610f\u800c\u4e3a\u7684\u9634\u8c0b\u5427\u2026\u2026\u201d\u4ed6\u8fd8\u662f\u6709\u4e9b\u62c5\u5fc3\u5f20\u606a\u8fc7\u6df1\u7684\u53bb\u5e72\u6d89\u56fd\u5185\u7684\u53f8\u6cd5\u4f53\u7cfb\u8fd0\u4f5c\uff0c\u8fd9\u662f\u76f8\u5f53\u73a9\u706b\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u6ca1\u6709\u8c01\u4f1a\u5c06\u5e0c\u671b\u5bc4\u6258\u5510\u5fe0\u8fd9\u51e0\u4e2a\u4eba\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u800c\u5782\u6b7b\u6323\u624e\u7684\u5510\u5bb6\u5144\u5f1f\u968f\u65f6\u90fd\u53ef\u80fd\u53cd\u54ac\u4e00\u53e3\uff0c\u8981\u80fd\u987a\u85e4\u6478\u74dc\u67e5\u5230\u4ec0\u4e48\u628a\u67c4\uff0c\u8fd9\u6837\u7684\u89d2\u8272\u8fd8\u771f\u4e0d\u914d\u505a\u9526\u6e56\u7684\u5bf9\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u7b11\u4e86\u7b11\uff0c\u89c1\u5f20\u606a\u9664\u4e86\u4e0d\u653e\u8fc7\u5510\u5bb6\u4eba\u4e4b\u5916\uff0c\u5176\u4ed6\u66f4\u591a\u7684\u5f53\u6210\u610f\u5916\u5904\u7f6e\uff0c\u6216\u8bb8\u662f\u6700\u6b63\u786e\u7684\u65b9\u5f0f\u3002\u4e5f\u5e78\u4e8f\u53ea\u662f\u865a\u60ca\u4e00\u573a\uff0c\u5bf9\u4e8e\u6784\u67b6\u8fd8\u5f88\u6563\u4e71\u7684\u9526\u6e56\u4f53\u7cfb\u6765\uff0c\u8fd8\u6ca1\u6709\u6b63\u5f0f\u7684\u5f62\u6210\u96c6\u4f53\u51b3\u7b56\u7684\u4f53\u5236\uff0c\u6240\u8c13\u7684\u8463\u4e8b\u4f1a\u51b3\u7b56\u673a\u5236\uff0c\u53c8\u4e0d\u9002\u5408\u7efc\u5408\u5546\u793e\u5f0f\u7684\u5927\u578b\u8d22\u56e2\uff0c\u6b64\u65f6\u8fd8\u6ca1\u6709\u8c01\u6216\u8005\u54ea\u4e2a\u6b63\u5f0f\u7684\u96c6\u4f53\u51b3\u7b56\u4f53\u5236\u80fd\u66ff\u4ee3\u5f20\u606a\u6b64\u65f6\u5728\u9526\u6e56\u4f53\u7cfb\u5185\u53d1\u6325\u7684\u4f5c\u7528\uff0c\u771f\u4e0d\u80fd\u60f3\u8c61\u5f20\u606a\u4e00\u65e6\u751f\u51fa\u53d8\u6545\u5bf9\u9526\u6e56\u662f\u4f55\u7b49\u6050\u6016\u7684\u707e\u96be\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5b59\u5c1a\u4e49\u5728\u9526\u6e56\u5185\u5f88\u53d7\u5c0a\u91cd\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u6e05\u695a\uff0c\u53d7\u5c0a\u91cd\u4e0e\u80fd\u5426\u6210\u4e3a\u4f53\u7cfb\u7684\u7075\u9b42\u4eba\u5fc3\u662f\u6709\u5f88\u5927\u533a\u522b\u7684\u3002</p><p>\u3000\u3000\u8fb9\u8bf4\u7740\u8bdd\uff0c\u8fb9\u5f80\u56de\u8d70\uff0c\u8d70\u5230\u75c5\u623f\u524d\uff0c\u6881\u683c\u73cd\u63a2\u51fa\u5934\u8bf4\u5f20\u606a\u4ed6\u5c0f\u5a76\u8ddf\u5976\u5976\u8d76\u8fc7\u6765\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u4f1a\u653e\u8fc7\u5510\u5fe0\u4ee5\u53ca\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\uff0c\u4e0d\u8fc7\u8fd9\u8fb9\uff0c\u4ed6\u5b81\u53ef\u5bb6\u4eba\u5f53\u8fd9\u4ef6\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\u8fc7\uff0c\u4e0d\u8981\u90fd\u5f80\u8fd9\u91cc\u62e5\u3002\u8fd9\u4e48\u591a\u4eba\uff0c\u4ed6\u60f3\u652f\u5f00\u5510\u5a67\u8ddf\u7fdf\u4e39\u9752\u5355\u72ec\u8bf4\u4e2a\u8bdd\u4e5f\u4e0d\u884c\uff0c\u5230\u7fdf\u4e39\u9752\u90a3\u5c4b\u5b50\u91cc\u8ddf\u5927\u5bb6\u5750\u4e0b\u6765\u8bf4\u8bdd\uff0c\u6253\u7535\u8bdd\u7ed9\u4e00\u4e9b\u4eb2\u8fd1\u7684\u4eba\u62a5\u5e73\u5b89\uff0c\u4e5f\u662f\u5bb3\u6015\u4eba\u5bb6\u90fd\u6345\u5230\u533b\u9662\u91cc\u6765\u63a2\u89c6\u3002</p><p>\u3000\u3000\u8fd9\u91cc\u65e2\u7136\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5f20\u77e5\u884c\u51b3\u5b9a\u591c\u91cc\u5c31\u56de\u65b0\u829c\u53bb\uff0c\u5e02\u91cc\u8fd8\u6709\u4e00\u5806\u4e8b\u60c5\uff0c\u6709\u4e9b\u5de5\u4f5c\u4e5f\u662f\u4e34\u65f6\u4e22\u4e0b\u6765\u7684\u3002\u7fdf\u7236\u4e5f\u575a\u6301\u8981\u56de\u53bb\uff0c\u7537\u4eba\u7684\u611f\u60c5\u603b\u662f\u7c97\u7cd9\u4e9b\uff0c\u89c1\u5973\u513f\u6ca1\u6709\u751f\u547d\u5371\u9669\uff0c\u867d\u7136\u8fd8\u53ea\u80fd\u8eba\u5728\u75c5\u5e8a\u4e0a\u65e0\u6cd5\u4fa7\u8eab\uff0c\u7559\u4e0b\u5979\u5988\u7167\u987e\u5c31\u8db3\u591f\u4e86\uff0c\u5728\u533b\u9662\u8349\u8349\u7684\u5403\u8fc7\u665a\u996d\uff0c\u5c31\u8ddf\u5f20\u77e5\u884c\u4e00\u9053\u56de\u65b0\u829c\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5b81\u613f\u4ed6\u5988\u591c\u91cc\u4e00\u8d77\u56de\u65b0\u829c\u53bb\uff0c\u4f46\u662f\u4e8b\u4e8b\u90fd\u672a\u5fc5\u5982\u4ed6\u6240\u613f\uff0c\u4ed6\u5988\u8fd8\u6709\u7fdf\u6bcd\u90fd\u8981\u7559\u5728\u533b\u9662\u91cc\u7167\u6599\u4ed6\u4eec\uff1b\u5510\u5a67\u4e5f\u662f\u4e34\u65f6\u4ece\u5b66\u6821\u91cc\u8bf7\u5047\u4ece\u9999\u6e2f\u56de\u6765\uff0c\u8fc7\u4e86\u4eca\u591c\u5c31\u8981\u56de\u9999\u6e2f\u7684\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u770b\u7740\u7236\u4eb2\u7684\u8f66\u4e0e\u5b59\u5c1a\u4e49\u7684\u8f66\u5b50\u524d\u540e\u9a76\u51fa\u533b\u9662\u7684\u5927\u95e8\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u7275\u624b\u533b\u9662\u91cc\u6e9c\u8fbe\u3002\u5165\u591c\u540e\uff0c\u7701\u4eba\u6c11\u533b\u9662\u6700\u5fd9\u788c\u7684\u5730\u65b9\u662f\u6025\u8bca\u5927\u697c\uff0c\u7efc\u5408\u697c\u4e0e\u4f4f\u9662\u90e8\u5927\u697c\u662f\u5171\u4e00\u5ea7\u88d9\u697c\u7684\u4e24\u5ea7\u5854\u697c\uff0c\u8fd9\u8fb9\u5c31\u8981\u51b7\u6e05\u5e7d\u9759\u591a\u4e86\uff0c\u5076\u5c14\u6709\u7a7f\u767d\u8902\u7684\u533b\u751f\u4e0e\u7a7f\u62a4\u58eb\u670d\u7684\u62a4\u58eb\u7ecf\u8fc7\uff0c\u4fbf\u6ca1\u6709\u5176\u4ed6\u4ec0\u4e48\u4eba\u5b66\u5f20\u606a\u5728\u533b\u9662\u91cc\u6563\u6b65\u3002</p><p>\u3000\u3000\u768e\u6d01\u7684\u6708\u8272\u8ba9\u533b\u9662\u91cc\u770b\u4e0a\u53bb\u5f02\u5e38\u7684\u5e72\u51c0\uff0c\u5510\u5a67\u5a34\u9759\u7684\u7ed9\u5f20\u606a\u7275\u7740\u624b\uff0c\u8fb9\u8d70\u8fb9\u8bf4\u8bdd\uff0c\u5a07\u67d4\u7f8e\u4e3d\u7684\u8138\u86cb\u3001\u9ad8\u6311\u7ea4\u67d4\u7684\u8eab\u5b50\u5374\u4e0d\u80af\u4f9d\u504e\u5728\u5f20\u606a\u7684\u6000\u91cc\uff1b\u539f\u56e0\u65e0\u4ed6\uff0c\u5085\u4fca\u4e0e\u4e09\u540d\u4fdd\u9556\u8d34\u8eab\u8ddf\u7740\u4ed6\u4eec\uff0c\u59cb\u7ec8\u4fdd\u6301\u5728\u89c6\u7ebf\u4e4b\u5185\uff0c\u5bb3\u7f9e\u7684\u5510\u5a67\u54ea\u6709\u52c7\u6c14\u5728\u522b\u4eba\u7684\u773c\u91cc\u8ddf\u5f20\u606a\u505a\u604b\u4eba\u95f4\u4eb2\u6635\u7684\u4e8b\u60c5\uff1f</p><p>\u3000\u3000\u8981\u60f3\u4e0d\u88ab\u4ed6\u5988\u8bad\u65a5\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u53ef\u6ca1\u6709\u52c7\u6c14\u53d6\u6d88\u5de5\u4f5c\u4eba\u5458\u5bf9\u4ed6\u7684\u8d34\u8eab\u4fdd\u536b\uff0c\u53ea\u662f\u8bd5\u63a2\u6027\u7684\u770b\u4e86\u5085\u4fca\u4e00\u773c\u3002\u5085\u4fca\u8038\u8038\u80a9\uff0c\u6bd4\u8d77\u5728\u5f20\u606a\u9762\u524d\u575a\u6301\u4e00\u4e0b\uff0c\u603b\u8981\u6bd4\u7ed9\u6881\u683c\u73cd\u8d23\u6028\u5f3a\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u5509\uff0c\u51fa\u95e8\u4e0a\u8857\u8fd8\u80fd\u9047\u4e0a\u8f66\u7978\u5462\uff01\u4f60\u8bf4\u5427\uff0c\u4e2d\u56fd\u6bcf\u5929\u5728\u516c\u8def\u4e0a\u90fd\u8981\u6b7b\u5341\u4e07\u516b\u4e07\u4eba\uff0c\u8c01\u80fd\u56e0\u4e3a\u9047\u4e0a\u4e00\u6b21\u6709\u60ca\u65e0\u9669\u7684\u8f66\u7978\u4ee5\u540e\u8fd8\u4e0d\u51fa\u95e8\u4e0a\u8857\u4e86\uff1f\u201d\u5f20\u606a\u5f88\u5934\u75bc\u7684\u8ddf\u5510\u5a67\u62b1\u6028\u9053\uff0c\u201c\u8fd9\u53eb\u56e0\u564e\u5e9f\u98df\uff0c\u4f60\u61c2\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff0c\u4f60\u6709\u672c\u4e8b\uff0c\u8fd9\u8bdd\u4f60\u8ddf\u6881\u59e8\u8bf4\u53bb\u554a\uff01\u201d\u5510\u5a67\u7b11\u4e86\u7b11\uff0c\u773c\u775b\u53c8\u957f\u53c8\u5a9a\uff0c\u4f38\u624b\u4eb2\u6635\u7684\u62cd\u4e86\u62cd\u5f20\u606a\u7684\u8138\u988a\uff0c\u201c\u8fd9\u6b21\u5e78\u4e8f\u4e39\u9752\u59d0\u66ff\u4f60\u6321\u4e00\u5200\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u773c\u795e\u53c8\u98d8\u5230\u8fdc\u5904\uff0c\u5979\u4e0d\u7981\u5728\u60f3\uff0c\u8981\u662f\u5f53\u65f6\u81ea\u5df1\u5728\u5f20\u606a\u8eab\u8fb9\uff0c\u4f1a\u66ff\u5f20\u606a\u6321\u8fd9\u5200\u5462\uff1f\u4f1a\u7684\uff0c\u5510\u5a67\u575a\u4fe1\u81ea\u5df1\u4f1a\u7684\uff0c\u4e5f\u76f8\u4fe1\u5f20\u606a\u4e5f\u4f1a\u8fd9\u4e48\u6beb\u4e0d\u72b9\u8c6b\u7684\u4fdd\u62a4\u81ea\u5df1\u7684\uff0c\u4f46\u662f\u4e39\u9752\u59d0\u4e3a\u4ec0\u4e48\u4e5f\u80fd\u6beb\u4e0d\u72b9\u8c6b\u7684\u6321\u8fd9\u5200\uff1f</p><p>\u3000\u3000\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u624b\u6307\u8d34\u7740\u5510\u5a67\u6ed1\u817b\u7684\u4e0b\u9894\uff0c\u5c06\u5979\u7684\u8138\u62e8\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u6211\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u5510\u5a67\u5728\u591c\u8272\u5f02\u5e38\u59a9\u5a9a\u7684\u773c\u775b\u51dd\u671b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6211\u731c\u4e00\u731c\u554a\u2026\u2026\u201d\u5f20\u606a\u624b\u6307\u5934\u53e9\u4e86\u53e9\u989d\u5934\uff0c\u505a\u51fa\u6df1\u601d\u72b6\uff0c\u201c\u4f60\u6216\u8bb8\u4f1a\u60f3\uff1a\u8fd9\u5c0f\u5b50\u4f1a\u4e0d\u4f1a\u501f\u53e3\u53f3\u624b\u53d7\u4e86\u4f24\u8ba9\u6211\u5e2e\u4ed6\u4e0a\u5395\u6240\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u4f60\u771f\u662f\u6df7\u86cb\uff0c\u8fd9\u65f6\u5019\u8fd8\u60f3\u8fd9\u79cd\u80ae\u810f\u4e8b\u60c5\uff0c\u9b3c\u624d\u5e2e\u4f60\u4e0a\u5395\u6240\uff0c\u4f60\u81ea\u5df1\u5c3f\u88e4\u5b50\u5f97\u4e86\u3002\u201d\u5510\u5a67\u5a07\u7f9e\u7684\u4f38\u624b\u53bb\u6390\u5f20\u606a\u7684\u8170\u8089\uff0c\u8fd9\u5bb6\u4f19\u8bf4\u8bdd\u6d51\u6ca1\u6709\u6b63\u7ecf\uff0c\u7ed9\u4ed6\u4e00\u6253\u5c94\uff0c\u4e5f\u4e0d\u53bb\u60f3\u7fdf\u4e39\u9752\u7684\u95ee\u9898\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u771f\u60f3\u4e0a\u5395\u6240\u4e86\uff0c\u4e0d\u662f\u968f\u4fbf\u8bf4\u8bf4\u3002\u201d\u5f20\u606a\u7ad9\u8d77\u8eab\u6765\uff0c\u5510\u5a67\u7b11\u7740\u5632\u7b11\u4ed6\uff1a\u201c\u5c0f\u5fc3\u5c3f\u88e4\u5b50\u4e0a\u3002\u201d\u7ad9\u8d77\u8eab\u6765\u966a\u4ed6\u4e00\u540c\u53bb\u4e86\u4f4f\u9662\u90e8\u5927\u697c\u5e95\u697c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u4e0d\u5962\u671b\u5510\u5a67\u80fd\u8fdb\u53bb\u5e2e\u4ed6\uff0c\u4f46\u4e5f\u575a\u51b3\u7684\u5236\u6b62\u5085\u4fca\u4ed6\u4eec\u8981\u8ddf\u7740\u8fdb\u5395\u6240\u7684\u610f\u56fe\uff1a\u201c\u6ca1\u6709\u90a3\u4e48\u5938\u5f20\uff0c\u7a0d\u5fae\u7ed9\u6211\u7559\u70b9\u79c1\u4eba\u7a7a\u95f4\uff0c\u8fd9\u65f6\u5019\u54ea\u6015\u80fd\u5355\u72ec\u4e0a\u4e2a\u5395\u6240\uff0c\u6211\u90fd\u89c9\u5f97\u5f88\u6ee1\u8db3\u2026\u2026\u201d</p><p>\u3000\u3000\u5085\u4fca\u4ed6\u4eec\u7b11\u4e86\u7b11\uff0c\u5c31\u5b88\u5728\u5395\u6240\u95e8\u5916\uff0c\u8ba9\u5f20\u606a\u81ea\u5df1\u8fdb\u5395\u6240\uff0c\u5510\u5a67\u5728\u8fc7\u9053\u91cc\u8e29\u7740\u5eca\u706f\u4e0b\u81ea\u5df1\u7684\u5f71\u5b50\u3002</p><p>\u3000\u3000\u4ece\u5973\u5395\u6240\u95f4\u8d70\u51fa\u4e00\u4e2a\u6e05\u4e3d\u7684\u5c11\u5973\uff0c\u8d70\u5230\u6d17\u6c34\u6c60\u524d\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u5f88\u597d\u5947\u8fd9\u4e9b\u4eba\u5b88\u5728\u5395\u6240\u95e8\u524d\u505a\u4ec0\u4e48\uff0c\u6b63\u597d\u4e0e\u5510\u5a67\u7684\u89c6\u7ebf\u5bf9\u4e0a\uff0c\u8fd9\u65f6\u5019\u5f20\u606a\u5728\u5395\u6240\u91cc\u80e1\u8bf4\u516b\u9053\uff1a\u201c\u554a\uff0c\u771f\u5c3f\u88e4\u5b50\u4e0a\u4e86\uff0c\u53f3\u624b\u540a\u5728\u8116\u5b50\u771f\u4e0d\u65b9\u4fbf\uff0c\u80fd\u4e0d\u80fd\u6765\u4e2a\u4eba\u642d\u628a\u624b\uff1f\u201d</p><p>\u3000\u3000\u5085\u4fca\u4ed6\u4eec\u8fd9\u65f6\u5019\u90fd\u8f6c\u8fc7\u8eab\u53bb\uff0c\u4e0d\u8ba9\u5510\u5a67\u770b\u5230\u4ed6\u4eec\u8138\u4e0a\u7684\u7b11\uff1b\u90a3\u6e05\u4e3d\u7684\u5c11\u5973\u542c\u4e86\u5395\u6240\u6709\u4eba\u7a81\u7136\u8fd9\u4e48\u626f\u4e00\u55d3\u5b50\u8fd8\u6709\u4e9b\u5947\u602a\uff0c\u770b\u5230\u5510\u5a67\u6ee1\u9762\u5a07\u7f9e\u7ea2\u6655\u6e10\u6d6e\u4e0a\u6765\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u8d77\u6765\uff0c\u6d17\u8fc7\u624b\uff0c\u8d70\u8fc7\u5510\u5a67\u8eab\u8fb9\u65f6\u8f7b\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u7537\u670b\u53cb\u4e00\u5b9a\u662f\u4e2a\u5927\u8272\u72fc\uff01\u201d</p><p>\u3000\u3000\u8fd9\u5bb6\u4f19\u80e1\u641e\u8d77\u6765\u8fd8\u771f\u662f\u4e0d\u7ba1\u522b\u4eba\u7684\u96be\u582a\uff0c\u53ea\u662f\u89c9\u5f97\u8fd9\u5973\u5b69\u5b50\u7684\u773c\u775b\u662f\u5982\u6b64\u7684\u6e05\u6f88\uff0c\u53ea\u662f\u7a7f\u7740\u5bbd\u5927\u75c5\u53f7\u670d\u7684\u8eab\u4f53\u770b\u4e0a\u53bb\u6709\u4e9b\u7ea4\u7ec6\u7626\u5f31\uff0c\u5510\u5a67\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u7684\u5e94\u4e86\u4e00\u58f0\uff1a\u201c\u55ef\uff0c\u662f\u4e2a\u6d51\u7403\u3002\u201d</p><p>\u3000\u3000\u90a3\u5c11\u5973\u8f7b\u7b11\u4e86\u4e00\u58f0\uff0c\u5f80\u8fdc\u5904\u8d70\u53bb\uff0c\u5979\u7684\u80cc\u5f71\u6d88\u5931\u8fc7\u9053\u65f6\uff0c\u5f20\u606a\u624d\u4ece\u5395\u6240\u91cc\u51fa\u53bb\uff0c\u5510\u5a67\u8d70\u8fc7\u53bb\u4f38\u811a\u8f7b\u8e22\u4ed6\uff1a\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\u554a\uff0c\u90fd\u8ba9\u522b\u4eba\u542c\u89c1\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u542c\u89c1\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u8146\u7740\u8138\u56de\u5934\u95ee\u5085\u4fca\uff0c\u201c\u4f60\u4eec\u6ca1\u6709\u5c06\u8033\u6735\u6342\u8d77\u6765\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5c06\u8033\u6735\u6342\u8d77\u6765\uff0c\u4e0d\u8fc7\u8fd8\u6709\u522b\u4eba\u7ecf\u8fc7\u3002\u201d\u5085\u4fca\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4f38\u624b\u644a\u5230\u5510\u5a67\u773c\u524d\uff0c\u8981\u5979\u5e2e\u5fd9\u6d17\u624b\u3002</p><p>\u3000\u3000\u201c\u53ef\u662f\u4e2a\u5f88\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u54e6\uff0c\u201d\u5510\u5a67\u6309\u4e86\u4e9b\u6d17\u624b\u6db2\uff0c\u8ba4\u771f\u7684\u5e2e\u5f20\u606a\u6d17\u624b\uff0c\u8fd8\u4e0d\u5fd8\u6253\u8da3\u4ed6\uff0c\u201c\u662f\u90a3\u79cd\u8ba9\u4eba\u5f88\u540e\u6094\u9519\u8fc7\u7684\u5973\u5b69\u5b50\u5462\uff0c\u4f60\u540e\u6094\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u4e0d\u4fe1\u5929\u4e0b\u8fd8\u8981\u6bd4\u4f60\u66f4\u503c\u5f97\u770b\u7684\u5973\u5b69\u5b50\uff0c\u201d\u5f20\u606a\u8ddf\u5510\u5a67\u6253\u8da3\u9053\uff0c\u201c\u90a3\u4e2a\u5973\u5b69\u5b50\u4e5f\u662f\u4f4f\u9662\u7684\u5417\uff1f\u90a3\u6211\u5c31\u5728\u533b\u9662\u91cc\u591a\u4f4f\u51e0\u5929\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u673a\u4f1a\u9047\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6d51\u7403\uff0c\u201d\u5510\u5a67\u5e2e\u5f20\u606a\u51b2\u5e72\u51c0\u624b\u4e0a\u7684\u6ce1\u6cab\uff0c\u8f6c\u8eab\u5c06\u5979\u7684\u624b\u5728\u5f20\u606a\u7684\u8863\u670d\u4e0a\u62b9\u5e72\u51c0\u624b\uff0c\u53c8\u63a2\u5934\u770b\u4e86\u770b\u8fc7\u9053\uff0c\u4eba\u5f71\u65e9\u5c31\u6d88\u5931\u4e86\uff0c\u53ea\u89c9\u5f97\u90a3\u5973\u5b69\u5b50\u6e05\u6f88\u7684\u773c\u795e\u8ba9\u4eba\u96be\u5fd8\uff0c\u8bf4\u9053\uff0c\u201c\u5e94\u8be5\u662f\u5427\uff0c\u8fd8\u7a7f\u7740\u75c5\u53f7\u670d\u5462\uff0c\u4e0d\u8fc7\u5979\u5df2\u7ecf\u77e5\u9053\u4f60\u662f\u4e2a\u5927\u8272\u72fc\u4e86\u3002\u201d</p><p>\u3000\u3000\u5085\u4fca\u5374\u662f\u8ba4\u51fa\u521a\u521a\u8fc7\u53bb\u7684\u5973\u5b69\u5b50\uff0c\u4ed6\u4e0d\u77e5\u9053\u5f20\u606a\u90a3\u5929\u5728\u65b0\u829c\u7684\u5c0f\u533a\u95e8\u53e3\u7b49\u4e86\u534a\u5929\u662f\u4e3a\u4ec0\u4e48\u3001\u5728\u9ec4\u660f\u7684\u96e8\u4e2d\u7ed9\u90a3\u5c11\u5973\u7cfb\u978b\u5e26\u9971\u542b\u7740\u600e\u6837\u7684\u6df1\u60c5\uff0c\u8fd8\u4ee5\u4e3a\u8fd9\u53ea\u662f\u5f20\u606a\u751f\u6027\u8df3\u8131\u3001\u65e0\u62d8\u65e0\u675f\u7684\u4e00\u6b21\u610f\u5916\u4e4b\u4e3e\u3002\u5f20\u606a\u8eab\u4e0a\u7ea0\u7f20\u7684\u60c5\u611f\uff0c\u5085\u4fca\u662f\u770b\u5f97\u6700\u6e05\u695a\u7684\u4e00\u4eba\uff0c\u4ed6\u60f3\u4e86\u60f3\uff0c\u8fd8\u662f\u89c9\u5f97\u4e0d\u8981\u518d\u8282\u5916\u751f\u679d\u7684\u544a\u8bc9\u5f20\u606a\u4e3a\u597d\u3002</p></article>"}}}]);
