(window.webpackJsonp=window.webpackJsonp||[]).push([[1157],{1180:function(p){p.exports={chapterTitle:null,title:"\u7b2c950\u7ae0 \u51fa\u9662",content:"<article><h2>\u7b2c950\u7ae0 \u51fa\u9662</h2><p>\u3000\u3000\u8bb8\u601d\u5230\u4f4f\u9662\u90e8\u5927\u697c\uff0c\u5148\u53bb\u770b\u671b\u8fc7\u7fdf\u4e39\u9752\uff0c\u5728\u7fdf\u4e39\u9752\u75c5\u623f\u91cc\u8bf4\u4e86\u4e00\u4f1a\u513f\u8bdd\u3002</p><p>\u3000\u3000\u4e0e\u665a\u6674\u5230\u5f20\u606a\u75c5\u623f\u91cc\uff0c\u8bb8\u601d\u5c06\u6350\u52a9\u8bbe\u7acb\u4e00\u4e2a\u533b\u7597\u57fa\u91d1\u7684\u4e8b\u60c5\u8ddf\u4ed6\u8bf4\u4e86\uff0c\u665a\u6674\u5728\u4e00\u65c1\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4e60\u60ef\u4e8b\u4e8b\u8bf7\u793a\u554a\uff0c\u8fd9\u4e8b\u60c5\u4f60\u81ea\u5df1\u4e0d\u4f1a\u62ff\u4e3b\u610f\u554a\uff1f\u201d</p><p>\u3000\u3000\u9ec4\u660f\u65f6\u4e00\u7f15\u9633\u5149\u900f\u8fdb\u6765\uff0c\u6d12\u8fdb\u5c4b\u91cc\uff0c\u62b9\u4e86\u4e00\u5c42\u91d1\u7ea2\uff0c\u4e5f\u8ba9\u8bb8\u601d\u7edd\u7f8e\u7684\u8138\u4e0a\u8f7b\u7b3c\u4e00\u5c42\u6de1\u6de1\u7684\u8ff7\u4eba\u5149\u6cfd\u3002\u8bb8\u601d\u7b11\u4e86\u7b11\uff0c\u9732\u51fa\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u5979\u53ef\u4ece\u6ca1\u6709\u8fd9\u6837\u7684\u610f\u8bc6\uff0c\u4ee5\u4e3a\u5979\u540d\u4e0b\u7684\u8d44\u4ea7\u5c31\u53ef\u4ee5\u4efb\u610f\u7684\u652f\u914d\u3002</p><p>\u3000\u3000\u5f20\u606a\u5de6\u624b\u6795\u5230\u5934\u4e0b\u659c\u8eba\u5728\u75c5\u5e8a\u4e0a\uff0c\u770b\u7740\u8bb8\u601d\uff0c\u5fc3\u60f3\u8bbe\u7acb\u8fd9\u4e2a\u533b\u5b66\u6216\u8005\u533b\u7597\u57fa\u91d1\uff0c\u6216\u8bb8\u662f\u8bb8\u601d\u957f\u671f\u4ee5\u6765\u7684\u5fc3\u613f\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8ddf\u6211\u4eec\u5546\u91cf\uff0c\u662f\u60f3\u4ece\u6211\u4eec\u8fd9\u91cc\u518d\u62c9\u4e9b\u8d5e\u52a9\uff1f\u201d</p><p>\u3000\u3000\u201c\u516c\u53f8\u7684\u80a1\u6743\u4e0d\u53bb\u7ba1\u4ed6\uff0c\u6bcf\u5e74\u7684\u5206\u7ea2\u5c31\u90a3\u4e48\u591a\uff0c\u6211\u7559\u90a3\u4e48\u94b1\u5728\u624b\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u8bb8\u601d\u5750\u5230\u5f20\u606a\u7684\u5e8a\u5934\uff0c\u5c06\u4ed6\u7684\u4f24\u81c2\u6367\u5728\u624b\u91cc\uff0c\u624b\u6307\u8f7b\u8f7b\u6469\u6332\u7740\u7ed3\u7740\u7c89\u7ea2\u8272\u75a4\u7684\u4f24\u53e3\uff0c\u201c\u4e5f\u4e0d\u6653\u5f97\u9700\u8981\u591a\u5c11\uff0c\u6bcf\u5e74\u7684\u90a3\u4e9b\u5206\u7ea2\u603b\u662f\u591f\u7684\u2014\u2014\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u5c06\u6bcf\u5e74\u7684\u5206\u7ea2\u90fd\u62ff\u51fa\u6765\u554a\uff0c\u90a3\u53ef\u8981\u627e\u4e2a\u4fe1\u4efb\u7684\u4eba\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u9664\u4e86\u8d8a\u79c0\u63a7\u80a1\u7684\u5927\u91cf\u80a1\u6743\u4e4b\u5916\uff0c\u8bb8\u601d\u5728\u7231\u8fbe\u96c6\u56e2\u3001\u65b0\u5149\u7eb8\u4e1a\u4ee5\u53ca\u9526\u6e56\u5546\u4e8b\u90fd\u76f4\u63a5\u62e5\u6709\u5c11\u91cf\u7684\u80a1\u6743\uff0c\u867d\u7136\u6bd4\u4f8b\u5f88\u5c11\uff0c\u4f46\u662f\u7231\u8fbe\u96c6\u56e2\u3001\u65b0\u5149\u7eb8\u4e1a\u3001\u9526\u6e56\u5546\u4e8b\u7684\u8d44\u4ea7\u89c4\u6a21\u5e9e\u5927\uff0c\u54ea\u60151\uff05\u7684\u80a1\u6743\u7b49\u82e5\u597d\u4e9b\u4e2a\u4ebf\u7684\u8d44\u4ea7\uff0c\u8bb8\u601d\u540d\u4e0b\u7684\u8d44\u4ea7\u7d2f\u52a0\u8d77\u6765\uff0c\u5341\u4e8c\u4e09\u4ebf\u603b\u662f\u6709\u7684\u3002\u516c\u53f8\u6b63\u503c\u9ad8\u901f\u53d1\u5c55\u9636\u6bb5\uff0c\u9700\u8981\u622a\u7559\u7684\u53d1\u5c55\u8d44\u91d1\u5f88\u591a\uff0c\u6bcf\u5e74\u7684\u5206\u7ea2\u6bd4\u4f8b\u4f1a\u63a7\u5236\u5f97\u6bd4\u8f83\u4f4e\uff0c\u8bb8\u601d\u6bcf\u5e74\u8fd8\u662f\u80fd\u62ff\u5230\u5dee\u4e0d\u591a\u6709\u4e00\u4ebf\u7684\u5206\u7ea2\u3002\u6263\u9664\u5229\u5f97\u7a0e\u7b49\u7b49\u4e4b\u540e\uff0c\u6bcf\u5e74\u4e03\u516b\u5343\u4e07\u7684\u95f2\u94b1\u603b\u662f\u6709\u7684\uff1b\u53e6\u5916\uff0c\u5efa\u7b51\u8bbe\u8ba1\u5e08\u4e8b\u52a1\u6240\u7684\u6536\u5165\u4e5f\u76f8\u5f53\u4e0d\u9519\u3002\u5728\u4e2d\u56fd\u3001\u5728\u4e5d\u516b\u5e74\uff0c\u5f80\u4e00\u4e2a\u533b\u5b66\u6216\u533b\u7597\u57fa\u91d1\u91cc\u6bcf\u5e74\u6295\u4e03\u516b\u5343\u4e07\u7684\u8d44\u91d1\uff0c\u7edd\u5bf9\u662f\u4ef6\u4e86\u4e0d\u5f97\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e0d\u4f1a\u7ba1\u8bb8\u601d\u5c06\u8fd9\u4e9b\u94b1\u7528\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u53ea\u662f\u5e0c\u671b\u5979\u80fd\u8c28\u614e\u4e00\u4e9b\uff0c\u4e0d\u8981\u62ff\u51fa\u8fd9\u4e48\u94b1\u51fa\u6765\uff0c\u6700\u540e\u641e\u5f97\u81ea\u5df1\u5fc3\u91cc\u53d7\u5835\u3002</p><p>\u3000\u3000\u201c\u7701\u4eba\u6c11\u533b\u9662\u91cc\u7ed9\u8bb8\u7ef4\u505a\u624b\u672f\u7684\u9b4f\u51a0\u534e\u533b\u751f\u6216\u8bb8\u662f\u53ef\u4ee5\u503c\u5f97\u4fe1\u4efb\u7684\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4e9b\u5e74\u5012\u6ca1\u6709\u6765\u62dc\u8bbf\u8fc7\uff0c\u521a\u624d\u5c31\u8d38\u7136\u8bf7\u4ed6\u5e2e\u5fd9\u4e86\uff0c\u201d\u8bb8\u601d\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u6253\u7b97\u5c06\u8981\u6295\u5165\u7684\u8d44\u91d1\u90fd\u4e8b\u5148\u8f6c\u5230\u665a\u6674\u59d0\u7684\u516c\u53f8\u540d\u4e0b\uff0c\u5177\u4f53\u7684\u4e8b\u60c5\u8fd8\u662f\u8981\u665a\u6674\u59d0\u5e2e\u5fd9\uff0c\u8ddf\u4f60\u4eec\u5148\u5546\u91cf\u5546\u91cf\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u884c\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u4e0e\u79c1\u4eba\u6ce8\u518c\u7684\u57fa\u91d1\u4f1a\u6ca1\u6709\u591a\u5c11\u533a\u522b\uff0c\u5728\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u4e0b\u518d\u8bbe\u7acb\u4e00\u4e2a\u533b\u5b66\u57fa\u91d1\u5012\u662f\u59a5\u5f53\uff0c\u201c\u4f60\u4eec\u5546\u91cf\u5427\uff0c\u8ddf\u6211\u53c8\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u536b\u5170\u6572\u95e8\u8fdb\u6765\uff0c\u8bf7\u4ed6\u4eec\u8fc7\u53bb\u5403\u665a\u996d\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8fd8\u8981\u5728\u75c5\u5e8a\u4e0a\u9759\u5367\uff0c\u4e0d\u80fd\u5c06\u5979\u4e00\u4eba\u4e22\u533b\u9662\u91cc\uff0c\u5927\u5bb6\u90fd\u5728\u75c5\u623f\u91cc\u7528\u9910\uff1b\u7279\u62a4\u75c5\u623f\u9664\u4e86\u8d77\u5c45\u5ba4\u3001\u966a\u62a4\u4f11\u606f\u7684\u5ba2\u623f\uff0c\u8fd8\u6709\u7528\u9910\u7684\u5c0f\u9910\u5385\uff0c\u533b\u9662\u91cc\u7684\u4f19\u98df\u4e5f\u76f8\u5f53\u4e0d\u9519\uff0c\u5f20\u606a\u6628\u5929\u5c06\u4ed6\u5988\u652f\u56de\u65b0\u829c\u53bb\u4e86\uff0c\u533b\u9662\u8fd9\u91cc\u5c31\u7fdf\u6bcd\u7559\u4e0b\u6765\u7167\u987e\u7fdf\u4e39\u9752\uff0c\u536b\u5170\u665a\u4e0a\u4f1a\u5230\u533b\u9662\u91cc\u6765\u5e2e\u5fd9\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u601d\u3001\u665a\u6674\u5230\u9694\u58c1\u7684\u75c5\u623f\u53bb\uff0c\u7fdf\u6bcd\u4e0e\u536b\u5170\u65e9\u5c31\u5c06\u996d\u83dc\u6446\u597d\u4e86\uff0c\u7fdf\u4e39\u9752\u7a7f\u7740\u84dd\u6761\u5b50\u7684\u75c5\u53f7\u670d\u5750\u5728\u9910\u684c\u524d\uff0c\u53cc\u773c\u70af\u70af\u6709\u795e\u7684\u76ef\u7740\u6ee1\u684c\u7684\u83dc\u80b4\u54bd\u53e3\u6c34\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u7b77\u5b50\u5728\u76d8\u5b50\u91cc\u62e8\u83dc\u3002\u770b\u7740\u7fdf\u4e39\u9752\u5939\u7740\u83dc\u8981\u5f80\u5634\u91cc\u585e\uff0c\u5f20\u606a\u7d27\u5f20\u7684\u8d70\u8fc7\u53bb\u62c9\u4f4f\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u6709\u4f60\u7684\u665a\u996d\uff0c\u8fd9\u4e9b\u83dc\u4f60\u4e0d\u80fd\u5403\uff0c\u5bf9\u4f24\u53e3\u6108\u5408\u4e0d\u597d\uff0c\u600e\u4e48\u8fd9\u4e48\u998b\uff0c\u71ac\u4e24\u5929\u4e0d\u884c\u554a\uff1f\u201d\u95ee\u536b\u5170\uff0c\u201c\u4e39\u9752\u59d0\u7684\u665a\u996d\u8fd8\u6ca1\u6709\u9001\u8fc7\u6765\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u95fb\u4e00\u95fb\u83dc\u9999\uff0c\u6211\u5149\u770b\u7740\u89e3\u89e3\u998b\u4e0d\u884c\u5417\uff1f\u201d\u7fdf\u4e39\u9752\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5c06\u624b\u91cc\u7b77\u5b50\u653e\u684c\u5b50\u4e0a\uff0c\u56de\u5934\u8ddf\u665a\u6674\u5e7d\u6028\u65e0\u6bd4\u7684\u8bf4\u9053\uff0c\u201c\u6bcf\u987f\u90fd\u559d\u6de1\u800c\u5473\u7684\u9e3d\u5b50\u6c64\uff0c\u559d\u4e86\u90fd\u5feb\u5410\u4e86\uff0c\u504f\u504f\u4ed6\u4eec\u5728\u8fd9\u91cc\u5403\u996d\u8fd8\u4e0d\u5c06\u95e8\u5173\u7d27\uff0c\u9999\u6c14\u76f4\u5f80\u9f3b\u5b50\u91cc\u94bb\uff0c\u600e\u4e48\u53eb\u4eba\u53d7\u5f97\u4e86\u554a\uff1f\u201d\u4e00\u526f\u998b\u574f\u4e86\u7684\u6a21\u6837\uff0c\u60f9\u5f97\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\u3002</p><p>\u3000\u3000\u5403\u8fc7\u996d\uff0c\u665a\u6674\u4e0e\u8bb8\u601d\u8981\u4e00\u8d77\u79bb\u5f00\uff0c\u7fdf\u4e39\u9752\u672c\u60f3\u8bf7\u5979\u4eec\u4f4f\u4e0b\u6765\uff0c\u6881\u683c\u73cd\u79bb\u5f00\u540e\uff0c\u5f20\u606a\u90a3\u75c5\u623f\u91cc\u8fd8\u6709\u5ba2\u623f\u7a7a\u4e0b\u6765\uff0c\u4e0d\u8fc7\u7559\u4eba\u5bb6\u4f4f\u533b\u9662\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u5409\u5229\uff0c\u518d\u8bf4\u8ba9\u5979\u4eec\u7761\u5f20\u606a\u90a3\u91cc\uff0c\u6307\u4e0d\u5b9a\u5bf9\u5f20\u606a\u7684\u4f24\u53e3\u6108\u5408\u4f1a\u6709\u5f71\u54cd\uff0c\u4fbf\u4e0d\u63d0\u8fd9\u6e23\uff0c\u95ee\u5979\u4eec\uff1a\u201c\u8fd9\u4e48\u665a\uff0c\u4f60\u4eec\u8fd8\u56de\u6d77\u5dde\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u4f4f\u9752\u5c71\u516c\u5bd3\u53bb\uff0c\u660e\u5929\u4e0a\u5348\u518d\u6765\u770b\u4f60\u4eec\uff0c\u201d\u665a\u6674\u8fd9\u4e48\u8bf4\u7740\uff0c\u53c8\u77a5\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4f4f\u9752\u5c71\u516c\u5bd3\u90a3\u91cc\uff0c\u6211\u4eec\u53c8\u4e0d\u6015\u6478\u9519\u4e86\u95e8\u3002\u201d</p><p>\u3000\u3000\u522b\u4eba\u542c\u5230\u8fd9\u90fd\u83ab\u660e\u5176\u5999\u7684\uff0c\u8ddf\u7740\u7b11\u4e86\u7b11\uff0c\u5f20\u606a\u5728\u4e00\u65c1\u542c\u4e86\u80c6\u98a4\u5fc3\u60ca\uff0c\u8fd9\u65f6\u5019\u90fd\u4e0d\u6562\u62ff\u773c\u795e\u8ddf\u7fdf\u4e39\u9752\u5bf9\u89c6\uff0c\u53ea\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u9001\u9001\u4f60\u4eec\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\u8ba9\u75c5\u4eba\u9001\u7684\uff1f\u201d\u665a\u6674\u6309\u7740\u5f20\u606a\u4f38\u6765\u7684\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u81ea\u5df1\u4e0b\u697c\u5c31\u884c\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u8ba9\u536b\u5170\u9001\u9001\u4f60\u4eec\u5427\uff1f\u201d\u5f20\u606a\u987a\u6c34\u63a8\u821f\u7684\u8bf4\u9053\uff0c\u201c\u660e\u5929\u65e9\u4e0a\u7b49\u7740\u4f60\u4eec\u8fc7\u6765\u5403\u65e9\u996d\u3002\u201d</p><p>\u3000\u3000\u7fdf\u6bcd\u4e5f\u8981\u4e0e\u536b\u5170\u4e00\u8d77\u4e0b\u697c\u9001\u665a\u6674\u4e0e\u8bb8\u601d\uff0c\u5f20\u606a\u662f\u6c42\u4e4b\u4e0d\u5f97\uff0c\u770b\u7740\u5979\u4eec\u90fd\u8fdb\u4e86\u7535\u68af\uff0c\u5f20\u606a\u624d\u8ddf\u7740\u7fdf\u4e39\u9752\u8fdb\u4e86\u75c5\u5e8a\uff0c\u5c0f\u58f0\u95ee\u5979\uff1a\u201c\u665a\u6674\u600e\u4e48\u77e5\u9053\u6211\u4eec\u7684\u4e8b\u60c5\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u60f3\u7740\u5973\u4eba\u7684\u773c\u775b\u90fd\u778e\u4e86\u8ba9\u4f60\u597d\u968f\u4fbf\u98ce\u6d41\uff0c\u201d\u7fdf\u4e39\u9752\u7f8e\u7738\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5a07\u6028\u7684\u8bf4\u9053\uff0c\u89c1\u5f20\u606a\u4f38\u624b\u62e5\u6765\uff0c\u8eab\u5b50\u50f5\u4e86\u50f5\uff0c\u7ec8\u662f\u6ca1\u6709\u8eb2\u5f00\uff0c\u9759\u9759\u7684\u8ba9\u5f20\u606a\u6402\u5728\u6000\u91cc\uff0c\u611f\u89c9\u4ed6\u8eab\u4e0a\u8ba9\u4eba\u6c89\u9189\u7684\u6c14\u606f\uff0c\u5fc3\u91cc\u72b9\u6709\u4e9b\u4e0d\u7518\uff0c\u5634\u91cc\u8bf4\u9053\uff0c\u201c\u771f\u662f\u540e\u6094\u767d\u767d\u7684\u66ff\u4f60\u6328\u4e86\u4e00\u5200\u3002\u201d\u611f\u89c9\u5230\u5f20\u606a\u7684\u624b\u5f80\u8863\u895f\u91cc\u4f38\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u7d27\u7ad9\u76f4\u8eab\u5b50\uff0c\u55d4\u9a82\u4ed6\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u8981\u6b7b\u554a\uff0c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u8fd9\u65f6\u5019\u60f3\u8fd9\u4e8b\uff0c\u4f60\u4e0d\u5982\u8ddf\u5979\u4eec\u4f4f\u5230\u9752\u5c71\u516c\u5bd3\u53bb\uff0c\u4e0d\u8981\u5728\u8fd9\u91cc\u5bb3\u6b7b\u6211\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u54ed\u7b11\u4e0d\u5f97\u7684\u89e3\u91ca\u9053\uff1a\u201c\u4e00\u76f4\u60f3\u770b\u770b\u4f24\u53e3\uff0c\u8fd9\u4e0d\u662f\u597d\u4e0d\u5bb9\u6613\u627e\u5230\u673a\u4f1a\uff0c\u4f60\u60f3\u54ea\u91cc\u53bb\u4e86\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u90a3\u53cc\u7f8e\u4e3d\u7684\u51e4\u773c\u6e05\u6f88\u7684\u51dd\u671b\u4e86\u5f20\u606a\u4e00\u4f1a\u513f\uff0c\u4fc4\u5c14\u53c8\u8bf4\uff1a\u201c\u90a3\u4e5f\u4e0d\u884c\u2014\u2014\u8fd8\u6ca1\u6709\u62c6\u7ebf\uff0c\u597d\u957f\u4e00\u9053\u75a4\uff0c\u4e11\u90fd\u4e11\u6b7b\u4e86\uff0c\u6709\u4ec0\u4e48\u597d\u770b\u7684\u3002\u201d\u6293\u7d27\u8863\u895f\u53cc\u624b\u62e2\u5728\u80f8\u524d\u4e0d\u7ed9\u5f20\u606a\u770b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4f24\u53e3\u7b2c\u4e03\u5929\u5c31\u62c6\u4e86\u7ebf\uff0c\u8fd8\u5728\u533b\u9662\u91cc\u89c2\u5bdf\u4e86\u4e09\u5929\u624d\u51fa\u4e86\u9662\uff0c\u4f4f\u56de\u9752\u5e74\u516c\u5bd3\u91cc\u53bb\uff0c\u5f20\u606a\u4e5f\u662f\u7b49\u5230\u90a3\u5929\u624d\u4e00\u8d77\u51fa\u9662\uff0c\u4ed6\u624b\u81c2\u4e0a\u7684\u4f24\u53e3\u65e9\u5c31\u4e0d\u788d\u4e8b\u4e86\u3002\u533b\u5b66\u57fa\u91d1\u7684\u4e8b\u60c5\uff0c\u8bb8\u601d\u4ea4\u7ed9\u5468\u9038\u6765\u5904\u7406\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u8fc7\u95ee\u3002</p><p>\u3000\u3000\u5728\u8fd9\u671f\u95f4\uff0c\u7ae0\u5dde\u6848\u521d\u5ba1\u6cd5\u5b98\u6d89\u5acc\u591a\u9879\u72af\u7f6a\u88ab\u68c0\u67e5\u673a\u5173\u62d8\u6355\uff0c\u5176\u4e2d\u4e00\u9879\u5c31\u662f\u7ae0\u5dde\u6848\u521d\u5ba1\u8fc7\u7a0b\u4e2d\u6536\u53d7\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5bb6\u4eba\u7684\u8d3f\u8d42\uff0c\u4e0d\u7ba1\u5728\u76d1\u72f1\u3001\u770b\u5b88\u6240\u7cfb\u7edf\u7684\u4e13\u9879\u6574\u6cbb\u8fd0\u52a8\u4e2d\u80fd\u4e0d\u80fd\u627e\u5230\u65b0\u7684\u7f6a\u8bc1\uff0c\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\u91cd\u5ba1\u52a0\u91cd\u5211\u7f5a\u4e4b\u4e8b\u5df2\u6210\u5b9a\u5c40\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6062\u590d\u60c5\u51b5\u5f88\u597d\uff0c\u7fdf\u6bcd\u4e5f\u62c5\u5fc3\u7fdf\u7236\u5728\u5bb6\u91cc\u4e0d\u4f1a\u7167\u987e\u81ea\u5df1\uff0c\u52a0\u4e0a\u9972\u517b\u7684\u5bb6\u79bd\u8fd8\u6709\u7530\u5730\u91cc\u7684\u5e84\u7a3c\u90fd\u8981\u4f3a\u5019\uff0c\u7fdf\u4e39\u9752\u4e0e\u5f20\u606a\u51fa\u9662\u7684\u5f53\u5929\uff0c\u5979\u5c31\u8981\u56de\u65b0\u829c\u53bb\uff0c\u8fd8\u575a\u6301\u4e0d\u80af\u8ba9\u8fd9\u8fb9\u6d3e\u8f66\u9001\u5979\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u8f66\u9001\u7fdf\u4e39\u9752\u5230\u8f66\u7ad9\u7ed9\u5979\u6bcd\u4eb2\u9001\u884c\uff0c\u53c8\u7ed9\u65b0\u829c\u90a3\u8fb9\u6253\u4e86\u7535\u8bdd\uff0c\u8ba9\u4eba\u51c6\u65f6\u5230\u8f66\u7ad9\u63a5\u7fdf\u6bcd\u3002\u4ece\u8f66\u7ad9\u56de\u6765\uff0c\u8f66\u4ece\u71d5\u5f52\u6e56\u897f\u5cb8\u7684\u6797\u836b\u9053\u7a7f\u8fc7\uff0c\u5915\u9633\u5782\u5728\u71d5\u5f52\u5c71\u5dc5\u4e4b\u4e0a\uff0c\u6beb\u65e0\u541d\u556c\u7684\u5c06\u91d1\u7ea2\u8272\u7684\u5915\u9633\u5149\u8f89\u6d12\u6cfb\u5728\u6ce2\u5149\u7cbc\u7cbc\u7684\u6e56\u9762\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8f66\u505c\u5728\u6e56\u8fb9\uff0c\u4e0e\u7fdf\u4e39\u9752\u4e0b\u4e86\u8f66\uff0c\u5728\u6e56\u6ee8\u7eff\u5730\u4e0a\u6709\u8bb8\u591a\u6563\u6b65\u7684\u604b\u4eba\uff0c\u7fdf\u4e39\u9752\u53ea\u662f\u9759\u9759\u7684\u7ad9\u5728\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u51dd\u89c6\u7740\u8fdc\u5904\u7684\u6e56\u6c34\u3002</p><p>\u3000\u3000\u5728\u5915\u9633\u5149\u8f89\u91cc\uff0c\u7fdf\u4e39\u9752\u7684\u808c\u80a4\u6709\u4e00\u79cd\u52a8\u4eba\u5fc3\u9b44\u7684\u767d\u5ae9\uff0c\u957f\u53d1\u5c31\u50cf\u9ed1\u4eae\u7684\u7f0e\u5b50\uff0c\u7f8e\u7738\u5c31\u4f3c\u8fd9\u660e\u6f88\u7684\u6e56\u6c34\uff0c\u95ea\u70c1\u7740\u795e\u79d8\u7684\u5149\u6cfd\uff0c\u5ae3\u7ea2\u7684\u5634\u5507\u5fae\u5fae\u7684\u5f20\u7740\uff0c\u5438\u7740\u6e56\u7554\u6e05\u65b0\u800c\u5fae\u5bd2\u7684\u7a7a\u6c14\uff0c\u5fae\u9732\u51fa\u6574\u9f50\u6d01\u767d\u7684\u7259\u9f7f\u3002</p><p>\u3000\u3000\u201c\u5148\u56de\u53bb\u5427\uff0c\u6628\u5929\u8fd8\u4e0e\u536b\u5170\u8bf4\u8981\u627e\u5bb6\u9910\u9986\u5e26\u4f60\u597d\u597d\u7684\u9971\u98df\u4e00\u987f\u5462\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4f1a\u513f\u5c31\u8d70\u4e0d\u52a8\u4e86\uff0c\u8fd9\u4e24\u5929\u90fd\u6ca1\u6709\u4ec0\u4e48\u95ee\u9898\u4e86\uff0c\u8fd8\u8981\u5403\u90a3\u96be\u5403\u7684\u8425\u517b\u9910\uff0c\u771f\u662f\u4e5f\u8be5\u53eb\u4f60\u597d\u597d\u7684\u8fde\u7eed\u5403\u4e0a\u5341\u5929\uff0c\u5c31\u77e5\u9053\u8fd9\u6ecb\u5473\u3002\u201d\u7fdf\u4e39\u9752\u624b\u63c9\u7740\u809a\u5b50\uff0c\u5ae3\u7136\u7684\u7b11\u7740\uff0c\u5a9a\u6001\u6a2a\u751f\uff0c\u8ba9\u5f20\u606a\u770b\u4e86\u5fc3\u60b8\uff0c\u66f4\u575a\u5b9a\u5148\u56de\u53bb\u4e00\u8d9f\u7684\u5ff5\u5934\u3002</p><p>\u3000\u3000\u5f00\u8f66\u56de\u5230\u9752\u5e74\u516c\u5bd3\uff0c\u6574\u680b\u697c\u90fd\u9759\u6084\u6084\u7684\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\uff0c\u5f20\u606a\u5bf9\u7fdf\u4e39\u9752\u8bf4\u9053\uff1a\u201c\u536b\u5170\u8bf4\u5e2e\u4f60\u6574\u7406\u8fc7\u623f\u95f4\u4e86\uff0c\u6211\u8ddf\u4f60\u53bb\u770b\u770b\uff0c\u8981\u662f\u6ca1\u6709\u6574\u7406\u597d\uff0c\u6211\u5e2e\u4f60\u2026\u2026\u201d\u4e0d\u7b49\u7fdf\u4e39\u9752\u62d2\u7edd\uff0c\u4fbf\u4e0e\u5979\u4e00\u8d77\u51fa\u4e86\u7535\u68af\u3002</p><p>\u3000\u3000\u6253\u5f00\u95e8\uff0c\u7fdf\u4e39\u9752\u8981\u5f80\u91cc\u8d70\uff0c\u5f20\u606a\u62fd\u7740\u5979\u7684\u624b\uff0c\u5c06\u5979\u62b5\u5230\u95e8\u540e\u51dd\u671b\u7740\u5979\u5fae\u5e26\u6c34\u8ff9\u7684\u7f8e\u7738\uff0c\u773c\u795e\u6d41\u76fc\u800c\u8ff7\u79bb\uff0c\u6444\u4eba\u5fc3\u9b44\u3002\u5f20\u606a\u53cc\u624b\u6284\u8fc7\u5979\u67d4\u8f6f\u7684\u8170\u80a2\u3001\u6258\u4f4f\u5979\u9971\u6ee1\u7684\u67d4\u4e2d\u5e26\u5f39\u7684\u81c0\u90e8\uff0c\u8f7b\u8f7b\u4e00\u6402\uff0c\u5c06\u5979\u7d27\u7d27\u7684\u62e5\u5728\u6000\u91cc\uff0c\u6709\u4e00\u80a1\u5e7d\u5e7d\u7684\u5170\u82b1\u9999\u6c14\u6251\u5165\u9f3b\u4e2d\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u8bf4\u8fc7\u6765\u5e2e\u6211\u6536\u62fe\u623f\u95f4\u7684\u5417\uff1f\u201d\u7fdf\u4e39\u9752\u8bed\u6c14\u6e29\u67d4\uff0c\u8fd8\u60f3\u6700\u540e\u6323\u624e\u4e00\u4e0b\uff0c\u5f20\u606a\u7684\u624b\u5df2\u7ecf\u5728\u63c9\u634f\u5979\u5bcc\u6709\u5f39\u6027\u7684\u81c0\u8089\u4e86\u3002</p><p>\u3000\u3000\u201c\u5148\u770b\u770b\u4f60\u7684\u4f24\u53e3\u2026\u2026\u201d</p><p>\u3000\u3000\u7a7a\u5185\u6253\u7740\u7a7a\u8c03\uff0c\u6e29\u6696\u5982\u6625\uff0c\u5f20\u606a\u5c06\u624b\u4f38\u624b\u7fdf\u4e39\u9752\u7684\u9886\u53e3\u91cc\uff0c\u7fdf\u4e39\u9752\u8eab\u5b50\u5fae\u5fae\u4e00\u7f29\uff0c\u5634\u91cc\u53ea\u662f\u8f7b\u547c\uff1a\u201c\u51b7\u2026\u2026\u201d\u6293\u4f4f\u5f20\u606a\u7684\u624b\uff0c\u5374\u6ca1\u6709\u5c06\u4ed6\u7684\u624b\u62bd\u51fa\u6765\uff0c\u800c\u662f\u90a3\u53ea\u5fae\u51b0\u7684\u624b\u8d34\u7740\u81ea\u5df1\u7684\u7410\u9aa8\u6e29\u4e00\u4e0b\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\u624d\u653e\u5f00\u624b\uff0c\u53cc\u624b\u73af\u4f4f\u5f20\u606a\u7684\u8116\u5b50\uff0c\u9644\u5634\u5230\u4ed6\u8033\u8fb9\u8f7b\u8bed\u9053\uff1a\u201c\u4e0d\u51b7\u4e86\u2026\u2026\u201d</p></article>"}}}]);
