(window.webpackJsonp=window.webpackJsonp||[]).push([[1158],{1181:function(p){p.exports={chapterTitle:null,title:"\u7b2c951\u7ae0 \u6700\u60e8\u7684\u6349\u5978",content:"<article><h2>\u7b2c951\u7ae0 \u6700\u60e8\u7684\u6349\u5978</h2><p>\u3000\u3000\u6b64\u65f6\uff0c\u5915\u9633\u624d\u6c89\u5230\u5c71\u811a\u4e0b\uff0c\u7a97\u5916\u6709\u7ec6\u7ec6\u788e\u788e\u7684\u6b4c\u58f0\u4f20\u6765\uff0c\u7ec6\u542c\u4e86\u4e00\u4f1a\u513f\uff0c\u662f\u5b66\u5e9c\u5df7\u90a3\u8fb9\u6709\u9ad8\u97f3\u5587\u53ed\u5728\u64ad\u653e\u6c5f\u9edb\u513f\u7684\u6b4c\u3002\u5f20\u606a\u51dd\u671b\u7740\u7fdf\u4e39\u9752\u7684\u773c\u775b\uff0c\u660e\u4eae\u800c\u5a9a\u6001\u6a2a\u751f\u7684\u7738\u5b50\u91cc\u6625\u8272\u6b63\u6d53\uff0c\u624b\u91cc\u8f7b\u8f7b\u7684\u6469\u6332\u7740\u5979\u7ea4\u7ec6\u7684\u7410\u9aa8\uff0c\u6709\u7740\u7ef8\u7f0e\u4e00\u6837\u7684\u67d4\u6ed1\uff0c\u5728\u7ea4\u7ec6\u624b\u6307\u7684\u8f7b\u629a\u4e0b\uff0c\u6d6e\u8d77\u7c89\u7ea2\u7684\u6655\u8272\uff0c\u9f3b\u606f\u707c\u70ed\u800c\u82ac\u82b3\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6d45\u6d45\u7684\u671b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u53c8\u5782\u4e0b\u773c\u5e18\uff0c\u957f\u800c\u9ad8\u5377\u7684\u776b\u6bdb\u98de\u6311\u7740\uff0c\u77b3\u5b50\u91cc\u6709\u7740\u6c34\u8ff9\uff0c\u76c8\u76c8\u53d1\u4eae\uff0c\u6ca1\u6709\u54ea\u4e2a\u7537\u4eba\u80fd\u53d7\u5f97\u4e86\u8fd9\u79cd\u773c\u795e\u7684\u64a9\u62e8\uff0c\u867d\u7136\u5728\u7ed9\u64a9\u62e8\u4e4b\u524d\uff0c\u5f20\u606a\u7684\u5fc3\u5c31\u4e71\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u603b\u7b97\u8fd8\u8bb0\u5f97\u4ed6\u7684\u501f\u53e3\uff0c\u5148\u5c06\u7fdf\u4e39\u9752\u7684\u4e0a\u8863\u8131\u6389\uff0c\u91cc\u9762\u7a7f\u7740\u540a\u5e26\u886b\uff0c\u6ca1\u6709\u5408\u9002\u7684\u4e73\u7f69\u4e0d\u78e8\u4fa7\u8fb9\u4e0a\u7684\u4f24\u53e3\uff0c\u540a\u5e26\u886b\u91cc\u5c31\u6ca1\u6709\u518d\u7a7f\u4ec0\u4e48\uff0c\u5f20\u606a\u6bd4\u7fdf\u4e39\u9752\u7565\u9ad8\u51e0\u516c\u5206\uff0c\u770b\u7740\u4ece\u540a\u5e26\u91cc\u66b4\u9732\u51fa\u6765\u7684\u5927\u534a\u4e2a\u4e73\u623f\uff0c\u9c9c\u5ae9\u3001\u9971\u6ee1\u800c\u662f\u9ad8\u8038\u633a\u62e8\uff0c\u96ea\u767d\u7684\u808c\u80a4\u8fd8\u95ea\u7740\u8ff7\u4eba\u7684\u5149\u6cfd\u3002</p><p>\u3000\u3000\u770b\u5230\u5f20\u606a\u5782\u6d8e\u6b32\u6ef4\u7684\u773c\u795e\uff0c\u7fdf\u4e39\u9752\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u5c06\u540a\u5e26\u5f80\u4e0a\u63d0\u4e86\u63d0\uff0c\u8bf4\u9053\uff1a\u201c\u4f24\u53e3\u5f88\u4e11\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6309\u7740\u5979\u67d4\u5ae9\u7684\u624b\uff0c\u5c06\u540a\u5e26\u62c9\u5230\u80f3\u818a\u8098\u5904\uff0c\u8ba9\u4e00\u5bf9\u4eba\u95f4\u5c11\u89c1\u7684\u4e30\u4e73\u5f7b\u5e95\u7684\u66b4\u9732\u51fa\u6765\uff0c\u5ae3\u7ea2\u7684\u4e73\u5c16\u4e5f\u5fae\u5fae\u5c16\u7acb\u7740\uff0c\u9971\u6ee1\u800c\u9c9c\u5ae9\uff0c\u4f24\u53e3\u5728\u53f3\u4e73\u7684\u5916\u4fa7\uff0c\u4e00\u9053\u7c89\u7ea2\u7684\u53e3\u5b50\u4e24\u4fa7\u662f\u5305\u624e\u7ebf\u62c6\u9664\u540e\u7559\u4e0b\u6765\u7684\u75d5\u8ff9\uff0c\u5c31\u50cf\u7d27\u62bf\u7740\u7684\u5a74\u513f\u5634\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u8ff7\u4eba\u3002\u201d\u5f20\u606a\u624b\u6258\u8d77\u6c89\u7538\u7538\u7684\u4e73\u5cf0\uff0c\u7a0d\u8e72\u4e0b\u6765\uff0c\u5634\u5507\u8d34\u5230\u4e73\u623f\u5916\u4fa7\u7684\u4f24\u53e3\u4e0a\u8f7b\u8f7b\u7684\u543b\u4e86\u543b\uff0c\u7fdf\u4e39\u9752\u8f7b\u98a4\u7684\u808c\u80a4\u8d77\u4e86\u4e00\u4e9b\u7ec6\u7c92\u5b50\uff0c\u5f20\u606a\u53c8\u5507\u5cf0\u8f7b\u8f7b\u626b\u8fc7\u7c89\u8272\u7684\u4e73\u6655\uff0c\u8f7b\u8f7b\u542b\u4f4f\u9c9c\u7ea2\u7684\u4e73\u5c16\uff0c\u542e\u8bb8\u8d77\u6765\uff0c\u53e6\u4e00\u53ea\u624b\u63c9\u5f04\u8d77\u7fdf\u4e39\u9752\u53e6\u4e00\u53ea\u672a\u53d7\u4f24\u7684\u4e73\u623f\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u6837\u5b50\u53ef\u6cbb\u4e0d\u4e86\u4f24\u2026\u2026\u201d\u7fdf\u4e39\u9752\u6323\u624e\u4e00\u4e0b\uff0c\u53ef\u662f\u4e73\u5c16\u7ed9\u8214\u5f04\u7740\u4e00\u9635\u9635\u7729\u6655\u671d\u8111\u6d77\u91cc\u6d8c\u6765\uff0c\u5927\u817f\u5916\u4fa7\u8fd8\u7ed9\u90a3\u6839\u53c8\u786c\u53c8\u7c97\u7684\u4e1c\u897f\u7d27\u538b\u7740\uff0c\u7a7f\u7740\u8584\u8584\u7684\u897f\u88e4\uff0c\u90fd\u611f\u89c9\u5230\u90a3\u6839\u4e1c\u897f\u7684\u706b\u70ed\u3002\u5f20\u606a\u7684\u624b\u4ece\u8170\u5e26\u91cc\u94bb\u8fdb\u53bb\uff0c\u7fdf\u4e39\u9752\u4e5f\u5c31\u6ca1\u6709\u6323\u624e\uff0c\u7ed9\u4ed6\u7ea4\u7ec6\u7684\u624b\u6307\u4f38\u5230\u67d4\u6ed1\u7ec6\u817b\u7684\u5927\u817f\u5fc3\u4e0a\u629a\u5f04\uff0c\u5979\u4e5f\u4e0d\u60f3\u6323\u624e\uff0c\u53ea\u662f\u5e0c\u671b\u80fd\u77dc\u6301\u4e9b\u4e0d\u8981\u5c06\u817f\u5206\u5f00\u7ed9\u4ed6\u629a\u5f04\u5c31\u597d\uff0c\u5979\u81ea\u5df1\u90fd\u611f\u89c9\u5230\u4e0b\u8eab\u6e7f\u7c98\u4e00\u7247\uff0c\u4ed6\u7684\u624b\u6307\u5f04\u5230\u58f6\u53e3\uff0c\u8fd8\u6070\u597d\u6709\u4e00\u80a1\u5bc6\u8ba1\u6d8c\u51fa\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u536b\u5170\u7761\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u542c\u89c1\u95e8\u9501\u54cd\uff0c\u4e0b\u610f\u8bc6\u60f3\u662f\u4e39\u9752\u56de\u6765\u4e86\uff0c\u6709\u4e9b\u4e0d\u5927\u613f\u610f\u8d77\u6765\uff0c\u8d56\u5728\u5e8a\u4e0a\u597d\u4e00\u4f1a\u513f\uff0c\u542c\u89c1\u5916\u95f4\u6709\u4e9b\u7ec6\u788e\u7684\u58f0\u97f3\u89c9\u5f97\u5947\u602a\uff0c\u624d\u6323\u624e\u7740\u722c\u8d77\u6765\u62c9\u5f00\u623f\u95e8\uff0c\u770b\u5230\u7fdf\u4e39\u9752\u6781\u4e3a\u4eab\u53d7\u7684\u95ed\u7740\u773c\u775b\u7ed9\u5f20\u606a\u62b5\u5728\u95e8\u80cc\u540e\uff0c\u9732\u51fa\u96ea\u5ae9\u4e30\u8174\u7684\u7855\u4e73\uff0c\u4ece\u5979\u7684\u89d2\u5ea6\u770b\u4e0d\u5230\u5f20\u606a\u7684\u4e00\u53ea\u624b\u5df2\u7ecf\u4f38\u5230\u7fdf\u4e39\u9752\u7684\u88e4\u5b50\u91cc\uff0c\u4f46\u662f\u5374\u6e05\u6e05\u695a\u695a\u7684\u770b\u89c1\u5f20\u606a\u53cc\u5507\u5c06\u7fdf\u4e39\u9752\u9c9c\u5ae9\u5ae3\u7ea2\u7684\u4e73\u5c16\u542b\u7740\u8214\u5f04\uff0c\u53e6\u4e00\u624b\u6293\u4f4f\u7fdf\u4e39\u9752\u53e6\u4e00\u53ea\u767d\u4e73\u5938\u5f20\u7684\u63c9\u634f\u7740\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u536b\u5170\u624b\u6342\u4f4f\u5634\u5df4\uff0c\u53ef\u60dc\u6ca1\u6765\u5f97\u53ca\u5c06\u90a3\u58f0\u60ca\u547c\u6342\u4f4f\u5728\u5634\u5df4\u91cc\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u53d7\u60ca\u7684\u7741\u5f00\u773c\uff0c\u5f20\u606a\u4e5f\u8c14\u7136\u8f6c\u8fc7\u5934\u6765\uff0c\u770b\u7740\u536b\u5170\u624b\u6342\u7740\u5634\u5507\uff0c\u77aa\u5927\u7684\u773c\u775b\u91cc\u6d41\u9732\u51fa\u4e0d\u53ef\u601d\u5f02\u7684\u795e\u8272\u6765\u3002\u53ea\u662f\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u60ca\u8c14\u7684\u795e\u8272\u6709\u4e9b\u6ed1\u7a3d\uff0c\u536b\u5170\u770b\u4e86\u8981\u5fcd\u4e0d\u4f4f\u7b11\u51fa\u6765\uff0c\u8fd9\u65f6\u5019\u624d\u770b\u89c1\u5f20\u606a\u7684\u53e6\u4e00\u53ea\u4ece\u7fdf\u4e39\u9752\u7684\u88e4\u5b50\u91cc\u62bd\u56de\u6765\uff0c\u624b\u6307\u5934\u4e0a\u8fd8\u4eae\u6676\u6676\u7684\u62b9\u7740\u6e05\u6cb9\u4e00\u6837\u7684\u871c\u6c41\uff0c\u5f20\u606a\u5fae\u4fa7\u8fc7\u8eab\uff0c\u4ed6\u4e0b\u8eab\u9ad8\u9ad8\u7684\u9686\u8d77\u4e00\u56e2\u76f4\u8981\u5c06\u88e4\u5b50\u7ed9\u6345\u7834\u3002\u536b\u5170\u54ea\u91cc\u89c1\u8fc7\u5982\u6b64\u9999\u8273\u7684\u573a\u9762\uff0c\u7c89\u8138\u817e\u7684\u6da8\u5f97\u901a\u7ea2\uff0c\u8d76\u7d27\u53cc\u624b\u6342\u4f4f\u773c\u775b\uff0c\u81ea\u6b3a\u6b3a\u4eba\u7684\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u770b\u89c1\uff0c\u6211\u6ca1\u6709\u770b\u89c1\u2026\u2026\u201d\u8f6c\u8eab\u8981\u5f80\u623f\u95f4\u91cc\u8eb2\u53bb\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u731b\u7136\u649e\u623f\u95e8\u8fb9\u68f1\u4e0a\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u8fd9\u614c\u624b\u614c\u811a\u7684\u731b\u7136\u4e00\u649e\uff0c\u53ea\u649e\u5f97\u773c\u5192\u91d1\u661f\uff0c\u75db\u5f97\u773c\u6cea\u90fd\u8981\u6d41\u51fa\u6765\uff0c\u536b\u5170\u5403\u4e0d\u4f4f\u75db\uff0c\u53ea\u5f97\u6342\u7740\u989d\u5934\u8e72\u5230\u5730\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u54ea\u91cc\u60f3\u5230\u536b\u5170\u4f1a\u5728\u8fd9\u5c4b\u5b50\u91cc\u7761\u89c9\uff0c\u7fdf\u4e39\u9752\u8d76\u7d27\u5c06\u8863\u670d\u7a7f\u597d\uff0c\u6253\u5f00\u623f\u95e8\u5c06\u8fd8\u4e00\u8138\u60ca\u8c14\u7684\u5f20\u606a\u5f80\u95e8\u5916\u63a8\uff0c\u201c\u7830\u201d\u7684\u5173\u597d\u95e8\u540e\uff0c\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u89c1\u536b\u5170\u8fd8\u5fcd\u7740\u75db\u8e72\u5728\u5730\u4e0a\uff0c\u8d70\u8fc7\u6765\u6400\u5979\u8d77\u6765\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\u7761\u89c9\uff1f\u201d</p><p>\u3000\u3000\u536b\u5170\u63c9\u7740\u7ed9\u649e\u5f97\u6781\u75db\u7684\u989d\u5934\uff0c\u54ed\u7b11\u4e0d\u5f97\u7684\u8bf4\u9053\uff1a\u201c\u8981\u77e5\u9053\u4f1a\u770b\u5230\u8fd9\u4e9b\u810f\u4e1c\u897f\uff0c\u6253\u6b7b\u6211\u90fd\u5728\u8fd9\u91cc\u7761\u89c9\u2014\u2014\u4e0b\u5348\u7684\u82f1\u8bed\u8bfe\u4e34\u65f6\u53d6\u6d88\u4e86\uff0c\u6211\u60f3\u7740\u8fc7\u6765\u518d\u5e2e\u4f60\u6536\u62fe\u4e00\u4e0b\u5c4b\u5b50\uff0c\u6536\u62fe\u5b8c\u5c31\u76f4\u72af\u56f0\u2014\u2014\u4f60\u5988\u5988\u6628\u5929\u7761\u5f97\u6c89\uff0c\u53c8\u6253\u547c\u4e86\uff0c\u6211\u6ca1\u6709\u7761\u597d\u3002\u4f60\u2026\u2026\u4f60\u2026\u2026\u4f60\u4eec\u5c31\u4e0d\u80fd\u5148\u770b\u770b\u5c4b\u5b50\u91cc\u6709\u6ca1\u6709\u5176\u4ed6\u4eba\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u89c1\u536b\u5170\u989d\u5934\u7ed9\u649e\u51fa\u4e00\u9053\u7ea2\u5370\u5b50\uff0c\u7709\u76b1\u773c\u6324\uff0c\u771f\u662f\u75db\u5f97\u5389\u5bb3\uff0c\u5fcd\u4e0d\u4f4f\u6251\u54e7\u7b11\u58f0\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u6709\u8138\u7b11\uff0c\u660e\u660e\u662f\u4f60\u4eec\u505a\u8fd9\u9f8c\u9f8a\u4e8b\uff0c\u600e\u4e48\u53eb\u6211\u649e\u8fd9\u4e48\u75db\uff1f\u201d\u536b\u5170\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u201c\u76f4\u4ecb\u51a4\u6789\u6b7b\u6211\u4e86\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u770b\u7740\u90a3\u6761\u7ea2\u5370\u5b50\uff0c\u5fc3\u75bc\u7684\u4f38\u624b\u6478\u5979\u63c9\u4e86\u63c9\u989d\u5934\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u63c9\u4e00\u63c9\u5c31\u4e0d\u75bc\u4e86\u2026\u2026\u201d\u5979\u4e0e\u536b\u5170\u4eb2\u8fd1\u60ef\u4e86\uff0c\u7ed9\u536b\u5170\u649e\u7834\u7684\u90a3\u5239\u90a3\u95f4\u5413\u4e86\u4e00\u8df3\uff0c\u5374\u6ca1\u6709\u89c9\u5f97\u7279\u522b\u7684\u96be\u582a\uff0c\u53ea\u662f\u5728\u5fc3\u91cc\u72e0\u72e0\u7684\u5c06\u5f20\u606a\u9a82\u4e86\u4e00\u901a\uff0c\u8981\u4e0d\u662f\u4ed6\u8fd9\u4e48\u6025\u8272\u521a\u8fdb\u5c4b\u5c31\u5c06\u81ea\u5df1\u62b5\u5728\u95e8\u540e\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u53d1\u73b0\u4e0d\u4e86\u536b\u5170\u7761\u8fd9\u5c4b\u5b50\u91cc\u3002</p><p>\u3000\u3000\u536b\u5170\u63c9\u7740\u989d\u5934\u5750\u5230\u4e00\u65c1\u7684\u6c99\u53d1\uff0c\u5f20\u606a\u8d3c\u4f3c\u7684\u9003\u4e86\u51fa\u53bb\uff0c\u6ca1\u6709\u5f20\u606a\u5728\uff0c\u5979\u5c31\u4e0d\u518d\u5fc3\u614c\u4e86\uff0c\u7259\u5c16\u5634\u5229\u7684\u95ee\u7fdf\u4e39\u9752\uff1a\u201c\u771f\u662f\u53e3\u662f\u5fc3\u975e\u7684\u4e00\u4e2a\u4eba\u554a\uff0c\u4e4b\u524d\u8fd8\u8bf4\u90a3\u5c0f\u5b50\u662f\u4e2a\u80fd\u5c06\u5973\u4eba\u5fc3\u513f\u3001\u9b44\u529b\u90fd\u541e\u566c\u6389\u8fde\u9aa8\u5934\u6e23\u90fd\u4e0d\u5269\u7684\u9b54\u5934\uff0c\u4f60\u81ea\u4e2a\u513f\u600e\u4e48\u5c31\u98de\u86fe\u6251\u706b\u9677\u8fdb\u53bb\uff1f\u9677\u8fdb\u53bb\u4e5f\u5c31\u7b97\u4e86\uff0c\u8fd8\u594b\u4e0d\u987e\u8eab\u7684\u66ff\u90a3\u5c0f\u5b50\u6321\u4e00\u5200\uff0c\u504f\u6211\u5fc3\u773c\u76f4\uff0c\u8fd8\u771f\u4ee5\u4e3a\u4f60\u4eec\u5173\u7cfb\u7eaf\u6d01\u5462\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u77e5\u9053\u8fd9\u59ae\u5b50\u5bf9\u5f20\u606a\u4e5f\u6709\u4e00\u8154\u60c5\u601d\uff0c\u81ea\u5df1\u5f53\u65f6\u8fd8\u529d\u8fc7\u5979\u4e0d\u8981\u9677\u8fdb\u53bb\uff0c\u504f\u504f\u81ea\u5df1\u9677\u8fdb\u53bb\u7684\u4e8b\u7ed9\u5979\u649e\u7834\u4e86\uff0c\u4e5f\u96be\u514d\u5979\u5fc3\u91cc\u6709\u4e9b\u4e0d\u5fff\uff0c\u8fd8\u771f\u65e0\u6cd5\u89e3\u91ca\u4ec0\u4e48\uff0c\u603b\u4e0d\u80fd\u8bf4\u4ed6\u8981\u770b\u4e73\u623f\u4e0a\u7684\u4f24\u53e3\u7ea0\u7f20\u4e0d\u8fc7\u624d\u8131\u4e0b\u8863\u670d\u6765\u7ed9\u4ed6\u770b\u5427\uff1f\u53ea\u662f\u8f7b\u63a8\u4e86\u536b\u5170\u4e00\u628a\uff0c\u4e0e\u5979\u6324\u5750\u5230\u4e00\u8d77\uff0c\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u4e5f\u662f\u7ed9\u9b3c\u8ff7\u4e86\u5fc3\u7a8d\uff0c\u51b7\u9759\u4e0b\u6765\u60f3\u4e00\u60f3\uff0c\u6211\u4f55\u82e6\u53bb\u8d9f\u8fd9\u6d51\u6c34\uff1f\u53ea\u662f\u60f3\u7740\u6211\u8fd9\u79cd\u5973\u4eba\u603b\u5bfb\u4e0d\u5230\u666e\u901a\u7684\u5e78\u798f\uff0c\u8fd9\u6837\u7684\u5feb\u4e50\u80fd\u5077\u4e00\u5206\u662f\u4e00\u5206\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u89c9\u5f97\u6211\u4f1a\u574f\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u600e\u4e48\u4f1a\uff0c\u6211\u89c9\u5f97\u5929\u4e0b\u5973\u4eba\u90fd\u6ca1\u6709\u4f60\u66f4\u6709\u8d44\u683c\u83b7\u5f97\u5e78\u798f\u4e86\uff0c\u53ea\u53ef\u60dc\u4f60\u7684\u5e78\u798f\uff0c\u666e\u901a\u7537\u4eba\u7ed9\u4e0d\u4e86\u4f60\u201d\u536b\u5170\u521a\u624d\u90a3\u901a\u8bdd\u8bf4\u51fa\u53e3\u5c31\u89c9\u5f97\u540e\u6094\u4e86\uff0c\u81ea\u5df1\u90fd\u89c9\u5f97\u918b\u610f\u5341\u8db3\uff0c\u90fd\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u770b\u7fdf\u4e39\u9752\uff0c\u8737\u817f\u5750\u5230\u6c99\u53d1\u4e0a\uff0c\u8eab\u5b50\u4f9d\u7740\u7fdf\u4e39\u9752\uff0c\u611f\u89c9\u5230\u540e\u80cc\u62b5\u7740\u7fdf\u4e39\u9752\u4e30\u633a\u7684\u4e73\u623f\uff0c\u8111\u5b50\u91cc\u53c8\u95ea\u8fc7\u521a\u624d\u9999\u8273\u7684\u4e00\u5e55\uff0c\u89c9\u5f97\u81ea\u5df1\u7684\u8138\u6709\u4e9b\u53d1\u70eb\uff0c\u540e\u80cc\u5f80\u540e\u9876\u4e86\u9876\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u8ddf\u90a3\u5c0f\u5b50\u5728\u4e00\u8d77\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u53d1\u5927\u6c34\u90a3\u9635\u5b50\u6574\u5929\u8ddf\u4ed6\u5728\u4e00\u8d77\uff0c\u4e0a\u5824\u65f6\u6709\u6b21\u4e0d\u5c0f\u5fc3\u6ed1\u8fdb\u6c34\u91cc\uff0c\u6211\u8dcc\u8fdb\u6c34\u91cc\u5012\u6ca1\u6709\u4ec0\u4e48\u60ca\u614c\uff0c\u53ea\u662f\u770b\u5230\u4ed6\u6beb\u4e0d\u72b9\u8c6b\u7684\u51b2\u5728\u4fdd\u536b\u524d\u9762\u8df3\u4e0b\u6765\uff0c\u5fc3\u91cc\u5c31\u60f3\u81ea\u5df1\u5b8c\u4e86\u2026\u2026\u201d\u7fdf\u4e39\u9752\u5bf9\u536b\u5170\u5012\u6ca1\u6709\u4ec0\u4e48\u597d\u9690\u7792\u7684\uff0c\u201c\u540e\u6765\u6211\u7238\u7ed9\u6211\u6253\u7535\u8bdd\uff0c\u6211\u8fd9\u5e73\u65f6\u575a\u786c\u5f97\u5f88\u7684\u5fc3\uff0c\u6574\u4e2a\u7684\u5c31\u50cf\u7ed9\u6ce1\u4e86\u6c34\u7684\u9762\u5305\u4f3c\u7684\uff0c\u8f7b\u8f7b\u7684\u4e00\u6233\u5c31\u662f\u4e00\u4e2a\u6d1e\uff0c\u90a3\u5929\u53c8\u751f\u4e86\u75c5\uff0c\u8eab\u5b50\u4e5f\u8f6f\u8f6f\u7684\u65e0\u529b\uff0c\u8fd9\u5bb6\u4f19\u6700\u5927\u7684\u6bdb\u75c5\u5c31\u662f\u5f85\u4eba\u592a\u6e29\u67d4\uff0c\u53ef\u89c1\u8fd9\u5bb6\u4f19\u5e73\u65e5\u73a9\u5f04\u7684\u5973\u4eba\u6709\u591a\u591a\uff0c\u504f\u504f\u8fd9\u5f20\u7f51\u5f20\u5728\u8eab\u524d\u6211\u8fd8\u8981\u8eab\u4e0d\u7531\u5df1\u7684\u9677\u8fdb\u53bb\u2026\u2026\u5c31\u8fd9\u6837\u6709\u8fc7\u4e00\u6b21\uff0c\u540e\u6765\u8fd8\u60f3\u8eb2\u5f00\u4ed6\uff0c\u4e0d\u80fd\u4e00\u9519\u518d\u9519\uff0c\u53c8\u53d1\u751f\u8fd9\u6863\u5b50\u4e8b\u2014\u2014\u6328\u4e86\u8fd9\u4e00\u5200\uff0c\u7b97\u662f\u770b\u900f\u5f7b\u4e86\uff1a\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f55\u66fe\u6709\u7537\u4eba\u7ed9\u6211\u8fd9\u6837\u7684\u6b22\uff1f\uff0c\u54ea\u6015\u53ea\u6709\u4e00\u5206\uff0c\u5374\u8981\u597d\u8fc7\u5176\u4ed6\u7537\u4eba\u80fd\u7ed9\u6211\u7684\u65e0\u6570\uff0c\u4f60\u8ba9\u6211\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u536b\u5170\u62bf\u7740\u5634\uff0c\u60f3\u8bf4\u4ec0\u4e48\u5374\u6ca1\u6709\u8bf4\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5077\u60c5\u7ed9\u649e\u7834\uff0c\u5f20\u606a\u4ed3\u7687\u7684\u9003\u51fa\u6765\uff0c\u8fdb\u4e86\u7535\u68af\u624d\u53d1\u73b0\u5916\u5957\u843d\u5728\u4e39\u9752\u623f\u91cc\u4e86\uff0c\u4ed6\u8fd9\u65f6\u5019\u7edd\u6ca1\u6709\u80c6\u91cf\u53bb\u62ff\u5916\u5957\u7684\uff0c\u4ed6\u6574\u4e86\u6574\u886c\u886b\uff0c\u51fa\u4e86\u7535\u68af\u8d70\u8fdb\u81ea\u5df1\u7684\u623f\u5b50\u91cc\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4e5f\u521a\u521a\u56de\u6765\uff0c\u6b63\u5c06\u624b\u888b\u4e22\u684c\u4e0a\uff0c\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u770b\u5230\u4f60\u5728\u6211\u524d\u9762\u8fdb\u5c0f\u533a\u7684\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u5148\u5230\u5bb6\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5148\u9001\u4e39\u9752\u59d0\u56de\u5c4b\u4e86\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u803d\u6401\u4e86\u4e00\u4f1a\u513f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u675c\u98de\u521a\u521a\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u8bf4\u662f\u5ea7\u4f4d\u65e9\u5c31\u8ba2\u597d\u4e86\uff0c\u201d\u5b59\u9759\u6aac\u4e5f\u4e0d\u7591\u5176\u4ed6\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u662f\u4e0d\u662f\u5148\u627e\u4e39\u9752\u59d0\u4e00\u8d77\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u4e0d\u6562\u53bb\u9762\u5bf9\u536b\u5170\uff0c\u66f4\u6015\u536b\u5170\u4f1a\u843d\u51fa\u4ec0\u4e48\u9a6c\u811a\u6cc4\u9732\u4ed6\u5077\u60c5\u7684\u4e8b\u60c5\uff0c\u771f\u662f\u5934\u75bc\u5f97\u7d27\uff0c\u8fd9\u5927\u6982\u5c31\u53eb\u5fc3\u6025\u5403\u4e0d\u4e86\u70ed\u8c46\u8150\u5427\uff1f\u89c1\u5b59\u9759\u6aac\u62ff\u8d77\u624b\u888b\u8981\u53bb\u627e\u7fdf\u4e39\u9752\uff0c\u4fbf\u8bf4\u9053\uff1a\u201c\u6211\u8981\u5148\u5904\u7406\u4e00\u4e0b\u90ae\u4ef6\uff0c\u8981\u4e0d\u4f60\u4eec\u5148\u4e0b\u53bb\uff0c\u7b49\u5dee\u4e0d\u591a\u8981\u5f00\u996d\u4e86\u518d\u7ed9\u6211\u6253\u7535\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u7701\u4eba\u6c11\u533b\u9662\u7684\u7279\u62a4\u75c5\u623f\u7adf\u7136\u6ca1\u6709\u7f51\u7edc\uff0c\u6709\u4e9b\u7d27\u6025\u7684\u4e8b\u60c5\uff0c\u90fd\u53ef\u4ee5\u901a\u8fc7\u7535\u8bdd\u8054\u7cfb\uff0c\u4e0d\u8fc7\u80fd\u9884\u89c1\u90ae\u7bb1\u91cc\u8fd8\u662f\u4f1a\u79ef\u7d2f\u4e00\u5927\u5806\u7684\u90ae\u4ef6\uff0c\u5f20\u606a\u4e00\u76f4\u62d6\u5230\u4e03\u70b9\u949f\uff0c\u7ed9\u8fde\u7eed\u50ac\u4e86\u51e0\u901a\u7535\u8bdd\uff0c\u624d\u59d7\u59d7\u7684\u8d70\u5230\u805a\u9910\u7684\u9910\u9986\uff0c\u8d70\u5c06\u8fdb\u53bb\uff0c\u770b\u5230\u597d\u4e9b\u4eba\u770b\u5230\u536b\u5170\u989d\u5934\u7684\u7ea2\u5370\u5b50\u90fd\u95ee\u600e\u4e48\u56de\u4e8b\u3002</p><p>\u3000\u3000\u536b\u5170\u53ea\u662f\u8bf4\uff1a\u201c\u4e0b\u5348\u770b\u5230\u9b3c\u4e86\uff0c\u7ed9\u5413\u4e86\u4e00\u8df3\uff0c\u8f6c\u8eab\u5c31\u649e\u95e8\u4e0a\u4e86\u2026\u2026\u201d\u60f3\u7740\u9999\u8273\u523a\u6fc0\u7684\u573a\u666f\uff0c\u5374\u4e5f\u4e0d\u597d\u610f\u601d\u8ddf\u5f20\u606a\u7684\u773c\u795e\u78b0\u4e0a\uff0c\u5c31\u597d\u50cf\u5979\u5077\u60c5\u7ed9\u5f20\u606a\u649e\u7834\u4f3c\u7684\u3002</p></article>"}}}]);
