(window.webpackJsonp=window.webpackJsonp||[]).push([[116],{138:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u516b\u8282 \u8131\u8eab",content:"<article><h2>\u7b2c\u4e8c\u5341\u516b\u8282 \u8131\u8eab</h2><p>\u5c0f\u80d6\u5b50\u8679\u5f69\u9f99\u7684\u5229\u722a\u5c16\u7259\u867d\u7136\u51f6\u731b\uff0c\u4f46\u5374\u6253\u4e0d\u4e2d\u5e7d\u7075\u865a\u4f53\uff0c\u5c3d\u6570\u843d\u7a7a\uff0c\u4ed6\u53c8\u4e0d\u65bd\u9b54\u6cd5\uff0c\u5c40\u9762\u5c31\u6210\u4e86\u5b8c\u5168\u7684\u88ab\u52a8\u6328\u6253\u3002\u603b\u7b97\u4ed6\u662f\u8679\u5f69\u9f99\uff0c\u5929\u8d4b\u5c31\u5bf9\u9b54\u6cd5\u6709\u6781\u5f3a\u7684\u62b5\u6297\u80fd\u529b\uff0c\u94f6\u9f99\u5e7d\u7075\u7684\u9b54\u6cd5\u653b\u51fb\u867d\u7136\u51f6\u72e0\u51cc\u5389\uff0c\u8fde\u7ef5\u4e0d\u7edd\uff0c\u6253\u5f97\u8679\u5f69\u9f99\u5de6\u652f\u53f3\u7ecc\uff0c\u72fc\u72c8\u4e0d\u582a\uff0c\u4e00\u65f6\u5374\u4e5f\u96be\u4ee5\u771f\u6b63\u4f24\u5bb3\u5230\u4ed6\u3002</p><p>\u8679\u5f69\u9f99\u6253\u4e0d\u4e2d\u94f6\u9f99\u5e7d\u7075\uff0c\u94f6\u9f99\u7684\u9b54\u6cd5\u4e00\u65f6\u4e5f\u4f24\u4e0d\u5230\u8679\u5f69\u9f99\uff0c\u53cc\u65b9\u5c31\u8fd9\u6837\u50f5\u6301\u4f4f\u4e86\u3002\u597d\u5728\u4ed6\u4eec\u662f\u5728\u9ad8\u7a7a\u4e2d\u93d6\u6218\uff0c\u6ca1\u6709\u6ce2\u53ca\u5730\u9762\u7684\u4eba\u7fa4\u548c\u5efa\u7b51\u3002</p><p>\u53ea\u662f\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u5f04\u6210\u8fd9\u4e48\u4e00\u526f\u5c40\u9762\uff1f\u94f6\u9f99\u5e7d\u7075\u597d\u7aef\u7aef\u5730\u88ab\u5c01\u5728\u5730\u4e0b\uff0c\u600e\u4e48\u4f1a\u8dd1\u51fa\u6765\uff1f\u5c0f\u80d6\u5b50\u4e0d\u662f\u745e\u6069\u65af\u5766\u7684\u8ddf\u73ed\u4e48\uff0c\u600e\u4e48\u7a81\u7136\u8dd1\u6765\u8ddf\u8fd9\u53ea\u94f6\u9f99\u6253\u67b6\uff1f</p><p>\u8fd9\u4e9b\u7b54\u6848\u90fd\u65e0\u4eba\u89e3\u7b54\uff0c\u4e24\u53ea\u5de8\u9f99\u7684\u53ae\u6740\uff0c\u9020\u6210\u7684\u52a8\u9759\u628a\u6574\u4e2a\u70db\u5821\u90fd\u5435\u9192\u4e86\uff0c\u70db\u5821\u81ea\u7136\u6709\u591c\u95f4\u5de1\u903b\u7684\u536b\u5175\uff0c\u4f46\u4ed6\u4eec\u5bf9\u7a7a\u4e2d\u6fc0\u6218\u7684\u5de8\u9f99\u4e5f\u675f\u624b\u65e0\u7b56\uff0c\u53ea\u80fd\u65c1\u89c2\u3002\u743c\u6069\u770b\u89c1\u5f88\u591a\u4eba\u4ece\u9ad8\u5854\u4e2d\u8dd1\u51fa\u6765\uff0c\u5176\u4e2d\u4e5f\u5305\u62ec\u5377\u518c\u5b88\u62a4\u8005\u4e4c\u5c14\u5170\u7279\u3002\u8001\u5148\u751f\u76b1\u7740\u7709\u5934\uff0c\u4ef0\u9762\u770b\u7740\u7a7a\u4e2d\u4e24\u6761\u5de8\u9f99\u6fc0\u6218\uff0c\u4ed6\u8eab\u65c1\u7ad9\u7740\u4e00\u4e2a\u8eab\u6750\u975e\u5e38\u9ad8\u5927\u7684\u4e2d\u5e74\u4eba\uff0c\u6bd4\u4e4c\u5c14\u5170\u7279\u8981\u6574\u6574\u9ad8\u51fa\u4e00\u4e2a\u5934\u8fd8\u591a\uff0c\u7a7f\u7740\u7070\u8272\u957f\u888d\uff0c\u624b\u91cc\u63e1\u7740\u4e00\u67c4\u9ed1\u8272\u6cd5\u6756\uff0c\u9762\u8272\u82cd\u767d\uff0c\u4e0d\u89c1\u534a\u70b9\u8840\u8272\uff0c\u914d\u4e0a\u9e70\u94a9\u9f3b\u5b50\u548c\u603b\u662f\u62bf\u7d27\u7684\u5634\u5507\uff0c\u770b\u8d77\u6765\u5012\u6709\u70b9\u50cf\u54c8\u5229\u6ce2\u7279\u91cc\u7684\u53f2\u5185\u666e\u3002\u800c\u4e14\u4ed6\u663e\u7136\u4e5f\u662f\u4e2a\u5deb\u5e08\u3002</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u8fd9\u4e2a\u50cf\u53f2\u5185\u666e\u7684\u5deb\u5e08\u6c89\u58f0\u8bf4\uff0c\u4ed6\u7684\u58f0\u97f3\u5e76\u4e0d\u5927\uff0c\u5374\u6e05\u6e05\u695a\u695a\u5730\u4f20\u5230\u70db\u5821\u5185\u6bcf\u4e2a\u4eba\u7684\u8033\u6735\u91cc\uff0c\u4eff\u4f5b\u5c31\u5728\u8eab\u65c1\u8bf4\u8bdd\u4f3c\u7684\u3002\u743c\u6069\u5fae\u5fae\u4e00\u60ca\uff0c\u8fd9\u770b\u8d77\u6765\u53ea\u662f\u4e2a\u5c0f\u4f0e\u4fe9\uff0c\u5b9e\u9645\u4e0a\u5374\u662f\u975e\u5e38\u5f3a\u5927\u7684\u6cd5\u672f\uff0c\u610f\u5473\u7740\u8fd9\u4e2a\u5deb\u5e08\u5728\u9b54\u6cd5\u4e0a\u7684\u9020\u8be3\u975e\u5e38\u9ad8\u6df1\u3002\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u6821\u7684\u90a3\u4e9b\u6559\u6388\uff0c\u5f88\u591a\u4e5f\u90fd\u505a\u4e0d\u5230\u8fd9\u70b9\u7684\u3002</p><p>\u4e00\u540d\u536b\u5175\u8dd1\u8fc7\u6765\uff0c\u5728\u4e4c\u5c14\u5170\u7279\u548c\u9e70\u94a9\u9f3b\u5b50\u5deb\u5e08\u9762\u524d\u505c\u4e0b\uff0c\u8eac\u8eab\uff0c\u4e0d\u77e5\u8bf4\u4e86\u51e0\u53e5\u4ec0\u4e48\u3002\u9e70\u94a9\u9f3b\u5b50\u5deb\u5e08\u542c\u4e86\uff0c\u5fae\u5fae\u70b9\u5934\uff0c\u9ad8\u9ad8\u4e3e\u8d77\u624b\u4e2d\u7684\u6cd5\u6756\u3002</p><p>\u4eff\u4f5b\u5929\u5d29\u5730\u88c2\u4f3c\u7684\u4e00\u58f0\u8f70\u7136\u9707\u54cd\uff0c\u539f\u672c\u6674\u6717\u7684\u591c\u7a7a\u9aa4\u7136\u95f4\u4e4c\u4e91\u56db\u5408\uff0c\u7d2b\u8272\u95ea\u7535\u4eff\u4f5b\u5de8\u86c7\u4e00\u822c\u5728\u4e91\u5c42\u4e2d\u873f\u8712\u7a7f\u68ad\uff0c\u84c4\u52bf\u4e0b\u51fb\u3002\u743c\u6069\u611f\u89c9\u5468\u56f4\u7a7a\u6c14\u7684\u6e29\u5ea6\u6084\u7136\u6025\u5267\u4e0b\u964d\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u81ea\u5df1\u53e3\u4e2d\u547c\u51fa\u7684\u5df2\u7ecf\u662f\u767d\u6c7d\u3002\u6b64\u65f6\u6b63\u503c\u6625\u590f\u4e4b\u4ea4\uff0c\u6c14\u6e29\u6e10\u5347\uff0c\u867d\u7136\u662f\u591c\u665a\uff0c\u4e5f\u4e0d\u8fc7\u662f\u5fae\u6709\u51c9\u610f\uff0c\u88ab\u8fd9\u7a81\u7136\u4e00\u51b7\uff0c\u4e0d\u7531\u5f97\u5fae\u5fae\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u8f6c\u8138\u770b\u6885\u83f2\u65af\uff0c\u5c11\u5973\u5374\u4eff\u4f5b\u4e00\u65e0\u6240\u89c9\uff0c\u53ea\u662f\u5168\u795e\u8d2f\u6ce8\u5730\u770b\u7740\u9ad8\u7a7a\u4e2d\u7684\u4e24\u53ea\u5de8\u9f99\u3002</p><p>\u70db\u5821\u4e2d\u5fc3\u5171\u6709\u4e5d\u5ea7\u9ad8\u5854\uff0c\u4e00\u5ea7\u5c45\u4e2d\uff0c\u516b\u5ea7\u73af\u7ed5\u62f1\u536b\u3002\u4e2d\u592e\u9ad8\u5854\u4e2d\u6240\u85cf\u7684\u90fd\u662f\u673a\u5bc6\u8d44\u6599\uff0c\u82e5\u4e0d\u662f\u80fd\u88ab\u5377\u518c\u5b88\u62a4\u8005\u5bb4\u8bf7\u7684\u5ba2\u4eba\uff0c\u90fd\u6ca1\u8d44\u683c\u8fdb\u5165\uff0c\u53ea\u80fd\u5728\u5468\u56f4\u516b\u5ea7\u9ad8\u5854\u4e2d\u67e5\u9605\u3002\u6b64\u65f6\u8fd9\u516b\u5ea7\u9ad8\u5854\u4e0a\uff0c\u9661\u7136\u90fd\u51b2\u8d77\u767d\u5149\u6765\uff0c\u76f4\u5c04\u591c\u7a79\u3002\u516b\u4e2a\u4eba\u5f71\u81ea\u5854\u9876\u51fa\u73b0\uff0c\u7070\u888d\u957f\u6756\uff0c\u5347\u4e0a\u9ad8\u7a7a\uff0c\u5c06\u6b63\u5728\u4e89\u6597\u7684\u4e24\u6761\u5de8\u9f99\u9065\u9065\u56f4\u4f4f\u3002</p><p>\u94f6\u9f99\u7c73\u8389\u4e9a\u59c6\u6b63\u5728\u548c\u8679\u5f69\u9f99\u7ea0\u7f20\uff0c\u89c1\u8fd9\u60c5\u5f62\uff0c\u4eff\u4f5b\u7a81\u7136\u9192\u609f\u4e86\u4e00\u822c\uff0c\u957f\u541f\u4e00\u58f0\uff0c\u632f\u7ffc\u5c31\u8981\u4e0a\u98de\uff0c\u8131\u79bb\u6218\u56e2\u3002\u5176\u5b9e\u5979\u6b64\u523b\u5df2\u7ecf\u662f\u5e7d\u7075\uff0c\u7eaf\u7136\u865a\u4f53\uff0c\u4e0a\u5929\u5165\u5730\u7a7f\u5899\u8fc7\u5c71\uff0c\u90fd\u4e0d\u8fc7\u662f\u968f\u5fc3\u6240\u6b32\uff0c\u5b8c\u5168\u65e0\u9700\u518d\u501f\u52a9\u53cc\u7ffc\u98de\u884c\uff0c\u53ea\u662f\u751f\u524d\u7684\u4e60\u60ef\u4f7f\u7136\u7f62\u4e86\u3002</p><p>\u5979\u662f\u865a\u4f53\uff0c\u8679\u5f69\u9f99\u4ec5\u51ed\u722a\u7259\u6839\u672c\u7275\u5236\u4e0d\u4f4f\uff0c\u8981\u6765\u4fbf\u6765\u8981\u8d70\u4fbf\u8d70\u3002\u53ea\u662f\u521a\u4e00\u98de\u8d77\uff0c\u5934\u9876\u4e0a\u9661\u7136\u73b0\u51fa\u4e00\u9762\u900f\u660e\u7684\u534a\u7403\u5f62\u5899\u58c1\u6765\uff0c\u4eff\u4f5b\u4e00\u4e2a\u5de8\u5927\u7684\u73bb\u7483\u7f69\uff0c\u5c06\u6574\u5ea7\u70db\u5821\u65b9\u5706\u4e94\u82f1\u91cc\u5185\u5168\u90fd\u7262\u7262\u7b3c\u4f4f\u4e86\u3002</p><p>\u94f6\u9f99\u5e7d\u7075\u649e\u4e0a\u62a4\u7f69\uff0c\u7830\u5730\u88ab\u91cd\u91cd\u53cd\u5f39\u4e86\u56de\u6765\uff0c\u51e0\u4e4e\u5760\u843d\u5230\u5730\u3002\u5979\u5389\u58f0\u6012\u543c\uff0c\u5f85\u8981\u6323\u624e\uff0c\u5c31\u89c1\u9ad8\u7a7a\u4e2d\u516b\u4e2a\u7070\u888d\u4eba\u624b\u6267\u957f\u6756\uff0c\u540c\u58f0\u8bf5\u5492\uff0c\u5343\u767e\u9053\u82e5\u6709\u82e5\u65e0\u7684\u7070\u8272\u5149\u7ebf\u4ece\u7a79\u9876\u76f4\u5c04\u4e0b\u6765\uff0c\u7ea0\u7f20\u7740\uff0c\u6346\u7f1a\u4f4f\u94f6\u9f99\u3002\u867d\u7136\u662f\u5e7d\u7075\u865a\u4f53\uff0c\u5374\u4e5f\u65e0\u6cd5\u6446\u8131\u8fd9\u4e9b\u7070\u8272\u5149\u7ebf\uff0c\u7247\u523b\u95f4\u8d8a\u7f20\u8d8a\u591a\uff0c\u5bc6\u5bc6\u531d\u531d\uff0c\u5df2\u7ecf\u5f62\u6210\u4e00\u4e2a\u534a\u900f\u660e\u7684\u7070\u8272\u5927\u8327\uff0c\u5c06\u94f6\u9f99\u5e7d\u7075\u5c01\u95ed\u5176\u4e2d\u3002</p><p>\u743c\u6069\u4ef0\u9762\u89c2\u770b\uff0c\u89c1\u90a3\u534a\u900f\u660e\u7684\u7070\u8272\u62a4\u7f69\u4e0a\uff0c\u65e0\u6570\u7ec6\u5fae\u800c\u7e41\u590d\u7684\u602a\u5f02\u7b26\u6587\u82e5\u9690\u82e5\u73b0\uff0c\u6d41\u52a8\u4e0d\u4f11\u3002\u8fd9\u7c7b\u4f3c\u7684\u60c5\u5f62\u4ed6\u5e76\u4e0d\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\uff0c\u4ee5\u524d\u5728\u9634\u9b42\u57ce\u65f6\uff0c\u5c31\u66fe\u7ecf\u89c1\u8fc7\u3002\u5f53\u65e5\u662f\u9634\u9b42\u57ce\u8981\u81ea\u5e7d\u5f71\u754c\u8fd4\u56de\u7269\u8d28\u754c\uff0c\u8fd9\u79cd\u4f4d\u9762\u7a7f\u68ad\u7d20\u6765\u51f6\u9669\uff0c\u4f55\u51b5\u662f\u8981\u79fb\u52a8\u6574\u5ea7\u57ce\u5e02\uff0c\u6240\u4ee5\u8ff7\u9501\u5168\u529b\u5f00\u542f\uff0c\u7ffc\u62a4\u5168\u57ce\uff0c\u5c31\u5dee\u4e0d\u591a\u4e5f\u662f\u8fd9\u6837\u4e00\u526f\u60c5\u5f62\u3002</p><p>\u743c\u6069\u66fe\u7ecf\u731c\u6d4b\u70db\u5821\u4e5f\u6709\u201c\u8ff7\u9501\u201d\u6216\u8005\u7c7b\u4f3c\u7684\u5927\u578b\u9b54\u6cd5\u9635\uff0c\u679c\u7136\u6240\u6599\u4e0d\u9519\u3002</p><p>\u8ff7\u9501\u5f00\u542f\uff0c\u5c06\u94f6\u9f99\u5e7d\u7075\u56f0\u4f4f\uff0c\u5929\u7a7a\u4e2d\u4e4c\u4e91\u5792\u79ef\uff0c\u96f7\u9e23\u9635\u9635\uff0c\u5343\u4e07\u9053\u7d2b\u7535\u94f6\u86c7\u76d8\u65cb\u7ed5\u531d\uff0c\u84c4\u52bf\u4e0b\u51fb\uff0c\u53ea\u662f\u542b\u800c\u4e0d\u53d1\u3002\u9ed1\u8272\u7684\u6f29\u6da1\u5728\u5730\u9762\u4e0a\u51fa\u73b0\uff0c\u9010\u6e10\u53d8\u5927\uff0c\u4eff\u4f5b\u5176\u4e2d\u6709\u65e0\u5f62\u7684\u5438\u5f15\u529b\uff0c\u5c06\u88ab\u7070\u8272\u5149\u7ebf\u6346\u7f1a\u4f4f\u7684\u94f6\u9f99\u5e7d\u7075\u541e\u566c\u8fdb\u53bb\uff0c\u968f\u5373\u6f29\u6da1\u6d88\u5931\u3002\u60f3\u5fc5\u662f\u53c8\u628a\u94f6\u9f99\u5c01\u5165\u5730\u4e0b\u4e86\u3002</p><p>\u8ff7\u9501\u5e76\u672a\u6d88\u5931\uff0c\u53cd\u800c\u9690\u9690\u66f4\u52a0\u5f3a\u5316\uff0c\u7070\u8272\u62a4\u7f69\u4e0a\u7684\u7b26\u7eb9\u5492\u8a00\u6d41\u8f6c\u95ea\u70c1\uff0c\u53d1\u51fa\u4e94\u989c\u516d\u8272\u7684\u5fae\u5149\u6765\u3002\u516b\u4f4d\u7070\u888d\u4eba\u4f9d\u65e7\u7ad9\u5728\u9ad8\u7a7a\uff0c\u624b\u6267\u957f\u6756\uff0c\u5c06\u90a3\u53ea\u8679\u5f69\u9f99\u9065\u9065\u5305\u56f4\u4f4f\u3002</p><p>\u6253\u67b6\u7684\u4e00\u65b9\u5df2\u7ecf\u88ab\u5236\u670d\uff0c\u5bf9\u4e8e\u53e6\u5916\u4e00\u65b9\uff0c\u70db\u5821\u663e\u7136\u4e5f\u5e76\u4e0d\u6253\u7b97\u653e\u8fc7\u3002</p><p>\u8679\u5f69\u9f99\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u5728\u7a7a\u4e2d\u76d8\u65cb\u7740\uff0c\u4eff\u4f5b\u5bf9\u73af\u4f3a\u5468\u56f4\u7684\u654c\u4eba\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002\u743c\u6069\u4e0d\u660e\u767d\u4ed6\u7684\u81ea\u4fe1\u4ece\u4f55\u800c\u6765\u3002\u8ff7\u9501\u5a01\u80fd\u83ab\u6d4b\uff0c\u8fd1\u4f3c\u4e8e\u521b\u9020\u51fa\u4e00\u4e2a\u7edd\u5bf9\u9886\u57df\uff0c\u53ea\u8981\u6fc0\u53d1\u8d77\u6765\uff0c\u533a\u57df\u4e4b\u5185\u751a\u81f3\u8fde\u9b54\u6cd5\u89c4\u5219\u90fd\u80fd\u6682\u65f6\u626d\u66f2\u4fee\u6539\uff0c\u5f53\u7136\u4e5f\u9700\u8981\u6709\u9ad8\u660e\u7684\u5927\u5deb\u5e08\u624d\u80fd\u542f\u52a8\u3002\u5347\u4e0a\u9ad8\u7a7a\u7684\u516b\u4e2a\u7070\u888d\u4eba\uff0c\u5927\u7ea6\u5c31\u662f\u5206\u522b\u6267\u638c\u9ad8\u5854\u7684\u516b\u4f4d\u201c\u9605\u8bfb\u8005\u201d\u4e86\uff0c\u5728\u70db\u5821\u4e2d\u4f4d\u9636\u4ec5\u6b21\u4e8e\u5377\u518c\u5b88\u62a4\u8005\u548c\u7b2c\u4e00\u9605\u8bfb\u8005\uff0c\u7b97\u662f\u9886\u8896\u4eba\u7269\u4e86\u3002</p><p>\u88ab\u91cd\u91cd\u56f4\u56f0\uff0c\u8ff7\u9501\u53c8\u5df2\u7ecf\u5f00\u542f\uff0c\u5c31\u7b97\u662f\u8679\u5f69\u9f99\uff0c\u770b\u4ed6\u521a\u624d\u548c\u94f6\u9f99\u5e7d\u7075\u4e89\u6597\u4e2d\u7684\u8868\u73b0\uff0c\u60f3\u8131\u8eab\u4f3c\u4e4e\u4e5f\u4e0d\u5bb9\u6613\u3002</p><p>\u516b\u4f4d\u9605\u8bfb\u8005\u540c\u58f0\u8bf5\u5492\uff0c\u957f\u6756\u9ad8\u4e3e\uff0c\u534a\u7403\u5f62\u7684\u900f\u660e\u62a4\u7f69\u5185\u58c1\u4e0a\uff0c\u8f70\u7136\u6d8c\u73b0\u65e0\u7a77\u65e0\u5c3d\u7684\u6740\u622e\u9b54\u6cd5\u6765\u3002\u94f6\u86c7\u95ea\u7535\u4ea4\u76f8\u8f70\u5c04\uff0c\u718a\u718a\u70c8\u7130\u4fb5\u88ad\u626b\u8361\uff0c\u5bd2\u51b0\u5f7b\u9aa8\u7684\u51b0\u5899\u5316\u6210\u4e86\u5343\u4e07\u9897\u51b0\u9525\u98de\u821e\uff0c\u65e0\u6570\u72f0\u72de\u6050\u6016\u7684\u602a\u7269\u6084\u65e0\u58f0\u606f\u5730\u4ece\u900f\u660e\u62a4\u7f69\u4e0a\u5177\u73b0\u51fa\u6765\uff0c\u5148\u662f\u900f\u660e\u865a\u5e7b\u7684\u8eab\u8eaf\uff0c\u7d27\u63a5\u7740\u5316\u4e3a\u5b9e\u4f53\uff0c\u5636\u543c\u7740\u6251\u5411\u8679\u5f69\u9f99\u3002</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u70db\u5821\u4e2d\u592e\u9ad8\u5854\u4e2d\uff0c\u9661\u5730\u5c04\u51fa\u4e00\u9053\u8679\u5149\u6765\uff0c\u77ac\u95f4\u649e\u4e0a\u534a\u7a7a\uff0c\u7efd\u653e\u5f00\u6765\uff0c\u5316\u4f5c\u4e00\u4e2a\u4eba\u5f62\uff0c\u6b63\u662f\u745e\u6069\u65af\u5766\u3002\u91d1\u53d1\u9752\u5e74\u4f9d\u65e7\u4e00\u8eab\u767d\u888d\uff0c\u5468\u8eab\u5374\u662f\u8679\u5149\u6d41\u6ea2\uff0c\u95ea\u70c1\u751f\u8f89\uff0c\u5de6\u624b\u4e2d\u62b1\u7740\u4e00\u5377\u7f8a\u76ae\u7eb8\u3002</p><p>\u201c\u591a\u6709\u6253\u6270\u4e86\uff0c\u5377\u518c\u5b88\u62a4\u8005\uff0c\u201d\u745e\u6069\u65af\u5766\u6717\u58f0\u5927\u7b11\uff0c\u770b\u7740\u5730\u9762\u4e0a\u7684\u4eba\u7fa4\uff0c\u201c\u5c31\u6b64\u522b\u8fc7\uff0c\u6211\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d</p><p>\u4ed6\u62ac\u8d77\u53f3\u624b\uff0c\u865a\u865a\u4e00\u6293\uff0c\u5c06\u90a3\u539f\u672c\u7262\u7262\u7b3c\u7f69\u7684\u7070\u8272\u62a4\u7f69\u786c\u751f\u751f\u6495\u88c2\u4e00\u9053\u7f1d\u9699\u3002\u745e\u6069\u65af\u5766\u5f53\u5148\u94bb\u4e86\u51fa\u53bb\uff0c\u201c\u8ff7\u5e7b\uff01\u201d\u4ed6\u53eb\u7740\u5c0f\u80d6\u5b50\u7684\u540d\u5b57\uff0c\u201c\u8be5\u8d70\u4e86\u3002\u201d</p><p>\u5c0f\u80d6\u5b50\u6b63\u9677\u5165\u8ff7\u9501\u8fde\u7ef5\u4e0d\u5c3d\u7684\u9b54\u6cd5\u653b\u51fb\u4e2d\u4e0d\u5f97\u8131\u8eab\uff0c\u542c\u745e\u6069\u65af\u5766\u62db\u547c\uff0c\u957f\u541f\u4e00\u58f0\uff0c\u53e3\u4e2d\u55b7\u51fa\u4e00\u9053\u9525\u5f62\u7480\u74a8\u8679\u5149\u6765\uff0c\u6240\u6709\u88ab\u8679\u5149\u89e6\u53ca\u4e4b\u7269\uff0c\u65e0\u8bba\u662f\u95ea\u7535\uff0c\u662f\u706b\u7130\uff0c\u662f\u51b0\u9525\u8fd8\u662f\u53ec\u5524\u602a\u7269\uff0c\u9f50\u9f50\u90fd\u5316\u4f5c\u7c89\u672b\uff0c\u6563\u5f52\u865a\u7a7a\u3002\u8679\u5f69\u9f99\u817e\u8eab\u800c\u8d77\uff0c\u4e5f\u4ece\u7f1d\u9699\u4e2d\u94bb\u4e86\u51fa\u53bb\uff0c\u77ac\u95f4\u98de\u5f97\u8fdc\u4e86\uff0c\u9690\u6ca1\u591c\u7a7a\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u4e00\u573a\u6fc0\u6218\u83ab\u540d\u5176\u5999\uff0c\u4e0d\u77e5\u6765\u7531\uff0c\u4f46\u743c\u6069\u5012\u4e5f\u9690\u9690\u7ea6\u7ea6\u731c\u5230\u4e86\u51e0\u5206\u3002\u5341\u6709\u516b\u4e5d\uff0c\u662f\u745e\u6069\u65af\u5766\u60f3\u8981\u90a3\u4efd\u6258\u5c14\u65af\u7684\u624b\u7a3f\uff0c\u5374\u88ab\u4e4c\u5c14\u5170\u7279\u62d2\u7edd\uff0c\u7d22\u6027\u52a8\u4e86\u6012\u6c14\uff0c\u6d3e\u5c0f\u80d6\u5b50\u8ddf\u73ed\u6363\u4e71\uff0c\u5438\u5f15\u4f17\u4eba\u6ce8\u610f\u529b\uff0c\u81ea\u5df1\u4e58\u673a\u53bb\u5077\u4e86\u8d44\u6599\u6765\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u5fae\u5fae\u51b7\u7b11\u3002\u4ed6\u5927\u7ea6\u4e5f\u80fd\u731c\u60f3\u5f97\u5230\uff0c\u745e\u6069\u65af\u5766\u65e2\u7136\u8eab\u4e3a\u8679\u5f69\u9f99\uff0c\u5929\u751f\u5c31\u662f\u8fd9\u4e16\u4e0a\u6700\u5f3a\u5927\u7684\u751f\u7269\uff0c\u4f3c\u4e4e\u8fd8\u548c\u795e\u7947\u6709\u4e9b\u5173\u7cfb\uff0c\u60f3\u5fc5\u4e5f\u662f\u81ea\u89c6\u751a\u9ad8\uff0c\u4ece\u6765\u90fd\u662f\u987a\u98ce\u987a\u6c34\u60ef\u4e86\uff0c\u54ea\u91cc\u628a\u8fd9\u4e9b\u7269\u8d28\u754c\u7684\u51e1\u4eba\u653e\u5728\u773c\u91cc\u3002\u4ed6\u5ba2\u6c14\u76f8\u6c42\uff0c\u82e5\u662f\u4e4c\u5c14\u5170\u7279\u8bc6\u76f8\uff0c\u65e9\u65e9\u5949\u4e0a\u8d44\u6599\uff0c\u81ea\u7136\u7686\u5927\u6b22\u559c\u3002\u5982\u4eca\u4e4c\u5c14\u5170\u7279\u5374\u65ad\u7136\u62d2\u7edd\uff0c\u8fd9\u65e0\u7591\u662f\u9006\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u5927\u5927\u62c2\u4e86\u4ed6\u7684\u9762\u5b50\uff0c\u8679\u5f69\u9f99\u5c82\u80fd\u5bb9\u5fcd\u3002</p><p>\u6211\u53ef\u4ee5\u5ba2\u6c14\uff0c\u4f46\u4e0d\u662f\u5c0a\u91cd\u3002\u6240\u8c13\u5ba2\u6c14\uff0c\u8fd9\u662f\u4fee\u517b\uff0c\u6211\u4e0d\u60f3\u663e\u5f97\u50cf\u4e2a\u5f3a\u76d7\u6216\u8005\u6076\u68cd\uff0c\u4f46\u4e0d\u7b49\u4e8e\u6211\u4f1a\u771f\u7684\u5c0a\u91cd\u4f60\u3002\u5982\u679c\u4f60\u987a\u6211\u5fc3\u610f\uff0c\u4e00\u5207\u597d\u8bf4\uff0c\u6ee1\u5802\u548c\u6c14\uff1b\u4f46\u5982\u679c\u4f60\u626b\u6211\u9762\u5b50\uff0c\u90a3\u6211\u5c31\u8981\u53d1\u98d9\u4e86\u3002</p><p>\u8fd9\u79cd\u8d35\u65cf\u5b50\u5f1f\u7684\u5fc3\u6001\uff0c\u5927\u7ea6\u5982\u6b64\u7f62\u3002\u743c\u6069\u51b7\u7b11\u7740\uff0c\u5374\u4e5f\u61d2\u5f97\u591a\u7ba1\uff0c\u53cd\u6b63\u662f\u70db\u5821\u7684\u4e8b\u60c5\uff0c\u4e0e\u81ea\u5df1\u65e0\u5e72\u3002\u800c\u4e14\u771f\u8981\u8bf4\u7684\u8bdd\uff0c\u90a3\u4e24\u53ea\u8679\u5f69\u9f99\u81f3\u5c11\u5bf9\u81ea\u5df1\u8fd8\u662f\u4e0d\u9519\u7684\uff0c\u745e\u6069\u65af\u5766\u7834\u6389\u4e86\u9f99\u9cde\u76fe\u4e0a\u7684\u76d1\u89c6\u9b54\u6cd5\uff0c\u53c8\u5f15\u8350\u743c\u6069\u7ed9\u4e4c\u5c14\u5170\u7279\uff0c\u95f4\u63a5\u8ba9\u4ed6\u5f97\u5230\u4e86\u62c9\u6c83\u514b\u7684\u8d44\u6599\uff1b\u90a3\u4e2a\u53eb\u8ff7\u5e7b\u7684\u5c0f\u80d6\u5b50\uff0c\u8fd8\u4e0d\u77e5\u4f55\u6545\u9001\u4e86\u4e00\u888b\u73cd\u73e0\u94bb\u77f3\u3002</p><p>\u8bf4\u8d77\u6765\uff0c\u81ea\u5df1\u8fd8\u6ca1\u7ed9\u5b83\u4eec\u4f30\u4ef7\u5462\u3002\u4e0d\u8fc7\u743c\u6069\u4e5f\u4e0d\u6e05\u695a\u7269\u8d28\u754c\u7684\u4ea4\u6613\u884c\u60c5\uff0c\u53ea\u77e5\u9053\u5f88\u503c\u94b1\uff0c\u4f46\u5230\u5e95\u503c\u591a\u5c11\u94b1\uff0c\u5374\u4e5f\u662f\u4e0d\u77e5\u9053\u7684\uff0c\u5927\u6982\u5f97\u53bb\u8bf7\u6559\u6885\u83f2\u65af\u3002</p><p>\u94f6\u9f99\u5e7d\u7075\u5df2\u7ecf\u88ab\u91cd\u65b0\u5c01\u4f4f\uff0c\u745e\u6069\u65af\u5766\u548c\u5c0f\u80d6\u5b50\u53c8\u5df2\u7ecf\u65e9\u65e9\u9041\u8d70\uff0c\u70db\u5821\u7684\u8ff7\u9501\u81ea\u7136\u4e5f\u65e0\u7ef4\u6301\u7684\u5fc5\u8981\u3002\u516b\u4f4d\u9605\u8bfb\u8005\u5404\u81ea\u56de\u5f52\u9ad8\u5854\uff0c\u518d\u4e0d\u9732\u9762\uff0c\u743c\u6069\u770b\u8349\u5730\u4e0a\u4e4c\u5c14\u5170\u7279\u548c\u4ed6\u8eab\u65c1\u90a3\u4f4d\u9e70\u94a9\u9f3b\u5b50\u5deb\u5e08\uff08\u5e94\u8be5\u5c31\u662f\u4f20\u8bf4\u4e2d\u7684\u201c\u7b2c\u4e00\u9605\u8bfb\u8005\u201d\uff0c\u70db\u5821\u7684\u4e8c\u628a\u624b\u4e86\uff09\uff0c\u53d1\u73b0\u4ed6\u4eec\u5012\u4e5f\u6ca1\u6709\u591a\u4e48\u607c\u6012\u7684\u795e\u8272\uff0c\u4eff\u4f5b\u88ab\u8fd9\u4e48\u5927\u95f9\u4e00\u573a\uff0c\u53c8\u88ab\u5077\u8d70\u8d44\u6599\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\u4f3c\u7684\uff0c\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\u3002</p><p>\u6743\u5f53\u662f\u770b\u4e86\u573a\u70ed\u95f9\u3002</p></article>"}}}]);
