(window.webpackJsonp=window.webpackJsonp||[]).push([[1161],{1184:function(p){p.exports={chapterTitle:null,title:"\u7b2c1158\u7ae0 \u4e01\u6587\u6021\u7684\u81ea\u767d\u72b6\u4e0e\u5f20\u606a\u843d\u6c34\u4e8b\u4ef6",content:"<article><h2>\u7b2c1158\u7ae0 \u4e01\u6587\u6021\u7684\u81ea\u767d\u72b6\u4e0e\u5f20\u606a\u843d\u6c34\u4e8b\u4ef6</h2><p>\u3000\u3000\u8bb8\u591a\u5b66\u751f\u6216\u8005\u56e0\u4e3a\u6253\u5de5\u6216\u8005\u56e0\u4e3a\u8003\u7814\u6216\u8005\u56e0\u4e3a\u5176\u4ed6\u4ec0\u4e48\u539f\u56e0\uff0c\u9009\u62e9\u7559\u5728\u5b66\u6821\uff0c\u7559\u5728\u8fd9\u5ea7\u708e\u70ed\u800c\u55a7\u56a3\u7684\u57ce\u5e02\u91cc\uff0c\u4e03\u6708\u6821\u56ed\u91cc\u4eba\u6765\u4eba\u5f80\uff0c\u5e76\u4e0d\u9759\u5bc2\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u77a7\u4f60\u4eec\u4e00\u4e2a\u4e2a\u7684\u6837\u5b50\uff0c\u597d\u50cf\u6211\u53d7\u4e86\u591a\u5927\u7684\u59d4\u5c48\u4f3c\u7684\uff0c\u201d\u4e01\u6587\u6021\u559d\u4e86\u4e9b\u9152\uff0c\u5f20\u606a\u81ea\u544a\u594b\u52c7\u7684\u8fc7\u6765\u66ff\u53f6\u5efa\u658c\u5f53\u201c\u8bf4\u5ba2\u201d\uff0c\u5979\u4fbf\u51fa\u6765\u5230\u4e1c\u5927\u6821\u56ed\u91cc\u8d70\u4e00\u8d70\uff0c\u900f\u53e3\u6c14\uff0c\u201c\u6211\u7238\u5988\u5e94\u8be5\u662f\u90a3\u79cd\u4e2d\u56fd\u5178\u578b\u7684\u5931\u8d25\u5a5a\u59fb\u2014\u2014\u6211\u7238\u5bf9\u6211\u5988\u4e0d\u6ee1\u610f\uff0c\u6211\u5988\u5bf9\u6211\u7238\u4e5f\u4e0d\u6ee1\u610f\uff0c\u4f46\u662f\u7531\u4e8e\u5404\u79cd\u9700\u8981\uff0c\u4ed6\u4eec\u7684\u5a5a\u59fb\u4e00\u76f4\u90fd\u5f88\u7a33\u5b9a\u3002\u7531\u4e8e\u53cc\u65b9\u90fd\u662f\u76f8\u5f53\u514b\u5236\u7684\u4eba\uff0c\u5728\u5916\u4eba\u7684\u773c\u91cc\uff0c\u6211\u7238\u5988\u7684\u5a5a\u59fb\u751f\u6d3b\u662f\u5e78\u798f\u7f8e\u6ee1\u7684\uff0c\u552f\u6709\u6211\u8ddf\u6211\u5f1f\u5f1f\u4e24\u4e2a\u4eba\u77e5\u9053\uff0c\u6211\u7238\u5988\u7684\u5a5a\u59fb\u53ea\u662f\u4e00\u5f20\u58f3\u800c\u5df2\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8ddf\u53f6\u54e5\u53ef\u662f\u5bf9\u5f7c\u6b64\u90fd\u6709\u611f\u60c5\u7684\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6709\u611f\u60c5\u5728\u4e00\u8d77\u800c\u4e0d\u7ed3\u5a5a\u7684\u4eba\u4e5f\u591a\u4e86\uff0c\u201d\u4e01\u6587\u6021\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u5a5a\u59fb\u53ea\u662f\u793e\u4f1a\u9700\u8981\u7684\u4e00\u5f20\u58f3\u7f62\u4e86\uff1b\u6709\u611f\u60c5\u4e5f\u7f62\uff0c\u65e0\u611f\u60c5\u4e5f\u7f62\uff0c\u5a5a\u59fb\u53ea\u662f\u4e00\u5f20\u58f3\u3002\u53ea\u662f\u6ca1\u6709\u611f\u60c5\u7684\u5a5a\u59fb\uff0c\u8fd9\u5f20\u58f3\u66f4\u6ca1\u6709\u610f\u4e49\u7f62\u4e86\u2026\u2026\u4e8b\u5b9e\u4e0a\uff0c\u771f\u6b63\u611a\u8822\u7684\u5973\u4eba\u603b\u662f\u5c11\u6570\uff0c\u66f4\u591a\u7684\u4eba\u53ea\u662f\u6709\u4e9b\u624b\u8db3\u65e0\u63aa\u7f62\u4e86\u3002\u4e0d\u8981\u770b\u4f60\u53f6\u54e5\u5e73\u65f6\u90fd\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u906e\u906e\u63a9\u63a9\uff0c\u6211\u65e9\u77e5\u9053\u4ed6\u8ddf\u4f60\u5b59\u59d0\u7684\u4e8b\u60c5\u3002\u5f53\u65f6\u5fcd\u7740\u6ca1\u6709\u5927\u5435\u5927\u95f9\uff0c\u5e76\u4e0d\u662f\u8bf4\u5bf9\u4f60\u53f6\u54e5\u6709\u5f88\u6df1\u539a\u7684\u611f\u60c5\u3001\u6709\u591a\u5f3a\u70c8\u7684\u4fe1\u4efb\uff0c\u53ea\u662f\u60f3\u5230\u8981\u662f\u95f9\u5f00\u6765\uff0c\u4e0d\u662f\u4fbf\u5b9c\u90a3\u72d0\u72f8\u7cbe\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u73b0\u5728\u53ef\u4e0d\u5c31\u4fbf\u5b9c\u4ed6\u4eec\u8fd9\u5bf9\u72d7\u7537\u5973\u4e86\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u54ea\u6709\u8d44\u683c\u8bf4\u522b\u4eba\uff01\u201d\u4e01\u6587\u6021\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u8ddf\u521a\u5f00\u59cb\u4e0d\u540c\uff0c\u521a\u5f00\u59cb\u6ca1\u6709\u592a\u6df1\u7684\u611f\u60c5\uff0c\u6ca1\u6709\u592a\u5f3a\u70c8\u7684\u4fe1\u4efb\uff0c\u5a5a\u59fb\u4f5c\u4e3a\u4e00\u5f20\u58f3\uff0c\u80fd\u7ed9\u6211\u5f88\u591a\u7684\u5b89\u5168\u611f\u3002\u6211\u9700\u8981\u90a3\u5f20\u58f3\uff0c\u6d51\u8eab\u795e\u7ecf\u7ef7\u7d27\u4e86\uff0c\u5c31\u6015\u4f60\u5b59\u59d0\u8ddf\u6211\u62a2\u8fd9\u5f20\u58f3\u3002\u73b0\u5728\uff0c\u6211\u8ddf\u4f60\u53f6\u54e5\u4e00\u8d77\u751f\u6d3b\u6709\u516b\u5e74\u4e86\uff0c\u5373\u4f7f\u77e5\u9053\u4ed6\u5fc3\u91cc\u6709\u4f60\u5b59\u59d0\uff0c\u4e5f\u77e5\u9053\u81ea\u5df1\u5728\u4ed6\u5fc3\u91cc\u6709\u4e9b\u7a7a\u95f4\uff0c\u6240\u4ee5\u6709\u4e9b\u4e8b\u60c5\u80fd\u770b\u6de1\u4e9b\uff0c\u751a\u81f3\u4f1a\u89c9\u5f97\u4f60\u5b59\u59d0\u4e5f\u4e0d\u5bb9\u6613\u2014\u2014\u8981\u662f\u5a5a\u59fb\u5bf9\u611f\u60c5\u6709\u6240\u610f\u4e49\uff0c\u5c82\u4e0d\u662f\u5bf9\u5979\u5f88\u4e0d\u516c\u5e73\u3002\u6709\u65f6\u4f1a\u7422\u78e8\u5979\u662f\u600e\u6837\u7684\u4e00\u4e2a\u4eba\uff0c\u4f1a\u7422\u78e8\u5979\u4e0e\u4f60\u53f6\u54e5\u4e4b\u95f4\u7684\u611f\u60c5\u2026\u2026\u6211\u7684\u5c11\u5973\u65f6\u671f\u8fc7\u5f97\u5f88\u4e4f\u5473\uff0c\u6211\u5bf9\u81ea\u5df1\u7684\u9752\u6625\u4e00\u70b9\u90fd\u4e0d\u6ee1\u610f\uff0c\u751a\u81f3\u6709\u4e9b\u618e\u6068\u3002\u8ddf\u4f60\u53f6\u54e5\u89c1\u9762\u65f6\uff0c\u5927\u5bb6\u90fd\u5df2\u7ecf\u6210\u5e74\uff0c\u603b\u4e4b\u65e0\u6cd5\u60f3\u8c61\u4f60\u53f6\u54e5\u5728\u8ba4\u8bc6\u6211\u4e4b\u524d\u7684\u4eba\u751f\u662f\u4e4f\u5473\u3001\u7a7a\u6d1e\u3001\u6ca1\u6709\u751f\u6c14\u7684\uff0c\u8981\u662f\u4f60\u53f6\u54e5\u7684\u9752\u5c11\u5e74\u65f6\u671f\u8ddf\u6211\u4e00\u4e2a\u6837\uff0c\u6211\u4e5f\u8bb8\u4e0d\u4f1a\u5bf9\u4ed6\u4ea7\u751f\u591a\u5c11\u611f\u60c5\u2014\u2014\u5e78\u597d\u4f60\u53f6\u54e5\u4e0d\u662f\uff0c\u4f60\u53f6\u54e5\u6df1\u6c89\u3001\u5145\u5b9e\u800c\u5145\u6ee1\u751f\u673a\uff0c\u8fd9\u6837\u7684\u4ed6\u624d\u6df1\u6df1\u7684\u5438\u5f15\u4e86\u6211\u2014\u2014\u662f\u4f60\u5b59\u59d0\u8ba9\u4f60\u53f6\u54e5\u53d8\u5f97\u5982\u6b64\uff0c\u6709\u65f6\u5019\u6211\u5c31\u5728\u60f3\uff0c\u6211\u662f\u4e0d\u662f\u7a83\u53d6\u4e86\u5979\u7684\u679c\u5b9e\uff1f\u5728\u8fd9\u79cd\u7a0b\u5ea6\u4e0a\uff0c\u6211\u662f\u611f\u8c22\u5979\u7684\uff0c\u751a\u81f3\u8fd8\u6709\u4e9b\u4e8f\u759a\u3002\u8981\u4e0d\u662f\u6211\uff0c\u4ed6\u4eec\u5c31\u4f1a\u6709\u5728\u4e00\u8d77\u7684\u673a\u4f1a\uff0c\u4e0d\u7528\u5077\u5077\u6478\u6478\u7684\u7ed9\u53cc\u65b9\u5957\u4e0a\u67b7\u9501\u4f3c\u7684\u4ea4\u5f80\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6320\u4e86\u6320\u8111\u95e8\uff0c\u4ed6\u4e0e\u4e01\u6587\u6021\u8d70\u5230\u7559\u5b66\u751f\u516c\u5bd3\u524d\u7684\u7d2b\u971e\u6e56\u7554\uff0c\u4ed6\u8e72\u5728\u6e56\u5cb8\u7684\u9a73\u77f3\u4e0a\uff0c\u62ff\u51fa\u70df\u6765\u62bd\uff0c\u5076\u5c14\u63d2\u4e00\u4e24\u53e5\u73a9\u7b11\u8bdd\uff1a\u201c\u6211\u53ef\u4e0d\u4e50\u610f\u542c\u4f60\u5728\u8fd9\u91cc\u4f7f\u52b2\u7684\u5938\u53f6\u54e5\uff0c\u5b8c\u5168\u53d1\u6325\u4e0d\u4e86\u6211\u5f53\u77e5\u5fc3\u5f1f\u5f1f\u7684\u4f5c\u7528\u554a\uff0c\u8981\u4e0d\u6211\u4eec\u73b0\u5728\u5c3d\u6311\u4e9b\u4ed6\u7684\u6bdb\u75c5\u72e0\u72e0\u7684\u6279\u5224\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c11\u5728\u8fd9\u91cc\u8d2b\u5634\uff0c\u4f60\u5bb6\u91cc\u90a3\u51e0\u4e2a\uff0c\u8fd8\u4e0d\u591f\u4f60\u8d2b\u7684\uff1f\u201d\u4e01\u6587\u6021\u7b11\u7740\u9a82\u4ed6\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u6709\u65f6\u5019\u751a\u81f3\u4f1a\u60f3\uff0c\u771f\u8be5\u8ba9\u4f60\u5b59\u59d0\u5149\u660e\u6b63\u5927\u7684\u8ddf\u4f60\u53f6\u54e5\u8fc7\u4e0a\u51e0\u5e74\u3002\u60f3\u6cd5\u6709\u4e9b\u8352\u5510\uff0c\u4e24\u4e2a\u4eba\u6bcf\u4eba\u8f6e\u4e0a\u5341\u5e74\u516b\u5e74\uff0c\u4e00\u76f4\u5230\u4eba\u751f\u7ec8\u8001\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u53ef\u4ee5\u7684\u2014\u2014\u6bd5\u7adf\u5f53\u53f6\u5bb6\u5927\u5c11\u5976\u5976\u771f\u7684\u662f\u4ef6\u82e6\u5dee\u4e8b\uff0c\u65b9\u65b9\u9762\u9762\u8981\u7167\u987e\u8bb8\u591a\uff0c\u8fd9\u82e6\u4e5f\u4e0d\u80fd\u6211\u4e00\u4eba\u6328\u4e86\u3002\u5a5a\u59fb\u771f\u7684\u53ea\u662f\u7ad9\u5728\u793e\u4f1a\u9762\u524d\u624d\u9700\u8981\u7684\u4e00\u5f20\u58f3\uff0c\u6211\u65e9\u5c31\u60f3\u660e\u767d\u4e86\uff0c\u4f60\u5b59\u59d0\u6000\u5b55\u53ea\u80fd\u7b97\u662f\u4e2a\u5951\u673a\u2014\u2014\u4f60\u53f6\u54e5\u8fd9\u6b21\u9886\u7740\u4f60\u5b59\u59d0\u56de\u6765\uff0c\u6211\u5c31\u529d\u4ed6\u8ddf\u6211\u79bb\u5a5a\uff0c\u82e5\u8bf4\u5a5a\u59fb\u6709\u66f4\u5927\u7684\u610f\u4e49\uff0c\u5bf9\u5c0f\u5b69\u7684\u6210\u957f\u66f4\u52a0\u4f5c\u7528\u2014\u2014\u6240\u4ee5\u4f60\u4eec\u4e5f\u4e0d\u8981\u89c9\u5f97\u6211\u53d7\u4e86\u591a\u5927\u4f24\u5bb3\uff0c\u5fcd\u53d7\u4e86\u591a\u5927\u7684\u59d4\u5c48\uff1b\u8981\u771f\u53d7\u4e86\u59d4\u5c48\uff0c\u53d7\u5230\u4f24\u5bb3\uff0c\u6211\u624d\u6ca1\u6709\u90a3\u4e48\u597d\u8bf4\u8bdd\u5462\u3002\u6709\u4ef6\u4e8b\uff0c\u6211\u73b0\u5728\u8fd8\u6ca1\u597d\u610f\u601d\u8ddf\u4f60\u53f6\u54e5\u8fd8\u6709\u4f60\u5b59\u59d0\u8bf4\uff0c\u4ed6\u4eec\u751f\u4e86\u5b69\u5b50\uff0c\u600e\u4e48\u7740\u4e5f\u8981\u7b97\u6211\u4e00\u5206\u5b50\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u8be5\u62c9\u5510\u5a67\u8fc7\u6765\u8ddf\u4f60\u4ea4\u6d41\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u8bf4\u8fd9\u8bdd\u5c31\u662f\u6b20\u6253\uff0c\u4f60\u5bb6\u5c0f\u5a67\u8fd8\u4e0d\u591f\u597d\uff1f\u201d\u4e01\u6587\u6021\u4f6f\u6012\u7684\u77aa\u7740\u5f20\u606a\uff0c\u53c8\u5ae3\u7136\u7b11\u9053\uff0c\u201c\u6211\u4eec\u8fd9\u6837\u7684\u4eba\u554a\uff0c\u5a5a\u59fb\u6839\u672c\u5c31\u4e0d\u662f\u8861\u91cf\u611f\u60c5\u7684\u6807\u51c6\u3002\u8981\u662f\u5a5a\u59fb\u662f\u8861\u91cf\u611f\u60c5\u7684\u6807\u51c6\uff0c\u4f60\u8fd9\u4e48\u82b1\u5fc3\uff0c\u53c8\u53ea\u80fd\u8ddf\u5176\u4e00\u4e2a\u7ed3\u5a5a\uff0c\u53e6\u5916\u51e0\u4e2a\u5c82\u4e0d\u662f\u5f88\u53d7\u4f24\u5bb3\uff1f\u8981\u4e0d\u6211\u6559\u5979\u4eec\u4e00\u62db\uff0c\u5c06\u4f60\u7684\u4eba\u751f\u5207\u6210\u4e00\u7247\u4e00\u7247\uff0c\u5206\u7ed9\u5979\u4eec\u51e0\u4e2a\uff1f\u6bcf\u5e74\u8ddf\u4f60\u7ed3\u5a5a\u8fc7\u4e0a\u5341\u5e74\uff0c\u4f60\u770b\u8fd9\u4e3b\u610f\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u5343\u4e07\u4e0d\u8981\u4f7f\u574f\u5fc3\u773c\uff0c\u201d\u5f20\u606a\u6c42\u6320\u9053\uff0c\u201c\u6211\u54ea\u6709\u5f88\u82b1\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u6850\u5728\u533b\u9662\u751f\u5c0f\u5b69\u5b50\u90a3\u6b21\uff0c\u90fd\u8bf4\u665a\u6674\u4eba\u5728\u5efa\u90ba\uff0c\u5979\u600e\u4e48\u4f1a\u4ece\u5317\u4eac\u5750\u98de\u673a\u56de\u6765\uff1f\u90a3\u56de\u4f60\u4e5f\u5728\u5317\u4eac\u5427\uff1f\u201d\u4e01\u6587\u6021\u7a81\u5140\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u5f20\u606a\u8111\u5b50\u91cc\u6253\u4e86\u4e2a\u7ed3\uff0c\u4e00\u65f6\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u5e94\u4e01\u6587\u6021\uff0c\u4e0d\u6653\u5f97\u600e\u4e48\u5c31\u7ed9\u4e01\u6587\u6021\u8fd9\u4e2a\u6700\u4e0d\u76f8\u5173\u7684\u4eba\u770b\u51fa\u7834\u7efd\uff0c\u4ed6\u5fc3\u91cc\u60f3\u7740\u7f16\u4e2a\u4ec0\u4e48\u501f\u53e3\u5c06\u8fd9\u4e8b\u7ed9\u906e\u63a9\u8fc7\u53bb\uff0c\u5374\u4e0d\u6599\u7a81\u7136\u6709\u9053\u7ea4\u5de7\u7684\u8eab\u5f71\u95ef\u5165\u773c\u5e18\uff0c\u8106\u751f\u751f\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u55e8\uff0c\u4f60\u597d\uff0c\u4f60\u6691\u5047\u4e5f\u6ca1\u6709\u56de\u65b0\u829c\u554a\uff0c\u4f60\u8fd8\u8ba4\u8bc6\u6211\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u53d1\u6123\u7684\u770b\u7740\u9648\u5b81\u8d70\u8fc7\u6765\u8ddf\u4ed6\u6253\u62db\u547c\uff0c\u5c31\u5728\u4ed6\u6700\u731d\u4e0d\u53ca\u9632\u7684\u65f6\u523b\uff0c\u50cf\u4e00\u9897\u6d41\u661f\u4f3c\u7684\u731b\u7136\u95ef\u8fdb\u4ed6\u7684\u5fc3\u95f4\uff0c\u4ed6\u614c\u7136\u8981\u7ad9\u8d77\u6765\uff0c\u5374\u4e0d\u6599\u8e72\u5728\u6e56\u8fb9\u597d\u4e00\u4f1a\u513f\u817f\u811a\u53d1\u9ebb\uff0c\u534a\u8e72\u8d77\u6765\u65f6\u811a\u6709\u4e9b\u53d1\u8f6f\u4f7f\u4e0d\u4e0a\u529b\uff0c\u8eab\u5b50\u6ca1\u6709\u7ad9\u4f4f\uff0c\u76f4\u5f80\u6e56\u91cc\u659c\u7740\u5012\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u4e01\u6587\u6021\u3001\u9648\u5b81\u90fd\u5413\u5f97\u4e00\u8df3\uff0c\u5c16\u53eb\u7740\u8981\u53bb\u62c9\u4ed6\u7684\u624b\uff0c\u6ca1\u6709\u62c9\u5230\uff0c\u5f20\u606a\u6574\u4e2a\u4eba\u6eda\u5230\u7d2b\u971e\u6e56\u91cc\u53bb\u4e86\u2026\u2026\u5979\u4eec\u4fbf\u5927\u58f0\u547c\u6551\uff1a\u201c\u6709\u4eba\u6389\u6c34\u4e86\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u6c89\u5165\u6e56\u91cc\u541e\u4e86\u4e24\u5927\u53e3\u6c34\uff0c\u5c31\u9547\u9759\u4e0b\u6765\uff0c\u6e56\u7554\u7684\u5bc6\u96c6\u6c34\u8349\u867d\u7136\u7f20\u4eba\uff0c\u4ed6\u8fd8\u987a\u7740\u6e56\u91cc\u7684\u8f6f\u6ce5\u722c\u4e0a\u5cb8\u6765\uff0c\u53ea\u662f\u5f62\u8c61\u6709\u4e9b\u51c4\u51c9\uff1a\u4e00\u53ea\u76ae\u978b\u9677\u5728\u6e56\u91cc\u8f6f\u6ce5\u91cc\u6ca1\u6709\u62d4\u4e0a\u6765\uff0c\u4ed6\u5149\u7740\u4e00\u53ea\u811a\uff0c\u8fd8\u6ee1\u662f\u8165\u81ed\u4e4c\u9ed1\u7684\u6e56\u6ce5\uff0c\u8eab\u4e0a\u8fd8\u7f20\u7740\u4e9b\u7eff\u8272\u7684\u6c34\u8349\uff0c\u5934\u53d1\u4e0a\u9876\u7740\u4e9b\u9752\u8272\u7684\u6c34\u85fb\uff0c\u886c\u886b\u3001\u957f\u88e4\u6e7f\u8d34\u5728\u8eab\u4e0a\uff0c\u4e0d\u65ad\u7684\u6709\u6c34\u6ef4\u4e0b\u6765\u2026\u2026\u5468\u56f4\u662f\u4e00\u7fa4\u95fb\u58f0\u8d76\u8fc7\u6765\u62a2\u4eba\u7684\u5b66\u751f\uff0c\u4e5f\u6709\u5f20\u606a\u968f\u884c\u7684\u4e24\u540d\u4fdd\u9556\u3002</p><p>\u3000\u3000\u4e01\u6587\u6021\u8ddf\u9648\u5b81\u5f00\u59cb\u8fd8\u6709\u4e9b\u60ca\u614c\uff0c\u5f85\u770b\u5230\u5f20\u606a\u8fd9\u5e45\u51c4\u51c9\u6837\uff0c\u53c8\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\u3002\u8fd9\u5927\u6982\u5f20\u606a\u7b2c\u4e00\u6b21\u9677\u5165\u80fd\u4ee4\u4ed6\u9762\u7ea2\u8033\u8d64\u7684\u7a98\u5883\uff0c\u5f85\u968f\u884c\u4eba\u5458\u5c06\u8f66\u5f00\u8fc7\u6765\uff0c\u4ed6\u5c31\u4ed3\u7687\u7684\u8eb2\u8fdb\u8f66\u91cc\uff0c\u4ed6\u6ca1\u6709\u52c7\u6c14\u8fd9\u6a21\u6837\u7a7f\u8fc7\u4e1c\u5927\u6821\u56ed\u56de\u9752\u5e74\u516c\u5bd3\u6362\u8863\u670d\u53bb\uff0c\u5750\u8fdb\u8f66\u91cc\uff0c\u4ed6\u626d\u5934\u770b\u5411\u8f66\u7a97\u5916\u7684\u9648\u5b81\uff0c\u5176\u5b9e\u4ed6\u662f\u6ca1\u6709\u52c7\u6c14\u9762\u5bf9\u9648\u5b81\u5982\u6b64\u707f\u70c2\u8000\u773c\u7684\u7b11\u5bb9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u56de\u9752\u5e74\u516c\u5bd3\u6d17\u6fa1\u6362\u8fc7\u8863\u670d\u624d\u56de1978\uff0c\u8fd8\u6ca1\u6709\u8d70\u8fdb\u53bb\uff0c\u5c31\u542c\u5230\u91cc\u9762\u7684\u54c4\u7b11\u58f0\uff0c\u72b9\u8c6b\u7740\u662f\u4e0d\u662f\u201c\u72ec\u81ea\u843d\u5bde\u201d\u7684\u79bb\u5f00\u66f4\u5408\u9002\u4e00\u4e9b\uff0c\u5c31\u542c\u89c1\u5b59\u9759\u6aac\u5728\u91cc\u9762\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u7b11\u4e86\uff0c\u4e0d\u8981\u7b11\u4e86\uff0c\u8981\u662f\u8ba9\u4ed6\u77e5\u9053\u6211\u4eec\u8fd9\u4e48\u6b22\u4e50\uff0c\u6307\u4e0d\u5b9a\u6ca1\u6709\u52c7\u6c14\u8fdb\u6765\u3002\u5148\u8ba9\u4ed6\u8fdb\u6765\uff0c\u7b49\u4f1a\u513f\u8c01\u8fc7\u53bb\u5c06\u4ed6\u7684\u6c34\u8349\u670d\u62ff\u8fc7\u6765\u7ed9\u6211\u4eec\u73b0\u573a\u518d\u7a7f\u4e00\u904d\u2026\u2026\u8981\u4e0d\u6211\u4eec\u9a97\u4ed6\u5230\u6e56\u8fb9\u518d\u8d70\u4e00\u8d9f\uff0c\u518d\u628a\u4ed6\u5f80\u6e56\u91cc\u63a8\u4e00\u4e0b\uff0c\u6b23\u8d4f\u4e0d\u5230\u4ed6\u843d\u6c34\u7684\u77ac\u95f4\uff0c\u4eba\u751f\u771f\u662f\u9057\u61be\u554a\u3002\u201d</p><p>\u3000\u3000\u5b8c\u5168\u6ca1\u6709\u521a\u624d\u7684\u6c89\u6291\uff0c\u9759\u6aac\u7684\u8bdd\u53c8\u5f15\u8d77\u4e00\u58f0\u6b22\u7b11\u58f0\uff1b\u5f20\u606a\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6ca1\u60f3\u5230\u4e01\u6587\u6021\u5148\u56de1978\u5c31\u5c06\u4ed6\u7684\u4e11\u4e8b\u5ba3\u626c\u5f00\u4e86\uff0c\u54ea\u91cc\u6709\u534a\u70b9\u201c\u5f03\u5987\u201d\u7684\u81ea\u89c9\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8146\u7740\u8138\u8d70\u8fdb\u53bb\uff0c\u770b\u7740\u8fd9\u4e9b\u4eba\u8138\u4e0a\u6273\u7740\u7684\u7b11\u5bb9\uff0c\u771f\u60f3\u627e\u53ea\u5927\u94c1\u9524\u4e00\u4eba\u8138\u4e0a\u7838\u4e00\u9524\u5b50\u624d\u591f\u75db\u5feb\uff1a\u201c\u6765\u554a\uff0c\u8c01\u8981\u53bb\u6e56\u8fb9\u6563\u6b65\uff0c\u6211\u5949\u966a\u5c31\u662f\u4e86\uff0c\u201d\u7ad9\u5230\u5510\u5a67\u7684\u8eab\u540e\uff0c\u6390\u7740\u5979\u7684\u5c0f\u8170\uff0c\u6076\u72e0\u72e0\u7684\u8bf4\u9053\uff0c\u201c\u77a7\u4f60\u5e78\u707e\u4e50\u7978\u7684\u6837\u5b50\uff0c\u6211\u4eec\u5148\u4e00\u8d77\u53bb\u6e56\u8fb9\u6563\u4e00\u6563\u6b65\uff01\u201d</p><p>\u3000\u3000\u5510\u5a67\u4e71\u626d\u7740\u5728\u4ece\u5f20\u606a\u7684\u9b54\u638c\u91cc\u6323\u624e\u4e0d\u8131\uff0c\u4f4e\u58f0\u8f6f\u6c14\u7684\u6c42\u6320\uff0c\u53ea\u662f\u8138\u4e0a\u7684\u7b11\u5bb9\u7279\u522b\u7684\u6b22\u4e50\u3002</p><p>\u3000\u3000\u4e01\u6587\u6021\u7b11\u7740\u8ddf\u5510\u5a67\u8bf4\uff1a\u201c\u4f60\u771f\u8be5\u770b\u7d27\u8fd9\u5c0f\u5b50\uff0c\u4ed6\u5c31\u770b\u89c1\u90a3\u4e2a\u5c0f\u7f8e\u5973\u8d70\u8fc7\u6765\u8ddf\u4ed6\u6253\u62db\u547c\u5c31\u60ca\u60f6\u5931\u63aa\u7684\u6389\u6e56\u91cc\u53bb\u4e86\u2026\u2026\u4f60\u4eec\u6ca1\u6709\u770b\u5230\u4ed6\u843d\u6c34\u7684\u60ca\u60f6\u5931\u63aa\u6837\u554a\uff0c\u6211\u4e00\u8f88\u5b50\u90fd\u6a21\u4eff\u4e0d\u51fa\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8baa\u8baa\u7b11\u7740\uff0c\u5750\u5728\u5427\u53f0\u524d\u62ff\u8d77\u79bb\u5f00\u524d\u6ca1\u559d\u5b8c\u7684\u9152\u676f\u559d\u9152\uff0c\u5fc3\u60f3\u9648\u5b81\u4e5f\u8bb8\u662f\u6691\u5047\u65f6\u7559\u4e0b\u6765\u6253\u5de5\u624d\u6ca1\u6709\u56de\u65b0\u829c\u53bb\uff1b\u4e01\u6587\u6021\u4e5f\u8bb8\u8ba4\u4e3a\u662f\u5979\u8bc6\u7a7f\u4ed6\u8ddf\u665a\u6674\u7684\u4e8b\u60c5\u624d\u8ba9\u4ed6\u60ca\u60f6\u843d\u6c34\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u542d\u58f0\uff0c\u5927\u5bb6\u80fd\u5728\u4e00\u8d77\u7545\u6000\u800c\u7b11\u4e5f\u4e0d\u5bb9\u6613\uff0c\u4ed6\u603b\u4e0d\u559c\u6b22\u60b2\u60b2\u621a\u621a\u7684\u6c1b\u56f4\u3002\u770b\u5b59\u9759\u9999\u4e0e\u4e01\u6587\u6021\u4e5f\u6ca1\u6709\u4ec0\u4e48\u82a5\u8482\u5b58\u5728\uff0c\u8fd9\u79cd\u4e8b\u6709\u5982\u996e\u6c34\uff0c\u51b7\u6696\u81ea\u77e5\uff0c\u4e0d\u9700\u8981\u522b\u4eba\u5f53\u8bf4\u5ba2\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u591c\u91cc\u8981\u8d76\u56de\u6d77\u5dde\u53bb\uff0c\u5c31\u6ca1\u6709\u5728\u5efa\u90ba\u591a\u803d\u6401\uff0c\u770b\u7740\u5929\u8272\u6e10\u66ae\uff0c\u5c31\u4e0a\u4e86\u8f66\uff0c\u4e0e\u53f6\u5efa\u658c\u3001\u5b59\u9759\u9999\u3001\u4e01\u6587\u6021\u544a\u522b\u3002</p><p>\u3000\u3000\u770b\u7740\u66ae\u8272\u4e2d\u53f6\u5efa\u658c\u3001\u5b59\u9759\u9999\u3001\u4e01\u6587\u6021\u4e09\u4eba\u8eab\u5f71\u6e10\u6de1\u76f4\u81f3\u6a21\u7cca\u4e00\u4e2a\u5f71\u5b50\uff0c\u5510\u5a67\u7a81\u7136\u611f\u6168\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u8981\u5e78\u798f\u7684\u6e21\u8fc7\u4eba\u751f\u771f\u4e0d\u662f\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\u5462\u2026\u2026\u201d</p></article>"}}}]);
