(window.webpackJsonp=window.webpackJsonp||[]).push([[1162],{1185:function(p){p.exports={chapterTitle:null,title:"\u7b2c955\u7ae0 \u5b64\u6ce8\u4e00\u63b7\u6218\u7565",content:"<article><h2>\u7b2c955\u7ae0 \u5b64\u6ce8\u4e00\u63b7\u6218\u7565</h2><p>\u3000\u3000\u4ece12\u670812\u65e5\u8d77\uff0c\u9526\u6e56\u5728\u5efa\u90ba\u53ec\u5f00\u4e86\u4e3a\u671f\u4e09\u5929\u7684\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\uff0c\u65f6\u95f4\u4e0a\u7d27\u8feb\u4e86\u4e00\u4e9b\uff0c\u80fd\u5145\u5206\u8ba8\u8bba\u7684\u95ee\u9898\u4e5f\u6709\u9650\uff0c\u4e0d\u8fc7\u5bf9\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u505a\u51fa\u4e00\u4e9b\u5236\u5ea6\u6027\u7684\u89c4\u5b9a\u3002\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u4ee5\u540e\u6bcf\u4e2a\u5b63\u5ea6\u90fd\u4f1a\u5b9a\u671f\u4e3e\u884c\uff0c\u96c6\u4f53\u8ba8\u8bba\u4e0e\u51b3\u7b56\u6574\u4e2a\u9526\u6e56\u4f53\u7cfb\u672a\u6765\u7684\u53d1\u5c55\u65b9\u5411\u4e0e\u6218\u7565\uff0c\u91cd\u8981\u4e8b\u9879\u4e5f\u5c06\u7531\u4f1a\u8bae\u529e\u516c\u5ba4\u901a\u62a5\u4f17\u4eba\uff0c\u4e5f\u51b3\u5b9a\u4ee5\u540e\u7684\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7531\u7231\u8fbe\u96c6\u56e2\u4e0e\u9526\u6e56\u5546\u4e8b\u7684\u603b\u88c1\u8f6e\u503c\u4e3b\u6301\uff0c\u773c\u4e0b\u5c31\u7531\u9648\u4fe1\u751f\u4e0e\u53f6\u5efa\u658c\u4e24\u4eba\u8f6e\u6d41\u62c5\u4efb\u4f1a\u8bae\u8f6e\u503c\u4e3b\u5e2d\uff0c\u8fd9\u6837\u5728\u5236\u5ea6\u4e0a\u786e\u4fdd\u7231\u8fbe\u96c6\u56e2\u4e0e\u9526\u6e56\u5546\u4e8b\u5728\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u6838\u5fc3\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5012\u662f\u5468\u6e38\u4e00\u53e5\u620f\u8a00\uff0c\u201c\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u201d\u8fd9\u4e2a\u4e0d\u6b63\u5f0f\u7684\u79f0\u8c13\u6df1\u5165\u4eba\u5fc3\uff0c\u9526\u6e56\u5185\u90e8\u8fd9\u51e0\u5929\u90fd\u4ee5\u8fd9\u4e2a\u540d\u79f0\u6765\u6307\u4ee3\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u3002</p><p>\u3000\u300014\u65e5\u4e0b\u5348\uff0c\u4e09\u5929\u7684\u201c\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u201d\u7ed3\u675f\u4e86\uff0c\u6309\u7167\u65e5\u7a0b\u7684\u5b89\u6392\uff0c\u8981\u7ec4\u7ec7\u53c2\u52a0\u53c2\u89c2\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u3002</p><p>\u3000\u3000\u5317\u65b9\u7684\u5bd2\u6d41\u8fd8\u6ca1\u6709\u4fb5\u6765\uff0c\u5efa\u90ba\u7684\u5929\u6c14\u867d\u7136\u6e05\u5bd2\uff0c\u4f46\u4e5f\u80fd\u8ba9\u4eba\u5fcd\u53d7\uff0c\u5b9e\u9a8c\u56ed\u91cc\u7684\u91d1\u9ec4\u8272\u6216\u91d1\u7ea2\u8272\u7684\u6a61\u6811\u53f6\u7c0c\u7c0c\u800c\u843d\uff0c\u56ed\u5703\u91cc\u79ef\u4e86\u8584\u8584\u7684\u4e00\u5c42\u3002</p><p>\u3000\u3000\u5b9e\u9a8c\u56ed\u533a\u5bf9\u5916\u7ba1\u5236\u4e0d\u4e25\u683c\uff0c\u751a\u81f3\u6709\u9644\u8fd1\u9ad8\u6821\u7684\u7537\u5973\u5b66\u751f\u5077\u6e9c\u8fdb\u6765\u8c08\u604b\u8bf4\u7231\uff0c\u95e8\u536b\u4e5f\u4e0d\u4f1a\u9a71\u9010\u4ed6\u4eec\uff0c\u8fdb\u5165\u5404\u4e2a\u7814\u53d1\u697c\u7684\u5927\u5802\u540e\u95e8\u7981\u7ba1\u5236\u624d\u4f1a\u4e25\u683c\u8d77\u6765\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8fd1\u4e00\u5e74\u6765\u5f88\u5c11\u6709\u65f6\u95f4\u5728\u5efa\u90ba\uff0c\u52a0\u4e0a\u4e5f\u6ca1\u6709\u4e8b\u52a1\u4e0a\u7684\u5fc5\u8981\uff0c\u4ed6\u8fd8\u662f\u7b2c\u4e00\u6b21\u8d70\u8fdb\u5b9e\u9a8c\u56ed\u7684\u7814\u53d1\u697c\u91cc\uff0c\u770b\u7740\u697c\u9053\u91cc\u7684\u5eca\u706f\u4eae\u5ea6\u968f\u7740\u4eba\u7684\u8d70\u8fd1\u800c\u81ea\u52a8\u8c03\u8282\uff0c\u5ba4\u5185\u7684\u7a7a\u8c03\u7cfb\u7edf\u4e5f\u662f\u81ea\u52a8\u8c03\u8282\uff0c\u90fd\u89c9\u5f97\u5f88\u60ca\u5947\uff0c\u76f8\u4fe1\u8fd8\u6709\u8bb8\u591a\u7ec6\u8282\u5904\u6ca1\u6709\u4eba\u63d0\u9192\u8fd8\u611f\u53d7\u4e0d\u5230\uff0c\u4ed6\u95ee\u5f20\u606a\uff1a\u201c\u8fd9\u51e0\u680b\u697c\u91cc\u7838\u4e86\u591a\u5c11\u94b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u539f\u5148\u4f30\u8ba1\u8fd9\u51e0\u680b\u697c\u9020\u4ef7\u6709\u591a\u5c11\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\uff0c\u201c\u5728\u697c\u5b87\u667a\u80fd\u7cfb\u7edf\u4e0a\u7684\u6295\u5165\uff0c\u5dee\u4e0d\u591a\u662f\u4f60\u6240\u4f30\u7b97\u7684\u4e24\u500d\u2026\u2026\u201d</p><p>\u3000\u3000\u5b9e\u9a8c\u56ed\u53bb\u5e74\u5efa\u6210\u56db\u680b\u697c\uff0c\u4eca\u5e74\u53c8\u5efa\u6210\u4e8c\u680b\u697c\u6295\u5165\u4f7f\u7528\uff0c\u7531\u4e8e\u71d5\u5f52\u6e56\u4e34\u8fd1\u5efa\u7b51\u6709\u9650\u5236\u7684\u8981\u6c42\uff0c\u516d\u680b\u7814\u53d1\u697c\u7684\u603b\u5efa\u7b51\u9762\u79ef\u4e5f\u4e0d\u8fc7\u5341\u4e07\u5e73\u65b9\u7c73\uff0c\u7531\u4e8e\u662f\u4f4e\u5c42\u5efa\u7b51\uff0c\u5efa\u7b51\u4e0a\u7684\u6210\u672c\u8981\u8fdc\u8fdc\u4f4e\u4e8e\u9ad8\u5c42\u5efa\u7b51\uff0c\u53f6\u5efa\u658c\u539f\u5148\u4f30\u7b97\u4e5f\u5c31\u4e24\u4e09\u4e2a\u4ebf\uff0c\u7edd\u6ca1\u6709\u60f3\u5230\u8fd9\u51e0\u680b\u697c\u91cc\u80fd\u6295\u5165\u4e03\u516b\u4e2a\u4ebf\uff0c\u8fd9\u5f53\u7136\u8fd8\u6ca1\u6709\u5c06\u5b9e\u9a8c\u8bbe\u5907\u7b97\u5728\u5185\uff0c\u5b9e\u9a8c\u8bbe\u5907\u7684\u4ef7\u503c\u7edd\u5bf9\u8981\u8fdc\u8fdc\u8d85\u8fc7\u8fd9\u51e0\u680b\u697c\u7684\u4ef7\u503c\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5476\u5476\u5634\uff0c\u4e0d\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u4e4b\u524d\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u4e1a\u52a1\u53ea\u80fd\u7b97\u6709\u7c97\u6d45\u7684\u4e86\u89e3\uff0c\u4ed6\u8981\u4e0e\u9648\u4fe1\u751f\u8f6e\u6d41\u62c5\u5f53\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u7684\u8f6e\u503c\u4e3b\u5e2d\uff0c\u867d\u8bf4\u7231\u8fbe\u96c6\u56e2\u7684\u53d1\u5c55\u4e5f\u4e3b\u8981\u4ee5\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u7684\u610f\u89c1\u4e3a\u4e3b\uff0c\u4f46\u662f\u4ed6\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u4e1a\u52a1\u4e86\u89e3\u4e5f\u4e0d\u80fd\u53ea\u505c\u7559\u5728\u539f\u6765\u7684\u5c42\u6b21\u4e0a\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u6709\u66b4\u53d1\u6237\u7684\u6c14\u6168\u554a\uff01\u201d\u76db\u9752\u5728\u65c1\u8fb9\u6447\u5934\u5fae\u5fae\u4e00\u53f9\uff0c\u8bf4\u9053\uff0c\u201c\u4ece\u53bb\u5e74\u4e94\u6708\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\u4ee5\u6765\uff0c\u5230\u73b0\u91d1\u5728\u7814\u7a76\u4f53\u7cfb\u91cc\u6295\u5165\u7684\u8d44\u91d1\u603b\u6570\u8d85\u8fc7\u56db\u5341\u4ebf\uff0c\u6211\u770b\u5230\u6750\u6599\u4e0a\u7684\u6570\u636e\u8fd8\u5728\u72af\u8ff7\u7cca\u5462\uff0c\u600e\u4e48\u80fd\u82b1\u8fd9\u4e48\u94b1\u51fa\u53bb\uff1f\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u3001\u76db\u9752\u4ed6\u4eec\u8fd9\u6b21\u624d\u7ed9\u6b63\u5f0f\u7684\u7eb3\u5165\u9526\u6e56\u6838\u5fc3\u7ba1\u7406\u5c42\u4e4b\u5217\uff0c\u4e5f\u662f\u8fd9\u6b21\u624d\u6b63\u5f0f\u7684\u63a5\u89e6\u7231\u8fbe\u96c6\u56e2\u7684\u6838\u5fc3\u5546\u4e1a\u673a\u5bc6\uff0c\u5f20\u606a\u4ed6\u4eec\u5728\u7814\u53d1\u4e0a\u7684\u6295\u5165\u4e4b\u51b3\u5fc3\uff0c\u4e5f\u6df1\u6df1\u7684\u9707\u61be\u4e86\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u4e0d\u5c06\u7231\u8fbe\u7535\u5b50\u501f\u58f3\u4e0a\u5e02\u5377\u5230\u56db\u5341\u4ebf\u8ba1\u7b97\u5728\u5185\uff0c\u5dee\u4e0d\u591a\u5c06\u8fd9\u51e0\u5e74\u5728\u789f\u673a\u3001\u624b\u673a\u7b49\u4e1a\u52a1\u4e0a\u5377\u5230\u7684\u5de8\u989d\u66b4\u5229\u90fd\u6295\u5165\u5230\u7814\u53d1\u4f53\u7cfb\u5185\u4e86\uff0c\u751a\u81f3\u8981\u8fdc\u8fdc\u8d85\u8fc7\u7231\u8fbe\u96c6\u56e2\u5728\u751f\u4ea7\u4f53\u7cfb\u4e0a\u7684\u6269\u5f20\u6295\u5165\uff0c\u62ff\u66b4\u53d1\u6237\u7684\u6c14\u6982\u6765\u5f62\u5bb9\uff0c\u8fd8\u771f\u662f\u6070\u5f53\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u6709\u8fd9\u4e48\u5927\u7684\u610f\u89c1\uff0c\u4f1a\u8bae\u4e0a\u4e0d\u63d0\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u672f\u4e1a\u6709\u4e13\u653b\uff0c\u5bf9\u7535\u5b50\u4ea7\u4e1a\u7684\u53d1\u5c55\uff0c\u6211\u54ea\u91cc\u80fd\u63d0\u51fa\u50cf\u6837\u7684\u610f\u89c1\u6765\uff1f\u201d\u76db\u9752\u7b11\u7740\u81ea\u5632\u9053\uff0c\u201c\u56fd\u5185\u4e5f\u6709\u51e0\u5bb6\u58f0\u52bf\u9887\u4e3a\u7684\u7535\u5b50\u3001\u7535\u5668\u5236\u9020\u4f01\u4e1a\uff0c\u50cf\u6625\u5170\u3001\u50cf\u6d77\u5c14\u3001\u50cf\u957f\u8679\u3001\u50cf\u65b0\u79d1\u3001\u50cf\u6b65\u6b65\u9ad8\uff0c\u8fd9\u4e9b\u5e74\u7684\u53d1\u5c55\u90fd\u5f88\u5feb\u901f\uff0c\u4e5f\u90fd\u8fd8\u5728\u79ef\u6781\u7684\u6269\u5f20\uff0c\u58f0\u52bf\u9887\u5927\u3002\u6d77\u5c14\u3001\u957f\u8679\u7b49\u4e0d\u53bb\u8bf4\u4ed6\uff0c\u5c31\u8bf4\u65b0\u6cf0\u7684\u6625\u5170\u96c6\u56e2\u5427\uff0c\u6700\u521d\u662f\u505a\u7a7a\u8c03\u8d77\u5bb6\uff0c\u5982\u4eca\u6625\u5170\u51b0\u7bb1\u3001\u6625\u5170\u6d17\u8863\u673a\u3001\u6625\u5170\u7535\u89c6\u3001\u6625\u5170\u7535\u996d\u7a9d\u3001\u6625\u5170\u5fae\u6ce2\u7089\u7b49\u4ea7\u54c1\u7433\u7405\u6ee1\u76ee\uff0c\u9664\u4e86\u4f20\u7edf\u7684\u5bb6\u7535\u884c\u4e1a\u5916\uff0c\u6625\u5170\u4e5f\u6d89\u8db3\u6469\u6258\u8f66\u3001\u6c7d\u8f66\u3001\u8ba1\u7b97\u673a\u7b49\u5236\u9020\u884c\u4e1a\u3002\u6bd4\u8f83\u8d77\u6625\u5170\uff0c\u7231\u8fbe\u5728\u56fd\u5185\u7684\u4ea7\u54c1\u5c31\u53ea\u80fd\u62ff\u53ef\u601c\u6765\u5f62\u5bb9\u4e86\uff0c\u9664\u4e86\u624b\u673a\u5c31\u662f\u5f71\u789f\u673a\uff0c\u54e6\uff0c\u7535\u5b50\u8bcd\u5178\u5728\u56fd\u5185\u5e02\u573a\u9500\u91cf\u6392\u7b2c\u4e8c\uff0c\u4e0d\u8fc7\u60f3\u6765\u7535\u5b50\u8bcd\u5178\u7684\u9500\u552e\u989d\u591a\u5c11\u4e5f\u6709\u4e9b\u5fae\u4e0d\u8db3\u9053\u4e86\u2026\u2026\u8981\u8ba4\u771f\u6bd4\u8f83\u8d77\u6765\uff0c\u6625\u5170\u7684\u73b0\u91d1\u6d41\u8fdc\u8fdc\u4e0d\u80fd\u8ddf\u7231\u8fbe\u76f8\u6bd4\uff0c\u7231\u8fbe\u7684\u54c1\u724c\u77e5\u540d\u5ea6\u6bd4\u6625\u5170\u4e5f\u4e0d\u5f31\uff0c\u6709\u4eba\u5c31\u4f1a\u4ea7\u751f\u8fd9\u6837\u7684\u7591\u95ee\uff0c\u7231\u8fbe\u5728\u4ea7\u54c1\u7ebf\u4e0a\u7684\u6269\u5f20\u662f\u4e0d\u662f\u592a\u4fdd\u5b88\u4e86\u4e9b\uff1f\u5f53\u7136\u4e86\uff0c\u5bfb\u5e38\u4eba\u90fd\u4e0d\u77e5\u9053\u7231\u8fbe\u5728\u4ea7\u4e1a\u94fe\u7684\u5ef6\u4f38\u4e0a\u5927\u6982\u5df2\u7ecf\u505a\u5230\u773c\u4e0b\u80fd\u505a\u7684\u6781\u81f4\uff0c\u800c\u4e14\u66f4\u591a\u7684\u8d44\u91d1\u90fd\u6295\u5165\u5230\u7814\u53d1\u4e2d\u6765\uff0c\u4ece\u6839\u672c\u4e0a\uff0c\u7231\u8fbe\u4e0e\u6625\u5170\u9009\u62e9\u8d70\u7684\u662f\u4e24\u6761\u622a\u7136\u4e0d\u540c\u7684\u53d1\u5c55\u9053\u8def\uff0c\u7231\u8fbe\u662f\u6df1\u624e\u6839\uff0c\u6625\u5170\u662f\u5148\u94fa\u644a\u5b50\uff0c\u6211\u8fd9\u4e9b\u5929\u5c31\u5728\u60f3\u8fd9\u4e24\u79cd\u53d1\u5c55\u6a21\u5f0f\u5b70\u4f18\u5b70\u52a3\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u9887\u4e3a\u610f\u5916\u7684\u770b\u4e86\u770b\u76db\u9752\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u770b\u4f60\u73b0\u5728\u662f\u8d8a\u6765\u8d8a\u6ca1\u6709\u897f\u57ce\u5927\u54e5\u7684\u6c14\u8d28\u4e86\u554a\uff1f\u201d</p><p>\u3000\u3000\u5176\u4ed6\u4eba\u8fd8\u987e\u7740\u5230\u76db\u9752\u7684\u9762\u5b50\u5fcd\u7740\u4e0d\u7b11\uff0c\u5f20\u77e5\u975e\u4e0e\u90b5\u81f3\u521a\u5374\u662f\u5927\u7b11\u8d77\u6765\uff0c\u82cf\u6d25\u4e1c\u4e5f\u6447\u5934\u800c\u7b11\uff0c\u76db\u9752\u6700\u521d\u7684\u8eab\u4efd\u653e\u5230\u53e4\u4ee3\u79f0\u201c\u5de8\u6ed1\u201d\uff0c\u73b0\u5728\u53eb\u201c\u5927\u6d41\u6c13\u201d\uff0c\u4e4b\u524d\u8d1f\u8d23\u76d7\u7248\u5236\u789f\u88e2\uff0c\u4ece\u76d7\u7248\u4ea7\u4e1a\u5f7b\u5e95\u9000\u51fa\u4e4b\u540e\uff0c\u5c31\u4e00\u76f4\u5728\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u7684\u4e1a\u52a1\uff0c\u4ed6\u80fd\u5bf9\u6625\u5170\u3001\u7231\u8fbe\u7684\u53d1\u5c55\u6a21\u5f0f\u6709\u8fd9\u4e00\u756a\u7684\u601d\u8003\uff0c\u8fd9\u6b21\u80fd\u8fdb\u5165\u4e8c\u5341\u4e5d\u4eba\u7684\u6838\u5fc3\u5c42\u4e5f\u4e0d\u662f\u5076\u7136\u554a\u3002</p><p>\u3000\u3000\u76db\u9752\u5c06\u8ddf\u7740\u8fc7\u6765\u53c2\u89c2\u5b9e\u9a8c\u56ed\u7684\u675c\u98de\u62c9\u8fc7\u6765\uff0c\u63c9\u4e86\u63c9\u4ed6\u7684\u8111\u74dc\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u524d\u7684\u897f\u57ce\u5927\u54e5\u662f\u675c\u98de\u4ed6\u7238\uff0c\u8ddf\u6211\u642d\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u662f\u6df1\u624e\u6839\u8fd8\u662f\u5148\u94fa\u644a\u5b50\u7684\u95ee\u9898\u53ef\u4ee5\u8bf4\u8fd9\u65f6\u5019\u56f0\u6320\u4e86\u5f88\u591a\u56fd\u5185\u7a0d\u6709\u89c4\u6a21\u3001\u7a0d\u6709\u6210\u5c31\u7684\u4f01\u4e1a\uff0c\u201d\u56de\u5934\u770b\u4e86\u5d14\u56fd\u6052\u4e00\u773c\uff0c\u201c\u5d14\u9662\u957f\uff0c\u4f60\u5bf9\u8fd9\u65b9\u9762\u5f88\u6709\u7814\u7a76\uff0c\u4f60\u600e\u4e48\u8bc4\u4ef7\uff1f\u201d</p><p>\u3000\u3000\u5d14\u56fd\u6052\u80fd\u591f\u6210\u4e8c\u5341\u4e5d\u4eba\u4e4b\u4e00\uff0c\u4e3b\u8981\u662f\u4ed6\u9886\u5bfc\u7684\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u4e3a\u9ad8\u7ea7\u7ecf\u7406\u4f1a\u8bae\u63d0\u4f9b\u667a\u529b\u652f\u6301\uff0c\u4ed6\u8d70\u5230\u524d\u9762\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6625\u5170\u7684\u6a21\u5f0f\u770b\u4e0a\u53bb\u662f\u5728\u5145\u5206\u6316\u6398\u54c1\u724c\u4ef7\u503c\u7684\u6f5c\u529b\uff0c\u4e0d\u8fc7\u4ea7\u54c1\u7ebf\u6269\u5f20\u592a\u5feb\uff0c\u4e5f\u4f1a\u8fc7\u5ea6\u7684\u7a00\u91ca\u54c1\u724c\u7684\u4ef7\u503c\u3002\u5b66\u754c\u6bd4\u8f83\u6b63\u7edf\u7684\u89c2\u70b9\u662f\u5e0c\u671b\u80fd\u5747\u8861\u53d1\u5c55\uff0c\u62ff\u6d77\u5c14\u6765\u8bf4\uff0c\u6d77\u5c14\u53d1\u5c55\u7684\u524d\u671f\u51e0\u5e74\u4e5f\u662f\u4e13\u6ce8\u5728\u51b0\u7bb1\u3001\u51b7\u67dc\u5236\u9020\u4e1a\u4e0a\uff0c\u5dee\u4e0d\u591a\u6709\u516d\u4e03\u5e74\u65f6\u95f4\u6ca1\u6709\u5206\u5fc3\uff0c\u6700\u8fd1\u51e0\u5e74\u624d\u5b9e\u884c\u591a\u5143\u5316\u53d1\u5c55\u2014\u2014\u6d77\u5c14\u7684\u6a21\u5f0f\u6bd4\u8f83\u7b26\u5408\u4f20\u7edf\u7ecf\u6d4e\u5b66\u754c\u7684\u601d\u7ef4\uff0c\u5148\u624e\u6839\u518d\u94fa\u644a\u5b50\uff0c\u4e0d\u8fc7\u6bd4\u8f83\u8d77\u7231\u8fbe\u6765\uff0c\u4ed6\u4eec\u7684\u6839\u5b50\u4e0d\u80fd\u7b97\u624e\u5f97\u6df1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5d14\u9662\u957f\u5728\u59d4\u5a49\u6279\u8bc4\u7231\u8fbe\u6839\u624e\u5f97\u592a\u6df1\u4e86\uff0c\u6709\u4e9b\u5b64\u6ce8\u4e00\u63b7\u4e86\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u7684\u8bf4\u9053\uff0c\u201c\u5c06\u9e21\u86cb\u653e\u5230\u4e00\u53ea\u7bee\u5b50\u91cc\u7684\u786e\u4e0d\u5229\u4e8e\u5206\u89e3\u98ce\u9669\uff0c\u6211\u4eec\u7684\u4ea7\u54c1\u7ebf\u5f88\u72ed\u7a84\uff0c\u5728\u56fd\u5185\u53ea\u6709\u789f\u673a\u3001\u624b\u673a\u4e0e\u638c\u4e0a\u7535\u8111\u4e09\u79cd\uff0c\u638c\u4e0a\u7535\u8111\u7684\u9500\u552e\u4efd\u989d\u592a\u5c0f\uff0c\u5bf9\u7231\u8fbe\u6ca1\u6709\u7279\u522b\u5927\u7684\u5f71\u54cd\u529b\uff0c\u789f\u673a\u53c8\u662f\u4e0a\u5e02\u516c\u53f8\u7684\u4e1a\u52a1\uff0c\u5bf9\u7231\u8fbe\u96c6\u56e2\u6765\u8bf4\uff0c\u5b9e\u9645\u4e0a\u5728\u56fd\u5185\u53ea\u6709\u624b\u673a\u4e00\u79cd\u6709\u5f71\u54cd\u529b\u7684\u4ea7\u54c1\uff0c\u81f3\u5c11\u5728\u56fd\u5185\u7684\u5e02\u573a\uff0c\u6211\u4eec\u5c06\u592a\u591a\u7684\u5e0c\u671b\u5bc4\u6258\u5728\u624b\u673a\u8fd9\u5355\u4e00\u4ea7\u54c1\u4e0a\u4e86\uff0c\u4ea7\u4e1a\u94fe\u7684\u5ef6\u4f38\u53d1\u5c55\uff0c\u7279\u522b\u662f\u6280\u672f\u7814\u53d1\u7684\u6295\u5165\uff0c\u4e5f\u90fd\u56f4\u7ed5\u7740\u624b\u673a\u8fd9\u5355\u4e00\u54c1\u79cd\uff0c\u8bf4\u5b64\u6ce8\u4e00\u63b7\u4e5f\u662f\u5408\u9002\u2026\u2026\u201d\u5f20\u606a\u8bf4\u5230\u8fd9\u91cc\uff0c\u73af\u987e\u5de6\u53f3\uff0c\u95ee\u9053\uff0c\u201c\u4f60\u4eec\u662f\u4e0d\u662f\u4e5f\u89c9\u5f97\u7231\u8fbe\u7684\u53d1\u5c55\u7b56\u7565\u662f\u5b64\u6ce8\u4e00\u63b7\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u4e00\u95ee\uff0c\u6c14\u6c1b\u5c31\u6709\u4e9b\u51dd\u91cd\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5927\u5bb6\u90fd\u4e0d\u8bf4\u8bdd\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u4e0d\u9ad8\u5174\u8bf4\u626b\u5174\u7684\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u201c\u5747\u8861\u53d1\u5c55\u7684\u786e\u66f4\u7b26\u5408\u6b63\u7edf\u7ecf\u6d4e\u5b66\u754c\u7684\u601d\u7ef4\uff0c\u201d\u9648\u4fe1\u751f\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff0c\u201c\u4ece\u5d14\u9662\u957f\u63d0\u4f9b\u7684\u6750\u6599\u91cc\uff0c\u6211\u4eec\u53ef\u4ee5\u770b\u5230\u4e09\u661f\u96c6\u56e2\u4e5f\u5728\u679c\u65ad\u780d\u4e00\u4e9b\u770b\u4e0a\u53bb\u5f88\u6709\u6f5c\u529b\u7684\u4e1a\u52a1\uff0c\u91cd\u5316\u5de5\u3001\u5efa\u7b51\u7b49\u7b49\u4e1a\u52a1\u90fd\u653e\u5f03\u6389\u4e86\uff0c\u5c31\u662f\u6700\u4e3a\u770b\u91cd\u7684\u4e09\u661f\u7535\u5b50\uff0c\u4e5f\u5c06\u4e3b\u8981\u7cbe\u529b\u90fd\u96c6\u4e2d\u5728\u6570\u5b57\u6280\u672f\u53d1\u5c55\u4e0a\u3002\u5728\u8d1f\u503a\u4e00\u767e\u4e03\u5341\u4ebf\u7f8e\u5143\u4e4b\u540e\uff0c\u4e09\u661f\u4e5d\u516b\u5e74\u7528\u5728\u6570\u5b57\u6280\u672f\u53d1\u5c55\u4e0a\u7684\u8d39\u7528\u5c06\u8fd1\u4e8c\u5341\u4ebf\u7f8e\u5143\u3002\u4ee5\u4f20\u7edf\u7684\u5b66\u672f\u89c2\u70b9\u53bb\u8bc4\u4ef7\uff0c\u4e09\u661f\u96c6\u56e2\u4e5f\u662f\u5b64\u6ce8\u4e00\u63b7\u7684\u5c06\u5b9d\u62bc\u5728\u6570\u5b57\u6280\u672f\u4e0a\u4e86\u2026\u2026\u7ed9\u7ecf\u6d4e\u5371\u673a\u62c9\u5230\u6fd2\u4e34\u7834\u4ea7\u8fb9\u7f18\u7684\u4e09\u661f\u96c6\u56e2\u4e3a\u4ec0\u4e48\u8fd8\u6562\u9009\u62e9\u5982\u6b64\u5b64\u6ce8\u4e00\u63b7\u7684\u53d1\u5c55\u6218\u7565\uff1f\u9996\u5148\uff0c\u4ed6\u4e0e\u6211\u4eec\u7684\u5224\u65ad\u76f8\u8fd1\uff0c\u8ba4\u4e3a\u6570\u5b57\u6280\u672f\u662f\u672a\u6765\u7535\u5b50\u4ea7\u4e1a\u7684\u5927\u52bf\u6240\u8d8b\uff0c\u6700\u4e3b\u8981\u7684\uff0c\u6211\u60f3\u8fd8\u662f\u4e09\u661f\u91ce\u5fc3\u52c3\u52c3\uff0c\u5c06\u66f4\u591a\u7684\u529b\u91cf\u653e\u5728\u4e00\u6761\u8def\u4e0a\uff0c\u63a8\u8fdb\u7684\u901f\u5ea6\u4e5f\u5c31\u66f4\u5feb\u2014\u2014\u4e09\u661f\u662f\u4e0d\u7518\u5fc3\u53ea\u505a\u4e8c\u6d41\u7684\u4f01\u4e1a\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5634\u91cc\u867d\u7136\u5728\u8bf4\u4e09\u661f\uff0c\u4e5f\u65e0\u7591\u5728\u5ba3\u544a\u9526\u6e56\u7684\u91ce\u5fc3\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u8fd9\u4e5f\u662f\u627f\u8ba4\u7231\u8fbe\u7684\u8def\u5b50\u662f\u5b64\u6ce8\u4e00\u63b7\u554a\u2026\u2026\u4e0d\u5728\u8fd9\u91cc\u591a\u505c\u7559\u4e86\uff0c\u8fd8\u662f\u8fb9\u53c2\u89c2\u8fb9\u8bae\u8bba\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u6700\u5148\u8d70\u8fdb\u53bb\u53c2\u89c2\u7684\u662f\u6676\u5c4f\u663e\u793a\u6280\u672f\u5b9e\u9a8c\u5ba4\uff0c\u9526\u6e56\u4ece\u5fb7\u4eea\u624b\u91cc\u83b7\u5f97\u7070\u9636STN\u6db2\u6676\u5c4f\u6280\u672f\uff0c\u4e5f\u8bf4\u662f\u5e38\u89c1\u7684\u9ed1\u767d\u5c4f\uff0c\u9526\u6e56\u7684\u84dd\u5c4f\u6280\u672f\u4e5f\u53ea\u662f\u5728\u9ed1\u767d\u5c4f\u4e0b\u7f6e\u84dd\u5149\u4e8c\u6781\u7ba1\u4f5c\u80cc\u5149\u6e90\u5f97\u4ee5\u5b9e\u73b0\uff0c\u8fd9\u4e24\u5e74\u4e5f\u4ece\u65e5\u97e9\u4e00\u4e9b\u5c0f\u516c\u53f8\u6536\u8d2d\u4e86\u4e00\u4e9b\u76f8\u5173\u7684\u6280\u672f\uff0c\u52a0\u5f3a\u4e86\u6280\u672f\u5e95\u5b50\u3002\u5728\u4f20\u7edf\u7070\u9636STN\u6676\u5c4f\u4e0a\u52a0\u5f69\u8272\u6ee4\u5149\u7247\u80fd\u5206\u89e3\u51fa\u7ea2\u3001\u7eff\u3001\u84dd\u4e09\u539f\u8272\uff0c\u7406\u8bba\u4e0a\u4e5f\u5c31\u53ef\u4ee5\u663e\u793a\u5f69\u8272\u753b\u9762\uff0c\u4f46\u662f\u5982\u4f55\u5728\u624b\u673a\u7b49\u7535\u5b50\u4ea7\u54c1\u4e0a\u5c06\u8fd9\u4e00\u6280\u672f\u5546\u7528\u5316\uff0c\u662f\u6a61\u6811\u56ed\u663e\u793a\u6280\u672f\u5b9e\u9a8c\u5ba4\u8fd9\u4e00\u5e74\u6765\u4e00\u76f4\u90fd\u5728\u6295\u5165\u4e3b\u8981\u7cbe\u529b\u653b\u514b\u7684\u4e00\u4e2a\u96be\u5173\u2014\u2014\u8fd9\u4e5f\u53ef\u4ee5\u8bf4\u662f\u7231\u8fbe\u96c6\u56e2\u5b64\u6ce8\u4e00\u63b7\u6218\u7565\u7684\u4f53\u73b0\u3002</p></article>"}}}]);
