(window.webpackJsonp=window.webpackJsonp||[]).push([[1165],{1188:function(p){p.exports={chapterTitle:null,title:"\u7b2c1162\u7ae0 \u8f6f\u5367\u8f66\u53a2\u91cc\u7684\u4e4c\u9f99\u4e8b\u4ef6",content:"<article><h2>\u7b2c1162\u7ae0 \u8f6f\u5367\u8f66\u53a2\u91cc\u7684\u4e4c\u9f99\u4e8b\u4ef6</h2><p>\u3000\u30004.26\u7684\u7eb3\u6307\u66b4\u8dcc\uff0c\u7275\u7d2f\u5168\u7403\u8bc1\u5238\u5e02\u573a\u65b0\u7ecf\u6d4e\u677f\u5757\u4e00\u9f50\u6df1\u9677\u60e8\u6de1\u9634\u4e91\u4e4b\u4e2d\uff0c\u5bf9\u8fd9\u65b9\u9762\u4e0d\u751a\u7cbe\u901a\u7684\u6797\u96ea\u4e5f\u5370\u8c61\u6df1\u523b\uff0c\u7761\u68a6\u91cc\u9690\u7ea6\u542c\u5230\u738b\u6d77\u7c9f\u5728\u8f66\u53a2\u8fc7\u9053\u8bf4\u7eb3\u6307\u5c3e\u76d8\u5927\u5e45\u8df3\u6c34\uff0c\u5979\u9661\u7136\u5c31\u60ca\u9192\u8fc7\u6765\uff0c\u7741\u5f00\u773c\u775b\u5374\u542c\u89c1\u738b\u6d77\u7c9f\u662f\u5728\u6572\u9694\u58c1\u5305\u53a2\u7684\u95e8\uff0c\u77e5\u9053\u4ed6\u60ca\u60f6\u5931\u63aa\u7684\u6572\u9519\u4e86\u95e8\uff0c\u5979\u5634\u91cc\u5495\u5499\u7684\u9a82\u4e86\u4e00\u58f0\uff1a\u771f\u662f\u5e9f\u7269\uff0c\u6253\u5f00\u5305\u53a2\u7684\u9876\u706f\uff0c\u62e2\u4e86\u62e2\u6709\u4e9b\u51cc\u6563\u7684\u957f\u53d1\uff0c\u624d\u4e0b\u4e86\u5e8a\u6253\u5f00\u5305\u53a2\u7684\u95e8\uff0c\u95ee\u7ad9\u5728\u9694\u58c1\u5305\u53a2\u95e8\u524d\u7684\u738b\u6d77\u7c9f\uff1a\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u738b\u6d77\u7c9f\u8fd9\u624d\u610f\u8bc6\u5230\u6572\u9519\u4e86\u5305\u53a2\u95e8\uff0c\u6123\u4e86\u6123\uff0c\u8bf4\u9053\uff0c\u201c\u4e25\u5148\u751f\u8bf7\u4f60\u5230\u4ed6\u5305\u53a2\u91cc\u8c08\u4e8b\u60c5\u53bb\u3002\u201d</p><p>\u3000\u3000\u6797\u96ea\u7761\u89c9\u524d\u6362\u4e0a\u7684\u7761\u88d9\u6709\u4e9b\u76b1\uff0c\u7ef8\u8d28\u7684\u9762\u6599\u5149\u6ed1\u95ea\u4eae\uff0c\u886c\u6258\u5f97\u5979\u7684\u808c\u80a4\u6108\u53d1\u7684\u5a07\u5ae9\uff0c\u7761\u88d9\u6709\u4e9b\u77ed\uff0c\u9886\u53e3\u7565\u4f4e\uff0c\u9732\u51fa\u5c0f\u534a\u7247\u7ec6\u767d\u6027\u611f\u7684\u4e73\u6c9f\uff0c\u60f9\u4eba\u9050\u601d\u3002\u6797\u96ea\u4e0d\u4f1a\u4ecb\u610f\u7537\u4eba\u7684\u773c\u5149\uff0c\u6b63\u6253\u7b97\u56de\u5305\u53a2\u6362\u8863\u670d\u518d\u53bb\u4e25\u6587\u4ecb\u5305\u53a2\u91cc\u8c08\u4e8b\u60c5\uff0c\u8fd9\u65f6\u5019\u8fd9\u8fb9\u5305\u53a2\u95e8\u4e5f\u6253\u5f00\u4e86\uff0c\u5f20\u606a\u9732\u51fa\u534a\u5f20\u8138\u6765\uff0c\u61d2\u6d0b\u6d0b\u7684\u8ddf\u738b\u6d77\u7c9f\u8bf4\u9053\uff1a\u201c\u7eb3\u6307\u66b4\u8dcc\u7684\u4e8b\u60c5\uff0c\u6211\u521a\u521a\u77e5\u9053\u4e86\uff0c\u4e0d\u8fc7\u8fd8\u662f\u8981\u8c22\u8c22\u4f60\u4eec\u544a\u8bc9\u6211\u2026\u2026\u201d\u597d\u50cf\u738b\u6d77\u7c9f\u771f\u662f\u8fc7\u6765\u901a\u77e5\u4ed6\u4f3c\u7684\u3002</p><p>\u3000\u3000\u4e0a\u706b\u8f66\u540e\uff0c\u77e5\u9053\u5f20\u606a\u5c31\u5728\u8fd9\u8f66\u53a2\u91cc\uff0c\u4f46\u662f\u4e00\u76f4\u90fd\u6ca1\u6709\u5728\u8fc7\u9053\u91cc\u9047\u5230\u4ed6\u7684\u4eba\uff0c\u6839\u672c\u5c31\u4e0d\u6653\u5f97\u4ed6\u7761\u5728\u81ea\u5df1\u9694\u58c1\uff0c\u738b\u6d77\u7c9f\u53c8\u641e\u51fa\u8fd9\u6837\u7684\u4e4c\u9f99\u4e8b\u6765\uff0c\u770b\u7740\u5f20\u606a\u5634\u89d2\u6d45\u6d45\u7684\u7b11\u5bb9\uff0c\u6797\u96ea\u5fc3\u91cc\u6709\u4e9b\u607c\u706b\uff0c\u89c1\u5f20\u606a\u7684\u773c\u795e\u77a5\u8fc7\u6765\uff0c\u5979\u4e0b\u610f\u8bc6\u7684\u53cc\u624b\u62e2\u5728\u80f8\u524d\uff0c\u906e\u4f4f\u5f00\u53e3\u6709\u4e9b\u4f4e\u7684\u7761\u88d9\u9886\u895f\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u6797\u96ea\u4e0d\u4ecb\u610f\u5176\u4ed6\u597d\u8272\u7537\u4eba\u7684\u773c\u795e\u5f80\u5979\u80f8\u53e3\u4e0a\u98d8\uff0c\u4f46\u5728\u5f20\u606a\u7684\u9762\u524d\u5374\u4e0d\u7531\u81ea\u4e3b\u7684\u4f1a\u62d8\u675f\u8d77\u6765\uff0c\u611f\u89c9\u4e0a\u675f\u624b\u675f\u811a\u7684\uff0c\u8ddf\u672a\u51fa\u793e\u4f1a\u7684\u5c0f\u4e2b\u5934\u4f3c\u7684\u3002\u624d\u5c06\u63a9\u7740\u9886\u53e3\u7684\u624b\u653e\u4e0b\u6765\uff0c\u611f\u89c9\u5f20\u606a\u7684\u773c\u795e\u53c8\u779f\u5230\u81ea\u5df1\u5149\u6e9c\u6e9c\u7684\u5c0f\u817f\u4e0a\uff0c\u751a\u81f3\u90fd\u89c9\u5f97\u90a3\u91cc\u6709\u4e9b\u53d1\u75d2\uff0c\u5979\u5fc3\u91cc\u6697\u9a82\u4e86\u81ea\u5df1\u4e00\u58f0\uff0c\u867d\u7136\u7eb3\u6307\u66b4\u8dcc\u662f\u4ef6\u4e86\u4e0d\u5f97\u7684\u4e8b\u60c5\uff0c\u4f46\u662f\u7edd\u4e0d\u60f3\u5728\u5f20\u606a\u9762\u524d\u8868\u73b0\u51fa\u60ca\u60f6\u5931\u63aa\u7684\u6837\u5b50\u6765\uff0c\u58f0\u97f3\u6de1\u6de1\u7684\u8ddf\u738b\u6d77\u7c9f\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4e86\uff0c\u6211\u6362\u8eab\u8863\u670d\u5c31\u8fc7\u6765\u3002\u201d\u8f6c\u8eab\u5c06\u5305\u53a2\u95e8\u5173\u4e0a\uff0c\u6362\u4e86\u4e00\u8eab\u8863\u670d\uff0c\u518d\u6b21\u6253\u5f00\u5305\u53a2\u95e8\u65f6\uff0c\u8fc7\u9053\u91cc\u9759\u6084\u6084\u7684\uff0c\u53ea\u6709\u706b\u8f66\u4e0e\u94c1\u8f68\u7684\u649e\u51fb\u58f0\u6709\u89c4\u5f8b\u7684\u4f20\u6765\u3002</p><p>\u3000\u3000\u8d70\u5230\u4e25\u6587\u4ecb\u7684\u5305\u53a2\u91cc\uff0c\u5218\u5947\u5cfb\u3001\u738b\u6d77\u7c9f\u90fd\u5728\uff0c\u6ca1\u6709\u60ca\u52a8\u8fd9\u6b21\u968f\u884c\u7684\u661f\u6cb3\u8bc1\u5238\u5efa\u90ba\u5206\u516c\u53f8\u7684\u8d1f\u8d23\u4eba\uff0c\u7531\u4e8e\u4e2d\u56fd\u79fb\u52a8\u8fd8\u6ca1\u6709\u6b63\u5f0f\u5927\u89c4\u6a21\u5347\u7ea7GPRS\u7f51\u7edc\uff0c\u4ed6\u4eec\u5728\u706b\u8f66\u4e0a\u65e0\u6cd5\u63a5\u6536\u5230\u5b9e\u65f6\u7684\u7f51\u7edc\u4fe1\u606f\uff0c\u7eb3\u6307\u6536\u76d8\u66b4\u8dcc\u7684\u6d88\u606f\u8fd8\u662f\u7559\u5b88\u5728\u5efa\u90ba\u7684\u5f20\u6587\u4fe1\u6253\u7535\u8bdd\u544a\u77e5\u7684\u3002\u5218\u5947\u5cfb\u6839\u636e\u7535\u8bdd\u5185\u5bb9\uff0c\u505a\u4e86\u7b80\u5355\u7684\u7eb3\u6307\u66f2\u7ebf\u8d70\u52bf\u56fe\u3002</p><p>\u3000\u3000\u867d\u7136\u662f\u8c6a\u534e\u8f6f\u5305\uff0c\u4f46\u662f\u7a7a\u95f4\u8fd8\u662f\u72ed\u5c0f\u538b\u6291\uff0c\u4e24\u5f20\u7d27\u8d34\u7740\u8f66\u53a2\u58c1\u7684\u5e8a\u94fa\u4e4b\u95f4\u7a7a\u9699\u53ea\u80fd\u591f\u7ad9\u4e00\u4e2a\u4eba\uff0c\u5218\u5947\u5cfb\u90a3\u5f20\u7b80\u5355\u753b\u7740\u7eb3\u6307\u66f2\u7ebf\u8d70\u52bf\u56fe\u7684A4\u767d\u7eb8\u644a\u5728\u8f66\u7a97\u524d\u7684\u5c0f\u65b9\u684c\u4e0a\uff0c\u4e25\u6587\u4ecb\u7ad9\u5728\u5e8a\u94fa\u4e4b\u95f4\uff0c\u7709\u5934\u9501\u7d27\u7740\u601d\u8003\u4e00\u4e9b\u4e1c\u897f\u3002\u6797\u96ea\u8d70\u8fdb\u6765\uff0c\u7a0d\u8e2e\u4e86\u8e2e\u811a\uff0c\u89c6\u7ebf\u8d8a\u8fc7\u4ed6\u7684\u80a9\u8180\u770b\u5230\u90a3\u5f20\u7eb8\uff0c\u5218\u5947\u5cfb\u4e0e\u738b\u6d77\u7c9f\u5404\u5750\u4e00\u8fb9\u7684\u5e8a\u94fa\u4e0a\u3002\u770b\u738b\u6d77\u7c9f\u7684\u8868\u60c5\uff0c\u5e94\u8be5\u6ca1\u6709\u5c06\u4ed6\u521a\u624d\u6572\u9519\u5305\u53a2\u95e8\u7684\u4e8b\u60c5\u8ddf\u4e25\u6587\u4ecb\u3001\u738b\u6d77\u7c9f\u8bf4\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u56de\u5934\u770b\u4e86\u6797\u96ea\u4e00\u773c\uff0c\u4ed6\u5750\u5230\u5218\u5947\u5cfb\u8eab\u8fb9\uff0c\u5c06\u8d70\u52bf\u56fe\u9012\u7ed9\u5979\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u770b\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u6797\u96ea\u63a5\u8fc7\u6765\u770b\u4e86\u770b\uff0c\u66f2\u7ebf\u56fe\u5728\u6700\u540e\u534a\u5c0f\u65f6\u5f62\u6210\u4e00\u4e2a\u5de8\u5927\u7684\u7f3a\u53e3\uff0c\u538b\u5f97\u4eba\u80f8\u53e3\u5598\u4e0d\u8fc7\u6c14\u6765\u3002\u5979\u4fa7\u5934\u770b\u5411\u8f66\u7a97\uff0c\u8f66\u7a97\u4e0a\u6620\u7740\u6de1\u6de1\u7684\u9752\u5149\uff0c\u5df2\u7ecf\u662f\u62c2\u6653\u4e86\u3002\u56fd\u5185\u62c2\u6653\u56db\u70b9\u949f\uff0c\u592a\u5e73\u6d0b\u5f7c\u5cb8\u5374\u662f\u665a\u971e\u707f\u70c2\u7684\u9ec4\u660f\u65f6\u5206\uff0c\u53ea\u662f\u7eb3\u6307\u5728\u5c3e\u7b14\u7684\u8df3\u6c34\u5374\u5b8c\u5168\u8ba9\u4eba\u611f\u89c9\u4e0d\u5230\u5915\u9633\u7684\u6e29\u6696\u3002</p><p>\u3000\u3000\u516d\u6708\u4e2d\u65ec\uff0c\u7eb3\u6307\u5f3a\u52bf\u53cd\u5f39\u65f6\uff0c\u4e1a\u754c\u5bf9\u540e\u52bf\u7684\u5224\u65ad\u5c31\u6cfe\u6e2d\u5206\u660e\u7684\u4e3b\u8981\u5206\u6210\u4e24\u6d3e\u3002\u4e00\u6d3e\u8ba4\u4e3a\u6240\u8c13\u7684\u5168\u7403\u65b0\u79d1\u6280\u7ecf\u6d4e\u5371\u673a\u53ea\u662f\u77ed\u6682\u7684\u5931\u8c03\uff0c\u5bf9\u65b0\u79d1\u6280\u7ecf\u6d4e\u7684\u53d1\u5c55\u5145\u6ee1\u7740\u4e50\u89c2\u7684\u59ff\u6001\uff0c\u8ba4\u4e3a\u7ecf\u8fc7\u8fd1\u4e24\u4e2a\u6708\u7684\u8c03\u6574\uff0c\u6ce1\u6cab\u5df2\u7ecf\u6324\u51fa\u5927\u90e8\u5206\uff0c\u4e00\u6d3e\u8ba4\u4e3a\u7eb3\u6307\u516d\u6708\u4e2d\u65ec\u5f3a\u52bf\u53cd\u5f39\u53ea\u662f\u524d\u671f\u8dcc\u5e45\u592a\u6df1\u800c\u51fa\u73b0\u7684\u6280\u672f\u6027\u53cd\u5f39\uff0c\u4e0d\u4f1a\u6539\u53d8\u65b0\u79d1\u6280\u7ecf\u6d4e\u6ce1\u6cab\u7834\u65e7\u7684\u8d8b\u52bf\u3002\u7eb3\u6307\u5f3a\u52bf\u53cd\u5f39\uff0c\u4ece\u6700\u4f4e\u4e09\u5343\u56db\u767e\u70b9\u53cd\u5f39\u5230\u518d\u6b21\u89e6\u53ca\u4e94\u5343\u70b9\u7684\u9ad8\u5cf0\uff0c\u4f3c\u4e4e\u66f4\u652f\u6301\u7b2c\u4e00\u6d3e\u7684\u89c2\u70b9\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u5012\u662f\u60f3\u505a\u4e24\u624b\u51c6\u5907\uff0c\u53ea\u662f\u6ca1\u6709\u60f3\u5230\u7eb3\u6307\u516d\u6708\u4e2d\u65ec\u7684\u53cd\u5f39\u4f1a\u4ee5\u9661\u7136\u62e8\u8d77\u7684\u59ff\u6001\u8fdb\u884c\uff0c\u6ca1\u6709\u7ed9\u4ed6\u4eec\u5bbd\u88d5\u7684\u51c6\u5907\u65f6\u95f4\u3002\u5728\u77ed\u77ed\u4e8c\u5341\u4ea4\u6613\u65e5\u5c31\u4e00\u6c14\u4e0d\u6b47\u7684\u53cd\u5f39\u62d4\u9ad8\u8fd150\uff05\uff0c\u4ee4\u4eba\u63aa\u624b\u4e0d\u53ca\uff0c\u4e5f\u96be\u514d\u8ba9\u4eba\u6000\u7591\u8fd9\u79cd\u53cd\u5f39\u4f1a\u540e\u7ee7\u65e0\u529b\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u4ed6\u4eec\u5bf9\u8fd9\u6837\u60c5\u51b5\u5012\u662f\u6709\u8fc7\u5224\u65ad\uff0c\u4f30\u8ba1\u8fd8\u662f\u6709\u4e9b\u4e0d\u8db3\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u5728\u4ed6\u4eec\u7684\u64cd\u7eb5\u4e0b\u53cd\u5f39\u5230\u516c\u5f00\u589e\u53d1\u7684\u6761\u4ef6\uff0c\u4f46\u662f\u5b9e\u65bd\u516c\u5f00\u589e\u53d1\u4ecd\u7136\u9700\u8981\u4e00\u4e9b\u65f6\u95f4\uff0c\u4e5f\u8bb8\u518d\u6709\u4e24\u5468\u7684\u65f6\u95f4\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u201c\u7eb3\u65af\u4ece\u516d\u6708\u4e2d\u65ec\u5f00\u59cb\u5728\u4e0d\u5230\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\u4e00\u6c14\u4e0a\u5347\u4e86\u4e00\u5343\u516d\u767e\u70b9\uff0c\u662f\u4e0d\u662f\u4e5f\u8be5\u7f13\u4e00\u53e3\u6c14\u4e86\uff1f\u201d\u6797\u96ea\u8bd5\u63a2\u6027\u7684\u95ee\u9053\uff0c\u5979\u5bf9\u91d1\u878d\u8bc1\u5238\u7279\u522b\u662f\u6d77\u5916\u8bc1\u5238\u7684\u77e5\u8bc6\u8c08\u4e0d\u4e0a\u7cbe\u901a\uff0c\u5979\u5728\u7cbe\u5178\u5730\u4ea7\u8bbe\u4e86\u4e2a\u6295\u8d44\u987e\u95ee\u7684\u804c\u4f4d\uff0c\u53ea\u662f\u4e0d\u4f1a\u968f\u65f6\u5c06\u4eba\u5bb6\u5e26\u5728\u8eab\u8fb9\u54a8\u8be2\uff0c\u8fd9\u65f6\u5019\u53ea\u80fd\u5f81\u8be2\u5218\u5947\u5cfb\u7684\u610f\u89c1\uff0c\u6bd5\u7adf\u8fd9\u51e0\u5e74\u6765\u5218\u5947\u5cfb\u7387\u9886\u6d59\u4e1c\u8d22\u56e2\u5728\u8bc1\u5238\u5e02\u573a\u6709\u8fc7\u591a\u8d77\u8f89\u714c\u7684\u6218\u7ee9\u3002</p><p>\u3000\u3000\u4e4b\u524d\u56fd\u5185\u7684\u91d1\u878d\u8bc1\u5238\u5e02\u573a\u76f8\u5bf9\u72ec\u7acb\uff0c\u53d7\u6d77\u5916\u91d1\u878d\u5e02\u573a\u7684\u5f71\u54cd\u5f88\u5c11\uff0c\u4f46\u662f\u56db\u6708\u672b\u7eb3\u6307\u66b4\u8dcc\u4e25\u91cd\u7275\u7d2f\u5230\u56fd\u5185\u6caa\u6df1\u4e24\u5e02\u7684\u65b0\u79d1\u6280\u7f51\u7edc\u80a1\uff0c\u5218\u5947\u5cfb\u5c31\u5728\u6d77\u5916\u91d1\u878d\u5e02\u573a\u4e0b\u4e86\u4e9b\u5de5\u592b\uff0c\u4ed6\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u516d\u6708\u4e2d\u65ec\u7eb3\u6307\u53cd\u5f39\u662f\u4ee5\u5fae\u8f6f\u53cd\u5784\u65ad\u518d\u83b7\u8f6c\u673a\u8fd9\u4e00\u5173\u952e\u3001\u53ef\u9884\u6d4b\u4e8b\u4ef6\u4e3a\u5951\u673a\u7684\uff0c\u53c8\u4e00\u6c14\u53cd\u5f39\u4e86\u8fd9\u4e48\u9ad8\uff0c\u591a\u5c11\u6709\u4e9b\u4eba\u5de5\u96d5\u7422\u7684\u75d5\u8ff9\u5728\u91cc\u9762\u3002\u7279\u522b\u662f\u5728\u516d\u6708\u4e2d\u65ec\u53cd\u5f39\u4e4b\u524d\uff0c\u7ebd\u7ea6\u534e\u5c14\u8857\u7684\u6295\u8d44\u673a\u6784\u90fd\u63d0\u524d\u8f6c\u5230\u7a7a\u5934\u7684\u9635\u8425\uff0c\u8bb8\u591a\u5a92\u4f53\u5c31\u6b64\u8bc4\u8bba\u534e\u5c14\u8857\u6295\u8d44\u5bf9\u8fd9\u6b21\u53cd\u5f39\u884c\u60c5\u770b\u8d70\u4e86\u773c\u2026\u2026\u6295\u8d44\u673a\u6784\u7684\u505a\u6cd5\u4e0d\u8fc7\u662f\u63d0\u524d\u91ca\u653e\u70df\u96fe\u5f39\u800c\u5df2\uff0c\u6211\u4eec\u5047\u8bbe\u6295\u8d44\u673a\u6784\u5171\u540c\u7b56\u5212\u4e86\u516d\u6708\u4e2d\u65ec\u7684\u5927\u53cd\u5f39\uff0c\u6211\u60f3\u8fd9\u65f6\u5019\u624d\u662f\u4ed6\u4eec\u771f\u6b63\u8f6c\u5165\u7a7a\u5934\u9635\u8425\u7684\u65f6\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u6797\u96ea\u5012\u5438\u4e00\u53e3\u51c9\u6c14\uff0c\u5218\u5947\u5cfb\u7684\u610f\u601d\u662f\u8bf4\u5927\u5e45\u6740\u8dcc\u7684\u65f6\u673a\u624d\u521a\u521a\u5230\u6765\u3002</p><p>\u3000\u3000\u201c\u56fd\u5185\u8bc1\u5238\u5e02\u573a\u53cd\u5e94\u786e\u662f\u8981\u6162\u4e00\u4e9b\uff0c\u201d\u4e25\u6587\u4ecb\u95ee\u5218\u5947\u5cfb\uff0c\u201c\u6211\u4eec\u6700\u591a\u80fd\u6293\u4f4f\u591a\u5c11\u65f6\u95f4\uff1f\u201d</p><p>\u3000\u3000\u201c\u6700\u591a\u4e00\u5468\u65f6\u95f4\uff0c\u201d\u5218\u5947\u5cfb\u8bf4\u9053\uff0c\u201c\u4e5f\u8bf4\u4e0d\u5b9a\u662f\u865a\u60ca\u4e00\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u865a\u60ca\u4e00\u573a\u5462\uff0c\u201d\u738b\u6d77\u7c9f\u58f0\u8c03\u7565\u9ad8\u4ea2\u7684\u8bf4\u9053\uff0c\u201c\u5c3e\u76d8\u8dcc\u5e45\u624d4\uff05\uff0c\u8981\u77e5\u90534\u670826\u300127\u300128\u4e09\u5929\u8dcc\u5e45\u5c3121\uff05\uff0c\u81f3\u5c11\u7f8e\u56fd\u4e5f\u6ca1\u6709\u4f20\u51fa\u4ec0\u4e48\u4e0d\u5229\u7684\u6d88\u606f\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5218\u5947\u5cfb\u770b\u4e86\u738b\u6d77\u7c9f\u4e00\u773c\uff0c\u6ca1\u6709\u542d\u58f0\uff0c\u4ed6\u90a3\u4e48\u8bf4\u53ea\u662f\u81ea\u6211\u5b89\u6170\uff0c\u738b\u6d77\u7c9f\u5374\u5f53\u771f\u4e86\uff0c\u6709\u4e9b\u4e1c\u897f\uff0c\u4ed6\u8981\u662f\u4e0d\u7406\u89e3\uff0c\u8ddf\u4ed6\u4ed4\u7ec6\u89e3\u91ca\u4e5f\u662f\u8d39\u529b\u4e0d\u8ba8\u597d\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u5c31\u5f53\u738b\u6d77\u7c9f\u90a3\u53e5\u8bdd\u6ca1\u6709\u8bf4\uff0c\u76f4\u63a5\u95ee\u5218\u5947\u5cfb\uff1a\u201c\u6211\u4eec\u8981\u600e\u4e48\u5e94\u5bf9\uff1f\u201d</p><p>\u3000\u3000\u5f88\u96be\u8bf4\u8981\u600e\u4e48\u5e94\u5bf9\uff0c\u4e4b\u524d\u8fd8\u597d\u64cd\u4f5c\u4e00\u4e0b\uff0c\u5c31\u6015\u6c5f\u654f\u4e4b\u4e00\u53ea\u773c\u775b\u5df2\u7ecf\u76ef\u4e0a\u4e86\u6d77\u7c9f\u79d1\u6280\uff0c\u8c08\u4ec0\u4e48\u5e94\u5bf9\u5c31\u6709\u4e9b\u56f0\u96be\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u659c\u9760\u5728\u5217\u8f66\u8f66\u53a2\u7edf\u4e00\u7684\u767d\u8272\u68c9\u5e03\u88ab\u5b50\u4e0a\uff0c\u542c\u7740\u8fc7\u9053\u91cc\u4eba\u8d70\u52a8\u7684\u58f0\u97f3\uff0c\u77e5\u9053\u7eb3\u6307\u5c3e\u76d8\u66b4\u8dcc\u7684\u6d88\u606f\u5c06\u8ba9\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u7b49\u4eba\u89e6\u76ee\u60ca\u5fc3\uff0c\u5f88\u663e\u7136\u7eb3\u6307\u7b2c\u4e8c\u6ce2\u66f4\u52a0\u60e8\u70c8\u7684\u8dcc\u52bf\u5c31\u5c06\u5c55\u5f00\uff0c\u800c\u4e14\u4e00\u7ecf\u5c55\u5f00\uff0c\u4e24\u4e09\u5e74\u5185\u4f11\u60f3\u6062\u590d\u5143\u6c14\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u5b58\u5728\u706b\u8f66\u4e0a\u7761\u4e0d\u7740\u89c9\u7684\u95ee\u9898\uff0c\u524d\u5c18\u5f80\u4e8b\u91cc\u521a\u4ece\u4e1c\u5927\u6bd5\u4e1a\u90a3\u4f1a\u513f\u4e3a\u4e86\u8282\u7ea6\u5dee\u65c5\u8d39\u7528\uff0c\u56db\u4e94\u5341\u5c0f\u65f6\u7684\u786c\u5ea7\u4e5f\u80fd\u4f11\u606f\u5f97\u5f88\u597d\u3002\u8981\u662f\u95f2\u6765\u6ca1\u4e8b\uff0c\u5f20\u606a\u8fd8\u662f\u559c\u6b22\u591c\u95f4\u51fa\u884c\uff0c\u9887\u6709\u4e9b\u65c5\u4eba\u7684\u6ecb\u5473\uff0c\u53ea\u662f\u8fd8\u6ca1\u6709\u5962\u4f88\u5230\u6c5f\u821f\u591c\u884c\u542c\u98ce\u96e8\u7684\u7a0b\u5ea6\u3002\u5efa\u90ba\u6682\u65f6\u8fd8\u6ca1\u6709\u591c\u95f4\u6ca1\u6709\u98de\u5317\u4eac\u7684\u822a\u73ed\uff0c\u5305\u673a\u53c8\u60ca\u52a8\u592a\u5927\uff0c\u8fd9\u5e74\u5934\u5728\u56fd\u5185\u8d2d\u4e70\u79c1\u4eba\u98de\u673a\u7eaf\u7cb9\u662f\u6446\u9970\uff0c\u4e58\u706b\u8f66\u5341\u4e00\u4e2a\u5c0f\u65f6\u7684\u591c\u95f4\u8f66\u7a0b\uff0c\u516b\u70b9\u949f\u4e0a\u8f66\u3001\u6e05\u6668\u4e03\u70b9\u62b5\u8fbe\u5317\u4eac\uff0c\u7ed9\u4eba\u611f\u89c9\u662f\u521a\u521a\u597d\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u5374\u60f3\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u7b49\u4eba\u5750\u706b\u8f66\u5927\u6982\u662f\u8981\u8d76\u5728\u4e0a\u5348\u8ddf\u661f\u6cb3\u8bc1\u5238\u7684\u5317\u4eac\u9ad8\u5c42\u8fdb\u884c\u6700\u540e\u7684\u6c9f\u901a\u5427\uff0c\u4ed6\u4e5f\u662f\u591c\u91cc\u624d\u77e5\u9053\u6c5f\u654f\u4e4b\u6307\u793a\u7701\u8bc1\u5238\u5c40\u5411\u8bc1\u76d1\u4f1a\u6c47\u62a5\u6d77\u7c9f\u79d1\u6280\u65b0\u80a1\u589e\u53d1\u5b58\u5728\u8fdd\u89c4\u64cd\u4f5c\u7684\u5acc\u7591\uff0c\u8fd9\u79cd\u7a0b\u5ea6\u7684\u6c47\u62a5\u4e0d\u4f1a\u7acb\u5373\u4fc3\u4f7f\u8bc1\u76d1\u4f1a\u8c03\u67e5\u6d77\u7c9f\u79d1\u6280\uff0c\u4f46\u662f\u6ca1\u6709\u4eba\u8c01\u518d\u6562\u8086\u65e0\u5fcc\u60ee\u7684\u7ed9\u6d77\u7c9f\u79d1\u6280\u7684\u65b0\u80a1\u589e\u53d1\u5f00\u7eff\u706f\uff0c\u751a\u81f3\u6709\u4eba\u4f1a\u65c1\u6572\u4fa7\u51fb\u7684\u9650\u5236\u4e00\u4e0b\u6d77\u7c9f\u79d1\u6280\uff0c\u6700\u597d\u80fd\u8ba9\u6d77\u7c9f\u79d1\u6280\u7684\u65b0\u80a1\u589e\u53d1\u8ba1\u5212\u6d41\u4ea7\u6389\uff0c\u514d\u5f97\u5f15\u706b\u70e7\u5230\u4ed6\u4eec\u8eab\u4e0a\u3002\u7eb3\u6307\u8fd9\u65f6\u5019\u53c8\u76f8\u5f53\u53ca\u65f6\u7684\u66b4\u8dcc\u4e86\u4e00\u4e0b\uff0c\u5927\u6982\u662f\u8fd9\u4e9b\u4eba\u4e50\u89c1\u7684\uff0c\u53ea\u662f\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u4f1a\u7518\u5fc3\u4ed6\u4eec\u7b79\u5212\u4e00\u5e74\u591a\u7684\u8ba1\u5212\u6d41\u4ea7\u5417\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e0d\u662f\u60f3\u7279\u522b\u7422\u78e8\u8fd9\u4ef6\u4e8b\uff0c\u65e0\u8bba\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u505a\u4ec0\u4e48\u9009\u62e9\uff0c\u5df2\u7ecf\u57fa\u672c\u80fd\u591f\u80af\u5b9a\u5927\u52bf\u5df2\u7ecf\u4e0d\u4f1a\u518d\u6ca1\u6709\u8f6c\u53d8\u4e86\u3002\u8981\u662f\u4ed6\u4eec\u62e7\u7740\u8111\u888b\u5f80\u524d\u51b2\uff0c\u53ea\u4f1a\u9677\u5165\u66f4\u6df1\uff1b\u4ed6\u4eec\u60f3\u6536\u624b\uff0c\u5374\u4e5f\u6765\u4e0d\u53ca\u8131\u8eab\u4e86\u2014\u2014\u5c31\u7b97\u8131\u8eab\uff0c\u4e5f\u8981\u4ece\u4ed6\u4eec\u8eab\u4e0a\u6252\u4e24\u5c42\u76ae\u4e0b\u6765\uff0c\u8fd9\u4ef6\u4e8b\u9887\u4e3a\u8ba9\u4eba\u723d\u5fc3\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u8868\uff0c\u624d\u56db\u70b9\u591a\u4e9b\uff0c\u9192\u6765\u5374\u6ca1\u6709\u7761\u610f\uff0c\u53c8\u4e0d\u4fbf\u8fd9\u4e48\u65e9\u5c06\u9648\u5983\u84c9\u4ece\u5e8a\u4e0a\u62c9\u8fc7\u6765\u966a\u4ed6\u8bf4\u8bdd\uff0c\u53ea\u62ff\u4e86\u4e00\u672c\u4e66\u659c\u5750\u5728\u5e8a\u5934\u770b\u8d77\u6765\uff0c\u8f66\u7a97\u4e0a\u9752\u8272\u7684\u5149\u4eae\u8d8a\u53d1\u7684\u660e\u4eae\uff0c\u5f85\u5230\u516d\u70b9\u949f\u65f6\uff0c\u9648\u5983\u84c9\u624d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u8fd9\u4e48\u65e9\u5c31\u8d77\u6765\u4e86\uff0c\u8fd8\u8981\u6709\u4e00\u4e2a\u5c0f\u65f6\u624d\u5230\u5317\u4eac\u5462\u3002\u201d\u5f20\u606a\u770b\u7740\u9648\u5983\u84c9\u5a07\u67d4\u53ef\u7231\u7684\u63c9\u7740\u60fa\u677e\u7684\u7761\u773c\uff0c\u4ed6\u77e5\u9053\u9648\u5983\u84c9\u4e0d\u4e60\u60ef\u8fd9\u4e48\u65e9\u8d77\u6765\uff0c\u4e5f\u8bb8\u662f\u4e0d\u4e60\u60ef\u591c\u95f4\u7761\u706b\u8f66\u8f6f\u5367\u3002</p><p>\u3000\u3000\u201c\u9192\u8fc7\u6765\u770b\u5230\u4f60\u8f6c\u53d1\u7684\u90ae\u4ef6\uff0c\u624d\u77e5\u9053\u4f60\u4e09\u70b9\u949f\u5c31\u9192\u8fc7\u6765\u4e86\uff0c\u600e\u4e48\u6ca1\u6709\u53eb\u9192\u6211\uff1f\u201d\u9648\u5983\u84c9\u95ee\u9053\u3002</p><p>\u3000\u3000\u4e2d\u56fd\u79fb\u52a8\u5728\u5efa\u90ba\u3001\u5317\u4eac\u7b49\u57ce\u5e02\u5df2\u7ecf\u5efa\u7acb\u4e86GPRS\u8bd5\u9a8c\u901a\u8baf\u7f51\u7edc\uff0c\u7231\u8fbe\u96c6\u56e2\u8ddf\u4e2d\u56fd\u79fb\u52a8\u7b7e\u7f72\u4e86\u4e00\u4e2a\u6253\u5305\u8d2d\u4e70GPRS\u670d\u52a1\u7684\u534f\u8bae\uff0c\u5728\u9ad8\u7aef\u5546\u52a1\u624b\u673a\u4ee5\u53caPDA\u4e2d\u7387\u5148\u690d\u5165\u65e0\u7ebf\u90ae\u4ef6\u6280\u672f\u3002\u7531\u4e8e\u65e0\u7ebf\u90ae\u4ef6\u7684\u6280\u672f\u5e02\u573a\u5728\u56fd\u5185\u76f8\u5f53\u7a84\uff0c\u524d\u671f\u5e76\u6ca1\u6709\u6253\u7b97\u4f5c\u4e3a\u624b\u673a\u8425\u9500\u7684\u91cd\u70b9\u63a8\u51fa\uff0c\u4e0d\u8fc7\u5374\u8ba9\u9526\u6e56\u6210\u5458\u4f01\u4e1a\u7684\u7ba1\u7406\u5c42\u62e5\u6709\u66f4\u4fbf\u5229\u7684\u529e\u516c\u6761\u4ef6\u3002</p><p>\u3000\u3000\u201c\u5c3c\u514b\u91cc\u68ee\u51cc\u6668\u4e09\u70b9\u949f\u6253\u7535\u8bdd\u53eb\u6211\u8d77\u6765\u770b\u90ae\u4ef6\uff0c\u6211\u6068\u4e0d\u5f97\u5c06\u7535\u8bdd\u7838\u4ed6\u8138\u4e0a\u53bb\uff0c\u628a\u6211\u56f0\u5f97\u2014\u2014\u201d\u5f20\u606a\u6253\u4e86\u54c8\u6b20\uff0c\u624b\u63a9\u7740\u53e3\u9f3b\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u53ef\u4e0d\u662f\u90a3\u79cd\u81ea\u5df1\u4e0d\u8212\u670d\u4e5f\u8981\u522b\u4eba\u966a\u7740\u6211\u4e0d\u8212\u670d\u7684\u4eba\u2014\u2014\u518d\u8bf4\u8981\u8c11\u5f85\u4f60\u6765\u65e5\u65b9\u957f\uff0c\u53ef\u4e0d\u80fd\u4e00\u5f00\u59cb\u5c31\u8ba9\u4f60\u5bb3\u6015\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u662f\u8c11\u5f85\u6211\uff0c\u6211\u53ef\u662f\u4f1a\u968f\u65f6\u8981\u6c42\u8c03\u8d70\u7684\uff0c\u201d\u9648\u5983\u84c9\u4fcf\u76ae\u7684\u7b11\u4e86\u7b11\uff0c\u5750\u5230\u5f20\u606a\u7684\u5bf9\u9762\uff0c\u95ee\u9053\uff0c\u201c\u5bf9\u51b2\u57fa\u91d1\u521a\u5927\u91cf\u4e70\u5165\u7eb3\u6307\u7a7a\u5934\u7a7a\u7ea6\uff0c\u7eb3\u6307\u5c31\u679c\u7136\u6389\u5934\u800c\u4e0b\uff0c\u672a\u5fc5\u6709\u4e9b\u592a\u795e\u5947\u4e86\u2026\u2026\u201d\u5979\u6b63\u5f0f\u7ed9\u5f20\u606a\u5f53\u52a9\u624b\uff0c\u81ea\u7136\u5f00\u59cb\u63a5\u89e6\u9526\u6e56\u6700\u6838\u5fc3\u7684\u5546\u4e1a\u673a\u5bc6\uff0c\u4f46\u662f\u5979\u4e0e\u7fdf\u4e39\u9752\u76f8\u6bd4\uff0c\u6bd5\u7adf\u8fd8\u7f3a\u4e00\u4e9b\u706b\u5019\uff0c\u6682\u65f6\u8fd8\u6ca1\u6709\u80fd\u529b\u8ddf\u5f20\u606a\u8ba8\u8bba\u4e00\u4e9b\u6df1\u5c42\u6b21\u7684\u5546\u4e1a\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u5927\u52bf\u603b\u662f\u5bb9\u6613\u5224\u65ad\uff0c\u7ec6\u5904\u5c31\u96be\u4ee5\u638c\u63e1\u4e86\u2014\u2014\u50cf\u8fd9\u6ce2\u53cd\u5f39\u4e5f\u662f\u610f\u6599\u4e4b\u4e2d\u7684\u4e8b\u60c5\uff0c\u4f46\u662f\u6ca1\u6709\u60f3\u5230\u53cd\u5f39\u884c\u60c5\u4f1a\u5982\u6b64\u6025\u4fc3\uff0c\u77ed\u77ed\u4e8c\u5341\u4e2a\u4ea4\u6613\u65e5\u5185\u5c31\u5b8c\u6210\u4e00\u6ce2\u5b8c\u6574\u7684\u53cd\u5f39\u884c\u60c5\uff0c\u7136\u540e\u57fa\u91d1\u524d\u671f\u4e70\u5165\u7684\u7eb3\u6307\u591a\u5934\u5408\u7ea6\u8fd8\u6709\u4e00\u4e2a\u6708\u624d\u5230\u671f\uff0c\u5f88\u53ef\u80fd\u4f1a\u6709\u4e8f\u635f\u2026\u2026\u201d\u5f20\u606a\u6709\u673a\u4f1a\u603b\u4e0d\u5fd8\u591a\u6559\u9648\u5983\u84c9\u4e00\u4e9b\uff0c\u8bf4\u9053\uff0c\u201c\u53e6\u5916\uff0c\u8fd9\u6b21\u4e5f\u5efa\u4e0d\u4e86\u591a\u5927\u89c4\u6a21\u7684\u7a7a\u5934\u5934\u5bf8\uff0c\u6bd5\u7adf\u7eb3\u6307\u53cd\u5f39\u5230\u4e94\u5343\u70b9\uff0c\u5df2\u7ecf\u6ca1\u6709\u591a\u5c11\u8d44\u91d1\u4f1a\u575a\u5b9a\u7684\u76f8\u4fe1\u7eb3\u6307\u8fd8\u4f1a\u7ee7\u7eed\u53cd\u5f39\u3002\u5927\u5bb6\u90fd\u8f6c\u5230\u505a\u7a7a\u7684\u9635\u8425\uff0c\u80fd\u5206\u5230\u7684\u94b1\u5c31\u76f8\u5f53\u6709\u9650\u4e86\u3002\u603b\u4e4b\uff0c\u591a\u5c11\u80fd\u8d5a\u4e00\u4e9b\uff0c\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u6db2\u6676\u4e1a\u52a1\u591a\u5c11\u4e5f\u4f1a\u7ed9\u6572\u8bc8\u6389\u4e00\u4e9b\uff0c\u591a\u5c11\u7b97\u662f\u8865\u507f\u5427\u3002\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u662f\u505a\u597d\u88ab\u6572\u8bc8\u7684\u51c6\u5907\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u529e\u6cd5\uff0c\u6211\u4eec\u7684\u5e95\u724c\u6bd5\u7adf\u592a\u6709\u9650\u4e86\uff0c\u5c31\u7b97\u653e\u5f03\u4e0d\u8ddf\u73b0\u4ee3\u534a\u5bfc\u4f53\u4ea4\u6613\uff0c\u8f6c\u800c\u627eIBM\u5408\u4f5c\uff0c\u6536\u8d2dIBM\u65d7\u4e0b\u7684\u6db2\u6676\u4e1a\u52a1\uff0c\u62a5\u4ef7\u4e5f\u4e0d\u4f1a\u592a\u4f4e\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u867d\u7136\u65e5\u97e9\u7535\u5b50\u5de8\u5934\u90fd\u4e00\u81f4\u53cd\u5bf9\u9526\u6e56\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u6db2\u6676\u4e1a\u52a1\uff0c\u4f46\u662f\u5bf9\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u503a\u6743\u65b9\u6765\u8bf4\uff0c\u5c3d\u5feb\u7684\u5c06\u5305\u88b1\u7529\u6389\u624d\u662f\u6700\u91cd\u8981\u7684\uff0c\u5176\u4ed6\u97e9\u56fd\u3001\u65e5\u672c\u7535\u5b50\u4f01\u4e1a\u867d\u7136\u4e00\u4e2a\u52b2\u7684\u53cd\u5bf9\uff0c\u5374\u53c8\u4e0d\u4f1a\u66ff\u4ed6\u4eec\u8fd9\u4e9b\u503a\u6743\u4eba\u5206\u62c5\u538b\u529b\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u56fd\u9645\u6700\u5927\u7684\u7535\u5b50\u4f01\u4e1a\u4e4b\u4e00IBM\u516c\u53f8\u4e5f\u6253\u7b97\u88c1\u51cf\u4e00\u4e9b\u975e\u6838\u5fc3\u4e1a\u52a1\uff0c\u5176\u65d7\u4e0b\u7684\u6db2\u6676\u4e1a\u52a1\u4e5f\u5728\u88c1\u51cf\u8ba1\u5212\u4e4b\u5217\uff0c\u8fd9\u4e5f\u662f\u9526\u6e56\u53ef\u4ee5\u8003\u8651\u63a5\u89e6\u7684\u5bf9\u8c61\u4e4b\u4e00\uff0c\u73b0\u4ee3\u534a\u5bfc\u4f53\u96c6\u56e2\u5305\u62ec\u73b0\u4ee3\u534a\u5bfc\u4f53\u96c6\u56e2\u80cc\u540e\u7684\u5b9e\u9645\u63a7\u5236\u56e2\u961f\u2014\u2014\u90a3\u4e9b\u94f6\u884c\u503a\u6743\u4eba\u4eec\u62d2\u7edd\u9526\u6e56\u7684\u8bed\u6c14\u5e76\u4e0d\u5341\u5206\u7684\u575a\u5b9a\uff0c\u97e9\u56fd\u97e9\u5174\u94f6\u884c\u7684\u4f1a\u957f\u8fd9\u6b21\u5230\u5317\u4eac\u6765\u8fdb\u884c\u5546\u52a1\u8bbf\u95ee\uff0c\u5c31\u6697\u793a\u53ef\u4ee5\u5728\u5317\u4eac\u8fdb\u884c\u9ad8\u5c42\u4f1a\u6664\uff0c\u5f20\u606a\u8fd9\u624d\u9887\u6709\u95f2\u60c5\u9038\u81f4\u7684\u5750\u706b\u8f66\u5230\u5317\u4eac\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u91d1\u5357\u52c7\u5f88\u65e9\u5c31\u8d77\u5e8a\u6765\uff0c\u5148\u4e60\u60ef\u6027\u7684\u6253\u5f00\u8ba1\u7b97\u673a\u6d4f\u89c8\u591c\u95f4\u53d1\u751f\u5728\u592a\u5e73\u6d0b\u5f7c\u5cb8\u7684\u8d22\u7ecf\u65b0\u95fb\uff0c\u770b\u5230\u7eb3\u6307\u5c3e\u76d8\u66b4\u8dcc\u7684\u8d22\u7ecf\u65b0\u95fb\uff0c\u9a6c\u4e0a\u5c31\u6253\u7535\u8bdd\u7ed9\u674e\u5728\u6d19\uff0c\u5c06\u8fd9\u5219\u6d88\u606f\u544a\u8bc9\u4ed6\u77e5\u9053\u3002</p><p>\u3000\u3000\u867d\u7136\u7eb3\u6307\u53ca\u5168\u7403\u8bc1\u5238\u5e02\u573a\u65b0\u79d1\u6280\u677f\u5757\u6301\u7eed\u56de\u5347\uff0c\u4f46\u662f\u5168\u7403\u7535\u5b50\u5b58\u50a8\u5143\u5668\u4ef6\u9700\u6c42\u91cf\u3001\u8d38\u6613\u91cf\u5374\u4e00\u76f4\u90fd\u5728\u6301\u7eed\u4e0b\u6ed1\uff0c\u4e09\u661f\u7535\u5b50\u4f5c\u4e3a\u5168\u7403\u7535\u5b50\u5b58\u50a8\u5143\u5668\u7684\u4e3b\u8981\u751f\u4ea7\u5382\u5546\uff0c\u5f53\u7136\u6e05\u9192\u7684\u8ba4\u8bc6\u5230\u4e92\u8054\u7f51\u6ce1\u6cab\u7834\u706d\u4e00\u76f4\u5728\u6301\u7eed\u4e2d\uff0c\u7eb3\u6307\u51fa\u73b0\u6280\u672f\u6027\u7684\u53cd\u5f39\uff0c\u6839\u672c\u6539\u53d8\u4e0d\u4e86\u6574\u4e2a\u6ed1\u843d\u7684\u5927\u52bf\u3002</p><p>\u3000\u3000\u770b\u5230\u7eb3\u6307\u5c3e\u76d8\u66b4\u8dcc\u7684\u8d22\u7ecf\u65b0\u95fb\uff0c\u91d1\u5357\u52c7\u4e0e\u674e\u5728\u6d19\u53ea\u662f\u90fd\u6709\u4e00\u79cd\u679c\u7136\u5982\u6b64\u7684\u611f\u89c9\u7f62\u4e86\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u73b0\u5728\u9700\u8981\u505a\u7684\u4e8b\u60c5\u5c31\u662f\u963b\u6b62\u97e9\u5174\u94f6\u884c\u4f1a\u957f\u6734\u771f\u660c\u4e0e\u9526\u6e56\u9ad8\u5c42\u89c1\u9762\uff0c\u91d1\u5357\u52c7\u65e9\u65e9\u5c31\u5230\u4ed6\u5728\u5317\u4eac\u7684\u5e9c\u90b8\uff0c\u4ed6\u6ca1\u6709\u803d\u6401\uff0c\u62c9\u7740\u91d1\u5357\u52c7\u5c31\u76f4\u63a5\u8d76\u5230\u6734\u771f\u660c\u843d\u69bb\u7684\u738b\u5e9c\u4e95\u5927\u9152\u5e97\uff0c\u8fd9\u65f6\u5019\u671d\u9633\u6b63\u5c06\u9152\u5e97\u91d1\u78a7\u8f89\u714c\u7684\u5927\u5802\u67d3\u5f97\u7ea2\u707f\u707f\u7684\u3002</p><p>\u3000\u3000\u6628\u5929\u5c31\u63d0\u51fa\u89c1\u9762\u7684\u8bf7\u6c42\uff0c\u97e9\u5174\u94f6\u884c\u4f1a\u957f\u6734\u771f\u660c\u4e5f\u662f\u52c9\u52c9\u5f3a\u5f3a\u7684\u7b54\u5e94\u4e0b\u6765\uff0c\u674e\u5728\u6d19\u5f97\u77e5\u5f20\u606a\u591c\u91cc\u5750\u4e0a\u524d\u6765\u5317\u4eac\u7684\u706b\u8f66\uff0c\u4ed6\u5c31\u63d0\u524d\u8d76\u5230\u9152\u5e97\u6765\u89c1\u6734\u771f\u660c\uff0c\u907f\u514d\u6734\u771f\u660c\u8ddf\u5f20\u606a\u89c1\u9762\u4e4b\u540e\u5f62\u6210\u9ed8\u5951\uff0c\u4e09\u661f\u518d\u60f3\u505a\u97e9\u5174\u94f6\u884c\u7684\u5de5\u4f5c\u5c31\u4e07\u5206\u56f0\u96be\u3002</p><p>\u3000\u3000\u6734\u771f\u660c\u662f\u4e2a\u4e2a\u5b50\u8f83\u5c0f\u3001\u8138\u7626\u957f\u578b\u7684\u4e2d\u5e74\u97e9\u56fd\u7537\u5b50\uff0c\u6709\u4e9b\u6c34\u4e2d\u7684\u5355\u773c\u76ae\u4f3c\u4e4e\u4e5f\u662f\u97e9\u56fd\u7537\u4eba\u901a\u6709\u7684\u7279\u8272\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u89c9\u5f97\u505a\u6734\u771f\u660c\u5de5\u4f5c\u7684\u96be\u5ea6\u5f88\u5927\uff0c\u6bd5\u7adf\u6ca1\u6709\u7406\u7531\u8981\u6c42\u97e9\u5174\u94f6\u884c\u8ddf\u4e09\u661f\u7684\u5229\u76ca\u4e00\u81f4\uff0c\u751a\u81f3\u6709\u4f20\u8a00\u8bf4\u6734\u771f\u660c\u5728\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u503a\u6743\u4eba\u4f1a\u8bae\u62b1\u6028\uff1a\u73b0\u4ee3\u7684\u6db2\u6676\u4e1a\u52a1\u4e4b\u6240\u4ee5\u6ca1\u6709\u4e3e\u6b65\u552f\u8270\u3001\u9677\u5165\u4e8f\u635f\uff0c\u5c31\u662f\u5927\u90e8\u5206\u6db2\u6676\u5e02\u573a\u7ed9\u4e09\u661f\u3001LG\u4ee5\u53ca\u65e5\u672c\u7535\u5b50\u4f01\u4e1a\u5206\u53bb\uff0c\u4ed6\u4eec\u53c8\u6709\u4ec0\u4e48\u7acb\u573a\u8ba9\u73b0\u4ee3\u6db2\u6676\u575a\u6301\u4e0b\u53bb\uff1f\u518d\u8bf4\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u4e3b\u8425\u82af\u7247\u4e1a\u52a1\u4e5f\u662f\u7ed9\u4e09\u661f\u538b\u5236\u5f97\u591f\u545b\uff0c\u65e0\u8bba\u662f\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u7ba1\u7406\u5c42\u8fd8\u662f\u80cc\u540e\u503a\u6743\u4eba\u4eec\u90fd\u6028\u58f0\u8f7d\u9053\uff0c\u8fd9\u6b21\u6700\u6709\u6548\u7684\u624b\u6bb5\u8fd8\u662f\u5229\u7528\u56fd\u5185\u7684\u8206\u8bba\u963b\u6320\u73b0\u4ee3\u534a\u5bfc\u4f53\u8ddf\u9526\u6e56\u4ea4\u6613\u3002\u81f3\u4e8e\u7d22\u5c3c\u5411\u73b0\u4ee3\u534a\u5bfc\u4f53\u63d0\u51fa\u6db2\u6676\u4e13\u5229\u4fb5\u6743\u8bc9\u8bbc\uff0c\u9526\u6e56\u8d1f\u8d23\u6536\u8d2d\u8c08\u5224\u7684\u5f20\u4e9a\u5e73\u516c\u5f00\u8868\u793a\u7231\u8fbe\u63a5\u624b\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u6db2\u6676\u4e1a\u52a1\u4e4b\u540e\u53ef\u4ee5\u7ee7\u7eed\u8ddf\u7d22\u5c3c\u6253\u4fb5\u6743\u5b98\u53f8\uff0c\u7531\u4e8e\u6cd5\u9662\u4e0d\u652f\u6301\u56e0\u6b64\u5c31\u51b0\u7ed3\u8fd9\u7b14\u4ea4\u6613\uff0c\u7d22\u5c3c\u7684\u8bc9\u8bbc\u53cd\u800c\u8ba9\u73b0\u4ee3\u534a\u5bfc\u4f53\u66f4\u52a0\u60f3\u5c06\u6db2\u6676\u4e1a\u52a1\u8131\u624b\u51fa\u53bb\u3002</p></article>"}}}]);
