(window.webpackJsonp=window.webpackJsonp||[]).push([[1168],{1191:function(p){p.exports={chapterTitle:null,title:"\u7b2c1165\u7ae0 \u9526\u6e56\u7684\u5e95\u7ec6",content:"<article><h2>\u7b2c1165\u7ae0 \u9526\u6e56\u7684\u5e95\u7ec6</h2><p>\u3000\u3000\u674e\u5728\u6d19\u4e5f\u6df1\u523b\u610f\u8bc6\u5230\u9526\u6e56\u5728\u7535\u5b50\u4ea7\u4e1a\u4e0a\u4f1a\u662f\u4e09\u661f\u5f3a\u52b2\u7684\u5bf9\u624b\u3002</p><p>\u3000\u3000\u4e03\u6708\uff0c\u5404\u5730\u9ad8\u6821\u5e94\u5c4a\u6bd5\u4e1a\u751f\u9646\u7eed\u79bb\u6821\u5c31\u4e1a\uff0c\u4eca\u5e74\u4e2d\u56fd\u5e94\u5c4a\u6bd5\u4e1a\u751f\u4eba\u6570\u7a81\u7834\u767e\u4e07\u4eba\u5173\u53e3\uff0c\u7ed9\u4e2d\u56fd\u4f01\u4e1a\u63d0\u4f9b\u4f18\u8d28\u5ec9\u4ef7\u7684\u4eba\u529b\u8d44\u6e90\uff0c\u674e\u5728\u6d19\u4e00\u5411\u8ba4\u4e3a\u662f\u4e2d\u56fd\u4f01\u4e1a\u80fd\u591f\u8fc5\u901f\u6210\u957f\u7684\u5173\u952e\u6027\u56e0\u7d20\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u5176\u4ed6\u6210\u5458\u4f01\u4e1a\u4e0d\u5927\u53d7\u5a92\u4f53\u7684\u5173\u6ce8\uff0c\u8fd9\u8ddf\u9526\u6e56\u5185\u90e8\u7684\u8206\u8bba\u63a7\u5236\u6709\u5927\u7684\u5173\u7cfb\uff0c\u4e0d\u8fc7\u7231\u8fbe\u96c6\u56e2\u7684\u62db\u8058\u8ba1\u5212\u65e9\u5728\u5e74\u524d\u542f\u52a8\u4e4b\u524d\u5c31\u7531\u5a92\u4f53\u5927\u8086\u5ba3\u626c\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u4eca\u5e74\u7684\u5e94\u5c4a\u6bd5\u4e1a\u751f\u5165\u804c\u4eba\u6570\u5c06\u8fbe\u5230\u4e94\u5343\u4eba\uff0c\u6210\u4e3a\u56fd\u5185\u4e00\u6b21\u62db\u8058\u5e94\u5c4a\u6bd5\u4e1a\u751f\u6700\u591a\u7684\u4f01\u4e1a\u800c\u5e7f\u53d7\u77a9\u76ee\u3002\u5176\u4e2d\u4e24\u5343\u4eba\u5c06\u6d41\u5411\u9526\u6e56\u7684\u6838\u5fc3\u7814\u53d1\u673a\u6784\u6a61\u6811\u56ed\u3002</p><p>\u3000\u3000\u4e0e\u5176\u4ed6\u4e2d\u56fd\u9ad8\u79d1\u6280\u4f01\u4e1a\u62db\u8058\u7684\u6bd5\u4e1a\u751f\u65b0\u751f\u4e0d\u540c\uff0c\u6a61\u6811\u56ed\u6700\u65e9\u5728\u5efa\u90ba\u8ddf\u5730\u65b9\u9ad8\u6821\u5b9e\u9a8c\u6027\u8d28\u7684\u521b\u8bbe\u53cc\u5bfc\u5e08\u57f9\u517b\u673a\u5236\uff0c\u5c06\u4fe1\u606f\u5de5\u7a0b\u3001\u5fae\u7535\u5b50\u7b49\u76f8\u5173\u4e13\u4e1a\u7684\u4f18\u79c0\u5b66\u751f\u5728\u6821\u671f\u95f4\u5c31\u5f15\u8fdb\u5230\u6a61\u6811\u56ed\u7684\u5b9e\u9a8c\u5ba4\u91cc\u8fdb\u884c\u5b9e\u4e60\uff0c\u7531\u6a61\u6811\u56ed\u9ad8\u85aa\u8058\u8bf7\u7684\u4e2d\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\u4f5c\u4e3a\u7b2c\u4e8c\u5bfc\u5e08\u8fdb\u884c\u5b66\u672f\u4e0e\u6280\u672f\u4e0a\u7684\u6307\u5bfc\uff0c\u8fd9\u6781\u5927\u7684\u5f25\u8865\u4e86\u4e2d\u56fd\u9ad8\u6821\u5728\u57f9\u517b\u4e2d\u9ad8\u7aef\u6280\u672f\u4eba\u624d\u4e0a\u7684\u4e0d\u8db3\u3002</p><p>\u3000\u3000\u968f\u7740\u6a61\u6811\u56ed\u5728\u5176\u4ed6\u4e2d\u5fc3\u57ce\u5e02\u8bbe\u7acb\u5206\u652f\u7814\u7a76\u673a\u6784\uff0c\u81ea\u7136\u4e5f\u5c06\u53cc\u5bfc\u5e08\u57f9\u517b\u673a\u5236\u5e26\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u4e09\u5e74\u7684\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u53cc\u5bfc\u5e08\u57f9\u517b\u673a\u5236\u4e5f\u5230\u4e86\u53ef\u4ee5\u6536\u83b7\u7684\u5b63\u8282\uff0c\u6a61\u6811\u56ed\u6b64\u6b21\u65b0\u589e\u7684\u4e24\u5343\u540d\u65b0\u4eba\u4e2d\u8d85\u8fc760\uff05\u90fd\u662f\u53cc\u5bfc\u5e08\u5236\u57f9\u517b\u51fa\u6765\u7684\u5e94\u5c4a\u6bd5\u4e1a\u751f\uff0c\u4ed6\u4eec\u5728\u6821\u671f\u95f4\u5c31\u5b9e\u9645\u5b8c\u6210\u4e86\u521d\u7ea7\u7814\u7a76\u5458\u5e94\u6709\u7684\u57f9\u517b\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u6b64\u524d\u6b63\u5f0f\u7684\u7814\u7a76\u5458\u624d\u56db\u5343\u4eba\uff0c\u4f46\u662f\u8fd9\u79cd\u7c7b\u4f3c\u84c4\u517b\u673a\u5236\u7684\u53cc\u5bfc\u5e08\u5236\uff0c\u4f7f\u6a61\u6811\u56ed\u7684\u5b9e\u9645\u5de5\u4f5c\u4eba\u5458\u8fdc\u8fdc\u8d85\u8fc7\u9526\u6e56\u5b98\u65b9\u516c\u5e03\u7684\u6570\u5b57\uff0c\u5e38\u5e38\u4f7f\u5916\u754c\u4f4e\u4f30\u9526\u6e56\u7684\u7814\u53d1\u5b9e\u529b\u3002\u674e\u5728\u6d19\u773c\u775b\u5e38\u5e74\u76ef\u7740\u9526\u6e56\uff0c\u8fd9\u4e24\u5e74\u6bcf\u5e74\u5927\u534a\u7684\u65f6\u95f4\u90fd\u7559\u5728\u5efa\u90ba\uff0c\u7422\u78e8\u900f\u9526\u6e56\u7684\u4e00\u4e9b\u7384\u673a\uff0c\u4e5f\u4f1a\u4f7f\u4e00\u4e9b\u4e0d\u5165\u6d41\u7684\u624b\u6bb5\u6765\u523a\u63a2\u9526\u6e56\u7684\u5546\u4e1a\u673a\u5bc6\uff0c\u77e5\u9053\u6a61\u6811\u56ed\u4eca\u5e74\u4e0a\u534a\u5e74\u5728\u56fd\u5185\u63d0\u4ea4\u7533\u8bf7\u7684\u53d1\u660e\u4e13\u5229\u8d85\u8fc7\u4e24\u5343\u516d\u767e\u9879\uff0c\u5728\u6d77\u5916\u63d0\u4ea4\u7533\u8bf7\u7684\u53d1\u660e\u4e13\u5229\u8d85\u8fc7\u516b\u767e\u9879\u3002</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ece\u9526\u6e56\u72ec\u529b\u5f00\u53d1\u6570\u5b57\u624b\u673a\u57fa\u5e26\u82af\u7247\u6280\u672f\u4e4b\u540e\uff0c\u5c31\u6709\u4e13\u95e8\u7684\u56fd\u9645\u5206\u6790\u673a\u6784\u5f00\u59cb\u5173\u6ce8\u9526\u6e56\u7684\u7814\u53d1\u5b9e\u529b\uff0c\u4f46\u662f\u9526\u6e56\u6bd5\u7adf\u662f\u521a\u5d1b\u8d77\u7684\u65b0\u5174\u79d1\u6280\u4f01\u4e1a\uff0c\u516c\u5f00\u62ab\u9732\u7684\u6570\u636e\u8ddf\u8d44\u6599\u5f88\u5c11\uff1b\u4e5f\u8bb8\u662f\u9526\u6e56\u523b\u610f\u800c\u4e3a\u4e4b\u3002\u867d\u7136\u8fd9\u4e9b\u6570\u636e\u4e0e\u6d88\u606f\u7684\u62ab\u9732\u80fd\u589e\u52a0\u9526\u6e56\u65d7\u4e0b\u54c1\u724c\u7684\u5f71\u54cd\u529b\uff0c\u4f46\u662f\u54c1\u724c\u4e0e\u5546\u8a89\u7684\u5851\u9020\u662f\u9879\u76f8\u5f53\u590d\u6742\u7684\u7cfb\u7edf\u5de5\u7a0b\uff0c\u6240\u8c13\u8fc7\u72b9\u4e0d\u8db3\uff0c\u9526\u6e56\u5728\u8fd9\u65b9\u9762\u8fd8\u662f\u5f88\u80fd\u62ff\u634f\u5206\u5bf8\u3002</p><p>\u3000\u3000\u9664\u975e\u50cf\u674e\u5728\u6d19\u4e13\u95e8\u7741\u7740\u4e00\u53ea\u773c\u775b\u76ef\u7740\u9526\u6e56\uff0c\u4e0d\u7136\u5f88\u96be\u6e05\u9192\u7684\u77e5\u9053\u9526\u6e56\u7684\u5e95\u7ec6\u3002</p><p>\u3000\u3000\u9526\u6e56\u5f53\u521d\u4e3a\u5168\u7403\u62db\u52df\u4e2d\u9ad8\u7ea7\u6280\u672f\u4eba\u5458\u629b\u51fa\u7684\u201c\u767e\u4eba\u8ba1\u5212\u201d\u5df2\u7ecf\u662f\u76f8\u5f53\u60ca\u8273\u4e86\uff0c\u5b9e\u9645\u4e0a\uff0c\u5305\u62ec\u4e2d\u6676\u5fae\u82af\u5f15\u8fdb\u7684\u4e2d\u9ad8\u7ea7\u4e13\u5bb6\uff0c\u9526\u6e56\u7684\u201c\u767e\u4eba\u8ba1\u5212\u201d\u6240\u52df\u96c6\u5230\u7684\u4e2d\u9ad8\u7ea7\u6280\u672f\u4eba\u5458\u5c06\u8fd1\u5343\u4eba\u3002\u9526\u6e56\u4e3a\u6b64\u989d\u5916\u652f\u4ed8\u7684\u798f\u5229\u6210\u672c\u5c31\u8d85\u8fc7\u4e00\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u5728\u5bf9\u5916\u9762\u5a92\u4f53\u7684\u516c\u5f00\u5ba3\u4f20\u4e2d\u58f0\u79f0\u4eca\u5e74\u6574\u4e2a\u6a61\u6811\u56ed\u7814\u7a76\u4f53\u7cfb\u7684\u8d44\u91d1\u9884\u7b97\u4e3a\u56db\u4ebf\u7f8e\u5143\u3002\u4e2d\u56fd\u4f01\u4e1a\u5728\u7814\u53d1\u4e0a\u5982\u6b64\u5927\u624b\u7b14\u7684\u6295\u5165\u5df2\u7ecf\u8ba9\u4eba\u77a0\u76ee\u7ed3\u820c\u4e86\uff0c\u5916\u754c\u9884\u8ba1\u7231\u8fbe\u96c6\u56e2\u4e24\u5343\u5e74\u7684\u9500\u552e\u6536\u5165\u5c06\u7a81\u7834\u4e09\u767e\u4ebf\uff0c\u7814\u53d1\u6295\u5165\u5360\u9500\u552e\u6536\u5165\u7684\u6bd4\u4f8b\u8d85\u8fc710\uff05\uff0c\u5c31\u5df2\u7ecf\u662f\u56fd\u9645\u4e00\u6d41\u79d1\u6280\u7684\u6280\u672f\u6295\u5165\u6c34\u5e73\u3002\u5c31\u7b97\u4e09\u661f\u7535\u5b50\u4e00\u5411\u90fd\u820d\u5f97\u5728\u7814\u53d1\u82b1\u5927\u672c\u94b1\uff0c\u4e24\u5343\u5e74\u7684\u7814\u53d1\u9884\u7b97\u4e5f\u4ec5\u4ec5\u5360\u9500\u552e\u6536\u5165\u76848\uff05\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e00\u4e9b\u5206\u6790\u673a\u6784\u81ea\u4ee5\u4e3a\u662f\u7684\u8ba4\u4e3a\u5df2\u7ecf\u9ad8\u4f30\u4e86\u9526\u6e56\u5728\u6280\u672f\u7814\u53d1\u4e0a\u7684\u51b3\u5fc3\uff0c\u674e\u5728\u6d19\u5374\u6e05\u695a\u56db\u4ebf\u7f8e\u5143\u7684\u7814\u53d1\u9884\u7b97\u53ea\u662f\u9526\u6e56\u653e\u51fa\u7684\u70df\u96fe\u5f39\u3002\u4ed6\u5206\u6790\u8fc7\u7231\u8fbe\u5206\u5f00\u7684\u8d22\u52a1\u6570\u636e\uff0c\u9526\u6e56\u5728\u7535\u5b50\u4ea7\u4e1a\u7814\u53d1\u4e0a\u7684\u6295\u5165\uff0c\u53bb\u5e74\u5f88\u6709\u53ef\u80fd\u5c31\u5df2\u7ecf\u4f1a\u8d85\u8fc7\u56db\u4ebf\u7f8e\u5143\uff0c\u4eca\u5e74\u5728\u7535\u5b50\u4ea7\u4e1a\u7814\u53d1\u4e0a\u7684\u6295\u5165\u751a\u81f3\u6709\u53ef\u80fd\u8d85\u8fc7\u516b\u4ebf\u7f8e\u5143\uff0c\u8fd9\u4e0d\u628a\u4e0a\u5e02\u516c\u53f8\u7231\u8fbe\u7535\u5b50\u5728\u789f\u673a\u9886\u57df\u4ee5\u53ca\u4e2d\u6676\u5fae\u82af\u5728\u6676\u5706\u5236\u7a0b\u65b0\u5de5\u827a\u7814\u53d1\u4e0a\u7684\u6295\u5165\u8ba1\u7b97\u5728\u5185\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u9526\u6e56\u5546\u4e8b\u627f\u62c5\u4e86\u9526\u6e56\u5728\u6d77\u5916\u7684\u878d\u8d44\u91cd\u4efb\uff0c\u9526\u6e56\u5546\u4e8b\u5fc5\u987b\u5411\u878d\u8d44\u5bf9\u8c61\u516c\u5e03\u4e00\u4e9b\u7ecf\u4e25\u683c\u5ba1\u67e5\u7684\u6838\u5fc3\u8d22\u52a1\u6570\u636e\uff0c\u4e09\u661f\u7684\u60c5\u62a5\u641c\u7d22\u90e8\u95e8\u8fd8\u7b97\u79f0\u804c\uff0c\u518d\u52a0\u4e0a\u7231\u8fbe\u96c6\u56e2\u7684\u4ea7\u54c1\u6570\u636e\u5f88\u96be\u63a9\u9970\uff0c\u6240\u4ee5\u4e0d\u96be\u4f30\u7b97\u51fa\u7231\u8fbe\u96c6\u56e2\u5b9e\u9645\u80fd\u83b7\u5f97\u7684\u73b0\u91d1\u6d41\u3002</p><p>\u3000\u3000\u5c31\u7b97\u4f1a\u6709\u76f8\u5f53\u4e00\u90e8\u5206\u7684\u6d77\u5916\u5229\u6da6\u8981\u7ed9\u9526\u6e56\u5546\u4e8b\u622a\u7559\uff0c\u4f46\u662f\u7231\u8fbe\u96c6\u56e2\u4e5d\u4e5d\u5e74\u5728\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u83b7\u5f97\u7684\u5229\u6da6\u5c31\u8d85\u8fc7\u4e94\u4ebf\u7f8e\u5143\uff0c\u4eca\u5e74\u7684\u589e\u6da8\u5e45\u5ea6\u9884\u8ba1\u4e0d\u4f1a\u4f4e\u4e8e50\uff05\uff0c\u518d\u52a0\u4e0a\u7231\u8fbe\u96c6\u56e2\u8fd8\u6709\u8bb8\u591a\u9690\u6027\u6536\u5165\uff0c\u6d41\u5165\u7684\u73b0\u91d1\u6d41\u91cf\u5c06\u5341\u5206\u7684\u5e9e\u5927\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u65e9\u6ce8\u610f\u5230\u9526\u6e56\u90fd\u5341\u5206\u4e25\u8c28\u7684\u4fdd\u8bc1\u6d41\u5165\u7684\u8d44\u91d1\u90fd\u4f9b\u7231\u8fbe\u96c6\u56e2\u81ea\u8eab\u4f7f\u7528\uff0c\u50cf\u4e16\u7eaa\u9526\u6e56\u3001\u65b0\u5149\u7eb8\u4e1a\u3001\u76db\u946b\u7535\u5668\u7b49\u9526\u6e56\u6210\u5458\u4f01\u4e1a\u4ece\u4e5d\u516b\u5e74\u540e\u5c31\u5f88\u591a\u83b7\u5f97\u7231\u8fbe\u7684\u8d44\u91d1\u652f\u6301\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u7231\u8fbe\u96c6\u56e2\u5728\u5de5\u5382\u5b9e\u4f53\u4e0a\u7684\u6295\u5165\u4e0a\u4e00\u5411\u90fd\u504f\u4fdd\u5b88\uff0c\u5c31\u50cf\u7231\u8fbe\u5728\u624b\u673a\u4eca\u5e74\u7684\u9500\u91cf\u751a\u81f3\u53ef\u6709\u7a81\u7834\u5343\u4e07\u53f0\u7684\u60c5\u51b5\u4e0b\u800c\u65d7\u4e0b\u5de5\u5382\u4eca\u5e74\u7684\u624b\u673a\u4ea7\u80fd\u6269\u5f20\u8ba1\u5212\u53ea\u6709\u516b\u767e\u4e07\u53f0\u2014\u2014\u6240\u4ee5\u8fd8\u662f\u80fd\u591f\u4f30\u7b97\u51fa\u9526\u6e56\u5728\u7535\u5b50\u4ea7\u4e1a\u7814\u7a76\u4e0a\u7684\u771f\u6b63\u9884\u7b97\u3002</p><p>\u3000\u3000\u5728\u9526\u6e56\u4e4b\u524d\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u5728\u90a3\u4e9b\u5bf9\u9526\u6e56\u7f3a\u4e4f\u8db3\u591f\u4e86\u89e3\u7684\u4eba\uff0c\u5f88\u96be\u60f3\u8c61\u4e00\u5bb6\u4e2d\u56fd\u4f01\u4e1a\u5728\u7814\u53d1\u4f53\u7cfb\u6295\u5165\u4e0a\u6709\u5982\u6b64\u6c14\u52bf\u78c5\u7934\u7684\u51b3\u5fc3\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u5173\u952e\u8fd8\u662f\u9526\u6e56\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e0a\u592a\u8d5a\u94b1\u4e86\uff0c\u5373\u4f7f\u662f\u5982\u6b64\u9ad8\u6bd4\u4f8b\u7684\u79d1\u7814\u6295\u5165\uff0c\u4e5f\u4e0d\u4f1a\u9020\u6210\u591a\u5927\u7684\u8d44\u91d1\u538b\u529b\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u7535\u5b50\u4ea7\u4e1a\u4e0a\u7684\u91ce\u5fc3\u8fd8\u771f\u662f\u9a87\u4eba\u542c\u95fb\u554a\uff0c\u674e\u5728\u6d19\u5fc3\u601d\u6c89\u91cd\uff0c\u7a0d\u7a0d\u5f80\u524d\u5750\u4e86\u4e9b\uff0c\u62cd\u4e86\u62cd\u91d1\u5357\u52c7\u7684\u80a9\u8180\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8ba4\u4e3a\u9526\u6e56\u6709\u591a\u5927\u7684\u91ce\u5fc3\uff0c\u662f\u4ee5\u6211\u4eec\u4e09\u661f\u4e3a\u76ee\u6807\uff0c\u8fd8\u662f\u4ee5\u7d22\u5c3c\u4e3a\u76ee\u6807\uff1f\u201d</p><p>\u3000\u3000\u4e24\u5343\u5e74\uff0c\u7d22\u5c3c\u8fd8\u662f\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u7684\u5f53\u7136\u738b\u8005\uff1b\u674e\u5728\u6d19\u81ea\u8be9\u4e09\u661f\u7535\u5b50\u5df2\u7ecf\u5f88\u5f3a\uff0c\u4f46\u662f\u6b64\u65f6\u5728\u7d22\u5c3c\u9762\u524d\u8fd8\u53ea\u80fd\u4fef\u9996\u79f0\u81e3\uff0c\u5c31\u7b97\u7d22\u5c3c\uff0c\u4e4b\u524d\u8ddf\u9526\u6e56\u76f8\u4e89\u4e5f\u6709\u789f\u673a\u673a\u82af\u7684\u6ed1\u94c1\u6cf8\u4e4b\u8d25\uff0c\u6b64\u65f6\u5728\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u5e02\u573a\uff0c\u7279\u522b\u662f\u4fbf\u643a\u5f0f\u6570\u5b57\u97f3\u4e50\u64ad\u653e\u5668\u5e02\u573a\u4e5f\u8282\u8282\u8d25\u9000\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u6ca1\u6709\u56de\u7b54\u674e\u5728\u6d19\u7684\u8fd9\u4e2a\u95ee\u9898\uff0c\u73b0\u5728\u660e\u773c\u4eba\u90fd\u80fd\u770b\u51fa\u9526\u6e56\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u4e0a\u7684\u91ce\u5fc3\uff0c\u4e2d\u56fd\u6392\u540d\u7b2c\u4e00\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4f01\u4e1a\u5df2\u7ecf\u8fdc\u8fdc\u4e0d\u80fd\u6ee1\u8db3\u9526\u6e56\u7684\u80c3\u53e3\u4e86\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u9526\u6e56\u5728TFT-LCD\u4e0a\u65e9\u6709\u5e03\u5c40\u4e86\uff0c\u6211\u6536\u96c6\u4e86\u4e00\u4efd\u540d\u5355\uff0c\u53d1\u73b0\u9526\u6e56\u4ece\u4e5d\u4e03\u5e74\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u65f6\u671f\u5c31\u6709\u610f\u8bc6\u7684\u5728\u62db\u52df\u73b0\u4ee3\u534a\u5bfc\u4f53\u6db2\u6676\u90e8\u95e8\u7684\u79bb\u804c\u4eba\u5458\uff0c\u5176\u2018\u767e\u4eba\u8ba1\u5212\u2019\u8fd8\u62db\u52df\u4e86\u591a\u540d\u6db2\u6676\u6280\u672f\u9886\u57df\u7684\u9876\u5c16\u4e13\u5bb6\u2026\u2026\u9526\u6e56\u65d7\u4e0b\u7684\u6676\u5c4f\u7535\u5b50\u751f\u4ea7STN\u6db2\u6676\uff0c\u4f46\u662f\u5728\u6574\u4e2a\u6db2\u6676\u4ea7\u4e1a\u91cc\uff0cSTN\u6280\u672f\u662f\u9762\u4e34\u88ab\u6dd8\u6c70\u3001\u53d1\u5c55\u524d\u9014\u76f8\u5f53\u6709\u9650\u7684\u6280\u672f\uff0c\u4e0d\u503c\u5f97\u5982\u6b64\u5927\u89c4\u6a21\u3001\u5927\u6c14\u529b\u7684\u6295\u5165\u3002\u9526\u6e56\u8fd9\u6b21\u5411\u73b0\u4ee3\u534a\u5bfc\u4f53\u63d0\u51fa\u6536\u8d2d\u5176TFT\u6db2\u6676\u90e8\u95e8\u7684\u8981\u7ea6\uff0c\u5bf9\u4e1a\u5185\u6765\u8bf4\u9887\u4e3a\u7a81\u7136\uff0c\u6211\u4ee5\u4e3a\u9526\u6e56\u53ef\u80fd\u65e9\u5c31\u51c6\u5907\u59a5\u5f53\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff1f\u201d\u674e\u5728\u6d19\u8bf4\u9053\uff0c\u201c\u4f60\u5c06\u4f60\u7684\u731c\u6d4b\u8bf4\u8bf4\u770b\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6000\u7591\u9526\u6e56\u5df2\u7ecf\u638c\u63e1\u4e86TFT\u7684\u76f8\u5173\u6280\u672f\uff0c\u5c31\u7b97\u9526\u6e56\u51fa\u4eba\u610f\u6599\u7684\u5ba3\u5e03\u638c\u63e1\u4e86\u624b\u673a\u57fa\u5e26\u6280\u672f\u4e00\u6837\uff0c\u73b0\u5728\u6700\u5927\u7684\u53ef\u80fd\u5c31\u662f\u65e5\u672c\u8ddf\u6211\u4eec\u97e9\u56fd\u591a\u5bb6\u4f01\u4e1a\u5728TFT\u6db2\u6676\u4ea7\u4e1a\u4e0a\u5e03\u5c40\u591a\u5e74\uff0c\u6709\u9526\u6e56\u7ed5\u4e0d\u8fc7\u7684\u4e13\u5229\u9677\u9631\uff0c\u4ed6\u4eec\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u6db2\u6676\u90e8\u95e8\u4e5f\u8bb8\u53ea\u662f\u4e3a\u4e86\u4e70\u4e00\u5f20\u5165\u95e8\u8bc1\u2026\u2026\u201d\u91d1\u5357\u52c7\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u4e48\u8ba4\u4e3a\uff1f\u201d\u674e\u5728\u6d19\u9661\u7136\u5750\u76f4\u8eab\u5b50\uff0c\u8fd9\u79cd\u731c\u6d4b\u8ddf\u4e4b\u524d\u7684\u9884\u6d4b\u6709\u5f88\u5927\u7684\u4e0d\u540c\uff0c\u4e4b\u524d\u9884\u6d4b\u9526\u6e56\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u6db2\u6676\u90e8\u95e8\u4e4b\u540e\u8fd8\u5fc5\u987b\u4f9d\u8d56\u97e9\u56fd\u7684\u7814\u53d1\u53ca\u751f\u4ea7\u4f53\u7cfb\uff0c\u9526\u6e56\u60f3\u8981\u5f7b\u5e95\u878d\u5408\u97e9\u56fd\u7684\u7814\u53d1\u53ca\u751f\u4ea7\u4f53\u7cfb\u7edd\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u5c31\u7b97\u7ed9\u9526\u6e56\u6536\u8d2d\u6210\u529f\uff0c\u8fd8\u662f\u53ef\u4ee5\u8bbe\u7f6e\u5f88\u591a\u7684\u9677\u9631\uff0c\u8981\u662f\u9526\u6e56\u65e9\u5c31\u505a\u597d\u6280\u672f\u4e0a\u7684\u51c6\u5907\uff0c\u8fd9\u6b21\u53ea\u4e3a\u4e00\u5f20\u5165\u95e8\u8bc1\u7684\u8bdd\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u9526\u6e56\u5bf9\u97e9\u56fd\u6db2\u6676\u7814\u53d1\u53ca\u751f\u4ea7\u4f53\u7cfb\u7684\u4f9d\u8d56\u964d\u5230\u6700\u4f4e\uff0c\u60f3\u8981\u4e8b\u540e\u518d\u52a8\u4ec0\u4e48\u624b\u811a\u5c31\u96be\u4e86\u3002</p><p>\u3000\u3000\u201c\u53ef\u80fd\u6027\u5f88\u5927\u3002\u201d\u91d1\u5357\u52c7\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u6709\u8fd9\u4e48\u5927\u7684\u51b3\u5fc3\uff0c\u96be\u9053\u4ed6\u4eec\u5c31\u575a\u5b9a\u7684\u8ba4\u4e3aTFT\u6db2\u6676\u6280\u672f\u4f1a\u6700\u7ec8\u538b\u5236\u7b49\u79bb\u5b50\u5c4f\u6280\u672f\uff1f\u201d\u674e\u5728\u6d19\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u9526\u6e56\u4e00\u5f00\u59cb\u5c31\u662f\u4ee5\u6211\u4eec\u4e09\u661f\u6216\u8005\u7d22\u5c3c\u4e3a\u8ffd\u8d76\u5bf9\u8c61\uff0c\u4ed6\u5728TFT\u6db2\u6676\u6280\u672f\u4e0e\u7b49\u79bb\u5b50\u5c4f\u6280\u672f\u4e0a\u5e76\u6ca1\u6709\u9009\u62e9\u7684\u4f59\u5730\u2026\u2026\u201d\u91d1\u5357\u52c7\u8bf4\u9053\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u70b9\u70b9\u5934\uff0c\u77e5\u9053\u91d1\u5357\u52c7\u7684\u610f\u601d\u3002TFT\u6db2\u6676\u5728\u5927\u5c4f\u5316\u4e0a\u5b58\u5728\u6280\u672f\u96be\u5173\uff0c\u7b49\u79bb\u5b50\u5c4f\u5374\u5728\u5c0f\u5c4f\u5316\u4e0a\u5b58\u5728\u6280\u672f\u96be\u5173\uff0c\u9526\u6e56\u73b0\u5728\u8fd8\u662f\u4ee5\u624b\u673a\u4e3a\u6838\u5fc3\u4ea7\u4e1a\uff0c\u5728\u6676\u5c4f\u6280\u672f\u9009\u62e9\u4e0a\uff0c\u9009\u62e9\u5bb9\u6613\u5b9e\u9645\u5c0f\u5c4f\u5316\u7684TFT\u6db2\u6676\u663e\u793a\u6280\u672f\u662f\u5f53\u7136\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u201c\u5373\u4f7f\u5982\u6b64\uff0c\u4e5f\u6ca1\u6709\u963b\u6321\u7684\u624b\u6bb5\u554a\u3002\u201d\u674e\u5728\u6d19\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u73b0\u4ee3\u534a\u5bfc\u4f53\u77e5\u9053\u8fd9\u662f\u9526\u6e56\u7684\u5e95\u7ec6\uff0c\u4e5f\u8bb8\u4f1a\u6709\u4e9b\u4f5c\u7528\u2026\u2026\u201d\u91d1\u5357\u52c7\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u4f60\u9a6c\u4e0a\u5e2e\u6211\u8054\u7cfb\u6734\u771f\u660c\uff0c\u4e0d\u7ba1\u771f\u5b9e\u60c5\u51b5\u7a76\u7adf\u5982\u4f55\uff0c\u4e5f\u5e94\u8be5\u8ba9\u6734\u771f\u660c\u76f8\u4fe1\u6709\u8fd9\u79cd\u53ef\u80fd\u2026\u2026\u201d\u674e\u5728\u6d19\u8bf4\u9053\u3002</p><p>\u3000\u3000\u4e4b\u524d\u73b0\u4ee3\u534a\u5bfc\u4f53\u5e55\u540e\u7684\u503a\u6743\u4eba\u62c5\u4efb\u65f6\u95f4\u62d6\u4e45\u4f1a\u9020\u6210\u6db2\u6676\u751f\u4ea7\u7ebf\u4ee5\u53ca\u6db2\u6676\u6280\u672f\u7b49\u6709\u5f62\u3001\u65e0\u5f62\u8d44\u4ea7\u7684\u8d2c\u503c\uff0c\u6240\u4ee5\u4e5f\u6709\u65e9\u65e5\u4fc3\u6210\u4ea4\u6613\u7684\u671f\u5f85\uff1b\u8981\u662f\u4ed6\u4eec\u786e\u4fe1\u9526\u6e56\u8d2a\u56fe\u7684\u4e0d\u662f\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u7814\u53d1\u4e0e\u751f\u4ea7\u4f53\u7cfb\uff0c\u552f\u6709\u90a3\u5f20\u8fdb\u95e8\u8bc1\u5bf9\u9526\u6e56\u6709\u5173\u952e\u6027\u7684\u4ef7\u503c\uff0c\u62d6\u5ef6\u65f6\u95f4\u7684\u98ce\u9669\u76f8\u5bf9\u5c31\u964d\u5230\u6700\u4f4e\u3002</p><p>\u3000\u3000\u4e09\u661f\u662f\u5e0c\u671b\u73b0\u4ee3\u534a\u5bfc\u4f53\u80fd\u62d6\u4e00\u62d6\u65f6\u95f4\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u79d8\u4e66\u8dd1\u8fc7\u6765\u8033\u8bed\u674e\u5728\u6d19\u79bb\u5f00\u4e0d\u4e45\u53c8\u6253\u6765\u7535\u8bdd\uff0c\u6734\u771f\u660c\u5fc3\u751f\u7591\u60d1\uff0c\u8fd8\u662f\u4e0d\u52a8\u58f0\u8272\u7684\u79bb\u573a\u5230\u9694\u58c1\u7684\u4e66\u623f\u53bb\u63a5\u674e\u5728\u6d19\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u770b\u7740\u6734\u771f\u660c\u8d70\u8fdb\u9694\u58c1\u7684\u623f\u95f4\u53bb\u63a5\u7535\u8bdd\uff0c\u5f20\u606a\u5fae\u5fae\u4fa7\u8fc7\u5934\uff0c\u8ddf\u5f20\u4e9a\u5e73\u5c0f\u58f0\u7684\u8bf4\uff1a\u201c\u8fd9\u4e9b\u5438\u8840\u9b3c\u771f\u4e0d\u597d\u6253\u53d1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u7b11\u4e86\u7b11\uff0c\u4ed6\u5750\u7684\u89d2\u5ea6\u53ef\u4ee5\u901a\u8fc7\u95e8\u7f1d\u770b\u5230\u6734\u771f\u660c\u901a\u7535\u8bdd\u7684\u60c5\u5f62\uff0c\u867d\u7136\u542c\u4e0d\u6e05\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff0c\u4f46\u662f\u770b\u5230\u4ed6\u8138\u4e0a\u8fdf\u7591\u4e0d\u5b9a\u7684\u7aa5\u63a2\u8fd9\u8fb9\uff0c\u8fd9\u901a\u7535\u8bdd\u4f3c\u4e4e\u8ddf\u4ed6\u4eec\u6709\u5173\uff0c\u5c0f\u58f0\u7684\u544a\u8bc9\u5f20\u606a\uff1a\u201c\u4e0d\u6653\u5f97\u662f\u4e0d\u662f\u4e34\u65f6\u5f97\u5230\u4e0d\u5229\u6211\u4eec\u7684\u4fe1\u606f\u4e86\uff0c\u4ed6\u7684\u773c\u795e\u6709\u4e9b\u8b66\u60d5\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u5fae\u772f\u8d77\u773c\u775b\uff0c\u6084\u7136\u8bf4\u9053\uff0c\u201c\u6211\u5df2\u7ecf\u6709\u7ed9\u4ed6\u4eec\u6572\u4e00\u7b14\u7684\u89c9\u609f\u3002\u6211\u60f3\u4e86\u60f3\uff0c\u4ed6\u4eec\u8fd8\u60f3\u518d\u8981\u62ac\u9ad8\u6536\u8d2d\u4ef7\uff0c\u4e5f\u4e0d\u662f\u4e0d\u53ef\u8c08\uff0c\u4f46\u662f\u6211\u4eec\u4e5f\u9700\u8981\u4ed6\u4eec\u8bbe\u8eab\u5904\u5730\u7684\u4e3a\u6211\u4eec\u8003\u8651\u4e00\u4e0b\u8d44\u91d1\u4e0a\u7684\u538b\u529b\uff0c\u6211\u60f3\u4ed6\u4eec\u8fd9\u4e9b\u505a\u91d1\u878d\u7684\uff0c\u603b\u662f\u9700\u8981\u5ba2\u6237\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u70b9\u70b9\u5934\uff0c\u5728\u65e2\u5b9a\u7684\u65b9\u6848\u4e2d\uff0c\u8c08\u5230\u4e00\u90e8\u5206\u6536\u8d2d\u8d44\u91d1\u7531\u4ee5\u671d\u5174\u94f6\u884c\u4e3a\u9996\u7684\u51e0\u5bb6\u503a\u6743\u94f6\u884c\u63d0\u4f9b\u8d37\u6b3e\uff0c\u8981\u662f\u5bf9\u65b9\u63d0\u9ad8\u6536\u8d2d\u4ef7\u7801\uff0c\u4ed6\u4eec\u76f8\u5e94\u7684\u53ef\u4ee5\u8981\u6c42\u503a\u6743\u94f6\u884c\u63d0\u9ad8\u8d37\u6b3e\u989d\u5ea6\u4ee5\u51b2\u9500\u8d44\u91d1\u4e0a\u7684\u538b\u529b\u3002\u81f3\u4e8e\u62b5\u62bc\u6240\u9700\u8981\u7684\u62b5\u62bc\uff0c\u5b8c\u5168\u53ef\u4ee5\u62ff\u6536\u8d2d\u540e\u7684\u56fa\u5b9a\u8d44\u4ea7\u505a\u62b5\u62bc\u3002</p><p>\u3000\u3000\u6734\u771f\u660c\u65e0\u6cd5\u786e\u5b9a\u674e\u5728\u6d19\u7684\u5224\u65ad\uff0c\u6bd5\u7adf\u674e\u5728\u6d19\u4e0d\u53ef\u80fd\u5c06\u4e09\u661f\u60c5\u62a5\u641c\u7d22\u90e8\u95e8\u6536\u96c6\u5230\u7684\u6709\u5173\u8d44\u6599\u8ddf\u6570\u636e\u6367\u5230\u4ed6\u7684\u9762\u524d\u3002\u6734\u771f\u660c\u751a\u81f3\u6000\u7591\u674e\u5728\u6d19\u60f3\u6076\u610f\u7684\u7834\u574f\u8fd9\u6b21\u6536\u8d2d\u4ea4\u6613\uff0c\u4ed6\u5b8c\u5168\u6709\u7406\u7531\u76f8\u4fe1\u4e09\u661f\u7684\u4eba\u4f1a\u8fd9\u4e48\u5e72\uff1b\u5f53\u7136\uff0c\u674e\u5728\u6d19\u6240\u8bf4\u7684\u53ef\u80fd\u6027\u4e5f\u7684\u786e\u5b58\u5728\u3002\u9526\u6e56\u4e0d\u52a8\u58f0\u606f\u7684\u7814\u7a76\u51fa\u624b\u673a\u57fa\u5e26\u82af\u7247\u6280\u672f\u5c31\u4ee4\u4eba\u5927\u5403\u4e00\u60ca\uff0c\u8981\u77e5\u9053\u4e09\u661f\u7535\u5b50\u5728\u57fa\u5e26\u82af\u7247\u6280\u672f\u9762\u524d\u66fe\u4e24\u5ea6\u6298\u621f\u3002</p><p>\u3000\u3000\u6734\u771f\u660c\u4e0d\u4f1a\u7acb\u5373\u5c31\u8bd5\u63a2\u9526\u6e56\u7684\u53e3\u98ce\uff0c\u6bd5\u7adf\u4ed6\u53ea\u80fd\u4ee3\u8868\u97e9\u5174\u94f6\u884c\u4e00\u5bb6\uff0c\u8bf4\u8d77\u6765\u4ed6\u8fd9\u6b21\u662f\u60f3\u501f\u5230\u5317\u4eac\u7684\u4eba\u7269\u89c1\u8bc6\u4e00\u4e0b\u85cf\u5728\u9526\u6e56\u80cc\u540e\u90a3\u4e2a\u5e74\u8f7b\u7684\u5f71\u5b50\uff0c\u8fd9\u4e2a\u5f71\u5b50\u53ef\u662f\u8ba9\u88ab\u97e9\u56fd\u56fd\u6c11\u89c6\u4e3a\u7ecf\u8425\u4e4b\u795e\u7684\u674e\u5065\u7199\u79c1\u4e0b\u91cc\u4e5f\u9887\u4e3a\u6b23\u8d4f\u3002</p><p>\u3000\u3000\u60f3\u8d77\u674e\u5728\u6d19\u7684\u62dc\u8bbf\uff0c\u6734\u771f\u660c\u5634\u89d2\u5c31\u5fcd\u4e0d\u4f4f\u8981\u9732\u51fa\u7b11\u610f\u4e86\uff0c\u7ed9\u674e\u5065\u7199\u6b23\u8d4f\u53ef\u771f\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u597d\u4e8b\u3002\u867d\u7136\u6765\u5317\u4eac\u4e4b\u524d\u542c\u4e86\u4e00\u4e9b\u5173\u4e8e\u773c\u524d\u8fd9\u9752\u5e74\u7684\u4f20\u95fb\uff0c\u6734\u771f\u660c\u5fc3\u91cc\u6709\u6240\u51c6\u5907\uff0c\u4f46\u662f\u770b\u5230\u5f20\u606a\u4e4b\u540e\uff0c\u5fc3\u91cc\u9707\u61be\u4f9d\u65e7\u5f3a\u70c8\u3002</p><p>\u3000\u3000\u6734\u771f\u660c\u8ddf\u8bb8\u591a\u97e9\u56fd\u4eba\u4e00\u6837\uff0c\u90fd\u6709\u4e9b\u4e2a\u4eba\u82f1\u96c4\u4e3b\u4e49\u60c5\u7ed3\uff0c\u89c9\u5f97\u6709\u4e9b\u4eba\u603b\u662f\u80fd\u6210\u5c31\u4e00\u4e9b\u7279\u6b8a\u7684\u4e8b\u4e1a\uff0c\u8fd8\u662f\u5f88\u96be\u62ff\u9526\u6e56\u7684\u5d1b\u8d77\u8ddf\u773c\u524d\u8fd9\u4e2a\u5e74\u8f7b\u4eba\u8054\u7cfb\u5728\u4e00\u8d77\u3002\u6839\u636e\u97e9\u5174\u94f6\u884c\u7684\u9884\u6d4b\uff0c\u7231\u8fbe\u96c6\u56e2\u5728\u4e2d\u56fd\u4ee5\u53ca\u5728\u4e2d\u56fd\u4e4b\u5916\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e1a\u52a1\u4eca\u5e74\u80fd\u591f\u83b7\u5f97\u7684\u5229\u6da6\u751a\u81f3\u8981\u8d85\u8fc7\u8bb8\u591a\u5728\u4e9a\u6d32\u8d6b\u8d6b\u6709\u540d\u7684\u97e9\u56fd\u7535\u5b50\u4f01\u4e1a\u3002\u7231\u8fbe\u96c6\u56e2\u770b\u4e0a\u53bb\u6709\u4e9b\u5f31\u5c0f\uff0c\u662f\u7231\u8fbe\u96c6\u56e2\u5c06\u5927\u90e8\u5206\u6d77\u5916\u4e1a\u52a1\u653e\u5230\u9526\u6e56\u5546\u4e8b\u7684\u540d\u4e0b\uff0c\u53c8\u6709\u591a\u5bb6\u6d77\u5916\u516c\u53f8\u4e0d\u4f1a\u5bf9\u5916\u516c\u5e03\u8d22\u52a1\u6570\u636e\uff0c\u9690\u85cf\u4e86\u672c\u8be5\u53ef\u4ee5\u5408\u5e76\u5230\u7231\u8fbe\u96c6\u56e2\u65d7\u4e0b\u7684\u6536\u5165\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5982\u6b64\uff0c\u7231\u8fbe\u96c6\u56e2\u4ecd\u7136\u662f\u4e2d\u56fd\u6392\u540d\u7b2c\u4e8c\u7684\u7535\u5b50\u4f01\u4e1a\uff0c\u4ec5\u6b21\u4e8e\u865a\u6709\u5176\u8868\u7684\u534e\u590f\u7535\u5b50\uff1b\u516c\u5e03\u51fa\u6765\u7684\u8d62\u5229\u80fd\u529b\u4e5f\u8fdc\u8fdc\u8d85\u8fc7\u534e\u590f\u7535\u5b50\u3002</p><p>\u3000\u3000\u53e6\u5916\uff0c\u6734\u771f\u660c\u662f\u5229\u76ca\u81f3\u4e0a\u8005\uff0c\u6ca1\u6709\u7279\u522b\u5f3a\u70c8\u7684\u6c11\u65cf\u60c5\u7ed3\u3002\u76f8\u53cd\u7684\uff0c\u4ed6\u5bf9\u6709\u4e9b\u97e9\u56fd\u9752\u5e74\u90a3\u79cd\u591c\u90ce\u81ea\u5927\u3001\u5750\u4e95\u89c2\u5929\u7684\u504f\u6fc0\u601d\u60f3\u611f\u5230\u6709\u4e9b\u8d39\u89e3\u3002\u5728\u4ed6\u770b\u6765\uff0c\u97e9\u56fd\u5728\u5730\u57df\u4e0a\u6839\u672c\u5c31\u7f3a\u4e4f\u6210\u4e3a\u5f3a\u56fd\u7684\u53ef\u80fd\u6027\uff0c\u5939\u6742\u5728\u5927\u56fd\u95f4\u8ffd\u6c42\u81ea\u8eab\u7684\u5229\u76ca\u624d\u662f\u6700\u91cd\u8981\u7684\uff0c\u6ca1\u6709\u5fc5\u8981\u5bf9\u4e2d\u56fd\u53ca\u4e2d\u56fd\u4f01\u4e1a\u7ad6\u8d77\u8fc7\u5206\u8b66\u60d5\u7684\u795e\u7ecf\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8981\u662f\u674e\u5728\u6d19\u7684\u5224\u65ad\u5c5e\u5b9e\uff0c\u6734\u771f\u660c\u4e5f\u4e0d\u4f1a\u5fd8\u4e86\u4ee3\u8868\u97e9\u5174\u94f6\u884c\u6572\u8bc8\u9526\u6e56\u4e00\u7b14\u7684\uff0c\u53ea\u662f\u8fd9\u4e2a\u5224\u65ad\u5bf9\u4ed6\u6765\u8bf4\u8fd8\u6ca1\u6709\u8db3\u591f\u7684\u8bc1\u636e\u652f\u6301\uff0c\u9700\u8981\u56de\u5230\u97e9\u56fd\u540e\u518d\u8ddf\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u5176\u4ed6\u503a\u6743\u4eba\u4ee3\u8868\u4e00\u8d77\u8ba8\u8bba\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5728\u4e0e\u97e9\u5174\u94f6\u884c\u6734\u771f\u660c\u4f1a\u957f\u5171\u540c\u5348\u9910\u4e4b\u540e\uff0c\u4e0b\u5348\u8fd8\u8981\u8ddf\u8054\u901a\u7684\u6768\u84c9\u5e73\u89c1\u9762\u3002</p><p>\u3000\u3000\u4e03\u6708\u4e0a\u65ec\uff0c\u8054\u901a\u5728\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u516c\u5f00\u4e0a\u5e02\u7684\u540c\u65f6\u53c8\u5728\u7f8e\u56fd\u7ebd\u7ea6\u8bc1\u5238\u4ea4\u6613\u6240\u6302\u724c\u4ea4\u6613\uff0c\u4e24\u5730\u4e0a\u5e02\u878d\u8d44\u91d1\u878d\u8d85\u8fc760\u4ebf\u7f8e\u5143\u3002\u8fd9\u662f\u9664\u65e5\u672c\u4f01\u4e1a\u4e4b\u5916\u4e9a\u6d32\u5730\u533a\u6700\u5927\u7684\u4e00\u6b21\u878d\u8d44\u6d3b\u52a8\uff0c\u6210\u4e3a\u4e03\u6708\u6700\u91cd\u8981\u7684\u8d22\u7ecf\u4e8b\u4ef6\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u4e5f\u5e94\u8be5\u8bf4\u7eb3\u6307\u7684\u5f3a\u52bf\u56de\u5347\u7ed9\u8054\u901a\u7684\u6210\u529f\u4e0a\u5e02\u521b\u9020\u4e86\u826f\u597d\u7684\u5916\u56f4\u91d1\u878d\u73af\u5883\uff0c\u6700\u4e3b\u8981\u7684\uff0c\u8054\u901a\u901a\u8fc7\u8fd9\u6b21\u878d\u8d44\u4e00\u4e3e\u89e3\u51b3\u4e86\u4e4b\u524d\u7684\u8d44\u91d1\u9965\u6e34\u75c7\u3002</p><p>\u3000\u3000\u4ece\u5e74\u521d\uff0c\u9526\u6e56\u4e00\u76f4\u9690\u8eab\u5e55\u540e\u63a8\u52a8\u8054\u901a\u4e0e\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u4e24\u5bb6\u4f01\u4e1a\u8fdb\u884c\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u7684\u5408\u4f5c\u3002\u7531\u4e8e\u62c5\u5fe7\u8054\u901aGSM\u7f51\u7edc\u5206\u5e03\u5bc6\u5ea6\u4e0e\u627f\u8f7d\u80fd\u529b\uff0c\u4e5f\u4e3a\u4e86\u907f\u514d\u8fc7\u5ea6\u7684\u523a\u6fc0\u4e2d\u79fb\u52a8\uff0c\u5f20\u606a\u8bf4\u670d\u8054\u901a\u540c\u610f\u653e\u5f03\u4e4b\u524d\u7684\u6fc0\u8fdb\u8ba1\u5212\uff0c\u5148\u671f\u6311\u9009\u4e00\u4e9bGSM\u7f51\u7edc\u8bbe\u5907\u5b8c\u5907\u7684\u4e2d\u5fc3\u57ce\u5e02\u8bd5\u9a8c\u6027\u7684\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u8fc4\u4eca\u6709\u8fd1\u534a\u5e74\u7684\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u8054\u901a\u5206\u522b\u5728\u5317\u4eac\u3001\u5e7f\u4e1c\u3001\u5efa\u90ba\u7b49\u8fd1\u5341\u5ea7\u4e2d\u5fc3\u57ce\u5e02\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u5f00\u5c55\u5404\u79cd\u7c7b\u4f3c\u4e8e\u201c\u5145\u503c\u9001\u624b\u673a\u201d\u7684\u4fc3\u9500\u6d3b\u52a8\u6765\u4fc3\u8fdb\u8be5\u5730\u533a\u7684\u65b0\u7528\u6237\u6210\u957f\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u62db\u975e\u5e38\u7ba1\u7528\uff0c\u5728\u8054\u901a\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u7684\u57ce\u5e02\uff0c\u65b0\u7528\u6237\u7684\u589e\u6da8\u5e45\u5ea6\u6bd4\u4ee5\u524d\u6709\u5927\u5e45\u7684\u63d0\u9ad8\uff0c\u751a\u81f3\u53d1\u5c55\u65b0\u7528\u6237\u901f\u5ea6\u80fd\u8ffd\u4e0a\u79fb\u52a8\u516c\u53f8\u7684\u5c3e\u5df4\u3002</p><p>\u3000\u3000\u5728\u666e\u901a\u4eba\u8fd8\u5c06\u624b\u673a\u5f53\u6210\u5962\u4f88\u54c1\u770b\u5f85\u7684\u65f6\u5019\uff0c\u5145\u503c\u9001\u624b\u673a\u7684\u6d3b\u52a8\u81ea\u7136\u6709\u5f88\u5f3a\u7684\u8bf1\u60d1\u529b\uff0c\u66f4\u662f\u5438\u5f15\u4e86\u8bb8\u591a\u5546\u52a1\u4eba\u58eb\u4ee5\u53ca\u4f01\u4e8b\u4e1a\u5355\u4f4d\u7684\u7ba1\u7406\u5c42\u3002\u8bb8\u591a\u4f01\u4e8b\u4e1a\u5355\u4f4d\u591a\u534a\u4e0d\u4f1a\u62a5\u9500\u8d2d\u4e70\u624b\u673a\u7684\u8d39\u7528\uff0c\u5374\u591a\u534a\u4f1a\u62a5\u9500\u7ba1\u7406\u8005\u6216\u5546\u52a1\u4eba\u58eb\u7684\u901a\u8baf\u8d39\u7528\u2014\u2014\u5546\u52a1\u4eba\u58eb\u4e0e\u4f01\u4e8b\u4e1a\u5355\u4f4d\u7ba1\u7406\u5c42\u53c8\u5386\u6765\u7ed9\u89c6\u4e3a\u79fb\u52a8\u901a\u8baf\u4ea7\u4e1a\u4e2d\u7684\u9ad8\u7aef\u7528\u6237\u7fa4\u4f53\u3002</p><p>\u3000\u3000\u5e74\u521d\u65f6\uff0c\u5f20\u606a\u627f\u8bfa\u5411\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u4f4e\u4ef7\u4f9b\u5e94\u4e00\u767e\u4e07\u5957\u57fa\u9891\u82af\u7247\u7ec4\u7528\u4e8e\u8054\u901a\u5b9a\u5236\u624b\u673a\u7684\u751f\u4ea7\uff0c\u4e00\u767e\u4e07\u53ea\u624b\u673a\u751a\u81f3\u8fd8\u8fdc\u4e0d\u8db3\u4ee5\u5e94\u4ed8\u8054\u901a\u4e2d\u5fc3\u57ce\u5e02\u8bd5\u9a8c\u6027\u7684\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u4e4b\u524d\uff0c\u4e00\u65b9\u9762\u662f\u8ddf\u5f20\u606a\u6709\u7ea6\u5b9a\uff0c\u4e00\u65b9\u9762\u662f\u8054\u901a\u4e5f\u81ea\u77e5\u8d22\u52a1\u7d27\u7f3a\uff0c\u5c31\u7b97\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u53d1\u5c55\u65b0\u7528\u6237\u7684\u624b\u6bb5\u5f88\u6709\u6548\uff0c\u8054\u901a\u65b9\u9762\u4e5f\u514b\u5236\u7740\u4e0d\u63d0\u51fa\u4ec0\u4e48\u522b\u7684\u8981\u6c42\u3002</p><p>\u3000\u3000\u73b0\u5728\u4e0d\u540c\u5f80\u6614\uff0c\u8054\u901a\u4e00\u4e3e\u878d\u8d44\u91d1\u989d\u8d85\u8fc7\u516d\u5341\u4ebf\u7f8e\u5143\uff0c\u5728\u5229\u7528\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u53d1\u5c55\u65b0\u7528\u6237\u65b9\u9762\u4e0d\u518d\u7f3a\u4e4f\u8d44\u91d1\uff0c\u4e3a\u4e86\u5411\u9999\u6e2f\u3001\u7f8e\u56fd\u80a1\u6c11\u4ea4\u4ee3\uff0c\u8054\u901a\u9700\u8981\u6709\u66f4\u597d\u7684\u7528\u6237\u589e\u6da8\u6570\u636e\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u4e4b\u524d\u7ea6\u5b9a\u7684\u534a\u5e74\u65f6\u95f4\u4e5f\u5feb\u5230\u671f\u4e86\uff0c\u5f20\u606a\u5728\u6625\u8282\u524d\u8ddf\u8054\u901a\u627f\u8bfa\u8981\u5728\u534a\u5e74\u540e\u63a8\u51fa\u589e\u5f3a\u578b\u7684\u7b2c\u4e8c\u4ee3\u57fa\u9891\u82af\u7247\u7ec4\u63d0\u4f9b\u7ed9\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u751f\u4ea7\u6027\u80fd\u66f4\u7a33\u5b9a\u3001\u529f\u80fd\u66f4\u5b8c\u5584\u7684\u5b9a\u5236\u624b\u673a\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u5f97\u77e5\u5f20\u606a\u5230\u5317\u4eac\u6765\uff0c\u5c31\u7ea6\u4ed6\u5728\u5317\u4eac\u518d\u89c1\u4e00\u9762\uff0c\u4e5f\u597d\u5bf9\u4e0b\u4e00\u6b65\u7684\u5408\u4f5c\u8ba1\u5212\u80fd\u6709\u4e2a\u521d\u6b65\u7684\u5171\u540c\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u4e0d\u5e0c\u671b\u8054\u901a\u592a\u6025\u5207\uff0c\u65e2\u7136\u4e0d\u518d\u7f3a\u4e4f\u8d44\u91d1\uff0c\u9996\u5148\u8981\u505a\u7684\u4e5f\u5e94\u8be5\u662f\u5c06\u901a\u8baf\u7f51\u7edc\u5148\u5b8c\u5584\u8d77\u6765\uff0c\u6d88\u9664\u4fe1\u53f7\u76f2\u533a\u3001\u63d0\u9ad8\u901a\u8baf\u627f\u8f7d\u5e26\u5bbd\uff0c\u4e4b\u540e\u518d\u8ddf\u4e2d\u79fb\u52a8\u4e00\u51b3\u96cc\u96c4\u4e0d\u8fdf\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u6709\u4e9b\u4e8b\u60c5\u672a\u5fc5\u80fd\u5c3d\u5982\u4eba\u610f\uff0c\u8054\u901a\u7ed9\u79fb\u52a8\u516c\u53f8\u7684\u524d\u8eab\u79fb\u52a8\u901a\u4fe1\u5c40\u7ed9\u538b\u5236\u5f97\u6570\u5e74\uff0c\u5883\u9047\u4e00\u76f4\u90fd\u5f88\u51c4\u51c9\uff0c\u6b64\u4e8b\u6709\u7ffb\u8eab\u7684\u673a\u4f1a\uff0c\u600e\u80fd\u538b\u5236\u5fc3\u5934\u7684\u6fc0\u52a8\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u8054\u901a\u5728\u79fb\u52a8\u901a\u4fe1\u5c40\u7684\u591a\u5e74\u538b\u5236\u4e0b\u751f\u5b58\u4e0b\u6765\uff0c\u4e5f\u662f\u51ed\u501f\u5251\u8d70\u504f\u950b\u7684\u624b\u6bb5\uff0c\u73b0\u5728\u53d1\u8fbe\u4e86\uff0c\u601d\u7ef4\u5374\u5f88\u8f6c\u53d8\u8fc7\u6765\uff0c\u4f9d\u65e7\u5f88\u949f\u60c5\u5251\u8d70\u504f\u950b\u7684\u624b\u6bb5\u3002\u5229\u7528\u5b9a\u5236\u624b\u673a\u7b49\u4f18\u60e0\u653f\u7b56\u8ddf\u79fb\u52a8\u5927\u641e\u4ef7\u683c\u6218\uff0c\u8fd8\u53ea\u662f\u8ddf\u5251\u8d70\u504f\u950b\u7684\u4e00\u4e2a\u4fa7\u9762\uff0c\u4e3b\u8981\u7684\u8fd8\u662f\u8054\u901a\u51b3\u5fc3\u5728GSM\u901a\u8baf\u7f51\u7edc\u4e4b\u5916\u518d\u65b0\u4e00\u4e2a\u5236\u5f0f\u4e0d\u76f8\u5bb9\u7684CDMA\u7f51\u5f3a\u6765\u5bf9\u6297\u79fb\u52a8\u96c6\u56e2\u5c06\u4eceGSM\u7f51\u7edc\u5347\u7ea7\u7684GPRS\u7f51\u7edc\uff0c\u8054\u901a\u8fd9\u6b21\u83b7\u5f97\u516d\u5341\u4ebf\u7f8e\u5143\u7684\u878d\u8d44\uff0c\u5c06\u4e3b\u8981\u7528\u4f5cCDMA\u7f51\u7edc\u7684\u5efa\u8bbe\u4e0e\u8fd0\u8425\u8d39\u7528\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u8fd8\u4e0d\u65e0\u6cd5\u5224\u65ad\u5c06\u8054\u901a\u7684\u6fc0\u8fdb\u8ba1\u5212\u63a8\u5ef6\u534a\u5e74\u80fd\u4ea7\u751f\u591a\u5c11\u6b63\u9762\u7684\u6548\u5e94\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u6e05\u695a\u8981\u662f\u4ec0\u4e48\u90fd\u4e0d\u505a\uff0c\u8054\u901a\u5927\u6982\u5f88\u96be\u6210\u4e3a\u5bf9\u79fb\u52a8\u6709\u6240\u5a01\u80c1\u7684\u7ade\u4e89\u4f01\u4e1a\u3002\u5f20\u606a\u8bb0\u5f97\u5728\u524d\u5c18\u5f80\u4e8b\u91cc\u8054\u901a\u957f\u671f\u4ee5\u6765\u7684\u8d62\u5229\u90fd\u5728\u79fb\u52a8\u5341\u5206\u4e4b\u4e00\u5de6\u53f3\u7684\u6c34\u5e73\u4e0a\u6ce2\u52a8\uff0c\u5b9e\u5728\u5bf9\u4e0d\u8d77\u4ed6\u4eec\u5728\u4e24\u5343\u5e74\u4e4b\u540e\u6240\u83b7\u5f97\u7684\u653f\u7b56\u6276\u6301\uff0c\u4e5f\u5b9e\u5728\u5bf9\u4e0d\u8d77\u4ed6\u4eec\u624b\u91cc\u90a3\u5f20\u5e26\u5784\u65ad\u6027\u8d28\u7684\u79fb\u52a8\u901a\u8baf\u8fd0\u8425\u724c\u7167\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u57fa\u9891\u82af\u7247\u7ec4\u53d1\u5c55\u7b2c\u4e8c\u4ee3\u589e\u5f3a\u578b\uff0c\u53ef\u4ee5\u8bf4\u5728GSM\u901a\u8baf\u6280\u672f\u4e0a\u5df2\u7ecf\u6210\u719f\u8d77\u6765\uff0c\u5177\u5907\u4e86\u8be5\u7ea7\u522b\u624b\u673a\u89c4\u6a21\u751f\u4ea7\u7684\u6761\u4ef6\u3002\u8054\u901a\u8981\u60f3\u5728\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u4e0a\u83b7\u5f97\u9065\u9065\u9886\u5148\u4e8e\u79fb\u52a8\u516c\u53f8\u7684\u6210\u672c\u4f18\u52bf\uff0c\u4ed6\u4eec\u5fc5\u987b\u4f9d\u8d56\u9526\u6e56\u7684\u6280\u672f\u89e3\u51b3\u65b9\u6848\u3002\u81ea\u79c1\u4e9b\u8bf4\uff0c\u8054\u901a\u8fd9\u65f6\u5019\u63a8\u884c\u6fc0\u8fdb\u6269\u5f20\u65b9\u6848\uff0c\u80fd\u6070\u662f\u65f6\u673a\u7684\u5e2e\u52a9\u9526\u6e56\u6269\u5927\u56fd\u5185\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u7684\u4efd\u989d\uff0c\u5bf9\u9526\u6e56\u8fd8\u662f\u5229\u5927\u4e8e\u5f0a\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u6625\u8282\u5bf9\u8054\u901a\u3001\u5bf9\u8054\u4fe1\u4ee5\u53ca\u5bf9\u79d1\u738b\u9ad8\u79d1\u90fd\u6709\u8fc7\u627f\u8bfa\uff0c\u4e24\u5e74\u65f6\u95f4\u5185\uff0c\u9526\u6e56\u7684ESS\u624b\u673a\u57fa\u9891\u6280\u672f\u53ea\u652f\u6301\u8054\u901a\u7684\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u4e24\u5e74\u65f6\u95f4\u5185\u9526\u6e56\u4e0d\u76f4\u63a5\u5229\u7528ESS\u624b\u673a\u57fa\u9891\u82af\u7247\u751f\u4ea7\u4f4e\u7aef\u624b\u673a\uff0c\u907f\u514d\u8ddf\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u7b49\u4f01\u4e1a\u5728\u56fd\u5185\u5e02\u573a\u76f4\u63a5\u7ade\u4e89\u3002</p></article>"}}}]);
