(window.webpackJsonp=window.webpackJsonp||[]).push([[117],{140:function(p){p.exports={chapterTitle:null,title:"\u7b2c1099\u7ae0 \u767b\u5802\u5165\u5ba4",content:"<article><h2>\u7b2c1099\u7ae0 \u767b\u5802\u5165\u5ba4</h2><p>\u3000\u3000\u591c\u91cc\u4ece\u91d1\u5c71\u6e56\u5f52\u6765\uff0c\u5f20\u606a\u5c31\u4f4f\u5728\u5f90\u5b66\u5e73\u5bb6\u3002\u4ed6\u4e60\u60ef\u665a\u7761\u665a\u8d77\uff0c\u6b21\u65e5\u6e05\u6668\uff0c\u5f90\u5b66\u5e73\u516d\u70b9\u949f\u5c31\u5750\u8f66\u53bb\u4e86\u7701\u59d4\uff0c\u542c\u89c1\u697c\u4e0b\u6c7d\u8f66\u53d1\u52a8\u7684\u58f0\u97f3\uff0c\u4ed6\u7ffb\u4e86\u4e2a\u8eab\u770b\u4e86\u4e0b\u65f6\u95f4\uff0c\u5c31\u7ee7\u7eed\u6c89\u6d78\u5728\u7761\u68a6\u91cc\u900d\u9065\u3002</p><p>\u3000\u3000\u9690\u9690\u7ea6\u7ea6\u542c\u89c1\u5510\u5a67\u7684\u8bf4\u8bdd\u58f0\uff0c\u5f20\u606a\u66f4\u4e0d\u6025\u7740\u8d77\u5e8a\uff0c\u7b49\u7740\u5c0f\u59ae\u5b50\u81ea\u6295\u7f57\u7f51\u9001\u4e0a\u95e8\u6765\u5c06\u5979\u62d6\u5230\u5e8a\u4e0a\u6765\u8e42\u8e8f\u4e00\u756a\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u542c\u89c1\u201c\u549a\u549a\u549a\u201d\u7684\u4e0a\u697c\u68af\u7684\u811a\u6b65\u58f0\uff0c\u5f20\u606a\u8fd8\u89c9\u5f97\u5947\u602a\u5510\u5a67\u600e\u4e48\u8fd9\u4e48\u6027\u6025\uff0c\u4e5f\u4e0d\u6015\u5468\u6dd1\u60e0\u5728\u5bb6\u770b\u4e86\u7b11\u8bdd\uff0c\u7d27\u63a5\u7740\u5c31\u542c\u89c1\u82b7\u5f64\u6e05\u8106\u7684\u56b7\u56b7\u58f0\uff1a\u201c\u5c0f\u606a\u3001\u5c0f\u606a\uff0c\u592a\u9633\u90fd\u6652\u5230\u5c41\u80a1\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u95e8\u7ed9\u649e\u5f00\uff0c\u4e09\u4e2a\u5c0f\u59d1\u5a18\u5174\u51b2\u51b2\u7684\u5c06\u8fdb\u6765\uff0c\u60dc\u5bb9\u3001\u60dc\u7fbd\u5404\u8dd1\u5230\u4e00\u5934\u5148\u5c06\u88ab\u5b50\u62d6\u626f\u5230\u5730\u677f\u4e0a\uff0c\u82b7\u5f64\u8dd1\u5230\u7a97\u53f0\u524d\u5c06\u7a97\u5e18\u62c9\u5f00\uff0c\u91d1\u707f\u707f\u7684\u592a\u9633\u5149\u6d12\u8fdb\u6765\uff0c\u679c\u771f\u4e00\u9053\u9633\u5149\u6652\u5230\u5f20\u606a\u7684\u5c41\u80a1\u4e0a\u3002\u5f20\u606a\u8737\u7740\u8eab\u5b50\u8fd8\u60f3\u518d\u8d56\u4e00\u4f1a\u513f\u5e8a\uff0c\u770b\u7740\u82b7\u5f64\u4f38\u624b\u8981\u53bb\u5f00\u7a97\u6237\uff0c\u8d76\u7d27\u5750\u8d77\u6765\u6551\u9976\uff0c\u5927\u6e05\u65e9\u5ba4\u5916\u5929\u5bd2\u5730\u51b0\u7684\uff0c\u6628\u5929\u770b\u5929\u6c14\u9884\u62a5\u5f97\u6709\u96f6\u4e0b\u4e94\u516d\u5ea6\uff0c\u8ba9\u5bd2\u6c14\u7a9c\u8fdb\u6765\uff0c\u7a7f\u7740\u5355\u8584\u7684\u5185\u8863\u53ef\u76f4\u591f\u597d\u53d7\uff0c\u770b\u89c1\u665a\u6674\u4ece\u540e\u9762\u8ddf\u7740\u4e0a\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u4eca\u5929\u5c31\u5230\u91d1\u5c71\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6628\u5929\u63a5\u5230\u5979\u5976\u5976\u7684\u7535\u8bdd\uff0c\u8bf4\u4f60\u5728\u91d1\u5c71\uff0c\u82b7\u5f64\u5c31\u5435\u7740\u4eca\u5929\u8fc7\u6765\uff0c\u5929\u6ca1\u4eae\u5c31\u4ece\u6d77\u5dde\u51fa\u53d1\uff0c\u6211\u90fd\u56f0\u6b7b\u4e86\uff0c\u201d\u665a\u6674\u795e\u60c5\u5a07\u5a9a\u7684\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u62cd\u7740\u4e09\u4e2a\u5c0f\u4e2b\u5934\u7684\u8111\u888b\uff0c\u8bf4\u9053\uff0c\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u5f20\u606a\u7ed9\u4f60\u4eec\u6210\u529f\u7684\u5435\u9192\u4e86\uff0c\u4f60\u4eec\u5148\u4e0b\u53bb\u5403\u65e9\u996d\u53bb\u2026\u2026\u201d\u8d70\u8fc7\u53bb\u5e2e\u5f20\u606a\u5c06\u5e8a\u8925\u6536\u62fe\u8d77\u6765\uff0c\u770b\u7740\u5f20\u606a\u7ad9\u5728\u90a3\u91cc\u624b\u5fd9\u811a\u4e71\u7684\u7a7f\u8863\u670d\uff0c\u4ed6\u7a7f\u7740\u8d34\u8eab\u7684\u5185\u8863\u7761\u89c9\uff0c\u65e9\u6668\u4e24\u817f\u4e4b\u95f4\u9876\u9686\u8d77\u4e00\u7823\uff0c\u62bf\u5634\u8f7b\u7b11\uff1a\u201c\u771f\u4e11\uff01\u201d\u53c8\u4f38\u624b\u5728\u4e0a\u9762\u8f7b\u62e7\u4e86\u4e00\u628a\uff0c\u5c06\u5e8a\u94fa\u644a\u6574\u9f50\uff0c\u4e5f\u5148\u4e0b\u697c\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8d77\u5e8a\u5934\u7684\u8155\u8868\u770b\u65f6\u95f4\uff0c\u624d\u516b\u70b9\u949f\u7a0d\u8fc7\u4e00\u4e9b\uff0c\u6628\u5929\u8fd8\u662f\u5230\u51cc\u6668\u624d\u7761\uff0c\u8fd9\u65f6\u5019\u7b97\u662f\u521a\u521a\u7761\u8db3\u3002\u5f90\u5b66\u5e73\u5e74\u7eaa\u5927\u4e86\uff0c\u8eab\u4e0a\u538b\u529b\u4e5f\u5927\uff0c\u7761\u7720\u5f88\u6d45\uff0c\u6bcf\u5929\u5c31\u7761\u56db\u4e94\u4e2a\u5c0f\u65f6\uff0c\u5468\u6dd1\u60e0\u5bf9\u6b64\u4e5f\u5f88\u5fe7\u5fc3\uff0c\u8fd9\u6b21\u4e09\u4e2a\u5c0f\u59d1\u5a18\u653e\u5bd2\u5047\u8ba1\u5212\u7740\u8981\u53bb\u6fb3\u5927\u5229\u4e9a\u8fc7\u51ac\uff0c\u5468\u6dd1\u60e0\u4e5f\u62c5\u5fe7\u5f90\u5b66\u5e73\u7684\u8eab\u4f53\u6ca1\u4eba\u7167\u987e\uff0c\u6ca1\u6709\u540c\u610f\u4e00\u8d77\u53bb\u6fb3\u5927\u5229\u4e9a\u6e21\u5047\u3002</p><p>\u3000\u3000\u5f20\u606a\u6d17\u6f31\u5b8c\u6bd5\u4e0b\u4e86\u697c\uff0c\u89c1\u674e\u660e\u745c\u4e0e\u5085\u4fca\u90fd\u5728\uff0c\u8ddf\u5085\u4fca\u8bf4\u9053\uff1a\u201c\u4f60\u660e\u5929\u4eb2\u81ea\u9001\u4ed6\u4eec\u53bb\u73c0\u65af\u5427\uff0c\u6211\u5dee\u4e0d\u591a\u8fd8\u8981\u8fc7\u4e00\u5468\u624d\u80fd\u62bd\u51fa\u65f6\u95f4\u53bb\u73c0\u65af\uff0c\u201d\u53c8\u95ee\u5510\u5a67\uff0c\u201c\u4f60\u662f\u7b49\u6211\u4e00\u8d77\u8fc7\u53bb\uff0c\u8fd8\u662f\u8ddf\u5c0f\u4e2b\u5934\u5979\u4eec\u5148\u8fc7\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c0f\u5a67\u59d0\u8ddf\u6211\u4eec\u4e00\u8d77\u8d70\uff0c\u5c0f\u606a\u4f60\u8bf4\u597d\u4e00\u661f\u671f\u7684\uff0c\u90a3\u6211\u4eec\u5c31\u5728\u73c0\u65af\u7b49\u4f60\u4e00\u661f\u671f\u554a\uff01\u201d\u82b7\u5f64\u5728\u65c1\u8fb9\u66ff\u5510\u5a67\u56de\u7b54\u9053\uff0c\u53c8\u6b6a\u7740\u8111\u888b\u95ee\u5f20\u606a\uff0c\u201c\u4eca\u5929\u4f60\u80fd\u4e0d\u80fd\u62bd\u51fa\u65f6\u95f4\u8ba9\u6211\u4eec\u966a\u4f60\u554a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6320\u4e86\u6320\u8111\u888b\uff0c\u770b\u7740\u5c0f\u4e2b\u5934\u773c\u5df4\u5df4\u7684\u795e\u8272\uff0c\u8ddf\u5085\u4fca\u8bf4\u9053\uff1a\u201c\u4f60\u5e2e\u6211\u8054\u7cfb\u4e00\u4e0b\uff0c\u6211\u6539\u4e58\u660e\u5929\u4e2d\u5348\u7684\u98de\u673a\u53bb\u5317\u4eac\uff0c\u603b\u8981\u5148\u5c06\u4f60\u4eec\u9001\u4e0a\u53bb\u73c0\u65af\u7684\u98de\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4fe1\u4ea7\u90e8\u6b63\u8ba1\u5212\u5bf9\u56fd\u5185\u7684\u79fb\u52a8\u901a\u8baf\u4ea7\u4e1a\u8fdb\u884c\u5927\u529b\u5ea6\u7684\u91cd\u7ec4\u3002\u8054\u901a\u7684GSM\u7f51\u4e1a\u52a1\u5c06\u8981\u8fdb\u884c\u5f7b\u5e95\u7684\u6574\u987f\uff0c\u7136\u540e\u5230\u9999\u6e2f\u4e0a\u5e02\uff1b\u4e2d\u56fd\u7535\u4fe1\u4e5f\u8981\u5c06\u79fb\u52a8\u901a\u8baf\u4e1a\u52a1\u72ec\u7acb\u51fa\u6765\uff0c\u6210\u7acb\u72ec\u7acb\u7684\u516c\u53f8\u3002\u8981\u60f3\u4fdd\u6301\u4e5d\u516b\u3001\u4e5d\u5e74\u5e74\u79fb\u52a8\u901a\u8baf\u5e02\u573a\u9ad8\u901f\u589e\u6da8\u7684\u52bf\u5934\uff0c\u79fb\u52a8\u901a\u8baf\u8d44\u6e90\u7684\u6df1\u5ea6\u6574\u5408\u662f\u5fc5\u987b\u8981\u8fdb\u884c\u7684\uff0c\u9664\u4e86GSM\u7f51\u7edc\u8d44\u6e90\u6574\u5408\u4e4b\u5916\uff0c\u56fd\u5185\u4e5f\u5c06\u8fc5\u901f\u5347\u7ea7GPRS\u7f51\u7edc\uff0c\u8fd8\u4f1a\u5bf9\u672a\u6765\u76843G\u901a\u8baf\u7f51\u7edc\u5efa\u8bbe\u6709\u6240\u89c4\u5212\u3002</p><p>\u3000\u3000\u867d\u7136\uff0c\u56fd\u5185\u79fb\u8fd0\u901a\u8baf\u7684\u8fd0\u8425\u5e02\u573a\u5c06\u59cb\u7ec8\u7531\u592e\u4f01\u628a\u6301\uff0c\u9526\u6e56\u4e5f\u4e0d\u662f\u627e\u4e0d\u5230\u66f4\u591a\u7684\u5229\u76ca\u6240\u5728\u3002\u5404\u79cd\u5c42\u6b21\u7684\u4ea4\u6d41\uff0c\u6700\u8fd1\u5c31\u53d8\u5f97\u6781\u4e3a\u5bc6\u5207\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u8fd8\u662f\u9700\u8981\u5f20\u606a\u4eb2\u81ea\u5230\u5317\u4eac\u8dd1\u4e00\u8dd1\uff0c\u4e5f\u4e0d\u4f1a\u7279\u522b\u7684\u6b63\u5f0f\uff1b\u6b63\u5f0f\u7684\u4ea4\u6d41\u81ea\u6709\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u4ee3\u8868\u9526\u6e56\u51fa\u9762\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u91d1\u5c71\u7684\u5bd2\u51ac\u4e5f\u4e0d\u662f\u6ca1\u6709\u503c\u5f97\u4e00\u6e38\u7684\u5730\u65b9\uff0c\u53ea\u4e0d\u662f\u8981\u8fc1\u5c31\u82b7\u5f64\u3001\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e09\u4e2a\u5c0f\u4e2b\u5934\uff0c\u5927\u5bb6\u4e00\u8d77\u5230\u52a8\u7269\u56ed\u4ee5\u53ca\u4eba\u6c11\u516c\u56ed\u7684\u51b0\u96d5\u5c55\u8017\u4e86\u4e00\u5929\u3002</p><p>\u3000\u3000\u4eba\u6c11\u516c\u56ed\u7684\u51b0\u96d5\u5c55\u5b9e\u5728\u6ca1\u6709\u4ec0\u4e48\u53ef\u770b\u7684\u5730\u65b9\uff0c\u8fdc\u8fdc\u53ca\u4e0d\u4e0a\u6b64\u65f6\u4e58\u98de\u673a\u5230\u54c8\u5c14\u6ee8\u770b\u51b0\u96d5\uff1b\u52a8\u7269\u56ed\u91cc\u5f02\u5473\u6251\u9f3b\uff0c\u52a0\u4e0a\u5929\u5bd2\u5730\u51b0\u7684\uff0c\u7b3c\u5b50\u91cc\u7684\u52a8\u7269\u4e5f\u90fd\u85cf\u5728\u6696\u548c\u7684\u5730\u65b9\uff0c\u4e0d\u5927\u613f\u610f\u51fa\u6765\u9732\u8138\uff0c\u5948\u4f55\u4e09\u4e2a\u5c0f\u4e2b\u5934\u56fe\u5174\u81f4\u5f88\u9ad8\uff0c\u5c0f\u8138\u86cb\u51b0\u5f97\u7ea2\u6251\u6251\uff0c\u5929\u64e6\u9ed1\uff0c\u90fd\u4e0d\u9ad8\u5174\u5f80\u56de\u8d76\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u4e0b\u5348\u5c31\u79bb\u5f00\u91d1\u5c71\u5230\u4e0b\u9762\u7684\u5730\u5e02\u89c6\u5bdf\u53bb\u4e86\uff0c\u5510\u5b66\u8c26\u4ece\u65b0\u4ead\u8d76\u56de\u6765\uff0c\u591c\u91cc\u5c31\u5728\u5510\u5b66\u8c26\u5bb6\uff0c\u70ed\u70ed\u95f9\u95f9\u7684\u5403\u4e86\u4e00\u987f\u5bb6\u5bb4\u3002\u4e09\u4e2a\u5c0f\u4e2b\u5934\u8a93\u4e0d\u5206\u79bb\uff0c\u90fd\u8ddf\u8c22\u665a\u6674\u4f4f\u5230\u5f90\u5b66\u5e73\u5bb6\u53bb\uff0c\u96be\u5f97\u6709\u673a\u4f1a\uff0c\u8fd8\u662f\u8981\u4e0e\u5510\u5b66\u8c26\u8c08\u4e9b\u4e8b\u60c5\uff0c\u591c\u91cc\u5c31\u4f4f\u4e0b\u6765\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u751f\u5206\u7684\uff0c\u552f\u4e00\u611f\u89c9\u4e0d\u597d\u7684\u5c31\u662f\u672a\u6765\u4e08\u6bcd\u5a18\u987e\u5efa\u840d\u62c9\u7740\u5510\u5a67\u4e0a\u697c\u7ed9\u4ed6\u6536\u62fe\u623f\u95f4\uff0c\u8ddf\u5510\u5a67\u8fd8\u662f\u4e0d\u80fd\u5149\u660e\u6b63\u5927\u7684\u7761\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u201c\u5dee\u4e0d\u591a\u5728\u8003\u8651\u5236\u6d46\u9879\u76ee\u4e86\u5427\uff1f\u201d\u5750\u5728\u4e66\u623f\u91cc\uff0c\u5510\u5b66\u8c26\u81ea\u987e\u81ea\u7684\u62ff\u51fa\u70df\u6765\u62bd\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u597d\u4e9b\u4eba\u90fd\u6015\u76f4\u63a5\u8ddf\u4f60\u4eec\u63d0\u8fd9\u4e2a\u95ee\u9898\uff0c\u8fc2\u56de\u66f2\u6298\u7684\u8fd8\u6709\u8dd1\u5230\u6211\u8fd9\u91cc\u6765\u627e\u542c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u597d\u610f\u601d\u5728\u5510\u5b66\u8c26\u9762\u524d\u62ff\u8d77\u684c\u4e0a\u7684\u70df\u76d2\u70b9\u4e00\u652f\u70df\u8ddf\u7740\u4e00\u8d77\u4eab\u53d7\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u8d77\u6765\uff0c\u8fd8\u662f\u8d44\u91d1\u7684\u95ee\u9898\u3002\u4e2d\u6676\u5fae\u82af\u7684\u8d44\u91d1\u538b\u529b\u6682\u65f6\u662f\u7f13\u89e3\u4e0b\u6765\u4e86\uff0c\u770b\u4e0a\u53bb\u5f88\u8f7b\u677e\uff0c\u81ea\u7136\u4e5f\u7ef7\u7d27\u4e86\u5927\u534a\u5e74\u7684\u795e\u7ecf\uff0c\u8fd9\u5927\u534a\u5e74\u6765\uff0c\u9526\u6e56\u6709\u597d\u4e9b\u4eba\u65e5\u5b50\u90fd\u4e0d\u597d\u8fc7\u3002\u201d</p><p>\u3000\u3000\u201c\u5236\u6d46\u9879\u76ee\u8981\u8fbe\u6210\u5230\u4e09\u767e\u4e07\u5428\u89c4\u6a21\uff0c\u624d\u80fd\u6ee1\u8db3\u65b0\u5149\u7eb8\u4e1a\u7684\u4e2d\u671f\u53d1\u5c55\u6240\u9700\uff0c\u6240\u9700\u8d44\u91d1\u7684\u786e\u662f\u6d77\u91cf\uff0c\u201d\u5510\u5b66\u8c26\u8bed\u6c14\u8f7b\u677e\u7684\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u5df2\u7ecf\u53d1\u5c55\u5230\u4eca\u5929\u7684\u89c4\u6a21\uff0c\u4e0a\u9762\u4e0d\u52a0\u9650\u5236\u5df2\u7ecf\u662f\u5f88\u5927\u7684\u8fdb\u6b65\uff0c\u5c31\u4e0d\u8981\u60f3\u5728\u5185\u5730\u89e3\u51b3\u878d\u8d44\u7684\u95ee\u9898\u4e86\u3002\u201d</p><p>\u3000\u3000\u4e0a\u767e\u4ebf\u89c4\u6a21\u7684\u878d\u8d44\uff0c\u6ca1\u6709\u4e2d\u592e\u7684\u503e\u659c\u6027\u653f\u7b56\uff0c\u5730\u65b9\u4e0a\u662f\u6839\u672c\u6ca1\u6709\u80fd\u529b\u89e3\u51b3\u7684\u3002\u6c5f\u5357\u7701\u4e5d\u4e5d\u5e74\u5168\u7701\u603b\u653e\u8d37\u89c4\u6a21\u4e5f\u53ea\u6709\u4e94\u767e\u4ebf\uff0c\u8981\u662f\u5f90\u5b66\u5e73\u6216\u5510\u5b66\u8c26\u4f5c\u4e3b\u6316\u4e00\u4e24\u767e\u4ebf\u6765\u7ed9\u9526\u6e56\uff0c\u53ea\u6015\u5f53\u591c\u5c31\u80fd\u6709\u4eba\u5c06\u72b6\u544a\u5230\u4e2d\u5357\u6d77\u53bb\u3002\u8fd9\u65f6\u5019\u8fd8\u60f3\u7740\u4e2d\u592e\u5927\u529b\u7684\u652f\u6301\u9526\u6e56\uff0c\u90a3\u4e5f\u662f\u4e0d\u73b0\u5b9e\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u65e9\u5c31\u6709\u8fd9\u4e2a\u89c9\u609f\u4e86\uff0c\u4e2d\u6676\u5fae\u82af\u7684\u65b0\u5efa\u9879\u76ee\uff0c\u6211\u4e5f\u6ca1\u6709\u53bb\u6c42\u7237\u7237\u544a\u5976\u5976\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u5012\u662f\u89c9\u5f97\u6709\u4e9b\u4eba\u5728\u7b49\u7740\u6211\u4eec\u6c42\u4e0a\u95e8\u53bb\u7136\u540e\u4e00\u672c\u6b63\u7ecf\u7684\u544a\u8bc9\u6211\u4eec\u2018\u6ca1\u95e8\u2019\uff0c\u53ef\u60dc\uff0c\u6211\u4eec\u6ca1\u6709\u8ba9\u8fd9\u4e9b\u4eba\u4eab\u53d7\u5230\u5feb\u611f\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u683c\u5916\u7684\u618e\u6068\u6211\u4eec\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6b63\u7ecf\u7684\u5728\u80cc\u540e\u7f16\u6392\u522b\u4eba\uff0c\u8fd8\u76f8\u5f53\u6076\u610f\uff0c\u201d\u5510\u5b66\u8c26\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u65e2\u7136\u4f60\u4eec\u90fd\u6709\u8fd9\u4e2a\u89c9\u609f\u4e86\uff0c\u9526\u6e56\u5728\u878d\u8d44\u65b9\u9762\u4e0b\u4e00\u6b65\u6709\u4ec0\u4e48\u5b89\u6392\uff1f\u201d</p><p>\u3000\u3000\u201c\u5236\u6d46\u9879\u76ee\u8fd8\u597d\u4e00\u4e9b\uff0c\u6625\u8282\u8fc7\u540e\u4f1a\u6b63\u5f0f\u5217\u5165\u5de5\u4f5c\u65e5\u7a0b\uff0c\u6211\u4eec\u8ba1\u5212\u7740\u63a8\u52a8\u65b0\u5149\u7eb8\u4e1a\u5728\u9999\u6e2f\u4e0a\u5e02\u7b79\u96c6\u5236\u6d46\u9879\u76ee\u7684\u4e00\u671f\u5efa\u8bbe\u8d44\u91d1\u3002\u4e0d\u8fc7\u80fd\u4e0d\u80fd\u6210\u529f\u8fd8\u8981\u770b\u5f53\u65f6\u7684\u5e02\u573a\u5927\u73af\u5883\uff0c\u73b0\u5728\u4e5f\u4e0d\u80fd\u8bf4\u5c31\u6709\u5341\u8db3\u7684\u628a\u63e1\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u90a3\u8fd8\u771f\u6025\u4e0d\u5f97\u3002\u201d\u5510\u5b66\u8c26\u5e94\u4e86\u4e00\u58f0\uff0c\u5f53\u521d\u789f\u673a\u4e1a\u52a1\u5230\u9999\u6e2f\u501f\u58f3\u4e0a\u5e02\u524d\u540e\u8fd8\u51c6\u5907\u4e86\u534a\u5e74\u591a\u65f6\u95f4\uff0c\u65b0\u80a1\u4e0a\u5e02\u7684\u7b79\u5907\u671f\u5c06\u66f4\u957f\uff0c\u65b0\u5149\u7eb8\u4e1a\u53d1\u5c55\u5230\u4eca\u5929\uff0c\u5546\u4e1a\u524d\u666f\u4e5f\u6bd4\u8f83\u660e\u6717\u5316\u4e86\uff0c\u5c06\u5236\u6d46\u73af\u8282\u8865\u4e0a\uff0c\u5546\u4e1a\u6a21\u5f0f\u3001\u7ecf\u8425\u72b6\u51b5\u5c31\u76f8\u5bf9\u7a33\u5b9a\u4e0b\u6765\uff0c\u5230\u516c\u5f00\u5e02\u573a\u8fdb\u884c\u878d\u8d44\u81ea\u7136\u662f\u6700\u4f73\u9009\u62e9\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u5c31\u7b97\u9526\u6e56\u7684\u4f9b\u8840\u80fd\u529b\u518d\u5f3a\uff0c\u4e5f\u9876\u4e0d\u591a\u8fd9\u4e48\u591a\u5927\u9879\u76ee\u6765\u62bd\u8840\u3002</p><p>\u3000\u3000\u201c\u5236\u6d46\u9879\u76ee\u8fd8\u597d\uff0c\u53ea\u8981\u6709\u5927\u8d44\u672c\u6295\u5165\u5c31\u80fd\u4ea7\u751f\u89c4\u6a21\u6548\u76ca\uff0c\u5728\u4ea7\u4e1a\u94fe\u62a2\u5148\u5e03\u5c40\uff0c\u4e5f\u7684\u786e\u80fd\u5360\u636e\u5148\u673a\uff0c\u771f\u6b63\u8ba9\u4eba\u5934\u75bc\u7684\u8fd8\u662f\u7535\u5b50\u4ea7\u4e1a\u4e0a\u7684\u6295\u5165\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5c31\u62ff\u6676\u5706\u9879\u76ee\u6765\u8bf4\uff0c\u6839\u672c\u5c31\u662f\u5927\u5bb6\u4e00\u8d77\u62fc\u7838\u94b1\u7684\u6e38\u620f\uff1a\u4e2d\u6676\u5fae\u82af\u8fd9\u6b21\u7838\u8fdb\u53bb\u4e8c\u5341\u4ebf\u7f8e\u5143\uff0c0.18\u5fae\u7c73\u7ebf\u7a0b\u4e5f\u80fd\u5efa\u4e24\u6761\u7ebf\uff0c\u7b97\u662f\u52c9\u5f3a\u8ffd\u4e0a\u4e9a\u6d32\u4e3b\u6d41\u5e02\u573a\u6280\u672f\u3002\u4e0d\u8fc7\u6211\u60f3\u4e0d\u7528\u4e24\u5e740.18\u5fae\u7c73\u7ebf\u7a0b\u7684\u82af\u7247\u5c31\u4f1a\u6210\u4e3a\u5927\u9646\u8d27\uff0c\u90a3\u65f6\u5019\u5c31\u8feb\u5207\u4e0a\u9a6c0.13\u5fae\u7c73\u7ebf\u7a0b\u7684\u751f\u4ea7\u7ebf\uff0c\u518d\u8fc7\u4e24\u4e09\u5e74\uff0c\u5c31\u53c8\u8981\u8003\u8651\u4e0a\u9a6c90\u7eb3\u7c73\u300165\u7eb3\u7c73\u7684\u751f\u4ea7\u7ebf\u3002\u6bcf\u5347\u7ea7\u4e00\u4ee3\u6280\u672f\uff0c\u90fd\u610f\u5473\u7740\u51e0\u5341\u4ebf\u7f8e\u5143\u7684\u6295\u8d44\uff0c\u800c\u8fd9\u4e48\u77ed\u65f6\u95f4\u7684\u65f6\u95f4\u95f4\u9694\uff0c\u4e4b\u524d\u7684\u9879\u76ee\u4e5f\u5b8c\u5168\u6765\u4e0d\u53ca\u6536\u56de\u6295\u8d44\u3002\u5230\u65f6\u5019\uff0c\u5927\u5bb6\u5c31\u4f1a\u611f\u6168\u7684\u8bf4\uff1a\u8fd9\u6839\u672c\u5c31\u662f\u65e0\u5e95\u6d1e\u554a\uff01\u9664\u4e86\u6676\u5706\u9879\u76ee\u4e4b\u5916\uff0c\u6211\u4eec\u8fd8\u7279\u522b\u60f3\u4e0a\u8584\u819c\u6db2\u6676\u9879\u76ee\u3002\u8fd9\u73a9\u827a\u513f\u53c8\u662f\u53e6\u4e00\u4e2a\u65e0\u5e95\u6d1e\uff0c\u6bcf\u5347\u7ea7\u4e00\u4ee3\u6280\u672f\u5c31\u610f\u5473\u7740\u51e0\u5341\u4ebf\u7f8e\u5143\u7684\u6295\u5165\uff0c\u800c\u5347\u7ea7\u7684\u65f6\u95f4\u95f4\u9694\u8d8a\u6765\u8d8a\u77ed\u3002\u5c31\u8fd9\u4e24\u4e2a\u9879\u76ee\uff0c\u8981\u771f\u6b63\u7684\u8ffd\u8d76\u4e0a\u56fd\u5916\u5148\u8fdb\u6c34\u51c6\uff0c\u5341\u5e74\u516b\u5e74\u65f6\u95f4\u91cc\u6ca1\u6709\u4e24\u4e09\u767e\u4ebf\u7f8e\u5143\u7684\u6295\u5165\uff0c\u60f3\u90fd\u4e0d\u8981\u60f3\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u591f\u75db\u82e6\u7684\uff0c\u201d\u5510\u5b66\u8c26\u5fae\u5fae\u4e00\u53f9\uff0c\u4e8c\u5341\u4ebf\u7f8e\u5143\u6295\u8d44\u5230\u5176\u4ed6\u5730\u65b9\uff0c\u53ef\u4ee5\u671f\u5f85\u4e00\u672c\u4e07\u5229\uff0c\u6295\u8d44\u5230\u6676\u5706\u5382\u6216\u8584\u819c\u6db2\u6676\u9879\u76ee\u4e0a\uff0c\u53ea\u662f\u610f\u5473\u7740\u63ed\u5f00\u6280\u672f\u4e0e\u8d44\u672c\u7ade\u4e89\u7684\u5f00\u7aef\uff0c\u80fd\u4e0d\u80fd\u5728\u8fd9\u6761\u72ed\u7a84\u7684\u9053\u8def\u4e0a\u6700\u7ec8\u83b7\u5f97\u80dc\uff0c\u8fd9\u65f6\u5019\u505a\u5224\u65ad\u8fd8\u592a\u65e9\uff0c\u201c\u4f60\u4eec\u4e5f\u786e\u5b9a\u8981\u4e0a\u8584\u819c\u6db2\u6676\u9879\u76ee\uff1f\u201d</p><p>\u3000\u3000\u201c\u672a\u6765\u7684\u8d8b\u52bf\u5982\u6b64\uff0c\u6211\u4eec\u73b0\u5728\u54ac\u7740\u7259\u4e0a\u9a6c\u9879\u76ee\uff0c\u8fd8\u80fd\u62a2\u56de\u4e00\u4e9b\u5148\u673a\uff0c\u518d\u62d6\u4e09\u56db\u5e74\uff0c\u5c31\u80fd\u5f7b\u5e95\u7684\u9677\u5165\u88ab\u52a8\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u90fd\u8bf4\u7b49\u79bb\u5b50\u4f53\u4e0e\u8584\u819c\u6db2\u6676\u662f\u672a\u6765\u663e\u793a\u7535\u5b50\u5e02\u573a\u53d1\u5c55\u4e24\u5927\u8d8b\u52bf\uff0c\u4f60\u4eec\u5df2\u7ecf\u8ba4\u51c6\u8584\u819c\u591c\u6676\uff1f\u201d\u5510\u5b66\u8c26\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u662f\u73b0\u5728\u5c31\u8ba4\u51c6\uff0c\u201d\u5f20\u606a\u4e5f\u4e0d\u80fd\u8ddf\u5510\u5b66\u8c26\u660e\u8bf4\u7b49\u79bb\u5b50\u4f53\u4e0e\u8584\u819c\u6db2\u6676\u4e4b\u4e89\u5230\u96f6\u4e03\u3001\u96f6\u516b\u5e74\u5c31\u4f1a\u5f7b\u5e95\u7684\u5206\u51fa\u80dc\u8d1f\uff0c\u4ed6\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u6839\u672c\u6ca1\u6709\u80fd\u529b\u53bb\u8ffd\u6c42\u5e73\u8861\uff0c\u53ea\u80fd\u5c06\u7b79\u7801\u90fd\u62bc\u5728\u4e00\u8fb9\u3002\u81f3\u5c11\u5728\u663e\u793a\u5668\u5e02\u573a\uff0c\u8584\u819c\u6db2\u6676\u6280\u672f\u7684\u80dc\u7b97\u66f4\u9ad8\u4e00\u4e9b\uff0c\u5c06\u6765\u5728\u7231\u8fbe\u7684\u7535\u5b50\u4ea7\u54c1\u4e2d\uff0c\u819c\u8584\u6db2\u6676\u6280\u672f\u6709\u7740\u66f4\u5e7f\u6cdb\u7684\u7528\u9014\uff0c\u73b0\u5728\uff0c\u5df2\u7ecf\u6709\u65e5\u7cfb\u624b\u673a\u5382\u5546\u5f00\u59cb\u7814\u7a76\u5c06\u8584\u819c\u6db2\u6676\u6280\u672f\u7528\u4e8e\u624b\u673a\u4e4b\u4e0a\u3002\u8981\u662f\u53ea\u80fd\u7b79\u7801\u62bc\u5728\u4e00\u8fb9\uff0c\u6211\u4eec\u5176\u5b9e\u6ca1\u6709\u4ec0\u4e48\u9009\u62e9\u4f59\u5730\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u6709\u73c0\u65af\u7684\u94c1\u77ff\u9879\u76ee\uff0c\u4f60\u4eec\u8fd9\u4e24\u5e74\u8981\u8d76\u7740\u505a\u7684\u4e8b\u60c5\u5f88\u591a\u554a\u3002\u201d\u5510\u5b66\u8c26\u80cc\u9760\u5728\u6c99\u53d1\u4e0a\u6478\u4e86\u6478\u5934\u9876\u7684\u5934\u53d1\uff0c\u5728\u706f\u5149\u4e0b\uff0c\u4ed6\u7684\u4e24\u9b13\u8d6b\u7136\u751f\u4e86\u4e9b\u767d\u53d1\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u804a\u8d77\u6765\u8fd8\u6ca1\u5b8c\u6ca1\u4e86\uff0c\u660e\u5929\u8fd8\u8981\u8d76\u65e9\u53bb\u673a\u573a\uff0c\u4e5f\u4e0d\u60f3\u7740\u65e9\u70b9\u4f11\u606f\uff0c\u201d\u987e\u5efa\u840d\u6000\u91cc\u6367\u7740\u4e00\u6761\u6bdb\u5dfe\u88ab\uff0c\u7ad9\u5728\u4e66\u623f\u95e8\u53e3\u6253\u65ad\u5510\u5b66\u8c26\u3001\u5f20\u606a\u7684\u8c08\u8bdd\uff0c\u201c\u4ee5\u540e\u5f20\u606a\u4e0a\u95e8\u6765\uff0c\u4e0d\u8bb8\u4f60\u4eec\u7237\u4fe9\u7a9d\u5728\u4e66\u623f\u91cc\u8c08\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u987e\u59e8\u6709\u610f\u89c1\u4e86\uff0c\u201d\u5510\u5b66\u8c26\u7b11\u7740\u7ad9\u8d77\u6765\uff0c\u201c\u8fd8\u662f\u5c0f\u5a67\u6709\u610f\u89c1\u5c06\u5979\u5988\u6307\u4f7f\u8fc7\u6765\u6405\u5c40\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u624d\u6ca1\u6709\u610f\u89c1\u5462\uff0c\u201d\u5510\u5a67\u6d17\u8fc7\u6fa1\uff0c\u6b63\u8737\u817f\u5750\u5728\u6c99\u53d1\u4e0a\u62ff\u5e72\u6bdb\u5dfe\u6413\u5934\u6bdb\uff0c\u8138\u86cb\u7ea2\u6251\u6251\u7684\uff0c\u542c\u89c1\u5979\u7238\u7f16\u6392\u5979\uff0c\u4e0b\u5df4\u78d5\u5728\u6c99\u53d1\u9760\u80cc\u4e0a\u671b\u8fc7\u6765\uff0c\u58f0\u97f3\u5a07\u67d4\u7684\u8bf4\u9053\uff0c\u201c\u5c31\u611f\u89c9\u4f60\u4eec\u7537\u4eba\u51d1\u5230\u4e00\u5757\uff0c\u52b3\u53e8\u8d77\u6765\u6ca1\u5b8c\uff0c\u6211\u4eec\u5973\u7684\u5c31\u53d8\u5f97\u7279\u591a\u4f59\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8bf4\u6ca1\u6709\u610f\u89c1\uff1f\u201d\u5510\u5b66\u8c26\u7b11\u7740\u8bf4\uff0c\u201c\u4e0d\u59a8\u788d\u4f60\u4eec\u4fe9\u8c08\u60c5\u8bf4\u7231\u7684\uff0c\u6211\u5f88\u8bc6\u76f8\uff0c\u77e5\u9053\u6211\u4eec\u8001\u4eba\u5bb6\u6709\u65f6\u5019\u4f1a\u53d8\u5f97\u5f88\u591a\u4f59\u2026\u2026\u201d\u8d70\u8fc7\u6765\u5750\u5230\u6c99\u53d1\uff0c\u62ff\u8d77\u8336\u51e0\u65f6\u7684\u6447\u63a7\u5668\uff0c\u987a\u624b\u8c03\u5230\u65b0\u95fb\u53f0\uff0c\u770b\u8d77\u65b0\u95fb\u6765\u3002</p><p>\u3000\u3000\u5510\u5a67\u9b3c\u7cbe\u7075\u7684\u5410\u4e86\u5410\u820c\u5934\uff0c\u7a7f\u7740\u62d6\u978b\u4ece\u6c99\u53d1\u4e0a\u8df3\u8d77\u6765\uff0c\u7b11\u563b\u563b\u7684\u62b1\u6028\u9053\uff1a\u201c\u6211\u7238\u5c31\u662f\u8fd9\u6837\u7684\u4eba\uff0c\u4e00\u70b9\u90fd\u4e0d\u987e\u522b\u4eba\u611f\u53d7\uff0c\u4ed6\u81ea\u5df1\u60f3\u770b\u65b0\u95fb\uff0c\u5c31\u4e0d\u7ba1\u522b\u4eba\u8fd8\u5728\u770b\u7535\u89c6\u5267\uff0c\u201d\u6402\u7740\u5f20\u606a\u7684\u80f3\u818a\u8bf4\u9053\uff0c\u201c\u6211\u9886\u4f60\u53bb\u770b\u770b\u623f\u95f4\uff0c\u88ab\u5b50\u8fd8\u7279\u610f\u6652\u8fc7\uff1f\u201d\u62c9\u7740\u5f20\u606a\u5c31\u5f80\u697c\u4e0a\u8d70\u3002</p><p>\u3000\u3000\u966a\u5510\u5b66\u8c26\u8bf4\u8bdd\u5012\u662f\u4e0d\u7d2f\uff0c\u5374\u662f\u767d\u5929\u966a\u4e09\u4e2a\u5c0f\u4e2b\u5934\u901b\u52a8\u7269\u56ed\u3001\u770b\u51b0\u96d5\u5c55\uff0c\u7d2f\u5f97\u9aa8\u5934\u8981\u6563\uff0c\u5f20\u606a\u4e5f\u60f3\u8eb2\u56de\u623f\u95f4\u7761\u5927\u89c9\uff0c\u8fdb\u4e86\u623f\u95f4\uff0c\u5c06\u5510\u5a67\u5a07\u8f6f\u7684\u8eab\u5b50\u6402\u5728\u6000\u91cc\uff0c\u4e00\u8d77\u5012\u5728\u677e\u8f6f\u7684\u5e8a\u8925\u4e0a\uff0c\u8ddf\u5979\u8bf4\u9053\uff1a\u201c\u521a\u624d\u770b\u5929\u6c14\u9884\u62a5\u8bf4\u660e\u5929\u5317\u4eac\u6709\u96f6\u4e0b\u5341\u516b\u5ea6\uff0c\u771f\u60f3\u8ddf\u4f60\u4eec\u4e00\u8d77\u5750\u98de\u673a\u53bb\u73c0\u65af\uff0c\u73b0\u5728\u53bb\u73c0\u65af\u53ef\u4ee5\u6ce1\u5728\u6d77\u6c34\u91cc\u770b\u5915\u9633\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u653e\u5fc3\u5427\uff0c\u6211\u4f1a\u628a\u4f60\u6025\u7740\u53bb\u73c0\u65af\u7684\u5fc3\u60c5\u4f20\u8fbe\u7ed9\u67d0\u4e9b\u4eba\u7684\u2026\u2026\u201d\u5510\u5a67\u5c0f\u5634\u8d34\u5728\u5f20\u606a\u7684\u8033\u8fb9\u8f7b\u58f0\u8bf4\u8bdd\uff0c\u7ec6\u7ec6\u7684\u7259\u5c16\u5374\u8f7b\u54ac\u7740\u5f20\u606a\u7684\u8033\u5782\u3002</p><p>\u3000\u3000\u5f20\u606a\u6bdb\u9aa8\u609a\u7136\u7684\uff0c\u6015\u5c0f\u59ae\u5b50\u72e0\u5fc3\u5728\u4ed6\u8033\u6735\u4e0a\u54ac\u4e00\u53e3\uff0c\u4ed6\u4e4b\u524d\u8fd8\u62c5\u5fc3\u5510\u5a67\u4e0d\u613f\u610f\u63d0\u524d\u966a\u82b7\u5f64\u4e09\u4e2a\u5c0f\u4e2b\u5934\u5148\u53bb\u73c0\u65af\u6e21\u5047\uff0c\u5510\u5a67\u65e2\u7136\u613f\u610f\u63d0\u524d\u8fc7\u53bb\uff0c\u806a\u660e\u7684\u7537\u4eba\u5c31\u4e0d\u4f1a\u518d\u7ea0\u7f20\u8fd9\u79cd\u8bdd\u9898\uff0c\u624b\u4ece\u5510\u5a67\u539a\u7ed2\u7761\u8863\u91cc\u94bb\u8fdb\u6765\uff0c\u8146\u7740\u8138\u8bf4\uff1a\u201c\u4f60\u665a\u4e0a\u662f\u4e0d\u662f\u4e5f\u7761\u8fd9\u91cc\uff1f\u8fd9\u4e48\u51b7\u7684\u5929\uff0c\u4e00\u4e2a\u4eba\u7761\u602a\u51b7\u6e05\u7684\uff0c\u4e5f\u6ca1\u6709\u4e00\u4e2a\u4eba\u966a\u7740\u8bf4\u8bf4\u8bdd\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u624b\u6709\u4e9b\u51b0\uff0c\u5510\u5a67\u7684\u8eab\u5b50\u7ed9\u4ed6\u7684\u51b0\u624b\u78b0\u5230\uff0c\u626d\u7740\u8170\u8eb2\u5f00\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u73b0\u5728\u5c31\u4e0d\u662f\u966a\u4f60\u5728\u8bf4\u8bdd\uff1f\u518d\u665a\u5c31\u8981\u7761\u89c9\u4e86\uff0c\u4f60\u7231\u60f3\u8c01\u60f3\u8c01\u53bb\uff01\u201d\u8eab\u5b50\u626d\u7740\u6ca1\u6709\u6323\u624e\u5f20\u606a\u8ddf\u7ae0\u9c7c\u89e6\u624b\u4f3c\u7684\u602a\u624b\uff0c\u4e0d\u4e00\u4f1a\u513f\uff0c\u65b0\u5265\u5ae9\u9e21\u5934\u7684\u9165\u80f8\u5c31\u7ed9\u5f20\u606a\u6293\u5728\u624b\u91cc\uff0c\u5012\u662f\u90a3\u91cc\u4e0d\u600e\u4e48\u6015\u51c9\uff0c\u4e73\u5c16\u5374\u624b\u6307\u8f7b\u8f7b\u5265\u52a8\u4e86\u4e24\u4e0b\uff0c\u9165\u9165\u9ebb\u9ebb\u7ed9\u901a\u4e86\u7535\u4f3c\u7684\uff0c\u5510\u5a67\u5c31\u61d2\u6d0b\u6d0b\u7684\u4e0d\u60f3\u6323\u624e\u4e86\u3002</p><p>\u3000\u3000\u6d17\u8fc7\u6fa1\uff0c\u7761\u8863\u91cc\u5c31\u6ca1\u6709\u6234\u4e73\u7f69\uff0c\u6ca1\u60f3\u5230\u5c31\u8fd9\u4e48\u4fbf\u5b9c\u5f20\u606a\u3002</p><p>\u3000\u3000\u4e00\u4f1a\u513f\uff0c\u95e8\u5916\u4f20\u6765\u5510\u5b66\u8c26\u4e0e\u987e\u5efa\u840d\u7684\u8bf4\u8bdd\u58f0\uff0c\u5510\u5a67\u80c6\u5b50\u5c0f\uff0c\u5939\u7740\u5f20\u606a\u7684\u624b\u4e0d\u8ba9\u4ed6\u52a8\u5f39\uff0c\u4e5f\u6ca1\u6709\u8ba9\u4ed6\u7684\u624b\u62bd\u51fa\u6765\uff0c\u542c\u89c1\u6572\u95e8\u58f0\uff0c\u5979\u624d\u614c\u4e71\u7684\u7ad9\u8d77\u6765\uff0c\u7406\u4e86\u7406\u8863\u670d\u8ba9\u5f20\u606a\u5750\u8d77\u6765\u3001\u786e\u5b9a\u4e0d\u4f1a\u6709\u8fbe\u7834\u7efd\u624d\u8d70\u8fc7\u53bb\u5f00\u95e8\uff0c\u5979\u5988\u7ad9\u5728\u95e8\u53e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u77e5\u9053\u5510\u5a67\u5979\u5988\u8ddf\u5979\u8bf4\u4e86\u4ec0\u4e48\uff0c\u89c1\u5510\u5a67\u518d\u8d70\u8fdb\u6765\u8138\u4e0a\u7ea2\u6251\u6251\u7684\uff0c\u95ee\u5979\uff1a\u201c\u4f60\u5988\u8ddf\u4f60\u8bf4\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u4f60\u7ba1\uff1f\u201d\u5510\u5a67\u5a07\u7f9e\u7684\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u53c8\u6251\u5230\u5e8a\u4e0a\uff0c\u62ff\u5f20\u606a\u7684\u624b\u81c2\u6795\u5230\u8111\u888b\u4e0b\uff0c\u4e0d\u597d\u610f\u601d\u7684\u8bf4\uff0c\u201c\u6211\u5988\u4e5f\u771f\u662f\u7684\uff0c\u5c31\u77e5\u9053\u5520\u53e8\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u5988\u600e\u4e48\u5520\u53e8\u4e86\uff1f\u201d\u5f20\u606a\u8fd8\u662f\u597d\u5947\u3002</p><p>\u3000\u3000\u201c\u8ba9\u597d\u5947\u5fc3\u6740\u6b7b\u4f60\uff0c\u201d\u5510\u5a67\u5a07\u55d4\u7684\u8db4\u5230\u5f20\u606a\u7684\u80f8\u53e3\u6390\u4ed6\u7684\u8138\u76ae\u5b50\uff0c\u7b11\u95f9\u4e86\u4e00\u9635\u5b50\uff0c\u5c31\u7ad9\u8d77\u6765\u8bf4\u9053\uff0c\u201c\u6211\u56de\u81ea\u5df1\u623f\u95f4\u7761\u89c9\u4e86\u2026\u2026\u201d\u6253\u5f00\u623f\u95e8\u53c8\u6545\u610f\u7684\u671d\u8fc7\u9053\u91cc\u558a\u9053\uff0c\u201c\u6211\u56de\u81ea\u5df1\u623f\u95f4\u7761\u89c9\u4e86\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u539f\u6765\u5510\u5a67\u5979\u5988\u53ee\u5631\u5979\u591c\u91cc\u56de\u81ea\u5df1\u623f\u95f4\u7761\u89c9\uff0c\u770b\u7740\u5c0f\u59ae\u5b50\u63a9\u95e8\u8d70\u51fa\u53bb\uff0c\u4ed6\u5fc3\u91cc\u60b2\u53f9\u4e86\u4e00\u58f0\uff0c\u53bb\u76e5\u6d17\u5ba4\u6d17\u6f31\u51c6\u5907\u7761\u89c9\uff0c\u542c\u7740\u623f\u95f4\u91cc\u7c0c\u7c0c\u7684\u54cd\u58f0\uff0c\u63a8\u5f00\u73bb\u7483\u95e8\u770b\u7740\u5510\u5a67\u62b1\u7740\u4e00\u53ea\u5361\u901a\u6795\u5934\u53c8\u9b3c\u9b3c\u795f\u795f\u7684\u63a9\u95e8\u8fdb\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u53c8\u56de\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u5fd9\u62ff\u624b\u6307\u62b5\u7740\u5634\u5507\u505a\u51fa\u4e00\u4e2a\u5664\u58f0\u7684\u624b\u52bf\uff0c\u5c06\u95e8\u5173\u4e25\u5b9e\uff0c\u624d\u8f7b\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u4e00\u624b\u53c9\u7740\u8170\uff0c\u4e00\u624b\u522b\u5728\u8170\u540e\uff0c\u6076\u72e0\u72e0\u7684\u76ef\u8fc7\u6765\u8bf4\uff1a\u201c\u7ed9\u4f60\u5c0f\u6df7\u86cb\u5bb3\u6b7b\u4e86\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u521a\u8981\u95ee\u81ea\u5df1\u600e\u4e48\u5bb3\u5979\u4e86\uff0c\u5c31\u89c1\u5979\u6254\u4e86\u4e00\u76d2\u4e1c\u897f\u8fc7\u6765\uff0c\u5934\u5f80\u8fb9\u4e0a\u95ea\u4e00\u4e0b\uff0c\u626d\u5934\u5c31\u770b\u7740\u4e00\u53ea\u6770\u58eb\u90a6\u7684\u76d2\u5b50\u6eda\u843d\u5728\u53f0\u76c6\u91cc\uff0c\u5510\u5a67\u795e\u60c5\u51f6\u6076\u7684\u8bf4\u9053\uff1a\u201c\u6211\u5988\u8bf4\u4e86\uff0c\u4f60\u8981\u4e0d\u6234\u4e0a\u8fd9\u73a9\u827a\u513f\uff0c\u5c31\u4e0d\u8ba9\u4f60\u78b0\u6211\u2014\u2014\u4f60\u8bf4\u5979\u5c0f\u8001\u592a\u4e00\u4e2a\uff0c\u6709\u95f2\u5de5\u592b\u7422\u78e8\u8fd9\u4e8b\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u55f7\u54e6\uff01\u201d\u5f20\u606a\u5174\u594b\u5f97\u5c31\u8981\u632f\u81c2\u6b22\u547c\uff0c\u539f\u6765\u521a\u624d\u5510\u5a67\u5979\u5988\u5c06\u5979\u558a\u51fa\u6765\u662f\u5429\u5490\u8fd9\u4e8b\uff0c\u770b\u7740\u5510\u5a67\u5f20\u7259\u821e\u722a\u7684\u5c31\u8981\u6251\u8fc7\u6765\uff0c\u5fd9\u6293\u4f4f\u5979\u7693\u5982\u7d20\u96ea\u7684\u624b\u8155\uff0c\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u5c31\u89c9\u5f97\u4f60\u5bb6\u7684\u5c0f\u8001\u592a\u5f88\u906d\u4eba\u559c\u6b22\u554a\uff0c\u6bd4\u6211\u5988\u597d\u591a\u4e86\uff0c\u6211\u4ee5\u540e\u94c1\u5b5d\u987a\u4f60\u5988\u8d85\u8fc7\u6211\u5988\uff0c\u201d\u4ece\u6770\u58eb\u90a6\u4ece\u8eab\u540e\u53f0\u76c6\u91cc\u635e\u51fa\u6765\uff0c\u5728\u5510\u5a67\u773c\u524d\u626c\u4e86\u626c\uff0c\u95ee\u9053\uff0c\u201c\u6211\u4eec\u7b49\u4f1a\u513f\u7528\u51e0\u53ea\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u6b7b\u554a\uff0c\u4e00\u53ea\u90fd\u4e0d\u8bb8\u7528\uff0c\u4e0d\u7136\u7b11\u90fd\u8981\u7ed9\u7b11\u6b7b\u4e86\uff01\u201d\u5510\u5a67\u5c06\u76d2\u5b50\u62a2\u4e0b\u6765\uff0c\u53c8\u72e0\u72e0\u7684\u6390\u4e86\u4e00\u4e0b\u5f20\u606a\u7684\u8170\u8089\uff0c\u5c31\u95ea\u8eab\u8eb2\u5f00\u4e86\uff0c\u201c\u6211\u65e9\u4e0a\u8fd8\u8981\u5077\u5077\u7761\u56de\u6211\u623f\u95f4\u53bb\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8981\u5bf9\u6211\u8fd9\u4e48\u6b8b\u5fcd\u5427\uff0c\u201d\u5f20\u606a\u5306\u5fd9\u5c06\u8138\u64e6\u5e72\u51c0\uff0c\u8dd1\u8fdb\u623f\u95f4\u91cc\u8981\u5c06\u76d2\u5b50\u4ece\u5510\u5a67\u624b\u91cc\u62a2\u8fc7\u6765\uff0c\u5510\u5a67\u8d56\u76ae\u7684\u8db4\u5728\u5e8a\u4e0a\uff0c\u5c06\u6770\u58eb\u90a6\u538b\u5728\u8eab\u4e0b\uff0c\u5f20\u606a\u6ca1\u6709\u6025\u7740\u5c06\u5979\u7684\u4eba\u4ece\u5e8a\u4e0a\u62c9\u4e0b\u6765\uff0c\u800c\u662f\u76f4\u63a5\u5c06\u5979\u7684\u7761\u88e4\u8fde\u540c\u5185\u88e4\u6252\u4e0b\u6765\uff0c\u9732\u51fa\u6d51\u5706\u633a\u7fd8\u7684\u96ea\u5ae9\u7389\u80a1\uff0c\u5510\u5a67\u5bb3\u7f9e\u7684\u94bb\u8fdb\u88ab\u5b50\u91cc\uff0c\u89c1\u5f20\u606a\u62ff\u8d77\u6770\u58eb\u90a6\u8981\u62c6\u5c01\uff0c\u624d\u544a\u8bc9\u4ed6\uff1a\u201c\u6211\u90a3\u4e2a\u521a\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fd9\u4e0d\u8fed\u7684\u5c06\u81ea\u5df1\u6252\u5149\uff0c\u4e5f\u8ddf\u7740\u94bb\u8fdb\u88ab\u7a9d\u91cc\uff0c\u5c06\u5510\u5a67\u7684\u4e0a\u8eab\u7761\u8863\u4e5f\u987a\u624b\u6252\u6389\uff0c\u5c06\u808c\u80a4\u96ea\u5ae9\u3001\u5f39\u8f6f\u70ed\u70eb\u7684\u5a07\u8eaf\u6402\u5728\u6000\u91cc\uff0c\u4f38\u5634\u543b\u5411\u5979\u5a07\u6da6\u7684\u53cc\u5507\uff0c\u5c06\u5979\u6709\u7740\u5e7d\u9999\u7684\u5507\u9876\u5f00\uff0c\u542e\u8bb8\u9999\u5ae9\u820c\u5c16\uff0c\u52fe\u5f15\u5c0f\u59ae\u5b50\u4e5f\u6e10\u6e10\u60c5\u4e0d\u81ea\u7981\u7684\u5410\u9732\u820c\u5c16\u6765\u7ea0\u7f20\u3001\u6405\u5f04\u3002</p><p>\u3000\u3000\u5510\u5a67\u72b9\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u5728\u706f\u5149\u4e0b\u6b22\u7231\uff0c\u7ed9\u5f20\u606a\u538b\u5728\u8eab\u4e0b\u53c8\u4e0d\u80fd\u4f38\u624b\u53bb\u5173\u706f\uff0c\u4e5f\u4e0d\u5fd8\u5c06\u88ab\u5b50\u8499\u4f4f\u5934\u9876\u3002\u7f1d\u9699\u900f\u8fdb\u5149\u6765\uff0c\u5f20\u606a\u89c1\u5979\u7f8e\u4e3d\u7684\u7738\u5b50\u65f6\u800c\u6df1\u60c5\u51dd\u671b\u81ea\u5df1\uff0c\u51dd\u671b\u4e00\u77ac\u95f4\uff0c\u53c8\u4f1a\u5a07\u7f9e\u7684\u95ed\u4e0a\uff0c\u900f\u51fa\u9752\u6625\u59a9\u5a9a\u7684\u4e07\u79cd\u98ce\u60c5\uff0c\u4fbf\u662f\u5982\u6b64\u7684\u8138\u86cb\u770b\u7740\u5c31\u8ba9\u4eba\u7740\u8ff7\u3002\u5927\u6982\u611f\u89c9\u5230\u5f20\u606a\u75f4\u8ff7\u7684\u770b\u5f97\u592a\u4e45\uff0c\u5510\u5a67\u4e0d\u597d\u610f\u601d\u7684\u63a2\u51fa\u8eab\u5b50\u5c06\u706f\u5173\u4e0a\uff0c\u89c1\u5f20\u606a\u8981\u53bb\u5f00\u706f\uff0c\u56db\u80a2\u5c31\u50cf\u516b\u811a\u7ae0\u9c7c\u4f3c\u7684\u5c06\u5f20\u606a\u7f20\u4e86\u4e2a\u7ed3\u5b9e\u3002</p><p>\u3000\u3000\u201c\u4f60\u597d\u7f8e\uff01\u6211\u60f3\u770b\u6e05\u695a\u4f60\u591a\u4e00\u4e9b\u3002\u201d\u5f20\u606a\u5634\u5507\u8d34\u5230\u5510\u5a67\u5254\u900f\u5982\u7389\u7684\u8033\u8fb9\u8f7b\u8bed\u3002</p><p>\u3000\u3000\u201c\u6709\u8bb8\u601d\u59d0\u7f8e\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u73b0\u5728\u5c31\u6bd4\u5979\u7f8e\u3002\u201d</p><p>\u3000\u3000\u201c\u9a97\u9b3c\u5462\uff01\u201d\u5510\u5a67\u5a07\u5a9a\u7684\u7b11\u4e86\uff0c\u5c06\u5f20\u606a\u7684\u8eab\u5b50\u6402\u5f97\u66f4\u7d27\uff0c\u4e0d\u8ba9\u4ed6\u6491\u8d77\u8eab\u5b50\u770b\u81ea\u5df1\u3002</p><p>\u3000\u3000\u5373\u4f7f\u53cc\u624b\u5728\u9ed1\u6697\u6478\u7d22\uff0c\u611f\u89e6\u7740\u96ea\u5ae9\u5f39\u8f6f\u8102\u6ed1\u5982\u7389\u7684\u6d51\u5706\u5927\u817f\uff0c\u5e7d\u5e7d\u4f53\u9999\u523a\u6fc0\u5f97\u60c5\u6b32\u52c3\u53d1\uff0c\u5e74\u8f7b\u7684\u604b\u4eba\u4e5f\u4e0d\u9700\u8981\u591a\u5c11\u524d\u620f\uff0c\u70ed\u543b\u5c31\u8db3\u4ee5\u8ba9\u4eba\u60c5\u6b32\u6f8e\u6e43\uff0c\u5f20\u606a\u4e00\u8fb9\u4e0e\u5510\u5a67\u7f20\u543b\uff0c\u4e00\u8fb9\u8ba9\u5979\u5728\u591c\u8272\u5fae\u5149\u4e0b\u663e\u5f97\u6108\u53d1\u96ea\u767d\u7684\u6d51\u5706\u53cc\u817f\u6253\u5f00\u7f20\u5728\u81ea\u5df1\u8170\u4e0a\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5373\u4f7f\u6e05\u6668\u5077\u5077\u8eb2\u56de\u81ea\u5df1\u7684\u623f\u95f4\u7761\u89c9\uff0c\u65e9\u6668\u5750\u8f66\u53bb\u8d76\u98de\u673a\u65f6\u4e0d\u65ad\u7684\u54c8\u6b20\u8fd8\u662f\u5c06\u5510\u5a67\u51fa\u5356\u4e86\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u624b\u91cc\u8fd8\u6709\u5de5\u4f5c\uff0c\u8fd9\u6b21\u4e0d\u80fd\u966a\u7740\u4e09\u4e2a\u5c0f\u4e2b\u5934\u4e00\u8d77\u53bb\u73c0\u65af\uff0c\u674e\u660e\u745c\u4e0e\u5085\u4fca\u8fd8\u6709\u5510\u5a67\u9886\u7740\u4e09\u4e2a\u5c0f\u4e2b\u5934\u5750\u98de\u673a\u53bb\u73c0\u65af\u3002\u7ecf\u9999\u6e2f\u8f6c\u673a\uff0c\u5230\u4e86\u73c0\u65af\uff0c\u90a3\u8fb9\u81ea\u6709\u7fdf\u4e39\u9752\u5b89\u6392\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff0c\u5012\u662f\u5468\u6dd1\u60e0\u6709\u4e9b\u4e0d\u820d\u5f97\uff0c\u89c9\u5f97\u51fa\u56fd\u6e21\u4e2a\u5047\u771f\u6709\u5341\u4e07\u516b\u5343\u91cc\u4f3c\u7684\uff0c\u770b\u7740\u82b7\u5f64\u8ddf\u7740\u5927\u5bb6\u8d70\u8fdb\u5b89\u68c0\u901a\u9053\uff0c\u90fd\u7981\u4e0d\u4f4f\u62b9\u8d77\u773c\u6cea\u6765\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u7b11\u7740\u8bf4\uff1a\u201c\u5988\u4f60\u8981\u820d\u4e0d\u5f97\u82b7\u5f64\uff0c\u5c31\u8ddf\u7740\u4e00\u8d77\u53bb\u73c0\u65af\u4f4f\u51e0\u5929\uff0c\u8fd9\u91cc\u5929\u5bd2\u5730\u51b0\u7684\uff0c\u542c\u8bf4\u73c0\u65af\u5374\u662f\u4e00\u5e74\u4e2d\u6700\u597d\u7684\u5b63\u8282\uff0c\u4f60\u6709\u4e9b\u5173\u8282\u708e\uff0c\u51ac\u5929\u53bb\u5357\u534a\u7403\u662f\u6700\u597d\u4e0d\u8fc7\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u73b0\u5728\u54ea\u6709\u8fd9\u4e2a\u597d\u798f\u6c14\u554a\uff0c\u8981\u4f11\u606f\uff0c\u4e5f\u8981\u7b49\u82b7\u5f64\u5979\u7237\u7237\u9000\u4e0b\u6765\u4e4b\u540e\u518d\u8bf4\uff0c\u82b7\u5f64\u53bb\u73c0\u65af\u73a9\u6ca1\u6709\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff0c\u5c31\u662f\u5fc3\u91cc\u6709\u4e9b\u820d\u4e0d\u5f97\uff0c\u201d\u5468\u6dd1\u60e0\u8138\u4e0a\u7684\u773c\u6cea\u8fd8\u6ca1\u6709\u62b9\u5e72\uff0c\u53c8\u7b11\u7740\u8bf4\u8d77\u6765\uff0c\u95ee\u665a\u6674\uff0c\u201c\u4f60\u4eca\u5929\u5c31\u56de\u6d77\u5dde\uff1f\u201d</p><p>\u3000\u3000\u201c\u5341\u4e00\u70b9\u6709\u53bb\u5efa\u90ba\u7684\u98de\u673a\uff0c\u6211\u5c31\u5728\u8fd9\u91cc\u5148\u9001\u5f20\u606a\u4e0a\u98de\u673a\uff0c\u7136\u540e\u76f4\u63a5\u5750\u98de\u673a\u53bb\u5efa\u90ba\u3002\u201d\u8c22\u665a\u6674\u8bf4\u9053\uff0c\u7b49\u7740\u5c06\u5468\u6dd1\u60e0\u3001\u987e\u5efa\u840d\u9001\u79bb\u673a\u573a\uff0c\u5979\u4e0e\u5f20\u606a\u8fd4\u56de\u5019\u673a\u5927\u5385\uff0c\u8f6c\u8eab\u8865\u4e86\u4e00\u5f20\u98de\u5317\u4eac\u7684\u673a\u7968\u3002</p></article>"}}}]);
