(window.webpackJsonp=window.webpackJsonp||[]).push([[1170],{1193:function(p){p.exports={chapterTitle:null,title:"\u7b2c1167\u7ae0 \u7f8e\u5c11\u5973\u5c0f\u5e08\u59b9",content:"<article><h2>\u7b2c1167\u7ae0 \u7f8e\u5c11\u5973\u5c0f\u5e08\u59b9</h2><p>\u3000\u3000\u4e0e\u6768\u84c9\u5e73\u89c1\u9762\u540e\uff0c\u5f20\u4e9a\u5e73\u7559\u4e0b\u6765\u4e0e\u8054\u901a\u5f53\u5c40\u8be6\u8c08ESS\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u5148\u8fd4\u56de\u57ce\u5357\u7684\u522b\u5885\uff0c\u4ed6\u672c\u6765\u7ea6\u8868\u54e5\u6881\u6587\u6c5f\u8ddf\u6881\u6587\u6c5f\u7684\u672a\u5a5a\u59bb\u738b\u5f69\u73b2\u591c\u91cc\u4e00\u8d77\u5403\u996d\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u56de\u5230\u57ce\u5357\u522b\u5885\u65f6\uff0c\u6881\u6587\u6c5f\u8ddf\u738b\u5f69\u73b2\u90fd\u5df2\u7ecf\u5148\u8d76\u4e86\u8fc7\u6765\uff0c\u4e0e\u4ed6\u4eec\u4e00\u8d77\u8fc7\u6765\u7684\uff0c\u8fd8\u6709\u738b\u5f69\u73b2\u7684\u54e5\u54e5\u738b\u5fd7\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u4e5f\u5728\u5317\u4eac\uff1f\u201d\u5f20\u606a\u4e0e\u738b\u5fd7\u63e1\u4e86\u63e1\u624b\uff0c\u9080\u4ed6\u4eec\u4e00\u8d77\u8fdb\u5c4b\uff0c\u5317\u4eac\u8fd9\u65f6\u8282\u4e5f\u662f\u9177\u6691\u96be\u8010\uff0c\u867d\u8bf4\u592a\u9633\u5df2\u7ecf\u897f\u659c\uff0c\u5c4b\u5916\u8fd8\u662f\u70ed\u6c14\u817e\u817e\u3002</p><p>\u3000\u3000\u738b\u5fd7\u662f\u4e13\u7a0b\u4ece\u6587\u821f\u4e58\u98de\u673a\u8d76\u56de\u5317\u4eac\u7684\uff0c\u5c31\u662f\u4e3a\u4e86\u89c1\u5f20\u606a\u4e00\u9762\uff0c\u4ed6\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u63d0\u8d77\u8fd9\u832c\uff0c\u5c31\u5f53\u662f\u6b63\u597d\u4e5f\u5728\u5317\u4eac\u3002</p><p>\u3000\u3000\u5f20\u606a\u8003\u8651\u7740\u8fc7\u6bb5\u65f6\u95f4\u62bd\u7a7a\u53bb\u4e00\u6bb5\u6587\u821f\uff0c\u53bb\u62dc\u8bbf\u5df2\u7ecf\u5230\u6d59\u4e1c\u62c5\u4efb\u7701\u59d4\u526f\u4e66\u8bb0\u7684\u8d75\u9633\uff0c\u4ed6\u8bb0\u5f97\u738b\u5f69\u73b2\u7684\u54e5\u54e5\u738b\u5fd7\u5728\u6d59\u4e1c\u7701\u653f\u5e9c\u529e\u516c\u5385\u91cc\u5de5\u4f5c\uff0c\u6709\u673a\u4f1a\u7684\u53ef\u4ee5\u5e2e\u4ed6\u4eec\u6b63\u5f0f\u5f15\u8350\u4e00\u4e0b\u3002\u5f20\u606a\u5230\u5317\u4eac\u4e4b\u524d\uff0c\u8ddf\u8868\u54e5\u6881\u6587\u6c5f\u901a\u8fc7\u7535\u8bdd\uff0c\u968f\u53e3\u63d0\u8d77\u8fd9\u4e2a\u610f\u601d\u3002</p><p>\u3000\u3000\u738b\u5fd7\u5728\u6d59\u4e1c\u4e0a\u7684\u5927\u5b66\uff0c\u6bd5\u4e1a\u540e\u6ca1\u6709\u56de\u5317\u4eac\uff0c\u8ddf\u5973\u670b\u53cb\u7559\u5728\u6587\u821f\u5de5\u4f5c\uff0c\u7ed3\u5a5a\u751f\u5b50\u3002\u867d\u7136\u738b\u5fd7\u7236\u4eb2\u738b\u65ed\u741b\u5728\u90e8\u59d4\u4e5f\u7b97\u662f\u6709\u76f8\u5f53\u7ea7\u522b\u7684\u6280\u672f\u5b98\u50da\uff0c\u4f46\u662f\u65e0\u6cd5\u5e2e\u886c\u8fdc\u5728\u6587\u821f\u5de5\u4f5c\u7684\u513f\u4e86\u3002\u738b\u5fd7\u8fd9\u51e0\u5e74\u5728\u7701\u653f\u5e9c\u91cc\u5de5\u4f5c\u9887\u4e3a\u4e0d\u987a\uff0c\u53ea\u662f\u4e8b\u4e1a\u4e5f\u7b97\u662f\u6709\u4e00\u5b9a\u57fa\u7840\uff0c\u518d\u8bf4\u59bb\u513f\u90fd\u4e60\u60ef\u5357\u65b9\u7684\u751f\u6d3b\uff0c\u4e5f\u65e0\u6cd5\u65ad\u7136\u56de\u5317\u4eac\u6765\u91cd\u65b0\u5f00\u59cb\u3002</p><p>\u3000\u3000\u4e5f\u8bb8\u5f20\u606a\u8ba4\u4e3a\u5e2e\u4ed6\u7ed9\u8d75\u9633\u5f15\u8350\u4e00\u4e0b\u662f\u5f88\u968f\u610f\u7684\u4e8b\u60c5\uff0c\u4e0d\u503c\u6709\u591a\u91cd\u89c6\uff0c\u738b\u5fd7\u5374\u6653\u5f97\u8fd9\u5bf9\u4ed6\u6765\u8bf4\u662f\u96be\u5f97\u7684\u673a\u9047\uff0c\u4e5f\u4e0d\u987e\u5f20\u606a\u8fd8\u53ea\u662f\u5728\u7535\u8bdd\u91cc\u8ddf\u6881\u6587\u6c5f\u968f\u53e3\u8bf4\u8bf4\uff0c\u4ed6\u59b9\u59b9\u738b\u5f69\u73b2\u7ed9\u4ed6\u7a0d\u900f\u9732\u53e3\u98ce\uff0c\u4ed6\u5c31\u51b3\u5b9a\u56de\u5317\u4eac\u6765\u80fd\u8ddf\u5f20\u606a\u518d\u89c1\u4e00\u9762\u66f4\u597d\u3002\u4e4b\u524d\u8ddf\u5f20\u606a\u89c1\u8fc7\u4e00\u9762\uff0c\u4f46\u662f\u4e5f\u6015\u90a3\u4e00\u9762\u4ed6\u7559\u7ed9\u5f20\u606a\u7684\u5370\u8c61\u4e0d\u6df1\u523b\uff0c\u771f\u6015\u5f20\u606a\u53ea\u662f\u968f\u4fbf\u63d0\u8d77\u5f15\u8350\u7684\u4e8b\u60c5\u4e34\u5230\u65f6\u53c8\u628a\u4ed6\u7ed9\u5fd8\u4e86\uff0c\u90a3\u4ed6\u4e5f\u53ea\u80fd\u671b\u773c\u6b32\u7a7f\u7684\u7ee7\u7eed\u5728\u6587\u821f\u865a\u8017\u9752\u6625\u3002\u867d\u7136\u9526\u6e56\u5728\u516c\u4f17\u9762\u524d\u4f9d\u65e7\u4e0d\u9732\u5c71\u4e0d\u663e\u6c34\uff0c\u4f46\u662f\u4ed6\u59b9\u59b9\u738b\u5f69\u73b2\u8ddf\u6881\u6587\u6c5f\u5df2\u7ecf\u5230\u4e86\u8c08\u5a5a\u8bba\u5ac1\u7684\u5730\u6b65\u4e86\uff0c\u738b\u5fd7\u5bf9\u9526\u6e56\u81ea\u7136\u4e5f\u6709\u8db3\u591f\u7684\u4e86\u89e3\uff0c\u77e5\u9053\u9526\u6e56\u5728\u5730\u65b9\u4ee5\u53ca\u5728\u4fe1\u606f\u7535\u5b50\u3001\u9020\u7eb8\u7b49\u4ea7\u4e1a\u5185\u5de8\u5927\u7684\u5f71\u54cd\u529b\u3002\u5c31\u662f\u73b0\u5728\u7684\u6881\u6587\u6c5f\u5728\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u90fd\u6709\u76f8\u5f53\u7684\u5f71\u54cd\u529b\u4e86\uff0c\u66f4\u4f55\u51b5\u9526\u6e56\uff1f\u9526\u6e56\u5728\u5e76\u8d2d\u60e0\u5c71\u9999\u96ea\u6d77\u5382\u65f6\uff0c\u4e0e\u65f6\u4efb\u60e0\u5c71\u5e02\u59d4\u4e66\u8bb0\u7684\u8d75\u9633\u5173\u7cfb\u5bc6\u5207\uff0c\u540e\u6765\u4e1c\u6d77\u542f\u52a8\u65b0\u7684\u9ad8\u65b0\u6280\u672f\u6218\u7565\uff0c\u5f53\u65f6\u8d75\u9633\u5c31\u88ab\u89c6\u4e3a\u662f\u575a\u5b9a\u652f\u6301\u9526\u6e56\u7684\u5730\u65b9\u6d3e\u3002\u8d75\u9633\u8c03\u5230\u6d59\u4e1c\u7701\u62c5\u4efb\u7701\u59d4\u526f\u4e66\u8bb0\uff0c\u867d\u7136\u672a\u5fc5\u5f88\u9042\u4ed6\u672c\u4eba\u7684\u5fc3\u613f\uff0c\u4f46\u6bd5\u7adf\u662f\u65c1\u4eba\u773c\u91cc\u9ad8\u4e0d\u53ef\u6500\u7684\u7701\u59d4\u526f\u4e66\u8bb0\u3002\u8981\u662f\u8bf4\u738b\u5fd7\u80fd\u8ddf\u8d75\u9633\u7684\u5206\u7ba1\u5de5\u4f5c\u6709\u76f4\u63a5\u7684\u5173\u7cfb\uff0c\u5927\u6982\u4e5f\u4e0d\u9700\u8981\u6b63\u5f0f\u7684\u5f15\u8350\uff0c\u8d75\u9633\u5c31\u80fd\u5e2e\u886c\u4e00\u628a\uff0c\u5173\u952e\u738b\u5fd7\u7684\u5de5\u4f5c\u5c97\u4f4d\u662f\u5728\u7701\u653f\u5e9c\u529e\u516c\u5385\uff0c\u8d75\u9633\u4e5f\u4e0d\u53ef\u80fd\u65e0\u7f18\u65e0\u6545\u7684\u624b\u4f38\u5230\u7701\u653f\u5e9c\u90a3\u5757\u6765\u5e2e\u886c\u4ed6\u4e00\u628a\u3002</p><p>\u3000\u3000\u738b\u5fd7\u8ddf\u7740\u59b9\u59b9\u738b\u5f69\u73b2\u4ee5\u53ca\u6881\u6587\u6c5f\u5230\u57ce\u5357\u522b\u5885\u6765\u7528\u9910\uff0c\u4e5f\u4e0d\u4f1a\u523b\u610f\u53bb\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u52a0\u6df1\u4e00\u4e0b\u63a5\u89e6\uff0c\u8ba9\u5f20\u606a\u5076\u5c14\u53bb\u6587\u821f\u65f6\u66f4\u4e0d\u5bb9\u6613\u5fd8\u6389\u4e86\u3002</p><p>\u3000\u3000\u57ce\u5357\u522b\u5885\u8fd9\u8fb9\u5f20\u606a\u4e5f\u53ea\u662f\u5230\u5317\u4eac\u6765\u624d\u4f4f\uff0c\u522b\u5885\u533a\u5916\u7684\u4e00\u5bb6\u9152\u5e97\u63d0\u4f9b\u6d3e\u5927\u53a8\u5e26\u98df\u6750\u4e0a\u95e8\u7684\u665a\u5bb4\u670d\u52a1\uff0c\u5f20\u606a\u8fd8\u662f\u559c\u6b22\u5728\u522b\u5885\u65f6\u8bf7\u4eba\u7528\u9910\u3002\u53a8\u623f\u91cc\u51c6\u5907\u7740\uff0c\u5f20\u606a\u4e0e\u6881\u6587\u6c5f\u3001\u738b\u5fd7\u5750\u4e66\u623f\u91cc\u8bf4\u6628\u5929\u7eb3\u6307\u66b4\u8dcc\u7684\u4e8b\u60c5\uff0c\u6881\u6587\u6c5f\u8bf4\u9053\uff1a\u201c\u770b\u6765\u8fd9\u4e00\u8dcc\u53c8\u8981\u6536\u4e0d\u4f4f\u52bf\u5934\u4e86\uff0c\u4eca\u5929\u6caa\u6df1\u4e24\u5e02\u7684\u7f51\u7edc\u80a1\u4e5f\u8ddf\u7740\u5927\u8dcc\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u90fd\u5c06\u8fd9\u4e8b\u7ed9\u5fd8\u4e86\uff0c\u201d\u5f20\u606a\u5934\u6291\u9760\u7740\u6905\u80cc\uff0c\u8ddf\u5750\u5728\u540e\u9762\u529e\u516c\u684c\u524d\u5199\u6750\u6599\u7684\u9648\u5983\u84c9\uff0c\u201c\u6d77\u7c9f\u79d1\u6280\u4eca\u5929\u7684\u884c\u60c5\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u5983\u84c9\u6253\u5f00\u7f51\u9875\uff0c\u5c06\u7b14\u8bb0\u672c\u671d\u5f20\u606a\u632a\u4e86\u4e2a\u65b9\u5411\uff0c\u8ba9\u4ed6\u76f4\u63a5\u770b\u6d77\u7c9f\u79d1\u6280\u8d70\u52bf\u56fe\uff0c\u5f00\u76d8\u8df3\u6c34\u7684\u5e45\u5ea6\u8f83\u6df1\uff0c\u4e0d\u8fc7\u6574\u5929\u90fd\u6709\u660e\u663e\u7684\u6258\u76d8\u75d5\u8ff9\uff0c\u80a1\u4ef7\u7ef4\u6301\u5728\u6bcf\u80a1\u4e09\u5341\u516d\u5143\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u5b8f\u4fe1\u3001\u7cbe\u5178\u8fd8\u5bf9\u589e\u53d1\u62b1\u6709\u5e0c\u671b\uff0c\u4fe1\u5fc3\u4e0e\u8d2a\u5ff5\u4e0d\u53ef\u80fd\u4e00\u51fb\u5c31\u6e83\uff0c\u4ed6\u7b11\u7740\u8ddf\u6881\u6587\u6c5f\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5c31\u4e0d\u8981\u7ba1\u8bc1\u5238\u5e02\u573a\u7684\u90a3\u4e9b\u4e8b\u4e86\uff0c\u4e00\u95e8\u5fc3\u601d\u5c06\u6606\u817e\u7ecf\u8425\u597d\u5c31\u884c\uff0c\u6700\u7ec8\u6606\u817e\u7684\u80a1\u4ef7\u8fd8\u662f\u9700\u8981\u4e1a\u7ee9\u4ee5\u53ca\u826f\u6027\u7684\u5546\u4e1a\u6a21\u5f0f\u53bb\u652f\u6491\u2026\u2026\u8981\u662f\u4f60\u4eec\u7ba1\u7406\u5c42\u60f3\u5728\u4f4e\u8c37\u65f6\u589e\u6301\u6269\u5927\u6301\u80a1\u6bd4\u4f8b\uff0c\u6211\u770b\u8fd8\u662f\u8981\u7b49\u4e0a\u4e00\u6bb5\u65f6\u95f4\u518d\u52a8\u624b\u4e0d\u8fdf\u3002\u201d</p><p>\u3000\u3000\u738b\u5fd7\u5012\u4e0d\u662f\u5bf9\u6881\u6587\u6c5f\u770b\u4e0d\u4e0a\u773c\uff0c\u6881\u6587\u6c5f\u7ed9\u4eba\u7684\u5370\u8c61\u4e00\u5411\u5c31\u8f83\u4e3a\u6c89\u95f7\uff0c\u9189\u5fc3\u4e8e\u6280\u672f\u7684\u90a3\u79cd\uff0c\u90a3\u65f6\u4ed6\u5728\u8857\u4e0a\u649e\u7834\u59b9\u59b9\u8ddf\u6881\u6587\u6c5f\u8c08\u604b\u7231\u7684\u4e8b\u60c5\uff0c\u5c31\u60f3\u7740\u4ed6\u59b9\u59b9\u662f\u90a3\u4e48\u8bdd\u591a\u7684\u4e00\u4e2a\u5973\u5b69\u5b50\uff0c\u8ddf\u6881\u6587\u6c5f\u751f\u6d3b\u4e00\u8f88\u5b50\uff0c\u4f1a\u4e0d\u4f1a\u89c9\u5f97\u95f7\uff1f\u90a3\u65f6\u5019\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u6881\u6587\u6c5f\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u6709\u5982\u6b64\u7684\u6210\u5c31\uff0c\u8981\u62ff\u6606\u817e\u5728\u7ebf\u5728\u7eb3\u65af\u7eb3\u8bc1\u5238\u4ea4\u6613\u5e02\u573a\u7684\u5cf0\u503c\u8ba1\u7b97\uff0c\u6881\u6587\u6c5f\u7684\u540d\u4e0b\u80a1\u6743\u8d44\u4ea7\u4e00\u5ea6\u8d85\u8fc7\u56db\u5343\u4e07\u7f8e\u5143\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u5bf9\u4e2a\u4eba\u8d22\u4ea7\u7684\u4e8b\u60c5\u4e5f\u770b\u5f97\u5f88\u6de1\uff0c\u82e5\u662f\u4e3a\u4e86\u4e2a\u4eba\u8d22\u5bcc\uff0c\u5f20\u606a\u65e9\u5c31\u8ddf\u4ed6\u5efa\u8bae\u5728\u6606\u817e\u5728\u7ebf\u5728\u7eb3\u65af\u8fbe\u514b\u8bc1\u5238\u4ea4\u6613\u5e02\u573a\u4e0a\u5e02\u4ea4\u6613\u540e\u5c31\u7acb\u5373\u4ece\u6606\u817e\u5728\u7ebf\u8f9e\u53bb\u9ad8\u7ba1\u7684\u804c\u4f4d\uff0c\u7136\u540e\u5c31\u53ef\u4ee5\u5728\u534a\u5e74\u540e\u5b8c\u5168\u51cf\u6301\u624b\u91cc\u6240\u6301\u80a1\u7968\uff0c\u82b1\u4e3a\u5b9e\u5b9e\u5728\u5728\u7684\u8d22\u5bcc\u2014\u2014\u6881\u6587\u6c5f\u6ca1\u6709\u4ec0\u4e48\u52a8\u6447\uff0c\u53ea\u662f\u773c\u89c1\u7eb3\u6307\u53c8\u5c06\u5d29\u76d8\u5f0f\u5927\u8dcc\uff0c\u5fcd\u4e0d\u4f4f\u4f1a\u628a\u8bdd\u9898\u5f80\u8fd9\u4e0a\u9762\u626f\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u7b2c\u4e09\u5929\u56de\u5efa\u90ba\u65f6\uff0c\u7eb3\u6307\u4ee5\u53ca\u56fd\u5185\u6caa\u6df1\u4e24\u5e02\u7f51\u7edc\u80a1\u4ef7\u5927\u8dcc\u4e5f\u6210\u4e3a\u4eba\u4eec\u8c08\u8bba\u7684\u7126\u70b9\u8bdd\u9898\u4e4b\u4e00\u3002\u5f20\u606a\u56de\u5230\u5efa\u90ba\uff0c\u4eba\u90fd\u6ca1\u6709\u4f11\u606f\uff0c\u675c\u98de\u7684\u7535\u8bdd\u5c31\u6253\u4e86\u8fdb\u6765\uff0c\u95ee\u4ed6\u6709\u6ca1\u6709\u65f6\u5019\u53bb\u4e1c\u5927\u521b\u57df\u8d70\u4e00\u8d9f\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5f53\u675c\u98de\u4ed6\u4eec\u8fd8\u662f\u62c5\u4efb\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u672a\u6765\uff0c\u867d\u7136\u5750\u98de\u673a\u56de\u5efa\u90ba\u6709\u4e9b\u8170\u9178\uff0c\u4f46\u662f\u8ddf\u675c\u98de\u4ed6\u4eec\u5728\u4e00\u8d77\u66f4\u80fd\u7f13\u89e3\u75b2\u52b3\uff0c\u95ee\u9648\u5983\u84c9\uff1a\u201c\u8981\u4e0d\u8981\u4e00\u8d77\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u62ff\u8d77\u624b\u91cc\u7684\u4e00\u53e0\u6587\u4ef6\uff0c\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u6211\u73b0\u5728\u53ef\u662f\u5728\u5de5\u4f5c\uff0c\u4f60\u53ef\u4ee5\u56db\u5904\u6e9c\u8fbe\uff0c\u6211\u8fd8\u8981\u56de\u884c\u653f\u5ba4\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u8da3\u7684\u6487\u4e86\u6487\uff0c\u5f00\u8f66\u5c06\u9648\u5983\u84c9\u9001\u5230\u4e16\u7eaa\u9526\u6e56\u697c\u4e0b\uff0c\u518d\u5f00\u8f66\u53bb\u521a\u521a\u8fc1\u5165\u6e56\u4e1c\u5546\u5708\u5185\u7684\u4e1c\u5927\u521b\u57df\uff0c\u8f66\u5b50\u521a\u5230\u697c\u4e0b\uff0c\u521a\u60f3\u627e\u5730\u65b9\u505c\u4e0b\u6765\uff0c\u5c31\u770b\u89c1\u675c\u98de\u5728\u697c\u6807\u524d\u7ad9\u7740\uff0c\u6253\u5f00\u8f66\u95e8\u94bb\u4e86\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u4e0d\u53bb\u4f60\u7684\u5730\u76d8\u4e0a\u8f6c\u4e00\u8f6c\uff0c\u51c6\u5907\u53bb\u54ea\u91cc\u554a\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u5728\u98de\u9e3f\u5e7f\u544a\u7684\u6444\u5f71\u68da\u90a3\u91cc\uff0c\u6211\u4eec\u8fc7\u53bb\u2026\u2026\u201d\u675c\u98de\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5e74\u8f7b\u7f8e\u8c8c\u7684\u6a21\u7279\uff0c\u6211\u5bf9\u6765\u8bf4\u5df2\u7ecf\u6ca1\u6709\u5438\u5f15\u529b\u4e86\uff0c\u53bb\u90a3\u91cc\u505a\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u675c\u98de\u4e5f\u4e0d\u660e\u8bf4\uff0c\u8ba9\u5f20\u606a\u5f00\u8f66\u8f7d\u4ed6\u8fc7\u53bb\u3002\u98de\u9e3f\u5e7f\u544a\u7684\u6444\u5f71\u5de5\u4f5c\u5ba4\u4e5f\u5728\u6e56\u4e1c\u5546\u5708\u7684\u5199\u5b57\u697c\u91cc\uff0c\u62d0\u8fc7\u8857\u89d2\u5c31\u662f\uff0c\u5f20\u606a\u5c06\u8f66\u505c\u5728\u697c\u4e0b\uff0c\u4e0e\u675c\u98de\u8d70\u8fdb\u6444\u5f71\u68da\u91cc\u3002</p><p>\u3000\u3000\u6444\u5f71\u68da\u7684\u5317\u4fa7\u6253\u7740\u5404\u5f0f\u5404\u6837\u7684\u6444\u5f71\u7528\u706f\uff0c\u79bb\u5f97\u7a0d\u8fdc\u4e00\u4e9b\uff0c\u53ea\u770b\u89c1\u95ea\u5149\u706f\u524d\u7684\u5973\u5b69\u5b50\u767d\u7699\u660e\u4e3d\uff0c\u7ed9\u706f\u5149\u6253\u5f97\u592a\u4eae\uff0c\u8138\u53cd\u800c\u770b\u4e0d\u771f\u5207\uff0c\u7a7f\u7740\u540a\u5e26\u8fde\u8863\u88d9\uff0c\u5f88\u6709\u9752\u6625\u6d3b\u529b\u7684\u6837\u5b50\uff0c\u6444\u5f71\u5e08\u8ddf\u6444\u5f71\u52a9\u7406\u5728\u5916\u56f4\u4e3e\u7740\u76f8\u7167\u62cd\u4e2a\u4e0d\u505c\uff0c\u8fd8\u4e0d\u505c\u7684\u8c03\u6574\u5149\u7ebf\u7684\u5f3a\u5f31\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u65f6\u5b66\u658c\u3001\u4ee4\u5c0f\u71d5\u4ed6\u4eec\u7ad9\u5728\u6444\u5f71\u68da\u7684\u8fb9\u4e0a\uff0c\u4ed6\u8ddf\u675c\u98de\u8d70\u8fc7\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u90fd\u6709\u8fd9\u95f2\u5de5\u592b\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u54e5\u54e5\uff01\u201d\u7ed9\u706f\u5149\u5305\u56f4\u7740\u7684\u5973\u5b69\u5b50\u770b\u7740\u5f20\u606a\u8ddf\u675c\u98de\u8d70\u8fdb\u6765\uff0c\u5174\u594b\u7684\u53eb\u4e86\u8d77\u6765\uff0c\u58f0\u97f3\u8106\u8106\u7684\uff0c\u4e5f\u4e0d\u987e\u90a3\u8fb9\u6b63\u62cd\u7740\u7167\uff0c\u8ddf\u6444\u5f71\u5e08\u6446\u6446\u624b\uff0c\u5c31\u5f80\u8fd9\u8fb9\u8df3\u7740\u8d70\u8fc7\u6765\uff0c\u8d70\u5230\u4e00\u534a\uff0c\u7a81\u7136\u505c\u4e0b\u6765\uff0c\u50cf\u662f\u52a8\u4f5c\u592a\u5927\uff0c\u5c06\u8fde\u8863\u88d9\u80cc\u540e\u7684\u6263\u5b50\u7ed9\u7ef7\u65ad\uff0c\u5979\u624b\u522b\u5230\u80cc\u540e\u6293\u4f4f\u655e\u5f00\u7684\u540e\u5f00\u9886\uff0c\u558a\u675c\u98de\uff1a\u201c\u675c\u98de\uff0c\u6211\u88d9\u5b50\u7684\u6263\u5b50\u6389\u4e86\uff0c\u4f60\u5feb\u6765\u5e2e\u6211\u6309\u4f4f\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u6ca1\u597d\u610f\u601d\uff0c\u4ee4\u5c0f\u71d5\u8d70\u8fc7\u53bb\u5e2e\u5fd9\u3002\u65f6\u5b66\u658c\u5728\u4e00\u65c1\u63f6\u63c4\u7684\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u7ed9\u4e00\u58f0\u5f20\u606a\u54e5\u54e5\u5fc3\u90fd\u53eb\u9165\u4e86\u534a\u622a\uff0c\u8fd9\u65f6\u5019\u89c9\u5f97\u8fd8\u662f\u7ed9\u76f4\u547c\u5176\u540d\u66f4\u4eb2\u70ed\uff1f\u8fd9\u4e00\u58f0\u58f0\u675c\u98de\u7ed9\u5524\u5f97\uff0c\u5fc3\u90fd\u70c2\u6210\u6d46\u7cca\u4e86\u3002\u201d</p><p>\u3000\u3000\u675c\u98de\u6376\u4e86\u65f6\u5b66\u658c\u4e00\u62f3\uff0c\u53c8\u6478\u4e86\u6478\u9f3b\u5b50\uff1b\u5f20\u606a\u5fae\u5fae\u800c\u7b11\uff0c\u5374\u4e0d\u77e5\u9053\u5468\u6653\u7490\u4ec0\u4e48\u65f6\u5019\u6765\u5efa\u90ba\u73a9\u4e86\uff0c\u60f3\u8d77\u524d\u4e16\u5468\u6653\u7490\u8ddf\u675c\u98de\u90a3\u6bb5\u60c5\u7f18\uff0c\u5fc3\u60f3\u51a5\u51a5\u4e2d\u4e5f\u8bb8\u6709\u4e9b\u4e8b\u60c5\u8fd8\u662f\u8981\u6ce8\u5b9a\u53d1\u751f\u7684\u3002</p><p>\u3000\u3000\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u6ca1\u6709\u89c1\u9762\uff0c\u5468\u6653\u7490\u6108\u53d1\u51fa\u8131\u7684\u6709\u5a77\u5a77\u7389\u7acb\u7f8e\u5c11\u5973\u7684\u6837\u5b50\u4e86\uff0c\u4ee4\u5c0f\u71d5\u8ddf\u6444\u5f71\u5ba4\u7684\u5de5\u4f5c\u4eba\u5458\u627e\u6765\u522b\u9488\u5c06\u8fde\u8863\u88d9\u522b\u597d\uff0c\u5468\u6653\u7490\u8d70\u5230\u5f20\u606a\u7684\u9762\u524d\uff0c\u5fae\u4ef0\u7740\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u8eab\u8863\u670d\u6f02\u4e0d\u6f02\u4eae\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u8863\u670d\u5012\u4e5f\u7f62\uff0c\u5173\u952e\u770b\u8c01\u7a7f\u4e86\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u89c1\u5468\u6653\u7490\u7a7f\u7684\u8fd9\u8eab\u540a\u5e26\u8fde\u8863\u88d9\u6709\u51e0\u5206\u7f51\u6e38showgirl\u7684\u5473\u9053\uff0c\u56de\u5934\u95ee\u675c\u98de\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u8ba9\u5468\u5c0f\u59b9\u540c\u5b66\u6765\u8bd5\u955c\u554a\uff1f\u201d\u62ff\u8d77\u624b\u673a\u8ddf\u9648\u5983\u84c9\u901a\u7535\u8bdd\uff0c\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u6709\u501f\u53e3\u6e9c\u51fa\u6765\u4e86\uff0c\u5468\u5c0f\u59b9\u540c\u5b66\u5728\u5efa\u90ba\u5462\uff0c\u8981\u4e0d\u8981\u6211\u5f00\u8f66\u8fc7\u53bb\u63a5\u4f60\u53bb\uff1f\u201d</p><p>\u3000\u3000\u675c\u98de\u626f\u4e86\u626f\u5f20\u606a\u7684\u8863\u8896\uff0c\u62c9\u4ed6\u5230\u4e00\u8fb9\u8bf4\u8bdd\u53bb\uff1a\u201c\u6211\u4eec\u6e38\u620f\u5e73\u53f0\u6253\u7b97\u627e\u4e00\u4ee3\u8a00\u4eba\uff0c\u8fd9\u6b21\u65f6\u5b66\u658c\u8ddf\u6211\u56de\u6d77\u5dde\u53bb\uff0c\u4f4f\u4e86\u4e24\u5929\uff0c\u968f\u4fbf\u627e\u6653\u7490\u51fa\u6765\u6643\u8361\uff0c\u65f6\u5b66\u658c\u76ef\u4e0a\u6653\u7490\uff0c\u89c9\u5f97\u5979\u7684\u6c14\u8d28\u5408\u9002\u505a\u6211\u4eec\u4ee3\u8a00\u4eba\u7684\u8981\u6c42\uff0c\u6653\u7490\u5979\u4e5f\u559c\u6b22\uff0c\u5c31\u8ddf\u7740\u5230\u5efa\u90ba\u4e00\u4e9b\u6765\u73a9\uff0c\u53cd\u6b63\u4e5f\u662f\u6691\u5047\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u8ba9\u5979\u5728\u5efa\u90ba\u73a9\u51e0\u5929\u5457\uff0c\u62c9\u7740\u6211\u8eb2\u4e00\u8fb9\u6765\uff0c\u795e\u795e\u79d8\u79d8\u7684\uff0c\u8ba9\u522b\u4eba\u770b\u89c1\u8fd8\u662f\u5546\u91cf\u4ec0\u4e48\u574f\u4e8b\u5462\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p></article>"}}}]);
