(window.webpackJsonp=window.webpackJsonp||[]).push([[1173],{1196:function(p){p.exports={chapterTitle:null,title:"\u7b2c1170\u7ae0 \u53eb\u4f60\u4e2b\u7684\u88c5\u903c",content:"<article><h2>\u7b2c1170\u7ae0 \u53eb\u4f60\u4e2b\u7684\u88c5\u903c</h2><p>\u3000\u3000\u5b59\u9759\u6aac\u5403\u4e0d\u5f97\u8fa3\u7684\uff0c\u5374\u5bf9\u5c06\u5ddd\u6e1d\u996d\u5e97\u90a3\u4e2a\u5973\u5b69\u5b50\u9a97\u52301978\u5de5\u4f5c\u7684\u4e8b\u60c5\u5f88\u611f\u5174\u8da3\u3002\u5f20\u606a\u53ea\u662f\u60f3\u963b\u6b62\u4e00\u4e9b\u53ef\u80fd\u4f1a\u53d1\u751f\u7684\u60b2\u5267\u91cd\u6f14\uff0c\u5b59\u9759\u6aac\u5374\u5bfb\u627e\u5230\u4e9b\u6076\u4f5c\u5267\u7684\u8da3\u5473\uff0c\u62ff\u4e86\u4e00\u5f20\u9910\u5dfe\u7eb8\u5c061978\u7684\u5730\u5740\u4e0e\u8054\u7edc\u65b9\u5f0f\u6284\u7ed9\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u770b\u7740\u5973\u5b69\u5b50\u62ff\u7740\u9910\u5dfe\u7eb8\u4e0e\u591a\u4f59\u7684\u7897\u789f\u56de\u5230\u64cd\u4f5c\u95f4\u53bb\uff0c\u5c0f\u58f0\u7684\u95ee\u5f20\u606a\uff1a\u201c\u5c31\u8fd9\u4e48\u8bf4\u8981\u5e2e\u5979\u4ecb\u7ecd\u5de5\u4f5c\uff0c\u4f1a\u4e0d\u4f1a\u592a\u7a81\u7136\u4e86\uff0c\u4e0d\u8981\u7ed9\u4eba\u5bb6\u5f53\u6210\u9a97\u5b50\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8eab\u4e0a\u54ea\u6709\u534a\u70b9\u50cf\u9a97\u5b50\u7684\u5730\u65b9\uff1f\u201d\u5f20\u606a\u53c9\u5f00\u624b\u81c2\u6401\u5728\u6905\u80cc\u4e0a\uff0c\u4ed6\u4e5f\u6653\u5f97\u6709\u4e9b\u5510\u7a81\u4e86\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff0c\u5927\u4e0d\u4e86\u6539\u5929\u8ba9\u522b\u4eba\u8fc7\u6765\u505a\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u8fc7\u4e86\u516b\u6708\u5c31\u5230\u56de\u6d77\u5dde\u8fdb\u5165\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u5de5\u4f5c\uff0c\u9700\u8981\u53e6\u627e\u4e2a\u4eba\u6765\u63a5\u624b1978\u3002</p><p>\u3000\u3000\u4e5f\u662f\u675c\u98de\u60f3\u5c06\u5468\u7490\u5b89\u6392\u8fdb\u4e16\u7eaa\u534e\u97f3\u7684\u5fc3\u601d\u8ba9\u4ed6\u60f3\u8d77\u4e86\u8bb8\u591a\u672a\u66fe\u53d1\u751f\u7684\u5f80\u4e8b\uff0c\u4e5f\u4e0d\u662f\u4e3a\u4e86\u8499\u4e50\u2014\u2014\u8499\u4e50\u6839\u672c\u8ddf\u8fd9\u4e2a\u5973\u5b69\u5b50\u6ca1\u6709\u4ea4\u96c6\u2014\u2014\u5f20\u606a\u53ea\u662f\u4e0d\u60f3\u770b\u5230\u7f8e\u597d\u7684\u4e1c\u897f\u8ba9\u8fd9\u5760\u843d\u4e0d\u4f11\u7684\u4e16\u4fd7\u6e6e\u706d\u6389\uff0c\u7279\u522b\u662f\u4ed6\u8fd8\u80fd\u505a\u4e9b\u4ec0\u4e48\u4e8b\u60c5\u7684\u65f6\u5019\uff0c\u4ed6\u4e00\u76f4\u90fd\u8bb0\u5f97\u8fd9\u4e2a\u5973\u5b69\u5b50\u59d3\u7530\uff0c\u4e0d\u77e5\u9053\u5979\u7684\u5927\u540d\u53eb\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u4f60\u54ea\u70b9\u4e0d\u50cf\u9a97\u5b50\uff0c\u201d\u5b59\u9759\u6aac\u4e0d\u5c51\u7684\u8bf4\u9053\uff0c\u62ff\u9910\u5dfe\u7eb8\u94fa\u5728\u7a0d\u6709\u4e9b\u6cb9\u817b\u7684\u684c\u4e0a\uff0c\u80f3\u818a\u8098\u652f\u5728\u4e0a\u9762\uff0c\u6258\u7740\u4e0b\u5df4\uff0c\u4f3c\u7b11\u975e\u7b11\u7684\u51dd\u89c6\u7740\u5f20\u606a\uff0c\u201c\u6211\u600e\u4e48\u611f\u89c9\u4f60\u5e38\u8eab\u4e0a\u4e0b\u90fd\u503c\u5f97\u6000\u7591\uff1f\u201d</p><p>\u3000\u3000\u201c\u5bf9\u4e0d\u8d77\uff0c\u5c0f\u7530\u5728\u8fd9\u91cc\u5f53\u670d\u52a1\u5458\u86ee\u597d\u7684\uff0c\u4e0d\u9700\u8981\u4e8c\u4f4d\u52b3\u5fc3\u66ff\u5979\u989d\u5916\u4ecb\u7ecd\u4ec0\u4e48\u966a\u9152\u5c0f\u59d0\u7684\u5de5\u4f5c\u2026\u2026\u201d\u4e00\u540d\u56db\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u5987\u5973\u4ece\u64cd\u4f5c\u95f4\u90a3\u5934\u8d70\u8fc7\u6765\uff0c\u5c06\u5199\u67091978\u8054\u7edc\u65b9\u5f0f\u7684\u9910\u5dfe\u7eb8\u4e22\u5230\u5f20\u606a\u7684\u9762\u524d\u3002</p><p>\u3000\u3000\u770b\u996d\u5e97\u8001\u677f\u5a18\u8b66\u60d5\u800c\u514b\u5236\u7684\u773c\u795e\uff0c\u5c31\u77e5\u9053\u5979\u8bef\u4f1a\u4e86\uff0c\u5f20\u606a\u6447\u5934\u82e6\u7b11\uff0c\u5728\u56fd\u5185\u9152\u5427\u8fd9\u4e9b\u591c\u5e97\u603b\u662f\u8ddf\u4e0d\u4e09\u4e0d\u56db\u7684\u5a31\u4e50\u573a\u6240\u8054\u7cfb\u5728\u4e00\u8d77\uff0c\u8981\u4e0d\u662f\u521a\u624d\u4ed6\u4eec\u6ca1\u6709\u8bf4\u4ec0\u4e48\u8fc7\u5206\u7684\u8bdd\uff0c\u5979\u5927\u6982\u4f1a\u76f4\u63a5\u8d76\u4ed6\u4eec\u51fa\u95e8\u5427\uff1f\u5373\u4f7f\u77e5\u9053\u4f1a\u5f88\u65e0\u529b\uff0c\u5f20\u606a\u8fd8\u662f\u7565\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u4f60\u662f\u8bef\u4f1a\u4e86\uff0c\u8fd9\u95f4\u9152\u5427\u662f\u6211\u4eec\u670b\u53cb\u5c0f\u805a\u7684\u5730\u65b9\uff0c\u4e0d\u56fe\u8d62\u5229\uff0c\u56e0\u4e3a\u4e4b\u524d\u8d1f\u8d23\u4eba\u8981\u79bb\u5f00\u5efa\u90ba\uff0c\u6240\u4ee5\u9700\u8981\u65b0\u7684\u4eba\u63a5\u624b\u3002\u5c0f\u7530\u8ddf\u6211\u4eec\u4e00\u4e2a\u670b\u53cb\u957f\u5f97\u5f88\u50cf\uff0c\u6240\u4ee5\u89c9\u5f97\u8bf7\u5979\u8fc7\u53bb\u5e2e\u5fd9\u4f1a\u89c9\u5f97\u5f88\u6709\u8da3\u2026\u2026\u201d</p><p>\u3000\u3000\u996d\u5e97\u8001\u677f\u5a18\u5c06\u4fe1\u5c06\u7591\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4ecd\u7136\u575a\u6301\u7684\u8bf4\u9053\uff1a\u201c\u5c0f\u7530\u4e0d\u9700\u8981\u53e6\u627e\u4ec0\u4e48\u5de5\u4f5c\uff0c\u8c22\u8c22\u4f60\u4eec\u4e86\u2026\u2026\u201d\u4e22\u4e0b\u8fd9\u4e48\u53e5\u8bdd\u5c31\u8d70\u4e86\u3002\u63a5\u4e0b\u6765\u8fd9\u8fb9\u4e5f\u662f\u5176\u4ed6\u670d\u52a1\u5458\u4e0a\u83dc\uff0c\u59d3\u7530\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\u7167\u987e\u5176\u4ed6\u684c\u53bb\u4e86\uff0c\u60f3\u5fc5\u4e5f\u662f\u60f3\u8eb2\u5f00\u8fd9\u4e24\u4e2a\u201c\u8863\u51a0\u9c9c\u695a\u7684\u9a97\u5b50\u201d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u53ea\u80fd\u62b1\u4ee5\u82e6\u7b11\uff0c\u60f3\u7740\u4e5f\u8bb8\u627e\u5230\u719f\u6089\u5730\u65b9\u7684\u4eba\u8fc7\u6765\u8bf4\u4e00\u8bf4\u66f4\u80fd\u53d6\u4fe1\u4e8e\u4eba\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5403\u4e0d\u4e86\u8fa3\u7684\uff0c\u83dc\u90fd\u8981\u5728\u77ff\u6cc9\u6c34\u91cc\u6dae\u4e00\u904d\u624d\u6562\u5403\u4e0b\u53bb\uff0c\u8fd9\u8fdb\u9910\u7684\u901f\u5ea6\u81ea\u7136\u662f\u5feb\u4e0d\u4e86\uff0c\u5f20\u606a\u4e5f\u4e50\u4e8e\u966a\u5979\u5728\u996d\u5e97\u91cc\u591a\u8017\u4e9b\u65f6\u95f4\uff0c\u53ea\u662f\u59d3\u7530\u7684\u5973\u5b69\u5b50\u8b66\u60d5\u5fc3\u4e0d\u6d88\uff0c\u4e00\u76f4\u90fd\u8fdc\u8fdc\u907f\u5f00\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5728\u4ed6\u4eec\u5403\u996d\u65f6\uff0c\u53c8\u8d70\u8fdb\u6765\u56db\u4e94\u4e2a\u9752\u5e74\uff0c\u8d70\u8fdb\u6765\u521a\u5750\u4e0b\u5c31\u5927\u547c\u5c0f\u53eb\u8d77\u6765\uff1a\u201c\u5feb\u628a\u83dc\u5355\u62ff\u8fc7\u6765\uff0c\u809a\u5b50\u90fd\u997f\u762a\u4e86\uff0c\u8981\u997f\u51fa\u95ee\u9898\uff0c\u4f60\u4eec\u996d\u5e97\u53ef\u8d1f\u8d23\uff1f\u5618\u2026\u2026\u201d\u8fd9\u624d\u6ce8\u610f\u5230\u5750\u5728\u4e00\u8fb9\u7684\u5b59\u9759\u6aac\uff0c\u5fcd\u4e0d\u4f4f\u8f7b\u4f7b\u7684\u5439\u8d77\u53e3\u54e8\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6293\u4f4f\u5b59\u9759\u6aac\u7684\u5c0f\u624b\uff0c\u5f97\u8ba9\u8fd9\u59ae\u5b50\u5fc3\u91cc\u6709\u6570\uff0c\u81ea\u5df1\u53ef\u4e0d\u662f\u4ec0\u4e48\u8d85\u4eba\uff0c\u5979\u8981\u662f\u514b\u5236\u4e0d\u4f4f\u5c06\u684c\u4e0a\u90a3\u7897\u8336\u6c34\u6cfc\u4eba\u5bb6\u8138\u4e0a\u53bb\uff0c\u5728\u4fdd\u9556\u4eec\u8d76\u8fc7\u6765\u4e4b\u524d\uff0c\u4ed6\u5f88\u53ef\u80fd\u5df2\u7ecf\u7ed9\u4eba\u5bb6\u63cd\u6210\u732a\u5934\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u81ea\u5df1\u53c8\u4e0d\u662f\u4ec0\u4e48\u90fd\u4e0d\u61c2\u3001\u53ea\u4f1a\u4e71\u53d1\u813e\u6c14\u7684\u5927\u5c0f\u59d0\uff0c\u53ea\u662f\u6709\u65f6\u5019\u5bf9\u6709\u4e9b\u4e8b\u60c5\u5b9e\u5728\u770b\u4e0d\u60ef\u7f62\u4e86\u2014\u2014\u5979\u4e5f\u6ca1\u6709\u7b49\u4fdd\u9556\u8fc7\u6765\u518d\u4ed7\u52bf\u6b3a\u4eba\u7684\u4e60\u60ef\uff0c\u53ea\u662f\u4fa7\u7740\u8eab\u5b50\u4e0d\u53bb\u7406\u4f1a\u90a3\u4e9b\u6311\u9017\u7684\u53e3\u54e8\u4ee5\u53ca\u8086\u65e0\u5fcc\u60ee\u7684\u773c\u795e\uff0c\u6162\u6761\u4e1d\u7406\u7ee7\u7eed\u6dae\u83dc\u5403\u3002</p><p>\u3000\u3000\u5927\u6982\u8fd9\u51e0\u4e2a\u9752\u5e74\u770b\u5230\u5f20\u606a\u505c\u5728\u95e8\u5916\u7684\u8f7f\u8f66\uff0c\u89c1\u5f20\u606a\u8ddf\u5b59\u9759\u6aac\u8ddf\u9a7c\u9e1f\u4f3c\u7684\u4e0d\u542d\u58f0\uff0c\u4ed6\u4eec\u4e5f\u6ca1\u6709\u8fc7\u5206\u7684\u6311\u8845\u3002\u59d3\u7530\u7684\u5973\u5b69\u5b50\u62ff\u7740\u83dc\u5355\u8fc7\u53bb\u7ed9\u4ed6\u4eec\u70b9\u83dc\uff0c\u4ed6\u4eec\u5c31\u6ca1\u6709\u90a3\u4e48\u8001\u5b9e\uff0c\u6709\u4eba\u70b9\u83dc\uff0c\u6709\u4e9b\u563b\u76ae\u7b11\u8138\u7684\u642d\u8baa\uff0c\u8fd8\u6709\u4e2a\u9752\u5e74\u5c06\u4e00\u53ea\u624b\u6084\u6084\u7684\u644a\u5728\u5973\u5b69\u5b50\u7684\u81c0\u90e8\u540e\u9762\uff0c\u60f3\u7b49\u7740\u5979\u540e\u9000\u81ea\u52a8\u5c06\u5c41\u80a1\u8d34\u5230\u4ed6\u624b\u4e0a\u53bb\u3002\u59d3\u7530\u7684\u5973\u5b69\u5b50\u5012\u662f\u673a\u7075\uff0c\u5fcd\u6c14\u541e\u58f0\u7684\u5199\u4e0b\u83dc\u5355\u79bb\u5f00\uff0c\u7b49\u4e0a\u83dc\u65f6\uff0c\u5c31\u8fdc\u8fdc\u8eb2\u5728\u8fd9\u684c\uff0c\u8ba9\u5176\u4ed6\u670d\u52a1\u5458\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u4e2a\u9752\u5e74\u5374\u4e0d\u4e50\u610f\u4e86\uff0c\u62ff\u7740\u7b77\u5b50\u5c06\u7897\u789f\u6572\u5f97\u549a\u549a\u549a\u76f4\u54cd\uff0c\u6709\u4e2a\u9752\u5e74\u62cd\u7740\u684c\u5b50\u7ad9\u8d77\u718a\u8d77\u8138\u77aa\u7740\u670d\u52a1\u5458\u558a\u9053\uff1a\u201c\u770b\u4e0d\u8d77\u6211\u4eec\u662f\u4e0d\u662f\uff1f\u6211\u4eec\u51fa\u6765\u6df7\u5c31\u56fe\u4e2a\u9762\u5b50\uff0c\u53c8\u4e0d\u662f\u7f3a\u4f60\u4eec\u94b1\uff0c\u521a\u624d\u90a3\u4e2a\u5c0f\u670d\u52a1\u5458\u4e3a\u4ec0\u4e48\u8dd1\u522b\u7684\u684c\u53bb\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u60f3\u770b\u8fd9\u51e0\u4e2a\u9752\u5e74\u4f1a\u6709\u4ec0\u4e48\u8868\u6f14\uff0c\u7d27\u63a5\u7740\u5c31\u770b\u4e00\u4e2a\u9752\u5e74\u53c8\u62cd\u7740\u684c\u5b50\u7ad9\u8d77\u6765\u558a\u9053\uff1a\u201c\u8fd9\u83dc\u91cc\u6709\u788e\u73bb\u7483\uff0c\u4f60\u4eec\u60f3\u5403\u6b7b\u6211\u4eec\u554a\uff01\u201d\u4ed6\u521a\u521a\u770b\u5230\u8fd9\u9752\u5e74\u4ece\u53e3\u888b\u91cc\u638f\u4ec0\u4e48\u4e1c\u897f\u6492\u76d8\u5b50\u91cc\uff0c\u6ca1\u60f3\u5230\u662f\u788e\u73bb\u7483\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e0d\u8d77\u2026\u2026\u201d\u5e97\u8001\u677f\u662f\u4e2a\u56db\u5341\u591a\u5c81\u7684\u4e2d\u5e74\u4eba\uff0c\u5fd9\u8dd1\u51fa\u6765\u6253\u5706\u573a\uff0c\u521a\u624d\u90a3\u4e2a\u8001\u677f\u5a18\u7ad9\u5728\u89d2\u843d\uff0c\u6709\u4e9b\u5e94\u4ed8\u4e0d\u4e86\u8fd9\u6837\u7684\u573a\u9762\uff0c\u5e97\u8001\u677f\u966a\u7b11\u8138\u8bf4\u9053\uff0c\u201c\u771f\u662f\u5bf9\u4e0d\u8d77\uff0c\u53ef\u80fd\u662f\u6361\u83dc\u65f6\u4e0d\u5c0f\u5fc3\uff0c\u6211\u8ba9\u53a8\u623f\u7ed9\u4f60\u4eec\u91cd\u505a\u4e00\u76d8\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u5478\uff01\u8fd9\u662f\u4ec0\u4e48\u8089\uff0c\u600e\u4e48\u8fd9\u4e2a\u5473\uff1f\u201d\u5750\u7684\u4e00\u4e2a\u9752\u5e74\u7a81\u7136\u5c06\u5634\u91cc\u56bc\u7740\u4e00\u53e3\u83dc\u5410\u684c\u4e0a\uff0c\u201c\u4f60\u4eec\u5bb6\u662f\u5f00\u9ed1\u5e97\u7684\u554a\uff0c\u8fd9\u662f\u4ec0\u4e48\u8089\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u80fd\u662f\u51b0\u7bb1\u4e32\u5473\u4e86\uff0c\u6211\u9a6c\u4e0a\u6362\u4e0b\u53bb\u91cd\u505a\u3002\u201d\u5e97\u8001\u677f\u81ea\u8ba4\u5012\u9709\uff0c\u4e00\u4e2a\u52b2\u7684\u966a\u7740\u7b11\u8138\uff0c\u4ec0\u4e48\u9519\u90fd\u63fd\u4e0b\u6765\uff0c\u5e0c\u671b\u8fd9\u4f19\u4eba\u4e0d\u8981\u6572\u8bc8\u5f97\u592a\u72e0\u3002</p><p>\u3000\u3000\u201c\u8089\u660e\u660e\u662f\u4eca\u5929\u65e9\u4e0a\u521a\u4e70\u7684\uff0c\u6211\u521a\u624d\u770b\u5230\u788e\u73bb\u7483\u662f\u4ed6\u4eec\u81ea\u5df1\u4e22\u8fdb\u53bb\u7684\u2026\u2026\u201d\u59d3\u7530\u7684\u5973\u5b69\u5b50\u770b\u4e0d\u8fc7\u53bb\uff0c\u8d70\u8fc7\u6765\u8ddf\u5e97\u8001\u677f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u4f60\u8bf4\u6211\u4eec\u662f\u6765\u6572\u8bc8\u7684\uff1f\u201d\u4e00\u4e2a\u9752\u5e74\u773c\u775b\u6068\u4e0d\u5f97\u8981\u77aa\u5230\u59d3\u7530\u7684\u5973\u5b69\u5b50\u7684\u8138\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u59d3\u7530\u7684\u5973\u5b69\u5b50\u7ed9\u51e0\u4e2a\u9752\u5e74\u51f6\u6076\u7684\u76ef\u7740\uff0c\u5fc3\u91cc\u4e5f\u7981\u4e0d\u4f4f\u5bb3\u6015\uff0c\u9f3b\u5c16\u90fd\u6e17\u51fa\u865a\u6c57\u6765\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u54ea\u53ea\u773c\u775b\u770b\u5230\u788e\u73bb\u7483\u662f\u6211\u4eec\u653e\u7684\uff1f\u201d\u53c8\u4e00\u4e2a\u7537\u9752\u5e74\u7ad9\u8d77\u6765\uff0c\u624b\u6307\u6076\u72e0\u72e0\u7684\u9876\u7740\u59d3\u7530\u7684\u5973\u5b69\u5b50\u7684\u989d\u95e8\uff0c\u9876\u5f97\u5979\u76f4\u5f80\u540e\u9000\uff0c\u5e97\u8001\u677f\u5fd9\u8fc7\u6765\u966a\u7b11\u8138\uff0c\u90a3\u7537\u9752\u5e74\u5374\u4e0d\u6320\uff0c\u53eb\u56a3\u9053\uff1a\u201c\u8089\u53d8\u5473\u4e86\uff0c\u83dc\u91cc\u6709\u788e\u73bb\u7483\uff0c\u8fd8\u4e0d\u8ba9\u4eba\u8bf4\u4e86\uff1f\u4f60\u54ea\u53ea\u773c\u775b\u770b\u5230\u6211\u4eec\u5728\u6572\u8bc8\uff1f\u4f60\u4eec\u62a5\u8b66\u53bb\u554a\uff01\u4f60\u4eec\u8981\u4e0d\u62a5\u8b66\uff0c\u7237\u4eca\u5929\u5c06\u4f60\u4eec\u8fd9\u7834\u5e97\u7838\u4e2a\u7a00\u5df4\u70c2\uff01\u201d</p><p>\u3000\u3000\u90a3\u9752\u5e74\u89c1\u7530\u59d3\u5973\u5b69\u8ddf\u5e97\u8001\u677f\u4ee5\u53ca\u5e97\u91cc\u5176\u4ed6\u670d\u52a1\u5458\u9762\u9732\u60e7\uff0c\u6c14\u52bf\u8d8a\u53d1\u7684\u65fa\u76db\uff0c\u8fd8\u60f3\u7740\u7838\u51e0\u53ea\u76d8\u5b50\u7ed9\u4e9b\u989c\u8272\u4ed6\u4eec\u770b\u770b\uff0c\u5374\u4e0d\u6599\u65c1\u8fb9\u6162\u60a0\u60a0\u7684\u98d8\u6765\u4e2a\u58f0\u97f3\uff1a\u201c\u5443\uff0c\u8b66\u5bdf\u8fd8\u6709\u4e09\u5206\u949f\u5c31\u5230\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u518d\u8010\u5fc3\u7684\u7b49\u4e00\u4f1a\u513f\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u90a3\u4f19\u9752\u5e74\u90fd\u4e00\u8d77\u53d1\u6123\u7684\u8f6c\u8fc7\u5934\u6765\uff0c\u770b\u7740\u5750\u5728\u65c1\u8fb9\u684c\u4e0a\u5403\u996d\u7684\u90a3\u5bf9\u7537\u5973\u3001\u7537\u7684\u6b63\u653e\u4e0b\u7b77\u5b50\u773c\u775b\u770b\u7740\u8fd9\u8fb9\uff0c\u5973\u7684\u6b63\u542b\u60c5\u8109\u8109\u7684\u770b\u7740\u7537\u7684\u5728\u7b11\uff0c\u6709\u4e2a\u9752\u5e74\u53d7\u4e0d\u4f4f\u6c14\uff0c\u6311\u8845\u7684\u8bf4\u9053\uff1a\u201c\u5144\u5f1f\uff0c\u4e0d\u5f52\u4f60\u7ba1\u7684\u4e8b\uff0c\u4e0d\u8981\u6311\u523a\uff01\u201d</p><p>\u3000\u3000\u201c\u5c0f\u7530\u8ddf\u987e\u8001\u677f\u662f\u6211\u7684\u670b\u53cb\uff0c\u4f60\u4eec\u4ee5\u540e\u4e0d\u8981\u8fc7\u6765\u627e\u4ed6\u4eec\u7684\u9ebb\u70e6\uff0c\u4eca\u5929\u7684\u4e8b\u60c5\u5c31\u5f53\u6ca1\u6709\u53d1\u751f\u8fc7\uff0c\u201d\u5f20\u606a\u624b\u5f80\u524d\u4e00\u4f38\uff0c\u4eae\u51fa\u8155\u8868\u7ed9\u8fd9\u4f19\u9752\u5e74\u770b\uff0c\u201c\u8fd8\u6709\u4e24\u5206\u949f\uff0c\u4f60\u4eec\u53ef\u4ee5\u5728\u8b66\u5bdf\u8d76\u8fc7\u6765\u4e4b\u524d\u79bb\u5f00\u3002\u201d</p><p>\u3000\u3000\u201c\u5988B\u7684\uff0c\u8001\u5b50\u51fa\u6765\u6df7\u8fd8\u6015\u8b66\u5bdf\u4e86\uff1f\u4f60\u5c0f\u5b50\u5f53\u5fc3\u70b9\uff0c\u5c0f\u5fc3\u4e0d\u8981\u5927\u8bdd\u8bf4\u7834\u4e86\u3002\u201d\u90a3\u9752\u5e74\u6076\u72e0\u72e0\u7684\u76ef\u7740\u5f20\u606a\uff0c\u4e00\u65f6\u770b\u4e0d\u7a7f\u4ed6\u7684\u865a\u5b9e\uff0c\u4e5f\u4e0d\u6562\u4e0a\u524d\u6765\u52a8\u624b\u6559\u8bad\u4ed6\u4e00\u987f\uff0c\u4ed6\u4eec\u521a\u5750\u4e0b\u6765\u6311\u523a\uff0c\u8fd8\u6ca1\u6709\u5b9e\u65bd\u6572\u8bc8\uff0c\u81ea\u7136\u4e0d\u6015\u8b66\u5bdf\u8fc7\u6765\uff0c\u8981\u662f\u7ed9\u522b\u4eba\u968f\u4fbf\u4e24\u53e5\u5927\u8bdd\u552c\u8d70\uff0c\u90a3\u624d\u53eb\u4e22\u8138\u5462\u3002</p><p>\u3000\u3000\u8fd9\u4f19\u9752\u5e74\u5c31\u5750\u5728\u90a3\u91cc\u7b49\u8b66\u5bdf\u8fc7\u6765\uff0c\u5e97\u8001\u677f\u8138\u8272\u5374\u662f\u53d8\u5f97\u66f4\u5dee\uff0c\u8fd9\u4e9b\u4eba\u662f\u8001\u6cb9\u5b50\u4e86\uff0c\u558a\u8b66\u5bdf\u8fc7\u6765\u9876\u591a\u6682\u65f6\u5c06\u8fd9\u4e9b\u4eba\u8d76\u8d70\uff0c\u5c31\u7b97\u5c06\u4ed6\u4eec\u902e\u5230\u5c40\u5b50\u91cc\u5173\u4e0a\u51e0\u5929\uff0c\u4ed6\u4eec\u8fc7\u6765\u4f1a\u52a0\u500d\u7684\u6311\u8845\uff0c\u8fd8\u4e0d\u5982\u4e00\u5f00\u59cb\u5c31\u82b1\u94b1\u6d88\u707e\u3002</p><p>\u3000\u3000\u6ca1\u8fc7\u4e24\u5206\u949f\uff0c\u8b66\u5bdf\u5c31\u8d76\u8fc7\u6765\u4e86\uff0c\u90a3\u4f19\u9752\u5e74\u60f3\u7740\u6076\u4eba\u5148\u544a\u72b6\uff0c\u5e26\u961f\u7684\u8b66\u5bdf\u8d70\u8fdb\u6765\u521a\u95ee\uff1a\u201c\u521a\u624d\u662f\u8c01\u62a5\u7684\u8b66\uff1f\u201d\u4ed6\u4eec\u62a2\u7740\u8fce\u8fc7\u53bb\uff1a\u201c\u8b66\u5bdf\u540c\u5fd7\uff0c\u6211\u4eec\u8fc7\u6765\u5403\u996d\uff0c\u5403\u7684\u8089\u53d8\u5473\uff0c\u8fd8\u6709\u788e\u73bb\u7483\uff0c\u6211\u4eec\u521a\u63d0\u8d77\u8fd9\u4e8b\uff0c\u4ed6\u4eec\u5c31\u53cd\u54ac\u4e00\u53e3\u8bf4\u6211\u4eec\u6572\u8bc8\u2026\u2026\u201d\u8bf4\u7740\u8bdd\u773c\u775b\u8fd8\u659c\u76ef\u7740\u5e97\u8001\u677f\uff0c\u6d41\u9732\u51fa\u5a01\u80c1\u7684\u610f\u601d\u6765\u3002</p><p>\u3000\u3000\u6709\u8b66\u5bdf\u8fc7\u6765\uff0c\u5e97\u8001\u677f\u603b\u662f\u5fc3\u5b89\u4e00\u4e9b\uff0c\u5373\u4f7f\u6709\u9ebb\u70e6\u90a3\u4e5f\u662f\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u4f46\u4e5f\u4e0d\u60f3\u8fc7\u5206\u7684\u5f97\u7f6a\u8fd9\u4e9b\uff0c\u5e0c\u671b\u80fd\u5c0f\u4e8b\u5316\u4e86\uff0c\u966a\u7740\u7b11\u8138\u6b63\u8981\u4e3b\u52a8\u66ff\u8fd9\u4f19\u9752\u5e74\u5f00\u8131\u3002</p><p>\u3000\u3000\u201c\u5c11\u8bf4\u5e9f\u8bdd\uff0c\u201d\u8b66\u5bdf\u7709\u5934\u4e00\u6311\uff0c\u5bf9\u8fd9\u4f19\u9752\u5e74\u5374\u4e0d\u5f85\u89c1\uff0c\u8bf4\u9053\uff0c\u201c\u65e2\u7136\u4e0d\u662f\u4f60\u4eec\u62a5\u7684\u8b66\uff0c\u5c31\u5446\u4e00\u8fb9\u4e0a\u53bb\u6b47\u7740\u53bb\uff0c\u6ca1\u8f6e\u5230\u4f60\u4eec\u8bf4\u8bdd\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u6211\u62a5\u7684\u8b66\uff0c\u4ed6\u4eec\u7684\u786e\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u6572\u8bc8\u94b1\uff0c\u4e0d\u8fc7\u6211\u4eb2\u773c\u770b\u5230\u4ed6\u4eec\u5c06\u788e\u73bb\u7483\u6492\u76d8\u5b50\u91cc\u4e4b\u540e\u627e\u832c\uff0c\u201d\u5f20\u606a\u7ad9\u8d77\u6765\u8bf4\u9053\uff0c\u95ee\u5e26\u961f\u7684\u8b66\u5bdf\uff0c\u201c\u4f60\u4eec\u4f55\u5c40\u957f\u5462\uff1f\u4ed6\u8bf4\u8981\u8fc7\u6765\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u4f55\u5c40\u63a5\u5230\u60a8\u7684\u7535\u8bdd\u6b63\u5f80\u8fd9\u8fb9\u8d76\u5462\uff0c\u8fd9\u65f6\u5019\u521a\u8fc7\u4e8c\u6865\uff0c\u662f\u4e0d\u662f\u7b49\u4f55\u5c40\u957f\u8fc7\u6765\u518d\u5904\u7406\u8fd9\u51e0\u4e2a\u5c0f\u6d41\u6c13\uff1f\u201d\u5e26\u961f\u7684\u8b66\u5bdf\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u656c\u4e2a\u793c\uff0c\u6bd5\u7adf\u773c\u524d\u8fd9\u4e2a\u9752\u5e74\u662f\u4e2a\u8ba9\u4f55\u526f\u5c40\u957f\u5728\u9152\u684c\u4e0a\u4e22\u4e0b\u7b77\u5b50\u5c31\u5f80\u6c5f\u5317\u8d76\u7684\u4e3b\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u7b49\u4f55\u7eaa\u4e91\u8fc7\u6765\u518d\u8bf4\u5427\uff0c\u201d\u5f20\u606a\u53ef\u4e0d\u60f3\u4e0d\u5c34\u4e0d\u5c2c\u7684\u63a5\u53d7\u8b66\u5bdf\u7684\u656c\u793c\uff0c\u8f6c\u56de\u8eab\u53c8\u6162\u60a0\u60a0\u7684\u5750\u4e0b\uff0c\u201c\u6211\u8fd8\u6709\u4e24\u53e3\u996d\u5c31\u5403\u5b8c\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8db4\u5728\u684c\u4e0a\u5fcd\u4f4f\u7b11\u5374\u5fcd\u4e0d\u4f4f\u62ff\u978b\u5c16\u53bb\u8e22\u5f20\u606a\u7684\u5c0f\u817f\uff0c\u53eb\u4f60\u4e2b\u7684\u88c5\u903c\u3002</p><p>\u3000\u3000\u90a3\u4f19\u9752\u5e74\u5374\u50bb\u4e86\u773c\uff0c\u4ed6\u4eec\u521a\u624d\u8fd8\u6709\u5e95\u6c14\u8bf4\u8fd9\u6b21\u8fd8\u6ca1\u6709\u5f00\u59cb\u9ad8\u8bc8\u4e0d\u6015\u8c01\u80fd\u54ac\u4ed6\u4eec\u4e00\u53e3\uff0c\u8fd9\u65f6\u5019\u5374\u614c\u4e86\u795e\u3002\u8bf7\u83e9\u8428\u5bb9\u6613\u9001\u83e9\u8428\u96be\uff0c\u4eba\u5bb6\u80fd\u4e3a\u8fd9\u79cd\u5c0f\u4e8b\u4e00\u4e2a\u7535\u8bdd\u8ba9\u5802\u5802\u533a\u516c\u5b89\u5c40\u7684\u5c40\u957f\u51fa\u9a6c\uff08\u4e5f\u8bb8\u662f\u526f\u5c40\u957f\uff09\uff0c\u4ed6\u4eec\u60f3\u4e0d\u8131\u5c42\u76ae\u90fd\u96be\u3002\u60f3\u8981\u8fc7\u53bb\u8bf4\u4e9b\u8f6f\u8bdd\uff0c\u53ef\u662f\u8fd8\u6ca1\u6709\u7b49\u4ed6\u4eec\u6709\u884c\u52a8\uff0c\u90a3\u5e26\u961f\u7684\u8b66\u5bdf\u5c31\u6c89\u58f0\u5429\u5490\u624b\u4e0b\uff1a\u201c\u628a\u4ed6\u4eec\u90fd\u80cc\u94d0\u8d77\u6765\uff0c\u5e26\u5230\u5916\u9762\u8e72\u7740\u53bb\u3002\u201d</p><p>\u3000\u3000\u5ba4\u5916\u7684\u6c14\u6e29\u9ad8\u8fbe\u56db\u5341\u5ea6\uff0c\u8fd9\u65f6\u5019\u7ed9\u94d0\u8d77\u53cc\u624b\u4e22\u5230\u5927\u592a\u9633\u4e0b\u66b4\u6652\uff0c\u771f\u662f\u597d\u4eab\u53d7\u3002</p><p>\u3000\u3000\u5e97\u8001\u677f\u867d\u7136\u77e5\u9053\u5f20\u606a\u505c\u5728\u996d\u5e97\u524d\u7684\u90a3\u8f86\u8f7f\u8f66\u4ef7\u503c\u4e0d\u83f2\uff0c\u53ef\u4e5f\u6ca1\u6709\u60f3\u5230\u4ed6\u662f\u4e2a\u80fd\u968f\u968f\u4fbf\u4fbf\u4f7f\u5524\u533a\u5206\u5b89\u5c40\u526f\u5c40\u957f\u7684\u4eba\u7269\uff0c\u4ed6\u5728\u65b0\u6d66\u8fd9\u8fb9\u5f53\u7136\u542c\u8bf4\u8fc7\u4f55\u7eaa\u4e91\uff0c\u6ca1\u60f3\u5230\u4f55\u7eaa\u4e91\u4f1a\u56e0\u4e3a\u8fd9\u70b9\u5c0f\u4e8b\u5429\u5490\u8fc7\u624b\u4e0b\u540e\u8fd8\u4e0d\u591f\u8fd8\u8981\u4eb2\u81ea\u6765\u8d70\u4e00\u8d9f\u3002\u60f3\u8fc7\u53bb\u642d\u4e9b\u5173\u7cfb\uff0c\u53c8\u4e0d\u77e5\u9053\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u521a\u624d\u8fd8\u6000\u7591\u4eba\u5bb6\u7ed9\u5c0f\u7530\u4ecb\u7ecd\u5de5\u4f5c\u4e0d\u6000\u597d\u5fc3\u5462\u3002\u4ed6\u5a46\u5a18\u51d1\u8fc7\u6765\uff0c\u9876\u4e86\u9876\u4ed6\u7684\u80a9\u8180\uff1a\u201c\u5ba2\u4eba\u7897\u91cc\u90fd\u6ca1\u6709\u7c73\u996d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5e97\u8001\u677f\u7ed9\u4e00\u63d0\u9192\uff0c\u5fd9\u8d70\u8fc7\u6765\u8981\u7ed9\u5f20\u606a\u6dfb\u996d\u3002</p><p>\u3000\u3000\u201c\u534a\u7897\u5c31\u884c\uff0c\u56de\u9505\u8089\u7684\u6c41\u4e0b\u996d\uff0c\u4e0d\u8fc7\u4e5f\u4e0d\u80fd\u5403\u592a\u591a\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u8fc7\u4e86\u4e00\u4f1a\u513f\u624d\u8d76\u5230\uff0c\u5f20\u606a\u8e22\u51fa\u4e00\u5f20\u51f3\u5b50\u8bf7\u4ed6\u5750\u4e0b\uff0c\u95ee\u9053\uff1a\u201c\u6709\u6ca1\u6709\u5403\u996d\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u5403\u5230\u4e00\u534a\u5c31\u63a5\u5230\u4f60\u7684\u7535\u8bdd\uff0c\u201d\u4f55\u7eaa\u4e91\u8bf4\u9053\uff0c\u53c8\u95ee\u5b59\u9759\u6aac\uff0c\u201c\u5b59\u5c0f\u59d0\u4ec0\u4e48\u65f6\u5019\u56de\u5efa\u90ba\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u5230\u5efa\u90ba\u624d\u8e6d\u5230\u5f20\u606a\u4e00\u987f\u4e2d\u996d\u3002\u201d\u5b59\u9759\u6aac\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u966a\u6211\u518d\u5403\u70b9\uff0c\u201d\u5f20\u606a\u8f6c\u8fc7\u8eab\u8981\u4f38\u5230\u9694\u58c1\u684c\u4e0a\u8981\u5e2e\u4f55\u7eaa\u4e91\u62ff\u526f\u5e72\u51c0\u7684\u9910\u5177\u6765\uff0c\u4f55\u7eaa\u4e91\u5fd9\u7ad9\u8d77\u6765\u63a5\uff0c\u5f20\u606a\u62c9\u7740\u4ed6\u5750\u4e0b\uff0c\u5c06\u7a7a\u7897\u9012\u7ed9\u5e97\u8001\u677f\uff0c\u8bf4\u9053\uff0c\u201c\u987e\u8001\u677f\u5e2e\u6211\u4eec\u4f55\u5c40\u957f\u6dfb\u7897\u996d\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5e97\u8001\u677f\u5fc3\u91cc\u5947\u602a\uff1a\u8fd9\u9752\u5e74\u600e\u4e48\u77e5\u9053\u4ed6\u59d3\u987e\uff1f\u5fc3\u91cc\u5947\u602a\u5f52\u5947\u602a\uff0c\u624b\u811a\u4e00\u70b9\u90fd\u4e0d\u6162\uff0c\u4ed6\u8fd9\u79cd\u4e2d\u7b49\u89c4\u6a21\u7684\u9910\u9986\u53ef\u5b8c\u5168\u6ca1\u6709\u8d44\u683c\u8ba9\u4f55\u7eaa\u4e91\u8fd9\u6837\u7684\u4eba\u7269\u4e0a\u95e8\u6765\u5403\u996d\u3002</p><p>\u3000\u3000\u5e97\u8001\u677f\u7aef\u4e86\u996d\u8fc7\u6765\uff0c\u5f20\u606a\u8ddf\u4f55\u7eaa\u4e91\u8bf4\u9053\uff1a\u201c\u53ef\u771f\u662f\u5de7\u4e86\uff0c\u6211\u5728\u8fd9\u5bb6\u996d\u5e97\u91cc\u9047\u5230\u4e2a\u4eba\uff0c\u4f60\u770b\u5979\u50cf\u8c01\uff1f\u201d\u6307\u7740\u8ddf\u56db\u4e2a\u5973\u670d\u52a1\u5458\u7ad9\u5728\u4e00\u8d77\u7684\u7530\u59d3\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u606a\u624b\u4e0d\u6307\uff0c\u4f55\u7eaa\u4e91\u8fd8\u770b\u4e0d\u51fa\u6765\uff0c\u8fd9\u4e00\u773c\u770b\u53bb\uff0c\u8fd8\u4ee5\u4e3a\u662f\u5e2d\u82e5\u7433\u4e54\u88c5\u6253\u626e\u7684\u5462\uff0c\u5567\u5567\u7684\u53eb\u5947\u9053\uff0c\u201c\u771f\u662f\u7684\u5462\uff0c\u4f60\u786e\u5b9a\u5979\u4e0d\u662f\u5e2d\u82e5\u7433\u7684\u59b9\u59b9\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u8fd9\u4e48\u6000\u7591\u6765\u7740\uff0c\u53ef\u5979\u5c31\u4e0d\u662f\u3002\u9759\u6aac\u60f3\u7740\u8bf7\u5979\u52301978\u5e2e\u5fd9\uff0c\u89c9\u5f97\u5929\u4e0b\u80fd\u6709\u4e24\u4e2a\u4eba\u957f\u5f97\u8fd9\u50cf\u8fd8\u771f\u662f\u597d\u73a9\uff0c\u518d\u8bf41978\u4e5f\u7f3a\u4e2a\u4eba\u624b\uff0c\u53c8\u6015\u7a81\u7136\u63d0\u51fa\u6765\u7ed9\u6000\u7591\u6709\u4ec0\u4e48\u522b\u7684\u4f01\u56fe\uff0c\u624d\u9ebb\u70e6\u8001\u4f55\u4f60\u8fc7\u6765\u4e00\u8d9f\uff0c\u7ed9\u6211\u4eec\u505a\u4e2a\u8bc1\u660e\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4ed6\u8fd9\u4e48\u8bf4\u7740\uff0c\u5c0f\u817f\u4e0a\u53c8\u6328\u4e86\u9759\u6aac\u4e00\u811a\u3002</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u54c8\u54c8\u4e00\u7b11\uff0c\u4ed6\u4e0d\u89c9\u5f97\u5f20\u606a\u4f1a\u6709\u522b\u7684\u5fc3\u601d\uff0c\u4e5f\u662f\u6076\u8da3\u5473\u5427\uff0c\u8fd9\u5973\u5b69\u5b50\u867d\u8bf4\u6f02\u4eae\uff0c\u4f46\u662f\u6bd4\u5f20\u606a\u8eab\u8fb9\u90a3\u4e9b\u4e2a\u5996\u5b7d\u8fd8\u7565\u900a\u4e00\u4e9b\uff0c\u7b11\u7740\u8ddf\u5e97\u8001\u677f\u8bf4\u9053\uff1a\u201c\u987e\u8001\u677f\u662f\u5427\uff1f\u5b66\u5e9c\u5df7\u53ef\u662f\u6211\u4eec\u533a\u5c40\u7684\u6cbb\u5b89\u91cd\u70b9\u533a\u57df\uff0c1978\u662f\u5bb6\u670b\u53cb\u805a\u4f1a\u7684\u9152\u5427\uff0c\u4e0d\u662f\u4ec0\u4e48\u4e0d\u4e09\u4e0d\u56db\u7684\u5730\u65b9\uff0c\u90a3\u4e2a\u5973\u5b69\u5b50\u8981\u662f\u60f3\u6362\u4efd\u597d\u5de5\u4f5c\uff0c\u6211\u4e5f\u63a8\u8350\u53bb1978\u5de5\u4f5c\u2026\u2026\u8981\u4e0d\u4f60\u8ba9\u5979\u518d\u4e86\u89e3\u4e86\u89e3\uff0c\u5148\u4e0d\u8981\u5fd9\u7740\u62d2\u7edd\u3002\u201d</p><p>\u3000\u3000\u201c\u786c\u662f\u8981\u4e86\u89e3\u7684\uff0c\u786c\u662f\u8981\u4e86\u89e3\u7684\u2026\u2026\u201d\u5e97\u8001\u677f\u5fd9\u4e0d\u8fed\u7684\u70b9\u5934\u7b54\u5e94\u3002</p></article>"}}}]);
