(window.webpackJsonp=window.webpackJsonp||[]).push([[1175],{1198:function(p){p.exports={chapterTitle:null,title:"\u7b2c1172\u7ae0 \u6177\u6168\u52a9\u5b66",content:"<article><h2>\u7b2c1172\u7ae0 \u6177\u6168\u52a9\u5b66</h2><p>\u3000\u3000\u5b66\u751f\u591a\u5df2\u79bb\u6821\uff0c\u5b66\u5e9c\u5df7\u7684\u591c\u665a\u8fd8\u662f\u706f\u7ea2\u9152\u7eff\u3001\u55a7\u56a3\u7e41\u534e\uff0c\u591c\u8272\u5728\u8def\u706f\u7167\u8000\u4e0d\u5230\u7684\u9ad8\u5904\u5f25\u6f2b\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u65e9\u6668\u5f88\u65e9\u4ece\u5c31\u9999\u6e2f\u51fa\u53d1\uff0c\u591c\u65f6\u592a\u6709\u4e9b\u72af\u56f0\uff0c\u65e9\u65e9\u7684\u4e0e\u9648\u5983\u84c9\u3001\u5468\u6653\u7490\u56de\u53bb\u4f11\u606f\uff0c\u5f20\u606a\u4e5f\u4e0d\u80fd\u626f\u7740\u55d3\u5b50\u544a\u8bc9\u5979\u9876\u697c\u7684\u516c\u5bd3\u91cc\u4e00\u76f4\u7559\u7740\u5979\u7684\u623f\u95f4\uff0c\u53ea\u6709\u7559\u4e0b\u6765\u966a\u675c\u98de\u3001\u8499\u4e50\u7ee7\u7eed\u559d\u9152\uff0c\u53c8\u6253\u7535\u8bdd\u5c06\u79e6\u521a\u7ed9\u558a\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u79e6\u521a\u8fd9\u4e24\u5e74\u7cbe\u529b\u90fd\u653e\u5728\u53d1\u6398\u7701\u5185\u7684\u8ba1\u7b97\u673a\u6559\u80b2\u5e02\u573a\u4e0a\uff0c\u4e0d\u5728\u5b66\u6821\u91cc\u62c5\u5f53\u4efb\u4f55\u804c\u52a1\u3002\u4e1c\u5927\u4e5f\u4e0d\u60f3\u8ddf\u9526\u6e56\u6487\u5f97\u592a\u6e05\uff0c\u6ca1\u6709\u5c06\u79e6\u521a\u7684\u4eba\u4e8b\u5173\u7cfb\u8e22\u51fa\u5b66\u6821\uff0c\u751a\u81f3\u6709\u4e9b\u798f\u5229\u8fd8\u4e0d\u4f1a\u5fd8\u6389\u4ed6\u3002\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u79e6\u521a\u62c5\u4efb\u603b\u7ecf\u7406\u7684\u4e1c\u5927\u521b\u57df\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\uff0c\u6709\u4e1c\u592730\uff05\u7684\u80a1\u4efd\u5728\u5185\uff0c\u4e1c\u5927\u4fdd\u7559\u79e6\u521a\u7684\u4eba\u4e8b\u5173\u7cfb\uff0c\u81f3\u5c11\u540d\u4e49\u4e0a\u53ef\u4ee5\u89c6\u4f5c\u7f51\u6821\u603b\u7ecf\u7406\u662f\u4e1c\u5927\u6d3e\u9063\u7684\u3002\u5f53\u7136\u4e86\uff0c\u6ca1\u6709\u8fd9\u5c42\u56e0\u7d20\uff0c\u4e1c\u5927\u4e5f\u4e0d\u4f1a\u8ba1\u8f83\u4ec0\u4e48\uff0c\u4e14\u4e0d\u8bf4\u5d14\u56fd\u6052\u7b49\u4e00\u5e72\u56fd\u5546\u9662\u7684\u5b66\u8005\u90fd\u5728\u62ff\u9526\u6e56\u7ecf\u7814\u4e2d\u5fc3\u7684\u4e00\u4efd\u8865\u8d34\uff0c\u9526\u6e56\u6bcf\u5e74\u6350\u52a9\u7ed9\u4e1c\u5927\u7684\u5b66\u79d1\u5efa\u8bbe\u8d44\u91d1\u5c31\u591a\u5f97\u4ee4\u5176\u4ed6\u9ad8\u6821\u5782\u6d8e\u4e09\u5c3a\u3002</p><p>\u3000\u3000\u65f6\u81f3\u4e00\u4e2a\u5b66\u5e74\u7ed3\u675f\uff0c\u65b0\u7684\u5b66\u5e74\u5373\u5c06\u5f00\u59cb\uff0c\u9526\u6e56\u5bf9\u4e1c\u5927\u53ca\u5efa\u90ba\u5404\u5927\u9ad8\u6821\u65b0\u4e00\u8f6e\u7684\u6350\u52a9\u53c8\u5c06\u5f00\u59cb\uff0c\u79e6\u521a\u770b\u5230\u5f20\u606a\u7684\u7b2c\u4e00\u53e5\u8bdd\u5c31\u662f\uff1a\u201c\u4eca\u5e74\u7684\u6350\u6b3e\u65b9\u6848\u51fa\u6765\u4e86\u6ca1\u6709\uff1f\u597d\u4e9b\u4eba\u90fd\u627e\u6211\u6253\u542c\uff0c\u6211\u53c8\u80fd\u8ddf\u4ed6\u4eec\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u65b9\u6848\u521a\u521a\u8ba8\u8bba\u51fa\u6765\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8ddf\u53bb\u5e74\u6ca1\u6709\u592a\u591a\u7684\u53d8\u5316\u3002\u201d</p><p>\u3000\u3000\u79e6\u521a\u77e5\u9053\u53bb\u5e74\u7684\u6350\u52a9\u60c5\u51b5\uff0c\u5c31\u6ca1\u6709\u591a\u95ee\u4ec0\u4e48\uff0c\u95ee\u675c\u98de\uff1a\u201c\u8fd9\u4e48\u665a\u6253\u7535\u8bdd\u5c06\u6211\u558a\u8fc7\u6765\u4e0d\u4f1a\u662f\u4e3a\u4e86\u8bf7\u6211\u559d\u9152\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u4ed6\u8bf4\u8d77\u5c06\u4ed6\u8c03\u5f80\u4e16\u7eaa\u534e\u5a31\u7684\u4e8b\u60c5\uff1a\u201c\u4e16\u7eaa\u534e\u5a31\u8981\u505a\u5927\uff0c\u6309\u7167\u6211\u4eec\u4e00\u60ef\u7684\u505a\u6cd5\uff0c\u4f1a\u505a\u4e00\u4e9b\u4eba\u624d\u8f93\u51fa\u901a\u9053\u65b9\u9762\u7684\u5de5\u4f5c\u3002\u8fd9\u6b21\u7684\u6350\u6b3e\u65b9\u6848\u867d\u7136\u6ca1\u6709\u5c06\u5efa\u90ba\u97f3\u4e50\u5b66\u9662\u7b49\u51e0\u6240\u827a\u672f\u5b66\u9662\u5305\u62ec\u5728\u5185\uff0c\u4e0d\u8fc7\u4e16\u7eaa\u534e\u5a31\u8fd9\u8fb9\u6709\u989d\u5916\u7684\u9884\u7b97\uff0c\u4eca\u5e74\u5c31\u53ef\u4ee5\u505a\u4e9b\u57fa\u7840\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u65f6\u5b66\u658c\u7fa1\u6155\u7684\u8bf4\u9053\uff0c\u201c\u65e9\u77e5\u9053\u6709\u8fd9\u79cd\u7f8e\u5dee\uff0c\u6211\u4e5f\u6295\u5954\u5b59\u4e8c\u5c0f\u59d0\u4e86\u2014\u2014\u53bb\u97f3\u4e50\u5b66\u9662\u53d1\u94b1\u662f\u591a\u98ce\u5149\u7684\u4e8b\u60c5\uff0c\u97f3\u4e50\u5b66\u9662\u7684\u9886\u5bfc\u603b\u5f97\u9009\u4e24\u4e2a\u9662\u82b1\u7ea7\u7684\u4eba\u7269\u966a\u7740\u5403\u996d\u8868\u793a\u4e00\u4e0b\u8bda\u610f\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u97f3\u4e50\u5b66\u9662\u7684\u9886\u5bfc\u6311\u4e2d\u5de6\u5c0f\u9752\u966a\u4f60\u5403\u996d\u600e\u4e48\u6837\uff1f\u201d\u675c\u98de\u62ff\u65f6\u5b66\u658c\u7684\u5973\u670b\u53cb\u5f00\u73a9\u7b11\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u8fd9\u4e2a\u5c31\u8981\u97f3\u4e50\u5b66\u9662\u6d3e\u4e09\u4e2a\u9662\u82b1\u7ea7\u7684\u7f8e\u5973\u51fa\u6765\u966a\u5403\u996d\uff0c\u201d\u65f6\u5b66\u658c\u6d51\u7b11\u9053\uff0c\u201c\u5de6\u5c0f\u9752\u8981\u7b97\u6211\u4eec\u81ea\u5e26\u7684\u3002\u201d</p><p>\u3000\u3000\u79e6\u521a\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u7406\u4f1a\u65f6\u5b66\u658c\u5728\u65c1\u8fb9\u63d2\u79d1\u6253\u8be8\uff0c\u95ee\u5f20\u606a\u5927\u6982\u80fd\u6709\u591a\u5c11\u9884\u7b97\u3002</p><p>\u3000\u3000\u201c\u97f3\u4e50\u5b66\u9662\u90a3\u8fb9\u5dee\u4e0d\u591a\u80fd\u6709\u4e24\u5343\u4e07\uff0c\u73b0\u5728\u534e\u5a31\u4e5f\u6ca1\u6709\u591a\u5c11\u8d44\u91d1\uff0c\u4e0d\u8fc7\u91d1\u989d\u592a\u5c11\uff0c\u4e5f\u672a\u5fc5\u4f1a\u8ba9\u4eba\u5bb6\u91cd\u89c6\u8d77\u6765\u2014\u2014\u53e6\u5916\uff0c\u7701\u91cc\u6709\u610f\u5c06\u97f3\u4e50\u5b66\u9662\u3001\u827a\u672f\u5b66\u9662\u5408\u5e76\uff0c\u4e0d\u8fc7\u5b58\u5728\u7740\u8c01\u5e76\u8c01\u7684\u95ee\u9898\uff0c\u53cc\u65b9\u4e00\u6b65\u90fd\u4e0d\u80af\u9000\u8ba9\uff0c\u9677\u5165\u50f5\u5c40\u4e4b\u4e2d\u3002\u6211\u5728\u60f3\u7740\uff0c\u662f\u4e0d\u662f\u523b\u610f\u5c06\u827a\u672f\u5b66\u9662\u7ed9\u5ffd\u89c6\u6389\u3001\u5c06\u7ed9\u97f3\u4e50\u5b66\u9662\u7684\u6350\u6b3e\u63d0\u9ad8\u5230\u4e09\u5343\u4e07\uff1f\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u62db\u4e5f\u592a\u9634\u4e86\uff0c\u201d\u8499\u4e50\u7b11\u9053\uff0c\u201c\u4f60\u8fd9\u4e0d\u662f\u903c\u7740\u827a\u672f\u5b66\u9662\u8ba9\u6b65\u5417\uff1f\u201d</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u4f5c\u4e3a\u56fd\u5185\u9876\u5c16\u7684\u7efc\u5408\u6027\u9662\u6821\uff0c\u6bcf\u5e74\u4ece\u4e2d\u592e\u4e0e\u7701\u91cc\u83b7\u5f97\u7684\u5e38\u89c4\u6559\u80b2\u8d22\u653f\u62e8\u6b3e\u603b\u989d\u4e5f\u4e0d\u8fc7\u4e24\u4ebf\u5143\uff0c\u7701\u7ea7\u827a\u672f\u7c7b\u9662\u6821\u80fd\u591f\u76f4\u63a5\u83b7\u5f97\u8d22\u653f\u652f\u6301\u90fd\u6781\u4e3a\u6709\u9650\u3002\u97f3\u4e50\u5b66\u9662\u524d\u5e74\u4e3a\u7b79\u5efa\u7efc\u5408\u6559\u5b66\u5927\u697c\uff0c\u6c42\u7237\u7237\u544a\u5976\u5976\uff0c\u7701\u91cc\u624d\u62e8\u4e86\u4e00\u5343\u4e8c\u767e\u4e07\uff0c\u97f3\u4e50\u5b66\u9662\u6700\u7ec8\u5c06\u9760\u556c\u56ed\u8def\u7684\u5341\u4ea9\u5730\u76ae\u5356\u6389\uff0c\u624d\u51d1\u8db3\u56db\u5343\u4e07\u5efa\u9020\u8d44\u91d1\u3002\u97f3\u4e50\u5b66\u9662\u6bcf\u5e74\u7684\u6559\u80b2\u62e8\u6b3e\u53ca\u5b66\u8d39\u6536\u5165\u52a0\u5728\u4e00\u8d77\u4e5f\u4e0d\u8db3\u516b\u5343\u4e07\u5143\uff0c\u9526\u6e56\u4e00\u6b21\u6027\u5c31\u6350\u52a9\u4e09\u5343\u4e07\u8d44\u91d1\uff0c\u7edd\u5bf9\u80fd\u8ba9\u97f3\u4e50\u5b66\u9662\u7684\u9662\u9886\u5bfc\u5931\u7720\u597d\u51e0\u5929\u3002\u827a\u672f\u5b66\u9662\u540c\u6837\u7684\u7f3a\u4e4f\u8d44\u91d1\uff0c\u770b\u7740\u97f3\u4e50\u5b66\u9662\u80fd\u83b7\u5f97\u90a3\u4e48\u5927\u7684\u5916\u6765\u8d22\u6e90\uff0c\u4e24\u6821\u5408\u5e76\u7684\u963b\u529b\u81ea\u7136\u80fd\u51cf\u5c11\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e76\u4e0d\u5173\u5fc3\u8c01\u5e76\u8c01\uff0c\u53ea\u60f3\u7740\u5c06\u8fd9\u79cd\u5c71\u5934\u4e3b\u4e49\u7684\u6c14\u7130\u706d\u6389\u4e00\u4e9b\uff0c\u8ba9\u5de5\u4f5c\u66f4\u987a\u5229\u8fdb\u884c\u4e0b\u53bb\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u770b\u4e86\u770b\u8155\u8868\uff0c\u65f6\u95f4\u4e0d\u65e9\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\u8ddf\u79e6\u521a\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u5bf9\u4e86\uff0c\u660e\u5929\u4e0b\u5348\u7701\u91cc\u6709\u4e2a\u78b0\u5934\u4f1a\u8bae\uff0c\u674e\u6821\u957f\u8ddf\u5d14\u9662\u957f\u90fd\u53c2\u52a0\uff0c\u4f60\u8981\u6709\u65f6\u95f4\u966a\u6211\u8d70\u4e00\u8d9f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u966a\u4f60\u8fc7\u53bb\u662f\u8981\u8ba8\u8bba\u9526\u6e56\u4eca\u5e74\u52a9\u5b66\u6b3e\u600e\u4e48\u5206\u914d\uff1f\u201d\u79e6\u521a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u80fd\u6709\u5176\u4ed6\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u5f20\u606a\u7b11\u7740\u53cd\u95ee\uff0c\u201c\u5404\u7701\u5e02\u90fd\u5c06\u533a\u57df\u4e00\u6240\u9662\u6821\u62ff\u51fa\u6765\u91cd\u70b9\u6295\u5165\uff0c\u8981\u5efa\u8bbe\u4e16\u754c\u7ea7\u7684\u4e00\u6d41\u9ad8\u6821\uff0c\u7701\u91cc\u53ea\u80fd\u5c06\u62c5\u5b50\u538b\u4e1c\u5927\u5934\u4e0a\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6691\u5047\u671f\u95f4\uff0c\u4e1c\u5927\u521b\u57df\u57f9\u8bad\u5b66\u6821\u6b63\u662f\u4e00\u5e74\u4e2d\u5b66\u5458\u6700\u591a\u7684\u65f6\u671f\uff0c\u867d\u8bf4\u8981\u5c06\u8fd9\u8fb9\u7684\u62c5\u5b50\u4ea4\u7ed9\u4ee4\u5c0f\u71d5\uff0c\u5374\u65e0\u6cd5\u8bf4\u8131\u624b\u5c31\u8131\u624b\uff0c\u603b\u8981\u6709\u4e00\u6bb5\u8fc7\u5ea6\u671f\u3002\u518d\u8bf4\u4e16\u7eaa\u534e\u5a31\u5728\u5efa\u90ba\u7684\u5206\u652f\u673a\u6784\u8fd8\u53ea\u6709\u90a3\u4e48\u51e0\u4e2a\u5de5\u4f5c\u4eba\u5458\uff0c\u79e6\u521a\u6b63\u5f0f\u8f6c\u8fc7\u6765\uff0c\u8fd8\u8981\u7b49\u5b59\u9759\u9999\u56de\u5efa\u90ba\u540e\u518d\u8bf4\u3002</p><p>\u3000\u3000\u4e2d\u5348\u524d\uff0c\u53f6\u5c0f\u6850\u6253\u7535\u8bdd\u6765\uff0c\u901a\u77e5\u4ed6\u4e0b\u5348\u5f00\u4f1a\u7684\u5730\u70b9\u4e0e\u65f6\u95f4\uff0c\u4e2d\u5348\u5f20\u606a\u4e0e\u53f6\u5c0f\u6850\u8981\u966a\u7701\u59d4\u4e66\u8bb0\u674e\u8fdc\u6e56\u5403\u996d\uff0c\u8981\u4ed6\u4e0b\u5348\u76f4\u63a5\u53bb\u4f1a\u8bae\u5730\u70b9\u8ddf\u4ed6\u4eec\u6c47\u5408\u5c31\u884c\u3002</p><p>\u3000\u3000\u79e6\u521a\u4e0b\u5348\u521a\u51fa\u5bb6\u95e8\u51c6\u5907\u5f00\u8f66\u53bb\u7701\u653f\u5e9c\uff0c\u63a5\u5230\u5d14\u56fd\u6052\u7684\u7535\u8bdd\uff0c\u8ba9\u4ed6\u5f00\u8f66\u53bb\u63a5\u4e00\u4e0b\u4ed6\u3002</p><p>\u3000\u3000\u5d14\u56fd\u6052\u5728\u9662\u529e\uff0c\u79e6\u521a\u5f00\u8f66\u5230\u9662\u529e\u697c\u4e0b\uff0c\u770b\u5230\u6821\u957f\u674e\u9e3f\u94ed\u7684\u4e5f\u505c\u5728\u9662\u529e\u524d\uff0c\u4ed6\u521a\u4e0b\u8f66\uff0c\u5c31\u770b\u89c1\u674e\u9e3f\u94ed\u4e0e\u5d14\u56fd\u6052\u4ece\u9662\u529e\u91cc\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u674e\u9e3f\u94ed\u770b\u5230\u79e6\u521a\uff0c\u5927\u58f0\u6253\u62db\u547c\uff1a\u201c\u5c0f\u79e6\u4e5f\u8981\u53bb\u53c2\u52a0\u4f1a\u8bae\uff0c\u662f\u4e0d\u662f\u5728\u9526\u6e56\u7684\u5de5\u4f5c\u8981\u8c03\u6574\uff1f\u201d</p><p>\u3000\u3000\u201c\u57f9\u8bad\u5b66\u6821\u8fd9\u4e00\u5757\u6682\u65f6\u4ea4\u7ed9\u522b\u4eba\u63a5\u624b\uff0c\u201d\u79e6\u521a\u8bf4\u9053\uff0c\u201c\u6211\u63a5\u4e0b\u6765\u505a\u4ec0\u4e48\uff0c\u6682\u65f6\u8fd8\u6ca1\u6709\u5b9a\u4e0b\u6765\uff0c\u5148\u8ddf\u7740\u6253\u6742\u3002\u201d</p><p>\u3000\u3000\u674e\u9e3f\u94ed\u62c9\u7740\u79e6\u521a\u4e0e\u5d14\u56fd\u6052\u4e00\u8d77\u5750\u4ed6\u7684\u8f66\uff0c\u4ed6\u94bb\u8fdb\u8f66\u91cc\u5c31\u95ee\u5d14\u56fd\u6052\uff1a\u201c\u5c0f\u5d14\uff0c\u4f60\u5148\u900f\u4e2a\u5e95\uff0c\u9526\u6e56\u8fd9\u6b21\u80fd\u6709\u591a\u6177\u6168\uff1f\u201d</p><p>\u3000\u3000\u5d14\u56fd\u6052\u7b11\u7740\u8bf4\uff1a\u201c\u7ecf\u7814\u4e2d\u5fc3\u63d0\u4ea4\u7684\u62a5\u544a\u53ea\u662f\u4f5c\u4e3a\u53c2\u8003\uff0c\u90a3\u8fb9\u505a\u4e86\u4ec0\u4e48\u51b3\u7b56\uff0c\u5374\u4e0d\u4f1a\u53ca\u65f6\u53cd\u9988\u5230\u7ecf\u7814\u4e2d\u5fc3\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c0f\u79e6\u77e5\u9053\u4e9b\u4ec0\u4e48\u6d88\u606f\uff1f\u201d\u674e\u9e3f\u94ed\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u8ddf\u53bb\u5e74\u5dee\u4e0d\u591a\u5427\uff0c\u201d\u79e6\u521a\u8bb0\u5f97\u5f20\u606a\u6628\u5929\u662f\u8fd9\u4e48\u8ddf\u4ed6\u8bf4\u7684\uff0c\u53c8\u8bf4\u4e86\u4ed6\u7684\u731c\u6d4b\uff0c\u201c\u8d44\u6e90\u53ef\u80fd\u4f1a\u66f4\u5411\u4e1c\u5927\u96c6\u4e2d\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u9ad8\u6821\u7684\u79d1\u7814\u529b\u91cf\u4e5f\u662f\u6a61\u6811\u56ed\u7814\u53d1\u4f53\u7cfb\u8981\u91cd\u70b9\u501f\u52a9\u7684\u529b\u91cf\u4e4b\u4e00\uff0c\u66f4\u4f55\u51b5\u8981\u4fdd\u8bc1\u4f18\u8d28\u4eba\u624d\u5f80\u9526\u6e56\u8f93\u5165\u7684\u901a\u9053\u901a\u7545\uff0c\u6bcf\u5e74\u90fd\u8981\u4ece\u6a61\u6811\u56ed\u7684\u79d1\u7814\u9884\u7b97\u4e2d\u62e8\u4e00\u5b9a\u7684\u8d44\u91d1\u6350\u7ed9\u9ad8\u6821\u652f\u63f4\u5b66\u79d1\u5efa\u8bbe\u6240\u7528\u3002</p><p>\u3000\u3000\u5efa\u90ba\u8fd9\u4e48\u591a\u6240\u9ad8\u6821\u4e2d\uff0c\u4e1c\u5927\u5386\u6765\u90fd\u662f\u9526\u6e56\u6350\u8d60\u7684\u91cd\u70b9\uff0c\u4e1c\u5927\u751a\u81f3\u5728\u9526\u6e56\u7684\u76f4\u63a5\u652f\u6301\u4e0b\u6210\u7acb\u5fae\u7535\u5b50\u5b66\u9662\u3002\u9664\u4e86\u9526\u6e56\u82b1\u8d39\u56db\u4ebf\u5de8\u8d44\u6350\u5efa\u7684\u56fe\u4e66\u9986\u4e4b\u5916\uff0c\u53bb\u5e74\u5168\u5e74\u9526\u6e56\u5171\u5411\u4e1c\u5927\u8f93\u9001\u4e86\u4e00\u4ebf\u4e94\u5343\u4e07\u7684\u6350\u6b3e\uff1b\u5efa\u90ba\u5176\u4ed6\u51e0\u6240\u9ad8\u6821\u5171\u83b7\u5f97\u9526\u6e56\u4e0d\u8db3\u4e94\u5343\u4e07\u7684\u6350\u6b3e\u3002</p><p>\u3000\u3000\u4e2d\u592e\u653f\u5e9c\u4e3a\u5728\u56fd\u5185\u5efa\u8bbe\u82e5\u5e72\u6240\u4e16\u754c\u4e00\u6d41\u5927\u5b66\u542f\u52a8985\u5de5\u7a0b\uff0c\u4e1c\u5927\u4f5c\u4e3a985\u5de5\u7a0b\u60e0\u53ca\u7684\u91cd\u70b9\u9ad8\u6821\u4e4b\u4e00\uff0c\u4eca\u5e74\u989d\u5916\u4ece\u4e2d\u592e\u53ca\u7701\u91cc\u83b7\u5f97\u7684\u6559\u80b2\u62e8\u6b3e\u4e5f\u53ea\u6709\u4e24\u4ebf\u5143\u3002\u4eca\u5e74\u4e1c\u5927\u83b7\u5f97\u7684\u8d22\u653f\u62e8\u6b3e\u3001\u5b66\u8d39\u6536\u5165\u4ee5\u53ca\u4ea7\u4e1a\u6295\u8d44\u6536\u5165\u9884\u8ba1\u53ea\u6709\u516d\u4ebf\u5143\uff0c\u82e5\u80fd\u518d\u4ece\u9526\u6e56\u83b7\u5f97\u4e00\u4ebf\u4e94\u5343\u4e07\u7684\u6350\u6b3e\u2014\u2014\u5373\u4f7f\u8fd9\u4e9b\u6350\u6b3e\u90fd\u8981\u6d41\u5411\u9526\u6e56\u6240\u6307\u5b9a\u7684\u5b66\u79d1\u9886\u57df\uff0c\u4e5f\u7edd\u5bf9\u80fd\u8ba9\u4e1c\u5927\u7d27\u5f20\u7684\u8d22\u52a1\u677e\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u9526\u6e56\u6350\u6b3e\u80fd\u66f4\u5411\u4e1c\u5927\u96c6\u4e2d\u4e00\u4e9b\u66f4\u597d\u3002</p><p>\u3000\u3000\u4e1c\u5927\u4ece\u53bb\u5e74\u8d77\u4e5f\u5c06\u76ee\u6807\u76ef\u4e0a\u201c\u4e16\u754c\u4e00\u6d41\u5927\u5b66\u201d\uff0c\u6ca1\u6709\u8fd9\u4e2a\u76ee\u6807\u8fd8\u597d\u4e00\u4e9b\uff0c\u6709\u4e86\u8fd9\u4e2a\u76ee\u6807\uff0c\u624d\u77e5\u9053\u5dee\u8ddd\u662f\u4f55\u7b49\u7684\u5927\u3002</p><p>\u3000\u3000\u79e6\u521a\u5750\u5728\u526f\u9a7e\u9a76\u7684\u4f4d\u5b50\uff0c\u542c\u7740\u674e\u9e3f\u94ed\u8ddf\u5d14\u56fd\u6052\u5728\u8f66\u540e\u53a2\u8ba8\u8bba\u5b66\u6821\u7684\u53d1\u5c55\uff0c\u5fc3\u60f3\u4e0d\u8981\u8bf4\u522b\u7684\uff0c\u50cf\u7f8e\u56fd\u6700\u51fa\u540d\u7684\u51e0\u6240\u77e5\u540d\u9662\u6821\uff0c\u6bcf\u5e74\u7684\u529e\u5b66\u7ecf\u8d39\u6295\u5165\u90fd\u662f\u5341\u4ebf\u7f8e\u5143\u8ba1\u7684\u3002\u4e1c\u5927\u8fd9\u4e24\u5e74\u9664\u4e86\u8d22\u653f\u62e8\u6b3e\u3001\u5b66\u8d39\u6536\u5165\u53ca\u5176\u4ed6\u81ea\u7b79\u8d44\u91d1\u4e4b\u5916\uff0c\u8fd8\u6709\u5c31\u662f\u901a\u8fc7\u94f6\u884c\u8d37\u6b3e\u6765\u52a0\u5f3a\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\uff0c\u5c31\u7b97\u5982\u6b64\uff0c\u4e1c\u5927\u6bcf\u5e74\u7684\u529e\u5b66\u7ecf\u8d39\u6295\u5165\u79bb\u5341\u4ebf\u4eba\u6c11\u5e01\u8fd8\u6709\u4e00\u4e9b\u5dee\u8ddd\uff0c\u79bb\u4e16\u754c\u4e00\u6d41\u9662\u6821\u7684\u76ee\u6807\u5c31\u5dee\u5f97\u66f4\u8fdc\u4e86\u3002</p><p>\u3000\u3000\u79e6\u521a\u4ed6\u4eec\u8d76\u5230\u7701\u653f\u5e9c\u8fd8\u6709\u4e9b\u65e9\uff0c\u5f20\u606a\u5728\u9646\u6587\u592b\u529e\u516c\u5ba4\u91cc\uff0c\u8bf7\u4ed6\u4eec\u5148\u8fc7\u53bb\u5750\u5750\u3002\u8ddf\u7740\u9646\u6587\u592b\u7684\u79d8\u4e66\u8fdb\u53bb\uff0c\u5f20\u606a\u4e0e\u9646\u6587\u592b\u5750\u5728\u6c99\u53d1\u4e0a\u8bf4\u8bdd\uff0c\u53f6\u5c0f\u6850\u7ad9\u5728\u89d2\u843d\u91cc\u4e0d\u6653\u5f97\u8ddf\u8c01\u901a\u7535\u8bdd\u3002\u8fd9\u6837\u7684\u573a\u5408\uff0c\u9648\u5983\u84c9\u5012\u662f\u6ca1\u6709\u51fa\u73b0\uff0c\u53f6\u5c0f\u6850\u662f\u884c\u653f\u5ba4\u603b\u88c1\uff0c\u5979\u624d\u662f\u5f20\u606a\u5b9e\u8d28\u610f\u4e49\u4e0a\u7684\u7b2c\u4e00\u52a9\u7406\uff0c\u8fd9\u4e2a\u804c\u52a1\u4e4b\u524d\u7531\u7fdf\u4e39\u9752\u62c5\u4efb\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4e00\u8d77\u8fc7\u6765\u4e86\u2026\u2026\u201d\u5f20\u606a\u4e0e\u9646\u6587\u592b\u7ad9\u8d77\u6765\uff0c\u8bf7\u674e\u9e3f\u94ed\u4ed6\u4eec\u5165\u5ea7\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u8d70\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u603b\u8fd8\u8981\u8fc7\u4e00\u523b\u949f\u624d\u80fd\u8d76\u5230\u3002\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7b49\u4ed6\uff0c\u674e\u526f\u7701\u957f\u90a3\u8fb9\u7684\u4f1a\u8bae\u8fd8\u6ca1\u6709\u7ed3\u675f\u5462\u3002\u201d</p><p>\u3000\u3000\u79e6\u521a\u8fd8\u4ee5\u4e3a\u53ea\u662f\u6682\u65f6\u5728\u9646\u6587\u592b\u529e\u516c\u5ba4\u91cc\u5750\u4e00\u5750\uff0c\u6ca1\u60f3\u5230\u4ed6\u4e5f\u8981\u53c2\u52a0\u4f1a\u8bae\uff0c\u5fc3\u60f3\uff1a\u674e\u526f\u7701\u957f\u7ed9\u9648\u5176\u658c\u6848\u7275\u6d89\u5230\u4e86\u8fd8\u7167\u5e38\u4e3b\u6301\u5de5\u4f5c\uff0c\u9646\u6587\u592b\u5374\u662f\u4ee5\u600e\u4e48\u8eab\u4efd\u53c2\u4e0e\u4eca\u5929\u7684\u4f1a\u8bae\uff0c\u4ed6\u7684\u5206\u7ba1\u5de5\u4f5c\u8ddf\u6559\u80b2\u53c8\u6cbe\u4e0d\u4e0a\u8fb9\uff1f\u5fc3\u60f3\u6709\u53ef\u80fd\u7701\u91cc\u8981\u9646\u6587\u592b\u60f3\u529e\u6cd5\u89e3\u51b3\u90e8\u5206\u6559\u80b2\u8d44\u91d1\u7684\u6765\u6e90\uff0c\u6bd5\u7adf\u9646\u6587\u592b\u5206\u7ba1\u7684\u90e8\u5206\u662f\u7701\u8d22\u653f\u7684\u4e3b\u8981\u6765\u6e90\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u7b49\u4e86\u7247\u523b\uff0c\u9648\u4fe1\u751f\u8d76\u4e86\u8fc7\u6765\u3002\u4ed6\u662f\u4ece\u9999\u6e2f\u8d76\u56de\u6765\u4e86\uff0c\u822a\u73ed\u8bef\u4e86\u70b9\uff0c\u76f4\u63a5\u4ece\u673a\u573a\u8d76\u8fc7\u6765\u53c2\u52a0\u4f1a\u8bae\uff0c\u79e6\u521a\u8fd8\u4ee5\u4e3a\u9526\u6e56\u7684\u6350\u6b3e\u8ddf\u53bb\u5e74\u5dee\u4e0d\u591a\uff0c\u968f\u4fbf\u8ba9\u8c01\u8fc7\u6765\u53c2\u52a0\u4f1a\u8bae\u5c31\u884c\uff0c\u5f20\u606a\u4eba\u5728\u5efa\u90ba\uff0c\u8981\u5c0a\u91cd\u4e00\u4e0b\u7701\u91cc\uff0c\u4eb2\u81ea\u53c2\u52a0\u4e00\u4e0b\u4e5f\u65e0\u6240\u8c13\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u9648\u4fe1\u751f\u4e5f\u4e00\u8d77\u8fc7\u6765\u3002</p><p>\u3000\u3000\u79e6\u521a\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u8fd9\u65f6\u5019\u95e8\u5916\u6709\u4eba\u6572\u95e8\u8fdb\u6765\uff0c\u662f\u7701\u957f\u6c5f\u654f\u4e4b\u7684\u79d8\u4e66\u859b\u660e\u697c\u3002</p><p>\u3000\u3000\u859b\u660e\u697c\u8d70\u8fdb\u6765\u8bf4\u9053\uff1a\u201c\u9646\u7701\u957f\uff0c\u6c5f\u7701\u957f\u80fd\u62bd\u51fa\u65f6\u95f4\u53c2\u52a0\u4e0b\u5348\u7684\u4f1a\u8bae\uff0c\u95ee\u4f1a\u8bae\u4ec0\u4e48\u65f6\u5019\u8fdb\u884c\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u7b49\u674e\u526f\u7701\u957f\u7684\u901a\u77e5\u5462\uff1f\u6559\u80b2\u5385\u9a6c\u8d5f\u5385\u957f\u5df2\u7ecf\u5728\u4f1a\u8bae\u5ba4\uff0c\u8981\u4e0d\u6211\u4eec\u5c31\u5148\u8fc7\u53bb\uff1f\u201d\u9646\u6587\u592b\u8bf4\u9053\u3002</p><p>\u3000\u3000\u859b\u660e\u697c\u9a6c\u4e0a\u79bb\u5f00\u53bb\u901a\u77e5\u6c5f\u654f\u4e4b\uff0c\u79e6\u521a\u624d\u77e5\u9053\u4eca\u5929\u7684\u4f1a\u8bae\u7ea7\u522b\u8fd8\u662f\u76f8\u5f53\u9ad8\u7684\uff0c\u4e4b\u524d\u6ca1\u60f3\u5230\u6c5f\u654f\u4e4b\u4f1a\u5217\u5e2d\uff0c\u770b\u674e\u9e3f\u94ed\u3001\u5d14\u56fd\u6052\u7684\u795e\u8272\uff0c\u4ed6\u4eec\u4e8b\u5148\u4e5f\u4e0d\u77e5\u9053\u6c5f\u654f\u4e4b\u540e\u53c2\u52a0\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6c5f\u654f\u4e4b\u672c\u6ca1\u6709\u8ba1\u5212\u53c2\u52a0\u4e0b\u5348\u7684\u78b0\u5934\u4f1a\uff0c\u674e\u8fdc\u6e56\u4e34\u65f6\u7ed9\u4ed6\u6253\u4e86\u7535\u8bdd\uff0c\u5e0c\u671b\u4ed6\u80fd\u4eb2\u81ea\u53c2\u52a0\u4e00\u4e0b\u3002\u6c5f\u654f\u4e4b\u5230\u4e1c\u6d77\u6765\uff0c\u674e\u8fdc\u6e56\u6781\u5c11\u4f1a\u76f4\u63a5\u901a\u77e5\u53c2\u52a0\u4ec0\u4e48\u5177\u4f53\u4f1a\u8bae\uff0c\u4ed6\u53c8\u77e5\u9053\u674e\u8fdc\u6e56\u4e2d\u5348\u8ddf\u5f20\u606a\u5728\u4e00\u8d77\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u9634\u5f71\uff0c\u5374\u4e0d\u4f1a\u8868\u73b0\u51fa\u6765\u3002</p><p>\u3000\u3000\u5728\u53c2\u52a0\u4f1a\u8bae\u4e4b\u524d\uff0c\u4ed6\u8fd8\u8ba9\u859b\u660e\u697c\u641c\u96c6\u4e86\u4e00\u4e9b\u4f1a\u8bae\u8d44\u6599\uff0c\u4e0a\u9762\u6709\u9526\u6e56\u8fd9\u4e09\u5e74\u6765\u5bf9\u5efa\u90ba\u5404\u9ad8\u6821\u7279\u522b\u662f\u4e1c\u5927\u7684\u6350\u8d60\u8be6\u7ec6\uff0c\u5176\u4e2d\u53c8\u4ee5\u53bb\u5e74\u6350\u5efa\u7684\u7231\u8fbe\u56fe\u4e66\u9986\u5f71\u54cd\u6700\u5927\uff0c\u9526\u6e56\u5171\u5411\u7231\u8fbe\u6587\u5316\u57fa\u91d1\u6ce8\u5165\u56db\u4ebf\u4eba\u6c11\u5e01\u7684\u8d44\u91d1\uff0c\u5efa\u6210\u56fd\u5185\u5355\u4f53\u89c4\u6a21\u6700\u5927\u7684\u56fe\u4e66\u9986\uff0c\u800c\u4e14\u6574\u4e2a\u56fe\u4e66\u9986\u7cfb\u7edf\u5c06\u71d5\u5f52\u6e56\u533a\u57df\u7684\u9ad8\u6821\u85cf\u4e66\u8d44\u6e90\u90fd\u6574\u5408\u5230\u4e00\u8d77\uff0c\u5f62\u6210\u5168\u56fd\u7b2c\u4e8c\u5927\u85cf\u4e66\u89c4\u6a21\u7684\u56fe\u4e66\u9986\u7cfb\u7edf\u3002</p><p>\u3000\u3000\u9664\u4e86\u8fd9\u4e2a\u4e4b\u5916\uff0c\u9526\u6e56\u4e5d\u4e5d\u5e74\u5230\u96f6\u96f6\u5e74\u5e74\u5ea6\u5171\u5411\u5168\u7701\u6559\u80b2\u7cfb\u7edf\u6350\u6b3e\u8fbe\u4e24\u4ebf\u5143\u3002</p><p>\u3000\u3000\u201c\u636e\u6559\u80b2\u5385\u9a6c\u8d5f\u5385\u957f\u5224\u65ad\u8bf4\u9526\u6e56\u4eca\u5e74\u7684\u6350\u6b3e\u5927\u6982\u4e0d\u4f1a\u6bd4\u53bb\u5e74\u4f4e\u3002\u201d\u859b\u660e\u697c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4f4e\u4e8e\u4e24\u4e2a\u4ebf\u554a\uff0c\u90a3\u8fd9\u4e2a\u4f1a\u8bae\u6211\u4e5f\u5e94\u8be5\u53c2\u52a0\u4e00\u4e0b\u2026\u2026\u201d\u6c5f\u654f\u4e4b\u8bf4\u9053\uff0c\u8ba9\u859b\u660e\u697c\u966a\u4ed6\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u859b\u660e\u697c\u5fc3\u60f3\u6c5f\u654f\u4e4b\u5fc3\u91cc\u53ea\u6015\u591a\u5c11\u6709\u4e9b\u4e0d\u60c5\u613f\uff0c\u4f46\u662f\u6c5f\u654f\u4e4b\u4e0d\u5217\u5e2d\u4e5f\u8bf4\u4e0d\u8fc7\u53bb\uff0c\u8981\u4e0d\u662f\u9526\u6e56\u7684\u6350\u6b3e\u90fd\u6d41\u5165\u4e1c\u6d77\u7701\u7684\u6559\u80b2\u7cfb\u7edf\uff0c\u800c\u4e14\u5bf9\u6350\u6b3e\u7528\u9014\u6709\u660e\u786e\u7684\u8981\u6c42\uff0c\u6bcf\u5e74\u4e24\u4ebf\u5143\u7684\u6559\u80b2\u6350\u6b3e\u8db3\u4ee5\u8ba9\u67d0\u4e2a\u56fd\u5bb6\u9886\u5bfc\u4eba\u51fa\u9762\u4e86\u3002</p><p>\u3000\u3000\u8d70\u8fdb\u4f1a\u8bae\u5ba4\u91cc\uff0c\u859b\u660e\u697c\u8d70\u5728\u6c5f\u654f\u4e4b\u540e\u9762\uff0c\u66f4\u65b9\u4fbf\u4ed6\u89c2\u5bdf\u5df2\u7ecf\u4f1a\u8bae\u5ba4\u91cc\u7684\u4f17\u4eba\u3002</p><p>\u3000\u3000\u4e3b\u7ba1\u6559\u80b2\u7684\u674e\u526f\u7701\u957f\u867d\u7136\u8fd8\u4e3b\u6301\u5de5\u4f5c\uff0c\u4f46\u662f\u4ed6\u6ca1\u80fd\u5f7b\u5e95\u7684\u4ece\u9648\u5176\u658c\u6848\u8131\u8eab\uff0c\u8138\u8272\u4e0d\u5927\u597d\uff0c\u597d\u50cf\u5c31\u7b97\u773c\u4e0b\u6709\u5929\u5927\u7684\u597d\u4e8b\u90fd\u8ddf\u4ed6\u6ca1\u6709\u5173\u7cfb\u3002\u9648\u5176\u658c\u6848\u53d1\uff0c\u6559\u80b2\u5385\u5385\u957f\u9a6c\u658c\u4e5f\u53ea\u80fd\u5e26\u75c5\u575a\u6301\u5de5\u4f5c\uff0c\u6682\u65f6\u4e3b\u6301\u7701\u6559\u80b2\u5385\u7684\u5c40\u9762\u3002\u9646\u6587\u592b\u4e0e\u5f20\u606a\u7ad9\u5728\u4e00\u8d77\uff0c\u4e1d\u6beb\u4e0d\u907f\u8bb3\u8ddf\u9526\u6e56\u7684\u5bc6\u5207\u5173\u7cfb\uff0c\u859b\u660e\u697c\u8fd8\u542c\u8bf4\u9646\u6587\u592b\u7684\u513f\u5b50\u8ddf\u5f20\u606a\u662f\u540c\u5b66\u3002\u79e6\u521a\u8ba4\u8bc6\u859b\u660e\u697c\uff0c\u859b\u660e\u697c\u5374\u4e0d\u8ba4\u8bc6\u79e6\u521a\uff0c\u60f3\u7740\u80fd\u8ddf\u5f20\u606a\u4e00\u8d77\u51fa\u5e2d\u4eca\u5929\u4f1a\u8bae\u7684\u9526\u6e56\u4eba\u5458\uff0c\u5e94\u5f53\u975e\u540c\u5c0f\u53ef\uff1b\u4ed6\u8ba4\u5f97\u53f6\u5c0f\u6850\uff0c\u56fd\u9632\u5175\u603b\u96c6\u56e2\u515a\u7ec4\u4e66\u8bb0\u3001\u526f\u603b\u7ecf\u7406\u53f6\u795d\u6c11\u7684\u5973\u513f\u3002\u53f6\u5c0f\u6850\u8ddf\u674e\u9e3f\u94ed\u5728\u8bf4\u8bdd\uff0c\u770b\u674e\u9e3f\u94ed\u7684\u795e\u8272\uff0c\u53f6\u5c0f\u6850\u4f3c\u4e4e\u8ddf\u4ed6\u5728\u8bf4\u4e00\u4ef6\u4ee4\u4ed6\u9707\u60ca\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u6c5f\u654f\u4e4b\u8d70\u8fdb\u6765\uff0c\u5927\u5bb6\u90fd\u793c\u8282\u6027\u7684\u7ad9\u8d77\u6765\uff0c\u6c5f\u654f\u4e4b\u6446\u4e86\u6446\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fc7\u6765\u65c1\u542c\u7684\uff0c\u4e5f\u662f\u4ee3\u8868\u7701\u91cc\u5bf9\u9526\u6e56\u7684\u6177\u6168\u4e49\u4e3e\u8868\u73b0\u611f\u8c22\u2026\u2026\u5927\u5bb6\u90fd\u5750\u4e0b\u8bf4\u8bdd\u5427\u3002\u201d</p><p>\u3000\u3000\u674e\u526f\u7701\u957f\u6ca1\u6709\u4ec0\u4e48\u7cbe\u795e\u5934\uff0c\u8fd8\u662f\u7531\u5e26\u75c5\u5de5\u4f5c\u7684\u7701\u6559\u80b2\u5385\u5385\u957f\u9a6c\u8d5f\u4e3b\u6301\u4f1a\u8bae\u3002\u7701\u91cc\u65e9\u5c31\u505a\u51fa\u9ad8\u6821\u6559\u80b2\u8d44\u6e90\u5411\u4e1c\u5927\u5c3d\u53ef\u80fd\u503e\u659c\u7684\u51b3\u5b9a\uff0c\u5f53\u7136\u4e5f\u8981\u4fdd\u8bc1\u7701\u5185\u5176\u4ed6\u9ad8\u6821\u7684\u53d1\u5c55\u6f5c\u529b\u3002\u4e5d\u4e5d\u5e74\u7701\u653f\u5e9c\u9884\u7b97\u5185\u9ad8\u6821\u6559\u80b2\u7ecf\u8d39\u62e8\u6b3e\u624d\u5341\u516d\u4e2a\u4ebf\uff0c\u9526\u6e56\u6d41\u5411\u7701\u9ad8\u6821\u7cfb\u7edf\u7684\u4e24\u4ebf\u5143\u6350\u6b3e\u76f8\u5f53\u7684\u53ef\u89c2\u3002\u9526\u6e56\u4e5f\u660e\u786e\u8868\u793a\u4eca\u5e74\u4f1a\u7ee7\u7eed\u7ed9\u7701\u9ad8\u6821\u7cfb\u7edf\u63d0\u4f9b\u5927\u91cf\u6350\u6b3e\uff0c\u5f00\u8fd9\u4e2a\u4f1a\u8bae\u4e00\u662f\u4e3a\u4e86\u8868\u793a\u5bf9\u9526\u6e56\u5c0a\u91cd\uff0c\u4e8c\u662f\u5e0c\u671b\u9526\u6e56\u7684\u6350\u6b3e\u80fd\u66f4\u591a\u5411\u4e1c\u5927\u503e\u659c\u3002\u9a6c\u8d5f\u4e5f\u77e5\u9053\u53ec\u5f00\u8fd9\u4e2a\u4f1a\u8bae\u4f1a\u7ed9\u5176\u4ed6\u5b66\u6821\u6307\u7740\u80cc\u810a\u9a82\uff0c\u53ef\u662f\u8c01\u8ba9\u7701\u6324\u4e0d\u51fa\u66f4\u591a\u7684\u8d44\u6e90\u7ed9\u4e1c\u5927\u5374\u53c8\u5bf9\u4e1c\u5927\u671f\u5f85\u8fc7\u9ad8\uff1f</p><p>\u3000\u3000\u4e0d\u662f\u7279\u522b\u6b63\u89c4\u7684\u4f1a\u8bae\uff0c\u6c5f\u654f\u4e4b\u5728\u9a6c\u8d5f\u4e4b\u540e\u4e5f\u63a5\u8fc7\u8bdd\u5934\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5c31\u7b49\u7740\u5f20\u606a\u7ed9\u7701\u91cc\u62a5\u4e2a\u6570\u5b57\uff0c\u5fc3\u91cc\u60f3\u7740\u6b63\u5f0f\u7684\u6350\u8d60\u4eea\u5f0f\u4e5f\u5e94\u8be5\u8981\u5217\u5e2d\u4e00\u4e0b\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u542d\u58f0\uff0c\u770b\u4e86\u4e00\u773c\u9648\u4fe1\u751f\uff0c\u8ba9\u9648\u4fe1\u751f\u4ee3\u8868\u9526\u6e56\u8868\u6001\u3002</p><p>\u3000\u3000\u201c\u56e0\u4e3a\u4e4b\u524d\u62df\u5b9a\u7684\u65b9\u6848\u9526\u6e56\u8fd8\u6ca1\u6709\u8ba8\u8bba\u901a\u8fc7\uff0c\u6240\u4ee5\u522b\u4eba\u4ece\u6211\u8fd9\u91cc\u8bd5\u63a2\u53e3\u98ce\uff0c\u6211\u53ea\u80fd\u544a\u8bc9\u4ed6\uff0c\u8ddf\u53bb\u5e74\u76f8\u5dee\u4e0d\u5dee\u3002\u8fd9\u65f6\u5019\u662f\u53ef\u4ee5\u544a\u8bc9\u5927\u5bb6\u4e00\u4e2a\u51c6\u786e\u7684\u6570\u5b57\uff0c\u4e5f\u7684\u786e\u53ea\u6bd4\u53bb\u5e74\u7565\u591a\u4e00\u4e9b\uff0c\u53ea\u662f\u5728\u65b9\u5f0f\u4e0a\u7565\u6709\u4e0d\u540c\u3002\u201d\u9648\u4fe1\u751f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u65b9\u5f0f\u4e0a\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d\u6559\u80b2\u5385\u5385\u957f\u9a6c\u8d5f\u95ee\u9053\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8ba1\u5212\u8bbe\u7acb\u4e00\u4e2a\u516c\u76ca\u6027\u8d28\u7684\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\uff0c\u9526\u6e56\u4ee5\u540e\u7684\u6559\u80b2\u6350\u6b3e\u5c06\u4e3b\u8981\u6d41\u5411\u8fd9\u4e2a\u57fa\u91d1\uff0c\u7701\u5185\u5404\u9ad8\u6821\u5305\u62ec\u4e1c\u5927\u5728\u5185\uff0c\u90fd\u53ef\u4ee5\u5411\u8be5\u57fa\u91d1\u7533\u8bf7\u5b66\u79d1\u5efa\u8bbe\u53ca\u79d1\u7814\u8d44\u91d1\u2026\u2026\u5f53\u7136\uff0c\u6211\u4eec\u6210\u7acb\u8be5\u57fa\u91d1\u7684\u76ee\u7684\uff0c\u4e3b\u8981\u662f\u4e3a\u4e86\u66f4\u597d\u7684\u4fc3\u8fdb\u4ea7\u5b66\u7814\u4e00\u4f53\u5316\u53d1\u5c55\uff0c\u6bcf\u5e74\u7684\u4fc3\u8fdb\u57fa\u91d1\u5728\u5404\u5b66\u79d1\u4e0a\u7684\u8d44\u91d1\u5206\u914d\u6bd4\u4f8b\u90fd\u6709\u4e25\u683c\u7684\u9650\u5236\uff0c\u57fa\u91d1\u7684\u7ba1\u7406\u4f1a\u8058\u8bf7\u5404\u9ad8\u6821\u7684\u77e5\u540d\u6559\u6388\u7ec4\u6210\u4e00\u4e2a\u8bc4\u8bae\u4f1a\u3002\u201d</p><p>\u3000\u3000\u6c5f\u654f\u4e4b\u77e5\u9053\u9526\u6e56\u6700\u7ec8\u8fd8\u662f\u60f3\u5c06\u6559\u80b2\u6350\u6b3e\u63a7\u5236\u5728\u81ea\u5df1\u7684\u624b\u91cc\uff0c\u54ea\u6240\u9ad8\u6821\u3001\u751a\u81f3\u9ad8\u6821\u91cc\u54ea\u4e2a\u6559\u6388\u6216\u8005\u7814\u7a76\u5458\u8981\u60f3\u4ece\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u91cc\u83b7\u5f97\u8d44\u91d1\uff0c\u5176\u7533\u8bf7\u6761\u4ef6\u5fc5\u987b\u662f\u6709\u5229\u4e8e\u9526\u6e56\u7684\u3002\u8fd9\u5927\u6982\u662f\u674e\u8fdc\u6e56\u8981\u4ed6\u53c2\u52a0\u4f1a\u8bae\u7684\u539f\u56e0\u5427\uff0c\u662f\u60f3\u4ed6\u5f00\u53e3\u652f\u6301\u9526\u6e56\u7684\u8fd9\u4e2a\u8ba1\u5212\uff0c\u6c5f\u654f\u4e4b\u77e5\u9053\u81ea\u5df1\u4e5f\u6ca1\u6709\u53cd\u5bf9\u7684\u7acb\u573a\uff0c\u4e4b\u524d\u9526\u6e56\u7684\u6559\u80b2\u6350\u6b3e\u4e5f\u662f\u7531\u9526\u6e56\u6307\u5b9a\u7528\u9014\u7684\uff0c\u53ea\u4e0d\u8fc7\u6ca1\u6709\u6b63\u5f0f\u7684\u5f62\u6210\u57fa\u91d1\u4f1a\u5236\u5ea6\u3002\u6c5f\u654f\u4e4b\u4e0d\u5fd9\u7740\u8868\u6001\uff0c\u770b\u5176\u4ed6\u4eba\u7684\u8138\u8272\u4f3c\u4e4e\u5e76\u4e0d\u89c9\u5f97\u6709\u591a\u5947\u602a\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u80fd\u5411\u57fa\u91d1\u6ce8\u5165\u591a\u5c11\u8d44\u91d1\uff1f\u201d</p><p>\u3000\u3000\u201c\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u5c06\u7eb3\u5165\u6a61\u6811\u56ed\u7684\u79d1\u7814\u7ecf\u8d39\u9884\u7b97\uff0c\u4eca\u5e74\u4ece\u5e74\u4e2d\u8865\u4e0a\uff0c\u6211\u4eec\u505a\u51fa\u516b\u5343\u4e07\u7f8e\u91d1\u7684\u9884\u7b97\uff0c\u660e\u5e74\u7684\u9884\u7b97\u4eca\u5e74\u5e74\u5e95\u5c31\u4f1a\u62ff\u51fa\u6765\uff0c\u9884\u8ba1\u4e0d\u4f1a\u4f4e\u4e8e\u4e00\u4ebf\u7f8e\u91d1\u2026\u2026\u201d</p><p>\u3000\u3000\u859b\u660e\u697c\u5b8c\u5168\u80fd\u7406\u89e3\u521a\u624d\u674e\u9e3f\u94ed\u4e3a\u4ec0\u4e48\u4e00\u8138\u9707\u60ca\u4e86\uff0c\u4e1c\u5927\u5c06\u662f\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u6700\u5927\u7684\u53d7\u76ca\u8005\uff0c\u8981\u662f\u6bcf\u5e74\u4ece\u4e2d\u83b7\u5f97\u4e00\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u5c06\u662f\u4f55\u7b49\u60ca\u4eba\u7684\u6570\u5b57\uff01\u90a3\u6a61\u6811\u56ed\u6bcf\u5e74\u7684\u79d1\u7814\u7ecf\u8d39\u53c8\u662f\u591a\u5c11\uff1f\u7edd\u5bf9\u4e0d\u4f1a\u53ea\u6709\u5411\u5916\u754c\u516c\u5e03\u7684\u56db\u4ebf\u7f8e\u5143\u90a3\u4e48\u70b9\u3002</p></article>"}}}]);
