(window.webpackJsonp=window.webpackJsonp||[]).push([[1177],{1200:function(p){p.exports={chapterTitle:null,title:"\u7b2c968\u7ae0 \u8ba2\u5a5a",content:"<article><h2>\u7b2c968\u7ae0 \u8ba2\u5a5a</h2><p>\u3000\u3000\u98de\u673a\u964d\u843d\u65f6\uff0c\u96ea\u5df2\u7ecf\u505c\u4e86\u3002</p><p>\u3000\u3000\u5317\u4eac\u6c14\u5019\u5e72\u71e5\uff0c\u9053\u65c1\u7530\u91ce\u4e0a\u7684\u96ea\u7c92\u7ed9\u98ce\u5439\u5f97\u5230\u5904\u98de\u65cb\uff0c\u65f6\u800c\u906e\u4f4f\u524d\u65b9\u7684\u89c6\u91ce\uff0c\u8f66\u5b50\u5728\u5317\u4eac\u90ca\u5916\u7684\u9053\u8def\u4e0a\u5f00\u5f97\u7f13\u6162\u800c\u5e73\u7a33\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u7684\u590f\u5929\uff0c\u7a7f\u7740\u725b\u4ed4\u88e4\u3001T\u6064\u886b\u7684\u9a6c\u5411\u4e1c\u4e0e\u5373\u5c06\u6bd5\u4e1a\u7684\u6e05\u534e\u5b66\u751f\u6ca1\u6709\u592a\u5927\u7684\u533a\u522b\uff1a\u91ce\u5fc3\u5f88\u5927\u3001\u60f3\u6cd5\u5f88\u591a\uff0c\u4e3a\u5c31\u4e1a\u6216\u51fa\u56fd\u4e24\u8005\u4e4b\u95f4\u5fc5\u987b\u505a\u51fa\u9009\u62e9\u800c\u82e6\u607c\uff0c\u6ca1\u6709\u5973\u670b\u53cb\uff0c\u4e8b\u4e1a\u672a\u6210\u4e4b\u65f6\uff0c\u4e5f\u6ca1\u6709\u5fc3\u601d\u627e\u5973\u670b\u53cb\uff0c\u4e24\u5e74\u524d\u7684\u590f\u5b63\u9a6c\u5411\u4e1c\u5373\u4f7f\u6709\u518d\u5927\u7684\u91ce\u5fc3\u4e5f\u4e0d\u4f1a\u8ba4\u4e3a\u81ea\u5df1\u7684\u7406\u60f3\u4f1a\u5728\u4e24\u5e74\u591a\u7684\u65f6\u95f4\u5185\u5b9e\u73b0\u3002</p><p>\u3000\u3000\u5728\u79bb\u4eba\u751f\u89c4\u5212\u7684\u7b2c\u4e00\u4e2a\u8282\u70b9\u8fd8\u8981\u63d0\u524d\u51e0\u5e74\u5c31\u62c5\u4efb\u4e00\u5bb6\u58f0\u540d\u663e\u8d6b\u7684\u516c\u53f8\u7684CEO\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\u90fd\u8981\u7b97\u4eba\u751f\u5f97\u610f\u4e86\u3002</p><p>\u3000\u3000\u201c\u597d\u5427\uff0c\u6211\u627f\u8ba4\u8fd9\u4e9b\u90fd\u662f\u6211\u4e3a\u4e09\u5341\u5c81\u5236\u5b9a\u7684\u76ee\u6807\uff0c\u63d0\u524d\u5b9e\u73b0\u53c8\u6709\u4ec0\u4e48\u4e0d\u597d\uff1f\u201d\u5728\u8c6a\u534e\u8f7f\u8f66\u7684\u8f66\u53a2\u91cc\uff0c\u9a6c\u5411\u4e1c\u7709\u98de\u8272\u821e\u7684\u8bf4\u9053\uff0c\u58f0\u8c03\u4e5f\u6709\u4e9b\u63a7\u5236\u4e0d\u4f4f\u7684\u4e0a\u626c\uff0c\u201c\u6211\u8fd8\u662f\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u4f60\u77e5\u9053\u7684\uff0c\u5176\u5b9e\u9526\u6e56\u53ef\u4ee5\u575a\u6301\u5728\u6606\u817e\u7684\u80a1\u6743\u4e0d\u88ab\u7a00\u91ca\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e5f\u77e5\u9053\u7684\uff0c\u5f53\u521d\u6210\u7acb\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u7684\u5b97\u65e8\u662f\u4fc3\u8fdb\u3001\u6276\u6301\u65b0\u79d1\u6280\u4f01\u4e1a\u7684\u5b75\u5316\u4e0e\u6210\u957f\uff0c\u201d\u5f20\u606a\u89e3\u91ca\u9053\uff0c\u201c\u5373\u4f7f\u8bf4\u6295\u8d44\uff0c\u90fd\u89c9\u5f97\u592a\u8fc7\u5206\u4e86\uff0c\u66f4\u4e0d\u80fd\u5e26\u534a\u70b9\u6295\u673a\u7684\u5fc3\u601d\u2026\u2026\u201d\u8fd9\u4e48\u8bf4\u7740\uff0c\u5f20\u606a\u81ea\u5df1\u90fd\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\u4e86\uff0c\u5634\u89d2\u6302\u7740\u6d45\u6d45\u7684\u7b11\uff0c\u5728\u9a6c\u5411\u4e1c\u3001\u8d75\u6893\u7433\u770b\u6765\u591a\u5c11\u6709\u4e9b\u9ad8\u6df1\u83ab\u6d4b\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u9ad8\u79d1\u6280\u4e0e\u7f51\u7edc\u7ecf\u6d4e\u7684\u5149\u73af\u7b3c\u7f69\u7740\u6574\u4e2a\u5317\u7f8e\u4e0e\u6b27\u6d32\u5730\u533a\uff0c\u6574\u4e2a\u5e02\u573a\u5bf9\u65b0\u79d1\u6280\u7684\u524d\u666f\u90fd\u6709\u7740\u7f8e\u597d\u7684\u9884\u671f\u2026\u2026\u4e0d\u8fc7\u6b64\u65f6\u770b\u6765\uff0c\u65b0\u79d1\u6280\u9700\u8981\u7ef4\u6301\u957f\u671f\u7684\u7e41\u8363\uff0c\u8fd8\u9700\u8981\u66f4\u6df1\u539a\u7684\u57fa\u7840\u3002\u201d\u8d75\u6893\u7433\u5f88\u5ba2\u89c2\u7684\u8bc4\u4ef7\u7740\uff0c\u5979\u4e5f\u4e0d\u8d5e\u540c\u5411\u4e92\u8054\u7f51\u4ea7\u4e1a\u6301\u7eed\u7684\u6295\u5165\u592a\u591a\u7684\u8d44\u672c\u3002</p><p>\u3000\u3000\u8d75\u6893\u7433\u4e0e\u5979\u9886\u5bfc\u7684\u56e2\u961f\u66ff\u9526\u6e56\u638c\u7ba1\u603b\u989d\u8fbe\u4e94\u4ebf\u5143\u7684\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\uff0c\u91cd\u70b9\u6276\u6301\u4e2d\u5c0f\u578b\u9ad8\u65b0\u79d1\u6280\u4f01\u4e1a\u7684\u6210\u957f\u3002\u8fd9\u4e24\u5e74\uff0c\u4e92\u8054\u7f51\u7edd\u5bf9\u662f\u65b0\u79d1\u6280\u6982\u5ff5\u4e2d\u6700\u91cd\u8981\u7684\u5206\u652f\uff0c\u56fd\u5185\u7684\u98ce\u6295\u8fd8\u5f88\u5f31\u5c0f\uff0c\u6a61\u6811\u56ed\u521b\u4e1a\u7edd\u5bf9\u8981\u7b97\u6709\u5b9e\u529b\u7684\u4e00\u5bb6\uff0c\u5728\u56fd\u5916\u98ce\u6295\u4e0d\u65ad\u6d8c\u5411\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\uff0c\u6a61\u6811\u56ed\u521b\u6295\u66f4\u503e\u5411\u6276\u6301\u4e0e\u57fa\u7840\u6027\u6280\u672f\u7684\u79d1\u6280\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u6a61\u6811\u56ed\u521b\u6295\u5bf9\u6606\u817e\u5728\u7ebf\u7684\u63a7\u80a1\uff0c\u53ef\u80fd\u8bf4\u662f\u6293\u4f4f\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u6700\u5927\u7684\u4e00\u5757\u86cb\u7cd5\u4e86\u3002\u9664\u4e86\u6a61\u6811\u56ed\u521b\u6295\u6700\u521d\u6295\u5165\u7684\u4e24\u5343\u4e94\u767e\u4e07\u8d77\u59cb\u8d44\u91d1\u5916\uff0c\u6606\u817e\u5728\u7ebf\u5728\u53bb\u5e74\u51ac\u5b63\u4ee5\u53ca\u4eca\u5e74\u590f\u5b63\u4ee5\u5e73\u5747\u6bcf\u80a11.6\u7f8e\u5143\u7684\u4ef7\u683c\u5411\u8457\u540d\u98ce\u6295\u534e\u767b\u7cfb\u65d7\u4e0b\u7684\u8fd1\u5341\u5bb6\u98ce\u6295\u57fa\u91d1\u8fdb\u884c\u4e86\u4e24\u6b21\u878d\u8d44\uff0c\u5171\u552e\u51fa\u8fd1\u4e00\u5343\u4e8c\u767e\u4e07\u80a1\u7684\u4f18\u5148\u80a1\uff0c\u83b7\u5f97\u8fd1\u4e24\u5343\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\u3002\u800c\u9526\u6e56\u6301\u6709\u6606\u817e\u5728\u7ebf\u80a1\u7968\u7684\u4ee3\u4ef7\uff0c\u6298\u7b97\u4e0b\u6765\uff0c\u751a\u81f3\u4e0d\u5230\u6bcf\u80a1\u4eba\u6c11\u5e012\u5143\uff0c\u5c31\u7b97\u4e0d\u4e0a\u5e02\uff0c\u5c31\u5c06\u9526\u6e56\u6301\u6709\u7684\u80a1\u4efd\u6089\u6570\u51fa\u552e\uff0c\u5df2\u7ecf\u80fd\u83b7\u5f97\u516d\u4e03\u500d\u7684\u66b4\u5229\uff0c\u4e0d\u8fc7\u5f20\u606a\u4e0d\u4f1a\u53ea\u6709\u8fd9\u70b9\u5fd7\u5411\u3002</p><p>\u3000\u3000\u867d\u8bf4\u95e8\u6237\u7f51\u7ad9\u7684\u8d62\u5229\u6a21\u5f0f\u4e0e\u9884\u671f\u90fd\u4e0d\u5341\u5206\u7684\u7406\u60f3\uff0c\u4f46\u662f\u5728\u7b2c\u4e00\u6b21\u65b0\u79d1\u6280\u6ce1\u6cab\u7834\u706d\u4e4b\u524d\uff0c\u95e8\u6237\u7f51\u7ad9\u7684\u6982\u5ff5\u5f97\u5230\u7edd\u5927\u591a\u6570\u6295\u8d44\u673a\u6784\u7684\u8ffd\u6367\u3002\u81f3\u5c11\u5728\u4e5d\u4e5d\u5e74\u4e4b\u524d\u575a\u5b9a\u4e0d\u79fb\u7684\u53d1\u5c55\u95e8\u6237\u7f51\u7edc\uff0c\u5728\u8d44\u91d1\u4e0a\u8fdb\u884c\u5927\u6295\u5165\uff0c\u6b64\u65f6\u7684\u6606\u817e\u5728\u7ebf\u5df2\u7ecf\u5f62\u6210\u5927\u9646\u3001\u9999\u6e2f\u3001\u53f0\u6e7e\u3001\u5317\u7f8e\u56db\u5730\u56db\u7ad9\u7684\u89c4\u6a21\uff0c\u5f53\u4e4b\u65e0\u6127\u7684\u662f\u5168\u7403\u7b2c\u4e00\u534e\u4eba\u95e8\u6237\u7f51\u7ad9\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4e86\u8ba9\u6606\u817e\u6210\u4e3a\u7b2c\u4e00\u5bb6\u6d77\u5916\u4e0a\u5e02\u7684\u4e92\u8054\u7f51\u4e2d\u56fd\u4f01\u4e1a\uff0c\u6211\u4eec\u5236\u5b9a\u5728\u660e\u5e74\u590f\u5b63\u4e4b\u524d\u4e0a\u5e02\u7684\u65f6\u95f4\u8868\uff0c\u660e\u5929\u4f1a\u8bae\u7684\u8ba8\u8bba\uff0c\u5c31\u5c06\u56f4\u7ed5\u7740\u8fd9\u4e2a\u65f6\u95f4\u8868\u8fdb\u884c\u2026\u2026\u201d\u9a6c\u5411\u4e1c\u5174\u594b\u7684\u8ddf\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u606a\u5c11\u5e94\u8be5\u4e0e\u9ad8\u76db\u7684\u66fe\u5b50\u8f89\u89c1\u4e00\u9762\uff0c\u6211\u60f3\u4f60\u4eec\u4e24\u4eba\u4f1a\u6709\u66f4\u591a\u7684\u5171\u540c\u8bed\u8a00\uff0c\u66fe\u5b50\u8f89\u4ed6\u672c\u4eba\u4e5f\u6781\u5176\u76fc\u671b\u4e0e\u4f60\u89c1\u4e00\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u8fc4\u4eca\u4e3a\u6b62\uff0c\u8fd8\u6ca1\u6709\u4e00\u5bb6\u4e2d\u56fd\u7684\u4e92\u8054\u7f51\u4f01\u4e1a\u5728\u6d77\u5916\u4e0a\u5e02\uff0c\u9876\u4e0a\u201c\u7b2c\u4e00\u5bb6\u6d77\u5916\u4e0a\u5e02\u7684\u4e2d\u56fd\u4e92\u8054\u7f51\u4f01\u4e1a\u201d\u7684\u5149\u73af\uff0c\u5bf9\u6295\u8d44\u6216\u6295\u673a\u673a\u6784\u6765\u8bf4\uff0c\u8c61\u5f81\u610f\u4e49\u65e0\u6bd4\u91cd\u8981\uff0c\u66f4\u5927\u8303\u56f4\u3001\u66f4\u77a9\u76ee\u7684\u5438\u5f15\u516c\u4f17\u4e0e\u6295\u8d44\u4eba\u7684\u76ee\u5149\uff0c\u5219\u610f\u5473\u7740\u4e0a\u5e02\u66f4\u9ad8\u7684\u80a1\u7968\u4ef7\u683c\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u521d\u6b65\u9009\u5b9a\u4e0e\u9ad8\u76db\u516c\u53f8\u5408\u4f5c\u8fdb\u884c\u6d77\u5916\u4e0a\u5e02\u7684\u8fd0\u4f5c\uff0c\u66fe\u5b50\u8f89\u662f\u9ad8\u76db\u4e9a\u6d32\u516c\u53f8\u7684\u526f\u603b\u88c1\uff0c\u4eca\u5e74\u521a\u521a\u5e74\u6ee1\u4e09\u5341\uff0c\u4e5f\u7edd\u5bf9\u80fd\u7b97\u5f97\u4e0a\u5c11\u5e74\u5f97\u5fd7\u7684\u4eba\u7269\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8981\u4e0e\u9ad8\u76db\u5408\u4f5c\uff0c\u9664\u4e86\u9009\u62e9\u9ad8\u76db\u4f5c\u4e3a\u6d77\u5916\u4e0a\u5e02\u7684\u4e3b\u627f\u9500\u5546\u4e4b\u5916\uff0c\u6606\u817e\u5728\u7ebf\u8fd8\u8981\u5411\u9ad8\u76db\u65d7\u4e0b\u7684\u98ce\u9669\u6295\u8d44\u57fa\u91d1\u8fdb\u884c\u7b2c\u4e09\u6b21\u66f4\u5927\u89c4\u6a21\u7684\u878d\u8d44\uff0c\u8fd9\u65f6\u4e0e\u9ad8\u76db\u516c\u53f8\u5c31\u878d\u8d44\u7684\u4ef7\u683c\u8c08\u5224\uff0c\u4f3c\u4e4e\u6bd4\u5236\u5b9a\u4ec0\u4e48\u65f6\u95f4\u8868\u66f4\u4e3a\u91cd\u8981\u3002</p><p>\u3000\u3000\u542c\u9a6c\u5411\u4e1c\u4f20\u8fbe\u66fe\u5b50\u8f89\u6709\u8ddf\u81ea\u5df1\u89c1\u9762\u7684\u610f\u601d\uff0c\u5f20\u606a\u6beb\u4e0d\u72b9\u8c6b\u7684\u7b54\u5e94\u4e0b\u6765\uff1a\u201c\u884c\uff0c\u4f60\u66ff\u6211\u5b89\u6392\u4e00\u4e0b\uff0c\u5728\u6211\u79bb\u5f00\u5317\u4eac\u4e4b\u524d\u8ddf\u4ed6\u89c1\u4e0a\u4e00\u9762\u4e5f\u65e0\u59a8\u3002\u201d</p><p>\u3000\u3000\u867d\u7136\u63d0\u524d\u5728\u4e5d\u4e5d\u5e74\u590f\u5b63\u4e0a\u5e02\u66f4\u52a0\u91cd\u8981\uff0c\u4f46\u662f\u5f20\u606a\u4e5f\u4e0d\u60f3\u7ed9\u522b\u4eba\u9020\u6210\u4ed6\u4e0d\u91cd\u89c6\u4ef7\u683c\u7684\u8868\u8c61\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4e0a\u5e02\u540e\u7684\u80a1\u7968\u4f9d\u8d56\u4e92\u8054\u7f51\u6982\u5ff5\u7684\u6574\u4f53\u72c2\u70ed\uff0c\u6210\u767e\u4e0a\u5343\u4e43\u81f3\u6570\u5343\u500d\u3001\u4e0a\u4e07\u500d\u7684\u5e02\u76c8\u5229\u90fd\u4e0d\u662f\u4ec0\u4e48\u795e\u8bdd\uff0c\u6b64\u65f6\u54ea\u6015\u767d\u9001\u9ad8\u76db\u51e0\u767e\u4e07\u80a1\u5bf9\u9526\u6e56\u7684\u6536\u76ca\u90fd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u592a\u5927\u7684\u5f71\u54cd\u3002\u4f46\u662f\uff0c\u767d\u9001\u7ed9\u4eba\u5bb6\u7684\u4e1c\u897f\u591a\u534a\u4e0d\u4f1a\u8ba9\u522b\u4eba\u73cd\u60dc\uff0c\u8c08\u5224\u81f3\u5c11\u8fd8\u662f\u663e\u5f97\u8270\u96be\u4e00\u4e9b\u7684\u597d\uff0c\u6216\u8005\u89c1\u9762\u5927\u5bb6\u80fd\u4e00\u89c1\u5982\u6545\u3001\u60fa\u60fa\u76f8\u60dc\u5012\u4e5f\u53ef\u4ee5\u52a0\u901f\u8c08\u5224\u7684\u8fdb\u7a0b\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9a6c\u5411\u4e1c\u7684\u5174\u5934\u5f88\u9ad8\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u602a\u3002</p><p>\u3000\u3000\u4e92\u8054\u7f51\u884c\u4e1a\u91cc\uff0c\u7edd\u5927\u591a\u6570\u90fd\u662f\u9a6c\u5411\u4e1c\u8fd9\u6837\u610f\u6c14\u98de\u626c\u7684\u5e74\u8f7b\u4eba\uff0c\u4e0d\u96be\u60f3\u50cf\u51fa\u5728\u4ed6\u4eec\u7684\u5708\u5b50\u5185\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u9ad8\u6da8\u7684\u70ed\u60c5\u3002\u4e0d\u8981\u8bf4\u5317\u4eac\u4e86\uff0c\u5c31\u5728\u5efa\u90ba\uff0c\u8d8a\u6765\u8d8a\u591a\u7684\u4eba\u90fd\u8ba4\u4e3a\u62e5\u6709\u4e92\u8054\u7f51\u5dee\u4e0d\u591a\u7b49\u540c\u4e8e\u62e5\u6709\u4e00\u5207\u3002</p><p>\u3000\u3000\u4ece\u4e5d\u56db\u5e74\u6625\u5b63\u8d77\uff0c\u56fd\u5185\u653f\u5e9c\u5bf9\u4e92\u8054\u7f51\u8fdb\u884c\u5927\u89c4\u6a21\u7684\u57fa\u5efa\uff0c\u5730\u65b9\u4fe1\u606f\u6e2f\u51e0\u4e4e\u5df2\u7ecf\u6df1\u5165\u5230\u6bcf\u4e00\u4e2a\u53bf\u7ea7\u57ce\u5e02\uff0c\u5b8c\u5584\u7684\u57fa\u5efa\u4e3a\u4e92\u8054\u7f51\u5185\u5bb9\u670d\u52a1\u63d0\u4f9b\u5546\u63d0\u63d0\u4f9b\u4e00\u4e2a\u65bd\u5c55\u7684\u5e73\u53f0\u3002\u6839\u636e\u6700\u65b0\u7684\u7edf\u8ba1\u6570\u636e\uff0c\u622a\u81f312\u6708\u4e2d\u65ec\uff0c\u56fd\u5185\u4e92\u8054\u7f51\u7528\u6237\u4e0a\u5347\u5230220\u4e07\u3002\u7535\u5b50\u90ae\u4ef6\u4e4b\u5916\uff0cBBS\u4e0e\u65b0\u95fb\u6210\u4e3a\u4e92\u8054\u7f51\u65b0\u7684\u6838\u5fc3\uff0c\u5438\u5f15\u8d8a\u6765\u8d8a\u591a\u7684\u8d44\u672c\u7684\u5173\u6ce8\u3002\u50cf\u9a6c\u5411\u4e1c\u5982\u4f55\u83b7\u5f97\u7b2c\u4e00\u7b14\u98ce\u9669\u6295\u8d44\u521b\u529e\u6606\u817e\u5728\u7ebf\u7684\u6545\u4e8b\uff0c\u4e5f\u6210\u4e3a\u4eba\u4eec\u8336\u4f59\u996d\u540e\u7684\u4e3b\u8981\u8c08\u8d44\u3002\u6b64\u65f6\u5a92\u4f53\u4e0e\u5927\u4f17\u4e5f\u5b81\u53ef\u76f8\u4fe1\uff0c\u5982\u679c\u4f60\u6709\u4e00\u4efd\u5b8c\u7f8e\u7684\u5546\u4e1a\u8ba1\u5212\u4e66\uff0c\u5c31\u8db3\u4ee5\u83b7\u5f97\u6295\u8d44\u5546\u6210\u5343\u4e0a\u4e07\u7684\u7f8e\u949e\u3002\u7f51\u7ad9\u521b\u5efa\u4eba\u4eec\u4e3a\u4e86\u8d62\u5f97\u8d44\u672c\u7684\u9752\u7750\uff0c\u6574\u5929\u90fd\u6316\u5fc3\u601d\u7684\u7b79\u5212\u5982\u4f55\u5438\u5f15\u5230\u66f4\u591a\u7684\u773c\u7403\uff0c\u5982\u4f55\u5360\u636e\u6bcf\u5929\u5404\u5927\u5a92\u4f53\u7684\u8981\u4f4d\u3002\u6b64\u65f6\u5317\u4eac\uff0c\u6bcf\u5929\u90fd\u6709\u65b0\u7684\u7f51\u7ad9\u8bde\u751f\uff0c\u4e92\u8054\u7f51\u516c\u53f8\u4e5f\u603b\u6709\u5f00\u4e0d\u5b8c\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u5317\u4eac\u4e5f\u51ed\u501f\u4e92\u8054\u7f51\u4ea7\u4e1a\u5728\u56fd\u5185\u9ad8\u65b0\u4ea7\u4e1a\u53d1\u5c55\u4e0a\u9886\u5148\u4e00\u6b65\u3002\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u5728\u6606\u817e\u5728\u7ebf\u4e4b\u5916\u5c31\u5f88\u5c11\u6d89\u53ca\u5230\u4e92\u8054\u7f51\u4f01\u4e1a\u7684\u6295\u8d44\uff0c\u5728\u5f88\u591a\u4eba\u770b\u6765\u90fd\u662f\u96be\u4ee5\u7406\u60f3\u4e8b\u60c5\uff0c\u5f20\u606a\u5fc3\u91cc\u5fae\u53f9\uff0c\u9526\u6e56\u5c31\u7b97\u6709\u518d\u5927\u7684\u80fd\u8010\uff0c\u4e5f\u4e0d\u80fd\u540c\u65f6\u6276\u6301\u4e24\u4e2a\u201c\u7b2c\u4e00\u534e\u4eba\u95e8\u6237\u7f51\u7ad9\u201d\u6765\uff0c\u8fd8\u4e0d\u77e5\u9053\u5728\u79d1\u6280\u80a1\u6ce1\u6cab\u7834\u706d\u3001\u65b0\u7ecf\u6d4e\u5bd2\u6d41\u9aa4\u7136\u6765\u88ad\u4e4b\u65f6\uff0c\u6709\u591a\u5c11\u96c4\u5fc3\u58ee\u5fd7\u4f1a\u7ed9\u6253\u51fb\u5f97\u7c89\u8eab\u788e\u9aa8\uff0c\u4e5f\u4e0d\u77e5\u9053\u4f1a\u6709\u591a\u5c11\u4eba\u5728\u5bd2\u6d41\u4e2d\u6323\u624e\u7740\u751f\u5b58\u4e0b\u6765\uff0c\u6210\u957f\u4e3a\u771f\u6b63\u7684\u4e92\u8054\u7f51\u82f1\u96c4\u3002</p><p>\u3000\u3000\u6b64\u65f6\uff0c\u6bd4\u8d77\u4e0e\u9ad8\u76db\u7684\u5408\u4f5c\uff0c\u5f20\u606a\u66f4\u62c5\u5fc3\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5bf9\u4e92\u8054\u7f51\u4f01\u4e1a\u6d77\u5916\u4e0a\u5e02\u7684\u8c28\u614e\u652f\u6301\u7684\u6001\u5ea6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8f66\u5b50\u9a76\u8fdb\u539f\u90ae\u7535\u90e8\u5bb6\u5c5e\u5c0f\u533a\uff0c\u8fdc\u8fdc\u7684\u770b\u89c1\u5c0f\u8868\u54e5\u6881\u6587\u6c5f\u4e0e\u738b\u5f69\u73b2\u7ad9\u5728\u697c\u524d\u7684\u7a7a\u573a\u4e0a\u7b49\u4ed6\uff0c\u4ed6\u4eec\u65c1\u8fb9\u8fd8\u7ad9\u7740\u4e00\u4e2a\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u524a\u7626\u9752\u5e74\u671d\u7740\u8f66\u5b50\u5f20\u671b\u3002\u5f20\u606a\u8ba9\u8f66\u9001\u4ed6\u5230\u697c\u4e0b\uff0c\u4e0b\u4e86\u8f66\uff0c\u6881\u6587\u6c5f\u5148\u4ecb\u7ecd\u738b\u5f69\u73b2\u7684\u54e5\u54e5\u738b\u5fd7\uff0c\u5f20\u606a\u4e0e\u738b\u5fd7\u63e1\u4e86\u63e1\u624b\uff0c\u95ee\u6881\u6587\u6c5f\uff1a\u201c\u5c0f\u8205\u5728\u697c\u4e0a\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u53bb\u9152\u5e97\u8fd8\u65e9\uff0c\u867d\u7136\u8bf4\u662f\u8ba2\u5a5a\u5bb4\uff0c\u4e5f\u53ea\u662f\u5bb6\u5bb4\u6027\u8d28\uff0c\u5f20\u606a\u8d76\u4e0a\u884c\u7a0b\u8fc7\u6765\u8e6d\u98df\u4e00\u9910\uff0c\u9664\u4e86\u5c0f\u8205\u4e0e\u5c0f\u8205\u5988\u8fc7\u6765\uff0c\u5927\u8868\u54e5\u6881\u6587\u5c71\u4e0e\u59bb\u5b50\u90fd\u6ca1\u6709\u5230\u5317\u4eac\u6765\u3002\u5012\u662f\u738b\u5f69\u73b2\u5bb6\u6bd4\u8f83\u91cd\u89c6\u4e9b\uff0c\u54e5\u54e5\u738b\u5fd7\u592b\u5987\u9886\u7740\u5973\u513f\u7279\u610f\u4ece\u6d59\u4e1c\u6587\u821f\u8d76\u56de\u6765\u3002</p><p>\u3000\u3000\u4e0a\u697c\u53bb\uff0c\u5c0f\u8205\u6881\u56fd\u76db\u4e0e\u738b\u5f69\u73b2\u7684\u7236\u4eb2\u738b\u65ed\u741b\u76d8\u68cb\u5750\u5728\u65e5\u5f0f\u7684\u4e66\u623f\u91cc\u4e0b\u68cb\uff0c\u4e0e\u5c0f\u8205\u5988\u8fd8\u738b\u5f69\u73b2\u7684\u6bcd\u4eb2\u6253\u8fc7\u62db\u547c\uff0c\u5c06\u793c\u7269\u9001\u4e0a\uff0c\u5f20\u606a\u5c31\u7ad9\u5728\u4e00\u65c1\u89c2\u68cb\uff0c\u738b\u5fd7\u5728\u65c1\u8fb9\u8bf4\u9053\uff1a\u201c\u542c\u6211\u7238\u4e0e\u6587\u6c5f\u8bf4\u4f60\u7684\u68cb\u5f88\u597d\uff0c\u5403\u996d\u8fd8\u65e9\uff0c\u80fd\u8bf7\u6559\u4e00\u76d8\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u738b\u5bb6\u8fd8\u51c6\u5907\u4e86\u4e24\u526f\u68cb\uff0c\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u6709\u610f\u51c6\u5907\uff0c\u5de6\u53f3\u4e5f\u662f\u65e0\u804a\uff0c\u8bf4\u9053\uff1a\u201c\u6253\u53d1\u65f6\u95f4\u6b63\u5408\u9002\u3002\u201d\u738b\u5f69\u73b2\u8dd1\u53bb\u62ff\u68cb\uff0c\u5f20\u606a\u4e0e\u6881\u6587\u6c5f\u4ed6\u4eec\u4e00\u8d77\u5230\u5ba2\u5385\u91cc\u5750\u4e0b\u6765\uff0c\u8fb9\u4e0b\u68cb\u8fb9\u804a\u5929\u3002</p><p>\u3000\u3000\u738b\u5fd7\u662f\u6d59\u4e1c\u6587\u821f\u5e02\u653f\u5e9c\u91cc\u7684\u666e\u901a\u516c\u52a1\u4eba\u5458\uff0c\u5f53\u5e74\u5728\u6d59\u4e1c\u8bfb\u7684\u5927\u5b66\uff0c\u8ba4\u8bc6\u73b0\u5728\u7684\u59bb\u5b50\uff0c\u6bd5\u4e1a\u540e\u5c31\u7559\u5728\u6d59\u4e1c\u6587\u821f\u5de5\u4f5c\u4e00\u76f4\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u632a\u8fc7\u7a9d\u3002\u738b\u5fd7\u4ece\u7236\u4eb2\u3001\u59b9\u59b9\u90a3\u91cc\u542c\u5230\u8bb8\u591a\u5173\u4e8e\u9526\u6e56\u582a\u79f0\u4f20\u5947\u7684\u6545\u4e8b\uff0c\u771f\u6b63\u770b\u5230\u5f20\u606a\u65f6\uff0c\u89c1\u4ed6\u7684\u8868\u73b0\u4e0e\u4e00\u4e2a\u7a0d\u5acc\u6709\u4e9b\u6c89\u9ed8\u7684\u5927\u5b66\u751f\u6ca1\u6709\u592a\u5927\u7684\u533a\u522b\uff0c\u4e5f\u770b\u4e0d\u5230\u591a\u4e48\u8000\u773c\u7684\u5149\u73af\u3002\u8981\u8bf4\u667a\u5546\u7684\u8bdd\uff0c\u5750\u5728\u5ba2\u5385\u91cc\u7684\u4eba\u8c01\u90fd\u4e0d\u4f1a\u627f\u8ba4\u667a\u5546\u4f1a\u6bd4\u522b\u4eba\u5dee\uff0c\u6216\u8bb8\u6881\u6587\u6c5f\u7684\u7eaf\u667a\u5546\u66f4\u9ad8\u4e00\u4e9b\u3002\u4e0d\u8fc7\u4e0e\u7236\u4eb2\u4e00\u6837\uff0c\u90fd\u662f\u6280\u672f\u578b\u7684\u4eba\u624d\uff0c\u7236\u4eb2\u5728\u4fe1\u606f\u4ea7\u4e1a\u90e8\u91cc\u867d\u7136\u662f\u53f8\u7ea7\u7684\u5e72\u90e8\u7f16\u5236\uff0c\u5b9e\u9645\u4e0a\u5374\u6ca1\u6709\u4ec0\u4e48\u5b9e\u6743\uff0c\u6881\u6587\u6c5f\u5728\u6606\u817e\u5728\u7ebf\u7684\u5730\u4f4d\u4e5f\u4e0d\u4f4e\uff0c\u4f46\u5bf9\u7ba1\u7406\u4e0a\u7684\u4e8b\u60c5\u5f88\u5c11\u8fc7\u95ee\u3002\u738b\u5fd7\u5f53\u521d\u4e5f\u662f\u62c5\u5fc3\u56de\u5317\u4eac\u4f1a\u7ed9\u5bb6\u91cc\u903c\u7740\u4ece\u4e8b\u6280\u672f\u5de5\u4f5c\u624d\u51b3\u5fc3\u7559\u5728\u6587\u821f\u7684\uff0c\u53ea\u4e0d\u8fc7\u7236\u4eb2\u5728\u5317\u4eac\u4f7f\u4e0d\u4e0a\u529b\uff0c\u5cb3\u7236\u5bb6\u5728\u6587\u821f\u4e5f\u6ca1\u6709\u4ec0\u4e48\u80cc\u666f\uff0c\u5de5\u4f5c\u4e0a\u5e76\u4e0d\u7b97\u7279\u522b\u7684\u5982\u610f\u3002</p></article>"}}}]);
