(window.webpackJsonp=window.webpackJsonp||[]).push([[118],{140:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u8282 \u65b0\u9ebb\u70e6",content:"<article><h2>\u7b2c\u4e09\u5341\u8282 \u65b0\u9ebb\u70e6</h2><p>\u4e24\u4eba\u5728\u5e8a\u4e0a\u7f20\u7ef5\u534a\u65e5\uff0c\u8d77\u8eab\u65f6\u53d1\u73b0\u90fd\u5df2\u7ecf\u662f\u65e9\u4e0a\u5341\u4e00\u70b9\u949f\u4e86\u3002\u8fd9\u4e2a\u4e16\u754c\u6ca1\u6709\u949f\u8868\uff0c\u4f46\u4e5f\u6709\u7c7b\u4f3c\u7684\u8bb0\u65f6\u5de5\u5177\uff0c\u5c31\u662f\u6c99\u6f0f\u4e86\u3002\u9b54\u6cd5\u6c99\u6f0f\u8db3\u4ee5\u4fdd\u8bc1\u8bb0\u65f6\u7cbe\u786e\uff0c\u4e0d\u4e9a\u4e8e\u949f\u8868\u7684\u3002</p><p>\u70db\u5821\u9ad8\u5854\u662f\u516b\u70b9\u949f\u5f00\u95e8\uff0c\u4f46\u4e5f\u6ca1\u89c4\u5b9a\u8fc7\u4e86\u65f6\u95f4\u5c31\u4e0d\u80fd\u518d\u8fdb\u3002\u4eca\u5929\u662f\u4ed6\u4eec\u505c\u7559\u70db\u5821\u7684\u7b2c\u5341\u5929\uff0c\u4e5f\u662f\u6700\u540e\u4e00\u65e5\uff0c\u660e\u5929\u65e9\u4e0a\u5c31\u5f97\u52a8\u8eab\u79bb\u5f00\u4e86\u3002</p><p>\u70db\u5821\u867d\u7136\u6536\u8d39\u6602\u8d35\uff0c\u5bb0\u5ba2\u6781\u72e0\uff0c\u4f46\u5012\u8fd8\u6ca1\u6709\u5f53\u94fa\u4e60\u6c14\u3002\u743c\u6069\u62ff\u51fa\u6628\u5929\u6311\u62e3\u51fa\u7684\u51e0\u9897\u94bb\u77f3\uff0c\u539f\u672c\u8fd8\u62c5\u5fc3\u5bf9\u65b9\u4f1a\u4e0d\u4f1a\u6545\u610f\u538b\u4f4e\u4ef7\u683c\uff0c\u4f46\u201c\u5f15\u5bfc\u8005\u201d\u53ea\u77a5\u4e86\u773c\uff0c\u62a5\u51fa\u4e00\u4e2a\u4ef7\u683c\u6765\uff0c\u548c\u6885\u83f2\u65af\u4e4b\u524d\u4f30\u7b97\u7684\u76f8\u5dee\u65e0\u51e0\u3002</p><p>\u4ed8\u51fa\u8fd9\u4e9b\u94bb\u77f3\uff0c\u743c\u6069\u6362\u6765\u7684\u662f\u9b54\u6cd5\u4e66\u4e0a\u65b0\u6284\u5f55\u4e86\u5341\u4e2a\u6cd5\u672f\u3002\u90a3\u4e48\uff0c\u8fd9\u6b21\u6765\u70db\u5821\u4e00\u884c\uff0c\u53ef\u8c13\u662f\u5927\u6709\u6536\u83b7\uff0c\u867d\u7136\u83ab\u540d\u5176\u5999\u88ab\u4e00\u4e2a\u5973\u6740\u624b\u8ffd\u6740\u5f88\u4e0d\u723d\uff0c\u4f46\u53cd\u6b63\u5979\u90fd\u5df2\u7ecf\u6d3b\u57cb\u5728\u5730\u4e0b\u4e86\uff0c\u4e5f\u4e0d\u5fc5\u518d\u653e\u5728\u5fc3\u4e0a\u3002</p><p>\u53ea\u5269\u4e0b\u4e00\u4e2a\u62c9\u6c83\u514b\u7684\u4e8b\u60c5\u8981\u5934\u75bc\u4e86\u2026\u2026\u5176\u5b9e\uff0c\u8fd9\u624d\u662f\u6700\u5934\u75bc\u7684\u95ee\u9898\u5427\u3002\u5185\u529f\u6709\u65e0\u63d0\u5347\uff0c\u9b54\u6cd5\u6280\u827a\u6709\u65e0\u589e\u8fdb\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u662f\u4e0d\u662f\u5728\u76d1\u89c6\u81ea\u5df1\uff0c\u751a\u81f3\u548c\u6885\u83f2\u65af\u7684\u5173\u7cfb\u662f\u5426\u6709\u8fdb\u5c55\uff0c\u8fd9\u90fd\u662f\u53ef\u4ee5\u6682\u4e14\u653e\u4e0b\u6162\u6162\u518d\u8bf4\u7684\u4e8b\u60c5\uff0c\u552f\u6709\u62c9\u6c83\u514b\u8fd9\u4ef6\u4e8b\uff0c\u5374\u662f\u5f53\u52a1\u4e4b\u6025\u3002\u907f\u4e0d\u8fc7\uff0c\u7ed5\u4e0d\u5f00\uff0c\u5fc5\u987b\u53bb\u505a\uff0c\u504f\u504f\u8fd8\u98ce\u9669\u5341\u8db3\u3002</p><p>\u4e3a\u4ec0\u4e48\u504f\u504f\u81ea\u5df1\u4f1a\u644a\u4e0a\u8fd9\u4e48\u4e2a\u70eb\u624b\u4efb\u52a1\u3002</p><p>\u4e0d\u6687\u8003\u8651\u8fd9\u4e9b\uff0c\u4ed6\u5f00\u59cb\u81ea\u5df1\u5728\u70db\u5821\u671f\u95f4\u7684\u6700\u540e\u4e00\u6b21\u9605\u8bfb\u3002\u8be5\u6284\u5f55\u7684\u6cd5\u672f\u90fd\u5df2\u7ecf\u6284\u5f55\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u6253\u7b97\u518d\u7814\u7a76\u65b0\u7684\uff0c\u53ea\u5728\u4e66\u67b6\u4e0a\u968f\u624b\u7ffb\u9605\uff0c\u770b\u6709\u6ca1\u6709\u4ec0\u4e48\u65b0\u9c9c\u6709\u8da3\u7684\u4e1c\u897f\u3002</p><p>\u201c\u585e\u5c14\u7684\u7ea2\u888d\u5deb\u5e08\uff1f\u201d</p><p>\u7ffb\u9605\u4e86\u534a\u5929\uff0c\u743c\u6069\u88ab\u8fd9\u4e2a\u4e66\u540d\u7ed9\u5438\u5f15\u4f4f\u4e86\u3002\u8fd9\u4e16\u754c\u4e0a\u5deb\u5e08\u591a\u5f97\u662f\uff0c\u4f46\u7279\u5730\u4ee5\u888d\u5b50\u989c\u8272\u6765\u547d\u540d\u7684\uff0c\u5012\u662f\u7f55\u89c1\u2026\u2026\u5982\u679c\u5355\u5355\u5982\u6b64\u5012\u4e5f\u7f62\u4e86\uff0c\u4f46\u743c\u6069\u4e0d\u77e5\u600e\u4e48\u641e\u7684\uff0c\u603b\u89c9\u5f97\u4ee5\u524d\u50cf\u662f\u5728\u54ea\u91cc\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u540d\u5b57\u4f3c\u7684\u3002</p><p>\u201c\u7ea2\u888d\u5deb\u5e08\u2026\u2026\u7ea2\u888d\u5deb\u5e08\u2026\u2026\u201d\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u601d\u7d22\u7740\uff0c\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u3002\u4ed6\u4f38\u624b\u4ece\u4e66\u67b6\u4e0a\u53d6\u4e0b\u4e66\uff0c\u7ffb\u5f00\u6d4f\u89c8\uff0c\u5f53\u4ed6\u770b\u5230\u76ee\u5f55\u4e2d\u201c\u73af\u6cd5\u4eea\u5f0f\u201d\u4e00\u680f\u7684\u65f6\u5019\uff0c\u9661\u5730\u56de\u5fc6\u8d77\u6765\u3002</p><p>\u201c\u662f\u4e86\uff0c\u5728\u5b66\u6821\u7684\u65f6\u5019\uff0c\u6559\u6388\u66fe\u7ecf\u8bf4\u8fc7\uff1a\u7269\u8d28\u754c\u6709\u4e00\u4e2a\u53eb\u585e\u5c14\u7684\u5730\u533a\uff0c\u653f\u4f53\u98ce\u4fd7\u9887\u6709\u7c7b\u4f3c\u9634\u9b42\u57ce\u4e4b\u5904\uff0c\u540c\u6837\u4e5f\u662f\u5deb\u5e08\u79c9\u653f\uff0c\u9b54\u6cd5\u660c\u76db\uff0c\u800c\u4e14\u4e8e\u9b54\u6cd5\u6280\u827a\u4e00\u9053\uff0c\u540c\u6837\u4e5f\u662f\u79c9\u6301\u2018\u4e13\u7cbe\u80dc\u4e8e\u535a\u5b66\u2019\u8fd9\u4e00\u89c2\u5ff5\uff0c\u6240\u6709\u7684\u9ad8\u4f4d\u9636\u8005\uff0c\u5168\u90fd\u662f\u7cbe\u7814\u67d0\u4e00\u5b66\u6d3e\u7684\u5deb\u5e08\u3002\u56e0\u4e3a\u4e00\u5f8b\u7a7f\u7ea2\u8272\u5deb\u5e08\u888d\uff0c\u6240\u4ee5\u901a\u5e38\u79f0\u4e3a\u7ea2\u888d\u5deb\u5e08\u3002\u8fd9\u73af\u6cd5\u4eea\u5f0f\uff0c\u636e\u8bf4\u5c31\u662f\u7ea2\u888d\u5deb\u5e08\u7684\u72ec\u95e8\u5bc6\u6280\uff0c\u5177\u4f53\u60c5\u5f62\u4e0d\u77e5\uff0c\u4f3c\u4e4e\u662f\u4e00\u79cd\u5927\u578b\u7684\u9b54\u6cd5\u9635\uff0c\u80fd\u79ef\u805a\u591a\u540d\u5deb\u5e08\u7684\u529b\u91cf\uff0c\u6682\u65f6\u5927\u5e45\u5ea6\u63d0\u5347\u67d0\u4e00\u540d\u5deb\u5e08\u7684\u65bd\u6cd5\u80fd\u529b\u3002\u201d</p><p>\u5f53\u65f6\u6559\u6388\u4e5f\u53ea\u662f\u6cdb\u6cdb\u800c\u8c08\uff0c\u6ca1\u6709\u4ed4\u7ec6\u89e3\u8bf4\uff0c\u743c\u6069\u5fc3\u4e2d\u9887\u6709\u4e9b\u597d\u5947\uff0c\u4f46\u65f6\u95f4\u957f\u4e86\u4e5f\u5c31\u5fd8\u8bb0\u3002\u5982\u4eca\u60f3\u8d77\uff0c\u767b\u65f6\u5bf9\u8fd9\u4efd\u8d44\u6599\u8d77\u4e86\u5174\u8da3\u3002</p><p>\u7ffb\u5f00\u770b\u770b\uff0c\u9996\u5148\u6620\u5165\u773c\u5e18\u7684\u662f\u4e00\u526f\u753b\u50cf\uff0c\u5012\u662f\u628a\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u548c\u5c1a\uff1f\u201d</p><p>\u5b9a\u775b\u518d\u770b\uff0c\u53d1\u73b0\u4e0d\u662f\u548c\u5c1a\uff0c\u53ea\u662f\u540c\u6837\u4e5f\u5243\u7740\u5149\u5934\uff0c\u800c\u4e14\u5934\u4e0a\u8fd8\u6709\u5947\u602a\u7684\u56fe\u6848\uff0c\u81ea\u5df1\u770b\u9519\u6210\u4e86\u6212\u75a4\u3002\u4ece\u7ea2\u8272\u7684\u957f\u888d\u548c\u624b\u4e2d\u6cd5\u6756\u6765\u5224\u65ad\uff0c\u8fd9\u663e\u7136\u5c31\u662f\u7ea2\u888d\u5deb\u5e08\u7684\u5f62\u8c61\u4e86\u3002</p><p>\u201c\u9760\uff0c\u5f53\u5deb\u5e08\u5c31\u8001\u8001\u5b9e\u5b9e\u5f53\u5deb\u5e08\uff0c\u628a\u5934\u53d1\u5243\u5149\u7b97\u4ec0\u4e48\uff0c\u60f3\u8fdb\u5c11\u6797\u5bfa\u88c5\u548c\u5c1a\u554a\u2026\u2026\u7b49\u7b49\uff0c\u600e\u4e48\u9664\u4e86\u5934\u53d1\uff0c\u8fde\u7709\u6bdb\u90fd\u5243\u4e86\uff1f\u201d</p><p>\u743c\u6069\u6765\u8fd9\u4e16\u754c\u4e5f\u5341\u4e94\u5e74\u4e86\uff0c\u751f\u6d3b\u5728\u5deb\u5e08\u904d\u5730\u8d70\u7684\u9634\u9b42\u57ce\uff0c\u81ea\u5df1\u4e5f\u662f\u4e2a\u5deb\u5e08\uff0c\u4ece\u6ca1\u542c\u8bf4\u8c01\u5403\u996d\u6ca1\u4e8b\u5e72\u628a\u5934\u53d1\u7709\u6bdb\u7ed9\u5243\u4e86\u3002</p><p>\u7ffb\u5f00\u540e\u9762\u7684\u4ecb\u7ecd\uff0c\u770b\u5230\u4e00\u884c\u5b57\u5982\u4e0b\uff1a</p><p>\u7ea2\u888d\u5deb\u5e08\u4eec\u4f1a\u5243\u9664\u5934\u9885\u4e0a\u6240\u6709\u7684\u6bdb\u53d1\uff08\u5305\u62ec\u7709\u6bdb\u4e0e\u773c\u776b\u6bdb\uff09\u2014\u2014\u6709\u4e9b\u751a\u81f3\u4f1a\u522e\u53bb\u5168\u8eab\u7684\u6bdb\u53d1\u2014\u2014\u5e76\u7eb9\u4e0a\u82b1\u6837\u7e41\u590d\u7684\u9b54\u6cd5\u523a\u9752\u3002\u8fd9\u4e9b\u523a\u9752\u901a\u5e38\u90fd\u50a8\u6709\u6cd5\u672f\uff0c\u53ef\u4f9b\u60c5\u51b5\u5371\u6025\u65f6\u4f7f\u7528\uff08\u56e0\u6b64\u8bb8\u591a\u5728\u56fd\u5916\u88ab\u6355\u7684\u585e\u5c14\u56da\u72af\u5e38\u5e38\u4f1a\u2018\u795e\u79d8\u5931\u8e2a\u2019\uff09\u3002</p><p>\u539f\u6765\u5982\u6b64\uff0c\u8fd9\u5e2e\u7ea2\u888d\u5deb\u5e08\u771f\u662f\u53d8\u6001\u3002\u521a\u624d\u90a3\u5149\u5934\u4e0a\u7684\u5947\u602a\u56fe\u6848\uff0c\u5e94\u8be5\u5c31\u662f\u8fd9\u6240\u8c13\u7684\u201c\u9b54\u6cd5\u523a\u9752\u201d\u4e86\uff0c\u81ea\u5df1\u5dee\u70b9\u770b\u6210\u4e86\u6212\u75a4\u3002</p><p>\u7ee7\u7eed\u770b\u4e0b\u53bb\uff0c\u6e10\u6e10\u5f00\u59cb\u770b\u51fa\u4e0d\u5bf9\u52b2\u7684\u5730\u65b9\u6765\u3002\u679c\u7136\u5982\u6559\u6388\u6240\u8bf4\uff0c\u8fd9\u7ea2\u888d\u5deb\u5e08\u7684\u5f88\u591a\u60c5\u51b5\uff0c\u786e\u5b9e\u548c\u9634\u9b42\u57ce\u9887\u4e3a\u76f8\u50cf\uff0c\u65e0\u8bba\u662f\u653f\u4f53\uff0c\u662f\u6559\u80b2\u5236\u5ea6\uff0c\u662f\u5deb\u5e08\u9009\u62d4\u4f53\u5236\uff0c\u751a\u81f3\u5f88\u591a\u9b54\u6cd5\u4e0a\u7684\u7406\u5ff5\u548c\u770b\u6cd5\uff0c\u90fd\u6709\u5f02\u66f2\u540c\u5de5\u6216\u8005\u4e0d\u8c0b\u800c\u5408\u4e4b\u5904\u2026\u2026\u8fd9\u662f\u4e0d\u662f\u4e5f\u592a\u5de7\u5408\u4e86\u70b9\u3002</p><p>\u4e00\u5904\u4e24\u5904\u96f7\u540c\uff0c\u53ef\u4ee5\u8bf4\u662f\u5de7\u5408\uff1b\u4f46\u8fd9\u4e48\u591a\u8fd1\u4f3c\u4e4b\u5904\uff0c\u53ea\u6015\u5c31\u6709\u6284\u88ad\u2026\u2026\u4e0d\uff0c\u501f\u9274\u7684\u5acc\u7591\u4e86\u3002</p><p>\u4f46\u9634\u9b42\u57ce\u4e00\u76f4\u5c01\u5728\u5e7d\u5f71\u754c\u4e00\u5343\u4e03\u767e\u591a\u5e74\uff0c\u51e0\u4e2a\u6708\u524d\u624d\u521a\u521a\u56de\u5230\u7269\u8d28\u754c\uff1b\u800c\u585e\u5c14\u56fd\u662f922DR\u5efa\u7acb\u7684\uff0c\u8ddd\u4eca\u4e0d\u8fc7\u56db\u767e\u4e94\u5341\u5e74\uff0c\u65f6\u95f4\u6839\u672c\u5bf9\u4e0d\u4e0a\u3002</p><p>\u5f88\u5947\u602a\u3002</p><p>\u743c\u6069\u8d77\u4e86\u597d\u5947\u5fc3\uff0c\u60f3\u4ed4\u7ec6\u7814\u7a76\u4e00\u4e0b\uff0c\u4f46\u65f6\u95f4\u5df2\u7ecf\u5269\u4e0b\u4e0d\u591a\u4e86\uff0c\u4ed6\u8fde\u901a\u89c8\u5168\u4e66\u90fd\u6765\u4e0d\u53ca\u3002\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u70db\u5821\u4e0d\u4ec5\u4ec5\u662f\u53ef\u4ee5\u6284\u5f55\u6cd5\u672f\u7684\uff0c\u53ea\u8981\u4f60\u4ed8\u94b1\uff0c\u6284\u4e66\u4e5f\u6ca1\u95ee\u9898\u3002</p><p>\u7528\u4e00\u5757\u84dd\u6c34\u6676\u505a\u4ee3\u4ef7\uff0c\u743c\u6069\u8bf7\u6284\u5f55\u5458\u5c06\u8fd9\u672c\u300a\u585e\u5c14\u7684\u7ea2\u888d\u5deb\u5e08\u300b\u590d\u5f55\u4e86\u4e00\u4efd\uff0c\u6536\u5165\u6000\u4e2d\u3002\u7a97\u5916\u66ae\u8272\u6e10\u8d77\uff0c\u5feb\u508d\u665a\u4e86\u3002</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u8d70\u51fa\u9ad8\u5854\uff0c\u51c6\u5907\u56de\u5230\u4f4f\u5904\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u5f97\u52a8\u8eab\u79bb\u5f00\u4e86\u3002\u743c\u6069\u7684\u623f\u95e8\u5df2\u7ecf\u5728\u767d\u5929\u88ab\u70db\u5821\u6d3e\u4eba\u4fee\u597d\u4e86\uff0c\u4f46\u4ed6\u6253\u7b97\u7ee7\u7eed\u50cf\u6628\u665a\u4e00\u6837\u8d56\u5230\u6885\u83f2\u65af\u623f\u95f4\u91cc\u53bb\uff0c\u660e\u660e\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u7f8e\u4eba\u53ef\u4ee5\u62b1\u7740\uff0c\u4e3a\u4f55\u8981\u4e00\u4e2a\u4eba\u5b64\u6795\u96be\u7720\uff0c\u8fd9\u4e0d\u662f\u8650\u5f85\u81ea\u5df1\u4e48\u3002\u6b63\u601d\u5fd6\u7740\uff0c\u80cc\u540e\u7a81\u7136\u4f20\u6765\u4e00\u58f0\u558a\u53eb\u3002</p><p>\u201c\u795e\u9009\u8005\u9601\u4e0b\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff01\u201d</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u9f50\u9f50\u56de\u5934\uff0c\u89c1\u70db\u5821\u7684\u57ce\u95e8\u53e3\u51b2\u8fdb\u6765\u4e00\u4e2a\u4eba\uff0c\u98ce\u5c18\u4ec6\u4ec6\uff0c\u7a7f\u4e00\u8eab\u76ae\u7532\uff0c\u6234\u7740\u9ed1\u8272\u5934\u76d4\uff0c\u4e24\u8fb9\u9576\u7740\u7ea2\u8272\u6761\u7eb9\uff0c\u8fd9\u662f\u535a\u5f97\u4e4b\u95e8\u7684\u536b\u5175\u7684\u6807\u51c6\u5236\u670d\u3002</p><p>\u6765\u4eba\u5598\u606f\u7740\uff0c\u8dd1\u5230\u8fd1\u524d\u884c\u793c\uff0c\u81ea\u79f0\u662f\u8389\u62c9\u516c\u7235\u6d3e\u6765\u7684\u4f7f\u8005\uff0c\u7279\u6765\u9001\u4fe1\uff0c\u968f\u5373\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5c01\u4fe1\u9012\u4e0a\u3002\u6885\u83f2\u65af\u63a5\u8fc7\u4fe1\uff0c\u62c6\u5f00\u4e00\u770b\uff0c\u7709\u5934\u5fae\u5fae\u76b1\u8d77\u6765\u3002</p><p>\u201c\u8389\u62c9\u516c\u7235\u8bf4\u6709\u6025\u4e8b\uff0c\u8bf7\u6211\u4eec\u56de\u535a\u5f97\u4e4b\u95e8\u5546\u8bae\u3002\u201d</p><p>\u743c\u6069\u9887\u6709\u4e9b\u5947\u602a\uff0c\u6309\u9053\u7406\u8bf4\uff0c\u6885\u83f2\u65af\u7684\u4efb\u52a1\u662f\u8981\u62a4\u9001\u9ece\u660e\u4e4b\u77f3\u5230\u535a\u5f97\u4e4b\u95e8\uff0c\u5982\u4eca\u5df2\u7ecf\u5b8c\u6210\u4e86\u3002\u5269\u4e0b\u6765\u7684\u4e8b\u60c5\u4e0e\u5979\u65e0\u5173\uff0c\u4e5f\u63d2\u4e0d\u4e0a\u624b\u3002\u7b97\u7b97\u65f6\u95f4\uff0c\u6668\u66e6\u4e4b\u795e\u7684\u796d\u7940\u4eea\u5f0f\u4e5f\u8be5\u5b8c\u6210\u4e86\uff0c\u96be\u9053\u53c8\u6709\u4ec0\u4e48\u53d8\u6545\u4e0d\u6210\uff1f</p><p>\u201c\u761f\u75ab\u60c5\u51b5\u5982\u4f55\uff1f\u201d\u743c\u6069\u95ee\u4f7f\u8005\u3002</p><p>\u56de\u7b54\u662f\u4e00\u5207\u987a\u5229\uff0c\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u51c6\u5907\u59a5\u5f53\uff0c\u5728\u4eca\u65e5\u51cc\u6668\u4e3e\u884c\u796d\u7940\u4eea\u5f0f\uff0c\u6fc0\u53d1\u9ece\u660e\u4e4b\u77f3\u7684\u5723\u529b\uff0c\u4e00\u4e3e\u9a71\u6563\u6d88\u89e3\u4e86\u7b3c\u7f69\u5728\u535a\u5f97\u4e4b\u95e8\u7684\u761f\u75ab\u9634\u4e91\uff0c\u5982\u4eca\u5168\u57ce\u6b22\u5e86\uff0c\u5f20\u706f\u7ed3\u5f69\u2026\u2026</p><p>\u201c\u597d\u4e86\u597d\u4e86\uff0c\u201d\u743c\u6069\u66f4\u52a0\u4e0d\u660e\u767d\u4e86\uff0c\u5982\u679c\u51fa\u4e86\u95ee\u9898\uff0c\u6765\u627e\u6885\u83f2\u65af\u8fd8\u53ef\u4ee5\u7406\u89e3\uff0c\u5982\u4eca\u65e2\u7136\u4e00\u5207\u987a\u5229\uff0c\u90a3\u8fd9\u4e48\u6025\u5306\u5306\u7684\u505a\u4ec0\u4e48\uff1f\u96be\u4e0d\u6210\u662f\u8981\u5f00\u8868\u5f70\u5927\u4f1a\u4e48\uff0c\u90a3\u4e5f\u4e0d\u7528\u8fd9\u4e48\u7740\u6025\u5427\uff0c\u201c\u90a3\u5230\u5e95\u662f\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p></article>"}}}]);
