(window.webpackJsonp=window.webpackJsonp||[]).push([[1180],{1203:function(p){p.exports={chapterTitle:null,title:"\u7b2c1177\u7ae0 \u5c0f\u7684\u4e5f\u662f\u8d54\u94b1\u8d27",content:"<article><h2>\u7b2c1177\u7ae0 \u5c0f\u7684\u4e5f\u662f\u8d54\u94b1\u8d27</h2><p>\u3000\u3000\u5b59\u9759\u6aac\u7a7f\u7740\u5bbd\u5927\u7684T\u6064\u8737\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u4fee\u957f\u7684\u53cc\u817f\u8086\u65e0\u5fcc\u60ee\u7684\u6401\u5728\u9ed1\u8272\u70e4\u6f06\u7f15\u82b1\u73bb\u7483\u8336\u51e0\u4e0a\uff0c\u50cf\u662f\u6446\u5728\u8336\u51e0\u4e0a\u7cbe\u7f8e\u5de5\u827a\u54c1\uff0cT\u6064\u5305\u7740\u6d51\u5706\u5145\u6ee1\u5f39\u6027\u7684\u81c0\u90e8\uff0c\u770b\u4e0d\u51fa\u6709\u7a7f\u7740\u5185\u88e4\u7684\u75d5\u8ff9\uff0c\u60f9\u5f97\u5f20\u606a\u5fc3\u75d2\u75d2\u7684\u5750\u7acb\u4e0d\u5b89\uff0c\u6362\u4e86\u597d\u51e0\u4e2a\u59ff\u52bf\uff0c\u624b\u673a\u90fd\u6389\u5730\u6bef\u4e0a\u4e24\u56de\uff0c\u5948\u4f55\u5b59\u9759\u6aac\u5750\u59ff\u5de7\u5999\uff0c\u5f20\u606a\u53ea\u770b\u5f97\u89c1\u96ea\u767d\u7684\u5927\u817f\u5185\u4fa7\u8fb9\u7f18\uff0c\u5374\u770b\u4e0d\u6e05\u91cc\u9762\u6709\u6ca1\u6709\u7a7f\u5185\u88e4\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u7740\u662f\u4e0d\u662f\u51d1\u5230\u8ddf\u524d\u53bb\uff0c\u53c8\u62c5\u5fc3\u5ba4\u5916\u8349\u576a\u4e0a\u7684\u4eba\u80fd\u900f\u8fc7\u73bb\u7483\u7a97\u770b\u5230\u91cc\u9762\u7684\u60c5\u5f62\uff0c\u601d\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u62ff\u8d77\u8054\u5408\u65e5\u62a5\u5047\u88c5\u6b63\u7ecf\u7684\u9605\u8bfb\u8d77\u6765\uff0c\u6ca1\u8fc7\u4e86\u4e00\u4f1a\u513f\u65f6\u95f4\uff0c\u5c31\u542c\u89c1\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u5728\u8fc7\u9053\u91cc\u7684\u8bf4\u8bdd\u58f0\uff0c\u5f20\u606a\u6697\u9053\u4fa5\u5e78\uff0c\u62cd\u4e86\u62cd\u80f8\u53e3\uff0c\u5b59\u9759\u6aac\u4e5f\u4f3c\u4e4e\u65e0\u610f\u7684\u62ff\u8d77\u4e00\u6761\u8584\u6bef\u5b50\u76d6\u5728\u5927\u817f\u4e0a\uff0c\u53c8\u9119\u89c6\u7684\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u5c0f\u606a\uff0c\u4f60\u8bf4\u5b59\u59d0\u8fd9\u6b21\u5728\u9a6c\u5c14\u4ee3\u592b\u8fd8\u4f1a\u9080\u8bf7\u54ea\u4e9b\u4eba\uff1f\u201d\u5510\u5a67\u63a8\u95e8\u51b2\u4e86\u8fdb\u6765\uff0c\u4ece\u540e\u9762\u6402\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u4e0b\u5df4\u538b\u5728\u4ed6\u7684\u8111\u888b\u4e0a\u95ee\u4ed6\u95ee\u9898\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u62ff\u7b14\u62b5\u7740\u5634\u5507\uff0c\u4f3c\u4e4e\u4e13\u5fc3\u7684\u7814\u7a76\u5979\u7684\u9009\u79c0\u8282\u76ee\u7b56\u5212\u6848\uff0c\u5bf9\u5510\u5a67\u4e0e\u5f20\u606a\u7684\u4eb2\u70ed\u89c6\u800c\u4e0d\u89c1\uff0c\u9648\u5983\u84c9\u5219\u4e00\u4ed8\u7d2f\u60e8\u7684\u6a21\u6837\uff0c\u5c06\u5979\u4e0e\u5510\u5a67\u4eca\u5929\u901b\u8857\u7684\u6536\u83b7\u2014\u2014\u4e00\u645e\u7cbe\u81f4\u7eb8\u63d0\u888b\u653e\u5728\u8336\u51e0\u4e0a\uff0c\u4e5f\u9887\u4e3a\u5173\u5fc3\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u795e\u795e\u79d8\u79d8\u7684\uff0c\u6211\u4e5f\u4e0d\u6653\u5f97\u554a\u3002\u201d\u5f20\u606a\u5934\u75bc\u7684\u76b1\u8d77\u7709\u5934\uff0c\u5b59\u9759\u9999\u4e0e\u53f6\u5efa\u658c\u6ca1\u6709\u60f3\u7740\u60ca\u52a8\u4ec0\u4e48\u4eba\u624d\u8eb2\u5230\u9a6c\u5c14\u4ee3\u592b\u4e3e\u529e\u5a5a\u793c\uff0c\u4f46\u4e5f\u4e0d\u662f\u4ec0\u4e48\u4eba\u90fd\u4e0d\u9080\u8bf7\uff0c\u53ea\u662f\u4ed6\u4eec\u9080\u8bf7\u4e86\u54ea\u4e9b\u4eba\u8fd9\u65f6\u5019\u90fd\u4fdd\u5bc6\u7740\uff0c\u4e0d\u900f\u9732\u4e00\u70b9\u53e3\u98ce\uff0c\u5f20\u606a\u4e0d\u62c5\u5fc3\u5176\u4ed6\u7684\uff0c\u4ed6\u4eec\u8981\u6709\u80fd\u8010\u5c06\u8d75\u6d4e\u4e1c\u603b\u7406\u8bf7\u8fc7\u53bb\u7ed9\u4ed6\u4eec\u4e3b\u6301\u5a5a\u793c\uff0c\u5f20\u606a\u90fd\u4e0d\u4f1a\u5927\u60ca\u5c0f\u602a\u7684\uff0c\u4f46\u662f\u4ed6\u62c5\u5fc3\u4ed6\u4eec\u5c06\u8bb8\u601d\u3001\u665a\u6674\u3001\u7fdf\u4e39\u9752\u8fd8\u6709\u9648\u9759\u90fd\u9080\u8bf7\u8fc7\u53bb\uff0c\u8fd9\u573a\u5408\u5bf9\u4ed6\u6765\u8bf4\u5c31\u591f\u58ee\u89c2\u7684\uff0c\u90fd\u591f\u79f0\u5f97\u4e0a\u58ee\u70c8\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u671f\u5f85\u6709\u8c01\u80fd\u7ed9\u9080\u8bf7\u554a\uff1f\u201d\u5510\u5a67\u5012\u4f3c\u770b\u7a7f\u5f20\u606a\u7684\u5fc3\u601d\uff0c\u7b11\u7740\u95ee\u4ed6\uff0c\u5634\u5374\u8d34\u7740\u4ed6\u7684\u8033\u6735\u8fb9\uff0c\u5fae\u5fae\u5f20\u7740\u3002</p><p>\u3000\u3000\u5f20\u606a\u6c57\u6bdb\u90fd\u7ad6\u4e86\u8d77\u6765\uff0c\u5c31\u6015\u4e00\u4e2a\u95ee\u9898\u4e0d\u5bf9\uff0c\u8033\u6735\u5c31\u8981\u7ed9\u8fd9\u59ae\u5b50\u54ac\u4e00\u53e3\uff0c\u60f3\u6765\u59bb\u59be\u548c\u8c10\u7684\u65e5\u5b50\u662f\u6c38\u8fdc\u90fd\u4e0d\u8981\u6307\u671b\u4e86\u3002\u8fd9\u65f6\u5019\u6401\u5728\u8336\u51e0\u4e0a\u7684\u624b\u673a\u8367\u7eff\u8272\u5c0f\u706f\u95ea\u4e86\u4e24\u4e0b\uff0c\u770b\u7740\u6709\u7535\u8bdd\u8fdb\u6765\uff0c\u5f20\u606a\u5fc3\u91cc\u5927\u547c\u6551\u5175\uff0c\u6ca1\u6709\u7b49\u624b\u673a\u94c3\u58f0\u54cd\u8d77\u6765\uff0c\u5c31\u5c06\u624b\u673a\u62ff\u5728\u624b\u91cc\uff0c\u89c1\u662f\u53f6\u5c0f\u6850\u7684\u7535\u8bdd\uff0c\u8ddf\u5510\u5a67\u8bf4\u9053\uff1a\u201c\u5e94\u8be5\u63a5\u5230\u53f6\u8001\u7237\u5b50\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e3a\u4e86\u8868\u793a\u5bf9\u53f6\u5efa\u658c\u201c\u5fd8\u6069\u8d1f\u4e49\u201d\u7684\u201c\u6124\u6168\u201d\u5e76\u8003\u8651\u5230\u4e01\u6587\u6021\u4e01\u5bb6\u7684\u611f\u53d7\uff0c\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u7684\u5a5a\u793c\uff0c\u53f6\u5bb6\u4e5f\u5c31\u53f6\u8001\u7237\u5b50\u53f6\u7956\u8303\u8ddf\u53f6\u5efa\u658c\u7684\u7236\u6bcd\u4ee5\u53ca\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u592b\u5987\u53c2\u52a0\uff0c\u53f6\u5c0f\u6850\u56e0\u5546\u52a1\u6d3b\u52a8\u63d0\u524d\u968f\u5f20\u606a\u4ed6\u4eec\u4e00\u8d77\u5230\u9999\u6e2f\uff0c\u53f6\u8001\u7237\u5b50\u8ddf\u53f6\u5efa\u658c\u7684\u7236\u6bcd\u4eca\u5929\u624d\u5750\u98de\u673a\u5230\u9999\u6e2f\u6765\u3002</p><p>\u3000\u3000\u4eca\u5929\u63a5\u673a\u4e5f\u662f\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u8ddf\u53f6\u5efa\u658c\u3001\u5b59\u9759\u9999\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u5c31\u5728\u4f4f\u5904\u7b49\u7740\u6d88\u606f\u665a\u4e0a\u4e00\u8d77\u5403\u996d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ee5\u4e3a\u53f6\u5c0f\u6850\u5728\u673a\u573a\u7ed9\u4ed6\u6253\u7535\u8bdd\uff0c\u521a\u63a5\u901a\u7535\u8bdd\uff0c\u5c31\u542c\u89c1\u697c\u4e0b\u6709\u6c7d\u8f66\u7684\u58f0\u97f3\uff0c\u7ad9\u8d77\u6765\u8d70\u5230\u843d\u5730\u7a97\u524d\uff0c\u5c31\u770b\u89c1\u53bb\u63a5\u673a\u7684\u4e24\u90e8\u8f7f\u8f66\u6b63\u9a76\u8fdb\u9662\u5b50\u91cc\u6765\uff0c\u539f\u6765\u4eba\u5df2\u7ecf\u63a5\u8fc7\u6765\u53c2\u89c2\u65b0\u623f\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u987e\u4e0d\u4e0a\u8ddf\u53f6\u5c0f\u6850\u5728\u7535\u8bdd\u5570\u55e6\u4ec0\u4e48\uff0c\u8ddf\u4e09\u4e2a\u5973\u5b69\u5b50\u8bf4\uff1a\u201c\u53f6\u8001\u7237\u5b50\u4ed6\u4eec\u76f4\u63a5\u4ece\u673a\u573a\u6765\u53c2\u89c2\u65b0\u623f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5b59\u9759\u6aac\u53ea\u662f\u968f\u610f\u7684\u7a7f\u7740\u4e00\u4ef6\u957fT\u6064\uff0c\u7ad9\u8d77\u6765\u521a\u521a\u906e\u4f4f\u5927\u817f\uff0c\u8ddf\u8d85\u77ed\u88d9\u4f3c\u7684\uff0c\u5728\u9999\u6e2f\u8fd9\u79cd\u7a7f\u7740\u901b\u8857\u90fd\u6ca1\u6709\u5173\u7cfb\uff0c\u53ea\u662f\u53bb\u89c1\u53f6\u8001\u7237\u5b50\u4ee5\u53ca\u53f6\u5efa\u658c\u7684\u7236\u6bcd\u672a\u514d\u592a\u8f7b\u4f7b\uff0c\u5979\u773c\u7738\u5b50\u4e71\u8f6c\u7740\u770b\u5411\u5510\u5a67\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u5728\u5357\u533a\u6d45\u6c34\u6e7e\u7ec8\u4e8e\u6709\u4e86\u81ea\u5df1\u7684\u4f4f\u5904\uff0c\u867d\u8bf4\u9999\u6e2f\u697c\u5e02\u5728\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u4e4b\u540e\u4e00\u76f4\u6ca1\u6709\u590d\u82cf\uff0c\u6d77\u666f\u8c6a\u5b85\u4ef7\u683c\u8dcc\u53bb40\uff05\uff0c\u8fd9\u680b\u4f4d\u4e8e\u6d45\u6c34\u6e7e\u897f\u9762\u5c71\u4e0a\u5360\u5730\u4e24\u82f1\u4ea9\u5927\u5c0f\u7684\u72ec\u680b\u9662\u5b50\u552e\u503c\u8fd8\u662f\u9ad8\u8fbe\u4e24\u4ebf\u6e2f\u5143\uff0c\u4e0d\u8fc7\u53f6\u5efa\u658c\u6ca1\u6709\u4e3a\u6b64\u638f\u4e00\u5206\u94b1\uff0c\u5b59\u5c1a\u4e49\u65e9\u5c31\u76f8\u4e2d\u8fd9\u91cc\uff0c\u5b59\u9759\u9999\u4e0e\u53f6\u5efa\u658c\u56de\u9999\u6e2f\u544a\u4e4b\u5a5a\u8baf\uff0c\u4ed6\u5f53\u5373\u5c31\u5c06\u6b64\u5904\u4e70\u4e0b\u6765\u5f53\u4f5c\u5a5a\u623f\u8d60\u9001\u7ed9\u65b0\u4eba\u3002</p><p>\u3000\u3000\u8c6a\u5b85\u5927\u9662\u9664\u4e86\u4e00\u5ea7\u5e95\u5ea7\u4e24\u5c42\u4e0a\u9762\u4e09\u5c42\u5e26\u843d\u5730\u5927\u7a97\u7684\u4e3b\u697c\u522b\u5885\u5916\uff0c\u4e34\u5c71\u5d16\u8fd8\u5efa\u6709\u4e00\u5ea7\u4e09\u9762\u73af\u7a97\u7684\u4e24\u5c42\u5c0f\u697c\u4f5c\u4e3a\u5ba2\u623f\u4f7f\u7528\u3002\u5f20\u606a\u8fd9\u6b21\u5230\u9999\u6e2f\u6765\uff0c\u6ca1\u6709\u4f4f\u9152\u5e97\uff0c\u5c31\u4f4f\u5728\u4e24\u5c42\u5c0f\u697c\u91cc\uff0c\u80cc\u5c71\u4e34\u6d77\uff0c\u8fd8\u53ef\u4ee5\u5c45\u9ad8\u4e34\u4e0b\u6b23\u8d4f\u6d45\u6c34\u6e7e\u6700\u70ed\u95f9\u7684\u6c99\u6ee9\uff0c\u53ea\u53ef\u60dc\u5c71\u7565\u9ad8\u4e00\u4e9b\uff0c\u6c99\u6ee9\u4e0a\u7684\u4eba\u5c0f\u5f97\u50cf\u8682\u8681\uff0c\u8981\u4e0d\u662f\u5728\u9999\u6e2f\u7684\u8fd9\u51e0\u5929\u6709\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u5b59\u9759\u6aac\u8fd9\u4e09\u53ea\u5c0f\u5996\u7cbe\u966a\u7740\uff0c\u5f20\u606a\u4e00\u5b9a\u4f1a\u63a7\u5236\u4e0d\u4f4f\u8ba9\u5085\u4fca\u4e0d\u60dc\u4e00\u5207\u4ee3\u4ef7\u641e\u4e00\u53ea\u9ad8\u500d\u7387\u5e26\u591c\u89c6\u529f\u80fd\u7684\u5929\u6587\u671b\u8fdc\u955c\u56de\u6765\u6401\u5728\u9601\u697c\u4e0b\u7684\u5929\u53f0\u4e0a\u3002</p><p>\u3000\u3000\u5510\u5a67\u3001\u9648\u5983\u84c9\u665a\u4e0a\u90fd\u7761\u8fd9\u91cc\uff0c\u5b59\u9759\u6aac\u767d\u5929\u5728\u8fd9\u91cc\u73a9\uff0c\u665a\u4e0a\u8fd8\u662f\u56de\u5bb6\u91cc\u53bb\uff0c\u867d\u8bf4\u5b59\u5b85\u79bb\u8fd9\u91cc\u4e5f\u4e0d\u8fdc\uff0c\u5f00\u8f66\u5c31\u4e09\u4e94\u5206\u949f\u7684\u4e8b\u60c5\uff0c\u53ea\u662f\u5ba2\u4eba\u90fd\u5230\u4e86\uff0c\u5979\u603b\u4e0d\u80fd\u5f00\u8f66\u56de\u53bb\u6362\u8eab\u8863\u670d\u518d\u6765\u3002</p><p>\u3000\u3000\u5510\u5a67\u5012\u662f\u60f3\u72e0\u72e0\u5fc3\u8f6c\u8fc7\u5934\u53bb\uff0c\u7ec8\u7a76\u654c\u4e0d\u8fc7\u5b59\u9759\u6aac\u88c5\u65e0\u8f9c\u7684\u773c\u795e\uff0c\u5fae\u62bf\u7740\u5634\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u5983\u84c9\u521a\u4e70\u4e86\u51e0\u5957\u8863\u670d\uff0c\u4e0d\u77e5\u9053\u4f60\u5408\u4e0d\u5408\u9002\u7a7f\uff0c\u4f60\u6311\u4e00\u4ef6\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u77e5\u9053\u5c0f\u5a67\u4f60\u5fc3\u80a0\u6700\u597d\u4e86\u3002\u201d\u5b59\u9759\u6aac\u8ba8\u597d\u7684\u8bf4\u9053\uff0c\u62ff\u8d77\u7eb8\u63d0\u888b\uff0c\u62ff\u8d77\u4e00\u4ef6\u7fe0\u7eff\u8272\u788e\u82b1\u88d9\u5b50\u5c31\u5306\u5fd9\u8d70\u8fdb\u6f31\u6d17\u95f4\u91cc\u53bb\u6362\uff0c\u6f31\u6d17\u95f4\u4e0e\u8d77\u5c45\u5ba4\u7684\u9694\u65ad\u662f\u534a\u900f\u660e\u7684\u78e8\u7802\u73bb\u7483\u5899\uff0c\u5b59\u9759\u6aac\u8fdb\u53bb\u5f00\u4e86\u706f\uff0c\u5c06\u5979\u8131\u8863\u670d\u7684\u5999\u66fc\u8eab\u59ff\u50cf\u526a\u7eb8\u753b\u4f3c\u7684\u6620\u5728\u73bb\u7483\u5899\uff0c\u5f20\u606a\u5fc3\u60f3\u518d\u51fa\u8272\u7684\u753b\u5bb6\u90fd\u672a\u5fc5\u80fd\u753b\u51fa\u5982\u6b64\u7f8e\u5999\u7684\u66f2\u7ebf\u6765\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8981\u6253\u5f00\u95e8\u770b\u4e24\u773c\uff1f\u201d\u5510\u5a67\u53c8\u4ece\u540e\u9762\u7f20\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u624b\u629a\u7740\u4ed6\u7684\u4e0b\u5df4\uff0c\u54ac\u7259\u5207\u9f7f\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u770b\u4ec0\u4e48\uff1f\u4f60\u8bf4\u6253\u5f00\u95e8\u5230\u9633\u53f0\u770b\u6c99\u6ee9\u4e0a\u7684\u6cf3\u88c5\u7f8e\u5973\u5417\uff1f\u4f60\u4e0d\u662f\u8ddf\u5983\u84c9\u7b54\u5e94\u8ddf\u6211\u591c\u91cc\u4e00\u8d77\u53bb\u6e38\u6cf3\u5417\uff0c\u6211\u9700\u8981\u770b\u8fd9\u4e9b\u5eb8\u59ff\u4fd7\u7c89\uff1f\u201d\u5f20\u606a\u773c\u795e\u7a7a\u4e86\u4e00\u4e0b\u5c31\u8f6c\u5230\u9633\u53f0\u73bb\u7483\u95e8\u4e0a\uff0c\u4e49\u6b63\u8f9e\u4e25\u7684\u6447\u4e86\u8d77\u6765\uff0c\u63a5\u7740\u773c\u775b\u53c8\u8f6c\u5230\u5983\u84c9\u5077\u7b11\u7684\u8138\u4e0a\uff0c\u80cc\u7740\u5510\u5a67\u671d\u9648\u5983\u84c9\u77aa\u4e86\u77aa\u773c\u775b\uff0c\u4e0d\u8ba9\u5979\u5e78\u707e\u4e50\u7978\u7684\u7b11\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u6211\u7a7f\u6b63\u597d\u5462\uff0c\u4f60\u4eec\u7684\u773c\u5149\u771f\u597d\uff0c\u6211\u600e\u4e48\u5c31\u9009\u4e0d\u4e2d\u5408\u610f\u7684\u8863\u670d\uff0c\u53ea\u80fd\u80e1\u4e71\u7684\u7a7f\u8863\u670d\uff0c\u4ee5\u540e\u8981\u62c9\u4f60\u4eec\u4e00\u8d77\u901b\u8857\u53bb\u3002\u201d\u5b59\u9759\u6aac\u8fb9\u63a8\u95e8\u8d70\u51fa\u6765\u8fb9\u5c06\u8eab\u4e0a\u7684\u88d9\u5b50\u626f\u987a\uff0c\u53ea\u662f\u624b\u5fd9\u811a\u4e71\u6ca1\u6709\u6ce8\u610f\u5230\u5546\u6807\u540a\u724c\u6302\u5728\u8116\u5b50\u540e\u9762\uff0c\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u5047\u88c5\u770b\u4e0d\u89c1\uff0c\u5f20\u606a\u4e5f\u53ea\u80fd\u5047\u88c5\u770b\u4e0d\u5230\uff0c\u8fd9\u65f6\u5019\u90fd\u542c\u89c1\u53f6\u7956\u8303\u8ddf\u5b59\u5c1a\u4e49\u5728\u9662\u5b50\u91cc\u7684\u8bf4\u8bdd\u58f0\uff0c\u5f20\u606a\u4fbf\u50ac\u4fc3\u4e09\u4e2a\u5973\u5b69\u5b50\u4e0b\u697c\u53bb\uff0c\u8d81\u7740\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u4e0d\u6ce8\u610f\uff0c\u8d70\u5728\u540e\u9762\u4e0b\u697c\u68af\u65f6\u5c06\u5546\u6807\u540a\u724c\u585e\u5230\u5b59\u9759\u6aac\u8116\u5b50\u91cc\u53bb\uff0c\u5b59\u9759\u6aac\u8fd9\u624d\u6ce8\u610f\u5230\u6ca1\u6709\u5c06\u5546\u6807\u526a\u4e0b\u6765\uff0c\u4e5f\u6ca1\u6709\u5f80\u522b\u5904\u60f3\u3002\u8d70\u51fa\u697c\u68af\u65f6\uff0c\u5f20\u606a\u7ed9\u5510\u5a67\u6402\u7740\u80f3\u818a\u53bb\u7ed9\u201c\u65e0\u610f\u201d\u8e29\u4e86\u4e00\u811a\uff0c\u5fcd\u75db\u54a7\u5634\u7b11\u7740\u671d\u53f6\u8001\u7237\u5b50\u4ed6\u4eec\u8d70\u8fc7\u53bb\uff1a\u201c\u5750\u8fd9\u4e48\u957f\u65f6\u95f4\u7684\u98de\u673a\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u8981\u5148\u56de\u9152\u5e97\u4f11\u606f\u4e00\u4e0b\u5462\u2026\u2026\u201d\u5fc3\u91cc\u5374\u89c9\u5f97\u5947\u602a\uff1a\u5c0f\u59ae\u5b50\u6628\u5929\u8fd8\u8ddf\u9759\u6aac\u4eb2\u70ed\u8ddf\u59d0\u59b9\u4f3c\u7684\uff0c\u600e\u4e48\u4eca\u5929\u5c31\u9488\u5bf9\u8d77\u5979\u6765\u4e86\uff1f\u4e0b\u5348\u901b\u8857\u8fd8\u5c06\u5b59\u9759\u6aac\u4e22\u4e0b\u6765\uff0c\u5c31\u62c9\u9648\u5983\u84c9\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u522b\u770b\u6211\u516b\u5341\u516d\u5c81\u4e86\uff0c\u6211\u53ef\u6ca1\u6709\u4f60\u60f3\u8c61\u7684\u90a3\u4e48\u4e0d\u4e2d\u7528\u2026\u2026\u201d\u53f6\u7956\u8303\u8170\u677f\u633a\u76f4\u8d77\u6765\u3001\u58f0\u97f3\u723d\u6717\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4ecb\u7ecd\u4ed6\u7236\u6bcd\u7ed9\u5f20\u606a\u8ba4\u8bc6\uff0c\u4e5d\u4e94\u5e74\u65f6\u53f6\u5e86\u6c11\u7ed9\u8c03\u5230\u6c88\u9633\u519b\u533a\u53bb\u4e86\uff0c\u73b0\u5728\u62c5\u4efb\u519b\u533a\u526f\u53c2\u8c0b\u957f\uff0c\u4e00\u76f4\u90fd\u6ca1\u6709\u673a\u4f1a\u8ddf\u5f20\u606a\u89c1\u4e0a\u9762\uff0c\u4e3b\u52a8\u63e1\u4f4f\u5f20\u606a\u7684\u624b\u7b11\u7740\u8bf4\uff1a\u201c\u4ece\u4e5d\u4e94\u5e74\u65f6\uff0c\u5c31\u542c\u8bf4\u4e86\u4f60\uff0c\u4e00\u6643\u773c\u4e94\u5e74\u65f6\u95f4\u8fc7\u53bb\u4e86\uff0c\u771f\u771f\u5207\u5207\u7684\u611f\u89c9\u5230\u4ec0\u4e48\u53eb\u4e16\u754c\u662f\u5c5e\u4e8e\u4f60\u4eec\u7684\uff0c\u505a\u51fa\u8fd9\u4e9b\u6210\u5c31\u771f\u662f\u4e0d\u7b80\u5355\u554a\uff0c\u6bd4\u6211\u8fd9\u6837\u5728\u90e8\u961f\u6df7\u4e86\u4e00\u8f88\u5b50\u7684\u4eba\u6709\u51fa\u606f\u591a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53f6\u4f2f\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6211\u8981\u8eb2\u56de\u623f\u91cc\u53bb\u4e86\u3002\u201d\u5728\u53f6\u5e86\u6c11\u9762\u524d\uff0c\u5f20\u606a\u603b\u8981\u88c5\u6210\u865a\u5fc3\u5c0f\u7ffc\u7684\u665a\u8f88\u5b50\u6837\u5b50\uff0c\u8ddf\u7740\u5927\u5bb6\u4e00\u8d77\u53c2\u89c2\u65b0\u623f\u3002</p><p>\u3000\u3000\u5f20\u606a\u60e6\u8bb0\u7740\u5510\u5a67\u9488\u5bf9\u9759\u6aac\u7684\u4e8b\uff0c\u770b\u9648\u5983\u84c9\u7684\u795e\u8272\u4f3c\u4e4e\u77e5\u9053\u4e9b\u4ec0\u4e48\uff0c\u53ea\u662f\u9648\u5983\u84c9\u5728\u9999\u6e2f\u6574\u5929\u8ddf\u5510\u5a67\u7c98\u5728\u4e00\u8d77\uff0c\u5b8c\u5168\u6ca1\u6709\u505a\u4ed6\u52a9\u7406\u7684\u81ea\u89c9\uff0c\u751a\u81f3\u4e24\u4e2a\u4eba\u8fd8\u5546\u8bae\u9648\u5983\u84c9\u5230\u9526\u6e56\u5546\u4e8b\u7684\u9999\u6e2f\u603b\u90e8\u5de5\u4f5c\u4e00\u5e74\u79ef\u7d2f\u4e9b\u5de5\u4f5c\u7ecf\u9a8c\u518d\u53bb\u5efa\u90ba\u7684\u884c\u653f\u5ba4\u5de5\u4f5c\uff0c\u8bf4\u767d\u4e86\u5c31\u662f\u60f3\u5728\u9999\u6e2f\u966a\u5510\u5a67\u4e00\u5e74\u2014\u2014\u5510\u5a67\u8fd8\u8981\u8fc7\u4e00\u5e74\u624d\u6bd5\u4e1a\u2014\u2014\u5f20\u606a\u60f3\u7740\u4eca\u5929\u90fd\u672a\u5fc5\u6709\u673a\u4f1a\u8ddf\u9648\u5983\u84c9\u5355\u72ec\u8bf4\u4e0a\u8bdd\uff0c\u53c2\u52a0\u5a5a\u623f\u65f6\uff0c\u5510\u5a67\u5174\u51b2\u51b2\u7684\u53bb\u53c2\u89c2\u5a74\u513f\u623f\u4e86\uff0c\u6070\u597d\u6709\u4e00\u901a\u627e\u5f20\u606a\u7684\u7535\u8bdd\u6253\u5230\u9648\u5983\u84c9\u7684\u624b\u673a\u4e0a\u3002\u5f20\u606a\u8d70\u5230\u7a97\u6237\u8fb9\u63a5\u542c\u7535\u8bdd\uff0c\u901a\u8fc7\u7535\u8bdd\u5c31\u7ad9\u5728\u7a97\u6237\u8fb9\u8981\u5c06\u624b\u673a\u9012\u8fd8\u7ed9\u9648\u5983\u84c9\uff0c\u7b49\u5979\u8d70\u8fc7\u6765\uff0c\u671d\u5a74\u623f\u91cc\u6487\u6487\u5634\uff0c\u5c0f\u58f0\u95ee\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u5ae3\u7136\u4e00\u7b11\uff0c\u5374\u53c8\u5fc3\u865a\u7684\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u89c6\u7ebf\u7adf\u7136\u77a5\u5411\u5b59\u5c1a\u4e49\u592b\u5987\u4e24\u4eba\u7684\u80cc\u666f\uff0c\u5373\u4f7f\u538b\u7740\u55d3\u5b50\u58f0\u97f3\u4e5f\u4f1a\u67d4\u7684\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u4e2d\u5348\u5403\u8fc7\u996d\uff0c\u6211\u8ddf\u5c0f\u5a67\u56de\u5904\u627e\u6c34\u679c\u5200\uff0c\u542c\u7740\u5b59\u603b\u8ddf\u845b\u59e8\u5728\u8fc7\u9053\u91cc\u8bf4\u8bdd\uff0c\u4f60\u77e5\u9053\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u80fd\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5b59\u603b\u7684\u8bed\u6c14\u5f88\u611f\u6168\uff0c\u201d\u9648\u5983\u84c9\u8bf4\u5b59\u5c1a\u4e49\u7684\u53e3\u6c14\uff0c\u53ea\u662f\u4e00\u70b9\u90fd\u4e0d\u50cf\uff0c\u201c\u2018\u8001\u5927\u7684\u4e8b\u603b\u7b97\u662f\u8ba9\u4eba\u7701\u5fc3\u4e86\u2026\u2026\u2019\u8fd9\u5927\u6982\u662f\u5728\u8bf4\u5b59\u59d0\u8ddf\u53f6\u54e5\u7684\u4e8b\uff0c\u845b\u59e8\u9876\u4e86\u4ed6\u4e00\u53e5\uff0c\u4f60\u731c\u600e\u4e48\u8bf4\u7684\uff1f\u201d\u9648\u5983\u84c9\u5b66\u845b\u660e\u73cf\u7684\u53e3\u6c14\u5012\u662f\u5f88\u50cf\uff0c\u201c\u2018\u5c0f\u7684\u4e5f\u662f\u8d54\u94b1\u8d27\uff0c\u6211\u7238\u6d3b\u7740\u7684\u65f6\u5019\u8bf4\u4e86\uff0c\u5c31\u5c0f\u7684\u8fd9\u81ed\u813e\u6c14\uff0c\u94c1\u786c\u662f\u8d54\u94b1\u5012\u8d34\u7ed9\u4eba\u5bb6\u53bb\u505a\u5c0f\u7684\uff0c\u4f60\u770b\u770b\u2026\u2026\u2019\u201d\u9648\u5983\u84c9\u53c8\u4fcf\u76ae\u7684\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u4e0d\u77e5\u9053\u845b\u59e8\u8bf4\u7684\u5c0f\u7684\u662f\u8c01\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u7a97\u6237\u5f00\u7740\uff0c\u98ce\u5439\u8fdb\u6765\uff0c\u5f20\u606a\u611f\u89c9\u80cc\u810a\u8fd9\u6c57\u6c34\u90fd\u5feb\u6dcc\u4e0b\u6765\u4e86\u3002</p></article>"}}}]);
