(window.webpackJsonp=window.webpackJsonp||[]).push([[1183],{1206:function(p){p.exports={chapterTitle:null,title:"\u7b2c974\u7ae0 \u591c\u884c\u8f66\u4e4b\u7231",content:"<article><h2>\u7b2c974\u7ae0 \u591c\u884c\u8f66\u4e4b\u7231</h2><p>\u3000\u3000\u8fdc\u79bb\u57ce\u5e02\u7480\u74a8\u7684\u706f\u5149\uff0c\u5217\u8f66\u5728\u591c\u8272\u8986\u76d6\u4e0b\u7684\u5927\u5730\u7a7f\u884c\uff0c\u9664\u4e86\u5217\u8f66\u884c\u9a76\u7684\u58f0\u97f3\uff0c\u671b\u7740\u7a97\u5916\u98d8\u7740\u7684\u96e8\u4e1d\uff0c\u5374\u6709\u5bc2\u5bde\u7684\u611f\u89c9\u3002\u5f20\u606a\u8fd8\u662f\u559c\u6b22\u8fd9\u79cd\u51ac\u591c\u65e0\u58f0\u7684\u7ec6\u96e8\uff0c\u770b\u4e86\u4e00\u4f1a\u513f\u968f\u8eab\u5e26\u7740\u7684\u8d44\u6599\uff0c\u53c8\u53bb\u770b\u8f66\u7a97\u5916\u7684\u591c\u8272\uff0c\u770b\u7740\u96e8\u4e1d\u5728\u7a97\u73bb\u7483\u51dd\u6210\u6c34\u73e0\u6ed1\u843d\u4ee5\u53ca\u7a97\u73bb\u7483\u4e0a\u5012\u6620\u7740\u7684\u5510\u5a67\u4e0e\u674e\u99a8\u4e88\u4e24\u4eba\u5a07\u5a9a\u7684\u8138\u86cb\uff0c\u5fc3\u60f3\u8fd9\u6837\u7684\u591c\u884c\u8f66\u4e0e\u8ba1\u5212\u4e2d\u6709\u4e9b\u5dee\u8ddd\uff0c\u611f\u89c9\u4e5f\u662f\u4e0d\u9519\u3002</p><p>\u3000\u3000\u5f20\u606a\u539f\u5148\u6253\u7b97\u7740\u4e0e\u5510\u5a67\u5728\u8f6f\u5367\u5305\u53a2\u91cc\u6765\u4e2a\u201c\u591c\u884c\u8f66\u4e4b\u7231\u201d\u7684\uff0c\u8fd9\u4f1a\u513f\u674e\u99a8\u4e88\u5728\u4ed6\u4eec\u8f66\u53a2\u91cc\uff0c\u5c31\u4e0d\u80fd\u9a6c\u4e0a\u5b9e\u65bd\u8fd9\u4e2a\u8ba1\u5212\u4e86\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u7a7f\u4e00\u4ef6\u9ed1\u8272\u7684\u4f4e\u9886\u8584\u7ed2\u886b\uff0c\u98a4\u5dcd\u5dcd\u7684\u4e73\u5cf0\u5c06\u8584\u7ed2\u575a\u5b9e\u7684\u9876\u8d77\uff0c\u9886\u53e3\u9732\u51fa\u96ea\u767d\u7c89\u5ae9\u7684\u4fee\u957f\u9888\u8116\u5b50\uff0c\u5706\u6da6\u7a0d\u5c16\u7684\u4e0b\u9894\uff0c\u8bf4\u5b8c\u8bdd\u4f1a\u4e0b\u610f\u8bc6\u5fae\u62bf\u4e00\u4e0b\u7684\u5ae3\u7ea2\u5634\u5507\uff0c\u79c0\u76f4\u7684\u9f3b\u6881\uff0c\u7738\u5b50\u6e05\u6f88\u800c\u8ff7\u4eba\uff0c\u6709\u7740\u6210\u719f\u4e30\u8174\u7684\u9b45\u529b\u4e0e\u97f5\u5473\u3002\u5510\u5a67\u7a7f\u7740\u6de1\u9752\u8272\u7684\u7ed2\u7ebf\u8863\uff0c\u80f8\u90e8\u575a\u5b9e\u5c0f\u5de7\uff0c\u5728\u540c\u9f84\u4eba\u4e2d\uff0c\u53d1\u80b2\u4e5f\u7b97\u826f\u597d\uff0c\u53ea\u662f\u4e0e\u674e\u99a8\u4e88\u5e76\u80a9\u5750\u5728\u4e00\u8d77\u8bf4\u8bdd\uff0c\u5c31\u6709\u4e9b\u4e0d\u5927\u663e\u773c\u4e86\uff0c\u957f\u4e14\u5927\u7684\u7738\u5b50\u91cc\u6709\u7740\u7279\u6709\u7684\u5a07\u5a9a\u4e4b\u6c14\uff0c\u624b\u6258\u4e0b\u7684\u8138\u816e\uff0c\u4e13\u6ce8\u7684\u4e0e\u674e\u99a8\u4e88\u8bf4\u8bdd\uff0c\u5076\u5c14\u56de\u5934\u770b\u5230\u5f20\u606a\u5012\u6620\u5728\u8f66\u7a97\u73bb\u7483\u4e0a\u7684\u773c\u775b\u6b63\u770b\u7740\u5979\u4eec\uff0c\u6175\u61d2\u7684\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u662f\u4e24\u4e2a\u5927\u7f8e\u5973\u5728\u8f66\u53a2\u91cc\u8ba9\u4f60\u5206\u5fc3\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u672c\u6765\u5c31\u6ca1\u6709\u4ec0\u4e48\u5fc5\u987b\u8981\u770b\u5230\u7684\u8d44\u6599\uff0c\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u5750\u5230\u5510\u5a67\u8eab\u8fb9\uff0c\u8eab\u5b50\u7f29\u5230\u5367\u94fa\u91cc\u9762\u9760\u7740\u8f66\u53a2\u58c1\u53c9\u817f\u800c\u5750\uff0c\u5c06\u5510\u5a67\u5a07\u8f6f\u6696\u9999\u7684\u8eab\u4f53\u6402\u5728\u6000\u91cc\uff0c\u8bf4\u9053\uff0c\u201c\u53ea\u662f\u4f60\u4eec\u8c08\u7684\u4e1c\u897f\u592a\u65e0\u804a\uff0c\u5728\u4e24\u4e2a\u7f8e\u5973\u9762\u524d\u6253\u5475\u6b20\u591a\u5c11\u6709\u4e9b\u65e0\u793c\u4e86\uff0c\u8fd8\u4e0d\u5982\u8eb2\u5230\u4e00\u8fb9\u53d1\u6123\u7684\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u5c31\u4e0d\u6253\u5475\u6b20\u4e86\uff1f\u201d\u5510\u5a67\u5934\u5fae\u5fae\u4ef0\u8d77\uff0c\u8eab\u5b50\u8737\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u5934\u62b5\u7740\u4ed6\u7684\u4e0b\u5df4\uff0c\u8ddf\u674e\u99a8\u4e88\u7b11\u7740\u8bf4\uff0c\u201c\u5f53\u9760\u6905\u7684\u611f\u89c9\u5012\u662f\u4e0d\u9519\uff0c\u6211\u4eec\u804a\u6211\u4eec\u7684\uff0c\u4e0d\u8981\u7ba1\u4ed6\u3002\u201d</p><p>\u3000\u3000\u201c\u4e5f\u8981\u4f11\u606f\u4e86\uff0c\u201d\u674e\u99a8\u4e88\u4f38\u624b\u8f7b\u8f7b\u62cd\u4e86\u62cd\u5ae3\u7ea2\u7684\u5634\u5507\uff0c\u6293\u8d77\u8f6f\u5367\u4e0a\u7684\u5916\u5957\u7ad9\u8d77\u6765\uff0c\u201c\u660e\u5929\u65e9\u4e0a\u518d\u89c1\u3002\u201d</p><p>\u3000\u3000\u4ece\u5317\u4eac\u5230\u5efa\u90ba\u7684\u591c\u884c\u8f66\u6ca1\u6709\u53cc\u4eba\u6216\u5355\u4eba\u7684\u8f6f\u5367\u5305\u53a2\uff0c\u53ea\u4e0d\u8fc7\u5f20\u606a\u4e0d\u4f1a\u4ecb\u610f\u5305\u53a2\u91cc\u7684\u53e6\u4e24\u5f20\u8f6f\u94fa\u90fd\u7a7a\u4e0b\u6765\uff0c\u4ed6\u6402\u7740\u5510\u5a67\u8170\u7684\u624b\u7a0d\u7a0d\u7528\u529b\u4e9b\uff0c\u514d\u5f97\u8fd9\u50bb\u4e2b\u5934\u7559\u674e\u99a8\u4e88\u591c\u91cc\u7761\u4ed6\u4eec\u5305\u53a2\u91cc\uff0c\u8ba9\u4ed6\u8ba1\u5212\u4e2d\u7684\u597d\u4e8b\u6ce1\u6c64\u3002</p><p>\u3000\u3000\u5f85\u674e\u99a8\u4e88\u79bb\u5f00\uff0c\u5f20\u606a\u8d76\u7d27\u5c06\u8f66\u95e8\u5173\u4e0a\uff0c\u8f66\u5f88\u5feb\u5728\u5c71\u4e1c\u5883\u5185\u53c8\u505c\u4e86\u4e00\u7ad9\uff0c\u5f85\u5217\u8f66\u9a76\u51fa\u8f66\u7ad9\uff0c\u5f20\u606a\u624d\u8d34\u8eab\u671d\u5510\u5a67\u5750\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5510\u5a67\u7b11\u7740\u4ece\u5367\u94fa\u4e0a\u8df3\u4e0b\u6765\uff0c\u95ea\u5230\u8f66\u7a97\u524d\uff0c\u7f9e\u6da9\u7684\u8bf4\uff1a\u201c\u4f60\u4e2a\u5927\u8272\u72fc\uff0c\u4f60\u4e5f\u4e0d\u770b\u770b\u674e\u99a8\u4e88\u79bb\u5f00\u65f6\u7684\u773c\u795e\uff0c\u5bb3\u4eba\u5bb6\u7ed9\u5979\u53d6\u7b11\uff01\u8fd8\u6709\uff0c\u4f60\u5c31\u5fcd\u5fc3\u8ba9\u8fd9\u4e48\u4e2a\u5927\u7f8e\u5973\u5b64\u96f6\u96f6\u7684\u5355\u72ec\u7761\u81ea\u5df1\u7684\u5305\u53a2\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u6bd4\u8d77\u81ea\u5df1\u7684\u597d\u4e8b\u6765\uff0c\u591a\u4f59\u7684\u601c\u9999\u60dc\u7389\u771f\u662f\u8981\u4e0d\u5f97\u3002\u201d\u5f20\u606a\u8d34\u5230\u5510\u5a67\u7684\u8eab\u540e\uff0c\u63e1\u4f4f\u5979\u7684\u624b\u4e00\u8d77\u671b\u5411\u7a97\u5916\uff0c\u7ec6\u8f6f\u5ae9\u6ed1\u7684\u4e00\u53cc\u624b\uff0c\u8ba9\u4eba\u5fc3\u91cc\u611f\u5230\u683c\u5916\u7684\u6e29\u6696\u3002\u5510\u5a67\u6e05\u695a\u5f20\u606a\u7684\u610f\u56fe\uff0c\u53ea\u662f\u5728\u591c\u884c\u8f66\u7684\u5305\u53a2\u91cc\uff0c\u4f3c\u4e4e\u6709\u4e9b\u8d85\u4e4e\u60f3\u8c61\u4e86\uff0c\u62c5\u5fc3\u5217\u8f66\u5458\u4f1a\u95ef\u8fdb\u6765\uff0c\u4e5f\u62c5\u5fc3\u5f04\u51fa\u4ec0\u4e48\u58f0\u97f3\u7ed9\u9694\u58c1\u8f66\u53a2\u7684\u4eba\u542c\u89c1\uff0c\u771f\u662f\u4e22\u8138\u4e22\u6b7b\u4e86\uff0c\u54c0\u601c\u7684\u770b\u7740\u5f20\u606a\uff0c\u5fc3\u60f3\u771f\u4e0d\u5e94\u8be5\u8ba9\u674e\u99a8\u4e88\u79bb\u5f00\u3002</p><p>\u3000\u3000\u5f20\u606a\u62cd\u8d77\u5510\u5a67\u5a07\u7f8e\u7684\u8138\uff0c\u8f7b\u8f7b\u7684\u543b\u4e0b\u6765\uff0c\u542e\u5438\u7740\u5979\u9999\u6ed1\u8f6f\u5ae9\u7684\u5c0f\u820c\uff0c\u5f85\u5979\u52a8\u60c5\u7684\u6402\u4e0a\u81ea\u5df1\u7684\u8116\u5b50\uff0c\u624b\u653e\u4e0b\u6765\u89e3\u5f00\u5979\u7684\u8170\u5e26\uff0c\u4f38\u8fdb\u725b\u4ed4\u88e4\u91cc\uff0c\u5728\u5979\u5706\u6ed1\u575a\u5b9e\u7684\u5c0f\u81c0\u4e0a\u629a\u6478\u7740\uff0c\u7ef5\u7ef5\u7684\uff0c\u6ed1\u6ed1\u7684\uff0c\u50cf\u4e00\u5757\u6e29\u70ed\u7684\u6696\u7389\uff0c\u624b\u6307\u5c16\u6ed1\u8fc7\u5c3e\u690e\u9aa8\uff0c\u8fd8\u611f\u89c9\u6000\u91cc\u654f\u611f\u7684\u8eab\u4f53\u5728\u8f7b\u8f7b\u4e00\u98a4\u3002\u624b\u518d\u5f80\u5185\u88e4\u91cc\u4f38\uff0c\u78b0\u5230\u83ca\u82b1\u74e3\u611f\u89c9\u90a3\u91cc\u8f7b\u8f7b\u7684\u4e00\u7f29\uff0c\u6307\u5c16\u518d\u5f80\u524d\u63a2\uff0c\u6ed1\u817b\u817b\u7684\u5df2\u7ecf\u6e7f\u5f00\u4e86\u3002</p><p>\u3000\u3000\u8d8a\u662f\u5728\u5bb9\u6613\u7ed9\u53d1\u89c9\u7684\u5730\u65b9\uff0c\u8d8a\u662f\u52a8\u60c5\u5f97\u5389\u5bb3\uff0c\u5f20\u606a\u60f3\u7740\u5510\u5a67\u5728\u4ed6\u5bb6\u505a\u7231\u4e5f\u662f\u989d\u5916\u7684\u654f\u611f\uff0c\u542c\u7740\u5979\u5728\u8f7b\u8f7b\u7684\u5598\u7740\u6c14\u8eab\u4f53\u5374\u626d\u7740\u60f3\u8981\u95ea\u8eb2\u5f00\uff0c\u4ed6\u54ea\u91cc\u4f1a\u8ba9\u8fd9\u6761\u7f8e\u4eba\u9c7c\u6e38\u8d70\uff0c\u53cc\u624b\u6258\u8d77\u5979\u7684\u81c0\u62b1\u8d77\u6765\u5f80\u5367\u94fa\u8fb9\u79fb\uff0c\u5510\u5a67\u77e5\u9053\u6323\u624e\u4e5f\u6ca1\u6709\u7528\uff0c\u5728\u5f20\u606a\u8033\u7554\u8f7b\u58f0\u7684\u54c0\u6c42\uff1a\u201c\u5927\u8272\u72fc\uff0c\u5c06\u706f\u5173\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u51dd\u671b\u7740\u5510\u5a67\u6f6e\u7ea2\u60c5\u52a8\u7684\u8138\u5e9e\uff0c\u53c8\u957f\u53c8\u5a9a\u7684\u7738\u5b50\u8499\u7740\u4e00\u5c42\u8ff7\u79bb\u5a9a\u60d1\u7684\u96fe\u6c14\uff0c\u706f\u5149\u4e0b\u7684\u5979\u6709\u4e00\u79cd\u72ec\u7279\u7684\u3001\u8ba9\u4eba\u770b\u4e86\u5fc3\u53d1\u7d27\u7684\u5a07\u61a8\u5a9a\u6c14\uff0c\u5f20\u606a\u53c8\u54ea\u91cc\u820d\u5f97\u5c06\u706f\u7ed9\u706d\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5230\u5367\u94fa\u4e0a\uff0c\u8ba9\u5510\u5a67\u7ad9\u5230\u81ea\u5df1\u7684\u4e24\u817f\u4e4b\u95f4\uff0c\u5c06\u5979\u7684\u725b\u4ed4\u88e4\u62c9\u4e0b\u6765\uff0c\u624b\u6307\u4f38\u5230\u5979\u4e24\u817f\u4e2d\u95f4\u7684\u79c1\u5904\u6162\u6162\u7684\u78e8\u64e6\uff0c\u5510\u5a67\u7684\u5598\u6c14\u58f0\u59cb\u7ec8\u5f88\u5c0f\uff0c\u5979\u52aa\u529b\u7684\u618b\u7740\uff0c\u4e0d\u53d1\u51fa\u547b\u541f\u58f0\u6765\uff0c\u4e24\u817f\u4e4b\u95f4\u7684\u6c34\u6cfd\u5df2\u8d8a\u6765\u8d8a\u4e30\u76c8\uff0c\u8fd8\u4ece\u6ed1\u817b\u5982\u7389\u7684\u5927\u817f\u4e0a\u6ed1\u6302\u4e0a\u6765\u3002\u5f20\u606a\u7684\u624b\u6307\u8981\u5f80\u91cc\u63d2\uff0c\u5510\u5a67\u8fd8\u662f\u4e0d\u5fd8\u6293\u4f4f\u4ed6\u7684\u624b\u8155\uff0c\u4e5f\u4e0d\u8ba9\u4ed6\u78b0\u540e\u9762\u7684\u83ca\u74e3\uff0c\u53c8\u8f7b\u78e8\u4e86\u597d\u51e0\u5341\u4e0b\uff0c\u5510\u5a67\u8eab\u5b50\u6302\u5728\u5f20\u606a\u7684\u80a9\u4e0a\uff0c\u53cc\u624b\u6402\u7d27\u4ed6\u7684\u5934\uff0c\u624b\u6307\u63d2\u8fdb\u5934\u53d1\u91cc\u7528\u529b\u7684\u6293\u7d27\uff0c\u633a\u7740\u8170\u62fc\u547d\u7684\u5c06\u5c0f\u8179\u5f80\u5f20\u606a\u8eab\u4e0a\u62b5\uff0c\u8270\u96be\u7684\u5fcd\u4e0d\u4f4f\u4e0d\u53d1\u51fa\u5927\u58f0\u7684\u547b\u541f\u6765\u3002</p><p>\u3000\u3000\u5f85\u9ad8\u6f6e\u75c9\u631b\u7a0d\u8fc7\uff0c\u5f20\u606a\u5c06\u5510\u5a67\u53d1\u70eb\u7684\u8eab\u4f53\u653e\u5230\u8f6f\u5367\u4e0a\uff0c\u8fd9\u624d\u5c06\u5979\u7684\u8863\u670d\u8131\u5c3d\uff0c\u5374\u8ba9\u5510\u5a67\u4f38\u624b\u5c06\u8f66\u53a2\u7684\u9876\u706f\u5173\u6389\uff0c\u53ea\u7559\u4e0b\u4e00\u76cf\u58c1\u706f\u8fd8\u4eae\u7740\u3002\u5728\u660f\u6697\u7684\u5149\u7ebf\u91cc\uff0c\u8f7b\u629a\u7740\u5510\u5a67\u53d1\u70eb\u7684\u8138\u988a\uff0c\u5979\u7684\u7738\u5b50\u8ff7\u79bb\u800c\u6e05\u4eae\uff0c\u76ee\u5149\u76f8\u89e6\uff0c\u5a07\u7f9e\u4e0d\u582a\u7684\u5510\u5a67\u4e0b\u610f\u8bc6\u7684\u8981\u8eb2\u95ea\u3002</p><p>\u3000\u3000\u201c\u5927\u8272\u72fc\uff0c\u4f60\u600e\u4e48\u53ef\u4ee5\u8fd9\u6837\u5bf9\u6211\uff1f\u201d\u5510\u5a67\u89c1\u5f20\u606a\u7684\u773c\u775b\u8fd8\u6709\u4e9b\u4fc3\u72ed\u7684\u610f\u5473\uff0c\u5f20\u53e3\u8981\u53bb\u54ac\u4ed6\u3002</p><p>\u3000\u3000\u201c\u4f60\u8981\u6211\u600e\u6837\u5bf9\u4f60\uff1f\u201d\u5f20\u606a\u8146\u8138\u7b11\u7740\uff0c\u4f38\u624b\u8981\u6321\u5979\u54ac\u6765\u7684\u5634\u3002</p><p>\u3000\u3000\u5510\u5a67\u770b\u5230\u5f20\u606a\u7684\u624b\u6307\u4e0a\u8fd8\u95ea\u7740\u871c\u6db2\u7684\u5149\u6cfd\uff0c\u8fd8\u8981\u81ea\u5df1\u5634\u5507\u4e0a\u62b9\uff0c\u5fc3\u91cc\u771f\u662f\u8981\u7f9e\u6b7b\u6389\u4e86\uff0c\u8eb2\u95ea\u4e0d\u8fc7\uff0c\u8eab\u5b50\u7ffb\u4e86\u8fc7\u6765\uff0c\u8db4\u5728\u5367\u94fa\u4e0a\uff0c\u5c06\u96ea\u767d\u7c89\u5ae9\u7684\u8ff7\u4eba\u81c0\u90e8\u9ad8\u9ad8\u8038\u7fd8\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u7ea4\u7ec6\u767d\u5ae9\u7684\u5c0f\u86ee\u8170\uff0c\u4f7f\u5f97\u81c0\u90e8\u7a0d\u5f97\u5bbd\u5927\u800c\u66f2\u7ebf\u9971\u6ee1\uff0c\u83ca\u82b1\u74e3\u5448\u6d45\u8910\u8272\uff0c\u518d\u5f80\u4e0b\u5c31\u662f\u5ae3\u7ea2\u7684\u6d78\u5728\u6c34\u6cfd\u91cc\u7684\u8ff7\u4eba\u9c9c\u5ae9\u5507\u7a74\u5939\u5728\u96ea\u767d\u8000\u773c\u7684\u81c0\u5fc3\uff0c\u8fb9\u4e0a\u8fd8\u6709\u4e9b\u5fae\u6e7f\u8d34\u7684\u7ec6\u7ed2\u6bdb\uff0c\u5c31\u50cf\u4e00\u53ea\u8ff7\u4eba\u7684\u96ea\u767d\u82f9\u679c\uff0c\u770b\u5f97\u5f20\u606a\u5fc3\u53d1\u7d27\uff0c\u7b80\u76f4\u8981\u878d\u5316\u6389\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e09\u4e0b\u4e94\u9664\u4e8c\u5c06\u8863\u88e4\u8131\u6389\uff0c\u5c06\u50f5\u76f4\u5230\u6781\u70b9\u7684\u534a\u622a\u80a2\u5e72\u62b5\u7740\u6e7f\u6da6\u6e29\u70ed\u7684\u5507\u53e3\u7f13\u7f13\u7684\u523a\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u5510\u5a67\u54ea\u91cc\u8bd5\u8fc7\u8fd9\u79cd\u4f53\u59ff\uff0c\u5fc3\u91cc\u7f9e\u6da9\u5f97\u5f88\uff0c\u8981\u7ffb\u8fc7\u8eab\u6765\uff0c\u4e0d\u8ba9\u5f20\u606a\u8fd9\u4e48\u73a9\u3002</p><p>\u3000\u3000\u5f20\u606a\u6309\u4f4f\u5979\u67d4\u5ae9\u7684\u7ec6\u8170\uff0c\u611f\u53d7\u7740\u5979\u91cc\u9762\u7684\u7d27\u7f29\u4e0e\u7ef5\u7ef5\u8f6f\u8f6f\u5230\u6781\u70b9\u7684\u6e7f\u6ed1\uff0c\u611f\u89c9\u6574\u4e2a\u4eba\u90fd\u7ed9\u5305\u88f9\u5728\u91cc\u4e00\u6837\uff0c\u5168\u8eab\u5fc3\u7684\u6b22\u7545\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0d\u518d\u6323\u624e\uff0c\u867d\u8bf4\u5185\u91cc\u60c5\u6b32\u518d\u71c3\u7684\u8815\u52a8\u7740\uff0c\u7f9e\u6da9\u7684\u5979\u5374\u6ca1\u6709\u52c7\u6c14\u50cf\u6210\u719f\u7684\u5987\u4eba\u90a3\u6837\u626d\u52a8\u7ea4\u7ec6\u7684\u8170\u80a2\u3001\u6447\u6446\u4e30\u76c8\u7684\u81c0\u90e8\uff0c\u66f4\u4e0d\u4f1a\u4e3b\u52a8\u7528\u9c9c\u5ae9\u7684\u5507\u7a74\u53bb\u6469\u64e6\u5f20\u606a\u7684\u4e0b\u8eab\uff0c\u5979\u53ea\u662f\u4e0a\u534a\u8eab\u65e0\u529b\u7684\u8db4\u5728\u90a3\u91cc\uff0c\u5934\u4fa7\u8d34\u7740\u8138\uff0c\u4e30\u76db\u51cc\u4e71\u7684\u5934\u53d1\u4e0b\u9732\u51fa\u8ff7\u4eba\u5230\u6781\u70b9\u7684\u6f6e\u7ea2\u8138\u86cb\uff0c\u5634\u91cc\u65e0\u610f\u8bc6\u7684\u53d1\u51fa\u8bf1\u4eba\u7684\u5462\u5583\uff0c\u8212\u7545\u662f\u8212\u7545\u5230\u6781\u70b9\uff0c\u624b\u91cc\u5374\u6e34\u671b\u7740\u8981\u6293\u4f4f\u4ec0\u4e48\uff0c\u8fd8\u4e0d\u5fd8\u63d0\u9192\u5f20\u606a\u4e00\u58f0\uff1a\u201c\u6211\u4e0d\u5728\u5b89\u5168\u671f\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5f97\u7a0d\u7f13\u6218\u4e8b\u62ff\u51fa\u201c\u96e8\u8863\u201d\u6234\u4e0a\uff0c\u8ba9\u5510\u5a67\u7ffb\u8f6c\u8fc7\u8eab\u6765\u4e0e\u5979\u6b63\u9762\u4ea4\u5408\u3002\u5510\u5a67\u8fd9\u624d\u80fd\u50cf\u516b\u722a\u7ae0\u9c7c\u4f3c\u7684\u53cc\u624b\u7f20\u4f4f\u5f20\u606a\u7684\u8116\u5b50\uff0c\u8f7b\u5598\u7740\u6c14\u8bf4\uff1a\u201c\u8fd8\u662f\u8fd9\u79cd\u5728\u4f60\u6000\u91cc\u7684\u611f\u89c9\u6700\u8212\u670d\u4e86\uff0c\u5c31\u8fd9\u6837\u8981\u6211\uff0c\u6162\u4e00\u4e9b\uff0c\u5927\u8272\u72fc\uff0c\u77e5\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u8f7b\u7684\u543b\u5979\u6e7f\u6da6\u7ef5\u8f6f\u7684\u5634\u5507\uff0c\u624b\u63c9\u538b\u7740\u5979\u7684\u767d\u4e73\uff0c\u4ea4\u5408\u7740\uff0c\u611f\u89c9\u5230\u5979\u4e30\u6c9b\u7684\u871c\u6db2\u5728\u4e0d\u65ad\u7684\u6d8c\u51fa\uff0c\u6b63\u503c\u5510\u5a67\u661f\u7738\u6d41\u76fc\u3001\u4e3e\u81c0\u8fce\u9001\uff0c\u4e24\u4eba\u90fd\u98d8\u98d8\u6b32\u4ed9\u3001\u9163\u7545\u5982\u9189\u4e4b\u65f6\uff0c\u8f66\u95e8\u7a81\u7136\u7ed9\u201c\u556a\u556a\u556a\u201d\u7684\u6572\u54cd\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\uff01\u201d</p><p>\u3000\u3000\u542c\u7740\u674e\u99a8\u4e88\u5728\u8fc7\u9053\u91cc\u7a0d\u5e26\u60ca\u614c\u7684\u558a\u4ed6\uff0c\u5f20\u606a\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u60c5\u51b5\uff0c\u8179\u90e8\u79bb\u5f00\u5510\u5a67\u7684\u4e0b\u4f53\uff0c\u95ee\u4e86\u4e00\u58f0\uff1a\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u5510\u5a67\u5374\u98de\u5feb\u7684\u5c06\u8d34\u8eab\u8863\u7269\u7a7f\u597d\uff0c\u53c8\u62c9\u8d77\u88ab\u5b50\u88f9\u597d\u8eab\u4f53\uff0c\u88c5\u51fa\u4e00\u526f\u5df2\u7ecf\u5165\u7761\u7684\u6a21\u6837\uff0c\u63a8\u7740\u5f20\u606a\u8981\u4ed6\u53bb\u5f00\u95e8\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5510\u5a67\u8138\u4e0a\u6302\u7740\u53d6\u7b11\u7684\u8868\u60c5\uff0c\u597d\u50cf\u521a\u624d\u7684\u6027\u4e8b\u8ddf\u5979\u5b8c\u5168\u65e0\u5173\u4f3c\u7684\uff0c\u90fd\u4e0d\u5fcd\u5fc3\u63d0\u9192\u5979\u5e8a\u94fa\u53ea\u6709\u4e00\u5f20\u662f\u7761\u8fc7\u7684\u6837\u5b50\u662f\u65e0\u6cd5\u63a9\u8033\u76d7\u94c3\u7684\u9a97\u4eba\u7684\u3002\u5f20\u606a\u9ebb\u5229\u7684\u7a7f\u4e0a\u79cb\u8863\u79cb\u88e4\uff0c\u6253\u5f00\u5305\u53a2\u95e8\uff0c\u8ba9\u53ea\u7a7f\u7740\u8d34\u8eab\u8863\u7269\u3001\u8eab\u4f53\u66f2\u7ebf\u65e0\u6bd4\u6027\u611f\u7684\u674e\u99a8\u4e88\u8fdb\u6765\uff1a\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u624d\u6709\u4e24\u4e2a\u7537\u4eba\u4e0a\u8f66\u6765\uff0c\u8ddf\u6211\u540c\u4e00\u4e2a\u8f66\u53a2\uff0c\u4ed6\u4eec\u7684\u773c\u795e\u2026\u2026\u201d\u674e\u99a8\u4e88\u5fc3\u6025\u6c14\u4fc3\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u5355\u72ec\u4e00\u4e2a\u5305\u53a2\uff1f\u201d\u5f20\u606a\u5fc3\u60f3\u674e\u99a8\u4e88\u5e94\u8be5\u4e0d\u4f1a\u541d\u556c\u5c06\u5305\u53a2\u91cc\u53e6\u4e09\u4e2a\u94fa\u5e2d\u7684\u7968\u90fd\u4e70\u4e0b\u6765\uff1b\u8f6f\u5367\u5305\u53a2\u5e26\u95e8\uff0c\u5355\u8eab\u5973\u4eba\u7ed9\u9a9a\u6270\u7684\u60c5\u51b5\u65f6\u6709\u53d1\u751f\uff0c\u4f55\u51b5\u50cf\u674e\u99a8\u4e88\u8fd9\u6837\u5bf9\u7537\u4eba\u6709\u7740\u6781\u81f4\u65c5\u60d1\u529b\u7684\u5999\u9f84\u5973\u90ce\u3002</p><p>\u3000\u3000\u201c\u4e34\u65f6\u51b3\u5b9a\u5750\u706b\u8f66\u7684\uff0c\u201d\u674e\u99a8\u4e88\u6709\u4e9b\u4e0d\u6562\u770b\u5f20\u606a\u7684\u773c\u775b\uff0c\u201c\u8fd8\u4ee5\u4e3a\u51fa\u4e86\u5317\u4eac\u5c31\u4e0d\u4f1a\u518d\u4eba\u4e0a\u8f6f\u5367\u8f66\u53a2\u4e86\uff0c\u6211\u80fd\u4e0d\u80fd\u2026\u2026\u201d\u77a5\u89c1\u5305\u53a2\u91cc\u53ea\u6709\u4e00\u5f20\u5e8a\u94fa\u6709\u7761\u8fc7\u7684\u75d5\u8ff9\uff0c\u63a5\u4e0b\u6765\u7684\u8981\u6c42\u5c31\u96be\u4ee5\u8bf4\u51fa\u53e3\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5510\u5a67\u7a81\u7136\u4e5f\u610f\u8bc6\u5230\u90a3\u91cc\u9732\u7834\u7efd\u4e86\uff0c\u5fcd\u7740\u7f9e\u6da9\u62c9\u674e\u99a8\u4e88\u5750\u4e0b\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u7761\u8fd9\u91cc\u597d\u4e86\uff0c\u53cd\u6b63\u8fd8\u6709\u4e24\u5f20\u5e8a\u591a\u7740\u5462\uff0c\u53ea\u8981\u4f60\u4e0d\u62c5\u5fc3\u6211\u4eec\u5305\u53a2\u91cc\u8fd8\u6709\u4e00\u53ea\u5927\u8272\u72fc\u591c\u91cc\u53ef\u80fd\u4f1a\u8d81\u673a\u9a9a\u6270\u4f60\u5c31\u884c\u3002\u201d\u53c8\u5429\u5490\u5f20\u606a\uff0c\u201c\u4f60\u53bb\u5e2e\u4eba\u5bb6\u5c06\u793c\u674e\u3001\u8863\u670d\u90fd\u62ff\u8fc7\u6765\u2026\u2026\u201d\u5374\u770b\u89c1\u674e\u99a8\u4e88\u624b\u6491\u5230\u8eab\u540e\u5750\u5230\u8f6f\u94fa\u4e0a\u624b\u53c8\u60ca\u7f29\u7684\u6536\u4e86\u56de\u6765\uff0c\u5510\u5a67\u521d\u65f6\u8fd8\u6709\u4e9b\u60ca\u8bb6\uff0c\u5f85\u770b\u5230\u674e\u99a8\u4e88\u624b\u638c\u4e0a\u6302\u4e0b\u6765\u7684\u6c34\u4e1d\u5728\u706f\u5149\u4e0b\u95ea\u7740\u5149\u6cfd\u5c31\u77e5\u9053\u5979\u7684\u624b\u6491\u5230\u4ec0\u4e48\u5730\u65b9\u4e0a\u53bb\u4e86\u2026\u2026</p></article>"}}}]);
