(window.webpackJsonp=window.webpackJsonp||[]).push([[1185],{1208:function(p){p.exports={chapterTitle:null,title:"\u7b2c976\u7ae0 \u7279\u6743",content:"<article><h2>\u7b2c976\u7ae0 \u7279\u6743</h2><p>\u3000\u3000\u8d70\u8fdb\u8fd9\u8282\u8f66\u53a2\u6765\u7684\u957f\u77ed\u53d1\u9752\u5e74\u7684\u4e94\u540d\u540c\u4f34\u91cc\u8fd8\u6709\u4e00\u540d\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\uff0c\u770b\u60c5\u5f62\u50cf\u662f\u540c\u4e00\u5bb6\u516c\u53f8\u91cc\u7684\u804c\u5458\uff0c\u5012\u662f\u8fd8\u6709\u4e24\u4e2a\u8170\u5706\u8180\u9614\u3001\u5b54\u6b66\u6709\u529b\u7684\u58ee\u6c49\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u9664\u4e86\u90a3\u540d\u7626\u5f31\u7684\u5973\u5b69\u5b50\u5916\uff0c\u5176\u4ed6\u4eba\u4e5f\u4e0d\u50cf\u666e\u901a\u7684\u516c\u53f8\u767d\u9886\uff0c\u773c\u775b\u91cc\u7684\u5533\u6c14\u5f88\u91cd\uff0c\u76ef\u8fc7\u6765\u53c8\u51f6\u53c8\u72e0\uff0c\u662f\u90a3\u79cd\u8857\u5934\u5e38\u89c1\u7684\u6311\u8845\u773c\u795e\uff0c\u4f3c\u4e4e\u4ed6\u4eec\u7684\u4eba\u7ed9\u6b3a\u8d1f\u4e86\u4e00\u5b9a\u8981\u627e\u56de\u8fc7\u8282\u6765\uff0c\u8981\u771f\u662f\u4ec0\u4e48\u516c\u53f8\u7684\u804c\u5458\uff0c\u90a3\u591a\u534a\u4e5f\u662f\u5f53\u4e0b\u7684\u62c6\u8fc1\u3001\u8ba8\u503a\u6216\u8005\u653e\u9ad8\u5229\u8d37\u4e4b\u7c7b\u7684\u516c\u53f8\u3002</p><p>\u3000\u3000\u8f66\u53a2\u91cc\u7684\u8fc7\u9053\u72ed\u9698\uff0c\u968f\u5085\u4fca\u8ddf\u7740\u5f20\u606a\u51fa\u6765\u7684\u4e24\u540d\u5b89\u5168\u4e8b\u52a1\u52a9\u7406\u90fd\u662f\u519b\u4f0d\u51fa\u8eab\uff0c\u8eab\u6750\u4e0d\u9ad8\uff0c\u6574\u6574\u9f50\u9f50\u7684\u7a7f\u7740\u897f\u88c5\uff0c\u4e5f\u770b\u4e0d\u51fa\u8eab\u4f53\u6709\u591a\u58ee\u5b9e\uff0c\u4f46\u4e00\u4eba\u5835\u5728\u8fc7\u9053\u91cc\u795e\u8272\u51b7\u5cfb\u7684\u76ef\u7740\u90a3\u5806\u4eba\uff0c\u5c31\u8ba9\u4ed6\u4eec\u4e0d\u6562\u8f7b\u6613\u7684\u903c\u8fd1\u3002</p><p>\u3000\u3000\u53ef\u60dc\u624d\u516d\u4e2a\u4eba\uff0c\u4e5f\u4e0d\u6015\u4ed6\u4eec\u4eba\u591a\u52bf\u4f17\uff0c\u771f\u8981\u52a8\u8d77\u624b\u6765\uff0c\u8fd9\u516d\u4e2a\u4eba\u8fd8\u4e0d\u591f\u5085\u4fca\u4ed6\u4eec\u70ed\u8eab\u7684\uff0c\u518d\u8bf4\u8c01\u6ca1\u4e8b\u627e\u4e8b\u5728\u8f6f\u5367\u5305\u53a2\u91cc\u6253\u67b6\u6597\u6bb4\uff0c\u7ed9\u4e58\u8b66\u4e24\u5934\u4e00\u5835\uff0c\u6e9c\u90fd\u6ca1\u5904\u6e9c\u3002\u5f20\u606a\u60a0\u7136\u81ea\u5f97\u7684\u5750\u5728\u8fc7\u9053\u7684\u5c0f\u684c\u65c1\uff0c\u5510\u5a67\u4e0e\u674e\u99a8\u4e88\u8fd9\u65f6\u5019\u7a7f\u4e86\u6574\u6574\u9f50\u9f50\u7684\u60f3\u63a2\u51fa\u5934\u6765\u7684\u770b\u7a76\u7adf\uff0c\u5f20\u606a\u6325\u4e86\u6325\u624b\u8ba9\u5979\u4eec\u56de\u5305\u53a2\u91cc\u53bb\uff0c\u6ca1\u4e8b\u4e0d\u8981\u968f\u4fbf\u770b\u70ed\u95f9\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u6253\u4e86\u4eba\u4e0d\u4f1a\u5f53\u4ec0\u4e48\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\u8fc7\u5427\uff1f\u201d\u4e00\u540d\u8eab\u9ad8\u4f53\u58ee\u7684\u9752\u5e74\u8d70\u51fa\u6765\uff0c\u4f38\u624b\u8981\u63a8\u5835\u5728\u4ed6\u524d\u9762\u7684\u5b89\u5168\u4e8b\u52a1\u52a9\u7406\uff0c\u624b\u8fd8\u6ca1\u6709\u89e6\u53ca\u5230\u4eba\u80a9\u4e0a\uff0c\u5c31\u7ed9\u4eba\u706b\u5149\u7535\u77f3\u7684\u53fc\u4f4f\u624b\u8155\u7ed9\u4e00\u62c9\u4e00\u9001\u7684\u76f4\u5f80\u540e\u8dcc\u4e86\u597d\u4e9b\u6b65\u649e\u5230\u540e\u9762\u4eba\u8eab\u4e0a\u5dee\u70b9\u4e00\u5c41\u80a1\u5750\u5730\u4e0a\u3002</p><p>\u3000\u3000\u8fd9\u4e0b\u5b50\u957f\u77ed\u53d1\u9752\u5e74\u90fd\u6e05\u9192\u7684\u8ba4\u8bc6\u5230\u4ed6\u4eec\u521a\u624d\u7ed9\u5236\u4f4f\u52a8\u5f39\u4e0d\u5f97\u4e0d\u662f\u4ec0\u4e48\u5076\u7136\uff0c\u4e5f\u4e0d\u662f\u4e0d\u7559\u610f\u624d\u5403\u4e86\u4e8f\uff0c\u8fd9\u8fb9\u4eba\u591a\u52bf\u4f17\u5728\u4e13\u4e1a\u4f1a\u4e24\u624b\u7684\u4eba\u9762\u524d\u6839\u672c\u5c31\u53d1\u6325\u4e0d\u4e86\u4f5c\u7528\u3002</p><p>\u3000\u3000\u90a3\u77ed\u53d1\u9752\u5e74\u8fd8\u6709\u4e9b\u4e0d\u7518\u5fc3\uff0c\u53eb\u56a3\u9053\uff1a\u201c\u5988\u7684\uff0c\u4e0d\u8981\u4ee5\u4e3a\u4f1a\u4e24\u4e0b\u5b50\u5c31\u6562\u5728\u706b\u8f66\u4e0a\u6a2a\u884c\u9738\u9053\u2026\u2026\u201d\u4eba\u5374\u7ad9\u5728\u540e\u9762\u4e0d\u6562\u4e0a\u524d\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5411\u5085\u4fca\u62db\u4e86\u62db\u624b\uff0c\u5429\u5490\u9053\uff1a\u201c\u62a5\u8b66\u5427\uff0c\u8ba9\u4e58\u8b66\u6765\u89e3\u51b3\uff0c\u4e0d\u80fd\u8ddf\u4ed6\u4eec\u4e00\u76f4\u62d6\u4e0b\u53bb\u4e0d\u7761\u89c9\u3002\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d\u8fd9\u65f6\u5019\u4e00\u540d\u5973\u4e58\u52a1\u5458\u4e0e\u4e24\u540d\u7537\u4e58\u8b66\u4ece\u8fc7\u9053\u90a3\u5934\u8d70\u8fc7\u6765\uff0c\u8d70\u5230\u4e24\u5806\u4eba\u7684\u4e2d\u95f4\uff0c\u4e4b\u524d\u7684\u52a8\u9759\u8fd8\u5c0f\u4e00\u4e9b\uff0c\u56db\u4e94\u4e2a\u4eba\u6c14\u52bf\u6c79\u6c79\u7684\u4ece\u5176\u4ed6\u8f66\u53a2\u7a7f\u5230\u8f6f\u5367\u8f66\u53a2\u6765\uff0c\u4e58\u52a1\u5458\u81ea\u7136\u4e0d\u80fd\u88c5\u4f5c\u4e0d\u77e5\u9053\uff0c\u558a\u4e86\u4e24\u4e58\u8b66\u4e00\u8d77\u624d\u6562\u8fc7\u6765\uff0c\u5e74\u7eaa\u7a0d\u957f\u7684\u4e58\u8b66\u4e25\u5389\u7684\u76ef\u7740\u4e24\u8fb9\u4eba\uff0c\u201c\u8fd9\u4e48\u665a\u8fd8\u4e0d\u7761\u89c9\uff0c\u6324\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u521a\u4e0a\u8f66\uff0c\u4ed6\u4eec\u4e2d\u6709\u4e2a\u5c0f\u5b50\u5077\u5077\u6478\u5230\u6211\u4eec\u8f66\u53a2\u91cc\u6765\u5077\u4e1c\u897f\uff0c\u7ed9\u6211\u4eec\u6293\u4f4f\u8fd8\u53cd\u54ac\u4e00\u53e3\uff0c\u53c8\u7ea0\u7ed3\u540c\u4f19\u6253\u4eba\uff0c\u6211\u4eec\u6c14\u6124\u4e0d\u8fc7\u624d\u627e\u4eba\u8ba8\u8fd8\u516c\u9053\u7684\u2026\u2026\u201d\u77ed\u53d1\u9752\u5e74\u7ed9\u5085\u4fca\u4ed6\u4eec\u6321\u4f4f\u89c6\u7ebf\uff0c\u4e5f\u6307\u4e0d\u5230\u5750\u5728\u540e\u9762\u7684\u5f20\u606a\uff0c\u53ea\u662f\u5c06\u7ed9\u5f20\u606a\u4e00\u62f3\u6253\u80bf\u7684\u8138\u626c\u8d77\u6765\u7ed9\u4e58\u8b66\u770b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u5085\u4fca\u4ed6\u4eec\u9002\u65f6\u7684\u9000\u5230\u5305\u53a2\u91cc\u4e0d\u6321\u4f4f\u5f20\u606a\u7684\u89c6\u91ce\uff0c\u5f20\u606a\u76ef\u7740\u77ed\u53d1\u9752\u5e74\uff0c\u8bed\u6c14\u5e73\u6de1\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u62a5\u8b66\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u62a5\u8b66\uff1f\u4ed6\u4eec\u8fdb\u53bb\u5077\u4e1c\u897f\u53c8\u6253\u4eba\uff0c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u4e0d\u62a5\u8b66\uff1f\u201d\u4e58\u8b66\u4e5f\u72d0\u7591\u7684\u76ef\u7740\u77ed\u53d1\u9752\u5e74\u3002</p><p>\u3000\u3000\u201c\u6211\u2026\u2026\u201d\u77ed\u53d1\u9752\u5e74\u7ed9\u8fd9\u53e5\u8bdd\u95ee\u7684\u96be\u4ee5\u8fa9\u89e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u7406\u4f1a\u4ed6\uff0c\u8ddf\u5085\u4fca\u8bf4\u9053\uff1a\u201c\u4f60\u8ddf\u4e58\u8b66\u540c\u5fd7\u89e3\u91ca\u4e00\u4e0b\uff0c\u6211\u5148\u8fdb\u53bb\u4f11\u606f\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u662f\u4ed6\u8fdb\u5305\u53a2\u5077\u4e1c\u897f\u6253\u4eba\u7684\uff0c\u4ed6\u600e\u4e48\u53ef\u4ee5\u8d70\uff1f\u201d\u957f\u53d1\u9752\u5e74\u53eb\u56a3\u9053\uff0c\u201c\u5c31\u662f\u4ed6\u4e00\u811a\u8e39\u6211\u809a\u5b50\u4e0a\u73b0\u5728\u90fd\u75bc\u2026\u2026\u201d\u4ed6\u8981\u51b2\u8fc7\u6765\u63ea\u4f4f\u5f20\u606a\u3002</p><p>\u3000\u3000\u5085\u4fca\u7ad9\u51fa\u6765\u4e00\u628a\u6293\u4f4f\u4ed6\u7684\u80a9\u8180\u4e0d\u8ba9\u4ed6\u52a8\u5f39\uff0c\u5c06\u4ed6\u5f80\u540e\u63a8\u5f00\u51e0\u6b65\uff0c\u638f\u51fa\u8bc1\u4ef6\u62ff\u7ed9\u4e58\u8b66\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u4eec\u7684\u5de5\u4f5c\u8bc1\u2014\u2014\u6211\u4eec\u516c\u53f8\u7684\u8d35\u5bbe\u4ece\u5317\u4eac\u4e0a\u8f66\u65f6\u5c31\u575012\u53f7\u5305\u53a2\uff0c\u4ed6\u4eec\u662f\u4ece\u9752\u5c9b\u7ad9\u4e0a\u7684\u8f66\uff0c\u4e5f\u662f12\u53f7\u5305\u53a2\uff0c\u4ed6\u4eec\u4e0a\u8f66\u540e\u5bf9\u6211\u4eec\u516c\u53f8\u7684\u8d35\u5bbe\u8a00\u884c\u4e0a\u6709\u6240\u9a9a\u6270\uff0c\u516c\u53f8\u7684\u8d35\u5bbe\u4e5f\u5fcd\u8010\u7740\u79fb\u5230\u5176\u4ed6\u5305\u53a2\u4f11\u606f\uff0c\u53ea\u662f\u5728\u53d6\u56de\u884c\u793c\u65f6\uff0c\u4ed6\u4eec\u767e\u822c\u963b\u6320\uff0c\u4e89\u6267\u4e2d\u6709\u4e9b\u80a2\u4f53\u51b2\u7a81\u2014\u2014\u5177\u4f53\u7684\u60c5\u51b5\u662f\u4e0d\u662f\u6211\u8ddf\u4f60\u4eec\u53bb\u4e58\u52a1\u5ba4\u8bf4\u6e05\u695a\uff0c\u4e0d\u8981\u5f71\u54cd\u5230\u8f66\u53a2\u91cc\u5176\u4ed6\u65c5\u5ba2\u7684\u4f11\u606f\uff1f\u201d</p><p>\u3000\u3000\u957f\u53d1\u9752\u5e74\u770b\u5230\u5085\u4fca\u4eae\u51fa\u8bc1\u4ef6\u5c31\u8ba9\u7d27\u7ef7\u7740\u8138\u7684\u4e58\u8b66\u8138\u8272\u7f13\u548c\u4e0b\u6765\uff0c\u4e0d\u5fff\u7684\u5429\u5490\u77ed\u53d1\u9752\u5e74\uff1a\u201c\u4f60\u4e5f\u5c06\u5de5\u4f5c\u8bc1\u62ff\u7ed9\u4e58\u8b66\u540c\u5fd7\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u5085\u4fca\u4e0d\u7406\u4f1a\u7684\u5c06\u8bc1\u4ef6\u6536\u4e86\u8d77\u6765\uff0c\u5e74\u957f\u7684\u4e58\u8b66\u63a5\u8fc7\u77ed\u53d1\u9752\u5e74\u9012\u4e0a\u6765\u7684\u5de5\u4f5c\u8bc1\uff0c\u7ffb\u770b\u4e86\u4e00\u4e0b\uff0c\u8138\u8272\u4e00\u5bd2\uff0c\u8bad\u65a5\u9053\uff1a\u201c\u8f6f\u5367\u5305\u53a2\u7ecf\u5e38\u63a5\u5230\u5355\u8eab\u5973\u4e58\u5ba2\u88ab\u9a9a\u6270\u7684\u6295\u8bc9\uff0c\u5c31\u662f\u4f60\u4eec\u8fd9\u4e9b\u5bb3\u7fa4\u4e4b\u9a6c\uff01\u90fd\u8ddf\u6211\u53bb\u9910\u5385\u8bf4\u660e\u60c5\u51b5\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u4e58\u8b66\u5fae\u7b11\u793a\u610f\uff0c\u76f8\u4fe1\u4e58\u8b66\u4e0e\u4e58\u52a1\u5458\u5bf9\u5bb9\u8c8c\u5982\u6b64\u660e\u8273\u7684\u674e\u99a8\u4e88\u4f1a\u6709\u6df1\u523b\u7684\u5370\u8c61\uff0c\u63a8\u7740\u5510\u5a67\u7684\u8111\u888b\u4e00\u8d77\u8fdb\u4e86\u5305\u53a2\u3002</p><p>\u3000\u3000\u201c\u5085\u4fca\u7684\u5de5\u4f5c\u8bc1\u6709\u4ec0\u4e48\u4e0d\u540c\uff0c\u4e3a\u4ec0\u4e48\u4e58\u8b66\u770b\u5230\u4ed6\u7684\u5de5\u4f5c\u8bc1\u8ddf\u53d8\u8272\u9f99\u4f3c\u7684\uff1f\u201d\u5510\u5a67\u7591\u60d1\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u6b6a\u7740\u5634\u7b11\u4e86\u7b11\uff0c\u201c\u662f\u6709\u4e9b\u4e0d\u540c\u3002\u201d\u5fc3\u60f3\u674e\u99a8\u4e88\u4e5f\u662f\u4e0d\u60f3\u60f9\u4e8b\u751f\u975e\u5f15\u8d77\u56fd\u5185\u5a92\u4f53\u7684\u5173\u6ce8\uff0c\u4e0d\u7136\u4ee5\u5979\u674e\u5efa\u7199\u5bb6\u65cf\u7ee7\u627f\u4eba\u7684\u8eab\u4efd\u76f4\u63a5\u5411\u4e58\u8b66\u6295\u8bc9\u88ab\u9a9a\u6270\uff0c\u4ee5\u56fd\u5185\u5bf9\u5916\u5bbe\u7684\u6001\u5ea6\uff0c\u90a3\u4e24\u4e2a\u5c0f\u5b50\u975e\u7ed9\u6574\u5f97\u6252\u5c42\u76ae\u4e0b\u6765\u4e0d\u53ef\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u5085\u4fca\u6240\u51fa\u793a\u7684\u5de5\u4f5c\u8bc1\uff0c\u5f53\u7136\u4e0d\u540c\u4e8e\u4e00\u822c\u516c\u53f8\u7ed9\u5458\u5de5\u6240\u53d1\u7684\u3001\u53ea\u5728\u516c\u53f8\u533a\u57df\u91cc\u6709\u9650\u7684\u5de5\u4f5c\u8bc1\u6216\u5de5\u4f5c\u5361\u3002</p><p>\u3000\u3000\u56fd\u5185\u5e76\u6ca1\u6709\u6cd5\u5f8b\u610f\u4e49\u4e0a\u7684\u79c1\u4eba\u4fdd\u9556\uff0c\u4e5d\u516b\u5e74\u6b63\u89c4\u7684\u4fdd\u5b89\u516c\u53f8\u53ea\u80fd\u662f\u516c\u5b89\u673a\u5173\u7684\u76f4\u5c5e\u5355\u4f4d\u3002</p><p>\u3000\u3000\u9526\u6e56\u65d7\u4e0b\u6709\u8bb8\u591a\u5b9e\u9a8c\u5ba4\u4e0e\u5de5\u5382\u7684\u6027\u8d28\u90fd\u5f88\u7279\u6b8a\uff0c\u5e76\u4e0d\u56e0\u4e3a\u6ce8\u518c\u8d44\u672c\u5f52\u5c5e\u79c1\u6709\u800c\u964d\u4f4e\u4fdd\u536b\u5de5\u4f5c\u7684\u7ea7\u522b\uff0c\u7279\u522b\u662f\u4e2d\u6676\u5fae\u82af\u4e0e\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u5bf9\u56fd\u5185\u7684\u7535\u5b50\u4ea7\u4e1a\u8fd8\u6709\u7740\u4e3e\u8db3\u8f7b\u91cd\u3001\u6682\u65f6\u8fd8\u65e0\u6cd5\u66ff\u4ee3\u7684\u5730\u4f4d\uff0c\u5c5e\u4e8e\u5728\u516c\u5b89\u90e8\u767b\u8bb0\u5907\u6848\u7684\u91cd\u70b9\u4fdd\u536b\u5355\u4f4d\u3002\u4e8b\u5b9e\u4e0a\u9526\u6e56\u4e3a\u5b89\u5168\u4e8b\u52a1\u6210\u7acb\u7684\u4fdd\u536b\u90e8\u95e8\u4e1a\u52a1\u4e0a\u8fd8\u8981\u53d7\u7701\u516c\u5b89\u5385\u7684\u76f4\u63a5\u6307\u5bfc\uff0c\u91cd\u70b9\u4fdd\u536b\u4e8b\u52a1\u7684\u5177\u4f53\u8d1f\u8d23\u4eba\u90fd\u8981\u7531\u7701\u516c\u5b89\u5385\u534f\u540c\u9009\u914d\uff0c\u5085\u4fca\u7b49\u4eba\u6240\u6301\u7684\u8bc1\u4ef6\u662f\u4e1c\u6d77\u7701\u516c\u5b89\u5385\u7edf\u4e00\u9881\u53d1\u7684\u2014\u2014\u5f53\u7136\u4e86\uff0c\u8fd9\u4e48\u505a\u591a\u5c11\u6709\u4e9b\u516c\u79c1\u4e0d\u5206\uff0c\u8fd9\u6b21\u968f\u884c\u7684\u4e24\u540d\u5b89\u5168\u4e8b\u52a1\u52a9\u7406\u4e2d\u751a\u81f3\u6709\u4e00\u4eba\u5c31\u662f\u5728\u804c\u8b66\u5bdf\u3002</p><p>\u3000\u3000\u501f\u7528\u5728\u804c\u8b66\u5bdf\u4e5f\u662f\u4e3a\u4e86\u65b9\u4fbf\u5e94\u4ed8\u4e00\u4e9b\u7279\u6b8a\u60c5\u51b5\uff0c\u50cf\u53f6\u5efa\u658c\u7684\u53f8\u673a\u5c31\u662f\u76f4\u63a5\u4ece\u6b66\u8b66\u90e8\u961f\u76f4\u5c5e\u7684\u8b66\u536b\u5c40\u501f\u7528\u8fc7\u6765\u7684\uff0c\u4e0d\u7136\u4ed6\u7684\u8f66\u5728\u5916\u5730\u7ed9\u5de1\u52e4\u622a\u4f4f\uff0c\u8f66\u4e0a\u7684\u519b\u724c\u53cd\u800c\u4f1a\u7ed9\u60f9\u6765\u9ebb\u70e6\u3002\u50cf\u5b8f\u4fe1\u5efa\u8bbe\u7684\u4e25\u6587\u4ecb\uff0c\u968f\u884c\u53f8\u673a\u4e0e\u4fdd\u9556\u91cc\uff0c\u4e5f\u6709\u73b0\u5f79\u519b\u4eba\u6216\u5728\u804c\u8b66\u5bdf\u7684\u8eab\u4efd\uff0c\u8fd9\u4e9b\u90fd\u662f\u8d22\u5bcc\u6216\u7279\u6b8a\u8eab\u4efd\u6240\u5e26\u6765\u7684\u4e00\u79cd\u7279\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u505a\u4e8b\u6709\u81ea\u5df1\u7684\u539f\u5219\u4e0e\u5e95\u9650\uff0c\u4f46\u4e5f\u4e0d\u8981\u5962\u671b\u80fd\u4ed6\u505a\u5230\u6e05\u5219\u65e0\u9c7c\u7684\u201c\u6e05\u201d\u3002\u8bf4\u5b9e\u8bdd\uff0c\u5f20\u606a\u4e5f\u4e0d\u60f3\u5229\u7528\u7279\u6743\u5c06\u8a00\u884c\u4e0a\u7a0d\u6709\u4e0d\u656c\u7684\u4eba\u62fc\u547d\u6309\u5728\u70c2\u6ce5\u5751\u91cc\u8e29\u2014\u2014\u90a3\u6837\u5c31\u592a\u86ee\u6a2a\u4e86\uff0c\u8ba9\u5085\u4fca\u51fa\u9762\u5904\u7406\uff0c\u4e5f\u662f\u5e0c\u671b\u957f\u77ed\u53d1\u9752\u5e74\u4ed6\u4eec\u80fd\u8bc6\u65f6\u52a1\u77e5\u96be\u800c\u9000\u3002</p><p>\u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u7591\u60d1\u7684\u95ee\u674e\u99a8\u4e88\uff1a\u201c\u5bf9\u4e86\uff0c\u4f60\u600e\u4e48\u6ca1\u6709\u5e26\u4fdd\u9556\u5728\u8eab\u8fb9\uff1f\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u5fae\u4fa7\u7740\u5934\uff0c\u907f\u5f00\u5f20\u606a\u7684\u773c\u775b\uff0c\u7ec6\u58f0\u8bf4\u9053\uff1a\u201c\u90fd\u8bf4\u4e86\u662f\u4e34\u65f6\u51b3\u5b9a\u4e58\u706b\u8f66\u56de\u5efa\u90ba\u4e86\uff0c\u8c01\u77e5\u9053\u4f1a\u9047\u4e0a\u8fd9\u4e9b\u4e8b\u2026\u2026\u7ed9\u4f60\u6dfb\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8bf4\u4ec0\u4e48\u8bdd\u554a\uff1f\u201d\u5f20\u606a\u5fc3\u91cc\u4e00\u52a8\uff0c\u60f3\u5230\u674e\u99a8\u4e88\u63a5\u5230\u81ea\u5df1\u56de\u590d\u7684\u77ed\u4fe1\u53ef\u80fd\u731c\u5230\u81ea\u5df1\u4f1a\u4e58\u706b\u8f66\u56de\u5efa\u90ba\u624d\u7279\u610f\u6539\u4e58\u706b\u8f66\u7684\uff0c\u90a3\u4e4b\u524d\u5728\u738b\u5e9c\u4e95\u5927\u9152\u5e97\u7684\u5496\u5561\u5385\u91cc\u9047\u5230\u5979\u72ec\u81ea\u4e00\u4eba\u5750\u5728\u90a3\u91cc\uff0c\u96be\u9053\u5979\u4e5f\u8ba4\u4e3a\u5728\u90a3\u91cc\u4f1a\u6709\u673a\u4f1a\u9047\u5230\u81ea\u5df1\u5417\uff1f</p><p>\u3000\u3000\u60f3\u5230\u674e\u99a8\u4e88\u5728\u53bb\u5317\u4eac\u7684\u98de\u673a\u4e0a\u9047\u5230\u5317\u4eac\u516c\u53f8\u7684\u5458\u5de5\uff0c\u5979\u6216\u8bb8\u4f1a\u8ba4\u4e3a\u81ea\u5df1\u77e5\u9053\u540e\u4f1a\u4e3a\u5979\u4f4f\u8fdb\u738b\u5e9c\u4e95\u5927\u9152\u5e97\u53bb\uff0c\u5f20\u606a\u5fc3\u91cc\u60f3\uff1a\u82e5\u4e0d\u662f\u5c0f\u8205\u4ed6\u4eec\u6b63\u597d\u4f4f\u5728\u738b\u5e9c\u4e95\u5927\u9152\u5e97\uff0c\u81ea\u5df1\u4f1a\u7279\u610f\u6362\u9152\u5e97\u5417\uff1f</p><p>\u3000\u3000\u6216\u8bb8\u5427\uff0c\u4e5f\u4e0d\u662f\u7edd\u6ca1\u6709\u53ef\u80fd\u3002\u8fd9\u4e48\u60f3\u7740\uff0c\u5f20\u606a\u4e5f\u6709\u4e9b\u5fc3\u865a\u4e86\uff0c\u7279\u522b\u662f\u5510\u5a67\u8fd8\u5728\u8fd9\u91cc\uff0c\u8138\u8f6c\u5230\u522b\u5904\uff0c\u8bf4\u9053\uff1a\u201c\u7761\u89c9\u5427\uff0c\u65e9\u4e0a\u4e03\u70b9\u949f\u5c31\u5230\u5efa\u90ba\uff0c\u6ca1\u6709\u51e0\u4e2a\u5c0f\u65f6\u597d\u7761\u4e86\u3002\u201d\u770b\u5230\u5510\u5a67\u94fa\u4e0a\u7684\u5e8a\u5355\u6362\u6389\u4e86\uff0c\u4e5f\u4e0d\u6653\u5f97\u5979\u6362\u54ea\u91cc\u53bb\u4e86\uff0c\u5f81\u8be2\u7684\u770b\u4e86\u5979\u4e00\u773c\uff0c\u5374\u7ed9\u5510\u5a67\u7f9e\u607c\u7684\u77aa\u4e86\u4e00\u773c\uff0c\u5510\u5a67\u4e5f\u53ea\u662f\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u4e0d\u518d\u5c06\u4ed6\u8d76\u51fa\u53bb\u3002</p><p>\u3000\u3000\u5510\u5a67\u3001\u674e\u99a8\u4e88\u7761\u4e0b\u94fa\uff0c\u5f20\u606a\u53ea\u80fd\u7761\u4e0a\u94fa\uff0c\u5c06\u706f\u5173\u4e86\uff0c\u542c\u7740\u5510\u5a67\u4e0e\u674e\u99a8\u4e88\u5728\u4e0b\u9762\u7a78\u7a78\u7c0c\u7c0c\u7684\u8131\u8863\u670d\uff0c\u60f3\u7740\u4e24\u4e2a\u7a7f\u7740\u8d34\u8eab\u8863\u7269\u3001\u751a\u81f3\u534a\u88f8\u7684\u5a07\u8eaf\u5c31\u8eba\u5728\u4e0b\u9762\uff0c\u60f3\u5230\u7ed9\u7a81\u7136\u4e2d\u65ad\u7684\u7f8e\u5999\u6027\u4e8b\uff0c\u5f20\u606a\u5fc3\u91cc\u8fd8\u771f\u662f\u90c1\u82e6\uff0c\u8eba\u5728\u5e8a\u4e0a\u60f3\u9759\u4e0b\u5fc3\u7761\u89c9\uff0c\u53ea\u662f\u4e0b\u534a\u8eab\u786c\u76f4\u5f97\u96be\u53d7\uff0c\u8fd9\u4e5f\u53ea\u80fd\u751f\u6371\u7740\u3002</p><p>\u3000\u3000\u8ff7\u8ff7\u7cca\u7cca\u7684\u7761\u4e86\u5927\u534a\u5bbf\uff0c\u5fae\u660e\u7684\u5929\u5149\u4ece\u8f66\u7a97\u5916\u900f\u8fdb\u6765\uff0c\u5f20\u606a\u5c31\u518d\u4e5f\u7761\u4e0d\u7740\u4e86\uff0c\u7ffb\u8eab\u8d77\u6765\uff0c\u5750\u5230\u5510\u5a67\u7684\u5e8a\u5934\uff0c\u6253\u5f00\u58c1\u706f\uff0c\u51c6\u5907\u770b\u4f1a\u4e66\u3002</p><p>\u3000\u3000\u7761\u5bf9\u9762\u7684\u674e\u99a8\u4e88\u7684\u7761\u76f8\u53ef\u4e0d\u597d\uff0c\u6d01\u767d\u7684\u8584\u88ab\u4e00\u5927\u534a\u90fd\u62d6\u5230\u5730\u4e0a\uff0c\u4e0b\u534a\u8eab\u4fa7\u4e0a\u4ef0\u5367\uff0c\u4e0a\u534a\u8eab\u5374\u662f\u4fa7\u4e0b\u8db4\u7740\uff0c\u4ee5\u5c0f\u7ec6\u8170\u4e3a\u8f74\uff0c\u6574\u4e2a\u4eba\u4f3c\u4e4e\u7ed9\u626d\u4e86\u4e5d\u5341\u5ea6\uff0c\u7a7f\u7740\u6de1\u7d2b\u8272\u7684\u7d27\u8eab\u8863\u88e4\uff0c\u5c06\u5979\u6027\u611f\u7684\u66f2\u7ebf\u5938\u5f20\u800c\u8bf1\u4eba\u7684\u5448\u73b0\u51fa\u6765\uff1a\u8eab\u6750\u4fee\u957f\uff0c\u8170\u7ea4\u7ec6\uff0c\u81c0\u5bbd\u5927\u800c\u4e30\u6ee1\u633a\u7fd8\uff0c\u5de8\u4e73\u538b\u5728\u80f8\u4e0b\uff0c\u4ece\u4fa7\u9762\u6324\u51fa\u5b8c\u7f8e\u7684\u534a\u5706\u6765\uff0c\u5185\u8863\u91cc\u4e5f\u6ca1\u7cfb\u4e73\u7f69\uff0c\u5934\u53d1\u4e30\u76db\u800c\u4e4c\u9ed1\u6cb9\u4eae\uff0c\u5728\u6e05\u6668\u7684\u5fae\u5149\u91cc\u5c31\u50cf\u95ea\u7740\u5149\u6cfd\u7684\u7ef8\u7f0e\uff0c\u9732\u51fa\u7684\u8138\u6709\u7740\u7edd\u7f8e\u7684\u6e05\u7eaf\u6c14\u8d28\uff0c\u90a3\u9c9c\u5ae9\u7684\u4e00\u89d2\u7ea2\u5507\u8bf1\u60d1\u4eba\u60f3\u53bb\u54ac\u4e00\u53e3\uff0c\u606c\u7136\u5165\u7761\u7684\u795e\u6001\u5c24\u5176\u7684\u52a8\u4eba\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u6f02\u4eae\u5230\u6781\u70b9\u4e86\u2014\u2014\u4e0d\u96be\u60f3\u8c61\u4f60\u4eec\u7537\u4eba\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u597d\u8272\u4e86\uff01\u201d</p><p>\u3000\u3000\u542c\u5230\u5510\u5a67\u7684\u8f7b\u58f0\u611f\u6168\uff0c\u5f20\u606a\u5413\u4e86\u4e00\u8df3\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u4e0d\u6653\u5f97\u5510\u5a67\u4ec0\u4e48\u65f6\u5019\u9192\u6765\uff0c\u4fa7\u5367\u7740\u4e5f\u6b63\u770b\u7740\u5bf9\u9762\u719f\u7761\u7684\u674e\u99a8\u4e88\u3002</p></article>"}}}]);
