(window.webpackJsonp=window.webpackJsonp||[]).push([[1186],{1209:function(p){p.exports={chapterTitle:null,title:"\u7b2c1183\u7ae0 \u86c7\u874e\u5973\u4e5f\u6709\u5f80\u4e8b",content:"<article><h2>\u7b2c1183\u7ae0 \u86c7\u874e\u5973\u4e5f\u6709\u5f80\u4e8b</h2><p>\u3000\u3000\u845b\u5efa\u5fb7\u534a\u591c\u4ece\u68a6\u4e2d\u60ca\u9192\uff0c\u7ed9\u751f\u751f\u7684\u5413\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u722c\u8d77\u6765\u5c06\u5e8a\u5934\u7684\u706f\u6253\u5f00\uff0c\u5149\u7740\u8180\u5b50\u5750\u5e8a\u8fb9\uff0c\u60f3\u7740\u68a6\u4e2d\u7684\u60c5\u666f\u4ecd\u5fc3\u6709\u60b8\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u68a6\u89c1\u4e0d\u4ec5\u8096\u745e\u6c11\uff0c\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u7b49\u6240\u6709\u7684\u534e\u590f\u7535\u5b50\u6210\u5458\u4f01\u4e1a\u8001\u603b\u90fd\u7ad9\u8d77\u6765\u53cd\u5bf9\u4ed6\uff0c\u4fe1\u4ea7\u90e8\u91cc\u803f\u91cd\u9633\u3001\u6613\u4e91\u98de\u5bf9\u4ed6\u4e5f\u770b\u4e0d\u987a\u773c\uff0c\u4ed6\u6210\u4e86\u5b64\u5bb6\u5be1\u4eba\uff0c\u88ab\u6324\u51fa\u534e\u590f\u7535\u5b50\uff0c\u4ece\u6b64\u95e8\u5ead\u51b7\u843d\u3001\u5b64\u5bc2\u7ec8\u8001\u3002</p><p>\u3000\u3000\u5750\u5728\u5e8a\u8fb9\uff0c\u845b\u5efa\u5fb7\u8fd8\u80fd\u6e05\u6670\u7684\u4f53\u4f1a\u5230\u68a6\u91cc\u95e8\u5ead\u51b7\u843d\u7684\u5fc3\u51b7\u611f\u89c9\uff0c\u6709\u80a1\u5b50\u5bd2\u610f\u4ece\u80cc\u810a\u91cc\u76f4\u7a9c\u4e0a\u6765\uff0c\u4ed6\u62b1\u81c2\u6413\u4e86\u6413\u624b\u8180\u5b50\uff0c\u9664\u4e86\u5fc3\u51c9\u4e4b\u5916\uff0c\u5934\u9876\u7684\u7a7a\u8c03\u8fd8\u5728\u4e0d\u505c\u7684\u5439\u7740\u51b7\u6c14\uff0c\u52a0\u5267\u4e86\u4ed6\u7684\u5bd2\u610f\u4e0e\u6050\u60e7\u3002</p><p>\u3000\u3000\u7edd\u4e0d\u80fd\u8ba9\u81ea\u5df1\u6ca6\u9677\u8fdb\u8fd9\u6837\u7684\u5883\u5730\uff01\u845b\u5efa\u5fb7\u634f\u62f3\u72e0\u72e0\u6253\u4e86\u4e00\u8bb0\u624b\u5fc3\uff0c\u4f46\u662f\u773c\u4e0b\u5b59\u5fd7\u521a\u660e\u663e\u6709\u4e0d\u542c\u8bdd\u7684\u8ff9\u8c61\u3002</p><p>\u3000\u3000\u534e\u590f\u7535\u5b50\u8fd9\u4e24\u5e74\u7684\u6210\u7ee9\u53ea\u662f\u4e00\u4e9b\u8868\u9762\u6587\u7ae0\uff0c\u4fe1\u4ea7\u90e8\u4e0e\u7ecf\u8d38\u59d4\u3001\u8ba1\u5212\u53d1\u5c55\u59d4\u91cc\u7684\u5927\u4f6c\u4eec\u90fd\u6e05\u695a\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u7ed9\u9526\u6e56\u6b7b\u6b7b\u7684\u538b\u5236\u4f4f\uff0c\u8981\u662f\u5b59\u5fd7\u521a\u7ad9\u8d77\u6765\u8ddf\u8096\u745e\u6c11\u4e00\u8d77\u5012\u6208\uff0c\u6307\u4e0d\u5b9a\u4e0a\u9762\u5c31\u4f1a\u52a8\u5fc3\u601d\u6362\u5e05\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u5bf9\u4fdd\u4f4f\u81ea\u5df1\u7684\u4f4d\u5b50\u6ca1\u6709\u4e07\u5168\u4e4b\u7b56\uff0c\u4e0d\u4ec5\u6ca1\u6709\u4e07\u5168\u4e4b\u7b56\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u5c8c\u5c8c\u53ef\u5371\uff0c\u5173\u952e\u662f\u5f97\u4e0d\u5230\u803f\u91cd\u9633\u7684\u652f\u6301\u3002</p><p>\u3000\u3000\u662f\u5e94\u8be5\u7ed9\u81ea\u5df1\u51c6\u5907\u9000\u8def\u4e86\uff0c\u845b\u5efa\u5fb7\u53ef\u4e0d\u60f3\u843d\u4e2a\u665a\u666f\u51c4\u51c9\u7684\u4e0b\u573a\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u4ece\u5e8a\u5934\u5c06\u624b\u673a\u6478\u51fa\u6765\uff0c\u627e\u51fa\u6797\u96ea\u7684\u7535\u8bdd\u53f7\u7801\uff0c\u4f46\u662f\u4ed6\u5bf9\u8fd9\u4e2a\u5973\u4eba\u4e5f\u4e0d\u662f\u5341\u4e8c\u5206\u7684\u653e\u5fc3\uff0c\u80fd\u5c06\u9000\u8def\u4ea4\u5230\u5979\u624b\u91cc\u5417\uff1f\u845b\u5efa\u5fb7\u6697\u6697\u7684\u601d\u91cf\uff0c\u53ea\u4e0d\u8fc7\u6d77\u7c9f\u79d1\u6280\u9677\u5165\u65e0\u6cd5\u6323\u624e\u7684\u6ce5\u6dd6\uff0c\u7cbe\u5178\u5730\u4ea7\u4ece\u4fe1\u901a\u94f6\u884c\u62b5\u62bc\u800c\u5f97\u7684\u5341\u4ebf\u5de8\u8d44\u90fd\u6df1\u9677\u5176\u4e2d\uff0c\u8fd9\u81f3\u5c11\u662f\u4e00\u4e2a\u673a\u4f1a\u2014\u2014\u673a\u4f1a\u4e0d\u4f1a\u6c38\u8fdc\u90fd\u505c\u7559\u5728\u773c\u524d\u7684\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u634f\u4e86\u634f\u62f3\u5934\uff0c\u6700\u7ec8\u8fd8\u662f\u5148\u6253\u901a\u8fd9\u4e2a\u5973\u4eba\u7684\u7535\u8bdd\u518d\u8bf4\uff0c\u542c\u7740\u624b\u673a\u91cc\u4f20\u6765\u4e24\u58f0\u7684\u547c\u53eb\u97f3\uff0c\u4ed6\u5fc3\u91cc\u8fd8\u7422\u78e8\u7740\u8981\u8ddf\u8fd9\u5973\u4eba\u600e\u4e48\u8bf4\uff0c\u6ca1\u60f3\u5230\u4e0b\u4e00\u79d2\u7535\u8bdd\u5c31\u7ed9\u6390\u6389\uff0c\u624b\u673a\u91cc\u4f20\u6765\u201c\u5bf9\u65b9\u6b63\u5728\u901a\u8bdd\u4e2d\u2026\u2026\u201d\u7684\u63d0\u793a\u97f3\u3002\u867d\u7136\u65e9\u77e5\u9053\u6797\u96ea\u8fd9\u5973\u4eba\u5c06\u7537\u5973\u6b22\u7231\u5f53\u6210\u4ea4\u6613\u7684\u7b79\u7801\u8ddf\u624b\u6bb5\uff0c\u8fd9\u65f6\u5019\u7535\u8bdd\u88ab\u6390\u8fd8\u662f\u8ba9\u845b\u5efa\u5fb7\u5fc3\u5934\u5f88\u4e0d\u662f\u6ecb\u5473\u3002\u5373\u4f7f\u77e5\u9053\u5979\u662f\u4e2a\u86c7\u874e\u5973\u4eba\uff0c\u7ffb\u8d77\u8138\u6765\u4f1a\u8981\u4eba\u547d\uff0c\u845b\u5efa\u5fb7\u4ece\u5fc3\u91cc\u4ecd\u7136\u60f3\u5c06\u8fd9\u4e48\u4e2a\u5a07\u6ef4\u6ef4\u7684\u7f8e\u4eba\u636e\u4e3a\u5df1\u6709\uff0c\u4e0d\u8ba9\u4ed6\u4eba\u5206\u4e00\u676f\u7fb9\u2014\u2014\u8fd9\u5927\u6982\u662f\u7537\u4eba\u7684\u5929\u6027\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u6bd5\u7adf\u4e0d\u662f\u521d\u9677\u60c5\u7f51\u7684\u6bdb\u5934\u5c0f\u4f19\u5b50\uff0c\u4ed6\u731c\u6d4b\u6797\u96ea\u662f\u53bb\u627e\u6210\u664b\u6770\u2014\u2014\u4e3a\u4e86\u6d77\u7c9f\u79d1\u6280\u516c\u5f00\u589e\u53d1\u65b0\u80a1\u7684\u4e8b\u60c5\uff0c\u6797\u96ea\u5c31\u8d34\u4e86\u4e0a\u53bb\uff0c\u6709\u597d\u51e0\u56de\u5979\u5230\u5317\u4eac\uff0c\u845b\u5efa\u5fb7\u90fd\u6ca1\u6709\u89c1\u7740\u2014\u2014\u4ed6\u81c6\u60f3\u7740\u8fd9\u5973\u4eba\u4e00\u526f\u597d\u76ae\u56ca\u5728\u6210\u664b\u6770\u90a3\u4e11\u964b\u8eab\u4e0b\u627f\u6b22\u3001\u53cc\u817f\u53c9\u5f00\u51e0\u4e4e\u6210\u201c\u4e00\u201d\u5b57\u3001\u7ea2\u5507\u7ffb\u6d6a\u7684\u60c5\u5f62\uff0c\u4ed6\u751a\u81f3\u89c9\u5f97\u6709\u4e9b\u5174\u594b\uff0c\u8fc7\u4e86\u6709\u4e00\u523b\u949f\u7684\u6837\u5b50\uff0c\u845b\u5efa\u5fb7\u624b\u91cc\u7684\u624b\u673a\u624d\u54cd\u8d77\u6765\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u867d\u7136\u81ea\u5df1\u4e5f\u5403\u836f\uff0c\u4e0d\u7136\u5728\u6797\u96ea\u6696\u7d27\u8ddf\u5c0f\u5634\u4f3c\u7684\u5a9a\u8089\u91cc\u71ac\u4e0d\u4f4f\u51e0\u5206\u949f\uff0c\u4ed6\u8fd8\u662f\u5fcc\u6068\u6210\u664b\u6770\uff0c\u5fc3\u91cc\u6697\u9a82\uff1a\u5403\u836f\uff0c\u5403\u6b7b\u4f60\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u665a\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u5417\uff1f\u201d</p><p>\u3000\u3000\u542c\u89c1\u6797\u96ea\u5728\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\u900f\u7740\u6b22\u7231\u540e\u7684\u6175\u61d2\uff0c\u845b\u5efa\u5fb7\u5b69\u5b50\u6c14\u7684\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u4e0d\u662f\u5acc\u6211\u59a8\u788d\u4f60\u8ddf\u6210\u9ebb\u5b50\u7684\u597d\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u8001\u5a18\u4e0d\u80fd\u627e\u652f\u9ec4\u74dc\u4f3a\u5019\u81ea\u5df1\uff1f\u6709\u4ec0\u4e48\u4e8b\u5c31\u8bf4\uff0c\u6211\u8981\u7761\u89c9\u4e86\uff0c\u4e5f\u4e0d\u770b\u770b\u90fd\u51e0\u70b9\u949f\uff1f\u201d</p><p>\u3000\u3000\u542c\u7740\u6797\u96ea\u4e0d\u8010\u70e6\u7684\u8bed\u6c14\uff0c\u845b\u5efa\u5fb7\u8d8a\u53d1\u7684\u611f\u53d7\u5230\u6b64\u65f6\u7684\u5371\u673a\uff0c\u4ed6\u8f7b\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u8981\u8fd9\u4e48\u4e0d\u8010\u70e6\uff0c\u6211\u80fd\u6709\u529e\u6cd5\u66ff\u4f60\u89e3\u51b3\u773c\u524d\u7684\u5371\u5c40\uff0c\u4f60\u8ddf\u6211\u8bf4\u8bdd\u4e5f\u662f\u8fd9\u79cd\u8bed\u6c14\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u662f\u5fc3\u70e6\uff1b\u4f60\u8981\u662f\u60f3\u540a\u6211\u7684\u80c3\u53e3\uff0c\u90a3\u7b49\u6211\u7761\u8db3\u4e86\u660e\u5929\u518d\u53bb\u627e\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u73b0\u5728\u5c31\u8fc7\u6765\u3002\u201d\u845b\u5efa\u5fb7\u6ca1\u6709\u7ed9\u6797\u96ea\u5546\u8bae\u7684\u4f59\u5730\uff0c\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\u5c31\u6302\u4e86\u7535\u8bdd\uff0c\u6302\u4e86\u7535\u8bdd\u53c8\u6709\u4e9b\u540e\u6094\uff0c\u5973\u4eba\u4ece\u6765\u90fd\u4e0d\u53ef\u7406\u55bb\uff0c\u8981\u662f\u5979\u4f7f\u4e86\u5c0f\u6027\u5b50\uff0c\u53cd\u800c\u4f1a\u9002\u5f97\u5176\u53cd\uff0c\u7b49\u4e86\u8bb8\u4e45\uff0c\u90fd\u4e0d\u89c1\u518d\u6709\u7535\u8bdd\u6253\u8fdb\u6765\uff0c\u845b\u5efa\u5fb7\u66f4\u52a0\u62c5\u5fc3\uff0c\u72b9\u8c6b\u7740\u662f\u4e0d\u662f\u6253\u7535\u8bdd\u8fc7\u53bb\u7f13\u548c\u4e00\u4e0b\uff0c\u53c8\u62c5\u5fc3\u9732\u4e86\u602f\uff0c\u4f1a\u8ba9\u8fd9\u5973\u4eba\u7684\u6c14\u7130\u66f4\u56a3\u5f20\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u6b63\u8fdb\u9000\u552f\u8c37\u65f6\uff0c\u7a97\u524d\u7ed9\u706f\u5149\u6253\u5f97\u96ea\u4eae\uff0c\u4ed6\u7ad9\u8d77\u6765\u8d70\u5230\u7a97\u524d\u770b\u89c1\u4e00\u8f86\u8f7f\u8f66\u505c\u5728\u9662\u95e8\u524d\uff0c\u6797\u96ea\u4ece\u8f66\u7a97\u91cc\u9732\u51fa\u8138\u6765\u2026\u2026</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u4e0b\u697c\u5f00\u95e8\u8ba9\u6797\u96ea\u5c06\u8f66\u5f00\u8fdb\u9662\u5b50\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u53ef\u4e0d\u8981\u9a97\u6211\uff0c\u6211\u4f1a\u626d\u5934\u5c31\u8d70\u7684\u2026\u2026\u201d\u6797\u96ea\u5634\u91cc\u867d\u7136\u8fd9\u4e48\u5a01\u80c1\u7684\uff0c\u5374\u6ee1\u8138\u6625\u8272\u8ba9\u4eba\u7740\u8ff7\uff0c\u4f3c\u4e4e\u662f\u8bf4\u4e00\u53e5\u73a9\u7b11\u8bdd\u3002\u845b\u5efa\u5fb7\u77e5\u9053\u8bdd\u4e0d\u6295\u673a\u8fd9\u5973\u4eba\u771f\u6709\u53ef\u80fd\u7ffb\u8138\u5c31\u8d70\uff0c\u504f\u504f\u5979\u8fd9\u822c\u98ce\u60c5\u5a07\u5a9a\u7684\u6837\u5b50\uff0c\u8ba9\u751f\u4e0d\u8d77\u6c14\u6765\uff0c\u7275\u7740\u5979\u7684\u624b\uff0c\u62cd\u4e86\u62cd\u81ea\u5df1\u7684\u5927\u817f\uff0c\u62c9\u5979\u8fdb\u6000\u5750\u4e0b\uff0c\u5634\u54c4\u5230\u5979\u7684\u8138\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4ec0\u4e48\u65f6\u5019\u9a97\u8fc7\u53bb\uff0c\u4f60\u96be\u9053\u8fd8\u4e0d\u6e05\u695a\u6211\u5bf9\u4f60\u7684\u611f\u60c5\uff1f\u8fd9\u4e9b\u5e74\uff0c\u6211\u5bf9\u5f53\u521d\u7684\u4e8b\u4e00\u76f4\u90fd\u5f88\u6127\u759a\uff0c\u4e00\u76f4\u90fd\u5728\u60f3\u5f25\u8865\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ee5\u524d\u7684\u4e8b\u60c5\uff0c\u6211\u90fd\u5fd8\u4e86\uff0c\u201d\u6797\u96ea\u6253\u4e86\u54c8\u6b20\uff0c\u62ac\u5934\u770b\u7740\u845b\u5efa\u5fb7\u90a3\u5f20\u5df2\u7ecf\u8d77\u76b1\u7eb9\u7684\u8138\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u662f\u8ba9\u6211\u8fc7\u6765\u542c\u4f60\u56de\u5fc6\u5f80\u4e8b\uff0c\u4f60\u8ba9\u6211\u5728\u4f60\u8fd9\u91cc\u7761\u4e00\u89c9\u5427\u3002\u201d</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u521a\u624d\u60f3\u7740\u5979\u4e0e\u6210\u664b\u6770\u5728\u505a\u597d\u4e8b\u5c31\u6709\u4e9b\u5174\u594b\uff0c\u8fd9\u65f6\u8ba9\u5979\u6d51\u5706\u4e30\u6ee1\u7684\u81c0\u90e8\u5750\u5728\u5927\u817f\u6839\u5b50\u4e0a\uff0c\u7acb\u65f6\u5c31\u6709\u4e86\u53cd\u5e94\uff0c\u89c1\u6797\u96ea\u5b8c\u5168\u6ca1\u6709\u5174\u8da3\u542c\u4ed6\u8bf4\u4ee5\u524d\u7684\u4e8b\u60c5\uff0c\u6ca1\u6709\u5f00\u573a\u767d\uff0c\u60f3\u7740\u5148\u4e50\u4e00\u4e50\u518d\u8ddf\u5979\u8bf4\u6b63\u4e8b\u3002</p><p>\u3000\u3000\u6797\u96ea\u7a7f\u7740\u88d9\u5b50\uff0c\u6ca1\u6709\u7a7f\u4e1d\u889c\uff0c\u89c1\u845b\u5efa\u5fb7\u8fd9\u4e2a\u8001\u5bb6\u4f19\u8010\u4e0d\u4f4f\u6027\u5b50\u8981\u786c\u4e0a\uff0c\u5979\u5fd9\u63a8\u5f00\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6761\u88d9\u5b50\u6211\u559c\u6b22\u7740\u5462\uff0c\u8ba9\u4f60\u626f\u7834\u4e86\uff0c\u6211\u53ef\u6ca1\u6709\u65f6\u95f4\u518d\u53bb\u4e70\u2026\u2026\u201d\u7ad9\u8d77\u6765\u4e3b\u52a8\u5c06\u8863\u88d9\u892a\u6389\uff0c\u9732\u51fa\u96ea\u767d\u7684\u8eab\u4f53\u6765\uff0c\u5f85\u845b\u5efa\u5fb7\u8fc5\u901f\u6252\u5149\u81ea\u5df1\u8db4\u5230\u5979\u8eab\u5b50\u91cc\uff0c\u5979\u4f38\u624b\u5230\u4ed6\u4e24\u817f\u4e4b\u95f4\u635e\u4f4f\u90a3\u8bdd\u513f\uff0c\u95ee\u9053\uff1a\u201c\u5230\u5e95\u80fd\u66ff\u6211\u60f3\u5230\u4ec0\u4e48\u6cd5\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u90fd\u5728\u56b7\u4e92\u8054\u7f51\u6ce1\u6cab\u7834\u574f\uff0c\u8bf4\u5230\u5e95\u8fd8\u4e0d\u662f\u8d44\u91d1\u88ab\u62bd\u51fa\u6210\u4e86\u65e0\u6c34\u4e4b\u6e90\uff1f\u534e\u590f\u7535\u5b50\u6210\u7acb\u4e4b\u521d\u5c31\u4ee5\u53d1\u5c55\u65b0\u79d1\u6280\u4ea7\u4e1a\u4e3a\u5df1\u4efb\uff0c\u4f60\u8bf4\u6211\u518d\u8981\u8c41\u51fa\u53bb\uff0c\u8fd8\u4f1a\u6ca1\u6709\u6cd5\u5b50\uff1f\u201d\u845b\u5efa\u5fb7\u6491\u8d77\u8eab\u5b50\uff0c\u76ef\u7740\u6797\u96ea\u770b\uff0c\u4ed6\u53ea\u77e5\u9053\u81ea\u5df1\u4e0d\u80fd\u7ed9\u5979\u7275\u7740\u9f3b\u5b50\u8d70\u3002</p><p>\u3000\u3000\u867d\u7136\u5f88\u591a\u4eba\u90fd\u6e05\u695a\u534e\u590f\u7535\u5b50\u5df2\u7ecf\u8fdc\u8fdc\u4e0d\u53ca\u7231\u8fbe\uff0c\u4f46\u662f\u5728\u540d\u4e49\u4e0a\uff0c\u534e\u590f\u7535\u5b50\u8fd8\u662f\u4e2d\u56fd\u7535\u5b50\u5de5\u4e1a\u767e\u5f3a\u699c\u7684\u9996\u5f3a\uff0c\u53c8\u662f\u8d85\u5927\u578b\u592e\u4f01\uff0c\u6bd4\u8d77\u90a3\u4e9b\u4e13\u5bb6\u6559\u6388\u53ea\u4f1a\u8eb2\u5728\u4e66\u658b\u91cc\u64b0\u6587\u5450\u558a\uff0c\u534e\u590f\u7535\u5b50\u82e5\u4ee5\u5b9e\u9645\u7684\u6295\u8d44\u884c\u52a8\u6765\u8868\u660e\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u4fe1\u5fc3\uff0c\u751a\u81f3\u53ef\u4ee5\u66f4\u8d64\u88f8\u88f8\u4e00\u4e9b\uff0c\u76f4\u63a5\u5bf9\u6d77\u7c9f\u79d1\u6280\u65d7\u4e0b\u7684\u4f01\u4e1a\u8fdb\u884c\u6295\u8d44\uff0c\u81ea\u7136\u80fd\u66f4\u5438\u5f15\u6295\u8d44\u8005\u7684\u773c\u7403\uff0c\u8bf1\u53d1\u4ed6\u4eec\u62a2\u8d2d\u6d77\u7c9f\u79d1\u6280\u80a1\u4ef7\u7684\u51b2\u52a8\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u8981\u9a97\u6211\u54e6\u2014\u2014\u4f60\u8fd9\u6b21\u8981\u5e2e\u4e86\u6211\uff0c\u6211\u5c31\u8ba4\u5b9a\u4f60\u662f\u771f\u6b63\u5bf9\u6211\u597d\u3002\u201d\u6797\u96ea\u89c1\u845b\u5efa\u5fb7\u7ec8\u4e8e\u662f\u8c41\u51fa\u53bb\uff0c\u5174\u594b\u7684\u53cc\u817f\u4e00\u5939\uff0c\u5356\u529b\u76d8\u5728\u4ed6\u7684\u8170\u4e0a\u633a\u9001\uff0c\u53cc\u773c\u5a9a\u7b11\u3002\u65e2\u7136\u845b\u5efa\u5fb7\u8c41\u51fa\u53bb\u4e86\uff0c\u6797\u96ea\u76f8\u4fe1\u4ed6\u73b0\u5728\u6240\u5904\u7684\u4f4d\u5b50\u8fd8\u662f\u80fd\u5e2e\u4e0a\u5f88\u5927\u7684\u5fd9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u4e0d\u77e5\u9053\u845b\u5efa\u5fb7\u4e0e\u6797\u96ea\u6df1\u591c\u5728\u5546\u8bae\u4ec0\u4e48\uff0c\u4ed6\u5f53\u5929\u591c\u95f4\u5c31\u4e58\u706b\u8f66\u8fd4\u56de\u5efa\u90ba\uff0c\u4e0e\u8bb8\u601d\u5728\u706b\u8f66\u91cc\u7f8e\u7f8e\u7684\u7761\u4e86\u4e00\u89c9\uff0c\u5dee\u4e0d\u591a\u5feb\u5230\u5efa\u90ba\u7ad9\uff0c\u624d\u7cbe\u6c14\u5b8c\u8db3\u7684\u9192\u6765\uff0c\u5306\u5306\u6d17\u6f31\u8fc7\u4e0b\u4e86\u706b\u8f66\uff0c\u5efa\u90ba\u8fd8\u6709\u4e00\u644a\u5b50\u4e8b\u60c5\u7b49\u7740\u5f20\u606a\u5904\u7406\uff0c\u8bb8\u601d\u5c31\u76f4\u63a5\u5750\u8f66\u56de\u6d77\u5dde\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u672c\u6765\u662f\u8ba1\u5212\u6628\u5929\u4e0b\u5348\u5c31\u5750\u98de\u673a\u56de\u5efa\u90ba\u7684\uff0c\u5b59\u5fd7\u521a\u7684\u6765\u8bbf\u5c06\u4ed6\u7684\u884c\u7a0b\u803d\u6401\u4e86\u534a\u5929\uff0c\u4ece\u706b\u8f66\u51fa\u6765\uff0c\u5f20\u606a\u5c31\u5750\u8f66\u76f4\u63a5\u8d76\u5f80\u4e16\u7eaa\u9526\u6e56\u5927\u53a6\uff0c\u4e16\u7eaa\u534e\u5a31\u4eca\u5929\u4e0a\u5348\u6709\u4e2a\u4f1a\u8bae\uff0c\u5b59\u9759\u9999\u65e9\u5c31\u8ddf\u4ed6\u6253\u8fc7\u62db\u547c\uff0c\u5e0c\u671b\u4ed6\u80fd\u62bd\u51fa\u65f6\u95f4\u53c2\u52a0\u4e00\u4e0b\uff0c\u8ba8\u8bba\u4e00\u4e0b\u4e16\u7eaa\u534e\u5a31\u8fd1\u671f\u7684\u53d1\u5c55\u76ee\u6807\u4e0e\u6838\u5fc3\u4efb\u52a1\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5927\u53a6\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u7684\u603b\u90e8\u5927\u53a6\uff0c\u9526\u6e56\u7684\u884c\u653f\u5ba4\u4e5f\u5728\u8fd9\u680b\u5927\u697c\u91cc\u2014\u2014\u8fd9\u680b\u5341\u4e8c\u5c42\u9ad8\u7684\u5199\u5b57\u697c\u5728\u6e56\u4e1c\u533a\u57df\u5df2\u7ecf\u77d7\u7acb\u8d77\u6765\u7684\u9ad8\u5c42\u5efa\u7b51\u7fa4\u91cc\u6beb\u4e0d\u8d77\u773c\uff0c\u5374\u662f\u9526\u6e56\u7684\u6838\u5fc3\u6240\u5728\u3002\u7531\u4e8e\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u521a\u521a\u5c06\u90e8\u5206\u529e\u516c\u573a\u6240\u642c\u5230\u6e56\u4e1c\u53bb\uff0c\u7a7a\u51fa\u6765\u7684\u529e\u516c\u697c\u5c42\u5c31\u7559\u7ed9\u4e16\u7eaa\u534e\u5a31\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u534e\u5a31\u7684\u524d\u8eab\u662f\u603b\u90e8\u8bbe\u5728\u9999\u6e2f\u7684\u4e16\u7eaa\u534e\u97f3\uff0c\u4f46\u662f\u5f20\u606a\u51b3\u5fc3\u5411\u5a31\u4e50\u4ea7\u4e1a\u8fdb\u519b\u4e4b\u540e\uff0c\u4ed6\u6253\u7684\u5fc3\u601d\u662f\u5229\u7528\u9999\u6e2f\u4e1a\u5185\u7684\u4f18\u8d28\u8d44\u6e90\u505a\u5927\u5185\u5730\u7684\u5a31\u4e50\u4ea7\u4e1a\uff0c\u91cd\u5fc3\u8fd8\u662f\u5728\u5185\u5730\uff0c\u4e16\u7eaa\u534e\u5a31\u7684\u91cd\u5fc3\u81ea\u7136\u4e5f\u8981\u8f6c\u79fb\u5230\u5185\u5730\u6765\u2014\u2014\u53ea\u662f\u4e00\u5207\u51c6\u5907\u5de5\u4f5c\u521a\u521a\u5c31\u7eea\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u6838\u5fc3\u4ea7\u4e1a\u662f\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\uff0c\u5f71\u89c6\u3001\u97f3\u4e50\u7b49\u5a31\u4e50\u4ea7\u4e1a\u4e0e\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u6709\u7740\u6781\u5bc6\u5207\u7684\u5173\u7cfb\u3002\u82e5\u662f\u6ca1\u6709\u5a31\u4e50\u4ea7\u4e1a\u63d0\u4f9b\u4e30\u5bcc\u591a\u5f69\u7684\u5185\u5bb9\u8d44\u6e90\uff0c\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u4e5f\u65e0\u6cd5\u5f97\u5230\u5f53\u524d\u89c4\u6a21\u7684\u53d1\u5c55\u2014\u2014\u5f53\u4e0b\u53c8\u6d89\u53ca\u5230\u4e92\u8054\u7f51\u5185\u5bb9\u8d44\u6e90\u7684\u95ee\u9898\uff0c\u5927\u89c4\u6a21\u6295\u8d44\u5a31\u4e50\u4ea7\u4e1a\u5c31\u6210\u4e86\u9526\u6e56\u6574\u4f53\u4ea7\u4e1a\u5e03\u5c40\u4e2d\u4e00\u6b65\u4e3e\u8db3\u8f7b\u91cd\u7684\u68cb\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba1\u5212\u5c06\u4e16\u7eaa\u534e\u5a31\u6253\u9020\u6210\u5f71\u89c6\u97f3\u4e50\u3001\u6570\u7801\u5a31\u4e50\u8282\u76ee\u5236\u4f5c\u4e0e\u53d1\u884c\u3001\u6f14\u827a\u4eba\u57f9\u517b\u4e0e\u7ecf\u7eaa\u3001\u5f00\u53d1\u65b0\u578b\u5a31\u4e50\u4ea7\u54c1\u3001\u670d\u52a1\u4e0e\u6280\u672f\u4ee5\u53ca\u7ecf\u8425\u5f71\u89c6\u62cd\u6444\u8bbe\u65bd\u3001\u5168\u7403\u7535\u89c6\u9891\u9053\u4e0e\u5bb6\u5ead\u5a31\u4e50\u8282\u76ee\u6295\u8d44\u7684\u7efc\u5408\u6027\u5a31\u4e50\u4ea7\u4e1a\u96c6\u56e2\u3002</p><p>\u3000\u3000\u6309\u7167\u4ea4\u53c9\u6301\u80a1\u7684\u539f\u5219\uff0c\u4e16\u7eaa\u534e\u5a31\u5c06\u76f4\u63a5\u4ece\u9526\u6e56\u65d7\u4e0b\u5404\u6210\u5458\u4f01\u4e1a\u6309\u6bd4\u4f8b\u5438\u7eb3\u6ce8\u8d44\uff0c\u9526\u6e56\u5546\u4e8b\u3001\u7231\u8fbe\u96c6\u56e2\u3001\u7231\u8fbe\u7535\u5b50\u3001\u76db\u946b\u7535\u5668\u3001\u76db\u946b\u7535\u8baf\u3001\u4e16\u7eaa\u9526\u57ce\u5730\u4ea7\u3001\u4e16\u7eaa\u9526\u57ce\u9910\u996e\u5a31\u4e50\u3001\u65b0\u5149\u7eb8\u4e1a\u3001\u9999\u96ea\u6d77\u3001\u65b0\u5149\u6797\u4e1a\u90fd\u6309\u6bd4\u4f8b\u6295\u8d44\uff0c\u4e5f\u5f15\u8fdb\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u3001\u4e07\u4e1a\u96c6\u56e2\u3001\u5357\u6d0b\u6d77\u8fd0\u7b49\u4e1c\u5357\u4e9a\u534e\u5546\u7684\u90e8\u5206\u6295\u8d44\uff0c\u968f\u4fbf\u51d1\u51d1\u5c31\u51d1\u8db3\u5341\u4ebf\u7684\u8d44\u91d1\u6765\u7528\u4e8e\u542f\u52a8\u4e16\u7eaa\u534e\u5a31\u7684\u521d\u671f\u53d1\u5c55\u8ba1\u5212\u2014\u2014\u5b59\u9759\u9999\u4e5f\u56e0\u6b64\u76f4\u63a5\u83b7\u5f97\u9526\u6e56\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u7684\u8d44\u683c\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fdb\u5927\u697c\u624d\u60f3\u65e9\u996d\u8fd8\u6ca1\u6709\u5403\uff0c\u7531\u4e8e\u4e16\u7eaa\u534e\u5a31\u7684\u697c\u5c42\u79d8\u4e66\u90fd\u662f\u65b0\u9762\u5b54\uff0c\u5f20\u606a\u5148\u8d76\u5230\u9876\u5c42\u51b2\u4e86\u676f\u5496\u5561\u7aef\u7740\u8d76\u5230\u697c\u4e0b\u7684\u4f1a\u8bae\u5ba4\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u3001\u5b59\u9759\u9999\u3001\u859b\u77f3\u3001\u8bb8\u7ef4\u3001\u79e6\u521a\u3001\u5b59\u9759\u6aac\u7b49\u4eba\u90fd\u5750\u5728\u4f1a\u8bae\u5ba4\u91cc\uff0c\u53f6\u5efa\u658c\u770b\u7740\u5f20\u606a\u4e00\u8fb9\u559d\u5496\u5561\u4e00\u8fb9\u6162\u60a0\u60a0\u7684\u63a8\u95e8\u8d70\u8fdb\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u8fd9\u4e2a\u2018\u871c\u6708\u2019\u53ef\u662f\u5ea6\u5f97\u6bd4\u6211\u4eec\u8fd8\u6ecb\u6da6\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u672c\u662f\u73a9\u7b11\u8bdd\uff0c\u5f20\u606a\u8fdb\u5c4b\u65f6\u6b63\u8ddf\u9759\u6aac\u7684\u89c6\u7ebf\u5bf9\u4e0a\uff0c\u770b\u7740\u9759\u6aac\u7709\u5934\u5fae\u626c\u6709\u77aa\u773c\u775b\u7684\u610f\u601d\uff0c\u4ed6\u4e00\u53e3\u5496\u5561\u545b\u5589\u5499\u91cc\uff0c\u5dee\u70b9\u55b7\u51fa\u6765\u2014\u2014\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u5728\u9a6c\u5c14\u4ee3\u592b\u4e3e\u884c\u5a5a\u793c\u4f11\u4e86\u4e00\u5468\u7684\u5047\u671f\u5c31\u6295\u5165\u5230\u5de5\u4f5c\u5f53\u4e2d\uff0c\u5f20\u606a\u5374\u8db3\u8db3\u5728\u5916\u6643\u60a0\u4e86\u534a\u4e2a\u6708\u624d\u56de\u5efa\u90ba\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u54ea\u91cc\u8f7b\u677e\uff1f\u5728\u5317\u4eac\u7ed9\u7f20\u4f4f\u8131\u4e0d\u8eab\u2026\u2026\u7b49\u4f1a\u513f\u6211\u627e\u4f60\u6709\u4e8b\u5546\u8bae\uff0c\u6211\u6628\u5929\u5c31\u901a\u77e5\u9648\u4fe1\u751f\u3001\u4e01\u6127\u3001\u82cf\u6d25\u4e1c\u8fd8\u6709\u5f20\u4e9a\u5e73\u4eca\u5929\u90fd\u5230\u5efa\u90ba\u6765\uff0c\u5dee\u4e0d\u591a\u90fd\u80fd\u8d76\u8fc7\u6765\u5403\u4e2d\u996d\u3002\u201d\u5f20\u606a\u5c06\u5496\u5561\u54bd\u4e0b\u53bb\uff0c\u4ed6\u8d70\u5230\u9759\u6aac\u7684\u8eab\u8fb9\uff0c\u62d6\u4e86\u4e00\u628a\u6905\u5b50\u5750\u5728\u65c1\u8fb9\uff0c\u987a\u4fbf\u5c94\u4e86\u4e00\u4e2a\u8bdd\u9898\u8f6c\u79fb\u5176\u4ed6\u4eba\u7684\u6ce8\u610f\u529b\u3002</p><p>\u3000\u3000\u773c\u4e0b\uff0c\u7231\u8fbe\u7684\u644a\u5b50\u94fa\u5f97\u6700\u5927\uff0c\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u5f20\u4e9a\u5e73\u518d\u52a0\u8096\u664b\u6210\u3001\u6210\u6c49\u748b\u7b49\u4eba\u5404\u4eba\u90fd\u5206\u7ba1\u4e00\u644a\u5b50\u4e8b\u60c5\uff0c\u8981\u6709\u4e09\u4e2a\u4eba\u4ee5\u4e0a\u805a\u5230\u4e00\u8d77\uff0c\u90a3\u5c31\u4e86\u4e0d\u5f97\u7684\u5927\u4e8b\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u5230\u4ed6\u6628\u5929\u521a\u521a\u6253\u7535\u8bdd\u8981\u9648\u4fe1\u3001\u4e01\u69d0\u3001\u82cf\u6d25\u3001\u5f20\u4e9a\u4eca\u5929\u4e2d\u5348\u4e4b\u524d\u90fd\u8d76\u5230\u5efa\u90ba\uff0c\u5927\u5bb6\u90fd\u5fc3\u60f3\u53c8\u6709\u4ec0\u4e48\u91cd\u5927\u52a8\u4f5c\u4e0d\u6210\uff1f\u5f20\u606a\u5374\u7fd8\u8d77\u4e8c\u90ce\u817f\uff0c\u5c06\u9759\u6aac\u684c\u524d\u7684\u6587\u4ef6\u62ff\u5728\u624b\u91cc\u7ffb\u770b\u8d77\u6765\uff0c\u62ff\u8d77\u6765\u4e00\u652f\u7b14\u88c5\u6a21\u4f5c\u6837\u7684\u5728\u4e0a\u9762\u5199\u4e9b\u4ec0\u4e48\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u5f00\u4f1a\u554a\uff0c\u4f60\u4eec\u521a\u624d\u4e0d\u662f\u5728\u5f00\u4f1a\u5417\uff1f\u4f60\u4eec\u7ee7\u7eed\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u671d\u81ea\u5df1\u7684\u6587\u4ef6\u4e0a\u77a5\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u5f20\u606a\u5728\u6587\u4ef6\u5939\u91cc\u7684\u7a7a\u767d\u9875\u4e0a\u5199\u9053\uff1a\u201c\u6628\u5929\u8fd8\u671f\u5f85\u7740\u5feb\u70b9\u56de\u5efa\u90ba\u5462\uff0c\u53bb\u673a\u573a\u4e4b\u524d\u7ed9\u4e8b\u60c5\u5835\u5728\u5317\u4eac\u2026\u2026\u7ed9\u4f60\u5199\u4e86\u8fd9\u4e48\u591a\u5c01\u90ae\u4ef6\uff0c\u600e\u4e48\u90fd\u4e0d\u7406\u6211\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u5c06\u6587\u4ef6\u5939\u4ece\u5f20\u606a\u624b\u91cc\u62a2\u4e86\u8fc7\u6765\uff0c\u8000\u6b66\u626c\u5a01\u7684\u6325\u4e86\u6325\u62f3\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u5927\u5bb6\u4ecb\u7ecd\u6211\u7684\u7b56\u5212\u6848\u5462\uff0c\u4e1c\u897f\u7ed9\u4f60\u62a2\u8fc7\u53bb\uff0c\u6211\u4eec\u600e\u4e48\u5f00\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u4e16\u7eaa\u534e\u5a31\u7684\u524d\u671f\u53d1\u5c55\u89c4\u5212\uff0c\u4e4b\u524d\u90fd\u6709\u8ba8\u8bba\uff0c\u4f1a\u8bae\u7684\u91cd\u70b9\u4e5f\u662f\u8ba8\u8bba\u5b59\u9759\u6aac\u6709\u5173\u97f3\u4e50\u771f\u4eba\u9009\u79c0\u8282\u76ee\u7684\u7b56\u5212\u8349\u6848\u3002\u5b59\u9759\u6aac\u524d\u524d\u540e\u540e\u82b1\u4e86\u8fd1\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u624d\u62df\u5b9a\u7b56\u5212\u6848\u7684\u8349\u7a3f\uff0c\u5f20\u606a\u6ca1\u6709\u53d1\u8868\u4efb\u4f55\u610f\u89c1\uff0c\u4f1a\u8bae\u539f\u5219\u4e0a\u901a\u8fc7\u7b56\u5212\u6848\u8349\u7a3f\u5c31\u8fdb\u5165\u7b79\u5907\u9636\u6bb5\uff0c\u5c31\u9700\u8981\u5217\u51fa\u9884\u7b97\u62db\u5175\u4e70\u9a6c\u7ec4\u5efa\u56e2\u961f\u4e0e\u4e1c\u6d77\u7701\u536b\u89c6\u6d3d\u8c08\u5408\u4f5c\u4e8b\u5b9c\u2014\u2014\u5185\u5730\u8fd8\u4e0d\u5141\u8bb8\u79c1\u4eba\u6216\u6c11\u8425\u673a\u6784\u6295\u8d44\u7535\u89c6\u9891\u9053\uff0c\u9009\u79c0\u8282\u76ee\u8fd8\u662f\u8981\u8ddf\u5730\u65b9\u6216\u4e2d\u592e\u7535\u89c6\u53f0\u5408\u4f5c\u8fdb\u884c\u2014\u2014\u5b59\u9759\u6aac\u4e5f\u7b97\u662f\u6709\u4e86\u81ea\u5df1\u7684\u4e8b\u4e1a\u9700\u8981\u53bb\u64cd\u6301\u3002</p><p>\u3000\u3000\u4e0a\u5348\u4f1a\u8bae\u7ed3\u675f\u65f6\uff0c\u5f20\u606a\u4e0d\u6ce8\u610f\u7ed9\u9759\u6aac\u72e0\u72e0\u7684\u6390\u4e86\u4e00\u8bb0\uff0c\u867d\u7136\u7ed9\u6390\u5f97\u6b7b\u75bc\uff0c\u5374\u8868\u660e\u60c5\u51b5\u90fd\u8fd8\u6b63\u5e38\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u5f20\u4e9a\u5e73\u4e2d\u5348\u4e4b\u524d\u90fd\u9f50\u805a\u5efa\u90ba\uff0c\u5f20\u4e9a\u5e73\u4e0e\u4e01\u69d0\u4e0b\u5348\u90fd\u6709\u4e8b\u5fc5\u987b\u8981\u79bb\u5f00\u5efa\u90ba\uff0c\u5f20\u606a\u53ea\u80fd\u8fb9\u7528\u9910\u8fb9\u8c08\u516c\u4e8b\u3002</p><p>\u3000\u3000\u6628\u5929\u5b59\u5fd7\u521a\u767b\u95e8\u62dc\u8bbf\u4e00\u4e8b\u4f20\u8fbe\u4e86\u5f88\u591a\u7684\u4fe1\u606f\uff0c\u4e0d\u7ba1\u5b59\u5fd7\u521a\u8ddf\u845b\u5efa\u5fb7\u6709\u6ca1\u6709\u51b3\u88c2\uff0c\u53ea\u8981\u9526\u6e56\u4e0e\u8054\u8baf\u5408\u4f5c\uff0c\u4ee5\u845b\u5efa\u5fb7\u7684\u72ed\u7a84\u5fc3\u80f8\uff0c\u52bf\u5fc5\u4e0d\u80fd\u5bb9\u5b59\u5fd7\u521a\u3002\u5b59\u5fd7\u521a\u662f\u806a\u660e\u4eba\uff0c\u5e94\u8be5\u77e5\u9053\u5230\u65f6\u5019\u8981\u600e\u4e48\u9009\u62e9\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u9526\u6e56\u4e0e\u8054\u901a\u3001\u8054\u4fe1\u4ee5\u53ca\u79d1\u738b\u9ad8\u79d1\u6709\u79c1\u4e0b\u534f\u8bae\uff0c\u4e0d\u53ef\u80fd\u5411\u8054\u8baf\u5927\u6279\u91cf\u7684\u63d0\u4f9bESS\u57fa\u9891\u82af\u7247\u5e2e\u52a9\u8054\u8baf\u964d\u4f4e\u5236\u9020\u6210\u672c\uff0c\u5f20\u606a\u5c06\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u5f20\u4e9a\u7ea6\u8fc7\u6765\uff0c\u5c31\u662f\u8981\u4ed6\u4eec\u5546\u8bae\u7740\u80fd\u6709\u4ec0\u4e48\u529e\u6cd5\u6258\u8054\u8baf\u4e00\u628a\u3002</p><p>\u3000\u3000\u5230\u4e86\u4e0b\u5348\uff0c\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u5373\u4f7f\u4e0d\u6025\u7740\u79bb\u5f00\u5efa\u90ba\uff0c\u4e5f\u6709\u5fd9\u4e0d\u5b8c\u7684\u4e8b\u60c5\uff0c\u5374\u662f\u5f20\u606a\u8ddf\u53f6\u5efa\u658c\u6bd4\u8f83\u60a0\u95f2\uff0c\u968f\u610f\u5728\u5b66\u5e9c\u5df7\u627e\u4e86\u4e00\u5bb6\u5496\u5561\u5385\u559d\u8336\u3002</p><p>\u3000\u3000\u201c\u845b\u5efa\u5fb7\u771f\u662f\u788d\u773c\uff0c\u8981\u80fd\u6709\u673a\u4f1a\u5c06\u4ed6\u6273\u5012\uff0c\u5c31\u4e0d\u5e94\u8be5\u72b9\u8c6b\u2026\u2026\u201d\u53f6\u5efa\u658c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u81ed\u866b\u4e00\u4e2a\u7f62\u4e86\uff0c\u8fd9\u4e48\u5e74\u4ed6\u6709\u5bf9\u9526\u6e56\u5f62\u6210\u5a01\u80c1\u5417\uff1f\u4e0d\u8fc7\u81ed\u866b\u5728\u773c\u524d\u8df3\u6765\u8df3\u53bb\uff0c\u603b\u662f\u8ba9\u4eba\u751f\u538c\uff0c\u201d\u5f20\u606a\u7b11\u9053\uff0c\u201c\u81f3\u5c11\u4eca\u5e74\u4e0b\u534a\u5e74\u7684\u5f62\u52bf\u5df2\u7ecf\u80fd\u770b\u51fa\u4e2a\u4e03\u4e03\u516b\u516b\uff0c\u65e0\u8bba\u5b59\u5fd7\u521a\u505a\u4ec0\u4e48\u9009\u62e9\uff0c\u845b\u5efa\u5fb7\u60f3\u7ee7\u7eed\u5750\u7a33\u4ed6\u534e\u590f\u7535\u5b50\u638c\u95e8\u4eba\u7684\u4f4d\u5b50\uff0c\u8fd8\u662f\u8981\u82b1\u4e0a\u4e00\u4e9b\u5fc3\u8840\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u662f\u7ee7\u7eed\u4efb\u4ed6\u8e66\u8fbe\u4e9b\u65f6\u95f4\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u5c06\u845b\u5efa\u5fb7\u8d76\u4e0b\u53bb\uff0c\u534e\u590f\u7535\u5b50\u4eca\u5e74\u4e1a\u7ee9\u5927\u6ed1\u5761\u7684\u9ed1\u9505\u8c01\u6765\uff1f\u201d\u5f20\u606a\u5634\u89d2\u6302\u7740\u6de1\u6de1\u7684\u6d45\u7b11\uff0c\u4ed6\u77e5\u9053\u8054\u8baf\u8ddf\u4e1c\u5174\u7684\u5e93\u5b58\u91cf\uff0c\u80fd\u8ba1\u7b97\u51fa\u8054\u8baf\u4e0e\u4e1c\u5174\u4eca\u5e74\u5f88\u53ef\u80fd\u4f1a\u53d1\u751f\u5e74\u5ea6\u4e8f\u635f\uff0c\u201c\u8981\u8ba9\u4ed6\u6eda\u86cb\uff0c\u81f3\u5c11\u4e5f\u8981\u8ba9\u4ed6\u80cc\u4e86\u8fd9\u4e2a\u9ed1\u9505\u518d\u8d70\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u770b\u7740\u5f20\u606a\u6447\u5934\u800c\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u845b\u5efa\u5fb7\u77e5\u9053\u4f60\u6574\u5929\u7422\u78e8\u7740\u600e\u4e48\u7ed9\u4ed6\u4e0b\u5957\u3001\u4f7f\u7eca\uff0c\u53ea\u8981\u4ed6\u4e00\u5929\u90fd\u7761\u4e0d\u8e0f\u5b9e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u845b\u5efa\u5fb7\u80cc\u540e\u7684\u6c34\u5f88\u6d51\u2014\u2014\u4f60\u77e5\u9053\u7cbe\u5178\u7684\u6797\u96ea\u8ddf\u845b\u5efa\u5fb7\u4ec0\u4e48\u5173\u7cfb\u4e0d\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u73b0\u5728\u80fd\u542c\u5230\u7684\u516b\u5366\u6d88\u606f\u4e5f\u5c11\u4e86\uff0c\u4ed6\u4eec\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u53f6\u5efa\u658c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u4e5f\u662f\u9053\u542c\u9014\u8bf4\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6797\u96ea\u662f\u845b\u5efa\u5fb7\u59bb\u4f84\u5973\uff0c\u845b\u5efa\u5fb7\u5341\u5e74\u524d\u5728\u5efa\u90ba\u6302\u804c\u65f6\uff0c\u4ed6\u8001\u5a46\u6ca1\u80fd\u966a\u4ed6\u8fc7\u6765\uff0c\u53c8\u62c5\u5fc3\u4ed6\u5728\u5efa\u90ba\u4e71\u641e\uff0c\u5c31\u6d3e\u6797\u96ea\u7ed9\u845b\u5efa\u5fb7\u5f53\u4fdd\u59c6\u76d1\u89c6\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e0d\u662f\u5c06\u8089\u5f80\u845b\u5efa\u5fb7\u5634\u91cc\u585e\uff1f\u201d\u53f6\u5efa\u658c\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u4e8b\u60c5\u7684\u53d1\u5c55\u4e5f\u6ca1\u6709\u51fa\u4e4e\u5e38\u4eba\u7684\u610f\u6599\u2014\u2014\u53ea\u4e0d\u8fc7\u8fd9\u4e9b\u5e74\u6765\u6211\u4eec\u90fd\u53ea\u770b\u5230\u6797\u96ea\u727a\u7272\u8272\u76f8\u4f9d\u9760\u845b\u5efa\u5fb7\u8fd9\u68f5\u5927\u6811\u4e58\u51c9\uff0c\u4f46\u662f\u5979\u7559\u5728\u5efa\u90ba\u6210\u7acb\u7cbe\u5178\u5730\u4ea7\u524d\u671f\u4e5f\u9887\u4e3a\u8f9b\u82e6\u2014\u2014\u5979\u6ca1\u6709\u8ddf\u7740\u845b\u5efa\u5fb7\u56de\u5317\u4eac\u76f4\u63a5\u5229\u7528\u4ed6\u7684\u6743\u529b\u655b\u8d22\u2014\u2014\u4e5f\u8bb8\u5f53\u521d\u53e6\u6709\u9690\u60c5\u4e5f\u8bf4\u4e0d\u5b9a\uff1f\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e0d\u7ba1\u5f53\u521d\u662f\u4e0d\u662f\u6709\u9690\u60c5\uff0c\u4f60\u770b\u8fd9\u5973\u7684\u8fd9\u4e9b\u5e74\u505a\u7684\u8fd9\u4e9b\u4e8b\u2026\u2026\u201d\u53f6\u5efa\u658c\u5bf9\u6797\u96ea\u53ef\u6ca1\u6709\u4e1d\u6beb\u7684\u540c\u60c5\uff0c\u8fc7\u4e86\u7247\u523b\u53c8\u8bf4\uff0c\u201c\u4f60\u8bf4\u662f\u6797\u96ea\u8fd9\u65f6\u5019\u4f1a\u7f20\u4f4f\u845b\u5efa\u5fb7\u8981\u4ed6\u66ff\u81ea\u5df1\u89e3\u51b3\u773c\u4e0b\u7684\u5371\u5c40\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u53ef\u80fd\u2026\u2026\u201d\u5f20\u606a\u70b9\u70b9\u5934\u3002</p></article>"}}}]);
