(window.webpackJsonp=window.webpackJsonp||[]).push([[1187],{1210:function(p){p.exports={chapterTitle:null,title:"\u7b2c977\u7ae0 \u94d0\u56de\u53bb\u534f\u52a9\u8c03\u67e5",content:"<article><h2>\u7b2c977\u7ae0 \u94d0\u56de\u53bb\u534f\u52a9\u8c03\u67e5</h2><p>\u3000\u3000\u706b\u8f66\u4ece\u6dee\u6c82\u8fdb\u5165\u4e1c\u6d77\u7701\u5883\u5185\uff0c\u5929\u5149\u6e10\u660e\uff0c\u9752\u5e7d\u5e7d\u7684\u6668\u5149\u900f\u8fdb\u6765\uff0c\u5f80\u5916\u671b\u51fa\u53bb\uff0c\u56e2\u5982\u68c9\u7d6e\u7684\u96ea\u82b1\u98d8\u7136\u800c\u81f3\uff0c\u9752\u8272\u7684\u9ea6\u7530\u4e0e\u8fdc\u5904\u7070\u767d\u8272\u7684\u805a\u5c45\u533a\u90fd\u7b3c\u7f69\u5728\u51ac\u5b63\u7684\u521d\u96ea\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0e\u674e\u99a8\u4e88\u90fd\u62e5\u88ab\u5750\u4e86\u8d77\u6765\uff0c\u4e24\u5f20\u767d\u7699\u8138\u86cb\u51d1\u5230\u8f66\u7a97\u524d\u770b\u5916\u9762\u7684\u96ea\u3002</p><p>\u3000\u3000\u201c\u4e0d\u77e5\u9053\u4e0b\u4e86\u591a\u4e45\u4e86\uff0c\u5230\u5efa\u90ba\uff0c\u8bf4\u4e0d\u5b9a\u5730\u9762\u90fd\u7ed9\u96ea\u8986\u76d6\u4e86\u2026\u2026\u201d\u9999\u6e2f\u7684\u51ac\u5b63\u751a\u81f3\u90fd\u65e0\u6cd5\u7ed9\u4eba\u5165\u79cb\u7684\u611f\u89c9\uff0c\u5510\u5a67\u8fd8\u662f\u5728\u559c\u6b22\u4e1c\u6d77\u7684\u51ac\u5b63\uff0c\u5b63\u8282\u5206\u660e\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u50cf\u5317\u65b9\u90a3\u4e48\u9177\u5bd2\uff0c\u56de\u5efa\u90ba\u7684\u7b2c\u4e00\u5929\u5c31\u8fce\u6765\u521d\u96ea\u5929\u6c14\uff0c\u8ba9\u5979\u9887\u4e3a\u5174\u594b\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u60f3\u8d77\u4e24\u4eba\u5728\u5317\u4eac\u7684\u9189\u9152\u89c2\u96ea\uff0c\u4e0d\u77e5\u9053\u4f55\u65f6\u518d\u6709\u8fd9\u6837\u7684\u673a\u7f18\uff0c\u5c06\u88ab\u5b50\u88f9\u7d27\u4e86\u4e00\u4e9b\uff0c\u4e13\u6ce8\u7684\u770b\u7740\u8f66\u7a97\u5916\u7684\u96ea\u4e0e\u5927\u7247\u7684\u7530\u91ce\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5728\u540c\u4e00\u5730\u533a\uff0c\u7ecf\u6d4e\u53d1\u5c55\u7684\u60c5\u51b5\u4e5f\u5927\u4e3a\u4e0d\u540c\uff0c\u6dee\u6c82\u5e02\u4f4d\u4e8e\u4e1c\u6d77\u7701\u4e1c\u5317\u90e8\u6cbf\u6d77\uff0c\u7ecf\u6d4e\u6761\u4ef6\u76f8\u6bd4\u5357\u8fb9\u7684\u6d77\u5dde\u3001\u60e0\u5c71\u8981\u5dee\u8bb8\u591a\uff0c\u5de5\u4e1a\u57fa\u7840\u6bd4\u4e1c\u6d77\u7701\u7684\u5185\u9646\u57ce\u5e02\u65b0\u829c\u8fd8\u8981\u7a0d\u5f31\u4e00\u4e9b\uff0c\u706b\u8f66\u7ecf\u8fc7\uff0c\u80fd\u770b\u5230\u5927\u7247\u8fde\u5728\u4e00\u7247\u7684\u7530\u91ce\uff0c\u706b\u8f66\u6240\u7ecf\u8fc7\u7684\u57ce\u5e02\u8fb9\u7f18\u5730\u5e26\u4e5f\u770b\u4e0d\u5230\u6709\u73b0\u4ee3\u90fd\u5e02\u7684\u6c14\u606f\uff0c\u591c\u884c\u8f66\u751a\u81f3\u4e0d\u4f1a\u5728\u6dee\u6c82\u7ad9\u505c\u9760\u3002</p><p>\u3000\u3000\u770b\u7740\u8f66\u7a97\u5916\u706b\u8f66\u6240\u884c\u7ecf\u7684\u5730\u533a\uff0c\u5f20\u606a\u5bf9\u4e1c\u6d77\u7701\u5185\u7684\u533a\u57df\u7ecf\u6d4e\u53d1\u5c55\u72b6\u51b5\u5c31\u4f1a\u6709\u66f4\u76f4\u89c2\u7684\u5370\u8c61\uff0c\u867d\u7136\u6dee\u6c82\u4e34\u6d77\uff0c\u4f46\u79bb\u5c0f\u6c5f\u6d41\u57df\u7684\u4e2d\u5fc3\u533a\u57df\u8f83\u8fdc\uff0c\u6d77\u6e2f\u6761\u4ef6\u4e5f\u4e0d\u7406\u60f3\uff0c\u5de5\u4e1a\u57fa\u7840\u53c8\u5f88\u8584\u5f31\uff0c\u96be\u514d\u8981\u7ed9\u8fb9\u7f18\u5316\u4e86\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u89c1\u5f20\u606a\u671b\u7740\u7a97\u5916\u82e5\u6709\u6240\u601d\u53c8\u968f\u624b\u5728\u672c\u5b50\u5199\u7740\u4ec0\u4e48\uff0c\u8111\u888b\u5fae\u4fa7\u8fc7\u6765\uff0c\u770b\u4ed6\u5728\u672c\u5b50\u4e0a\u5199\u4e9b\u4ec0\u4e48\uff0c\u53ea\u662f\u5f20\u606a\u7684\u5b57\u8ff9\u6f66\u8349\uff0c\u867d\u8bf4\u6709\u7740\u8bf4\u4e0d\u51fa\u7684\u7f8e\u611f\uff0c\u5374\u4e0d\u597d\u8fa9\u8ba4\uff0c\u53ea\u662f\u6de1\u6de1\u7684\u5fae\u7b11\uff0c\u4e5f\u4e0d\u4f1a\u7ec6\u95ee\u4ed6\u5230\u5e95\u5728\u5199\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u6216\u8bb8\u662f\u98ce\u96ea\u7684\u7f18\u6545\uff0c\u706b\u8f66\u5230\u5efa\u90ba\u7684\u65f6\u95f4\u7a0d\u5ef6\u8fdf\u4e86\u4e00\u4e9b\uff0c\u8fdb\u7ad9\u5df2\u7ecf\u8fc7\u516b\u70b9\u4e86\uff0c\u5f20\u606a\u4ed6\u4eec\u6d17\u6f31\u5b8c\u6bd5\u6536\u62fe\u8fc7\u884c\u674e\u51c6\u5907\u4e0b\u8f66\uff0c\u8fd9\u65f6\u5019\u624d\u60f3\u8d77\u6765\u95ee\u5085\u4fca\u6628\u5929\u591c\u91cc\u4e8b\u60c5\u5904\u7406\u7684\u60c5\u51b5\u3002</p><p>\u3000\u3000\u201c\u4e58\u8b66\u5728\u9910\u8f66\u91cc\u5c06\u4ed6\u4eec\u6279\u8bc4\u6559\u80b2\u4e86\u4e00\u901a\u2026\u2026\u770b\u4ed6\u4eec\u5012\u4e0d\u50cf\u662f\u63a5\u53d7\u4e86\u6279\u8bc4\u3002\u201d\u5085\u4fca\u8bf4\u9053\u3002</p><p>\u3000\u3000\u7279\u6743\u4e5f\u662f\u6709\u9650\u5ea6\u7684\uff0c\u4f55\u51b5\u5bf9\u65b9\u5e76\u6ca1\u6709\u505a\u51fa\u5b9e\u8d28\u6027\u7684\u4f24\u5bb3\u884c\u4e3a\uff0c\u4e5f\u53d7\u5230\u4e9b\u6559\u8bad\uff1b\u5982\u4eca\u7684\u793e\u4f1a\uff0c\u518d\u5927\u7684\u7279\u6743\uff0c\u4e5f\u4e0d\u80fd\u56e0\u4e3a\u4eba\u77aa\u4e86\u4e00\u773c\u6216\u8005\u8bf4\u8a00\u8bed\u4e0a\u6709\u6240\u4e0d\u656c\u5c31\u53ef\u4ee5\u8086\u65e0\u5fcc\u60ee\u7684\u5c06\u4eba\u4e00\u811a\u8e29\u6b7b\u3002\u8fd9\u5e74\u5934\u7a0d\u6709\u4e9b\u6743\u52bf\uff0c\u5c31\u5f53\u8001\u5b50\u662f\u5929\u4e0b\u7b2c\u4e00\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u653e\u5728\u5fc3\u91cc\uff0c\u53ea\u5f53\u4e8b\u60c5\u5c31\u8fd9\u6837\u89e3\u51b3\u4e86\u3002\u706b\u8f66\u8fdb\u5165\u5efa\u90ba\u5357\u7ad9\u540e\uff0c\u4ed6\u4eec\u90fd\u7ad9\u5230\u8fc7\u9053\u91cc\u7b49\u7740\u4e0b\u8f66\uff0c\u6ca1\u6709\u770b\u5230\u6628\u5929\u591c\u91cc\u7684\u90a3\u4e24\u4e2a\u957f\u77ed\u53d1\u9752\u5e74\uff0c\u6216\u8bb8\u5df2\u7ecf\u4e0b\u4e86\u8f66\uff0c\u6216\u8bb8\u53bb\u4e86\u5176\u4ed6\u8f66\u53a2\u2014\u2014\u4ed6\u4eec\u540c\u4f34\u6240\u5728\u7684\u8f66\u53a2\u3002</p><p>\u3000\u3000\u9ebb\u70e6\u8fd8\u662f\u4e0b\u8f66\u540e\u51fa\u8f66\u7ad9\u524d\u7f20\u4e0a\u6765\uff0c\u5f20\u606a\u4e3a\u514d\u51fa\u7ad9\u592a\u62e5\u6324\uff0c\u62d6\u540e\u4e9b\u624d\u4e0b\u8f66\uff0c\u8d70\u5230\u51fa\u7ad9\u53e3\u524d\u90fd\u770b\u5230\u5efa\u90ba\u8fd9\u8fb9\u5230\u8f66\u7ad9\u6765\u63a5\u7684\u53f8\u673a\u4e86\uff0c\u8fd9\u65f6\u5019\u77ed\u53d1\u9752\u5e74\u9886\u4e24\u540d\u8b66\u5bdf\u4ece\u65c1\u8fb9\u671d\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u4e00\u8138\u7684\u56a3\u5f20\uff0c\u77aa\u773c\u6307\u7740\u5f20\u606a\uff1b\u201c\u4f60\u4ed6\u5988\u7684\u518d\u7ed9\u8001\u5b50\u725b\u6bd4\uff01\u201d\u5bf9\u7740\u4e24\u540d\u8b66\u5bdf\u53d1\u53f7\u53f8\u4ee4\uff0c\u201c\u5c06\u4ed6\u4eec\u90fd\u94d0\u56de\u6d3e\u51fa\u6240\u53bb\uff0c\u738b\u603b\u5c31\u662f\u7ed9\u8fd9\u5bb6\u4f19\u4e00\u811a\u8e39\u809a\u5b50\u7684\u2026\u2026\u8fd9\u53eb\u4ec0\u4e48\u6765\u7740\uff0c\u662f\u4e0d\u662f\u53eb\u534f\u52a9\u8c03\u67e5\uff1f\u628a\u4ed6\u4eec\u90fd\u94d0\u56de\u53bb\u534f\u52a9\u8c03\u67e5\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7709\u5934\u5fae\u8e59\u7740\uff0c\u6ca1\u60f3\u5230\u9047\u4e0a\u8fd9\u6837\u6b7b\u7f20\u70c2\u6253\u7684\u5bb6\u4f19\uff0c\u8981\u662f\u6362\u4f5c\u5176\u4ed6\u4eba\uff0c\u5c82\u4e0d\u662f\u8981\u7ed9\u6b3a\u8d1f\u5f88\u60e8\uff1f\u771f\u662f\u53ef\u6068\u3002</p><p>\u3000\u3000\u90a3\u4e2a\u81ea\u79f0\u662f\u4ec0\u4e48\u201c\u738b\u603b\u201d\u7684\u957f\u53d1\u9752\u5e74\u4e0e\u4e00\u540d\u8b66\u5bdf\u7ad9\u5728\u8fdc\u5904\u770b\u7740\u8fd9\u8fb9\uff0c\u770b\u90a3\u540d\u8b66\u5bdf\u7684\u80a9\u7ae0\u662f\u4e8c\u7ea7\u8b66\u7763\uff0c\u5e94\u8be5\u662f\u4e2a\u6d3e\u51fa\u6240\u6240\u957f\u4e4b\u7c7b\u7684\u89d2\u8272\uff0c\u5f20\u606a\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u638f\u51fa\u624b\u673a\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u662f\u8f66\u7ad9\u6d3e\u51fa\u6240\u7684\u6c11\u8b66\u8fd8\u662f\u65b0\u6885\u56ed\u6d3e\u51fa\u6240\u7684\uff1f\u201d\u5085\u4fca\u8d70\u8fc7\u6765\u6321\u5728\u6c11\u8b66\u4e0e\u5f20\u606a\u8fd8\u6709\u5510\u5a67\u3001\u674e\u99a8\u4e88\u4e4b\u95f4\uff0c\u4ed6\u603b\u4e0d\u80fd\u8ba9\u5f20\u606a\u4ed6\u4eec\u53d7\u534a\u70b9\u59d4\u5c48\uff0c\u8981\u8bf4\u8b66\u5bdf\uff0c\u968f\u884c\u7684\u5b89\u5168\u4e8b\u52a1\u52a9\u7406\u5c31\u6709\u4e00\u4eba\u662f\u5728\u804c\u8b66\u5bdf\u7684\u8eab\u4efd\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u591a\u5e9f\u8bdd\u505a\u4ec0\u4e48\uff0c\u6211\u4eec\u63a5\u5230\u62a5\u8b66\uff0c\u8bf7\u4f60\u4eec\u56de\u6240\u91cc\u534f\u52a9\u8c03\u67e5\uff0c\u4f60\u4eec\u8001\u5b9e\u70b9\u914d\u5408\u7740\uff0c\u4e0d\u8981\u81ea\u8ba8\u82e6\u5403\u3002\u201d\u4e00\u540d\u6c11\u8b66\u5389\u58f0\u8bad\u65a5\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u62cd\u4e86\u62cd\u5085\u4fca\u7684\u80a9\u8180\uff0c\u8ba9\u4ed6\u8ba9\u5f00\u4e9b\uff0c\u5c06\u624b\u673a\u9012\u7ed9\u6c11\u8b66\uff0c\u6307\u7740\u8fdc\u8fdc\u7ad9\u5728\u4e00\u65c1\u7684\u4e8c\u7ea7\u8b66\u7763\uff1a\u201c\u8bf7\u4f60\u4eec\u7684\u9886\u5bfc\u63a5\u4e00\u4e0b\u8fd9\u4e2a\u7535\u8bdd\uff01\u6211\u4eec\u8ddf\u4f60\u4eec\u53bb\u6d3e\u51fa\u6240\u63a5\u53d7\u8c03\u67e5\uff0c\u4f60\u4eec\u60f3\u600e\u4e48\u8c03\u67e5\u90fd\u6210\uff01\u201d\u53c8\u9634\u6c89\u7740\u8138\u8865\u5145\u4e86\u4e00\u53e5\uff0c\u201c\u4f60\u4eec\u60f3\u5c06\u6211\u4eec\u94d0\u56de\u53bb\u8c03\u67e5\u4e5f\u884c\u3002\u201d</p><p>\u3000\u3000\u8bf4\u5230\u5e95\uff0c\u4e24\u6c11\u8b66\u8fd8\u771f\u4e0d\u6562\u94d0\u4eba\uff0c\u53ea\u4e0d\u8fc7\u60f3\u627e\u4e9b\u6a21\u51cc\u4e24\u53ef\u7684\u540d\u4e49\u5201\u96be\u4e00\u4e0b\uff0c\u518d\u8bf4\u4ed6\u4eec\u4e5f\u4e0d\u662f\u8f66\u7ad9\u6d3e\u51fa\u6240\u7684\u6c11\u8b66\uff0c\u65b0\u6885\u56ed\u6d3e\u51fa\u6240\u7684\u7ba1\u8f96\u8303\u56f4\u5728\u5357\u7ad9\u4e4b\u5916\u3002\u8fd9\u4e48\u505a\u4e5f\u662f\u5e2e\u957f\u6797\u516c\u53f8\u7684\u738b\u5efa\u6797\u627e\u56de\u4e9b\u989c\u9762\u3001\u51fa\u53e3\u6076\u6c14\uff0c\u4ed6\u4eec\u770b\u5230\u8fd9\u8fb9\u4e24\u4e2a\u5999\u9f84\u5973\u90ce\u7684\u7edd\u7f8e\u5bb9\u989c\uff0c\u5fc3\u91cc\u90fd\u60f3\u6307\u4e0d\u5b9a\u738b\u5efa\u6797\u7684\u4e3b\u8981\u610f\u56fe\u8fd8\u662f\u8981\u7ea0\u7f20\u8fd9\u4e24\u4eba\uff0c\u8fd9\u65f6\u5019\u89c1\u5f20\u606a\u9012\u8fc7\u624b\u673a\u6765\uff0c\u51b7\u5cfb\u7684\u795e\u8272\u4e0e\u6c14\u52bf\u4e5f\u538b\u4eba\uff0c\u4e5f\u4e0d\u6562\u968f\u4fbf\u7c97\u66b4\u7684\u62d2\u7edd\uff0c\u771f\u8e22\u5230\u94c1\u677f\u4e0a\u5c31\u6ca1\u6709\u8f6c\u571c\u7684\u4f59\u5730\u4e86\uff0c\u63a5\u8fc7\u624b\u673a\u5c31\u6298\u4e86\u56de\u6765\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff01\u201d\u65b0\u6885\u56ed\u6d3e\u51fa\u6240\u6240\u957f\u6210\u5bcc\u6cbb\u89c1\u624b\u4e0b\u6c11\u8b66\u62ff\u7740\u4eba\u5bb6\u7684\u624b\u673a\u6298\u56de\u6765\uff0c\u89c1\u90a3\u53ea\u624b\u673a\u4e5f\u4e0d\u600e\u4e48\u6837\u7684\u9ad8\u6863\uff0c\u6709\u4e9b\u4e0d\u8010\u70e6\u7684\u7709\u5934\u5fae\u76b1\u8d77\u6765\uff0c\u201c\u600e\u4e48\u8fd9\u4e48\u591a\u5e9f\u4e8b\uff0c\u4ee5\u4e3a\u968f\u4fbf\u6258\u4e2a\u5173\u7cfb\u5c31\u80fd\u5c06\u624b\u4f38\u5230\u65b0\u6885\u56ed\u6765\uff1f\u201d\u5634\u91cc\u9a82\u9a82\u54a7\u54a7\u7684\u63a5\u8fc7\u624b\u673a\uff0c\u201c\u5582\uff0c\u4f60\u662f\u54ea\u4f4d\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u662f\u9648\u79c9\u5fb7\uff0c\u4f60\u662f\u54ea\u4f4d\uff1f\u201d\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\u6709\u7740\u538b\u6291\u6124\u6012\u7684\u9634\u6c89\u3002</p><p>\u3000\u3000\u201c\u9648\u5c40\u957f\u2026\u2026\u201d\u6210\u5bcc\u6cbb\u624b\u91cc\u4e00\u54c6\u55e6\uff0c\u4ed6\u5728\u5168\u5e02\u516c\u5b89\u7cfb\u7edf\u7684\u5927\u4f1a\u4e0a\u542c\u8fc7\u9648\u79c9\u5fb7\u505a\u62a5\u544a\u7684\u58f0\u97f3\uff0c\u201c\u6211\u662f\u65b0\u6885\u56ed\u6d3e\u51fa\u6240\u7684\u6240\u957f\u6210\u5bcc\u6cbb\uff0c\u63a5\u5230\u62a5\u8b66\u624d\u8bf7\u4eba\u56de\u53bb\u534f\u52a9\u8c03\u67e5\uff0c\u4e0d\u77e5\u9053\u4ed6\u4eec\u8ddf\u9648\u5c40\u957f\u60a8\u8ba4\u8bc6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c01\u7ed9\u4f60\u6743\u529b\u8fdb\u706b\u8f66\u7ad9\u94d0\u4eba\uff01\u201d\u9648\u79c9\u5fb7\u524d\u4e00\u53e5\u8fd8\u514b\u5236\uff0c\u63a5\u4e0b\u6765\u4e00\u53e5\u8bdd\u5c31\u5728\u7535\u8bdd\u5486\u54ee\u8d77\u6765\uff0c\u201c\u8c01\u4ed6\u5988\u7ed9\u4f60\u5403\u4e86\u8c79\u5b50\u80c6\uff01\u5403\u9971\u4e86\u6491\u4e0d\u6b7b\u4f60\u4eec\u3002\u201d</p><p>\u3000\u3000\u5486\u54ee\u58f0\u4e4b\u5927\uff0c\u751a\u81f3\u8ba9\u738b\u5efa\u6797\u5728\u55a7\u95f9\u7684\u51fa\u8f66\u7ad9\u90fd\u9690\u7ea6\u542c\u5230\u5486\u54ee\u7684\u5185\u5bb9\u3002</p><p>\u3000\u3000\u6210\u5bcc\u6cbb\u9762\u8272\u715e\u767d\uff0c\u4ece\u9648\u79c9\u5fb7\u4e0d\u52a0\u514b\u5236\u7684\u6124\u6012\u91cc\u77e5\u9053\u8fd9\u6b21\u8e22\u7684\u94c1\u677f\u6709\u591a\u4e25\u91cd\uff0c\u867d\u7136\u65b0\u6885\u56ed\u9644\u8fd1\u7684\u5730\u5934\u86c7\u90fd\u8981\u770b\u4ed6\u6210\u5bcc\u6cbb\u7684\u8138\u8272\uff0c\u4f46\u5728\u9648\u79c9\u5fb7\u9762\u524d\uff0c\u4ed6\u8fde\u53ea\u5c0f\u6ce5\u7a23\u90fd\u7b97\u4e0d\u4e0a\uff0c\u53ea\u60f3\u7740\u6d17\u8131\u5e72\u7cfb\uff0c\u98a4\u58f0\u89e3\u91ca\u9053\uff1a\u201c\u6211\u4eec\u63a5\u5230\u957f\u6797\u516c\u53f8\u7684\u738b\u603b\u62a5\u8b66\uff0c\u957f\u6797\u516c\u53f8\u7684\u738b\u603b\u8bf4\u6628\u591c\u5728\u706b\u8f66\u91cc\u7ed9\u5bf9\u65b9\u56f4\u6bb4\uff01\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u738b\u603b\uff0c\u4e0d\u8ba4\u8bc6\uff01\u4ed6\u8fd9\u662f\u6076\u610f\u62a5\u8b66\uff0c\u628a\u4ed6\u5988\u7684\u5148\u94d0\u8d77\u6765\uff0c\u7b49\u6211\u8ddf\u4f60\u4eec\u533a\u5206\u5c40\u7684\u94b1\u6842\u534e\u8fc7\u6765\u5904\u7406\uff01\u201d</p><p>\u3000\u3000\u6210\u5bcc\u6cbb\u60f3\u89e3\u91ca\u4e00\u4e0b\u738b\u5efa\u6797\u5c31\u662f\u533a\u5206\u5c40\u5c40\u957f\u94b1\u6842\u534e\u7684\u5c0f\u8205\u5b50\uff0c\u60f3\u60f3\u4e5f\u6ca1\u6709\u80c6\u91cf\u8bf4\uff0c\u8fd9\u4e00\u8bf4\uff0c\u4e0d\u662f\u660e\u663e\u5c06\u81ea\u5df1\u626f\u4e86\u8fdb\u6765\u5417\uff1f\u4ed6\u53c8\u4e0d\u6562\u771f\u5c31\u7ed9\u738b\u5efa\u6797\u4e0a\u94d0\uff0c\u60f3\u518d\u95ee\u6e05\u695a\u9648\u79c9\u5fb7\u5177\u4f53\u7684\u6307\u793a\uff0c\u9648\u79c9\u5fb7\u5728\u90a3\u5934\u5df2\u7ecf\u6302\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u6210\u5bcc\u6cbb\u770b\u7740\u624b\u91cc\u7684\u624b\u673a\uff0c\u624b\u811a\u90fd\u6709\u4e9b\u53d1\u51c9\u4e86\uff0c\u4e3a\u4e86\u4ee5\u540e\u80fd\u53bb\u738b\u5efa\u6797\u7684\u4f1a\u6240\u514d\u8d39\u900d\u9065\uff0c\u5374\u5c06\u4e00\u5757\u70e7\u5f97\u7ea2\u70ed\u7684\u94c1\u677f\u6293\u5728\u624b\u91cc\u3002</p><p>\u3000\u3000\u738b\u5efa\u6797\u770b\u5230\u6210\u5bcc\u6cbb\u63a5\u7535\u8bdd\u7684\u795e\u8272\uff0c\u4e5f\u5fc3\u865a\u8d77\u6765\uff0c\u95ee\u9053\uff1b\u201c\u662f\u54ea\u4e2a\u9648\u5c40\u957f\uff0c\u4e1c\u534e\u533a\u5c40\u7684\u9648\u4e00\u7ae0\uff0c\u8fd8\u662f\u65b0\u6d66\u7684\u9648\u8d35\u6625\uff1f\u201d</p><p>\u3000\u3000\u65b0\u6885\u56ed\u53c8\u4e0d\u5f52\u4e1c\u534e\u533a\u6216\u9ad8\u65b0\u533a\u7ba1\u8f96\uff0c\u9648\u4e00\u7ae0\u4e0e\u9648\u8d35\u6625\u51ed\u4ec0\u4e48\u5bf9\u8001\u5b50\u543c\u8fd9\u4e48\u5927\u58f0\uff1f\u6210\u5bcc\u6cbb\u4e5f\u65e0\u65f6\u95f4\u8ddf\u4ed6\u62b1\u6028\uff0c\u538b\u7740\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u9648\u79c9\u5fb7\u7684\u7535\u8bdd\u2026\u2026\u201d\u8bf4\u7f62\uff0c\u624b\u6324\u4e86\u6324\u8138\uff0c\u8ba9\u50f5\u786c\u7684\u8138\u677e\u8f6f\u4e00\u4e9b\uff0c\u4e5f\u5bb9\u6613\u6324\u51fa\u7b11\u5bb9\u6765\uff0c\u62ff\u7740\u624b\u673a\u671d\u5f20\u606a\u4ed6\u4eec\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u738b\u5efa\u6797\u6123\u5728\u90a3\u91cc\uff0c\u90fd\u6709\u4e9b\u53d1\u8499\uff0c\u4e0d\u8fc7\u8fd8\u5fc3\u5b58\u4fa5\u5e78\uff0c\u5fc3\u60f3\u4ed6\u59d0\u592b\u5e94\u8be5\u80fd\u6446\u5e73\u5173\u7cfb\uff0c\u5927\u4e0d\u4e86\u5230\u65f6\u5019\u7834\u8d39\u4e00\u4e9b\uff0c\u8ddf\u5728\u6210\u5bcc\u6cbb\u7684\u540e\u9762\uff0c\u60f3\u7740\u5148\u5c06\u6001\u5ea6\u8f6f\u4e0b\u6765\u3002</p><p>\u3000\u3000\u4e24\u540d\u65b0\u6885\u56ed\u6d3e\u51fa\u6240\u7684\u6c11\u8b66\u4ee5\u53ca\u77ed\u53d1\u9752\u5e74\u8fd8\u6709\u957f\u6797\u516c\u53f8\u8fd9\u6b21\u51fa\u5dee\u4e00\u8d77\u591a\u5317\u4eac\u56de\u6765\u7684\u804c\u5458\u8fd9\u65f6\u5019\u90fd\u77e5\u9053\u5bf9\u65b9\u7684\u6765\u5934\u771f\u7684\u5f88\u5927\uff0c\u81f3\u5c11\u4ed6\u4eec\u627e\u5230\u7684\u5173\u7cfb\u53ef\u4ee5\u8086\u65e0\u5fcc\u60ee\u7684\u5728\u7535\u8bdd\u5bf9\u6210\u5bcc\u6cbb\u7834\u53e3\u5927\u9a82\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u65b0\u6885\u56ed\u6d3e\u51fa\u6240\u7684\u6240\u957f\uff0c\u521a\u624d\u8bf4\u63a5\u5230\u62a5\u8b66\u662f\u573a\u8bef\u4f1a\uff0c\u6211\u4eec\u5df2\u7ecf\u7ed9\u9648\u5c40\u957f\u72e0\u72e0\u7684\u6559\u8bad\u8fc7\u4e86\uff0c\u4e5f\u6df1\u523b\u7684\u8ba4\u8bc6\u5230\u9519\u8bef\uff0c\u6b63\u51c6\u5907\u505a\u66f4\u6df1\u523b\u7684\u68c0\u8ba8\uff0c\u201d\u6210\u5bcc\u6cbb\u52aa\u529b\u8ba9\u81ea\u5df1\u7684\u58f0\u97f3\u542c\u4e0a\u53bb\u548c\u5a49\u4e00\u4e9b\uff0c\u5c06\u624b\u673a\u8fd8\u7ed9\u5f20\u606a\uff0c\u201c\u4f60\u662f\u4e0d\u662f\u7ed9\u9648\u5c40\u957f\u6253\u4e2a\u7535\u8bdd\u89e3\u91ca\u4e00\u4e0b\u8fd9\u91cc\u7684\u60c5\u51b5\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u624b\u673a\u53c8\u9707\u52a8\u8d77\u6765\uff0c\u5f20\u606a\u89c1\u662f\u9648\u79c9\u5fb7\u56de\u62e8\u8fc7\u6765\u7684\u7535\u8bdd\uff0c\u5c31\u6309\u4e86\u901a\u8bdd\u952e\u63a5\u901a\uff1a\u201c\u2026\u2026\u55ef\uff0c\u597d\u7684\uff0c\u6211\u5c31\u5728\u65b0\u6885\u56ed\u6d3e\u51fa\u6240\u7b49\u4f60\u2026\u2026\u201d\u6302\u4e86\u7535\u8bdd\uff0c\u770b\u4e86\u773c\u524d\u7684\u6210\u5bcc\u6cbb\u4e00\u773c\uff0c\u4e0d\u9634\u4e0d\u9633\u7684\u8bf4\u9053\uff0c\u201c\u6211\u8fd8\u662f\u968f\u4f60\u4eec\u56de\u6240\u91cc\u534f\u52a9\u8c03\u67e5\u5427\uff01\u201d\u56de\u5934\u8ddf\u5510\u5a67\u3001\u674e\u99a8\u4e88\u8bf4\u9053\uff0c\u201c\u6211\u8ba9\u53f8\u673a\u5148\u9001\u4f60\u4eec\u56de\u53bb\uff0c\u6211\u8fd8\u8981\u803d\u6401\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u6076\u4eba\u81ea\u6709\u6076\u4eba\u78e8\uff0c\u5f20\u606a\u4e0d\u4ecb\u610f\u505a\u56de\u6076\uff0c\u53ef\u4e00\u53ef\u4e8c\uff0c\u8fd9\u957f\u77ed\u53d1\u9752\u5e74\u5374\u518d\u800c\u4e09\u7684\u6b7b\u7f20\u70c2\u6253\u8fc7\u6765\uff0c\u53ef\u60f3\u800c\u77e5\u5f97\u7f6a\u4ed6\u4eec\u7684\u4eba\u4f1a\u906d\u5230\u600e\u4e48\u7684\u5f85\u9047\uff01</p><p>\u3000\u3000\u5f20\u606a\u575a\u6301\u8981\u8ddf\u7740\u56de\u6240\u91cc\uff0c\u518d\u8bf4\u9648\u79c9\u5fb7\u4e5f\u660e\u786e\u8bf4\u4e86\u8981\u4e0e\u533a\u5206\u5c40\u5c40\u957f\u94b1\u6842\u534e\u4e00\u8d77\u5230\u6d3e\u51fa\u6240\u6765\u5904\u7406\u8fd9\u4ef6\u4e8b\uff0c\u6210\u5bcc\u6cbb\u4e5f\u53ea\u80fd\u5c06\u6682\u65f6\u5c06\u9ebb\u70e6\u90fd\u63fd\u8fc7\u6765\uff0c\u60f3\u5230\u533a\u5206\u5c40\u5c40\u957f\u94b1\u6842\u534e\u603b\u5f52\u4f1a\u5e2e\u4ed6\u8bf4\u8bdd\u7684\uff0c\u6bd5\u7adf\u9ebb\u70e6\u662f\u4ed6\u5c0f\u8205\u5b50\u60f9\u6765\u7684\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u62c5\u5fc3\u3002\u4e00\u8d77\u51fa\u4e86\u51fa\u7ad9\u53e3\uff0c\u5efa\u90ba\u8fd9\u8fb9\u6765\u4e86\u4e24\u90e8\u8f66\u63a5\u5f20\u606a\u4ed6\u4eec\uff0c\u5085\u4fca\u5b89\u6392\u4e00\u90e8\u8f66\u9001\u674e\u99a8\u4e88\u4e0e\u5510\u5a67\u5148\u56de\u5b66\u5e9c\u5df7\uff0c\u8ba9\u53e6\u4e00\u90e8\u8f66\u8ddf\u5728\u4ed6\u4eec\u540e\u9762\uff0c\u4ed6\u966a\u5f20\u606a\u5750\u8b66\u8f66\uff0c\u4e5f\u96be\u5f97\u6709\u673a\u4f1a\u5750\u8b66\u8f66\u3002</p><p>\u3000\u3000\u6210\u5bcc\u6cbb\u770b\u7740\u8ddf\u7740\u8b66\u8f66\u540e\u9762\u7684\u5954\u9a70600\uff0c\u4e00\u9897\u51b0\u51c9\u7684\u5fc3\u76f4\u5f80\u4e0b\u6c89\uff1a\u8f66\u724c\u5012\u662f\u666e\u901a\uff0c\u53ef\u662f\u524d\u6321\u98ce\u73bb\u7483\u91cc\u8d34\u7740\u4e00\u5f20\u7279\u6b8a\u901a\u884c\u8bc1\uff0c\u8fd9\u5f20\u901a\u884c\u8bc1\u8981\u662f\u771f\u7684\uff0c\u90a3\u6574\u4e2a\u4e1c\u6d77\u7701\u90fd\u4e0d\u8d85\u8fc7\u4e8c\u5341\u5f20\u3002</p></article>"}}}]);
