(window.webpackJsonp=window.webpackJsonp||[]).push([[1189],{1212:function(p){p.exports={chapterTitle:null,title:"\u7b2c1186\u7ae0 \u4e0d\u8981\u505a\u50bb\u4e8b",content:"<article><h2>\u7b2c1186\u7ae0 \u4e0d\u8981\u505a\u50bb\u4e8b</h2><p>\u3000\u3000\u770b\u5230\u6c5f\u654f\u4e4b\u7684\u8f66\u5728\u524d\u9762\uff0c\u4e5f\u4e0d\u786e\u5b9a\u6c5f\u654f\u4e4b\u6709\u6ca1\u6709\u5750\u5728\u91cc\u9762\uff0c\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u4e0a\u7684\u5085\u4fca\u8fd8\u662f\u4f9d\u7167\u60ef\u4f8b\u8ba9\u53f8\u673a\u5f00\u6162\u4e00\u4e9b\uff0c\u56de\u5934\u8ddf\u5f20\u606a\u8bf4\u4e86\u4e00\u58f0\u3002\u5f20\u606a\u62ac\u5934\u65f6\u6b63\u770b\u5230\u524d\u9762\u7684\u5965\u8fea\u8f66\u51cf\u901f\u4e86\uff0c\u8fd8\u4ee5\u4e3a\u6c5f\u654f\u4e4b\u6709\u8bdd\u8ddf\u4ed6\u8bf4\uff0c\u5c31\u8ba9\u53f8\u673a\u5c06\u8f66\u5f00\u4e0a\u53bb\uff0c\u6ca1\u60f3\u5230\u5f00\u4e0a\u53bb\u4e4b\u540e\uff0c\u624d\u770b\u5230\u6c5f\u654f\u4e4b\u4e0d\u5728\u8f66\u91cc\u3002\u8f66\u91cc\u5750\u7740\u6c5f\u6e44\u8ddf\u4e00\u4e2a\u7a7f\u519b\u8bad\u670d\u7684\u5b66\u751f\uff0c\u6c5f\u654f\u4e4b\u7684\u79d8\u4e66\u859b\u660e\u697c\u6b63\u7ed9\u4ed6\u4eec\u5f53\u53f8\u673a\u2014\u2014\u5f20\u606a\u8fd9\u624d\u610f\u8bc6\u5230\u859b\u660e\u697c\u662f\u5728\u7ed9\u4ed6\u8ba9\u8def\u5462\uff0c\u4ed6\u6ca1\u6709\u4ec0\u4e48\u8bdd\u597d\u8ddf\u859b\u660e\u697c\u3001\u6c5f\u6e44\u8bf4\u7684\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u8ddf\u859b\u660e\u697c\u3001\u6c5f\u6e44\u6253\u4ec0\u4e48\u62db\u547c\uff0c\u5c31\u76f4\u63a5\u8ba9\u53f8\u673a\u52a0\u901f\u8d85\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u675c\u98de\u8f6c\u8fc7\u5934\u770b\uff0c\u8fd8\u76ef\u7740\u6c5f\u654f\u4e4b\u7684\u5ea7\u9a7e\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u56de\u5934\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u90a3\u4e2a\u5bb6\u4f19\u5c31\u662f\u526f\u56fd\u7ea7\u7684\u8859\u5185\u5427\uff1f\u201d</p><p>\u3000\u3000\u56fd\u52a1\u9662\u603b\u7406\u3001\u526f\u603b\u7406\u3001\u56fd\u52a1\u59d4\u5458\u4e5f\u5c31\u90a3\u4e48\u5341\u51e0\u53f7\u4eba\uff0c\u5f20\u606a\u5bf9\u56fd\u52a1\u59d4\u5458\u66f9\u5175\u8fd8\u662f\u6709\u4e9b\u4e86\u89e3\u7684\uff0c\u7b97\u662f\u80fd\u540f\uff0c\u9887\u6709\u6e05\u8a89\uff0c\u53ea\u662f\u5206\u7ba1\u533a\u57df\u8ddf\u9526\u6e56\u6ca1\u6709\u5173\u7cfb\uff0c\u5f20\u606a\u5bf9\u4ed6\u4e5f\u6ca1\u6709\u7279\u522b\u6df1\u5165\u7684\u8ba4\u8bc6\u3002\u6c5f\u5bb6\u4e0e\u66f9\u5bb6\u7b97\u4e16\u4ea4\uff0c\u4e0e\u53f6\u5bb6\u8ddf\u987e\u5bb6\u7684\u5173\u7cfb\u5dee\u4e0d\u591a\uff0c\u859b\u660e\u697c\u3001\u6c5f\u6e44\u8fc7\u6765\u627e\u8fd9\u4f4d\u8859\u5185\u4e5f\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\uff0c\u53ea\u662f\u7adf\u7136\u5c06\u6c5f\u654f\u4e4b\u7684\u8f66\u5f00\u4e86\u8fc7\u6765\u2026\u2026\u5f20\u606a\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u8bb8\u5b66\u6821\u8981\u865a\u60ca\u4e00\u573a\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u563b\u563b\u7684\u4e00\u7b11\uff0c\u5b66\u6821\u91cc\u8981\u662f\u6709\u4e9b\u4eba\u770b\u5230\u6c5f\u654f\u4e4b\u7684\u4e13\u8f66\u5c31\u624b\u5fd9\u811a\u4e71\u7684\u5927\u52a8\u5e72\u6208\uff0c\u4ed6\u624d\u591a\u534a\u4f1a\u6709\u770b\u70ed\u95f9\u7684\u5fc3\u6001\uff0c\u8f6c\u5ff5\u60f3\u5230\u5f20\u606a\u8fd9\u53e5\u8bdd\u91cc\u522b\u7684\u610f\u601d\uff0c\u53c8\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u654f\u4e4b\u5927\u6982\u5bf9\u8fd9\u4f4d\u8859\u5185\u4e0d\u4f1a\u91cd\u89c6\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u542c\u8bf4\u859b\u660e\u697c\u4e5f\u662f\u5c0f\u5fc3\u8c28\u614e\u7684\u4eba\uff0c\u591a\u534a\u662f\u8fd9\u4f4d\u8859\u5185\u60f3\u70ab\u8000\uff0c\u9f13\u52a8\u6c5f\u6e44\u8981\u631f\u859b\u660e\u697c\u5c06\u6c5f\u654f\u4e4b\u7684\u4e13\u8f66\u5f00\u5230\u5b66\u6821\u6765\u901e\u5a01\u98ce\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5f\u8bb8\u5dee\u4e0d\u591a\u5c31\u662f\u8fd9\u6837\uff0c\u5f20\u606a\u5fc3\u91cc\u6697\u60f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u5916\u7684\uff0c\u5bf9\u8fd9\u4f4d\u8859\u5185\u5927\u6982\u5c31\u662f\u8fd9\u6837\u7684\u5370\u8c61\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u7b11\u4e86\u7b11\uff0c\u5fc3\u60f3\u8fd9\u4f4d\u8859\u5185\u7684\u7ea7\u522b\u8d8a\u9ad8\uff0c\u8d8a\u6709\u53ef\u80fd\u7ed9\u63d0\u524d\u544a\u8beb\u9526\u6e56\u7684\u5e95\u7ec6\uff0c\u6240\u4ee5\u4ed6\u4e5f\u4e0d\u62c5\u5fc3\u8ddf\u8fd9\u4e2a\u4eba\u4f1a\u8d77\u4ec0\u4e48\u51b2\u7a81\uff0c\u770b\u4e0d\u60ef\u4e0d\u8981\u7406\u776c\u5c31\u662f\u4e86\u3002\u4ed6\u53c8\u95ee\u5f20\u606a\uff1a\u201c\u4e09\u661f\u6ca1\u6709\u7ed9\u6211\u4eec\u8bf7\u67ec\u5462\uff0c\u6211\u4eec\u7a81\u7136\u8dd1\u5230\u4eba\u5bb6\u7684\u65b0\u54c1\u53d1\u5e03\u9152\u4f1a\u4e0a\u53bb\u4f1a\u4e0d\u4f1a\u7ed9\u8d76\u51fa\u6765\uff1f\u201d\u4ed6\u8ddf\u5f20\u606a\u8981\u53bb\u53c2\u52a0\u4e09\u661f\u5728\u5e0c\u5c14\u987f\u9152\u5e97\u4e3e\u529e\u7684\u79cb\u5b63\u65b0\u54c1\u53d1\u5e03\u4f1a\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4eec\u8981\u505a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u6ca1\u6709\u53d7\u5230\u9080\u8bf7\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u8981\u53bb\u4e86\u624d\u77e5\u9053\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u8d76\u4eba\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u8bf4\u8fd9\u53e5\u8bdd\u65f6\uff0c\u4ed6\u7684\u795e\u8272\u5374\u5f88\u4e25\u8083\u3002</p><p>\u3000\u3000\u4e03\u6708\u4e2d\u65ec\uff0c\u5728\u673a\u573a\u89c1\u8fc7\u674e\u99a8\u4e88\u4e4b\u540e\uff0c\u542c\u5979\u8bf4\u8981\u56de\u97e9\u56fd\u6e21\u5047\uff0c\u8bb0\u5f97\u5f53\u65f6\u4e5f\u8bf4\u8fc7\u6691\u671f\u8fc7\u53bb\u518d\u89c1\u9762\u7684\u5206\u522b\u8bdd\uff0c\u674e\u99a8\u4e88\u521a\u56de\u5230\u97e9\u56fd\u6c49\u57ce\u8fd8\u6253\u6765\u8fc7\u7535\u8bdd\u62a5\u5e73\u5b89\uff0c\u4f46\u662f\u4e4b\u540e\u5c31\u4e00\u76f4\u6ca1\u6709\u5979\u7684\u6d88\u606f\u3002\u5f20\u606a\u521d\u65f6\u8fd8\u6ca1\u6709\u89c9\u5f97\u6709\u4ec0\u4e48\uff0c\u90a3\u6bb5\u65f6\u95f4\u7684\u4e8b\u60c5\u4e5f\u591a\uff0c\u7ed9\u4e1c\u5c94\u897f\u5c94\u7684\uff0c\u5230\u4e86\u516b\u6708\u5e95\u624d\u53d1\u73b0\u674e\u99a8\u4e88\u6709\u4e00\u4e2a\u6708\u6ca1\u6709\u8054\u7cfb\u4ed6\u4e86\u2014\u2014\u8fd9\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u4f46\u662f\u65b0\u7684\u5b66\u671f\u5f00\u59cb\u4e86\uff0c\u674e\u99a8\u4e88\u5374\u6ca1\u6709\u9732\u9762\uff0c\u5f20\u606a\u5c31\u89c9\u5f97\u6709\u4e9b\u8e4a\u8df7\u3002\u5f53\u674e\u99a8\u4e88\u7ed9\u4ed6\u7559\u4e0b\u7684\u6240\u6709\u8054\u7cfb\u65b9\u5f0f\u90fd\u8054\u7cfb\u4e0d\u4e0a\u5979\u65f6\uff0c\u5f20\u606a\u610f\u8bc6\u5230\u53ef\u80fd\u662f\u4ed6\u4e0e\u674e\u99a8\u4e88\u4e4b\u95f4\u7684\u5173\u7cfb\u7ed9\u8bef\u4f20\u5230\u674e\u5065\u7199\u8033\u6735\u91cc\u3001\u674e\u99a8\u4e88\u7ed9\u7981\u8db3\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u4e16\u754c\u4e0a\u80fd\u9650\u5236\u674e\u99a8\u4e88\u81ea\u7531\u7684\uff0c\u5927\u6982\u4e5f\u53ea\u6709\u674e\u5065\u7199\u4e86\uff0c\u674e\u5065\u7199\u5927\u6982\u662f\u5c06\u4ed6\u8ddf\u674e\u99a8\u4e88\u7684\u5173\u7cfb\u5f53\u6210\u5bb6\u65cf\u4e11\u95fb\u6765\u9632\u8303\u2026\u2026</p><p>\u3000\u3000\u8981\u662f\u5176\u4ed6\u4eba\uff0c\u5f20\u606a\u4e5f\u8bb8\u5c31\u8ba9\u8fd9\u4ef6\u4e8b\u60c5\u70df\u6d88\u4e91\u6563\uff0c\u4f46\u662f\u4ed6\u5fd8\u4e0d\u6389\u524d\u5c18\u5f80\u4e16\u91cc\u6709\u5173\u674e\u99a8\u4e88\u516c\u5bd3\u81ea\u6ea2\u7684\u65b0\u95fb\u62a5\u9053\u8ddf\u65b0\u95fb\u91cc\u8ba9\u4eba\u89e6\u76ee\u60ca\u5fc3\u7684\u56fe\u7247\u3002\u6628\u5929\u591c\u91cc\u505a\u68a6\u68a6\u89c1\u674e\u99a8\u4e88\u5c06\u6bdb\u5dfe\u7cfb\u5728\u6d74\u7f38\u7684\u9f99\u5934\uff0c\u6574\u4e2a\u4eba\u5e73\u8db4\u5728\u6d74\u7f38\u91cc\u5934\u5957\u7740\u6bdb\u5dfe\u81ea\u6ea2\u7684\u60c5\u5f62\u2014\u2014\u4ece\u68a6\u4e2d\u60ca\u9192\uff0c\u5f20\u606a\u5c31\u5750\u5728\u5e8a\u8fb9\u9759\u7b49\u5230\u5929\u4eae\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u60f3\u8d77\u76f8\u8bc6\u8fd9\u6bb5\u65f6\u95f4\u6765\u7684\u70b9\u70b9\u6ef4\u6ef4\u4ee5\u53ca\u9759\u6aac\u6240\u8bf4\u7684\u201c\u8f66\u524d\u706f\u201d\u7684\u73a9\u7b11\u8bdd\u3001\u706b\u8f66\u4e0a\u7684\u51e0\u6b21\u76f8\u9047\u4ee5\u53ca\u5728\u6d77\u5dde\u5e2e\u5979\u4ece\u4fdd\u9556\u773c\u76ae\u5b50\u5e95\u4e0b\u9003\u8dd1\uff0c\u674e\u99a8\u4e88\u662f\u90a3\u79cd\u6253\u773c\u770b\u4e0a\u53bb\u5c31\u77e5\u9053\u67d4\u5f31\u987a\u4ece\u7684\u6027\u5b50\uff0c\u8c01\u53c8\u80fd\u60f3\u8c61\u524d\u4e16\u7684\u5979\u4f1a\u4ee5\u8fd9\u79cd\u65b9\u5f0f\u7ed3\u675f\u82b1\u5b63\u7684\u751f\u547d\uff1f</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6709\u5979\u521a\u70c8\u3001\u4e0d\u7518\u5c48\u4ece\u7684\u4e00\u9762\uff0c\u5f20\u606a\u9189\u9152\u7761\u5728\u5979\u7684\u5367\u5ba4\u91cc\u7ed9\u674e\u5728\u6d19\u3001\u6c60\u4f50\u79c0\u85cf\u8bef\u4f1a\uff0c\u5979\u9009\u62e9\u4e0d\u505a\u89e3\u91ca\u7684\u5c31\u642c\u5230\u9752\u5e74\u516c\u5bd3\u6765\uff0c\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u81ea\u604b\u5230\u8ba4\u4e3a\u674e\u99a8\u4e88\u8fd9\u662f\u5728\u6295\u6000\u9001\u62b1\uff0c\u77e5\u9053\u8fd9\u662f\u5979\u5bf9\u81ea\u5df1\u4e4b\u524d\u4eba\u751f\u7684\u4e00\u79cd\u6c89\u9ed8\u62b5\u6297\uff01</p><p>\u3000\u3000\u5f20\u606a\u5c06\u62c5\u5fe7\u6df1\u6df1\u7684\u57cb\u5728\u5fc3\u91cc\uff0c\u6709\u4e9b\u4e8b\u60c5\u5f88\u96be\u8ddf\u522b\u4eba\u8bc9\u8bf4\uff0c\u4e5f\u6ca1\u6cd5\u8bf4\uff0c\u8981\u662f\u8ddf\u522b\u4eba\u8bf4\u7a81\u7136\u7ed9\u7981\u9522\u7684\u674e\u99a8\u4e88\u53ef\u80fd\u4f1a\u7528\u4e00\u79cd\u6fc0\u70c8\u7684\u65b9\u5f0f\u53bb\u6c89\u9ed8\u7684\u6297\u62d2\u81ea\u5df1\u7ed9\u522b\u4eba\u5b89\u6392\u7684\u4eba\u751f\uff0c\u6ca1\u6709\u4eba\u4f1a\u76f8\u4fe1\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u4ed6\u5728\u56fd\u5185\u8981\u60f3\u5c3d\u5feb\u8054\u7cfb\u4e0a\u674e\u99a8\u4e88\uff0c\u53ea\u6709\u901a\u8fc7\u674e\u5728\u6d19\u2014\u2014\u65e2\u7136\u674e\u5065\u7199\u90fd\u9650\u6b62\u7981\u99a8\u4e88\u8ddf\u5916\u754c\u7279\u522b\u662f\u8ddf\u4ed6\u8054\u7cfb\uff0c\u5f20\u606a\u4e5f\u77e5\u9053\u4ece\u674e\u5728\u6d19\u90a3\u91cc\u8054\u7cfb\u4e0a\u674e\u99a8\u4e88\u7684\u53ef\u80fd\u6027\u5f88\u5c0f\uff0c\u66f4\u4f55\u51b5\u4ed6\u8ddf\u674e\u5728\u6d19\u7684\u5173\u7cfb\u4e00\u5411\u90fd\u5f88\u7cdf\u7cd5\u2014\u2014\u5373\u4f7f\u77e5\u9053\u53ef\u80fd\u6027\u5f88\u5c0f\uff0c\u5f20\u606a\u4e5f\u5fc5\u987b\u53bb\u5c1d\u8bd5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5efa\u90ba\u5e0c\u5c14\u987f\u9152\u5e97\u9876\u697c\u7684\u5bb4\u4f1a\u5927\u5385\uff0c\u4e09\u661f\u6b63\u53ec\u96c6\u5a92\u4f53\u8bb0\u8005\u4e0e\u7279\u9080\u5609\u5bbe\u4e3e\u529e\u4e09\u661f\u7535\u5b502000\u79cb\u5b63\u65b0\u54c1\u53d1\u5e03\u9152\u5e97\uff0c\u9c9c\u82b1\u9526\u7c07\uff0c\u706f\u5149\u9646\u79bb\uff0c\u540d\u6d41\u4ed5\u5973\u7a7f\u68ad\u5176\u95f4\uff0c\u9999\u9b13\u5982\u4e91\u3002\u6444\u5f71\u8bb0\u8005\u624b\u91cc\u7684\u95ea\u5149\u706f\u9891\u9891\u95ea\u4eae\uff0c\u867d\u4e0d\u77e5\u4ed6\u4eec\u662f\u62cd\u6444\u5c55\u67dc\u91cc\u7684\u4e09\u661f\u7535\u5b50\u79cb\u5b63\u65b0\u54c1\uff0c\u8fd8\u662f\u62cd\u6444\u5c55\u62d2\u8fb9\u5bb9\u8c8c\u8ff7\u4eba\u7684\u6a21\u7279\u3001\u6291\u6216\u5728\u8ffd\u9010\u9152\u4f1a\u4e0a\u540d\u6d41\u7684\u8eab\u5f71\uff0c\u674e\u5728\u6d19\u5bf9\u4eca\u5929\u7684\u65b0\u54c1\u53d1\u5e03\u9152\u4f1a\u662f\u76f8\u5f53\u7684\u6ee1\u610f\u3002</p><p>\u3000\u3000\u4e09\u661f\u7535\u5b50\u7684\u65b0\u54c1\u53d1\u5e03\u4f1a\u901a\u5e38\u4f1a\u9009\u62e9\u4e0d\u540c\u7684\u5730\u65b9\uff0c\u7531\u4e8e\u9526\u6e56\u7684\u63a8\u52a8\uff0c\u5efa\u90ba\u5b9e\u5728\u4e0a\u5df2\u7ecf\u6210\u4e3a\u4e2d\u56fd\u624b\u673a\u4ea7\u4e1a\u7684\u4e2d\u5fc3\uff0c\u4e09\u661f\u7535\u5b50\u8fd9\u6b21\u79cb\u5b63\u53d1\u5e03\u4f1a\u4e3b\u8981\u63a8\u52a8\u65b0\u6b3eA288\u624b\u673a\uff0c\u6240\u4ee5\u9009\u62e9\u5728\u5efa\u90ba\u53ec\u5f00\u65b0\u54c1\u53d1\u5e03\u9152\u4f1a\u3002</p><p>\u3000\u3000A288\u662f\u4e09\u661f\u7535\u5b50\u4e13\u95e8\u9488\u5bf9\u4e2d\u56fd\u5927\u9646\u3001\u9999\u6e2f\u4ee5\u6709\u53f0\u6e7e\u5e02\u573a\u8bbe\u8ba1\u5f00\u53d1\u7684\u53cc\u5c4f\u624b\u673a\uff0c\u4e0e\u4e09\u661f\u4e4b\u524d\u5728\u4e2d\u56fd\u5e02\u573a\u63a8\u51fa\u7684\u624b\u673a\u4e0d\u540c\uff0cA288\u624b\u673a\u7684\u5916\u90e8\u7ebf\u6761\u67d4\u548c\uff0c\u9020\u578b\u7b80\u6d01\u800c\u79c0\u7f8e\uff0c\u7ea4\u5de7\u8f7b\u7075\u7684\u673a\u8eab\u786e\u5b9e\u5f88\u5438\u5f15\u4eba\u7684\u773c\u7403\uff0c\u5f53\u7136A288\u624b\u673a\u57fa\u4e8eGPRS\u901a\u8baf\u7f51\u7edc\u7684\u529f\u80fd\u4e5f\u662f\u5341\u5206\u7684\u5f3a\u52b2\u2014\u2014\u674e\u5728\u6d19\u5bf9A288\u53d6\u5f97\u7684\u5e02\u573a\u6210\u7ee9\u5c06\u8d85\u8d8a\u6b64\u65f6\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u4e0a\u4efb\u4f55\u4e00\u6b3eGPRS\u624b\u673a\u6709\u7740\u5f3a\u70c8\u7684\u4fe1\u5fc3\uff0c\u4ed6\u610f\u6c14\u98ce\u53d1\u7684\u770b\u7740\u9152\u4f1a\u4e0a\u7684\u4eba\uff0c\u60f3\u5230\u53bb\u5e74\u7ed9\u9526\u6e56\u53cc\u5c4f\u624b\u673a\u6253\u538b\u4ee5\u6765\u8fd1\u4e00\u5e74\u7684\u90c1\u95f7\u4eca\u5929\u7ec8\u4e8e\u53ef\u4ee5\u5410\u5c3d\u4e86\uff0c\u6574\u4e2a\u4e2d\u56fd\u90e8\u95e8\u90fd\u4e3a\u6b64\u618b\u4e86\u6574\u6574\u4e00\u5e74\u7684\u6c14\uff0c\u9526\u6e56\u590f\u5b63\u53d1\u5e03\u7684\u4e24\u6b3eGPRS\u624b\u673a\u867d\u7136\u6709\u51fa\u8272\u7684\u5730\u65b9\uff0c\u4f46\u662f\u8ddfA288\u76f8\u6bd4\u8fd8\u662f\u6709\u4e00\u5b9a\u7684\u5dee\u8ddd\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u5728\u610f\u6c14\u98de\u626c\u4e4b\u65f6\uff0c\u770b\u5230\u91d1\u5357\u52c7\u98de\u5feb\u7684\u7a7f\u8fc7\u4eba\u7fa4\u671d\u4ed6\u8d70\u8fc7\u6765\uff0c\u51d1\u5230\u4ed6\u8033\u8fb9\u4f4e\u8bed\uff1a\u201c\u5f20\u606a\u8fc7\u6765\u4e86\uff01\u201d</p><p>\u3000\u3000\u201c\u4ed6\u6765\u5e72\u4ec0\u4e48\uff01\u201d\u674e\u5728\u6d19\u6d51\u8eab\u6253\u4e00\u54c6\u55e6\uff0c\u7d27\u63a5\u7740\u4ed6\u81ea\u5df1\u4e5f\u53d1\u73b0\u53cd\u5e94\u8fc7\u4e8e\u6fc0\u70c8\u4e86\uff0c\u95ee\u91d1\u5357\u52c7\uff0c\u201c\u6211\u4eec\u6ca1\u6709\u7ed9\u9526\u6e56\u53d1\u8bf7\u5e16\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u2026\u2026\u201d\u91d1\u5357\u52c7\u6447\u4e86\u6447\u5934\uff0c\u53c8\u8fdf\u7591\u7684\u8ddf\u674e\u5728\u6d19\u8bf4\u9053\uff0c\u201c\u4ed6\u4f1a\u4e0d\u4f1a\u662f\u4e3a\u99a8\u4e88\u7684\u4e8b\u60c5\u800c\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u53ef\u80fd\uff0c\u4ed6\u4e0d\u662f\u8fd9\u79cd\u6ca1\u773c\u8272\u7684\u4eba\uff01\u201d\u674e\u5728\u6d19\u65ad\u7136\u5426\u8ba4\u8fd9\u79cd\u53ef\u80fd\uff0c\u201c\u4ed6\u8981\u662f\u8fd9\u79cd\u4e0d\u77e5\u8f7b\u91cd\u7684\u4eba\uff0c\u4ed6\u8fd8\u6709\u4ec0\u4e48\u8ba9\u4eba\u5bb3\u6015\u7684\uff1f\u201d</p><p>\u3000\u3000\u91d1\u5357\u52c7\u60f3\u60f3\u4e5f\u662f\uff0c\u5f20\u606a\u8eab\u8fb9\u7684\u5973\u4eba\u7edd\u4e0d\u4f1a\u7f3a\u4e86\u99a8\u4e88\u4e00\u4e2a\uff0c\u99a8\u4e88\u7684\u8eab\u4efd\u4e4b\u7279\u6b8a\uff0c\u5f20\u606a\u5fc3\u91cc\u6bd4\u8c01\u90fd\u6e05\u695a\uff0c\u4ed6\u8981\u662f\u8d2a\u56fe\u99a8\u4e88\u7684\u7f8e\u8272\u60f3\u7ea0\u7f20\u4e0b\u53bb\uff0c\u90a3\u624d\u662f\u611a\u8822\u4e4b\u6781\u5462\uff0c\u4ed6\u96be\u9053\u8ba4\u4e3a\u674e\u5bb6\u4f1a\u5141\u8bb8\u8fd9\u79cd\u4e11\u95fb\u51fa\u73b0\u5417\uff1f\u8bf4\u5b9e\u8bdd\uff0c\u91d1\u5357\u52c7\u731c\u6d4b\u674e\u5065\u7199\u4f1a\u957f\u66f4\u62c5\u5fc3\u5f20\u606a\u4f1a\u5229\u7528\u8fd9\u4e2a\u4e11\u95fb\uff0c\u8fd9\u624d\u4e7e\u5764\u51b3\u65ad\u5c06\u99a8\u4e88\u6263\u62bc\u5728\u6c49\u57ce\u4e0d\u8ba9\u5979\u56de\u5efa\u90ba\u3002\u5f20\u606a\u771f\u8981\u806a\u660e\u7684\u4eba\uff0c\u5c31\u592a\u8be5\u5f53\u8fd9\u4ef6\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\u8fc7\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u8fd9\u4e48\u60f3\u7740\uff0c\u773c\u775b\u770b\u7740\u5f20\u606a\u5728\u675c\u98de\u3001\u5085\u4fca\u7684\u966a\u540c\u8d70\u8fdb\u5bb4\u4f1a\u5385\u6765\u3002</p><p>\u3000\u3000\u4e09\u661f\u7535\u5b50\u6b64\u6b21\u9009\u62e9\u6d77\u7c9f\u79d1\u6280\u65d7\u4e0b\u7684\u7535\u5b50\u5546\u52a1\u7f51\u7ad98818\u4f5c\u4e3aA288\u624b\u673a\u4e2d\u56fd\u5730\u533a\u4e92\u8054\u7f51\u65b0\u54c1\u53d1\u5e03\u7684\u5e73\u53f0\uff0c\u5f20\u6587\u4fe1\u4f5c\u4e3a8818\u7684\u6267\u884c\u603b\u88c1\u3001\u4e25\u6587\u4ecb\u4f5c\u4e3a\u4e09\u661f\u7535\u5b50\u5728\u4e2d\u56fd\u5730\u533a\u7684\u91cd\u8981\u5408\u4f5c\u4f19\u4f34\u4e5f\u51fa\u5e2d\u4e86\u4eca\u5929\uff0c\u4ed6\u4eec\u770b\u89c1\u5f20\u606a\u51fa\u73b0\u5728\u9152\u4f1a\u73b0\u573a\u4e5f\u9887\u4e3a\u60ca\u8be7\uff0c\u770b\u89c1\u5f20\u606a\u7ad9\u5728\u5bb4\u4f1a\u5385\u7684\u95e8\u53e3\u671d\u5185\u770b\u4e86\u7247\u523b\uff0c\u5c31\u5f84\u76f4\u671d\u674e\u5728\u6d19\u8d70\u8fc7\u53bb\u2026\u2026</p><p>\u3000\u3000\u674e\u5728\u6d19\u7422\u78e8\u4e0d\u900f\u5f20\u606a\u7684\u6765\u610f\uff0c\u4f46\u662f\u770b\u5230\u4ed6\u9510\u5229\u7684\u773c\u795e\u6709\u4e9b\u5fc3\u865a\uff0c\u786c\u7740\u5934\u76ae\u8fce\u4e0a\u53bb\uff0c\u8138\u76ae\u5b50\u8df3\u4e86\u8df3\uff0c\u624d\u6324\u51fa\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u662f\u51fa\u4e4e\u610f\u6599\u7684\u4e0d\u901f\u4e4b\u5ba2\u554a\uff0c\u60f3\u662f\u5bf9\u6211\u4eec\u4e09\u661f\u7684\u8fd9\u6b21\u79cb\u5b63\u65b0\u54c1\u53d1\u5e03\u4f1a\u6709\u4ec0\u4e48\u9ad8\u89c1\u8981\u5f53\u9762\u8d50\u6559\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u6837\u624d\u80fd\u8ddf\u8d35\u4f1a\u957f\u7684\u5343\u91d1\u8054\u7cfb\u4e0a\uff01\u201d\u5f20\u606a\u773c\u775b\u76ef\u7740\u674e\u5728\u6d19\uff0c\u6ca1\u6709\u5fc5\u8981\u5ba2\u5957\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u6577\u884d\uff0c\u5f84\u76f4\u7684\u8bf4\u51fa\u4ed6\u8fc7\u6765\u7684\u610f\u56fe\uff0c\u8fde\u675c\u98de\u8ddf\u5085\u4fca\u90fd\u9887\u4e3a\u60ca\u8bb6\uff0c\u674e\u5728\u6d19\u66f4\u662f\u6123\u5728\u5f53\u573a\uff0c\u4ed6\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4f1a\u8fd9\u4e48\u4e0d\u77e5\u9053\u8fdb\u9000\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u7b54\u597d\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u544a\u8bc9\u4f60\uff1f\u201d\u91d1\u5357\u52c7\u63d2\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u4f60\u6ca1\u6709\u8d44\u683c\u8ddf\u6211\u8bf4\u8bdd\uff0c\u6eda\u4e00\u8fb9\u53bb\uff01\u201d\u5f20\u606a\u53ea\u77a5\u4e86\u91d1\u5357\u52c7\u4e00\u773c\uff0c\u8bf4\u8bdd\u975e\u5e38\u7684\u4e0d\u5ba2\u6c14\uff0c\u58f0\u97f3\u4e5f\u4e0d\u5c0f\uff0c\u4ee4\u65c1\u8fb9\u4eba\u90fd\u5f02\u5e38\u7684\u60ca\u8bb6\uff0c\u5f20\u606a\u5374\u4e0d\u987e\u522b\u4eba\u773c\u91cc\u7684\u60ca\u8bb6\u4e0e\u8c14\u7136\uff0c\u53ea\u662f\u76ef\u7740\u674e\u5728\u6d19\u770b\uff0c\u53c8\u8bed\u8c03\u575a\u786c\u7684\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u600e\u4e48\u6837\u624d\u6709\u8ddf\u8d35\u4f1a\u957f\u7684\u5343\u91d1\u8054\u7cfb\u4e0a\uff01\u201d\u5f20\u606a\u77e5\u9053\u4ece\u674e\u5728\u6d19\u5634\u91cc\u95ee\u4e0d\u51fa\u4ec0\u4e48\u6765\uff0c\u4f46\u662f\u5fc5\u987b\u8981\u65bd\u52a0\u538b\u529b\uff0c\u5e0c\u671b\u80fd\u901a\u8fc7\u674e\u5728\u6d19\u6216\u8005\u5176\u4ed6\u5728\u573a\u7684\u4e09\u661f\u5458\u5de5\u8ba9\u65e0\u6cd5\u8054\u7cfb\u4e0a\u7684\u674e\u99a8\u4e88\u77e5\u9053\uff1a\u4ed6\u5728\u5173\u5fc3\u5979\uff0c\u5343\u4e07\u4e0d\u8981\u505a\u50bb\u4e8b\uff01</p></article>"}}}]);
