(window.webpackJsonp=window.webpackJsonp||[]).push([[1193],{1216:function(p){p.exports={chapterTitle:null,title:"\u7b2c983\u7ae0 \u5143\u65e6\u72c2\u6b22\u591c",content:"<article><h2>\u7b2c983\u7ae0 \u5143\u65e6\u72c2\u6b22\u591c</h2><p>\u3000\u3000\u738b\u5efa\u6797\u6478\u7740\u4e0b\u9894\uff0c\u56de\u5230\u4ed6\u5728\u4e91\u68a6\u9601\u7684\u529e\u516c\u5ba4\u91cc\uff0c\u62ff\u94a5\u5319\u6253\u5f00\u62bd\u5c49\uff0c\u62ff\u51fa\u4e00\u76d2\u5f55\u50cf\u5e26\u6765\u653e\u8fdb\u5f55\u50cf\u673a\u91cc\uff0c\u9152\u52b2\u6d8c\u4e0a\u6765\uff0c\u5fcd\u4e0d\u4f4f\u6253\u4e86\u55dd\uff0c\u5fcd\u7740\u9152\u81ed\u7684\u6076\u5fc3\u52b2\uff0c\u624d\u6293\u8d77\u684c\u4e0a\u7684\u9065\u63a7\u5668\u6253\u5f00\u7535\u89c6\uff0c\u96ea\u82b1\u70b9\u95ea\u70c1\u4e86\u4e00\u4e0b\u5c31\u663e\u793a\u51fa\u753b\u9762\u6765\uff0c\u753b\u9762\u91cc\u662f\u4e00\u95f4\u8d34\u7740\u6d45\u91d1\u8272\u58c1\u7eb8\u3001\u88c5\u6f62\u5962\u534e\u7684\u5305\u53a2\u3002</p><p>\u3000\u3000\u738b\u5efa\u6797\u5750\u5230\u8001\u677f\u6905\u4e0a\uff0c\u817f\u9ad8\u8df7\u5230\u6a80\u7ea2\u8272\u7684\u529e\u516c\u684c\u4e0a\uff0c\u770b\u7740\u5c4f\u5e55\u91cc\u7684\u8089\u6218\uff0c\u9976\u6709\u5174\u8da3\u7684\u770b\u8d77\u6765\uff0c\u4ed6\u8d77\u521d\u8fd8\u4ee5\u4e3a\u8fd9\u4e24\u4e2a\u5c0f\u755c\u751f\u4f1a\u5206\u5f00\u6765\u529e\u4e8b\uff0c\u663e\u7136\u4ed6\u662f\u4f4e\u4f30\u4e86\u5f53\u4ee3\u5927\u5b66\u751f\u7684\u7d20\u8d28\uff0c\u5728\u5c4f\u5e55\u4e0a\u53ea\u9732\u51fa\u80cc\u5f71\u7684\u4e24\u4e2a\u4eba\u4fa7\u8fc7\u5934\u4ea4\u6362\u4e86\u773c\u8272\uff0c\u5634\u89d2\u6302\u8d77\u90aa\u6076\u7684\u6d45\u7b11\uff0c\u5c31\u5206\u5f00\u5c06\u5750\u5728\u6c99\u53d1\u4e0a\u7684\u5973\u5b69\u5b50\u6402\u5728\u6000\u91cc\uff0c\u5c24\u5176\u4ee5\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u6700\u8feb\u4e0d\u53ca\u5f85\u7684\u624b\u76f4\u63a5\u4f38\u5230\u5973\u5b69\u5b50\u7684\u4e24\u817f\u95f4\u63c9\u5f04\u8d77\u6765\u2026\u2026\u770b\u5230\u5c4f\u5e55\u4e0a\u4ea4\u6362\u7684\u955c\u5934\uff0c\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u7adf\u7136\u8fd8\u8ba9\u4e24\u4e2a\u5c0f\u59d0\u5728\u6c99\u53d1\u4e0a\u6446\u51fa69\u59ff\u52bf\u4e92\u543b\uff0c\u4ed6\u4e0e\u540c\u4f34\u5206\u522b\u4ece\u540e\u9762\u63d2\u5165\uff0c\u738b\u5efa\u6797\u770b\u5230\u8fd9\u91cc\u89c9\u5f97\u81ea\u5df1\u8fd8\u8981\u7b97\u7eaf\u6d01\u7684\uff0c\u81f3\u5c11\u8fd9\u79cd\u59ff\u52bf\uff0c\u4ed6\u60f3\u90fd\u6ca1\u6709\u60f3\u5230\u8fc7\uff0c\u753b\u9762\u523a\u6fc0\u8ba9\u4eba\u8840\u8109\u8d32\u5f20\u4e4b\u7a0b\u5ea6\uff0c\u5012\u4e0d\u6bd4\u4e0a\u5b66\u65f6\u5077\u5077\u770b\u8fc7\u7684\u90a3\u76d8\u300a\u6b66\u5219\u5929\u300b\u7a0d\u5dee\u3002</p><p>\u3000\u3000\u201c\u5431\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u89c1\u95e8\u6253\u5f00\u7684\u58f0\u97f3\uff0c\u738b\u5efa\u6797\u5413\u4e86\u4e00\u8df3\uff0c\u4e0b\u610f\u8bc6\u7684\u6309\u4e86\u4e0b\u9065\u63a7\u5668\u5c06\u5f55\u50cf\u673a\u5173\u6389\uff0c\u7ad9\u8d77\u6765\u770b\u89c1\u4ed6\u59d0\u592b\u63a8\u95e8\u8d70\u8fdb\u6765\uff0c\u4ed6\u786c\u751f\u751f\u7684\u5c06\u8981\u9a82\u51fa\u53e3\u7684\u8bdd\u54bd\u4e86\u56de\u53bb\uff0c\u95ee\u9053\uff1a\u201c\u59d0\u592b\uff0c\u4f60\u600e\u4e48\u56de\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u59d0\u521a\u6253\u7535\u8bdd\u7ed9\u6211\uff0c\u8bf4\u5979\u521a\u7ed9\u4eba\u62c9\u53bb\u6253\u901a\u5bb5\u9ebb\u5c06\uff0c\u90a3\u6211\u5c31\u4e0d\u6025\u7740\u56de\u53bb\uff0c\u201d\u94b1\u6842\u534e\u7709\u5934\u5fae\u76b1\u7684\u77a5\u4e86\u77a5\u738b\u5efa\u6797\u7684\u88e4\u88c6\uff0c\u201c\u4f60\u5c31\u8fd9\u70b9\u51fa\u606f\uff0c\u9700\u8981\u8eb2\u5728\u529e\u516c\u5ba4\u91cc\u770b\u8fd9\u79cd\u7247\u5b50\uff1f\u201d\u4ed6\u63a8\u95e8\u8fdb\u6765\u53ea\u770b\u5230\u767d\u82b1\u82b1\u7684\u4e00\u5806\u8089\u4f53\u5360\u4e86\u5927\u534a\u4e2a\u5c4f\u5e55\uff0c\u518d\u770b\u5230\u738b\u5efa\u6797\u8fd9\u6837\u5b50\uff0c\u4e5f\u6ca1\u6709\u5f80\u5176\u4ed6\u5730\u65b9\u60f3\uff0c\u4ee5\u4e3a\u738b\u5efa\u6797\u8eb2\u5728\u529e\u516c\u5ba4\u91cc\u770b\u6bdb\u7247\uff0c\u5fc3\u60f3\u7537\u4eba\u603b\u662f\u6709\u4e9b\u602a\u7656\u597d\uff0c\u8c01\u80fd\u60f3\u5230\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u559c\u6b22\u641e\u5c0f\u59d0\uff1f</p><p>\u3000\u3000\u738b\u5efa\u6797\u5012\u662f\u80fd\u7406\u89e3\u8fd9\u4e24\u4e2a\u5c0f\u755c\u751f\uff0c\u666e\u901a\u7684\u5973\u5b69\u5b50\u54ea\u91cc\u80fd\u8fd9\u4e48\u653e\u5f00\u7684\u73a9\uff1f\u54ea\u80fd\u73a9\u5f97\u8fd9\u4e48\u523a\u6fc0\uff1f\u4ed6\u5750\u56de\u5230\u6905\u5b50\u4e0a\uff0c\u5c06\u4e11\u6001\u906e\u4f4f\uff0c\u5c0f\u5fc3\u7684\u770b\u7740\u59d0\u592b\u7684\u773c\u8272\uff0c\u4ed6\u5e76\u4e0d\u60f3\u4ec0\u4e48\u4e8b\u60c5\u90fd\u7ed9\u522b\u4eba\u63a7\u5236\uff0c\u89c1\u4ed6\u59d0\u592b\u8138\u8272\u65e0\u5f02\uff0c\u53ea\u5f53\u53ca\u65f6\u5c06\u5f55\u50cf\u673a\u5173\u6389\uff0c\u8146\u8138\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u4e0d\u6211\u7ed9\u4f60\u5b89\u6392\u4e24\u4e2a\u5973\u5b69\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c31\u8fd8\u4e0a\u6b21\u90a3\u4e2a\u5c0f\u7389\uff0c\u201d\u94b1\u6842\u534e\u8ddf\u8205\u5b50\u8bf4\u8d77\u6765\u8fd9\u4e2a\uff0c\u8138\u8272\u8fd8\u662f\u6709\u4e9b\u8baa\u8baa\u7684\uff0c\u201c\u4f60\u7ed9\u4f60\u59d0\u6253\u4e2a\u7535\u8bdd\uff0c\u5c31\u8bf4\u4f60\u786c\u8981\u5c06\u6211\u7559\u4e0b\u6765\u6413\u9ebb\u5c06\u2026\u2026\u201d\u8d70\u5230\u95e8\u53e3\uff0c\u53c8\u8f6c\u8fc7\u8eab\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u521a\u624d\u5403\u996d\u65f6\uff0c\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u4f3c\u4e4e\u5bf9\u4ed6\u4eec\u5b66\u6821\u7684\u4e00\u4e2a\u5973\u5b69\u5b50\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u4f60\u66ff\u4ed6\u7559\u610f\u4e00\u4e0b\uff0c\u60f3\u60f3\u529e\u6cd5\uff0c\u8fd9\u4e8b\u529e\u6210\u4e86\uff0c\u5bf9\u6211\u4eec\u6709\u597d\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53ea\u8981\u6709\u8db3\u591f\u7684\u672c\u94b1\u4e0b\u53bb\uff0c\u6709\u51e0\u4e2a\u8d1e\u64cd\u70c8\u5973\u80fd\u7ecf\u5f97\u4f4f\u8bf1\u60d1\u4e0d\u6e7f\u8eab\u7684\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u96ea\u5728\u5165\u591c\u4e4b\u524d\u5c31\u505c\u4e86\uff0c\u8def\u7259\u4e0a\u79ef\u7740\u539a\u539a\u7684\u4e00\u5c42\u96ea\u3002</p><p>\u3000\u3000\u5f20\u606a\u9080\u59da\u6587\u76db\u4e0e\u6613\u99a8\u53bb\u5b66\u5e9c\u5df7\u518d\u5750\u4e00\u5750\uff0c\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0c\u5929\u8272\u8fd8\u65e9\u5f97\u5f88\uff0c\u4e24\u90e8\u8f66\u4e00\u524d\u4e00\u540e\u7684\u5f80\u5b66\u5e9c\u5df7\u9a76\u53bb\u3002</p><p>\u3000\u3000\u5c31\u5c06\u8f66\u505c\u5728\u5b66\u5e9c\u5df7\u5185\u5df7\uff0c\u8fdb\u4e861978\uff0c\u89c1\u4ee4\u5c0f\u71d5\u7ad9\u5728\u5427\u53f0\u540e\u64e6\u73bb\u7483\u676f\uff0c\u5f20\u606a\u6328\u7740\u5427\u53f0\u5750\u4e0b\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u600e\u4e48\u6709\u5fc3\u601d\u8fc7\u6765\u4f53\u9a8c\u751f\u6d3b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u5403\u706b\u9505\u8fd8\u6ca1\u6709\u6563\u4f19\u5462\uff0c\u201d\u4ee4\u5c0f\u71d5\u8f6c\u8eab\u6307\u7740\u80cc\u540e\u9152\u67dc\u4e0a\u65b9\u7684\u6295\u5f71\u5f0f\u949f\u8868\uff0c\u8fd8\u4e0d\u5230\u4e5d\u70b9\u949f\uff0c\u201c\u4eca\u5929\u771f\u662f\u5934\u75bc\uff0c\u8f6e\u73ed\u7684\u4e24\u4e2a\u5b66\u751f\u90fd\u4e34\u65f6\u6709\u4e8b\u8bf7\u4e86\u5047\uff0c\u6731\u5c0f\u519b\u9001\u4ed6\u7236\u6bcd\u53bb\u4e61\u4e0b\u4e86\uff0c\u6211\u5de6\u53f3\u65e0\u804a\uff0c\u5c31\u6765\u2018\u4f53\u9a8c\u751f\u6d3b\u2019\u4e86\u2026\u2026\u201d\u4e0e\u59da\u6587\u76db\u3001\u6613\u99a8\u70b9\u5934\u6253\u62db\u547c\uff0c\u59da\u6587\u76db\u53ef\u662f\u5979\u672a\u5a5a\u592b\u6731\u5c0f\u519b\u7684\u9876\u5934\u4e0a\u53f8\uff0c\u95ee\u9053\uff0c\u201c\u4f60\u4eec\u559d\u4ec0\u4e48\uff1f\u6211\u6765\u8bf7\u5ba2\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u4e86\u9152\u6c34\uff0c\u770b\u7740\u53f0\u7403\u684c\u90a3\u91cc\u6ca1\u6709\u4eba\uff0c\u8ba9\u4ee4\u5c0f\u71d5\u62ff\u51fa\u6258\u76d8\u6765\uff0c\u4ed6\u4eec\u81ea\u5df1\u5c06\u9152\u6c34\u7aef\u8fc7\u53bb\uff0c\u521a\u8981\u79bb\u5f00\u5427\u53f0\uff0c\u8f6c\u8eab\u53c8\u5bf9\u4ee4\u5c0f\u71d5\u8bf4\u9053\uff1a\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u4f60\u5e2e\u6211\u8ddf\u6731\u5c0f\u519b\u8bf4\u58f0\uff0c\u660e\u5929\u4e0b\u5348\u6211\u8981\u5728\u5bb6\u91cc\u8bf7\u4e24\u4e2a\u5ba2\u4eba\uff0c\u4f60\u4eec\u8981\u662f\u6709\u65f6\u95f4\uff0c\u4e5f\u4e00\u8d77\u8fc7\u6765\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u597d\u7684\u3002\u201d\u4ee4\u5c0f\u71d5\u723d\u5229\u7684\u7b54\u5e94\u4e0b\u6765\uff0c\u201c\u6709\u4ec0\u4e48\u8981\u51c6\u5907\u7684\uff0c\u6211\u8ba9\u6731\u5c0f\u519b\u8fc7\u6765\u5e2e\u4f60\u8dd1\u817f\u3002\u201d\u5979\u542c\u5b59\u9759\u6aac\u8bf4\u5f20\u606a\u4e0e\u5510\u5a67\u665a\u4e0a\u53bb\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u5bb6\u5403\u665a\u996d\u53bb\uff0c\u7f57\u541b\u7684\u5973\u513f\u4e0e\u7537\u670b\u53cb\u5230\u5efa\u90ba\uff0c\u5fc3\u60f3\u5f20\u606a\u660e\u5929\u5e94\u8be5\u662f\u5bb4\u8bf7\u4ed6\u4eec\u5427\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u59da\u6587\u76db\u4e0e\u6613\u99a8\u660e\u5929\u80af\u5b9a\u4f1a\u662f\u5ea7\u4e0a\u5ba2\uff0c\u5c31\u7b97\u6731\u5c0f\u519b\u8ba1\u5212\u7740\u5728\u4e61\u4e0b\u518d\u4f4f\u4e00\u5929\uff0c\u4e5f\u6b63\u597d\u6709\u501f\u53e3\u8ba9\u4ed6\u63d0\u524d\u56de\u5e02\u91cc\u6765\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5085\u4fca\u8d70\u8fdb\u6765\u544a\u8bc9\u5f20\u606a\uff0c\u4e00\u76f4\u5230\u665a\u4e0a\u516b\u70b9\u534a\u949f\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u80e1\u91d1\u661f\u624d\u4e0e\u540c\u4f34\u79bb\u5f00\u4e91\u68a6\u9601\uff0c\u4ed6\u4eec\u79bb\u5f00\u65f6\uff0c\u8fd8\u662f\u738b\u5efa\u6797\u4e0e\u94b1\u6842\u534e\u9001\u4e0a\u8f66\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4e0e\u5f20\u606a\u4e4b\u524d\u7684\u5224\u65ad\u4e0d\u540c\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u56db\u724c\u697c\u90a3\u91cc\u7684\u591c\u5e97\uff0c\u4e0d\u7b97\u7279\u522b\u7684\u9ad8\u6863\uff0c\u9876\u591a\u662f\u738b\u6d77\u7c9f\u6ca1\u6709\u53d1\u8ff9\u4e4b\u524d\u7ecf\u5e38\u6df7\u7684\u5730\u65b9\uff0c\u5c31\u7b97\u80e1\u5b97\u5e86\u540c\u610f\u80e1\u91d1\u661f\u63a5\u89e6\u4e09\u6559\u4e5d\u6d41\u4eba\u7269\uff0c\u738b\u5efa\u6797\u4e4b\u6d41\u4f3c\u4e4e\u8fd8\u7a0d\u5dee\u4e86\u4e9b\uff1b\u53e6\u5916\uff0c\u94b1\u6842\u534e\u8981\u662f\u4e0e\u80e1\u5b97\u5e86\u6709\u76f4\u63a5\u7684\u7275\u6d89\uff0c\u4e5f\u4e0d\u53ef\u80fd\u9648\u79c9\u5fb7\u3001\u4f59\u5e86\u6797\u4ee5\u53ca\u5357\u90ba\u533a\u59d4\u4e66\u8bb0\u5f20\u5065\u90fd\u6beb\u4e0d\u77e5\u60c5\uff0c\u6216\u8bb8\u53ea\u662f\u80e1\u91d1\u661f\u6253\u7740\u80e1\u5b97\u5e86\u7684\u540d\u53f7\u5728\u5916\u9762\u80e1\u4f5c\u975e\u4e3a\uff0c\u770b\u6765\u80e1\u91d1\u661f\u7684\u80c6\u5b50\u8fd8\u662f\u633a\u4e0d\u8ba9\u4eba\u5931\u671b\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u60f3\u4ed6\u8fd9\u8fb9\u4ecb\u5165\u6b64\u4e8b\u592a\u6df1\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u53ea\u8981\u5f53\u65f6\u6709\u4eba\u8ddf\u8fdb\u6d74\u573a\u91cc\uff0c\u5c31\u80fd\u77e5\u9053\u8fd9\u4e00\u5207\u7684\u53d1\u751f\u4e5f\u662f\u975e\u5e38\u7684\u5076\u7136\uff0c\u66f4\u52a0\u4e0d\u4f1a\u77e5\u9053\u738b\u5efa\u6797\u81ea\u4f5c\u806a\u660e\u7684\u5c06\u76db\u60c5\u6b3e\u5f85\u80e1\u91d1\u661f\u7684\u573a\u9762\u90fd\u5f55\u4e86\u4e0b\u6765\u3002</p><p>\u3000\u3000\u6709\u53ef\u80fd\u6d89\u53ca\u5230\u80e1\u5b97\u5e86\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u8981\u4e0b\u9762\u4eba\u6ce8\u610f\u9690\u853d\u8eab\u4efd\uff0c\u8fd8\u6ca1\u6709\u5230\u6495\u5f00\u8138\u4e92\u6bb4\u7684\u65f6\u5019\uff0c\u8fd9\u65f6\u5019\u8d70\u8fdb\u6765\u4e24\u4e2a\u4eba\uff0c\u4e24\u4e2a\u4eba\u90fd\u662f\u8001\u76f8\u8bc6\uff0c\u90fd\u662f\u7ecf\u5e38\u4e0e\u80e1\u91d1\u661f\u53ae\u6df7\u5728\u4e00\u8d77\u7684\u6821\u5b66\u751f\u4f1a\u4e3b\u5e2d\u9648\u52c7\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u539f\u5148\u866b\u4ff1\u4e50\u90e8\u6210\u5458\u90b5\u5f6c\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u4e0e\u5085\u4fca\u7ad9\u5728\u95e8\u53e3\u7684\u5730\u65b9\u8bf4\u8bdd\uff0c\u7279\u522b\u662f\u770b\u5230\u5f20\u606a\u770b\u5230\u4ed6\u4eec\u65f6\u7709\u5934\u90fd\u626c\u4e86\u8d77\u6765\uff0c\u5728\u95e8\u53e3\u6123\u4e86\u4e24\u4e09\u79d2\u949f\uff0c\u5c31\u626d\u5934\u8d70\u4e86\u51fa\u53bb\u2014\u2014\u4ed6\u4eec\u867d\u7136\u4e0d\u77e5\u9053\u5f20\u606a\u7684\u5e95\u7ec6\uff0c\u4f46\u662f\u5f20\u606a\u5f6a\u608d\u7684\u6027\u683c\u4ee5\u53ca\u51e0\u4ef6\u8db3\u4ee5\u626c\u540d\u4e1c\u5927\u7684\u4e8b\u8ff9\u90fd\u8ba9\u4ed6\u4eec\u60f3\u7740\u4e0e\u4e4b\u5c11\u6709\u74dc\u845b\u7684\u597d\u3002</p><p>\u3000\u3000\u201c\u662f\u4ed6\u4e0b\u5348\u8ddf\u80e1\u5b97\u5e86\u7684\u4f84\u5b50\u5728\u4e00\u8d77\u3002\u201d\u5085\u4fca\u6307\u7740\u6821\u5b66\u751f\u4f1a\u4e3b\u5e2d\u9648\u52c7\u79bb\u5f00\u7684\u80cc\u5f71\u8ddf\u5f20\u606a\u8bf4\u3002</p><p>\u3000\u3000\u9648\u52c7\u662f\u7701\u6559\u80b2\u5385\u526f\u5385\u957f\u9648\u800c\u8d35\u7684\u513f\u5b50\uff0c\u867d\u8bf4\u4e1c\u5927\u7684\u7ea7\u522b\u6bd4\u7701\u6559\u80b2\u5385\u8fd8\u9ad8\u534a\u7ea7\uff0c\u4e0d\u8fc7\u8fd8\u662f\u5f88\u4e70\u5730\u65b9\u7701\u5385\u7684\u8d26\u3002</p><p>\u3000\u3000\u4e1c\u5927\u91cc\u7684\u7ea8\u7ed4\u5b50\u5f1f\u662f\u4e0d\u5c11\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4eba\u6562\u5728\u4e1c\u5927\u91cc\u4e71\u6765\uff1b\u50cf\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u8fd9\u6837\uff0c\u591a\u534a\u4e5f\u53ea\u5728\u5916\u9762\u80e1\u4f5c\u975e\u4e3a\uff0c\u90a3\u79cd\u6562\u5728\u5bbf\u820d\u91cc\u96c6\u4f53\u62db\u5993\u7684\uff0c\u5c31\u7b97\u662f\u90e8\u957f\u3001\u7701\u957f\u7684\u516c\u5b50\uff0c\u4e1c\u5927\u4e5f\u662f\u4e00\u5f8b\u5f00\u9664\u7684\uff0c\u50cf\u5f20\u606a\u4e5f\u9669\u4e9b\u7ed9\u9b4f\u4e1c\u5f3a\u90fd\u641e\u5f97\u88ab\u5f00\u9664\u5b66\u7c4d\uff0c\u60f3\u8d77\u9b4f\u4e1c\u5f3a\u6765\uff0c\u5f20\u606a\u5fc3\u91cc\u8fd8\u784c\u5f97\u614c\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u9648\u52c7\u8fd9\u4e48\u65e9\u5c31\u4e0e\u80e1\u91d1\u661f\u5206\u5f00\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u80e1\u91d1\u661f\u53e6\u6709\u4e8b\u60c5\uff0c\u4e0d\u7136\u94b1\u6842\u534e\u3001\u738b\u5efa\u6797\u6b3e\u5f85\u4ed6\u4eec\u4e5f\u4e0d\u53ef\u80fd\u8fd9\u4e48\u65e9\u5c31\u7ed3\u675f\uff1b\u5c06\u8f66\u94a5\u5319\u4e22\u7ed9\u5085\u4fca\uff0c\u8ba9\u4ed6\u5c06\u8f66\u5f00\u56de\u53bb\uff0c\u4ed6\u8fd8\u8981\u57281978\u73a9\u5f88\u4e45\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0e\u6613\u99a8\u5df2\u7ecf\u7aef\u4e86\u9152\u6c34\u8fc7\u53bb\u4e86\uff0c\u5f20\u606a\u8d70\u8fc7\u6765\uff0c\u4ece\u59da\u6587\u76db\u624b\u91cc\u63a5\u8fc7\u7403\u6746\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8d81\u7740\u5b59\u9759\u6aac\u56de\u6765\u4e4b\u524d\u5148\u8c11\u4f60\u4e24\u76d8\u2026\u2026\u201d\u8f6c\u8eab\u770b\u89c1\u6797\u51b0\u5e2e\u5510\u5a67\u3001\u6613\u99a8\u5979\u4eec\u5c06\u9152\u6c34\u7aef\u4e86\u8fc7\u6765\u3002\u6b63\u5de7\u6731\u5c0f\u519b\u4eca\u5929\u8981\u966a\u7236\u6bcd\u56de\u4e61\u4e0b\u8001\u5bb6\u53bb\uff0c\u4ee4\u5c0f\u71d5\u8fd9\u624d\u5b64\u96f6\u96f6\u7684\u4e00\u4e2a\u4eba\u52301978\u6765\u503c\u73ed\uff0c\u5f20\u606a\u5012\u662f\u5947\u602a\u6797\u51b0\u600e\u4e48\u4e5f\u5728\u5143\u65e6\u591c\u7ed9\u4ee4\u5c0f\u71d5\u62c9\u8fc7\u6765\u5e2e\u5fd9\uff1f\u89c1\u5979\u8138\u4e0a\u8584\u65bd\u7c89\u9edb\uff0c\u5374\u6709\u7740\u63a9\u9970\u4e0d\u4e86\u7684\u6194\u60b4\uff0c\u4e0d\u65b9\u4fbf\u76f4\u63a5\u95ee\u5979\uff0c\u800c\u662f\u671d\u8fdc\u5904\u5427\u53f0\u540e\u9762\u7684\u4ee4\u5c0f\u71d5\u770b\u4e86\u4e00\u773c\uff0c\u4ee4\u5c0f\u71d5\u5476\u6487\u4e86\u4e00\u4e0b\u5634\uff0c\u793a\u610f\u662f\u6709\u9690\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u5c31\u8c11\u4e86\u59da\u6587\u76db\u4e24\u5c40\uff0c\u665a\u4e0a\u5403\u706b\u9505\u7684\u4eba\u5c31\u6d8c\u4e86\u8fdb\u6765\uff0c\u5b59\u9759\u6aac\u770b\u5230\u5f20\u606a\u5728\u91cc\u9762\uff0c\u5f00\u5fc3\u7684\u8ddf\u5927\u5bb6\u8bf4\u9053\uff1a\u201c\u653e\u5fc3\u4e86\uff0c\u571f\u8d22\u4e3b\u5728\u8fd9\u91cc\uff0c\u63a5\u4e0b\u6765\u4e0d\u7528\u6211\u6765\u8bf7\u5ba2\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u5230\u5b59\u9759\u6aac\u8fdb\u6765\uff0c\u5f20\u606a\u5c31\u8001\u5b9e\u7684\u653e\u4e0b\u7403\u6746\uff0c\u514d\u5f97\u7ed9\u5b59\u9759\u6aac\u6253\u5230\u673a\u4f1a\u8c11\u81ea\u5df1\uff0c\u8d70\u8fc7\u6765\uff0c\u8ddf\u5979\u4eec\u8bf4\uff1a\u201c\u5c06\u5927\u95e8\u63a9\u4e0a\uff0c\u5c0f\u95e8\u5f00\u7740\u5c31\u884c\uff0c\u9664\u4e86\u8ba4\u8bc6\u7684\uff0c\u5176\u4ed6\u4eba\u90fd\u4e0d\u8981\u653e\u8fdb\u6765\u4e86\u2026\u2026\u8ba4\u8bc6\u7684\u4eba\uff0c\u90fd\u7531\u6211\u8bf7\u5ba2\u3002\u201d</p><p>\u3000\u30001978\u662f\u82f1\u5f0f\u98ce\u683c\u7684\u9152\u5427\uff0c\u5927\u95e8\u63a9\u4e0a\u5c31\u8868\u793a\u975e\u719f\u4eba\u4e0d\u63a5\u7eb3\uff1b\u4e4b\u524d\u8fdb\u6765\u70b9\u9152\u6c34\u7684\u5ba2\u4eba\u90fd\u5df2\u7ecf\u7ed3\u8fc7\u8d26\uff0c\u5c06\u5927\u95e8\u63a9\u4e0a\uff0c\u4e5f\u4e0d\u7528\u4eba\u523b\u610f\u5b88\u7740\u5427\u53f0\u4e86\uff0c\u5927\u5bb6\u90fd\u70ed\u70ed\u95f9\u95f9\u7684\u56f4\u5230\u53f0\u7403\u684c\u8fd9\u8fb9\u6765\uff0c\u65f6\u5b66\u658c\u4ed6\u4eec\u53c8\u98ce\u9a9a\u65e0\u6bd4\u7684\u5230\u5c0f\u821e\u6c60\u91cc\u62ff\u7740\u8bdd\u7b52\u5531\u8d77\u6b4c\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u675c\u98de\u3001\u8499\u4e50\u660e\u5929\u4e0b\u5348\u5c06\u65f6\u95f4\u7a7a\u4e0b\u6765\uff0c\u4e00\u8d77\u966a\u540c\u7f57\u541b\u7684\u5973\u513f\u7f57\u742a\u53ca\u5979\u7684\u7537\u670b\u53cb\u8d75\u7559\u5b87\uff0c\u8fd8\u8ba9\u59da\u6587\u76db\u8bf7\u9646\u5065\u660e\u5929\u4e00\u8d77\u8fc7\u6765\u7528\u9910\u3002</p><p>\u3000\u3000\u4e25\u683c\u4e0a\u6765\u8bf4\uff0c\u9646\u5065\uff08\u7edf\u6218\u90e8\u90e8\u957f\u52a9\u7406\u9646\u65b0\u534e\u4e4b\u5b50\uff09\u3001\u8d75\u7559\u5b87\u3001\u7f57\u742a\u7b49\u4eba\u8ddf\u53f6\u5efa\u658c\u3001\u53f6\u5c0f\u6850\u3001\u59da\u6587\u76db\u4e00\u6837\uff0c\u4ed6\u4eec\u7684\u7236\u8f88\u6b63\u5f53\u58ee\u5e74\uff0c\u53c8\u90fd\u5904\u5728\u4ed5\u9014\u7684\u4e0a\u5347\u671f\uff0c\u4ed6\u4eec\u90fd\u5c5e\u4e8e\u7b2c\u4e8c\u4ee3\u7684\u592a\u5b50\u515a\u4eba\u7269\uff0c\u8fd9\u4e9b\u4eba\u53c8\u6709\u660e\u663e\u7684\u7279\u5f81\uff0c\u6216\u662f\u5bb6\u65cf\u5b89\u6392\u4e5f\u597d\uff0c\u6216\u662f\u81ea\u8eab\u7684\u5185\u5728\u9700\u6c42\uff0c\u90fd\u6709\u8f83\u5f3a\u4e14\u8ba4\u771f\u7684\u4e8b\u4e1a\u5fc3\uff0c\u4e0d\u4f1a\u4e3a\u79c1\u6b32\u65e0\u8282\u5236\u7684\u6ee5\u7528\u7279\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u73b0\u5728\u7ecf\u5e38\u6df7\u8ff9\u5230\u4e2d\u8001\u5e74\u961f\u4f0d\u4e2d\u53bb\uff0c\u867d\u8bf4\u4e5f\u4f1a\u5e0c\u671b\u4e0e\u4ed6\u4eec\u5efa\u7acb\u8d77\u79c1\u4eba\u5173\u7cfb\uff0c\u4f46\u662f\u4e2a\u4eba\u7684\u7cbe\u529b\u6bd5\u7adf\u6709\u9650\uff0c\u5e94\u8be5\u8ba9\u675c\u98de\u4e0e\u8499\u4e50\u6b63\u5f0f\u7684\u4ecb\u5165\u8fd9\u4e2a\u5708\u5b50\u91cc\u6765\uff0c\u4e3a\u4ed6\u4eec\u5c06\u6765\u7684\u4e8b\u4e1a\u6253\u4e0b\u575a\u5b9e\u57fa\u7840\u7684\u540c\u65f6\uff0c\u4e5f\u597d\u66ff\u81ea\u5df1\u5206\u62c5\u4e9b\u538b\u529b\u3002\u6731\u5c0f\u519b\u6709\u65f6\u5019\u672a\u514d\u6d41\u4e8e\u4e16\u6545\uff0c\u4f46\u603b\u4f53\u6765\u8bf4\uff0c\u8fd8\u662f\u503c\u5f97\u4fe1\u4efb\u7684\uff0c\u4e5f\u503c\u5f97\u5851\u9020\u3002</p></article>"}}}]);
