(window.webpackJsonp=window.webpackJsonp||[]).push([[1199],{1222:function(p){p.exports={chapterTitle:null,title:"\u7b2c988\u7ae0 \u6ca1\u94b1\u53ef\u4ee5\u53bb\u770b\u4e66",content:"<article><h2>\u7b2c988\u7ae0 \u6ca1\u94b1\u53ef\u4ee5\u53bb\u770b\u4e66</h2><p>\u3000\u3000\u6b63\u5982\u5f20\u606a\u6240\u6599\uff0c\u674e\u5728\u6d19\u7b56\u5212\u7684\u201c\u78d5\u5934\u62dc\u5e74\u201d\u4f7f\u5f97\u4e09\u661f\u5728\u8fd9\u6bb5\u65f6\u95f4\u6210\u4e3a\u56fd\u5185\u5a92\u4f53\u4e0e\u516c\u4f17\u8bae\u8bba\u4e0e\u5173\u6ce8\u7684\u7126\u70b9\uff0c\u867d\u7136\u6709\u4e9b\u5a92\u4f53\u5728\u6279\u5224\u4e09\u661f\u7684\u8fd9\u79cd\u884c\u5f84\uff0c\u4f46\u5bf9\u5e02\u573a\u5b9e\u9645\u7684\u89c2\u6d4b\uff0c\u8fd9\u573a\u5546\u4e1a\u79c0\u5bf9\u4e09\u661f\u5728\u4e2d\u56fd\u7684\u5e02\u573a\u6269\u5927\u54c1\u724c\u5f71\u54cd\u5374\u662f\u6709\u6b63\u9762\u4fc3\u8fdb\u4f5c\u7528\u7684\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u53ca\u94a2\u94c1\u4ea7\u4e1a\u57fa\u5730\u5173\u952e\u6027\u7684\u4f9b\u7535\u914d\u5957\u5de5\u7a0b\uff0c\u6295\u8d44\u516d\u4ebf\u7f8e\u5143\u7684\u4e1c\u5c71\u7535\u5382\u9879\u76ee\u5728\u5143\u65e6\u8fc7\u540e\u5c31\u83b7\u5f97\u56fd\u5bb6\u8ba1\u5212\u53d1\u5c55\u59d4\u7684\u5efa\u8bbe\u6279\u6587\uff0c\u4e1c\u6d77\u3001\u6c5f\u5357\u4e24\u7701\u7684\u591a\u540d\u515a\u653f\u9886\u5bfc\u4eba\u4e00\u6708\u516b\u65e5\u90fd\u9f50\u805a\u5230\u65b0\u4ead\u4e3a\u4e1c\u5c71\u7535\u5382\u4e3e\u884c\u9686\u91cd\u7684\u5960\u57fa\u793c\uff0c\u8fd9\u610f\u5473\u7740\u5efa\u8bbe\u4e1c\u5c71\u6e2f\u632f\u5174\u6c5f\u5357\u7ecf\u6d4e\u7684\u8ba1\u5212\u6b63\u5f0f\u7684\u542f\u52a8\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u7535\u5382\u4e4b\u5916\uff0c\u4e0e\u4e1c\u5c71\u6e2f\u914d\u5957\u7684\u5415\u6d0b\u6e2f\u6269\u5efa\u5de5\u7a0b\u7684\u5960\u57fa\u793c\u4e5f\u5728\u540c\u4e00\u5929\u8fdb\u884c\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u672a\u6765\u4e1c\u5c71\u6e2f\u7684\u4e00\u90e8\u5206\uff0c\u5415\u6d0b\u6e2f\u7684\u6539\u6269\u5efa\u9879\u76ee\u5c06\u5728\u539f\u89c4\u6a21\u4e0a\u6269\u5927\u4e09\u500d\u7684\u8d27\u8fd0\u541e\u5410\u91cf\uff0c\u5efa\u8bbe\u4e24\u5ea7\u4e07\u5428\u7ea7\u6cca\u4f4d\uff0c\u5de5\u7a0b\u5efa\u8bbe\u671f\u4e5f\u53ea\u6709\u77ed\u77ed\u7684\u4e00\u5e74\u3002\u4e00\u5e74\u4e4b\u540e\uff0c\u4e1c\u5c71\u94a2\u94c1\u7684\u8ba1\u6539\u9879\u76ee\u5c31\u5c06\u5b8c\u6210\uff0c\u4e1c\u5c71\u7535\u5382\u3001\u5343\u4e07\u5428\u94a2\u94c1\u4ea7\u4e1a\u57fa\u5730\u4ee5\u53ca\u4e1c\u5c71\u6e2f\u90fd\u5c06\u8fdb\u5165\u5efa\u8bbe\u9ad8\u6f6e\u671f\uff0c\u901a\u6e2f\u94c1\u8def\u5373\u4f7f\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u83b7\u5f97\u6279\u6587\uff0c\u5dee\u4e0d\u591a\u4e5f\u8981\u4e09\u5e74\u4ee5\u4e0a\u7684\u5efa\u8bbe\u671f\uff0c\u4e00\u5e74\u4e4b\u540e\u5c31\u9700\u8981\u6269\u5efa\u540e\u7684\u5415\u6d0b\u6e2f\u63d0\u4f9b\u8db3\u591f\u7684\u7269\u6d41\u8fd0\u8f93\u4fdd\u969c\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u4e5f\u4e0d\u96be\u60f3\u8c61\u4e00\u5e74\u4e4b\u540e\u7684\u5415\u6d0b\u6e2f\u662f\u4f55\u7b49\u7684\u7e41\u5fd9\uff0c\u4ec5\u8981\u6ee1\u8db3\u4e1c\u5c71\u94a2\u94c1\u8ba1\u6539\u540e\u5bf9\u94c1\u77ff\u77f3\u7684\u9700\u6c42\uff0c\u5415\u6d0b\u6e2f\u5dee\u4e0d\u591a\u6bcf\u5929\u5c31\u8981\u505c\u9760\u4e00\u8258\u4e07\u5428\u7ea7\u7684\u77ff\u77f3\u8239\u3002\u5728\u5efa\u8bbe\u65b0\u6cca\u4f4d\u7684\u540c\u65f6\uff0c\u5f15\u8fdb\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u5148\u8fdb\u9ad8\u6548\u7684\u7ba1\u7406\u6a21\u5f0f\uff0c\u5bf9\u5415\u6d0b\u6e2f\u7ba1\u7406\u673a\u6784\u8fdb\u884c\u5f7b\u5e95\u7684\u6539\u9020\u3001\u63d0\u9ad8\u6e2f\u53e3\u8fd0\u8f6c\u6548\u7387\u4e5f\u662f\u91cd\u4e2d\u4e4b\u91cd\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u6e2f\u5740\u4e3b\u8981\u5efa\u8bbe\u6df1\u6c34\u6cca\u4f4d\u7801\u5934\uff0c\u4e00\u671f\u89c4\u5212\u5efa\u8bbe\u5b8c\u6210\u4e4b\u540e\uff0c\u4e1c\u5c71\u6e2f\u6df1\u6c34\u7801\u5934\u6ee1\u6cca\u4f4d\u505c\u6cca\u4e5f\u53ea\u80fd\u540c\u65f6\u505c\u9760\u5341\u8258\u4e07\u5428\u7ea7\u4ee5\u4e0a\u5de8\u8f6e\u3002\u4f5c\u4e3a\u533a\u57df\u67a2\u7ebd\u6027\u8d28\u7684\u5927\u578b\u6d77\u6e2f\uff0c\u9664\u4e86\u5927\u578b\u6df1\u6c34\u6cca\u4f4d\u505c\u9760\u5de8\u8f6e\u5916\uff0c\u8fd8\u9700\u8981\u5927\u91cf\u7684\u4e2d\u5c0f\u6cca\u4f4d\u505c\u6cca\u4e07\u5428\u7ea7\u4ee5\u4e07\u5428\u7ea7\u4ee5\u4e0b\u6d77\u8f6e\uff0c\u627f\u62c5\u4e0e\u56fd\u5185\u822a\u9053\u76f8\u8854\u63a5\u7684\u4e3b\u8981\u91cd\u4efb\u3002\u4e0d\u4f1a\u8ba9\u5927\u91cf\u4e2d\u5c0f\u6cca\u4f4d\u7684\u5efa\u8bbe\u53bb\u6324\u5360\u4e1c\u6d77\u5c9b\u7a00\u7f3a\u7684\u6df1\u6c34\u822a\u9053\u8d44\u6e90\uff0c\u672a\u6765\u4ece\u5415\u6d0b\u6e2f\u5230\u4e1c\u5c71\u5c9b\u4e4b\u95f4\u7684\u6d77\u5cb8\u90fd\u5c06\u7ed9\u5927\u5927\u5c0f\u5c0f\u7684\u7801\u5934\u586b\u6ee1\uff0c\u5f62\u6210\u4e00\u5ea7\u8d85\u5927\u578b\u7684\u7efc\u5408\u6d77\u6e2f\u57ce\u5e02\uff0c\u5f7b\u5e95\u9006\u8f6c\u65b0\u4ead\u7684\u7ecf\u6d4e\u9762\u8c8c\u3002</p><p>\u3000\u3000\u5bf9\u4e1c\u5c71\u6e2f\u7684\u76f4\u63a5\u5f00\u53d1\u4e2d\uff0c\u9526\u6e56\u5546\u4e8b\u4ee3\u8868\u9526\u6e56\u4e2d\u6301\u670916\uff05\u7684\u6743\u76ca\uff0c\u4e0d\u8bf4\u5b9e\u9645\u7684\u63a7\u5236\u529b\uff0c\u4f5c\u4e3a\u5e55\u540e\u7684\u4e3b\u5bfc\u8005\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u7406\u7531\u51fa\u5e2d\u8fd9\u6837\u7684\u5960\u57fa\u793c\u3002</p><p>\u3000\u3000\u518d\u8bf4\u4e1c\u5c71\u6e2f\u5efa\u8bbe\u662f\u7531\u4e00\u7cfb\u5217\u5927\u4e2d\u578b\u9879\u76ee\u7ec4\u5408\u6210\u7684\u8d85\u5927\u578b\u7efc\u5408\u5de5\u7a0b\uff0c\u4e5f\u4e0d\u662f\u4e00\u5e74\u4e24\u5e74\u5c31\u80fd\u5efa\u8bbe\u5b8c\u6210\u7684\uff0c\u8981\u60f3\u6709\u7ffb\u5929\u8986\u5730\u7684\u53d8\u5316\uff0c\u5dee\u4e0d\u591a\u4e5f\u8981\u6709\u4e03\u516b\u5e74\u7684\u65f6\u95f4\u624d\u591f\u3002\u8fd9\u4e03\u516b\u5e74\u91cc\uff0c\u5927\u5927\u5c0f\u5c0f\u7684\u5960\u57fa\u793c\u4e0d\u65ad\uff0c\u5f20\u606a\u54ea\u91cc\u6709\u8fd9\u4e48\u591a\u7684\u95f2\u5de5\u592b\u53bb\u53c2\u52a0\u8fd9\u79cd\u5960\u57fa\u793c\u554a\uff1f\u7d22\u6027\u4e00\u5f00\u59cb\u5c31\u4e0d\u7406\u776c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5510\u5a67\u4e5f\u4e0d\u80fd\u4ece\u9999\u6e2f\u56de\u6765\u6574\u4e2a\u5047\u671f\u90fd\u7559\u5728\u5efa\u90ba\u966a\u7740\u5f20\u606a\uff0c\u5979\u603b\u8981\u53bb\u91d1\u5c71\u4f4f\u4e00\u6bb5\u65f6\u95f4\u5b89\u6170\u5979\u5988\u5988\u7684\u601d\u5973\u4e4b\u5fc3\uff0c\u5f20\u606a\u5374\u8981\u7559\u5728\u5efa\u90ba\u8ba4\u771f\u7684\u51c6\u5907\u671f\u672b\u8003\u8bd5\u3002</p><p>\u3000\u3000\u529f\u8bfe\u867d\u7136\u7b80\u5355\uff0c\u603b\u4e5f\u8981\u82b1\u4e9b\u65f6\u95f4\u51c6\u5907\u4e00\u4e0b\uff0c\u8fd9\u4e9b\u4e66\u672c\u91cd\u65b0\u6e29\u4e60\u4e00\u4e0b\uff0c\u4e0d\u662f\u8bf4\u6beb\u65e0\u76ca\u5904\u3002\u6700\u8ba9\u4eba\u5934\u75bc\u7684\u5c31\u662f\u4e1c\u5927\u7684\u671f\u672b\u8003\u8bd5\u6bd4\u8f83\u5206\u6563\uff0c\u4e0d\u4f1a\u96c6\u4e2d\u5728\u4e09\u4e24\u5929\u95f4\u8003\u5b8c\uff0c\u4e00\u5b66\u671f\u4e03\u516b\u95e8\u79d1\u76ee\u62d6\u62d6\u62c9\u62c9\u5dee\u4e0d\u591a\u8981\u5927\u534a\u4e2a\u6708\u7684\u65f6\u95f4\u624d\u80fd\u8003\u8bd5\uff0c\u8fd9\u5927\u534a\u4e2a\u6708\u91cc\uff0c\u5f20\u606a\u5dee\u4e0d\u591a\u4e0d\u80fd\u79bb\u5f00\u5efa\u90ba\u3002</p><p>\u3000\u3000\u56fe\u4e66\u9986\u9605\u89c8\u5ba4\u91cc\u5c31\u4eba\u6ee1\u4e3a\u60a3\uff0c\u671f\u672b\u5927\u5bb6\u90fd\u5fd9\u7740\u590d\u4e60\u51c6\u5907\u8003\u8bd5\u662f\u4e00\u56de\u4e8b\uff0c\u5efa\u90ba\u4eca\u5e74\u7684\u51ac\u5929\u4e5f\u683c\u5916\u7684\u5bd2\u51b7\uff0c\u8fde\u7eed\u51fa\u73b0\u96f6\u4e0b\u516b\u4e5d\u5ea6\u7684\u5929\u6c14\uff0c\u5728\u5bbf\u820d\u6216\u5728\u6559\u5ba4\u91cc\uff0c\u90fd\u4f1a\u89c9\u5f97\u9634\u5bd2\u523a\u9aa8\uff0c\u6709\u6696\u6c14\u7684\u56fe\u4e66\u9986\u81ea\u7136\u6210\u4e86\u907f\u5bd2\u7684\u597d\u53bb\u5904\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5c31\u4f1a\u89c9\u5f97\u56fe\u4e66\u9986\u9605\u89c8\u5ba4\u592a\u5c0f\u4e86\uff0c\u7684\u786e\u662f\u592a\u5c0f\u4e86\uff0c\u4e1c\u5927\u71d5\u5f52\u6e56\u672c\u90e8\u7684\u672c\u79d1\u751f\u52a0\u7814\u7a76\u751f\u603b\u6570\u5df2\u7ecf\u63a5\u8fd1\u4e24\u4e07\u4eba\uff0c\u800c\u9605\u89c8\u5ba4\u91cc\u7684\u5ea7\u4f4d\u624d\u4e24\u767e\u591a\u4e2a\u3002\u5bf9\u4e1c\u5927\u6765\u8bf4\uff0c\u9605\u89c8\u5ba4\u5982\u6b64\u7684\u89c4\u6a21\uff0c\u5373\u4f7f\u5e73\u65f6\u4e5f\u8fdc\u8fdc\u4e0d\u591f\u7528\uff0c\u597d\u5728\u5e73\u65f6\u6559\u5ba4\u90fd\u662f\u5bf9\u5b66\u751f\u5f00\u653e\uff0c\u4e5f\u53ef\u4ee5\u7559\u5728\u5bbf\u820d\u91cc\u770b\u4e66\uff0c\u552f\u6709\u590f\u51ac\u4e24\u5b63\u6700\u662f\u96be\u71ac\uff0c\u5bbf\u820d\u4e0e\u6559\u5ba4\u91cc\u90fd\u6ca1\u6709\u7a7a\u8c03\uff0c\u90a3\u65f6\u5019\u5c31\u4f1a\u89c9\u5f97\u9605\u89c8\u5ba4\u683c\u5916\u7684\u5c0f\uff0c\u504f\u504f\u5efa\u90ba\u53c8\u662f\u51ac\u5bd2\u590f\u708e\u7684\u57ce\u5e02\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4e66\u631f\u5728\u814b\u4e0b\uff0c\u7ad9\u5728\u9605\u89c8\u5ba4\u7684\u95e8\u53e3\uff0c\u770b\u5230\u8bb8\u591a\u4eba\u8d70\u8fc7\u6765\uff0c\u90fd\u9887\u4e3a\u65e0\u5948\u7684\u6298\u8fd4\uff0c\u4e0d\u8fc7\u597d\u4e9b\u4eba\u5bf9\u6b64\u90fd\u6709\u51c6\u5907\uff0c\u968f\u8eab\u7a7f\u7740\u539a\u5b9e\u7684\u519b\u68c9\u5927\u8863\uff0c\u770b\u5230\u8fd9\u91cc\u4eba\u6ee1\uff0c\u8f6c\u8eab\u5c31\u5f80\u6559\u5b66\u697c\u8d70\u53bb\u3002\u5f20\u606a\u5728\u524d\u5c18\u5f80\u4e16\u91cc\uff0c\u5bd2\u51b7\u7684\u51ac\u5b63\u4e5f\u90fd\u7a7f\u8fd9\u79cd\u68c9\u5927\u8863\u5728\u6559\u5ba4\u91cc\u770b\u4e66\uff0c\u53ea\u662f\u590f\u5929\u6bd4\u8f83\u96be\u71ac\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u8d70\u5427\uff0c\u6211\u4eec\u53e6\u627e\u5730\u65b9\u590d\u4e60\u53bb\uff01\u201d\u5f20\u606a\u4e0e\u5c06\u4e66\u672c\u62b1\u5728\u80f8\u53e3\u7684\u9648\u5983\u84c9\u4e5f\u53ea\u80fd\u9000\u4e86\u51fa\u6765\uff0c\u4e5f\u4e0d\u4f1a\u53bb\u6559\u5ba4\u53d7\u7f6a\uff0c\u5f80\u5b66\u5e9c\u5df7\u8d70\u53bb\uff0c\u627e\u5bb6\u4e66\u5427\u6216\u8005\u5496\u5561\u5385\u3001\u8336\u5ea7\uff0c\u4e5f\u53ef\u4ee5\u5b89\u9759\u7684\u770b\u4e66\u3002</p><p>\u3000\u3000\u4e5f\u53ea\u80fd\u6028\u5b66\u5e9c\u5df7\u7684\u7ecf\u8425\u8fc7\u4e8e\u6210\u529f\u4e86\uff0c\u53c8\u4f4d\u4e8e\u71d5\u5f52\u6e56\u9ad8\u6821\u7fa4\u7684\u4e2d\u5fc3\uff0c\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u51fa\u6765\u8f83\u665a\uff0c\u5b66\u5e9c\u5df7\u91cc\u7684\u51e0\u5bb6\u5496\u5561\u5385\u3001\u8336\u5ea7\u4ee5\u53ca\u4e66\u5427\u91cc\u90fd\u6ee1\u4e86\uff0c\u80af\u5fb7\u57fa\u5e97\u627e\u4e0d\u5230\u7a7a\u5ea7\u4f4d\u3002</p><p>\u3000\u3000\u201c\u5728\u5bd2\u51b7\u7684\u51ac\u5929\uff0c\u5728\u6709\u7a7a\u8c03\u7684\u623f\u95f4\u91cc\uff0c\u5750\u5728\u4e34\u8857\u7684\u7a97\u524d\uff0c\u559d\u7740\u4e00\u676f\u70ed\u5496\u5561\u6e29\u4e66\u7684\u611f\u89c9\u771f\u662f\u4e0d\u9519\u2026\u2026\u201d\u4ece\u80af\u5fb7\u57fa\u5e97\u91cc\u9000\u51fa\u6765\uff0c\u5f20\u606a\u8ddf\u9648\u5983\u84c9\u611f\u6168\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u53bb\u6559\u5ba4\u5427\uff1f\u6211\u5148\u56de\u5bbf\u820d\u6362\u4ef6\u539a\u4e9b\u5916\u5957\uff0c\u6211\u53ef\u6ca1\u6709\u4f60\u60f3\u7684\u5a07\u6c14\uff0c\u201d\u9648\u5983\u84c9\u7b11\u7740\u8bf4\uff0c\u5979\u4f3c\u4e4e\u523b\u610f\u7684\u5fd8\u6389\u53bb\u9752\u5e74\u516c\u5bd3\u91cc\u4f1a\u66f4\u8212\u5766\uff0c\u201c\u867d\u7136\u5728\u9605\u89c8\u5ba4\u91cc\u770b\u4e66\u66f4\u8212\u670d\u4e9b\uff0c\u6ca1\u6709\u4f4d\u5b50\u4e5f\u6ca1\u6709\u529e\u6cd5\u3002\u201d</p><p>\u3000\u3000\u201c\u966a\u6211\u5148\u53bb\u4e2a\u5730\u65b9\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4f38\u624b\u5c06\u9648\u5983\u84c9\u624b\u91cc\u7684\u4e66\u62ff\u8fc7\u6765\uff0c\u201c\u4f60\u5c06\u624b\u63d2\u53e3\u888b\u91cc\uff0c\u4e0d\u8981\u51b0\u7740\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\u3002\u201d\u9648\u5983\u84c9\u6413\u4e86\u6413\u7ed9\u51b0\u5f97\u901a\u7ea2\u7684\u5c0f\u624b\uff0c\u63d2\u5230\u53e3\u888b\u91cc\uff0c\u8ddf\u7740\u5f20\u606a\u7a7f\u7740\u5185\u5df7\u5f80\u5317\u8d70\u3002\u5317\u98ce\u6b63\u76db\uff0c\u98ce\u5439\u5230\u8138\u4e0a\u6709\u7ed9\u5200\u5272\u4f3c\u7684\u75bc\uff0c\u5f20\u606a\u4f3c\u4e4e\u611f\u89c9\u5230\u9648\u5983\u84c9\u7684\u96be\u53d7\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u5e2e\u9648\u5983\u84c9\u62c9\u8d77\u5916\u5957\u7684\u515c\u5e3d\u6234\u597d\uff0c\u5c3d\u53ef\u80fd\u6321\u4f4f\u5979\u7ed9\u51b0\u5f97\u5fae\u7ea2\u7684\u5c0f\u8138\uff0c\u5bf9\u5979\u8bf4\uff1a\u201c\u4f60\u8eb2\u5728\u6211\u7684\u8eab\u5b50\u540e\u9762\uff0c\u53ef\u4ee5\u6321\u7740\u98ce\u3002\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u4fbf\u8eb2\u5728\u5f20\u606a\u7684\u8eab\u540e\u8ddf\u7740\u4ed6\u8d70\uff0c\u8d70\u5230\u4e1c\u5927\u4e0e\u97f3\u4e50\u5b66\u9662\u4e4b\u95f4\u7684\u90a3\u6761\u8def\u5f80\u897f\u8d70\u3002</p><p>\u3000\u3000\u5728\u9752\u5e74\u516c\u5bd3\u7b2c\u4e09\u793e\u533a\u5317\u5927\u95e8\u4e0e\u4e1c\u5927\u5317\u5927\u95e8\u4e4b\u95f4\uff0c\u539f\u5148\u662f\u4e00\u7247\u6c11\u623f\uff0c\u5982\u4eca\u6c11\u623f\u5899\u58c1\u4f53\u4e0a\u90fd\u7528\u767d\u77f3\u7070\u5199\u6ee1\u201c\u62c6\u201d\uff0c\u6709\u4e9b\u6c11\u623f\u5df2\u7ecf\u5f00\u59cb\u62c6\u9664\u4e86\uff0c\u53ea\u4e0d\u8fc7\u4eca\u5929\u5efa\u90ba\u5929\u6c14\u683c\u5916\u7684\u5bd2\u51b7\uff0c\u597d\u4e9b\u5ba4\u5916\u5de5\u7a0b\u90fd\u505c\u4e86\u5de5\u3002</p><p>\u3000\u3000\u80cc\u7740\u98ce\uff0c\u5f20\u606a\u5728\u8fd9\u91cc\u505c\u4e0b\u6765\uff0c\u5bd2\u51b7\u7684\u5929\u6c14\uff0c\u8def\u4e0a\u7684\u884c\u4eba\u3001\u8f66\u8f86\u90fd\u5f88\u5c11\uff0c\u5f20\u606a\u8ddf\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u6709\u65f6\u5019\uff0c\u6211\u5728\u60f3\uff0c\u8981\u662f\u6211\u6ca1\u6709\u94b1\uff0c\u4e0d\u80fd\u50cf\u5bb6\u5ead\u5bbd\u88d5\u7684\u5e74\u8f7b\u4eba\u90a3\u6837\u53ef\u4ee5\u53bb\u7f51\u5427\u3001\u53bb\u8fea\u5385\u3001\u53bb\u5496\u5561\u5385\u6d88\u78e8\u65f6\u5149\uff0c\u6211\u5c31\u5e0c\u671b\u80fd\u6709\u4e00\u5ea7\u56fe\u4e66\u9986\u53ef\u4ee5\u8ba9\u6211\u5b89\u5b89\u9759\u9759\u7684\u8bfb\u4e66\uff0c\u590f\u5929\u4e0d\u4f1a\u70ed\u3001\u51ac\u5929\u4e0d\u4f1a\u51b7\u2014\u2014\u5b66\u5e9c\u5df7\u7e41\u534e\u4f3c\u9526\uff0c\u7ec8\u7a76\u662f\u90a3\u4e9b\u5bb6\u5ead\u5bbd\u88d5\u7684\u5b66\u751f\u53ef\u53bb\u5e38\u53bb\u6d88\u8c34\u7684\u5730\u65b9\uff0c\u71d5\u5f52\u6e56\u9644\u8fd1\u6709\u5341\u4e07\u5927\u5b66\u751f\uff0c\u4e0d\u8981\u8bf4\u5bb6\u5ead\u72b6\u51b5\u4e00\u822c\u7684\uff0c\u8d2b\u56f0\u5b66\u751f\u7684\u6bd4\u4f8b\u4e5f\u5f88\u9ad8\uff0c\u4e24\u4e09\u4e07\u4eba\u603b\u662f\u8981\u6709\u7684\uff0c\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0c\u9ad8\u6d88\u8d39\u7684\u5730\u65b9\u53bb\u4e0d\u4e86\uff0c\u4f46\u6211\u5e0c\u671b\u8fd9\u5ea7\u57ce\u5e02\u53ef\u4ee5\u5927\u58f0\u7684\u5bf9\u4ed6\u4eec\u8bf4\uff1a\u6ca1\u94b1\uff0c\u6ca1\u5173\u7cfb\uff0c\u8fd8\u53ef\u4ee5\u53bb\u770b\u4e66\uff01\u201d\u5f20\u606a\u624b\u6bd4\u5212\u7740\u524d\u9762\u90a3\u7247\u6216\u5f85\u62c6\u6216\u5df2\u62c6\u7684\u6c11\u623f\uff0c\u95ee\u9648\u5983\u84c9\uff0c\u201c\u8fd9\u91cc\u5efa\u4e00\u5ea7\u56fe\u4e66\u9986\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\u8981\u5efa\u56fe\u4e66\u9986\u5417\uff1f\u201d\u9648\u5983\u84c9\u5174\u594b\u7684\u95ee\u9053\uff0c\u201c\u90a3\u771f\u662f\u592a\u597d\u4e86\uff01\u4e4b\u524d\u542c\u675c\u98de\u4ed6\u4eec\u8bf4\u8fd9\u91cc\u4e0d\u662f\u4f5c\u4e3a\u5b66\u5e9c\u5df7\u7684\u5ef6\u4f38\u6bb5\u5f00\u53d1\u5417\uff1f\u4ed6\u4eec\u90fd\u8ba1\u5212\u597d\u5728\u8fd9\u91cc\u5efa\u5f00\u4e00\u95f4\u521b\u57df\u7f51\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0d\u7ba1\u4ed6\u4eec\u4e4b\u524d\u4ec0\u4e48\u6253\u7b97\uff0c\u4f46\u662f\u773c\u775b\u4e0d\u80fd\u53ea\u76ef\u7740\u5546\u4e1a\u5229\u76ca\u4e0a\u2014\u2014\u6211\u521a\u624d\u7a81\u7136\u89c9\u5f97\u5728\u8fd9\u5757\u5730\u5c31\u5e94\u8be5\u62ff\u6765\u5efa\u56fe\u4e66\u9986\uff0c\u624d\u62c9\u4f60\u8fc7\u6765\u4e00\u8d77\u770b\uff0c\u4f60\u89c9\u5f97\u600e\u4e48\u6837\uff1f\u4f60\u8981\u662f\u4e5f\u8d5e\u540c\u6211\u7684\u89c2\u70b9\uff0c\u4e5f\u89c9\u5f97\u8fd9\u5757\u5730\u5c31\u5e94\u8be5\u62ff\u6765\u5efa\u56fe\u4e66\u9986\uff0c\u6211\u5c31\u7ed9\u90b5\u81f3\u521a\u6253\u7535\u8bdd\uff0c\u8ba9\u4ed6\u5c06\u4ed6\u7684\u89c4\u5212\u4e66\u4e22\u5230\u706b\u7089\u91cc\u70e7\u6389\uff0c\u8fd9\u5757\u5730\u6211\u4eec\u8981\u4e86\u3002\u201d\u5f20\u606a\u5927\u58f0\u95ee\u9648\u5983\u84c9\u3002</p><p>\u3000\u3000\u4f3c\u4e4e\u8fd9\u4e48\u5927\u7684\u51b3\u5b9a\u4e00\u4e0b\u5b50\u90fd\u843d\u5728\u5979\u7684\u80a9\u4e0a\uff0c\u9648\u5983\u84c9\u770b\u7740\u5f20\u606a\u5728\u5bd2\u51b0\u7684\u7a7a\u6c14\u91cc\u8d8a\u53d1\u6e05\u6f88\u7684\u53d1\u775b\uff0c\u634f\u7d27\u7c89\u62f3\u626c\u4e86\u626c\uff0c\u4fcf\u8138\u5174\u594b\u7684\u7eef\u7ea2\u660e\u8273\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5757\u5730\u6211\u4eec\u8981\u4e86\uff0c\u5efa\u56fe\u4e66\u9986\uff0c\u8fd9\u5757\u5730\u5c31\u5e94\u8be5\u62ff\u6765\u5efa\u56fe\u4e66\u9986\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4ece\u88e4\u515c\u91cc\u638f\u51fa\u624b\u673a\u6765\uff0c\u62e8\u7ed9\u90b5\u81f3\u521a\uff0c\u95ee\u4ed6\u5728\u54ea\u91cc\uff0c\u90b5\u81f3\u521a\u5728\u7535\u8bdd\u8bf4\u4ed6\u5728\u5f00\u4f1a\uff0c\u5728\u7814\u7a76\u5982\u4f55\u63a8\u52a8\u4e1c\u5927\u5317\u9762\u5730\u5757\u7684\u5f00\u53d1\u8fdb\u5ea6\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8981\u5f00\u4f1a\u4e86\uff0c\u4e5f\u4e0d\u8981\u8ba8\u8bba\u4f60\u4eec\u7684\u4ec0\u4e48\u5f00\u53d1\u8ba1\u5212\u4e86\uff0c\u8fd9\u5757\u5730\u6211\u4eec\u8981\u4e86\uff0c\u6211\u5c31\u5728\u8fd9\u91cc\uff0c\u770b\u7740\u4f60\u4eec\u7684\u5de5\u5730\uff0c\u4f60\u4e4b\u524d\u6709\u4ec0\u4e48\u6253\u7b97\u90fd\u53d6\u6d88\u6389\uff0c\u635f\u5931\u7684\u94b1\uff0c\u6211\u90fd\u8865\u8d34\u7ed9\u4f60\u4eec\uff0c\u4f60\u7ed9\u6211\u5728\u8fd9\u5757\u5730\u4e0a\u5efa\u4e00\u5ea7\u56fe\u4e66\u9986\u6765\uff0c\u4e00\u5ea7\u81f3\u5c11\u6709\u4e00\u4e07\u4e2a\u9605\u89c8\u5ea7\u5e2d\u7684\u56fe\u4e66\u9986\u2026\u2026\u4f60\u8981\u8d76\u8fc7\u6765\uff0c\u8fd9\u4e48\u51b7\u7684\u5929\u6c14\uff0c\u4f60\u8ba9\u6211\u5728\u5929\u5bd2\u5730\u51bb\u7684\u9a6c\u8def\u8fb9\u7b49\u4f60\u5341\u5206\u949f\uff1f\u90a3\u597d\u5427\u2026\u2026\u5c31\u7b49\u4f60\u5341\u5206\u949f\uff0c\u6211\u5148\u53bb\u8def\u8fb9\u7684\u5c0f\u5e97\u91cc\u907f\u907f\u98ce\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5148\u627e\u4e2a\u5730\u65b9\u8eb2\u4e00\u4f1a\u513f\u98ce\u2026\u2026\u201d\u5f20\u606a\u6302\u4e86\u7535\u8bdd\uff0c\u8ba9\u9648\u5983\u84c9\u5c06\u624b\u63d2\u53e3\u888b\u91cc\uff0c\u7ed9\u98ce\u5439\u5f97\u751f\u75bc\uff0c\u5f80\u4e1c\u8d70\uff0c\u9752\u5e74\u516c\u5bd3\u7b2c\u4e09\u793e\u533a\u5317\u95e8\u4e34\u8857\u5e95\u5c42\u90fd\u662f\u7a97\u540d\u51e0\u4eae\u7684\u5546\u94fa\uff0c\u8fd9\u91cc\u7684\u5546\u94fa\u662f\u4e0e\u5b66\u5e9c\u5df7\u6eb6\u4e3a\u4e00\u4f53\u7684\uff0c\u518d\u5f80\u897f\u90a3\u7247\u6c11\u623f\u62c6\u4e86\u4f5c\u5b66\u5e9c\u5df7\u5546\u4e1a\u8857\u7684\u5ef6\u4f38\u6bb5\u7684\u786e\u5f88\u597d\uff0c\u4e0d\u8fc7\u5f20\u606a\u8fd9\u65f6\u8981\u5c06\u8fd9\u5757\u5730\u62ff\u51fa\u6765\u5efa\u56fe\u4e66\u9986\u3002</p></article>"}}}]);
