(window.webpackJsonp=window.webpackJsonp||[]).push([[12],{34:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e00\u8282 \u7b80\u5355\u7684\u5165\u5b66",content:"<article><h2>\u7b2c\u5341\u4e00\u8282 \u7b80\u5355\u7684\u5165\u5b66</h2><p>\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u743c\u6069\u80cc\u7740\u5305\u88f9\uff0c\u5e26\u7740\u4e00\u4e9b\u7b80\u5355\u7684\u968f\u8eab\u7269\u54c1\uff0c\u8d70\u8fdb\u5deb\u5e08\u5b66\u6821\u3002\u91cd\u91cd\u7684\u94c1\u95e8\u4ece\u8eab\u540e\u6084\u65e0\u58f0\u606f\u5730\u5173\u4e0a\uff0c\u8ba9\u4ed6\u611f\u89c9\u81ea\u5df1\u50cf\u662f\u88ab\u5173\u8fdb\u4e86\u96c6\u4e2d\u8425\u3002</p><p>\u8bfb\u4e66\u4e0a\u5b66\uff0c\u5bf9\u4ed6\u6765\u8bf4\u81ea\u7136\u5e76\u4e0d\u964c\u751f\uff0c\u4e0d\u8fc7\u5947\u5e7b\u4e16\u754c\u7684\u5deb\u5e08\u5b66\u6821\uff0c\u663e\u7136\u548c\u5730\u7403\u4e0a\u7684\u5927\u5b66\u975e\u5e38\u4e0d\u540c\u3002\u5982\u679c\u8981\u52c9\u5f3a\u6765\u8bf4\u7684\u8bdd\uff0c\u54c8\u5229\u6ce2\u7279\u7684\u6545\u4e8b\u91cc\uff0c\u4e5f\u6709\u5deb\u5e08\u5b66\u6821\uff0c\u5012\u662f\u548c\u6b64\u5904\u6709\u51e0\u5206\u76f8\u50cf\u3002</p><p>\u5fc5\u987b\u8bf4\uff0c\u4e24\u767e\u91d1\u5e01\u7684\u5b66\u8d39\u867d\u7136\u6602\u8d35\uff0c\u5374\u4e5f\u771f\u6ca1\u767d\u82b1\u3002\u5deb\u5e08\u5b66\u6821\u4e2d\uff0c\u6bcf\u4e2a\u5b66\u751f\u90fd\u62e5\u6709\u4e00\u4e2a\u5355\u72ec\u7684\u5bbf\u820d\uff0c\u5305\u62ec\u5367\u5ba4\u3001\u4f1a\u5ba2\u5385\u548c\u536b\u751f\u95f4\uff1b\u5230\u4e86\u4e09\u5e74\u7ea7\uff0c\u8fd8\u80fd\u62e5\u6709\u72ec\u7acb\u7684\u5b9e\u9a8c\u5ba4\u3002</p><p>\u4e00\u65e5\u4e09\u9910\u90fd\u662f\u98df\u5802\u514d\u8d39\u4f9b\u5e94\uff0c\u51e0\u5341\u79cd\u83dc\u80b4\u4efb\u7531\u9009\u62e9\u3002\u5b66\u751f\u5728\u6821\u671f\u95f4\uff0c\u5fc5\u987b\u7a7f\u7edf\u4e00\u7684\u5236\u670d\u2014\u2014\u4e5f\u5c31\u662f\u5deb\u5e08\u888d\u4e86\uff0c\u8d39\u7528\u540c\u6837\u5df2\u7ecf\u5305\u542b\u5728\u90a3\u4e24\u767e\u91d1\u5e01\u7684\u5b66\u8d39\u91cc\u3002\u743c\u6069\u521a\u8fdb\u5b66\u6821\uff0c\u5c31\u88ab\u53d1\u4e86\u4e94\u5957\u9ed1\u8272\u7684\u5deb\u5e08\u957f\u888d\uff0c\u4e94\u5957\u7070\u8272\u7684\u6597\u7bf7\u3002</p><p>\u8fd9\u79cd\u53d1\u7ed9\u4e00\u5e74\u7ea7\u5b66\u751f\u7684\u5deb\u5e08\u888d\uff0c\u81ea\u7136\u4e0d\u4f1a\u662f\u4ec0\u4e48\u4e0a\u7b49\u8d27\u8272\uff0c\u526a\u88c1\u5de5\u827a\u867d\u7136\u4e0d\u9519\uff0c\u5374\u6ca1\u6709\u9644\u7740\u4efb\u4f55\u5f3a\u5316\u6216\u8005\u795d\u798f\u9b54\u6cd5\u3002\u6bd4\u8d77\u666e\u901a\u7684\u957f\u888d\uff0c\u6700\u7279\u522b\u7684\u5730\u65b9\u5c31\u662f\u5b83\u7684\u5185\u4fa7\uff0c\u5bc6\u5bc6\u9ebb\u9ebb\u7f1d\u7740\u51e0\u5341\u4e2a\u5c0f\u53e3\u888b\u3002</p><p>\u8fd9\u4e9b\u5c0f\u53e3\u888b\uff0c\u90fd\u662f\u7528\u6765\u88c5\u6cd5\u672f\u6750\u6599\u7528\u7684\u3002</p><p>\u8fd9\u4e16\u754c\u4e0a\u7684\u9b54\u6cd5\uff0c\u5206\u795e\u672f\u548c\u5965\u672f\u3002\u5deb\u5e08\u6240\u4fee\u4e60\u7684\u5965\u672f\uff0c\u7edd\u5927\u591a\u6570\u90fd\u8981\u501f\u52a9\u4e00\u5b9a\u7684\u65bd\u6cd5\u6750\u6599\u4f5c\u4e3a\u5a92\u4ecb\u624d\u80fd\u987a\u5229\u65bd\u5c55\uff0c\u552f\u6709\u6781\u5c11\u6570\u662f\u4f8b\u5916\u3002</p><p>\u6240\u4ee5\u5deb\u5e08\u51fa\u95e8\uff0c\u5c24\u5176\u662f\u8981\u53bb\u5192\u9669\u6216\u8005\u4f5c\u6218\uff0c\u7d20\u6765\u90fd\u662f\u51c6\u5907\u597d\u4e00\u5927\u5806\u6750\u6599\uff0c\u5206\u95e8\u522b\u7c7b\uff0c\u4ed4\u7ec6\u88c5\u5728\u5deb\u5e08\u888d\u5185\u4fa7\u7684\u5c0f\u53e3\u888b\u91cc\u3002\u54ea\u79cd\u6750\u6599\u653e\u54ea\u4e2a\u53e3\u888b\uff0c\u8fd9\u81ea\u7136\u770b\u4e2a\u4eba\u4e60\u60ef\uff0c\u4f46\u6240\u6709\u5deb\u5e08\u90fd\u5fc5\u987b\u7262\u7262\u8bb0\u4f4f\u81ea\u5df1\u653e\u6750\u6599\u7684\u4f4d\u7f6e\uff0c\u5426\u5219\u4e07\u4e00\u6218\u6597\u4e2d\u8981\u65bd\u67d0\u4e2a\u9b54\u6cd5\uff0c\u63a2\u624b\u5165\u6000\u7ed3\u679c\u4e00\u628a\u6293\u9519\u4e86\u6cd5\u672f\u6750\u6599\uff0c\u90a3\u5c31\u545c\u547c\u54c0\u54c9\uff0c\u7b49\u7740\u88ab\u5bf9\u624b\u8f70\u6740\u5427\u3002</p><p>\u5deb\u5e08\u7684\u57fa\u672c\u529f\u4e4b\u4e00\uff0c\u5c31\u662f\u8981\u80fd\u5728\u6700\u5feb\u65f6\u95f4\u5185\uff0c\u4ece\u957f\u888d\u5185\u4fa7\u7684\u51e0\u5341\u4e2a\u53e3\u888b\u4e2d\uff0c\u51c6\u786e\u53d6\u51fa\u9700\u8981\u7684\u6750\u6599\u3002\u771f\u6b63\u5728\u5b9e\u6218\u4e2d\uff0c\u751f\u6b7b\u5f80\u5f80\u51b3\u4e8e\u4e00\u77ac\uff0c\u62fc\u7684\u5c31\u662f\u901f\u5ea6\u3002\u8c01\u80fd\u62a2\u5148\u53d6\u51fa\u6750\u6599\uff0c\u5b8c\u6210\u9b54\u6cd5\uff0c\u8c01\u5c31\u66f4\u6709\u53ef\u80fd\u6d3b\u4e0b\u6765\u3002</p><p>\u65e2\u7136\u662f\u5deb\u5e08\u5b66\u6821\uff0c\u53d1\u7ed9\u5b66\u751f\u7684\uff0c\u81ea\u7136\u4e0d\u4ec5\u4ec5\u662f\u5deb\u5e08\u888d\uff0c\u8fd8\u5305\u62ec\u6cd5\u6756\u548c\u9b54\u6cd5\u4e66\u3002</p><p>\u6240\u8c13\u6cd5\u6756\uff0c\u5176\u5b9e\u4e5f\u4e0d\u8fc7\u5c31\u662f\u4e00\u6839\u6a61\u6728\u5236\u4f5c\u7684\u77ed\u68cd\uff0c\u5927\u7ea6\u6709\u534a\u4eba\u9ad8\u3002\u548c\u5deb\u5e08\u888d\u4e00\u6837\uff0c\u53d1\u7ed9\u4e00\u5e74\u7ea7\u5b66\u751f\u7684\u4e1c\u897f\uff0c\u4e0d\u4f1a\u662f\u4ec0\u4e48\u4e0a\u7b49\u8d27\u8272\u3002\u548c\u743c\u6069\u4ee5\u524d\u770b\u8fc7\u7684\u5f88\u591a\u5947\u5e7b\u5c0f\u8bf4\u4e0d\u540c\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e2d\uff0c\u6cd5\u6756\u5bf9\u5deb\u5e08\u65bd\u6cd5\u800c\u8a00\u5e76\u4e0d\u662f\u4ec0\u4e48\u5fc5\u5907\u5de5\u5177\u3002\u8bb0\u5f97\u5728\u54c8\u5229\u6ce2\u7279\u7684\u6545\u4e8b\u91cc\uff0c\u5deb\u5e08\u4e00\u65e6\u6ca1\u6709\u6cd5\u6756\u5728\u624b\uff0c\u7eb5\u7136\u6709\u5929\u5927\u7684\u6cd5\u529b\u4e5f\u65e0\u4ece\u65bd\u5c55\uff1b\u4f46\u5728\u8fd9\u4e2a\u4e16\u754c\u91cc\uff0c\u559c\u6b22\u7a7a\u624b\u65bd\u6cd5\u7684\u5deb\u5e08\u591a\u5f97\u662f\u3002\u5f53\u7136\uff0c\u5b66\u6821\u8fd8\u662f\u6309\u60ef\u4f8b\uff0c\u7ed9\u6bcf\u4e2a\u5b66\u751f\u90fd\u53d1\u4e86\u4e00\u6839\u3002</p><p>\u9b54\u6cd5\u4e66\u5bf9\u4e8e\u5deb\u5e08\u800c\u8a00\uff0c\u5374\u662f\u65bd\u6cd5\u7684\u5fc5\u5907\u5de5\u5177\u2014\u2014\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u51c6\u5907\u9b54\u6cd5\u7684\u5fc5\u5907\u5de5\u5177\u3002\u5728\u8fd9\u4e2a\u4e16\u754c\u91cc\uff0c\u5deb\u5e08\u60f3\u8981\u65bd\u6cd5\uff0c\u90fd\u662f\u8981\u4e8b\u5148\u51c6\u5907\u597d\u7684\u3002\u6ca1\u6709\u9b54\u6cd5\u4e66\uff0c\u5deb\u5e08\u5c31\u65e0\u6cd5\u4e0e\u9b54\u6cd5\u7f51\u7edc\u53d6\u5f97\u8054\u7ed3\uff0c\u65e0\u6cd5\u4ece\u7f51\u7edc\u4e2d\u63d0\u53d6\u529b\u91cf\uff0c\u51c6\u5907\u9b54\u6cd5\u3002</p><p>\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u9b54\u6cd5\u4e66\u5c31\u662f\u4e00\u67c4\u548c\u9b54\u6cd5\u7f51\u7edc\u8054\u7ed3\u7684\u94a5\u5319\uff0c\u5f53\u7136\uff0c\u5e76\u975e\u552f\u4e00\u7684\u94a5\u5319\u3002</p><p>\u7a7f\u4e0a\u9ed1\u8272\u7684\u5deb\u5e08\u888d\uff0c\u624b\u6301\u6cd5\u6756\uff0c\u6000\u4e2d\u63e3\u7740\u9b54\u6cd5\u4e66\uff0c\u743c\u6069\u5728\u955c\u5b50\u91cc\u81ea\u6211\u7167\u7167\uff0c\u201c\u5514\uff0c\u4f3c\u4e4e\u6709\u51e0\u5206\u5deb\u5e08\u7684\u6837\u5b50\u4e86\u3002\u201d</p><p>\u884c\u5934\u914d\u5907\u5b8c\u6210\uff0c\u63a5\u4e0b\u6765\uff0c\u5c31\u662f\u7d27\u5f20\u7684\u5b66\u4e60\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e00\u5e74\u7ea7\u5b66\u751f\uff0c\u8981\u5b66\u4e60\u7684\u4e3b\u8981\u662f\u9b54\u6cd5\u7684\u57fa\u7840\u539f\u7406\uff0c\u4ee5\u53ca\u5deb\u5e08\u7684\u57fa\u672c\u529f\u8bad\u7ec3\u3002\u8bfe\u7a0b\u5305\u62ec\u9b54\u6cd5\u53f2\u3001\u5143\u7d20\u5b66\u3001\u9b54\u836f\u5b66\u3001\u4f4d\u9762\u5b66\u3001\u9b54\u6cd5\u529b\u5b66\u539f\u7406\u4e4b\u7c7b\uff0c\u4e94\u82b1\u516b\u95e8\uff0c\u5343\u5947\u767e\u602a\u3002\u597d\u5728\u5927\u591a\u90fd\u662f\u57fa\u7840\u77e5\u8bc6\uff0c\u771f\u6b63\u6df1\u5965\u7684\u90e8\u5206\uff0c\u8981\u7b49\u4ee5\u540e\u518d\u6559\uff0c\u5b66\u8d77\u6765\u5012\u4e5f\u4e0d\u5982\u4f55\u8270\u96be\u3002</p><p>\u771f\u6b63\u9ebb\u70e6\u7684\uff0c\u5728\u4e8e\u300a\u9f99\u8bed\u5b66\u300b\u3002</p><p>\u5728\u8fd9\u4e2a\u4e16\u754c\uff0c\u5deb\u5e08\u7684\u5492\u8bed\u90fd\u662f\u7528\u9f99\u8bed\uff0c\u56e0\u4e3a\u9f99\u662f\u5929\u751f\u5c31\u62e5\u6709\u65bd\u6cd5\u80fd\u529b\u7684\u79cd\u65cf\uff0c\u5b83\u4eec\u7684\u8bed\u8a00\u5929\u7136\u542b\u6709\u4e0e\u9b54\u7f51\u6c9f\u901a\u7684\u529b\u91cf\u3002\u82e5\u4e0d\u80fd\u7cbe\u901a\u9f99\u8bed\uff0c\u81ea\u7136\u8c08\u4e0d\u4e0a\u6210\u4e3a\u4e00\u540d\u5deb\u5e08\u3002</p><p>\u743c\u6069\u5728\u5b66\u4e60\u8bed\u8a00\u8fd9\u65b9\u9762\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u591a\u4e48\u5929\u8d4b\u8d85\u4eba\uff0c\u5374\u4e5f\u8fd8\u7b97\u53ef\u4ee5\u3002\u4f46\u9f99\u8bed\u7740\u5b9e\u662f\u7cbe\u6df1\u5965\u5999\u2014\u2014\u8bed\u6cd5\u89c4\u5219\u6781\u5ea6\u590d\u6742\uff0c\u8bcd\u6c47\u53c8\u8d85\u7ea7\u591a\uff0c\u56e0\u4e3a\u662f\u9f99\u7684\u8bed\u8a00\uff0c\u6240\u4ee5\u5f88\u591a\u5355\u8bcd\u7684\u53d1\u97f3\u8fd8\u8d85\u7ea7\u53e4\u602a\u62d7\u53e3\uff0c\u7b80\u76f4\u5c31\u4e0d\u662f\u4eba\u7c7b\u7684\u58f0\u5e26\u80fd\u53d1\u51fa\u6765\u7684\u3002</p><p>\u743c\u6069\u7684\u540c\u5b66\u4eec\uff0c\u4e2a\u4e2a\u90fd\u662f\u5deb\u5e08\u4e16\u5bb6\u51fa\u6765\u7684\uff0c\u4ece\u5c0f\u8033\u6fe1\u76ee\u67d3\uff0c\u57fa\u7840\u6bd4\u743c\u6069\u8981\u624e\u5b9e\u5f97\u591a\u3002\u800c\u4e14\u8bf4\u53e5\u5b9e\u8bdd\uff0c\u8bba\u8d44\u8d28\uff0c\u8bba\u5965\u672f\u5929\u8d4b\uff0c\u743c\u6069\u4e5f\u786e\u5b9e\u5e76\u4e0d\u7b97\u591a\u4e48\u51fa\u8272\uff0c\u5728\u540c\u5b66\u4e2d\u4e5f\u4e0d\u8fc7\u5c31\u662f\u4e2d\u7b49\u6c34\u51c6\u3002\u5982\u679c\u4ed6\u770b\u5f97\u6ca1\u9519\uff0c\u540c\u5b66\u4e4b\u4e2d\uff0c\u751a\u81f3\u8fd8\u6709\u51e0\u4e2a\u672f\u58eb\u3002</p><p>\u6240\u8c13\u672f\u58eb\uff0c\u5c31\u662f\u548c\u9f99\u4e00\u6837\uff0c\u5929\u8d4b\u5c31\u6709\u65bd\u6cd5\u80fd\u529b\u7684\u5bb6\u4f19\u3002\u636e\u8bf4\u62e5\u6709\u9f99\u7684\u8840\u8109\u8005\uff0c\u6216\u8005\u795e\u7947\u7684\u540e\u88d4\uff0c\u5f80\u5f80\u4f1a\u51fa\u73b0\u672f\u58eb\u3002\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u4eec\u5f7c\u6b64\u8054\u59fb\uff0c\u4e00\u4ee3\u53c8\u4e00\u4ee3\u7684\u8840\u7edf\u7d2f\u79ef\uff0c\u4e5f\u6709\u53ef\u80fd\u7a81\u7136\u67d0\u4e00\u4ee3\u8bde\u751f\u51fa\u8fd9\u79cd\u5929\u624d\u6765\uff0c\u611f\u89c9\u50cf\u662f\u57fa\u56e0\u7a81\u53d8\u3002</p><p>\u5176\u5b9e\u8981\u7eaf\u8bba\u806a\u660e\u624d\u667a\uff0c\u672f\u58eb\u4e5f\u672a\u5fc5\u5c31\u591a\u4e48\u4f18\u5f02\uff0c\u751a\u81f3\u4ec5\u548c\u666e\u901a\u4eba\u5dee\u4e0d\u591a\uff0c\u4f46\u4ed6\u4eec\u5374\u662f\u5929\u751f\u7684\u5965\u6cd5\u6750\u6599\u2014\u2014\u4ed6\u4eec\u5929\u751f\u5c31\u4f1a\u8bf4\u9f99\u8bed\uff0c\u5929\u751f\u5c31\u80fd\u611f\u89c9\u5230\u9b54\u6cd5\u7f51\u7edc\u7684\u5b58\u5728\uff0c\u7a0d\u52a0\u8bad\u7ec3\u5c31\u80fd\u8054\u7ed3\u5230\u9b54\u6cd5\u7f51\u7edc\uff0c\u4ece\u4e2d\u63d0\u53d6\u529b\u91cf\uff0c\u800c\u4e14\u65e0\u9700\u501f\u52a9\u9b54\u6cd5\u4e66\u4f5c\u4e3a\u94a5\u5319\u3002</p><p>\u5f53\u7136\uff0c\u5929\u8d4b\u5f02\u7980\uff0c\u5e76\u4e0d\u4ee3\u8868\u5c31\u4e00\u5b9a\u4f1a\u6709\u6770\u51fa\u6210\u5c31\u3002\u987b\u77e5\u7eb5\u7136\u6709\u518d\u9ad8\u660e\u7684\u5929\u8d4b\uff0c\u5982\u82e5\u4e0d\u7ecf\u8fc7\u540e\u5929\u7684\u523b\u82e6\u8bad\u7ec3\uff0c\u7167\u6837\u4e5f\u53ea\u80fd\u201c\u6cef\u7136\u4f17\u4eba\u77e3\u201d\u3002\u800c\u4e14\u672f\u58eb\u4e5f\u662f\u6709\u5176\u7f3a\u9677\u7684\uff0c\u4ed6\u4eec\u5927\u591a\u90fd\u8fc7\u4e8e\u611f\u6027\uff0c\u4e0d\u5584\u63a7\u5236\u81ea\u5df1\u60c5\u7eea\uff0c\u5728\u7406\u6027\u601d\u7ef4\u548c\u903b\u8f91\u5206\u6790\u80fd\u529b\u4e0a\u5f80\u5f80\u4e0d\u8db3\u2014\u2014\u6709\u53e5\u5f88\u523b\u8584\u7684\u8bdd\uff0c\u672f\u58eb\u5c31\u50cf\u5973\u4eba\u3002</p><p>\u4ee5\u9634\u9b42\u57ce\u4e00\u8d2f\u7684\u201c\u7cbe\u82f1\u5316\u201d\u7406\u5ff5\uff0c\u7eb5\u7136\u662f\u5929\u8d4b\u5f02\u7980\u7684\u672f\u58eb\uff0c\u4e5f\u8981\u9001\u8fdb\u5deb\u5e08\u5b66\u6821\u6765\uff0c\u548c\u5176\u4ed6\u4eba\u4e00\u6837\u63a5\u53d7\u4e25\u683c\u7684\u7cfb\u7edf\u5316\u8bad\u7ec3\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5929\u751f\u901a\u6653\u9f99\u8bed\uff0c\u5929\u751f\u4eb2\u8fd1\u9b54\u7f51\uff0c\u8fd9\u90fd\u662f\u975e\u5e38\u5927\u7684\u4f18\u52bf\u2014\u2014\u5c24\u5176\u5728\u521d\u671f\u9636\u6bb5\uff0c\u5b9e\u5728\u662f\u5360\u4e86\u5f88\u5927\u7684\u4fbf\u5b9c\u3002</p><p>\u5e78\u597d\u52e4\u80fd\u8865\u62d9\u3002</p><p>\u867d\u7136\u743c\u6069\u4fee\u4e60\u5185\u529f\u7684\u6210\u7ee9\u4e0d\u4f73\uff0c\u4f46\u4e09\u5e74\u7684\u523b\u82e6\u953b\u70bc\uff0c\u603b\u4e5f\u4e0d\u662f\u5168\u7136\u6ca1\u6709\u6548\u7528\u3002\u522b\u4eba\u4e00\u5929\u81f3\u5c11\u8981\u7761\u7720\u516b\u5c0f\u65f6\uff0c\u5426\u5219\u80af\u5b9a\u4f1a\u88ab\u90a3\u575a\u6da9\u62d7\u53e3\u7684\u9f99\u8bed\u7ed9\u5f04\u5f97\u6655\u5934\u80c0\u8111\uff0c\u743c\u6069\u5374\u53ea\u9700\u8fd0\u529f\u8c03\u606f\u4e24\u5c0f\u65f6\uff0c\u5c31\u80fd\u4fdd\u6301\u5934\u8111\u6e05\u660e\uff0c\u5b66\u4e60\u6548\u7387\u8fd8\u66f4\u9ad8\u3002</p><p>\u9760\u7740\u6bcf\u5929\u6bd4\u522b\u4eba\u591a\u8fd9\u516d\u5c0f\u65f6\u7684\u5b66\u4e60\u65f6\u95f4\uff0c\u743c\u6069\u867d\u7136\u57fa\u7840\u6b20\u7f3a\uff0c\u8d44\u8d28\u4e5f\u4e0d\u7b97\u4e0a\u7b49\uff0c\u6210\u7ee9\u5374\u8fd8\u4e00\u76f4\u4e0d\u9519\u3002</p></article>"}}}]);
