(window.webpackJsonp=window.webpackJsonp||[]).push([[120],{142:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e8c\u8282 \u6c83\u91d1\u7267\u5e08",content:"<article><h2>\u7b2c\u4e09\u5341\u4e8c\u8282 \u6c83\u91d1\u7267\u5e08</h2><p>\u4f4f\u5904\u65e9\u5df2\u5b89\u6392\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u81ea\u56de\u53bb\u7b49\u5f85\u6d88\u606f\u3002\u636e\u8389\u62c9\u516c\u7235\u8bf4\uff0c\u5e02\u653f\u5e9c\u5df2\u7ecf\u8d34\u51fa\u544a\u793a\uff0c\u62db\u63fd\u6709\u80c6\u91cf\u6709\u80fd\u529b\u7684\u5192\u9669\u8005\uff0c\u6765\u534f\u52a9\u4ed6\u4eec\u5b8c\u6210\u8fd9\u9879\u4efb\u52a1\u3002\u5f53\u7136\uff0c\u544a\u793a\u4e0a\u4e0d\u4f1a\u660e\u8bf4\u662f\u8981\u53bb\u780d\u4e00\u53ea\u5343\u5e74\u8001\u5deb\u5996\uff0c\u53ea\u5f3a\u8c03\u9ad8\u98ce\u9669\u9ad8\u56de\u62a5\uff0c\u4e8b\u6210\u4e4b\u540e\uff0c\u91cd\u91d1\u916c\u8c22\u3002</p><p>\u91cd\u8d4f\u4e4b\u4e0b\uff0c\u5fc5\u6709\u52c7\u592b\u3002</p><p>\u535a\u5f97\u4e4b\u95e8\u662f\u5546\u8d38\u5927\u57ce\uff0c\u4f4d\u4e8e\u5357\u5317\u4ea4\u901a\u8981\u9053\uff0c\u6b64\u524d\u662f\u56e0\u4e3a\u761f\u75ab\u7684\u7f18\u6545\uff0c\u5927\u5bb6\u907f\u4e4b\u552f\u6050\u4e0d\u53ca\uff1b\u5982\u4eca\u761f\u75ab\u9a71\u9664\uff0c\u81ea\u7136\u53c8\u5f88\u5feb\u6062\u590d\u4e86\u7e41\u8363\u666f\u8c61\u3002\u90a3\u4e9b\u9003\u51fa\u53bb\u7684\u5c45\u6c11\u7eb7\u7eb7\u53c8\u8fc1\u56de\u6765\uff0c\u5546\u65c5\u884c\u4eba\u518d\u5ea6\u7199\u7199\u6518\u6518\u5730\u62e5\u6324\u8857\u9053\uff0c\u9152\u9986\u91cc\u540c\u6837\u53c8\u585e\u6ee1\u4e86\u5404\u5f0f\u5404\u6837\u7684\u5546\u4eba\u3001\u6c34\u624b\u3001\u9152\u9b3c\u3001\u5bc6\u63a2\uff0c\u4ee5\u53ca\u8ffd\u9010\u8d22\u5bcc\u548c\u8363\u8000\u7684\u4f63\u5175\u548c\u5192\u9669\u8005\u3002</p><p>\u542c\u8bf4\u5e02\u653f\u5e9c\u5728\u91cd\u91d1\u62db\u8058\u52c7\u58eb\uff0c\u987f\u65f6\u5c31\u6709\u5f88\u591a\u4f63\u5175\u548c\u5192\u9669\u8005\u6765\u81ea\u8350\u3002\u4f46\u5927\u90e8\u5206\u4eba\u4e00\u542c\u8bf4\u8981\u8fdb\u5de8\u9b54\u5c71\u8109\uff0c\u987f\u65f6\u5c31\u5148\u6253\u4e86\u9000\u5802\u9f13\uff1b\u5269\u4e0b\u7684\u4eba\u4e2d\uff0c\u5f97\u77e5\u662f\u53bb\u5bf9\u4ed8\u4e00\u53ea\u5deb\u5996\uff0c\u9a6c\u4e0a\u4e5f\u9000\u7f29\u4e86\u5927\u534a\u3002</p><p>\u96be\u5f97\u5269\u4e0b\u51e0\u4e2a\u80c6\u91cf\u5927\u7684\uff0c\u6ca1\u6709\u88ab\u201c\u5de8\u9b54\u5c71\u8109\u201d\u548c\u201c\u5deb\u5996\u201d\u5413\u8dd1\u7684\uff0c\u5374\u53c8\u7acb\u523b\u88ab\u6dd8\u6c70\u4e86\uff0c\u56e0\u4e3a\u4ed6\u4eec\u751a\u81f3\u90fd\u63a5\u4e0d\u4e0b\u6885\u83f2\u65af\u4e00\u5251\u3002</p><p>\u201c\u8fd9\u79cd\u6c34\u51c6\uff0c\u53bb\u4e86\u4e0d\u8fc7\u662f\u767d\u767d\u9001\u6b7b\uff0c\u201d\u6885\u83f2\u65af\u5bf9\u8389\u62c9\u516c\u7235\u8bf4\uff0c\u201c\u6211\u9700\u8981\u66f4\u5f3a\u4e00\u4e9b\u7684\u52a9\u624b\u3002\u201d</p><p>\u8389\u62c9\u516c\u7235\u4e5f\u5f88\u5934\u75bc\u3002\u8fd9\u4e16\u754c\u4e0a\u6709\u80fd\u529b\u7684\u4eba\u5f88\u591a\uff0c\u6709\u80c6\u91cf\u7684\u4eba\u4e5f\u4e0d\u5c11\uff0c\u4f46\u65e2\u6709\u80fd\u529b\u53c8\u6709\u80c6\u91cf\uff0c\u6b64\u65f6\u6070\u597d\u53c8\u5728\u535a\u5f97\u4e4b\u95e8\uff0c\u8fd8\u613f\u610f\u53bb\u5192\u8fd9\u4e2a\u9669\u2014\u2014\u540c\u65f6\u7b26\u5408\u4ee5\u4e0a\u6761\u4ef6\u7684\u4eba\uff0c\u786e\u5b9e\u662f\u4e0d\u5bb9\u6613\u627e\u3002</p><p>\u8fd9\u4e8b\u60c5\u53c8\u4e0d\u80fd\u4e45\u62d6\u4e0b\u53bb\uff0c\u8c01\u77e5\u9053\u62c9\u6c83\u514b\u90a3\u4e2a\u8001\u5deb\u5996\u4f1a\u4e0d\u4f1a\u4ec0\u4e48\u65f6\u5019\u5fc3\u8840\u6765\u6f6e\uff0c\u518d\u53d1\u52a8\u4e00\u6b21\u761f\u75ab\u3002\u636e\u6885\u83f2\u65af\u8bf4\uff0c\u9ece\u660e\u4e4b\u77f3\u867d\u7136\u6709\u9a71\u6563\u761f\u75ab\u7684\u5a01\u80fd\uff0c\u4f46\u7528\u8fc7\u4e00\u6b21\u4e4b\u540e\uff0c\u5c31\u8981\u8fc7\u5f88\u957f\u65f6\u95f4\uff08\u5927\u7ea6\u4e00\u5e74\uff09\u624d\u80fd\u518d\u6b21\u4f7f\u7528\u3002\u8fd9\u4e5f\u662f\u5f53\u65f6\u9762\u5bf9\u4eba\u9762\u72ee\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u575a\u51b3\u4e0d\u80af\u8ba9\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u4f7f\u7528\u9ece\u660e\u4e4b\u77f3\u7684\u7f18\u6545\uff0c\u56e0\u4e3a\u4e00\u65e6\u7528\u4e86\uff0c\u5c31\u6ca1\u6cd5\u518d\u7528\u6765\u5bf9\u4ed8\u761f\u75ab\u4e86\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5982\u4eca\u9ece\u660e\u4e4b\u77f3\u5df2\u7ecf\u6682\u65f6\u662f\u4e2a\u5e9f\u7269\u4e86\u3002\u5982\u679c\u771f\u7684\u518d\u6765\u4e00\u6b21\u761f\u75ab\uff0c\u90a3\u5927\u5bb6\u53ea\u597d\u9003\u96be\u5427\u3002</p><p>\u6b63\u5f53\u5927\u5bb6\u90fd\u5f88\u5931\u671b\u7684\u65f6\u5019\uff0c\u7ec8\u4e8e\u6709\u4e00\u4e2a\u5408\u683c\u7684\u5e94\u5f81\u8005\u51fa\u73b0\u4e86\u3002</p><p>\u4e00\u4e2a\u6c83\u91d1\u7267\u5e08\u3002</p><p>\u203b\u203b\u203b</p><p>\u201c\u6c83\u91d1\uff1f\u201d\u743c\u6069\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\u7684\u65f6\u5019\uff0c\u5fae\u5fae\u6014\u4e86\u4e00\u4e0b\uff0c\u968f\u5373\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u5c31\u662f\u90a3\u4e2a\u5931\u8e2a\u7684\u5546\u4e1a\u5973\u795e\uff1f\u201d</p><p>\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u6821\u91cc\uff0c\u4e5f\u5f00\u8bbe\u6709\u795e\u7947\u5b66\u8bfe\u7a0b\uff0c\u743c\u6069\u5bf9\u7cbe\u7075\u3001\u77ee\u4eba\u3001\u517d\u4eba\u7b49\u7b49\u975e\u4eba\u7c7b\u795e\u7947\u4e0d\u751a\u5728\u610f\uff0c\u4f46\u5bf9\u4eba\u7c7b\u8bf8\u795e\uff0c\u8fd8\u662f\u90fd\u7565\u6709\u6240\u77e5\u7684\u3002</p><p>\u6c83\u91d1\u662f\u6267\u638c\u5546\u4e1a\u3001\u8d38\u6613\u548c\u8d22\u5bcc\u7684\u5973\u795e\uff0c\u5d1b\u8d77\u5927\u7ea6\u8fd1\u5343\u5e74\uff0c\u4ee5\u795e\u7947\u7684\u773c\u5149\u6765\u770b\uff0c\u7b97\u662f\u975e\u5e38\u5e74\u8f7b\u4e86\u3002\u5979\u7684\u6559\u4f1a\u53d1\u5c55\u6781\u5176\u8fc5\u901f\uff0c\u4fe1\u5f92\u65e5\u589e\uff0c\u66fe\u7ecf\u4e00\u5ea6\u8dfb\u8eab\u4e8e\u5927\u9646\u6700\u5f3a\u5927\u7684\u6559\u4f1a\u4e4b\u4e00\uff0c\u743c\u6069\u5bf9\u6b64\u5f88\u80fd\u7406\u89e3\uff0c\u6bd5\u7adf\uff0c\u5979\u5176\u5b9e\u5c31\u76f8\u5f53\u4e8e\u8d22\u795e\uff0c\u800c\u5728\u4efb\u4f55\u4e16\u754c\u91cc\uff0c\u53ea\u8981\u5171\u4ea7\u4e3b\u4e49\u8fd8\u6ca1\u5b9e\u73b0\uff0c\u5d07\u62dc\u8d22\u795e\u7684\u4eba\u5c31\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5c11\u3002</p><p>\u4f46\u5982\u679c\u743c\u6069\u8bb0\u5f97\u6ca1\u9519\u7684\u8bdd\uff0c\u5728\u5341\u51e0\u5e74\u524d\u2014\u2014\u51c6\u786e\u5730\u8bf4\u597d\u50cf\u662f\u5341\u4e94\u5e74\u524d\uff0c\u4e5f\u5c31\u662f\u743c\u6069\u51fa\u751f\u7684\u90a3\u4e00\u5e74\uff0c\u6574\u4e2a\u56fd\u5ea6\u53d1\u751f\u4e86\u4e00\u573a\u5267\u53d8\u3002\u5177\u4f53\u8be6\u60c5\u743c\u6069\u5e76\u4e0d\u5f88\u6e05\u695a\uff0c\u53ea\u662f\u542c\u8bf4\u5f88\u591a\u795e\u7947\u90fd\u4ee5\u5723\u8005\u5f62\u6001\u51fa\u73b0\u5728\u7269\u8d28\u754c\uff0c\u5176\u4e2d\u51e0\u4f4d\u88ab\u6740\u6b7b\u4e86\uff0c\u540c\u65f6\u8bde\u751f\u4e86\u51e0\u4f4d\u65b0\u7684\u795e\u7947\u3002\u5728\u8fd9\u671f\u95f4\uff0c\u6240\u6709\u5deb\u5e08\u7684\u65bd\u6cd5\u90fd\u906d\u53d7\u4e86\u6781\u5927\u7684\u6df7\u4e71\uff0c\u6240\u6709\u7267\u5e08\u7684\u7948\u7977\u90fd\u5f97\u4e0d\u5230\u795e\u7947\u7684\u56de\u5e94\uff0c\u5f88\u591a\u5730\u533a\u90fd\u7206\u53d1\u4e86\u6218\u4e89\u3001\u7834\u574f\u548c\u6bc1\u706d\uff0c\u6574\u4e2a\u7269\u8d28\u754c\u90fd\u9677\u5165\u4e00\u573a\u5927\u6df7\u4e71\u3002</p><p>\u5e78\u597d\uff0c\u6df7\u4e71\u5e76\u6ca1\u6709\u4e00\u76f4\u6301\u7eed\u4e0b\u53bb\uff0c\u5927\u7ea6\u6298\u817e\u4e86\u56db\u4e2a\u6708\uff0c\u4e00\u5207\u53c8\u7a81\u7136\u6062\u590d\u4e86\u539f\u72b6\u3002\u795e\u7947\u4eec\u4ece\u7269\u8d28\u754c\u6d88\u5931\u4e86\uff0c\u8fd4\u56de\u5404\u81ea\u7684\u795e\u57df\uff0c\u5deb\u5e08\u4eec\u7684\u65bd\u6cd5\u53c8\u7a33\u5b9a\u4e86\uff0c\u7267\u5e08\u4eec\u7684\u7948\u7977\u53c8\u5f97\u5230\u4e86\u56de\u5e94\uff0c\u4e00\u5207\u91cd\u65b0\u4e95\u4e95\u6709\u6761\u3002</p><p>\u56e0\u4e3a\u6b64\u4e8b\uff0c\u8fd9\u4e00\u5e74\u4e5f\u5c31\u88ab\u79f0\u4e3a\u201c\u52a8\u8361\u4e4b\u5e74\u201d\u3002</p><p>\u52a8\u8361\u7ed3\u675f\u540e\uff0c\u5927\u90e8\u5206\u795e\u7947\u5b89\u7136\u5f52\u4f4d\uff0c\u4ed6\u4eec\u7684\u6559\u4f1a\u7ee7\u7eed\u6b63\u5e38\u8fd0\u8f6c\u3002\u6709\u51e0\u4f4d\u4e0d\u5e78\u7684\u795e\u7947\u88ab\u6467\u6bc1\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u6ca1\u5173\u7cfb\uff0c\u81ea\u7136\u53c8\u6709\u65b0\u7684\u795e\u7947\u7ee7\u4efb\u3002</p><p>\u4f46\u6c83\u91d1\u5374\u662f\u4e2a\u4f8b\u5916\uff0c\u5979\u65e2\u6ca1\u6709\u5b89\u7136\u5f52\u4f4d\uff0c\u4e5f\u6ca1\u6709\u88ab\u6467\u6bc1\uff0c\u5979\u5931\u8e2a\u4e86\u3002</p><p>\u6ca1\u6709\u4eba\u77e5\u9053\u5979\u53bb\u4e86\u54ea\u91cc\uff0c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f46\u5979\u5e76\u6ca1\u6709\u6b7b\u4ea1\u3002\u5979\u7684\u6559\u4f1a\u4f9d\u65e7\u7ef4\u6301\u7740\uff0c\u5979\u7684\u7267\u5e08\u4f9d\u65e7\u7948\u7977\uff0c\u5e76\u4e14\u80fd\u83b7\u5f97\u56de\u5e94\uff0c\u56e0\u4e3a\u6709\u53e6\u5916\u4e00\u4f4d\u795e\u7947\uff0c\u6b22\u4e50\u5973\u795e\u9ece\u5c14\u62c9\uff0c\u6c83\u91d1\u7684\u76df\u53cb\uff0c\u4ee3\u5979\u6267\u638c\u795e\u804c\u3002\u9ece\u5c14\u62c9\u660e\u786e\u544a\u8bc9\u6c83\u91d1\u7684\u4fe1\u5f92\uff0c\u4ed6\u4eec\u7684\u5973\u795e\u5e76\u6ca1\u6709\u5728\u52a8\u8361\u5e74\u4ee3\u4e2d\u6b7b\u4ea1\uff0c\u53ea\u662f\u6682\u65f6\u4e0d\u77e5\u53bb\u5411\u3002</p><p>\u4f46\u8fd9\u4e2a\u201c\u6682\u65f6\u201d\uff0c\u4e00\u76f4\u6301\u7eed\u4e86\u5341\u51e0\u5e74\u3002\u52a8\u8361\u5e74\u4ee3\u662f1358DR\uff0c\u76f4\u52301370DR\uff0c\u4e5f\u5c31\u662f\u743c\u6069\u4e09\u5e74\u7ea7\u7684\u65f6\u5019\uff0c\u6559\u6388\u8fd8\u66fe\u7ecf\u8bf4\uff0c\u56e0\u4e3a\u6c83\u91d1\u7684\u957f\u671f\u5931\u8e2a\uff0c\u5f88\u591a\u4fe1\u5f92\u7eb7\u7eb7\u79bb\u5f00\uff0c\u6539\u53d8\u4fe1\u4ef0\uff0c\u53e6\u6295\u5176\u4ed6\u6559\u4f1a\uff0c\u6c83\u91d1\u6559\u4f1a\u7684\u52bf\u529b\u5df2\u7ecf\u6025\u5267\u8870\u9000\uff0c\u5927\u4e0d\u5982\u524d\u4e86\u3002</p><p>\u5973\u795e\u5176\u5b9e\u5df2\u7ecf\u6b7b\u4e86\u5427\uff0c\u5f88\u591a\u4eba\u90fd\u8fd9\u6837\u731c\u6d4b\u3002</p><p>\u542c\u5230\u743c\u6069\u8fd9\u4e48\u8bf4\uff0c\u6885\u83f2\u65af\u7565\u7565\u6709\u4e9b\u5947\u602a\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u6c83\u91d1\u4e0d\u662f\u53bb\u5e74\u5c31\u56de\u5f52\u4e86\u4e48\uff1f\u201d</p><p>\u4f5c\u4e3a\u63d0\u5c14\u7684\u4fe1\u5f92\uff0c\u6885\u83f2\u65af\u5bf9\u6c83\u91d1\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u656c\u610f\uff0c\u76f4\u547c\u5176\u540d\u3002\u4f46\u743c\u6069\u542c\u5979\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5012\u662f\u5fae\u5fae\u4e00\u6014\uff0c\u201c\u56de\u6765\u4e86\uff1f\u201d</p><p>\u539f\u6765\u6c83\u91d1\u6ca1\u6b7b\u554a\u3002</p><p>1371DR\uff0c\u4e5f\u5c31\u662f\u53bb\u5e74\uff0c\u5931\u8e2a\u4e86\u5341\u4e09\u5e74\u7684\u5546\u4e1a\u5973\u795e\u518d\u5ea6\u51fa\u73b0\uff0c\u5979\u5411\u6240\u6709\u7684\u4fe1\u5f92\u5c55\u793a\u795e\u8ff9\uff0c\u5ba3\u544a\u5979\u7684\u590d\u51fa\u3002\u5728\u90a3\u4e00\u523b\uff0c\u6240\u6709\u7684\u6c83\u91d1\u4fe1\u5f92\uff08\u5927\u90e8\u5206\u662f\u5546\u4eba\uff09\uff0c\u90fd\u53d1\u73b0\u4ed6\u4eec\u7684\u91d1\u5e01\u4e0a\uff0c\u7a81\u7136\u6d6e\u73b0\u51fa\u4e00\u4e2a\u6709\u7740\u4eae\u91d1\u8272\u534e\u4e3d\u957f\u53d1\uff0c\u53cc\u773c\u4eff\u4f5b\u878d\u5316\u91d1\u7403\u7684\u7f8e\u8c8c\u5c11\u5973\u7684\u5f71\u50cf\u6765\u2014\u2014\u8fd9\u6b63\u662f\u6c83\u91d1\u5973\u795e\u7684\u5f62\u8c61\u3002</p><p>\u6c83\u91d1\u56de\u5f52\uff0c\u53d6\u56de\u4e86\u7531\u6b22\u4e50\u5973\u795e\u9ece\u5c14\u62c9\u4ee3\u4e3a\u6267\u638c\u7684\u795e\u804c\uff0c\u4eb2\u81ea\u7edf\u9886\u6559\u4f1a\u3002\u4f46\u5728\u5979\u5931\u8e2a\u7684\u8fd9\u5341\u4e09\u5e74\u95f4\uff0c\u5f88\u591a\u4fe1\u5f92\u6d41\u5931\uff0c\u5176\u4e2d\u5927\u90e8\u5206\u8f6c\u6295\u5230\u9ece\u5c14\u62c9\u7684\u6559\u4f1a\u4e2d\u3002\u6c83\u91d1\u8bd5\u56fe\u593a\u56de\u8fd9\u4e9b\u4fe1\u5f92\uff0c\u8fd9\u9020\u6210\u4e86\u4e24\u4f4d\u795e\u7947\u7684\u5acc\u9699\u52a0\u5267\uff0c\u6614\u65e5\u7684\u76df\u53cb\u5173\u7cfb\u5df2\u7ecf\u9010\u6e10\u7834\u88c2\u3002</p><p>\u743c\u6069\u5728\u9634\u9b42\u57ce\u4e2d\u6d88\u606f\u4e0d\u7075\u901a\uff0c\u4e5f\u4e0d\u77e5\u9053\u539f\u6765\u6c83\u91d1\u5df2\u7ecf\u56de\u5f52\uff0c\u4e0d\u8fc7\u8fd9\u4e9b\u4e8b\u60c5\u539f\u672c\u5c31\u4e0e\u4ed6\u65e0\u5173\uff0c\u4e5f\u61d2\u5f97\u591a\u5728\u610f\u3002\u535a\u5f97\u4e4b\u95e8\u662f\u5546\u8d38\u5927\u57ce\uff0c\u6c83\u91d1\u7684\u4fe1\u5f92\u81ea\u7136\u4e0d\u5c11\uff0c\u4e5f\u662f\u5efa\u6709\u795e\u6bbf\u7684\u3002\u53ea\u662f\u6c83\u91d1\u7267\u5e08\u79c9\u627f\u795e\u610f\uff0c\u7d20\u6765\u4ee5\u5546\u4eba\u81ea\u5c45\uff0c\u79ef\u6781\u5f00\u8bbe\u5404\u79cd\u91d1\u878d\u673a\u6784\uff0c\u53c2\u4e0e\u5404\u79cd\u5546\u4e1a\u6d3b\u52a8\uff0c\u6781\u5c11\u6709\u613f\u610f\u53bb\u91ce\u5916\u5192\u9669\u7684\u3002\u8fd9\u4e2a\u7267\u5e08\u4e3b\u52a8\u524d\u6765\u5e94\u5f81\uff0c\u5012\u662f\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u4eba\u90fd\u5df2\u7ecf\u6765\u4e86\u3002</p><p>\u743c\u6069\u5728\u65c1\u8fb9\u770b\u7740\u8fd9\u4f4d\u81ea\u79f0\u540d\u53eb\u201c\u82cf\u5c3c\xb7\u79d1\u6069\u201d\u7684\u6c83\u91d1\u7267\u5e08\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u7684\u773c\u775b\u6709\u70b9\u53d1\u82b1\uff0c\u56e0\u4e3a\u8fd9\u4f4d\u7267\u5e08\u7684\u8863\u9970\u6253\u626e\u5b9e\u5728\u662f\u592a\u8fc7\u4e8e\u5bcc\u4e3d\u5802\u7687\u4e86\uff0c\u5168\u8eab\u4e0a\u4e0b\u65e0\u4e00\u5904\u4e0d\u662f\u73e0\u5149\u5b9d\u6c14\uff0c\u95ea\u70c1\u8000\u773c\u3002\u4ed6\u7a7f\u7740\u7528\u6602\u8d35\u5e03\u6599\u5236\u4f5c\u7684\u77ed\u4e0a\u8863\uff0c\u5916\u9762\u7f69\u4e00\u4ef6\u989c\u8272\u6d53\u91cd\u7684\u9540\u91d1\u5927\u7ea2\u8272\u62ab\u98ce\uff0c\u62ab\u98ce\u4e0a\u7f00\u6ee1\u4e86\u65e0\u6570\u91d1\u94f6\u4eae\u7247\u53ca\u6302\u52fe\uff0c\u4e0d\u77e5\u6709\u4f55\u7528\u9014\uff1b\u4e0b\u8eab\u662f\u4e00\u4ef6\u707f\u91d1\u8272\u7684\u957f\u88e4\uff0c\u914d\u4e0a\u767d\u8272\u7684\u957f\u7b52\u889c\u548c\u76ae\u5236\u77ed\u9774\uff0c\u94f6\u767d\u8272\u8170\u5e26\u4e0a\uff0c\u6302\u7740\u4e00\u4e9b\u743c\u6069\u770b\u4e0d\u51fa\u7528\u9014\u7684\u5c0f\u5de5\u5177\u3002\u4ed6\u7684\u5934\u4e0a\u6234\u7740\u4e00\u9876\u9540\u91d1\u6cd5\u51a0\uff0c\u4e0a\u9762\u7f00\u6ee1\u5404\u79cd\u7ec6\u5c0f\u7cbe\u81f4\u7684\u5b9d\u77f3\u3002\u867d\u7136\u5df2\u81f3\u590f\u65e5\uff0c\u4ed6\u4f9d\u65e7\u6234\u7740\u96ea\u767d\u7684\u624b\u5957\uff0c\u5de6\u624b\u6301\u4e00\u67c4\u9540\u91d1\u6743\u6756\u3002</p><p>\u7b2c\u4e00\u773c\u770b\u89c1\u7684\u65f6\u5019\uff0c\u743c\u6069\u6697\u81ea\u6000\u7591\u8fd9\u662f\u4e2a\u66b4\u53d1\u6237\uff0c\u6216\u8005\u662f\u4e2a\u683c\u8c03\u5eb8\u4fd7\u7684\u5546\u4eba\u3002\u4f46\u4ed6\u5f88\u5feb\u6253\u6d88\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u56e0\u4e3a\u5c31\u5728\u521a\u624d\uff0c\u8fd9\u4f4d\u5168\u8eab\u73e0\u5149\u5b9d\u6c14\u7684\u6c83\u91d1\u7267\u5e08\u5c55\u793a\u4e86\u4ed6\u60ca\u4eba\u7684\u5b9e\u529b\u3002</p><p>\u4ed6\u5750\u5728\u6905\u5b50\u4e0a\uff0c\u4f38\u51fa\u53f3\u624b\uff0c\u98df\u6307\u548c\u4e2d\u6307\u95f4\u5939\u7740\u4e00\u679a\u91d1\u5e01\uff0c\u4eff\u4f5b\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u5f80\u65c1\u8fb9\u7684\u6728\u684c\u4e0a\u8f7b\u8f7b\u6572\u4e86\u4e00\u6572\u3002\u6b63\u5f53\u743c\u6069\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u610f\u601d\u7684\u65f6\u5019\uff0c\u90a3\u5f20\u6728\u684c\u7a81\u7136\u6d3b\u52a8\u8d77\u6765\u3002\u5b83\u5c45\u7136\u5c55\u5f00\u56db\u6761\u817f\uff0c\u5f00\u59cb\u539f\u5730\u65cb\u8f6c\u8df3\u8e22\u8e0f\u821e\uff0c\u6216\u8005\u662f\u67d0\u79cd\u8fd1\u4f3c\u7684\u821e\u8e48\u3002</p><p>\u201c\u5e7b\u672f\uff1f\u201d\u743c\u6069\u968f\u5373\u5426\u51b3\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u4ed6\u80fd\u770b\u5f97\u51fa\u6765\uff0c\u8fd9\u5e76\u975e\u5e7b\u672f\uff0c\u800c\u662f\u7267\u5e08\u771f\u7684\u8ba9\u8fd9\u5f20\u684c\u5b50\u201c\u6d3b\u201d\u4e86\u8d77\u6765\uff0c\u4eff\u4f5b\u6709\u4e86\u751f\u547d\u4e00\u822c\u3002</p><p>\u7267\u5e08\u5fae\u7b11\u7740\uff0c\u6307\u5c16\u7684\u91d1\u5e01\u8f7b\u8f7b\u7ffb\u4e86\u4e2a\u9762\uff0c\u90a3\u5f20\u6b63\u5728\u8df3\u8e22\u8e0f\u821e\u7684\u684c\u5b50\u505c\u4e0b\u6765\uff0c\u7136\u540e\u56db\u8db3\u4ea4\u9519\uff0c\u50cf\u4e00\u53ea\u7b28\u62d9\u7684\u732b\u4f3c\u7684\uff0c\u671d\u6885\u83f2\u65af\u8d70\u8fc7\u53bb\u3002\u5973\u5723\u6b66\u58eb\u7b49\u684c\u5b50\u8d70\u8fd1\uff0c\u4f38\u624b\u5728\u684c\u9762\u4e0a\u6309\u4e86\u4e00\u6309\uff0c\u8ba9\u5b83\u5b89\u9759\u4e0b\u6765\u3002\u201c\u6d3b\u5316\u7269\u4f53\uff1f\u201d\u5979\u95ee\u3002</p><p>\u7267\u5e08\u9887\u4e3a\u81ea\u5f97\u5730\u70b9\u70b9\u5934\u3002</p><p>\u6885\u83f2\u65af\u5514\u4e86\u4e00\u58f0\uff0c\u6beb\u4e0d\u5ba2\u6c14\u5730\u6253\u91cf\u7740\u5bf9\u65b9\uff0c\u5979\u7684\u773c\u5149\u5728\u7267\u5e08\u7684\u80f8\u53e3\u505c\u7559\u4e86\u5f88\u4e45\uff0c\u8fd9\u8ba9\u743c\u6069\u5f88\u5947\u602a\u3002\u5deb\u5e08\u987a\u7740\u5c11\u5973\u7684\u773c\u5149\u770b\u8fc7\u53bb\uff0c\u89c1\u5979\u5173\u6ce8\u7684\u662f\u4e00\u679a\u954c\u5728\u7267\u5e08\u77ed\u4e0a\u8863\u4e0a\u7684\u5fbd\u7ae0\u3002</p><p>\u90a3\u5fbd\u7ae0\u50cf\u662f\u4e00\u679a\u91d1\u5e01\uff0c\u4e0a\u9762\u96d5\u523b\u7740\u4e00\u4f4d\u91d1\u53d1\u5973\u5b50\u7684\u4fa7\u8138\u3002</p><p>\u201c\u662f\u6c83\u91d1\u7684\u5723\u5fbd\u5427\u3002\u201d\u743c\u6069\u731c\u6d4b\uff0c\u8fd9\u5f88\u6b63\u5e38\u554a\uff0c\u7267\u5e08\u57fa\u672c\u90fd\u4f1a\u4e50\u4e8e\u5728\u8863\u670d\u4e0a\u954c\u7ee3\u5723\u5fbd\u7684\u6807\u5fd7\uff0c\u65f6\u523b\u5411\u4eba\u5ba3\u544a\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u9664\u975e\u90a3\u4e9b\u4e60\u60ef\u9690\u5bc6\u8eab\u4efd\u7684\u90aa\u795e\u7267\u5e08\u3002</p><p>\u4f46\u6885\u83f2\u65af\u770b\u5f97\u5f88\u8ba4\u771f\uff0c\u4e0d\u77e5\u5979\u5230\u5e95\u53d1\u73b0\u4e86\u4ec0\u4e48\u5f02\u6837\uff0c\u5bf9\u65b9\u4f3c\u4e4e\u4e5f\u4e0d\u89c9\u5947\u602a\uff0c\u53ea\u662f\u9759\u9759\u5730\u5750\u7740\u7b49\u5f85\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6885\u83f2\u65af\u6536\u56de\u76ee\u5149\uff0c\u671d\u7267\u5e08\u5fae\u5fae\u70b9\u5934\uff0c\u201c\u6211\u4eec\u53ef\u80fd\u4f1a\u5728\u56db\u5929\u540e\u51fa\u53d1\uff0c\u53ef\u4ee5\u5417\uff0c\u79d1\u6069\u5148\u751f\uff1f\u201d\u5979\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\u3002</p><p>\u201c\u6ca1\u95ee\u9898\u3002\u201d\u7267\u5e08\u7acb\u523b\u56de\u7b54\u3002</p></article>"}}}]);
