(window.webpackJsonp=window.webpackJsonp||[]).push([[1207],{1230:function(p){p.exports={chapterTitle:null,title:"\u7b2c996\u7ae0 \u627f\u62c5\u8d23\u4efb",content:"<article><h2>\u7b2c996\u7ae0 \u627f\u62c5\u8d23\u4efb</h2><p>\u3000\u3000\u5f20\u606a\u89c1\u8fc7\u5357\u90ba\u533a\u59d4\u4e66\u8bb0\u5f20\u5065\u51e0\u9762\uff0c\u53ea\u662f\u6ca1\u6709\u600e\u4e48\u8bf4\u8fc7\u8bdd\uff0c\u4ed6\u8d76\u5230\u5efa\u90ba\u5e02\u59d4\uff0c\u8ddf\u7740\u7f57\u541b\u7684\u79d8\u4e66\u8054\u7cfb\u8fc7\uff0c\u76f4\u63a5\u5230\u7f57\u541b\u7684\u529e\u516c\u5ba4\uff0c\u6572\u95e8\u65f6\uff0c\u9690\u7ea6\u542c\u89c1\u7f57\u541b\u5728\u529e\u516c\u5ba4\u91cc\u5927\u58f0\u8bad\u8bc9\u5f20\u5065\uff1a\u201c\u5357\u90ba\u533a\u59d4\u660e\u77e5\u9053\u94b1\u6842\u534e\u6709\u95ee\u9898\uff0c\u5374\u6ca1\u6709\u679c\u65ad\u7684\u91c7\u53d6\u63aa\u65bd\uff0c\u53cd\u800c\u9001\u51fa\u56fd\u95e8\u7ed9\u4ed6\u5728\u8003\u5bdf\u671f\u95f4\u6f5c\u9003\u9020\u6210\u5ba2\u89c2\u4e0a\u7684\u4fbf\u5229\uff0c\u8fd9\u4e2a\u8d23\u4efb\uff0c\u4f60\u8981\u600e\u4e48\u627f\u5f53\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6572\u4e86\u6572\u95e8\uff0c\u662f\u9648\u79c9\u5fb7\u5f00\u7684\u95e8\uff0c\u770b\u7740\u529e\u516c\u5ba4\u91cc\u7684\u6c14\u6c1b\u9634\u6c89\u51dd\u91cd\u5f97\u8981\u6ef4\u51fa\u6c34\u6765\u3002</p><p>\u3000\u3000\u9648\u79c9\u5fb7\u4e0e\u5f20\u5065\u5230\u7f57\u541b\u529e\u516c\u5ba4\u6765\u6c47\u62a5\u738b\u5efa\u6797\u6848\uff0c\u4f59\u5e86\u6797\u6ca1\u6709\u8fc7\u6765\uff0c\u7f57\u541b\u5750\u5728\u529e\u516c\u684c\u540e\uff0c\u8138\u8272\u4e25\u5cfb\uff0c\u4ed6\u770b\u89c1\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u8fdf\u7591\u4e86\u4e00\u4f1a\u513f\u624d\u7ad9\u8d77\u6765\uff0c\u8bf7\u4ed6\u5750\u5230\u6c99\u53d1\u4e0a\u3002</p><p>\u3000\u3000\u201c\u7ed9\u5e02\u91cc\u6dfb\u9ebb\u70e6\u4e86\uff0c\u201d\u5f20\u606a\u770b\u7740\u7f57\u541b\u7d27\u7ef7\u7740\u7684\u8138\uff0c\u53c8\u5fae\u5fae\u7684\u7b11\u4e86\u4e00\u7b11\uff0c\u8fd9\u624d\u89e3\u91ca\u8bf4\u9053\uff0c\u201c\u5143\u65e6\u524d\u591c\u4ece\u5317\u4eac\u8d81\u706b\u8f66\u56de\u5efa\u90ba\uff0c\u6070\u597d\u4e0e\u4e09\u661f\u674e\u5065\u7199\u5bb6\u65cf\u7684\u7ee7\u627f\u4eba\u674e\u99a8\u4e88\u5c0f\u59d0\u540c\u4e00\u8f66\u53a2\uff0c\u674e\u99a8\u4e88\u5c0f\u59d0\u4e34\u65f6\u51b3\u5b9a\u8d81\u706b\u8f66\u56de\u5efa\u90ba\uff0c\u52c9\u4e3a\u5176\u96be\u4e0e\u4ed6\u4eba\u540c\u8f6f\u5367\u5305\u53a2\uff0c\u591c\u95f4\u53d7\u5230\u9a9a\u6270\uff0c\u9526\u6e56\u4e0e\u4e09\u661f\u867d\u8bf4\u662f\u6597\u5f97\u5389\u5bb3\uff0c\u4f46\u662f\u8fd9\u79cd\u4e8b\u60c5\u603b\u4e0d\u4f1a\u8896\u624b\u65c1\u89c2\uff0c\u4fbf\u8bf7\u4e58\u8b66\u51fa\u9762\u5236\u6b62\uff0c\u6ca1\u66fe\u60f3\u5230\u5bf9\u65b9\u6b7b\u7f20\u70c2\u6253\uff0c\u5728\u6211\u4eec\u4e0b\u8f66\u65f6\uff0c\u53c8\u5506\u4f7f\u6885\u56ed\u6d3e\u51fa\u6240\u51fa\u8b66\u60f3\u6263\u62bc\u6211\u4eec\uff0c\u8003\u8651\u5230\u4e8b\u60c5\u89e3\u51b3\u4e0d\u5f53\u6291\u6216\u62d6\u5ef6\u592a\u4e45\u4f1a\u9020\u6210\u6076\u52a3\u7684\u56fd\u9645\u5f71\u54cd\uff0c\u6211\u5c31\u76f4\u63a5\u8ddf\u9648\u5c40\u957f\u6c42\u6551\u2014\u2014\u5012\u662f\u4e0d\u60f3\u7ed9\u5e02\u5c40\u9020\u6210\u989d\u5916\u7684\u538b\u529b\uff0c\u6211\u6ca1\u6709\u8ddf\u9648\u5c40\u957f\u63d0\u8d77\u88ab\u9a9a\u6270\u4eba\u7684\u8eab\u4efd\uff0c\u8fc7\u6765\u8ddf\u7f57\u4e66\u8bb0\u4f60\u89e3\u91ca\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u8fd9\u4e2a\u9648\u5c40\u957f\u5012\u6ca1\u6709\u8ddf\u6211\u6c47\u62a5\uff0c\u201d\u7f57\u541b\u7d27\u7ef7\u8d77\u7684\u8138\u8272\u7a0d\u7a0d\u7f13\u548c\u4e86\u4e00\u4e0b\uff0c\u6bd4\u8d77\u6b64\u65f6\u7684\u5934\u75bc\uff0c\u5f53\u65f6\u82e5\u5f15\u8d77\u5916\u4ea4\u7ea0\u7eb7\uff0c\u9ebb\u70e6\u4f3c\u4e4e\u66f4\u5927\u4e00\u4e9b\uff0c\u8f6c\u8eab\u7ed9\u9648\u79c9\u5fb7\u8bf4\u9053\uff0c\u201c\u7b49\u4f1a\u513f\u4f1a\u8bae\u4e0a\uff0c\u4f60\u5c06\u8fd9\u70b9\u8865\u5145\u8fdb\u53bb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u82e5\u6709\u5fc5\u8981\uff0c\u6211\u53ef\u4ee5\u8bf7\u674e\u99a8\u4e88\u5c0f\u59d0\u5230\u5e02\u5c40\u8865\u5145\u4e00\u4efd\u7b14\u5f55\uff1f\u201d</p><p>\u3000\u3000\u7f57\u541b\u6c89\u541f\u4e00\u4e8c\uff0c\u5429\u5490\u9648\u79c9\u5fb7\uff1a\u201c\u8865\u5145\u7b14\u5f55\u5df2\u7ecf\u5f88\u9ebb\u70e6\u5979\u4e86\uff0c\u5e02\u5c40\u662f\u4e0d\u662f\u6d3e\u4eba\u4eb2\u81ea\u8d70\u4e00\u8d9f\uff1f\u5bf9\u56fd\u9645\u53cb\u4eba\u4e00\u5b9a\u8981\u6ce8\u610f\u6001\u5ea6\u3002\u201d</p><p>\u3000\u3000\u9648\u79c9\u5fb7\u8fd9\u65f6\u5019\u4eba\u4e5f\u4e0d\u80fd\u79bb\u5f00\u7f57\u541b\u7684\u529e\u516c\u5ba4\uff0c\u4e5f\u4e0d\u77e5\u9053\u8ba9\u5e02\u5c40\u8c01\u51fa\u9762\u5408\u9002\uff0c\u60f3\u5230\u9ad8\u65b0\u533a\u5206\u5c40\u7684\u526f\u5c40\u957f\u4f55\u7eaa\u4e91\u4e0e\u9526\u6e56\u5173\u7cfb\u751a\u5bc6\uff0c\u5e94\u8be5\u80fd\u914d\u5408\u597d\u9526\u6e56\u5c06\u7b14\u5f55\u505a\u597d\uff0c\u8d70\u5230\u4e00\u8fb9\uff0c\u62ff\u51fa\u624b\u673a\u76f4\u63a5\u7ed9\u4f55\u7eaa\u4e91\u6253\u7535\u8bdd\u3002</p><p>\u3000\u3000\u7f57\u541b\u4e3a\u4f55\u5982\u6b64\u76db\u6012\uff1f\u5173\u952e\u8fd8\u5728\u4e8e\u4e8b\u4ef6\u5f71\u54cd\u6076\u52a3\uff0c\u7f57\u541b\u4e5f\u8981\u7ed9\u4e0a\u9762\u4e00\u4e2a\u81ea\u5706\u5176\u8bf4\u7684\u89e3\u91ca\u3002</p><p>\u3000\u3000\u660e\u77e5\u9053\u94b1\u6842\u534e\u6709\u95ee\u9898\u8fd8\u9001\u4ed6\u51fa\u56fd\u8003\u5bdf\uff0c\u5f53\u521d\u662f\u8003\u8651\u8c03\u864e\u79bb\u5c71\uff0c\u7ed9\u4fa6\u67e5\u738b\u5efa\u6797\u6d89\u9ed1\u6848\u63d0\u4f9b\u4fbf\u5229\uff0c\u6839\u672c\u4e0a\uff0c\u4e5f\u662f\u60f3\u5c06\u6848\u4ef6\u4fa6\u5bdf\u63a7\u5236\u5728\u4e00\u5b9a\u7684\u8303\u56f4\u4e4b\u5185\uff0c\u538b\u6839\u513f\u5c31\u6ca1\u6709\u51c6\u5907\u52a8\u94b1\u6842\u534e\u4ee5\u53ca\u94b1\u6842\u534e\u80cc\u540e\u9760\u5c71\u7684\u5fc3\u601d\uff0c\u81ea\u7136\u4e5f\u5c31\u6ca1\u6709\u9632\u8303\u94b1\u6842\u534e\u4f1a\u5728\u56fd\u5916\u6f5c\u9003\u3002</p><p>\u3000\u3000\u8fd9\u4efd\u5fc3\u601d\u53ea\u80fd\u610f\u4f1a\uff0c\u4e0d\u80fd\u8a00\u4f20\uff0c\u66f4\u4e0d\u80fd\u62ff\u51fa\u6765\u4f5c\u4e3a\u6b63\u5f53\u7406\u7531\u8ddf\u4e0a\u9762\u89e3\u91ca\u3002</p><p>\u3000\u3000\u8fd9\u662f\u4e00\u4e2a\u65e0\u6cd5\u89e3\u91ca\u7684\u6b7b\u7ed3\uff0c\u5c06\u674e\u99a8\u4e88\u626f\u8fdb\u6765\uff0c\u770b\u8d77\u6765\u5bf9\u89e3\u91ca\u8fd9\u4e2a\u6b7b\u7ed3\u6ca1\u6709\u4efb\u4f55\u7684\u5e2e\u52a9\uff0c\u4f46\u662f\u674e\u99a8\u4e88\u7684\u8eab\u4efd\u4ee5\u53ca\u56fd\u5185\u5bf9\u5916\u4ea4\u7684\u653f\u7b56\u59ff\u6001\uff0c\u5c06\u674e\u99a8\u4e88\u626f\u8fdb\u6765\u80fd\u6781\u5927\u7684\u5206\u6563\u4e0a\u9762\u5bf9\u8fd9\u4e8b\u7684\u6ce8\u610f\u529b\uff1a\u5c31\u5982\u7f57\u541b\u6240\u60f3\uff0c\u6bd4\u8f83\u8d77\u5b98\u5458\u5728\u56fd\u5916\u8003\u5bdf\u6f5c\u9003\uff0c\u8f6f\u5367\u5305\u53a2\u9a9a\u6270\u5987\u5973\u95f9\u8d77\u56fd\u9645\u7ea0\u7eb7\u6765\u66f4\u9ebb\u70e6\uff0c\u66f4\u7834\u574f\u4e2d\u56fd\u7684\u56fd\u9645\u5f62\u8c61\u3002</p><p>\u3000\u3000\u5728\u67d0\u4e9b\u4eba\u7684\u601d\u7ef4\u91cc\uff0c\u6216\u8bb8\u4f1a\u60f3\u5f20\u5065\u4e0e\u9648\u79c9\u5fb7\u7684\u884c\u4e3a\u867d\u7136\u7ed9\u94b1\u6842\u534e\u5728\u56fd\u5916\u6f5c\u9003\u5ba2\u89c2\u4e0a\u5236\u9020\u4e86\u4fbf\u5229\u6761\u4ef6\uff0c\u4f46\u662f\u4ed6\u4eec\u4e5f\u6210\u529f\u7684\u963b\u6b62\u4e86\u4e00\u8d77\u5916\u4ea4\u7ea0\u7eb7\u7684\u53d1\u751f\uff0c\u8fd9\u91cc\u9762\u4e5f\u6709\u529f\u8fc7\u76f8\u62b5\u7684\u610f\u5473\uff0c\u738b\u5efa\u6797\u6d89\u9ed1\u6848\u5012\u4e0d\u662f\u4ec0\u4e48\u91cd\u70b9\uff0c\u4f3c\u4e4e\u5c31\u56e0\u4e3a\u4ed6\u5728\u706b\u8f66\u8f6f\u5367\u5305\u53a2\u91cc\u9a9a\u6270\u56fd\u9645\u5987\u5973\u5c31\u8be5\u6740\u4e00\u767e\u904d\u3002</p><p>\u3000\u3000\u5f20\u5065\u89c1\u7f57\u541b\u8138\u8272\u7a0d\u7f13\u4e0b\u6765\uff0c\u624d\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u53ea\u89c9\u80cc\u810a\u51c9\u55d6\u55d6\u7684\uff0c\u8fd9\u624d\u611f\u89c9\u5230\u540e\u80cc\u7ed9\u6c57\u6e7f\u4e86\uff0c\u9a6c\u4e0a\u5c31\u8981\u53ec\u5f00\u7d27\u6025\u5e38\u59d4\u4f1a\u8bae\u8ba8\u8bba\u94b1\u6842\u534e\u6f5c\u9003\u7684\u5e94\u5bf9\u5904\u7f6e\u3002\u8981\u67e5\u51fa\u8c01\u5728\u80cc\u540e\u901a\u98ce\u62a5\u4fe1\u7684\u53ef\u80fd\u6027\u51e0\u4e4e\u4e3a\u96f6\uff0c\u5f20\u5065\u5dee\u70b9\u90fd\u8ba4\u547d\u81ea\u5df1\u4f1a\u7ed9\u62ff\u51fa\u6765\u5f53\u66ff\u7f6a\u7f8a\uff0c\u6ca1\u60f3\u5230\u4e8b\u60c5\u5230\u8fd9\u4f1a\u513f\u8fd8\u6709\u8f6c\u673a\u3002</p><p>\u3000\u3000\u7f57\u541b\u7ad9\u5230\u529e\u516c\u684c\u524d\uff0c\u624b\u6307\u6572\u7740\u529e\u516c\u684c\uff0c\u6c89\u541f\u4e86\u7247\u523b\uff0c\u624d\u8f6c\u8fc7\u8eab\u6765\uff0c\u8ddf\u5f20\u5065\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u597d\u4e86\uff0c\u4f60\u4e0e\u9648\u5c40\u957f\u5546\u91cf\u7684\u4e8b\u60c5\uff0c\u5c31\u4e0d\u8981\u5728\u5e38\u59d4\u4f1a\u8bae\u4e0a\u89e3\u91ca\u4e86\uff0c\u591a\u89e3\u91ca\u4e5f\u4e0d\u597d\uff0c\u4e0d\u8fc7\u5357\u90ba\u533a\u5b98\u5458\u5728\u51fa\u56fd\u8003\u5bdf\u671f\u95f4\u6f5c\u9003\uff0c\u5357\u90ba\u533a\u59d4\u4e5f\u8981\u8ba4\u771f\u7684\u3001\u6df1\u523b\u7684\u603b\u7ed3\u6559\u8bad\u2026\u2026\u201d\u53c8\u95ee\u9648\u79c9\u5fb7\uff0c\u201c\u6253\u9ed1\u4e13\u9879\u884c\u52a8\u662f\u5728\u94b1\u6842\u534e\u51fa\u56fd\u540e\u624d\u90e8\u7f72\u7684\uff0c\u80fd\u4e0d\u80fd\u8ffd\u67e5\u5230\u8c01\u5728\u5173\u952e\u65f6\u523b\u901a\u98ce\u62a5\u4fe1\uff1f\u201d</p><p>\u3000\u3000\u201c\u76ee\u524d\u53ea\u80fd\u8ffd\u67e5\u5efa\u90ba\u6253\u51fa\u53bb\u7684\u8d8a\u6d0b\u7535\u8bdd\u2026\u2026\u201d\u9648\u79c9\u5fb7\u5b9e\u8bdd\u5b9e\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u5efa\u90ba\u5e02\u8b66\u65b9\u6392\u67e5\u53ea\u5c40\u9650\u5728\u5efa\u90ba\uff0c\u8fd8\u6ca1\u6709\u80fd\u529b\u5728\u5168\u56fd\u8303\u56f4\u5185\u8fdb\u884c\u6392\u67e5\uff0c\u5982\u679c\u901a\u98ce\u62a5\u4fe1\u7684\u4eba\u4e0d\u662f\u76f4\u63a5\u5728\u5efa\u90ba\u6253\u8d8a\u6d0b\u7535\u8bdd\u901a\u98ce\u62a5\u4fe1\uff0c\u80fd\u67e5\u5230\u7684\u53ef\u80fd\u6027\u5c31\u51e0\u4e4e\u4e3a\u96f6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u6000\u7591\u80e1\u5b97\u5e86\u3001\u6797\u96ea\u4e24\u4eba\uff0c\u867d\u7136\u80e1\u5b97\u5e86\u3001\u6797\u96ea\u4e0e\u94b1\u6842\u534e\u6709\u7275\u6d89\u7684\u53ef\u80fd\u6781\u5fae\uff0c\u4f46\u662f\u4ed6\u4eec\u731c\u5230\u5e02\u5c40\u4fa6\u7834\u738b\u5efa\u6797\u6848\u8ddf\u81ea\u5df1\u6709\u5173\uff0c\u6307\u4e0d\u5b9a\u4ed6\u4eec\u60f3\u901a\u5173\u952e\u5904\u4f1a\u987a\u6c34\u63a8\u821f\u4e00\u628a\u7ed9\u81ea\u5df1\u60f9\u4e9b\u9ebb\u70e6\u6765\u3002</p><p>\u3000\u3000\u8fd9\u4ef6\u4e8b\u662f\u5f88\u5934\u75bc\uff0c\u5373\u4f7f\u7f57\u541b\u6700\u5927\u9650\u5ea6\u7684\u5305\u63fd\u4e0b\u6765\uff0c\u5f20\u5065\u8fd8\u662f\u8981\u627f\u62c5\u4e00\u5b9a\u7684\u653f\u6cbb\u8d23\u4efb\uff0c\u5012\u662f\u9648\u79c9\u5fb7\u7684\u8d23\u4efb\u8f7b\u4e00\u4e9b\uff0c\u6bd5\u7adf\u4ed6\u662f\u5728\u79c9\u516c\u529e\u6848\uff0c\u5728\u5236\u6b62\u5916\u4ea4\u7ea0\u7eb7\u4e0a\u8fd8\u7acb\u4e86\u529f\u2014\u2014\u53e6\u4e00\u65b9\u9762\uff0c\u5373\u4f7f\u7f57\u541b\u51fa\u9762\u5305\u63fd\uff0c\u80e1\u5b97\u5e86\u3001\u8096\u660e\u5efa\u4ed6\u4eec\u53c8\u600e\u4e48\u4f1a\u653e\u5f03\u5982\u6b64\u826f\u673a\uff1f</p><p>\u3000\u3000\u8fd9\u4e2a\u731c\u6d4b\u4e5f\u53ea\u80fd\u5fc3\u91cc\u60f3\u7740\uff0c\u4e0e\u7f57\u541b\u7684\u5173\u7cfb\u8fd8\u6ca1\u6709\u5bc6\u5207\u80fd\u968f\u610f\u7684\u5c06\u8fd9\u79cd\u731c\u6d4b\u8bf4\u51fa\u6765\uff0c\u518d\u8bf4\u5f20\u606a\u4e5f\u80fd\u770b\u51fa\u7f57\u541b\u7684\u6124\u6012\u91cc\u4e5f\u6709\u4e0d\u559c\u6b22\u81ea\u5df1\u63d2\u624b\u4ed6\u4e00\u4ea9\u4e09\u5206\u5730\u7684\u610f\u5473\u5728\u5185\u3002</p><p>\u3000\u3000\u5f20\u606a\u79bb\u5f00\u7f57\u541b\u7684\u529e\u516c\u5ba4\uff0c\u76f4\u63a5\u56de\u5230\u5b66\u5e9c\u5df7\uff0c\u8fd9\u8fb9\u674e\u99a8\u4e88\u521a\u7ed9\u4f55\u7eaa\u4e91\u505a\u4e86\u4e00\u4e9b\u706b\u8f66\u9a9a\u6270\u4e8b\u4ef6\u7684\u8865\u5145\u8bf4\u660e\uff0c\u5230\u4e2d\u5348\u65f6\uff0c\u63a5\u5230\u53c2\u52a0\u5e02\u59d4\u5e38\u59d4\u4f1a\u8bae\u7684\u738b\u7ef4\u5747\u7684\u7535\u8bdd\u5f97\u77e5\u5e02\u59d4\u5e38\u59d4\u4f1a\u8bae\u4e0a\u7684\u4e89\u6267\u60c5\u5f62\u3002\u80e1\u5b97\u5e86\u679c\u7136\u5bf9\u5f20\u5065\u4e34\u65f6\u5c06\u94b1\u6842\u534e\u3001\u5434\u7231\u6c11\u4e24\u4eba\u589e\u6dfb\u5230\u51fa\u56fd\u8003\u5bdf\u540d\u5355\u4e0a\u4e00\u4e8b\u54ac\u4f4f\u4e0d\u653e\uff0c\u8981\u4e0d\u662f\u7f57\u541b\u51fa\u9762\u8892\u62a4\uff0c\u4f1a\u8bae\u4e0a\u505a\u51fa\u5bf9\u5f20\u5065\u505c\u804c\u8c03\u67e5\u7684\u51b3\u5b9a\u90fd\u6709\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u4e0e\u80e1\u5b97\u5e86\u7684\u5bbf\u6028\u4e00\u76f4\u90fd\u6709\uff0c\u8fd8\u4e0d\u80fd\u4ece\u80e1\u5b97\u5e86\u5728\u5e38\u59d4\u4f1a\u8bae\u4e0a\u7684\u8868\u73b0\u6765\u5224\u65ad\u4ed6\u5c31\u662f\u80cc\u540e\u901a\u98ce\u62a5\u4fe1\u7684\u4eba\uff0c\u5f20\u606a\u5fc3\u60f3\u80e1\u5b97\u5e86\u53ef\u80fd\u4f1a\u987e\u8651\u5230\u653f\u6cbb\u4e0a\u7684\u98ce\u9669\uff0c\u6bd4\u8d77\u80e1\u5b97\u5e86\u6765\uff0c\u5012\u89c9\u5f97\u6797\u96ea\u66f4\u6709\u53ef\u80fd\u901a\u98ce\u62a5\u4fe1\u2014\u2014\u8fd9\u4e2a\u86c7\u874e\u5973\u4eba\u505a\u4e9b\u635f\u4eba\u4e0d\u5229\u5df1\u7684\u4e8b\u60c5\u771f\u662f\u4e00\u70b9\u90fd\u4e0d\u5947\u602a\uff0c\u6628\u5929\u591c\u91cc\u4e5f\u662f\u5979\u9996\u5148\u5c06\u7ebf\u7d22\u8054\u7cfb\u5230\u4ed6\u5934\u4e0a\u4e86\u2014\u2014\u4e5f\u4e0d\u6392\u9664\u90a3\u4e9b\u62c5\u5fc3\u94b1\u6842\u534e\u56de\u56fd\u4f1a\u7275\u6d89\u5230\u81ea\u5df1\u7684\u5b98\u5458\u3002</p><p>\u3000\u3000\u5f20\u5065\u662f\u8981\u627f\u5f53\u8d23\u4efb\uff0c\u4f46\u53ea\u8981\u786e\u5b9a\u6ca1\u6709\u4e3b\u89c2\u4e0a\u7684\u610f\u613f\uff0c\u5de5\u4f5c\u4e0a\u7684\u5931\u8bef\u5012\u4e0d\u662f\u7279\u522b\u91cd\u5927\u7684\u8d23\u4efb\uff0c\u6bd5\u7adf\u6bcf\u5e74\u90fd\u6709\u5b98\u5458\u6f5c\u9003\u56fd\u5916\uff0c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u6709\u4eba\u4e3a\u6b64\u627f\u5f53\u5168\u90e8\u8d23\u4efb\u800c\u7ed9\u6458\u6389\u4e4c\u7eb1\u5e3d\u3002\u5f53\u7136\uff0c\u5bf9\u5f20\u5065\u65e5\u540e\u7684\u4ed5\u9014\u8fd8\u662f\u6709\u76f8\u5f53\u7684\u8d1f\u9762\u5f71\u54cd\uff0c\u5f88\u53ef\u80fd\u5357\u90ba\u533a\u59d4\u4e66\u8bb0\u5c31\u662f\u4ed6\u5728\u4ed5\u9014\u4e0a\u518d\u4e5f\u65e0\u6cd5\u7a81\u7834\u7684\u574e\u4e86\u3002\u53e6\u5916\uff0c\u94b1\u6842\u534e\u3001\u5434\u7231\u6c11\u5728\u56fd\u5916\u8003\u5bdf\u6f5c\u9003\u4e00\u4e8b\u867d\u7136\u6ca1\u6709\u5a92\u4f53\u62a5\u9053\uff0c\u4e00\u65b9\u9762\u5728\u5efa\u90ba\u5e02\u3001\u4e1c\u6d77\u7701\u5b98\u573a\u5185\u90e8\u9020\u6210\u5f88\u5927\u7684\u5f71\u54cd\uff0c\u7701\u59d4\u7ec4\u7ec7\u90e8\u751a\u81f3\u51fa\u53f0\u89c4\u5b9a\u8981\u6c42\u53bf\u5904\u7ea7\u4ee5\u4e0a\u5b98\u5458\u5e73\u65e5\u5c06\u51fa\u56fd\u8bc1\u4ef6\u4ea4\u7531\u7ec4\u7ec7\u90e8\u95e8\u7edf\u4e00\u7ba1\u7406\uff0c\u51fa\u56fd\u5904\u4e5f\u8981\u6c42\u52a0\u5f3a\u653f\u5ba1\u5de5\u4f5c\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u6f5c\u9003\u4e00\u6848\u5728\u65b0\u534e\u5185\u53c2\u4e0a\u6709\u62a5\u9053\uff0c\u53c8\u53e6\u5916\u4e13\u6848\u5448\u62a5\u4e2d\u592e\uff0c\u4e5f\u5bf9\u5efa\u90ba\u591a\u5c11\u6709\u4e9b\u8d1f\u9762\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u5f25\u8865\u6f5c\u9003\u6848\u5bf9\u5efa\u90ba\u7684\u8d1f\u9762\u5f71\u54cd\uff0c\u5f20\u606a\u4ee5\u7231\u8fbe\u96c6\u56e2\u540d\u4e49\u6350\u8d44\u5efa\u56fe\u4e66\u9986\u4e00\u4e8b\u5c31\u9700\u8981\u66f4\u5927\u529b\u7684\u5ba3\u4f20\u4e0e\u9020\u52bf\uff0c\u4e5f\u5c06\u529f\u7ee9\u66f4\u5927\u9650\u5ea6\u7684\u52a0\u5728\u5efa\u90ba\u5e02\u59d4\u5934\u4e0a\u3002</p><p>\u3000\u3000\u9a6c\u6765\u897f\u4e9a\u90ed\u677e\u5ef6\u5bb6\u65cf\u5bf9\u56fd\u5185\u7684\u7eb8\u4e1a\u5e02\u573a\u5f88\u6709\u4fe1\u5fc3\uff0c\u5f20\u606a\u5c06\u540d\u4e0b\u7ea66\uff05\u7684\u65b0\u5149\u7eb8\u4e1a\u80a1\u7968\u8f6c\u8ba9\u7ed9\u90ed\u677e\u5ef6\u5bb6\u65cf\uff0c\u7b79\u63aa\u5230\u5efa\u8bbe\u56fe\u4e66\u9986\u6240\u9700\u8981\u7684\u5efa\u8bbe\u8d44\u91d1\uff0c\u4e00\u6b21\u6027\u6295\u5165\u4e09\u4ebf\u5143\u5de8\u8d44\u4ee5\u7231\u8fbe\u96c6\u56e2\u7684\u540d\u4e49\u8d5e\u52a9\u6210\u7acb\u4e86\u7231\u8fbe\u6587\u5316\u57fa\u91d1\u4f1a\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u59d4\u5e02\u653f\u5e9c\u4ece\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u624b\u91cc\u6536\u56de\u4e1c\u5927\u5317\u95e8\u5730\u5757\uff0c\u4ee5\u65e0\u507f\u5212\u62e8\u7684\u5f62\u5f0f\u5c06\u90a3\u5757\u5730\u7684\u4f7f\u7528\u6743\u8f6c\u7ed9\u7231\u8fbe\u6587\u5316\u57fa\u91d1\u4f1a\uff0c\u7528\u4f5c\u56fe\u4e66\u9986\u7684\u5efa\u8bbe\u7528\u5730\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u56fd\u5185\u6700\u5927\u89c4\u6a21\u7684\u5355\u4f53\u56fe\u4e66\u9986\uff0c\u5373\u4f7f\u5728\u5df2\u6709\u7684\u56fe\u4e66\u9986\u89c4\u6a21\uff0c\u4e5f\u5c06\u6324\u8fdb\u524d\u4e09\u7532\uff0c\u800c\u4e14\u53c8\u662f\u6148\u5584\u6350\u5efa\uff0c\u5728\u56fd\u5185\u81ea\u7136\u5f15\u8d77\u6781\u5927\u7684\u8f70\u52a8\u4e0e\u53cd\u54cd\u3002</p><p>\u3000\u3000\u590f\u5b63\u6d2a\u707e\u65f6\uff0c\u9526\u6e56\u6350\u6b3e\u6350\u7269\u4e5f\u7d2f\u79ef\u5c06\u8fd1\u4e24\u4ebf\u4e4b\u5de8\uff0c\u53ea\u662f\u8003\u8651\u5230\u56fd\u5185\u67d0\u4e9b\u5b98\u5458\u7684\u5fc3\u6001\uff0c\u9526\u6e56\u6700\u7ec8\u53ea\u4ee5\u7231\u8fbe\u96c6\u56e2\u7684\u540d\u4e49\u627f\u8ba4\u4e86\u4e00\u5343\u4e07\u7684\u6350\u8d60\u6570\u989d\uff0c\u4e00\u5343\u4e07\u4e5f\u662f\u9526\u6e56\u9664\u4e86\u4e1c\u6d77\u3001\u4e1c\u5357\u5730\u533a\u4e4b\u5916\u7684\u6350\u8d60\u6b3e\u9879\u3002</p><p>\u3000\u3000\u4e0e\u5929\u707e\u4eba\u7978\u4e0d\u540c\uff0c\u6350\u5efa\u56fe\u4e66\u9986\u53ef\u4ee5\u8bf4\u662f\u6587\u5316\u76db\u4e8b\uff0c\u5927\u8086\u5ba3\u626c\u4e5f\u4e0d\u4f1a\u6709\u8c01\u4f1a\u89c9\u5f97\u88ab\u6253\u8138\u2014\u2014\u5373\u4f7f\u8fd8\u6709\u4eba\u89c9\u5f97\u88ab\u62bd\u8138\uff0c\u8fd9\u4e9b\u4eba\u7684\u610f\u89c1\u4e5f\u53ef\u4ee5\u4e0d\u4e88\u7406\u4f1a\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u6700\u5927\u7684\u9650\u5ea6\u53d1\u5c55\u6350\u5efa\u56fe\u4e66\u9986\u7684\u4f5c\u7528\uff0c\u4f7f\u4e1c\u5927\u3001\u5e08\u5927\u7b49\u71d5\u5f52\u6e56\u5730\u533a\u9ad8\u6821\u56fe\u4e66\u9986\u4e0e\u6350\u5efa\u56fe\u4e66\u9986\u5f62\u6210\u4e00\u4e2a\u8d44\u6e90\u5171\u4eab\u4e92\u8865\u7684\u5927\u7cfb\u7edf\uff0c\u7231\u8fbe\u6587\u5316\u57fa\u91d1\u4f1a\u8fd8\u7279\u610f\u8058\u8bf7\u4e1c\u6d77\u5927\u5b66\u6821\u957f\u62c5\u4efb\u57fa\u91d1\u4f1a\u540d\u8a89\u7406\u4e8b\u957f\uff0c\u53c8\u8058\u8bf7\u5e08\u5927\u7b49\u9ad8\u6821\u6821\u957f\u4ee5\u53ca\u7701\u5e02\u591a\u540d\u6587\u5316\u90e8\u95e8\u5b98\u5458\u62c5\u4efb\u7406\u4e8b\u4f1a\u540d\u8a89\u7406\u4e8b\u3002</p><p>\u3000\u3000\u56fd\u5185\u5efa\u5de5\u7a0b\u6709\u7740\u8fb9\u8bbe\u8ba1\u8fb9\u5ba1\u6279\u8fb9\u5efa\u8bbe\u7684\u4e60\u60ef\uff0c\u5927\u5bb6\u90fd\u671f\u5f85\u7231\u8fbe\u56fe\u4e66\u9986\u80fd\u65e9\u4e00\u6b65\u5efa\u6210\uff0c\u5728\u8bbe\u8ba1\u521d\u7a3f\u901a\u8fc7\u5ba1\u6838\u4e4b\u540e\uff0c\u56fe\u4e66\u9986\u5c31\u8d76\u5728\u6625\u8282\u4e4b\u524d\u6b63\u5f0f\u7834\u571f\u52a8\u5de5\u4e86\u3002</p></article>"}}}]);
