(window.webpackJsonp=window.webpackJsonp||[]).push([[121],{144:function(p){p.exports={chapterTitle:null,title:"\u7b2c1103\u7ae0 \u541b\u5b50\u534f\u5b9a",content:"<article><h2>\u7b2c1103\u7ae0 \u541b\u5b50\u534f\u5b9a</h2><p>\u3000\u3000\u8054\u4fe1\u4e0e\u8054\u901a\u5c31\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u7684\u5408\u4f5c\u8c08\u5224\u5df2\u7ecf\u79d8\u5bc6\u8fdb\u884c\u4e86\u8bb8\u4e45\uff0c\u63d0\u524d\u4e0e\u5f20\u606a\u8a00\u8bed\u4e00\u58f0\uff0c\u4e5f\u53ea\u662f\u5728\u6b63\u5f0f\u8bd5\u6c34\u5b9a\u5236\u4e1a\u52a1\u4e4b\u524d\u8ddf\u9526\u6e56\u6253\u58f0\u62db\u547c\u3002</p><p>\u3000\u30001\u670812\u65e5\uff0c\u4e5f\u5c31\u662f\u5f20\u606a\u6b64\u6b21\u5230\u5317\u4eac\u7684\u7b2c\u4e09\u5929\uff0c\u8054\u901a\u7684\u5317\u4eac\u5206\u516c\u53f8\u5c31\u6b63\u5f0f\u63a8\u51fa\u5e74\u672b\u5145\u503c\u9001\u624b\u673a\u7684\u65b0\u5e74\u793c\u5305\u6d3b\u52a8\u3002\u4e4b\u524d\u4fdd\u5bc6\u5de5\u4f5c\u505a\u5f97\u5f88\u51fa\u8272\uff0c\u6d3b\u52a8\u6b63\u5f0f\u505a\u8d77\u6765\uff0c\u53c8\u901a\u8fc7\u5317\u4eac\u7535\u89c6\u53f0\u3001\u5317\u4eac\u6668\u62a5\u7b49\u5a92\u4f53\u8fdb\u884c\u4e86\u5ba3\u4f20\uff0c\u641e\u5f97\u9887\u5177\u58f0\u52bf\u3002</p><p>\u3000\u3000\u5f53\u5929\u591c\u91cc\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u8fd8\u4e13\u95e8\u4e3e\u529e\u4e86\u5e86\u795d\u5bb4\u4f1a\uff0c\u6768\u84c9\u5e73\u4f5c\u4e3a\u8054\u901a\u8d1f\u8d23\u76f8\u5173\u4e1a\u52a1\u7684\u5206\u7ba1\u9886\u5bfc\uff0c\u901a\u8fc7\u8096\u745e\u6c11\u9080\u8bf7\u9526\u6e56\u5728\u5317\u4eac\u7684\u9ad8\u5c42\u53c2\u52a0\u5e86\u795d\u5bb4\u4f1a\uff0c\u4e5f\u662f\u77e5\u9053\u5f20\u606a\u4eba\u5728\u5317\u4eac\uff0c\u5e0c\u671b\u501f\u6b64\u673a\u4f1a\u4e0e\u5f20\u606a\u975e\u6b63\u5f0f\u7684\u63a5\u89e6\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u63d0\u524d\u79bb\u5f00\u5317\u4eac\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u4ee5\u53ca\u5218\u9e23\u950b\u3001\u59da\u575a\u524d\u5f80\u53c2\u52a0\u5bb4\u4f1a\uff0c\u4e5f\u662f\u60f3\u901a\u8fc7\u8fd9\u6b21\u673a\u4f1a\u4e0e\u8054\u901a\u9ad8\u5c42\u63a5\u89e6\u3002</p><p>\u3000\u3000\u8bf4\u5b9e\u8bdd\uff0c\u5f20\u606a\u4e4b\u524d\u4e5f\u6ca1\u6709\u60f3\u5230\u8054\u4fe1\u4e0e\u8054\u901a\u7684\u6b65\u4f10\u4f1a\u8fd9\u4e48\u5feb\uff0c\u60f3\u60f3\u4e5f\u91ca\u7136\uff0c\u8096\u745e\u6c11\u6bd5\u7adf\u8fd8\u662f\u4e2a\u5f88\u6709\u60f3\u6cd5\u7684\u4eba\uff0c\u8054\u4fe1\u4e0e\u9526\u6e56\u5408\u4f5c\uff0c\u751a\u81f3\u5728\u6280\u672f\u4e0a\u4f9d\u8d56\u9526\u6e56\uff0c\u4f46\u5e76\u4e0d\u610f\u5473\u7740\u8054\u4fe1\u5c31\u8981\u505a\u9526\u6e56\u7684\u9644\u5eb8\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u662f\u62c5\u5fe7\u8054\u4fe1\u4e0e\u8054\u901a\u4f1a\u592a\u4ed3\u4fc3\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u65f6\u95f4\u521a\u521a\u7ffb\u5230\u65b0\u5343\u5e74\u7684\u7b2c\u4e00\u9875\uff0c\u5bf9\u666e\u901a\u5e02\u6c11\u6765\u8bf4\uff0c\u624b\u673a\u8fd8\u5c5e\u4e8e\u5962\u4f88\u54c1\uff0c\u9664\u4e86\u624b\u673a\u672c\u8eab\u552e\u4ef7\u4e0d\u83f2\u4e4b\u5916\uff0c\u9ad8\u6602\u7684\u901a\u4fe1\u8d44\u8d39\u4e5f\u4e0d\u662f\u666e\u901a\u5e02\u6c11\u80fd\u591f\u627f\u53d7\u3002</p><p>\u3000\u3000\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u63a8\u51fa\u8bdd\u8d39\u5145\u503c2400\u5143\u8d60\u9001\u4e00\u90e8\u8054\u4fe1\u624b\u673a\u7684\u65b0\u5e74\u793c\u5305\u6d3b\u52a8\uff0c\u7684\u786e\u80fd\u5438\u5f15\u5230\u666e\u901a\u5e02\u6c11\u7684\u773c\u7403\u3002</p><p>\u3000\u3000\u7531\u4e8e\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u5341\u4e00\u6708\u4efd\u63a8\u51fa\u4f4e\u7aefESS\u624b\u673a\u90fd\u6ca1\u6709\u8fdb\u884c\u4ec0\u4e48\u5ba3\u4f20\uff0c\u5728\u666e\u901a\u5e02\u6c11\u7684\u5370\u8c61\u91cc\uff0c\u4e00\u90e8\u624b\u673a\u518d\u4fbf\u5b9c\u4e5f\u8981\u4e00\u5343\u516b\u4e5d\u767e\u5143\uff0c\u53c2\u52a0\u8054\u901a\u7684\u8fd9\u4e2a\u6d3b\u52a8\u7b80\u76f4\u5c31\u662f\u5b9e\u5b9e\u5728\u5728\u7684\u4e70\u4e00\u8d60\u4e00\u6d3b\u52a8\u3002\u5373\u4f7f\u5bf9\u8054\u4fe1\u624b\u673a\u6709\u6240\u8ba4\u8bc6\uff0c\u4e5f\u77e5\u9053\u540c\u89c4\u683c\u7684\u8054\u4fe1\u624b\u673a\u5e02\u573a\u552e\u4ef7\u4e3a1380\u5143\uff0c\u6263\u9664\u624b\u673a\u6210\u672c\uff0c\u8bdd\u8d39\u76f8\u5f53\u4e8e\u53ea\u6253\u4e86\u56db\u6298\uff0c\u552f\u4e00\u7684\u9650\u5236\u6761\u4ef6\u5c31\u662f\u5145\u503c\u8bdd\u8d39\u6709\u5341\u4e8c\u4e2a\u6708\u7684\u4f7f\u7528\u65f6\u9650\u3002</p><p>\u3000\u3000\u5728\u9ad8\u6602\u79fb\u52a8\u901a\u4fe1\u8d44\u8d39\u6210\u4e3a\u963b\u6b62\u624b\u673a\u5e02\u573a\u7ee7\u7eed\u9ad8\u901f\u6269\u5f20\u7684\u4e3b\u8981\u56e0\u7d20\u4e4b\u65f6\uff0c\u5373\u4f7f\u6709\u5341\u4e8c\u4e2a\u6708\u7684\u4f7f\u7528\u65f6\u9650\uff0c\u56db\u6298\u7684\u8bdd\u8d39\u6298\u6263\u7387\u4e5f\u4ee4\u4eba\u7830\u7136\u5fc3\u52a8\u3002\u518d\u8bf4\u5f53\u524d\u79fb\u8fd0\u901a\u4fe1\u666e\u904d\u9ad8\u8d39\u7387\uff0c\u6bcf\u4e2a\u6708\u4e24\u767e\u5143\u7684\u8bdd\u8d39\u4e5f\u8981\u6bd4\u8f83\u8282\u7ea6\u624d\u591f\u7528\uff0c\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u8fd9\u6b21\u6d3b\u52a8\u81ea\u7136\u4e5f\u5c31\u5bb9\u6613\u8ba9\u4eba\u6177\u6168\u89e3\u56ca\u3002</p><p>\u3000\u3000\u7531\u4e8e\u8054\u901a\u4e0e\u8054\u4fe1\u4e4b\u524d\u7684\u4fdd\u5bc6\u5de5\u4f5c\u505a\u5f97\u76f8\u5f53\u51fa\u8272\uff0c\u521d\u671f\u7684\u8bd5\u6c34\u4e5f\u4ec5\u4ec5\u9650\u4e8e\u5317\u4eac\u5730\u533a\u7684\u5e02\u573a\uff0c\u5e02\u573a\u7b2c\u4e00\u5929\u5bf9\u8054\u901a\u629b\u51fa\u5982\u6b64\u91cd\u91cf\u7ea7\u7684\u6d3b\u52a8\u8fd8\u6765\u4e0d\u53ca\u4ea7\u751f\u591a\u5927\u7684\u53cd\u5e94\u3002\u6b64\u65f6\u8fd8\u65e0\u6cd5\u51c6\u786e\u9884\u6d4b\u8fd9\u6b21\u6d3b\u52a8\u4f1a\u4ea7\u751f\u591a\u5927\u7684\u5f71\u54cd\uff0c\u4e0d\u8fc7\u5f53\u5929\u4e0b\u5348\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u5404\u8425\u4e1a\u7f51\u70b9\u7684\u53cd\u9988\u6d88\u606f\u90fd\u76f8\u5f53\u7684\u70ed\u70c8\u3002</p><p>\u3000\u3000\u6216\u8bb8\u6768\u84c9\u5e73\u77e5\u9053\u5f20\u606a\u6709\u907f\u5f00\u5a92\u4f53\u7684\u4e60\u60ef\uff0c\u4e5f\u6216\u8bb8\u8096\u745e\u6c11\u6709\u5e2e\u5f20\u606a\u6253\u62db\u547c\uff0c\u8054\u901a\u603b\u90e8\u53ca\u5317\u4eac\u5206\u516c\u53f8\u5728\u738b\u5e9c\u5927\u996d\u5e97\u91cc\u4e3e\u884c\u7684\u5e86\u795d\u5bb4\u4f1a\u53ea\u9080\u8bf7\u5185\u90e8\u4eba\u5458\u53c2\u52a0\uff0c\u5f20\u606a\u3001\u9648\u4fe1\u751f\u3001\u5218\u9e23\u950b\u4ee5\u53ca\u59da\u575a\u7b49\u5317\u65b9\u516c\u53f8\u7684\u4e00\u4e9b\u9ad8\u5c42\u5217\u5e2d\u591a\u5c11\u6709\u4e9b\u7a81\u5140\uff0c\u62ff\u5f20\u606a\u8ddf\u6768\u84c9\u5e73\u521d\u89c1\u9762\u65f6\u7684\u5bd2\u6684\u8bdd\u6765\u8bf4\uff1a\u201c\u8fc7\u6765\u53c2\u52a0\u5bb4\u5e2d\u662f\u65e0\u529f\u53d7\u7984\u2026\u2026\u201d</p><p>\u3000\u3000\u8054\u4fe1\u5077\u5077\u6478\u6478\u7684\u5411\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u63d0\u4f9b\u5b9a\u5236\u624b\u673a\uff0c\u9526\u6e56\u4e09\u5929\u524d\u624d\u77e5\u9053\u5177\u4f53\u7684\u6d88\u606f\uff0c\u53ef\u4ee5\u8bf4\u662f\u6ca1\u6709\u505a\u4ec0\u4e48\u8d21\u732e\u3002</p><p>\u3000\u3000\u65e9\u5728\u9648\u4fe1\u751f\u62c5\u4efb\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u65f6\uff0c\u6768\u84c9\u5e73\u5c31\u4e0e\u4ed6\u6709\u8fc7\u63a5\u89e6\uff0c\u5f7c\u6b64\u95f4\u4e0d\u7b97\u964c\u751f\uff0c\u5f20\u606a\u8fd8\u662f\u9996\u6b21\u89c1\uff0c\u62ff\u4ed6\u7684\u8bdd\u6765\u8bf4\uff1a\u201c\u601d\u6155\u5df2\u4e45\uff0c\u5bf9\u4eca\u5929\u7684\u89c1\u9762\u4e5f\u671f\u5f85\u5df2\u4e45\u2026\u2026\u201d\u4ed6\u5fc3\u91cc\u5f88\u6e05\u695a\uff0c\u5708\u5b50\u5185\u7a0d\u6709\u4e9b\u5c42\u6b21\u7684\u4eba\u5fc3\u91cc\u90fd\u5f88\u6e05\u695a\uff0c\u7231\u8fbe\u8981\u4e89\u7535\u5b50\u767e\u5f3a\u699c\u699c\u9996\u7684\u4f4d\u5b50\u8f7b\u800c\u6613\u4e3e\u3002\u7231\u8fbe\u4f3c\u4e4e\u5f88\u5728\u610f\u7167\u987e\u534e\u590f\u7535\u5b50\u7b49\u56fd\u4f01\u7684\u9762\u5b50\uff0c\u9664\u4e86\u7231\u8fbe\u7535\u5b50\u5355\u72ec\u6838\u7b97\u3001\u96c6\u56e2\u6d77\u5916\u4e1a\u52a1\u5e76\u5165\u9526\u6e56\u5546\u4e8b\u4e4b\u5916\uff0c\u4e5d\u4e5d\u5e74\u8fd8\u5c06\u5143\u5668\u4ef6\u90e8\u95e8\u7684\u8d22\u52a1\u72ec\u7acb\u51fa\u53bb\uff0c\u52c9\u5f3a\u5c06\u7231\u8fbe\u96c6\u56e2\u4e5d\u4e5d\u5e74\u7684\u9500\u552e\u989d\u63a7\u5236\u5728\u4e24\u767e\u4ebf\u4ee5\u5185\u3002\u771f\u4e0d\u77e5\u9053\u7231\u8fbe\u96c6\u56e2\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u789f\u673a\u4e1a\u52a1\u3001\u6d77\u5916\u4e1a\u52a1\u4ee5\u53ca\u5143\u5668\u4ef6\u90e8\u95e8\u90fd\u5408\u5e76\u5230\u96c6\u56e2\u8d22\u52a1\u5185\u4e00\u8d77\u8ba1\u5212\uff0c\u5e74\u9500\u552e\u989d\u4f1a\u4e0d\u4f1a\u7a81\u7834\u4e94\u767e\u4ebf\uff1f\u5e74\u51c0\u76c8\u5229\u6216\u8bb8\u4e0d\u4f1a\u6bd4\u79fb\u52a8\u901a\u4fe1\u5c40\u53ca\u5176\u4e0b\u5c5e\u7684\u5404\u7701\u5e02\u79fb\u52a8\u516c\u53f8\u5dee\u591a\u5c11\u5427\uff1f\u5f20\u606a\u7f14\u9020\u5982\u6b64\u5f3a\u76db\u7ecf\u6d4e\u5e1d\u56fd\u4e4b\u521d\u8fd8\u53ea\u662f\u521a\u8bfb\u9ad8\u4e2d\u7684\u5c11\u5e74\uff0c\u5bf9\u4e8e\u77e5\u9053\u4e00\u4e9b\u8be6\u60c5\u7684\u6768\u84c9\u5e73\u6765\u8bf4\uff0c\u770b\u9526\u6e56\u7684\u5d1b\u8d77\u4e8b\u8ff9\uff0c\u5b9b\u82e5\u5728\u770b\u4e00\u6bb5\u4ee4\u4eba\u5fc3\u7eea\u6fc0\u8361\u7684\u4f20\u5947\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8096\u745e\u6c11\u4e4b\u524d\u6253\u8fc7\u62db\u547c\uff0c\u4e0d\u641e\u5927\u5f20\u65d7\u9f13\u8fce\u63a5\u90a3\u4e00\u5957\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u7b49\u4eba\u4f4e\u8c03\u7684\u76f4\u63a5\u8fdb\u5165\u5bb4\u4f1a\u5385\u7684\u5305\u53a2\u91cc\uff0c\u518d\u7531\u8096\u745e\u6c11\u66ff\u4ed6\u4ee3\u8868\u4ecb\u7ecd\u3002</p><p>\u3000\u3000\u5728\u6768\u84c9\u5e73\u7684\u773c\u91cc\uff0c\u5f20\u606a\u6c14\u8d28\u6de1\u6cca\u6c89\u9759\uff0c\u770b\u4e0a\u53bb\u6bd4\u5b9e\u9645\u5e74\u9f84\u8981\u6210\u719f\u4e00\u4e9b\uff0c\u4e4b\u524d\u89c9\u5f97\u4ed6\u80fd\u53d6\u5f97\u5982\u6b64\u8f89\u714c\u7684\u6210\u5c31\uff0c\u6216\u8bb8\u662f\u7279\u522b\u5e78\u8fd0\u7684\u7f18\u6545\uff0c\u4f46\u662f\u5728\u89c1\u9762\u4e4b\u540e\uff0c\u8fd9\u79cd\u611f\u89c9\u5c31\u7acb\u5373\u6de1\u4e86\u8bb8\u591a\uff0c\u4f1a\u60c5\u4e0d\u81ea\u7981\u7684\u60f3\uff1a\u6216\u8bb8\u4e5f\u771f\u8be5\u8fd9\u79cd\u4eba\u7269\u624d\u80fd\u83b7\u5f97\u5982\u6b64\u8f89\u714c\u7684\u6210\u5c31\uff01\u53e6\u5916\u53c8\u60f3\uff1a\u4f5c\u4e3a\u7537\u4eba\u4e5f\u8bb8\u592a\u82f1\u4fca\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5bb4\u5e2d\u5f00\u59cb\u524d\uff0c\u6768\u84c9\u5e73\u4e0e\u8096\u745e\u6c11\u4ee5\u53ca\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u5206\u522b\u4ee3\u8868\u4e24\u5bb6\u516c\u53f8\u5230\u5927\u5385\u524d\u7684\u81f4\u8f9e\u3002\u5927\u5385\u91cc\u676f\u6765\u76cf\u5f80\u5341\u5206\u7684\u70ed\u95f9\uff0c\u6768\u84c9\u5e73\u662f\u4ee3\u8868\u603b\u90e8\u7684\u9ad8\u5c42\uff0c\u5c31\u662f\u6709\u4eba\u8dd1\u5230\u5305\u53a2\u91cc\u6765\u656c\u9152\u51d1\u70ed\u95f9\uff0c\u4e5f\u4e0d\u4f1a\u8fc7\u5206\u7684\u529d\u9152\uff0c\u5f20\u606a\u4ed6\u4eec\u5012\u662f\u6709\u5fc3\u60c5\u7ec6\u7ec6\u7684\u54c1\u5c1d\u7f8e\u9152\u4f73\u80b4\u3002</p><p>\u3000\u3000\u5e2d\u81f3\u4e2d\u9014\uff0c\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u603b\u7ecf\u7406\u63a5\u5230\u4e00\u4e2a\u7535\u8bdd\uff0c\u53c8\u51d1\u5230\u6768\u84c9\u5e73\u8033\u6735\u8bf4\u4e86\u4e00\u53e5\u8bdd\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u559c\u4e0a\u7709\u68a2\uff0c\u8ddf\u8096\u745e\u6c11\u3001\u5f20\u606a\u3001\u9648\u4fe1\u751f\u4ed6\u4eec\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7ad9\u5728\u8fd9\u91cc\u559d\u9152\u7528\u9910\uff0c\u521a\u521a\u6709\u4e2a\u597d\u6d88\u606f\u4f20\u6765\uff0c\u5c31\u5728\u521a\u624d\uff0c\u6211\u4eec\u7684\u5b9a\u5236\u624b\u673a\u5c31\u5356\u51fa\u53bb\u7b2c\u4e00\u5343\u90e8\uff01\u8054\u901a\u5317\u4eac\u5730\u533a\u4eca\u5929\u65b0\u589e\u7528\u6237\u8d85\u8fc7\u4e00\u5343\u516b\u767e\u4e2a\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u8981\u4e3a\u6b64\u559d\u4e00\u676f\uff1f\u201d</p><p>\u3000\u3000\u4e5f\u5c31\u662f\u8bf4\uff0c\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7b2c\u4e00\u5929\u63a8\u51fa\u5145\u503c\u9001\u624b\u673a\u7684\u65b0\u5e74\u793c\u5305\u6d3b\u52a8\uff0c\u7528\u6237\u589e\u6da8\u901f\u5ea6\u5c31\u63d0\u9ad8\u4e86\u4e00\u500d\u6709\u4f59\uff0c\u96be\u602a\u6768\u84c9\u5e73\u4f1a\u559c\u4e0a\u7709\u68a2\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u4e5f\u968f\u5174\u7684\u4e3e\u8d77\u9152\u676f\u91cc\u795d\u8d3a\u8054\u901a\u516c\u53f8\u53d6\u5f97\u5982\u6b64\u4f73\u7ee9\uff0c\u6768\u84c9\u5e73\u53c8\u8ba9\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u5230\u5927\u5385\u91cc\u5c06\u6700\u65b0\u7684\u6d88\u606f\u6c47\u62a5\u7ed9\u4eca\u665a\u53c2\u52a0\u5bb4\u4f1a\u7684\u6240\u6709\u4eba\u77e5\u9053\u3002</p><p>\u3000\u3000\u4eca\u5929\u4e0d\u662f\u5047\u671f\u4e5f\u4e0d\u662f\u5468\u672b\uff0c\u5317\u4eac\u79fb\u52a8\u516c\u53f8\u666e\u901a\u65e5\u671f\u7684\u65b0\u589e\u7528\u6237\u6570\u91cf\u5927\u7ea6\u4e5f\u53ea\u6709\u4e24\u5343\u7a0d\u591a\u4e00\u4e9b\uff0c\u4e5f\u96be\u602a\u8054\u901a\u9ad8\u5c42\u4f1a\u559c\u5f62\u4e8e\u8272\uff0c\u4e1a\u7ee9\u6c38\u8fdc\u662f\u88ab\u89c6\u4e3a\u7b2c\u4e00\u4f4d\u7684\uff0c\u8054\u901a\u5c31\u8981\u5728\u7ebd\u7ea6\u3001\u9999\u6e2f\u4e24\u5730\u540c\u65f6\u4e0a\u5e02\uff0c\u4e0a\u5e02\u4e4b\u524d\uff0c\u8054\u901a\u9ad8\u5c42\u81ea\u7136\u4e5f\u60f3\u62ff\u51fa\u66f4\u6f02\u4eae\u7684\u4e1a\u7ee9\u6570\u636e\u51fa\u6765\u53bb\u5438\u5f15\u6295\u8d44\u8005\u3002</p><p>\u3000\u3000\u5bb4\u5e2d\u70ed\u70ed\u95f9\u95f9\u7684\u8fdb\u884c\uff0c\u6309\u7167\u4e2d\u56fd\u5bb4\u5e2d\u7279\u8272\u7684\u4f20\u7edf\uff0c\u4e5f\u51ff\u5b9e\u6709\u597d\u4e9b\u4eba\u559d\u9ad8\u4e86\u8db4\u684c\u5b50\u5e95\u4e0b\u53bb\u4e86\uff0c\u76f8\u6276\u6301\u7740\u4e58\u8f66\u79bb\u53bb\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u8dd1\u8fc7\u6765\uff0c\u770b\u4e86\u770b\u624b\u8868\uff0c\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u8054\u901a\u7684\u6768\u603b\u5e0c\u671b\u627e\u5730\u65b9\u518d\u805a\u4e00\u805a\uff1f\u201d</p><p>\u3000\u3000\u5bb4\u5e2d\u95f4\u676f\u6765\u76cf\u5f80\uff0c\u518d\u52a0\u4e0a\u4e0d\u65ad\u7684\u6709\u4eba\u8dd1\u8fdb\u6765\u656c\u9152\uff0c\u5b9e\u5728\u4e0d\u80fd\u7b97\u662f\u8c08\u8bdd\u7684\u597d\u573a\u5408\uff0c\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1b\u201c\u627e\u4e2a\u6e05\u51c0\u7684\u5730\u65b9\u5c31\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6e05\u51c0\u4e0d\u6e05\u51c0\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u5ba2\u968f\u4e3b\u4fbf\uff0c\u6211\u4eec\u5c31\u542c\u4e3b\u4eba\u7684\u5b89\u6392\u5427\u3002\u201d\u8096\u745e\u6c11\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u9648\u4fe1\u751f\u4ed6\u4eec\u5750\u8f66\u8ddf\u5728\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u7684\u8f66\u540e\uff0c\u5f80\u4ec0\u5239\u6d77\u65b9\u5411\u884c\u9a76\uff0c\u98ce\u96ea\u672a\u505c\uff0c\u5728\u5317\u4eac\u5e02\u533a\uff0c\u5728\u9ad8\u697c\u5927\u53a6\u7684\u5939\u88f9\u4e0b\uff0c\u5012\u4e0d\u89c9\u5f97\u98ce\u96ea\u6709\u591a\u51db\u6d0c\u3002</p><p>\u3000\u3000\u8f66\u961f\u5728\u4e00\u5bb6\u540d\u4e3a\u897f\u4eac\u71d5\u95e8\u591c\u603b\u4f1a\u7684\u4f1a\u6240\u524d\u505c\u4e0b\uff0c\u505c\u8f66\u573a\u91cc\u505c\u7740\u90fd\u662f\u6b27\u7f8e\u8c6a\u534e\u8f7f\u8f66\uff0c\u51e0\u4e4e\u770b\u5230\u65e5\u97e9\u4ea7\u7684\u8f66\uff0c\u8fd9\u5bb6\u4f1a\u6240\uff0c\u5f20\u606a\u4e5f\u542c\u8bf4\u8fc7\uff0c\u53ea\u662f\u6ca1\u6709\u673a\u4f1a\u8fc7\u6765\u957f\u89c1\u8bc6\uff0c\u6ca1\u60f3\u5230\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u7684\u9ad8\u5c42\u5bf9\u8fd9\u91cc\u8f7b\u8f66\u719f\u8def\uff0c\u4e5f\u53ef\u80fd\u662f\u4e3a\u4e86\u62db\u5f85\u4ed6\u4eec\u8fd9\u4e9b\u8d35\u5bbe\u7279\u610f\u5b89\u6392\u5728\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u59da\u575a\u8ddf\u5218\u9e23\u950b\u5f00\u73a9\u7b11\u8bf4\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u5f00\u65e5\u672c\u8f66\u90fd\u4e0d\u597d\u610f\u601d\u8ddf\u522b\u4eba\u6253\u62db\u547c\uff0c\u5230\u91cc\u9762\u8981\u662f\u4e8c\u7ebf\u7684\u5c0f\u660e\u661f\u90fd\u4e0d\u597d\u610f\u601d\u633a\u8d77\u80f8\u819b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u59da\u575a\u7b11\u7740\u8bf4\uff1a\u201c\u518d\u7f57\u55e6\u8ba9\u4f60\u56de\u5bb6\u966a\u8001\u5a46\u53bb\uff01\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u4e0e\u6768\u84c9\u5e73\u5728\u524d\u9762\u4e0b\u4e86\u8f66\uff0c\u4e3b\u52a8\u8dd1\u8fc7\u6765\u9080\u5f20\u606a\u3001\u9648\u4fe1\u751f\u4e00\u8d77\u5f80\u4f1a\u6240\u91cc\u8d70\u53bb\u3002</p><p>\u3000\u3000\u4f1a\u6240\u5916\u8868\u770b\u4e0d\u51fa\u7279\u522b\u7684\u7cbe\u5f69\u6765\uff0c\u8d70\u8fdb\u5927\u5385\u91cc\u5374\u9c9c\u82b1\u7e41\u7c07\u3001\u706f\u5149\u7480\u74a8\uff0c\u5c3d\u663e\u8c6a\u534e\u3001\u5178\u96c5\u3001\u91d1\u78a7\u8f89\u714c\u3002\u5e73\u65f6\u8d70\u5728\u5317\u4eac\u7684\u8857\u5934\uff0c\u6216\u8bb8\u4f1a\u89c9\u5f97\u5317\u4eac\u7684\u5973\u6027\u8863\u7740\u3001\u88c5\u626e\u4e0d\u53ca\u5357\u65b9\u5973\u4eba\u8273\u4e3d\uff0c\u4ee5\u9ed1\u7070\u7d20\u8272\u4e3a\u4e3b\uff0c\u770b\u7740\u5927\u5385\u91cc\u7a7f\u68ad\u7684\u540d\u6d41\u4ed5\u5973\u4e00\u4e2a\u4e2a\u6253\u626e\u5165\u65f6\u3001\u6d53\u5986\u8273\u62b9\uff0c\u624d\u77e5\u9053\u5317\u4eac\u7684\u7f8e\u8272\u5176\u5b9e\u90fd\u85cf\u5728\u6df1\u5b85\u5927\u9662\u91cc\uff0c\u60a0\u626c\u7684\u97f3\u4e50\u5728\u5f25\u6f2b\uff0c\u9b13\u5f71\u8863\u9999\uff0c\u4f7f\u4eba\u98d8\u98d8\u7136\u7136\u3002</p><p>\u3000\u3000\u8054\u4fe1\u65b9\u9762\uff0c\u5c31\u8096\u745e\u6c11\u4e0e\u4ed6\u7684\u52a9\u624b\uff0c\u662f\u4e2a\u4e09\u5341\u591a\u5c81\u7684\u9752\u5e74\uff0c\u8ddf\u968f\u8096\u745e\u6c11\uff0c\u4e0e\u5f20\u606a\u4e5f\u63a5\u89e6\u8fc7\u51e0\u6b21\uff0c\u4e0d\u4f1a\u592a\u964c\u751f\uff0c\u70ed\u60c5\u7684\u5e2e\u7740\u8096\u745e\u6c11\u62db\u5f85\u5f20\u606a\u7b49\u4eba\u5750\u4e0b\u3002</p><p>\u3000\u3000\u8054\u901a\u65b9\u9762\uff0c\u9664\u4e86\u6768\u84c9\u5e73\u4e0e\u5317\u4eac\u5206\u516c\u53f8\u603b\u7ecf\u7406\u4e4b\u5916\uff0c\u8fd8\u6709\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u6f02\u4eae\u5973\u4eba\u8ddf\u7740\u8fdb\u6765\uff0c\u8d70\u8fdb\u5305\u53a2\u540e\uff0c\u5979\u8131\u4e0b\u5927\u8863\uff0c\u91cc\u9762\u8fd8\u7a7f\u7740\u529e\u516c\u5ba4\u91cc\u7684\u6d45\u5496\u5561\u8272\u5957\u88c5\uff0c\u7b52\u88d9\u4e0b\u9732\u51fa\u7a7f\u7740\u8089\u8272\u4e1d\u889c\u7684\u7ea4\u7ec6\u5c0f\u817f\uff0c\u5012\u4e0d\u6bd4\u5927\u5385\u91cc\u7a7f\u68ad\u7684\u90a3\u4e9b\u6253\u626e\u5165\u65f6\u7684\u5973\u4eba\u59ff\u8272\u7a0d\u5dee\u3002</p><p>\u3000\u3000\u53c2\u52a0\u5bb4\u5e2d\u65f6\uff0c\u5f20\u606a\u8fde\u5317\u4eac\u5206\u516c\u53f8\u603b\u7ecf\u7406\u7684\u540d\u5b57\u90fd\u6ca1\u6709\u542c\u6e05\u695a\uff0c\u4e5f\u6ca1\u6709\u89c1\u5230\u8fd9\u4e2a\u5973\u4eba\u51fa\u573a\uff0c\u79bb\u5f00\u9152\u5e97\u65f6\u4e5f\u6ca1\u6709\u770b\u5230\u8fd9\u4e2a\u5973\u4eba\u4e0a\u8f66\uff0c\u8fd9\u65f6\u5019\u89c1\u5979\u5728\u90a3\u91cc\u5f20\u7f57\uff0c\u8eab\u4e0a\u4e5f\u6ca1\u6709\u4ec0\u4e48\u98ce\u5c18\u5473\uff0c\u4e5f\u4e0d\u77e5\u9053\u5979\u662f\u4ec0\u4e48\u8eab\u4efd\uff0c\u770b\u8096\u745e\u6c11\u4ed6\u4eec\u4f3c\u4e4e\u8ddf\u8fd9\u4e2a\u5973\u4eba\u90fd\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u201c\u5f20\u5148\u751f\u559c\u6b22\u559d\u4ec0\u4e48\u9152\uff1f\u201d\u90a3\u4e2a\u5973\u4eba\u62ff\u7740\u9152\u6c34\u5355\u5148\u8dd1\u5230\u5f20\u606a\u8eab\u8fb9\u70ed\u60c5\u800c\u4eb2\u5207\u5148\u95ee\u5f20\u606a\u7684\u610f\u89c1\uff0c\u5979\u5fae\u97a0\u7740\u8eab\u5b50\uff0c\u5957\u88c5\u5185\u7684\u6d45\u84dd\u8272\u6761\u886c\u886b\u5fae\u8361\u4e0b\u6765\uff0c\u53ea\u89e3\u5f00\u4e00\u7c92\u6263\u5b50\uff0c\u9732\u51fa\u6ed1\u817b\u7ec6\u767d\u7684\u8116\u5b50\uff0c\u6709\u6de1\u6de1\u597d\u95fb\u7684\u5e7d\u9999\u4f20\u5230\u9f3b\u7aef\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u9152\u6c34\u5355\u63a5\u8fc7\u6765\uff0c\u4e22\u5230\u6df1\u68d5\u8272\u7684\u73bb\u7483\u684c\u4e0a\uff0c\u4fa7\u8fc7\u5934\u8ddf\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u8bf4\u9053\uff1a\u201c\u652f\u6301\u56fd\u8d27\uff0c\u8981\u4ee5\u8eab\u4f5c\u5219\uff0c\u6709\u5f20\u88d5\u559d\u5f20\u88d5\uff0c\u6ca1\u5f20\u88d5\u559d\u738b\u671d\u4e5f\u884c\uff0c\u996e\u6599\u6765\u5065\u529b\u5b9d\uff0c\u6ca1\u5065\u529b\u5b9d\uff0c\u6765\u5a03\u54c8\u54c8\u4e5f\u884c\uff0c\u6768\u603b\u4e0e\u8096\u603b\u4ee5\u4e3a\u5462\uff1f\u201d</p><p>\u3000\u3000\u5728\u8fd9\u91cc\uff0c\u8981\u662f\u7545\u5f00\u6000\u559d\u6d0b\u9152\uff0c\u4e00\u591c\u6d88\u8d39\u8d85\u8fc7\u767e\u4e07\u4e0d\u662f\u4ec0\u4e48\u7a00\u5947\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u81ea\u5df1\u638f\u94b1\uff0c\u4e0d\u4f1a\u89c9\u5f97\u6709\u4ec0\u4e48\uff0c\u53ea\u662f\u4eca\u591c\u4e0d\u80fd\u62d2\u7edd\u8054\u901a\u505a\u4e1c\uff0c\u592a\u5962\u4f88\u4e86\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u66ff\u522b\u4eba\u611f\u5230\u4e0d\u5b89\u3002</p><p>\u3000\u3000\u90a3\u5973\u4eba\u5fae\u5fae\u4e00\u6123\uff0c\u53ea\u5f53\u5f20\u606a\u5f00\u73a9\u7b11\uff0c\u5979\u90fd\u4e0d\u77e5\u9053\u8fd9\u91cc\u6709\u6ca1\u6709\u56fd\u4ea7\u7ea2\u9152\uff0c\u66f4\u4e0d\u8981\u8bf4\u5065\u529b\u5b9d\u3001\u5a03\u54c8\u54c8\u996e\u6599\u4e86\uff0c\u53ea\u662f\u5f20\u606a\u8fd9\u8bdd\u662f\u5bf9\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u8bf4\u7684\uff0c\u5979\u5c34\u5c2c\u7684\u6324\u7740\u7b11\u5bb9\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u8bdd\u624d\u597d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u90fd\u54c8\u54c8\u5927\u7b11\uff0c\u6768\u84c9\u5e73\u5bf9\u90a3\u4e2a\u5973\u4eba\u8bf4\u9053\uff1a\u201c\u5c0f\u6210\uff0c\u4f60\u5c31\u542c\u5f20\u5148\u751f\u7684\u8bdd\u8fd9\u4e48\u5b89\u6392\u597d\u4e86\u2026\u2026\u201d\u4e5f\u77e5\u9053\u5f20\u606a\u4e0d\u662f\u77eb\u60c5\uff0c\u60f3\u6765\u4ee5\u5f20\u606a\u7684\u8eab\u5bb6\uff0c\u4ec0\u4e48\u5962\u4f88\u6ca1\u6709\u4eab\u53d7\u8fc7\uff0c\u4f55\u5fc5\u8ddf\u4ed6\u4eec\u865a\u5047\u5ba2\u5957\uff1f</p><p>\u3000\u3000\u90a3\u4e2a\u5973\u4eba\u8d70\u51fa\u53bb\u5b89\u6392\uff0c\u6768\u84c9\u5e73\u624b\u6478\u7740\u4e0b\u5df4\uff0c\u8ddf\u5f20\u606a\u3001\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u5bf9\u5916\u9762\uff0c\u6211\u4eec\u53ea\u62a5\u559c\u4e0d\u62a5\u5fe7\uff0c\u5728\u4f60\u4eec\u9762\u524d\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u8bf4\u7c89\u9970\u592a\u5e73\u7684\u8bdd\uff0c\u8bf4\u4e86\u4e5f\u6ca1\u6709\u7528\uff0c\u7792\u4e0d\u8fc7\u4f60\u4eec\u7684\u6cd5\u773c\uff0c\u8fd8\u4e0d\u662f\u5766\u8bda\u4ee5\u5f85\uff0c\u624d\u80fd\u901a\u529b\u5408\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6768\u603b\u8bf7\u8bf4\u2026\u2026\u201d\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u6768\u84c9\u5e73\u4f1a\u5f00\u95e8\u89c1\u5c71\u7684\u9648\u4ee5\u5b9e\u60c5\uff0c\u504f\u5fae\u4fa7\u8fc7\u8eab\u5b50\u6765\u542c\u4ed6\u7ec6\u8bf4\uff0c\u505a\u51fa\u5f88\u8ba4\u771f\u542c\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u201c\u8054\u901a\u8ba1\u5212\u516d\u6708\u4efd\u4e4b\u524d\u5728\u7ebd\u7ea6\u3001\u9999\u6e2f\u540c\u65f6\u4e0a\u5e02\uff0c\u4e0d\u4f1a\u53bb\u4f2a\u9020\u6570\u636e\uff0c\u4f46\u4e5f\u5e0c\u671b\u6570\u636e\u80fd\u66f4\u597d\u770b\u4e00\u4e9b\uff0c\u7279\u522b\u662f\u51b3\u5b9a\u8054\u901a\u516c\u53f8\u672a\u6765\u6210\u957f\u6027\u7684\u65b0\u7528\u6237\u589e\u6da8\u6570\u91cf\u5c24\u5176\u53d7\u5230\u6295\u8d44\u8005\u7684\u91cd\u89c6\u2026\u2026\u201d\u6768\u84c9\u5e73\u5766\u8bda\u7684\u89e3\u91ca\u6b64\u65f6\u4ed3\u4fc3\u542f\u52a8\u5b9a\u5236\u7b56\u7565\u3001\u63a8\u51fa\u5145\u503c\u9001\u624b\u673a\u65b0\u5e74\u793c\u5305\u6d3b\u52a8\u7684\u6839\u672c\u539f\u56e0\uff0c\u4ed6\u4e5f\u77e5\u9053\u8fd9\u79cd\u4e1c\u897f\u6839\u672c\u7792\u4e0d\u8fc7\u9526\u6e56\uff0c\u9526\u6e56\u8981\u4e0d\u662f\u5bf9\u6574\u4e2a\u4ea7\u4e1a\u7814\u7a76\u5f97\u975e\u5e38\u7684\u900f\u5f7b\uff0c\u4e5f\u7edd\u4e0d\u53ef\u80fd\u505a\u5230\u4eca\u5929\u7684\u5730\u6b65\u3002</p><p>\u3000\u3000\u201c\u4ece\u8054\u901a\u5317\u4eac\u5206\u53f8\u4eca\u5929\u7684\u5b9e\u7ee9\u6765\uff0c\u6548\u679c\u975e\u5e38\u597d\u554a\uff01\u201d\u9648\u4fe1\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u6768\u84c9\u5e73\u8981\u8868\u73b0\u8d64\u8bda\uff0c\u5f20\u606a\u4e5f\u5f88\u914d\u5408\u7684\u505a\u51fa\u5f88\u52a8\u5bb9\u3001\u8ba4\u771f\u606d\u542c\u7684\u6837\u5b50\uff0c\u4ed6\u5c31\u5728\u90a3\u91cc\u6253\u8d77\u592a\u6781\u62f3\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u4e0e\u9526\u6e56\u63a5\u89e6\u6700\u591a\uff0c\u77e5\u9053\u6768\u84c9\u5e73\u8fd9\u5957\u4ee5\u60c5\u52a8\u4eba\u7684\u628a\u620f\u5728\u5f20\u606a\u3001\u9648\u4fe1\u751f\u9762\u524d\u6ca1\u6709\u7528\u5904\uff1b\u8981\u73a9\u4ee5\u60c5\u52a8\u4eba\uff0c\u4e5f\u4e0d\u8be5\u6765\u8fd9\u79cd\u5962\u534e\u573a\u5408\uff0c\u6765\u8fd9\u79cd\u5962\u534e\u573a\u5408\u4e5f\u53ea\u662f\u63d0\u9192\u9526\u6e56\u5927\u5bb6\u7684\u5408\u4f5c\u4e00\u5207\u4ee5\u5229\u76ca\u4e3a\u524d\u63d0\u3002\u5176\u5b9e\u4ee5\u5229\u76ca\u4e3a\u524d\u63d0\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u6bd5\u7adf\u591a\u8d62\u5c40\u9762\u5e76\u4e0d\u662f\u6ca1\u6709\u53ef\u80fd\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u7684\u60c5\u51b5\u8fd8\u7b97\u4ee4\u4eba\u6ee1\u610f\uff0c\u4e0d\u8fc7\u73b0\u5b9e\u7684\u56f0\u96be\u5c31\u662f\u8054\u4fe1\u6bcf\u4e2a\u6708\u53ea\u80fd\u4ece\u9526\u6e56\u83b7\u5f97\u4e24\u4e07\u5957ESS\u57fa\u9891\u677f\uff0c\u5c31\u7b97\u8054\u4fe1\u5168\u529b\u7684\u652f\u6301\u8054\u901a\u5728\u5317\u4eac\u5730\u533a\u53d1\u5c55\u65b0\u7528\u6237\uff0c\u4e5f\u8fdc\u8fdc\u4e0d\u591f\u554a\uff01\u201d</p><p>\u3000\u3000\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u5728\u5341\u4e00\u6708\u4efd\u63a8\u51faESS\u624b\u673a\u4e4b\u65f6\u4e3a\u4f55\u4e0d\u8fdb\u884c\u4efb\u4f55\u5ba3\u4f20\uff1f\u5012\u4e0d\u662f\u5bf9\u8be5\u6b3e\u624b\u673a\u6ca1\u6709\u4ec0\u4e48\u4fe1\u5fc3\uff0c\u5b9e\u5728\u662f\u4ea7\u80fd\u7684\u74f6\u9888\u4e00\u65f6\u6253\u4e0d\u5f00\u3002\u4f4e\u7aef\u5e02\u573a\u6c38\u8fdc\u90fd\u662f\u7edd\u5bf9\u6570\u91cf\u6700\u4e3a\u5e9e\u5927\u7684\u5e02\u573a\uff0c\u6bcf\u6708\u56db\u4e07\u5957\u7684\u4ea7\u91cf\u5982\u4f55\u80fd\u6ee1\u8db3\uff1f\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u6839\u672c\u5c31\u4e0d\u6562\u8fdb\u884c\u5ba3\u4f20\uff0c\u8425\u9500\u7f51\u7edc\u7684\u94fa\u8d27\u751a\u81f3\u8fd8\u662f\u6709\u9009\u62e9\u7684\u5728\u8fdb\u884c\u3002</p><p>\u3000\u3000\u5c31\u62ff\u5317\u4eac\u5730\u533a\u6765\u8bf4\uff0c\u4eca\u5929\u8fd8\u4e0d\u662f\u53cc\u4f11\u65e5\u6216\u8005\u8282\u5047\u65e5\uff0c\u8054\u901a\u5b9a\u5236\u624b\u673a\u5c31\u5356\u6389\u4e00\u5343\u53ea\uff0c\u65b0\u5e74\u5145\u503c\u9001\u624b\u673a\u7684\u6d3b\u52a8\u8fdb\u884c\u4e0b\u53bb\uff0c\u5317\u4eac\u5730\u533a\u6625\u8282\u524d\u540e\u5c11\u8bf4\u4e5f\u8981\u51c6\u5907\u4e94\u516d\u4e07\u53ea\u624b\u673a\u624d\u7b97\u6bd4\u8f83\u5145\u5206\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u5f20\u606a\u89c9\u5f97\u8054\u4fe1\u4e0e\u8054\u901a\u8bd5\u6c34\u5b9a\u5236\u4e1a\u52a1\u6709\u4e9b\u4ed3\u4fc3\u7684\u4e00\u4e2a\u539f\u56e0\uff0c\u8054\u901a\u7684\u5b9a\u5236\u624b\u673a\u5907\u8d27\u91cf\u6839\u672c\u5c31\u4e0d\u591f\u652f\u6491\u8fd9\u4e48\u91cd\u8981\u7684\u63a8\u5e7f\u6d3b\u52a8\uff0c\u5907\u8d27\u8ddf\u4e0d\u4e0a\uff0c\u6d3b\u52a8\u8fdb\u884c\u70ed\u706b\u671d\u5929\u7684\u65f6\u5019\u5c31\u560e\u7136\u800c\u6b62\uff0c\u672a\u514d\u5c31\u663e\u5f97\u4e0d\u591f\u5b8c\u7f8e\u3002\u8fd9\u8fd8\u53ea\u662f\u5317\u4eac\u5730\u533a\uff0c\u8054\u901a\u8981\u662f\u5728\u5176\u4ed6\u5730\u533a\u63a8\u5e7f\u8fd9\u6837\u7684\u6d3b\u52a8\uff0c\u5907\u8d27\u91cf\u66f4\u662f\u5de8\u5927\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u624b\u673a\u5382\u5546\u559c\u6b22\u8ddf\u8fd0\u8425\u5546\u5408\u4f5c\u7684\u539f\u56e0\uff0c\u51e0\u4e4e\u4e0d\u7528\u4ed8\u51fa\u591a\u5c11\u989d\u5916\u7684\u8425\u9500\u6210\u672c\uff0c\u4ea7\u54c1\u7684\u9500\u91cf\u5c31\u80fd\u8fc5\u901f\u7684\u63d0\u4e0a\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u6768\u84c9\u5e73\u4e0e\u8096\u745e\u6c11\u5bf9\u4eca\u5929\u7684\u5c40\u9762\u4e5f\u6ca1\u6709\u5145\u5206\u7684\u9884\u6599\uff0c\u4e0d\u7136\u5c31\u4e0d\u4f1a\u8fd9\u4e48\u4ed3\u4fc3\u4e86\u3002\u5728\u5bb4\u5e2d\u4e0a\u542c\u5230\u8054\u901a\u5317\u4eac\u5730\u533a\u7684\u5b9a\u5236\u624b\u673a\u7528\u6237\u4e00\u5929\u5c31\u8d85\u8fc7\u4e00\u5343\u4eba\u65f6\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u77e5\u9053\u4eca\u5929\u4f1a\u7ed9\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u7ea0\u7f20\u4f4f\u3002</p><p>\u3000\u3000\u65e2\u7136\u8096\u745e\u6c11\u63d0\u5230\u8fd9\u5173\u952e\u95ee\u9898\u4e0a\u6765\uff0c\u9648\u4fe1\u751f\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u770b\u4ed6\u4f1a\u4e0d\u4f1a\u88c5\u7cca\u6d82\u5c06\u4ea7\u91cf\u7684\u95ee\u9898\u63a8\u5230\u81ea\u5df1\u5934\u4e0a\u6765\uff0c\u5374\u4e0d\u6599\u5f20\u606a\u6478\u4e86\u6478\u540e\u8111\u52fa\uff0c\u5482\u7740\u5634\u8bf4\uff1a\u201c\u7231\u8fbe\u5728\u6d77\u5dde\u7684\u65b0\u5382\u6700\u65e9\u4e5f\u8981\u8fd9\u4e2a\u6708\u4e0b\u65ec\u624d\u80fd\u542f\u52a8\uff0c\u4e4b\u524d\u8fd8\u8ba1\u5212\u7b49\u6625\u8282\u4e4b\u540e\u624d\u8003\u8651\u6295\u4ea7\u7684\uff0c\u65b0\u5382\u6295\u4ea7\u4e4b\u524d\u7684ESS\u57fa\u9891\u677f\u90fd\u7ed9\u8054\u4fe1\u4e0e\u79d1\u738b\u9ad8\u79d1\u74dc\u5206\u6389\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u610f\u601d\u662f\u8bf4\uff0c\u8054\u4fe1\u4e0e\u8054\u901a\u7684\u56f0\u5883\u4ed6\u4e86\u89e3\u5230\u4e86\uff0c\u4e5f\u8003\u8651\u5c06\u6625\u8282\u4e4b\u540e\u624d\u8ba1\u5212\u6295\u4ea7\u7684\u65b0\u5382\u63d0\u524d\u5230\u8fd9\u4e2a\u6708\u6708\u5e95\u542f\u52a8\uff0c\u4f46\u662f\u4e5f\u53ea\u80fd\u505a\u5230\u8fd9\u4e00\u6b65\u4e86\u2026\u2026\u4f3c\u4e4e\u4e5f\u4e0d\u80fd\u89e3\u51b3\u8054\u4fe1\u3001\u8054\u901a\u773c\u4e0b\u7f3a\u8d27\u7684\u56f0\u5883\uff0c\u9648\u4fe1\u751f\u4f30\u6478\u7740\u8054\u901a\u5c31\u7ed9\u5317\u4eac\u5730\u533a\u5907\u4e86\u4e00\u4e07\u4ef6\u8d27\uff0c\u9876\u591a\u80fd\u6491\u4e0a\u5341\u5929\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u77e5\u9053\u4ee5\u5143\u5668\u4ef6\u90e8\u95e8\u6708\u7ec4\u4ef6\u8fd1\u767e\u4e07\u4ef6\u7684\u4ea7\u80fd\uff0c\u534f\u8c03\u6324\u51fa\u56db\u4e94\u4e07\u5957\u57fa\u9891\u677f\u5e94\u6025\u4e00\u4e0b\uff0c\u4e0d\u4f1a\u6709\u7279\u522b\u5927\u7684\u95ee\u9898\uff0c\u4f46\u662f\u592a\u8f7b\u6613\u7684\u5c31\u6ee1\u8db3\u8054\u4fe1\u4e0e\u8054\u901a\u7684\u8981\u6c42\uff0c\u5e76\u4e0d\u7b26\u5408\u9526\u6e56\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u4e86\u91d1\u53e3\uff0c\u8096\u745e\u6c11\u5c31\u4e0d\u5962\u671b\u5230\u9648\u4fe1\u751f\u90a3\u91cc\u8fd8\u6709\u56de\u65cb\u7684\u4f59\u5730\u3002\u7f3a\u8d27\u4e00\u4e8b\uff0c\u9664\u4e86\u4ece\u9526\u6e56\u60f3\u529e\u6cd5\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u6761\u9014\u5f84\u53ef\u8d70\uff0c\u90a3\u5c31\u662f\u4ece\u79d1\u738b\u9ad8\u79d1\u7d27\u6025\u8c03\u4e00\u6279\u540c\u89c4\u683c\u7684\u624b\u673a\u6765\uff0c\u5f20\u606a\u8bdd\u91cc\u7684\u610f\u601d\u4e5f\u662f\u8fd9\u4e48\u6697\u793a\u7684\uff0c\u4f46\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u76f8\u5f53\u4e8e\u4e3b\u52a8\u7684\u5c06\u79d1\u738b\u9ad8\u79d1\u4e00\u8d77\u62c9\u8fdb\u6765\u53c2\u4e0e\u8054\u901a\u7684\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u800c\u4e14\u8fd9\u65f6\u5019\u662f\u8054\u901a\u4e3b\u52a8\u627e\u4e0a\u95e8\u53bb\u6c42\u79d1\u738b\u9ad8\u79d1\uff0c\u79d1\u738b\u9ad8\u79d1\u751a\u81f3\u80fd\u83b7\u5f97\u6bd4\u8054\u4fe1\u66f4\u4f18\u60e0\u7684\u6761\u4ef6\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u591a\u5c11\u6709\u4e9b\u4e0d\u7518\u5fc3\u3002</p><p>\u3000\u3000\u5728\u540c\u7b49\u6761\u4ef6\u4e0b\uff0c\u8054\u901a\u5728\u9009\u62e9\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u5408\u4f5c\u5bf9\u8c61\u65f6\uff0c\u88ab\u8981\u6c42\u5411\u56fd\u6709\u4f01\u4e1a\u503e\u659c\uff0c\u6240\u4ee5\u6700\u7ec8\u9009\u62e9\u4e0e\u8054\u4fe1\u5408\u4f5c\u3002\u6768\u84c9\u5e73\u5fc3\u91cc\u5f88\u6e05\u695a\uff0c\u5f88\u591a\u4eba\u5fc3\u91cc\u90fd\u5f88\u6e05\u695a\uff0c\u9526\u6e56\u624d\u662f\u8054\u901a\u6700\u4f73\u7684\u5408\u4f5c\u5bf9\u8c61\uff0c\u8054\u4fe1ESS\u7684\u624b\u673a\u6280\u672f\u6839\u672c\u5c31\u662f\u6765\u6e90\u4e8e\u9526\u6e56\uff0c\u76f4\u63a5\u4e0e\u9526\u6e56\u5408\u4f5c\uff0c\u4f1a\u6709\u66f4\u591a\u7684\u6210\u672c\u4f18\u52bf\u3002\u53ea\u662f\u5f88\u591a\u4eba\uff0c\u751a\u81f3\u5305\u62ec\u8054\u901a\u5185\u90e8\u4e5f\u6709\u8bb8\u591a\u9ad8\u5c42\u90fd\u4e0d\u5e0c\u671b\u770b\u5230\u9526\u6e56\u53d8\u5f97\u66f4\u5f3a\u52bf\uff0c\u6240\u4ee5\u4e00\u5f00\u59cb\u5c31\u5c06\u9526\u6e56\u6392\u9664\u5728\u5916\u3002\u4f46\u662f\u8054\u4fe1\u7684\u4ea7\u80fd\u4e0d\u80fd\u6ee1\u8db3\u9700\u6c42\u65f6\uff0c\u8054\u901a\u4e5f\u4e0d\u80fd\u5c31\u5728\u4e00\u68f5\u6811\u4e0a\u540a\u6b7b\uff0c\u6768\u84c9\u5e73\u5fc3\u91cc\u5176\u5b9e\u66f4\u5e0c\u671b\u5f20\u606a\u3001\u9648\u4fe1\u751f\u8fd9\u65f6\u5019\u501f\u673a\u63d0\u51fa\u9526\u6e56\u76f4\u63a5\u53c2\u4e0e\u8054\u901a\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u7684\u8981\u6c42\u3002</p><p>\u3000\u3000\u91c7\u7528ESS\u57fa\u9891\u677f\u7684\u4e00\u6b3e\u624b\u673a\uff0c\u8054\u4fe1\u5c06\u5236\u9020\u6210\u672c\u63a7\u5236\u5728\u6bcf\u4ef6\u516b\u767e\u5143\u5de6\u53f3\uff0c\u8054\u901a\u4ece\u8054\u4fe1\u7684\u91c7\u8d2d\u4ef7\u4e3a\u6bcf\u4ef6\u4e5d\u767e\u516b\u5341\u5143\u3002\u540c\u7b49\u89c4\u683c\u7684\u624b\u673a\uff0c\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u3001\u7231\u7acb\u4fe1\u7684\u5236\u9020\u6210\u672c\u4e0d\u4f1a\u4f4e\u4e8e\u4e00\u5343\u4e24\u767e\u5143\uff0c\u7ed9\u8054\u901a\u7684\u5b9a\u5236\u4ef7\u4e0d\u4f1a\u4f4e\u4e8e\u4e00\u5343\u56db\u767e\u5143\uff0c\u4e0e\u8054\u8baf\u3001\u4e1c\u5174\u7b49\u5176\u4ed6\u56fd\u4ea7\u624b\u673a\u4ea7\u5546\u5408\u4f5c\u7684\u91c7\u8d2d\u6210\u672c\u5c06\u66f4\u9ad8\u3002\u8d85\u8fc740\uff05\u7684\u91c7\u8d2d\u6210\u672c\u5dee\u5f02\uff0c\u8054\u901a\u4e5f\u552f\u6709\u9009\u62e9\u8054\u4fe1\u4f5c\u4e3a\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u7684\u6218\u7565\u5408\u4f5c\u4f19\u4f34\uff0c\u4e5f\u5c31\u662f\u8bf4\u8054\u901a\u4e3a\u5145\u503c\u9001\u8bdd\u8d39\u6d3b\u52a8\u5b9e\u9645\u7ed9\u6bcf\u4e2a\u65b0\u5f00\u6237\u7528\u6237\u8865\u8d34\u4e5d\u767e\u516b\u5341\u5143\u3002</p><p>\u3000\u3000\u8981\u662f\u80fd\u4e0e\u9526\u6e56\u5408\u4f5c\uff0c\u6768\u84c9\u4f30\u7b97\u7740\u8054\u901a\u53ef\u4ee5\u4e58\u673a\u5c06\u5b9a\u5236\u624b\u673a\u7684\u91c7\u8d2d\u6210\u672c\u538b\u7f29\u5230\u4e5d\u767e\u5143\u4ee5\u5185\u3002</p><p>\u3000\u3000\u4e0d\u8981\u5c11\u770b\u8fd9\u5c0f\u5c0f\u7684\u516b\u5341\u5143\u5dee\u8ddd\uff0c\u8054\u901a\u6b64\u65f6\u7684\u51c0\u5229\u6da6\u7387\u751a\u81f3\u4e0d\u8db36\uff05\uff0c\u8981\u662f\u5927\u4e3e\u5728\u5168\u56fd\u5730\u533a\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u6765\u53d1\u5c55\u65b0\u7528\u6237\uff0c\u6bcf\u4ef6\u624b\u673a\u7684\u91c7\u8d2d\u6210\u672c\u538b\u7f29\u516b\u5341\u5143\uff0c\u9884\u8ba1\u80fd\u8ba9\u8054\u901a\u7684\u51c0\u5229\u6da6\u7387\u63d0\u9ad8\u4e24\u4e09\u4e2a\u70b9\uff0c\u8fd9\u5c06\u662f\u4e00\u4e2a\u76f8\u5f53\u4e86\u4e0d\u8d77\u7684\u6210\u5c31\u3002</p><p>\u3000\u3000\u8054\u901a\u5c31\u8981\u5728\u7ebd\u7ea6\u3001\u9999\u6e2f\u540c\u65f6\u4e0a\u5e02\uff0c\u4e1a\u7ee9\u5c06\u662f\u8054\u901a\u5c06\u6765\u9996\u5148\u8003\u8651\u7684\u56e0\u7d20\uff0c\u4e2a\u4eba\u7684\u611f\u53d7\u8981\u653e\u5728\u540e\u9762\uff0c\u6768\u84c9\u5e73\u5374\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u5e76\u6ca1\u6709\u6025\u7740\u8df3\u51fa\u6765\u6316\u8054\u4fe1\u5899\u89d2\u7684\u610f\u601d\uff0c\u4e5f\u6ca1\u6709\u65e0\u9650\u652f\u6301\u8054\u4fe1\u7684\u610f\u601d\uff0c\u5f20\u606a\u8bdd\u91cc\u7684\u610f\u601d\u662f\u6697\u793a\u8054\u901a\u53ef\u4ee5\u4ece\u79d1\u738b\u9ad8\u79d1\u7d27\u6025\u8c03\u8d27\u3002</p><p>\u3000\u3000\u4f8d\u5e94\u751f\u5c06\u9152\u6c34\u996e\u6599\u679c\u76d8\u9001\u8fdb\u6765\uff0c\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u4e5f\u8ddf\u4e86\u8fdb\u6765\u3002\u8fd9\u91cc\u8bf4\u8bdd\u4e0d\u5e0c\u671b\u4f8d\u5e94\u751f\u542c\u5230\u8033\u6735\u91cc\u53bb\uff0c\u6768\u84c9\u5e73\u6325\u4e86\u6325\u624b\uff0c\u8ba9\u4f8d\u5e94\u751f\u51fa\u53bb\uff0c\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u4e0e\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u603b\u7ecf\u7406\u4eb2\u81ea\u52a8\u624b\u5e2e\u5927\u5bb6\u5012\u9152\u6c34\u3002</p><p>\u3000\u3000\u5f20\u606a\u63a5\u8fc7\u9152\u676f\uff0c\u770b\u7740\u9ad8\u811a\u9152\u74f6\u91cc\u7684\u534a\u676f\u5e72\u7ea2\uff0c\u62bf\u4e86\u4e00\u53e3\uff0c\u89c9\u5f97\u6709\u4e9b\u6da9\uff0c\u62ff\u8d77\u996e\u6599\u74f6\u8981\u5f80\u91cc\u9762\u63ba\u96ea\u78a7\uff0c\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u7b11\u7740\u63d0\u9192\u4ed6\u8bf4\u9053\uff1a\u201c\u5e72\u7ea2\u672c\u6765\u5c31\u662f\u8131\u4e86\u7cd6\u7684\u8461\u8404\u9152\uff0c\u4f60\u5f80\u91cc\u63ba\u96ea\u78a7\uff0c\u5c31\u53c8\u6210\u4e86\u8461\u8404\u9152\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u8461\u8404\u9152\u5417\uff1f\u6211\u89c9\u5f97\u901a\u5316\u8461\u8404\u9152\u5c31\u86ee\u597d\u559d\u7684\u3002\u201d\u5f20\u606a\u54a7\u7740\u5634\u8bf4\u9053\u3002</p><p>\u3000\u3000\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u53d1\u7a98\u7684\u7ad9\u5728\u90a3\u91cc\uff0c\u4fcf\u8138\u7c89\u7ea2\uff0c\u5979\u60f3\u4e0d\u7740\u610f\u7684\u5c06\u5f20\u606a\u4e00\u519b\uff0c\u4f1a\u8ba9\u5f20\u606a\u6709\u4e9b\u6536\u655b\uff0c\u81f3\u5c11\u5979\u9047\u5230\u7684\u6bcf\u4e2a\u7537\u4eba\u90fd\u4f1a\u5343\u65b9\u767e\u8ba1\u7684\u60f3\u5728\u5979\u9762\u524d\u6446\u51fa\u4e00\u4ed8\u9ad8\u6df1\u83ab\u6d4b\u3001\u65e0\u6240\u4e0d\u77e5\u7684\u6a21\u6837\uff0c\u7edd\u4e0d\u4f1a\u60f3\u5728\u5979\u9762\u524d\u7ed9\u5979\u6216\u7ed9\u522b\u4eba\u8f7b\u89c6\uff0c\u5979\u54ea\u91cc\u60f3\u5230\u5f20\u606a\u4f1a\u5982\u6b64\u7684\u6d51\u4e0d\u5728\u610f\uff0c\u4e0d\u77e5\u9053\u662f\u4e0d\u662f\u771f\u8981\u9000\u51fa\u53bb\u5e2e\u5f20\u606a\u627e\u90a3\u79cd\u4f4e\u6863\u6b21\u7684\u8461\u8404\u9152\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u7406\u4f1a\u8fd9\u4e2a\u6f02\u4eae\u5973\u4eba\u53d1\u7a98\u7684\u795e\u8272\uff0c\u81ea\u987e\u81ea\u7684\u62ff\u8d77\u996e\u6599\u74f6\u5f80\u9ad8\u811a\u73bb\u7483\u676f\u91cc\u63ba\u96ea\u78a7\uff0c\u7b11\u7740\u8ddf\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u4ed6\u4eec\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u5962\u671b\u8fd9\u91cc\u80fd\u559d\u5230\u901a\u5316\u8461\u8404\u9152\u4e86\uff0c\u5b9e\u5728\u4e0d\u5fcd\u5fc3\u52b3\u70e6\u8fd9\u4f4d\u5c0f\u59d0\u7279\u610f\u8dd1\u5230\u5916\u9762\u53bb\u5e2e\u6211\u4e70\u9152\u53bb\uff0c\u6211\u8fd8\u662f\u81ea\u529b\u66f4\u751f\u7684\u597d\uff0c\u4f60\u4eec\u8981\u662f\u8840\u7cd6\u4e0d\u9ad8\uff0c\u5e72\u7ea2\u91cc\u63ba\u96ea\u78a7\uff0c\u53e3\u611f\u66f4\u597d\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u6768\u84c9\u5e73\u3001\u8096\u745e\u6c11\u90fd\u7b11\u563b\u563b\u7684\u63a5\u8fc7\u96ea\u78a7\u74f6\u5b50\u6765\u5f80\u5e72\u7ea2\u91cc\u63ba\u96ea\u78a7\uff0c\u5012\u662f\u9648\u4fe1\u751f\u4e60\u60ef\u5e72\u7ea2\u7684\u53e3\u611f\uff0c\u59da\u575a\u3001\u5218\u9e23\u950b\u4ed6\u4eec\u66f4\u4e0d\u4f1a\u51d1\u8fd9\u4e2a\u70ed\u95f9\uff0c\u4ed6\u4eec\u53ef\u4e0d\u4ee5\u4e3a\u80fd\u6709\u8d44\u683c\u8ddf\u7740\u4e00\u8d77\u8c03\u620f\u8fd9\u4e48\u4e2a\u7f8e\u4eba\u513f\u3002</p><p>\u3000\u3000\u5f20\u606a\u62bf\u4e86\u4e00\u53e3\u63ba\u96ea\u78a7\u7684\u5e72\u7ea2\uff0c\u5482\u4e86\u5482\u5634\uff0c\u53c8\u52a0\u4e86\u4e9b\u51b0\u5757\uff0c\u5927\u996e\u4e86\u4e00\u53e3\u7a0d\u89e3\u6e34\u610f\uff0c\u624d\u5c06\u9152\u676f\u653e\u5230\u73bb\u7483\u684c\uff0c\u4e0e\u6768\u84c9\u5e73\u8bf4\u9053\uff1a\u201c\u6768\u603b\u521a\u624d\u975e\u5e38\u771f\u8bda\u7684\u8ddf\u6211\u8bf4\u4e86\u8054\u901a\u73b0\u9636\u6bb5\u7684\u4e00\u4e9b\u60c5\u51b5\uff0c\u8ba9\u6211\u65e0\u6cd5\u4e0d\u638f\u5fc3\u7a9d\u5b50\u7684\u8ddf\u6768\u603b\u60a8\u4ea4\u6362\u4e00\u4e9b\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u6768\u84c9\u5e73\u8981\u662f\u4fe1\u5f20\u606a\u4f1a\u8ddf\u4ed6\u638f\u5fc3\u7a9d\u5b50\u624d\u53eb\u89c1\u4e86\u9b3c\uff0c\u4e0d\u8fc7\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u975e\u5e38\u914d\u5408\u7684\u52a8\u5bb9\u7684\u503e\u8fc7\u8eab\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6b22\u8fce\u4e4b\u81f3\u554a\uff0c\u8fd9\u5e74\u5934\u53ef\u6ca1\u6709\u8c01\u6562\u5938\u6d77\u53e3\u8bf4\u6bd4\u4f60\u7684\u610f\u89c1\u66f4\u5177\u542b\u91d1\u91cf\u4e86\uff01\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u4e0d\u4f1a\u7279\u522b\u7684\u4e2d\u542c\uff0c\u201d\u5f20\u606a\u772f\u773c\u7b11\u4e86\u7b11\uff0c\u201c\u8054\u901a\u5317\u4eac\u5206\u516c\u53f8\u4eca\u5929\u7684\u4e1a\u7ee9\uff0c\u542c\u4e86\u662f\u5f88\u8ba9\u4eba\u9f13\u821e\uff0c\u4f46\u662f\u636e\u6211\u6240\u77e5\uff0c\u8054\u901aGSM\u7f51\u7edc\u5efa\u8bbe\u4e0e\u79fb\u52a8\u516c\u53f8\u76f8\u6bd4\uff0c\u5b8c\u5584\u7a0b\u5ea6\u4e0a\u5c1a\u6709\u4e0d\u8db3\uff0c\u5c31\u7b97\u5728\u5317\u4eac\u5730\u533a\uff0c\u7b97\u662f\u8054\u901aG\u7f51\u5efa\u8bbe\u6700\u5b8c\u5584\u7684\u4e00\u4e2a\u533a\uff0c\u8fd8\u6709\u4e00\u4e9b\u7684\u901a\u4fe1\u76f2\u533a\uff0c\u5e26\u5bbd\u7a84\u4e5f\u662f\u4e00\u4e2a\u95ee\u9898\uff0c\u8054\u901a\u6b64\u65f6\u7684\u7528\u6237\u7fa4\u89c4\u6a21\u6709\u9650\uff0c\u5e26\u5bbd\u7a84\u7684\u95ee\u9898\u8fd8\u6ca1\u6709\u5145\u5206\u66b4\u9732\u51fa\u6765\uff0c\u4e00\u65e6\u7528\u6237\u6570\u91cf\u6fc0\u589e\uff0c\u8054\u901a\u8981\u5982\u4f55\u5e94\u4ed8\u8fd9\u4e2a\u95ee\u9898\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u6768\u84c9\u5e73\u6ca1\u60f3\u5230\u5f20\u606a\u4e00\u4e0b\u5b50\u5c31\u5c16\u9510\u7684\u63ed\u5f00\u8054\u901a\u7684\u75db\u5904\uff0c\u8054\u901a\u751a\u81f3\u5728\u4e0a\u5e02\u878d\u8d44\u62a5\u544a\u91cc\u523b\u610f\u7684\u56de\u907f\u4e86\u8fd9\u4e00\u95ee\u9898\uff0c\u8054\u901a\u60f3\u63a9\u8033\u76d7\u94c3\uff0c\u4f46\u662f\u5e76\u4e0d\u610f\u5473\u7740\u522b\u4eba\u7684\u8033\u76ee\u5c31\u771f\u7684\u7ed9\u906e\u853d\u4f4f\u7684\uff0c\u9526\u6e56\u5bf9\u56fd\u5185\u7684\u901a\u8baf\u4ea7\u4e1a\u6709\u7740\u6700\u6df1\u523b\u7684\u8ba4\u8bc6\uff0c\u9526\u6e56\u80fd\u770b\u7a7f\u8054\u901a\u7684\u75db\u811a\uff0c\u6768\u84c9\u5e73\u4e0d\u4f1a\u6709\u4ec0\u4e48\u610f\u5916\uff0c\u53ea\u662f\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4f1a\u76f4\u63a5\u63d0\u51fa\u6765\uff0c\u4ed6\u7a0d\u7a0d\u72b9\u8c6b\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u7684\u786e\u662f\u6bd4\u8f83\u68d8\u624b\u7684\u4e00\u4e2a\u95ee\u9898\uff0c\u606a\u5c11\u65e2\u7136\u63d0\u51fa\u8fd9\u4e2a\u95ee\u9898\uff0c\u60f3\u5fc5\u5bf9\u8fd9\u4e2a\u95ee\u9898\u6709\u6240\u601d\u8003\uff0c\u6211\u6b63\u597d\u8981\u5411\u606a\u5c11\u4f60\u8bf7\u6559\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6768\u603b\u8ba4\u4e3a\u9526\u6e56\u5728ESS\u57fa\u9891\u677f\u7684\u5236\u9020\u6210\u672c\u4f18\u52bf\u80fd\u4fdd\u6301\u591a\u4e45\uff1f\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u6768\u84c9\u5e73\u542c\u4e86\u5f20\u606a\u8fd9\u8bdd\u624d\u771f\u7684\u5fae\u5fae\u52a8\u5bb9\uff0c\u63d0\u5230\u6210\u672c\u4f18\u52bf\uff0c\u8fd8\u6ca1\u6709\u4e00\u5bb6\u624b\u673a\u82af\u7247\u5382\u5546\u80fd\u8ddf\u9526\u6e56\u6bd4\u62fc\uff0c\u5373\u4f7f\u5176\u4ed6\u624b\u673a\u82af\u7247\u5382\u5546\u8c03\u6574\u4ea7\u54c1\u7b56\u7565\uff0c\u4e5f\u9700\u8981\u5c06\u5236\u9020\u57fa\u5730\u8fc1\u79fb\u5230\u4e2d\u56fd\u6216\u8005\u52b3\u52a8\u529b\u6210\u672c\u4e0e\u4e2d\u56fd\u76f8\u5f53\u7684\u5730\u533a\uff0c\u624d\u80fd\u5728\u5236\u9020\u6210\u672c\u4f18\u52bf\u4e0a\u4e0e\u9526\u6e56\u6bd4\u62fc\uff0c\u8bf4\u5230\u6280\u672f\uff0c\u8c01\u77e5\u9053\u9526\u6e56\u5728\u53d1\u5c55\u57fa\u5e26\u82af\u7247\u6280\u672f\u65f6\u80cc\u540e\u6709\u6ca1\u6709\u5f97\u5230\u5fb7\u4eea\u7684\u652f\u6301\u2026\u2026</p><p>\u3000\u3000\u201c\u82e5\u662f\u8054\u901a\u8ba4\u4e3a\u9526\u6e56\u5728ESS\u57fa\u9891\u677f\u4e0a\u7684\u6210\u672c\u4f18\u52bf\u80fd\u4fdd\u6301\u66f4\u4e45\uff0c\u9526\u6e56\u4e0d\u59a8\u4e0e\u8054\u901a\u505a\u4e00\u4e2a\u541b\u5b50\u534f\u5b9a\uff0c\u4e24\u5e74\u65f6\u95f4\u5185\uff0c\u9526\u6e56\u4e0d\u4f1a\u5728ESS\u57fa\u9891\u677f\u4e0a\u8ddf\u79fb\u52a8\u901a\u4fe1\u5c40\u5408\u4f5c\uff0c\u4ee5\u4fdd\u8bc1\u8054\u901a\u7684\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u4e0a\u80fd\u591f\u83b7\u5f97\u8db3\u591f\u7684\u6210\u672c\u4f18\u52bf\u2026\u2026\u201d\u5f20\u606a\u53c8\u62ff\u8d77\u684c\u4e0a\u7684\u9152\u676f\u559d\u4e86\u4e00\u53e3\u3002</p><p>\u3000\u3000\u6768\u84c9\u5e73\u7684\u773c\u775b\u90fd\u77aa\u5706\u4e86\uff0c\u5f20\u606a\u8fd9\u4e2a\u5c06\u79fb\u52a8\u6392\u65a5\u5728\u5916\u7684\u627f\u8bfa\u51e0\u4e4e\u8ba9\u4ed6\u7684\u80be\u4e0a\u817a\u7d20\u6c34\u5e73\u90fd\u9661\u5347\u4e86\u51e0\u5206\u3002</p><p>\u3000\u3000\u8054\u901a\u91c7\u53d6\u624b\u673a\u5b9a\u5236\u7b56\u7565\u4ee5\u5145\u503c\u9001\u624b\u673a\u7684\u6d3b\u52a8\u6765\u5438\u5f15\u65b0\u7528\u6237\uff0c\u80fd\u9884\u6599\u4f1a\u8d77\u5f88\u597d\u7684\u6548\u679c\uff0c\u4f46\u662f\u79fb\u52a8\u90a3\u8fb9\u4e5f\u4e0d\u4f1a\u72c2\u5984\u81ea\u5927\u7684\u4efb\u8054\u901a\u4ee5\u8fd9\u79cd\u8d85\u5e38\u89c4\u7684\u7b56\u7565\u4e89\u593a\u65b0\u7528\u6237\u3002</p><p>\u3000\u3000\u5bf9\u79fb\u52a8\u6765\u8bf4\uff0c\u6700\u597d\u7684\u5e94\u5bf9\u7b56\u7565\uff0c\u4e5f\u5c31\u662f\u6709\u6837\u5b66\u6837\u7684\u590d\u5236\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u79fb\u52a8\u4f9d\u65e7\u80fd\u51ed\u501f\u66f4\u5b8c\u5584\u7684\u901a\u4fe1\u7f51\u7edc\u6761\u4ef6\u5c06\u8054\u901a\u6b7b\u6b7b\u7684\u538b\u5236\u5728\u8eab\u4e0b\u3002</p><p>\u3000\u3000\u4e00\u65e6\u9526\u6e56\u51b3\u5fc3\u5c06\u79fb\u52a8\u6392\u65a5\u5728\u5916\uff0c\u79fb\u52a8\u8981\u53d1\u5c55\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u81f3\u5c11\u5728\u4f4e\u7aef\u5b9a\u5236\u624b\u673a\u7684\u91c7\u8d2d\u6210\u672c\u52bf\u5fc5\u8981\u6bd4\u8054\u901a\u9ad840\uff05\u751a\u81f3\u66f4\u9ad8\u3002\u8054\u901a\u5c31\u6709\u66f4\u591a\u7684\u6210\u672c\u4f18\u52bf\u53bb\u8ffd\u8d76\u79fb\u52a8\u3002</p><p>\u3000\u3000\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u5df2\u7ecf\u662f\u76f8\u5f53\u5bbd\u677e\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u63d0\u51fa\u7684\u8fd9\u4e2a\u627f\u8bfa\u8ba9\u6768\u84c9\u5e73\u5982\u4f55\u4e0d\u52a8\u5bb9\uff1f\u4f46\u662f\u6768\u84c9\u5e73\u4e5f\u77e5\u9053\u5929\u4e0b\u6ca1\u6709\u767d\u5403\u7684\u5348\u9910\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u767d\u767d\u7684\u5c06\u597d\u4e8b\u9001\u4e0a\u95e8\uff0c\u9526\u6e56\u771f\u7684\u8981\u5c06\u79fb\u52a8\u6392\u65a5\u5728\u5916\uff0c\u662f\u8981\u8ddf\u79fb\u52a8\u7ed3\u4ec7\u7684\uff0c\u6240\u627f\u62c5\u7684\u98ce\u9669\u4e0d\u5c11\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u9700\u8981\u8054\u901a\u627f\u8bfa\u4ec0\u4e48\uff1f\u201d\u6768\u84c9\u5e73\u6309\u5948\u4f4f\u6fc0\u52a8\u7684\u5fc3\u60c5\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u9996\u5148\u8fd9\u4e2a\u5408\u4f5c\u662f\u8981\u53cc\u8d62\u7684\uff0c\u81f3\u5c11\u4e0d\u80fd\u8ba9\u79fb\u52a8\u901a\u4fe1\u5c40\u7279\u522b\u5f3a\u70c8\u7684\u611f\u89c9\u5230\u9526\u6e56\u4e0e\u8054\u901a\u7684\u5408\u4f5c\u662f\u6545\u610f\u9488\u5bf9\u4ed6\u4eec\uff0c\u9526\u6e56\u4e5f\u613f\u610f\u914d\u5408\u8054\u4fe1\u7ed9\u8054\u901a\u4e13\u95e8\u5f00\u53d1\u66f4\u591a\u529f\u80fd\u4e30\u5bcc\u3001\u6027\u80fd\u5353\u8d8a\u7684\u624b\u673a\u4ea7\u54c1\u51fa\u6765\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u597d\u50cf\u662f\u5728\u8bf4\u4e00\u4ef6\u8f7b\u63cf\u6de1\u5199\u7684\u4e8b\u60c5\uff0c\u201c\u53e6\u4e00\u65b9\u9762\uff0c\u9526\u6e56\u624b\u673a\u82af\u7247\u7684\u4ea7\u91cf\u63d0\u5347\u9700\u8981\u4e00\u4e2a\u8fc7\u7a0b\uff0c\u6211\u60f3\u8054\u4fe1\u6269\u4ea7\u4e5f\u65e0\u6cd5\u4e00\u8e74\u800c\u5c31\uff0c\u8054\u901a\u63a8\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u80fd\u4e0d\u80fd\u7a0d\u6709\u4e9b\u8010\u5fc3\uff0c\u5148\u4ece\u901a\u4fe1\u7f51\u7edc\u6761\u4ef6\u597d\u7684\u4e00\u7ebf\u57ce\u5e02\u5f00\u59cb\uff0c\u6682\u65f6\u4e0d\u6025\u7740\u5168\u7ebf\u94fa\u5f00\uff1f\u201d</p><p>\u3000\u3000\u6768\u84c9\u5e73\u542c\u660e\u767d\u4e86\u5f20\u606a\u7684\u8bdd\uff0c\u6309\u8bf4\u9526\u6e56\u8981\u5e72\u6d89\u8054\u901a\u7684\u5185\u90e8\u4e8b\u52a1\uff0c\u4ed6\u5e94\u8be5\u611f\u5230\u975e\u5e38\u7684\u4e0d\u60a6\u624d\u662f\uff0c\u53ea\u662f\u5f20\u606a\u521a\u624d\u7684\u627f\u8bfa\u592a\u6709\u8bf1\u60d1\u529b\u4e86\u3002</p><p>\u3000\u3000\u8054\u901a\u53d1\u5c55\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u5f53\u7136\u4e0d\u4f1a\u53ea\u53d1\u5c55\u4f4e\u7aef\u624b\u673a\u4ea7\u54c1\uff0c\u4e2d\u9ad8\u7aef\u624b\u673a\u4ea7\u54c1\u81ea\u7136\u53ef\u4ee5\u9009\u62e9\u5176\u4ed6\u624b\u673a\u5382\u5546\u5408\u4f5c\uff0c\u50cf\u5145\u503c\u9001\u624b\u673a\u7684\u793c\u5305\u6d3b\u52a8\u7c7b\u578b\u4e5f\u53ef\u4ee5\u66f4\u4e30\u5bcc\u4e00\u4e9b\u3002\u53ea\u8981\u8bd5\u6c34\u6210\u529f\uff0c\u8054\u901a\u7684\u786e\u8ba1\u5212\u8ba9\u5404\u7701\u5e02\u5206\u516c\u53f8\u90fd\u6269\u5e7f\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u505a\u5230\u5168\u7ebf\u94fa\u5f00\u7684\u53d1\u5c55\u65b0\u7528\u6237\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u505a\u6700\u5927\u7684\u574f\u5904\u5c31\u662f\u9664\u4e00\u4e8c\u7ebf\u4e2d\u5fc3\u57ce\u5e02\u4e4b\u5916\uff0c\u5176\u4ed6\u901a\u4fe1\u7f51\u7edc\u6761\u4ef6\u5dee\u3001\u5e26\u5bbd\u7a84\u7684\u5730\u533a\u4f1a\u56e0\u4e3a\u5927\u91cf\u7684\u65b0\u589e\u7528\u6237\u5bfc\u81f4\u7ebf\u8def\u62e5\u585e\u3001\u901a\u8bdd\u8d28\u91cf\u964d\u4f4e\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u4e0a\u5e02\u878d\u8d44\uff0c\u4e3a\u4e86\u7ed9\u6295\u8d44\u8005\u66f4\u660e\u786e\u7684\u53d1\u5c55\u6570\u636e\u4ea4\u5f85\uff0c\u6709\u4e9b\u95ee\u9898\u662f\u9700\u8981\u6682\u65f6\u5ffd\u89c6\u7684\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u91ce\u5fc3\u662f\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u9526\u6e56\u7adf\u7136\u8981\u6c42\u8054\u901a\u66f4\u6539\u65e2\u5b9a\u7684\u5168\u7ebf\u94fa\u5f00\u53d1\u5c55\u65b0\u7528\u6237\u7684\u7b56\u7565\uff0c\u96c6\u4e2d\u5728\u901a\u4fe1\u7f51\u7edc\u6761\u4ef6\u597d\u7684\u4e00\u4e8c\u7ebf\u57ce\u5e02\u5148\u63a8\u5e7f\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u53d1\u5c55\u65b0\u7528\u6237\uff0c\u7531\u9526\u6e56\u914d\u5408\u8054\u4fe1\u7ed9\u8054\u901a\u63d0\u4f9b\u66f4\u591a\u3001\u66f4\u4e30\u5bcc\u7684\u5b9a\u5236\u624b\u673a\uff0c\u540c\u65f6\u4e5f\u662f\u8ba9\u8054\u901a\u7684\u624b\u673a\u5b9a\u5236\u4e1a\u52a1\u66f4\u7d27\u5bc6\u7684\u4e0e\u9526\u6e56\u6346\u7ed1\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u8054\u901a\u9664\u4e86\u80fd\u591f\u5728\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u4e0a\u83b7\u5f97\u66f4\u591a\u7684\u6210\u672c\u4f18\u52bf\u4e4b\u5916\uff0c\u8fd8\u53ef\u4ee5\u96c6\u4e2d\u8d44\u6e90\u4f18\u5148\u5b8c\u5584\u4e00\u4e8c\u7ebf\u4e2d\u5fc3\u57ce\u5e02\u7684GSM\u901a\u4fe1\u7f51\u7edc\u57fa\u7840\u8bbe\u65bd\u3001\u7f13\u89e3\u4e09\u56db\u7ebf\u57ce\u9547\u53ca\u519c\u6751\u5730\u533a\u7684\u901a\u4fe1\u8d44\u6e90\u7d27\u5f20\u95ee\u9898\uff0c\u4f46\u662f\u65b0\u7528\u6237\u589e\u6da8\u901f\u5ea6\u6bd4\u9884\u671f\u5c31\u4f1a\u5927\u6253\u6298\u6263\uff0c\u5728\u7528\u6237\u7fa4\u89c4\u6a21\u4e0a\u8ffd\u8d76\u79fb\u52a8\u7684\u65f6\u95f4\u53c8\u5c06\u5ef6\u540e\u4e86\u2026\u2026</p></article>"}}}]);
